  INSERT INTO words (word, word_type, description) VALUES  (    'Foreshow', 'v. t.', 'To show or exhibit beforehand; to give foreknowledge\n   of; to prognosticate; to foretell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foreshower', 'n.', 'One who predicts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foreside', 'n.', 'The front side; the front; esp., a stretch of country\n   fronting the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foreside', 'n.', 'The outside or external covering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foresight', 'n.', 'The act or the power of foreseeing; prescience;\n   foreknowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foresight', 'n.', 'Action in reference to the future; provident care;\n   prudence; wise forethought.');
