  INSERT INTO words (word, word_type, description) VALUES  (    'Forespeech', 'n.', 'A preface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forespent', 'a.', 'Already spent; gone by; past.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forespent', 'a.', 'See Forspent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forespurrer', 'n.', 'One who rides before; a harbinger.');
