  INSERT INTO words (word, word_type, description) VALUES  (    'Forest', 'a.', 'Of or pertaining to a forest; sylvan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forest', 'v. t.', 'To cover with trees or wood.');
