  INSERT INTO words (word, word_type, description) VALUES  (    'Forestall', 'v. t.', 'To deprive; -- with of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forestall', 'v. t.', 'To obstruct or stop up, as a way; to stop the passage\n   of on highway; to intercept on the road, as goods on the way to market.');
