  INSERT INTO words (word, word_type, description) VALUES  (    'Forestay', 'n.', 'A large, strong rope, reaching from the foremast head to\n   the bowsprit, to support the mast. See Illust. under Ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forester', 'n.', 'One who has charge of the growing timber on an estate; an\n   officer appointed to watch a forest and preserve the game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forester', 'n.', 'An inhabitant of a forest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forester', 'n.', 'A forest tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forester', 'n.', 'A lepidopterous insect belonging to Alypia and allied\n   genera; as, the eight-spotted forester (A. octomaculata), which in the\n   larval state is injurious to the grapevine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forestick', 'n.', 'Front stick of a hearth fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forestry', 'n.', 'The art of forming or of cultivating forests; the\n   management of growing timber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foreswart', 'a.', 'Alt. of Foreswart');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foreswart', 'a.', 'See Forswat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foretaste', 'n.', 'A taste beforehand; enjoyment in advance; anticipation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foretaste', 'v. t.', 'To taste before full possession; to have previous\n   enjoyment or experience of; to anticipate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foretaste', 'v. t.', 'To taste before another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foretaster', 'n.', 'One who tastes beforehand, or before another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foreteach', 'v. t.', 'To teach beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foretold', 'imp. & p. p.', 'of Foretell');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foretelling', 'p. pr. & vb. n.', 'of Foretell');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foretell', 'v. t.', 'To predict; to tell before occurence; to prophesy; to\n   foreshow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foretell', 'v. i.', 'To utter predictions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foreteller', 'n.', 'One who predicts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forethink', 'v. t.', 'To think beforehand; to anticipate in the mind; to\n   prognosticate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forethink', 'v. t.', 'To contrive (something) beforehend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forethink', 'v. i.', 'To contrive beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forethought', 'a.', 'Thought of, or planned, beforehand; aforethought;\n   prepense; hence, deliberate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forethought', 'n.', 'A thinking or planning beforehand; prescience;\n   premeditation; forecast; provident care.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forethoughtful', 'a.', 'Having forethought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foretime', 'n.', 'The past; the time before the present.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foretoken', 'n.', 'Prognostic; previous omen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foretokened', 'imp. & p. p.', 'of Foretoken');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foretokening', 'p. pr. & vb. n.', 'of Foretoken');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foretoken', 'v. t.', 'To foreshow; to presignify; to prognosticate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fore teeth', 'pl. ', 'of Fore tooth');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fore tooth', '', 'One of the teeth in the forepart of the mouth; an\n   incisor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foretop', 'n.', 'The hair on the forepart of the head; esp., a tuft or lock\n   of hair which hangs over the forehead, as of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foretop', 'n.', 'That part of a headdress that is in front; the top of a\n   periwig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foretop', 'n.', 'The platform at the head of the foremast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fore-topgallant', 'a.', 'Designating the mast, sail, yard, etc., above the\n   topmast; as, the fore-topgallant sail. See Sail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fore-topmast', 'n.', 'The mast erected at the head of the foremast, and at\n   the head of which stands the fore-topgallant mast. See Ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fore-topsail', 'n.', 'See Sail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forever', 'adv.', 'Through eternity; through endless ages, eternally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forever', 'adv.', 'At all times; always.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forevouched', 'a.', 'Formerly vouched or avowed; affirmed in advance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foreward', 'n.', 'The van; the front.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forewarned', 'imp. & p. p.', 'of Forewarn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forewarning', 'p. pr. & vb. n.', 'of Forewarn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forewarn', 'v. t.', 'To warn beforehand; to give previous warning,\n   admonition, information, or notice to; to caution in advance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forewaste', 'v. t.', 'See Forewaste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forewend', 'v. t.', 'To go before.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forewish', 'v. t.', 'To wish beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forewit', 'n.', 'A leader, or would-be leader, in matters of knowledge or\n   taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forewit', 'n.', 'Foresight; prudence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forewot', 'pres. indic. sing., ', 'of Forewite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forewost', '2d person', 'of Forewite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forewiten', 'pl.', 'of Forewite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forewiste', 'imp. sing.', 'of Forewite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forewisten', 'pl.', 'of Forewite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forewiting', 'p. pr. & vb. n.', 'of Forewite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forewite', 'v. t.', 'To foreknow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forewomen', 'pl. ', 'of Forewoman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forewoman', 'n.', 'A woman who is chief; a woman who has charge of the work\n   or workers in a shop or other place; a head woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foreword', 'n.', 'A preface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foreworn', 'a.', 'Worn out; wasted; used up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forewot', '', 'pres. indic., 1st & 3d pers. sing. of Forewite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foreyard', 'n.', 'The lowermost yard on the foremast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forfalture', 'n.', 'Forfeiture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forfeit', 'n.', 'Injury; wrong; mischief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forfeit', 'n.', 'A thing forfeit or forfeited; what is or may be taken from\n   one in requital of a misdeed committed; that which is lost, or the\n   right to which is alienated, by a crime, offense, neglect of duty, or\n   breach of contract; hence, a fine; a mulct; a penalty; as, he who\n   murders pays the forfeit of his life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forfeit', 'n.', 'Something deposited and redeemable by a sportive fine; --\n   whence the game of forfeits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forfeit', 'n.', 'Lost or alienated for an offense or crime; liable to penal\n   seizure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forfeited', 'imp. & p. p.', 'of Forfeit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forfeiting', 'p. pr. & vb. n.', 'of Forfeit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forfeit', 'n.', 'To lose, or lose the right to, by some error, fault,\n   offense, or crime; to render ones self by misdeed liable to be\n   deprived of; to alienate the right to possess, by some neglect or\n   crime; as, to forfeit an estate by treason; to forfeit reputation by a\n   breach of promise; -- with to before the one acquiring what is\n   forfeited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forfeit', 'v. i.', 'To be guilty of a misdeed; to be criminal; to\n   transgress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forfeit', 'v. i.', 'To fail to keep an obligation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forfeit', 'p. p. / a.', 'In the condition of being forfeited; subject to\n   alienation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourfeitable', 'a.', 'Liable to be forfeited; subject to forfeiture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forfeiter', 'n.', 'One who incurs a penalty of forfeiture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forfeiture', 'n.', 'The act of forfeiting; the loss of some right,\n   privilege, estate, honor, office, or effects, by an offense, crime,\n   breach of condition, or other act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forfeiture', 'n.', 'That which is forfeited; a penalty; a fine or mulct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forfend', 'v. t.', 'To prohibit; to forbid; to avert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forfered', 'p. p. & a.', 'Excessively alarmed; in great fear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forfete', 'v. i.', 'To incur a penalty; to transgress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forfex', 'n.', 'A pair of shears.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forficate', 'a.', 'Deeply forked, as the tail of certain birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forficula', 'n.', 'A genus of insects including the earwigs. See Earwig, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgather', 'v. i.', 'To convene; to gossip; to meet accidentally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgave', '', 'imp. of Forgive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forge', 'n.', 'A place or establishment where iron or other metals are\n   wrought by heating and hammering; especially, a furnace, or a shop with\n   its furnace, etc., where iron is heated and wrought; a smithy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forge', 'n.', 'The works where wrought iron is produced directly from the\n   ore, or where iron is rendered malleable by puddling and shingling; a\n   shingling mill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forge', 'n.', 'The act of beating or working iron or steel; the manufacture\n   of metalic bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forged', 'imp. & p. p.', 'of Forge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forging', 'p. pr. & vb. n.', 'of Forge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forge', 'n.', 'To form by heating and hammering; to beat into any\n   particular shape, as a metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forge', 'n.', 'To form or shape out in any way; to produce; to frame; to\n   invent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forge', 'n.', 'To coin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forge', 'n.', 'To make falsely; to produce, as that which is untrue or not\n   genuine; to fabricate; to counterfeit, as, a signature, or a signed\n   document.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forge', 'v. t.', 'To commit forgery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forge', 'v. t.', 'To move heavily and slowly, as a ship after the sails are\n   furled; to work ones way, as one ship in outsailing another; -- used\n   especially in the phrase to forge ahead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forge', 'v. t.', 'To impel forward slowly; as, to forge a ship forward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgemen', 'pl. ', 'of Forgeman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgeman', 'n.', 'A skilled smith, who has a hammerer to assist him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forger', 'n. & v. t.', 'One who forges, makes, of forms; a fabricator; a\n   falsifier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forger', 'n. & v. t.', 'Especially: One guilty of forgery; one who makes or\n   issues a counterfeit document.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgeries', 'pl. ', 'of Forgery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgery', 'n.', 'The act of forging metal into shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgery', 'n.', 'The act of forging, fabricating, or producing falsely;\n   esp., the crime of fraudulently making or altering a writing or\n   signature purporting to be made by another; the false making or\n   material alteration of or addition to a written instrument for the\n   purpose of deceit and fraud; as, the forgery of a bond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgery', 'n.', 'That which is forged, fabricated, falsely devised, or\n   counterfeited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgot', 'imp.', 'of Forget');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgat', '', 'of Forget');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgotten', 'p. p.', 'of Forget');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgot', '', 'of Forget');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgetting', 'p. pr. & vb. n.', 'of Forget');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forget', 'v. t.', 'To lose the remembrance of; to let go from the memory;\n   to cease to have in mind; not to think of; also, to lose the power of;\n   to cease from doing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forget', 'v. t.', 'To treat with inattention or disregard; to slight; to\n   neglect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgetful', 'a.', 'Apt to forget; easily losing remembrance; as, a\n   forgetful man should use helps to strengthen his memory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgetful', 'a.', 'Heedless; careless; neglectful; inattentive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgetful', 'a.', 'Causing to forget; inducing oblivion; oblivious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgetfully', 'adv.', 'In a forgetful manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgetfulness', 'n.', 'The quality of being forgetful; prononess to let\n   slip from the mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgetfulness', 'n.', 'Loss of remembrance or recollection; a ceasing to\n   remember; oblivion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgetfulness', 'n.', 'Failure to bear in mind; careless omission;\n   inattention; as, forgetfulness of duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgetive', 'a.', 'Inventive; productive; capable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forget-me-not', 'n.', 'A small herb, of the genus Myosotis (M. palustris,\n   incespitosa, etc.), bearing a beautiful blue flower, and extensively\n   considered the emblem of fidelity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgettable', 'a.', 'Liable to be, or that may be, forgotten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgetter', 'n.', 'One who forgets; a heedless person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgettingly', 'adv.', 'By forgetting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forging', 'n.', 'The act of shaping metal by hammering or pressing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forging', 'n.', 'The act of counterfeiting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forging', 'n.', 'A piece of forged work in metal; -- a general name for a\n   piece of hammered iron or steel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgivable', 'a.', 'Capable of being forgiven; pardonable; venial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgave', 'imp.', 'of Forgive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgiven', 'p. p.', 'of Forgive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgiving', 'p. pr. & vb. n.', 'of Forgive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgive', 'v. t.', 'To give wholly; to make over without reservation; to\n   resign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgive', 'v. t.', 'To give up resentment or claim to requital on account\n   of (an offense or wrong); to remit the penalty of; to pardon; -- said\n   in reference to the act forgiven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgive', 'v. t.', 'To cease to feel resentment against, on account of\n   wrong committed; to give up claim to requital from or retribution upon\n   (an offender); to absolve; to pardon; -- said of the person offending.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgiveness', 'n.', 'The act of forgiving; the state of being forgiven; as,\n   the forgiveness of sin or of injuries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgiveness', 'n.', 'Disposition to pardon; willingness to forgive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgiver', 'n.', 'One who forgives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgiving', 'a.', 'Disposed to forgive; inclined to overlook offenses;\n   mild; merciful; compassionate; placable; as, a forgiving temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forwent', 'imp.', 'of Forgo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgone', 'p. p.', 'of Forgo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgoing', 'p. pr. & vb. n.', 'of Forgo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgo', 'v. i.', 'To pass by; to leave. See 1st Forego.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgot', '', 'imp. & p. p. of Forget.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forgotten', '', 'p. p. of Forget.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forhall', 'v. t.', 'To harass; to torment; to distress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forhend', 'v. t.', 'To seize upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forinsecal', 'a.', 'Foreign; alien.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forisfamiliated', 'imp. & p. p.', 'of Forisfamiliate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forisfamiliating', 'p. pr. & vb. n.', 'of Forisfamiliate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forisfamiliate', 'v. t.', 'Literally, to put out of a family; hence, to\n   portion off, so as to exclude further claim of inheritance; to\n   emancipate (as a with his own consent) from paternal authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forisfamiliate', 'v. i.', 'To renounce a legal title to a further share of\n   paternal inheritance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forisfamiliation', 'n.', 'The act of forisfamiliating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fork', 'n.', 'An instrument consisting of a handle with a shank terminating\n   in two or more prongs or tines, which are usually of metal, parallel\n   and slightly curved; -- used from piercing, holding, taking up, or\n   pitching anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fork', 'n.', 'Anything furcate or like a fork in shape, or furcate at the\n   extremity; as, a tuning fork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fork', 'n.', 'One of the parts into which anything is furcated or divided;\n   a prong; a branch of a stream, a road, etc.; a barbed point, as of an\n   arrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fork', 'n.', 'The place where a division or a union occurs; the angle or\n   opening between two branches or limbs; as, the fork of a river, a tree,\n   or a road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fork', 'n.', 'The gibbet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forked', 'imp. & p. p.', 'of Fork');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forking', 'p. pr. & vb. n.', 'of Fork');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fork', 'v. i.', 'To shoot into blades, as corn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fork', 'v. i.', 'To divide into two or more branches; as, a road, a tree,\n   or a stream forks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fork', 'v. t.', 'To raise, or pitch with a fork, as hay; to dig or turn\n   over with a fork, as the soil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forkbeard', 'n.', 'A European fish (Raniceps raninus), having a large flat\n   head; -- also called tadpole fish, and lesser forked beard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forkbeard', 'n.', 'The European forked hake or hakes-dame (Phycis\n   blennoides); -- also called great forked beard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forked', 'a.', 'Formed into a forklike shape; having a fork; dividing into\n   two or more prongs or branches; furcated; bifurcated; zigzag; as, the\n   forked lighting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forked', 'a.', 'Having a double meaning; ambiguous; equivocal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forkerve', 'v. t.', 'See Forcarve, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forkiness', 'n.', 'The quality or state or dividing in a forklike manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forkless', 'a.', 'Having no fork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forktail', 'n.', 'One of several Asiatic and East Indian passerine birds,\n   belonging to Enucurus, and allied genera. The tail is deeply forking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forktail', 'n.', 'A salmon in its fourth years growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fork-tailed', 'a.', 'Having the outer tail feathers longer than the median\n   ones; swallow-tailed; -- said of many birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forky', 'a.', 'Opening into two or more parts or shoots; forked; furcated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forlaft', '', 'p. p. of Forleave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forlay', 'v. t.', 'To lie in wait for; to ambush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forleave', 'v. t.', 'To leave off wholly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forlend', 'v. t.', 'To give up wholly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forlore', 'p. p.', 'of Forlese');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forlorn', '', 'of Forlese');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forlese', 'v. t.', 'To lose utterly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forlet', 'v. t.', 'To give up; to leave; to abandon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forlie', 'v. i.', 'See Forelie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forlore', '', 'imp. pl. & p. p. of Forlese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forlorn', 'v. t.', 'Deserted; abandoned; lost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forlorn', 'v. t.', 'Destitute; helpless; in pitiful plight; wretched;\n   miserable; almost hopeless; desperate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forlorn', 'n.', 'A lost, forsaken, or solitary person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forlorn', 'n.', 'A forlorn hope; a vanguard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forlornly', 'adv.', 'In a forlorn manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forlornness', 'n.', 'State of being forlorn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forlye', 'v. i.', 'Same as Forlie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'form', 'n.', 'A suffix used to denote in the form / shape of, resembling,\n   etc.; as, valiform; oviform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Form', 'n.', 'The shape and structure of anything, as distinguished from\n   the material of which it is composed; particular disposition or\n   arrangement of matter, giving it individuality or distinctive\n   character; configuration; figure; external appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Form', 'n.', 'Constitution; mode of construction, organization, etc.;\n   system; as, a republican form of government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Form', 'n.', 'Established method of expression or practice; fixed way of\n   proceeding; conventional or stated scheme; formula; as, a form of\n   prayer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Form', 'n.', 'Show without substance; empty, outside appearance; vain,\n   trivial, or conventional ceremony; conventionality; formality; as, a\n   matter of mere form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Form', 'n.', 'Orderly arrangement; shapeliness; also, comeliness; elegance;\n   beauty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Form', 'n.', 'A shape; an image; a phantom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Form', 'n.', 'That by which shape is given or determined; mold; pattern;\n   model.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Form', 'n.', 'A long seat; a bench; hence, a rank of students in a school;\n   a class; also, a class or rank in society.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Form', 'n.', 'The seat or bed of a hare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Form', 'n.', 'The type or other matter from which an impression is to be\n   taken, arranged and secured in a chase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Form', 'n.', 'The boundary line of a material object. In painting, more\n   generally, the human body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Form', 'n.', 'The particular shape or structure of a word or part of\n   speech; as, participial forms; verbal forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Form', 'n.', 'The combination of planes included under a general\n   crystallographic symbol. It is not necessarily a closed solid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Form', 'n.', 'That assemblage or disposition of qualities which makes a\n   conception, or that internal constitution which makes an existing thing\n   to be what it is; -- called essential or substantial form, and\n   contradistinguished from matter; hence, active or formative nature; law\n   of being or activity; subjectively viewed, an idea; objectively, a law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Form', 'n.', 'Mode of acting or manifestation to the senses, or the\n   intellect; as, water assumes the form of ice or snow. In modern usage,\n   the elements of a conception furnished by the minds own activity, as\n   contrasted with its object or condition, which is called the matter;\n   subjectively, a mode of apprehension or belief conceived as dependent\n   on the constitution of the mind; objectively, universal and necessary\n   accompaniments or elements of every object known or thought of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Form', 'n.', 'The peculiar characteristics of an organism as a type of\n   others; also, the structure of the parts of an animal or plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formed', 'imp. & p. p.', 'of Form');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forming', 'p. pr. & vb. n.', 'of Form');
  INSERT INTO words (word, word_type, description) VALUES  (    'Form', 'n.', 'To give form or shape to; to frame; to construct; to make; to\n   fashion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Form', 'n.', 'To give a particular shape to; to shape, mold, or fashion\n   into a certain state or condition; to arrange; to adjust; also, to\n   model by instruction and discipline; to mold by influence, etc.; to\n   train.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Form', 'n.', 'To go to make up; to act as constituent of; to be the\n   essential or constitutive elements of; to answer for; to make the shape\n   of; -- said of that out of which anything is formed or constituted, in\n   whole or in part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Form', 'n.', 'To provide with a form, as a hare. See Form, n., 9.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Form', 'n.', 'To derive by grammatical rules, as by adding the proper\n   suffixes and affixes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Form', 'v. i.', 'To take a form, definite shape, or arrangement; as, the\n   infantry should form in column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Form', 'v. i.', 'To run to a form, as a hare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formal', 'n.', 'See Methylal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formal', 'a.', 'Belonging to the form, shape, frame, external appearance,\n   or organization of a thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formal', 'a.', 'Belonging to the constitution of a thing, as distinguished\n   from the matter composing it; having the power of making a thing what\n   it is; constituent; essential; pertaining to or depending on the forms,\n   so called, of the human intellect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formal', 'a.', 'Done in due form, or with solemnity; according to regular\n   method; not incidental, sudden or irregular; express; as, he gave his\n   formal consent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formal', 'a.', 'Devoted to, or done in accordance with, forms or rules;\n   punctilious; regular; orderly; methodical; of a prescribed form; exact;\n   prim; stiff; ceremonious; as, a man formal in his dress, his gait, his\n   conversation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formal', 'a.', 'Having the form or appearance without the substance or\n   essence; external; as, formal duty; formal worship; formal courtesy,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formal', 'a.', 'Dependent in form; conventional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formal', 'a.', 'Sound; normal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formaldehyde', 'n.', 'A colorless, volatile liquid, H2CO, resembling acetic\n   or ethyl aldehyde, and chemically intermediate between methyl alcohol\n   and formic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formalism', 'n.', 'The practice or the doctrine of strict adherence to, or\n   dependence on, external forms, esp. in matters of religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formalist', 'n.', 'One overattentive to forms, or too much confined to\n   them; esp., one who rests in external religious forms, or observes\n   strictly the outward forms of worship, without possessing the life and\n   spirit of religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formalities', 'pl. ', 'of Formality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formality', 'n.', 'The condition or quality of being formal, strictly\n   ceremonious, precise, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formality', 'n.', 'Form without substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formality', 'n.', 'Compliance with formal or conventional rules; ceremony;\n   conventionality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formality', 'n.', 'An established order; conventional rule of procedure;\n   usual method; habitual mode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formality', 'n.', 'The dress prescribed for any body of men, academical,\n   municipal, or sacerdotal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formality', 'n.', 'That which is formal; the formal part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formality', 'n.', 'The quality which makes a thing what it is; essence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formality', 'n.', 'The manner in which a thing is conceived or constituted\n   by an act of human thinking; the result of such an act; as, animality\n   and rationality are formalities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formalized', 'imp. & p. p.', 'of Formalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formalizing', 'p. pr. & vb. n.', 'of Formalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formalize', 'v. t.', 'To give form, or a certain form, to; to model.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formalize', 'v. t.', 'To render formal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formalize', 'v. i.', 'To affect formality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formally', 'adv.', 'In a formal manner; essentially; characteristically;\n   expressly; regularly; ceremoniously; precisely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formate', 'n.', 'A salt of formic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formation', 'n.', 'The act of giving form or shape to anything; a forming;\n   a shaping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formation', 'n.', 'The manner in which a thing is formed; structure;\n   construction; conformation; form; as, the peculiar formation of the\n   heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formation', 'n.', 'A substance formed or deposited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formation', 'n.', 'Mineral deposits and rock masses designated with\n   reference to their origin; as, the siliceous formation about geysers;\n   alluvial formations; marine formations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formation', 'n.', 'A group of beds of the same age or period; as, the\n   Eocene formation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formation', 'n.', 'The arrangement of a body of troops, as in a square,\n   column, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formative', 'a.', 'Giving form; having the power of giving form; plastic;\n   as, the formative arts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formative', 'a.', 'Serving to form; derivative; not radical; as, a\n   termination merely formative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formative', 'a.', 'Capable of growth and development; germinal; as, living\n   or formative matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formative', 'n.', 'That which serves merely to give form, and is no part of\n   the radical, as the prefix or the termination of a word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formative', 'n.', 'A word formed in accordance with some rule or usage, as\n   from a root.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forme', 'a.', 'Same as Pate or Patte.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forme', 'a.', 'First.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formed', 'a.', 'Arranged, as stars in a constellation; as, formed stars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formed', 'a.', 'Having structure; capable of growth and development;\n   organized; as, the formed or organized ferments. See Ferment, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formedon', 'n.', 'A writ of right for a tenant in tail in case of a\n   discontinuance of the estate tail. This writ has been abolished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formell', 'n.', 'The female of a hawk or falcon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Former', 'n.', 'One who forms; a maker; a creator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Former', 'n.', 'A shape around which an article is to be shaped, molded,\n   woven wrapped, pasted, or otherwise constructed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Former', 'n.', 'A templet, pattern, or gauge by which an article is shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Former', 'n.', 'A cutting die.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Former', 'a.', 'Preceding in order of time; antecedent; previous; prior;\n   earlier; hence, ancient; long past.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Former', 'a.', 'Near the beginning; preceeding; as, the former part of a\n   discourse or argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Former', 'a.', 'Earlier, as between two things mentioned together; first\n   mentioned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formeret', 'n.', 'One of the half ribs against the walls in a ceiling\n   vaulted with ribs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formerly', 'adv.', 'In time past, either in time immediately preceding or\n   at any indefinite distance; of old; heretofore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formful', 'a.', 'Creative; imaginative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formic', 'a.', 'Pertaining to, or derived from, ants; as, formic acid; in\n   an extended sense, pertaining to, or derived from, formic acid; as,\n   formic ether.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formica', 'n.', 'A Linnaean genus of hymenopterous insects, including the\n   common ants. See Ant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formicaroid', 'a.', 'Like or pertaining to the family Formicaridae or ant\n   thrushes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formicary', 'n.', 'The nest or dwelling of a swarm of ants; an ant-hill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formicate', 'a.', 'Resembling, or pertaining to, an ant or ants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formication', 'n.', 'A sensation resembling that made by the creeping of\n   ants on the skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formicid', 'a.', 'Pertaining to the ants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formicid', 'n.', 'One of the family Formicidae, or ants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formidability', 'n.', 'Formidableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formidable', 'a.', 'Exciting fear or apprehension; impressing dread;\n   adapted to excite fear and deter from approach, encounter, or\n   undertaking; alarming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formidableness', 'n.', 'The quality of being formidable, or adapted to\n   excite dread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formidably', 'adv.', 'In a formidable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formidolose', 'a.', 'Very much afraid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forming', 'n.', 'The act or process of giving form or shape to anything;\n   as, in shipbuilding, the exact shaping of partially shaped timbers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formless', 'a.', 'Shapeless; without a determinate form; wanting regularity\n   of shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formulas', 'pl. ', 'of Formula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formulae', 'pl. ', 'of Formula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formula', 'n.', 'A prescribed or set form; an established rule; a fixed or\n   conventional method in which anything is to be done, arranged, or said.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formula', 'n.', 'A written confession of faith; a formal statement of\n   foctrines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formula', 'n.', 'A rule or principle expressed in algebraic language; as,\n   the binominal formula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formula', 'n.', 'A prescription or recipe for the preparation of a\n   medicinal compound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formula', 'n.', 'A symbolic expression (by means of letters, figures, etc.)\n   of the constituents or constitution of a compound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formularistic', 'a.', 'Pertaining to, or exhibiting, formularization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formularization', 'n.', 'The act of formularizing; a formularized or\n   formulated statement or exhibition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formularize', 'v. t.', 'To reduce to a forula; to formulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formulary', 'a.', 'Stated; prescribed; ritual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formularies', 'pl. ', 'of Formulary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formulary', 'n.', 'A book containing stated and prescribed forms, as of\n   oaths, declarations, prayers, medical formulaae, etc.; a book of\n   precedents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formulary', 'n.', 'Prescribed form or model; formula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formulated', 'imp. & p. p.', 'of Formulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formulating', 'p. pr. & vb. n.', 'of Formulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formulate', 'v. t.', 'To reduce to, or express in, a formula; to put in a\n   clear and definite form of statement or expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formulation', 'n.', 'The act, process, or result of formulating or reducing\n   to a formula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formule', 'n.', 'A set or prescribed model; a formula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formulization', 'n.', 'The act or process of reducing to a formula; the\n   state of being formulized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formulized', 'imp. & p. p.', 'of Formulize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formulizing', 'p. pr. & vb. n.', 'of Formulize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formulize', 'v. t.', 'To reduce to a formula; to formulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formyl', 'n.', 'A univalent radical, H.C:O, regarded as the essential\n   residue of formic acid and aldehyde.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Formyl', 'n.', 'Formerly, the radical methyl, CH3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forncast', 'p. p.', 'Predestined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fornical', 'a.', 'Relating to a fornix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fornicate', 'a.', 'Alt. of Fornicated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fornicated', 'a.', 'Vaulted like an oven or furnace; arched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fornicated', 'a.', 'Arching over; overarched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fornicate', 'v. i.', 'To commit fornication; to have unlawful sexual\n   intercourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fornication', 'n.', 'Unlawful sexual intercourse on the part of an\n   unmarried person; the act of such illicit sexual intercourse between a\n   man and a woman as does not by law amount to adultery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fornication', 'n.', 'Adultery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fornication', 'n.', 'Incest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fornication', 'n.', 'Idolatry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fornicator', 'n.', 'An unmarried person, male or female, who has criminal\n   intercourse with the other sex; one guilty of fornication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fornicatress', 'n.', 'A woman guilty of fornication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fornices', 'pl. ', 'of Fornix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fornix', 'n.', 'An arch or fold; as, the fornix, or vault, of the cranium;\n   the fornix, or reflection, of the conjuctiva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fornix', 'n.', 'Esp., two longitudinal bands of white nervous tissue\n   beneath the lateral ventricles of the brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forold', 'a.', 'Very old.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forpass', 'v. t. & i.', 'To pass by or along; to pass over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forpine', 'v. t.', 'To waste away completely by suffering or torment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forray', 'v. t.', 'To foray; to ravage; to pillage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forray', 'n.', 'The act of ravaging; a ravaging; a predatory excursion. See\n   Foray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forrill', 'n.', 'Lambskin parchment; vellum; forel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forsook', 'imp.', 'of Forsake');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forsaken', 'p. p.', 'of Forsake');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forsaking', 'p. pr. & vb. n.', 'of Forsake');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forsake', 'v. t.', 'To quit or leave entirely; to desert; to abandon; to\n   depart or withdraw from; to leave; as, false friends and flatterers\n   forsake us in adversity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forsake', 'v. t.', 'To renounce; to reject; to refuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forsaker', 'n.', 'One who forsakes or deserts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forsay', 'v. t.', 'To forbid; to renounce; to forsake; to deny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forshape', 'v. t.', 'To render misshapen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forslack', 'v. t.', 'To neglect by idleness; to delay or to waste by sloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forslouthe', 'v. t.', 'To lose by sloth or negligence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forslow', 'v. t.', 'To delay; to hinder; to neglect; to put off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forslow', 'v. i.', 'To loiter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forslugge', 'v. t.', 'To lsoe by idleness or slotch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forsooth', 'adv.', 'In truth; in fact; certainly; very well; -- formerly\n   used as an expression of deference or respect, especially to woman; now\n   used ironically or contemptuously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forsooth', 'v. t.', 'To address respectfully with the term forsooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forsooth', 'n.', 'A person who used forsooth much; a very ceremonious and\n   deferential person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forspeak', 'v. t.', 'To forbid; to prohibit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forspeak', 'v. t.', 'To bewitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forspent', 'a.', 'Wasted in strength; tired; exhausted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forstall', 'v. t.', 'To forestall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forster', 'n.', 'A forester.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forstraught', 'p. p. & a.', 'Distracted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forswat', 'a.', 'Spent with heat; covered with sweat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forswore', 'imp.', 'of Forswear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forsworn', 'p. p.', 'of Forswear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forswearing', 'p. pr. & vb. n.', 'of Forswear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forswear', 'v. i.', 'To reject or renounce upon oath; hence, to renounce\n   earnestly, determinedly, or with protestations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forswear', 'v. i.', 'To deny upon oath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forswear', 'v. i.', 'To swear falsely; to commit perjury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forswearer', 'n.', 'One who rejects of renounces upon oath; one who swears\n   a false oath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forswonk', 'a.', 'Overlabored; exhausted; worn out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forswore', '', 'imp. of Forswear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forsworn', '', 'p. p. of Forswear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forswornness', 'n.', 'State of being forsworn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forsythia', 'a.', 'A shrub of the Olive family, with yellow blossoms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fort', 'n.', 'A strong or fortified place; usually, a small fortified\n   place, occupied only by troops, surrounded with a ditch, rampart, and\n   parapet, or with palisades, stockades, or other means of defense; a\n   fortification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortalice', 'n.', 'A small outwork of a fortification; a fortilage; --\n   called also fortelace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forte', 'n.', 'The strong point; that in which one excels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forte', 'n.', 'The stronger part of the blade of a sword; the part of half\n   nearest the hilt; -- opposed to foible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forte', 'a. & adv.', 'Loudly; strongly; powerfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forted', 'a.', 'Furnished with, or guarded by, forts; strengthened or\n   defended, as by forts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forth', 'adv.', 'Forward; onward in time, place, or order; in advance from\n   a given point; on to end; as, from that day forth; one, two, three, and\n   so forth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forth', 'adv.', 'Out, as from a state of concealment, retirement,\n   confinement, nondevelopment, or the like; out into notice or view; as,\n   the plants in spring put forth leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forth', 'adv.', 'Beyond a (certain) boundary; away; abroad; out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forth', 'adv.', 'Throughly; from beginning to end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forth', 'prep.', 'Forth from; out of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forth', 'n.', 'A way; a passage or ford.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forthby', 'adv.', 'See Forby.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forthcoming', 'a.', 'Ready or about to appear; making appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forthgoing', 'n.', 'A going forth; an utterance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forthgoing', 'a.', 'Going forth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forthink', 'v. t.', 'To repent; to regret; to be sorry for; to cause\n   regret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forthputing', 'a.', 'Bold; forward; aggressive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forthright', 'adv.', 'Straight forward; in a straight direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forthright', 'a.', 'Direct; straightforward; as, a forthright man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forthright', 'n.', 'A straight path.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forthrightness', 'n.', 'Straightforwardness; explicitness; directness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forthward', 'adv.', 'Forward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forthwith', 'adv.', 'Immediately; without delay; directly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forthwith', 'adv.', 'As soon as the thing required may be done by\n   reasonable exertion confined to that object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forthy', 'adv.', 'Therefore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forties', 'n. pl.', 'See Forty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortieth', 'a.', 'Following the thirty-ninth, or preceded by thirty-nine\n   units, things, or parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortieth', 'a.', 'Constituting one of forty equal parts into which anything\n   is divided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortieth', 'n.', 'One of forty equal parts into which one whole is divided;\n   the quotient of a unit divided by forty; one next in order after the\n   thirty-ninth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortifiable', 'a.', 'Capable of being fortified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortification', 'n.', 'The act of fortifying; the art or science of\n   fortifying places in order to defend them against an enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortification', 'n.', 'That which fortifies; especially, a work or works\n   erected to defend a place against attack; a fortified place; a\n   fortress; a fort; a castle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortifier', 'n.', 'One who, or that which, fortifies, strengthens,\n   supports, or upholds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortified', 'imp. & p. p.', 'of Fortify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortifying', 'p. pr. & vb. n.', 'of Fortify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortify', 'v. t.', 'To add strength to; to strengthen; to confirm; to\n   furnish with power to resist attack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortify', 'v. t.', 'To strengthen and secure by forts or batteries, or by\n   surrounding with a wall or ditch or other military works; to render\n   defensible against an attack by hostile forces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortify', 'v. i.', 'To raise defensive works.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortilage', 'n.', 'A little fort; a blockhouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortin', 'n.', 'A little fort; a fortlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortissimo', 'adv.', 'Very loud; with the utmost strength or loudness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortition', 'n.', 'Casual choice; fortuitous selection; hazard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortitude', 'n.', 'Power to resist attack; strength; firmness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortitude', 'n.', 'That strength or firmness of mind which enables a person\n   to encounter danger with coolness and courage, or to bear pain or\n   adversity without murmuring, depression, or despondency; passive\n   courage; resolute endurance; firmness in confronting or bearing up\n   against danger or enduring trouble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortitudinous', 'a.', 'Having fortitude; courageous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortlet', 'n.', 'A little fort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortnight', 'n.', 'The space of fourteen days; two weeks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortnightly', 'a.', 'Occurring or appearing once in a fortnight; as, a\n   fortnightly meeting of a club; a fortnightly magazine, or other\n   publication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortnightly', 'adv.', 'Once in a fortnight; at intervals of a fortnight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortread', 'v. t.', 'To tread down; to trample upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortresses', 'pl. ', 'of Fortress');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortress', 'n.', 'A fortified place; a large and permanent fortification,\n   sometimes including a town; a fort; a castle; a stronghold; a place of\n   defense or security.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortress', 'v. t.', 'To furnish with a fortress or with fortresses; to\n   guard; to fortify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortuitous', 'a.', 'Happening by chance; coming or occuring unexpectedly,\n   or without any known cause; chance; as, the fortuitous concourse of\n   atoms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortuitous', 'a.', 'Happening independently of human will or means of\n   foresight; resulting from unavoidable physical causes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortuity', 'n.', 'Accident; chance; casualty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortunate', 'n.', 'Coming by good luck or favorable chance; bringing some\n   good thing not foreseen as certain; presaging happiness; auspicious;\n   as, a fortunate event; a fortunate concurrence of circumstances; a\n   fortunate investment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortunate', 'n.', 'Receiving same unforeseen or unexpected good, or some\n   good which was not dependent on ones own skill or efforts; favored\n   with good forune; lucky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortunately', 'adv.', 'In a fortunate manner; luckily; successfully;\n   happily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortunateness', 'n.', 'The condition or quality of being fortunate; good\n   luck; success; happiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortune', 'n.', 'The arrival of something in a sudden or unexpected manner;\n   chance; accident; luck; hap; also, the personified or deified power\n   regarded as determining human success, apportioning happiness and\n   unhappiness, and distributing arbitrarily or fortuitously the lots of\n   life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortune', 'n.', 'That which befalls or is to befall one; lot in life, or\n   event in any particular undertaking; fate; destiny; as, to tell ones\n   fortune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortune', 'n.', 'That which comes as the result of an undertaking or of a\n   course of action; good or ill success; especially, favorable issue;\n   happy event; success; prosperity as reached partly by chance and partly\n   by effort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortune', 'n.', 'Wealth; large possessions; large estate; riches; as, a\n   gentleman of fortune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortune', 'n.', 'To make fortunate; to give either good or bad fortune to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortune', 'n.', 'To provide with a fortune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortune', 'n.', 'To presage; to tell the fortune of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortune', 'v. i.', 'To fall out; to happen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortuneless', 'a.', 'Luckless; also, destitute of a fortune or portion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fortunize', 'v. t.', 'To regulate the fortune of; to make happy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forty', 'a.', 'Four times ten; thirty-nine and one more.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forties', 'pl. ', 'of Forty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forty', 'n.', 'The sum of four tens; forty units or objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forty', 'n.', 'A symbol expressing forty units; as, 40, or xl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forty-spot', 'n.', 'The Tasmanian forty-spotted diamond bird (Pardalotus\n   quadragintus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forums', 'pl. ', 'of Forum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fora', 'pl. ', 'of Forum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forum', 'n.', 'A market place or public place in Rome, where causes were\n   judicially tried, and orations delivered to the people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forum', 'n.', 'A tribunal; a court; an assembly empowered to hear and\n   decide causes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forwaked', 'p. p. & a.', 'Tired out with excessive waking or watching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forwander', 'v. i.', 'To wander away; to go astray; to wander far and to\n   weariness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forward', 'n.', 'An agreement; a covenant; a promise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forward', 'adv.', 'Alt. of Forwards');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forwards', 'adv.', 'Toward a part or place before or in front; onward; in\n   advance; progressively; -- opposed to backward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forward', 'a.', 'Near, or at the fore part; in advance of something else;\n   as, the forward gun in a ship, or the forward ship in a fleet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forward', 'a.', 'Ready; prompt; strongly inclined; in an ill sense,\n   overready; to hasty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forward', 'a.', 'Ardent; eager; earnest; in an ill sense, less reserved or\n   modest than is proper; bold; confident; as, the boy is too forward for\n   his years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forward', 'a.', 'Advanced beyond the usual degree; advanced for season; as,\n   the grass is forward, or forward for the season; we have a forward\n   spring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forwarded', 'imp. & p. p.', 'of Forward');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forwarding', 'p. pr. & vb. n.', 'of Forward');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forward', 'v. t.', 'To help onward; to advance; to promote; to accelerate;\n   to quicken; to hasten; as, to forward the growth of a plant; to forward\n   one in improvement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forward', 'v. t.', 'To send forward; to send toward the place of\n   destination; to transmit; as, to forward a letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forwarder', 'n.', 'One who forwards or promotes; a promoter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forwarder', 'n.', 'One who sends forward anything; (Com.) one who transmits\n   goods; a forwarding merchant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forwarder', 'n.', 'One employed in forwarding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forwarding', 'n.', 'The act of one who forwards; the act or occupation of\n   transmitting merchandise or other property for others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forwarding', 'n.', 'The process of putting a book into its cover, and\n   making it ready for the finisher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forwardly', 'adv.', 'Eagerly; hastily; obtrusively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forwardness', 'n.', 'The quality of being forward; cheerful readiness;\n   promtness; as, the forwardness of Christians in propagating the gospel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forwardness', 'n.', 'An advanced stage of progress or of preparation;\n   advancement; as, his measures were in great forwardness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forwardness', 'n.', 'Eagerness; ardor; as, it is difficult to restrain the\n   forwardness of youth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forwardness', 'n.', 'Boldness; confidence; assurance; want of due reserve\n   or modesty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forwardness', 'n.', 'A state of advance beyond the usual degree;\n   prematureness; precocity; as, the forwardnessof spring or of corn; the\n   forwardness of a pupil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forwards', 'adv.', 'Same as Forward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forwaste', 'v. t.', 'To desolate or lay waste utterly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forwweary', 'v. t.', 'To weary extremely; to dispirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forweep', 'v. i.', 'To weep much.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forwete', 'v. t.', 'See Forewite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forwhy', 'conj.', 'Wherefore; because.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forworn', 'a.', 'Much worn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forwot', '', 'pres. indic. 1st & 3d pers. sing. of Forwete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forwrap', 'v. t.', 'To wrap up; to conceal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foryelde', 'v. t.', 'To repay; to requite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foryete', 'v. t.', 'To forget.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foryetten', '', 'p. p. of Foryete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Forzando', 'adv.', 'See Sforzato.');
  INSERT INTO words (word, word_type, description) VALUES  (    'FossAe', 'pl. ', 'of Fossa');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fossa', 'n.', 'A pit, groove, cavity, or depression, of greater or less\n   depth; as, the temporal fossa on the side of the skull; the nasal\n   fossae containing the nostrils in most birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fossane', 'n.', 'A species of civet (Viverra fossa) resembling the genet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fosse', 'n.', 'A ditch or moat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fosse', 'n.', 'See Fossa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fosset', 'n.', 'A faucet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fossette', 'n.', 'A little hollow; hence, a dimple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fossette', 'n.', 'A small, deep-centered ulcer of the transparent cornea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fosseway', 'n.', 'One of the great military roads constructed by the Romans\n   in England and other parts of Europe; -- so called from the fosse or\n   ditch on each side for keeping it dry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fossil', 'a.', 'Dug out of the earth; as, fossil coal; fossil salt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fossil', 'a.', 'Like or pertaining to fossils; contained in rocks, whether\n   petrified or not; as, fossil plants, shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fossil', 'n.', 'A substance dug from the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fossil', 'n.', 'The remains of an animal or plant found in stratified\n   rocks. Most fossils belong to extinct species, but many of the later\n   ones belong to species still living.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fossil', 'n.', 'A person whose views and opinions are extremely antiquated;\n   one whose sympathies are with a former time rather than with the\n   present.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fossiliferous', 'a.', 'Containing or composed of fossils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fossilification', 'n.', 'The process of becoming fossil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fossilism', 'n.', 'The science or state of fossils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fossilism', 'n.', 'The state of being extremely antiquated in views and\n   opinions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fossilist', 'n.', 'One who is versed in the science of fossils; a\n   paleontologist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fossilization', 'n.', 'The process of converting, or of being converted,\n   into a fossil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fossilized', 'imp. & p. p.', 'of Fossilize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fossilizing', 'p. pr. & vb. n.', 'of Fossilize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fossilize', 'v. t.', 'To convert into a fossil; to petrify; as, to\n   fossilize bones or wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fossilize', 'v. t.', 'To cause to become antiquated, rigid, or fixed, as by\n   fossilization; to mummify; to deaden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fossilize', 'v. i.', 'To become fossil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fossilize', 'v. i.', 'To become antiquated, rigid, or fixed, beyond the\n   influence of change or progress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fossilized', 'a.', 'Converted into a fossil; antiquated; firmly fixed in\n   views or opinions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fossores', 'n. pl.', 'A group of hymenopterous insects including the sand\n   wasps. They excavate cells in earth, where they deposit their eggs,\n   with the bodies of other insects for the food of the young when\n   hatched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fossoria', 'n. pl.', 'See Fossores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fossorial', 'a.', 'Fitted for digging, adapted for burrowing or digging;\n   as, a fossorial foot; a fossorial animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fossorious', 'a.', 'Adapted for digging; -- said of the legs of certain\n   insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fossulate', 'a.', 'Having, or surrounded by, long, narrow depressions or\n   furrows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fostered', 'imp. & p. p.', 'of Foster');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fostering', 'p. pr. & vb. n.', 'of Foster');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foster', 'v. t.', 'To feed; to nourish; to support; to bring up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foster', 'v. t.', 'To cherish; to promote the growth of; to encourage; to\n   sustain and promote; as, to foster genius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foster', 'v. i.', 'To be nourished or trained up together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foster', 'v. t.', 'Relating to nourishment; affording, receiving, or\n   sharing nourishment or nurture; -- applied to father, mother, child,\n   brother, etc., to indicate that the person so called stands in the\n   relation of parent, child, brother, etc., as regards sustenance and\n   nurture, but not by tie of blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foster', 'n.', 'A forester.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fosterage', 'n.', 'The care of a foster child; the charge of nursing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foster', 'n.', 'One who, or that which, fosters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fosterling', 'n.', 'A foster child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fosterment', 'n.', 'Food; nourishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fostress', 'n.', 'A woman who feeds and cherishes; a nurse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fother', 'n.', 'A wagonload; a load of any sort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fother', 'n.', 'See Fodder, a unit of weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fothered', 'imp. & p. p.', 'of Fother');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fothering', 'p. pr. & vb. n.', 'of Fother');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fother', 'v. t.', 'To stop (a leak in a ship at sea) by drawing under its\n   bottom a thrummed sail, so that the pressure of the water may force it\n   into the crack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fotive', 'a.', 'Nourishing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fotmal', 'n.', 'Seventy pounds of lead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fougade', 'n.', 'Alt. of Fougasse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fougasse', 'n.', 'A small mine, in the form of a well sunk from the surface\n   of the ground, charged with explosive and projectiles. It is made in a\n   position likely to be occupied by the enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fought', '', 'imp. & p. p. of Fight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foughten', '', 'p. p. of Fight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foul', 'n.', 'A bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foul', 'superl.', 'Covered with, or containing, extraneous matter which is\n   injurious, noxious, offensive, or obstructive; filthy; dirty; not\n   clean; polluted; nasty; defiled; as, a foul cloth; foul hands; a foul\n   chimney; foul air; a ships bottom is foul when overgrown with\n   barnacles; a gun becomes foul from repeated firing; a well is foul with\n   polluted water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foul', 'superl.', 'Scurrilous; obscene or profane; abusive; as, foul words;\n   foul language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foul', 'superl.', 'Hateful; detestable; shameful; odious; wretched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foul', 'superl.', 'Loathsome; disgusting; as, a foul disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foul', 'superl.', 'Ugly; homely; poor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foul', 'superl.', 'Not favorable; unpropitious; not fair or advantageous;\n   as, a foul wind; a foul road; cloudy or rainy; stormy; not fair; --\n   said of the weather, sky, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foul', 'superl.', 'Not conformed to the established rules and customs of a\n   game, conflict, test, etc.; unfair; dishonest; dishonorable; cheating;\n   as, foul play.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foul', 'superl.', 'Having freedom of motion interfered with by collision or\n   entanglement; entangled; -- opposed to clear; as, a rope or cable may\n   get foul while paying it out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fouled', 'imp. & p. p.', 'of Foul');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fouling', 'p. pr. & vb. n.', 'of Foul');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foul', 'v. t.', 'To make filthy; to defile; to daub; to dirty; to soil; as,\n   to foul the face or hands with mire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foul', 'v. t.', 'To incrust (the bore of a gun) with burnt powder in the\n   process of firing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foul', 'v. t.', 'To cover (a ships bottom) with anything that impered its\n   sailing; as, a bottom fouled with barnacles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foul', 'v. t.', 'To entangle, so as to impede motion; as, to foul a rope or\n   cable in paying it out; to come into collision with; as, one boat\n   fouled the other in a race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foul', 'v. i.', 'To become clogged with burnt powder in the process of\n   firing, as a gun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foul', 'v. i.', 'To become entagled, as ropes; to come into collision with\n   something; as, the two boats fouled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foul', 'n.', 'An entanglement; a collision, as in a boat race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foul', 'n.', 'See Foul ball, under Foul, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foulard', 'n.', 'A thin, washable material of silk, or silk and cotton,\n   originally imported from India, but now also made elsewhere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foulder', 'v. i.', 'To flash, as lightning; to lighten; to gleam; to\n   thunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foule', 'adv.', 'Foully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foully', 'v.', 'In a foul manner; filthily; nastily; shamefully; unfairly;\n   dishonorably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foul-mouthed', 'a.', 'Using language scurrilous, opprobrious, obscene, or\n   profane; abusive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foulness', 'n.', 'The quality or condition of being foul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foul-spoken', 'a.', 'Using profane, scurrilous, slanderous, or obscene\n   language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foumart', 'a.', 'The European polecat; -- called also European ferret, and\n   fitchew. See Polecat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Found', '', 'imp. & p. p. of Find.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Founded', 'imp. & p. p.', 'of Found');
  INSERT INTO words (word, word_type, description) VALUES  (    'Founding', 'p. pr. & vb. n.', 'of Found');
  INSERT INTO words (word, word_type, description) VALUES  (    'Found', 'v. t.', 'To form by melting a metal, and pouring it into a mold;\n   to cast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Found', 'n.', 'A thin, single-cut file for combmakers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Founded', 'imp. & p. p.', 'of Found');
  INSERT INTO words (word, word_type, description) VALUES  (    'Founding', 'p. pr. & vb. n.', 'of Found');
  INSERT INTO words (word, word_type, description) VALUES  (    'Found', 'v. i.', 'To lay the basis of; to set, or place, as on something\n   solid, for support; to ground; to establish upon a basis, literal or\n   figurative; to fix firmly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Found', 'v. i.', 'To take the ffirst steps or measures in erecting or\n   building up; to furnish the materials for beginning; to begin to raise;\n   to originate; as, to found a college; to found a family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foundation', 'n.', 'The act of founding, fixing, establishing, or beginning\n   to erect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foundation', 'n.', 'That upon which anything is founded; that on which\n   anything stands, and by which it is supported; the lowest and\n   supporting layer of a superstructure; groundwork; basis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foundation', 'n.', 'The lowest and supporting part or member of a wall,\n   including the base course (see Base course (a), under Base, n.) and\n   footing courses; in a frame house, the whole substructure of masonry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foundation', 'n.', 'A donation or legacy appropriated to support a\n   charitable institution, and constituting a permanent fund; endowment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foundation', 'n.', 'That which is founded, or established by endowment; an\n   endowed institution or charity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foundationer', 'n.', 'One who derives support from the funds or foundation\n   of a college or school.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foundationless', 'a.', 'Having no foundation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Founder', 'n.', 'One who founds, establishes, and erects; one who lays a\n   foundation; an author; one from whom anything originates; one who\n   endows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Founder', 'n.', 'One who founds; one who casts metals in various forms; a\n   caster; as, a founder of cannon, bells, hardware, or types.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foundered', 'imp. & p. p.', 'of Founder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foundering', 'p. pr. & vb. n.', 'of Founder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Founder', 'v. i.', 'To become filled with water, and sink, as a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Founder', 'v. i.', 'To fall; to stumble and go lame, as a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Founder', 'v. i.', 'To fail; to miscarry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Founder', 'v. t.', 'To cause internal inflammation and soreness in the feet\n   or limbs of (a horse), so as to disable or lame him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Founder', 'n.', 'A lameness in the foot of a horse, occasioned by\n   inflammation; closh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Founder', 'n.', 'An inflammatory fever of the body, or acute rheumatism;\n   as, chest founder. See Chest ffounder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Founderous', 'a.', 'Difficult to travel; likely to trip one up; as, a\n   founderous road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foundershaft', 'n.', 'The first shaft sunk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Founderies', 'pl. ', 'of Foundery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foundery', 'n.', 'Same as Foundry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Founding', 'n.', 'The art of smelting and casting metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foundling', 'v. t.', 'A deserted or exposed infant; a child found without a\n   parent or owner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foundress', 'n.', 'A female founder; a woman who founds or establishes, or\n   who endows with a fund.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foundries', 'pl. ', 'of Foundry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foundry', 'n.', 'The act, process, or art of casting metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foundry', 'n.', 'The buildings and works for casting metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fount', 'n.', 'A font.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fount', 'n.', 'A fountain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fountain', 'n.', 'A spring of water issuing from the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fountain', 'n.', 'An artificially produced jet or stream of water; also,\n   the structure or works in which such a jet or stream rises or flows; a\n   basin built and constantly supplied with pure water for drinking and\n   other useful purposes, or for ornament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fountain', 'n.', 'A reservoir or chamber to contain a liquid which can be\n   conducted or drawn off as needed for use; as, the ink fountain in a\n   printing press, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fountain', 'n.', 'The source from which anything proceeds, or from which\n   anything is supplied continuously; origin; source.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fountainless', 'a.', 'Having no fountain; destitute of springs or sources\n   of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fountful', 'a.', 'Full of fountains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Four', 'a.', 'One more than three; twice two.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Four', 'n.', 'The sum of four units; four units or objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Four', 'n.', 'A symbol representing four units, as 4 or iv.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Four', 'n.', 'Four things of the same kind, esp. four horses; as, a chariot\n   and four.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourb', 'n.', 'Alt. of Fourbe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourbe', 'n.', 'A trickly fellow; a cheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourche', 'a.', 'Having the ends forked or branched, and the ends of the\n   branches terminating abruptly as if cut off; -- said of an ordinary,\n   especially of a cross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourchette', 'n.', 'A table fork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourchette', 'n.', 'A small fold of membrane, connecting the labia in the\n   posterior part of the vulva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourchette', 'n.', 'The wishbone or furculum of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourchette', 'n.', 'The frog of the hoof of the horse and allied animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourchette', 'n.', 'An instrument used to raise and support the tongue\n   during the cutting of the fraenum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourchette', 'n.', 'The forked piece between two adjacent fingers, to which\n   the front and back portions are sewed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Four-cornered', 'a.', 'Having four corners or angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourdrinier', 'n.', 'A machine used in making paper; -- so named from an\n   early inventor of improvements in this class of machinery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourfold', 'a. & adv.', 'Four times; quadruple; as, a fourfold division.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourfold', 'n.', 'Four times as many or as much.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourfold', 'v. t.', 'To make four times as much or as many, as an\n   assessment,; to quadruple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourfooted', 'a.', 'Having four feet; quadruped; as, fourfooted beasts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourgon', 'n.', 'An ammunition wagon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourgon', 'n.', 'A French baggage wagon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourhanded', 'a.', 'Having four hands; quadrumanous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourhanded', 'a.', 'Requiring four \"hands\" or players; as, a fourhanded\n   game at cards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourierism', 'n.', 'The cooperative socialistic system of Charles Fourier,\n   a Frenchman, who recommended the reorganization of society into small\n   communities, living in common.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourierist', 'n.', 'Alt. of Fourierite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourierite', 'n.', 'One who adopts the views of Fourier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Four-in-hand', 'a.', 'Consisting of four horses controlled by one person;\n   as, a four-in-hand team; drawn by four horses driven by one person; as,\n   a four-in-hand coach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Four-in-hand', 'n.', 'A team of four horses driven by one person; also, a\n   vehicle drawn by such a team.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourling', 'n.', 'One of four children born at the same time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourling', 'n.', 'A compound or twin crystal consisting of four\n   individuals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourneau', 'n.', 'The chamber of a mine in which the powder is placed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Four-oclock', 'n.', 'A plant of the genus Mirabilis. There are about half\n   a dozen species, natives of the warmer parts of America. The common\n   four-oclock is M. Jalapa. Its flowers are white, yellow, and red, and\n   open toward sunset, or earlier in cloudy weather; hence the name. It is\n   also called marvel of Peru, and afternoon lady.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Four-oclock', 'n.', 'The friar bird; -- so called from its cry, which\n   resembles these words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourpence', 'n.', 'A British silver coin, worth four pence; a groat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourpence', 'n.', 'A name formerly given in New England to the Spanish half\n   real, a silver coin worth six and a quarter cents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Four-poster', 'n.', 'A large bedstead with tall posts at the corners to\n   support curtains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourrier', 'n.', 'A harbinger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourscore', 'n.', 'Four times twenty; eighty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourscore', 'n.', 'The product of four times twenty; eighty units or\n   objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foursquare', 'a.', 'Having four sides and four equal angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourteen', 'a.', 'Four and ten more; twice seven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourteen', 'n.', 'The sum of ten and four; forteen units or objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourteen', 'n.', 'A symbol representing fourteen, as 14 or xiv.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourteenth', 'a.', 'Next in order after the thirteenth; as, the fourteenth\n   day of the month.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourteenth', 'a.', 'Making or constituting one of fourteen equal parts into\n   which anything may be derived.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourteenth', 'n.', 'One of fourteen equal parts into which one whole may be\n   divided; the quotient of a unit divided by fourteen; one next after the\n   thirteenth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourteenth', 'n.', 'The octave of the seventh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourth', 'a.', 'Next in order after the third; the ordinal of four.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourth', 'a.', 'Forming one of four equal parts into which anything may be\n   divided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourth', 'n.', 'One of four equal parts into which one whole may be\n   divided; the quotient of a unit divided by four; one coming next in\n   order after the third.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourth', 'n.', 'The interval of two tones and a semitone, embracing four\n   diatonic degrees of the scale; the subdominant of any key.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fourthly', 'adv.', 'In the fourth place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Four-way', 'a.', 'Allowing passage in either of four directions; as, a\n   four-way cock, or valve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Four-wheeled', 'a.', 'Having four wheels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Four-wheeler', 'n.', 'A vehicle having four wheels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foussa', 'n.', 'A viverrine animal of Madagascar (Cryptoprocta ferox). It\n   resembles a cat in size and form, and has retractile claws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fouter', 'n.', 'A despicable fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foutra', 'n.', 'A fig; -- a word of contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fouty', 'a.', 'Despicable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foveae', 'pl. ', 'of Fovea');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fovea', 'n.', 'A slight depression or pit; a fossa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foveate', 'a.', 'Having pits or depressions; pitted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foveolae', 'pl. ', 'of Foveola');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foveola', 'n.', 'A small depression or pit; a fovea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foveolate', 'a.', 'Having small pits or depression, as the receptacle in\n   some composite flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foveolated', 'a.', 'Foveolate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fovillae', 'pl. ', 'of Fovilla');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fovilla', 'n.', 'One of the fine granules contained in the protoplasm of a\n   pollen grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fowls', 'pl. ', 'of Fowl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fowl', 'n.', 'Any bird; esp., any large edible bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fowl', 'n.', 'Any domesticated bird used as food, as a hen, turkey, duck;\n   in a more restricted sense, the common domestic cock or hen (Gallus\n   domesticus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fowled', 'imp. & p. p.', 'of Fowl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fowling', 'p. pr. & vb. n.', 'of Fowl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fowl', 'v. i.', 'To catch or kill wild fowl, for game or food, as by\n   shooting, or by decoys, nets, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fowler', 'n.', 'A sportsman who pursues wild fowl, or takes or kills for\n   food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fowlerite', 'n.', 'A variety of rhodonite, from Franklin Furnace, New\n   Jersey, containing some zinc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fowlers solution', '', 'An aqueous solution of arsenite of potassium, of\n   such strength that one hundred parts represent one part of arsenious\n   acid, or white arsenic; -- named from Fowler, an English physician who\n   first brought it into use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foxes', 'pl. ', 'of Fox');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fox', 'n.', 'A carnivorous animal of the genus Vulpes, family Canidae, of\n   many species. The European fox (V. vulgaris or V. vulpes), the American\n   red fox (V. fulvus), the American gray fox (V. Virginianus), and the\n   arctic, white, or blue, fox (V. lagopus) are well-known species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fox', 'n.', 'The European dragonet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fox', 'n.', 'The fox shark or thrasher shark; -- called also sea fox. See\n   Thrasher shark, under Shark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fox', 'n.', 'A sly, cunning fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fox', 'n.', 'Rope yarn twisted together, and rubbed with tar; -- used for\n   seizings or mats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fox', 'n.', 'A sword; -- so called from the stamp of a fox on the blade, or\n   perhaps of a wolf taken for a fox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fox', 'n.', 'A tribe of Indians which, with the Sacs, formerly occupied the\n   region about Green Bay, Wisconsin; -- called also Outagamies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foxed', 'imp. & p. p.', 'of Fox');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foxing', 'p. pr. & vb. n.', 'of Fox');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fox', 'n.', 'To intoxicate; to stupefy with drink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fox', 'n.', 'To make sour, as beer, by causing it to ferment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fox', 'n.', 'To repair the feet of, as of boots, with new front upper\n   leather, or to piece the upper fronts of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fox', 'v. i.', 'To turn sour; -- said of beer, etc., when it sours in\n   fermenting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foxearth', 'n.', 'A hole in the earth to which a fox resorts to hide\n   himself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fracas', 'v. t.', 'An uproar; a noisy quarrel; a disturbance; a brawl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fracho', 'n.', 'A shallow iron pan to hold glass ware while being annealed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fracid', 'a.', 'Rotten from being too ripe; overripe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fract', 'v. t.', 'To break; to violate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fracted', 'a.', 'Having a part displaced, as if broken; -- said of an\n   ordinary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foxed', 'a.', 'Discolored or stained; -- said of timber, and also of the\n   paper of books or engravings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foxed', 'a.', 'Repaired by foxing; as, foxed boots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foxery', 'n.', 'Behavior like that of a fox; cunning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foxes', 'n. pl.', 'See Fox, n., 7.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foxfish', 'n.', 'The fox shark; -- called also sea fox. See Thrasher shark,\n   under Shark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foxfish', 'n.', 'The european dragonet. See Dragonet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foxglove', 'n.', 'Any plant of the genus Digitalis. The common English\n   foxglove (Digitalis purpurea) is a handsome perennial or biennial\n   plant, whose leaves are used as a powerful medicine, both as a sedative\n   and diuretic. See Digitalis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foxhound', 'n.', 'One of a special breed of hounds used for chasing foxes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fox-hunting', 'a.', 'Pertaining to or engaged in the hunting of foxes; fond\n   of hunting foxes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foxiness', 'n.', 'The state or quality of being foxy, or foxlike;\n   craftiness; shrewdness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foxiness', 'n.', 'The state of being foxed or discolored, as books; decay;\n   deterioration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foxiness', 'n.', 'A coarse and sour taste in grapes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foxish', 'a.', 'Foxlike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foxlike', 'a.', 'Resembling a fox in his characteristic qualities; cunning;\n   artful; foxy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foxly', 'a.', 'Foxlike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foxship', 'n.', 'Foxiness; craftiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foxtail', 'n.', 'The tail or brush of a fox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foxtail', 'n.', 'The name of several kinds of grass having a soft dense\n   head of flowers, mostly the species of Alopecurus and Setaria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foxtail', 'n.', 'The last cinders obtained in the fining process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foxy', 'a.', 'Like or pertaining to the fox; foxlike in disposition or\n   looks; wily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foxy', 'a.', 'Having the color of a fox; of a yellowish or reddish brown\n   color; -- applied sometimes to paintings when they have too much of\n   this color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foxy', 'a.', 'Having the odor of a fox; rank; strong smeelling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foxy', 'a.', 'Sour; unpleasant in taste; -- said of wine, beer, etc., not\n   properly fermented; -- also of grapes which have the coarse flavor of\n   the fox grape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foy', 'n.', 'Faith; allegiance; fealty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foy', 'n.', 'A feast given by one about to leave a place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foyer', 'n.', 'A lobby in a theater; a greenroom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foyer', 'n.', 'The crucible or basin in a furnace which receives the molten\n   metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foyson', 'n.', 'See Foison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foziness', 'n.', 'The state of being fozy; spiritlessness; dullness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fozy', 'a.', 'Spongy; soft; fat and puffy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fra', 'adv. & prep.', 'Fro.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fra', 'n.', 'Brother; -- a title of a monk of friar; as, Fra Angelo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frab', 'v. i. & t.', 'To scold; to nag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frabbit', 'a.', 'Crabbed; peevish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraction', 'n.', 'The act of breaking, or state of being broken, especially\n   by violence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraction', 'n.', 'A portion; a fragment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraction', 'n.', 'One or more aliquot parts of a unit or whole number; an\n   expression for a definite portion of a unit or magnitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraction', 'v. t.', 'To separate by means of, or to subject to, fractional\n   distillation or crystallization; to fractionate; -- frequently used\n   with out; as, to fraction out a certain grade of oil from pretroleum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fractional', 'a.', 'Of or pertaining to fractions or a fraction;\n   constituting a fraction; as, fractional numbers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fractional', 'a.', 'Relatively small; inconsiderable; insignificant; as, a\n   fractional part of the population.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fractionally', 'adv.', 'By fractions or separate portions; as, to distill a\n   liquid fractionally, that is, so as to separate different portions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fractionary', 'a.', 'Fractional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fractionate', 'v. t.', 'To separate into different portions or fractions,\n   as in the distillation of liquids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fractious', 'a.', 'Apt to break out into a passion; apt to scold; cross;\n   snappish; ugly; unruly; as, a fractious man; a fractious horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fractural', 'a.', 'Pertaining to, or consequent on, a fracture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fracture', 'n.', 'The act of breaking or snapping asunder; rupture; breach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fracture', 'n.', 'The breaking of a bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fracture', 'n.', 'The texture of a freshly broken surface; as, a compact\n   fracture; an even, hackly, or conchoidal fracture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fractured', 'imp. & p. p.', 'of Fracture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fracturing', 'p. pr. & vb. n.', 'of Fracture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fracture', 'v. t.', 'To cause a fracture or fractures in; to break; to\n   burst asunder; to crack; to separate the continuous parts of; as, to\n   fracture a bone; to fracture the skull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraenula', 'pl. ', 'of Fraenulum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraenulum', 'n.', 'A fraenum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraenums', 'pl. ', 'of Frenum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraena', 'pl. ', 'of Frenum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraenum', 'n.', 'Alt. of Frenum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frenum', 'n.', 'A connecting fold of membrane serving to support or\n   restrain any part; as, the fraenum of the tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fragile', 'a.', 'Easily broken; brittle; frail; delicate; easily destroyed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fragility', 'n.', 'The condition or quality of being fragile; brittleness;\n   frangibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fragility', 'n.', 'Weakness; feebleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fragility', 'n.', 'Liability to error and sin; frailty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fragment', 'v. t.', 'A part broken off; a small, detached portion; an\n   imperfect part; as, a fragment of an ancient writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fragmentak', 'a.', 'Fragmentary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fragmentak', 'a.', 'Consisting of the pulverized or fragmentary material of\n   rock, as conglomerate, shale, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fragmental', 'n.', 'A fragmentary rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fragmentarily', 'adv.', 'In a fragmentary manner; piecemeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fragmentariness', 'n.', 'The quality or property of being in fragnebts, or\n   broken pieces, incompleteness; want of continuity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fragmentary', 'a.', 'Composed of fragments, or broken pieces; disconnected;\n   not complete or entire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fragmentary', 'a.', 'Composed of the fragments of other rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fragmented', 'a.', 'Broken into fragments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fragmentist', 'n.', 'A writer of fragments; as, the fragmentist of\n   Wolfenbuttel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fragor', 'n.', 'A loud and sudden sound; the report of anything bursting; a\n   crash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fragor', 'n.', 'A strong or sweet scent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fragrance', 'n.', 'Alt. of Fragrancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fragrancy', 'n.', 'The quality of being fragrant; sweetness of smell; a\n   sweet smell; a pleasing odor; perfume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fragrant', 'a.', 'Affecting the olfactory nerves agreeably; sweet of smell;\n   odorous; having or emitting an agreeable perfume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraight', 'a.', 'Same as Fraught.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frail', 'n.', 'A basket made of rushes, used chiefly for containing figs\n   and raisins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frail', 'n.', 'The quantity of raisins -- about thirty-two, fifty-six, or\n   seventy-five pounds, -- contained in a frail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frail', 'n.', 'A rush for weaving baskets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frail', 'superl', 'Easily broken; fragile; not firm or durable; liable to\n   fail and perish; easily destroyed; not tenacious of life; weak; infirm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frail', 'superl', 'Tender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frail', 'superl', 'Liable to fall from virtue or be led into sin; not\n   strong against temptation; weak in resolution; also, unchaste; -- often\n   applied to fallen women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frailly', 'adv.', 'Weakly; infirmly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frailness', 'n.', 'Frailty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frailties', 'pl. ', 'of Frailty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frailty', 'a.', 'The condition quality of being frail, physically,\n   mentally, or morally, frailness; infirmity; weakness of resolution;\n   liableness to be deceived or seduced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frailty', 'a.', 'A fault proceeding from weakness; foible; sin of\n   infirmity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraischeur', 'a.', 'Freshness; coolness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraise', 'n.', 'A large and thick pancake, with slices of bacon in it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraise', 'n.', 'A defense consisting of pointed stakes driven into the\n   ramparts in a horizontal or inclined position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraise', 'n.', 'A fluted reamer for enlarging holes in stone; a small\n   milling cutter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraise', 'v. t.', 'To protect, as a line of troops, against an onset of\n   cavalry, by opposing bayonets raised obliquely forward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraised', 'a.', 'Fortified with a fraise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraken', 'n.', 'A freckle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Framable', 'a.', 'Capable of being framed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frambaesia', 'n.', 'The yaws. See Yaws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Framed', 'imp. & p. p.', 'of Frame');
  INSERT INTO words (word, word_type, description) VALUES  (    'Framing', 'p. pr. & vb. n.', 'of Frame');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frame', 'v. t.', 'To construct by fitting and uniting the several parts of\n   the skeleton of any structure; specifically, in woodwork, to put\n   together by cutting parts of one member to fit parts of another. See\n   Dovetail, Halve, v. t., Miter, Tenon, Tooth, Tusk, Scarf, and Splice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frame', 'v. t.', 'To originate; to plan; to devise; to contrive; to\n   compose; in a bad sense, to invent or fabricate, as something false.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frame', 'v. t.', 'To fit to something else, or for some specific end; to\n   adjust; to regulate; to shape; to conform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frame', 'v. t.', 'To cause; to bring about; to produce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frame', 'v. t.', 'To support.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frame', 'v. t.', 'To provide with a frame, as a picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frame', 'v. i.', 'To shape; to arrange, as the organs of speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frame', 'v. i.', 'To proceed; to go.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frame', 'n.', 'Anything composed of parts fitted and united together; a\n   fabric; a structure; esp., the constructional system, whether of timber\n   or metal, that gives to a building, vessel, etc., its model and\n   strength; the skeleton of a structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frame', 'n.', 'The bodily structure; physical constitution; make or build\n   of a person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frame', 'n.', 'A kind of open case or structure made for admitting,\n   inclosing, or supporting things, as that which incloses or contains a\n   window, door, picture, etc.; that on which anything is held or\n   stretched');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frame', 'n.', 'The skeleton structure which supports the boiler and\n   machinery of a locomotive upon its wheels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frame', 'n.', 'A molding box or flask, which being filled with sand serves\n   as a mold for castings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frame', 'n.', 'The ribs and stretchers of an umbrella or other structure\n   with a fabric covering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frame', 'n.', 'A structure of four bars, adjustable in size, on which\n   cloth, etc., is stretched for quilting, embroidery, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frame', 'n.', 'A glazed portable structure for protecting young plants from\n   frost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frame', 'n.', 'A stand to support the type cases for use by the compositor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frame', 'n.', 'A term applied, especially in England, to certain machines\n   built upon or within framework; as, a stocking frame; lace frame;\n   spinning frame, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frame', 'n.', 'Form; shape; proportion; scheme; structure; constitution;\n   system; as, a frameof government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frame', 'n.', 'Particular state or disposition, as of the mind; humor;\n   temper; mood; as, to be always in a happy frame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frame', 'n.', 'Contrivance; the act of devising or scheming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Framer', 'n.', 'One who frames; as, the framer of a building; the framers\n   of the Constitution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Framework', 'n.', 'The work of framing, or the completed work; the frame or\n   constructional part of anything; as, the framework of society.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Framework', 'n.', 'Work done in, or by means of, a frame or loom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Framing', 'n.', 'The act, process, or style of putting together a frame, or\n   of constructing anything; a frame; that which frames.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Framing', 'n.', 'A framework, or a sy/ of frames.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frampel', 'a.', 'Alt. of Frampoid');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frampoid', 'a.', 'Peevish; cross; vexatious; quarrelsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Franc', 'a.', 'A silver coin of France, and since 1795 the unit of the\n   French monetary system. It has been adopted by Belgium and Swizerland.\n   It is equivalent to about nineteen cents, or ten pence, and is divided\n   into 100 centimes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Franchise', 'a.', 'Exemption from constraint or oppression; freedom;\n   liberty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Franchise', 'a.', 'A particular privilege conferred by grant from a\n   sovereign or a government, and vested in individuals; an imunity or\n   exemption from ordinary jurisdiction; a constitutional or statutory\n   right or privilege, esp. the right to vote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Franchise', 'a.', 'The district or jurisdiction to which a particular\n   privilege extends; the limits of an immunity; hence, an asylum or\n   sanctuary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Franchise', 'a.', 'Magnanimity; generosity; liberality; frankness;\n   nobility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Franchised', 'imp. & p. p.', 'of Franchise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Franchising', 'p. pr. & vb. n.', 'of Franchise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Franchise', 'v. t.', 'To make free; to enfranchise; to give liberty to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Franchisement', 'n.', 'Release; deliverance; freedom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Francic', 'a.', 'Pertaining to the Franks, or their language; Frankish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Franciscan', 'a.', 'Belonging to the Order of St. Francis of the\n   Franciscans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Franciscan', 'n.', 'A monk or friar of the Order of St. Francis, a large\n   and zealous order of mendicant monks founded in 1209 by St. Francis of\n   Assisi. They are called also Friars Minor; and in England, Gray Friars,\n   because they wear a gray habit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Francolin', 'n.', 'A spurred partidge of the genus Francolinus and allied\n   genera, of Asia and Africa. The common species (F. vulgaris) was\n   formerly common in southern Europe, but is now nearly restricted to\n   Asia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Francolite', 'n.', 'A variety of apatite from Wheal Franco in Devonshire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frangent', 'a.', 'Causing fracture; breaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frangibility', 'n.', 'The state or quality of being frangible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frangible', 'a.', 'Capable of being broken; brittle; fragile; easily\n   broken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frangipane', 'n.', 'A perfume of jasmine; frangipani.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frangipane', 'n.', 'A species of pastry, containing cream and almonds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frangipani', 'n.', 'Alt. of Frangipanni');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frangipanni', 'n.', 'A perfume derived from, or imitating the odor of, the\n   flower of the red jasmine, a West Indian tree of the genus Plumeria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frangulic', 'a.', 'Alt. of Frangulinic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frangulinic', 'a.', 'Pertaining to, or drived from, frangulin, or a species\n   (Rhamnus Frangula) of the buckthorn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frangulin', 'n.', 'A yellow crystalline dyestuff, regarded as a glucoside,\n   extracted from a species (Rhamnus Frangula) of the buckthorn; -- called\n   also rhamnoxanthin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Franion', 'n.', 'A paramour; a loose woman; also, a gay, idle fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frank', 'n.', 'A pigsty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frank', 'v. t.', 'To shut up in a frank or sty; to pen up; hence, to cram;\n   to fatten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frank', 'n.', 'The common heron; -- so called from its note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frank', 'n.', 'Unbounded by restrictions, limitations, etc.; free.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frank', 'n.', 'Free in uttering ones real sentiments; not reserved; using\n   no disguise; candid; ingenuous; as, a frank nature, conversation,\n   manner, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frank', 'n.', 'Liberal; generous; profuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frank', 'n.', 'Unrestrained; loose; licentious; -- used in a bad sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Franked', 'imp. & p. p.', 'of Frank');
  INSERT INTO words (word, word_type, description) VALUES  (    'Franking', 'p. pr. & vb. n.', 'of Frank');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frank', 'v. t.', 'To send by public conveyance free of expense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frank', 'v. t.', 'To extempt from charge for postage, as a letter, package,\n   or packet, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frank', 'a.', 'The privilege of sending letters or other mail matter, free\n   of postage, or without charge; also, the sign, mark, or signature\n   denoting that a letter or other mail matter is to free of postage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frank', 'a.', 'A member of one of the German tribes that in the fifth\n   century overran and conquered Gaul, and established the kingdom of\n   France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frank', 'a.', 'A native or inhabitant of Western Europe; a European; -- a\n   term used in the Levant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frank', 'a.', 'A French coin. See Franc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frankalmoigne', 'a.', 'A tenure by which a religious corporation holds\n   lands given to them and their successors forever, usually on condition\n   of praying for the soul of the donor and his heirs; -- called also\n   tenure by free alms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frank-chase', 'n.', 'The liberty or franchise of having a chase; free\n   chase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frank-fee', 'n.', 'A species of tenure in fee simple, being the opposite of\n   ancient demesne, or copyhold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frankfort black', '', 'A black pigment used in copperplate printing,\n   prepared by burning vine twigs, the lees of wine, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frankincense', 'n.', 'A fragrant, aromatic resin, or gum resin, burned as\n   an incense in religious rites or for medicinal fumigation. The best\n   kinds now come from East Indian trees, of the genus Boswellia; a\n   commoner sort, from the Norway spruce (Abies excelsa) and other\n   coniferous trees. The frankincense of the ancient Jews is still\n   unidentified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Franking', 'n.', 'A method of forming a joint at the intersection of\n   window-sash bars, by cutting away only enough wood to show a miter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frankish', 'a.', 'Like, or pertaining to, the Franks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frank-law', 'n.', 'The liberty of being sworn in courts, as a juror or\n   witness; one of the ancient privileges of a freeman; free and common\n   law; -- an obsolete expression signifying substantially the same as the\n   American expression civil rights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Franklin', 'a.', 'An English freeholder, or substantial householder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Franklinic', 'a.', 'Of or pertaining to Benjamin Franklin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Franklinite', 'n.', 'A kind of mineral of the spinel group.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Franklin stove', '', 'A kind of open stove introduced by Benjamin Franklin,\n   the peculiar feature of which was that a current of heated air was\n   directly supplied to the room from an air box; -- now applied to other\n   varieties of open stoves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frankly', 'adv.', 'In a frank manner; freely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frank-marriage', 'n.', 'A certain tenure in tail special; an estate of\n   inheritance given to a man his wife (the wife being of the blood of the\n   donor), and descendible to the heirs of their two bodies begotten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frankness', 'n.', 'The quality of being frank; candor; openess;\n   ingenuousness; fairness; liberality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frankpledge', 'n.', 'A pledge or surety for the good behavior of freemen,\n   -- each freeman who was a member of an ancient decennary, tithing, or\n   friborg, in England, being a pledge for the good conduct of the others,\n   for the preservation of the public peace; a free surety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frankpledge', 'n.', 'The tithing itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frantic', 'a.', 'Mad; raving; furious; violent; wild and disorderly;\n   distracted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frapped', 'imp. & p. p.', 'of Frap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frapping', 'p. pr. & vb. n.', 'of Frap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frap', 'v. t.', 'To draw together; to bind with a view to secure and\n   strengthen, as a vessel by passing cables around it; to tighten; as a\n   tackle by drawing the lines together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frap', 'v. t.', 'To brace by drawing together, as the cords of a drum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frape', 'n.', 'A crowd, a rabble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frapler', 'n.', 'A blusterer; a rowdy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frater', 'n.', 'A monk; also, a frater house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraternal', 'a.', 'Pf, pertaining to, or involving, brethren; becoming to\n   brothers; brotherly; as, fraternal affection; a fraternal embrace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraternate', 'v. i.', 'To fraternize; to hold fellowship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraternation', 'n.', 'Alt. of Fraternism');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraternism', 'n.', 'Fraternization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraternities', 'pl. ', 'of Fraternity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraternity', 'n.', 'The state or quality of being fraternal or brotherly;\n   brotherhood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraternity', 'n.', 'A body of men associated for their common interest,\n   business, or pleasure; a company; a brotherhood; a society; in the\n   Roman Catholic Chucrch, an association for special religious purposes,\n   for relieving the sick and destitute, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraternity', 'n.', 'Men of the same class, profession, occupation,\n   character, or tastes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraternization', 'n.', 'The act of fraternizing or uniting as brothers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraternized', 'imp. & p. p.', 'of Fraternize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraternizing', 'p. pr. & vb. n.', 'of Fraternize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraternize', 'v. i.', 'To associate or hold fellowship as brothers, or as\n   men of like occupation or character; to have brotherly feelings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraternize', 'v. t.', 'To bring into fellowship or brotherly sympathy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraternizer', 'n.', 'One who fraternizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fratery', 'n.', 'A frater house. See under Frater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fratrage', 'n.', 'A sharing among brothers, or brothers kin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fratricelli', 'n. pl.', 'The name which St. Francis of Assisi gave to his\n   followers, early in the 13th century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fratricelli', 'n. pl.', 'A sect which seceded from the Franciscan Order,\n   chiefly in Italy and Sicily, in 1294, repudiating the pope as an\n   apostate, maintaining the duty of celibacy and poverty, and\n   discountenancing oaths. Called also Fratricellians and Fraticelli.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fratricidal', 'a.', 'Of or pertaining to fratricide; of the nature of\n   fratricide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fratricide', 'n.', 'The act of one who murders or kills his own brother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fratricide', 'n.', 'One who murders or kills his own brother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraud', 'n.', 'Deception deliberately practiced with a view to gaining an\n   unlawful or unfair advantage; artifice by which the right or interest\n   of another is injured; injurious stratagem; deceit; trick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraud', 'n.', 'An intentional perversion of truth for the purpose of\n   obtaining some valuable thing or promise from another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraud', 'n.', 'A trap or snare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraudful', 'a.', 'Full of fraud, deceit, or treachery; trickish;\n   treacherous; fraudulent; -- applied to persons or things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraudless', 'a.', 'Free from fraud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraudulence', 'n.', 'Alt. of Fraudulency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraudulency', 'n.', 'The quality of being fraudulent; deliberate deceit;\n   trickishness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraudulent', 'a.', 'Using fraud; trickly; deceitful; dishonest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraudulent', 'a.', 'Characterized by,, founded on, or proceeding from,\n   fraund; as, a fraudulent bargain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraudulent', 'a.', 'Obtained or performed by artifice; as, fraudulent\n   conquest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraudulently', 'adv.', 'In a fraudulent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraught', 'n.', 'A freight; a cargo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraught', 'a.', 'Freighted; laden; filled; stored; charged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraughted', 'imp. & p. p.', 'of Fraught');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraught', '', 'of Fraught');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraughting', 'p. pr. & vb. n.', 'of Fraught');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraught', 'n.', 'To freight; to load; to burden; to fill; to crowd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraughtage', 'n.', 'Freight; loading; cargo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraughting', 'a.', 'Constituting the freight or cargo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraunhofer lines', '', 'The lines of the spectrun; especially and properly,\n   the dark lines of the solar spectrum, so called because first\n   accurately observed and interpreted by Fraunhofer, a German physicist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraxin', 'n.', 'A colorless crystalline substance, regarded as a glucoside,\n   and found in the bark of the ash (Fraxinus) and along with esculin in\n   the bark of the horse-chestnut. It shows a delicate fluorescence in\n   alkaline solutions; -- called also paviin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraxinus', 'n.', 'A genus of deciduous forest trees, found in the north\n   temperate zone, and including the true ash trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fray', 'n.', 'Affray; broil; contest; combat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frayed', 'imp. & p. p.', 'of Fray');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraying', 'p. pr. & vb. n.', 'of Fray');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fray', 'v. t.', 'To frighten; to terrify; to alarm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fray', 'v. t.', 'To bear the expense of; to defray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fray', 'v. t.', 'To rub; to wear off, or wear into shreds, by rubbing; to\n   fret, as cloth; as, a deer is said to fray her head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fray', 'v. i.', 'To rub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fray', 'v. i.', 'To wear out or into shreads, or to suffer injury by\n   rubbing, as when the threads of the warp or of the woof wear off so\n   that the cross threads are loose; to ravel; as, the cloth frays badly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fray', 'n.', 'A fret or chafe, as in cloth; a place injured by rubbing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fraying', 'n.', 'The skin which a deer frays from his horns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freaked', 'imp. & p. p.', 'of Freak');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freaking', 'p. pr. & vb. n.', 'of Freak');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freak', 'v. t.', 'To variegate; to checker; to streak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freak', 'n.', 'A sudden causeless change or turn of the mind; a whim of\n   fancy; a capricious prank; a vagary or caprice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freaking', 'a.', 'Freakish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freakish', 'a.', 'Apt to change the mind suddenly; whimsical; capricious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freck', 'v. t.', 'To checker; to diversify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freckle', 'v. t.', 'A small yellowish or brownish spot in the skin,\n   particularly on the face, neck, or hands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freckle', 'v. t.', 'Any small spot or discoloration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freckled', 'imp. & p. p.', 'of Freckle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freckling', 'p. pr. & vb. n.', 'of Freckle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freckle', 'v. t.', 'To spinkle or mark with freckle or small discolored\n   spots; to spot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freckle', 'v. i.', 'To become covered or marked with freckles; to be\n   spotted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freckled', 'a.', 'Marked with freckles; spotted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freckledness', 'n.', 'The state of being freckled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freckly', 'a.', 'Full of or marked with freckles; sprinkled with spots;\n   freckled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fred', 'n.', 'Peace; -- a word used in composition, especially in proper\n   names; as, Alfred; Frederic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fredstole', 'n.', 'See Fridstol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free', 'superl.', 'Exempt from subjection to the will of others; not under\n   restraint, control, or compulsion; able to follow ones own impulses,\n   desires, or inclinations; determining ones own course of action; not\n   dependent; at liberty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free', 'superl.', 'Not under an arbitrary or despotic government; subject\n   only to fixed laws regularly and fairly administered, and defended by\n   them from encroachments upon natural or acquired rights; enjoying\n   political liberty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free', 'superl.', 'Liberated, by arriving at a certain age, from the\n   control of parents, guardian, or master.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free', 'superl.', 'Not confined or imprisoned; released from arrest;\n   liberated; at liberty to go.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free', 'superl.', 'Not subjected to the laws of physical necessity; capable\n   of voluntary activity; endowed with moral liberty; -- said of the will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free', 'superl.', 'Clear of offense or crime; guiltless; innocent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free', 'superl.', 'Unconstrained by timidity or distrust; unreserved;\n   ingenuous; frank; familiar; communicative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free', 'superl.', 'Unrestrained; immoderate; lavish; licentious; -- used in\n   a bad sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free', 'superl.', 'Not close or parsimonious; liberal; open-handed; lavish;\n   as, free with his money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free', 'superl.', 'Exempt; clear; released; liberated; not encumbered or\n   troubled with; as, free from pain; free from a burden; -- followed by\n   from, or, rarely, by of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free', 'superl.', 'Characteristic of one acting without restraint;\n   charming; easy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free', 'superl.', 'Ready; eager; acting without spurring or whipping;\n   spirited; as, a free horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free', 'superl.', 'Invested with a particular freedom or franchise;\n   enjoying certain immunities or privileges; admitted to special rights;\n   -- followed by of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free', 'superl.', 'Thrown open, or made accessible, to all; to be enjoyed\n   without limitations; unrestricted; not obstructed, engrossed, or\n   appropriated; open; -- said of a thing to be possessed or enjoyed; as,\n   a free school.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free', 'superl.', 'Not gained by importunity or purchase; gratuitous;\n   spontaneous; as, free admission; a free gift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free', 'superl.', 'Not arbitrary or despotic; assuring liberty; defending\n   individual rights against encroachment by any person or class;\n   instituted by a free people; -- said of a government, institutions,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free', 'superl.', 'Certain or honorable; the opposite of base; as, free\n   service; free socage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free', 'superl.', 'Privileged or individual; the opposite of common; as, a\n   free fishery; a free warren.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free', 'superl.', 'Not united or combined with anything else; separated;\n   dissevered; unattached; at liberty to escape; as, free carbonic acid\n   gas; free cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free', 'adv.', 'Freely; willingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free', 'adv.', 'Without charge; as, children admitted free.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freed', 'imp. & p. p.', 'of Free');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freeing', 'p. pr. & vb. n.', 'of Free');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free', 'a.', 'To make free; to set at liberty; to rid of that which\n   confines, limits, embarrasses, oppresses, etc.; to release; to\n   disengage; to clear; -- followed by from, and sometimes by off; as, to\n   free a captive or a slave; to be freed of these inconveniences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free', 'a.', 'To remove, as something that confines or bars; to relieve\n   from the constraint of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free', 'a.', 'To frank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freebooter', 'n.', 'One who plunders or pillages without the authority of\n   national warfare; a member of a predatory band; a pillager; a\n   buccaneer; a sea robber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freebootery', 'n.', 'The act, practice, or gains of a freebooter;\n   freebooting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freebooting', 'n.', 'Robbery; plunder; a pillaging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freebooting', 'a.', 'Acting the freebooter; practicing freebootery;\n   robbing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freebooty', 'n.', 'Freebootery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freeborn', 'a.', 'Born free; not born in vassalage; inheriting freedom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free-denizen', 'v. t.', 'To make free.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freedmen', 'pl. ', 'of Freedman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freedman', 'n.', 'A man who has been a slave, and has been set free.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freedom', 'n.', 'The state of being free; exemption from the power and\n   control of another; liberty; independence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freedom', 'n.', 'Privileges; franchises; immunities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freedom', 'n.', 'Exemption from necessity, in choise and action; as, the\n   freedom of the will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freedom', 'n.', 'Ease; facility; as, he speaks or acts with freedom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freedom', 'n.', 'Frankness; openness; unreservedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freedom', 'n.', 'Improper familiarity; violation of the rules of decorum;\n   license.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freedom', 'n.', 'Generosity; liberality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freedstool', 'n.', 'See Fridstol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free-hand', 'a.', 'Done by the hand, without support, or the guidance of\n   instruments; as, free-hand drawing. See under Drawing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free-handed', 'a.', 'Open-handed; liberal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free-hearted', 'a.', 'Open; frank; unreserved; liberal; generous; as,\n   free-hearted mirth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freehold', 'n.', 'An estate in real property, of inheritance (in fee simple\n   or fee tail) or for life; or the tenure by which such estate is held.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freeholder', 'n.', 'The possessor of a freehold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free-liver', 'n.', 'One who gratifies his appetites without stint; one\n   given to indulgence in eating and drinking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free-living', 'n.', 'Unrestrained indulgence of the appetites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free-love', 'n.', 'The doctrine or practice of consorting with the opposite\n   sex, at pleasure, without marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free-lover', 'n.', 'One who believes in or practices free-love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freelte', 'n.', 'Frailty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freely', 'adv.', 'In a free manner; without restraint or compulsion;\n   abundantly; gratuitously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freemen', 'pl. ', 'of Freeman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freeman', 'n.', 'One who enjoys liberty, or who is not subject to the will\n   of another; one not a slave or vassal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freeman', 'n.', 'A member of a corporation, company, or city, possessing\n   certain privileges; a member of a borough, town, or State, who has the\n   right to vote at elections. See Liveryman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free-martin', 'n.', 'An imperfect female calf, twinborn with a male.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freemason', 'n.', 'One of an ancient and secret association or fraternity,\n   said to have been at first composed of masons or builders in stone, but\n   now consisting of persons who are united for social enjoyment and\n   mutual assistance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freemasonic', 'a.', 'Pertaining to, or resembling, the institutions or the\n   practices of freemasons; as, a freemasonic signal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freemasonry', 'n.', 'The institutions or the practices of freemasons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free-milling', 'a.', 'Yielding free gold or silver; -- said of certain ores\n   which can be reduced by crushing and amalgamation, without roasting or\n   other chemical treatment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free-minded', 'a.', 'Not perplexed; having a mind free from care.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freeness', 'n.', 'The state or quality of being free; freedom; liberty;\n   openness; liberality; gratuitousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freer', 'n.', 'One who frees, or sets free.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free-soil', 'a.', 'Pertaining to, or advocating, the non-extension of\n   slavery; -- esp. applied to a party which was active during the period\n   1846-1856.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free-spoken', 'a.', 'Accustomed to speak without reserve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freestone', 'n.', 'A stone composed of sand or grit; -- so called because\n   it is easily cut or wrought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freestone', 'a.', 'Having the flesh readily separating from the stone, as\n   in certain kinds of peaches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free-swimming', 'a.', 'Swimming in the open sea; -- said of certain marine\n   animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freethinker', 'n.', 'One who speculates or forms opinions independently of\n   the authority of others; esp., in the sphere or religion, one who forms\n   opinions independently of the authority of revelation or of the church;\n   an unbeliever; -- a term assumed by deists and skeptics in the\n   eighteenth century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freethinking', 'n.', 'Undue boldness of speculation; unbelief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freethinking', 'a.', 'Exhibiting undue boldness of speculation; skeptical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free-tongued', 'a.', 'Speaking without reserve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free will', '', 'A will free from improper coercion or restraint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Free will', '', 'The power asserted of moral beings of willing or choosing\n   without the restraints of physical or absolute necessity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freewill', 'a.', 'Of or pertaining to free will; voluntary; spontaneous;\n   as, a freewill offering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freezable', 'a.', 'Capable of being frozen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freeze', 'n.', 'A frieze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Froze', 'imp.', 'of Freeze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frozen', 'p. p.', 'of Freeze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freezing', 'p. pr. & vb. n.', 'of Freeze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freeze', 'v. i.', 'To become congealed by cold; to be changed from a liquid\n   to a solid state by the abstraction of heat; to be hardened into ice or\n   a like solid body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freeze', 'v. i.', 'To become chilled with cold, or as with cold; to suffer\n   loss of animation or life by lack of heat; as, the blood freezes in the\n   veins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freeze', 'v. t.', 'To congeal; to harden into ice; to convert from a fluid\n   to a solid form by cold, or abstraction of heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freeze', 'v. t.', 'To cause loss of animation or life in, from lack of\n   heat; to give the sensation of cold to; to chill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freeze', 'n.', 'The act of congealing, or the state of being congealed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freezer', 'n.', 'One who, or that which, cools or freezes, as a\n   refrigerator, or the tub and can used in the process of freezing ice\n   cream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freezing', 'a.', 'Tending to freeze; for freezing; hence, cold or distant\n   in manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freieslebenite', 'n.', 'A sulphide of antimony, lead, and silver, occuring\n   in monoclinic crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freight', 'n.', 'That with which anything in fraught or laden for\n   transportation; lading; cargo, especially of a ship, or a car on a\n   railroad, etc.; as, a freight of cotton; a full freight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freight', 'n.', 'The sum paid by a party hiring a ship or part of a ship\n   for the use of what is thus hired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freight', 'n.', 'The price paid a common carrier for the carriage of goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freight', 'n.', 'Freight transportation, or freight line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freight', 'a.', 'Employed in the transportation of freight; having to do\n   with freight; as, a freight car.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freighted', 'imp. & p. p.', 'of Freight');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freighting', 'p. pr. & vb. n.', 'of Freight');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freight', 'v. t.', 'To load with goods, as a ship, or vehicle of any kind,\n   for transporting them from one place to another; to furnish with\n   freight; as, to freight a ship; to freight a car.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freightage', 'n.', 'Charge for transportation; expense of carriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freightage', 'n.', 'The transportation of freight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freightage', 'n.', 'Freight; cargo; lading. Milton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freighter', 'n.', 'One who loads a ship, or one who charters and loads a\n   ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freighter', 'n.', 'One employed in receiving and forwarding freight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freighter', 'n.', 'One for whom freight is transported.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freighter', 'n.', 'A vessel used mainly to carry freight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freightless', 'a.', 'Destitute of freight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frelte', 'n.', 'Frailty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fremd', 'a.', 'Alt. of Fremed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fremed', 'a.', 'Strange; foreign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fren', 'a.', 'A stranger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'French', 'a.', 'Of or pertaining to France or its inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'French', 'n.', 'The language spoken in France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'French', 'n.', 'Collectively, the people of France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frenchified', 'imp. & p. p.', 'of Frenchify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frenchifying', 'p. pr. & vb. n.', 'of Frenchify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frenchify', 'v. t.', 'To make French; to infect or imbue with the manners\n   or tastes of the French; to Gallicize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frenchism', 'n.', 'A French mode or characteristic; an idiom peculiar to\n   the French language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frenchmen', 'pl. ', 'of Frenchman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frenchman', 'n.', 'A native or one of the people of France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frenetir', 'a.', 'Distracted; mad; frantic; phrenetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frenetical', 'a.', 'Frenetic; frantic; frenzied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frenums', 'pl. ', 'of Frenum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frena', 'pl. ', 'of Frenum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frenum', 'n.', 'A cheek stripe of color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frenum', 'n.', 'Same as Fraenum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frenzical', 'a.', 'Frantic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frenzied', 'p. p. & a.', 'Affected with frenzy; frantic; maddened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frenzies', 'pl. ', 'of Frenzy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frenzy', 'n.', 'Any violent agitation of the mind approaching to\n   distraction; violent and temporary derangement of the mental faculties;\n   madness; rage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frenzy', 'a.', 'Mad; frantic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frenzy', 'v. t.', 'To affect with frenzy; to drive to madness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frequence', 'n.', 'A crowd; a throng; a concourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frequence', 'n.', 'Frequency; abundance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frequencies', 'pl. ', 'of Frequency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frequency', 'n.', 'The condition of returning frequently; occurrence often\n   repeated; common occurence; as, the frequency of crimes; the frequency\n   of miracles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frequency', 'n.', 'A crowd; a throng.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frequent', 'n.', 'Often to be met with; happening at short intervals; often\n   repeated or occurring; as, frequent visits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frequent', 'n.', 'Addicted to any course of conduct; inclined to indulge in\n   any practice; habitual; persistent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frequent', 'n.', 'Full; crowded; thronged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frequent', 'n.', 'Often or commonly reported.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frequented', 'imp. & p. p.', 'of Frequent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frequenting', 'p. pr. & vb. n.', 'of Frequent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frequent', 'a.', 'To visit often; to resort to often or habitually.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frequent', 'a.', 'To make full; to fill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frequentable', 'a.', 'Accessible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frequentage', 'n.', 'The practice or habit of frequenting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frequentation', 'n.', 'The act or habit of frequenting or visiting often;\n   resort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frequentative', 'a.', 'Serving to express the frequent repetition of an\n   action; as, a frequentative verb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frequentative', 'n.', 'A frequentative verb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frequenter', 'n.', 'One who frequents; one who often visits, or resorts to\n   customarily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frequently', 'adv.', 'At frequent or short intervals; many times; often;\n   repeatedly; commonly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frequentness', 'n.', 'The quality of being frequent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frere', 'n.', 'A friar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frescade', 'a.', 'A cool walk; shady place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frescoes', 'pl. ', 'of Fresco');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frescos', 'pl. ', 'of Fresco');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fresco', 'a.', 'A cool, refreshing state of the air; duskiness; coolness;\n   shade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fresco', 'a.', 'The art of painting on freshly spread plaster, before it\n   dries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fresco', 'a.', 'In modern parlance, incorrectly applied to painting on\n   plaster in any manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fresco', 'a.', 'A painting on plaster in either of senses a and b.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frescoed', 'imp. & p. p.', 'of Fresco');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frescoing', 'p. pr. & vb. n.', 'of Fresco');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fresco', 'v. t.', 'To paint in fresco, as walls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fresh', 'superl', 'Possessed of original life and vigor; new and strong;\n   unimpaired; sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fresh', 'superl', 'New; original; additional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fresh', 'superl', 'Lately produced, gathered, or prepared for market; not\n   stale; not dried or preserved; not wilted, faded, or tainted; in good\n   condition; as, fresh vegetables, flowers, eggs, meat, fruit, etc.;\n   recently made or obtained; occurring again; repeated; as, a fresh\n   supply of goods; fresh tea, raisins, etc.; lately come or made public;\n   as, fresh news; recently taken from a well or spring; as, fresh water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fresh', 'superl', 'Youthful; florid; as, these fresh nymphs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fresh', 'superl', 'In a raw, green, or untried state; uncultivated;\n   uncultured; unpracticed; as, a fresh hand on a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fresh', 'superl', 'Renewed in vigor, alacrity, or readiness for action; as,\n   fresh for a combat; hence, tending to renew in vigor; rather strong;\n   cool or brisk; as, a fresh wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fresh', 'superl', 'Not salt; as, fresh water, in distinction from that\n   which is from the sea, or brackish; fresh meat, in distinction from\n   that which is pickled or salted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freshes', 'pl. ', 'of Fresh');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fresh', 'n.', 'A stream or spring of fresh water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fresh', 'n.', 'A flood; a freshet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fresh', 'n.', 'The mingling of fresh water with salt in rivers or bays, as\n   by means of a flood of fresh water flowing toward or into the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fresh', 'v. t.', 'To refresh; to freshen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freshened', 'imp. & p. p.', 'of Freshen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freshening', 'p. pr. & vb. n.', 'of Freshen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freshen', 'v. t.', 'To make fresh; to separate, as water, from saline\n   ingredients; to make less salt; as, to freshen water, fish, or flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freshen', 'v. t.', 'To refresh; to revive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freshen', 'v. t.', 'To relieve, as a rope, by change of place where\n   friction wears it; or to renew, as the material used to prevent\n   chafing; as, to freshen a hawse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freshen', 'v. i.', 'To grow fresh; to lose saltness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freshen', 'v. i.', 'To grow brisk or strong; as, the wind freshens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freshet', 'a.', 'A stream of fresh water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freshet', 'a.', 'A flood or overflowing of a stream caused by heavy rains\n   or melted snow; a sudden inundation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freshly', 'adv.', 'In a fresh manner; vigorously; newly, recently;\n   brightly; briskly; coolly; as, freshly gathered; freshly painted; the\n   wind blows freshly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freshmen', 'pl. ', 'of Freshman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freshman', 'n.', 'novice; one in the rudiments of knowledge; especially, a\n   student during his fist year in a college or university.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freshmanship', 'n.', 'The state of being a freshman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freshment', 'n.', 'Refreshment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freshness', 'n.', 'The state of being fresh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fresh-new', 'a.', 'Unpracticed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fresh-water', 'a.', 'Of, pertaining to, or living in, water not salt; as,\n   fresh-water geological deposits; a fresh-water fish; fresh-water\n   mussels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fresh-water', 'a.', 'Accustomed to sail on fresh water only; unskilled as a\n   seaman; as, a fresh-water sailor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fresh-water', 'a.', 'Unskilled; raw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fresnel lamp', '', 'Alt. of Fresnel lantern');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fresnel lantern', '', 'A lantern having a lamp surrounded by a hollow\n   cylindrical Fresnel lens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fresnel lens', '', 'See under Lens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fret', 'n.', 'See 1st Frith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fretted', 'imp. & p. p.', 'of Fret');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fretting', 'p. pr. & vb. n.', 'of Fret');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fret', 'v. t.', 'To devour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fret', 'v. t.', 'To rub; to wear away by friction; to chafe; to gall;\n   hence, to eat away; to gnaw; as, to fret cloth; to fret a piece of gold\n   or other metal; a worm frets the plants of a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fret', 'v. t.', 'To impair; to wear away; to diminish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fret', 'v. t.', 'To make rough, agitate, or disturb; to cause to ripple;\n   as, to fret the surface of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fret', 'v. t.', 'To tease; to irritate; to vex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fret', 'v. i.', 'To be worn away; to chafe; to fray; as, a wristband frets\n   on the edges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fret', 'v. i.', 'To eat in; to make way by corrosion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fret', 'v. i.', 'To be agitated; to be in violent commotion; to rankle; as,\n   rancor frets in the malignant breast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fret', 'v. i.', 'To be vexed; to be chafed or irritated; to be angry; to\n   utter peevish expressions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fret', 'n.', 'The agitation of the surface of a fluid by fermentation or\n   other cause; a rippling on the surface of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fret', 'n.', 'Agitation of mind marked by complaint and impatience;\n   disturbance of temper; irritation; as, he keeps his mind in a continual\n   fret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fret', 'n.', 'Herpes; tetter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fret', 'n.', 'The worn sides of river banks, where ores, or stones\n   containing them, accumulate by being washed down from the hills, and\n   thus indicate to the miners the locality of the veins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fret', 'v. t.', 'To ornament with raised work; to variegate; to diversify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fret', 'n.', 'Ornamental work in relief, as carving or embossing. See\n   Fretwork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fret', 'n.', 'An ornament consisting of smmall fillets or slats\n   intersecting each other or bent at right angles, as in classical\n   designs, or at obilique angles, as often in Oriental art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fret', 'n.', 'The reticulated headdress or net, made of gold or silver\n   wire, in which ladies in the Middle Ages confined their hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fret', 'n.', 'A saltire interlaced with a mascle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fret', 'n.', 'A short piece of wire, or other material fixed across the\n   finger board of a guitar or a similar instrument, to indicate where the\n   finger is to be placed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fret', 'v. t.', 'To furnish with frets, as an instrument of music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fretful', 'a.', 'Disposed to fret; ill-humored; peevish; angry; in a state\n   of vexation; as, a fretful temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frett', 'n.', 'The worn side of the bank of a river. See 4th Fret, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frett', 'n.', 'A vitreous compound, used by potters in glazing, consisting\n   of lime, silica, borax, lead, and soda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fretted', 'p. p. & a.', 'Rubbed or worn away; chafed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fretted', 'p. p. & a.', 'Agitated; vexed; worried.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fretted', 'p. p. & a.', 'Ornamented with fretwork; furnished with frets;\n   variegated; made rough on the surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fretted', 'p. p. & a.', 'Interlaced one with another; -- said of charges\n   and ordinaries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fretten', 'a.', 'Rubbed; marked; as, pock-fretten, marked with the\n   smallpox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fretter', 'n.', 'One who, or that which, frets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fretty', 'a.', 'Adorned with fretwork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freta', 'pl. ', 'of Fretum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fretum', 'n.', 'A strait, or arm of the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fretwork', 'n.', 'Work adorned with frets; ornamental openwork or work in\n   relief, esp. when elaborate and minute in its parts. Hence, any minute\n   play of light and shade, dark and light, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Freya', 'n.', 'The daughter of Njord, and goddess of love and beauty; the\n   Scandinavian Venus; -- in Teutonic myths confounded with Frigga, but in\n   Scandinavian, distinct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friabiiity', 'n.', 'The quality of being friable; friableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friable', 'a.', 'Easily crumbled, pulverized, or reduced to powder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friar', 'n.', 'A brother or member of any religious order, but especially\n   of one of the four mendicant orders, viz: (a) Minors, Gray Friars, or\n   Franciscans. (b) Augustines. (c) Dominicans or Black Friars. (d) White\n   Friars or Carmelites. See these names in the Vocabulary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friar', 'n.', 'A white or pale patch on a printed page.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friar', 'n.', 'An American fish; the silversides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friarly', 'a.', 'Like a friar; inexperienced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friary', 'n.', 'Like a friar; pertaining to friars or to a convent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friary', 'n.', 'A monastery; a convent of friars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friary', 'n.', 'The institution or praactices of friars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friation', 'n.', 'The act of breaking up or pulverizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frible', 'a.', 'Frivolous; trifling; sily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fribble', 'n.', 'A frivolous, contemptible fellow; a fop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fribble', 'v. i.', 'To act in a trifling or foolish manner; to act\n   frivolously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fribble', 'v. i.', 'To totter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fribbler', 'n.', 'A trifler; a fribble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fribbling', 'a.', 'Frivolous; trining; toolishly captious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friborg', 'n.', 'Alt. of Friborgh');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friborgh', 'n.', 'The pledge and tithing, afterwards called by the Normans\n   frankpledge. See Frankpledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fricace', 'n.', 'Meat sliced and dressed with strong sauce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fricace', 'n.', 'An unguent; also, the act of rubbing with the unguent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fricandeau', 'n.', 'Alt. of Fricando');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fricando', 'n.', 'A ragout or fricassee of veal; a fancy dish of veal or of\n   boned turkey, served as an entree, -- called also fricandel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fricassee', 'n.', 'A dish made of fowls, veal, or other meat of small\n   animals cut into pieces, and stewed in a gravy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fricassed', 'imp. & p. p.', 'of Frlcassee');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fricasseeing', 'p. pr. &. vb. n.', 'of Frlcassee');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frlcassee', 'v. t.', 'To dress like a fricassee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frication', 'n.', 'Friction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fricative', 'a.', 'Produced by the friction or rustling of the breath,\n   intonated or unintonated, through a narrow opening between two of the\n   mouth organs; uttered through a close approach, but not with a complete\n   closure, of the organs of articulation, and hence capable of being\n   continued or prolonged; -- said of certain consonantal sounds, as f, v,\n   s, z, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fricative', 'n.', 'A fricative consonant letter or sound. See Guide to\n   Pronunciation, // 197-206, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fricatrice', 'n.', 'A lewd woman; a harlot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frickle', 'n.', 'A bushel basket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ftiction', 'n.', 'The act of rubbing the surface of one body against that\n   of another; attrition; in hygiene, the act of rubbing the body with the\n   hand, with flannel, or with a brush etc., to excite the skin to healthy\n   action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ftiction', 'n.', 'The resistance which a body meets with from the surface\n   on which it moves. It may be resistance to sliding motion, or to\n   rolling motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ftiction', 'n.', 'A clashing between two persons or parties in opinions or\n   work; a disagreement tending to prevent or retard progress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frictional', 'a.', 'Relating to friction; moved by friction; produced by\n   friction; as, frictional electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frictionless', 'a.', 'Having no friction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friday', 'n.', 'The sixth day of the week, following Thursday and preceding\n   Saturday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fridge', 'n.', 'To rub; to fray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fridstol', 'n.', 'Alt. of Frithstool');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frithstool', 'n.', 'A seat in churches near the altar, to which offenders\n   formerly fled for sanctuary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fried', '', 'imp. & p. p. of Fry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friend', 'n.', 'One who entertains for another such sentiments of esteem,\n   respect, and affection that he seeks his society aud welfare; a\n   wellwisher; an intimate associate; sometimes, an attendant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friend', 'n.', 'One not inimical or hostile; one not a foe or enemy; also,\n   one of the same nation, party, kin, etc., whose friendly feelings may\n   be assumed. The word is some times used as a term of friendly address.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friend', 'n.', 'One who looks propitiously on a cause, an institution, a\n   project, and the like; a favorer; a promoter; as, a friend to commerce,\n   to poetry, to an institution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friend', 'n.', 'One of a religious sect characterized by disuse of outward\n   rites and an ordained ministry, by simplicity of dress and speech, and\n   esp. by opposition to war and a desire to live at peace with all men.\n   They are popularly called Quakers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friend', 'n.', 'A paramour of either sex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friended', 'imp. & p. p.', 'of Friend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friending', 'p. pr. & vb. n.', 'of Friend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friend', 'v. t.', 'To act as the friend of; to favor; to countenance; to\n   befriend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friended', 'a.', 'Having friends;');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friended', 'a.', 'Inclined to love; well-disposed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friending', 'n.', 'Friendliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friendless', 'a.', 'Destitute of friends; forsaken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friendlily', 'adv.', 'In a friendly manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friendliness', 'n.', 'The condition or quality of being friendly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friendly', 'a.', 'Having the temper and disposition of a friend; disposed\n   to promote the good of another; kind; favorable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friendly', 'a.', 'Appropriate to, or implying, friendship; befitting\n   friends; amicable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friendly', 'a.', 'Not hostile; as, a friendly power or state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friendly', 'a.', 'Promoting the good of any person; favorable; propitious;\n   serviceable; as, a friendly breeze or gale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friendly', 'adv.', 'In the manner of friends; amicably; like friends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friendship', 'n.', 'The state of being friends; friendly relation, or\n   attachment, to a person, or between persons; affection arising from\n   mutual esteem and good will; friendliness; amity; good will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friendship', 'n.', 'Kindly aid; help; assistance,');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friendship', 'n.', 'Aptness to unite; conformity; affinity; harmony;\n   correspondence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frier', 'n.', 'One who fries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friese', 'n.', 'Same as Friesic, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friesic', 'a.', 'Of or pertaining to Friesland, a province in the northern\n   part of the Netherlands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friesic', 'n.', 'The language of the Frisians, a Teutonic people formerly\n   occupying a large part of the coast of Holland and Northwestern\n   Germany. The modern dialects of Friesic are spoken chiefly in the\n   province of Friesland, and on some of the islands near the coast of\n   Germany and Denmark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friesish', 'a.', 'Friesic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frieze', 'n.', 'That part of the entablature of an order which is between\n   the architrave and cornice. It is a flat member or face, either uniform\n   or broken by triglyphs, and often enriched with figures and other\n   ornaments of sculpture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frieze', 'n.', 'Any sculptured or richly ornamented band in a building or,\n   by extension, in rich pieces of furniture. See Illust. of Column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frieze', 'n.', 'A kind of coarse woolen cloth or stuff with a shaggy or\n   tufted (friezed) nap on one side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frieze', 'v. t.', 'To make a nap on (cloth); to friz. See Friz, v. t., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friezed', 'a.', 'Gathered, or having the map gathered, into little tufts,\n   knots, or protuberances. Cf. Frieze, v. t., and Friz, v. t., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friezer', 'n.', 'One who, or that which, friezes or frizzes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frigate', 'n.', 'Originally, a vessel of the Mediterranean propelled by\n   sails and by oars. The French, about 1650, transferred the name to\n   larger vessels, and by 1750 it had been appropriated for a class of war\n   vessels intermediate between corvettes and ships of the line. Frigates,\n   from about 1750 to 1850, had one full battery deck and, often, a spar\n   deck with a lighter battery. They carried sometimes as many as fifty\n   guns. After the application of steam to navigation steam frigates of\n   largely increased size and power were built, and formed the main part\n   of the navies of the world till about 1870, when the introduction of\n   ironclads superseded them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frigate', 'n.', 'Any small vessel on the water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frigate-built', 'a.', 'Built like a frigate with a raised quarter-deck and\n   forecastle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frigatoon', 'n.', 'A Venetian vessel, with a square stern, having only a\n   mainmast, jigger mast, and bowsprit; also a sloop of war ship-rigged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frigefaction', 'n.', 'The act of making cold. [Obs.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frigefactive', 'a.', 'Cooling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frigerate', 'e. t.', 'To make cool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frigg', 'n.', 'Alt. of Frigga');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frigga', 'n.', 'The wife of Odin and mother of the gods; the supreme\n   goddess; the Juno of the Valhalla. Cf. Freya.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fright', 'n.', 'A state of terror excited by the sudden appearance of\n   danger; sudden and violent fear, usually of short duration; a sudden\n   alarm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fright', 'n.', 'Anything strange, ugly or shocking, producing a feeling of\n   alarm or aversion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frighted', 'imp.', 'of Fright');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frighting', 'p. pr. & vb. n.', 'of Fright');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fright', 'n.', 'To alarm suddenly; to shock by causing sudden fear; to\n   terrify; to scare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frightened', 'imp.', 'of Frighten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frightening', 'p. pr. & vb. n.', 'of Frighten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frighten', 'v. t.', 'To disturb with fear; to throw into a state of alarm\n   or fright; to affright; to terrify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frightful', 'a.', 'Full of fright; affrighted; frightened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frightful', 'a.', 'Full of that which causes fright; exciting alarm;\n   impressing terror; shocking; as, a frightful chasm, or tempest; a\n   frightful appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frightfully', 'adv.', 'In a frightful manner; to a frightful dagree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frightfulness', 'n.', 'The quality of being frightful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frightless', 'a.', 'Free from fright; fearless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frightment', 'n.', 'Fear; terror.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frigid', 'a.', 'Cold; wanting heat or warmth; of low temperature; as, a\n   frigid climate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frigid', 'a.', 'Wanting warmth, fervor, ardor, fire, vivacity, etc.;\n   unfeeling; forbidding in manner; dull and unanimated; stiff and formal;\n   as, a frigid constitution; a frigid style; a frigid look or manner;\n   frigid obedience or service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frigid', 'a.', 'Wanting natural heat or vigor sufficient to excite the\n   generative power; impotent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frigidaria', 'pl. ', 'of Frigidarium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frigidarium', 'n.', 'The cooling room of the Roman thermae, furnished with\n   a cold bath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prigidity', 'n.', 'The condition or quality of being frigid; coldness; want\n   of warmth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prigidity', 'n.', 'Want of ardor, animation, vivacity, etc.; coldness of\n   affection or of manner; dullness; stiffness and formality; as,\n   frigidity of a reception, of a bow, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Prigidity', 'n.', 'Want of heat or vigor; as, the frigidity of old age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frigidly', 'adv.', 'In a frigid manner; coldly; dully; without affection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frigidness', 'n.', 'The state of being frigid; want of heat, vigor, or\n   affection; coldness; dullness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frigorific', 'a.', 'Alt. of Frigorifical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frigorifical', 'a.', 'Causing cold; producing or generating cold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frilled', 'imp. & p. p.', 'of Frill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frilling', 'p. pr. & vb. n.', 'of Frill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frill', 'v. i.', 'To shake or shiver as with cold; as, the hawk frills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frill', 'v. i.', 'To wrinkle; -- said of the gelatin film.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frill', 'v. t.', 'To provide or decorate with a frill or frills; to turn\n   back. in crimped plaits; as, to frill a cap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frill', 'v. i.', 'A ruffing of a birds feathers from cold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frill', 'v. i.', 'A ruffle, consisting of a fold of membrane, of hairs, or\n   of feathers, around the neck of an animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frill', 'v. i.', 'A similar ruffle around the legs or other appendages of\n   animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frill', 'v. i.', 'A ruffled varex or fold on certain shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frill', 'v. i.', 'A border or edging secured at one edge and left free at\n   the other, usually fluted or crimped like a very narrow flounce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frilled', 'a.', 'Furnished with a frill or frills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frim', 'a.', 'Flourishing; thriving; fresh; in good case; vigorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frimaire', 'n.', 'The third month of the French republican calendar. It\n   commenced November 21, and ended December 20., See Vendemiaire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fringe', 'n.', 'An ornamental appendage to the border of a piece of stuff,\n   originally consisting of the ends of the warp, projecting beyond the\n   woven fabric; but more commonly made separate and sewed on, consisting\n   sometimes of projecting ends, twisted or plaited together, and\n   sometimes of loose threads of wool, silk, or linen, or narrow strips of\n   leather, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fringe', 'n.', 'Something resembling in any respect a fringe; a line of\n   objects along a border or edge; a border; an edging; a margin; a\n   confine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fringe', 'n.', 'One of a number of light or dark bands, produced by the\n   interference of light; a diffraction band; -- called also interference\n   fringe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fringe', 'n.', 'The peristome or fringelike appendage of the capsules of\n   most mosses. See Peristome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fringed', 'imp. & p. p.', 'of Fringe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fringing', 'p. pr. & vb. a.', 'of Fringe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fringe', 'v. t.', 'To adorn the edge of with a fringe or as with a fringe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fringed', 'a.', 'Furnished with a fringe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fringeless', 'a.', 'Having no fringe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fringent', 'a.', 'Encircling like a fringe; bordering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fringilla', 'a.', 'A genus of birds, with a short, conical, pointed bill.\n   It formerly included all the sparrows and finches, but is now\n   restricted to certain European finches, like the chaffinch and\n   brambling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fringillaceous', 'a.', 'Fringilline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fringilline', 'a.', 'Pertaining to the family Fringillidae; characteristic\n   of finches; sparrowlike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fringy', 'a.', 'Aborned with fringes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fripper', 'n.', 'One who deals in frippery or in old clothes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fripperer', 'n.', 'A fripper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frippery', 'n.', 'Coast-off clothes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frippery', 'n.', 'Hence: Secondhand finery; cheap and tawdry decoration;\n   affected elegance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frippery', 'n.', 'A place where old clothes are sold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frippery', 'n.', 'The trade or traffic in old clothes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frippery', 'a.', 'Trifling; contemptible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friseur', 'n.', 'A hairdresser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frisian', 'a.', 'Of or pertaining to Friesland, a province of the\n   Netherlands; Friesic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frisian', 'n.', 'A native or inhabitant of Friesland; also, the language\n   spoken in Friesland. See Friesic, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frisk', 'a.', 'Lively; brisk; frolicsome; frisky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frisk', 'a.', 'A frolic; a fit of wanton gayety; a gambol: a little playful\n   skip or leap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frisked', 'imp. & p. p.', 'of Frisk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frisking', 'p. pr. & vb. n.', 'of Frisk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frisk', 'v. i.', 'To leap, skip, dance, or gambol, in fronc and gayety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friskal', 'n.', 'A leap or caper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frisker', 'n.', 'One who frisks; one who leaps of dances in gayety; a\n   wanton; an inconstant or unsettled person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frisket', 'a.', 'The light frame which holds the sheet of paper to the\n   tympan in printing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friskful', 'a.', 'Brisk; lively; frolicsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friskily', 'adv.', 'In a frisky manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friskiness', 'n.', 'State or quality of being frisky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frisky', 'a.', 'Inclined to frisk; frolicsome; gay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frislet', 'n.', 'A kind of small ruffle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frist', 'v. t.', 'To sell upon credit, as goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frisure', 'n.', 'The dressing of the hair by crisping or curling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frit', 'v. t.', 'The material of which glass is made, after having been\n   calcined or partly fused in a furnace, but before vitrification. It is\n   a composition of silex and alkali, occasionally with other ingredients.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frit', 'v. t.', 'The material for glaze of pottery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fritted', 'imp. & p. p.', 'of Frit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fritting', 'p. pr. & vb. n.', 'of Frit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frit', 'v. t.', 'To prepare by heat (the materials for making glass); to\n   fuse partially.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frit', 'v. t.', 'To fritter; -- with away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frith', 'n.', 'A narrow arm of the sea; an estuary; the opening of a river\n   into the sea; as, the Frith of Forth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frith', 'n.', 'A kind of weir for catching fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frith', 'a.', 'A forest; a woody place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frith', 'a.', 'A small field taken out of a common, by inclosing it; an\n   inclosure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frithy', 'a.', 'Woody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fritillaria', 'n.', 'A genus of liliaceous plants, of which the\n   crown-imperial (Fritillaria imperialis) is one species, and the\n   Guinea-hen flower (F. Meleagris) another. See Crown-imperial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fritillary', 'n.', 'A plant with checkered petals, of the genus\n   Fritillaria: the Guinea-hen flower. See Fritillaria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fritillary', 'n.', 'One of several species of butterflies belonging to\n   Argynnis and allied genera; -- so called because the coloring of their\n   wings resembles that of the common Fritillaria. See Aphrodite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fritinancy', 'n.', 'A chirping or creaking, as of a cricket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fritter', 'v. t.', 'A small quantity of batter, fried in boiling lard or in\n   a frying pan. Fritters are of various kinds, named from the substance\n   inclosed in the batter; as, apple fritters, clam fritters, oyster\n   fritters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fritter', 'v. t.', 'A fragment; a shred; a small piece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frittered', 'imp. & p. p.', 'of Fritter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frittering', 'p. pr. & vb. n.', 'of Fritter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fritter', 'v. t.', 'To cut, as meat, into small pieces, for frying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fritter', 'v. t.', 'To break into small pieces or fragments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fritting', 'n.', 'The formation of frit or slag by heat with but incipient\n   fusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frivolism', 'n.', 'Frivolity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frivolities', 'pl. ', 'of Frivolity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frivolity', 'n.', 'The condition or quality of being frivolous; also, acts\n   or habits of trifling; unbecoming levity of disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frivolous', 'a.', 'Of little weight or importance; not worth notice;\n   slight; as, a frivolous argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frivolous', 'a.', 'Given to trifling; marked with unbecoming levity; silly;\n   interested especially in trifling matters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frizzed', 'imp. & p. p.', 'of Friz');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frizzing', 'p. pr. & vb. n.', 'of Friz');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friz', 'v. t.', 'To curl or form into small curls, as hair, with a crisping\n   pin; to crisp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friz', 'v. t.', 'To form into little burs, prominences, knobs, or tufts, as\n   the nap of cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friz', 'v. t.', 'To soften and make of even thickness by rubbing, as with\n   pumice stone or a blunt instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frizzes', 'pl. ', 'of Friz');
  INSERT INTO words (word, word_type, description) VALUES  (    'Friz', 'n.', 'That which is frizzed; anything crisped or curled, as a wig;\n   a frizzle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frize', 'n.', 'See 1st Frieze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frizel', 'a.', 'A movable furrowed piece of steel struck by the flint, to\n   throw sparks into the pan, in an early form of flintlock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frizette', 'n.', 'A curl of hair or silk; a pad of frizzed hair or silk\n   worn by women under the hair to stuff it out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frizz', 'v. t. & n.', 'See Friz, v. t. & n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frizzled', 'imp. & p. p.', 'of Frizzle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frizzling', 'p. pr. & vb. n.', 'of Frizzle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frizzle', 'v. t.', 'To curl or crisp, as hair; to friz; to crinkle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frizzle', 'n.', 'A curl; a lock of hair crisped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frizzler', 'n.', 'One who frizzles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frizzly', 'a.', 'Alt. of Frizzy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frizzy', 'a.', 'Curled or crisped; as, frizzly, hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fro', 'adv.', 'From; away; back or backward; -- now used only in opposition\n   to the word to, in the phrase to and fro, that is, to and from. See To\n   and fro under To.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fro', 'prep.', 'From.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frock', 'n.', 'A loose outer garment; especially, a gown forming a part of\n   European modern costume for women and children; also, a coarse\n   shirtlike garment worn by some workmen over their other clothes; a\n   smock frock; as, a marketmans frock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frock', 'n.', 'A coarse gown worn by monks or friars, and supposed to take\n   the place of all, or nearly all, other garments. It has a hood which\n   can be drawn over the head at pleasure, and is girded by a cord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frock', 'v. t.', 'To clothe in a frock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frock', 'v. t.', 'To make a monk of. Cf. Unfrock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frocked', 'a.', 'Clothed in a frock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frockless', 'a.', 'Destitute of a frock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Froe', 'n.', 'A dirty woman; a slattern; a frow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Froe', 'n.', 'An iron cleaver or splitting tool; a frow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frog', 'n.', 'An amphibious animal of the genus Rana and related genera, of\n   many species. Frogs swim rapidly, and take long leaps on land. Many of\n   the species utter loud notes in the springtime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frog', 'n.', 'The triangular prominence of the hoof, in the middle of the\n   sole of the foot of the horse, and other animals; the fourchette.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frog', 'n.', 'A supporting plate having raised ribs that form continuations\n   of the rails, to guide the wheels where one track branches from another\n   or crosses it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frog', 'n.', 'An oblong cloak button, covered with netted thread, and\n   fastening into a loop instead of a button hole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frog', 'n.', 'The loop of the scabbard of a bayonet or sword.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frog', 'v. t.', 'To ornament or fasten (a coat, etc.) with trogs. See Frog,\n   n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frogbit', 'n.', 'A European plant (Hydrocharis Morsus-ranae), floating on\n   still water and propagating itself by runners. It has roundish leaves\n   and small white flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frogbit', 'n.', 'An American plant (Limnobium Spongia), with similar\n   habits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frogfish', 'n.', 'See Angler, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frogfish', 'n.', 'An oceanic fish of the genus Antennarius or\n   Pterophrynoides; -- called also mousefish and toadfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frogged', 'a.', 'Provided or ornamented with frogs; as, a frogged coat. See\n   Frog, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Froggy', 'a.', 'Abounding in frogs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frogmouth', 'n.', 'One of several species of Asiatic and East Indian birds\n   of the genus Batrachostomus (family Podargidae); -- so called from\n   their very broad, flat bills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frogs-bit', 'n.', 'Frogbit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frogshell', 'n.', 'One of numerous species of marine gastropod shells,\n   belonging to Ranella and allied genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Froise', 'n.', 'A kind of pancake. See 1st Fraise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frolic', 'a.', 'Full of levity; dancing, playing, or frisking about; full\n   of pranks; frolicsome; gay; merry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frolic', 'n.', 'A wild prank; a flight of levity, or of gayety and mirth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frolic', 'n.', 'A scene of gayety and mirth, as in lively play, or in\n   dancing; a merrymaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frolicked', 'imp. & p. p.', 'of Frolic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frolicking', 'p. pr. & vb. n.', 'of Frolic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frolic', 'v. i.', 'To play wild pranks; to play tricks of levity, mirth,\n   and gayety; to indulge in frolicsome play; to sport.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frolicful', 'a.', 'Frolicsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frolicky', 'a.', 'Frolicsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frolicly', 'adv.', 'In a frolicsome manner; with mirth and gayety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frolicsome', 'a.', 'Full of gayety and mirth; given to pranks; sportive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'From', 'prep.', 'Out of the neighborhood of; lessening or losing proximity\n   to; leaving behind; by reason of; out of; by aid of; -- used whenever\n   departure, setting out, commencement of action, being, state,\n   occurrence, etc., or procedure, emanation, absence, separation, etc.,\n   are to be expressed. It is construed with, and indicates, the point of\n   space or time at which the action, state, etc., are regarded as setting\n   out or beginning; also, less frequently, the source, the cause, the\n   occasion, out of which anything proceeds; -- the aritithesis and\n   correlative of to; as, it, is one hundred miles from Boston to\n   Springfield; he took his sword from his side; light proceeds from the\n   sun; separate the coarse wool from the fine; men have all sprung from\n   Adam, and often go from good to bad, and from bad to worse; the merit\n   of an action depends on the principle from which it proceeds; men judge\n   of facts from personal knowledge, or from testimony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fromward', 'prep.', 'Alt. of Fromwards');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fromwards', 'prep.', 'A way from; -- the contrary of toward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frond', 'n.', 'The organ formed by the combination or union into one body\n   of stem and leaf, and often bearing the fructification; as, the frond\n   of a fern or of a lichen or seaweed; also, the peculiar leaf of a palm\n   tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frondation', 'n.', 'The act of stripping, as trees, of leaves or branches;\n   a kind of pruning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fronde', 'n.', 'A political party in France, during the minority of Louis\n   XIV., who opposed the government, and made war upon the court party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fronded', 'a.', 'Furnished with fronds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frondent', 'a.', 'Covered with leaves; leafy; as, a frondent tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frondesce', 'v. i.', 'To unfold leaves, as plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frondescence', 'n.', 'The time at which each species of plants unfolds its\n   leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frondescence', 'n.', 'The act of bursting into leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frondeur', 'n.', 'A member of the Fronde.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frondiferous', 'a.', 'Producing fronds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frondlet', 'n.', 'A very small frond, or distinct portion of a compound\n   frond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frondose', 'a.', 'Frond bearing; resembling a frond; having a simple\n   expansion not separable into stem and leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frondose', 'a.', 'Leafy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frondous', 'a.', 'Frondose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frons', 'n.', 'The forehead; the part of the cranium between the orbits and\n   the vertex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Front', 'n.', 'The forehead or brow, the part of the face above the eyes;\n   sometimes, also, the whole face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Front', 'n.', 'The forehead, countenance, or personal presence, as\n   expressive of character or temper, and especially, of boldness of\n   disposition, sometimes of impudence; seeming; as, a bold front; a\n   hardened front.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Front', 'n.', 'The part or surface of anything which seems to look out, or\n   to be directed forward; the fore or forward part; the foremost rank;\n   the van; -- the opposite to back or rear; as, the front of a house; the\n   front of an army.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Front', 'n.', 'A position directly before the face of a person, or before\n   the foremost part of a thing; as, in front of un person, of the troops,\n   or of a house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Front', 'n.', 'The most conspicuous part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Front', 'n.', 'That which covers the foremost part of the head: a front\n   piece of false hair worn by women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Front', 'n.', 'The beginning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Front', 'a.', 'Of or relating to the front or forward part; having a\n   position in front; foremost; as, a front view.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fronted', 'imp. & p. p.', 'of Front');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fronting', 'p. pr. & vb. n.', 'of Front');
  INSERT INTO words (word, word_type, description) VALUES  (    'Front', 'v. t.', 'To oppose face to face; to oppose directly; to meet in a\n   hostile manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Front', 'v. t.', 'To appear before; to meet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Front', 'v. t.', 'To face toward; to have the front toward; to confront;\n   as, the house fronts the street.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Front', 'v. t.', 'To stand opposed or opposite to, or over against as, his\n   house fronts the church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Front', 'v. t.', 'To adorn in front; to supply a front to; as, to front a\n   house with marble; to front a head with laurel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Front', 'v. t.', 'To have or turn the face or front in any direction; as,\n   the house fronts toward the east.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frontage', 'n.', 'The front part of an edifice or lot; extent of front.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frontal', 'a.', 'Belonging to the front part; being in front');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frontal', 'a.', 'Of or pertaining to the forehead or the anterior part of\n   the roof of the brain case; as, the frontal bones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frontal', 'n.', 'Something worn on the forehead or face; a frontlet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frontal', 'n.', 'An ornamental band for the hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frontal', 'n.', 'The metal face guard of a soldier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frontal', 'n.', 'A little pediment over a door or window.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frontal', 'n.', 'A movable, decorative member in metal, carved wood, or,\n   commonly, in rich stuff or in embroidery, covering the front of the\n   altar. Frontals are usually changed according to the different\n   ceremonies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frontal', 'n.', 'A medicament or application for the forehead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frontal', 'n.', 'The frontal bone, or one of the two frontal bones, of the\n   cranium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frontate', 'a.', 'Alt. of Frontated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frontated', 'a.', 'Growing broader and broader, as a leaf; truncate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fronted', 'a.', 'Formed with a front; drawn up in line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frontier', 'n.', 'That part of a country which fronts or faces another\n   country or an unsettled region; the marches; the border, confine, or\n   extreme part of a country, bordering on another country; the border of\n   the settled and cultivated part of a country; as, the frontier of\n   civilization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frontier', 'n.', 'An outwork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frontier', 'a.', 'Lying on the exterior part; bordering; conterminous; as,\n   a frontier town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frontier', 'a.', 'Of or relating to a frontier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frontier', 'v. i.', 'To constitute or form a frontier; to have a frontier;\n   -- with on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frontiered', 'p. a.', 'Placed on the frontiers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frontiersmen', 'pl. ', 'of Floatiersman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Floatiersman', 'n.', 'A man living on the frontier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frontignac', 'n.', 'Alt. of Frontignan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frontignan', 'n.', 'A sweet muscadine wine made in Frontignan (Languedoc),\n   France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frontignan', 'n.', 'A grape of many varieties and colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frontingly', 'adv.', 'In a fronting or facing position; opposingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frontiniac', 'n.', 'See Frontignac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frontispiece', 'n.', 'The part which first meets the eye');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frontispiece', 'n.', 'The principal front of a building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frontispiece', 'n.', 'An ornamental figure or illustration fronting the\n   first page, or titlepage, of a book; formerly, the titlepage itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frontless', 'a.', 'Without face or front; shameless; not diffident;\n   impudent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frontlessly', 'adv.', 'Shamelessly; impudently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frontlet', 'n.', 'A frontal or brow band; a fillet or band worn on the\n   forehead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frontlet', 'n.', 'A frown (likened to a frontlet).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frontlet', 'n.', 'The margin of the head, behind the bill of birds, often\n   bearing rigid bristles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fronto-', '', 'A combining form signifying relating to the forehead or the\n   frontal bone; as, fronto-parietal, relating to the frontal and the\n   parietal bones; fronto-nasal, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fronton', 'n.', 'Same as Frontal, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Froppish', 'a.', 'Peevish; froward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frore', 'adv.', 'Frostily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frorn', 'p. a.', 'Frozen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frory', 'a.', 'Frozen; stiff with cold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frory', 'a.', 'Covered with a froth like hoarfrost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frost', 'v. i.', 'The act of freezing; -- applied chiefly to the\n   congelation of water; congelation of fluids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frost', 'v. i.', 'The state or temperature of the air which occasions\n   congelation, or the freezing of water; severe cold or freezing weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frost', 'v. i.', 'Frozen dew; -- called also hoarfrost or white frost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frost', 'v. i.', 'Coldness or insensibility; severity or rigidity of\n   character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frostted', 'imp. & p. p.', 'of Frost');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frosting', 'p. pr. & vb. n.', 'of Frost');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frost', 'v. t.', 'To injure by frost; to freeze, as plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frost', 'v. t.', 'To cover with hoarfrost; to produce a surface resembling\n   frost upon, as upon cake, metals, or glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frost', 'v. t.', 'To roughen or sharpen, as the nail heads or calks of\n   horseshoes, so as to fit them for frosty weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frostbird', 'n.', 'The golden plover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frostbite', 'n.', 'The freezing, or effect of a freezing, of some part of\n   the body, as the ears or nose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frostbite', 'v. t.', 'To expose to the effect of frost, or a frosty air; to\n   blight or nip with frost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frost-bitten', 'p. a.', 'Nipped, withered, or injured, by frost or\n   freezing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frost-blite', 'n.', 'A plant of the genus Atriplex; orache.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frost-blite', 'n.', 'The lambs-quarters (Chenopodium album).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frosted', 'a.', 'Covered with hoarfrost or anything resembling hoarfrost;\n   ornamented with frosting; also, frost-bitten; as, a frosted cake;\n   frosted glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frostfish', 'n.', 'The tomcod; -- so called because it is abundant on the\n   New England coast in autumn at about the commencement of frost. See\n   Tomcod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frostfish', 'n.', 'The smelt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frostfish', 'n.', 'A name applied in New Zealand to the scabbard fish\n   (Lepidotus) valued as a food fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frostily', 'adv.', 'In a frosty manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frostiness', 'n.', 'State or quality of being frosty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frosting', 'n.', 'A composition of sugar and beaten egg, used to cover or\n   ornament cake, pudding, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frosting', 'n.', 'A lusterless finish of metal or glass; the process of\n   producing such a finish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frostless', 'a.', 'Free from frost; as, a frostless winter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frostweed', 'n.', 'An American species of rockrose (Helianthemum\n   Canadense), sometimes used in medicine as an astringent or aromatic\n   tonic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frostwork', 'n.', 'The figurework, often fantastic and delicate, which\n   moisture sometimes forms in freezing, as upon a window pane or a\n   flagstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frostwort', 'n.', 'Same as Frostweed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frosty', 'a.', 'Attended with, or producing, frost; having power to congeal\n   water; cold; freezing; as, a frosty night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frosty', 'a.', 'Covered with frost; as, the grass is frosty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frosty', 'a.', 'Chill in affection; without warmth of affection or courage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frosty', 'a.', 'Appearing as if covered with hoarfrost; white; gray-haired;\n   as, a frosty head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frote', 'v. t.', 'To rub or wear by rubbing; to chafe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Froterer', 'n.', 'One who frotes; one who rubs or chafes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Froth', 'n.', 'The bubbles caused in fluids or liquors by fermentation or\n   agitation; spume; foam; esp., a spume of saliva caused by disease or\n   nervous excitement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Froth', 'n.', 'Any empty, senseless show of wit or eloquence; rhetoric\n   without thought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Froth', 'n.', 'Light, unsubstantial matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frothed', 'imp. & p. p.', 'of Froth');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frothing', 'p. pr. & vb. n.', 'of Froth');
  INSERT INTO words (word, word_type, description) VALUES  (    'Froth', 'v. t.', 'To cause to foam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Froth', 'v. t.', 'To spit, vent, or eject, as froth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Froth', 'v. t.', 'To cover with froth; as, a horse froths his chain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Froth', 'v. i.', 'To throw up or out spume, foam, or bubbles; to foam; as\n   beer froths; a horse froths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frothily', 'adv.', 'In a frothy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frothiness', 'n.', 'State or quality of being frothy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frothing', 'n.', 'Exaggerated declamation; rant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frothless', 'a.', 'Free from froth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frothy', 'superl.', 'Full of foam or froth, or consisting of froth or light\n   bubbles; spumous; foamy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frothy', 'superl.', 'Not firm or solid; soft; unstable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frothy', 'superl.', 'Of the nature of froth; light; empty; unsubstantial;\n   as, a frothy speaker or harangue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frounced', 'imp. & p. p.', 'of Frounce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frouncing', 'p. pr. & vb. n.', 'of Frounce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frounce', 'v. i.', 'To gather into or adorn with plaits, as a dress; to\n   form wrinkles in or upon; to curl or frizzle, as the hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frounce', 'v. i.', 'To form wrinkles in the forehead; to manifest\n   displeasure; to frown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frounce', 'n.', 'A wrinkle, plait, or curl; a flounce; -- also, a frown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frounce', 'n.', 'An affection in hawks, in which white spittle gathers\n   about the hawks bill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frounceless', 'a.', 'Without frounces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frouzy', 'a.', 'Fetid, musty; rank; disordered and offensive to the smell\n   or sight; slovenly; dingy. See Frowzy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frow', 'n.', 'A woman; especially, a Dutch or German woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frow', 'n.', 'A dirty woman; a slattern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frow', 'n.', 'A cleaving tool with handle at right angles to the blade, for\n   splitting cask staves and shingles from the block; a frower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frow', 'a.', 'Brittle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Froward', 'a.', 'Not willing to yield or compIy with what is required or is\n   reasonable; perverse; disobedient; peevish; as, a froward child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frower', 'n.', 'A tool. See 2d Frow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frowey', 'a.', 'Working smoothly, or without splitting; -- said of timber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frowned', 'imp. &, p. p.', 'of Frown');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frowning', 'p. pr. & vb. n.', 'of Frown');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frown', 'v. i.', 'To contract the brow in displeasure, severity, or\n   sternness; to scowl; to put on a stern, grim, or surly look.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frown', 'v. i.', 'To manifest displeasure or disapprobation; to look with\n   disfavor or threateningly; to lower; as, polite society frowns upon\n   rudeness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frown', 'v. t.', 'To repress or repel by expressing displeasure or\n   disapproval; to rebuke with a look; as, frown the impudent fellow into\n   silence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frown', 'n.', 'A wrinkling of the face in displeasure, rebuke, etc.; a\n   sour, severe, or stere look; a scowl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frown', 'n.', 'Any expression of displeasure; as, the frowns of Providence;\n   the frowns of Fortune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frowningly', 'adv.', 'In a frowning manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frowny', 'a.', 'Frowning; scowling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frowy', 'a.', 'Musty. rancid; as, frowy butter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frowzy', 'a.', 'Slovenly; unkempt; untidy; frouzy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Froze', '', 'imp. of Freeze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frozen', 'a.', 'Congealed with cold; affected by freezing; as, a frozen\n   brook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frozen', 'a.', 'Subject to frost, or to long and severe cold; chilly; as,\n   the frozen north; the frozen zones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frozen', 'a.', 'Cold-hearted; unsympathetic; unyielding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frozenness', 'n.', 'A state of being frozen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frubish', 'v. t.', 'To rub up: to furbish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fructed', 'a.', 'Bearing fruit; -- said of a tree or plant so represented\n   upon an escutcheon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fructescence', 'n.', 'The maturing or ripening of fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fructiculose', 'a.', 'Fruitful; full of fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fructidor', 'n.', 'The twelfth month of the French republican calendar; --\n   commencing August 18, and ending September 16. See Vendemiaire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fructiferuos', 'a.', 'Bearing or producing fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fructification', 'n.', 'The act of forming or producing fruit; the act of\n   fructifying, or rendering productive of fruit; fecundation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fructification', 'n.', 'The collective organs by which a plant produces its\n   fruit, or seeds, or reproductive spores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fructification', 'n.', 'The process of producing fruit, or seeds, or\n   spores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fructify', 'v. i.', 'To bear fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fructified', 'imp. & p. p.', 'of Fructify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fructifying', 'p. pr. & vb. n.', 'of Fructify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fructify', 'v. t.', 'To make fruitful; to render productive; to fertilize;\n   as, to fructify the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fructose', 'n.', 'Fruit sugar; levulose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fructuaries', 'pl. ', 'of Fructuary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fructuary', 'n.', 'One who enjoys the profits, income, or increase of\n   anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fructuation', 'n.', 'Produce; fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fructuous', 'a.', 'Fruitful; productive; profitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fructure', 'n.', 'Use; fruition; enjoyment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frue vanner', '', 'A moving, inclined, endless apron on which ore is\n   concentrated by a current of water; a kind of buddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frugal', 'n.', 'Economical in the use or appropriation of resources; not\n   wasteful or lavish; wise in the expenditure or application of force,\n   materials, time, etc.; characterized by frugality; sparing; economical;\n   saving; as, a frugal housekeeper; frugal of time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frugal', 'n.', 'Obtained by, or appropriate to, economy; as, a frugal\n   fortune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frugalities', 'pl. ', 'of Frugality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frugality', 'n.', 'The quality of being frugal; prudent economy; that\n   careful management of anything valuable which expends nothing\n   unnecessarily, and applies what is used to a profitable purpose;\n   thrift; --- opposed to extravagance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frugality', 'n.', 'A sparing use; sparingness; as, frugality of praise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frugally', 'adv.', 'Thriftily; prudently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frugalness', 'n.', 'Quality of being frugal; frugality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frugiferous', 'a.', 'Producing fruit; fruitful; fructiferous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frugivora', 'n. pl.', 'The fruit bate; a group of the Cheiroptera,\n   comprising the bats which live on fruits. See Eruit bat, under Fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frugivorous', 'a.', 'Feeding on fruit, as birds and other animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fruit', 'v. t.', 'Whatever is produced for the nourishment or enjoyment of\n   man or animals by the processes of vegetable growth, as corn, grass,\n   cotton, flax, etc.; -- commonly used in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fruit', 'v. t.', 'The pulpy, edible seed vessels of certain plants,\n   especially those grown on branches above ground, as apples, oranges,\n   grapes, melons, berries, etc. See 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fruit', 'v. t.', 'The ripened ovary of a flowering plant, with its contents\n   and whatever parts are consolidated with it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fruit', 'v. t.', 'The spore cases or conceptacles of flowerless plants, as\n   of ferns, mosses, algae, etc., with the spores contained in them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fruit', 'v. t.', 'The produce of animals; offspring; young; as, the fruit\n   of the womb, of the loins, of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fruit', 'v. t.', 'That which is produced; the effect or consequence of any\n   action; advantageous or desirable product or result; disadvantageous or\n   evil consequence or effect; as, the fruits of labor, of self-denial, of\n   intemperance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fruit', 'v. i.', 'To bear fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fruitage', 'n.', 'Fruit, collectively; fruit, in general; fruitery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fruitage', 'n.', 'Product or result of any action; effect, good or ill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fruiter', 'a.', 'A ship for carrying fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fruiterer', 'n.', 'One who deals in fruit; a seller of fruits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fruiteress', 'n.', 'A woman who sells fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fruiteries', 'pl. ', 'of Fruitery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fruitery', 'n.', 'Fruit, taken collectively; fruitage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fruitery', 'n.', 'A repository for fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fruitestere', 'n.', 'A fruiteress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fruitful', 'a.', 'Full of fruit; producing fruit abundantly; bearing\n   results; prolific; fertile; liberal; bountiful; as, a fruitful tree, or\n   season, or soil; a fruitful wife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fruiting', 'a.', 'Pertaining to, or producing, fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fruiting', 'n.', 'The bearing of fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fruition', 'n.', 'Use or possession of anything, especially such as is\n   accompanied with pleasure or satisfaction; pleasure derived from\n   possession or use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fruitive', 'a.', 'Enjoying; possessing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fruitless', 'a.', 'Lacking, or not bearing, fruit; barren; destitute of\n   offspring; as, a fruitless tree or shrub; a fruitless marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fruitless', 'a.', 'Productive of no advantage or good effect; vain; idle;\n   useless; unprofitable; as, a fruitless attempt; a fruitless\n   controversy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fruity', 'a.', 'Having the odor, taste, or appearance of fruit; also,\n   fruitful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frumentaceous', 'a.', 'Made of, or resembling, wheat or other grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frumentarious', 'a.', 'Of or pertaining to wheat or grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frumentation', 'n.', 'A largess of grain bestowed upon the people, to quiet\n   them when uneasy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frumenty', 'n.', 'Food made of hulled wheat boiled in milk, with sugar,\n   plums, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frump', 'v. t.', 'To insult; to flout; to mock; to snub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frump', 'n.', 'A contemptuous speech or piece of conduct; a gibe or flout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frump', 'n.', 'A cross, old-fashioned person; esp., an old woman; a gossip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frumper', 'n.', 'A mocker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frumpish', 'a.', 'Cross-tempered; scornful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frumpish', 'a.', 'Old-fashioned, as a womans dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frush', 'v. t.', 'To batter; to break in pieces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frush', 'a.', 'Easily broken; brittle; crisp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frush', 'n.', 'Noise; clatter; crash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frush', 'n.', 'The frog of a horses foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frush', 'n.', 'A discharge of a fetid or ichorous matter from the frog of a\n   horses foot; -- also caled thrush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frustrable', 'a.', 'Capable of beeing frustrated or defeated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frustraneous', 'a.', 'Vain; useless; unprofitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frustrate', 'a.', 'Vain; ineffectual; useless; unprofitable; null; voil;\n   nugatory; of no effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frustrated', 'imp. & p. p.', 'of Frustrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frustrating', 'p. pr. & vb. n.', 'of Frustrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frustrate', 'v. t.', 'To bring to nothing; to prevent from attaining a\n   purpose; to disappoint; to defeat; to baffle; as, to frustrate a plan,\n   design, or attempt; to frustrate the will or purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frustrate', 'v. t.', 'To make null; to nullifly; to render invalid or of no\n   effect; as, to frustrate a conveyance or deed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frustrately', 'adv.', 'In vain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frustration', 'n.', 'The act of frustrating; disappointment; defeat; as,\n   the frustration of ones designs');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frustrative', 'a.', 'Tending to defeat; fallacious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frustratory', 'a.', 'Making void; rendering null; as, a frustratory appeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frustule', 'n.', 'The siliceous shell of a diatom. It is composed of two\n   valves, one overlapping the other, like a pill box and its cover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frustulent', 'a.', 'Abounding in fragments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frusta', 'pl. ', 'of Frustum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frustums', 'pl. ', 'of Frustum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frustum', 'n.', 'The part of a solid next the base, formed by cutting off\n   the, top; or the part of any solid, as of a cone, pyramid, etc.,\n   between two planes, which may be either parallel or inclined to each\n   other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frustum', 'n.', 'One of the drums of the shaft of a column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frutage', 'n.', 'A picture of fruit; decoration by representation of fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frutage', 'n.', 'A confection of fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frutescent', 'a.', 'Somewhat shrubby in character; imperfectly shrubby, as\n   the American species of Wistaria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frutex', 'n.', 'A plant having a woody, durable stem, but less than a tree;\n   a shrub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fruticant', 'a.', 'Full of shoots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fruticose', 'a.', 'Pertaining to a shrub or shrubs; branching like a shrub;\n   shrubby; shrublike; as, a fruticose stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fruticous', 'a.', 'Fruticose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fruticulose', 'a.', 'Like, or pertaining to, a small shrub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fried', 'imp. & p. p.', 'of Fry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frying', 'p. pr. & vb. n.', 'of Fry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fry', 'v. t.', 'To cook in a pan or on a griddle (esp. with the use of fat,\n   butter, or olive oil) by heating over a fire; to cook in boiling lard\n   or fat; as, to fry fish; to fry doughnuts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fry', 'v. i.', 'To undergo the process of frying; to be subject to the\n   action of heat in a frying pan, or on a griddle, or in a kettle of hot\n   fat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fry', 'v. i.', 'To simmer; to boil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fry', 'v. i.', 'To undergo or cause a disturbing action accompanied with a\n   sensation of heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fry', 'v. i.', 'To be agitated; to be greatly moved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ery', 'n.', 'A dish of anything fried.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ery', 'n.', 'A state of excitement; as, to be in a fry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fry', 'n.', 'The young of any fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fry', 'n.', 'A swarm or crowd, especially of little fishes; young or small\n   things in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Frying', 'n.', 'The process denoted by the verb fry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuage', 'n.', 'Same as Fumage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuar', 'n.', 'Same as Feuar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fub', 'n.', 'Alt. of Fubs');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fubs', 'n.', 'A plump young person or child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fub', 'v. t.', 'To put off by trickery; to cheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fubbery', 'n.', 'Cheating; deception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fubby', 'a.', 'Alt. of Fubsy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fubsy', 'a.', 'Plump; chubby; short and stuffy; as a fubsy sofa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fucate', 'a.', 'Alt. of Fucated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fucated', 'a.', 'Painted; disguised with paint, or with false show.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuchs', 'n.', 'A student of the first year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuchsias', 'pl. ', 'of Fuchsia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuchslae', 'pl. ', 'of Fuchsia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuchsia', 'n.', 'A genus of flowering plants having elegant drooping\n   flowers, with four sepals, four petals, eight stamens, and a single\n   pistil. They are natives of Mexico and South America. Double-flowered\n   varieties are now common in cultivation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuchsine', 'n.', 'Aniline red; an artificial coal-tar dyestuff, of a\n   metallic green color superficially, resembling cantharides, but when\n   dissolved forming a brilliant dark red. It consists of a hydrochloride\n   or acetate of rosaniline. See Rosaniline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fucivorous', 'a.', 'Eating fucus or other seaweeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fucoid', 'a.', 'Properly, belonging to an order of alga: (Fucoideae) which\n   are blackish in color, and produce oospores which are not fertilized\n   until they have escaped from the conceptacle. The common rockweeds and\n   the gulfweed (Sargassum) are fucoid in character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fucoid', 'a.', 'In a vague sense, resembling seaweeds, or of the nature of\n   seaweeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fucoid', 'n.', 'A plant, whether recent or fossil, which resembles a\n   seaweed. See Fucoid, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fucoidal', 'a.', 'Fucoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fucoidal', 'a.', 'Containing impressions of fossil fucoids or seaweeds; as,\n   fucoidal sandstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuci', 'pl. ', 'of Fucus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fucus', 'n.', 'A paint; a dye; also, false show.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fucus', 'n.', 'A genus of tough, leathery seaweeds, usually of a dull\n   brownish green color; rockweed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fucusol', 'n.', 'An oily liquid, resembling, and possibly identical with,\n   furfurol, and obtained from fucus, and other seaweeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fud', 'n.', 'The tail of a hare, coney, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fud', 'n.', 'Woolen waste, for mixing with mungo and shoddy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fudder', 'n.', 'See Fodder, a weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuddled', 'imp. & p. p.,', 'of Fuddle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuddling', 'p. pr. & vb. n.', 'of Fuddle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuddle', 'v. t.', 'To make foolish by drink; to cause to become\n   intoxicated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuddle', 'v. i.', 'To drink to excess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuddler', 'n.', 'A drunkard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fudge', 'n.', 'A made-up story; stuff; nonsense; humbug; -- often an\n   exclamation of contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fudged', 'imp. & p. p.', 'of Fudge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fudging', 'p. pr. & vb. n.', 'of Fudge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fudge', 'v. t.', 'To make up; to devise; to contrive; to fabricate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fudge', 'v. t.', 'To foist; to interpolate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fudge wheel', '', 'A tool for ornamenting the edge of a sole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuegian', 'a.', 'Of or pertaining to Terra del Fuego.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuegian', 'n.', 'A native of Terra del Fuego.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuel', 'n.', 'Any matter used to produce heat by burning; that which feeds\n   fire; combustible matter used for fires, as wood, coal, peat, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuel', 'n.', 'Anything that serves to feed or increase passion or\n   excitement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuel', 'v. t.', 'To feed with fuel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuel', 'v. t.', 'To store or furnish with fuel or firing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fueler', 'n.', 'One who, or that which, supplies fuel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuero', 'n.', 'A code; a charter; a grant of privileges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuero', 'n.', 'A custom having the force of law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuero', 'n.', 'A declaration by a magistrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuero', 'n.', 'A place where justice is administered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuero', 'n.', 'The jurisdiction of a tribunal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuff', 'v. t. & i.', 'To puff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuffy', 'a.', 'Light; puffy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuga', 'n.', 'A fugue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fugacious', 'a.', 'Flying, or disposed to fly; fleeing away; lasting but a\n   short time; volatile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fugacious', 'a.', 'Fleeting; lasting but a short time; -- applied\n   particularly to organs or parts which are short-lived as compared with\n   the life of the individual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fugaciousness', 'n.', 'Fugacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fugacity', 'a.', 'The quality of being fugacious; fugaclousness;\n   volatility; as, fugacity of spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fugacity', 'a.', 'Uncertainty; instability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fugacy', 'n.', 'Banishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fugato', 'a.', 'in the gugue style, but not strictly like a fugue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fugato', 'n.', 'A composition resembling a fugue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fugh', 'interj.', 'An exclamation of disgust; foh; faugh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fughetta', 'n.', 'a short, condensed fugue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fugitive', 'a.', 'Fleeing from pursuit, danger, restraint, etc., escaping,\n   from service, duty etc.; as, a fugitive solder; a fugitive slave; a\n   fugitive debtor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fugitive', 'a.', 'Not fixed; not durable; liable to disappear or fall away;\n   volatile; uncertain; evanescent; liable to fade; -- applied to material\n   and immaterial things; as, fugitive colors; a fugitive idea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fugitive', 'n.', 'One who flees from pursuit, danger, restraint, service,\n   duty, etc.; a deserter; as, a fugitive from justice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fugitive', 'n.', 'Something hard to be caught or detained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fugitively', 'adv.', 'In a fugitive manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fugitiveness', 'n.', 'The quality or condition of being fugitive;\n   evanescence; volatility; fugacity; instability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fugle', 'v. i.', 'To maneuver; to move hither and thither.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuglemen', 'pl. ', 'of Fugleman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fugleman', 'n.', 'A soldier especially expert and well drilled, who takes\n   his place in front of a military company, as a guide for the others in\n   their exercises; a file leader. He originally stood in front of the\n   right wing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fugleman', 'n.', 'Hence, one who leads the way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fugue', 'n.', 'A polyphonic composition, developed from a given theme or\n   themes, according to strict contrapuntal rules. The theme is first\n   given out by one voice or part, and then, while that pursues its way,\n   it is repeated by another at the interval of a fifth or fourth, and so\n   on, until all the parts have answered one by one, continuing their\n   several melodies and interweaving them in one complex progressive\n   whole, in which the theme is often lost and reappears.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuguist', 'n.', 'A musician who composes or performs fugues.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ful', 'a.', 'A suffix signifying full of, abounding with; as, boastful,\n   harmful, woeful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulahs', 'n. pl.', 'Alt. of Foolahs');
  INSERT INTO words (word, word_type, description) VALUES  (    'Foolahs', 'n. pl.', 'A peculiar African race of uncertain origin, but\n   distinct from the negro tribes, inhabiting an extensive region of\n   Western Soudan. Their color is brown or yellowish bronze. They are\n   Mohammedans. Called also Fellatahs, Foulahs, and Fellani. Fulah is also\n   used adjectively; as, Fulah empire, tribes, language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulbe', 'n.', 'Same as Fulahs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuldble', 'a.', 'Capable of being propped up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulciment', 'n.', 'A prop; a fulcrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulcra', 'n. pl.', 'See Fulcrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulcrate', 'a.', 'Propped; supported by accessory organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulcrate', 'a.', 'Furnished with fulcrums.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulcra', 'pl. ', 'of Fulcrum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulcrums', 'pl. ', 'of Fulcrum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulcrum', 'n.', 'A prop or support.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulcrum', 'n.', 'That by which a lever is sustained, or about which it\n   turns in lifting or moving a body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulcrum', 'n.', 'An accessory organ such as a tendril, stipule, spine, and\n   the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulcrum', 'n.', 'The horny inferior surface of the lingua of certain\n   insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulcrum', 'n.', 'One of the small, spiniform scales found on the front edge\n   of the dorsal and caudal fins of many ganoid fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulcrum', 'n.', 'The connective tissue supporting the framework of the\n   retina of the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulfilled', 'imp. & p. p.', 'of Fulfill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulfilling', 'p. pr. & vb. n.', 'of Fulfill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulfill', 'v. t.', 'To fill up; to make full or complete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulfill', 'v. t.', 'To accomplish or carry into effect, as an intention,\n   promise, or prophecy, a desire, prayer, or requirement, etc.; to\n   complete by performance; to answer the requisitions of; to bring to\n   pass, as a purpose or design; to effectuate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulfiller', 'n.', 'One who fulfills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulfillment', 'n.', 'The act of fulfilling; accomplishment; completion; as,\n   the fulfillment of prophecy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulfillment', 'n.', 'Execution; performance; as, the fulfillment of a\n   promise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulgency', 'n.', 'Brightness; splendor; glitter; effulgence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulgent', 'a.', 'Exquisitely bright; shining; dazzling; effulgent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulgently', 'adv.', 'Dazzlingly; glitteringly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulgid', 'a.', 'Shining; glittering; dazzling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulgidity', 'n.', 'Splendor; resplendence; effulgence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulgor', 'n.', 'Dazzling brightness; splendor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulgurant', 'a.', 'Lightening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulgurata', 'n.', 'A spectro-electric tube in which the decomposition of a\n   liquid by the passage of an electric spark is observed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulgurate', 'v. i.', 'To flash as lightning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulgurating', 'a.', 'Resembling lightning; -- used to describe intense\n   lancinating pains accompanying locomotor ataxy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulguration', 'n.', 'The act of lightening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulguration', 'n.', 'The sudden brightening of a fused globule of gold or\n   silver, when the last film of the oxide of lead or copper leaves its\n   surface; -- also called blick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulgurite', 'n.', 'A vitrified sand tube produced by the striking of\n   lightning on sand; a lightning tube; also, the portion of rock surface\n   fused by a lightning discharge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulgury', 'n.', 'Lightning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulham', 'n.', 'A false die.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuliginosity', 'n.', 'The condition or quality of being fuliginous;\n   sootiness; matter deposited by smoke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuliginous', 'a.', 'Pertaining to soot; sooty; dark; dusky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuliginous', 'a.', 'Pertaining to smoke; resembling smoke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuliginously', 'adv.', 'In a smoky manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulimart', 'n.', 'Same as Foumart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Full', 'Compar.', 'Filled up, having within its limits all that it can\n   contain; supplied; not empty or vacant; -- said primarily of hollow\n   vessels, and hence of anything else; as, a cup full of water; a house\n   full of people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Full', 'Compar.', 'Abundantly furnished or provided; sufficient in.\n   quantity, quality, or degree; copious; plenteous; ample; adequate; as,\n   a full meal; a full supply; a full voice; a full compensation; a house\n   full of furniture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Full', 'Compar.', 'Not wanting in any essential quality; complete, entire;\n   perfect; adequate; as, a full narrative; a person of full age; a full\n   stop; a full face; the full moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Full', 'Compar.', 'Sated; surfeited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Full', 'Compar.', 'Having the mind filled with ideas; stocked with\n   knowledge; stored with information.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Full', 'Compar.', 'Having the attention, thoughts, etc., absorbed in any\n   matter, and the feelings more or less excited by it, as, to be full of\n   some project.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Full', 'Compar.', 'Filled with emotions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Full', 'Compar.', 'Impregnated; made pregnant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Full', 'n.', 'Complete measure; utmost extent; the highest state or degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Full', 'adv.', 'Quite; to the same degree; without abatement or diminution;\n   with the whole force or effect; thoroughly; completely; exactly;\n   entirely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Full', 'v. i.', 'To become full or wholly illuminated; as, the moon fulls\n   at midnight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulled', 'imp. & p. p.', 'of Full');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulling', 'p. pr. & vb. n.', 'of Full');
  INSERT INTO words (word, word_type, description) VALUES  (    'Full', 'n.', 'To thicken by moistening, heating, and pressing, as cloth; to\n   mill; to make compact; to scour, cleanse, and thicken in a mill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Full', 'v. i.', 'To become fulled or thickened; as, this material fulls\n   well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fullage', 'n.', 'The money or price paid for fulling or cleansing cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fullam', 'n.', 'A false die. See Fulham.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Full-blooded', 'a.', 'Having a full supply of blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Full-blooded', 'a.', 'Of pure blood; thoroughbred; as, a full-blooded\n   horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Full-bloomed', 'a.', 'Like a perfect blossom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Full-blown', 'a.', 'Fully expanded, as a blossom; as, a full-bloun rose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Full-blown', 'a.', 'Fully distended with wind, as a sail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Full-bottomed', 'a.', 'Full and large at the bottom, as wigs worn by\n   certain civil officers in Great Britain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Full-bottomed', 'a.', 'Of great capacity below the water line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Full-butt', 'adv.', 'With direct and violentop position; with sudden\n   collision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Full-drive', 'adv.', 'With full speed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuller', 'v. t.', 'One whose occupation is to full cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuller', 'a.', 'A die; a half-round set hammer, used for forming grooves\n   and spreading iron; -- called also a creaser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuller', 'v. t.', 'To form a groove or channel in, by a fuller or set\n   hammer; as, to fuller a bayonet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulleries', 'pl. ', 'of Fullery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fullery', 'n.', 'The place or the works where the fulling of cloth is\n   carried on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Full-formed', 'a.', 'Full in form or shape; rounded out with flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Full-grown', 'a.', 'Having reached the limits of growth; mature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Full-hearted', 'a.', 'Full of courage or confidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Full-hot', 'a.', 'Very fiery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulling', 'n.', 'The process of cleansing, shrinking, and thickening cloth\n   by moisture, heat, and pressure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Full-manned', 'a.', 'Completely furnished wiith men, as a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fullmart', 'n.', 'See Foumart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fullness', 'n.', 'The state of being full, or of abounding; abundance;\n   completeness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fullonical', 'a.', 'Pertaining to a fuller of cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Full-orbed', 'a.', 'Having the orb or disk complete or fully illuminated;\n   like the full moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Full-sailed', 'a.', 'Having all its sails set,; hence, without restriction\n   or reservation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Full-winged', 'a.', 'Having large and strong or complete wings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Full-winged', 'a.', 'Beady for flight; eager.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fully', 'adv.', 'In a full manner or degree; completely; entirely; without\n   lack or defect; adequately; satisfactorily; as, to be fully persuaded\n   of the truth of a proposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulmar', 'n.', 'One of several species of sea birds, of the family\n   procellariidae, allied to the albatrosses and petrels. Among the\n   well-known species are the arctic fulmar (Fulmarus glacialis) (called\n   also fulmar petrel, malduck, and mollemock), and the giant fulmar\n   (Ossifraga gigantea).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulminant', 'a.', 'Thundering; fulminating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulminated', 'imp. & p. p.', 'of Fulminate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulminating', 'p. pr. & vb. n.', 'of Fulminate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulminate', 'v. i.', 'To thunder; hence, to make a loud, sudden noise; to\n   detonate; to explode with a violent report.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulminate', 'v. i.', 'To issue or send forth decrees or censures with the\n   assumption of supreme authority; to thunder forth menaces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulminate', 'v. t.', 'To cause to explode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulminate', 'v. t.', 'To utter or send out with denunciations or censures;\n   -- said especially of menaces or censures uttered by ecclesiastical\n   authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulminate', 'v. i.', 'A salt of fulminic acid. See under Fulminic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulminate', 'v. i.', 'A fulminating powder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulminating', 'a.', 'Thundering; exploding in a peculiarly sudden or\n   violent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulminating', 'a.', 'Hurling denunciations, menaces, or censures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulmination', 'n.', 'The act of fulminating or exploding; detonation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulmination', 'n.', 'The act of thundering forth threats or censures, as\n   with authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulmination', 'n.', 'That which is fulminated or thundered forth; vehement\n   menace or censure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulminatory', 'a.', 'Thundering; striking terror.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulmine', 'v.', 'To thunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulmine', 'v. t.', 'To shoot; to dart like lightning; to fulminate; to\n   utter with authority or vehemence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulmineous', 'a.', 'Of, or concerning thunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulmiaic', 'a.', 'Pertaining to fulmination; detonating; specifically\n   (Chem.), pertaining to, derived from, or denoting, an acid, so called;\n   as, fulminic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulminuric', 'a.', 'Pertaining to fulminic and cyanuric acids, and\n   designating an acid so called.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Falness', 'n.', 'See Fullness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulsamic', 'a.', 'Fulsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulsome', 'a.', 'Full; abundant; plenteous; not shriveled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulsome', 'a.', 'Offending or disgusting by overfullness, excess, or\n   grossness; cloying; gross; nauseous; esp., offensive from excess of\n   praise; as, fulsome flattery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulsome', 'a.', 'Lustful; wanton; obscene; also, tending to obscenity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulvid', 'a.', 'Fulvous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fulvous', 'a.', 'Tawny; dull yellow, with a mixture of gray and brown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fum', 'v. i.', 'To play upon a fiddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumacious', 'a.', 'Smoky; hence, fond of smoking; addicted to smoking\n   tobacco.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumades', 'pl. ', 'of Fumado');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumadoes', 'pl. ', 'of Fumado');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumade', 'v. i.', 'Alt. of Fumado');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumado', 'v. i.', 'A salted and smoked fish, as the pilchard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumage', 'n.', 'Hearth money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumarate', 'n.', 'A salt of fumaric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumaric', 'a.', 'Pertaining to, or derived from, fumitory (Fumaria\n   officinalis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumarine', 'n.', 'An alkaloid extracted from fumitory, as a white\n   crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumarole', 'n.', 'A hole or spot in a volcanic or other region, from which\n   fumes issue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumatory', 'n.', 'See Fumitory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumbled', 'imp. & p. p.', 'of Fumble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumbling', 'p. pr. & vb. n.', 'of Fumble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumble', 'v. i.', 'To feel or grope about; to make awkward attempts to do\n   or find something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumble', 'v. i.', 'To grope about in perplexity; to seek awkwardly; as, to\n   fumble for an excuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumble', 'v. i.', 'To handle much; to play childishly; to turn over and\n   over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumble', 'v. t.', 'To handle or manage awkwardly; to crowd or tumble\n   together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumbler', 'n.', 'One who fumbles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumblingly', 'adv.', 'In the manner of one who fumbles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fume', 'n.', 'Exhalation; volatile matter (esp. noxious vapor or smoke)\n   ascending in a dense body; smoke; vapor; reek; as, the fumes of\n   tobacco.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fume', 'n.', 'Rage or excitement which deprives the mind of self-control;\n   as, the fumes of passion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fume', 'n.', 'Anything vaporlike, unsubstantial, or airy; idle conceit;\n   vain imagination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fume', 'n.', 'The incense of praise; inordinate flattery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumed', 'imp. & p. p.', 'of Fume');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuming', 'p. pr. & vb. n.', 'of Fume');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fume', 'n.', 'To smoke; to throw off fumes, as in combustion or chemical\n   action; to rise up, as vapor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fume', 'n.', 'To be as in a mist; to be dulled and stupefied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fume', 'n.', 'To pass off in fumes or vapors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fume', 'n.', 'To be in a rage; to be hot with anger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fume', 'v. t.', 'To expose to the action of fumes; to treat with vapors,\n   smoke, etc.; as, to bleach straw by fuming it with sulphur; to fill\n   with fumes, vapors, odors, etc., as a room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fume', 'v. t.', 'To praise inordinately; to flatter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fume', 'v. t.', 'To throw off in vapor, or as in the form of vapor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumeless', 'a.', 'Free from fumes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumer', 'n.', 'One that fumes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumer', 'n.', 'One who makes or uses perfumes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumerell', 'n.', 'See Femerell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumet', 'n.', 'The dung of deer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumet', 'n.', 'Alt. of Fumette');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumette', 'n.', 'The stench or high flavor of game or other meat when kept\n   long.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumetere', 'n.', 'Fumitory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumid', 'a.', 'Smoky; vaporous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumidity', 'n.', 'Alt. of Fumidness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumidness', 'n.', 'The state of being fumid; smokiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumiferous', 'a.', 'Producing smoke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumifugist', 'n.', 'One who, or that which, drives away smoke or fumes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumify', 'v. t.', 'To subject to the action of smoke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumigant', 'a.', 'Fuming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumigated', 'imp. & p. p.', 'of Fumigate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumigating', 'p. pr. & vb. n.', 'of Fumigate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumigate', 'n.', 'To apply smoke to; to expose to smoke or vapor; to\n   purify, or free from infection, by the use of smoke or vapors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumigate', 'n.', 'To smoke; to perfume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumigation', 'n.', 'The act of fumigating, or applying smoke or vapor, as\n   for disinfection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumigation', 'n.', 'Vapor raised in the process of fumigating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumigator', 'n.', 'One who, or that which, fumigates; an apparattus for\n   fumigating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumigatory', 'a.', 'Having the quality of purifying by smoke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumlly', 'adv.', 'Smokily; with fume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuming', 'a.', 'Producing fumes, or vapors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumingly', 'adv.', 'In a fuming manner; angrily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Famish', 'a.', 'Smoky; hot; choleric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumishness', 'n.', 'Choler; fretfulness; passion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumitez', 'n.', 'Fumitory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumitory', 'n.', 'The common uame of several species of the genus Fumaria,\n   annual herbs of the Old World, with finely dissected leaves and small\n   flowers in dense racemes or spikes. F. officinalis is a common species,\n   and was formerly used as an antiscorbutic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fummel', 'n.', 'A hinny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumosity', 'n.', 'The fumes of drink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumous', 'a.', 'Producing smoke; smoky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumous', 'a.', 'Producing fumes; full of fumes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fumy', 'a.', 'Producing fumes; fumous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fun', 'n.', 'Sport; merriment; frolicsome amusement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funambulate', 'v. i.', 'To walk or to dance on a rope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funambulation', 'n.', 'Ropedancing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funambulatory', 'a.', 'Performing like a ropedancer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funambulatory', 'a.', 'Narrow, like the walk of a ropedancer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funambulist', 'n.', 'A ropewalker or ropedancer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funambulo', 'n.', 'Alt. of Funambulus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funambulus', 'n.', 'A ropewalker or ropedancer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Function', 'n.', 'The act of executing or performing any duty, office, or\n   calling; per formance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Function', 'n.', 'The appropriate action of any special organ or part of an\n   animal or vegetable organism; as, the function of the heart or the\n   limbs; the function of leaves, sap, roots, etc.; life is the sum of the\n   functions of the various organs and parts of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Function', 'n.', 'The natural or assigned action of any power or faculty,\n   as of the soul, or of the intellect; the exertion of an energy of some\n   determinate kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Function', 'n.', 'The course of action which peculiarly pertains to any\n   public officer in church or state; the activity appropriate to any\n   business or profession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Function', 'n.', 'A quantity so connected with another quantity, that if\n   any alteration be made in the latter there will be a consequent\n   alteration in the former. Each quantity is said to be a function of the\n   other. Thus, the circumference of a circle is a function of the\n   diameter. If x be a symbol to which different numerical values can be\n   assigned, such expressions as x2, 3x, Log. x, and Sin. x, are all\n   functions of x.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Function', 'v. i.', 'Alt. of Functionate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Functionate', 'v. i.', 'To execute or perform a function; to transact ones\n   regular or appointed business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Functional', 'a.', 'Pertaining to, or connected with, a function or duty;\n   official.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Functional', 'a.', 'Pertaining to the function of an organ or part, or to\n   the functions in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Functionalize', 'v. t.', 'To assign to some function or office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Functionally', 'adv.', 'In a functional manner; as regards normal or\n   appropriate activity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Functionaries', 'pl. ', 'of Functionary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Functionary', 'n.', 'One charged with the performance of a function or\n   office; as, a public functionary; secular functionaries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Functionless', 'a.', 'Destitute of function, or of an appropriate organ.\n   Darwin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fund', 'n.', 'An aggregation or deposit of resources from which supplies\n   are or may be drawn for carrying on any work, or for maintaining\n   existence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fund', 'n.', 'A stock or capital; a sum of money appropriated as the\n   foundation of some commercial or other operation undertaken with a view\n   to profit; that reserve by means of which expenses and credit are\n   supported; as, the fund of a bank, commercial house, manufacturing\n   corporation, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fund', 'n.', 'The stock of a national debt; public securities; evidences\n   (stocks or bonds) of money lent to government, for which interest is\n   paid at prescribed intervals; -- called also public funds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fund', 'n.', 'An invested sum, whose income is devoted to a specific\n   object; as, the fund of an ecclesiastical society; a fund for the\n   maintenance of lectures or poor students; also, money systematically\n   collected to meet the expenses of some permanent object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fund', 'n.', 'A store laid up, from which one may draw at pleasure; a\n   supply; a full provision of resources; as, a fund of wisdom or good\n   sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funded', 'imp. & p. p.', 'of Fund');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funding', 'p. pr. & vb. n.', 'of Fund');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fund', 'v. t.', 'To provide and appropriate a fund or permanent revenue for\n   the payment of the interest of; to make permanent provision of\n   resources (as by a pledge of revenue from customs) for discharging the\n   interest of or principal of; as, to fund government notes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fund', 'v. t.', 'To place in a fund, as money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fund', 'v. t.', 'To put into the form of bonds or stocks bearing regular\n   interest; as, to fund the floating debt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fundable', 'a.', 'Capable of being funded, or converted into a fund;\n   convertible into bonds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fundament', 'n.', 'Foundation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fundament', 'n.', 'The part of the body on which one sits; the buttocks;\n   specifically (Anat.), the anus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fundamental', 'a.', 'Pertaining to the foundation or basis; serving for the\n   foundation. Hence: Essential, as an element, principle, or law;\n   important; original; elementary; as, a fundamental truth; a fundamental\n   axiom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fundamental', 'n.', 'A leading or primary principle, rule, law, or article,\n   which serves as the groundwork of a system; essential part, as, the\n   fundamentals of the Christian faith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fundamentally', 'adv.', 'Primarily; originally; essentially; radically; at\n   the foundation; in origin or constituents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funded', 'a.', 'Existing in the form of bonds bearing regular interest; as,\n   funded debt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funded', 'a.', 'Invested in public funds; as, funded money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fundholder', 'a.', 'One who has money invested in the public funds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funding', 'a.', 'Providing a fund for the payment of the interest or\n   principal of a debt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funding', 'a.', 'Investing in the public funds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funuless', 'a.', 'Destitute of funds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fundus', 'n.', 'The bottom or base of any hollow organ; as, the fundus of\n   the bladder; the fundus of the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funebrial', 'a.', 'Pertaining to a funeral or funerals; funeral; funereal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funebrious', 'a.', 'Funebrial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funeral', 'n.', 'The solemn rites used in the disposition of a dead human\n   body, whether such disposition be by interment, burning, or otherwise;\n   esp., the ceremony or solemnization of interment; obsequies; burial; --\n   formerly used in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funeral', 'n.', 'The procession attending the burial of the dead; the show\n   and accompaniments of an interment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funeral', 'n.', 'A funeral sermon; -- usually in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funeral', 'n.', 'Per. taining to a funeral; used at the interment of the\n   dead; as, funeral rites, honors, or ceremonies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funerate', 'v. t.', 'To bury with funeral rites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funeration', 'n.', 'The act of burying with funeral rites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funereal', 'a.', 'Suiting a funeral; pertaining to burial; solemn. Hence:\n   Dark; dismal; mournful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funest', 'a.', 'Lamentable; doleful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fungal', 'a.', 'Of or pertaining to fungi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fungate', 'n.', 'A salt of fungic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funge', 'n.', 'A blockhead; a dolt; a fool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fungi', 'n. pl.', 'See Fungus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fungia', 'n.', 'A genus of simple, stony corals; -- so called because they\n   are usually flat and circular, with radiating plates, like the gills of\n   a mushroom. Some of them are eighteen inches in diameter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fungian', 'a.', 'Of or pertaining to the Fungidae, a family of stony\n   corals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fungian', 'n.', 'One of the Fungidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fungibles', 'n. pl.', 'Things which may be furnished or restored in kind,\n   as distinguished from specific things; -- called also fungible things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fungibles', 'n. pl.', 'Movable goods which may be valued by weight or\n   measure, in contradistinction from those which must be judged of\n   individually.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fungic', 'a.', 'Pertaining to, or obtained from, mushrooms; as, fungic\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fungicide', 'n.', 'Anything that kills fungi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fungiform', 'a.', 'Shaped like a fungus or mushroom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fungilliform', 'a.', 'Shaped like a small fungus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fungin', 'n.', 'A name formerly given to cellulose found in certain fungi\n   and mushrooms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fungite', 'n.', 'A fossil coral resembling Fungia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fungivorous', 'a.', 'Eating fungi; -- said of certain insects and snails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fungoid', 'a.', 'Like a fungus; fungous; spongy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fungologist', 'n.', 'A mycologist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fungology', 'n.', 'Mycology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fungosity', 'n.', 'The quality of that which is fungous; fungous\n   excrescence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fungous', 'a.', 'Of the nature of fungi; spongy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fungous', 'a.', 'Growing suddenly, but not substantial or durable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fungi', 'pl. ', 'of Fungus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funguses', 'pl. ', 'of Fungus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fungus', 'n.', 'Any one of the Fungi, a large and very complex group of\n   thallophytes of low organization, -- the molds, mildews, rusts, smuts,\n   mushrooms, toadstools, puff balls, and the allies of each.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fungus', 'n.', 'A spongy, morbid growth or granulation in animal bodies, as\n   the proud flesh of wounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funic', 'a.', 'Funicular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funicle', 'n.', 'A small cord, ligature, or fiber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funicle', 'n.', 'The little stalk that attaches a seed to the placenta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funicular', 'a.', 'Consisting of a small cord or fiber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funicular', 'a.', 'Dependent on the tension of a cord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funicular', 'a.', 'Pertaining to a funiculus; made up of, or resembling, a\n   funiculus, or funiculi; as, a funicular ligament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funiculate', 'a.', 'Forming a narrow ridge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funiculi', 'pl. ', 'of Funiculus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funiculus', 'n.', 'A cord, baud, or bundle of fibers; esp., one of the\n   small bundles of fibers, of which large nerves are made up; applied\n   also to different bands of white matter in the brain and spinal cord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funiculus', 'n.', 'A short cord which connects the embryo of some myriapods\n   with the amnion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funiculus', 'n.', 'In Bryozoa, an organ extending back from the stomach.\n   See Bryozoa, and Phylactolema.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funiliform', 'a.', 'Resembling a cord in toughness and flexibility, as the\n   roots of some endogenous trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funis', 'n.', 'A cord; specifically, the umbilical cord or navel string.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funk', 'n.', 'An offensive smell; a stench.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funk', 'v. t.', 'To envelop with an offensive smell or smoke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funk', 'v. i.', 'To emit an offensive smell; to stink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funk', 'v. i.', 'To be frightened, and shrink back; to flinch; as, to funk\n   at the edge of a precipice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funk', 'n.', 'Alt. of Funking');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funking', 'n.', 'A shrinking back through fear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funky', 'a.', 'Pertaining to, or characterized by, great fear, or funking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funnel', 'v. t.', 'A vessel of the shape of an inverted hollow cone,\n   terminating below in a pipe, and used for conveying liquids into a\n   close vessel; a tunnel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funnel', 'v. t.', 'A passage or avenue for a fluid or flowing substance;\n   specifically, a smoke flue or pipe; the iron chimney of a steamship or\n   the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funnelform', 'a.', 'Having the form of a funnel, or tunnel; that is,\n   expanding gradually from the bottom upward, as the corolla of some\n   flowers; infundibuliform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funny', 'superl.', 'Droll; comical; amusing; laughable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funnies', 'pl. ', 'of Funny');
  INSERT INTO words (word, word_type, description) VALUES  (    'Funny', 'n.', 'A clinkerbuit, narrow boat for sculling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fur', 'n.', 'The short, fine, soft hair of certain animals, growing thick\n   on the skin, and distinguished from the hair, which is longer and\n   coarser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fur', 'n.', 'The skins of certain wild animals with the fur; peltry; as, a\n   cargo of furs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fur', 'n.', 'Strips of dressed skins with fur, used on garments for warmth\n   or for ornament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fur', 'n.', 'Articles of clothing made of fur; as, a set of furs for a lady\n   (a collar, tippet, or cape, muff, etc.).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fur', 'n.', 'Any coating considered as resembling fur');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fur', 'n.', 'A coat of morbid matter collected on the tongue in persons\n   affected with fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fur', 'n.', 'The soft, downy covering on the skin of a peach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fur', 'n.', 'The deposit formed on the interior of boilers and other\n   vessels by hard water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fur', 'n.', 'One of several patterns or diapers used as tinctures. There\n   are nine in all, or, according to some writers, only six.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fur', 'a.', 'Of or pertaining to furs; bearing or made of fur; as, a fur\n   cap; the fur trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furred', 'imp. & p. p.', 'of Fur');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furring', 'p. pr. & vb. n.', 'of Fur');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fur', 'v. t.', 'To line, face, or cover with fur; as, furred robes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fur', 'v. t.', 'To cover with morbid matter, as the tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fur', 'v. t.', 'To nail small strips of board or larger scantling upon, in\n   order to make a level surface for lathing or boarding, or to provide\n   for a space or interval back of the plastered or boarded surface, as\n   inside an outer wall, by way of protection against damp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furacious', 'a.', 'Given to theft; thievish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furacity', 'n.', 'Addictedness to theft; thievishness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furbelow', 'n.', 'A plaited or gathered flounce on a womans garment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furbelowed', 'imp. & p. p.', 'of Furhelow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furbelowing', 'p. pr. & vb. n.', 'of Furhelow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furhelow', 'v. t.', 'To put a furbelow on; to ornament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furbished', 'imp. & p. p.', 'of Furbish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furbishing', 'p. pr. & vb. n.', 'of Furbish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furbish', 'v. t.', 'To rub or scour to brightness; to clean; to burnish;\n   as, to furbish a sword or spear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furbishable', 'a.', 'Capable of being furbished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furbisher', 'n.', 'One who furbishes; esp., a sword cutler, who finishes\n   sword blades and similar weapons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furcate', 'a.', 'Alt. of Furcated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furcated', 'a.', 'Forked; branching like a fork; as, furcate twigs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furcation', 'n.', 'A branching like a. fork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furciferous', 'a.', 'Rascally; scandalous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furcula', 'n.', 'A forked process; the wishbone or furculum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furcular', 'a.', 'Shaped like a fork; furcate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furculum', 'n.', 'The wishbone or merrythought of birds, formed by the\n   united clavicles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furdle', 'v. t.', 'To draw up into a bundle; to roll up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furfur', 'n.', 'Scurf; dandruff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furfuraceous', 'a.', 'Made of bran; like bran; scurfy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furfuran', 'n.', 'A colorless, oily substance, C4H4O, obtained by\n   distilling certain organic substances, as pine wood, salts of pyromucic\n   acid, etc.; -- called also tetraphenol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furfuration', 'n.', 'Falling of scurf from the head; desquamation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furfurine', 'n.', 'A white, crystalline base, obtained indirectly from\n   furfurol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furfurol', 'n.', 'A colorless oily liquid, C4H3O.CHO, of a pleasant odor,\n   obtained by the distillation of bran, sugar, etc., and regarded as an\n   aldehyde derivative of furfuran; -- called also furfural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furfurous', 'a.', 'Made of bran; furfuraceous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furial', 'a.', 'Furious; raging; tormenting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furibundal', 'a.', 'Full of rage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furies', 'n. pl.', 'See Fury, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furile', 'n.', 'A yellow, crystalline substance, (C4H3O)2.C2O2, obtained by\n   the oxidation of furoin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furilic', 'a.', 'Pertaining to, or derived from, furile; as, furilic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furioso', 'a. & adv.', 'With great force or vigor; vehemently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furious', 'a.', 'Transported with passion or fury; raging; violent; as, a\n   furious animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furious', 'a.', 'Rushing with impetuosity; moving with violence; as, a\n   furious stream; a furious wind or storm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furld', 'imp. & p. p.', 'of Furl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furling', 'p. pr. & vb. n.', 'of Furl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furl', 'v. t.', 'To draw up or gather into close compass; to wrap or roll,\n   as a sail, close to the yard, stay, or mast, or, as a flag, close to or\n   around its staff, securing it there by a gasket or line. Totten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furlong', 'a.', 'A measure of length; the eighth part of a mile; forty\n   rods; two hundred and twenty yards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furlough', 'a.', 'Leave of abserice; especially, leave given to an offcer\n   or soldier to be absent from service for a certain time; also, the\n   document granting leave of absence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furloughed', 'imp. & p. p.', 'of Furlough');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furloughing', 'p. pr. & vb. n.', 'of Furlough');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furlough', 'v. t.', 'To furnish with a furlough; to grant leave of absence\n   to, as to an offcer or soldier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furmonty', 'n.', 'Alt. of Furmity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furmity', 'n.', 'Same as Frumenty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furnace', 'n.', 'An inclosed place in which heat is produced by the\n   combustion of fuel, as for reducing ores or melting metals, for warming\n   a house, for baking pottery, etc.; as, an iron furnace; a hot-air\n   furnace; a glass furnace; a boiler furnace, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furnace', 'n.', 'A place or time of punishment, affiction, or great trial;\n   severe experience or discipline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furnace', 'n.', 'To throw out, or exhale, as from a furnace; also, to put\n   into a furnace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furniment', 'n.', 'Furniture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furnished', 'imp. & p. p.', 'of Furnish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furnishing', 'p. pr. & vb. n.', 'of Furnish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furnish', 'v. t.', 'To supply with anything necessary, useful, or\n   appropriate; to provide; to equip; to fit out, or fit up; to adorn; as,\n   to furnish a family with provisions; to furnish one with arms for\n   defense; to furnish a Cable; to furnish the mind with ideas; to furnish\n   one with knowledge or principles; to furnish an expedition or\n   enterprise, a room or a house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furnish', 'v. t.', 'To offer for use; to provide (something); to give\n   (something); to afford; as, to furnish food to the hungry: to furnish\n   arms for defense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furnish', 'n.', 'That which is furnished as a specimen; a sample; a supply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furnisher', 'n.', 'One who supplies or fits out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furnishment', 'n.', 'The act of furnishing, or of supplying furniture;\n   also, furniture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furniture', 'v. t.', 'That with which anything is furnished or supplied;\n   supplies; outfit; equipment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furniture', 'v. t.', 'Articles used for convenience or decoration in a\n   house or apartment, as tables, chairs, bedsteads, sofas, carpets,\n   curtains, pictures, vases, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furniture', 'v. t.', 'The necessary appendages to anything, as to a\n   machine, a carriage, a ship, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furniture', 'v. t.', 'The masts and rigging of a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furniture', 'v. t.', 'The mountings of a gun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furniture', 'v. t.', 'Builders hardware such as locks, door and window\n   trimmings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furniture', 'v. t.', 'Pieces of wood or metal of a lesser height than the\n   type, placed around the pages or other matter in a form, and, with the\n   quoins, serving to secure the form in its place in the chase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furniture', 'v. t.', 'A mixed or compound stop in an organ; -- sometimes\n   called mixture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furoin', 'n.', 'A colorless, crystalline substance, C10H8O4, from furfurol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furore', 'n.', 'Excitement; commotion; enthusiasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furrier', 'n.', 'A dealer in furs; one who makes or sells fur goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furriery', 'n.', 'Furs, in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furriery', 'n.', 'The business of a furrier; trade in furs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furring', 'n.', 'The leveling of a surface, or the preparing of an air\n   space, by means of strips of board or of larger pieces. See Fur, v. t.,\n   3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furring', 'v. t.', 'The strips thus laid on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furring', 'v. t.', 'Double planking of a ships side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furring', 'v. t.', 'A deposit from water, as on the inside of a boiler;\n   also, the operation of cleaning away this deposit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furrow', 'n.', 'A trench in the earth made by, or as by, a plow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furrow', 'n.', 'Any trench, channel, or groove, as in wood or metal; a\n   wrinkle on the face; as, the furrows of age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furrowed', 'imp. & p. p.', 'of Furrow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furrowing', 'p. pr. & vb. n.', 'of Furrow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furrow', 'n.', 'To cut a furrow in; to make furrows in; to plow; as, to\n   furrow the ground or sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furrow', 'n.', 'To mark with channels or with wrinkles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furrowy', 'a.', 'Furrowed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furry', 'a.', 'Covered with fur; dressed in fur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furry', 'a.', 'Consisting of fur; as, furry spoils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furry', 'a.', 'Resembling fur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Further', 'adv.', 'To a greater distance; in addition; moreover. See\n   Farther.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Further', 'superl.', 'More remote; at a greater distance; more in advance;\n   farther; as, the further end of the field. See Farther.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Further', 'superl.', 'Beyond; additional; as, a further reason for this\n   opinion; nothing further to suggest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furthered', 'imp. & p. p.', 'of Further');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furthering', 'p. pr. & vb. n.', 'of Further');
  INSERT INTO words (word, word_type, description) VALUES  (    'Further', 'adv.', 'To help forward; to promote; to advance; to forward; to\n   help or assist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furtherance', 'n.', 'The act of furthering or helping forward; promotion;\n   advancement; progress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fartherer', 'n.', 'One who furthers. or helps to advance; a promoter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furthermore', 'adv.', 'or conj. Moreover; besides; in addition to what has\n   been said.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furthermost', 'a.', 'Most remote; furthest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furthersome', 'a.', 'Tending to further, advance, or promote; helpful;\n   advantageous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furthest', 'a.', 'superl. Most remote; most in advance; farthest. See\n   Further, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furthest', 'adv.', 'At the greatest distance; farthest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furtive', 'a.', 'Stolen; obtained or characterized by stealth; sly; secret;\n   stealthy; as, a furtive look.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furtively', 'adv.', 'Stealthily by theft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furuncle', 'n.', 'A superficial, inflammatory tumor, suppurating with a\n   central core; a boil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Faruncular', 'a.', 'Of or pertaining to a furuncle; marked by the presence\n   of furuncles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fury', 'n.', 'A thief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furies', 'pl. ', 'of Fury');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fury', 'n.', 'Violent or extreme excitement; overmastering agitation or\n   enthusiasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fury', 'n.', 'Violent anger; extreme wrath; rage; -- sometimes applied to\n   inanimate things, as the wind or storms; impetuosity; violence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fury', 'n.', 'pl. (Greek Myth.) The avenging deities, Tisiphone, Alecto,\n   and Megaera; the Erinyes or Eumenides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fury', 'n.', 'One of the Parcae, or Fates, esp. Atropos.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fury', 'n.', 'A stormy, turbulent violent woman; a hag; a vixen; a virago;\n   a termagant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furze', 'n.', 'A thorny evergreen shrub (Ulex Europaeus), with beautiful\n   yellow flowers, very common upon the plains and hills of Great Britain;\n   -- called also gorse, and whin. The dwarf furze is Ulex nanus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furzechat', 'n.', 'The whinchat; -- called also furzechuck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furzeling', 'n.', 'An English warbler (Melizophilus provincialis); --\n   called also furze wren, and Dartford warbler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furzen', 'a.', 'Furzy; gorsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Furzy', 'a. a.', 'bounding in, or overgrown with, furze; characterized by\n   furze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusain', 'n.', 'Fine charcoal of willow wood, used as a drawing implement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusain', 'n.', 'A drawing made with it. See Charcoal, n. 2, and Charcoal\n   drawing, under Charcoal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusarole', 'n.', 'A molding generally placed under the echinus or quarter\n   round of capitals in the Doric, Ionic, and Corinthian orders of\n   architecture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuscation', 'n.', 'A darkening; obscurity; obfuscation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuscin', 'n.', 'A brown, nitrogenous pigment contained in the retinal\n   epithelium; a variety of melanin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuscine', 'n.', 'A dark-colored substance obtained from empyreumatic animal\n   oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuscous', 'a.', 'Brown or grayish black; darkish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fused', 'imp. & p. p.', 'of Fuse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusing', 'p. pr. & vb. n.', 'of Fuse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuse', 'v. t.', 'To liquefy by heat; to render fiuid; to dissolve; to melt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuse', 'v. t.', 'To unite or blend, as if melted together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuse', 'v. i.', 'To be reduced from a solid to a Quid state by heat; to be\n   melted; to melt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuse', 'v. i.', 'To be blended, as if melted together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuse', 'n.', 'A tube or casing filled with combustible matter, by means of\n   which a charge of powder is ignited, as in blasting; -- called also\n   fuzee. See Fuze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusee', 'n.', 'A flintlock gun. See 2d Fusil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusee', 'n.', 'A fuse. See Fuse, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusee', 'n.', 'A kind of match for lighting a pipe or cigar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusee', 'n.', 'A small packet of explosive material with wire appendages\n   allowing it to be conveniently attached to a railroad track. It will\n   explode with a loud report when run over by a train, and is used to\n   provide a warning signal to the engineer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusee', 'n.', 'The track of a buck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusee', 'n.', 'The cone or conical wheel of a watch or clock, designed to\n   equalize the power of the mainspring by having the chain from the\n   barrel which contains the spring wind in a spiral groove on the surface\n   of the cone in such a manner that the diameter of the cone at the point\n   where the chain acts may correspond with the degree of tension of the\n   spring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusee', 'n.', 'A similar wheel used in other machinery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusel', '', 'Alt. of Fusel oil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusel oil', '', 'A hot, acrid, oily liquid, accompanying many alcoholic\n   liquors (as potato whisky, corn whisky, etc.), as an undesirable\n   ingredient, and consisting of several of the higher alcohols and\n   compound ethers, but particularly of amyl alcohol; hence, specifically\n   applied to amyl alcohol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusibility', 'n.', 'The quality of being fusible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusible', 'v. t.', 'CapabIe of being melted or liquefied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusiform', 'a.', 'Shaped like a spindle; tapering at each end; as, a\n   fusiform root; a fusiform cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusil', 'v. t.', 'Capable of being melted or rendered fluid by heat;\n   fusible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusil', 'v. t.', 'Running or flowing, as a liquid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusil', 'v. t.', 'Formed by melting and pouring into a mold; cast; founded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusil', 'n.', 'A light kind of flintlock musket, formerly in use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusil', 'n.', 'A bearing of a rhomboidal figure; -- named from its shape,\n   which resembles that of a spindle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusile', 'a.', 'Same as Fusil, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusileer', 'n.', 'Alt. of Fusilier');
