  INSERT INTO words (word, word_type, description) VALUES  (    'Fusilier', 'n.', 'A title now borne by some regiments and companies; as,\n   \"The Royal Fusiliers,\" etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusillade', 'n.', 'A simultaneous discharge of firearms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusillader', 'imp. & p. p.', 'of Fusillade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusillading', 'p. pr. & vb. n.', 'of Fusillade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusillade', 'v. t.', 'To shoot down of shoot at by a simultaneous discharge\n   of firearms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusion', 'v. t.', 'The act or operation of melting or rendering fluid by\n   heat; the act of melting together; as, the fusion of metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusion', 'v. t.', 'The state of being melted or dissolved by heat; a state\n   of fluidity or flowing in consequence of heat; as, metals in fusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusion', 'v. t.', 'The union or blending together of things, as, melted\n   together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusion', 'v. t.', 'The union, or binding together, of adjacent parts or\n   tissues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusome', 'a.', 'Handy; reat; handsome; notable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuss', 'n.', 'A tumult; a bustle; unnecessary or annoying ado about\n   trifles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuss', 'n.', 'One who is unduly anxious about trifles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fussed', 'imp. & p. p.', 'of Fuss');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fussing', 'p. pr. & vb. n.', 'of Fuss');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuss', 'v. i.', 'To be overbusy or unduly anxious about trifles; to make a\n   bustle or ado.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fussily', 'adv.', 'In a fussy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fussiness', 'n.', 'The quality of being fussy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fussy', 'superl', 'Making a fuss; disposed to make an unnecessary ado about\n   trifles; overnice; fidgety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fast', 'n.', 'The shaft of a column, or trunk of pilaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fust', 'n.', 'A strong, musty smell; mustiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fust', 'v. i.', 'To become moldy; to smell ill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusted', 'a.', 'Moldy; ill-smelling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusteric', 'n.', 'The coloring matter of fustet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fustet', 'n.', 'The wood of the Rhus Cptinus or Venice sumach, a shrub of\n   Southern Europe, which yields a fine orange color, which, however, is\n   not durable without a mordant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fustian', 'n.', 'A kind of coarse twilled cotton or cotton and linen stuff,\n   including corduroy, velveteen, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fustian', 'n.', 'An inflated style of writing; a kind of writing in which\n   high-sounding words are used, above the dignity of the thoughts or\n   subject; bombast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fustian', 'a.', 'Made of fustian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fustian', 'a.', 'Pompous; ridiculously tumid; inflated; bombastic; as,\n   fustian history.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fustianist', 'n.', 'A writer of fustian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fustic', 'n.', 'The wood of the Maclura tinctoria, a tree growing in the\n   West Indies, used in dyeing yellow; -- called also old fustic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fustigate', 'v. t.', 'To cudgel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fustigation', 'n.', 'A punishment by beating with a stick or club;\n   cudgeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fastilarian', 'n.', 'A low fellow; a stinkard; a scoundrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fustilug', 'n.', 'Alt. of Fustilugs');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fustilugs', 'n.', 'A gross, fat, unwieldy person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusiness', 'n.', 'A fusty state or quality; moldiness; mustiness; an ill\n   smell from moldiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusty', 'superl', 'Moldy; musty; ill-smelling; rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fusty', 'superl', 'Moping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fussure', 'v. t.', 'Act of fusing; fusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Futchel', 'n.', 'The jaws between which the hinder end of a carriage tongue\n   is inserted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Futile', 'v. t.', 'Talkative; loquacious; tattling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Futile', 'v. t.', 'Of no importance; answering no useful end; useless;\n   vain; worthless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Futilely', 'adv.', 'In a futile manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Futility', 'n.', 'The quality of being talkative; talkativeness;\n   loquaciousness; loquacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Futility', 'n.', 'The quality of producing no valuable effect, or of coming\n   to nothing; uselessness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Futilous', 'a.', 'Futile; trifling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Futtock', 'n.', 'One of the crooked timbers which are scarfed together to\n   form the lower part of the compound rib of a vessel; one of the crooked\n   transverse timbers passing across and over the keel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Futurable', 'a.', 'Capable of being future; possible to occur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Future', 'v. i.', 'That is to be or come hereafter; that will exist at any\n   time after the present; as, the next moment is future, to the present.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Future', 'a.', 'Time to come; time subsequent to the present (as, the\n   future shall be as the present); collectively, events that are to\n   happen in time to come.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Future', 'a.', 'The possibilities of the future; -- used especially of\n   prospective success or advancement; as, he had great future before him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Future', 'a.', 'A future tense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Futureless', 'a.', 'Without prospect of betterment in the future.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Futurely', 'adv.', 'In time to come.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Futurist', 'n.', 'One whose chief interests are in what is to come; one who\n   anxiously, eagerly, or confidently looks forward to the future; an\n   expectant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Futurist', 'n.', 'One who believes or maintains that the fulfillment of the\n   prophecies of the Bible is to be in the future.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Futuritial', 'a.', 'Relating to what is to come; pertaining to futurity;\n   future.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Futurition', 'n.', 'The state of being future; futurity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Futurities', 'pl. ', 'of Futurity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Futurity', 'n.', 'State of being that is yet to come; future state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Futurity', 'n.', 'Future time; time to come; the future.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Futurity', 'n.', 'Event to come; a future event.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuze', 'n.', 'A tube, filled with combustible matter, for exploding a\n   shell, etc. See Fuse, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuzz', 'v. t.', 'To make drunk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuzz', 'n.', 'Fine, light particles or fibers; loose, volatile matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuzz', 'v. i.', 'To fly off in minute particles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuzzle', 'v. t.', 'To make drunk; to intoxicate; to fuddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuzzy', 'n.', 'Not firmly woven; that ravels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fuzzy', 'n.', 'Furnished with fuzz; having fuzz; like fuzz; as, the fuzzy\n   skin of a peach.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-fy', '', 'A suffix signifying to make, to form into, etc.; as, acetify,\n   amplify, dandify, Frenchify, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fy', 'interj.', 'A word which expresses blame, dislike, disapprobation,\n   abhorrence, or contempt. See Fie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fyke', 'n.', 'A long bag net distended by hoops, into which fish can pass\n   easily, without being able to return; -- called also fyke net.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fyllot', 'n.', 'A rebated cross, formerly used as a secret emblem, and a\n   common ornament. It is also called gammadion, and swastika.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fyrd', 'v. i.', 'Alt. of Fyrdung');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fyrdung', 'v. i.', 'The military force of the whole nation, consisting of\n   all men able to bear arms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Fytte', 'n.', 'See Fit a song.\n   G () G is the seventh letter of the English alphabet, and a vocal\n   consonant. It has two sounds; one simple, as in gave, go, gull; the\n   other compound (like that of j), as in gem, gin, dingy. See Guide to\n   Pronunciation, // 231-6, 155, 176, 178, 179, 196, 211, 246.');
  INSERT INTO words (word, word_type, description) VALUES  (    'G', '', 'G is the name of the fifth tone of the natural or model scale; --\n   called also sol by the Italians and French. It was also originally used\n   as the treble clef, and has gradually changed into the character\n   represented in the margin. See Clef. G/ (G sharp) is a tone\n   intermediate between G and A.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gab', 'n.', 'The hook on the end of an eccentric rod opposite the strap.\n   See. Illust. of Eccentric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gab', 'v. i.', 'The mouth; hence, idle prate; chatter; unmeaning talk;\n   loquaciousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gab', 'v. i.', 'To deceive; to lie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gab', 'v. i.', 'To talk idly; to prate; to chatter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gabarage', 'n.', 'A kind of coarse cloth for packing goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gabardine', 'n.', 'Alt. of Gaberdine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaberdine', 'n.', 'A coarse frock or loose upper garment formerly worn by\n   Jews; a mean dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gabber', 'n.', 'A liar; a deceiver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gabber', 'n.', 'One addicted to idle talk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gabbled', 'imp. & p. p.', 'of Gabble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gabbling', 'p. pr. & vb. n.', 'of Gabble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gabble', 'v. i.', 'To talk fast, or to talk without meaning; to prate; to\n   jabber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gabble', 'v. i.', 'To utter inarticulate sounds with rapidity; as, gabbling\n   fowls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gabble', 'n.', 'Loud or rapid talk without meaning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gabble', 'n.', 'Inarticulate sounds rapidly uttered; as of fowls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gabbier', 'n.', 'One who gabbles; a prater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gabbro', 'n.', 'A name originally given by the Italians to a kind of\n   serpentine, later to the rock called euphotide, and now generally used\n   for a coarsely crystalline, igneous rock consisting of lamellar\n   pyroxene (diallage) and labradorite, with sometimes chrysolite (olivine\n   gabbro).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gabel', 'n.', 'A rent, service, tribute, custom, tax, impost, or duty; an\n   excise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gabeler', 'n.', 'A collector of gabels or taxes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gabelle', 'n.', 'A tax, especially on salt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gabelleman', 'n.', 'A gabeler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaberdine', 'n.', 'See Gabardine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaber-lunzie', 'n.', 'A beggar with a wallet; a licensed beggar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gabert', 'n.', 'A lighter, or vessel for inland navigation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gabion', 'n.', 'A hollow cylinder of wickerwork, like a basket without a\n   bottom. Gabions are made of various sizes, and filled with earth in\n   building fieldworks to shelter men from an enemys fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gabion', 'n.', 'An openwork frame, as of poles, filled with stones and\n   sunk, to assist in forming a bar dyke, etc., as in harbor improvement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gabionade', 'n.', 'A traverse made with gabions between guns or on their\n   flanks, protecting them from enfilading fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gabionade', 'n.', 'A structure of gabions sunk in lines, as a core for a\n   sand bar in harbor improvements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gabionage', 'n.', 'The part of a fortification built of gabions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gabioned', 'p. a.', 'Furnished with gabions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gabionnade', 'n.', 'See Gabionade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gable', 'n.', 'A cable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gable', 'n.', 'The vertical triangular portion of the end of a building,\n   from the level of the cornice or eaves to the ridge of the roof. Also,\n   a similar end when not triangular in shape, as of a gambrel roof and\n   the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gable', 'n.', 'The end wall of a building, as distinguished from the front\n   or rear side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gable', 'n.', 'A decorative member having the shape of a triangular gable,\n   such as that above a Gothic arch in a doorway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gablet', 'n.', 'A small gable, or gable-shaped canopy, formed over a\n   tabernacle, niche, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gablock', 'n.', 'A false spur or gaff, fitted on the heel of a gamecock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaby', 'n.', 'A simpleton; a dunce; a lout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gad', 'n.', 'The point of a spear, or an arrowhead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gad', 'n.', 'A pointed or wedge-shaped instrument of metal, as a steel\n   wedge used in mining, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gad', 'n.', 'A sharp-pointed rod; a goad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gad', 'n.', 'A spike on a gauntlet; a gadling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gad', 'n.', 'A wedge-shaped billet of iron or steel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gad', 'n.', 'A rod or stick, as a fishing rod, a measuring rod, or a rod\n   used to drive cattle with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gadded', 'imp. & p. p.', 'of Gad');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gadding', 'p. pr. & vb. n.', 'of Gad');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gad', 'n.', 'To walk about; to rove or go about, without purpose; hence, to\n   run wild; to be uncontrolled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gadabout', 'n.', 'A gadder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gadbee', 'n.', 'The gadfly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gadder', 'n.', 'One who roves about idly, a rambling gossip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gadding', 'a. & n.', 'Going about much, needlessly or without purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaddingly', 'adv.', 'In a roving, idle manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaddish', 'a.', 'Disposed to gad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gade', 'n.', 'A small British fish (Motella argenteola) of the Cod family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gade', 'n.', 'A pike, so called at Moray Firth; -- called also gead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gadere', 'v. t. & i.', 'Alt. of Gadre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gadre', 'v. t. & i.', 'To gather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gadflies', 'pl. ', 'of Gadfly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gadfly', 'n.', 'Any dipterous insect of the genus Oestrus, and allied\n   genera of botflies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gadhelic', 'a.', 'Of or pertaining to that division of the Celtic\n   languages, which includes the Irish, Gaelic, and Manx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gadic', 'a.', 'Pertaining to, or derived from, the cod (Gadus); -- applied\n   to an acid obtained from cod-liver oil, viz., gadic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaditanian', 'a.', 'Of or relating to Cadiz, in Spain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaditanian', 'n.', 'A native or inhabitant of Cadiz.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gadling', 'n.', 'See Gad, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gadling', 'v. i.', 'Gadding about.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gadling', 'n.', 'A roving vagabond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gadman', 'n.', 'A gadsman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gadoid', 'a.', 'Of or pertaining to the family of fishes (Gadidae) which\n   includes the cod, haddock, and hake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gadoid', 'n.', 'One of the Gadidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gadolinia', 'n.', 'A rare earth, regarded by some as an oxide of the\n   supposed element gadolinium, by others as only a mixture of the oxides\n   of yttrium, erbium, ytterbium, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gadolinic', 'a.', 'Pertaining to or containing gadolinium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gadolinite', 'n.', 'A mineral of a nearly black color and vitreous luster,\n   and consisting principally of the silicates of yttrium, cerium, and\n   iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gadolinium', 'n.', 'A supposed rare metallic element, with a characteristic\n   spectrum, found associated with yttrium and other rare metals. Its\n   individuality and properties have not yet been determined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gadsman', 'n.', 'One who uses a gad or goad in driving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaduin', 'n.', 'A yellow or brown amorphous substance, of indifferent\n   nature, found in cod-liver oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gadwall', 'n.', 'A large duck (Anas strepera), valued as a game bird, found\n   in the northern parts of Europe and America; -- called also gray duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gael', 'n.sing. & pl.', 'A Celt or the Celts of the Scotch Highlands or of\n   Ireland; now esp., a Scotch Highlander of Celtic origin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaelic', 'a.', 'Of or pertaining to the Gael, esp. to the Celtic\n   Highlanders of Scotland; as, the Gaelic language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaelic', 'n.', 'The language of the Gaels, esp. of the Highlanders of\n   Scotland. It is a branch of the Celtic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaff', 'n.', 'A barbed spear or a hook with a handle, used by fishermen in\n   securing heavy fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaff', 'n.', 'The spar upon which the upper edge of a fore-and-aft sail is\n   extended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaff', 'n.', 'Same as Gaffle, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaffed', 'imp. & p. p.', 'of Gaff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaffing', 'p. pr. & vb. n.', 'of Gaff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaff', 'v. t.', 'To strike with a gaff or barbed spear; to secure by means\n   of a gaff; as, to gaff a salmon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaffer', 'n.', 'An old fellow; an aged rustic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaffer', 'n.', 'A foreman or overseer of a gang of laborers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaffle', 'n.', 'An artificial spur or gaff for gamecocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaffle', 'n.', 'A lever to bend crossbows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaff-topsail', 'n.', 'A small triangular sail having its foot extended upon\n   the gaff and its luff upon the topmast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gagged', 'imp. & p. p.', 'of Gag');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gagging', 'p. pr. & vb. n.', 'of Gag');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gag', 'v. t.', 'To stop the mouth of, by thrusting sometimes in, so as to\n   hinder speaking; hence, to silence by authority or by violence; not to\n   allow freedom of speech to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gag', 'v. t.', 'To pry or hold open by means of a gag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gag', 'v. t.', 'To cause to heave with nausea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gag', 'v. i.', 'To heave with nausea; to retch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gag', 'v. i.', 'To introduce gags or interpolations. See Gag, n., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gag', 'n.', 'Something thrust into the mouth or throat to hinder speaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gag', 'n.', 'A mouthful that makes one retch; a choking bit; as, a gag of\n   mutton fat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gag', 'n.', 'A speech or phrase interpolated offhand by an actor on the\n   stage in his part as written, usually consisting of some seasonable or\n   local allusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gagate', 'n.', 'Agate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gage', 'n.', 'A pledge or pawn; something laid down or given as a security\n   for the performance of some act by the person depositing it, and\n   forfeited by nonperformance; security.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gage', 'n.', 'A glove, cap, or the like, cast on the ground as a challenge\n   to combat, and to be taken up by the accepter of the challenge; a\n   challenge; a defiance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gage', 'n.', 'A variety of plum; as, the greengage; also, the blue gage,\n   frost gage, golden gage, etc., having more or less likeness to the\n   greengage. See Greengage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaged', 'imp. & p. p.', 'of Gage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaging', 'p. pr & vb. n.', 'of Gage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gage', 'n.', 'To give or deposit as a pledge or security for some act; to\n   wage or wager; to pawn or pledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gage', 'n.', 'To bind by pledge, or security; to engage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gage', 'n.', 'A measure or standard. See Gauge, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gage', 'v. t.', 'To measure. See Gauge, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gager', 'n.', 'A measurer. See Gauger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gagger', 'n.', 'One who gags.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gagger', 'n.', 'A piece of iron imbedded in the sand of a mold to keep the\n   sand in place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaggled', 'imp. & p. p.', 'of Gaggle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaggling', 'p. pr. & vb. n.', 'of Gaggle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaggle', 'v. i.', 'To make a noise like a goose; to cackle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaggle', 'v. i.', 'A flock of wild geese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gagtooth', 'n.', 'A projecting tooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gag-toothed', 'a.', 'Having gagteeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gahnite', 'n.', 'Zinc spinel; automolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaidic', 'a.', 'Pertaining to hypogeic acid; -- applied to an acid obtained\n   from hypogeic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaiety', 'n.', 'Same as Gayety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gailer', 'n.', 'A jailer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaillard', 'a.', 'Gay; brisk; merry; galliard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gailliarde', 'n.', 'A lively French and Italian dance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaily', 'adv.', 'Merrily; showily. See gaily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gain', 'n.', 'A square or beveled notch cut out of a girder, binding joist,\n   or other timber which supports a floor beam, so as to receive the end\n   of the floor beam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gain', 'a.', 'Convenient; suitable; direct; near; handy; dexterous; easy;\n   profitable; cheap; respectable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gain', 'v. t.', 'That which is gained, obtained, or acquired, as increase,\n   profit, advantage, or benefit; -- opposed to loss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gain', 'v. t.', 'The obtaining or amassing of profit or valuable\n   possessions; acquisition; accumulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gained', 'imp. & p. p.', 'of Gain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaining', 'p. pr. & vb. n.', 'of Gain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gain', 'n.', 'To get, as profit or advantage; to obtain or acquire by\n   effort or labor; as, to gain a good living.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gain', 'n.', 'To come off winner or victor in; to be successful in; to\n   obtain by competition; as, to gain a battle; to gain a case at law; to\n   gain a prize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gain', 'n.', 'To draw into any interest or party; to win to ones side; to\n   conciliate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gain', 'n.', 'To reach; to attain to; to arrive at; as, to gain the top of\n   a mountain; to gain a good harbor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gain', 'n.', 'To get, incur, or receive, as loss, harm, or damage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gain', 'v. i.', 'To have or receive advantage or profit; to acquire gain;\n   to grow rich; to advance in interest, health, or happiness; to make\n   progress; as, the sick man gains daily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gainable', 'v. t.', 'Capable of being obtained or reached.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gainage', 'v. t.', 'The horses, oxen, plows, wains or wagons and implements\n   for carrying on tillage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gainage', 'v. t.', 'The profit made by tillage; also, the land itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gainer', 'n.', 'One who gains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gainful', 'a.', 'Profitable; advantageous; lucrative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaingiving', 'n.', 'A misgiving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gainless', 'a.', 'Not producing gain; unprofitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gainly', 'a.', 'Handily; readily; dexterously; advantageously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gainpain', 'n.', 'Bread-gainer; -- a term applied in the Middle Ages to the\n   sword of a hired soldier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gainsaid', 'imp. & p. p.', 'of Gainsay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gainsaying', 'p. pr. & vb. n.', 'of Gainsay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gainsay', 'v. t.', 'To contradict; to deny; to controvert; to dispute; to\n   forbid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gainsayer', 'n.', 'One who gainsays, contradicts, or denies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gainsome', 'a.', 'Gainful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gainsome', 'a.', 'Prepossessing; well-favored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gainst', 'prep.', 'A contraction of Against.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gainstood', 'imp. & p. p.', 'of Gainstand');
  INSERT INTO words (word, word_type, description) VALUES  (    'gainstanding', 'p. pr. & vb. n.', 'of Gainstand');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gainstand', 'v. t.', 'To withstand; to resist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gainstrive', 'v. t. & i.', 'To strive or struggle against; to withstand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gairfowl', 'n.', 'See Garefowl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gairish', 'n.', 'Alt. of Gairish/ness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gairishly', 'n.', 'Alt. of Gairish/ness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gairish/ness', 'n.', 'Same as Garish, Garishly, Garishness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gait', 'n.', 'A going; a walk; a march; a way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gait', 'n.', 'Manner of walking or stepping; bearing or carriage while\n   moving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaited', 'a.', 'Having (such) a gait; -- used in composition; as,\n   slow-gaited; heavy-gaited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaiter', 'n.', 'A covering of cloth or leather for the ankle and instep, or\n   for the whole leg from the knee to the instep, fitting down upon the\n   shoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaiter', 'n.', 'A kind of shoe, consisting of cloth, and covering the\n   ankle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaiter', 'v. t.', 'To dress with gaiters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaitre', 'n.', 'Alt. of Gaytre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaytre', 'n.', 'The dogwood tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gala', 'n.', 'Pomp, show, or festivity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galacta-gogue', 'n.', 'An agent exciting secretion of milk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galactic', 'a.', 'Of or pertaining to milk; got from milk; as, galactic\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galactic', 'a.', 'Of or pertaining to the galaxy or Milky Way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galactin', 'n.', 'An amorphous, gelatinous substance containing nitrogen,\n   found in milk and other animal fluids. It resembles peptone, and is\n   variously regarded as a coagulating or emulsifying agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galactin', 'n.', 'A white waxy substance found in the sap of the South\n   American cow tree (Galactodendron).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galactin', 'n.', 'An amorphous, gummy carbohydrate resembling gelose, found\n   in the seeds of leguminous plants, and yielding on decomposition\n   several sugars, including galactose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galactodensimeter', 'n.', 'Same as Galactometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galactometer', 'n.', 'An instrument for ascertaining the quality of milk\n   (i.e., its richness in cream) by determining its specific gravity; a\n   lactometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galactophagist', 'n.', 'One who eats, or subsists on, milk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galactophagous', 'a.', 'Feeding on milk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galactophorous', 'a.', 'Milk-carrying; lactiferous; -- applied to the ducts\n   of mammary glands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galactopoietic', 'a.', 'Increasing the flow of milk; milk-producing. -- n.\n   A galactopoietic substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galactose', 'n.', 'A white, crystalline sugar, C6H12O6, isomeric with\n   dextrose, obtained by the decomposition of milk sugar, and also from\n   certain gums. When oxidized it forms mucic acid. Called also lactose\n   (though it is not lactose proper).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galage', 'n.', 'See Galoche.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galagos', 'pl. ', 'of Galago');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galago', 'n.', 'A genus of African lemurs, including numerous species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galanga', 'n.', 'Alt. of Galangal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galangal', 'n.', 'The pungent aromatic rhizome or tuber of certain East\n   Indian or Chinese species of Alpinia (A. Galanga and A. officinarum)\n   and of the Kaempferia Galanga), -- all of the Ginger family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galantine', 'n.', 'A dish of veal, chickens, or other white meat, freed\n   from bones, tied up, boiled, and served cold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galapee tree', '', 'The West Indian Sciadophyllum Brownei, a tree with very\n   large digitate leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galatian', 'a.', 'Of or pertaining to Galatia or its inhabitants. -- A\n   native or inhabitant of Galatia, in Asia Minor; a descendant of the\n   Gauls who settled in Asia Minor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galaxies', 'pl. ', 'of Galaxy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galaxy', 'n.', 'The Milky Way; that luminous tract, or belt, which is seen\n   at night stretching across the heavens, and which is composed of\n   innumerable stars, so distant and blended as to be distinguishable only\n   with the telescope. The term has recently been used for remote clusters\n   of stars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galaxy', 'n.', 'A splendid assemblage of persons or things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galban', 'n.', 'Alt. of Galbanum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galbanum', 'n.', 'A gum resin exuding from the stems of certain Asiatic\n   umbelliferous plants, mostly species of Ferula. The Bubon Galbanum of\n   South Africa furnishes an inferior kind of galbanum. It has an acrid,\n   bitter taste, a strong, unpleasant smell, and is used for medical\n   purposes, also in the arts, as in the manufacture of varnish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gale', 'n.', 'A strong current of air; a wind between a stiff breeze and a\n   hurricane. The most violent gales are called tempests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gale', 'n.', 'A moderate current of air; a breeze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gale', 'n.', 'A state of excitement, passion, or hilarity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gale', 'v. i.', 'To sale, or sail fast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gale', 'n.', 'A song or story.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gale', 'v. i.', 'To sing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gale', 'n.', 'A plant of the genus Myrica, growing in wet places, and\n   strongly resembling the bayberry. The sweet gale (Myrica Gale) is found\n   both in Europe and in America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gale', 'n.', 'The payment of a rent or annuity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galea', 'n.', 'The upper lip or helmet-shaped part of a labiate flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galea', 'n.', 'A kind of bandage for the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galea', 'n.', 'Headache extending all over the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galea', 'n.', 'A genus of fossil echini, having a vaulted, helmet-shaped\n   shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galea', 'n.', 'The anterior, outer process of the second joint of the\n   maxillae in certain insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galeas', 'n.', 'See Galleass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galeate', 'a.', 'Alt. of Galeated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galeated', 'a.', 'Wearing a helmet; protected by a helmet; covered, as with\n   a helmet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galeated', 'a.', 'Helmeted; having a helmetlike part, as a crest, a flower,\n   etc.; helmet-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galei', 'n. pl.', 'That division of elasmobranch fishes which includes the\n   sharks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galena', 'n.', 'A remedy or antidose for poison; theriaca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galena', 'n.', 'Lead sulphide; the principal ore of lead. It is of a bluish\n   gray color and metallic luster, and is cubic in crystallization and\n   cleavage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galenic', 'a.', 'Alt. of Galenical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galenical', 'a.', 'Pertaining to, or containing, galena.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galenic', 'an.', 'Alt. of Galenical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galenical', 'an.', 'Relating to Galen or to his principles and method of\n   treating diseases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galenism', 'n.', 'The doctrines of Galen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galenist', 'n.', 'A follower of Galen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galenite', 'n.', 'Galena; lead ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gale-opithecus', 'n.', 'A genus of flying Insectivora, formerly called\n   flying lemurs. See Colugo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galericu-late', 'a.', 'Covered as with a hat or cap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galerite', 'n.', 'A cretaceous fossil sea urchin of the genus Galerites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galician', 'a.', 'Of or pertaining to Galicia, in Spain, or to Galicia, the\n   kingdom of Austrian Poland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galician', 'n.', 'A native of Galicia in Spain; -- called also Gallegan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galilean', 'a.', 'Of or pertaining to Galileo; as, the Galilean telescope.\n   See Telescope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galilean', 'a.', 'Of or relating to Galilee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galilean', 'n.', 'A native or inhabitant of Galilee, the northern province\n   of Palestine under the Romans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galilean', 'n.', 'One of the party among the Jews, who opposed the payment\n   of tribute to the Romans; -- called also Gaulonite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galilean', 'n.', 'A Christian in general; -- used as a term of reproach by\n   Mohammedans and Pagans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galilee', 'n.', 'A porch or waiting room, usually at the west end of an\n   abbey church, where the monks collected on returning from processions,\n   where bodies were laid previous to interment, and where women were\n   allowed to see the monks to whom they were related, or to hear divine\n   service. Also, frequently applied to the porch of a church, as at Ely\n   and Durham cathedrals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galimatias', 'n.', 'Nonsense; gibberish; confused and unmeaning talk;\n   confused mixture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galingale', 'n.', 'A plant of the Sedge family (Cyperus longus) having\n   aromatic roots; also, any plant of the same genus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galiot', 'n.', 'A small galley, formerly used in the Mediterranean, built\n   mainly for speed. It was moved both by sails and oars, having one mast,\n   and sixteen or twenty seats for rowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galiot', 'n.', 'A strong, light-draft, Dutch merchant vessel, carrying a\n   mainmast and a mizzenmast, and a large gaff mainsail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galipot', 'n.', 'An impure resin of turpentine, hardened on the outside of\n   pine trees by the spontaneous evaporation of its essential oil. When\n   purified, it is called yellow pitch, white pitch, or Burgundy pitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gall', 'n.', 'The bitter, alkaline, viscid fluid found in the gall bladder,\n   beneath the liver. It consists of the secretion of the liver, or bile,\n   mixed with that of the mucous membrane of the gall bladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gall', 'n.', 'The gall bladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gall', 'n.', 'Anything extremely bitter; bitterness; rancor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gall', 'n.', 'Impudence; brazen assurance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gall', 'n.', 'An excrescence of any form produced on any part of a plant by\n   insects or their larvae. They are most commonly caused by small\n   Hymenoptera and Diptera which puncture the bark and lay their eggs in\n   the wounds. The larvae live within the galls. Some galls are due to\n   aphids, mites, etc. See Gallnut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gall', 'v. t.', 'To impregnate with a decoction of gallnuts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galled', 'imp. & p. p.', 'of Gall');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galling', 'p. pr. & vb. n.', 'of Gall');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gall', 'v. t.', 'To fret and wear away by friction; to hurt or break the\n   skin of by rubbing; to chafe; to injure the surface of by attrition;\n   as, a saddle galls the back of a horse; to gall a mast or a cable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gall', 'v. t.', 'To fret; to vex; as, to be galled by sarcasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gall', 'v. t.', 'To injure; to harass; to annoy; as, the troops were galled\n   by the shot of the enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gall', 'v. i.', 'To scoff; to jeer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gall', 'n.', 'A wound in the skin made by rubbing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallant', 'a.', 'Showy; splendid; magnificent; gay; well-dressed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallant', 'a.', 'Noble in bearing or spirit; brave; high-spirited;\n   courageous; heroic; magnanimous; as, a gallant youth; a gallant\n   officer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallant', 'a.', 'Polite and attentive to ladies; courteous to women;\n   chivalrous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallant', 'n.', 'A man of mettle or spirit; a gay; fashionable man; a young\n   blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallant', 'n.', 'One fond of paying attention to ladies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallant', 'n.', 'One who wooes; a lover; a suitor; in a bad sense, a\n   seducer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallanted', 'imp. & p. p.', 'of Gallant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallanting', 'p. pr. & vb. n.', 'of Gallant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallant', 'v. t.', 'To attend or wait on, as a lady; as, to gallant ladies\n   to the play.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallant', 'v. t.', 'To handle with grace or in a modish manner; as, to\n   gallant a fan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallantly', 'adv.', 'In a polite or courtly manner; like a gallant or\n   wooer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallantly', 'adv.', 'In a gallant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallantness', 'n.', 'The quality of being gallant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallantries', 'pl. ', 'of Gallantry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallantry', 'n.', 'Splendor of appearance; ostentatious finery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallantry', 'n.', 'Bravery; intrepidity; as, the troops behaved with great\n   gallantry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallantry', 'n.', 'Civility or polite attention to ladies; in a bad sense,\n   attention or courtesy designed to win criminal favors from a female;\n   freedom of principle or practice with respect to female virtue;\n   intrigue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallantry', 'n.', 'Gallant persons, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallate', 'n.', 'A salt of gallic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallature', 'n.', 'The tread, treadle, or chalasa of an egg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galleass', 'n.', 'A large galley, having some features of the galleon, as\n   broadside guns; esp., such a vessel used by the southern nations of\n   Europe in the 16th and 17th centuries. See Galleon, and Galley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallegan', 'n.', 'Alt. of Gallego');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallego', 'n.', 'A native or inhabitant of Galicia, in Spain; a Galician.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallein', 'n.', 'A red crystalline dyestuff, obtained by heating together\n   pyrogallic and phthalic acids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galleon', 'n.', 'A sailing vessel of the 15th and following centuries,\n   often having three or four decks, and used for war or commerce. The\n   term is often rather indiscriminately applied to any large sailing\n   vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galleot', 'n.', 'See Galiot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galleries', 'pl. ', 'of Gallery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallery', 'a.', 'A long and narrow corridor, or place for walking; a\n   connecting passageway, as between one room and another; also, a long\n   hole or passage excavated by a boring or burrowing animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallery', 'a.', 'A room for the exhibition of works of art; as, a picture\n   gallery; hence, also, a large or important collection of paintings,\n   sculptures, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallery', 'a.', 'A long and narrow platform attached to one or more sides\n   of public hall or the interior of a church, and supported by brackets\n   or columns; -- sometimes intended to be occupied by musicians or\n   spectators, sometimes designed merely to increase the capacity of the\n   hall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallery', 'a.', 'A frame, like a balcony, projecting from the stern or\n   quarter of a ship, and hence called stern gallery or quarter gallery,\n   -- seldom found in vessels built since 1850.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallery', 'a.', 'Any communication which is covered overhead as well as at\n   the sides. When prepared for defense, it is a defensive gallery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallery', 'a.', 'A working drift or level.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galletyle', 'n.', 'A little tile of glazed earthenware.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galleys', 'pl. ', 'of Galley');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galley', 'n.', 'A vessel propelled by oars, whether having masts and sails\n   or not');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galley', 'n.', 'A large vessel for war and national purposes; -- common in\n   the Middle Ages, and down to the 17th century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galley', 'n.', 'A name given by analogy to the Greek, Roman, and other\n   ancient vessels propelled by oars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galley', 'n.', 'A light, open boat used on the Thames by customhouse\n   officers, press gangs, and also for pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galley', 'n.', 'One of the small boats carried by a man-of-war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galley', 'n.', 'The cookroom or kitchen and cooking apparatus of a vessel;\n   -- sometimes on merchant vessels called the caboose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galley', 'n.', 'An oblong oven or muffle with a battery of retorts; a\n   gallery furnace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galley', 'n.', 'An oblong tray of wood or brass, with upright sides, for\n   holding type which has been set, or is to be made up, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galley', 'n.', 'A proof sheet taken from type while on a galley; a galley\n   proof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galley-bird', 'n.', 'The European green woodpecker; also, the spotted\n   woodpecker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galley-worm', 'n.', 'A chilognath myriapod of the genus Iulus, and allied\n   genera, having numerous short legs along the sides; a milliped or\n   \"thousand legs.\" See Chilognatha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallflies', 'pl. ', 'of Gallfly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallfly', 'n.', 'An insect that deposits its eggs in plants, and occasions\n   galls, esp. any small hymenopteran of the genus Cynips and allied\n   genera. See Illust. of Gall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallyambic', 'a.', 'Consisting of two iambic dimeters catalectic, the last\n   of which lacks the final syllable; -- said of a kind of verse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallian', 'a.', 'Gallic; French.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galliard', 'a.', 'Gay; brisk; active.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galliard', 'n.', 'A brisk, gay man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galliard', 'a.', 'A gay, lively dance. Cf. Gailliarde.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galliardise', 'a.', 'Excessive gayety; merriment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galliardness', 'n.', 'Gayety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galliass', 'n.', 'Same as Galleass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallic', 'a.', 'Pertaining to, or containing, gallium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallic', 'a.', 'Pertaining to, or derived from, galls, nutgalls, and the\n   like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallic', 'a.', 'Pertaining to Gaul or France; Gallican.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallican', 'a.', 'Of or pertaining to Gaul or France; Gallic; French; as,\n   the Gallican church or clergy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallican', 'n.', 'An adherent to, and supporter of, Gallicanism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallicanism', 'n.', 'The principles, tendencies, or action of those, within\n   the Roman Catholic Church in France, who (esp. in 1682) sought to\n   restrict the papal authority in that country and increase the power of\n   the national church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallicism', 'n.', 'A mode of speech peculiar to the French; a French idiom;\n   also, in general, a French mode or custom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallicized', 'imp. & p. p.', 'of Gallicize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallicizing', 'p. pr. & vb. n.', 'of Gallicize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallicize', 'v. t.', 'To conform to the French mode or idiom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallied', 'p. p. & a.', 'Worried; flurried; frightened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galliform', 'a.', 'Like the Gallinae (or Galliformes) in structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galligaskins', 'n. pl.', 'Loose hose or breeches; leather leg quards. The\n   word is used loosely and often in a jocose sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallimatia', 'n.', 'Senseless talk. [Obs. or R.] See Galimatias.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallimaufries', 'pl. ', 'of Gallimaufry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallimaufry', 'n.', 'A hash of various kinds of meats, a ragout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallimaufry', 'n.', 'Any absurd medley; a hotchpotch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallin', 'n.', 'A substance obtained by the reduction of gallein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallinaceae', 'n. pl.', 'Same as Gallinae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallinacean', 'n.', 'One of the Gallinae or gallinaceous birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallinaceous', 'a.', 'Resembling the domestic fowls and pheasants; of or\n   pertaining to the Gallinae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallinae', 'n.', 'An order of birds, including the common domestic fowls,\n   pheasants, grouse, quails, and allied forms; -- sometimes called\n   Rasores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galling', 'a.', 'Fitted to gall or chafe; vexing; harassing; irritating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallinipper', 'n.', 'A large mosquito.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallinule', 'n.', 'One of several wading birds, having long, webless toes,\n   and a frontal shield, belonging to the family Rallidae. They are\n   remarkable for running rapidly over marshes and on floating plants. The\n   purple gallinule of America is Ionornis Martinica, that of the Old\n   World is Porphyrio porphyrio. The common European gallinule (Gallinula\n   chloropus) is also called moor hen, water hen, water rail, moor coot,\n   night bird, and erroneously dabchick. Closely related to it is the\n   Florida gallinule (Gallinula galeata).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galliot', 'n.', 'See Galiot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallipoli oil', '', 'An inferior kind of olive oil, brought from Gallipoli,\n   in Italy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallipot', 'n.', 'A glazed earthen pot or vessel, used by druggists and\n   apothecaries for containing medicines, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallium', 'n.', 'A rare metallic element, found in certain zinc ores. It is\n   white, hard, and malleable, resembling aluminium, and remarcable for\n   its low melting point (86/ F., 30/C). Symbol Ga. Atomic weight 69.9.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallivant', 'v. i.', 'To play the beau; to wait upon the ladies; also, to\n   roam about for pleasure without any definite plan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallivat', 'n.', 'A small armed vessel, with sails and oars, -- used on the\n   Malabar coast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galliwasp', 'n.', 'A West Indian lizard (Celestus occiduus), about a foot\n   long, imagined by the natives to be venomous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallnut', 'n.', 'A round gall produced on the leaves and shoots of various\n   species of the oak tree. See Gall, and Nutgall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallomania', 'n.', 'An excessive admiration of what is French.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallon', 'n.', 'A measure of capacity, containing four quarts; -- used, for\n   the most part, in liquid measure, but sometimes in dry measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galloon', 'n.', 'A narrow tapelike fabric used for binding hats, shoes,\n   etc., -- sometimes made ornamental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galloon', 'n.', 'A similar bordering or binding of rich material, such as\n   gold lace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallooned', 'a.', 'Furnished or adorned with galloon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galloped', 'imp. & p. p.', 'of Gallop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galloping', 'p. pr. & vb. n.', 'of Gallop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallop', 'v. i.', 'To move or run in the mode called a gallop; as a horse;\n   to go at a gallop; to run or move with speed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallop', 'v. i.', 'To ride a horse at a gallop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallop', 'v. i.', 'Fig.: To go rapidly or carelessly, as in making a hasty\n   examination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallop', 'v. t.', 'To cause to gallop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallop', 'v. i.', 'A mode of running by a quadruped, particularly by a\n   horse, by lifting alternately the fore feet and the hind feet, in\n   successive leaps or bounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallopade', 'n.', 'I horsemanship, a sidelong or curveting kind of gallop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallopade', 'n.', 'A kind of dance; also, music to the dance; a galop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallopaded', 'imp. & p. p.', 'of Gallopade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallopading', 'p. pr. & vb. n.', 'of Gallopade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallopade', 'v. i.', 'To gallop, as on horseback.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallopade', 'v. i.', 'To perform the dance called gallopade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galloper', 'n.', 'One who, or that which, gallops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galloper', 'n.', 'A carriage on which very small guns were formerly\n   mounted, the gun resting on the shafts, without a limber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallopin', 'v. i.', 'An under servant for the kitchen; a scullion; a cooks\n   errand boy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galloping', 'a.', 'Going at a gallop; progressing rapidly; as, a galloping\n   horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallotannic', 'a.', 'Pertaining to the tannin or nutgalls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallow', 'v. t.', 'To fright or terrify. See Gally, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galloway', 'n.', 'A small horse of a breed raised at Galloway, Scotland; --\n   called also garran, and garron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallowglass', 'n.', 'A heavy-armed foot soldier from Ireland and the\n   Western Isles in the time of Edward /');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallowses', 'pl. ', 'of Gallows');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallows', 'pl. ', 'of Gallows');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallows', 'n. sing.', 'A frame from which is suspended the rope with which\n   criminals are executed by hanging, usually consisting of two upright\n   posts and a crossbeam on the top; also, a like frame for suspending\n   anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallows', 'n. sing.', 'A wretch who deserves the gallows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallows', 'n. sing.', 'The rest for the tympan when raised.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallows', 'n. sing.', 'A pair of suspenders or braces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallstone', 'n.', 'A concretion, or calculus, formed in the gall bladder or\n   biliary passages. See Calculus, n., 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gally', 'v. t.', 'To frighten; to worry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gally', 'a.', 'Like gall; bitter as gall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gally', 'n.', 'See Galley, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gallygaskins', 'n. pl.', 'See Galligaskins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galoche', '', 'Alt. of Galoshe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galoshe', '', 'A clog or patten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galoshe', '', 'Hence: An overshoe worn in wet weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galoshe', '', 'A gaiter, or legging, covering the upper part of the shoe\n   and part of the leg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galoot', 'n.', 'A noisy, swaggering, or worthless fellow; a rowdy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galop', 'n.', 'A kind of lively dance, in 2-4 time; also, the music to the\n   dance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galore', 'n. & a.', 'Plenty; abundance; in abundance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galoshe', 'n.', 'Same as Galoche.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galpe', 'v. i.', 'To gape,; to yawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galsome', 'a.', 'Angry; malignant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galt', 'n.', 'Same as Gault.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galvanic', 'a.', 'Of or pertaining to, or exhibiting the phenomena of,\n   galvanism; employing or producing electrical currents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galvanism', 'n.', 'Electricity excited by the mutual action of certain\n   liquids and metals; dynamical electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galvanism', 'n.', 'The branch of physical science which treats of dynamical\n   elecricity, or the properties and effects of electrical currents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galvanist', 'n.', 'One versed in galvanism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galvanization', 'n.', 'The act of process of galvanizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galvanized', 'imp. & p. p.', 'of Galvanize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galvanizing', 'p pr. & vb. n.', 'of Galvanize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galvanize', 'v. t.', 'To affect with galvanism; to subject to the action of\n   electrical currents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galvanize', 'v. t.', 'To plate, as with gold, silver, etc., by means of\n   electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galvanize', 'v. t.', 'To restore to consciousness by galvanic action (as\n   from a state of suspended animation); hence, to stimulate or excite to\n   a factitious animation or activity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galvanize', 'v. t.', 'To coat, as iron, with zinc. See Galvanized iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galvanizer', 'n.', 'One who, or that which, galvanize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galvanocaustic', 'a.', 'Relating to the use of galvanic heat as a caustic,\n   especially in medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galvanocautery', 'n.', 'Cautery effected by a knife or needle heated by the\n   passage of a galvanic current.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galvanoglyphy', 'n.', 'Same as Glyphography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galvanograph', 'n.', 'A copperplate produced by the method of\n   galvanography; also, a picture printed from such a plate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galvanographic', 'a.', 'Of or pertaining to galvanography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galvanography', 'n.', 'The art or process of depositing metals by\n   electricity; electrotypy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galvanography', 'n.', 'A method of producing by means of electrotyping\n   process (without etching) copperplates which can be printed from in the\n   same manner as engraved plates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galvanologist', 'n.', 'One who describes the phenomena of galvanism; a\n   writer on galvanism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galvanology', 'n.', 'A treatise on galvanism, or a description of its\n   phenomena.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galvanometer', 'n.', 'An instrument or apparatus for measuring the\n   intensity of an electric current, usually by the deflection of a\n   magnetic needle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galvanometric', 'a.', 'Of, pertaining to, or measured by, a galvanometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galvanometry', 'n.', 'The art or process of measuring the force of electric\n   currents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galvanoplastic', 'a.', 'Of or pertaining to the art or process of\n   electrotyping; employing, or produced by, the process of electolytic\n   deposition; as, a galvano-plastic copy of a medal or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galvanoplasty', 'n.', 'The art or process of electrotypy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galvanopuncture', 'n.', 'Same as Electro-puncture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galvanoscope', 'n.', 'An instrument or apparatus for detecting the presence\n   of electrical currents, especially such as are of feeble intensity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galvanoscopic', 'a.', 'Of or pertaining to a galvanoscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galvanoscopy', 'n.', 'The use of galvanism in physiological experiments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galvanotonus', 'n.', 'Same as Electrotonus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galvanotropism', 'n.', 'The tendency of a root to place its axis in the\n   line of a galvanic current.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Galwes', 'n.', 'Gallows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gama grass', '', 'A species of grass (Tripsacum dactyloides) tall, stout,\n   and exceedingly productive; cultivated in the West Indies, Mexico, and\n   the Southern States of North America as a forage grass; -- called also\n   sesame grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gamashes', 'n. pl.', 'High boots or buskins; in Scotland, short\n   spatterdashes or riding trousers, worn over the other clothing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gamba', 'n.', 'A viola da gamba.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gambadoes', 'n.', 'Same as Gamashes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gambeson', 'n.', 'Same as Gambison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gambet', 'n.', 'Any bird of the genuis Totanus. See Tattler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gambier', 'n.', 'The inspissated juice of a plant (Uncaria Gambir) growing\n   in Malacca. It is a powerful astringent, and, under the name of Terra\n   Japonica, is used for chewing with the Areca nut, and is exported for\n   tanning and dyeing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gambier', 'n.', 'Catechu.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gambison', 'n.', 'A defensive garment formerly in use for the body, made of\n   cloth stuffed and quilted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gambist', 'n.', 'A performer upon the viola di gamba. See under Viola.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gambit', 'n.', 'A mode of opening the game, in which a pawn is sacrificed\n   to gain an attacking position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gambled', 'imp. & p. p.', 'of Gamble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gambling', 'p. pr. & vb. n.', 'of Gamble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gamble', 'v. i.', 'To play or game for money or other stake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gamble', 'v. t.', 'To lose or squander by gaming; -- usually with away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gambler', 'n.', 'One who gambles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gamboge', 'n.', 'A concrete juice, or gum resin, produced by several\n   species of trees in Siam, Ceylon, and Malabar. It is brought in masses,\n   or cylindrical rolls, from Cambodia, or Cambogia, -- whence its name.\n   The best kind is of a dense, compact texture, and of a beatiful reddish\n   yellow. Taking internally, it is a strong and harsh cathartic and\n   emetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gambogian', 'a.', 'Alt. of Gambogic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gambogic', 'a.', 'Pertaining to, resembling, or containing, gamboge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gambol', 'n.', 'A skipping or leaping about in frolic; a hop; a sportive\n   prank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gamboled', 'imp. & p. p.', 'of Gambol');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gambolled', '', 'of Gambol');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gamboling', 'p. pr. & vb. n.', 'of Gambol');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gambolling', '', 'of Gambol');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gambol', 'v. i.', 'To dance and skip about in sport; to frisk; to skip; to\n   play in frolic, like boys or lambs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gambrel', 'n.', 'The hind leg of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gambrel', 'n.', 'A stick crooked like a horses hind leg; -- used by\n   butchers in suspending slaughtered animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gambrel', 'v. t.', 'To truss or hang up by means of a gambrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gambroon', 'n.', 'A kind of twilled linen cloth for lining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Game', 'n.', 'Crooked; lame; as, a game leg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Game', 'v. i.', 'Sport of any kind; jest, frolic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Game', 'v. i.', 'A contest, physical or mental, according to certain rules,\n   for amusement, recreation, or for winning a stake; as, a game of\n   chance; games of skill; field games, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Game', 'v. i.', 'The use or practice of such a game; a single match at\n   play; a single contest; as, a game at cards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Game', 'v. i.', 'That which is gained, as the stake in a game; also, the\n   number of points necessary to be scored in order to win a game; as, in\n   short whist five points are game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Game', 'v. i.', 'In some games, a point credited on the score to the player\n   whose cards counts up the highest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Game', 'v. i.', 'A scheme or art employed in the pursuit of an object or\n   purpose; method of procedure; projected line of operations; plan;\n   project.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Game', 'v. i.', 'Animals pursued and taken by sportsmen; wild meats\n   designed for, or served at, table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Game', 'a.', 'Having a resolute, unyielding spirit, like the gamecock;\n   ready to fight to the last; plucky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Game', 'a.', 'Of or pertaining to such animals as are hunted for game, or\n   to the act or practice of hunting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gamed', 'imp. & p. p.', 'of Game');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaming', 'p. pr. & vb. n.', 'of Game');
  INSERT INTO words (word, word_type, description) VALUES  (    'Game', 'n.', 'To rejoice; to be pleased; -- often used, in Old English,\n   impersonally with dative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Game', 'n.', 'To play at any sport or diversion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Game', 'n.', 'To play for a stake or prize; to use cards, dice, billiards,\n   or other instruments, according to certain rules, with a view to win\n   money or other thing waged upon the issue of the contest; to gamble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gamecock', 'n.', 'The male game fowl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Game fowl', '', 'A handsome breed of the common fowl, remarkable for the\n   great courage and pugnacity of the males.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gameful', 'a.', 'Full of game or games.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gamekeeper', 'n.', 'One who has the care of game, especially in a park or\n   preserve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gameless', 'a.', 'Destitute of game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gamely', 'adv.', 'In a plucky manner; spiritedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gameness', 'n.', 'Endurance; pluck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gamesome', 'a.', 'Gay; sportive; playful; frolicsome; merry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gamester', 'n.', 'A merry, frolicsome person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gamester', 'n.', 'A person who plays at games; esp., one accustomed to play\n   for a stake; a gambler; one skilled in games.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gamester', 'n.', 'A prostitute; a strumpet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gamic', 'a.', 'Pertaining to, or resulting from, sexual connection; formed\n   by the union of the male and female elements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gamin', 'n.', 'A neglected and untrained city boy; a young street Arab.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaming', 'n.', 'The act or practice of playing games for stakes or wagers;\n   gambling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gamma', 'n.', 'The third letter (/, / = Eng. G) of the Greek alphabet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gammadion', 'n.', 'A cross formed of four capital gammas, formerly used as\n   a mysterious ornament on ecclesiastical vestments, etc. See Fylfot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gammer', 'n.', 'An old wife; an old woman; -- correlative of gaffer, an old\n   man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gammon', 'n.', 'The buttock or thigh of a hog, salted and smoked or dried;\n   the lower end of a flitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gammoned', 'imp. & p. p.', 'of Gammon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gammoning', 'p. pr. & vb. n.', 'of Gammon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gammon', 'v. t.', 'To make bacon of; to salt and dry in smoke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gammon', 'n.', 'Backgammon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gammon', 'n.', 'An imposition or hoax; humbug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gammon', 'v. t.', 'To beat in the game of backgammon, before an antagonist\n   has been able to get his \"men\" or counters home and withdraw any of\n   them from the board; as, to gammon a person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gammon', 'v. t.', 'To impose on; to hoax; to cajole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gammon', 'v. t.', 'To fasten (a bowsprit) to the stem of a vessel by\n   lashings of rope or chain, or by a band of iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gammoning', 'n.', 'The lashing or iron band by which the bowsprit of a\n   vessel is secured to the stem to opposite the lifting action of the\n   forestays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gammoning', 'n.', 'The act of imposing upon or hoaxing a person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gamogenesis', 'n.', 'The production of offspring by the union of parents of\n   different sexes; sexual reproduction; -- the opposite of agamogenesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gamogenetic', 'a.', 'Relating to gamogenesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gamomorphism', 'n.', 'That stage of growth or development in an organism,\n   in which the reproductive elements are generated and matured in\n   preparation for propagating the species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gamopetalous', 'a.', 'Having the petals united or joined so as to form a\n   tube or cup; monopetalous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gamophyllous', 'a.', 'Composed of leaves united by their edges\n   (coalescent).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gamosepalous', 'a.', 'Formed of united sepals; monosepalous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gamut', 'n.', 'The scale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gamy', 'a.', 'Having the flavor of game, esp. of game kept uncooked till\n   near the condition of tainting; high-flavored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gamy', 'a.', 'Showing an unyielding spirit to the last; plucky; furnishing\n   sport; as, a gamy trout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gan', 'v.', 'Began; commenced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ganch', 'n.', 'To drop from a high place upon sharp stakes or hooks, as the\n   Turks dropped malefactors, by way of punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gander', 'n.', 'The male of any species of goose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gane', 'v. i.', 'To yawn; to gape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ganesa', 'n.', 'The Hindoo god of wisdom or prudence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gang', 'v. i.', 'To go; to walk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gang', 'v. i.', 'A going; a course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gang', 'v. i.', 'A number going in company; hence, a company, or a number\n   of persons associated for a particular purpose; a group of laborers\n   under one foreman; a squad; as, a gang of sailors; a chain gang; a gang\n   of thieves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gang', 'v. i.', 'A combination of similar implements arranged so as, by\n   acting together, to save time or labor; a set; as, a gang of saws, or\n   of plows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gang', 'v. i.', 'A set; all required for an outfit; as, a new gang of\n   stays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gang', 'v. i.', 'The mineral substance which incloses a vein; a matrix; a\n   gangue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ganger', 'n.', 'One who oversees a gang of workmen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gangetic', 'a.', 'Pertaining to, or inhabiting, the Ganges; as, the\n   Gangetic shark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gang-flower', 'n.', 'The common English milkwort (Polygala vulgaris), so\n   called from blossoming in gang week.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gangion', 'n.', 'A short line attached to a trawl. See Trawl, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gangliac', 'a.', 'Alt. of Ganglial');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ganglial', 'a.', 'Relating to a ganglion; ganglionic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gangliate', 'a.', 'Alt. of Gangliated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gangliated', 'a.', 'Furnished with ganglia; as, the gangliated cords of the\n   sympathetic nervous system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gangliform', 'a.', 'Alt. of Ganglioform');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ganglioform', 'a.', 'Having the form of a ganglion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ganglia', 'pl. ', 'of Ganglion');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ganglions', 'pl. ', 'of Ganglion');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ganglion', 'n.', 'A mass or knot of nervous matter, including nerve cells,\n   usually forming an enlargement in the course of a nerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ganglion', 'n.', 'A node, or gland in the lymphatic system; as, a lymphatic\n   ganglion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ganglion', 'n.', 'A globular, hard, indolent tumor, situated somewhere on a\n   tendon, and commonly formed by the effusion of a viscid fluid into it;\n   -- called also weeping sinew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ganglionary', 'a.', 'Ganglionic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ganglionic', 'a.', 'Pertaining to, containing, or consisting of, ganglia or\n   ganglion cells; as, a ganglionic artery; the ganglionic columns of the\n   spinal cord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gangrel', 'v. i.', 'Wandering; vagrant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gangrenate', 'v. t.', 'To gangrene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gangrene', 'n.', 'A term formerly restricted to mortification of the soft\n   tissues which has not advanced so far as to produce complete loss of\n   vitality; but now applied to mortification of the soft parts in any\n   stage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gangrened', 'imp. & p. p.', 'of Gangrene');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gangrening', 'p. pr. & vb. n.', 'of Gangrene');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gangrene', 'v. t. & i.', 'To produce gangrene in; to be affected with\n   gangrene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gangrenescent', 'a.', 'Tending to mortification or gangrene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gangrenous', 'a.', 'Affected by, or produced by, gangrene; of the nature of\n   gangrene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gangue', 'n.', 'The mineral or earthy substance associated with metallic\n   ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gangway', 'v. i.', 'A passage or way into or out of any inclosed place;\n   esp., a temporary way of access formed of planks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gangway', 'v. i.', 'In the English House of Commons, a narrow aisle across\n   the house, below which sit those who do not vote steadly either with\n   the government or with the opposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gangway', 'v. i.', 'The opening through the bulwarks of a vessel by which\n   persons enter or leave it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gangway', 'v. i.', 'That part of the spar deck of a vessel on each side of\n   the booms, from the quarter-deck to the forecastle; -- more properly\n   termed the waist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ganil', 'n.', 'A kind of brittle limestone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ganister', 'n.', 'Alt. of Gannister');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gannister', 'n.', 'A refractory material consisting of crushed or ground\n   siliceous stone, mixed with fire clay; -- used for lining Bessemer\n   converters; also used for macadamizing roads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ganja', 'n.', 'The dried hemp plant, used in India for smoking. It is\n   extremely narcotic and intoxicating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gannet', 'n.', 'One of several species of sea birds of the genus Sula,\n   allied to the pelicans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ganocephala', 'n. pl.', 'A group of fossil amphibians allied to the\n   labyrinthodonts, having the head defended by bony, sculptured plates,\n   as in some ganoid fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ganocephalous', 'a.', 'Of or pertaining to the Ganocephala.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ganoid', 'a.', 'Of or pertaining to Ganoidei. -- n. One of the Ganoidei.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ganoidal', 'a.', 'Ganoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ganoidei', 'n. pl.', 'One of the subclasses of fishes. They have an\n   arterial cone and bulb, spiral intestinal valve, and the optic nerves\n   united by a chiasma. Many of the species are covered with bony plates,\n   or with ganoid scales; others have cycloid scales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ganoidian', 'a. & n.', 'Ganoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ganoine', 'n.', 'A peculiar bony tissue beneath the enamel of a ganoid\n   scale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gansa', 'n.', 'Same as Ganza.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gantlet', 'n.', 'A military punishment formerly in use, wherein the\n   offender was made to run between two files of men facing one another,\n   who struck him as he passed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gantlet', 'n.', 'A glove. See Gauntlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gantline', 'n.', 'A line rigged to a mast; -- used in hoisting rigging; a\n   girtline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gantlope', 'n.', 'See Gantlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gantry', 'n.', 'See Gauntree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ganza', 'n.', 'A kind of wild goose, by a flock of which a virtuoso was\n   fabled to be carried to the lunar world.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaol', 'n.', 'A place of confinement, especially for minor offenses or\n   provisional imprisonment; a jail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaoler', 'n.', 'The keeper of a jail. See Jailer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gap', 'n.', 'An opening in anything made by breaking or parting; as, a gap\n   in a fence; an opening for a passage or entrance; an opening which\n   implies a breach or defect; a vacant space or time; a hiatus; a\n   mountain pass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gap', 'v. t.', 'To notch, as a sword or knife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gap', 'v. t.', 'To make an opening in; to breach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaped', 'imp. & p. p.', 'of Gape');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaping', 'p. pr. & vb. n.', 'of Gape');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gape', 'v. i.', 'To open the mouth wide');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gape', 'v. i.', 'Expressing a desire for food; as, young birds gape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gape', 'v. i.', 'Indicating sleepiness or indifference; to yawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gape', 'v. i.', 'To pen or part widely; to exhibit a gap, fissure, or\n   hiatus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gape', 'v. i.', 'To long, wait eagerly, or cry aloud for something; -- with\n   for, after, or at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gape', 'n.', 'The act of gaping; a yawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gape', 'n.', 'The width of the mouth when opened, as of birds, fishes, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'The gapes', '', 'A fit of yawning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'The gapes', '', 'A disease of young poultry and other birds, attended with\n   much gaping. It is caused by a parasitic nematode worm (Syngamus\n   trachealis), in the windpipe, which obstructs the breathing. See\n   Gapeworm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaper', 'n.', 'One who gapes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaper', 'n.', 'A European fish. See 4th Comber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaper', 'n.', 'A large edible clam (Schizothaerus Nuttalli), of the Pacific\n   coast; -- called also gaper clam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaper', 'n.', 'An East Indian bird of the genus Cymbirhynchus, related to\n   the broadbills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gapeseed', 'n.', 'Any strange sight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gapesing', 'n.', 'Act of gazing about; sightseeing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gapeworm', 'n.', 'The parasitic worm that causes the gapes in birds. See\n   Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gapingstock', 'n.', 'One who is an object of open-mouthed wonder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gap-toothed', 'a.', 'Having interstices between the teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gar', 'v.', 'Any slender marine fish of the genera Belone and Tylosurus.\n   See Garfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gar', 'v.', 'The gar pike. See Alligator gar (under Alligator), and Gar\n   pike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gar', 'n.', 'To cause; to make.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garancin', 'n.', 'An extract of madder by sulphuric acid. It consists\n   essentially of alizarin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garb', 'n.', 'Clothing in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garb', 'n.', 'The whole dress or suit of clothes worn by any person,\n   especially when indicating rank or office; as, the garb of a clergyman\n   or a judge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garb', 'n.', 'Costume; fashion; as, the garb of a gentleman in the 16th\n   century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garb', 'n.', 'External appearance, as expressive of the feelings or\n   character; looks; fashion or manner, as of speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garb', 'n.', 'A sheaf of grain (wheat, unless otherwise specified).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garb', 'v. t.', 'To clothe; array; deck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garbage', 'n.', 'Offal, as the bowels of an animal or fish; refuse animal\n   or vegetable matter from a kitchen; hence, anything worthless,\n   disgusting, or loathsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garbage', 'v. t.', 'To strip of the bowels; to clean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garbed', 'a.', 'Dressed; habited; clad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garbel', 'n.', 'Same as Garboard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garbel', 'v. t.', 'Anything sifted, or from which the coarse parts have\n   been taken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garbled', 'imp. & p. p.', 'of Garble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garbling', 'p. pr. & vb. n.', 'of Garble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garble', 'v. t.', 'To sift or bolt, to separate the fine or valuable parts\n   of from the coarse and useless parts, or from dros or dirt; as, to\n   garble spices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garble', 'v. t.', 'To pick out such parts of as may serve a purpose; to\n   mutilate; to pervert; as, to garble a quotation; to garble an account.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garble', 'n.', 'Refuse; rubbish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garble', 'n.', 'Impurities separated from spices, drugs, etc.; -- also\n   called garblings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garbler', 'n.', 'One who garbles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garboard', 'n.', 'One of the planks next the keel on the outside, which\n   form a garboard strake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garboil', 'n.', 'Tumult; disturbance; disorder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garcinia', 'n.', 'A genus of plants, including the mangosteen tree\n   (Garcinia Mangostana), found in the islands of the Indian Archipelago;\n   -- so called in honor of Dr. Garcin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gard', 'n.', 'Garden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gard', 'v. & n.', 'See Guard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gardant', 'a.', 'Turning the head towards the spectator, but not the body;\n   -- said of a lion or other beast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garden', 'n.', 'A piece of ground appropriated to the cultivation of herbs,\n   fruits, flowers, or vegetables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garden', 'n.', 'A rich, well-cultivated spot or tract of country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gardened', 'imp. & p. p.', 'of Garden');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gardening', 'p. pr. & vb. n.', 'of Garden');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garden', 'v. i.', 'To lay out or cultivate a garden; to labor in a garden;\n   to practice horticulture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garden', 'v. t.', 'To cultivate as a garden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gardener', 'n.', 'One who makes and tends a garden; a horticulturist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gardenia', 'n.', 'A genus of plants, some species of which produce\n   beautiful and fragrant flowers; Cape jasmine; -- so called in honor of\n   Dr. Alexander Garden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gardening', 'n.', 'The art of occupation of laying out and cultivating\n   gardens; horticulture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gardenless', 'a.', 'Destitute of a garden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gardenly', 'a.', 'Like a garden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gardenship', 'n.', 'Horticulture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gardon', 'n.', 'A European cyprinoid fish; the id.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gardyloo', 'n.', 'An old cry in throwing water, slops, etc., from the\n   windows in Edingburgh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gare', 'n.', 'Coarse wool on the legs of sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garefowl', 'n.', 'The great auk; also, the razorbill. See Auk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garfish', 'n.', 'A European marine fish (Belone vulgaris); -- called also\n   gar, gerrick, greenback, greenbone, gorebill, hornfish, longnose,\n   mackerel guide, sea needle, and sea pike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garfish', 'n.', 'One of several species of similar fishes of the genus\n   Tylosurus, of which one species (T. marinus) is common on the Atlantic\n   coast. T. Caribbaeus, a very large species, and T. crassus, are more\n   southern; -- called also needlefish. Many of the common names of the\n   European garfish are also applied to the American species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gargalize', 'v. t.', 'To gargle; to rinse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garganey', 'n.', 'A small European duck (Anas querquedula); -- called also\n   cricket teal, and summer teal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gargantuan', 'a.', 'Characteristic of Gargantua, a gigantic, wonderful\n   personage; enormous; prodigious; inordinate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gargarism', 'n.', 'A gargle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gargarize', 'v. t.', 'To gargle; to rinse or wash, as the mouth and throat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garget', 'n.', 'The throat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garget', 'n.', 'A diseased condition of the udders of cows, etc., arising\n   from an inflammation of the mammary glands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garget', 'n.', 'A distemper in hogs, indicated by staggering and loss of\n   appetite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garget', 'n.', 'See Poke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gargil', 'n.', 'A distemper in geese, affecting the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gargle', 'n.', 'See Gargoyle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garggled', 'imp. & p. p.', 'of Gargle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gargling', 'p. pr. & vb. n.', 'of Gargle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gargle', 'v. t.', 'To wash or rinse, as the mouth or throat, particular the\n   latter, agitating the liquid (water or a medicinal preparation) by an\n   expulsion of air from the lungs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gargle', 'v. t.', 'To warble; to sing as if gargling');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gargle', 'n.', 'A liquid, as water or some medicated preparation, used to\n   cleanse the mouth and throat, especially for a medical effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gargol', 'n.', 'A distemper in swine; garget.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gargoulette', 'n.', 'A water cooler or jug with a handle and spout; a\n   gurglet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gargoyle', 'n.', 'A spout projecting from the roof gutter of a building,\n   often carved grotesquely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gargyle', 'n.', 'See Gargoyle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garibaldi', 'n.', 'A jacket worn by women; -- so called from its\n   resemblance in shape to the red shirt worn by the Italians patriot\n   Garibaldi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garibaldi', 'n.', 'A California market fish (Pomancentrus rubicundus) of a\n   deep scarlet color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garish', 'a.', 'Showy; dazzling; ostentatious; attracting or exciting\n   attention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garish', 'a.', 'Gay to extravagance; flighty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garland', 'n.', 'The crown of a king.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garland', 'n.', 'A wreath of chaplet made of branches, flowers, or\n   feathers, and sometimes of precious stones, to be worn on the head like\n   a crown; a coronal; a wreath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garland', 'n.', 'The top; the thing most prized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garland', 'n.', 'A book of extracts in prose or poetry; an anthology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garland', 'n.', 'A sort of netted bag used by sailors to keep provision in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garland', 'n.', 'A grommet or ring of rope lashed to a spar for convenience\n   in handling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garlanded', 'imp. & p. p.', 'of Garland');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garlanding', 'p. pr. & vb. n.', 'of Garland');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garland', 'v. t.', 'To deck with a garland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garlandless', 'a.', 'Destitute of a garland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garlic', 'n.', 'A plant of the genus Allium (A. sativum is the cultivated\n   variety), having a bulbous root, a very strong smell, and an acrid,\n   pungent taste. Each root is composed of several lesser bulbs, called\n   cloves of garlic, inclosed in a common membranous coat, and easily\n   separable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garlic', 'n.', 'A kind of jig or farce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garlicky', 'a.', 'Like or containing garlic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garment', 'n.', 'Any article of clothing, as a coat, a gown, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garmented', 'p. a.', 'Having on a garment; attired; enveloped, as with a\n   garment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garmenture', 'n.', 'Clothing; dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garner', 'n.', 'A granary; a building or place where grain is stored for\n   preservation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garnered', 'imp. & p. p.', 'of Garner');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garnering', 'p. pr. & vb. n.', 'of Garner');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garner', 'v. t.', 'To gather for preservation; to store, as in a granary;\n   to treasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garnet', 'n.', 'A mineral having many varieties differing in color and in\n   their constituents, but with the same crystallization (isometric), and\n   conforming to the same general chemical formula. The commonest color is\n   red, the luster is vitreous, and the hardness greater than that of\n   quartz. The dodecahedron and trapezohedron are the common forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garnet', 'n.', 'A tackle for hoisting cargo in our out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garnetiferous', 'a.', 'Containing garnets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garnierite', 'n.', 'An amorphous mineral of apple-green color; a hydrous\n   silicate of nickel and magnesia. It is an important ore of nickel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garnished', 'imp. & p. p.', 'of Garnish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garnishing', 'p. pr. & vb. n.', 'of Garnish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garnish', 'v. t.', 'To decorate with ornamental appendages; to set off; to\n   adorn; to embellish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garnish', 'v. t.', 'To ornament, as a dish, with something laid about it;\n   as, a dish garnished with parsley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garnish', 'v. t.', 'To furnish; to supply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garnish', 'v. t.', 'To fit with fetters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garnish', 'v. t.', 'To warn by garnishment; to give notice to; to\n   garnishee. See Garnishee, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garnish', 'n.', 'Something added for embellishment; decoration; ornament;\n   also, dress; garments, especially such as are showy or decorated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garnish', 'n.', 'Something set round or upon a dish as an embellishment.\n   See Garnish, v. t., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garnish', 'v. t.', 'Fetters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garnish', 'v. t.', 'A fee; specifically, in English jails, formerly an\n   unauthorized fee demanded by the old prisoners of a newcomer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garnishee', 'n.', 'One who is garnished; a person upon whom garnishment has\n   been served in a suit by a creditor against a debtor, such person\n   holding property belonging to the debtor, or owing him money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garnisheed', 'imp. & p. p.', 'of Garnishee');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garnisheeing', 'p. pr. & vb. n.', 'of Garnishee');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garnishee', 'v. t.', 'To make (a person) a garnishee; to warn by\n   garnishment; to garnish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garnishee', 'v. t.', 'To attach (the fund or property sought to be secured\n   by garnishment); to trustee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garnisher', 'n.', 'One who, or that which, garnishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garnishment', 'n.', 'Ornament; embellishment; decoration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garnishment', 'n.', 'Warning, or legal notice, to one to appear and give\n   information to the court on any matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garnishment', 'n.', 'Warning to a person in whose hands the effects of\n   another are attached, not to pay the money or deliver the goods to the\n   defendant, but to appear in court and give information as garnishee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garnishment', 'n.', 'A fee. See Garnish, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garniture', 'v. t.', 'That which garnishes; ornamental appendage;\n   embellishment; furniture; dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garookuh', 'n.', 'A small fishing vessel met with in the Persian Gulf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garous', 'a.', 'Pertaining to, or resembling, garum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gar pike', '', 'Alt. of Garpike');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garpike', '', 'See under Gar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garran', 'n.', 'See Galloway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garret', 'n.', 'A turret; a watchtower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garret', 'n.', 'That part of a house which is on the upper floor,\n   immediately under or within the roof; an attic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garreted', 'a.', 'Protected by turrets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garreteer', 'n.', 'One who lives in a garret; a poor author; a literary\n   hack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garreting', 'n.', 'Small splinters of stone inserted into the joints of\n   coarse masonry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garrison', 'n.', 'A body of troops stationed in a fort or fortified town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garrison', 'n.', 'A fortified place, in which troops are quartered for its\n   security.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garrisoned', 'imp. & p. p.', 'of Garrison');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garrisoning', 'p. pr. & vb. n.', 'of Garrison');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garrison', 'v. t.', 'To place troops in, as a fortification, for its\n   defense; to furnish with soldiers; as, to garrison a fort or town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garrison', 'v. t.', 'To secure or defend by fortresses manned with troops;\n   as, to garrison a conquered territory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garron', 'n.', 'Same as Garran.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garrot', 'n.', 'A stick or small wooden cylinder used for tightening a\n   bandage, in order to compress the arteries of a limb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garrot', 'n.', 'The European golden-eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garrote', 'n.', 'A Spanish mode of execution by strangulation, with an iron\n   collar affixed to a post and tightened by a screw until life become\n   extinct; also, the instrument by means of which the punishment is\n   inflicted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garroted', 'imp. & p. p.', 'of Garrote');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garroting', 'p. pr. & vb. n.', 'of Garrote');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garrote', 'v. t.', 'To strangle with the garrote; hence, to seize by the\n   throat, from behind, with a view to strangle and rob.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garroter', 'n.', 'One who seizes a person by the throat from behind, with a\n   view to strangle and rob him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garrulity', 'n.', 'Talkativeness; loquacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garrulous', 'a.', 'Talking much, especially about commonplace or trivial\n   things; talkative; loquacious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garrulous', 'a.', 'Having a loud, harsh note; noisy; -- said of birds; as,\n   the garrulous roller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garrupa', 'n.', 'One of several species of California market fishes, of the\n   genus Sebastichthys; -- called also rockfish. See Rockfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garter', 'n.', 'A band used to prevent a stocking from slipping down on the\n   leg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garter', 'n.', 'The distinguishing badge of the highest order of knighthood\n   in Great Britain, called the Order of the Garter, instituted by Edward\n   III.; also, the Order itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garter', 'n.', 'Same as Bendlet.');
