  INSERT INTO words (word, word_type, description) VALUES  (    'Gartering', 'p. pr. & vb. n.', 'of Garter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garter', 'v. t.', 'To bind with a garter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garter', 'v. t.', 'To invest with the Order of the Garter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garth', 'n.', 'A close; a yard; a croft; a garden; as, a cloister garth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garth', 'n.', 'A dam or weir for catching fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garth', 'n.', 'A hoop or band.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garum', 'n.', 'A sauce made of small fish. It was prized by the ancients.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Garvie', 'n.', 'The sprat; -- called also garvie herring, and garvock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gases', 'pl. ', 'of Gas');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gas', 'n.', 'An aeriform fluid; -- a term used at first by chemists as\n   synonymous with air, but since restricted to fluids supposed to be\n   permanently elastic, as oxygen, hydrogen, etc., in distinction from\n   vapors, as steam, which become liquid on a reduction of temperature. In\n   present usage, since all of the supposed permanent gases have been\n   liquified by cold and pressure, the term has resumed nearly its\n   original signification, and is applied to any substance in the elastic\n   or aeriform state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gas', 'n.', 'A complex mixture of gases, of which the most important\n   constituents are marsh gas, olefiant gas, and hydrogen, artificially\n   produced by the destructive distillation of gas coal, or sometimes of\n   peat, wood, oil, resin, etc. It gives a brilliant light when burned,\n   and is the common gas used for illuminating purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gas', 'n.', 'Laughing gas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gas', 'n.', 'Any irrespirable aeriform fluid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gasalier', 'n.', 'A chandelier arranged to burn gas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gas-burner', 'n.', 'The jet piece of a gas fixture where the gas is burned\n   as it escapes from one or more minute orifices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gascoines', 'n. pl.', 'See Gaskins, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gascon', 'a.', 'Of or pertaining to Gascony, in France, or to the Gascons;\n   also, braggart; swaggering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gascon', 'n.', 'A native of Gascony; a boaster; a bully. See Gasconade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gasconade', 'n.', 'A boast or boasting; a vaunt; a bravado; a bragging;\n   braggodocio.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gasconaded', 'imp. & p. p.', 'of Gasconade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gasconading', 'p. pr. & vb. n.', 'of Gasconade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gasconade', 'v. i.', 'To boast; to brag; to bluster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gasconader', 'n.', 'A great boaster; a blusterer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gascoynes', 'n. pl.', 'Gaskins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaseity', 'n.', 'State of being gaseous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaseous', 'a.', 'In the form, or of the nature, of gas, or of an aeriform\n   fluid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaseous', 'a.', 'Lacking substance or solidity; tenuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gashed', 'imp. & p. p.', 'of Gash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gashing', 'p. pr. & vb. n.', 'of Gash');
