  INSERT INTO words (word, word_type, description) VALUES  (    'Gash', 'n.', 'A deep and long cut; an incision of considerable length and\n   depth, particularly in flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gashful', 'a.', 'Full of gashes; hideous; frightful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gasification', 'n.', 'The act or process of converting into gas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gasiform', 'a.', 'Having a form of gas; gaseous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gasified', 'imp. & p. p.', 'of Gasify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gasifying', 'p. pr. & vb. n.', 'of Gasify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gasify', 'v. t.', 'To convert into gas, or an aeriform fluid, as by the\n   application of heat, or by chemical processes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gasify', 'v. i.', 'To become gas; to pass from a liquid to a gaseous state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gasket', 'n.', 'A line or band used to lash a furled sail securely. Sea\n   gaskets are common lines; harbor gaskets are plaited and decorated\n   lines or bands. Called also casket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gasket', 'n.', 'The plaited hemp used for packing a piston, as of the steam\n   engine and its pumps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gasket', 'n.', 'Any ring or washer of packing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaskins', 'n.pl.', 'Loose hose or breeches; galligaskins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaskins', 'n.pl.', 'Packing of hemp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaskins', 'n.pl.', 'A horses thighs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaslight', 'n.', 'The light yielded by the combustion of illuminating gas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaslight', 'n.', 'A gas jet or burner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gasogen', 'n.', 'An apparatus for the generation of gases, or for\n   impregnating a liquid with a gas, or a gas with a volatile liquid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gasogen', 'n.', 'A volatile hydrocarbon, used as an illuminant, or for\n   charging illuminating gas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gasolene', 'n.', 'See Gasoline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gasolier', 'n.', 'Same as Gasalier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gasoline', 'n.', 'A highly volatile mixture of fluid hydrocarbons, obtained\n   from petroleum, as also by the distillation of bituminous coal. It is\n   used in making air gas, and in giving illuminating power to water gas.\n   See Carburetor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gasometer', 'n.', 'An apparatus for holding and measuring of gas; in gas\n   works, a huge iron cylinder closed at one end and having the other end\n   immersed in water, in which it is made to rise or fall, according to\n   the volume of gas it contains, or the pressure required.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gasometric', 'a.', 'Alt. of Gasometrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gasometrical', 'a.', 'Of or pertaining to the measurement of gases; as,\n   gasometric analysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gasometry', 'n.', 'The art or practice of measuring gases; also, the\n   science which treats of the nature and properties of these elastic\n   fluids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gasoscope', 'n.', 'An apparatus for detecting the presence of any dangerous\n   gas, from a gas leak in a coal mine or a dwelling house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gasped', 'imp. & p. p.', 'of Gasp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gasping', 'p. pr. & vb. n.', 'of Gasp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gasp', 'v. i.', 'To open the mouth wide in catching the breath, or in\n   laborious respiration; to labor for breath; to respire convulsively; to\n   pant violently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gasp', 'v. i.', 'To pant with eagerness; to show vehement desire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gasp', 'v. t.', 'To emit or utter with gasps; -- with forth, out, away,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gasp', 'n.', 'The act of opening the mouth convulsively to catch the\n   breath; a labored respiration; a painful catching of the breath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaspereau', 'n.', 'The alewife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gasserian', 'a.', 'Relating to Casserio (L. Gasserius), the discover of the\n   Gasserian ganglion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gassing', 'n.', 'The process of passing cotton goods between two rollers\n   and exposing them to numerous minute jets of gas to burn off the small\n   fibers; any similar process of singeing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gassing', 'n.', 'Boasting; insincere or empty talk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gassy', 'a.', 'Full of gas; like gas. Hence: [Colloq.] Inflated; full of\n   boastful or insincere talk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gast', 'v. t.', 'To make aghast; to frighten; to terrify. See Aghast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaster', 'v. t.', 'To gast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gasteromycetes', 'n. pl.', 'An order of fungi, in which the spores are\n   borne inside a sac called the peridium, as in the puffballs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gasteropod', 'n.', 'Same as Gastropod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gasteropoda', 'n. pl.', 'Same as Gastropoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gasteropodous', 'a.', 'Same as Gastropodous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastful', 'a.', 'Alt. of Gastly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastly', 'a.', 'See Ghastful, Ghastly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastight', 'a.', 'So tightly fitted as to preclude the escape of gas;\n   impervious to gas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastness', 'n.', 'See Ghastness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastornis', 'n.', 'A genus of large eocene birds from the Paris basin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastraea', 'n.', 'A primeval larval form; a double-walled sac from which,\n   according to the hypothesis of Haeckel, man and all other animals, that\n   in the first stages of their individual evolution pass through a\n   two-layered structural stage, or gastrula form, must have descended.\n   This idea constitutes the Gastraea theory of Haeckel. See Gastrula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastralgia', 'n.', 'Pain in the stomach or epigastrium, as in gastric\n   disorders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastric', 'a.', 'Of, pertaining to, or situated near, the stomach; as, the\n   gastric artery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastriloquist', 'n.', 'One who appears to speak from his stomach; a\n   ventriloquist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastriloquous', 'a.', 'Ventriloquous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastriloquy', 'n.', 'A voice or utterance which appears to proceed from the\n   stomach; ventriloquy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastritis', 'n.', 'Inflammation of the stomach, esp. of its mucuos\n   membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastro-', '', 'A combining form from the Gr. /, /, the stomach, or belly;\n   as in gastrocolic, gastrocele, gastrotomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastrocnemius', 'n.', 'The muscle which makes the greater part of the calf\n   of the leg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastrocolic', 'a.', 'Pertaining to both the stomach and the colon; as, the\n   gastrocolic, or great, omentum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastrodisc', 'n.', 'That part of blastoderm where the hypoblast appears\n   like a small disk on the inner face of the epibladst.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastroduodenal', 'a.', 'Pertaining to the stomach and duodenum; as, the\n   gastroduodenal artery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastroduodenitis', 'n.', 'Inflammation of the stomach and duodenum. It is\n   one of the most frequent causes of jaundice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastroelytrotomy', 'n.', 'The operation of cutting into the upper part of\n   the vagina, through the abdomen (without opening the peritoneum), for\n   the purpose of removing a fetus. It is a substitute for the Caesarean\n   operation, and less dangerous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastroenteric', 'a.', 'Gastrointestinal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastroenteritis', 'n.', 'Inflammation of the lining membrane of the stomach\n   and the intestines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastroepiploic', 'a.', 'Of or pertaining to the stomach and omentum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastrohepatic', 'a.', 'Pertaining to the stomach and liver; hepatogastric;\n   as, the gastrohepatic, or lesser, omentum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastrohysterotomy', 'n.', 'Caesarean section. See under Caesarean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastrointestinal', 'a.', 'Of or pertaining to the stomach and intestines;\n   gastroenteric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastrolith', 'n.', 'See Crabs eyes, under Crab.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastrology', 'n.', 'The science which treats of the structure and functions\n   of the stomach; a treatise of the stomach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastromalacia', 'n.', 'A softening of the coats of the stomach; -- usually\n   a post-morten change.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastromancy', 'n.', 'A kind of divination, by means of words seemingly\n   uttered from the stomach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastromancy', 'n.', 'A species of divination, by means of glasses or other\n   round, transparent vessels, in the center of which figures are supposed\n   to appear by magic art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastromyces', 'n.', 'The fungoid growths sometimes found in the stomach;\n   such as Torula, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastromyth', 'n.', 'One whose voice appears to proceed from the stomach; a\n   ventriloquist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastronome', 'n.', 'Alt. of Gastronomer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastronomer', 'n.', 'One fond of good living; an epicure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastronomic', 'a.', 'Alt. of Gastronomical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastronomical', 'a.', 'Pertaining to gastromony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastronomist', 'n.', 'A gastromomer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastronomy', 'n.', 'The art or science of good eating; epicurism; the art\n   of good cheer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastrophrenic', 'a.', 'Pertaining to the stomach and diaphragm; as, the\n   gastrophrenic ligament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastropneumatic', 'a.', 'Pertaining to the alimentary canal and air\n   passages, and to the cavities connected with them; as, the\n   gastropneumatic mucuos membranes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastropod', 'n.', 'One of the Gastropoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastropoda', 'n. pl.', 'One of the classes of Mollusca, of great extent. It\n   includes most of the marine spiral shells, and the land and fresh-water\n   snails. They generally creep by means of a flat, muscular disk, or\n   foot, on the ventral side of the body. The head usually bears one or\n   two pairs of tentacles. See Mollusca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastropodous', 'a.', 'Of or pertaining to the Gastropoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastroraphy', 'n.', 'The operation of sewing up wounds of the abdomen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastroscope', 'n.', 'An instrument for viewing or examining the interior of\n   the stomach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastroscopic', 'a.', 'Of or pertaining to gastroscopy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastroscopy', 'n.', 'Examination of the abdomen or stomach, as with the\n   gastroscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastrosplenic', 'n.', 'Pertaining to the stomach and spleen; as, the\n   gastrosplenic ligament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastrostege', 'n.', 'One of the large scales on the belly of a serpent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastrostomy', 'n.', 'The operation of making a permanent opening into the\n   stomach, for the introduction of food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastrotomy', 'n.', 'A cutting into, or opening of, the abdomen or the\n   stomach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastrotricha', 'n. pl.', 'A group of small wormlike animals, having cilia\n   on the ventral side. The group is regarded as an ancestral or synthetic\n   one, related to rotifers and annelids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastrotrocha', 'n.', 'A form of annelid larva having cilia on the ventral\n   side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastrovascular', 'a.', 'Having the structure, or performing the functions,\n   both of digestive and circulatory organs; as, the gastrovascular cavity\n   of c/lenterates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastrulae', 'pl. ', 'of Gastrula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastrula', 'n.', 'An embryonic form having its origin in the invagination\n   or pushing in of the wall of the planula or blastula (the blastosphere)\n   on one side, thus giving rise to a double-walled sac, with one opening\n   or mouth (the blastopore) which leads into the cavity (the archenteron)\n   lined by the inner wall (the hypoblast). See Illust. under\n   Invagination. In a more general sense, an ideal stage in embryonic\n   development. See Gastraea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastrula', 'a.', 'Of or pertaining to a gastrula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastrulation', 'n.', 'The process of invagination, in embryonic\n   development, by which a gastrula is formed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastrura', 'n. pl.', 'See Stomatopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gastrurous', 'a.', 'Pertaining to the Gastrura.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gat', '', 'imp. of Get.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gate', 'n.', 'A large door or passageway in the wall of a city, of an\n   inclosed field or place, or of a grand edifice, etc.; also, the movable\n   structure of timber, metal, etc., by which the passage can be closed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gate', 'n.', 'An opening for passage in any inclosing wall, fence, or\n   barrier; or the suspended framework which closes or opens a passage.\n   Also, figuratively, a means or way of entrance or of exit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gate', 'n.', 'A door, valve, or other device, for stopping the passage of\n   water through a dam, lock, pipe, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gate', 'n.', 'The places which command the entrances or access; hence,\n   place of vantage; power; might.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gate', 'n.', 'In a lock tumbler, the opening for the stump of the bolt to\n   pass through or into.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gate', 'n.', 'The channel or opening through which metal is poured into the\n   mold; the ingate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gate', 'n.', 'The waste piece of metal cast in the opening; a sprue or\n   sullage piece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gate', 'v. t.', 'To supply with a gate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gate', 'v. t.', 'To punish by requiring to be within the gates at an\n   earlier hour than usual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gate', 'n.', 'A way; a path; a road; a street (as in Highgate).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gate', 'n.', 'Manner; gait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gated', 'a.', 'Having gates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gatehouse', 'n.', 'A house connected or associated with a gate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gateless', 'a.', 'Having no gate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gateman', 'n.', 'A gate keeper; a gate tender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gatepost', 'n.', 'A post to which a gate is hung; -- called also swinging /\n   hinging post.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gatepost', 'n.', 'A post against which a gate closes; -- called also\n   shutting post.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gateway', 'n.', 'A passage through a fence or wall; a gate; also, a frame,\n   arch, etc., in which a gate in hung, or a structure at an entrance or\n   gate designed for ornament or defense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gatewise', 'adv.', 'In the manner of a gate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gathered', 'imp. & p. p.', 'of Gather');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gathering', 'p. pr. & vb. n.', 'of Gather');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gather', 'v. t.', 'To bring together; to collect, as a number of separate\n   things, into one place, or into one aggregate body; to assemble; to\n   muster; to congregate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gather', 'v. t.', 'To pick out and bring together from among what is of\n   less value; to collect, as a harvest; to harvest; to cull; to pick off;\n   to pluck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gather', 'v. t.', 'To accumulate by collecting and saving little by little;\n   to amass; to gain; to heap up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gather', 'v. t.', 'To bring closely together the parts or particles of; to\n   contract; to compress; to bring together in folds or plaits, as a\n   garment; also, to draw together, as a piece of cloth by a thread; to\n   pucker; to plait; as, to gather a ruffle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gather', 'v. t.', 'To derive, or deduce, as an inference; to collect, as a\n   conclusion, from circumstances that suggest, or arguments that prove;\n   to infer; to conclude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gather', 'v. t.', 'To gain; to win.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gather', 'v. t.', 'To bring together, or nearer together, in masonry, as\n   where the width of a fireplace is rapidly diminished to the width of\n   the flue, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gather', 'v. t.', 'To haul in; to take up; as, to gather the slack of a\n   rope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gather', 'v. i.', 'To come together; to collect; to unite; to become\n   assembled; to congregate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gather', 'v. i.', 'To grow larger by accretion; to increase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gather', 'v. i.', 'To concentrate; to come to a head, as a sore, and\n   generate pus; as, a boil has gathered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gather', 'v. i.', 'To collect or bring things together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gather', 'n.', 'A plait or fold in cloth, made by drawing a thread through\n   it; a pucker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gather', 'n.', 'The inclination forward of the axle journals to keep the\n   wheels from working outward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gather', 'n.', 'The soffit or under surface of the masonry required in\n   gathering. See Gather, v. t., 7.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gatherable', 'a.', 'Capable of being gathered or collected; deducible from\n   premises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gatherer', 'n.', 'One who gathers or collects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gatherer', 'n.', 'An attachment for making gathers in the cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gathering', 'n.', 'The act of collecting or bringing together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gathering', 'n.', 'That which is gathered, collected, or brought together');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gathering', 'n.', 'A crowd; an assembly; a congregation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gathering', 'n.', 'A charitable contribution; a collection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gathering', 'n.', 'A tumor or boil suppurated or maturated; an abscess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gathering', 'a.', 'Assembling; collecting; used for gathering or\n   concentrating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gatling gun', '', 'An American machine gun, consisting of a cluster of\n   barrels which, being revolved by a crank, are automatically loaded and\n   fired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gatten tree', '', 'A name given to the small trees called guelder-rose\n   (Viburnum Opulus), cornel (Cornus sanguinea), and spindle tree\n   (Euonymus Europaeus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gat-toothed', 'a.', 'Goat-toothed; having a lickerish tooth; lustful;\n   wanton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauche', 'n.', 'Left handed; hence, awkward; clumsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauche', 'n.', 'Winding; twisted; warped; -- applied to curves and\n   surfaces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaucherie', 'n.', 'An awkward action; clumsiness; boorishness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauchos', 'pl. ', 'of Gaucho');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaucho', 'n.', 'One of the native inhabitants of the pampas, of\n   Spanish-American descent. They live mostly by rearing cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaud', 'n.', 'Trick; jest; sport.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaud', 'n.', 'Deceit; fraud; artifice; device.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaud', 'n.', 'An ornament; a piece of worthless finery; a trinket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaud', 'n.', 'To sport or keep festival.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauded', 'imp. & p. p.', 'of Gaud');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauding', 'p. pr. & vb. n.', 'of Gaud');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaud', 'v. t.', 'To bedeck gaudily; to decorate with gauds or showy\n   trinkets or colors; to paint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaud-day', 'n.', 'See Gaudy, a feast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaudery', 'n.', 'Finery; ornaments; ostentatious display.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaudful', 'a.', 'Joyful; showy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaudily', 'adv.', 'In a gaudy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaudiness', 'n.', 'The quality of being gaudy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaudish', 'a.', 'Gaudy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaudless', 'a.', 'Destitute of ornament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaudy', 'superl.', 'Ostentatiously fine; showy; gay, but tawdry or\n   meretricious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaudy', 'superl.', 'Gay; merry; festal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaudies', 'pl. ', 'of Gaudy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaudy', 'n.', 'One of the large beads in the rosary at which the\n   paternoster is recited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaudy', 'n.', 'A feast or festival; -- called also gaud-day and gaudy day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaudygreen', 'a. / n.', 'Light green.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauffer', 'v. t.', 'To plait, crimp, or flute; to goffer, as lace. See\n   Goffer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauffering', 'n.', 'A mode of plaiting or fluting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauffre', 'n.', 'A gopher, esp. the pocket gopher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauged', 'imp. & p. p.', 'of Gauge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauging', 'p. pr. & vb. n.', 'of Gauge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauge', 'v. t.', 'To measure or determine with a gauge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauge', 'v. t.', 'To measure or to ascertain the contents or the capacity\n   of, as of a pipe, barrel, or keg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauge', 'v. t.', 'To measure the dimensions of, or to test the accuracy of\n   the form of, as of a part of a gunlock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauge', 'v. t.', 'To draw into equidistant gathers by running a thread\n   through it, as cloth or a garment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauge', 'v. t.', 'To measure the capacity, character, or ability of; to\n   estimate; to judge of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauge', 'n.', 'A measure; a standard of measure; an instrument to determine\n   dimensions, distance, or capacity; a standard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauge', 'n.', 'Measure; dimensions; estimate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauge', 'n.', 'Any instrument for ascertaining or regulating the dimensions\n   or forms of things; a templet or template; as, a button makers gauge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauge', 'n.', 'Any instrument or apparatus for measuring the state of a\n   phenomenon, or for ascertaining its numerical elements at any moment;\n   -- usually applied to some particular instrument; as, a rain gauge; a\n   steam gauge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauge', 'n.', 'Relative positions of two or more vessels with reference to\n   the wind; as, a vessel has the weather gauge of another when on the\n   windward side of it, and the lee gauge when on the lee side of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauge', 'n.', 'The depth to which a vessel sinks in the water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauge', 'n.', 'The distance between the rails of a railway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauge', 'n.', 'The quantity of plaster of Paris used with common plaster to\n   accelerate its setting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauge', 'n.', 'That part of a shingle, slate, or tile, which is exposed to\n   the weather, when laid; also, one course of such shingles, slates, or\n   tiles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaugeable', 'a.', 'Capable of being gauged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauged', 'p. a.', 'Tested or measured by, or conformed to, a gauge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauger', 'n.', 'One who gauges; an officer whose business it is to\n   ascertain the contents of casks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauger-ship', 'n.', 'The office of a gauger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauging rod', '', 'See Gauge rod, under Gauge, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaul', 'n.', 'The Anglicized form of Gallia, which in the time of the\n   Romans included France and Upper Italy (Transalpine and Cisalpine\n   Gaul).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaul', 'n.', 'A native or inhabitant of Gaul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaulish', 'a.', 'Pertaining to ancient France, or Gaul; Gallic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gault', 'n.', 'A series of beds of clay and marl in the South of England,\n   between the upper and lower greensand of the Cretaceous period.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaultheria', 'n.', 'A genus of ericaceous shrubs with evergreen foliage,\n   and, often, edible berries. It includes the American winter-green\n   (Gaultheria procumbens), and the larger-fruited salal of Northwestern\n   America (Gaultheria Shallon).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaunt', 'a.', 'Attenuated, as with fasting or suffering; lean; meager;\n   pinched and grim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauntlet', 'n.', 'See Gantlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauntlet', 'n.', 'A glove of such material that it defends the hand from\n   wounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauntlet', 'n.', 'A long glove, covering the wrist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauntlet', 'n.', 'A rope on which hammocks or clothes are hung for drying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauntletted', 'a.', 'Wearing a gauntlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauntly', 'adv.', 'In a gaunt manner; meagerly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauntree', 'n.', 'Alt. of Gauntry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauntry', 'n.', 'A frame for supporting barrels in a cellar or elsewhere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauntry', 'n.', 'A scaffolding or frame carrying a crane or other\n   structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaur', 'n.', 'An East Indian species of wild cattle (Bibos gauris), of\n   large size and an untamable disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaure', 'v. i.', 'To gaze; to stare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauze', 'n.', 'A very thin, slight, transparent stuff, generally of silk;\n   also, any fabric resembling silk gauze; as, wire gauze; cotton gauze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauze', 'a.', 'Having the qualities of gauze; thin; light; as, gauze merino\n   underclothing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauziness', 'n.', 'The quality of being gauzy; flimsiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gauzy', 'a.', 'Pertaining to, or resembling, gauze; thin and slight as\n   gauze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gave', '', 'imp. of Give.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gavel', 'n.', 'A gable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gavel', 'n.', 'A small heap of grain, not tied up into a bundle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gavel', 'n.', 'The mallet of the presiding officer in a legislative body,\n   public assembly, court, masonic body, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gavel', 'n.', 'A masons setting maul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gavel', 'n.', 'Tribute; toll; custom. [Obs.] See Gabel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gavelet', 'n.', 'An ancient special kind of cessavit used in Kent and\n   London for the recovery of rent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gavelkind', 'n.', 'A tenure by which land descended from the father to all\n   his sons in equal portions, and the land of a brother, dying without\n   issue, descended equally to his brothers. It still prevails in the\n   county of Kent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaveloche', 'n.', 'Same as Gavelock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gavelock', 'n.', 'A spear or dart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gavelock', 'n.', 'An iron crow or lever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaverick', 'n.', 'The European red gurnard (Trigla cuculus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaviae', 'n. pl.', 'The division of birds which includes the gulls and\n   terns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gavial', 'n.', 'A large Asiatic crocodilian (Gavialis Gangeticus); --\n   called also nako, and Gangetic crocodile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gavot', 'n.', 'A kind of difficult dance; a dance tune, the air of which\n   has two brisk and lively, yet dignified, strains in common time, each\n   played twice over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gawby', 'n.', 'A baby; a dunce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gawk', 'n.', 'A cuckoo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gawk', 'n.', 'A simpleton; a booby; a gawky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gawk', 'v. i.', 'To act like a gawky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gawky', 'superl.', 'Foolish and awkward; clumsy; clownish; as, gawky\n   behavior. -- n. A fellow who is awkward from being overgrown, or from\n   stupidity, a gawk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gawn', 'n.', 'A small tub or lading vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gawntree', 'n.', 'See Gauntree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gay', 'superl.', 'Excited with merriment; manifesting sportiveness or\n   delight; inspiring delight; livery; merry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gay', 'superl.', 'Brilliant in colors; splendid; fine; richly dressed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gay', 'superl.', 'Loose; dissipated; lewd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gay', 'n.', 'An ornament');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gayal', 'n.', 'A Southern Asiatic species of wild cattle (Bibos frontalis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaydiang', 'n.', 'A vessel of Anam, with two or three masts, lofty\n   triangular sails, and in construction somewhat resembling a Chinese\n   junk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gayeties', 'pl. ', 'of Gayety');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gayety', 'a.', 'The state of being gay; merriment; mirth; acts or\n   entertainments prompted by, or inspiring, merry delight; -- used often\n   in the plural; as, the gayeties of the season.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gayety', 'a.', 'Finery; show; as, the gayety of dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaylussite', 'n.', 'A yellowish white, translucent mineral, consisting of\n   the carbonates of lime and soda, with water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gayly', 'adv.', 'With mirth and frolic; merrily; blithely; gleefully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gayly', 'adv.', 'Finely; splendidly; showily; as, ladies gayly dressed; a\n   flower gayly blooming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gayne', 'v. i.', 'To avail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gayness', 'n.', 'Gayety; finery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaysome', 'a.', 'Full of gayety. Mir. for Mag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaytre', 'n.', 'The dogwood tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gazed', 'imp. & p. p.', 'of Gaze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gazing', 'p. pr. & vb. n.', 'of Gaze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaze', 'v. i.', 'To fixx the eyes in a steady and earnest look; to look\n   with eagerness or curiosity, as in admiration, astonishment, or with\n   studious attention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaze', 'v. t.', 'To view with attention; to gaze on .');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaze', 'n.', 'A fixed look; a look of eagerness, wonder, or admiration; a\n   continued look of attention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gaze', 'n.', 'The object gazed on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gazeebo', 'n.', 'A summerhouse so situated as to command an extensive\n   prospect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gazeful', 'a.', 'Gazing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gazehound', 'n.', 'A hound that pursues by the sight rather than by the\n   scent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gazel', 'n.', 'The black currant; also, the wild plum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gazel', 'n.', 'See Gazelle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gazelle', 'n.', 'One of several small, swift, elegantly formed species of\n   antelope, of the genus Gazella, esp. G. dorcas; -- called also algazel,\n   corinne, korin, and kevel. The gazelles are celebrated for the luster\n   and soft expression of their eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gazement', 'n.', 'View.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gazer', 'n.', 'One who gazes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gazet', 'n.', 'A Venetian coin, worth about three English farthings, or one\n   and a half cents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gazette', 'n.', 'A newspaper; a printed sheet published periodically; esp.,\n   the official journal published by the British government, and\n   containing legal and state notices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gazetted', 'imp. & p. p.', 'of Gazette');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gazetting', 'p. pr. & vb. n.', 'of Gazette');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gazette', 'v. t.', 'To announce or publish in a gazette; to announce\n   officially, as an appointment, or a case of bankruptcy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gazetteer', 'n.', 'A writer of news, or an officer appointed to publish\n   news by authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gazetteer', 'n.', 'A newspaper; a gazette.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gazetteer', 'n.', 'A geographical dictionary; a book giving the names and\n   descriptions, etc., of many places.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gazetteer', 'n.', 'An alphabetical descriptive list of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gazingstock', 'n.', 'A person or thing gazed at with scorn or abhorrence;\n   an object of curiosity or contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gazogene', 'n.', 'A portable apparatus for making soda water or aerated\n   liquids on a small scale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gazon', 'n.', 'One of the pieces of sod used to line or cover parapets and\n   the faces of earthworks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ge-', '', 'An Anglo-Saxon prefix. See Y-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geal', 'v. i.', 'To congeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gean', 'n.', 'A species of cherry tree common in Europe (Prunus avium);\n   also, the fruit, which is usually small and dark in color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geanticlinal', 'n.', 'An upward bend or flexure of a considerable portion\n   of the earths crust, resulting in the formation of a class of mountain\n   elevations called anticlinoria; -- opposed to geosynclinal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gear', 'n.', 'Clothing; garments; ornaments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gear', 'n.', 'Goods; property; household stuff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gear', 'n.', 'Whatever is prepared for use or wear; manufactured stuff or\n   material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gear', 'n.', 'The harness of horses or cattle; trapping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gear', 'n.', 'Warlike accouterments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gear', 'n.', 'Manner; custom; behavior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gear', 'n.', 'Business matters; affairs; concern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gear', 'n.', 'A toothed wheel, or cogwheel; as, a spur gear, or a bevel\n   gear; also, toothed wheels, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gear', 'n.', 'An apparatus for performing a special function; gearing; as,\n   the feed gear of a lathe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gear', 'n.', 'Engagement of parts with each other; as, in gear; out of\n   gear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gear', 'n.', 'See 1st Jeer (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gear', 'n.', 'Anything worthless; stuff; nonsense; rubbish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geared', 'imp. & p. p.', 'of Gear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gearing', 'p. pr. & vb. n.', 'of Gear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gear', 'v. t.', 'To dress; to put gear on; to harness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gear', 'v. t.', 'To provide with gearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gear', 'v. i.', 'To be in, or come into, gear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gearing', 'n.', 'Harness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gearing', 'n.', 'The parts by which motion imparted to one portion of an\n   engine or machine is transmitted to another, considered collectively;\n   as, the valve gearing of locomotive engine; belt gearing; esp., a train\n   of wheels for transmitting and varying motion in machinery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geason', 'a.', 'Rare; wonderful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geat', 'n.', 'The channel or spout through which molten metal runs into a\n   mold in casting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gecarcinian', 'n.', 'A land crab of the genus Gecarcinus, or of allied\n   genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geck', 'n.', 'Scorn, derision, or contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geck', 'n.', 'An object of scorn; a dupe; a gull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geck', 'n.', 'To deride; to scorn; to mock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geck', 'n.', 'To cheat; trick, or gull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geck', 'v. i.', 'To jeer; to show contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geckoes', 'pl. ', 'of Gecko');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gecko', 'n.', 'Any lizard of the family Geckonidae. The geckoes are small,\n   carnivorous, mostly nocturnal animals with large eyes and vertical,\n   elliptical pupils. Their toes are generally expanded, and furnished\n   with adhesive disks, by which they can run over walls and ceilings.\n   They are numerous in warm countries, and a few species are found in\n   Europe and the United States. See Wall gecko, Fanfoot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geckotian', 'n.', 'A gecko.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ged', 'n.', 'Alt. of Gedd');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gedd', 'n.', 'The European pike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geed', 'imp. & p. p.', 'of Gee');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geeing', 'p. pr. & vb. n.', 'of Gee');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gee', 'v. i.', 'To agree; to harmonize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gee', 'v. i.', 'To turn to the off side, or from the driver (i.e., in the\n   United States, to the right side); -- said of cattle, or a team; used\n   most frequently in the imperative, often with off, by drivers of oxen,\n   in directing their teams, and opposed to haw, or hoi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gee', 'v. t.', 'To cause (a team) to turn to the off side, or from the\n   driver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geer', '', 'Alt. of Geering');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geering', '', 'See Gear, Gearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geese', 'n.', 'pl. of Goose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geest', 'n.', 'Alluvial matter on the surface of land, not of recent\n   origin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geet', 'n.', 'Jet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geez', 'n.', 'The original native name for the ancient Ethiopic language or\n   people. See Ethiopic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gehenna', 'n.', 'The valley of Hinnom, near Jerusalem, where some of the\n   Israelites sacrificed their children to Moloch, which, on this account,\n   was afterward regarded as a place of abomination, and made a receptacle\n   for all the refuse of the city, perpetual fires being kept up in order\n   to prevent pestilential effluvia. In the New Testament the name is\n   transferred, by an easy metaphor, to Hell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geic', 'a.', 'Pertaining to, or derived from, earthy or vegetable mold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gein', 'n.', 'See Humin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geissler tube', '', 'A glass tube provided with platinum electrodes, and\n   containing some gas under very low tension, which becomes luminous when\n   an electrical discharge is passed through it; -- so called from the\n   name of a noted maker in germany. It is called also Plucker tube, from\n   the German physicist who devised it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geitonogamy', 'n.', 'Fertilization of flowers by pollen from other flowers\n   on the same plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelable', 'a.', 'Capable of being congealed; capable of being converted\n   into jelly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelada', 'n.', 'A baboon (Gelada Ruppelli) of Abyssinia, remarkable for the\n   length of the hair on the neck and shoulders of the adult male.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelastic', 'a.', 'Pertaining to laughter; used in laughing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelatification', 'n.', 'The formation of gelatin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelatigenous', 'n.', 'Producing, or yielding, gelatin; gelatiniferous; as,\n   the gelatigeneous tissues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelatin', 'n.', 'Alt. of Gelatine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelatine', 'n.', 'Animal jelly; glutinous material obtained from animal\n   tissues by prolonged boiling. Specifically (Physiol. Chem.), a\n   nitrogeneous colloid, not existing as such in the animal body, but\n   formed by the hydrating action of boiling water on the collagen of\n   various kinds of connective tissue (as tendons, bones, ligaments,\n   etc.). Its distinguishing character is that of dissolving in hot water,\n   and forming a jelly on cooling. It is an important ingredient of\n   calfs-foot jelly, isinglass, glue, etc. It is used as food, but its\n   nutritious qualities are of a low order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelatinated', 'imp. & p. p.', 'of Gelatinate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelatinating', 'p. pr. & vb. n.', 'of Gelatinate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelatinate', 'v. t.', 'To convert into gelatin, or into a substance\n   resembling jelly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelatinate', 'v. i.', 'To be converted into gelatin, or into a substance\n   like jelly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelatination', 'n.', 'The act of process of converting into gelatin, or a\n   substance like jelly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelatine', 'n.', 'Same as Gelatin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelatiniferous', 'a.', 'Yielding gelatin on boiling with water; capable of\n   gelatination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelatiniform', 'a.', 'Having the form of gelatin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelatinization', 'n.', 'Same as Gelatination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelatinize', 'v. t.', 'To convert into gelatin or jelly. Same as\n   Gelatinate, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelatinize', 'v. t.', 'To coat, or otherwise treat, with gelatin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelatinize', 'v. i.', 'Same as Gelatinate, v. i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelatinous', 'a.', 'Of the nature and consistence of gelatin or the jelly;\n   resembling jelly; viscous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelation', 'n.', 'The process of becoming solid by cooling; a cooling and\n   solidifying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geld', 'n.', 'Money; tribute; compensation; ransom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelded', 'imp. & p. p.', 'of Geld');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelding', 'p. pr. & vb. n.', 'of Geld');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geld', 'v. t.', 'To castrate; to emasculate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geld', 'v. t.', 'To deprive of anything essential.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geld', 'v. t.', 'To deprive of anything exceptionable; as, to geld a book,\n   or a story; to expurgate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geldable', 'a.', 'Capable of being gelded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geldable', 'a.', 'Liable to taxation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelder', 'n.', 'One who gelds or castrates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelder-rose', 'n.', 'Same as Guelder-rose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelding', 'v. t.', 'A castrated animal; -- usually applied to a horse, but\n   formerly used also of the human male.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelding', 'p. pr. a. & vb. n.', 'from Geld, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelid', 'a.', 'Cold; very cold; frozen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelidity', 'n.', 'The state of being gelid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelidly', 'adv.', 'In a gelid manner; coldly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelidness', 'n.', 'The state of being gelid; gelidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelly', 'n.', 'Jelly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geloscopy', 'n.', 'Divination by means of laughter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelose', 'n.', 'An amorphous, gummy carbohydrate, found in Gelidium,\n   agar-agar, and other seaweeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelsemic', 'a.', 'Gelseminic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelsemine', 'n.', 'An alkaloid obtained from the yellow jasmine (Gelsemium\n   sempervirens), as a bitter white semicrystalline substance; -- called\n   also gelsemia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelseminic', 'n.', 'Pertaining to, or derived from, the yellow jasmine\n   (Gelsemium sempervirens); as, gelseminic acid, a white crystalline\n   substance resembling esculin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelsemium', 'n.', 'A genus of climbing plants. The yellow (false) jasmine\n   (Gelsemium sempervirens) is a native of the Southern United States. It\n   has showy and deliciously fragrant flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelsemium', 'n.', 'The root of the yellow jasmine, used in malarial fevers,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelt', 'n.', 'Trubute, tax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelt', 'v. t.', 'A gelding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gelt', 'n.', 'Gilding; tinsel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gem', 'n.', 'A bud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gem', 'n.', 'A precious stone of any kind, as the ruby, emerald, topaz,\n   sapphire, beryl, spinel, etc., especially when cut and polished for\n   ornament; a jewel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gem', 'n.', 'Anything of small size, or expressed within brief limits,\n   which is regarded as a gem on account of its beauty or value, as a\n   small picture, a verse of poetry, a witty or wise saying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemmed', 'imp. & p. p.', 'of Gem');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemming', 'p. pr. & vb. n.', 'of Gem');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gem', 'v. t.', 'To put forth in the form of buds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gem', 'v. t.', 'To adorn with gems or precious stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gem', 'v. t.', 'To embellish or adorn, as with gems; as, a foliage gemmed\n   with dewdrops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemara', 'n.', 'The second part of the Talmud, or the commentary on the\n   Mishna (which forms the first part or text).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemaric', 'a.', 'Pertaining to the Gemara.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemarist', 'n.', 'One versed in the Gemara, or adhering to its teachings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemel', 'a.', 'Coupled; paired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemel', 'n.', 'One of the twins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemel', 'n.', 'One of the barrulets placed parallel and closed to each\n   other. Cf. Bars gemel, under Gemel, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemellipa-rous', 'a.', 'Producing twins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geminal', 'a.', 'A pair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geminate', 'a.', 'In pairs or twains; two together; binate; twin; as,\n   geminate flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geminate', 'v. t.', 'To double.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemination', 'n.', 'A doubling; duplication; repetition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemini', 'n. pl.', 'A constellation of the zodiac, containing the two\n   bright stars Castor and Pollux; also, the third sign of the zodiac,\n   which the sun enters about May 20th.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geminiflorous', 'a.', 'Having the flowers arranged in pairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geminous', 'a.', 'Double; in pairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geminy', 'n.', 'Twins; a pair; a couple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemitores', 'n. pl.', 'A division of birds including the true pigeons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemmae', 'pl. ', 'of Gemma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemma', 'n.', 'A leaf bud, as distinguished from a flower bud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemma', 'n.', 'A bud spore; one of the small spores or buds in the\n   reproduction of certain Protozoa, which separate one at a time from the\n   parent cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemmaceous', 'a.', 'Of or pertaining to gems or to gemmae; of the nature\n   of, or resembling, gems or gemmae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemmary', 'a.', 'Of or pertaining to gems.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemmary', 'n.', 'A receptacle for jewels or gems; a jewel house; jewels or\n   gems, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemmate', 'a.', 'Having buds; reproducing by buds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemmated', 'a.', 'Having buds; adorned with gems or jewels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemmation', 'n.', 'The formation of a new individual, either animal or\n   vegetable, by a process of budding; an asexual method of reproduction;\n   gemmulation; gemmiparity. See Budding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemmation', 'n.', 'The arrangement of buds on the stalk; also, of leaves in\n   the bud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemmeous', 'a.', 'Pertaining to gems; of the nature of gems; resembling\n   gems.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemmiferous', 'a.', 'Producing gems or buds');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemmiferous', 'a.', 'multiplying by buds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemmification', 'n.', 'The production of a bud or gem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemmiflorate', 'a.', 'Having flowers like buds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemminess', 'n.', 'The state or quality of being gemmy; spruceness;\n   smartness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemmipara', 'n. pl.', 'Alt. of Gemmipares');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemmipares', 'n. pl.', 'Animals which increase by budding, as hydroids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemmiparity', 'n.', 'Reproduction by budding; gemmation. See Budding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemmiparous', 'a.', 'Producing buds; reproducing by buds. See Gemmation, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemmosity', 'n.', 'The quality or characteristics of a gem or jewel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemmulation', 'n.', 'See Gemmation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemmule', 'n.', 'A little leaf bud, as the plumule between the cotyledons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemmule', 'n.', 'One of the buds of mosses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemmule', 'n.', 'One of the reproductive spores of algae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemmule', 'n.', 'An ovule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemmule', 'n.', 'A bud produced in generation by gemmation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemmule', 'n.', 'One of the imaginary granules or atoms which, according to\n   Darwins hypothesis of pangenesis, are continually being thrown off\n   from every cell or unit, and circulate freely throughout the system,\n   and when supplied with proper nutriment multiply by self-division and\n   ultimately develop into cells like those from which they were derived.\n   They are supposed to be transmitted from the parent to the offspring,\n   but are often transmitted in a dormant state during many generations\n   and are then developed. See Pangenesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemmuliferous', 'a.', 'Bearing or producing gemmules or buds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemmy', 'n.', 'Full of gems; bright; glittering like a gem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemmy', 'n.', 'Spruce; smart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemote', 'v. t.', 'A meeting; -- used in combination, as, Witenagemote, an\n   assembly of the wise men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gems', 'n.', 'The chamois.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemsbok', 'n.', 'A South African antelope (Oryx Capensis), having long,\n   sharp, nearly straight horns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gems-horn', 'n.', 'An organ stop with conical tin pipes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gemul', 'n.', 'A small South American deer (Furcifer Chilensis), with\n   simple forked horns.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-gen', '', 'A suffix used in scientific words in the sense of producing,\n   generating: as, amphigen, amidogen, halogen.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-gen', '', 'A suffix meaning produced, generated; as, exogen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gena', '', 'The cheek; the feathered side of the under mandible of a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gena', '', 'The part of the head to which the jaws of an insect are\n   attached.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genappe', 'n.', 'A worsted yarn or cord of peculiar smoothness, used in the\n   manufacture of braid, fringe, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gendarmes', 'pl. ', 'of Gendarme');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gens darmes', 'pl. ', 'of Gendarme');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gendarme', 'n.', 'One of a body of heavy cavalry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gendarme', 'n.', 'An armed policeman in France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gendarmery', 'n.', 'The body of gendarmes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gender', 'n.', 'Kind; sort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gender', 'n.', 'Sex, male or female.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gender', 'n.', 'A classification of nouns, primarily according to sex; and\n   secondarily according to some fancied or imputed quality associated\n   with sex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gendered', 'imp. & p. p.', 'of Gender');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gendering', 'p. pr. & vb. n.', 'of Gender');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gender', 'n.', 'To beget; to engender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gender', 'v. i.', 'To copulate; to breed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genderless', 'a.', 'Having no gender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geneagenesis', 'n.', 'Alternate generation. See under Generation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genealogic', 'a.', 'Genealogical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genealogical', 'a.', 'Of or pertaining to genealogy; as, a genealogical\n   table; genealogical order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genealogist', 'n.', 'One who traces genealogies or the descent of persons\n   or families.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genealogize', 'v. i.', 'To investigate, or relate the history of, descents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genealogies', 'pl. ', 'of Genealogy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genealogy', 'n.', 'An account or history of the descent of a person or\n   family from an ancestor; enumeration of ancestors and their children in\n   the natural order of succession; a pedigree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genealogy', 'n.', 'Regular descent of a person or family from a progenitor;\n   pedigree; lineage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genearch', 'n.', 'The chief of a family or tribe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genera', 'n. pl.', 'See Genus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generability', 'n.', 'Capability of being generated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generable', 'a.', 'Capable of being generated or produced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'General', 'a.', 'Relating to a genus or kind; pertaining to a whole class\n   or order; as, a general law of animal or vegetable economy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'General', 'a.', 'Comprehending many species or individuals; not special or\n   particular; including all particulars; as, a general inference or\n   conclusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'General', 'a.', 'Not restrained or limited to a precise import; not\n   specific; vague; indefinite; lax in signification; as, a loose and\n   general expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'General', 'a.', 'Common to many, or the greatest number; widely spread;\n   prevalent; extensive, though not universal; as, a general opinion; a\n   general custom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'General', 'a.', 'Having a relation to all; common to the whole; as, Adam,\n   our general sire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'General', 'a.', 'As a whole; in gross; for the most part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'General', 'a.', 'Usual; common, on most occasions; as, his general habit or\n   method.');
  INSERT INTO words (word, word_type, description) VALUES  (    'General', 'a.', 'The whole; the total; that which comprehends or relates to\n   all, or the chief part; -- opposed to particular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'General', 'a.', 'One of the chief military officers of a government or\n   country; the commander of an army, of a body of men not less than a\n   brigade. In European armies, the highest military rank next below field\n   marshal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'General', 'a.', 'The roll of the drum which calls the troops together; as,\n   to beat the general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'General', 'a.', 'The chief of an order of monks, or of all the houses or\n   congregations under the same rule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'General', 'a.', 'The public; the people; the vulgar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generalia', 'n. pl.', 'Generalities; general terms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generalissimo', 'a.', 'The chief commander of an army; especially, the\n   commander in chief of an army consisting of two or more grand divisions\n   under separate commanders; -- a title used in most foreign countries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generalities', 'pl. ', 'of Generality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generality', 'n.', 'The state of being general; the quality of including\n   species or particulars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generality', 'n.', 'That which is general; that which lacks specificalness,\n   practicalness, or application; a general or vague statement or phrase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generality', 'n.', 'The main body; the bulk; the greatest part; as, the\n   generality of a nation, or of mankind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generalizable', 'a.', 'Capable of being generalized, or reduced to a\n   general form of statement, or brought under a general rule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generalization', 'n.', 'The act or process of generalizing; the act of\n   bringing individuals or particulars under a genus or class; deduction\n   of a general principle from particulars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generalization', 'n.', 'A general inference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generalized', 'imp. & p. p.', 'of Generalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generalizing', 'p. pr. & vb. n.', 'of Generalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generalize', 'v. t.', 'To bring under a genus or under genera; to view in\n   relation to a genus or to genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generalize', 'v. t.', 'To apply to other genera or classes; to use with a\n   more extensive application; to extend so as to include all special\n   cases; to make universal in application, as a formula or rule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generalize', 'v. t.', 'To derive or deduce (a general conception, or a\n   general principle) from particulars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generalize', 'v. i.', 'To form into a genus; to view objects in their\n   relations to a genus or class; to take general or comprehensive views.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generalized', 'a.', 'Comprising structural characters which are separated\n   in more specialized forms; synthetic; as, a generalized type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generalizer', 'n.', 'One who takes general or comprehensive views.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generally', 'adv.', 'In general; commonly; extensively, though not\n   universally; most frequently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generally', 'adv.', 'In a general way, or in general relation; in the main;\n   upon the whole; comprehensively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generally', 'adv.', 'Collectively; as a whole; without omissions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generalness', 'n.', 'The condition or quality of being general; frequency;\n   commonness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generalship', 'n.', 'The office of a general; the exercise of the functions\n   of a general; -- sometimes, with the possessive pronoun, the\n   personality of a general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generalship', 'n.', 'Military skill in a general officer or commander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generalship', 'n.', 'Fig.: Leadership; management.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generalty', 'n.', 'Generality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generant', 'a.', 'Generative; producing');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generant', 'a.', 'acting as a generant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generant', 'n.', 'That which generates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generant', 'n.', 'A generatrix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generated', 'imp. & p. p.', 'of Generate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generating', 'p. pr. & vb. n.', 'of Generate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generate', 'v. t.', 'To beget; to procreate; to propagate; to produce (a\n   being similar to the parent); to engender; as, every animal generates\n   its own species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generate', 'v. t.', 'To cause to be; to bring into life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generate', 'v. t.', 'To originate, especially by a vital or chemical\n   process; to produce; to cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generate', 'v. t.', 'To trace out, as a line, figure, or solid, by the\n   motion of a point or a magnitude of inferior order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generation', 'n.', 'The act of generating or begetting; procreation, as of\n   animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generation', 'n.', 'Origination by some process, mathematical, chemical, or\n   vital; production; formation; as, the generation of sounds, of gases,\n   of curves, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generation', 'n.', 'That which is generated or brought forth; progeny;\n   offspiring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generation', 'n.', 'A single step or stage in the succession of natural\n   descent; a rank or remove in genealogy. Hence: The body of those who\n   are of the same genealogical rank or remove from an ancestor; the mass\n   of beings living at one period; also, the average lifetime of man, or\n   the ordinary period of time at which one rank follows another, or\n   father is succeeded by child, usually assumed to be one third of a\n   century; an age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generation', 'n.', 'Race; kind; family; breed; stock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generation', 'n.', 'The formation or production of any geometrical\n   magnitude, as a line, a surface, a solid, by the motion, in accordance\n   with a mathematical law, of a point or a magnitude; as, the generation\n   of a line or curve by the motion of a point, of a surface by a line, a\n   sphere by a semicircle, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generation', 'n.', 'The aggregate of the functions and phenomene which\n   attend reproduction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generative', 'a.', 'Having the power of generating, propagating,\n   originating, or producing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generator', 'n.', 'One who, or that which, generates, begets, causes, or\n   produces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generator', 'n.', 'An apparatus in which vapor or gas is formed from a\n   liquid or solid by means of heat or chemical process, as a steam\n   boiler, gas retort, or vessel for generating carbonic acid gas, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generator', 'n.', 'The principal sound or sounds by which others are\n   produced; the fundamental note or root of the common chord; -- called\n   also generating tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generatrices', 'pl. ', 'of Generatrix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generatrixes', 'pl. ', 'of Generatrix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generatrix', 'n.', 'That which generates; the point, or the mathematical\n   magnitude, which, by its motion, generates another magnitude, as a\n   line, surface, or solid; -- called also describent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generic', 'a.', 'Alt. of Generical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generical', 'a.', 'Pertaining to a genus or kind; relating to a genus, as\n   distinct from a species, or from another genus; as, a generic\n   description; a generic difference; a generic name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generical', 'a.', 'Very comprehensive; pertaining or appropriate to large\n   classes or their characteristics; -- opposed to specific.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generically', 'adv.', 'With regard to a genus, or an extensive class; as,\n   an animal generically distinct from another, or two animals or plants\n   generically allied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genericalness', 'n.', 'The quality of being generic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generification', 'n.', 'The act or process of generalizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generosity', 'n.', 'Noble birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generosity', 'n.', 'The quality of being noble; noble-mindedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generosity', 'n.', 'Liberality in giving; munificence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generous', 'a.', 'Of honorable birth or origin; highborn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generous', 'a.', 'Exhibiting those qualities which are popularly reregarded\n   as belonging to high birth; noble; honorable; magnanimous; spirited;\n   courageous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generous', 'a.', 'Open-handed; free to give; not close or niggardly;\n   munificent; as, a generous friend or father.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generous', 'a.', 'Characterized by generosity; abundant; overflowing; as, a\n   generous table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Generous', 'a.', 'Full of spirit or strength; stimulating; exalting; as,\n   generous wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genesee epoch', '', 'The closing subdivision of the Hamilton period in the\n   American Devonian system; -- so called because the formations of this\n   period crop out in Genesee, New York.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genesial', 'a.', 'Of or relating to generation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genesiolgy', 'n.', 'The doctrine or science of generation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genesis', 'n.', 'The act of producing, or giving birth or origin to\n   anything; the process or mode of originating; production; formation;\n   origination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genesis', 'n.', 'The first book of the Old Testament; -- so called by the\n   Greek translators, from its containing the history of the creation of\n   the world and of the human race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genesis', 'n.', 'Same as Generation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genet', 'n.', 'Alt. of Genette');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genette', 'n.', 'One of several species of small Carnivora of the genus\n   Genetta, allied to the civets, but having the scent glands less\n   developed, and without a pouch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genette', 'n.', 'The fur of the common genet (Genetta vulgaris); also, any\n   skin dressed in imitation of this fur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genet', 'n.', 'A small-sized, well-proportioned, Spanish horse; a jennet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genethliac', 'a.', 'Pertaining to nativities; calculated by astrologers;\n   showing position of stars at ones birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genethliac', 'n.', 'A birthday poem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genethliac', 'n.', 'One skilled in genethliacs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genethliacal', 'a.', 'Genethliac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genethliacs', 'n.', 'The science of calculating nativities, or predicting\n   the future events of life from the stars which preside at birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genethlialogy', 'n.', 'Divination as to the destinies of one newly born;\n   the act or art of casting nativities; astrology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genethliatic', 'n.', 'One who calculates nativities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genetic', 'a.', 'Same as Genetical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genetical', 'a.', 'Pertaining to, concerned with, or determined by, the\n   genesis of anything, or its natural mode of production or development.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genetically', 'adv.', 'In a genetical manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geneva', 'n.', 'The chief city of Switzerland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geneva', 'n.', 'A strongly alcoholic liquor, flavored with juniper berries;\n   -- made in Holland; Holland gin; Hollands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genevan', 'a.', 'Of or pertaining to Geneva, in Switzerland; Genevese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genevan', 'n.', 'A native or inhabitant of Geneva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genevan', 'n.', 'A supported of Genevanism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genevanism', 'n.', 'Strict Calvinism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genevese', 'a.', 'Of or pertaining to Geneva, in Switzerland; Genevan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genevese', 'n. sing. & pl.', 'A native or inhabitant of Geneva;\n   collectively, the inhabitants of Geneva; people of Geneva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genial', 'a.', 'Same as Genian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genial', 'a.', 'Contributing to, or concerned in, propagation or\n   production; generative; procreative; productive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genial', 'a.', 'Contributing to, and sympathizing with, the enjoyment of\n   life; sympathetically cheerful and cheering; jovial and inspiring joy\n   or happiness; exciting pleasure and sympathy; enlivening; kindly; as,\n   she was of a cheerful and genial disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genial', 'a.', 'Belonging to ones genius or natural character; native;\n   natural; inborn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genial', 'a.', 'Denoting or marked with genius; belonging to the higher\n   nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geniality', 'n.', 'The quality of being genial; sympathetic cheerfulness;\n   warmth of disposition and manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genially', 'adv.', 'By genius or nature; naturally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genially', 'adv.', 'Gayly; cheerfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genialness', 'n.', 'The quality of being genial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genian', 'a.', 'Of or pertaining to the chin; mental; as, the genian\n   prominence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geniculate', 'a.', 'Bent abruptly at an angle, like the knee when bent; as,\n   a geniculate stem; a geniculate ganglion; a geniculate twin crystal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geniculated', 'imp. & p. p.', 'of Geniculate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geniculating', 'p. pr. & vb. n.', 'of Geniculate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geniculate', 'v. t.', 'To form joints or knots on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geniculated', 'a.', 'Same as Geniculate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geniculation', 'n.', 'The act of kneeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geniculation', 'n.', 'The state of being bent abruptly at an angle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genie', 'n.', 'See Genius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genio', 'n.', 'A man of a particular turn of mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geniohyoid', 'a.', 'Of or pertaining to the chin and hyoid bone; as, the\n   geniohyoid muscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genipap', 'n.', 'The edible fruit of a West Indian tree (Genipa Americana)\n   of the order Rubiaceae. It is oval in shape, as a large as a small\n   orange, of a pale greenish color, and with dark purple juice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genista', 'n.', 'A genus of plants including the common broom of Western\n   Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genital', 'a.', 'Pertaining to generation, or to the generative organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genitals', 'a.', 'The organs of generation; the sexual organs; the private\n   parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geniting', 'n.', 'A species of apple that ripens very early.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genitival', 'a.', 'Possessing genitive from; pertaining to, or derived\n   from, the genitive case; as, a genitival adverb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genitive', 'a.', 'Of or pertaining to that case (as the second case of\n   Latin and Greek nouns) which expresses source or possession. It\n   corresponds to the possessive case in English.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genitive', 'n.', 'The genitive case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genitocrural', 'a.', 'Pertaining to the genital organs and the thigh; --\n   applied especially to one of the lumbar nerves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genitor', 'n.', 'One who begets; a generator; an originator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genitor', 'n.', 'The genitals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genitourinary', 'a.', 'See Urogenital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geniture', 'n.', 'Generation; procreation; birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geniuses', 'pl. ', 'of Genius');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genii', 'pl. ', 'of Genius');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genius', 'n.', 'A good or evil spirit, or demon, supposed by the ancients\n   to preside over a mans destiny in life; a tutelary deity; a\n   supernatural being; a spirit, good or bad. Cf. Jinnee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genius', 'n.', 'The peculiar structure of mind with whoch each individual\n   is endowed by nature; that disposition or aptitude of mind which is\n   peculiar to each man, and which qualifies him for certain kinds of\n   action or special success in any pursuit; special taste, inclination,\n   or disposition; as, a genius for history, for poetry, or painting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genius', 'n.', 'Peculiar character; animating spirit, as of a nation, a\n   religion, a language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genius', 'n.', 'Distinguished mental superiority; uncommon intellectual\n   power; especially, superior power of invention or origination of any\n   kind, or of forming new combinations; as, a man of genius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genius', 'n.', 'A man endowed with uncommon vigor of mind; a man of\n   superior intellectual faculties; as, Shakespeare was a rare genius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genoese', 'a.', 'Of or pertaining to Genoa, a city of Italy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genoese', 'n. sing. & pl.', 'A native or inhabitant of Genoa; collectively,\n   the people of Genoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genouillere', 'n.', 'A metal plate covering the knee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genouillere', 'n.', 'That part of a parapet which lies between the gun\n   platform and the bottom of an embrasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-genous', '', 'A suffix signifying producing, yielding; as, alkaligenous;\n   endogenous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genre', 'n.', 'A style of painting, sculpture, or other imitative art,\n   which illustrates everyday life and manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentes', 'pl. ', 'of Gens');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gens', 'a.', 'A clan or family connection, embracing several families of\n   the same stock, who had a common name and certain common religious\n   rites; a subdivision of the Roman curia or tribe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gens', 'a.', 'A minor subdivision of a tribe, among American aborigines. It\n   includes those who have a common descent, and bear the same totem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gent', 'a.', 'Gentle; noble; of gentle birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gent', 'a.', 'Neat; pretty; fine; elegant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genteel', 'a.', 'Possessing or exhibiting the qualities popularly regarded\n   as belonging to high birth and breeding; free from vulgarity, or\n   lowness of taste or behavior; adapted to a refined or cultivated taste;\n   polite; well-bred; as, genteel company, manners, address.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genteel', 'a.', 'Graceful in mien or form; elegant in appearance, dress, or\n   manner; as, the lady has a genteel person. Law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genteel', 'a.', 'Suited to the position of lady or a gentleman; as, to live\n   in a genteel allowance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genteelish', 'a.', 'Somewhat genteel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genteelly', 'adv.', 'In a genteel manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genteelness', 'n.', 'The quality of being genteel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genterie', 'n.', 'Alt. of Gentrie');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentrie', 'n.', 'Nobility of birth or of character; gentility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentian', 'n.', 'Any one of a genus (Gentiana) of herbaceous plants with\n   opposite leaves and a tubular four- or five-lobed corolla, usually\n   blue, but sometimes white, yellow, or red. See Illust. of Capsule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentianaceous', 'a.', 'Of or pertaining to a natural order of plants\n   (Gentianaceae) of which the gentian is the type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentianella', 'n.', 'A kind of blue color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentianic', 'a.', 'Pertaining to or derived from the gentian; as, gentianic\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentianine', 'n.', 'A bitter, crystallizable substance obtained from\n   gentian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentianose', 'n.', 'A crystallizable, sugarlike substance, with a slightly\n   sweetish taste, obtained from the gentian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentil', 'a. & n.', 'Gentle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentile', 'a.', 'One of a non-Jewish nation; one neither a Jew nor a\n   Christian; a worshiper of false gods; a heathen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentile', 'a.', 'Belonging to the nations at large, as distinguished from\n   the Jews; ethnic; of pagan or heathen people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentile', 'a.', 'Denoting a race or country; as, a gentile noun or\n   adjective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentile-falcon', 'n.', 'See Falcon-gentil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentilesse', 'a.', 'Gentleness; courtesy; kindness; nobility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentilish', 'a.', 'Heathenish; pagan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentilism', 'n.', 'Hethenism; paganism; the worship of false gods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentilism', 'n.', 'Tribal feeling; devotion to ones gens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentilitial', 'a.', 'Alt. of Gentilitious');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentilitious', 'a.', 'Peculiar to a people; national.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentilitious', 'a.', 'Hereditary; entailed on a family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentility', 'n.', 'Good extraction; dignity of birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentility', 'n.', 'The quality or qualities appropriate to those who are\n   well born, as self-respect, dignity, courage, courtesy, politeness of\n   manner, a graceful and easy mien and behavior, etc.; good breeding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentility', 'n.', 'The class in society who are, or are expected to be,\n   genteel; the gentry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentility', 'n.', 'Paganism; heathenism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentilize', 'v. i.', 'To live like a gentile or heathen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentilize', 'v. i.', 'To act the gentleman; -- with it (see It, 5).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentilize', 'v. i.', 'To render gentile or gentlemanly; as, to gentilize\n   your unworthy sones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentilly', 'a.', 'In a gentle or hoble manner; frankly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentiopikrin', 'n.', 'A bitter, yellow, crystalline substance, regarded as\n   a glucoside, and obtained from the gentian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentisin', 'n.', 'A tasteless, yellow, crystalline substance, obtained from\n   the gentian; -- called also gentianin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentle', 'superl.', 'Well-born; of a good family or respectable birth,\n   though not noble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentle', 'superl.', 'Quiet and refined in manners; not rough, harsh, or\n   stern; mild; meek; bland; amiable; tender; as, a gentle nature, temper,\n   or disposition; a gentle manner; a gentle address; a gentle voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentle', 'superl.', 'A compellative of respect, consideration, or\n   conciliation; as, gentle reader.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentle', 'superl.', 'Not wild, turbulent, or refractory; quiet and docile;\n   tame; peaceable; as, a gentle horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentle', 'superl.', 'Soft; not violent or rough; not strong, loud, or\n   disturbing; easy; soothing; pacific; as, a gentle touch; a gentle\n   gallop .');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentle', 'n.', 'One well born; a gentleman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentle', 'n.', 'A trained falcon. See Falcon-gentil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentle', 'n.', 'A dipterous larva used as fish bait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentle', 'v. t.', 'To make genteel; to raise from the vulgar; to ennoble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentle', 'v. t.', 'To make smooth, cozy, or agreeable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentle', 'v. t.', 'To make kind and docile, as a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentlefolk', 'n. pl.', 'Alt. of Gentlefolks');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentlefolks', 'n. pl.', 'Persons of gentle or good family and breeding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentle-hearted', 'a.', 'Having a kind or gentle disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentlemen', 'pl. ', 'of Gentleman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentleman', 'n.', 'A man well born; one of good family; one above the\n   condition of a yeoman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentleman', 'n.', 'One of gentle or refined manners; a well-bred man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentleman', 'n.', 'One who bears arms, but has no title.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentleman', 'n.', 'The servant of a man of rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentleman', 'n.', 'A man, irrespective of condition; -- used esp. in the\n   plural (= citizens; people), in addressing men in popular assemblies,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentlemanhood', 'n.', 'The qualities or condition of a gentleman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentlemanlike', 'a.', 'Alt. of Gentlemanly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentlemanly', 'a.', 'Of, pertaining to, resembling, or becoming, a\n   gentleman; well-behaved; courteous; polite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentlemanliness', 'n.', 'The state of being gentlemanly; gentlemanly\n   conduct or manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentlemanship', 'n.', 'The carriage or quality of a gentleman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentleness', 'n.', 'The quality or state of being gentle, well-born, mild,\n   benevolent, docile, etc.; gentility; softness of manners, disposition,\n   etc.; mildness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentleship', 'n.', 'The deportment or conduct of a gentleman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentlesse', 'n.', 'Gentilesse; gentleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentlewomen', 'pl. ', 'of Gentlewoman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentlewoman', 'n.', 'A woman of good family or of good breeding; a woman\n   above the vulgar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentlewoman', 'n.', 'A woman who attends a lady of high rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gently', 'adv.', 'In a gentle manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentoos', 'pl. ', 'of Gentoo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentoo', 'n.', 'A native of Hindostan; a Hindoo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentry', 'a.', 'Birth; condition; rank by birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentry', 'a.', 'People of education and good breeding; in England, in a\n   restricted sense, those between the nobility and the yeomanry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gentry', 'a.', 'Courtesy; civility; complaisance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genty', 'a.', 'Neat; trim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genua', 'pl. ', 'of Genu');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genu', 'n.', 'The knee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genu', 'n.', 'The kneelike bend, in the anterior part of the callosum of\n   the brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genuflected', 'imp. & p. p.', 'of Genuflect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genuflecting', 'p. pr. & vb. n.', 'of Genuflect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genuflect', 'v. i.', 'To bend the knee, as in worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genuflection', 'n.', 'The act of bending the knee, particularly in worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genuine', 'a.', 'Belonging to, or proceeding from, the original stock;\n   native; hence, not counterfeit, spurious, false, or adulterated;\n   authentic; real; natural; true; pure; as, a genuine text; a genuine\n   production; genuine materials.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genera', 'pl. ', 'of Genus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genus', 'n.', 'A class of objects divided into several subordinate species;\n   a class more extensive than a species; a precisely defined and exactly\n   divided class; one of the five predicable conceptions, or sorts of\n   terms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genus', 'n.', 'An assemblage of species, having so many fundamental points\n   of structure in common, that in the judgment of competent scientists,\n   they may receive a common substantive name. A genus is not necessarily\n   the lowest definable group of species, for it may often be divided into\n   several subgenera. In proportion as its definition is exact, it is\n   natural genus; if its definition can not be made clear, it is more or\n   less an artificial genus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Genys', 'n.', 'See Gonys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geocentric', 'a.', 'Alt. of Geocentrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geocentrical', 'a.', 'Having reference to the earth as center; in relation\n   to or seen from the earth, -- usually opposed to heliocentric, as seen\n   from the sun; as, the geocentric longitude or latitude of a planet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geocentrical', 'a.', 'Having reference to the center of the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geocentrically', 'adv.', 'In a geocentric manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geocronite', 'n.', 'A lead-gray or grayish blue mineral with a metallic\n   luster, consisting of sulphur, antimony, and lead, with a small\n   proportion of arsenic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geocyclic', 'a.', 'Of, pertaining to, or illustrating, the revolutions of\n   the earth; as, a geocyclic machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geocyclic', 'a.', 'Circling the earth periodically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geode', 'n.', 'A nodule of stone, containing a cavity, lined with crystals\n   or mineral matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geode', 'n.', 'The cavity in such a nodule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geodephagous', 'a.', 'Living in the earth; -- applied to the ground\n   beetles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geodesic', 'a.', 'Alt. of Geodesical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geodesical', 'a.', 'Of or pertaining to geodesy; geodetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geodesic', 'n.', 'A geodetic line or curve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geodesist', 'n.', 'One versed in geodesy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geodesy', 'n.', 'That branch of applied mathematics which determines, by\n   means of observations and measurements, the figures and areas of large\n   portions of the earths surface, or the general figure and dimenshions\n   of the earth; or that branch of surveying in which the curvature of the\n   earth is taken into account, as in the surveys of States, or of long\n   lines of coast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geodetic', 'a.', 'Alt. of Geodetical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geodetical', 'a.', 'Of or pertaining to geodesy; obtained or determined by\n   the operations of geodesy; engaged in geodesy; geodesic; as, geodetic\n   surveying; geodetic observers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geodetically', 'adv.', 'In a geodetic manner; according to geodesy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geodetics', 'n.', 'Same as Geodesy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geodiferous', 'a.', 'Producing geodes; containing geodes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geoduck', 'n.', 'A gigantic clam (Glycimeris generosa) of the Pacific coast\n   of North America, highly valued as an article of food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geognosis', 'n.', 'Knowledge of the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geognost', 'n.', 'One versed in geognosy; a geologist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geognostic', 'a.', 'Alt. of Geognostical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geognostical', 'a.', 'Of or pertaining to geognosy, or to a knowledge of\n   the structure of the earth; geological.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geognosy', 'n.', 'That part of geology which treats of the materials of the\n   earths structure, and its general exterior and interior constitution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geogonic', 'a.', 'Alt. of Geogonical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geogonical', 'a.', 'Of or pertaining to geogony, or to the formation of the\n   earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geogony', 'n.', 'The branch of science which treats of the formation of the\n   earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geographer', 'n.', 'One versed in geography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geographic', 'a.', 'Alt. of Geographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geographical', 'a.', 'Of or pertaining to geography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geographically', 'adv.', 'In a geographical manner or method; according to\n   geography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geographies', 'pl. ', 'of Geography');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geography', 'n.', 'The science which treats of the world and its\n   inhabitants; a description of the earth, or a portion of the earth,\n   including its structure, fetures, products, political divisions, and\n   the people by whom it is inhabited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geography', 'n.', 'A treatise on this science.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geolatry', 'n.', 'The worship of the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geologer', 'n.', 'Alt. of Geologian');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geologian', 'n.', 'A geologist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geologic', 'a.', 'Alt. of Geological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geological', 'a.', 'Of or pertaining to geology, or the science of the\n   earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geologically', 'adv.', 'In a geological manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geologist', 'n.', 'One versed in the science of geology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geologized', 'imp. & p. p.', 'of Geologize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geologizing', 'p. pr. & vb. n.', 'of Geologize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geologize', 'v. i.', 'To study geology or make geological investigations in\n   the field; to discourse as a geologist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geologies', 'pl. ', 'of Geology');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geology', 'n.', 'The science which treats: (a) Of the structure and mineral\n   constitution of the globe; structural geology. (b) Of its history as\n   regards rocks, minerals, rivers, valleys, mountains, climates, life,\n   etc.; historical geology. (c) Of the causes and methods by which its\n   structure, features, changes, and conditions have been produced;\n   dynamical geology. See Chart of The Geological Series.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geology', 'n.', 'A treatise on the science.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geomalism', 'n.', 'The tendency of an organism to respond, during its\n   growth, to the force of gravitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geomancer', 'n.', 'One who practices, or is versed in, geomancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geomancy', 'n.', 'A kind of divination by means of figures or lines, formed\n   by little dots or points, originally on the earth, and latterly on\n   paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geomantic', 'a.', 'Alt. of Geomantical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geomantical', 'a.', 'Pertaining or belonging to geomancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geometer', 'n.', 'One skilled in geometry; a geometrician; a mathematician.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geometer', 'n.', 'Any species of geometrid moth; a geometrid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geometral', 'a.', 'Pertaining to geometry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geometric', 'a.', 'Alt. of Geometrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geometrical', 'a.', 'Pertaining to, or according to the rules or principles\n   of, geometry; determined by geometry; as, a geometrical solution of a\n   problem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geometrically', 'adv.', 'According to the rules or laws of geometry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geometrician', 'n.', 'One skilled in geometry; a geometer; a mathematician.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geometrid', 'a.', 'Pertaining or belonging to the Geometridae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geometrid', 'n.', 'One of numerous genera and species of moths, of the\n   family Geometridae; -- so called because their larvae (called loopers,\n   measuring worms, spanworms, and inchworms) creep in a looping manner,\n   as if measuring. Many of the species are injurious to agriculture, as\n   the cankerworms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geometrized', 'imp. & p. p.', 'of Geometrize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geometrizing', 'p. pr. & vb. n.', 'of Geometrize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geometrize', 'v. i.', 'To investigate or apprehend geometrical quantities\n   or laws; to make geometrical constructions; to proceed in accordance\n   with the principles of geometry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geometries', 'pl. ', 'of Geometry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geometry', 'n.', 'That branch of mathematics which investigates the\n   relations, properties, and measurement of solids, surfaces, lines, and\n   angles; the science which treats of the properties and relations of\n   magnitudes; the science of the relations of space.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geometry', 'n.', 'A treatise on this science.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geophagism', 'n.', 'The act or habit of eating earth. See Dirt eating,\n   under Dirt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geophagist', 'n.', 'One who eats earth, as dirt, clay, chalk, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geophagous', 'a.', 'Earth-eating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geophila', 'n. pl.', 'The division of Mollusca which includes the land\n   snails and slugs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geoponic', 'a.', 'Alt. of Geoponical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geoponical', 'a.', 'Pertaining to tillage of the earth, or agriculture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geoponics', 'n.', 'The art or science of cultivating the earth;\n   agriculture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Georama', 'n.', 'A hollow globe on the inner surface of which a map of the\n   world is depicted, to be examined by one standing inside.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geordie', 'n.', 'A name given by miners to George Stephensons safety lamp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'George', 'n.', 'A figure of St. George (the patron saint of England) on\n   horseback, appended to the collar of the Order of the Garter. See\n   Garter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'George', 'n.', 'A kind of brown loaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'George noble', '', 'A gold noble of the time of Henry VIII. See Noble, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Georgian', 'a.', 'Of or pertaining to Georgia, in Asia, or to Georgia, one\n   of the United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Georgian', 'a.', 'Of or relating to the reigns of the four Georges, kings\n   of Great Britan; as, the Georgian era.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Georgian', 'n.', 'A native of, or dweller in, Georgia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Georgic', 'a.', 'A rural poem; a poetical composition on husbandry,\n   containing rules for cultivating lands, etc.; as, the Georgics of\n   Virgil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Georgic', 'a.', 'Alt. of Georgical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Georgical', 'a.', 'Relating to agriculture and rural affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Georgium Sidus', '', 'The planet Uranus, so named by its discoverer, Sir W.\n   Herschel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geoscopy', 'n.', 'Knowledge of the earth, ground, or soil, obtained by\n   inspection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geoselenic', 'a.', 'Pertaining to the earth and moon; belonging to the\n   joint action or mutual relations of the earth and moon; as, geoselenic\n   phenomena.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geostatic', 'a.', 'Relating to the pressure exerted by earth or similar\n   substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geosynclinal', 'n.', 'the downward bend or subsidence of the earths crust,\n   which allows of the gradual accumulation of sediment, and hence forms\n   the first step in the making of a mountain range; -- opposed to\n   geanticlinal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geothermometer', 'n.', 'A thermometer specially constructed for measuring\n   temperetures at a depth below the surface of the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geotic', 'a.', 'Belonging to earth; terrestrial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geotropic', 'a.', 'Relating to, or showing, geotropism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geotropism', 'n.', 'A disposition to turn or incline towards the earth; the\n   influence of gravity in determining the direction of growth of an\n   organ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gephyrea', 'n. pl.', 'An order of marine Annelida, in which the body is\n   imperfectly, or not at all, annulated externally, and is mostly without\n   setae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gephyrean', 'a.', 'Belonging to the Gephyrea. -- n. One of the Gerphyrea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gephyreoid', 'a. & n.', 'Gephyrean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gepound', 'n.', 'See Gipoun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gerah', 'n.', 'A small coin and weight; 1-20th of a shekel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geraniaceous', 'a.', 'Of or pertaining to a natural order of pants\n   (Geraniaceae) which includes the genera Geranium, Pelargonium, and many\n   others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geraniine', 'n.', 'Alt. of Geranine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geranine', 'n.', 'A valuable astringent obtained from the root of the\n   Geranium maculatum or cranes-bill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geranine', 'n.', 'A liquid terpene, obtained from the cranes-bill\n   (Geranium maculatum), and having a peculiar mulberry odor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geranium', 'n.', 'A genus of plants having a beaklike tours or receptacle,\n   around which the seed capsules are arranged, and membranous\n   projections, or stipules, at the joints. Most of the species have showy\n   flowers and a pungent odor. Called sometimes cranes-bill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geranium', 'n.', 'A cultivated pelargonium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gerant', 'n.', 'The manager or acting partner of a company, joint-stock\n   association, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gerbe', 'n.', 'A kind of ornamental firework.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gerbil', 'n.', 'Alt. of Gerbille');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gerbille', 'n.', 'One of several species of small, jumping, murine rodents,\n   of the genus Gerbillus. In their leaping powers they resemble the\n   jerboa. They inhabit Africa, India, and Southern Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gerboa', 'n.', 'The jerboa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gere', 'n.', 'Gear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gerent', 'a.', 'Bearing; carrying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gerfalcon', 'n.', 'See Gyrfalcon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gerful', 'a.', 'Changeable; capricious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gerland', 'n.', 'Alt. of Gerlond');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gerlond', 'n.', 'A garland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gerlind', 'n.', 'A salmon returning from the sea the second time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Germ', 'n.', 'That which is to develop a new individual; as, the germ of a\n   fetus, of a plant or flower, and the like; the earliest form under\n   which an organism appears.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Germ', 'n.', 'That from which anything springs; origin; first principle;\n   as, the germ of civil liberty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Germ', 'v. i.', 'To germinate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Germain', 'a.', 'See Germane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'German', 'a.', 'Nearly related; closely akin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Germans', 'pl. ', 'of German');
  INSERT INTO words (word, word_type, description) VALUES  (    'German', 'n.', 'A native or one of the people of Germany.');
  INSERT INTO words (word, word_type, description) VALUES  (    'German', 'n.', 'The German language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'German', 'n.', 'A round dance, often with a waltz movement, abounding in\n   capriciosly involved figures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'German', 'n.', 'A social party at which the german is danced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'German', 'n.', 'Of or pertaining to Germany.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Germander', 'n.', 'A plant of the genus Teucrium (esp. Teucrium Chamaedrys\n   or wall germander), mintlike herbs and low shrubs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Germane', 'a.', 'Literally, near akin; hence, closely allied; appropriate\n   or fitting; relevant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Germanic', 'a.', 'Pertaining to, or containing, germanium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Germanic', 'n.', 'Of or pertaining to Germany; as, the Germanic\n   confederacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Germanic', 'n.', 'Teutonic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Germanism', 'n.', 'An idiom of the German language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Germanism', 'n.', 'A characteristic of the Germans; a characteristic German\n   mode, doctrine, etc.; rationalism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Germanium', 'n.', 'A rare element, recently discovered (1885), in a silver\n   ore (argyrodite) at Freiberg. It is a brittle, silver-white metal,\n   chemically intermediate between the metals and nonmetals, resembles\n   tin, and is in general identical with the predicted ekasilicon. Symbol\n   Ge. Atomic weight 72.3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Germanization', 'n.', 'The act of Germanizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Germanized', 'imp. & p. p.', 'of Germanize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Germanizing', 'p. pr. & vb. n.', 'of Germanize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Germanize', 'v. t.', 'To make German, or like what is distinctively German;\n   as, to Germanize a province, a language, a society.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Germanize', 'v. i.', 'To reason or write after the manner of the Germans.');
