  INSERT INTO words (word, word_type, description) VALUES  (    'Gingerness', 'n.', 'Cautiousness; tenderness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gingham', 'n.', 'A kind of cotton or linen cloth, usually in stripes or\n   checks, the yarn of which is dyed before it is woven; -- distinguished\n   from printed cotton or prints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ginging', 'n.', 'The lining of a mine shaft with stones or bricks to\n   prevent caving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gingival', 'a.', 'Of or pertaining to the gums.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gingle', 'n. & v.', 'See Jingle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ginglyform', 'a.', 'Ginglymoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ginglymodi', 'n.', 'An order of ganoid fishes, including the modern gar\n   pikes and many allied fossil forms. They have rhombic, ganoid scales, a\n   heterocercal tail, paired fins without an axis, fulcra on the fins, and\n   a bony skeleton, with the vertebrae convex in front and concave behind,\n   forming a ball and socket joint. See Ganoidel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ginglymoid', 'a.', 'Alt. of Ginglymoidal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ginglymoidal', 'a.', 'Pertaining to, or resembling, a ginglymus, or hinge\n   joint; ginglyform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ginglymi', 'pl. ', 'of Ginglymus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ginglymus', 'n.', 'A hinge joint; an articulation, admitting of flexion and\n   extension, or motion in two directions only, as the elbow and the\n   ankle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ginhouse', 'n.', 'A building where cotton is ginned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ginkgoes', 'pl. ', 'of Ginkgo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ginkgo', 'n.', 'A large ornamental tree (Ginkgo biloba) from China and\n   Japan, belonging to the Yew suborder of Coniferae. Its leaves are so\n   like those of some maidenhair ferns, that it is also called the\n   maidenhair tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ginn', 'pl. ', 'of Ginnee');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ginnee', 'n.', 'See Jinnee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ginnet', 'n.', 'See Genet, a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ginning', 'v. i.', 'Beginning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ginny-carriage', 'n.', 'A small, strong carriage for conveying materials on\n   a railroad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ginseng', 'n.', 'A plant of the genus Aralia, the root of which is highly\n   valued as a medicine among the Chinese. The Chinese plant (Aralia\n   Schinseng) has become so rare that the American (A. quinquefolia) has\n   largely taken its place, and its root is now an article of export from\n   America to China. The root, when dry, is of a yellowish white color,\n   with a sweetness in the taste somewhat resembling that of licorice,\n   combined with a slight aromatic bitterness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ginshop', 'n.', 'A shop or barroom where gin is sold as a beverage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gip', 'v. t.', 'To take out the entrails of (herrings).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gip', 'n.', 'A servant. See Gyp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gipoun', 'n.', 'A short cassock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gipser', 'n.', 'Alt. of Gipsire');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gipsire', 'n.', 'A kind of pouch formerly worn at the girdle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gipsy', 'n. a.', 'See Gypsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gipsyism', 'n.', 'See Gypsyism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Giraffe', 'n.', 'An African ruminant (Camelopardalis giraffa) related to\n   the deers and antelopes, but placed in a family by itself; the\n   camelopard. It is the tallest of animals, being sometimes twenty feet\n   from the hoofs to the top of the head. Its neck is very long, and its\n   fore legs are much longer than its hind legs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girandole', 'n.', 'An ornamental branched candlestick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girandole', 'n.', 'A flower stand, fountain, or the like, of branching\n   form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girandole', 'n.', 'A kind of revolving firework.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girandole', 'n.', 'A series of chambers in defensive mines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girasole Girasol', 'n.', 'See Heliotrope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girasole Girasol', 'n.', 'A variety of opal which is usually milk white,\n   bluish white, or sky blue; but in a bright light it reflects a reddish\n   color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gird', 'n.', 'A stroke with a rod or switch; a severe spasm; a twinge; a\n   pang.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gird', 'n.', 'A cut; a sarcastic remark; a gibe; a sneer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gird', 'v.', 'To strike; to smite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gird', 'v.', 'To sneer at; to mock; to gibe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gird', 'v. i.', 'To gibe; to sneer; to break a scornful jest; to utter\n   severe sarcasms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girt', 'imp. & p. p.', 'of Gird');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girded', '', 'of Gird');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girding', 'p. pr. & vb. n.', 'of Gird');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gird', 'v. t.', 'To encircle or bind with any flexible band.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gird', 'v. t.', 'To make fast, as clothing, by binding with a cord, girdle,\n   bandage, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gird', 'v. t.', 'To surround; to encircle, or encompass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gird', 'v. t.', 'To clothe; to swathe; to invest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gird', 'v. t.', 'To prepare; to make ready; to equip; as, to gird ones\n   self for a contest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girder', 'n.', 'One who girds; a satirist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girder', 'n.', 'One who, or that which, girds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girder', 'n.', 'A main beam; a stright, horizontal beam to span an opening\n   or carry weight, such as ends of floor beams, etc.; hence, a framed or\n   built-up member discharging the same office, technically called a\n   compound girder. See Illusts. of Frame, and Doubleframed floor, under\n   Double.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girding', 'n.', 'That with which one is girded; a girdle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girdle', 'n.', 'A griddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girdle', 'n.', 'That which girds, encircles, or incloses; a circumference;\n   a belt; esp., a belt, sash, or article of dress encircling the body\n   usually at the waist; a cestus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girdle', 'n.', 'The zodiac; also, the equator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girdle', 'n.', 'The line ofgreatest circumference of a brilliant-cut\n   diamond, at which it is grasped by the setting. See Illust. of\n   Brilliant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girdle', 'n.', 'A thin bed or stratum of stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girdle', 'n.', 'The clitellus of an earthworm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girdled', 'imp. & p. p.', 'of Girdle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girdling', 'p. pr. & vb. n.', 'of Girdle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girdle', 'v. t.', 'To bind with a belt or sash; to gird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girdle', 'v. t.', 'To inclose; to environ; to shut in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girdle', 'v. t.', 'To make a cut or gnaw a groove around (a tree, etc.)\n   through the bark and alburnum, thus killing it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girdler', 'n.', 'One who girdles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girdler', 'n.', 'A maker of girdles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girdler', 'n.', 'An American longicorn beetle (Oncideres cingulatus) which\n   lays its eggs in the twigs of the hickory, and then girdles each branch\n   by gnawing a groove around it, thus killing it to provide suitable food\n   for the larvae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girdlestead', 'n.', 'That part of the body where the girdle is worn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girdlestead', 'n.', 'The lap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gire', 'n.', 'See Gyre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girkin', 'n.', 'See Gherkin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girl', 'n.', 'A young person of either sex; a child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girl', 'n.', 'A female child, from birth to the age of puberty; a young\n   maiden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girl', 'n.', 'A female servant; a maidservant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girl', 'n.', 'A roebuck two years old.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girlhood', 'n.', 'State or time of being a girl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girlish', 'a.', 'Like, or characteristic of, a girl; of or pertaining to\n   girlhood; innocent; artless; immature; weak; as, girlish ways; girlish\n   grief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girlond', 'n.', 'A garland; a prize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girn', 'n.', 'To grin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girondist', 'n.', 'A member of the moderate republican party formed in the\n   French legislative assembly in 1791. The Girondists were so called\n   because their leaders were deputies from the department of La Gironde.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girondist', 'a.', 'Of or pertaining to the Girondists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girrock', 'n.', 'A garfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girt', '', 'imp. & p. p. of Gird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girted', 'imp. & p. p.', 'of Girt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girting', 'p. pr. & vb. n.', 'of Girt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girt', 'v.', 'To gird; to encircle; to invest by means of a girdle; to\n   measure the girth of; as, to girt a tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girt', 'a.', 'Bound by a cable; -- used of a vessel so moored by two\n   anchors that she swings against one of the cables by force of the\n   current or tide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girt', 'n.', 'Same as Girth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girth', 'n.', 'A band or strap which encircles the body; especially, one by\n   which a saddle is fastened upon the back of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girth', 'n.', 'The measure round the body, as at the waist or belly; the\n   circumference of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girth', 'n.', 'A small horizontal brace or girder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girth', 'v. t.', 'To bind as with a girth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Girtline', 'n.', 'A gantline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gisarm', 'n.', 'A weapon with a scythe-shaped blade, and a separate long\n   sharp point, mounted on a long staff and carried by foot soldiers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gise', 'v. t.', 'To feed or pasture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gise', 'n.', 'Guise; manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gisle', 'n.', 'A pledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gismondine', 'n.', 'Alt. of Gismondite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gismondite', 'n.', 'A native hydrated silicate of alumina, lime, and\n   potash, first noticed near Rome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gist', 'n.', 'A resting place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gist', 'n.', 'The main point, as of a question; the point on which an\n   action rests; the pith of a matter; as, the gist of a question.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Git', 'n.', 'See Geat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gite', 'n.', 'A gown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gith', 'n.', 'The corn cockle; also anciently applied to the Nigella, or\n   fennel flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gittern', 'n.', 'An instrument like a guitar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gittern', 'v. i.', 'To play on gittern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gittith', 'n.', 'A musical instrument, of unknown character, supposed by\n   some to have been used by the people of Gath, and thence obtained by\n   David. It is mentioned in the title of Psalms viii., lxxxi., and\n   lxxxiv.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guist', 'n.', 'Same as Joust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Giusto', 'a.', 'In just, correct, or suitable time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gave', 'imp.', 'of Give');
  INSERT INTO words (word, word_type, description) VALUES  (    'Given', 'p. p.', 'of Give');
  INSERT INTO words (word, word_type, description) VALUES  (    'Giving', 'p. pr. & vb. n.', 'of Give');
  INSERT INTO words (word, word_type, description) VALUES  (    'Give', 'n.', 'To bestow without receiving a return; to confer without\n   compensation; to impart, as a possession; to grant, as authority or\n   permission; to yield up or allow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Give', 'n.', 'To yield possesion of; to deliver over, as property, in\n   exchange for something; to pay; as, we give the value of what we buy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Give', 'n.', 'To yield; to furnish; to produce; to emit; as, flint and\n   steel give sparks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Give', 'n.', 'To communicate or announce, as advice, tidings, etc.; to\n   pronounce; to render or utter, as an opinion, a judgment, a sentence, a\n   shout, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Give', 'n.', 'To grant power or license to; to permit; to allow; to\n   license; to commission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Give', 'n.', 'To exhibit as a product or result; to produce; to show; as,\n   the number of men, divided by the number of ships, gives four hundred\n   to each ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Give', 'n.', 'To devote; to apply; used reflexively, to devote or apply\n   ones self; as, the soldiers give themselves to plunder; also in this\n   sense used very frequently in the past participle; as, the people are\n   given to luxury and pleasure; the youth is given to study.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Give', 'n.', 'To set forth as a known quantity or a known relation, or as a\n   premise from which to reason; -- used principally in the passive form\n   given.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Give', 'n.', 'To allow or admit by way of supposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Give', 'n.', 'To attribute; to assign; to adjudge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Give', 'n.', 'To excite or cause to exist, as a sensation; as, to give\n   offense; to give pleasure or pain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Give', 'n.', 'To pledge; as, to give ones word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Give', 'n.', 'To cause; to make; -- with the infinitive; as, to give one to\n   understand, to know, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Give', 'v. i.', 'To give a gift or gifts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Give', 'v. i.', 'To yield to force or pressure; to relax; to become less\n   rigid; as, the earth gives under the feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Give', 'v. i.', 'To become soft or moist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Give', 'v. i.', 'To move; to recede.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Give', 'v. i.', 'To shed tears; to weep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Give', 'v. i.', 'To have a misgiving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Give', 'v. i.', 'To open; to lead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Given', '', 'p. p. & a. from Give, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Given', 'v.', 'Granted; assumed; supposed to be known; set forth as a known\n   quantity, relation, or premise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Given', 'v.', 'Disposed; inclined; -- used with an adv.; as, virtuously\n   given.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Given', 'adv.', 'Stated; fixed; as, in a given time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Giver', 'n.', 'One who gives; a donor; a bestower; a grantor; one who\n   imparts or distributes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gives', 'n.', 'Fetters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Giving', 'n.', 'The act of bestowing as a gift; a conferring or imparting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Giving', 'n.', 'A gift; a benefaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Giving', 'n.', 'The act of softening, breaking, or yielding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gizzard', 'n.', 'The second, or true, muscular stomach of birds, in which\n   the food is crushed and ground, after being softened in the glandular\n   stomach (crop), or lower part of the esophagus; the gigerium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gizzard', 'n.', 'A thick muscular stomach found in many invertebrate\n   animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gizzard', 'n.', 'A stomach armed with chitinous or shelly plates or teeth,\n   as in certain insects and mollusks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glabell/', 'pl. ', 'of Glabella');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glabella', 'n.', 'The space between the eyebrows, also including the\n   corresponding part of the frontal bone; the mesophryon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glabella', 'pl. ', 'of Glabellum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glabellum', 'n.', 'The median, convex lobe of the head of a trilobite. See\n   Trilobite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glabrate', 'a.', 'Becoming smooth or glabrous from age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glabreate', 'v. t.', 'Alt. of Glabriate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glabriate', 'v. t.', 'To make smooth, plain, or bare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glabrity', 'n.', 'Smoothness; baldness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glabrous', 'a.', 'Smooth; having a surface without hairs or any unevenness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glacial', 'a.', 'Pertaining to ice or to its action; consisting of ice;\n   frozen; icy; esp., pertaining to glaciers; as, glacial phenomena.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glacial', 'a.', 'Resembling ice; having the appearance and consistency of\n   ice; -- said of certain solid compounds; as, glacial phosphoric or\n   acetic acids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glacialist', 'n.', 'One who attributes the phenomena of the drift, in\n   geology, to glaciers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaciate', 'v. i.', 'To turn to ice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaciate', 'v. t.', 'To convert into, or cover with, ice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaciate', 'v. t.', 'To produce glacial effects upon, as in the scoring of\n   rocks, transportation of loose material, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaciation', 'n.', 'Act of freezing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaciation', 'n.', 'That which is formed by freezing; ice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaciation', 'n.', 'The process of glaciating, or the state of being\n   glaciated; the production of glacial phenomena.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glacier', 'n.', 'An immense field or stream of ice, formed in the region of\n   perpetual snow, and moving slowly down a mountain slope or valley, as\n   in the Alps, or over an extended area, as in Greenland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glacious', 'a.', 'Pertaining to, consisting of or resembling, ice; icy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glacis', 'n.', 'A gentle slope, or a smooth, gently sloping bank;\n   especially (Fort.), that slope of earth which inclines from the covered\n   way toward the exterior ground or country (see Illust. of Ravelin).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glad', 'superl.', 'Pleased; joyous; happy; cheerful; gratified; -- opposed\n   to sorry, sorrowful, or unhappy; -- said of persons, and often followed\n   by of, at, that, or by the infinitive, and sometimes by with,\n   introducing the cause or reason.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glad', 'superl.', 'Wearing a gay or bright appearance; expressing or\n   exciting joy; producing gladness; exhilarating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gladded', 'imp. & p. p.', 'of Glad');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gladding', 'p. pr. & vb. n.', 'of Glad');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glad', 'v. t.', 'To make glad; to cheer; to gladden; to exhilarate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glad', 'v. i.', 'To be glad; to rejoice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gladdened', 'imp. & p. p.', 'of Gladden');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gladdening', 'p. pr. & vb. n.', 'of Gladden');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gladden', 'v. t.', 'To make glad; to cheer; to please; to gratify; to\n   rejoice; to exhilarate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gladden', 'v. i.', 'To be or become glad; to rejoice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gladder', 'n.', 'One who makes glad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glade', 'n.', 'An open passage through a wood; a grassy open or cleared\n   space in a forest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glade', 'n.', 'An everglade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glade', 'n.', 'An opening in the ice of rivers or lakes, or a place left\n   unfrozen; also, smooth ice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gladen', 'n.', 'Sword grass; any plant with sword-shaped leaves, esp. the\n   European Iris foetidissima.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gladeye', 'n.', 'The European yellow-hammer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gladful', 'a.', 'Full of gladness; joyful; glad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gladiate', 'a.', 'Sword-shaped; resembling a sword in form, as the leaf of\n   the iris, or of the gladiolus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gladiator', 'n.', 'Originally, a swordplayer; hence, one who fought with\n   weapons in public, either on the occasion of a funeral ceremony, or in\n   the arena, for public amusement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gladiator', 'n.', 'One who engages in any fierce combat or controversy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gladiatorial', 'a.', 'Alt. of Gladiatorian');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gladiatorian', 'a.', 'Of or pertaining to gladiators, or to contests or\n   combatants in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gladiatorism', 'n.', 'The art or practice of a gladiator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gladiatorship', 'n.', 'Conduct, state, or art, of a gladiator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gladiatory', 'a.', 'Gladiatorial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gladiature', 'n.', 'Swordplay; fencing; gladiatorial contest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gladiole', 'n.', 'A lilylike plant, of the genus Gladiolus; -- called also\n   corn flag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gladioli', 'pl. ', 'of Gladiolus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gladioluses', 'pl. ', 'of Gladiolus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gladiolus', 'n.', 'A genus of plants having bulbous roots and gladiate\n   leaves, and including many species, some of which are cultivated and\n   valued for the beauty of their flowers; the corn flag; the sword lily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gladiolus', 'n.', 'The middle portion of the sternum in some animals; the\n   mesosternum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gladii', 'pl. ', 'of Gladius');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gladius', 'n.', 'The internal shell, or pen, of cephalopods like the\n   squids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gladly', 'a.', 'Preferably; by choice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gladly', 'a.', 'With pleasure; joyfully; cheerfully; eagerly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gladness', 'n.', 'State or quality of being glad; pleasure; joyful\n   satisfaction; cheerfulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gladship', 'n.', 'A state of gladness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gladsome', 'a.', 'Pleased; joyful; cheerful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gladsome', 'a.', 'Causing joy, pleasure, or cheerfulness; having the\n   appearance of gayety; pleasing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gladstone', 'n.', 'A four-wheeled pleasure carriage with two inside seats,\n   calash top, and seats for driver and footman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gladwyn', 'n.', 'See Gladen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glair', 'a.', 'The white of egg. It is used as a size or a glaze in\n   bookbinding, for pastry, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glair', 'a.', 'Any viscous, transparent substance, resembling the white of\n   an egg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glair', 'a.', 'A broadsword fixed on a pike; a kind of halberd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaired', 'imp. & p. p.', 'of Glair');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glairing', 'p. pr. & vb. n.', 'of Glair');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glair', 'v. t.', 'To smear with the white of an egg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaire', 'n.', 'See Glair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaireous', 'a.', 'Glairy; covered with glair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glairin', 'n.', 'A glairy viscous substance, which forms on the surface of\n   certain mineral waters, or covers the sides of their inclosures; --\n   called also baregin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glairy', 'a.', 'Like glair, or partaking of its qualities; covered with\n   glair; viscous and transparent; slimy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaive', 'n.', 'A weapon formerly used, consisting of a large blade fixed\n   on the end of a pole, whose edge was on the outside curve; also, a\n   light lance with a long sharp-pointed head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaive', 'n.', 'A sword; -- used poetically and loosely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glama', 'n.', 'A copious gummy secretion of the humor of the eyelids, in\n   consequence of some disorder; blearedness; lippitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glamour', 'n.', 'A charm affecting the eye, making objects appear different\n   from what they really are.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glamour', 'n.', 'Witchcraft; magic; a spell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glamour', 'n.', 'A kind of haze in the air, causing things to appear\n   different from what they really are.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glamour', 'n.', 'Any artificial interest in, or association with, an\n   object, through which it appears delusively magnified or glorified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glamourie', 'n.', 'Glamour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glance', 'n.', 'A sudden flash of light or splendor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glance', 'n.', 'A quick cast of the eyes; a quick or a casual look; a swift\n   survey; a glimpse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glance', 'n.', 'An incidental or passing thought or allusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glance', 'n.', 'A name given to some sulphides, mostly dark-colored, which\n   have a brilliant metallic luster, as the sulphide of copper, called\n   copper glance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glanced', 'imp. & p. p.', 'of Glance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glancing', 'p. pr. & vb. n.', 'of Glance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glance', 'v. i.', 'To shoot or emit a flash of light; to shine; to flash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glance', 'v. i.', 'To strike and fly off in an oblique direction; to dart\n   aside. \"Your arrow hath glanced\".');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glance', 'v. i.', 'To look with a sudden, rapid cast of the eye; to snatch\n   a momentary or hasty view.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glance', 'v. i.', 'To make an incidental or passing reflection; to allude;\n   to hint; -- often with at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glance', 'v. i.', 'To move quickly, appearing and disappearing rapidly; to\n   be visible only for an instant at a time; to move interruptedly; to\n   twinkle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glance', 'v. t.', 'To shoot or dart suddenly or obliquely; to cast for a\n   moment; as, to glance the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glance', 'v. t.', 'To hint at; to touch lightly or briefly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glancing', 'a.', 'Shooting, as light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glancing', 'a.', 'Flying off (after striking) in an oblique direction; as,\n   a glancing shot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glancingly', 'adv.', 'In a glancing manner; transiently; incidentally;\n   indirectly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gland', 'n.', 'An organ for secreting something to be used in, or\n   eliminated from, the body; as, the sebaceous glands of the skin; the\n   salivary glands of the mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gland', 'n.', 'An organ or part which resembles a secreting, or true,\n   gland, as the ductless, lymphatic, pineal, and pituitary glands, the\n   functions of which are very imperfectly known.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gland', 'n.', 'A special organ of plants, usually minute and globular,\n   which often secretes some kind of resinous, gummy, or aromatic product.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gland', 'n.', 'Any very small prominence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gland', 'n.', 'The movable part of a stuffing box by which the packing is\n   compressed; -- sometimes called a follower. See Illust. of Stuffing\n   box, under Stuffing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gland', 'n.', 'The crosspiece of a bayonet clutch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glandage', 'n.', 'A feeding on nuts or mast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glandered', 'a.', 'Affected with glanders; as, a glandered horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glanderous', 'a.', 'Of or pertaining to glanders; of the nature of\n   glanders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glanders', 'n.', 'A highly contagious and very destructive disease of\n   horses, asses, mules, etc., characterized by a constant discharge of\n   sticky matter from the nose, and an enlargement and induration of the\n   glands beneath and within the lower jaw. It may transmitted to dogs,\n   goats, sheep, and to human beings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glandiferous', 'a.', 'Bearing acorns or other nuts; as, glandiferous trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glandiform', 'a.', 'Having the form of a gland or nut; resembling a gland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glandular', 'a.', 'Containing or supporting glands; consisting of glands;\n   pertaining to glands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glandulation', 'n.', 'The situation and structure of the secretory vessels\n   in plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glandule', 'n.', 'A small gland or secreting vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glanduliferous', 'a.', 'Bearing glandules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glandulose', 'a.', 'Same as Glandulous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glandulosity', 'n.', 'Quality of being glandulous; a collection of glands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glandulous', 'a.', 'Containing glands; consisting of glands; pertaining to\n   glands; resembling glands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glandes', 'pl. ', 'of Glans');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glans', 'n.', 'The vascular body which forms the apex of the penis, and the\n   extremity of the clitoris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glans', 'n.', 'The acorn or mast of the oak and similar fruits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glans', 'n.', 'Goiter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glans', 'n.', 'A pessary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glared', 'imp. & p. p.', 'of Glare');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaring', 'p. pr. & vb. n.', 'of Glare');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glare', 'v. i.', 'To shine with a bright, dazzling light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glare', 'v. i.', 'To look with fierce, piercing eyes; to stare earnestly,\n   angrily, or fiercely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glare', 'v. i.', 'To be bright and intense, as certain colors; to be\n   ostentatiously splendid or gay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glare', 'v. t.', 'To shoot out, or emit, as a dazzling light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glare', 'n.', 'A bright, dazzling light; splendor that dazzles the eyes; a\n   confusing and bewildering light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glare', 'n.', 'A fierce, piercing look or stare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glare', 'n.', 'A viscous, transparent substance. See Glair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glare', 'n.', 'A smooth, bright, glassy surface; as, a glare of ice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glare', 'n.', 'Smooth and bright or translucent; -- used almost exclusively\n   of ice; as, skating on glare ice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glareous', 'a.', 'Glairy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glariness', 'n.', 'Alt. of Glaringness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaringness', 'n.', 'A dazzling luster or brilliancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaring', 'a.', 'Clear; notorious; open and bold; barefaced; as, a glaring\n   crime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glary', 'a.', 'Of a dazzling luster; glaring; bright; shining; smooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glass', 'v. t.', 'A hard, brittle, translucent, and commonly transparent\n   substance, white or colored, having a conchoidal fracture, and made by\n   fusing together sand or silica with lime, potash, soda, or lead oxide.\n   It is used for window panes and mirrors, for articles of table and\n   culinary use, for lenses, and various articles of ornament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glass', 'v. t.', 'Any substance having a peculiar glassy appearance, and a\n   conchoidal fracture, and usually produced by fusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glass', 'v. t.', 'Anything made of glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glass', 'v. t.', 'A looking-glass; a mirror.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glass', 'v. t.', 'A vessel filled with running sand for measuring time; an\n   hourglass; and hence, the time in which such a vessel is exhausted of\n   its sand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glass', 'v. t.', 'A drinking vessel; a tumbler; a goblet; hence, the\n   contents of such a vessel; especially; spirituous liquors; as, he took\n   a glass at dinner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glass', 'v. t.', 'An optical glass; a lens; a spyglass; -- in the plural,\n   spectacles; as, a pair of glasses; he wears glasses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glass', 'v. t.', 'A weatherglass; a barometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glassed', 'imp. & p. p.', 'of Glass');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glassing', 'p. pr. & vb. n.', 'of Glass');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glass', 'v. t.', 'To reflect, as in a mirror; to mirror; -- used\n   reflexively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glass', 'v. t.', 'To case in glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glass', 'v. t.', 'To cover or furnish with glass; to glaze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glass', 'v. t.', 'To smooth or polish anything, as leater, by rubbing it\n   with a glass burnisher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glass-crab', 'n.', 'The larval state (Phyllosoma) of the genus Palinurus\n   and allied genera. It is remarkable for its strange outlines, thinness,\n   and transparency. See Phyllosoma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glassen', 'a.', 'Glassy; glazed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glasseye', 'n.', 'A fish of the great lakes; the wall-eyed pike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glasseye', 'n.', 'A species of blindness in horses in which the eye is\n   bright and the pupil dilated; a sort of amaurosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glass-faced', 'a.', 'Mirror-faced; reflecting the sentiments of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glassfuls', 'pl. ', 'of Glassful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glassful', 'n.', 'The contents of a glass; as much of anything as a glass\n   will hold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glassful', 'a.', 'Glassy; shining like glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glass-gazing', 'a.', 'Given to viewing ones self in a glass or mirror;\n   finical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glasshouse', 'n.', 'A house where glass is made; a commercial house that\n   deals in glassware.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glassily', 'adv.', 'So as to resemble glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glassiness', 'n.', 'The quality of being glassy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glassite', 'n.', 'A member of a Scottish sect, founded in the 18th century\n   by John Glass, a minister of the Established Church of Scotland, who\n   taught that justifying faith is \"no more than a simple assent to the\n   divine testimone passively recived by the understanding.\" The English\n   and American adherents of this faith are called Sandemanians, after\n   Robert Sandeman, the son-in-law and disciple of Glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glass maker', 'n.', 'Alt. of Glassmaker');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glassmaker', 'n.', 'One who makes, or manufactures, glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glass-rope', 'n.', 'A remarkable vitreous sponge, of the genus Hyalonema,\n   first brought from Japan. It has a long stem, consisting of a bundle of\n   long and large, glassy, siliceous fibers, twisted together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glass-snail', 'n.', 'A small, transparent, land snail, of the genus\n   Vitrina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glass-snake', 'n.', 'A long, footless lizard (Ophiosaurus ventralis), of\n   the Southern United States; -- so called from its fragility, the tail\n   easily breaking into small pieces. It grows to the length of three\n   feet. The name is applied also to similar species found in the Old\n   World.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glass-sponge', 'n.', 'A siliceous sponge, of the genus Hyalonema, and\n   allied genera; -- so called from their glassy fibers or spicules; --\n   called also vitreous sponge. See Glass-rope, and Euplectella.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glassware', 'n.', 'Ware, or articles collectively, made of glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glasswork', 'n.', 'Manufacture of glass; articles or ornamentation made of\n   glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glasswort', 'n.', 'A seashore plant of the Spinach family (Salicornia\n   herbacea), with succulent jointed stems; also, a prickly plant of the\n   same family (Salsola Kali), both formerly burned for the sake of the\n   ashes, which yield soda for making glass and soap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glassy', 'a.', 'Made of glass; vitreous; as, a glassy substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glassy', 'a.', 'Resembling glass in its properties, as in smoothness,\n   brittleness, or transparency; as, a glassy stream; a glassy surface;\n   the glassy deep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glassy', 'a.', 'Dull; wanting life or fire; lackluster; -- said of the\n   eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glasstonbury thorn', '', 'A variety of the common hawthorn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glasynge', 'n.', 'Glazing or glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glauberite', 'n.', 'A mineral, consisting of the sulphates of soda and\n   lime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaubers salt', '', 'Alt. of Glaubers salts');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaubers salts', '', 'Sulphate of soda, a well-known cathartic. It is a\n   white crystalline substance, with a cooling, slightly bitter taste, and\n   is commonly called \"salts.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaucescent', 'a.', 'Having a somewhat glaucous appearance or nature;\n   becoming glaucous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaucic', 'a.', 'Of or pertaining to the Glaucium or horned poppy; --\n   formerly applied to an acid derived from it, now known to be fumaric\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaucine', 'a.', 'Glaucous or glaucescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaucine', 'n.', 'An alkaloid obtained from the plant Glaucium, as a\n   bitter, white, crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaucodot', 'n.', 'A metallic mineral having a grayish tin-white color, and\n   containing cobalt and iron, with sulphur and arsenic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaucoma', 'n.', 'Dimness or abolition of sight, with a diminution of\n   transparency, a bluish or greenish tinge of the refracting media of the\n   eye, and a hard inelastic condition of the eyeball, with marked\n   increase of tension within the eyeball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaucomatous', 'a.', 'Having the nature of glaucoma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaucometer', 'n.', 'See Gleucometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glauconite', 'n.', 'The green mineral characteristic of the greensand of\n   the chalk and other formations. It is a hydrous silicate of iron and\n   potash. See Greensand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaucophane', 'n.', 'A mineral of a dark bluish color, related to\n   amphibole. It is characteristic of certain crystalline rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaucosis', 'n.', 'Same as Glaucoma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaucous', 'a.', 'Of a sea-green color; of a dull green passing into\n   grayish blue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaucous', 'a.', 'Covered with a fine bloom or fine white powder easily\n   rubbed off, as that on a blue plum, or on a cabbage leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaucus', 'n.', 'A genus of nudibranchiate mollusks, found in the warmer\n   latitudes, swimming in the open sea. These mollusks are beautifully\n   colored with blue and silvery white.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaum', 'v. i.', 'To grope with the hands, as in the dark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glave', 'n.', 'See Glaive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaver', 'v. i.', 'To prate; to jabber; to babble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaver', 'v. i.', 'To flatter; to wheedle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaverer', 'n.', 'A flatterer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaymore', 'n.', 'A claymore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glased', 'imp. & p. p.', 'of Glase');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glazing', 'p. pr. & vb. n.', 'of Glase');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glase', 'v. t.', 'To furnish (a window, a house, a sash, a ease, etc.) with\n   glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glase', 'v. t.', 'To incrust, cover, or overlay with a thin surface,\n   consisting of, or resembling, glass; as, to glaze earthenware; hence,\n   to render smooth, glasslike, or glossy; as, to glaze paper, gunpowder,\n   and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glase', 'v. t.', 'To apply thinly a transparent or semitransparent color to\n   (another color), to modify the effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaze', 'v. i.', 'To become glazed of glassy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaze', 'n.', 'The vitreous coating of pottery or porcelain; anything used\n   as a coating or color in glazing. See Glaze, v. t., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaze', 'v. t.', 'Broth reduced by boiling to a gelatinous paste, and\n   spread thinly over braised dishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glaze', 'v. t.', 'A glazing oven. See Glost oven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glazen', 'a.', 'Resembling glass; glasslike; glazed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glazer', 'n.', 'One who applies glazing, as in pottery manufacture, etc.;\n   one who gives a glasslike or glossy surface to anything; a calenderer\n   or smoother of cloth, paper, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glazer', 'n.', 'A tool or machine used in glazing, polishing, smoothing,\n   etc.; amoung cutlers and lapidaries, a wooden wheel covered with emery,\n   or having a band of lead and tin alloy, for polishing cutlery, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glazier', 'n.', 'One whose business is to set glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glazing', 'n.', 'The act or art of setting glass; the art of covering with\n   a vitreous or glasslike substance, or of polishing or rendering glossy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glazing', 'n.', 'The glass set, or to be set, in a sash, frame. etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glazing', 'n.', 'The glass, glasslike, or glossy substance with which any\n   surface is incrusted or overlaid; as, the glazing of pottery or\n   porcelain, or of paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glazing', 'n.', 'Transparent, or semitransparent, colors passed thinly over\n   other colors, to modify the effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glazy', 'a.', 'Having a glazed appearance; -- said of the fractured surface\n   of some kinds of pin iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glead', 'n.', 'A live coal. See Gleed.');
