  INSERT INTO words (word, word_type, description) VALUES  (    'Gleam', 'n.', 'A shoot of light; a small stream of light; a beam; a ray; a\n   glimpse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gleam', 'n.', 'Brightness; splendor.');
