  INSERT INTO words (word, word_type, description) VALUES  (    'Gleaming', 'p. pr. & vb. n.', 'of Gleam');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gleam', 'v. t.', 'To shoot, or dart, as rays of light; as, at the dawn,\n   light gleams in the east.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gleam', 'v. t.', 'To shine; to cast light; to glitter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gleam', 'v. t.', 'To shoot out (flashes of light, etc.).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gleamy', 'a.', 'Darting beams of light; casting light in rays; flashing;\n   coruscating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gleaned', 'imp. & p. p.', 'of Glean');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gleaning', 'p. pr. & vb. n.', 'of Glean');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glean', 'v. t.', 'To gather after a reaper; to collect in scattered or\n   fragmentary parcels, as the grain left by a reaper, or grapes left\n   after the gathering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glean', 'v. t.', 'To gather from (a field or vineyard) what is left.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glean', 'v. t.', 'To collect with patient and minute labor; to pick out; to\n   obtain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glean', 'v. i.', 'To gather stalks or ears of grain left by reapers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glean', 'v. i.', 'To pick up or gather anything by degrees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glean', 'n.', 'A collection made by gleaning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glean', 'n.', 'Cleaning; afterbirth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gleaner', 'n.', 'One who gathers after reapers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gleaner', 'n.', 'One who gathers slowly with labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gleaning', 'n.', 'The act of gathering after reapers; that which is\n   collected by gleaning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glebe', 'n.', 'A lump; a clod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glebe', 'n.', 'Turf; soil; ground; sod.');
