  INSERT INTO words (word, word_type, description) VALUES  (    'Glebosity', 'n.', 'The quality of being glebous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glebous', 'a.', 'Alt. of Gleby');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gleby', 'a.', 'Pertaining to the glebe; turfy; cloddy; fertile; fruitful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glede', 'v. i.', 'The common European kite (Milvus ictinus). This name is\n   also sometimes applied to the buzzard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glede', 'n.', 'A live coal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glee', 'n.', 'Music; minstrelsy; entertainment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glee', 'n.', 'Joy; merriment; mirth; gayety; paricularly, the mirth enjoyed\n   at a feast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glee', 'n.', 'An unaccompanied part song for three or more solo voices. It\n   is not necessarily gleesome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gleed', 'v. i.', 'A live or glowing coal; a glede.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gleeful', 'a.', 'Merry; gay; joyous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gleek', 'n.', 'A jest or scoff; a trick or deception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gleek', 'n.', 'An enticing look or glance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gleek', 'v. i.', 'To make sport; to gibe; to sneer; to spend time idly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gleek', 'n.', 'A game at cards, once popular, played by three persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gleek', 'n.', 'Three of the same cards held in the same hand; -- hence,\n   three of anything.');
