  INSERT INTO words (word, word_type, description) VALUES  (    'Gleeman', 'n.', 'A name anciently given to an itinerant minstrel or\n   musician.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gleen', 'v. i.', 'To glisten; to gleam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gleesome', 'a.', 'Merry; joyous; gleeful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gleet', 'n.', 'A transparent mucous discharge from the membrane of the\n   urethra, commonly an effect of gonorrhea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gleet', 'v. i.', 'To flow in a thin, limpid humor; to ooze, as gleet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gleet', 'v. i.', 'To flow slowly, as water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gleety', 'a.', 'Ichorous; thin; limpid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gleg', 'a.', 'Quick of perception; alert; sharp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gleire', 'n.', 'Alt. of Gleyre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gleyre', 'n.', 'See Glair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glen', 'n.', 'A secluded and narrow valley; a dale; a depression between\n   hills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glenlivat', 'n.', 'Alt. of Glenlivet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glenlivet', 'n.', 'A kind of Scotch whisky, named from the district in\n   which it was first made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glenoid', 'a.', 'Having the form of a smooth and shallow depression;\n   socketlike; -- applied to several articular surfaces of bone; as, the\n   glenoid cavity, or fossa, of the scapula, in which the head of the\n   humerus articulates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glenoidal', 'a.', 'Glenoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glent', 'n. & v.', 'See Glint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gleucometer', 'n.', 'An instrument for measuring the specific gravity and\n   ascertaining the quantity of sugar contained in must.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glew', 'n.', 'See Glue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gley', 'v. i.', 'To squint; to look obliquely; to overlook things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gley', 'adv.', 'Asquint; askance; obliquely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gliadin', 'n.', 'Vegetable glue or gelatin; glutin. It is one of the\n   constituents of wheat gluten, and is a tough, amorphous substance,\n   which resembles animal glue or gelatin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glib', 'superl.', 'Smooth; slippery; as, ice is glib.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glib', 'superl.', 'Speaking or spoken smoothly and with flippant rapidity;\n   fluent; voluble; as, a glib tongue; a glib speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glib', 'v. t.', 'To make glib.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glib', 'n.', 'A thick lock of hair, hanging over the eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glib', 'v. t.', 'To castrate; to geld; to emasculate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gilbbery', 'a.', 'Slippery; changeable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gilbbery', 'a.', 'Moving easily; nimble; voluble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glibly', 'adv.', 'In a glib manner; as, to speak glibly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glibness', 'n.', 'The quality of being glib.');
