  INSERT INTO words (word, word_type, description) VALUES  (    'Gliding', 'p. pr. & vb. n.', 'of Glide');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glide', 'v. i.', 'To move gently and smoothly; to pass along without noise,\n   violence, or apparent effort; to pass rapidly and easily, or with a\n   smooth, silent motion, as a river in its channel, a bird in the air, a\n   skater over ice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glide', 'v. i.', 'To pass with a glide, as the voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glide', 'n.', 'The act or manner of moving smoothly, swiftly, and without\n   labor or obstruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glide', 'n.', 'A transitional sound in speech which is produced by the\n   changing of the mouth organs from one definite position to another, and\n   with gradual change in the most frequent cases; as in passing from the\n   begining to the end of a regular diphthong, or from vowel to consonant\n   or consonant to vowel in a syllable, or from one component to the other\n   of a double or diphthongal consonant (see Guide to Pronunciation, //\n   19, 161, 162). Also (by Bell and others), the vanish (or brief final\n   element) or the brief initial element, in a class of diphthongal\n   vowels, or the brief final or initial part of some consonants (see\n   Guide to Pronunciation, // 18, 97, 191).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gliden', '', 'p. p. of Glide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glider', 'n.', 'One who, or that which, glides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glidingly', 'adv.', 'In a gliding manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gliff', 'n.', 'A transient glance; an unexpected view of something that\n   startles one; a sudden fear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gliff', 'n.', 'A moment: as, for a gliff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glike', 'n.', 'A sneer; a flout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glim', 'n.', 'Brightness; splendor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glim', 'n.', 'A light or candle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glimmered', 'imp. & p. p.', 'of Glimmer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glimmering', 'p. pr. & vb. n.', 'of Glimmer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glimmer', 'v. i.', 'To give feeble or scattered rays of light; to shine\n   faintly; to show a faint, unsteady light; as, the glimmering dawn; a\n   glimmering lamp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glimmer', 'n.', 'A faint, unsteady light; feeble, scattered rays of light;\n   also, a gleam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glimmer', 'n.', 'Mica. See Mica.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glimmering', 'n.', 'Faint, unsteady light; a glimmer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glimmering', 'n.', 'A faint view or idea; a glimpse; an inkling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glimpse', 'n.', 'A sudden flash; transient luster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glimpse', 'n.', 'A short, hurried view; a transitory or fragmentary\n   perception; a quick sight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glimpse', 'n.', 'A faint idea; an inkling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glimpsed', 'imp. & p. p.', 'of Glimpse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glimpsing', 'p. pr. & vb. n.', 'of Glimpse');
