  INSERT INTO words (word, word_type, description) VALUES  (    'Glimpse', 'v. t.', 'To catch a glimpse of; to see by glimpses; to have a\n   short or hurried view of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glint', 'n.', 'A glimpse, glance, or gleam.');
