  INSERT INTO words (word, word_type, description) VALUES  (    'Glinting', 'p. pr. & vb. n.', 'of Glint');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glint', 'v. i.', 'To glance; to peep forth, as a flower from the bud; to\n   glitter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glint', 'v. t.', 'To glance; to turn; as, to glint the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glioma', 'n.', 'A tumor springing from the neuroglia or connective tissue\n   of the brain, spinal cord, or other portions of the nervous system.');
