  INSERT INTO words (word, word_type, description) VALUES  (    'Glistered', 'imp. & p. p.', 'of Glister');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glistering', 'p. pr. & vb. n.', 'of Glister');
