  INSERT INTO words (word, word_type, description) VALUES  (    'Glisteringly', 'adv.', 'In a glistering manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glittered', 'imp. & p. p.', 'of Glitter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glittering', 'p. pr. & vb. n.', 'of Glitter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glitter', 'v. i.', 'To sparkle with light; to shine with a brilliant and\n   broken light or showy luster; to gleam; as, a glittering sword.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glitter', 'v. i.', 'To be showy, specious, or striking, and hence\n   attractive; as, the glittering scenes of a court.');
