  INSERT INTO words (word, word_type, description) VALUES  (    'Gloat', 'v. i.', 'To look steadfastly; to gaze earnestly; -- usually in a\n   bad sense, to gaze with malignant satisfaction, passionate desire,\n   lust, or avarice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Globard', 'n.', 'A glowworm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Globate', 'a.', 'Alt. of Globated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Globated', 'a.', 'Having the form of a globe; spherical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Globe', 'n.', 'A round or spherical body, solid or hollow; a body whose\n   surface is in every part equidistant from the center; a ball; a sphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Globe', 'n.', 'Anything which is nearly spherical or globular in shape; as,\n   the globe of the eye; the globe of a lamp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Globe', 'n.', 'The earth; the terraqueous ball; -- usually preceded by the\n   definite article.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Globe', 'n.', 'A round model of the world; a spherical representation of\n   the earth or heavens; as, a terrestrial or celestial globe; -- called\n   also artificial globe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Globe', 'n.', 'A body of troops, or of men or animals, drawn up in a\n   circle; -- a military formation used by the Romans, answering to the\n   modern infantry square.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Globed', 'imp. & p. p.', 'of Globe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Globing', 'p. pr. & vb. n.', 'of Globe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Globe', 'v. t.', 'To gather or form into a globe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Globefish', 'n.', 'A plectognath fish of the genera Diodon, Tetrodon, and\n   allied genera. The globefishes can suck in water or air and distend the\n   body to a more or less globular form. Called also porcupine fish, and\n   sea hedgehog. See Diodon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Globeflower', 'n.', 'A plant of the genus Trollius (T. Europaeus), found in\n   the mountainous parts of Europe, and producing handsome globe-shaped\n   flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Globeflower', 'n.', 'The American plant Trollius laxus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Globe-shaped', 'a.', 'Shaped like a globe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Globiferous', 'a.', 'Having a round or globular tip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Globigerin/', 'pl. ', 'of Globigerina');
  INSERT INTO words (word, word_type, description) VALUES  (    'Globigerina', 'n.', 'A genus of small Foraminifera, which live abundantly\n   at or near the surface of the sea. Their dead shells, falling to the\n   bottom, make up a large part of the soft mud, generally found in depths\n   below 3,000 feet, and called globigerina ooze. See Illust. of\n   Foraminifera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Globose', 'a.', 'Having a rounded form resembling that of a globe;\n   globular, or nearly so; spherical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Globosely', 'adv.', 'In a globular manner; globularly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Globosity', 'n.', 'Sphericity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Globous', 'a.', 'Spherical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Globular', 'a.', 'Globe-shaped; having the form of a ball or sphere;\n   spherical, or nearly so; as, globular atoms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Globularity', 'n.', 'The state of being globular; globosity; sphericity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Globularly', 'adv.', 'Spherically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Globularness', 'n.', 'Sphericity; globosity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Globule', 'n.', 'A little globe; a small particle of matter, of a spherical\n   form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Globule', 'n.', 'A minute spherical or rounded structure; as blood, lymph,\n   and pus corpuscles, minute fungi, spores, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Globule', 'n.', 'A little pill or pellet used by homeopathists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Globulet', 'n.', 'A little globule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Globuliferous', 'a.', 'Bearing globules; in geology, used of rocks, and\n   denoting a variety of concretionary structure, where the concretions\n   are isolated globules and evenly distributed through the texture of the\n   rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Globulimeter', 'n.', 'An instrument for measuring the number of red blood\n   corpuscles in the blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Globulin', 'n.', 'An albuminous body, insoluble in water, but soluble in\n   dilute solutions of salt. It is present in the red blood corpuscles\n   united with haematin to form haemoglobin. It is also found in the\n   crystalline lens of the eye, and in blood serum, and is sometimes\n   called crystallin. In the plural the word is applied to a group of\n   proteid substances such as vitellin, myosin, fibrinogen, etc., all\n   insoluble in water, but soluble in dilute salt solutions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Globulite', 'n.', 'A rudimentary form of crystallite, spherical in shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Globulous', 'a.', 'Globular; spherical; orbicular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Globy', 'a.', 'Resembling, or pertaining to, a globe; round; orbicular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glochidiate', 'a.', 'Having barbs; as, glochidiate bristles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glochidia', 'pl. ', 'of Glochidium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glochidium', 'n.', 'The larva or young of the mussel, formerly thought to\n   be a parasite upon the parents gills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glode', '', 'imp. of Glide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glombe', 'v. i.', 'Alt. of Glome');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glome', 'v. i.', 'To gloom; to look gloomy, morose, or sullen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glome', 'n.', 'Gloom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glome', 'n.', 'One of the two prominences at the posterior extremity of the\n   frog of the horses foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glomerate', 'a.', 'Gathered together in a roundish mass or dense cluster;\n   conglomerate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glomerated', 'imp. & p. p.', 'of Glomerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glomerating', 'p. pr. & vb. n.', 'of Glomerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glomerate', 'v. t. & i.', 'To gather or wind into a ball; to collect into a\n   spherical form or mass, as threads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glomeration', 'n.', 'The act of forming or gathering into a ball or round\n   mass; the state of being gathered into a ball; conglomeration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glomeration', 'n.', 'That which is formed into a ball; a ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glomerous', 'a.', 'Gathered or formed into a ball or round mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glomerule', 'n.', 'A head or dense cluster of flowers, formed by\n   condensation of a cyme, as in the flowering dogwood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glomerule', 'n.', 'A glomerulus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glomeruli', 'pl. ', 'of Glomerulus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glomerulus', 'n.', 'The bunch of looped capillary blood vessels in a\n   Malpighian capsule of the kidney.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glomuliferous', 'a.', 'Having small clusters of minutely branched\n   coral-like excrescences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glonoin', 'n.', 'Alt. of Glonoine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glonoine', 'n.', 'Same as Nitroglycerin; -- called also oil of glonoin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glonoine', 'n.', 'A dilute solution of nitroglycerin used as a neurotic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloom', 'n.', 'Partial or total darkness; thick shade; obscurity; as, the\n   gloom of a forest, or of midnight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloom', 'n.', 'A shady, gloomy, or dark place or grove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloom', 'n.', 'Cloudiness or heaviness of mind; melancholy; aspect of\n   sorrow; low spirits; dullness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloom', 'n.', 'In gunpowder manufacture, the drying oven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloomed', 'imp. & p. p.', 'of Gloom');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glooming', 'p. pr. & vb. n.', 'of Gloom');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloom', 'v. i.', 'To shine or appear obscurely or imperfectly; to glimmer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloom', 'v. i.', 'To become dark or dim; to be or appear dismal, gloomy, or\n   sad; to come to the evening twilight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloom', 'v. t.', 'To render gloomy or dark; to obscure; to darken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloom', 'v. t.', 'To fill with gloom; to make sad, dismal, or sullen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloomily', 'adv.', 'In a gloomy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloominess', 'n.', 'State of being gloomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glooming', 'n.', 'Twilight (of morning or evening); the gloaming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloomth', 'n.', 'Gloom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloomy', 'superl.', 'Imperfectly illuminated; dismal through obscurity or\n   darkness; dusky; dim; clouded; as, the cavern was gloomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloomy', 'superl.', 'Affected with, or expressing, gloom; melancholy;\n   dejected; as, a gloomy temper or countenance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloppen', 'v. t. & i.', 'To surprise or astonish; to be startled or\n   astonished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glore', 'v. i.', 'To glare; to glower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloria', 'n.', 'A doxology (beginning Gloria Patri, Glory be to the\n   Father), sung or said at the end of the Psalms in the service of the\n   Roman Catholic and other churches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloria', 'n.', 'A portion of the Mass (Gloria in Excelsis Deo, Glory be to\n   God on high), and also of the communion service in some churches. In\n   the Episcopal Church the version in English is used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloria', 'n.', 'The musical setting of a gloria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloriation', 'n.', 'Boast; a triumphing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloried', 'a.', 'Illustrious; honorable; noble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glorification', 'n.', 'The act of glorifyng or of giving glory to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glorification', 'n.', 'The state of being glorifed; as, the glorification\n   of Christ after his resurrection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glorified', 'imp. & p. p.', 'of Glorify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glorifying', 'p. pr. & vb. n.', 'of Glorify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glorify', 'v. t.', 'To make glorious by bestowing glory upon; to confer\n   honor and distinction upon; to elevate to power or happiness, or to\n   celestial glory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glorify', 'v. t.', 'To make glorious in thought or with the heart, by\n   ascribing glory to; to asknowledge the excellence of; to render homage\n   to; to magnify in worship; to adore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloriole', 'n.', 'An aureole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloriosa', 'n.', 'A genus of climbing plants with very showy lilylike\n   blossoms, natives of India.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glorioser', 'n.', 'A boaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glorioso', 'n.', 'A boaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glorious', 'n.', 'Exhibiting attributes, qualities, or acts that are worthy\n   of or receive glory; noble; praiseworthy; excellent; splendid;\n   illustrious; inspiring admiration; as, glorious deeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glorious', 'n.', 'Eager for glory or distinction; haughty; boastful;\n   ostentatious; vainglorious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glorious', 'n.', 'Ecstatic; hilarious; elated with drink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glory', 'n.', 'Praise, honor, admiration, or distinction, accorded by\n   common consent to a person or thing; high reputation; honorable fame;\n   renown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glory', 'n.', 'That quality in a person or thing which secures general\n   praise or honor; that which brings or gives renown; an object of pride\n   or boast; the occasion of praise; excellency; brilliancy; splendor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glory', 'n.', 'Pride; boastfulness; arrogance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glory', 'n.', 'The presence of the Divine Being; the manifestations of the\n   divine nature and favor to the blessed in heaven; celestial honor;\n   heaven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glory', 'n.', 'An emanation of light supposed to proceed from beings of\n   peculiar sanctity. It is represented in art by rays of gold, or the\n   like, proceeding from the head or body, or by a disk, or a mere line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloried', 'imp. & p. p.', 'of Glory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glorying', 'p. pr. & vb. n.', 'of Glory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glory', 'n.', 'To exult with joy; to rejoice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glory', 'n.', 'To boast; to be proud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glose', 'n. & v.', 'See Gloze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloser', 'n.', 'See Glosser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloss', 'n.', 'Brightness or luster of a body proceeding from a smooth\n   surface; polish; as, the gloss of silk; cloth is calendered to give it\n   a gloss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloss', 'n.', 'A specious appearance; superficial quality or show.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glossed', 'imp. & p. p.', 'of Gloss');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glossing', 'p. pr. & vb. n.', 'of Gloss');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloss', 'v. t.', 'To give a superficial luster or gloss to; to make smooth\n   and shining; as, to gloss cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloss', 'n.', 'A foreign, archaic, technical, or other uncommon word\n   requiring explanation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloss', 'n.', 'An interpretation, consisting of one or more words,\n   interlinear or marginal; an explanatory note or comment; a running\n   commentary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloss', 'n.', 'A false or specious explanation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloss', 'v. t.', 'To render clear and evident by comments; to illustrate;\n   to explain; to annotate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloss', 'v. t.', 'To give a specious appearance to; to render specious and\n   plausible; to palliate by specious explanation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloss', 'v. i.', 'To make comments; to comment; to explain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloss', 'v. i.', 'To make sly remarks, or insinuations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloss/', 'pl. ', 'of Glossa');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glossa', 'n.', 'The tongue, or lingua, of an insect. See Hymenoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glossal', 'a.', 'Of or pertaining to the tongue; lingual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glossanthrax', 'n.', 'A disease of horses and cattle accompanied by\n   carbuncles in the mouth and on the tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glossarial', 'a.', 'Of or pertaining to glosses or to a glossary;\n   containing a glossary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glossarially', 'adv.', 'In the manner of a glossary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glossarist', 'n.', 'A writer of glosses or of a glossary; a commentator; a\n   scholiast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gossaries', 'pl. ', 'of Glossary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glossary', 'n.', 'A collection of glosses or explanations of words and\n   passages of a work or author; a partial dictionary of a work, an\n   author, a dialect, art, or science, explaining archaic, technical, or\n   other uncommon words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glossata', 'n. pl.', 'The Lepidoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glossator', 'n.', 'A writer of glosses or comments; a commentator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glosser', 'n.', 'A polisher; one who gives a luster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glosser', 'n.', 'A writer of glosses; a scholiast; a commentator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glossic', 'n.', 'A system of phonetic spelling based upon the present\n   values of English letters, but invariably using one symbol to represent\n   one sound only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glossily', 'adv.', 'In a glossy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glossiness', 'n.', 'The condition or quality of being glossy; the luster or\n   brightness of a smooth surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glossist', 'n.', 'A writer of comments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glossitis', 'n.', 'Inflammation of the tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glossly', 'adv.', 'Like gloss; specious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glossocomon', 'n.', 'A kind of hoisting winch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glossoepiglottic', 'a.', 'Pertaining to both tongue and epiglottis; as,\n   glossoepiglottic folds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glossographer', 'n.', 'A writer of a glossary; a commentator; a scholiast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glossographical', 'a.', 'Of or pertaining to glossography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glossography', 'n.', 'The writing of glossaries, glosses, or comments for\n   illustrating an author.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glossohyal', 'a.', 'Pertaining to both the hyoidean arch and the tongue; --\n   applied to the anterior segment of the hyoidean arch in many fishes. --\n   n. The glossohyal bone or cartilage; lingual bone; entoglossal bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glossolalia', 'n.', 'Alt. of Glossolaly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glossolaly', 'n.', 'The gift of tongues. Farrar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glossological', 'a.', 'Of or pertaining to glossology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glassologist', 'n.', 'One who defines and explains terms; one who is versed\n   in glossology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glossology', 'n.', 'The definition and explanation of terms; a glossary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glossology', 'n.', 'The science of language; comparative philology;\n   linguistics; glottology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glossopharyngeal', 'a.', 'Pertaining to both the tongue and the pharynx; --\n   applied especially to the ninth pair of cranial nerves, which are\n   distributed to the pharynx and tongue. -- n. One of the\n   glossopharyngeal nerves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glossy', 'superl.', 'Smooth and shining; reflecting luster from a smooth\n   surface; highly polished; lustrous; as, glossy silk; a glossy surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glossy', 'superl.', 'Smooth; specious; plausible; as, glossy deceit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glost oven', '', 'An oven in which glazed pottery is fired; -- also called\n   glaze kiln, or glaze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glottal', 'a.', 'Of or pertaining to, or produced by, the glottis; glottic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glottic', 'a.', 'Alt. of Glottidean');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glottidean', 'a.', 'Of or pertaining to the glottis; glottal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glottis', 'n.', 'The opening from the pharynx into the larynx or into the\n   trachea. See Larynx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glottological', 'a.', 'Of or pertaining to glottology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glottologist', 'n.', 'A linguist; a philologist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glottology', 'n.', 'The science of tongues or languages; comparative\n   philology; glossology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glout', 'v. i.', 'To pout; to look sullen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glout', 'v. t.', 'To view attentively; to gloat on; to stare at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glove', 'n.', 'A cover for the hand, or for the hand and wrist, with a\n   separate sheath for each finger. The latter characteristic\n   distinguishes the glove from the mitten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glove', 'n.', 'A boxing glove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloved', 'imp. & p. p.', 'of Glove');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloving', 'p. pr. & vb. n.', 'of Glove');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glove', 'v. t.', 'To cover with, or as with, a glove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glover', 'n.', 'One whose trade it is to make or sell gloves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glowed', 'imp. & p. p.', 'of Glow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glowing', 'p. pr. & vb. n.', 'of Glow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glow', 'v. i.', 'To shine with an intense or white heat; to give forth\n   vivid light and heat; to be incandescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glow', 'v. i.', 'To exhibit a strong, bright color; to be brilliant, as if\n   with heat; to be bright or red with heat or animation, with blushes,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glow', 'v. i.', 'To feel hot; to have a burning sensation, as of the skin,\n   from friction, exercise, etc.; to burn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glow', 'v. i.', 'To feel the heat of passion; to be animated, as by intense\n   love, zeal, anger, etc.; to rage, as passior; as, the heart glows with\n   love, zeal, or patriotism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glow', 'v. t.', 'To make hot; to flush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glow', 'n.', 'White or red heat; incandscence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glow', 'n.', 'Brightness or warmth of color; redness; a rosy flush; as, the\n   glow of health in the cheeks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glow', 'n.', 'Intense excitement or earnestness; vehemence or heat of\n   passion; ardor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glow', 'n.', 'Heat of body; a sensation of warmth, as that produced by\n   exercise, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glowbard', 'n.', 'The glowworm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glowered', 'imp. & p. p.', 'of Glower');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glowering', 'p. pr. & vb. n.', 'of Glower');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glower', 'v. i.', 'to look intently; to stare angrily or with a scowl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glowingly', 'adv.', 'In a glowing manner; with ardent heat or passion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glowlamp', 'n.', 'An aphlogistic lamp. See Aphlogistic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glowlamp', 'n.', 'An incandescent lamp. See Incandescent, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glowworm', 'n.', 'A coleopterous insect of the genus Lampyris; esp., the\n   wingless females and larvae of the two European species (L. noctiluca,\n   and L. splendidula), which emit light from some of the abdominal\n   segments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloxinia', 'n.', 'American genus of herbaceous plants with very handsome\n   bell-shaped blossoms; -- named after B. P. Gloxin, a German botanist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glozed', 'imp. & p. p.', 'of Gloze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glozing', 'p. pr. & vb. n.', 'of Gloze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloze', 'v. i.', 'To flatter; to wheedle; to fawn; to talk smoothly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloze', 'v. i.', 'To give a specious or false meaning; to ministerpret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloze', 'v. t.', 'To smooth over; to palliate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloze', 'n.', 'Flattery; adulation; smooth speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gloze', 'n.', 'Specious show; gloss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glozer', 'n.', 'A flatterer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glucic', 'a.', 'Pertaining to, or obtained from, sugar; as, glucic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glucina', 'n.', 'A white or gray tasteless powder, the oxide of the element\n   glucinum; -- formerly called glucine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glucinic', 'a.', 'Pertaining to, derived from, or containing, glucinum; as,\n   glucinic oxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glucinum', 'n.', 'A rare metallic element, of a silver white color, and low\n   specific gravity (2.1), resembling magnesium. It never occurs naturally\n   in the free state, but is always combined, usually with silica or\n   alumina, or both; as in the minerals phenacite, chrysoberyl, beryl or\n   emerald, euclase, and danalite. It was named from its oxide glucina,\n   which was known long before the element was isolated. Symbol Gl. Atomic\n   weight 9.1. Called also beryllium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glucogen', 'n.', 'See Glycogen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glucogenesis', 'n.', 'Glycogenesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gluconic', 'a.', 'Pertaining to, or derived from, glucose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glucose', 'n.', 'A variety of sugar occurring in nature very abundantly, as\n   in ripe grapes, and in honey, and produced in great quantities from\n   starch, etc., by the action of heat and acids. It is only about half as\n   sweet as cane sugar. Called also dextrose, grape sugar, diabetic sugar,\n   and starch sugar. See Dextrose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glucose', 'n.', 'Any one of a large class of sugars, isometric with glucose\n   proper, and including levulose, galactose, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glucose', 'n.', 'The trade name of a sirup, obtained as an uncrystallizable\n   reside in the manufacture of glucose proper, and containing, in\n   addition to some dextrose or glucose, also maltose, dextrin, etc. It is\n   used as a cheap adulterant of sirups, beers, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glucoside', 'n.', 'One of a large series of amorphous or crystalline\n   substances, occurring very widely distributed in plants, rarely in\n   animals, and regarded as influental agents in the formation and\n   disposition of the sugars. They are frequently of a bitter taste, but,\n   by the action of ferments, or of dilute acids and alkalies, always\n   break down into some characteristic substance (acid, aldehyde, alcohol,\n   phenole, or alkaloid) and glucose (or some other sugar); hence the\n   name. They are of the nature of complex and compound ethers, and\n   ethereal salts of the sugar carbohydrates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glucosuria', 'n.', 'A condition in which glucose is discharged in the\n   urine; diabetes mellitus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glue', 'n.', 'A hard brittle brownish gelatin, obtained by boiling to a\n   jelly the skins, hoofs, etc., of animals. When gently heated with\n   water, it becomes viscid and tenaceous, and is used as a cement for\n   uniting substances. The name is also given to other adhesive or viscous\n   substances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glued', 'imp. & p. p.', 'of Glue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gluing', 'p. pr. & vb. n.', 'of Glue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glue', 'n.', 'To join with glue or a viscous substance; to cause to stick\n   or hold fast, as if with glue; to fix or fasten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gluepot', 'n.', 'A utensil for melting glue, consisting of an inner pot\n   holding the glue, immersed in an outer one containing water which is\n   heated to soften the glue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gluer', 'n.', 'One who cements with glue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gluey', 'a.', 'Viscous; glutinous; of the nature of, or like, glue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glueyness', 'n.', 'Viscidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gluish', 'a.', 'Somewhat gluey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glum', 'n.', 'Sullenness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glum', 'a.', 'Moody; silent; sullen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glum', 'v. i.', 'To look sullen; to be of a sour countenance; to be glum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glumaceous', 'a.', 'Having glumes; consisting of glumes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glumal', 'a.', 'Characterized by a glume, or having the nature of a glume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glume', 'n.', 'The bracteal covering of the flowers or seeds of grain and\n   grasses; esp., an outer husk or bract of a spikelt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glumella', 'n.', 'Alt. of Glumelle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glumelle', 'n.', 'One of the pelets or inner chaffy scales of the flowers\n   or spikelets of grasses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glumly', 'adv.', 'In a glum manner; sullenly; moodily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glummy', 'a.', 'dark; gloomy; dismal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glumness', 'n.', 'Moodiness; sullenness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glump', 'v. i.', 'To manifest sullenness; to sulk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glumpy', 'a.', 'Glum; sullen; sulky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glunch', 'a.', 'Frowning; sulky; sullen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glunch', 'n.', 'A sullen, angry look; a look of disdain or dislike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glutted', 'imp. & p. p.', 'of Glut');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glutting', 'p. pr. & vb. n.', 'of Glut');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glut', 'v. t.', 'To swallow, or to swallow greedlly; to gorge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glut', 'v. t.', 'To fill to satiety; to satisfy fully the desire or craving\n   of; to satiate; to sate; to cloy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glut', 'v. i.', 'To eat gluttonously or to satiety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glut', 'n.', 'That which is swallowed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glut', 'n.', 'Plenty, to satiety or repletion; a full supply; hence, often,\n   a supply beyond sufficiency or to loathing; over abundance; as, a glut\n   of the market.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glut', 'n.', 'Something that fills up an opening; a clog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glut', 'n.', 'A wooden wedge used in splitting blocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glut', 'n.', 'A piece of wood used to fill up behind cribbing or tubbing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glut', 'n.', 'A bat, or small piece of brick, used to fill out a course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glut', 'n.', 'An arched opening to the ashpit of a klin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glut', 'n.', 'A block used for a fulcrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glut', 'n.', 'The broad-nosed eel (Anguilla latirostris), found in Europe,\n   Asia, the West Indies, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glutaconic', 'a.', 'Pertaining to, or derived from, an acid intermediate\n   between glutaric and aconitic acids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glutaeus', 'n.', 'The great muscle of the buttock in man and most mammals,\n   and the corresponding muscle in many lower animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glutamic', 'a.', 'Of or pertaining to gluten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glutaric', 'a.', 'Of, pertaining to, or designating, an acid so called; as,\n   glutaric ethers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glutazine', 'n.', 'A nitrogenous substance, forming a heavy, sandy powder,\n   white or nearly so. It is a derivative of pyridine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gluteal', 'a.', 'Pertaining to, or in the region of, the glutaeus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gluten', 'n.', 'The viscid, tenacious substance which gives adhesiveness to\n   dough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gluteus', 'n.', 'Same as Glut/us.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glutin', 'n.', 'Same as Gliadin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glutin', 'n.', 'Sometimes synonymous with Gelatin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glutinated', 'imp. & p. p.', 'of Glutinate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glutinating', 'p. pr. & vb. n.', 'of Glutinate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glutinate', 'v. t.', 'To unite with glue; to cement; to stick together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glutination', 'n.', 'The act of uniting with glue; sticking together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glutinative', 'a.', 'Having the quality of cementing; tenacious; viscous;\n   glutinous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glutinosity', 'n.', 'The quality of being glutinous; viscousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glutinous', 'a.', 'Of the nature of glue; resembling glue; viscous; viscid;\n   adhesive; gluey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glutinous', 'a.', 'Havig a moist and adhesive or sticky surface, as a leaf\n   or gland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glutinousness', 'n.', 'The quality of being glutinous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glutton', 'n.', 'One who eats voraciously, or to excess; a gormandizer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glutton', 'n.', 'Fig.: One who gluts himself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glutton', 'n.', 'A carnivorous mammal (Gulo luscus), of the family\n   Mustelidae, about the size of a large badger. It was formerly believed\n   to be inordinately voracious, whence the name; the wolverene. It is a\n   native of the northern parts of America, Europe, and Asia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glutton', 'a.', 'Gluttonous; greedy; gormandizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glutton', 'v. t. & i.', 'To glut; to eat voraciously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gluttonish', 'a.', 'Gluttonous; greedy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gluttonized', 'imp. & p. p.', 'of Gluttonize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gluttonizing', 'p. pr. & vb. n.', 'of Gluttonize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gluttonize', 'v. i.', 'To eat to excess; to eat voraciously; to gormandize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gluttonous', 'a.', 'Given to gluttony; eating to excess; indulging the\n   appetite; voracious; as, a gluttonous age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gluttonies', 'pl. ', 'of Gluttony');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gluttony', 'n.', 'Excess in eating; extravagant indulgence of the appetite\n   for food; voracity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glycerate', 'n.', 'A salt of glyceric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glyceric', 'a.', 'Pertaining to, or derived from, glycerin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glyceride', 'n.', 'A compound ether (formed from glycerin). Some glycerides\n   exist ready formed as natural fats, others are produced artificially.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glycerin', 'n.', 'Alt. of Glycerine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glycerine', 'n.', 'An oily, viscous liquid, C3H5(OH)3, colorless and\n   odorless, and with a hot, sweetish taste, existing in the natural fats\n   and oils as the base, combined with various acids, as oleic, margaric,\n   stearic, and palmitic. It is a triatomic alcohol, and hence is also\n   called glycerol. See Note under Gelatin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glycerite', 'n.', 'A medicinal preparation made by mixing or dissolving a\n   substance in glycerin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glycerol', 'n.', 'Same as Glycerin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Clycerole', 'n.', 'Same as Glycerite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glyceryl', 'n.', 'A compound radical, C3H5, regarded as the essential\n   radical of glycerin. It is metameric with allyl. Called also propenyl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glycide', 'n.', 'A colorless liquid, obtained from certain derivatives of\n   glycerin, and regarded as a partially dehydrated glycerin; -- called\n   also glycidic alcohol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glycidic', 'a.', 'Pertaining to, or derived from, glycide; as, glycidic\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glycin', 'n.', 'Same as Glycocoll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glycocholate', 'n.', 'A salt of glycocholic acid; as, sodium glycocholate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glycocholic', 'a.', 'Pertaining to, or composed of, glycocoll and cholic\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glycocin', 'n.', 'Same as Glycocoll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glycocoll', 'n.', 'A crystalline, nitrogenous substance, with a sweet\n   taste, formed from hippuric acid by boiling with hydrochloric acid, and\n   present in bile united with cholic acid. It is also formed from gelatin\n   by decomposition with acids. Chemically, it is amido-acetic acid.\n   Called also glycin, and glycocin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glycogen', 'n.', 'A white, amorphous, tasteless substance resembling\n   starch, soluble in water to an opalescent fluid. It is found abundantly\n   in the liver of most animals, and in small quantity in other organs and\n   tissues, particularly in the embryo. It is quickly changed into sugar\n   when boiled with dilute sulphuric or hydrochloric acid, and also by the\n   action of amylolytic ferments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glycogenic', 'a.', 'Pertaining to, or caused by, glycogen; as, the\n   glycogenic function of the liver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glycogeny', 'n.', 'Alt. of Glycogenesis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glycogenesis', 'n.', 'The production or formation of sugar from gycogen, as\n   in the liver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glycol', 'n.', 'A thick, colorless liquid, C2H4(OH)2, of a sweetish taste,\n   produced artificially from certain ethylene compounds. It is a diacid\n   alcohol, intermediate between ordinary ethyl alcohol and glycerin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glycol', 'n.', 'Any one of the large class of diacid alcohols, of which\n   glycol proper is the type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glycolic', 'a.', 'Pertaining to, or derived from, glycol; as, glycolic\n   ether; glycolic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glycolide', 'n.', 'A white amorphous powder, C4H4O, obtained by heating and\n   dehydrating glycolic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glycoluric', 'a.', 'Pertaining to, derived from, glycol and urea; as,\n   glycoluric acid, which is called also hydantoic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glycoluril', 'n.', 'A white, crystalline, nitrogenous substance, obtained\n   by the reduction of allantoin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glycolyl', 'n.', 'A divalent, compound radical, CO.CH2, regarded as the\n   essential radical of glycolic acid, and a large series of related\n   compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glyconian', 'a. & n.', 'Glyconic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glyconic', 'a.', 'Consisting of a spondee, a choriamb, and a pyrrhic; --\n   applied to a kind of verse in Greek and Latin poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glyconic', 'n.', 'A glyconic verse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glyconin', 'n.', 'An emulsion of glycerin and the yolk of eggs, used as an\n   ointment, as a vehicle for medicines, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glycosine', 'n.', 'An organic base, C6H6N4, produced artificially as a\n   white, crystalline powder, by the action of ammonia on glyoxal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glycosuria', 'n.', 'Same as Glucosuria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glycyrrhiza', 'n.', 'A genus of papilionaceous herbaceous plants, one\n   species of which (G. glabra), is the licorice plant, the roots of which\n   have a bittersweet mucilaginous taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glycyrrhiza', 'n.', 'The root of Glycyrrhiza glabra (liquorice root), used\n   as a demulcent, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glycyrrhizimic', 'a.', 'From, or pertaining to, glycyrrhizin; as,\n   glycyrrhizimic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glycyrrhizin', 'n.', 'A glucoside found in licorice root (Glycyrrhiza), in\n   monesia bark (Chrysophyllum), in the root of the walnut, etc., and\n   extracted as a yellow, amorphous powder, of a bittersweet taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glyn', 'n.', 'Alt. of Glynne');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glynne', 'n.', 'A glen. See Glen. [Obs. singly, but occurring often in\n   locative names in Ireland, as Glen does in Scotland.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glyoxal', 'n.', 'A white, amorphous, deliquescent powder, (CO.H)2, obtained\n   by the partial oxidation of glycol. It is a double aldehyde, between\n   glycol and oxalic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glyoxalic', 'a.', 'Pertaining to, or designating, an aldehyde acid,\n   intermediate between glycol and oxalic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glyoxaline', 'n.', 'A white, crystalline, organic base, C3H4N2, produced by\n   the action of ammonia on glyoxal, and forming the origin of a large\n   class of derivatives hence, any one of the series of which glyoxaline\n   is a type; -- called also oxaline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glyoxime', 'n.', 'A white, crystalline, nitrogenous substance, produced by\n   the action of hydroxylamine on glyoxal, and belonging to the class of\n   oximes; also, any one of a group of substances resembling glyoxime\n   proper, and of which it is a type. See Oxime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glyph', 'n.', 'A sunken channel or groove, usually vertical. See Triglyph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glyphic', 'a.', 'Of or pertaining to sculpture or carving of any sort, esp.\n   to glyphs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glyphograph', 'n.', 'A plate made by glyphography, or an impression taken\n   from such a plate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glyphographic', 'a.', 'Of or pertaining to glyphography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glyphography', 'n.', 'A process similar to etching, in which, by means of\n   voltaic electricity, a raised copy of a drawing is made, so that it can\n   be used to print from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glyptic', 'a.', 'Of or pertaining to gem engraving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glyptic', 'a.', 'Figured; marked as with figures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glyptics', 'n.', 'The art of engraving on precious stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glyptodon', 'n.', 'An extinct South American quaternary mammal, allied to\n   the armadillos. It was as large as an ox, was covered with tessellated\n   scales, and had fluted teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glyptodont', 'n.', 'One of a family (Glyptodontidae) of extinct South\n   American edentates, of which Glyptodon is the type. About twenty\n   species are known.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glyptographic', 'a.', 'Relating to glyptography, or the art of engraving on\n   precious stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glyptography', 'n.', 'The art or process of engraving on precious stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glyptotheca', 'n.', 'A building or room devoted to works of sculpture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glyster', 'n.', 'Same as Clyster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gmelinite', 'n.', 'A rhombohedral zeolitic mineral, related in form and\n   composition to chabazite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnaphalium', 'n.', 'A genus of composite plants with white or colored dry\n   and persistent involucres; a kind of everlasting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnar', 'n.', 'A knot or gnarl in wood; hence, a tough, thickset man; --\n   written also gnarr.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnarred', 'imp. & p. p.', 'of Gnar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnarring', 'p. pr. & vb. n.', 'of Gnar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnar', 'v. i.', 'To gnarl; to snarl; to growl; -- written also gnarr.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnarled', 'imp. & p. p.', 'of Gnarl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnarling', 'p. pr. & vb. n.', 'of Gnarl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnarl', 'v. i.', 'To growl; to snarl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnarl', 'n.', 'a knot in wood; a large or hard knot, or a protuberance with\n   twisted grain, on a tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnarled', 'a.', 'Knotty; full of knots or gnarls; twisted; crossgrained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnarly', 'a.', 'Full of knots; knotty; twisted; crossgrained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnashed', 'imp. & p. p.', 'of Gnash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnashing', 'p. pr. & vb. n.', 'of Gnash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnash', 'v. t.', 'To strike together, as in anger or pain; as, to gnash the\n   teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnash', 'v. i.', 'To grind or strike the teeth together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnashingly', 'adv.', 'With gnashing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnat', 'n.', 'A blood-sucking dipterous fly, of the genus Culex, undergoing\n   a metamorphosis in water. The females have a proboscis armed with\n   needlelike organs for penetrating the skin of animals. These are\n   wanting in the males. In America they are generally called mosquitoes.\n   See Mosquito.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnat', 'n.', 'Any fly resembling a Culex in form or habits; esp., in\n   America, a small biting fly of the genus Simulium and allies, as the\n   buffalo gnat, the black fly, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnathic', 'a.', 'Of or pertaining to the jaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnathidia', 'pl. ', 'of Gnathidium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnathidium', 'n.', 'The ramus of the lower jaw of a bird as far as it is\n   naked; -- commonly used in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnathite', 'n.', 'Any one of the mouth appendages of the Arthropoda. They\n   are known as mandibles, maxillae, and maxillipeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnathonic', 'a.', 'Alt. of Gnathonical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnathonical', 'a.', 'Flattering; deceitful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnathopod', 'n.', 'A gnathopodite or maxilliped. See Maxilliped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnathopodite', 'n.', 'Any leglike appendage of a crustacean, when modified\n   wholly, or in part, to serve as a jaw, esp. one of the maxillipeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnathastegite', 'n.', 'One of a pair of broad plates, developed from the\n   outer maxillipeds of crabs, and forming a cover for the other mouth\n   organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnathostoma', 'n. pl.', 'A comprehensive division of vertebrates, including\n   all that have distinct jaws, in contrast with the leptocardians and\n   marsipobranchs (Cyclostoma), which lack them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'GnathothecAe', 'pl. ', 'of Gnathotheca');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnathotheca', 'n.', 'The horney covering of the lower mandible of a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnatling', 'n.', 'A small gnat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnatworm', 'n.', 'The aquatic larva of a gnat; -- called also,\n   colloquially, wiggler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnawed', 'imp. & p. p.', 'of Gnaw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnawing', 'p. pr. & vb. n.', 'of Gnaw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnaw', 'v. t.', 'To bite, as something hard or tough, which is not readily\n   separated or crushed; to bite off little by little, with effort; to\n   wear or eat away by scraping or continuous biting with the teeth; to\n   nibble at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnaw', 'v. t.', 'To bite in agony or rage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnaw', 'v. t.', 'To corrode; to fret away; to waste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnaw', 'v. i.', 'To use the teeth in biting; to bite with repeated effort,\n   as in eating or removing with the teethsomething hard, unwiedly, or\n   unmanageable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnawer', 'n.', 'One who, or that which, gnaws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnawer', 'n.', 'A rodent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gneiss', 'n.', 'A crystalline rock, consisting, like granite, of quartz,\n   feldspar, and mica, but having these materials, especially the mica,\n   arranged in planes, so that it breaks rather easily into coarse slabs\n   or flags. Hornblende sometimes takes the place of the mica, and it is\n   then called hornblendic / syenitic gneiss. Similar varieties of related\n   rocks are also called gneiss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gneissic', 'a.', 'Relating to, or resembling, gneiss; consisting of gneiss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gneissoid', 'a.', 'Resembling gneiss; having some of the characteristics of\n   gneiss; -- applied to rocks of an intermediate character between\n   granite and gneiss, or mica slate and gneiss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gneissose', 'a.', 'Having the structure of gneiss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnew', '', 'imp. of Gnaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnide', 'v. t.', 'To rub; to bruise; to break in pieces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnof', 'n.', 'Churl; curmudgeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnome', 'n.', 'An imaginary being, supposed by the Rosicrucians to inhabit\n   the inner parts of the earth, and to be the guardian of mines,\n   quarries, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnome', 'n.', 'A dwarf; a goblin; a person of small stature or misshapen\n   features, or of strange appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnome', 'n.', 'A small owl (Glaucidium gnoma) of the Western United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnome', 'n.', 'A brief reflection or maxim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnomic', 'a.', 'Alt. of Gnomical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnomical', 'a.', 'Sententious; uttering or containing maxims, or striking\n   detached thoughts; aphoristic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnomical', 'a.', 'Gnomonical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnomically', 'adv.', 'In a gnomic, didactic, or sententious manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnomologic', 'a.', 'Alt. of Gnomological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnomological', 'a.', 'Pertaining to, of the nature of, or resembling, a\n   gnomology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnomology', 'n.', 'A collection of, or a treatise on, maxims, grave\n   sentences, or reflections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnomon', 'n.', 'The style or pin, which by its shadow, shows the hour of\n   the day. It is usually set parallel to the earths axis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnomon', 'n.', 'A style or column erected perpendicularly to the horizon,\n   formerly used in astronomocal observations. Its principal use was to\n   find the altitude of the sun by measuring the length of its shadow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnomon', 'n.', 'The space included between the boundary lines of two\n   similar parallelograms, the one within the other, with an angle in\n   common; as, the gnomon bcdefg of the parallelograms ac and af. The\n   parallelogram bf is the complement of the parallelogram df.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnomon', 'n.', 'The index of the hour circle of a globe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnomonic', 'a.', 'Alt. of Gnomonical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnomonical', 'a.', 'Of or pertaining to the gnomon, or the art of dialing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnomonically', 'adv.', 'According to the principles of the gnomonic\n   projection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnomonics', 'n.', 'The art or science of dialing, or of constructing dials\n   to show the hour of the day by the shadow of a gnomon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnomonist', 'n.', 'One skilled in gnomonics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnomonology', 'n.', 'A treatise on gnomonics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnoscopine', 'n.', 'An alkaloid existing in small quantities in opium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnosis', 'n.', 'The deeper wisdom; knowledge of spiritual truth, such as\n   was claimed by the Gnostics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnostic', 'a.', 'Knowing; wise; shrewd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnostic', 'a.', 'Of or pertaining to Gnosticism or its adherents; as, the\n   Gnostic heresy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnostic', 'n.', 'One of the so-called philosophers in the first ages of\n   Christianity, who claimed a true philosophical interpretation of the\n   Christian religion. Their system combined Oriental theology and Greek\n   philosophy with the doctrines of Christianity. They held that all\n   natures, intelligible, intellectual, and material, are derived from the\n   Deity by successive emanations, which they called Eons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnosticism', 'n.', 'The system of philosophy taught by the Gnostics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnow', 'imp.', 'Gnawed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gnu', 'n.', 'One of two species of large South African antelopes of the\n   genus Catoblephas, having a mane and bushy tail, and curved horns in\n   both sexes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Go', 'p. p.', 'Gone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Went', 'imp.', 'of Go');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gone', 'p. p.', 'of Go');
  INSERT INTO words (word, word_type, description) VALUES  (    'Going', 'p. pr. & vb. n.', 'of Go');
  INSERT INTO words (word, word_type, description) VALUES  (    'Go', 'v. i.', 'To pass from one place to another; to be in motion; to be in\n   a state not motionless or at rest; to proceed; to advance; to make\n   progress; -- used, in various applications, of the movement of both\n   animate and inanimate beings, by whatever means, and also of the\n   movements of the mind; also figuratively applied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Go', 'v. i.', 'To move upon the feet, or step by step; to walk; also, to\n   walk step by step, or leisurely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Go', 'v. i.', 'To be passed on fron one to another; to pass; to circulate;\n   hence, with for, to have currency; to be taken, accepted, or regarded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Go', 'v. i.', 'To proceed or happen in a given manner; to fare; to move on\n   or be carried on; to have course; to come to an issue or result; to\n   succeed; to turn out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Go', 'v. i.', 'To proceed or tend toward a result, consequence, or product;\n   to tend; to conduce; to be an ingredient; to avail; to apply; to\n   contribute; -- often with the infinitive; as, this goes to show.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Go', 'v. i.', 'To apply ones self; to set ones self; to undertake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Go', 'v. i.', 'To proceed by a mental operation; to pass in mind or by an\n   act of the memory or imagination; -- generally with over or through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Go', 'v. i.', 'To be with young; to be pregnant; to gestate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Go', 'v. i.', 'To move from the person speaking, or from the point whence\n   the action is contemplated; to pass away; to leave; to depart; -- in\n   opposition to stay and come.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Go', 'v. i.', 'To pass away; to depart forever; to be lost or ruined; to\n   perish; to decline; to decease; to die.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Go', 'v. i.', 'To reach; to extend; to lead; as, a line goes across the\n   street; his land goes to the river; this road goes to New York.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Go', 'v. i.', 'To have recourse; to resort; as, to go to law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Go', 'v. t.', 'To take, as a share in an enterprise; to undertake or become\n   responsible for; to bear a part in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Go', 'v. t.', 'To bet or wager; as, Ill go you a shilling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Go', 'n.', 'Act; working; operation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Go', 'n.', 'A circumstance or occurrence; an incident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Go', 'n.', 'The fashion or mode; as, quite the go.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Go', 'n.', 'Noisy merriment; as, a high go.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Go', 'n.', 'A glass of spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Go', 'n.', 'Power of going or doing; energy; vitality; perseverance; push;\n   as, there is no go in him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Go', 'n.', 'That condition in the course of the game when a player can not\n   lay down a card which will not carry the aggregate count above\n   thirty-one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goa', 'n.', 'A species of antelope (Procapra picticauda), inhabiting\n   Thibet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goad', 'v. t.', 'A pointed instrument used to urge on a beast; hence, any\n   necessity that urges or stimulates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goaded', 'imp. & p. p.', 'of Goad');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goading', 'p. pr. & vb. n.', 'of Goad');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goad', 'v. t.', 'To prick; to drive with a goad; hence, to urge forward, or\n   to rouse by anything pungent, severe, irritating, or inflaming; to\n   stimulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goafs', 'pl. ', 'of Goaf');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goaves', 'pl. ', 'of Goaf');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goaf', 'n.', 'That part of a mine from which the mineral has been partially\n   or wholly removed; the waste left in old workings; -- called also gob .');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goal', 'n.', 'The mark set to bound a race, and to or around which the\n   constestants run, or from which they start to return to it again; the\n   place at which a race or a journey is to end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goal', 'n.', 'The final purpose or aim; the end to which a design tends, or\n   which a person aims to reach or attain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goal', 'n.', 'A base, station, or bound used in various games; in football,\n   a line between two posts across which the ball must pass in order to\n   score; also, the act of kicking the ball over the line between the goal\n   posts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goa powder', '', 'A bitter powder (also called araroba) found in the\n   interspaces of the wood of a Brazilian tree (Andira araroba) and used\n   as a medicine. It is the material from which chrysarobin is obtained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goar', 'n.', 'Same as lst Gore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goarish', 'a.', 'Patched; mean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goat', 'n.', 'A hollow-horned ruminant of the genus Capra, of several\n   species and varieties, esp. the domestic goat (C. hircus), which is\n   raised for its milk, flesh, and skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goatee', 'n.', 'A part of a mans beard on the chin or lower lip which is\n   allowed to grow, and trimmed so as to resemble the beard of a goat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goatfish', 'n.', 'A fish of the genus Upeneus, inhabiting the Gulf of\n   Mexico. It is allied to the surmullet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goatherd', 'n.', 'One who tends goats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goatish', 'a.', 'Characteristic of a goat; goatlike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goatlike', 'a.', 'Like a goat; goatish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goatskin', 'n.', 'The skin of a goat, or leather made from it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goatskin', 'a.', 'Made of the skin of a goat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goatsucker', 'n.', 'One of several species of insectivorous birds,\n   belonging to Caprimulgus and allied genera, esp. the European species\n   (Caprimulgus Europaeus); -- so called from the mistaken notion that it\n   sucks goats. The European species is also goat-milker, goat owl, goat\n   chaffer, fern owl, night hawk, nightjar, night churr, churr-owl, gnat\n   hawk, and dorhawk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goaves', 'n.', 'Old workings. See Goaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gob', 'n.', 'Same as Goaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gob', 'n.', 'A little mass or collection; a small quantity; a mouthful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gob', 'n.', 'The mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gobbet', 'n.', 'A mouthful; a lump; a small piece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gobbet', 'v. t.', 'To swallow greedily; to swallow in gobbets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gobbetly', 'adv.', 'In pieces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gobbing', 'n.', 'The refuse thrown back into the excavation after removing\n   the coal. It is called also gob stuff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gobbing', 'n.', 'The process of packing with waste rock; stowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gobbled', 'imp. & p. p.', 'of Gobble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gobbling', 'p. pr. & vb. n.', 'of Gobble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gobble', 'v. t.', 'To swallow or eat greedily or hastily; to gulp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gobble', 'v. t.', 'To utter (a sound) like a turkey cock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gobble', 'v. i.', 'To eat greedily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gobble', 'v. i.', 'To make a noise like that of a turkey cock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gobble', 'n.', 'A noise made in the throat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gobbler', 'n.', 'A turkey cock; a bubbling Jock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gobelin', 'a.', 'Pertaining to tapestry produced in the so-called Gobelin\n   works, which have been maintained by the French Government since 1667.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gobemouche', 'n.', 'Literally, a fly swallower; hence, once who keeps his\n   mouth open; a boor; a silly and credulous person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gobet', 'n.', 'See Gobbet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Go-between', 'n.', 'An intermediate agent; a broker; a procurer; -- usually\n   in a disparaging sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gobioid', 'a.', 'Like, or pertaining to, the goby, or the genus Gobius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gobioid', 'n.', 'A gobioid fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goblet', 'n.', 'A kind of cup or drinking vessel having a foot or standard,\n   but without a handle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goblin', 'n.', 'An evil or mischievous spirit; a playful or malicious elf;\n   a frightful phantom; a gnome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gobline', 'n.', 'One of the ropes or chains serving as stays for the\n   dolphin striker or the bowsprit; -- called also gobrope and gaubline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goblinize', 'v. t.', 'To transform into a goblin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gobies', 'pl. ', 'of Goby');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goby', 'n.', 'One of several species of small marine fishes of the genus\n   Gobius and allied genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Go-by', 'n.', 'A passing without notice; intentional neglect; thrusting\n   away; a shifting off; adieu; as, to give a proposal the go-by.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gocart', 'n.', 'A framework moving on casters, designed to support children\n   while learning to walk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'God', 'a. & n.', 'Good.');
  INSERT INTO words (word, word_type, description) VALUES  (    'God', 'n.', 'A being conceived of as possessing supernatural power, and to\n   be propitiated by sacrifice, worship, etc.; a divinity; a deity; an\n   object of worship; an idol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'God', 'n.', 'The Supreme Being; the eternal and infinite Spirit, the\n   Creator, and the Sovereign of the universe; Jehovah.');
  INSERT INTO words (word, word_type, description) VALUES  (    'God', 'n.', 'A person or thing deified and honored as the chief good; an\n   object of supreme regard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'God', 'n.', 'Figuratively applied to one who wields great or despotic\n   power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'God', 'v. t.', 'To treat as a god; to idolize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Godchild', 'n.', 'One for whom a person becomes sponsor at baptism, and\n   whom he promises to see educated as a Christian; a godson or\n   goddaughter. See Godfather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goddaughter', 'n.', 'A female for whom one becomes sponsor at baptism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goddess', 'n.', 'A female god; a divinity, or deity, of the female sex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goddess', 'n.', 'A woman of superior charms or excellence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gode', 'a. & n.', 'Good.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Godelich', 'a.', 'Goodly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Godfather', 'n.', 'A man who becomes sponsor for a child at baptism, and\n   makes himself a surety for its Christian training and instruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Godfather', 'v. t.', 'To act as godfather to; to take under ones fostering\n   care.');
  INSERT INTO words (word, word_type, description) VALUES  (    'God-fearing', 'a.', 'Having a reverential and loving feeling towards God;\n   religious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Godhead', 'n.', 'Godship; deity; divinity; divine nature or essence;\n   godhood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Godhead', 'n.', 'The Deity; God; the Supreme Being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Godhead', 'n.', 'A god or goddess; a divinity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Godhood', 'n.', 'Divine nature or essence; deity; godhead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Godild', '', 'A corruption of God yield, i. e., God reward or bless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Godless', 'a.', 'Having, or acknowledging, no God; without reverence for\n   God; impious; wicked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Godlike', 'a.', 'Resembling or befitting a god or God; divine; hence,\n   preeminently good; as, godlike virtue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Godlily', 'adv.', 'Righteously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Godliness', 'n.', 'Careful observance of, or conformity to, the laws of\n   God; the state or quality of being godly; piety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Godling', 'n.', 'A diminutive god.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Godly', 'n.', 'Pious; reverencing God, and his character and laws; obedient\n   to the commands of God from love for, and reverence of, his character;\n   conformed to Gods law; devout; righteous; as, a godly life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Godly', 'adv.', 'Piously; devoutly; righteously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Godlyhead', 'n.', 'Goodness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Godmother', 'n.', 'A woman who becomes sponsor for a child in baptism. See\n   Godfather');
  INSERT INTO words (word, word_type, description) VALUES  (    'Godown', 'n.', 'A warehouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Godroon', 'n.', 'An ornament produced by notching or carving a rounded\n   molding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Godsend', 'n.', 'Something sent by God; an unexpected acquisiton or piece\n   of good fortune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Godship', 'n.', 'The rank or character of a god; deity; divinity; a god or\n   goddess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Godsib', 'n.', 'A gossip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Godson', 'n.', 'A male for whom one has stood sponsor in baptism. See\n   Godfather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Godspeed', 'n.', 'Success; prosperous journeying; -- a contraction of the\n   phrase, \"God speed you.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Godward', 'adv.', 'Toward God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Godwit', 'n.', 'One of several species of long-billed, wading birds of the\n   genus Limosa, and family Tringidae. The European black-tailed godwit\n   (Limosa limosa), the American marbled godwit (L. fedoa), the Hudsonian\n   godwit (L. haemastica), and others, are valued as game birds. Called\n   also godwin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goel', 'a.', 'Yellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goeland', 'n.', 'A white tropical tern (Cygis candida).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goemin', 'n.', 'A complex mixture of several substances extracted from\n   Irish moss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goen', '', 'p. p. of Go.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goer', 'n.', 'One who, or that which, goes; a runner or walker');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goer', 'n.', 'A foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goer', 'n.', 'A horse, considered in reference to his gait; as, a good\n   goer; a safe goer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goety', 'n.', 'Invocation of evil spirits; witchcraft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goff', 'n.', 'A silly clown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goff', 'n.', 'A game. See Golf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goffered', 'imp. & p. p.', 'of Goffer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goffering', 'p. pr. & vb. n.', 'of Goffer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goffer', 'v. t.', 'To plait, flute, or crimp. See Gauffer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gog', 'n.', 'Haste; ardent desire to go.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goggled', 'imp. & p. p.', 'of Goggle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goggling', 'p. pr. & vb. n.', 'of Goggle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goggle', 'v. i.', 'To roll the eyes; to stare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goggle', 'a.', 'Full and rolling, or staring; -- said of the eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goggle', 'v. i.', 'A strained or affected rolling of the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goggle', 'v. i.', 'A kind of spectacles with short, projecting eye tubes,\n   in the front end of which are fixed plain glasses for protecting the\n   eyes from cold, dust, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goggle', 'v. i.', 'Colored glasses for relief from intense light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goggle', 'v. i.', 'A disk with a small aperture, to direct the sight\n   forward, and cure squinting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goggle', 'v. i.', 'Any screen or cover for the eyes, with or without a slit\n   for seeing through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goggled', 'a.', 'Prominent; staring, as the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goggle-eye', 'n.', 'One of two or more species of American fresh-water\n   fishes of the family Centrarchidae, esp. Chaenobryttus antistius, of\n   Lake Michigan and adjacent waters, and Ambloplites rupestris, of the\n   Great Lakes and Mississippi Valley; -- so called from their prominent\n   eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goggle-eye', 'n.', 'The goggler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goggle-eyed', 'a.', 'Having prominent and distorted or rolling eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goggler', 'n.', 'A carangoid oceanic fish (Trachurops crumenophthalmus),\n   having very large and prominent eyes; -- called also goggle-eye,\n   big-eyed scad, and cicharra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goglet', 'n.', 'See Gurglet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Going', 'n.', 'The act of moving in any manner; traveling; as, the going is\n   bad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Going', 'n.', 'Departure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Going', 'n.', 'Pregnancy; gestation; childbearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Going', 'n.', 'Course of life; behavior; doings; ways.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goiter', 'n.', 'Alt. of Goitre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goitre', 'n.', 'An enlargement of the thyroid gland, on the anterior part\n   of the neck; bronchocele. It is frequently associated with cretinism,\n   and is most common in mountainous regions, especially in certain parts\n   of Switzerland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goitered', 'a.', 'Alt. of Goitred');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goitred', 'a.', 'Affected with goiter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goitrous', 'a.', 'Pertaining to the goiter; affected with the goiter; of\n   the nature of goiter or bronchocele.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gold', 'n.', 'Alt. of Goolde');
  INSERT INTO words (word, word_type, description) VALUES  (    'Golde', 'n.', 'Alt. of Goolde');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goolde', 'n.', 'An old English name of some yellow flower, -- the marigold\n   (Calendula), according to Dr. Prior, but in Chaucer perhaps the\n   turnsole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gold', 'v. t.', 'A metallic element, constituting the most precious metal\n   used as a common commercial medium of exchange. It has a characteristic\n   yellow color, is one of the heaviest substances known (specific gravity\n   19.32), is soft, and very malleable and ductile. It is quite\n   unalterable by heat, moisture, and most corrosive agents, and therefore\n   well suited for its use in coin and jewelry. Symbol Au (Aurum). Atomic\n   weight 196.7.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gold', 'v. t.', 'Money; riches; wealth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gold', 'v. t.', 'A yellow color, like that of the metal; as, a flower\n   tipped with gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gold', 'v. t.', 'Figuratively, something precious or pure; as, hearts of\n   gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gold-beaten', 'a.', 'Gilded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gold-beating', 'n.', 'The art or process of reducing gold to extremely thin\n   leaves, by beating with a hammer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gold-bound', 'a.', 'Encompassed with gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goldcrest', 'n.', 'The European golden-crested kinglet (Regulus cristatus,\n   or R. regulus); -- called also golden-crested wren, and golden wren.\n   The name is also sometimes applied to the American golden-crested\n   kinglet. See Kinglet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goldcup', 'n.', 'The cuckoobud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Golden', 'a.', 'Made of gold; consisting of gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Golden', 'a.', 'Having the color of gold; as, the golden grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Golden', 'a.', 'Very precious; highly valuable; excellent; eminently\n   auspicious; as, golden opinions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Golden-eye', 'n.', 'A duck (Glaucionetta clangula), found in Northern\n   Europe, Asia, and America. The American variety (var. Americana) is\n   larger. Called whistler, garrot, gowdy, pied widgeon, whiteside, curre,\n   and doucker. Barrows golden-eye of America (G. Islandica) is less\n   common.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goden ly', 'adv.', 'In golden terms or a golden manner; splendidly;\n   delightfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Golden-rod', 'n.', 'A tall herb (Solidago Virga-aurea), bearing yellow\n   flowers in a graceful elongated cluster. The name is common to all the\n   species of the genus Solidago.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goldfinch', 'n.', 'A beautiful bright-colored European finch (Carduelis\n   elegans). The name refers to the large patch of yellow on the wings.\n   The front of the head and throat are bright red; the nape, with part of\n   the wings and tail, black; -- called also goldspink, goldie, fools\n   coat, drawbird, draw-water, thistle finch, and sweet William.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goldfinch', 'n.', 'The yellow-hammer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goldfinch', 'n.', 'A small American finch (Spinus tristis); the thistle\n   bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goldfinny', 'n.', 'One of two or more species of European labroid fishes\n   (Crenilabrus melops, and Ctenolabrus rupestris); -- called also\n   goldsinny, and goldney.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goldfish', 'n.', 'A small domesticated cyprinoid fish (Carassius auratus);\n   -- so named from its color. It is native of China, and is said to have\n   been introduced into Europe in 1691. It is often kept as an ornament,\n   in small ponds or glass globes. Many varieties are known. Called also\n   golden fish, and golden carp. See Telescope fish, under Telescope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goldfish', 'n.', 'A California marine fish of an orange or red color; the\n   garibaldi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gold-hammer', 'n.', 'The yellow-hammer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goldie', 'n.', 'The European goldfinch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goldie', 'n.', 'The yellow-hammer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goldilocks', 'n.', 'Same as Goldylocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goldin', 'n.', 'Alt. of Golding');
  INSERT INTO words (word, word_type, description) VALUES  (    'Golding', 'n.', 'A conspicuous yellow flower, commonly the corn marigold\n   (Chrysanthemum segetum).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goldless', 'a.', 'Destitute of gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goldney', 'n.', 'See Gilthead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goldseed', 'n.', 'Dogs-tail grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goldsinny', 'n.', 'See Goldfinny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goldsmith', 'n.', 'An artisan who manufactures vessels and ornaments, etc.,\n   of gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goldsmith', 'n.', 'A banker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goldtit', 'n.', 'See Verdin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goldylocks', 'n.', 'A plant of several species of the genus Chrysocoma; --\n   so called from the tufts of yellow flowers which terminate the stems;\n   also, the Ranunculus auricomus, a kind of buttercup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Golet', 'n.', 'The gullet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Golet', 'n.', 'A California trout. See Malma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Golf', 'n.', 'A game played with a small ball and a bat or club crooked at\n   the lower end. He who drives the ball into each of a series of small\n   holes in the ground and brings it into the last hole with the fewest\n   strokes is the winner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Golfer', 'n.', 'One who plays golf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Golgotha', 'n.', 'Calvary. See the Note under Calvary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goliard', 'n.', 'A buffoon in the Middle Ages, who attended rich mens\n   tables to make sport for the guests by ribald stories and songs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goliardery', 'n.', 'The satirical or ribald poetry of the Goliards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goliath beetle', '', 'Any species of Goliathus, a genus of very large and\n   handsome African beetles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goll', 'n.', 'A hand, paw, or claw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goloe-shoe', 'n.', 'A galoche.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Golore', 'n.', 'See Galore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goloshe', 'n.', 'See Galoche.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goltschut', 'n.', 'A small ingot of gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goltschut', 'n.', 'A silver ingot, used in Japan as money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Golyardeys', 'n.', 'A buffoon. See Gollard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goman', 'n.', 'A husband; a master of a family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gomarist', 'n.', 'Alt. of Gomarite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gomarite', 'n.', 'One of the followers of Francis Gomar or Gomarus, a Dutch\n   disciple of Calvin in the 17th century, who strongly opposed the\n   Arminians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gombo', 'n.', 'See Gumbo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gome', 'n.', 'A man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gome', 'n.', 'The black grease on the axle of a cart or wagon wheel; --\n   called also gorm. See Gorm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gomer', 'n.', 'A Hebrew measure. See Homer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gomer', 'n.', 'A conical chamber at the breech of the bore in heavy\n   ordnance, especially in mortars; -- named after the inventor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gommelin', 'n.', 'See Dextrin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gomphiasis', 'n.', 'A disease of the teeth, which causes them to loosen and\n   fall out of their sockets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gomphosis', 'n.', 'A form of union or immovable articulation where a hard\n   part is received into the cavity of a bone, as the teeth into the jaws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gomuti', 'n.', 'A black, fibrous substance resembling horsehair, obtained\n   from the leafstalks of two kinds of palms, Metroxylon Sagu, and Arenga\n   saccharifera, of the Indian islands. It is used for making cordage.\n   Called also ejoo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gon', '', 'imp. & p. p. of Go.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonads', 'pl. ', 'of Gonad');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonad', 'n.', 'One of the masses of generative tissue primitively alike in\n   both sexes, but giving rise to either an ovary or a testis; a\n   generative gland; a germ gland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonakie', 'n.', 'An African timber tree (Acacia Adansonii).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonangia', 'pl. ', 'of Gonangium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonangiums', 'pl. ', 'of Gonangium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonangium', 'n.', 'See Gonotheca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gondola', 'n.', 'A long, narrow boat with a high prow and stern, used in\n   the canals of Venice. A gondola is usually propelled by one or two\n   oarsmen who stand facing the prow, or by poling. A gondola for\n   passengers has a small open cabin amidships, for their protection\n   against the sun or rain. A sumptuary law of Venice required that\n   gondolas should be painted black, and they are customarily so painted\n   now.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gondola', 'n.', 'A flat-bottomed boat for freight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gondola', 'n.', 'A long platform car, either having no sides or with very\n   low sides, used on railroads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gondolet', 'n.', 'A small gondola.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gondolier', 'n.', 'A man who rows a gondola.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gone', '', 'p. p. of Go.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goneness', 'n.', 'A state of exhaustion; faintness, especially as resulting\n   from hunger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonfalon', 'n.', 'Alt. of Gonfanon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonfanon', 'n.', 'The ensign or standard in use by certain princes or\n   states, such as the mediaeval republics of Italy, and in more recent\n   times by the pope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonfanon', 'n.', 'A name popularly given to any flag which hangs from a\n   crosspiece or frame instead of from the staff or the mast itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonfalonier', 'n.', 'He who bears the gonfalon; a standard bearer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonfalonier', 'n.', 'An officer at Rome who bears the standard of the\n   Church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonfalonier', 'n.', 'The chief magistrate of any one of several republics\n   in mediaeveal Italy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonfalonier', 'n.', 'A Turkish general, and standard keeper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gong', 'n.', 'A privy or jakes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gong', 'n.', 'An instrument, first used in the East, made of an alloy of\n   copper and tin, shaped like a disk with upturned rim, and producing,\n   when struck, a harsh and resounding noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gong', 'n.', 'A flat saucerlike bell, rung by striking it with a small\n   hammer which is connected with it by various mechanical devices; a\n   stationary bell, used to sound calls or alarms; -- called also gong\n   bell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goniatite', 'n.', 'One of an extinct genus of fossil cephalopods, allied to\n   the Ammonites. The earliest forms are found in the Devonian formation,\n   the latest, in the Triassic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonidial', 'a.', 'Pertaining to, or containing, gonidia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonidial', 'a.', 'Of or pertaining to the angles of the mouth; as, a\n   gonidial groove of an actinian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonidium', 'n.', 'A special groove or furrow at one or both angles of the\n   mouth of many Anthozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonidia', 'pl. ', 'of Gonidium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonidium', 'n.', 'A component cell of the yellowish green layer in certain\n   lichens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonimia', 'n. pl.', 'Bluish green granules which occur in certain lichens,\n   as Collema, Peltigera, etc., and which replace the more usual gonidia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonimous', 'a.', 'Pertaining to, or containing, gonidia or gonimia, as that\n   part of a lichen which contains the green or chlorophyll-bearing cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goniometer', 'n.', 'An instrument for measuring angles, especially the\n   angles of crystals, or the inclination of planes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goniometric', 'a.', 'Alt. of Goniometrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goniometrical', 'a.', 'Pertaining to, or determined by means of, a\n   goniometer; trigonometric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goniometry', 'n.', 'The art of measuring angles; trigonometry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonoblastid', 'n.', 'A reproductive bud of a hydroid; a simple gonophore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonoblastidia', 'pl. ', 'of Gonoblastidium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonoblastidium', 'n.', 'A blastostyle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonocalyx', 'n.', 'The bell of a sessile gonozooid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonochorism', 'n.', 'Separation of the sexes in different individuals; --\n   opposed to hermaphroditism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonochorism', 'n.', 'In ontogony, differentiation of male and female\n   individuals from embryos having the same rudimentary sexual organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonochorism', 'n.', 'In phylogeny, the evolution of distinct sexes in\n   species previously hermaphrodite or sexless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonococcus', 'n.', 'A vegetable microorganism of the genus Micrococcus,\n   occurring in the secretion in gonorrhea. It is believed by some to\n   constitute the cause of this disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonoph', 'n.', 'A pickpocket or thief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonophore', 'n.', 'A sexual zooid produced as a medusoid bud upon a\n   hydroid, sometimes becoming a free hydromedusa, sometimes remaining\n   attached. See Hydroidea, and Illusts. of Athecata, Campanularian, and\n   Gonosome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonophore', 'n.', 'A lengthened receptacle, bearing the stamens and carpels\n   in a conspicuous manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonorrhea', 'n.', 'Alt. of Gonorrhoea');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonorrhoea', 'n.', 'A contagious inflammatory disease of the genitourinary\n   tract, affecting especially the urethra and vagina, and characterized\n   by a mucopurulent discharge, pain in urination, and chordee; clap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonorrheal', 'a.', 'Alt. of Gonorrhoeal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonorrhoeal', 'a.', 'Of or pertaining to gonorrhea; as, gonorrheal\n   rheumatism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonosome', 'n.', 'The reproductive zooids of a hydroid colony,\n   collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonothec/', 'pl. ', 'of Gonotheca');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonotheca', 'n.', 'A capsule developed on certain hydroids (Thecaphora),\n   inclosing the blastostyle upon which the medusoid buds or gonophores\n   are developed; -- called also gonangium, and teleophore. See Hydroidea,\n   and Illust. of Campanularian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonozooid', 'n.', 'A sexual zooid, or medusoid bud of a hydroid; a\n   gonophore. See Hydroidea, and Illust. of Campanularian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonydial', 'a.', 'Pertaining to the gonys of a birds beak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gonys', 'n.', 'The keel or lower outline of a birds bill, so far as the\n   mandibular rami are united.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goober', 'n.', 'A peanut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Good', 'superl.', 'Possessing desirable qualities; adapted to answer the\n   end designed; promoting success, welfare, or happiness; serviceable;\n   useful; fit; excellent; admirable; commendable; not bad, corrupt, evil,\n   noxious, offensive, or troublesome, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Good', 'superl.', 'Possessing moral excellence or virtue; virtuous; pious;\n   religious; -- said of persons or actions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Good', 'superl.', 'Kind; benevolent; humane; merciful; gracious; polite;\n   propitious; friendly; well-disposed; -- often followed by to or toward,\n   also formerly by unto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Good', 'superl.', 'Serviceable; suited; adapted; suitable; of use; to be\n   relied upon; -- followed especially by for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Good', 'superl.', 'Clever; skillful; dexterous; ready; handy; -- followed\n   especially by at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Good', 'superl.', 'Adequate; sufficient; competent; sound; not fallacious;\n   valid; in a commercial sense, to be depended on for the discharge of\n   obligations incurred; having pecuniary ability; of unimpaired credit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Good', 'superl.', 'Real; actual; serious; as in the phrases in good\n   earnest; in good sooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Good', 'superl.', 'Not small, insignificant, or of no account;\n   considerable; esp., in the phrases a good deal, a good way, a good\n   degree, a good share or part, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Good', 'superl.', 'Not lacking or deficient; full; complete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Good', 'superl.', 'Not blemished or impeached; fair; honorable; unsullied;\n   as in the phrases a good name, a good report, good repute, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Good', 'n.', 'That which possesses desirable qualities, promotes success,\n   welfare, or happiness, is serviceable, fit, excellent, kind,\n   benevolent, etc.; -- opposed to evil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Good', 'n.', 'Advancement of interest or happiness; welfare; prosperity;\n   advantage; benefit; -- opposed to harm, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Good', 'n.', 'Wares; commodities; chattels; -- formerly used in the\n   singular in a collective sense. In law, a comprehensive name for almost\n   all personal property as distinguished from land or real property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Good', 'adv.', 'Well, -- especially in the phrase as good, with a following\n   as expressed or implied; equally well with as much advantage or as\n   little harm as possible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Good', 'v. t.', 'To make good; to turn to good.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Good', 'v. t.', 'To manure; to improve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Good-by', 'n. / interj.', 'Alt. of Good-bye');
  INSERT INTO words (word, word_type, description) VALUES  (    'Good-bye', 'n. / interj.', 'Farewell; a form of address used at parting.\n   See the last Note under By, prep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Good-den', 'interj.', 'A form of salutation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Good-fellowship', 'n.', 'Agreeable companionship; companionableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goodgeon', 'n.', 'Same as Gudgeon, 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Good-humored', 'a.', 'Having a cheerful spirit and demeanor; good-tempered.\n   See Good-natured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Good-humoredly', 'adv.', 'With a cheerful spirit; in a cheerful or\n   good-tempered manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goodish', 'a.', 'Rather good than the contrary; not actually bad;\n   tolerable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goodless', 'a.', 'Having no goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goodlich', 'a.', 'Goodly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goodliness', 'n.', 'Beauty of form; grace; elegance; comeliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Good-looking', 'a.', 'Handsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goodly', 'adv.', 'Excellently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goodly', 'superl.', 'Pleasant; agreeable; desirable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goodly', 'superl.', 'Of pleasing appearance or character; comely; graceful;\n   as, a goodly person; goodly raiment, houses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goodly', 'superl.', 'Large; considerable; portly; as, a goodly number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goodlyhead', 'n.', 'Alt. of Goodlyhood');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goodlyhood', 'n.', 'Goodness; grace; goodliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goodman', 'n.', 'A familiar appellation of civility, equivalent to \"My\n   friend\", \"Good sir\", \"Mister;\" -- sometimes used ironically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goodman', 'n.', 'A husband; the master of a house or family; -- often used\n   in speaking familiarly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Good-natured', 'a.', 'Naturally mild in temper; not easily provoked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Good-naturedly', 'adv.', 'With maldness of temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goodness', 'n.', 'The quality of being good in any of its various senses;\n   excellence; virtue; kindness; benevolence; as, the goodness of timber,\n   of a soil, of food; goodness of character, of disposition, of conduct,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Good now', '', 'An exclamation of wonder, surprise, or entreaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goods', 'n. pl.', 'See Good, n., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goodship', 'n.', 'Favor; grace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Good-tempered', 'a.', 'Having a good temper; not easily vexed. See\n   Good-natured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goodwife', 'n.', 'The mistress of a house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goodies', 'pl. ', 'of Goody');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goody', 'n.', 'A bonbon, cake, or the like; -- usually in the pl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goody', 'n.', 'An American fish; the lafayette or spot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goodies', 'pl. ', 'of Goody');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goody', 'n.', 'Goodwife; -- a low term of civility or sport.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gode-year', 'n.', 'The venereal disease; -- often used as a mild oath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goody-goody', 'a.', 'Mawkishly or weakly good; exhibiting goodness with\n   silliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goodyship', 'n.', 'The state or quality of a goody or goodwife');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gooroo', 'n.', 'Alt. of Guru');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guru', 'n.', 'A spiritual teacher, guide, or confessor amoung the Hindoos.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goosander', 'n.', 'A species of merganser (M. merganser) of Northern Europe\n   and America; -- called also merganser, dundiver, sawbill, sawneb,\n   shelduck, and sheldrake. See Merganser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Geese', 'pl. ', 'of Goose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goose', 'n.', 'Any large web-footen bird of the subfamily Anserinae, and\n   belonging to Anser, Branta, Chen, and several allied genera. See\n   Anseres.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goose', 'n.', 'Any large bird of other related families, resembling the\n   common goose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goose', 'n.', 'A tailors smoothing iron, so called from its handle, which\n   resembles the neck of a goose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goose', 'n.', 'A silly creature; a simpleton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goose', 'n.', 'A game played with counters on a board divided into\n   compartments, in some of which a goose was depicted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gooseberries', 'pl. ', 'of Gooseberry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gooseberry', 'a.', 'Any thorny shrub of the genus Ribes; also, the edible\n   berries of such shrub. There are several species, of which Ribes\n   Grossularia is the one commonly cultivated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gooseberry', 'a.', 'A silly person; a goose cap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goosefish', 'n.', 'See Angler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goosefoot', 'n.', 'A genus of herbs (Chenopodium) mostly annual weeds;\n   pigweed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gooseries', 'pl. ', 'of Goosery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goosery', 'n.', 'A place for keeping geese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goosery', 'n.', 'The characteristics or actions of a goose; silliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goosewing', 'n.', 'One of the clews or lower corners of a course or a\n   topsail when the middle part or the rest of the sail is furled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goosewinged', 'a.', 'Having a \"goosewing.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goosewinged', 'a.', 'Said of a fore-and-aft rigged vessel with foresail set\n   on one side and mainsail on the other; wing and wing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goosish', 'a.', 'Like a goose; foolish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goost', 'n.', 'Ghost; spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goot', 'n.', 'A goat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Go-out', 'n.', 'A sluice in embankments against the sea, for letting out\n   the land waters, when the tide is out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gopher', 'n.', 'One of several North American burrowing rodents of the\n   genera Geomys and Thomomys, of the family Geomyidae; -- called also\n   pocket gopher and pouched rat. See Pocket gopher, and Tucan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gopher', 'n.', 'One of several western American species of the genus\n   Spermophilus, of the family Sciuridae; as, the gray gopher\n   (Spermophilus Franklini) and the striped gopher (S. tridecemlineatus);\n   -- called also striped prairie squirrel, leopard marmot, and leopard\n   spermophile. See Spermophile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gopher', 'n.', 'A large land tortoise (Testudo Carilina) of the Southern\n   United States, which makes extensive burrows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gopher', 'n.', 'A large burrowing snake (Spilotes Couperi) of the Southern\n   United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gopher wood', '', 'A species of wood used in the construction of Noahs\n   ark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goracco', 'n.', 'A paste prepared from tobacco, and smoked in hookahs in\n   Western India.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goral', 'n.', 'An Indian goat antelope (Nemorhedus goral), resembling the\n   chamois.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goramy', 'n.', 'Same as Gourami.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gor-bellied', 'a.', 'Bog-bellied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gor-belly', 'n.', 'A prominent belly; a big-bellied person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorce', 'n.', 'A pool of water to keep fish in; a wear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorcock', 'n.', 'The moor cock, or red grouse. See Grouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorcrow', 'n.', 'The carrion crow; -- called also gercrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gord', 'n.', 'An instrument of gaming; a sort of dice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gordiacea', 'n. pl.', 'A division of nematoid worms, including the\n   hairworms or hair eels (Gordius and Mermis). See Gordius, and\n   Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gordian', 'a.', 'Pertaining to Gordius, king of Phrygia, or to a knot tied\n   by him; hence, intricate; complicated; inextricable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gordian', 'a.', 'Pertaining to the Gordiacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gordian', 'n.', 'One of the Gordiacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gordius', 'n.', 'A genus of long, slender, nematoid worms, parasitic in\n   insects until near maturity, when they leave the insect, and live in\n   water, in which they deposit their eggs; -- called also hair eel,\n   hairworm, and hair snake, from the absurd, but common and widely\n   diffused, notion that they are metamorphosed horsehairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gore', 'n.', 'Dirt; mud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gore', 'n.', 'Blood; especially, blood that after effusion has become thick\n   or clotted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gore', 'v.', 'A wedgeshaped or triangular piece of cloth, canvas, etc.,\n   sewed into a garment, sail, etc., to give greater width at a particular\n   part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gore', 'v.', 'A small traingular piece of land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gore', 'v.', 'One of the abatements. It is made of two curved lines,\n   meeting in an acute angle in the fesse point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gored', 'imp. & p. p.', 'of Gore');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goring', 'p. pr. & vb. n.', 'of Gore');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gore', 'v. t.', 'To pierce or wound, as with a horn; to penetrate with a\n   pointed instrument, as a spear; to stab.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gore', 'v. t.', 'To cut in a traingular form; to piece with a gore; to\n   provide with a gore; as, to gore an apron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorebill', 'n.', 'The garfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorflies', 'pl. ', 'of Gorfly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorfly', 'n.', 'A dung fly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorge', 'n.', 'The throat; the gullet; the canal by which food passes to\n   the stomach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorge', 'n.', 'A narrow passage or entrance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorge', 'n.', 'A defile between mountains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorge', 'n.', 'The entrance into a bastion or other outwork of a fort; --\n   usually synonymous with rear. See Illust. of Bastion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorge', 'n.', 'That which is gorged or swallowed, especially by a hawk or\n   other fowl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorge', 'n.', 'A filling or choking of a passage or channel by an\n   obstruction; as, an ice gorge in a river.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorge', 'n.', 'A concave molding; a cavetto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorge', 'n.', 'The groove of a pulley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorged', 'imp. & p. p.', 'of Gorge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorging', 'p. pr. & vb. n.', 'of Gorge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorge', 'n.', 'To swallow; especially, to swallow with greediness, or in\n   large mouthfuls or quantities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorge', 'n.', 'To glut; to fill up to the throat; to satiate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorge', 'v. i.', 'To eat greedily and to satiety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorged', 'a.', 'Having a gorge or throat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorged', 'a.', 'Bearing a coronet or ring about the neck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorged', 'a.', 'Glutted; fed to the full.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorgelet', 'n.', 'A small gorget, as of a humming bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorgeous', 'n.', 'Imposing through splendid or various colors; showy; fine;\n   magnificent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorgerin', 'n.', 'In some columns, that part of the capital between the\n   termination of the shaft and the annulet of the echinus, or the space\n   between two neck moldings; -- called also neck of the capital, and\n   hypotrachelium. See Illust. of Column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorget', 'n.', 'A piece of armor, whether of chain mail or of plate,\n   defending the throat and upper part of the breast, and forming a part\n   of the double breastplate of the 14th century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorget', 'n.', 'A piece of plate armor covering the same parts and worn\n   over the buff coat in the 17th century, and without other steel armor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorget', 'n.', 'A small ornamental plate, usually crescent-shaped, and of\n   gilded copper, formerly hung around the neck of officers in full\n   uniform in some modern armies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorget', 'n.', 'A ruff worn by women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorget', 'n.', 'A cutting instrument used in lithotomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorget', 'n.', 'A grooved instrunent used in performing various operations;\n   -- called also blunt gorget.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorget', 'n.', 'A crescent-shaped, colored patch on the neck of a bird or\n   mammal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorgon', 'n.', 'One of three fabled sisters, Stheno, Euryale, and Medusa,\n   with snaky hair and of terrific aspect, the sight of whom turned the\n   beholder to stone. The name is particularly given to Medusa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorgon', 'n.', 'Anything very ugly or horrid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorgon', 'n.', 'The brindled gnu. See Gnu.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorgon', 'a.', 'Like a Gorgon; very ugly or terrific; as, a Gorgon face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorgonacea', 'n. pl.', 'See Gorgoniacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorgonean', 'a.', 'See Gorgonian, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorgoneia', 'pl. ', 'of Gorgoneion');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorgoneion', 'n.', 'A mask carved in imitation of a Gorgons head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorgonia', 'n.', 'A genus of Gorgoniacea, formerly very extensive, but now\n   restricted to such species as the West Indian sea fan (Gorgonia\n   flabellum), sea plume (G. setosa), and other allied species having a\n   flexible, horny axis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorgonia', 'n.', 'Any slender branched gorgonian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorgoniacea', 'n. pl.', 'One of the principal divisions of Alcyonaria,\n   including those forms which have a firm and usually branched axis,\n   covered with a porous crust, or c/nenchyma, in which the polyp cells\n   are situated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorgonian', 'a.', 'Pertaining to, or resembling, a Gorgon; terrifying into\n   stone; terrific.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorgonian', 'a.', 'Pertaining to the Gorgoniacea; as, gorgonian coral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorgonian', 'n.', 'One of the Gorgoniacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorgonize', 'v. t.', 'To have the effect of a Gorgon upon; to turn into\n   stone; to petrify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorhen', 'n.', 'The female of the gorcock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorilla', 'n.', 'A large, arboreal, anthropoid ape of West Africa. It is\n   larger than a man, and is remarkable for its massive skeleton and\n   powerful muscles, which give it enormous strength. In some respects its\n   anatomy, more than that of any other ape, except the chimpanzee,\n   resembles that of man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goring', 'n.', 'Alt. of Goring cloth');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goring cloth', 'n.', 'A piece of canvas cut obliquely to widen a sail at\n   the foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorm', 'n.', 'Axle grease. See Gome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorm', 'v. t.', 'To daub, as the hands or clothing, with gorm; to daub with\n   anything sticky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorma', 'n.', 'The European cormorant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gormand', 'n.', 'A greedy or ravenous eater; a luxurious feeder; a\n   gourmand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gormand', 'a.', 'Gluttonous; voracious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gormander', 'n.', 'See Gormand, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gormandism', 'n.', 'Gluttony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gormandized', 'imp. & p. p.', 'of Gormandize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gormandizing', 'p. pr. & vb. n.', 'of Gormandize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gormandize', 'v. i. & t.', 'To eat greedily; to swallow voraciously; to\n   feed ravenously or like a glutton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gormandizer', 'n.', 'A greedy, voracious eater; a gormand; a glutton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goroon shell', '', 'A large, handsome, marine, univalve shell (Triton\n   femorale).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gorse', 'n.', 'Furze. See Furze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gory', 'a.', 'Covered with gore or clotted blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gory', 'a.', 'Bloody; murderous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goshawk', 'n.', 'Any large hawk of the genus Astur, of which many species\n   and varieties are known. The European (Astur palumbarius) and the\n   American (A. atricapillus) are the best known species. They are noted\n   for their powerful flight, activity, and courage. The Australian\n   goshawk (A. Novae-Hollandiae) is pure white.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gosherd', 'n.', 'One who takes care of geese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goslet', 'n.', 'One of several species of pygmy geese, of the genus\n   Nettepus. They are about the size of a teal, and inhabit Africa, India,\n   and Australia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gosling', 'n.', 'A young or unfledged goose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gosling', 'n.', 'A catkin on nut trees and pines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gospel', 'v.', 'Glad tidings; especially, the good news concerning Christ,\n   the Kingdom of God, and salvation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gospel', 'v.', 'One of the four narratives of the life and death of Jesus\n   Christ, written by Matthew, Mark, Luke, and John.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gospel', 'v.', 'A selection from one of the gospels, for use in a religious\n   service; as, the gospel for the day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gospel', 'v.', 'Any system of religious doctrine; sometimes, any system of\n   political doctrine or social philosophy; as, this political gospel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gospel', 'v.', 'Anything propounded or accepted as infallibly true; as,\n   they took his words for gospel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gospel', 'a.', 'Accordant with, or relating to, the gospel; evangelical;\n   as, gospel righteousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gospel', 'v. t.', 'To instruct in the gospel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gospeler', 'n.', 'One of the four evangelists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gospeler', 'n.', 'A follower of Wyclif, the first English religious\n   reformer; hence, a Puritan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gospeler', 'n.', 'A priest or deacon who reads the gospel at the altar\n   during the communion service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gospelized', 'imp. & p. p.', 'of Gospelize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gospelizing', 'p. pr. & vb. n.', 'of Gospelize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gospelize', 'v. t.', 'To form according to the gospel; as, a command\n   gospelized to us.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gospelize', 'v. t.', 'To instruct in the gospel; to evangelize; as, to\n   gospelize the savages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goss', 'n.', 'Gorse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gossamer', 'n.', 'A fine, filmy substance, like cobwebs, floating in the\n   air, in calm, clear weather, especially in autumn. It is seen in\n   stubble fields and on furze or low bushes, and is formed by small\n   spiders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gossamer', 'n.', 'Any very thin gauzelike fabric; also, a thin waterproof\n   stuff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gossamer', 'n.', 'An outer garment, made of waterproof gossamer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gossamery', 'a.', 'Like gossamer; flimsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gossan', 'n.', 'Decomposed rock, usually reddish or ferruginous (owing to\n   oxidized pyrites), forming the upper part of a metallic vein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gossaniferous', 'a.', 'Containing or producing gossan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gossat', 'n.', 'A small British marine fish (Motella tricirrata); -- called\n   also whistler and three-bearded rockling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gossib', 'n.', 'A gossip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gossip', 'n.', 'A sponsor; a godfather or a godmother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gossip', 'n.', 'A friend or comrade; a companion; a familiar and customary\n   acquaintance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gossip', 'n.', 'One who runs house to house, tattling and telling news; an\n   idle tattler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gossip', 'n.', 'The tattle of a gossip; groundless rumor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gossip', 'v. t.', 'To stand sponsor to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gossiped', 'imp. & p. p.', 'of Gossip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gossiping', 'p. pr. & vb. n.', 'of Gossip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gossip', 'v. i.', 'To make merry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gossip', 'v. i.', 'To prate; to chat; to talk much.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gossip', 'v. i.', 'To run about and tattle; to tell idle tales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gossiper', 'n.', 'One given to gossip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gossiprede', 'n.', 'The relationship between a person and his sponsors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gossipry', 'n.', 'Spiritual relationship or affinity; gossiprede; special\n   intimacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gossipry', 'n.', 'Idle talk; gossip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gossipy', 'a.', 'Full of, or given to, gossip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gossoon', 'n.', 'A boy; a servant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gossypium', 'n.', 'A genus of plants which yield the cotton of the arts.\n   The species are much confused. G. herbaceum is the name given to the\n   common cotton plant, while the long-stapled sea-island cotton is\n   produced by G. Barbadense, a shrubby variety. There are several other\n   kinds besides these.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Got', '', 'imp. & p. p. of Get. See Get.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gote', 'n.', 'A channel for water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goter', 'n.', 'a gutter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goth', 'n.', 'One of an ancient Teutonic race, who dwelt between the Elbe\n   and the Vistula in the early part of the Christian era, and who overran\n   and took an important part in subverting the Roman empire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goth', 'n.', 'One who is rude or uncivilized; a barbarian; a rude, ignorant\n   person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gothamist', 'n.', 'A wiseacre; a person deficient in wisdom; -- so called\n   from Gotham, in Nottinghamshire, England, noted for some pleasant\n   blunders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gothamite', 'n.', 'A gothamist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gothamite', 'n.', 'An inhabitant of New York city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gothic', 'a.', 'Pertaining to the Goths; as, Gothic customs; also, rude;\n   barbarous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gothic', 'a.', 'Of or pertaining to a style of architecture with pointed\n   arches, steep roofs, windows large in proportion to the wall spaces,\n   and, generally, great height in proportion to the other dimensions --\n   prevalent in Western Europe from about 1200 to 1475 a. d. See Illust.\n   of Abacus, and Capital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gothic', 'n.', 'The language of the Goths; especially, the language of that\n   part of the Visigoths who settled in Moesia in the 4th century. See\n   Goth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gothic', 'n.', 'A kind of square-cut type, with no hair lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gothic', 'n.', 'The style described in Gothic, a., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gothicism', 'n.', 'A Gothic idiom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gothicism', 'n.', 'Conformity to the Gothic style of architecture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gothicism', 'n.', 'Rudeness of manners; barbarousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gothicized', 'imp. & p. p.', 'of Gothicize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gothicizing', 'p. pr. & vb. n.', 'of Gothicize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gothicize', 'v. t.', 'To make Gothic; to bring back to barbarism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gothite', 'n.', 'Alt. of Goethite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goethite', 'n.', 'A hydrous oxide of iron, occurring in prismatic crystals,\n   also massive, with a fibrous, reniform, or stalactitic structure. The\n   color varies from yellowish to blackish brown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gotten', '', 'p. p. of Get.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gouache', 'n.', 'A method of painting with opaque colors, which have been\n   ground in water and mingled with a preparation of gum; also, a picture\n   thus painted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goud', 'n.', 'Woad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goudron', 'n.', 'a small fascine or fagot, steeped in wax, pitch, and glue,\n   used in various ways, as for igniting buildings or works, or to light\n   ditches and ramparts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gouge', 'n.', 'A chisel, with a hollow or semicylindrical blade, for\n   scooping or cutting holes, channels, or grooves, in wood, stone, etc.;\n   a similar instrument, with curved edge, for turning wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gouge', 'n.', 'A bookbinders tool for blind tooling or gilding, having a\n   face which forms a curve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gouge', 'n.', 'An incising tool which cuts forms or blanks for gloves,\n   envelopes, etc. from leather, paper, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gouge', 'n.', 'Soft material lying between the wall of a vein aud the solid\n   vein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gouge', 'n.', 'The act of scooping out with a gouge, or as with a gouge; a\n   groove or cavity scooped out, as with a gouge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gouge', 'n.', 'Imposition; cheat; fraud; also, an impostor; a cheat; a\n   trickish person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gouged', 'imp. & p. p.', 'of Bouge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gouging', 'p. pr. & vb. n.', 'of Bouge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Bouge', 'v. t.', 'To scoop out with a gouge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Bouge', 'v. t.', 'To scoop out, as an eye, with the thumb nail; to force\n   out the eye of (a person) with the thumb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Bouge', 'v. t.', 'To cheat in a bargain; to chouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gouger', 'n.', 'See Plum Gouger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gougeshell', 'n.', 'A sharp-edged, tubular, marine shell, of the genus\n   Vermetus; also, the pinna. See Vermetus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goujere', 'n.', 'The venereal disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gouland', 'n.', 'See Golding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goulards extract', '', 'An aqueous solution of the subacetate of lead, used\n   as a lotion in cases of inflammation. Goulards cerate is a cerate\n   containing this extract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gour', 'n.', 'A fire worshiper; a Gheber or Gueber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gour', 'n.', 'See Koulan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goura', 'n.', 'One of several species of large, crested ground pigeons of\n   the genus Goura, inhabiting New Guinea and adjacent islands. The Queen\n   Victoria pigeon (Goura Victoria) and the crowned pigeon (G. coronata)\n   are among the beat known species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gourami', 'n.', 'A very largo East Indian freshwater fish (Osphromenus\n   gorami), extensively reared in artificial ponds in tropical countries,\n   and highly valued as a food fish. Many unsuccessful efforts have been\n   made to introduce it into Southern Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gourd', 'n.', 'A fleshy, three-celled, many-seeded fruit, as the melon,\n   pumpkin, cucumber, etc., of the order Cucurbitaceae; and especially the\n   bottle gourd (Lagenaria vulgaris) which occurs in a great variety of\n   forms, and, when the interior part is removed, serves for bottles,\n   dippers, cups, and other dishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gourd', 'n.', 'A dipper or other vessel made from the shell of a gourd;\n   hence, a drinking vessel; a bottle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gourd', 'n.', 'A false die. See Gord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gourd', 'n.', 'Alt. of Gourde');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gourde', 'n.', 'A silver dollar; -- so called in Cuba, Hayti, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gourdiness', 'n.', 'The state of being gourdy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gourd tree', '', 'A tree (the Crescentia Cujete, or calabash tree) of the\n   West Indies and Central America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gourdworm', 'n.', 'The fluke of sheep. See Fluke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gourdy', 'a.', 'Swelled in the legs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gourmand', 'n.', 'A greedy or ravenous eater; a glutton. See Gormand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gourmet', 'n.', 'A connoisseur in eating and drinking; an epicure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gournet', 'n.', 'A fish. See Gurnet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gout', 'n.', 'A drop; a clot or coagulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gout', 'n.', 'A constitutional disease, occurring by paroxysms. It consists\n   in an inflammation of the fibrous and ligamentous parts of the joints,\n   and almost always attacks first the great toe, next the smaller joints,\n   after which it may attack the greater articulations. It is attended\n   with various sympathetic phenomena, particularly in the digestive\n   organs. It may also attack internal organs, as the stomach, the\n   intestines, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gout', 'n.', 'A disease of cornstalks. See Corn fly, under Corn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gout', 'n.', 'Taste; relish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goutily', 'adv.', 'In a gouty manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goutiness', 'n.', 'The state of being gouty; gout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goutweed', 'n.', 'Alt. of Goutwort');
  INSERT INTO words (word, word_type, description) VALUES  (    'Goutwort', 'n.', 'A coarse umbelliferous plant of Europe (Aegopodium\n   Podagraria); -- called also bishops weed, ashweed, and herb gerard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gouty', 'a.', 'Diseased with, or subject to, the gout; as, a gouty person;\n   a gouty joint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gouty', 'a.', 'Pertaining to the gout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gouty', 'a.', 'Swollen, as if from gout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gouty', 'a.', 'Boggy; as, gouty land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gove', 'n.', 'A mow; a rick for hay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Governed', 'imp. & p. p.', 'of Govern');
  INSERT INTO words (word, word_type, description) VALUES  (    'Governing', 'p. pr. & vb. n.', 'of Govern');
  INSERT INTO words (word, word_type, description) VALUES  (    'Govern', 'v. t.', 'To direct and control, as the actions or conduct of men,\n   either by established laws or by arbitrary will; to regulate by\n   authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Govern', 'v. t.', 'To regulate; to influence; to direct; to restrain; to\n   manage; as, to govern the life; to govern a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Govern', 'v. t.', 'To require to be in a particular case; as, a transitive\n   verb governs a noun in the objective case; or to require (a particular\n   case); as, a transitive verb governs the objective case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Govern', 'v. i.', 'To exercise authority; to administer the laws; to have\n   the control.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Governability', 'n.', 'Governableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Governable', 'a.', 'Capable of being governed, or subjected to authority;\n   controllable; manageable; obedient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Governableness', 'n.', 'The quality of being governable; manageableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Governal', 'n.', 'Alt. of Governail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Governail', 'n.', 'Management; mastery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Governance', 'n.', 'Exercise of authority; control; government;\n   arrangement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Governante', 'n.', 'A governess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Governess', 'n.', 'A female governor; a woman invested with authority to\n   control and direct; especially, one intrusted with the care and\n   instruction of children, -- usually in their homes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Governing', 'a.', 'Holding the superiority; prevalent; controlling; as, a\n   governing wind; a governing party in a state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Governing', 'a.', 'Requiring a particular case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Government', 'n.', 'The act of governing; the exercise of authority; the\n   administration of laws; control; direction; regulation; as, civil,\n   church, or family government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Government', 'n.', 'The mode of governing; the system of polity in a state;\n   the established form of law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Government', 'n.', 'The right or power of governing; authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Government', 'n.', 'The person or persons authorized to administer the\n   laws; the ruling power; the administration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Government', 'n.', 'The body politic governed by one authority; a state;\n   as, the governments of Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Government', 'n.', 'Management of the limbs or body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Government', 'n.', 'The influence of a word in regard to construction,\n   requiring that another word should be in a particular case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Governmental', 'a.', 'Pertaining to government; made by government; as,\n   governmental duties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Governor', 'n.', 'One who governs; especially, one who is invested with the\n   supreme executive authority in a State; a chief ruler or magistrate;\n   as, the governor of Pennsylvania.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Governor', 'n.', 'One who has the care or guardianship of a young man; a\n   tutor; a guardian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Governor', 'n.', 'A pilot; a steersman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Governor', 'n.', 'A contrivance applied to steam engines, water wheels, and\n   other machinery, to maintain nearly uniform speed when the resistances\n   and motive force are variable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Governor general', '', 'A governor who has lieutenant or deputy governors\n   under him; as, the governor general of Canada, of India.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Governorship', 'n.', 'The office of a governor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gowan', 'n.', 'The daisy, or mountain daisy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gowan', 'n.', 'Decomposed granite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gowany', 'a.', 'Having, abounding in, or decked with, daisies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gowd', 'n.', 'Gold; wealth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gowden', 'a.', 'Golden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gowdie', 'n.', 'See Dragont.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gowdnook', 'n.', 'The saury pike; -- called also gofnick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gowk', 'v. t.', 'To make a, booby of one); to stupefy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gowk', 'n.', 'The European cuckoo; -- called also gawky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gowk', 'n.', 'A simpleton; a gawk or gawky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gowl', 'v. i.', 'To howl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gown', 'n.', 'A loose, flowing upper garment');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gown', 'n.', 'The ordinary outer dress of a woman; as, a calico or silk\n   gown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gown', 'n.', 'The official robe of certain professional men and scholars,\n   as university students and officers, barristers, judges, etc.; hence,\n   the dress of peace; the dress of civil officers, in distinction from\n   military.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gown', 'n.', 'A loose wrapper worn by gentlemen within doors; a dressing\n   gown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gown', 'n.', 'Any sort of dress or garb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gowned', 'p. a.', 'Dressed in a gown; clad.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-men', 'pl. ', 'of Gownman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gownsman', 'n.', 'Alt. of Gownman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gownman', 'n.', 'One whose professional habit is a gown, as a divine or\n   lawyer, and particularly a member of an English university; hence, a\n   civilian, in distinction from a soldier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gozzard', 'n.', 'See Gosherd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graafian', 'a.', 'Pertaining to, or discovered by, Regnier de Graaf, a\n   Dutch physician.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graal', 'n.', 'See Grail., a dish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grab', 'n.', 'A vessel used on the Malabar coast, having two or three\n   masts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grabbed', 'imp. & p. p.', 'of Grab');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grabbing', 'p. pr. & vb. n.', 'of Grab');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grab', 'v. t. & i.', 'To gripe suddenly; to seize; to snatch; to clutch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grab', 'n.', 'A sudden grasp or seizure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grab', 'n.', 'An instrument for clutching objects for the purpose of\n   raising them; -- specially applied to devices for withdrawing drills,\n   etc., from artesian and other wells that are drilled, bored, or driven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grabber', 'n.', 'One who seizes or grabs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grabbled', 'imp. & p. p.', 'of Grabble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grabbling', 'p. pr. & vb. n.', 'of Grabble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grabble', 'v. i.', 'To grope; to feel with the hands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grabble', 'v. i.', 'To lie prostrate on the belly; to sprawl on the ground;\n   to grovel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grace', 'n.', 'The exercise of love, kindness, mercy, favor; disposition to\n   benefit or serve another; favor bestowed or privilege conferred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grace', 'n.', 'The divine favor toward man; the mercy of God, as\n   distinguished from His justice; also, any benefits His mercy imparts;\n   divine love or pardon; a state of acceptance with God; enjoyment of the\n   divine favor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grace', 'n.', 'The prerogative of mercy execised by the executive, as\n   pardon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grace', 'n.', 'The same prerogative when exercised in the form of equitable\n   relief through chancery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grace', 'n.', 'Fortune; luck; -- used commonly with hard or sorry when it\n   means misfortune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grace', 'n.', 'Inherent excellence; any endowment or characteristic fitted\n   to win favor or confer pleasure or benefit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grace', 'n.', 'Beauty, physical, intellectual, or moral; loveliness;\n   commonly, easy elegance of manners; perfection of form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grace', 'n.', 'Graceful and beautiful females, sister goddesses,\n   represented by ancient writers as the attendants sometimes of Apollo\n   but oftener of Venus. They were commonly mentioned as three in number;\n   namely, Aglaia, Euphrosyne, and Thalia, and were regarded as the\n   inspirers of the qualities which give attractiveness to wisdom, love,\n   and social intercourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grace', 'n.', 'The title of a duke, a duchess, or an archbishop, and\n   formerly of the king of England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grace', 'n.', 'Thanks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grace', 'n.', 'A petition for grace; a blessing asked, or thanks rendered,\n   before or after a meal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grace', 'n.', 'Ornamental notes or short passages, either introduced by the\n   performer, or indicated by the composer, in which case the notation\n   signs are called grace notes, appeggiaturas, turns, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grace', 'n.', 'An act, vote, or decree of the government of the\n   institution; a degree or privilege conferred by such vote or decree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grace', 'n.', 'A play designed to promote or display grace of motion. It\n   consists in throwing a small hoop from one player to another, by means\n   of two sticks in the hands of each. Called also grace hoop or hoops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graced', 'imp. & p. p.', 'of Grace');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gracing', 'p. pr. & vb. n.', 'of Grace');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grace', 'v. t.', 'To adorn; to decorate; to embellish and dignify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grace', 'v. t.', 'To dignify or raise by an act of favor; to honor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grace', 'v. t.', 'To supply with heavenly grace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grace', 'v. t.', 'To add grace notes, cadenzas, etc., to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graced', 'a.', 'Endowed with grace; beautiful; full of graces; honorable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graceful', 'a.', 'Displaying grace or beauty in form or action; elegant;\n   easy; agreeable in appearance; as, a graceful walk, deportment,\n   speaker, air, act, speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graceless', 'a.', 'Wanting in grace or excellence; departed from, or\n   deprived of, divine grace; hence, depraved; corrupt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graceless', 'a.', 'Unfortunate. Cf. Grace, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gracile', 'a.', 'Alt. of Gracillent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gracillent', 'a.', 'Slender; thin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gracility', 'n.', 'State of being gracilent; slenderness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gracious', 'a.', 'Abounding in grace or mercy; manifesting love,. or\n   bestowing mercy; characterized by grace; beneficent; merciful; disposed\n   to show kindness or favor; condescending; as, his most gracious\n   majesty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gracious', 'a.', 'Abounding in beauty, loveliness, or amiability; graceful;\n   excellent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gracious', 'a.', 'Produced by divine grace; influenced or controlled by the\n   divine influence; as, gracious affections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graciously', 'adv.', 'In a gracious manner; courteously; benignantly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graciously', 'adv.', 'Fortunately; luckily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graciousness', 'n.', 'Quality of being gracious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grackle', 'n.', 'One of several American blackbirds, of the family\n   Icteridae; as, the rusty grackle (Scolecophagus Carolinus); the\n   boat-tailed grackle (see Boat-tail); the purple grackle (Quiscalus\n   quiscula, or Q. versicolor). See Crow blackbird, under Crow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grackle', 'n.', 'An Asiatic bird of the genus Gracula. See Myna.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gradate', 'v. t.', 'To grade or arrange (parts in a whole, colors in\n   painting, etc.), so that they shall harmonize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gradate', 'v. t.', 'To bring to a certain strength or grade of\n   concentration; as, to gradate a saline solution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gradation', 'n.', 'The act of progressing by regular steps or orderly\n   arrangement; the state of being graded or arranged in ranks; as, the\n   gradation of castes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gradation', 'n.', 'The act or process of bringing to a certain grade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gradation', 'n.', 'Any degree or relative position in an order or series.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gradation', 'n.', 'A gradual passing from one tint to another or from a\n   darker to a lighter shade, as in painting or drawing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gradation', 'n.', 'A diatonic ascending or descending succession of chords.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gradation', 'v. t.', 'To form with gradations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gradational', 'a.', 'By regular steps or gradations; of or pertaining to\n   gradation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gradatory', 'a.', 'Proceeding step by step, or by gradations; gradual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gradatory', 'a.', 'Suitable for walking; -- said of the limbs of an animal\n   when adapted for walking on land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gradatory', 'n.', 'A series of steps from a cloister into a church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grade', 'n.', 'A step or degree in any series, rank, quality, order;\n   relative position or standing; as, grades of military rank; crimes of\n   every grade; grades of flour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grade', 'n.', 'The rate of ascent or descent; gradient; deviation from a\n   level surface to an inclined plane; -- usually stated as so many feet\n   per mile, or as one foot rise or fall in so many of horizontal\n   distance; as, a heavy grade; a grade of twenty feet per mile, or of 1\n   in 264.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grade', 'n.', 'A graded ascending, descending, or level portion of a road;\n   a gradient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grade', 'n.', 'The result of crossing a native stock with some better\n   breed. If the crossbreed have more than three fourths of the better\n   blood, it is called high grade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graded', 'imp. & p. p.', 'of Grade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grading', 'p. pr. & vb. n.', 'of Grade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grade', 'v. t.', 'To arrange in order, steps, or degrees, according to\n   size, quality, rank, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grade', 'v. t.', 'To reduce to a level, or to an evenly progressive ascent,\n   as the line of a canal or road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grade', 'v. t.', 'To cross with some better breed; to improve the blood of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gradely', 'a.', 'Decent; orderly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gradely', 'adv.', 'Decently; in order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grader', 'n.', 'One who grades, or that by means of which grading is done\n   or facilitated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gradient', 'a.', 'Moving by steps; walking; as, gradient automata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gradient', 'a.', 'Rising or descending by regular degrees of inclination;\n   as, the gradient line of a railroad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gradient', 'a.', 'Adapted for walking, as the feet of certain birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gradient', 'n.', 'The rate of regular or graded ascent or descent in a\n   road; grade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gradient', 'n.', 'A part of a road which slopes upward or downward; a\n   portion of a way not level; a grade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gradient', 'n.', 'The rate of increase or decrease of a variable magnitude,\n   or the curve which represents it; as, a thermometric gradient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gradin', 'n.', 'Alt. of Gradine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gradine', 'n.', 'Any member like a step, as the raised back of an altar or\n   the like; a set raised over another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gradine', 'n.', 'A toothed chised by sculptors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grading', 'n.', 'The act or method of arranging in or by grade, or of\n   bringing, as the surface of land or a road, to the desired level or\n   grade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gradinos', 'pl. ', 'of Gradino');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gradino', 'n.', 'A step or raised shelf, as above a sideboard or altar. Cf.\n   Superaltar, and Gradin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gradual', 'n.', 'Proceeding by steps or degrees; advancing, step by step,\n   as in ascent or descent or from one state to another; regularly\n   progressive; slow; as, a gradual increase of knowledge; a gradual\n   decline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gradual', 'n.', 'An antiphon or responsory after the epistle, in the Mass,\n   which was sung on the steps, or while the deacon ascended the steps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gradual', 'n.', 'A service book containing the musical portions of the\n   Mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gradual', 'n.', 'A series of steps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graduality', 'n.', 'The state of being gradual; gradualness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gradually', 'adv.', 'In a gradual manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gradually', 'adv.', 'In degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gradualness', 'n.', 'The quality or state of being gradual; regular\n   progression or gradation; slowness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graduated', 'imp. & p. p.', 'of Graduate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graduating', 'p. pr. & vb. n.', 'of Graduate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graduate', 'n.', 'To mark with degrees; to divide into regular steps,\n   grades, or intervals, as the scale of a thermometer, a scheme of\n   punishment or rewards, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graduate', 'n.', 'To admit or elevate to a certain grade or degree; esp.,\n   in a college or university, to admit, at the close of the course, to an\n   honorable standing defined by a diploma; as, he was graduated at Yale\n   College.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graduate', 'n.', 'To prepare gradually; to arrange, temper, or modify by\n   degrees or to a certain degree; to determine the degrees of; as, to\n   graduate the heat of an oven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graduate', 'n.', 'To bring to a certain degree of consistency, by\n   evaporation, as a fluid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graduate', 'v. i.', 'To pass by degrees; to change gradually; to shade off;\n   as, sandstone which graduates into gneiss; carnelian sometimes\n   graduates into quartz.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graduate', 'v. i.', 'To taper, as the tail of certain birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graduate', 'v. i.', 'To take a degree in a college or university; to become\n   a graduate; to receive a diploma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graduate', 'n.', 'One who has received an academical or professional\n   degree; one who has completed the prescribed course of study in any\n   school or institution of learning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graduate', 'n.', 'A graduated cup, tube, or flask; a measuring glass used\n   by apothecaries and chemists. See under Graduated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graduate', 'n. & v.', 'Arranged by successive steps or degrees; graduated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graduated', 'a.', 'Marked with, or divided into, degrees; divided into\n   grades.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graduated', 'a.', 'Tapered; -- said of a birds tail when the outer\n   feathers are shortest, and the others successively longer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graduateship', 'n.', 'State of being a graduate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graduation', 'n.', 'The act of graduating, or the state of being graduated;\n   as, graduation of a scale; graduation at a college; graduation in\n   color; graduation by evaporation; the graduation of a birds tail, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graduation', 'n.', 'The marks on an instrument or vessel to indicate\n   degrees or quantity; a scale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graduation', 'n.', 'The exposure of a liquid in large surfaces to the air,\n   so as to hasten its evaporation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graduator', 'n.', 'One who determines or indicates graduation; as, a\n   graduator of instruments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graduator', 'n.', 'An instrument for dividing any line, right or curve,\n   into small, regular intervals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graduator', 'n.', 'An apparatus for diffusing a solution, as brine or\n   vinegar, over a large surface, for exposure to the air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gradus', 'n.', 'A dictionary of prosody, designed as an aid in writing\n   Greek or Latin poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graf', 'n.', 'A German title of nobility, equivalent to earl in English, or\n   count in French. See Earl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graff', 'n.', 'A steward; an overseer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graff', 'n. & v.', 'See Graft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graffage', 'n.', 'The scarp of a ditch or moat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graffer', 'n.', 'a notary or scrivener.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graffiti', 'n. pl.', 'Inscriptions, figure drawings, etc., found on the\n   walls of ancient sepulchers or ruins, as in the Catacombs, or at\n   Pompeii.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graft', 'n.', 'A small shoot or scion of a tree inserted in another tree,\n   the stock of which is to support and nourish it. The two unite and\n   become one tree, but the graft determines the kind of fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graft', 'n.', 'A branch or portion of a tree growing from such a shoot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graft', 'n.', 'A portion of living tissue used in the operation of\n   autoplasty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grafted', 'imp. & p. p.', 'of Graft');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grafting', 'p. pr. & vb. n.', 'of Graft');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graft', 'n.', 'To insert (a graft) in a branch or stem of another tree; to\n   propagate by insertion in another stock; also, to insert a graft upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graft', 'n.', 'To implant a portion of (living flesh or akin) in a lesion\n   so as to form an organic union.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graft', 'n.', 'To join (one thing) to another as if by grafting, so as to\n   bring about a close union.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graft', 'n.', 'To cover, as a ring bolt, block strap, splicing, etc., with\n   a weaving of small cord or rope-yarns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graft', 'v. i.', 'To insert scions from one tree, or kind of tree, etc.,\n   into another; to practice grafting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grafter', 'n.', 'One who inserts scions on other stocks, or propagates\n   fruit by ingrafting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grafter', 'n.', 'An instrument by which grafting is facilitated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grafter', 'n.', 'The original tree from which a scion has been taken for\n   grafting upon another tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grafting', 'n.', 'The act or method of weaving a cover for a ring, rope\n   end, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grafting', 'n.', 'The transplanting of a portion of flesh or skin to a\n   denuded surface; autoplasty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grafting', 'n.', 'A scarfing or endwise attachment of one timber to\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graham bread', '', 'Bread made of unbolted wheat flour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grahamite', 'n.', 'One who follows the dietetic system of Graham.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grail', 'n.', 'A book of offices in the Roman Catholic Church; a gradual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grail', 'n.', 'A broad, open dish; a chalice; -- only used of the Holy\n   Grail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grail', 'n.', 'Small particles of earth; gravel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grail', 'n.', 'One of the small feathers of a hawk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graille', 'n.', 'A halfround single-cut file or fioat, having one curved\n   face and one straight face, -- used by comb makers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grain', 'v. & n.', 'See Groan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grain', 'n.', 'A single small hard seed; a kernel, especially of those\n   plants, like wheat, whose seeds are used for food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grain', 'n.', 'The fruit of certain grasses which furnish the chief food of\n   man, as corn, wheat, rye, oats, etc., or the plants themselves; -- used\n   collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grain', 'n.', 'Any small, hard particle, as of sand, sugar, salt, etc.;\n   hence, any minute portion or particle; as, a grain of gunpowder, of\n   pollen, of starch, of sense, of wit, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grain', 'n.', 'The unit of the English system of weights; -- so called\n   because considered equal to the average of grains taken from the middle\n   of the ears of wheat. 7,000 grains constitute the pound avoirdupois,\n   and 5,760 grains the pound troy. A grain is equal to .0648 gram. See\n   Gram.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grain', 'n.', 'A reddish dye made from the coccus insect, or kermes; hence,\n   a red color of any tint or hue, as crimson, scarlet, etc.; sometimes\n   used by the poets as equivalent to Tyrian purple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grain', 'n.', 'The composite particles of any substance; that arrangement\n   of the particles of any body which determines its comparative roughness\n   or hardness; texture; as, marble, sugar, sandstone, etc., of fine\n   grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grain', 'n.', 'The direction, arrangement, or appearance of the fibers in\n   wood, or of the strata in stone, slate, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grain', 'n.', 'The fiber which forms the substance of wood or of any\n   fibrous material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grain', 'n.', 'The hair side of a piece of leather, or the marking on that\n   side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grain', 'n.', 'The remains of grain, etc., after brewing or distillation;\n   hence, any residuum. Also called draff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grain', 'n.', 'A rounded prominence on the back of a sepal, as in the\n   common dock. See Grained, a., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grain', 'a.', 'Temper; natural disposition; inclination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grain', 'a.', 'A sort of spice, the grain of paradise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grained', 'imp. & p. p.', 'of Grain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graining.', 'p. pr. & vb. n.', 'of Grain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grain', 'v. t.', 'To paint in imitation of the grain of wood, marble, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grain', 'v. t.', 'To form (powder, sugar, etc.) into grains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grain', 'v. t.', 'To take the hair off (skins); to soften and raise the\n   grain of (leather, etc.).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grain', 'n.', 'To yield fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grain', 'n.', 'To form grains, or to assume a granular ferm, as the result\n   of crystallization; to granulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grain', 'n.', 'A branch of a tree; a stalk or stem of a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grain', 'n.', 'A tine, prong, or fork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grain', 'n.', 'One the branches of a valley or of a river.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grain', 'n.', 'An iron first speak or harpoon, having four or more barbed\n   points.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grain', 'n.', 'A blade of a sword, knife, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grain', 'n.', 'A thin piece of metal, used in a mold to steady a core.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grained', 'a.', 'Having a grain; divided into small particles or grains;\n   showing the grain; hence, rough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grained', 'a.', 'Dyed in grain; ingrained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grained', 'a.', 'Painted or stained in imitation of the grain of wood,\n   marble, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grained', 'a.', 'Having tubercles or grainlike processes, as the petals or\n   sepals of some flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grainer', 'n.', 'An infusion of pigeons dung used by tanners to neutralize\n   the effects of lime and give flexibility to skins; -- called also\n   grains and bate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grainer', 'n.', 'A knife for taking the hair off skins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grainer', 'n.', 'One who paints in imitation of the grain of wood, marble,\n   etc.; also, the brush or tool used in graining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grainfield', 'n.', 'A field where grain is grown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graining', 'n.', 'Indentation; roughening; milling, as on edges of coins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graining', 'n.', 'A process in dressing leather, by which the skin is\n   softened and the grain raised.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graining', 'n.', 'Painting or staining, in imitation of the grain of wood,\n   atone, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graining', 'n.', 'The process of separating soap from spent lye, as with\n   salt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graining', 'n.', 'A small European fresh-water fish (Leuciscus vulgaris); -\n   called also dobule, and dace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grains', 'n. pl.', 'See 5th Grain, n., 2 (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grains', 'n.', 'Pigeons dung used in tanning. See Grainer. n., 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grainy', 'a.', 'Resembling grains; granular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graip', 'n.', 'A dungfork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graith', 'v. t.', 'See Greith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graith', 'n.', 'Furniture; apparatus or accouterments for work, traveling,\n   war, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grakle', 'n.', 'See Grackle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grallae', 'n. pl.', 'An order of birds which formerly included all the\n   waders. By later writers it is usually restricted to the sandpipers,\n   plovers, and allied forms; -- called also Grallatores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grallatores', 'n. pl.', 'See Grallae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grallatorial', 'a.', 'Alt. of Grallatory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grallatory', 'a.', 'Of or pertaining to the Grallatores, or waders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grallic', 'a.', 'Pertaining to the Grallae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gralline', 'a.', 'Of or pertaining to the Grallae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gralloch', 'n.', 'Offal of a deer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gralloch', 'v. t.', 'To remove the offal from (a deer).');
  INSERT INTO words (word, word_type, description) VALUES  (    '-gram', '', 'A suffix indicating something drawn or written, a drawing,\n   writing; -- as, monogram, telegram, chronogram.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gram', 'a.', 'Angry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gram', 'n.', 'The East Indian name of the chick-pea (Cicer arietinum) and\n   its seeds; also, other similar seeds there used for food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gram', 'n.', 'Alt. of Gramme');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gramme', 'n.', 'The unit of weight in the metric system. It was intended to\n   be exactly, and is very nearly, equivalent to the weight in a vacuum of\n   one cubic centimeter of pure water at its maximum density. It is equal\n   to 15.432 grains. See Grain, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grama grass', '', 'The name of several kinds of pasture grasses found in\n   the Western United States, esp. the Bouteloua oligostachya.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gramarye', 'n.', 'Necromancy; magic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gramashes', 'n. pl.', 'Gaiters reaching to the knee; leggings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grame', 'a.', 'Anger; wrath; scorn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grame', 'a.', 'Sorrow; grief; misery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gramercy', 'interj.', 'A word formerly used to express thankfulness, with\n   surprise; many thanks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graminaceous', 'a.', 'Pertaining to, or resembling, the grasses;\n   gramineous; as, graminaceous plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gramineal', 'a.', 'Gramineous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gramineous', 'a.', 'Like, Or pertaining to, grass. See Grass, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graminifolious', 'a.', 'Bearing leaves resembling those of grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graminivorous', 'a.', 'Feeding or subsisting on grass, and the like food;\n   -- said of horses, cattle, and other animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grammalogue', 'n.', 'Literally, a letter word; a word represented by a\n   logogram; as, it, represented by |, that is, t. pitman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grammar', 'n.', 'The science which treats of the principles of language;\n   the study of forms of speech, and their relations to one another; the\n   art concerned with the right use aud application of the rules of a\n   language, in speaking or writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grammar', 'n.', 'The art of speaking or writing with correctness or\n   according to established usage; speech considered with regard to the\n   rules of a grammar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grammar', 'n.', 'A treatise on the principles of language; a book\n   containing the principles and rules for correctness in speaking or\n   writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grammar', 'n.', 'treatise on the elements or principles of any science; as,\n   a grammar of geography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grammar', 'v. i.', 'To discourse according to the rules of grammar; to use\n   grammar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grammarian', 'n.', 'One versed in grammar, or the construction of\n   languages; a philologist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grammarian', 'n.', 'One who writes on, or teaches, grammar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grammarianism', 'n.', 'The principles, practices, or peculiarities of\n   grammarians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grammarless', 'a.', 'Without grammar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grammates', 'n. pl.', 'Rudiments; first principles, as of grammar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grammatic', 'a.', 'Grammatical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grammatical', 'a.', 'Of or pertaining to grammar; of the nature of grammar;\n   as, a grammatical rule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grammatical', 'a.', 'According to the rules of grammar; grammatically\n   correct; as, the sentence is not grammatical; the construction is not\n   grammatical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grammaticaster', 'n.', 'A petty grammarian; a grammatical pedant or\n   pretender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grammatication', 'n.', 'A principle of grammar; a grammatical rule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grammaticism', 'n.', 'A point or principle of grammar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grammaticized', 'imp. & p. p.', 'of Grammaticize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grammaticizing', 'p. pr. & vb. n.', 'of Grammaticize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grammaticize', 'v. t.', 'To render grammatical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grammatist', 'n.', 'A petty grammarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gramme', 'n.', 'Same as Gram the weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gramme machine', '', 'A kind of dynamo-electric machine; -- so named from\n   its French inventor, M. Gramme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grampuses', 'pl. ', 'of Grampus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grampus', 'n.', 'A toothed delphinoid cetacean, of the genus Grampus, esp.\n   G. griseus of Europe and America, which is valued for its oil. It grows\n   to be fifteen to twenty feet long; its color is gray with white\n   streaks. Called also cowfish. The California grampus is G. Stearnsii.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grampus', 'n.', 'A kind of tongs used in a bloomery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granade', 'n.', 'Alt. of Granado');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granado', 'n.', 'See Grenade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granadilla', 'n.', 'The fruit of certain species of passion flower (esp.\n   Passiflora quadrangularis) found in Brazil and the West Indies. It is\n   as large as a childs head, and is a good dessert fruit. The fruit of\n   Passiflora edulis is used for flavoring ices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granaries', 'pl. ', 'of Granary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granary', 'n.', 'A storehouse or repository for grain, esp. after it is\n   thrashed or husked; a cornbouse; also (Fig.), a region fertile in\n   grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granate', 'n.', 'See Garnet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granatin', 'n.', 'Mannite; -- so called because found in the pomegranate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granatite', 'n.', 'See Staurolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grand', 'superl.', 'Of large size or extent; great; extensive; hence,\n   relatively great; greatest; chief; principal; as, a grand mountain; a\n   grand army; a grand mistake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grand', 'superl.', 'Great in size, and fine or imposing in appearance or\n   impression; illustrious, dignifled, or noble (said of persons);\n   majestic, splendid, magnificent, or sublime (said of things); as, a\n   grand monarch; a grand lord; a grand general; a grand view; a grand\n   conception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grand', 'superl.', 'Having higher rank or more dignity, size, or importance\n   than other persons or things of the same name; as, a grand lodge; a\n   grand vizier; a grand piano, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grand', 'superl.', 'Standing in the second or some more remote degree of\n   parentage or descent; -- generalIy used in composition; as,\n   grandfather, grandson, grandchild, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grandam', 'n.', 'An old woman; specifically, a grandmother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grandaunt', 'n.', 'The aunt of ones father or mother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grandchild', 'n.', 'A sons or daughters child; a child in the second\n   degree of descent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granddaughter', 'n.', 'The daughter of ones son or daughter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grandee', 'n.', 'A man of elevated rank or station; a nobleman. In Spain, a\n   nobleman of the first rank, who may be covered in the kings presence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grandeeship', 'n.', 'The rank or estate of a grandee; lordship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grandeur', 'n.', 'The state or quality of being grand; vastness; greatness;\n   splendor; magnificence; stateliness; sublimity; dignity; elevation of\n   thought or expression; nobility of action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grandevity', 'n.', 'Great age; long life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grandevous', 'a.', 'Of great age; aged; longlived.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grand-ducal', 'a.', 'Of or pertaining to a grand duke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grandfather', 'n.', 'A fathers or mothers father; an ancestor in the next\n   degree above the father or mother in lineal ascent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grandfatherly', 'a.', 'Like a grandfather in age or manner; kind;\n   benignant; indulgent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grandific', 'a.', 'Making great.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grandiloquence', 'n.', 'The use of lofty words or phrases; bombast; --\n   usually in a bad sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grandiloquent', 'a.', 'Speaking in a lofty style; pompous; bombastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grandiloquous', 'a.', 'Grandiloquent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grandinous', 'a.', 'Consisting of hail; abounding in hail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grandiose', 'a.', 'Impressive or elevating in effect; imposing; splendid;\n   striking; -- in a good sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grandiose', 'a.', 'Characterized by affectation of grandeur or splendor;\n   flaunting; turgid; bombastic; -- in a bad sense; as, a grandiose style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grandiosity', 'n.', 'The state or quality of being grandiose,');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grandity', 'n.', 'Grandness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grandly', 'adv.', 'In a grand manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grandma', 'n.', 'Alt. of Grandmamma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grandmamma', 'n.', 'A grandmother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grand mercy', '', 'See Gramercy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grandmother', 'n.', 'The mother of ones father or mother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grandmotherly', 'a.', 'Like a grandmother in age or manner; kind;\n   indulgent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grandnephew', 'n.', 'The grandson of ones brother or sister.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grandness', 'n.', 'Grandeur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grandniece', 'n.', 'The granddaughter of ones brother or sister.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grandpa', 'n.', 'Alt. of Grandpapa');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grandpapa', 'n.', 'A grandfather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grandsire', 'n.', 'Specifically, a grandfather; more generally, any\n   ancestor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grandson', 'n.', 'A sons or daughters son.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granduncle', 'n.', 'A fathers or mothers uncle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grane', 'v. & n.', 'See Groan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grange', 'n.', 'A building for storing grain; a granary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grange', 'n.', 'A farmhouse, with the barns and other buildings for farming\n   purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grange', 'n.', 'A farmhouse of a monastery, where the rents and tithes,\n   paid in grain, were deposited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grange', 'n.', 'A farm; generally, a farm with a house at a distance from\n   neighbors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grange', 'n.', 'An association of farmers, designed to further their\n   interests, aud particularly to bring producers and consumers, farmers\n   and manufacturers, into direct commercial relations, without\n   intervention of middlemen or traders. The first grange was organized in\n   1867.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granger', 'n.', 'A farm steward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granger', 'n.', 'A member of a grange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grangerism', 'n.', 'The practice of illustrating a particular book by\n   engravings collected from other books.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grangerite', 'n.', 'One who collects illustrations from various books for\n   the decoration of one book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grangerize', 'v. t. & i.', 'To collect (illustrations from books) for\n   decoration of other books.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graniferous', 'a.', 'Bearing grain, or seeds like grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graniform', 'a.', 'Formed like of corn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granilla', 'n.', 'Small grains or dust of cochineal or the coccus insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granite', 'n.', 'A crystalline, granular rock, consisting of quartz,\n   feldspar, and mica, and usually of a whitish, grayish, or flesh-red\n   color. It differs from gneiss in not having the mica in planes, and\n   therefore in being destitute of a schistose structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granite State', '', 'New Hampshire; -- a nickname alluding to its\n   mountains, which are chiefly of granite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granitic', 'a.', 'Like granite in composition, color, etc.; having the\n   nature of granite; as, granitic texture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granitic', 'a.', 'Consisting of granite; as, granitic mountains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granitical', 'a.', 'Granitic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granitification', 'n.', 'The act or the process of forming into granite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granitiform', 'a.', 'Resembling granite in structure or shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granitoid', 'a.', 'Resembling granite in granular appearance; as, granitoid\n   gneiss; a granitoid pavement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granivorous', 'a.', 'Eating grain; feeding or subsisting on seeds; as,\n   granivorous birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grannam', 'n.', 'A grandam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granny', 'n.', 'A grandmother; a grandam; familiarly, an old woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granolithic', 'n.', 'A kind of hard artificial stone, used for pavements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granted', 'imp. & p. p.', 'of Grant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granting', 'p. pr. & vb. n.', 'of Grant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grant', 'v. t.', 'To give over; to make conveyance of; to give the\n   possession or title of; to convey; -- usually in answer to petition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grant', 'v. t.', 'To bestow or confer, with or without compensation,\n   particularly in answer to prayer or request; to give.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grant', 'v. t.', 'To admit as true what is not yet satisfactorily proved;\n   to yield belief to; to allow; to yield; to concede.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grant', 'v. i.', 'To assent; to consent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grant', 'v. t.', 'The act of granting; a bestowing or conferring;\n   concession; allowance; permission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grant', 'v. t.', 'The yielding or admission of something in dispute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grant', 'v. t.', 'The thing or property granted; a gift; a boon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grant', 'v. t.', 'A transfer of property by deed or writing; especially, au\n   appropriation or conveyance made by the government; as, a grant of land\n   or of money; also, the deed or writing by which the transfer is made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grantable', 'a.', 'Capable of being granted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grantee', 'n.', 'The person to whom a grant or conveyance is made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granter', 'n.', 'One who grants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grantor', 'n.', 'The person by whom a grant or conveyance is made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granular', 'a.', 'Consisting of, or resembling, grains; as, a granular\n   substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granularly', 'adv.', 'In a granular form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granulary', 'a.', 'Granular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granulated', 'imp. & p. p.', 'of Granulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granulating', 'p. pr. & vb. n.', 'of Granulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granulate', 'v. t.', 'To form into grains or small masses; as, to granulate\n   powder, sugar, or metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granulate', 'v. t.', 'To raise in granules or small asperities; to make\n   rough on the surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granulate', 'v. i.', 'To collect or be formed into grains; as, cane juice\n   granulates into sugar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granulate', 'a.', 'Alt. of Granulated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granulated', 'a.', 'Consisting of, or resembling, grains; crystallized in\n   grains; granular; as, granulated sugar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granulated', 'a.', 'Having numerous small elevations, as shagreen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granulation', 'n.', 'The act or process of forming or crystallizing into\n   grains; as, the granulation of powder and sugar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granulation', 'n.', 'The state of being granulated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granulation', 'n.', 'One of the small, red, grainlike prominences which\n   form on a raw surface (that of wounds or ulcers), and are the efficient\n   agents in the process of healing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granulation', 'n.', 'The act or process of the formation of such\n   prominences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granule', 'n.', 'A little grain a small particle; a pellet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granuliferous', 'a.', 'Full of granulations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granuliform', 'a.', 'Having a granular structure; granular; as, granuliform\n   limestone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granulite', 'n.', 'A whitish, granular rock, consisting of feldspar and\n   quartz intimately mixed; -- sometimes called whitestone, and leptynite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granulose', 'n.', 'The main constituent of the starch grain or granule, in\n   distinction from the framework of cellulose. Unlike cellulose, it is\n   colored blue by iodine, and is converted into dextrin and sugar by\n   boiling acids and amylolytic ferments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Granulous', 'a.', 'Full of grains; abounding with granular substances;\n   granular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grape', 'n.', 'A well-known edible berry growing in pendent clusters or\n   bunches on the grapevine. The berries are smooth-skinned, have a juicy\n   pulp, and are cultivated in great quantities for table use and for\n   making wine and raisins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grape', 'n.', 'The plant which bears this fruit; the grapevine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grape', 'n.', 'A mangy tumor on the leg of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grape', 'n.', 'Grapeshot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grape fruit', '', 'The shaddock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grapeless', 'a.', 'Wanting grapes or the flavor of grapes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grapery', 'n.', 'A building or inclosure used for the cultivation of\n   grapes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grapeshot', 'n.', 'A cluster, usually nine in number, of small iron balls,\n   put together by means of cast-iron circular plates at top and bottom,\n   with two rings, and a central connecting rod, in order to be used as a\n   charge for a cannon. Formerly grapeshot were inclosed in canvas bags.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grapestone', 'n.', 'A seed of the grape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grapevine', 'n.', 'A vine or climbing shrub, of the genus Vitis, having\n   small green flowers and lobed leaves, and bearing the fruit called\n   grapes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graphic', 'a.', 'Alt. of Graphical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graphical', 'a.', 'Of or pertaining to the arts of painting and drawing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graphical', 'a.', 'Of or pertaining to the art of writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graphical', 'a.', 'Written or engraved; formed of letters or lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graphical', 'a.', 'Well delineated; clearly and vividly described.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graphical', 'a.', 'Having the faculty of, or characterized by, clear and\n   impressive description; vivid; as, a graphic writer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graphically', 'adv.', 'In a graphic manner; vividly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graphicness', 'n.', 'Alt. of Graphicalness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graphicalness', 'n.', 'The quality or state of being graphic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graphics', 'n.', 'The art or the science of drawing; esp. of drawing\n   according to mathematical rules, as in perspective, projection, and the\n   like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graphiscope', 'n.', 'See Graphoscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graphite', 'n.', 'Native carbon in hexagonal crystals, also foliated or\n   granular massive, of black color and metallic luster, and so soft as to\n   leave a trace on paper. It is used for pencils (improperly called lead\n   pencils), for crucibles, and as a lubricator, etc. Often called\n   plumbago or black lead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graphitic', 'a.', 'Pertaining to, containing, derived from, or resembling,\n   graphite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graphitoid', 'a.', 'Alt. of Graphitoidal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graphitoidal', 'a.', 'Resembling graphite or plumbago.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grapholite', 'n.', 'Any species of slate suitable to be written on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graphology', 'n.', 'The art of judging of a persons character,\n   disposition, and aptitude from his handwriting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graphoscope', 'n.', 'An optical instrument for magnifying engravings,\n   photographs, etc., usually having one large lens and two smaller ones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graphotype', 'n.', 'A process for producing a design upon a surface in\n   relief so that it can be printed from. Prepared chalk or oxide of zinc\n   is pressed upon a smooth plate by a hydraulic press, and the design is\n   drawn upon this in a peculiar ink which hardens the surface wherever it\n   is applied. The surface is then carefully rubbed or brushed, leaving\n   the lines in relief.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-graphy', '', 'A suffix denoting the art of writing or describing; also,\n   the writing or description itself; a treatise; as, calligraphy,\n   biography, geography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grapnel', 'n.', 'A small anchor, with four or five flukes or claws, used to\n   hold boats or small vessels; hence, any instrument designed to grapple\n   or hold; a grappling iron; a grab; -- written also grapline, and\n   crapnel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grappled', 'imp. & p. p.', 'of Grapple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grappling', 'p. pr. & vb. n.', 'of Grapple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grapple', 'v. t.', 'To seize; to lay fast hold of; to attack at close\n   quarters: as, to grapple an antagonist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grapple', 'v. t.', 'To fasten, as with a grapple; to fix; to join\n   indissolubly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grapple', 'v. i.', 'To use a grapple; to contend in close fight; to attach\n   ones self as if by a grapple, as in wrestling; to close; to seize one\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grapple', 'v. t.', 'A seizing or seizure; close hug in contest; the\n   wrestlers hold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grapple', 'v. t.', 'An instrument, usually with hinged claws, for seizing\n   and holding fast to an object; a grab.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grapple', 'v. t.', 'A grappling iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grapplement', 'n.', 'A grappling; close fight or embrace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grappling', 'n.', 'A laying fast ho1d of; also, that by which anything is\n   seized and held, a grapnel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grappling', 'n.', 'A grapple; a struggle. A match for yards in fight, in\n   grappling for the bear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grapsoid', 'a.', 'Pertaining to the genus Grapsus or the family Grapsidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grapsoid', 'n.', 'A grapsoid crab.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graptolite', 'n.', 'One of numerous species of slender and delicate\n   fossils, of the genus Graptolites and allied genera, found in the\n   Silurian rocks. They belong to an extinct group (Graptolithina)\n   supposed to be hydroids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graptolitic', 'a.', 'Of or pertaining to graptolites; containing\n   graptolites; as, a graptolitic slate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grapy', 'a.', 'Composed of, or resembling, grapes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grasper', 'imp. & p. p.', 'of Grasp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Qraspine', 'p. pr. & vb. n.', 'of Grasp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grasp', 'v. t.', 'To seize and hold by clasping or embracing with the\n   fingers or arms; to catch to take possession of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grasp', 'v. t.', 'To lay hold of with the mind; to become thoroughly\n   acquainted or conversant with; to comprehend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grasp', 'v. i.', 'To effect a grasp; to make the motion of grasping; to\n   clutch; to struggle; to strive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grasp', 'n.', 'A gripe or seizure of the hand; a seizure by embrace, or\n   infolding in the arms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grasp', 'n.', 'Reach of the arms; hence, the power of seizing and holding;\n   as, it was beyond his grasp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grasp', 'n.', 'Forcible possession; hold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grasp', 'n.', 'Wide-reaching power of intellect to comprehend subjects and\n   hold them under survey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grasp', 'n.', 'The handle of a sword or of an oar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graspable', 'a.', 'Capable of being grasped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graaper', 'n.', 'One who grasps or seizes; one who catches or holds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grasping', 'a.', 'Seizing; embracing; catching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grasping', 'a.', 'Avaricious; greedy of gain; covetous; close; miserly; as,\n   he is a grasping man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graspless', 'a.', 'Without a grasp; relaxed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grass', 'n.', 'Popularly: Herbage; the plants which constitute the food of\n   cattle and other beasts; pasture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grass', 'n.', 'An endogenous plant having simple leaves, a stem generally\n   jointed and tubular, the husks or glumes in pairs, and the seed single.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grass', 'n.', 'The season of fresh grass; spring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grass', 'n.', 'Metaphorically used for what is transitory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grassed', 'imp. & p. p.', 'of Grass');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grassing', 'p. pr. & vb. n.', 'of Grass');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grass', 'v. t.', 'To cover with grass or with turf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grass', 'v. t.', 'To expose, as flax, on the grass for bleaching, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grass', 'v. t.', 'To bring to the grass or ground; to land; as, to grass a\n   fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grass', 'v. i.', 'To produce grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grassation', 'n.', 'A wandering about with evil intentions; a rioting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grass-green', 'a.', 'Green with grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grass-green', 'a.', 'Of the color of grass; clear and vivid green.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grass-grown', 'a.', 'Overgrown with grass; as, a grass-grown road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grasshopper', 'n.', 'Any jumping, orthopterous insect, of the families\n   Acrididae and Locustidae. The species and genera are very numerous. The\n   former family includes the Western grasshopper or locust (Caloptenus\n   spretus), noted for the great extent of its ravages in the region\n   beyond the Mississippi. In the Eastern United States the red-legged\n   (Caloptenus femurrubrum and C. atlanis) are closely related species,\n   but their ravages are less important. They are closely related to the\n   migratory locusts of the Old World. See Locust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grasshopper', 'n.', 'In ordinary square or upright pianos of London make,\n   the escapement lever or jack, so made that it can be taken out and\n   replaced with the key; -- called also the hopper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grassiness', 'n.', 'The state of abounding with grass; a grassy state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grassless', 'a.', 'Destitute of grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grassplot', 'n.', 'A plot or space covered with grass; a lawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grass tree', '', 'An Australian plant of the genus Xanthorrhoea, having a\n   thick trunk crowned with a dense tuft of pendulous, grasslike leaves,\n   from the center of which arises a long stem, bearing at its summit a\n   dense flower spike looking somewhat like a large cat-tail. These plants\n   are often called \"blackboys\" from the large trunks denuded and\n   blackened by fire. They yield two kinds of fragrant resin, called\n   Botany-bay gum, and Gum Acaroides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grass tree', '', 'A similar Australian plant (Kingia australis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grassy', 'a.', 'Covered with grass; abounding with grass; as, a grassy\n   lawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grassy', 'a.', 'Resembling grass; green.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grate', 'a.', 'Serving to gratify; agreeable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grate', 'n.', 'A structure or frame containing parallel or crosed bars,\n   with interstices; a kind of latticework, such as is used ia the windows\n   of prisons and cloisters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grate', 'n.', 'A frame or bed, or kind of basket, of iron bars, for holding\n   fuel while burning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grated', 'imp. & p. p.', 'of Grate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grating', 'p. pr. &. vb. n.', 'of Grate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grate', 'v. t.', 'To furnish with grates; to protect with a grating or\n   crossbars; as, to grate a window.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grate', 'v. t.', 'To rub roughly or harshly, as one body against another,\n   causing a harsh sound; as, to grate the teeth; to produce (a harsh\n   sound) by rubbing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grate', 'v. t.', 'To reduce to small particles by rubbing with anything\n   rough or indented; as, to grate a nutmeg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grate', 'v. t.', 'To fret; to irritate; to offend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grate', 'v. i.', 'To make a harsh sound by friction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grate', 'v. i.', 'To produce the effect of rubbing with a hard rough\n   material; to cause wearing, tearing, or bruising. Hence; To produce\n   exasperation, soreness, or grief; to offend by oppression or\n   importunity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grated', 'a.', 'Furnished with a grate or grating; as, grated windows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grateful', 'a.', 'Having a due sense of benefits received; kindly disposed\n   toward one from whom a favor has been received; willing to acknowledge\n   and repay, or give thanks for, benefits; as, a grateful heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grateful', 'a.', 'Affording pleasure; pleasing to the senses; gratifying;\n   delicious; as, a grateful present; food grateful to the palate;\n   grateful sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grater', 'a.', 'One who, or that which, grates; especially, an instrument\n   or utensil with a rough, indented surface, for rubbing off small\n   particles of any substance; as a grater for nutmegs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graticulation', 'n.', 'The division of a design or draught into squares, in\n   order the more easily to reproduce it in larger or smaller dimensions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graticule', 'n.', 'A design or draught which has been divided into squares,\n   in order to reproduce it in other dimensions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gratification', 'n.', 'The act of gratifying, or pleasing, either the mind,\n   the taste, or the appetite; as, the gratification of the palate, of the\n   appetites, of the senses, of the desires, of the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gratification', 'n.', 'That which affords pleasure; satisfaction;\n   enjoyment; fruition: delight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gratification', 'n.', 'A reward; a recompense; a gratuity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Glatified', 'a.', 'Pleased; indulged according to desire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gratifier', 'n.', 'One who gratifies or pleases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gratified', 'imp. & p. p.', 'of Gratify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gratifying', 'p. pr. & vb. n.', 'of Gratify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gratify', 'v. t.', 'To please; to give pleasure to; to satisfy; to soothe;\n   to indulge; as, to gratify the taste, the appetite, the senses, the\n   desires, the mind, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gratify', 'v. t.', 'To requite; to recompense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grating', 'n.', 'A partition, covering, or frame of parallel or cross bars;\n   a latticework resembling a window grate; as, the grating of a prison or\n   convent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grating', 'n.', 'A system of close equidistant and parallel lines lines or\n   bars, especially lines ruled on a polished surface, used for producing\n   spectra by diffraction; -- called also diffraction grating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grating', 'n.', 'The strong wooden lattice used to cover a hatch, admitting\n   light and air; also, a movable Lattice used for the flooring of boats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grating', 'a.', 'That grates; making a harsh sound; harsh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grating', 'n.', 'A harsh sound caused by attrition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gratiolin', 'n.', 'One of the essential principles of the hedge hyssop\n   (Gratiola officinalis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gratis', 'adv.', 'For nothing; without fee or recompense; freely;\n   gratuitously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gratitude', 'a.', 'The state of being grateful; warm and friendly feeling\n   toward a benefactor; kindness awakened by a favor received;\n   thankfulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gratuitous', 'a.', 'Given without an equivalent or recompense; conferred\n   without valuable consideration; granted without pay, or without claim\n   or merit; not required by justice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gratuitous', 'a.', 'Not called for by the circumstances; without reason,\n   cause, or proof; adopted or asserted without any good ground; as, a\n   gratuitous assumption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gtratuities', 'pl. ', 'of Gratuity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gratuity', 'n.', 'Something given freely or without recompense; a free\n   gift; a present.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gratuity', 'n.', 'Something voluntarily given in return for a favor or\n   service, as a recompense or acknowledgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grqatulated', 'imp. & p. p.', 'of Gratulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gratulating', 'p. pr. & vb. n.', 'of Gratulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gratulate', 'a.', 'To salute with declaration of joy; to congratulate.');
