  INSERT INTO words (word, word_type, description) VALUES  (    'Gravamen', 'a.', 'The grievance complained of; the substantial cause of the\n   action; also, in general, the ground or essence of a complaint.\n   Bouvier.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-grave', '', 'A final syllable signifying a ruler, as in landgrave,\n   margrave. See Margrave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grave', 'v. t.', 'To clean, as a vessels bottom, of barnacles, grass,\n   etc., and pay it over with pitch; -- so called because graves or\n   greaves was formerly used for this purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grave', 'superl.', 'Of great weight; heavy; ponderous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grave', 'superl.', 'Of importance; momentous; weighty; influential; sedate;\n   serious; -- said of character, relations, etc.; as, grave deportment,\n   character, influence, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grave', 'superl.', 'Not light or gay; solemn; sober; plain; as, a grave\n   color; a grave face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grave', 'superl.', 'Not acute or sharp; low; deep; -- said of sound; as, a\n   grave note or key.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grave', 'superl.', 'Slow and solemn in movement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graved', 'imp.', 'of Grave');
