  INSERT INTO words (word, word_type, description) VALUES  (    'Grave', 'n.', 'To dig. [Obs.] Chaucer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grave', 'n.', 'To carve or cut, as letters or figures, on some hard\n   substance; to engrave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grave', 'n.', 'To carve out or give shape to, by cutting with a chisel; to\n   sculpture; as, to grave an image.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grave', 'n.', 'To impress deeply (on the mind); to fix indelibly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grave', 'n.', 'To entomb; to bury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grave', 'v. i.', 'To write or delineate on hard substances, by means of\n   incised lines; to practice engraving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grave', 'n.', 'An excavation in the earth as a place of burial; also, any\n   place of interment; a tomb; a sepulcher. Hence: Death; destruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graveclothes', 'n. pl.', 'The clothes or dress in which the dead are\n   interred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gravedigger', 'n.', 'A digger of graves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gravedigger', 'n.', 'See Burying beetle, under Bury, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gravel', 'n.', 'Small stones, or fragments of stone; very small pebbles,\n   often intermixed with particles of sand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gravel', 'n.', 'A deposit of small calculous concretions in the kidneys and\n   the urinary or gall bladder; also, the disease of which they are a\n   symptom.');
