  INSERT INTO words (word, word_type, description) VALUES  (    'Gravel', 'v. t.', 'To run (as a ship) upon the gravel or beach; to run\n   aground; to cause to stick fast in gravel or sand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gravel', 'v. t.', 'To check or stop; to embarrass; to perplex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gravel', 'v. t.', 'To hurt or lame (a horse) by gravel lodged between the\n   shoe and foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graveless', 'a.', 'Without a grave; unburied.');
