  INSERT INTO words (word, word_type, description) VALUES  (    'Gravelling', 'n.', 'A salmon one or two years old, before it has gone to\n   sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gravelliness', 'n.', 'State of being gravelly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gravelly', 'a.', 'Abounding with gravel; consisting of gravel; as, a\n   gravelly soil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gravel-stone', 'n.', 'A pebble, or small fragment of stone; a calculus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gravely', 'adv.', 'In a grave manner.');
