  INSERT INTO words (word, word_type, description) VALUES  (    'Graveness', 'n.', 'The quality of being grave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gravenstein', 'n.', 'A kind of fall apple, marked with streaks of deep red\n   and orange, and of excellent flavor and quality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graveolence', 'n.', 'A strong and offensive smell; rancidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graveolent', 'a.', 'Having a rank smell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graver', 'n.', 'One who graves; an engraver or a sculptor; one whose\n   occupation is te cut letters or figures in stone or other hard\n   material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graver', 'n.', 'An ergraving or cutting tool; a burin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gravery', 'n.', 'The act, process, or art, of graving or carving;\n   engraving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graves', 'n. pl.', 'The sediment of melted tallow. Same as Greaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graves disease', '', 'Same as Basedows disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gravestone', 'n.', 'A stone laid over, or erected near, a grave, usually\n   with an inscription, to preserve the memory of the dead; a tombstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graveyard', 'n.', 'A yard or inclosure for the interment of the dead; a\n   cemetery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gravic', 'a.', 'Pertaining to, or causing, gravitation; as, gravic forces;\n   gravic attraction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gravid', 'a.', 'Being with child; heavy with young; pregnant; fruitful; as,\n   a gravid uterus; gravid piety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gravidated', 'a.', 'Made pregnant; big.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gravidation', 'n.', 'Gravidity.');
