  INSERT INTO words (word, word_type, description) VALUES  (    'Gravimetric', 'a.', 'Of or pertaining to measurement by weight; measured by\n   weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graving', 'n.', 'The act of cleaning a ships bottom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graving', 'n.', 'The act or art of carving figures in hard substances, esp.\n   by incision or in intaglio.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graving', 'n.', 'That which is graved or carved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graving', 'n.', 'Impression, as upon the mind or heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gravitated', 'imp. & p. p.', 'of Gravitate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gravitating', 'p. pr. & vb. n.', 'of Gravitate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gravitate', 'v. i.', 'To obey the law of gravitation; to exert a force Or\n   pressure, or tend to move, under the influence of gravitation; to tend\n   in any direction or toward any object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gravitation', 'n.', 'The act of gravitating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gravitation', 'n.', 'That species of attraction or force by which all\n   bodies or particles of matter in the universe tend toward each other;\n   called also attraction of gravitation, universal gravitation, and\n   universal gravity. See Attraction, and Weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gravitational', 'a.', 'Of or pertaining to the force of gravity; as,\n   gravitational units.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gravitative', 'a.', 'Causing to gravitate; tending to a center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gravities', 'pl. ', 'of Gravity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gravity', 'a.', 'The state of having weight; beaviness; as, the gravity of\n   lead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gravity', 'a.', 'Sobriety of character or demeanor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gravity', 'a.', 'Importance, significance, dignity, etc; hence,\n   seriousness; enormity; as, the gravity of an offense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gravity', 'a.', 'The tendency of a mass of matter toward a center of\n   attraction; esp., the tendency of a body toward the center of the\n   earth; terrestrial gravitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gravity', 'a.', 'Lowness of tone; -- opposed to acuteness.');
