  INSERT INTO words (word, word_type, description) VALUES  (    'Gravy', 'n.', 'Liquid dressing for meat, fish, vegetables, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gray', 'superl.', 'White mixed with black, as the color of pepper and salt,\n   or of ashes, or of hair whitened by age; sometimes, a dark mixed color;\n   as, the soft gray eye of a dove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gray', 'superl.', 'Gray-haired; gray-headed; of a gray color; hoary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gray', 'superl.', 'Old; mature; as, gray experience. Ames.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gray', 'n.', 'A gray color; any mixture of white and black; also, a neutral\n   or whitish tint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gray', 'n.', 'An animal or thing of gray color, as a horse, a badger, or a\n   kind of salmon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grayback', 'n.', 'The California gray whale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grayback', 'n.', 'The redbreasted sandpiper or knot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grayback', 'n.', 'The dowitcher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grayback', 'n.', 'The body louse.');
