  INSERT INTO words (word, word_type, description) VALUES  (    'Grayish', 'a.', 'Somewhat gray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graylag', 'n.', 'The common wild gray goose (Anser anser) of Europe,\n   believed to be the wild form of the domestic goose. See Illust. of\n   Goose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grayling', 'a.', 'A European fish (Thymallus vulgaris), allied to the\n   trout, but having a very broad dorsal fin; -- called also umber. It\n   inhabits cold mountain streams, and is valued as a game fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grayling', 'a.', 'An American fish of the genus Thymallus, having similar\n   habits to the above; one species (T. Ontariensis), inhabits several\n   streams in Michigan; another (T. montanus), is found in the Yellowstone\n   region.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grayness', 'n.', 'The quality of being gray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gtraystone', 'n.', 'A grayish or greenish compact rock, composed of\n   feldspar and augite, and allied to basalt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graywacke', 'n.', 'A conglomerate or grit rock, consisting of rounded\n   pebbles sand firmly united together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grazed', 'imp. & p. p.', 'of Graze');
