  INSERT INTO words (word, word_type, description) VALUES  (    'Graze', 'v. i.', 'To yield grass for grazing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graze', 'v. i.', 'To touch something lightly in passing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graze', 'n.', 'The act of grazing; the cropping of grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Graze', 'n.', 'A light touch; a slight scratch.');
