  INSERT INTO words (word, word_type, description) VALUES  (    'Grazier', 'n.', 'One who pastures cattle, and rears them for market.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grazing', 'n.', 'The act of one who, or that which, grazes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grazing', 'n.', 'A pasture; growing grass.');
