  INSERT INTO words (word, word_type, description) VALUES  (    'Grease', 'n.', 'An inflammation of a horses heels, suspending the ordinary\n   greasy secretion of the part, and producing dryness and scurfiness,\n   followed by cracks, ulceration, and fungous excrescences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greased', 'imp. & p. p.', 'of Grease');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greasing', 'p. pr. & vb. n.', 'of Grease');
