  INSERT INTO words (word, word_type, description) VALUES  (    'Greasily', 'adv.', 'In a greasy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greasily', 'adv.', 'In a gross or indelicate manner.');
