  INSERT INTO words (word, word_type, description) VALUES  (    'Greasy', 'superl.', 'Like grease or oil; smooth; seemingly unctuous to the\n   touch, as is mineral soapstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greasy', 'superl.', 'Fat of body; bulky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greasy', 'superl.', 'Gross; indelicate; indecent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greasy', 'superl.', 'Affected with the disease called grease; as, the heels\n   of a horse. See Grease, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Great', 'superl.', 'Large in space; of much size; big; immense; enormous;\n   expanded; -- opposed to small and little; as, a great house, ship,\n   farm, plain, distance, length.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Great', 'superl.', 'Large in number; numerous; as, a great company,\n   multitude, series, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Great', 'superl.', 'Long continued; lengthened in duration; prolonged in\n   time; as, a great while; a great interval.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Great', 'superl.', 'Superior; admirable; commanding; -- applied to\n   thoughts, actions, and feelings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Great', 'superl.', 'Endowed with extraordinary powers; uncommonly gifted;\n   able to accomplish vast results; strong; powerful; mighty; noble; as, a\n   great hero, scholar, genius, philosopher, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Great', 'superl.', 'Holding a chief position; elevated: lofty: eminent;\n   distingushed; foremost; principal; as, great men; the great seal; the\n   great marshal, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Great', 'superl.', 'Entitled to earnest consideration; weighty; important;\n   as, a great argument, truth, or principle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Great', 'superl.', 'Pregnant; big (with young).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Great', 'superl.', 'More than ordinary in degree; very considerable in\n   degree; as, to use great caution; to be in great pain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Great', 'superl.', 'Older, younger, or more remote, by single generation;\n   -- often used before grand to indicate one degree more remote in the\n   direct line of descent; as, great-grandfather (a grandfathers or a\n   grandmothers father), great-grandson, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Great', 'n.', 'The whole; the gross; as, a contract to build a ship by the\n   great.');
