  INSERT INTO words (word, word_type, description) VALUES  (    'Great-grandfather', 'n.', 'The father of ones grandfather or grandmother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Great-grandmother', 'n.', 'The mother of ones grandfather or grandmother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Great-grandson', 'n.', 'A son of ones grandson or granddaughter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Great-hearted', 'a.', 'High-spirited; fearless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Great-hearted', 'a.', 'Generous; magnanimous; noble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Great-heartedness', 'n.', 'The quality of being greathearted;\n   high-mindedness; magnanimity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greatly', 'adv.', 'In a great degree; much.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greatly', 'adv.', 'Nobly; illustriously; magnanimously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greatness', 'n.', 'The state, condition, or quality of being great; as,\n   greatness of size, greatness of mind, power, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greatness', 'n.', 'Pride; haughtiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greave', 'n.', 'A grove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greave', 'n.', 'Armor for the leg below the knee; -- usually in the plural.');
