  INSERT INTO words (word, word_type, description) VALUES  (    'Greave', 'v. t.', 'To clean (a ships bottom); to grave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greaves', 'n. pl.', 'The sediment of melted tallow. It is made into cakes\n   for dogs food. In Scotland it is called cracklings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grebe', 'n.', 'One of several swimming birds or divers, of the genus\n   Colymbus (formerly Podiceps), and allied genera, found in the northern\n   parts of America, Europe, and Asia. They have strong, sharp bills, and\n   lobate toes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grecian', 'a.', 'Of or pertaining to Greece; Greek.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grecian', 'n.', 'A native or naturalized inhabitant of Greece; a Greek.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grecian', 'n.', 'A jew who spoke Greek; a Hellenist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grecian', 'n.', 'One well versed in the Greek language, literature, or\n   history.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grecism', 'n.', 'An idiom of the Greek language; a Hellenism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grecized', 'imp. & p. p.', 'of Grecize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grecizing', 'p. pr. & vb. n.', 'of Grecize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grecize', 'v. t.', 'To render Grecian; also, to cause (a word or phrase in\n   another language) to take a Greek form; as, the name is Grecized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grecize', 'v. t.', 'To translate into Greek.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grecize', 'v. i.', 'Alt. of Grecianize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grecianize', 'v. i.', 'To conform to the Greek custom, especially in\n   speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greco-Roman', 'a.', 'Having characteristics that are partly Greek and\n   partly Roman; as, Greco-Roman architecture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grecque', 'n.', 'An ornament supposed to be of Greek origin, esp. a fret or\n   meander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gree', 'n.', 'Good will; favor; pleasure; satisfaction; -- used esp. in\n   such phrases as: to take in gree; to accept in gree; that is, to take\n   favorably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gree', 'n.', 'Rank; degree; position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gree', 'n.', 'The prize; the honor of the day; as, to bear the gree, i. e.,\n   to carry off the prize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gree', 'v. i.', 'To agree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grees', 'pl. ', 'of Gree');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greece', 'pl. ', 'of Gree');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grice', 'pl. ', 'of Gree');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grise', 'pl. ', 'of Gree');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grize', 'pl. ', 'of Gree');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gree', 'n.', 'A step.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greece', 'n. pl.', 'See Gree a step.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greed', 'n.', 'An eager desire or longing; greediness; as, a greed of gain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greedily', 'adv.', 'In a greedy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greediness', 'n.', 'The quality of being greedy; vehement and selfish\n   desire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greedy', 'superl.', 'Having a keen appetite for food or drink; ravenous;\n   voracious; very hungry; -- followed by of; as, a lion that is greedy of\n   his prey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greedy', 'superl.', 'Having a keen desire for anything; vehemently\n   desirous; eager to obtain; avaricious; as, greedy of gain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greedy-gut', 'n.', 'A glutton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greegree', 'n.', 'An African talisman or Grigri charm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greek', 'a.', 'Of or pertaining to Greece or the Greeks; Grecian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greek', 'n.', 'A native, or one of the people, of Greece; a Grecian; also,\n   the language of Greece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greek', 'n.', 'A swindler; a knave; a cheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greek', 'n.', 'Something unintelligible; as, it was all Greek to me.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greekess', 'n.', 'A female Greek.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greekish', 'a.', 'Peculiar to Greece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greekling', 'n.', 'A little Greek, or one of small esteem or pretensions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Green', 'superl.', 'Having the color of grass when fresh and growing;\n   resembling that color of the solar spectrum which is between the yellow\n   and the blue; verdant; emerald.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Green', 'superl.', 'Having a sickly color; wan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Green', 'superl.', 'Full of life aud vigor; fresh and vigorous; new;\n   recent; as, a green manhood; a green wound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Green', 'superl.', 'Not ripe; immature; not fully grown or ripened; as,\n   green fruit, corn, vegetables, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Green', 'superl.', 'Not roasted; half raw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Green', 'superl.', 'Immature in age or experience; young; raw; not trained;\n   awkward; as, green in years or judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Green', 'superl.', 'Not seasoned; not dry; containing its natural juices;\n   as, green wood, timber, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Green', 'n.', 'The color of growing plants; the color of the solar spectrum\n   intermediate between the yellow and the blue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Green', 'n.', 'A grassy plain or plat; a piece of ground covered with\n   verdant herbage; as, the village green.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Green', 'n.', 'Fresh leaves or branches of trees or other plants; wreaths;\n   -- usually in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Green', 'n.', 'pl. Leaves and stems of young plants, as spinach, beets,\n   etc., which in their green state are boiled for food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Green', 'n.', 'Any substance or pigment of a green color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greened', 'imp. & p. p.', 'of Green');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greening', 'p. pr. & vb. n.', 'of Green');
  INSERT INTO words (word, word_type, description) VALUES  (    'Green', 'v. t.', 'To make green.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Green', 'v. i.', 'To become or grow green.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greenback', 'n.', 'One of the legal tender notes of the United States; --\n   first issued in 1862, and having the devices on the back printed with\n   green ink, to prevent alterations and counterfeits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greenbacker', 'n.', 'One of those who supported greenback or paper money,\n   and opposed the resumption of specie payments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greenbone', 'n.', 'Any garfish (Belone or Tylosurus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greenbone', 'n.', 'The European eelpout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Green-broom', 'n.', 'A plant of the genus Genista (G. tinctoria); dyers\n   weed; -- called also greenweed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greencloth', 'n.', 'A board or court of justice formerly held in the\n   counting house of the British sovereigns household, composed of the\n   lord steward and his officers, and having cognizance of matters of\n   justice in the household, with power to correct offenders and keep the\n   peace within the verge of the palace, which extends two hundred yards\n   beyond the gates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greenery', 'n.', 'Green plants; verdure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Green-eyed', 'a.', 'Having green eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Green-eyed', 'a.', 'Seeing everything through a medium which discolors or\n   distorts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greenfinch', 'n.', 'A European finch (Ligurinus chloris); -- called also\n   green bird, green linnet, green grosbeak, green olf, greeny, and\n   peasweep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greenfinch', 'n.', 'The Texas sparrow (Embernagra rufivirgata), in which\n   the general color is olive green, with four rufous stripes on the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greenfish', 'n.', 'See Bluefish, and Pollock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greengage', 'n.', 'A kind of plum of medium size, roundish shape, greenish\n   flesh, and delicious flavor. It is called in France Reine Claude, after\n   the queen of Francis I. See Gage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greengill', 'n.', 'An oyster which has the gills tinged with a green\n   pigment, said to be due to an abnormal condition of the blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greengrocer', 'n.', 'A retailer of vegetables or fruits in their fresh or\n   green state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greenhead', 'n.', 'The mallard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greenhead', 'n.', 'The striped bass. See Bass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greenhead', 'n.', 'Alt. of Greenhood');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greenhood', 'n.', 'A state of greenness; verdancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greenhorn', 'n.', 'A raw, inexperienced person; one easily imposed upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greenhouse', 'n.', 'A house in which tender plants are cultivated and\n   sheltered from the weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greening', 'n.', 'A greenish apple, of several varieties, among which the\n   Rhode Island greening is the best known for its fine-grained acid flesh\n   and its excellent keeping quality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greenish', 'a.', 'Somewhat green; having a tinge of green; as, a greenish\n   yellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greenlander', 'n.', 'A native of Greenland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Green-leek', 'n.', 'An Australian parrakeet (Polytelis Barrabandi); --\n   called also the scarlet-breasted parrot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greenlet', 'n.', 'l. (Zool.) One of numerous species of small American\n   singing birds, of the genus Vireo, as the solitary, or blue-headed\n   (Vireo solitarius); the brotherly-love (V. Philadelphicus); the\n   warbling greenlet (V. gilvus); the yellow-throated greenlet (V.\n   flavifrons) and others. See Vireo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greenlet', 'n.', 'Any species of Cyclorhis, a genus of tropical American\n   birds allied to the tits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greenly', 'adv.', 'With a green color; newly; freshly, immaturely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greenly', 'a.', 'Of a green color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greenness', 'n.', 'The quality of being green; viridity; verdancy; as, the\n   greenness of grass, or of a meadow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greenness', 'n.', 'Freshness; vigor; newness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greenness', 'n.', 'Immaturity; unripeness; as, the greenness of fruit;\n   inexperience; as, the greenness of youth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greenockite', 'n.', 'Native cadmium sulphide, a mineral occurring in yellow\n   hexagonal crystals, also as an earthy incrustation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greenroom', 'n.', 'The retiring room of actors and actresses in a theater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greensand', 'n.', 'A variety of sandstone, usually imperfectly\n   consolidated, consisting largely of glauconite, a silicate of iron and\n   potash of a green color, mixed with sand and a trace of phosphate of\n   lime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greenshank', 'n.', 'A European sandpiper or snipe (Totanus canescens); --\n   called also greater plover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Green-stall', 'n.', 'A stall at which greens and fresh vegetables are\n   exposed for sale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greenstone', 'n.', 'A name formerly applied rather loosely to certain\n   dark-colored igneous rocks, including diorite, diabase, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greensward', 'n.', 'Turf green with grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greenth', 'n.', 'The state or quality of being green; verdure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greenweed', 'n.', 'See Greenbroom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greenwood', 'n.', 'A forest as it appears is spring and summer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greenwood', 'a.', 'Pertaining to a greenwood; as, a greenwood shade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greet', 'a.', 'Great.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greet', 'v. i.', 'To weep; to cry; to lament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greet', 'n.', 'Mourning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greeted', 'imp. & p. p.', 'of Greet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greeting', 'p. pr. & vb. n.', 'of Greet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greet', 'v. t.', 'To address with salutations or expressions of kind\n   wishes; to salute; to hail; to welcome; to accost with friendship; to\n   pay respects or compliments to, either personally or through the\n   intervention of another, or by writing or token.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greet', 'v. t.', 'To come upon, or meet, as with something that makes the\n   heart glad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greet', 'v. t.', 'To accost; to address.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greet', 'v. i.', 'To meet and give salutations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greet', 'n.', 'Greeting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greeter', 'n.', 'One who greets or salutes another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greeter', 'n.', 'One who weeps or mourns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greeting', 'n.', 'Expression of kindness or joy; salutation at meeting; a\n   compliment from one absent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greeve', 'n.', 'See Grieve, an overseer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greeze', 'n.', 'A step. See Gree, a step.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greffier', 'n.', 'A registrar or recorder; a notary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gregal', 'a.', 'Pertaining to, or like, a flock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gregarian', 'a.', 'Gregarious; belonging to the herd or common sort;\n   common.');
  INSERT INTO words (word, word_type, description) VALUES  (    '\\d8Gregarin\\91', 'n. pl.', 'An order of Protozoa, allied to the Rhizopoda,\n   and parasitic in other animals, as in the earthworm, lobster, etc. When\n   adult, they have a small, wormlike body inclosing a nucleus, but\n   without external organs; in one of the young stages, they are\n   amoebiform; -- called also Gregarinida, and Gregarinaria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gregarine', 'a.', 'Of or pertaining to the Gregarinae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gregarine', 'n.', 'One of the Gregarinae.');
  INSERT INTO words (word, word_type, description) VALUES  (    '\\d8Gregarinida', '', 'Gregarinae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gregarious', 'a.', 'Habitually living or moving in flocks or herds; tending\n   to flock or herd together; not habitually solitary or living alone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grege', 'v. t.', 'Alt. of Gregge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gregge', 'v. t.', 'To make heavy; to increase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greggoe', 'n.', 'Alt. of Grego');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grego', 'n.', 'A short jacket or cloak, made of very thick, coarse cloth,\n   with a hood attached, worn by the Greeks and others in the Levant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gregorian', 'a.', 'Pertaining to, or originated by, some person named\n   Gregory, especially one of the popes of that name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greillade', 'n.', 'Iron ore in coarse powder, prepared for reduction by the\n   Catalan process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greisen', 'n.', 'A crystalline rock consisting of quarts and mica, common\n   in the tin regions of Cornwall and Saxony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greit', 'v. i.', 'See Greet, to weep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greith', 'v. t.', 'To make ready; -- often used reflexively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greith', 'v.', 'Goods; furniture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gremial', 'a.', 'Of or pertaining to the lap or bosom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gremial', 'n.', 'A bosom friend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gremial', 'n.', 'A cloth, often adorned with gold or silver lace, placed on\n   the bishops lap while he sits in celebrating mass, or in ordaining\n   priests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grenade', 'n.', 'A hollow ball or shell of iron filled with powder of other\n   explosive, ignited by means of a fuse, and thrown from the hand among\n   enemies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grenadier', 'n.', 'Originaly, a soldier who carried and threw grenades;\n   afterward, one of a company attached to each regiment or battalion,\n   taking post on the right of the line, and wearing a peculiar uniform.\n   In modern times, a member of a special regiment or corps; as, a\n   grenadier of the guard of Napoleon I. one of the regiment of Grenadier\n   Guards of the British army, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grenadier', 'n.', 'Any marine fish of the genus Macrurus, in which the body\n   and tail taper to a point; they mostly inhabit the deep sea; -- called\n   also onion fish, and rat-tail fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grenadier', 'n.', 'A bright-colored South African grosbeak (Pyromelana\n   orix), having the back red and the lower parts black.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grenadillo', 'n.', 'A handsome tropical American wood, much used for making\n   flutes and other wind instruments; -- called also Grenada cocos, or\n   cocus, and red ebony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grenadine', 'n.', 'A thin gauzelike fabric of silk or wool, for womens\n   wear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grenadine', 'n.', 'A trade name for a dyestuff, consisting essentially of\n   impure fuchsine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grenado', 'n.', 'Same as Grenade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grene', 'a.', 'Green.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gres', 'n.', 'Grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gressorial', 'a.', 'Alt. of Gressorious');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gressorious', 'a.', 'Adapted for walking; anisodactylous; as the feet of\n   certain birds and insects. See Illust. under Aves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gret', 'a.', 'Alt. of Grete');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grete', 'a.', 'Great.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gretto', '', 'imp. of Greet, to salute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greve', 'n.', 'A grove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grew', '', 'imp. of Grow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grewsome', 'a.', 'Alt. of Gruesome');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gruesome', 'a.', 'Ugly; frightful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grey', 'a.', 'See Gray (the correct orthography).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greyhound', 'n.', 'A slender, graceful breed of dogs, remarkable for keen\n   sight and swiftness. It is one of the oldest varieties known, and is\n   figured on the Egyptian monuments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greylag', 'n.', 'See Graylag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gribble', 'n.', 'A small marine isopod crustacean (Limnoria lignorum or L.\n   terebrans), which burrows into and rapidly destroys submerged timber,\n   such as the piles of wharves, both in Europe and America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grice', 'n.', 'A little pig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grice', 'n.', 'See Gree, a step.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grid', 'n.', 'A grating of thin parallel bars, similar to a gridiron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Griddle', 'n.', 'An iron plate or pan used for cooking cakes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Griddle', 'n.', 'A sieve with a wire bottom, used by miners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Griddlecake', 'n.', 'A cake baked or fried on a griddle, esp. a thin batter\n   cake, as of buckwheat or common flour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grided', 'imp. & p. p.', 'of Gride');
  INSERT INTO words (word, word_type, description) VALUES  (    'Griding', 'p. pr. & vb. n.', 'of Gride');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gride', 'e. i.', 'To cut with a grating sound; to cut; to penetrate or\n   pierce harshly; as, the griding sword.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grade', 'n.', 'A harsh scraping or cutting; a grating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gridelin', 'n.', 'A color mixed of white, and red, or a gray violet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gridiron', 'n.', 'A grated iron utensil for broiling flesh and fish over\n   coals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gridiron', 'n.', 'An openwork frame on which vessels are placed for\n   examination, cleaning, and repairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gridiron', 'n.', 'A football field.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grief', 'a.', 'Pain of mind on account of something in the past; mental\n   suffering arising from any cause, as misfortune, loss of friends,\n   misconduct of ones self or others, etc.; sorrow; sadness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grief', 'a.', 'Cause of sorrow or pain; that which afficts or distresses;\n   trial; grievance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grief', 'a.', 'Physical pain, or a cause of it; malady.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Griefful', 'a.', 'Full of grief or sorrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Griefless', 'a.', 'Without grief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Griego', 'n.', 'See Greggoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grievable', 'a.', 'Lamentable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grievance', 'v. t.', 'A cause of uneasiness and complaint; a wrong done and\n   suffered; that which gives ground for remonstrance or resistance, as\n   arising from injustice, tyranny, etc.; injury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grievance', 'v. t.', 'Grieving; grief; affliction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grievancer', 'n.', 'One who occasions a grievance; one who gives ground for\n   complaint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grieve', 'n.', 'Alt. of Greeve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Greeve', 'n.', 'A manager of a farm, or overseer of any work; a reeve; a\n   manorial bailiff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grieved', 'imp. & p. p.', 'of Grieve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grieving', 'p. pr. & vb. n.', 'of Grieve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grieve', 'v. t.', 'To occasion grief to; to wound the sensibilities of; to\n   make sorrowful; to cause to suffer; to afflict; to hurt; to try.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grieve', 'v. t.', 'To sorrow over; as, to grieve ones fate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grieve', 'v. i.', 'To feel grief; to be in pain of mind on account of an\n   evil; to sorrow; to mourn; -- often followed by at, for, or over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Griever', 'n.', 'One who, or that which, grieves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grieving', 'a.', 'Sad; sorrowful; causing grief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grieving', 'n.', 'The act of causing grief; the state of being grieved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grievous', 'a.', 'Causing grief or sorrow; painful; afflictive; hard to\n   bear; offensive; harmful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grievous', 'a.', 'Characterized by great atrocity; heinous; aggravated;\n   flagitious; as, a grievous sin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grievous', 'a.', 'Full of, or expressing, grief; showing great sorrow or\n   affliction; as, a grievous cry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Griff', 'n.', 'Grasp; reach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Griff', 'n.', 'An arrangement of parallel bars for lifting the hooked wires\n   which raise the warp threads in a loom for weaving figured goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Griffe', 'n.', 'The offspring of a mulatto woman and a negro; also, a\n   mulatto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Griffin', 'n.', 'An Anglo-Indian name for a person just arrived from\n   Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Griffin', 'n.', 'Alt. of Griffon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Griffon', 'n.', 'A fabulous monster, half lion and half eagle. It is often\n   represented in Grecian and Roman works of art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Griffon', 'n.', 'A representation of this creature as an heraldic charge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Griffon', 'n.', 'A species of large vulture (Gyps fulvus) found in the\n   mountainous parts of Southern Europe, North Africa, and Asia Minor; --\n   called also gripe, and grype. It is supposed to be the \"eagle\" of the\n   Bible. The bearded griffin is the lammergeir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Griffon', 'n.', 'An English early apple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grig', 'n.', 'A cricket or grasshopper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grig', 'n.', 'Any small eel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grig', 'n.', 'The broad-nosed eel. See Glut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grig', 'n.', 'Heath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gril', 'a.', 'Harsh; hard; severe; stern; rough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grill', 'v. t.', 'A gridiron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grill', 'v. t.', 'That which is broiled on a gridiron, as meat, fish, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grilled', 'imp. & p. p.', 'of Grill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grilling', 'p. pr. & vb. n.', 'of Grill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grill', 'n.', 'To broil on a grill or gridiron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grill', 'n.', 'To torment, as if by broiling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grillade', 'v. t.', 'The act of grilling; also, that which is grilled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grillage', 'n.', 'A framework of sleepers and crossbeams forming a\n   foundation in marshy or treacherous soil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grille', 'v. t.', 'A lattice or grating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grilly', 'v. t.', 'To broil; to grill; hence, To harass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grilse', 'n.', 'A young salmon after its first return from the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grim', 'Compar.', 'Of forbidding or fear-inspiring aspect; fierce; stern;\n   surly; cruel; frightful; horrible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grimace', 'n.', 'A distortion of the countenance, whether habitual, from\n   affectation, or momentary aad occasional, to express some feeling, as\n   contempt, disapprobation, complacency, etc.; a smirk; a made-up face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grimace', 'v. i.', 'To make grimaces; to distort ones face; to make faces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grimaced', 'a.', 'Distorted; crabbed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grimalkin', 'n.', 'An old cat, esp. a she-cat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grime', 'n.', 'Foul matter; dirt, rubbed in; sullying blackness, deeply\n   ingrained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grime', 'v. t.', 'To sully or soil deeply; to dirt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grimily', 'adv.', 'In a grimy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Griminess', 'n.', 'The state of being grimy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grimly', 'a.', 'Grim; hideous; stern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grimly', 'adv.', 'In a grim manner; fiercely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grimme', 'n.', 'A West African antelope (Cephalophus rufilotus) of a deep\n   bay color, with a broad dorsal stripe of black; -- called also\n   conquetoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grimness', 'n.', 'Fierceness of look; sternness; crabbedness;\n   forbiddingness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grimsir', 'n.', 'A stern man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grimy', 'superl.', 'Full of grime; begrimed; dirty; foul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grin', 'n.', 'A snare; a gin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grinned', 'imp. & p. p.', 'of Grin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grinning', 'p. pr. & vb. n.', 'of Grin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grin', 'v. i.', 'To show the teeth, as a dog; to snarl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grin', 'v. i.', 'To set the teeth together and open the lips, or to open\n   the mouth and withdraw the lips from the teeth, so as to show them, as\n   in laughter, scorn, or pain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grin', 'v. t.', 'To express by grinning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grin', 'n.', 'The act of closing the teeth and showing them, or of\n   withdrawing the lips and showing the teeth; a hard, forced, or sneering\n   smile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ground', 'imp. & p. p.', 'of Grind');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grinding', 'p. pr. & vb. n.', 'of Grind');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grind', 'v. t.', 'To reduce to powder by friction, as in a mill, or with\n   the teeth; to crush into small fragments; to produce as by the action\n   of millstones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grind', 'v. t.', 'To wear down, polish, or sharpen, by friction; to make\n   smooth, sharp, or pointed; to whet, as a knife or drill; to rub against\n   one another, as teeth, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grind', 'v. t.', 'To oppress by severe exactions; to harass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grind', 'v. t.', 'To study hard for examination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grind', 'v. i.', 'To perform the operation of grinding something; to turn\n   the millstones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grind', 'v. i.', 'To become ground or pulverized by friction; as, this corn\n   grinds well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grind', 'v. i.', 'To become polished or sharpened by friction; as, glass\n   grinds smooth; steel grinds to a sharp edge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grind', 'v. i.', 'To move with much difficulty or friction; to grate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grind', 'v. i.', 'To perform hard aud distasteful service; to drudge; to\n   study hard, as for an examination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grind', 'n.', 'The act of reducing to powder, or of sharpening, by\n   friction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grind', 'n.', 'Any severe continuous work or occupation; esp., hard and\n   uninteresting study.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grind', 'n.', 'A hard student; a dig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grinded', 'p. p.', 'Ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grindelia', 'n.', 'The dried stems and leaves of tarweed (Grindelia), used\n   as a remedy in asthma and bronchitis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grinder', 'n.', 'One who, or that which, grinds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grinder', 'n.', 'One of the double teeth, used to grind or masticate the\n   food; a molar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grinder', 'n.', 'The restless flycatcher (Seisura inquieta) of Australia;\n   -- called also restless thrush and volatile thrush. It makes a noise\n   like a scissors grinder, to which the name alludes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grindery', 'n.', 'Leather workers materials.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grinding', 'a. & n.', 'from Grind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grindingly', 'adv.', 'In a grinding manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grindle', 'n.', 'The bowfin; -- called also Johnny Grindle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grindle stone', '', 'A grindstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grindlet', 'n.', 'A small drain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grindstone', 'n.', 'A flat, circular stone, revolving on an axle, for\n   grinding or sharpening tools, or shaping or smoothing objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grinner', 'n.', 'One who grins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grinningly', 'adv.', 'In a grinning manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grint', '', '3d pers. sing. pres. of Grind, contr. from grindeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grinte', '', 'imp. of Grin, v. i., 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grinting', 'n.', 'Grinding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grip', 'n.', 'The griffin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grip', 'n.', 'A small ditch or furrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grip', 'v. t.', 'To trench; to drain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grip', 'v. t.', 'An energetic or tenacious grasp; a holding fast; strength\n   in grasping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grip', 'v. t.', 'A peculiar mode of clasping the hand, by which members of\n   a secret association recognize or greet, one another; as, a masonic\n   grip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grip', 'v. t.', 'That by which anything is grasped; a handle or gripe; as,\n   the grip of a sword.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grip', 'v. t.', 'A device for grasping or holding fast to something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grip', 'v. t.', 'To give a grip to; to grasp; to gripe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gripe', 'n.', 'A vulture; the griffin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Griped', 'imp. & p. p.', 'of Gripe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Griping', 'p. pr. & vb. n.', 'of Gripe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gripe', 'v. t.', 'To catch with the hand; to clasp closely with the\n   fingers; to clutch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gripe', 'v. t.', 'To seize and hold fast; to embrace closely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gripe', 'v. t.', 'To pinch; to distress. Specifically, to cause pinching\n   and spasmodic pain to the bowels of, as by the effects of certain\n   purgative or indigestible substances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gripe', 'v. i.', 'To clutch, hold, or pinch a thing, esp. money, with a\n   gripe or as with a gripe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gripe', 'v. i.', 'To suffer griping pains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gripe', 'v. i.', 'To tend to come up into the wind, as a ship which, when\n   sailing closehauled, requires constant labor at the helm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gripe', 'n.', 'Grasp; seizure; fast hold; clutch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gripe', 'n.', 'That on which the grasp is put; a handle; a grip; as, the\n   gripe of a sword.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gripe', 'n.', 'A device for grasping or holding anything; a brake to stop a\n   wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gripe', 'n.', 'Oppression; cruel exaction; affiction; pinching distress;\n   as, the gripe of poverty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gripe', 'n.', 'Pinching and spasmodic pain in the intestines; -- chiefly\n   used in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gripe', 'n.', 'The piece of timber which terminates the keel at the fore\n   end; the forefoot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gripe', 'n.', 'The compass or sharpness of a ships stern under the water,\n   having a tendency to make her keep a good wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gripe', 'n.', 'An assemblage of ropes, dead-eyes, and hocks, fastened to\n   ringbolts in the deck, to secure the boats when hoisted; also, broad\n   bands passed around a boat to secure it at the davits and prevent\n   swinging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gripeful', 'a.', 'Disposed to gripe; extortionate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Griper', 'a.', 'One who gripes; an oppressor; an extortioner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gripingly', 'adv.', 'In a griping or oppressive manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Griman', 'n.', 'The man who manipulates a grip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grippe', 'n.', 'The influenza or epidemic catarrh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gripper', 'n.', 'One who, or that which, grips or seizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gripper', 'n.', 'In printing presses, the fingers or nippers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gripple', 'n.', 'A grasp; a gripe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gripple', 'a.', 'Griping; greedy; covetous; tenacious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grippleness', 'n.', 'The quality of being gripple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gripsack', 'n.', 'A travelers handbag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gris', 'a.', 'Gray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gris', 'a.', 'A costly kind of fur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gris', 'n. sing. & pl.', 'A little pig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grisaille', 'n.', 'Decorative painting in gray monochrome; -- used in\n   English especially for painted glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grisaille', 'n.', 'A kind of French fancy dress goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grisamber', 'n.', 'Ambergris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grise', 'n.', 'See Grice, a pig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grise', 'n.', 'A step (in a flight of stairs); a degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Griseous', 'a.', 'Of a light color, or white, mottled with black or brown;\n   grizzled or grizzly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grisette', 'n.', 'A French girl or young married woman of the lower class;\n   more frequently, a young working woman who is fond of gallantry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Griskin', 'n.', 'The spine of a hog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grisled', 'a.', 'See Grizzled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grisliness', 'n.', 'The quality or state of being grisly; horrid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grisly', 'a.', 'Frightful; horrible; dreadful; harsh; as, grisly locks; a\n   grisly specter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grison', 'n.', 'A South American animal of the family Mustelidae (Galictis\n   vittata). It is about two feet long, exclusive of the tail. Its under\n   parts are black. Also called South American glutton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grison', 'n.', 'A South American monkey (Lagothrix infumatus), said to be\n   gluttonous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grisons', 'n. pl.', 'Inhabitants of the eastern Swiss Alps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grisons', 'n. pl.', 'The largest and most eastern of the Swiss cantons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grist', 'n.', 'Ground corn; that which is ground at one time; as much grain\n   as is carried to the mill at one time, or the meal it produces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grist', 'n.', 'Supply; provision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grist', 'n.', 'In rope making, a given size of rope, common grist being a\n   rope three inches in circumference, with twenty yarns in each of the\n   three strands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gristle', 'n.', 'Cartilage. See Cartilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gristly', 'a.', 'Consisting of, or containing, gristle; like gristle;\n   cartilaginous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gristmill', 'n.', 'A mill for grinding grain; especially, a mill for\n   grinding grists, or portions of grain brought by different customers; a\n   custom mill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grit', 'n.', 'Sand or gravel; rough, hard particles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grit', 'n.', 'The coarse part of meal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grit', 'n.', 'Grain, esp. oats or wheat, hulled and coarsely ground; in\n   high milling, fragments of cracked wheat smaller than groats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grit', 'n.', 'A hard, coarse-grained siliceous sandstone; as, millstone\n   grit; -- called also gritrock and gritstone. The name is also applied\n   to a finer sharp-grained sandstone; as, grindstone grit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grit', 'n.', 'Structure, as adapted to grind or sharpen; as, a hone of good\n   grit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grit', 'n.', 'Firmness of mind; invincible spirit; unyielding courage;\n   fortitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grit', 'v. i.', 'To give forth a grating sound, as sand under the feet; to\n   grate; to grind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gritted', 'imp. & p. p.', 'of Grit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gritting', 'p. pr. &, vb. n.', 'of Grit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grit', 'v. t.', 'To grind; to rub harshly together; to grate; as, to grit\n   the teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grith', 'n.', 'Peace; security; agreement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gritrock', 'n.', 'Alt. of Gritstone');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gritstone', 'n.', 'See Grit, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grittiness', 'n.', 'The quality of being gritty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gritty', 'a.', 'Containing sand or grit; consisting of grit; caused by\n   grit; full of hard particles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gritty', 'a.', 'Spirited; resolute; unyielding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grivet', 'n.', 'A monkey of the upper Nile and Abyssinia (Cercopithecus\n   griseo-viridis), having the upper parts dull green, the lower parts\n   white, the hands, ears, and face black. It was known to the ancient\n   Egyptians. Called also tota.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grize', 'n.', 'Same as 2d Grise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grizelin', 'a.', 'See Gridelin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grizzle', 'n.', 'Gray; a gray color; a mixture of white and black.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grizzled', 'a.', 'Gray; grayish; sprinkled or mixed with gray; of a mixed\n   white and black.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grizzly', 'a.', 'Somewhat gray; grizzled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grizzlies', 'pl. ', 'of Grizzly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grizzly', 'n.', 'A grizzly bear. See under Grizzly, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grizzly', 'a.', 'In hydraulic mining, gratings used to catch and throw out\n   large stones from the sluices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groaned', 'imp. & p. p.', 'of Groan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groaning', 'p. pr. & vb. n.', 'of Groan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groan', 'v. i.', 'To give forth a low, moaning sound in breathing; to utter\n   a groan, as in pain, in sorrow, or in derision; to moan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groan', 'v. i.', 'To strive after earnestly, as with groans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groan', 'v. t.', 'To affect by groans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groan', 'n.', 'A low, moaning sound; usually, a deep, mournful sound\n   uttered in pain or great distress; sometimes, an expression of strong\n   disapprobation; as, the remark was received with groans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groanful', 'a.', 'Agonizing; sad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groat', 'n.', 'An old English silver coin, equal to four pence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groat', 'n.', 'Any small sum of money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groats', 'n. pl.', 'Dried grain, as oats or wheat, hulled and broken or\n   crushed; in high milling, cracked fragments of wheat larger than grits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grocer', 'n.', 'A trader who deals in tea, sugar, spices, coffee, fruits,\n   and various other commodities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groceries', 'pl. ', 'of Grocery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grocery', 'n.', 'The commodities sold by grocers, as tea, coffee, spices,\n   etc.; -- in the United States almost always in the plural form, in this\n   sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grocery', 'n.', 'A retail grocers shop or store.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grog', 'n.', 'A mixture of spirit and water not sweetened; hence, any\n   intoxicating liquor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groggeries', 'pl. ', 'of Groggery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groggery', 'n.', 'A grogshop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grogginess', 'n.', 'State of being groggy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grogginess', 'n.', 'Tenderness or stiffness in the foot of a horse, which\n   causes him to move in a hobbling manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groggy', 'a.', 'Overcome with grog; tipsy; unsteady on the legs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groggy', 'a.', 'Weakened in a fight so as to stagger; -- said of pugilists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groggy', 'a.', 'Moving in a hobbling manner, owing to ten der feet; -- said\n   of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grogram', 'n.', 'Alt. of Grogran');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grogran', 'n.', 'A coarse stuff made of silk and mohair, or of coarse silk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grogshop', 'n.', 'A shop or room where strong liquors are sold and drunk; a\n   dramshop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groin', 'n.', 'The snout of a swine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groin', 'v. i.', 'To grunt to growl; to snarl; to murmur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groin', 'n.', 'The line between the lower part of the abdomen and the\n   thigh, or the region of this line; the inguen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groin', 'n.', 'The projecting solid angle formed by the meeting of two\n   vaults, growing more obtuse as it approaches the summit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groin', 'n.', 'The surface formed by two such vaults.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groin', 'n.', 'A frame of woodwork across a beach to accumulate and retain\n   shingle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groined', 'imp. & p. p.', 'of Groin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groining', 'p. pr. & vb. n.', 'of Groin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groin', 'v. t.', 'To fashion into groins; to build with groins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groined', 'a.', 'Built with groins; as, a groined ceiling; a groined vault.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gromet', 'n.', 'Same as Grommet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gromill', 'n.', 'See Gromwell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grommet', 'n.', 'A ring formed by twisting on itself a single strand of an\n   unlaid rope; also, a metallic eyelet in or for a sail or a mailbag.\n   Sometimes written grummet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grommet', 'n.', 'A ring of rope used as a wad to hold a cannon ball in\n   place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gromwell', 'n.', 'A plant of the genus Lithospermum (L. arvense), anciently\n   used, because of its stony pericarp, in the cure of gravel. The German\n   gromwell is the Stellera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grond', '', 'obs. imp. of Grind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gronte', '', 'obs. imp. of Groan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groom', 'n.', 'A boy or young man; a waiter; a servant; especially, a man\n   or boy who has charge of horses, or the stable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groom', 'n.', 'One of several officers of the English royal household,\n   chiefly in the lord chamberlains department; as, the groom of the\n   chamber; the groom of the stole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groom', 'n.', 'A man recently married, or about to be married; a\n   bridegroom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groomed', 'imp. & p. p.', 'of Groom');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grooming', 'p. pr. & vb. n.', 'of Groom');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groom', 'v. i.', 'To tend or care for, or to curry or clean, as a, horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groomer', 'n.', 'One who, or that which, grooms horses; especially, a brush\n   rotated by a flexible or jointed revolving shaft, for cleaning horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groomsmen', 'pl. ', 'of Groomsman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groomsman', 'n.', 'A male attendant of a bridegroom at his wedding; -- the\n   correlative of bridesmaid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grooper', 'n.', 'See Grouper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groove', 'n.', 'A furrow, channel, or long hollow, such as may be formed by\n   cutting, molding, grinding, the wearing force of flowing water, or\n   constant travel; a depressed way; a worn path; a rut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groove', 'n.', 'Hence: The habitual course of life, work, or affairs; fixed\n   routine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groove', 'n.', 'A shaft or excavation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grooved', 'imp. & p. p.', 'of Groove');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groving', 'p. pr. & vb. n.', 'of Groove');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groove', 'v. t.', 'To cut a groove or channel in; to form into channels or\n   grooves; to furrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groover', 'n.', 'One who or that which grooves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groover', 'n.', 'A miner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grooving', 'n.', 'The act of forming a groove or grooves; a groove, or\n   collection of grooves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groped', 'imp. & p. p.', 'of Grope');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groping', 'p. pr. & vb. n.', 'of Grope');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grope', 'v. i.', 'To feel with or use the hands; to handle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grope', 'v. i.', 'To search or attempt to find something in the dark, or,\n   as a blind person, by feeling; to move about hesitatingly, as in\n   darkness or obscurity; to feel ones way, as with the hands, when one\n   can not see.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grope', 'v. t.', 'To search out by feeling in the dark; as, we groped our\n   way at midnight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grope', 'v. t.', 'To examine; to test; to sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groper', 'n.', 'One who gropes; one who feels his way in the dark, or\n   searches by feeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groping-ly', 'adv.', 'In a groping manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gros', 'n.', 'A heavy silk with a dull finish; as, gros de Naples; gros de\n   Tours.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grosbeak', 'n.', 'One of various species of finches having a large, stout\n   beak. The common European grosbeak or hawfinch is Coccothraustes\n   vulgaris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groschen', 'n.', 'A small silver coin and money of account of Germany,\n   worth about two cents. It is not included in the new monetary system of\n   the empire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grosgrain', 'a.', 'Of a coarse texture; -- applied to silk with a heavy\n   thread running crosswise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gross', 'superl.', 'Great; large; bulky; fat; of huge size; excessively\n   large.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gross', 'superl.', 'Coarse; rough; not fine or delicate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gross', 'superl.', 'Not easily aroused or excited; not sensitive in\n   perception or feeling; dull; witless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gross', 'superl.', 'Expressing, Or originating in, animal or sensual\n   appetites; hence, coarse, vulgar, low, obscene, or impure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gross', 'superl.', 'Thick; dense; not attenuated; as, a gross medium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gross', 'superl.', 'Great; palpable; serious; vagrant; shameful; as, a\n   gross mistake; gross injustice; gross negligence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gross', 'superl.', 'Whole; entire; total; without deduction; as, the gross\n   sum, or gross amount, the gross weight; -- opposed to net.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gross', 'a.', 'The main body; the chief part, bulk, or mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gross', 'sing. & pl.', 'The number of twelve dozen; twelve times twelve;\n   as, a gross of bottles; ten gross of pens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grossbeak', 'n.', 'See Grosbeak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gross-headed', 'a.', 'Thick-skulled; stupid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grossification', 'n.', 'The act of making gross or thick, or the state of\n   becoming so.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grossification', 'n.', 'The swelling of the ovary of plants after\n   fertilization. Henslow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grossly', 'adv.', 'In a gross manner; greatly; coarsely; without delicacy;\n   shamefully; disgracefully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grossness', 'n.', 'The state or quality of being gross; thickness;\n   corpulence; coarseness; shamefulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grossular', 'a.', 'Pertaining too, or resembling, a gooseberry; as,\n   grossular garnet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grossular', 'a.', 'A translucent garnet of a pale green color like that of\n   the gooseberry; -- called also grossularite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grossularia', 'n.', 'Same as Grossular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grossulin', 'n.', 'A vegetable jelly, resembling pectin, found in\n   gooseberries (Ribes Grossularia) and other fruits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grot', 'n.', 'A grotto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grot', 'n.', 'Alt. of Grote');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grote', 'n.', 'A groat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grotesgue', 'a.', 'Like the figures found in ancient grottoes; grottolike;\n   wildly or strangely formed; whimsical; extravagant; of irregular forms\n   and proportions; fantastic; ludicrous; antic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grotesque', 'n.', 'A whimsical figure, or scene, such as is found in old\n   crypts and grottoes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grotesque', 'n.', 'Artificial grotto-work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grotesquely', 'adv.', 'In a grotesque manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grotesqueness', 'n.', 'Quality of being grotesque.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grottoes', 'pl. ', 'of Grotto');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grotto', 'n.', 'A natural covered opening in the earth; a cave; also, an\n   artificial recess, cave, or cavernlike apartment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grotto-work', 'n.', 'Artificial and ornamental rockwork in imitation of a\n   grotto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ground', 'n.', 'The surface of the earth; the outer crust of the globe, or\n   some indefinite portion of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ground', 'n.', 'A floor or pavement supposed to rest upon the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ground', 'n.', 'Any definite portion of the earths surface; region;\n   territory; country. Hence: A territory appropriated to, or resorted to,\n   for a particular purpose; the field or place of action; as, a hunting\n   or fishing ground; a play ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ground', 'n.', 'Land; estate; possession; field; esp. (pl.), the gardens,\n   lawns, fields, etc., belonging to a homestead; as, the grounds of the\n   estate are well kept.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ground', 'n.', 'The basis on which anything rests; foundation. Hence: The\n   foundation of knowledge, belief, or conviction; a premise, reason, or\n   datum; ultimate or first principle; cause of existence or occurrence;\n   originating force or agency; as, the ground of my hope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ground', 'n.', 'That surface upon which the figures of a composition are\n   set, and which relieves them by its plainness, being either of one tint\n   or of tints but slightly contrasted with one another; as, crimson\n   Bowers on a white ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ground', 'n.', 'In sculpture, a flat surface upon which figures are raised\n   in relief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ground', 'n.', 'In point lace, the net of small meshes upon which the\n   embroidered pattern is applied; as, Brussels ground. See Brussels lace,\n   under Brussels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ground', 'n.', 'A gummy composition spread over the surface of a metal to\n   be etched, to prevent the acid from eating except where an opening is\n   made by the needle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ground', 'n.', 'One of the pieces of wood, flush with the plastering, to\n   which moldings, etc., are attached; -- usually in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ground', 'n.', 'A composition in which the bass, consisting of a few bars\n   of independent notes, is continually repeated to a varying melody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ground', 'n.', 'The tune on which descants are raised; the plain song.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ground', 'n.', 'A conducting connection with the earth, whereby the earth\n   is made part of an electrical circuit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ground', 'n.', 'Sediment at the bottom of liquors or liquids; dregs; lees;\n   feces; as, coffee grounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ground', 'n.', 'The pit of a theater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grounded', 'imp. & p. p.', 'of Ground');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grounding', 'p. pr. & vb. n.', 'of Ground');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ground', 'v. t.', 'To lay, set, or run, on the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ground', 'v. t.', 'To found; to fix or set, as on a foundation, reason, or\n   principle; to furnish a ground for; to fix firmly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ground', 'v. t.', 'To instruct in elements or first principles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ground', 'v. t.', 'To connect with the ground so as to make the earth a\n   part of an electrical circuit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ground', 'v. t.', 'To cover with a ground, as a copper plate for etching\n   (see Ground, n., 5); or as paper or other materials with a uniform tint\n   as a preparation for ornament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ground', 'v. i.', 'To run aground; to strike the bottom and remain fixed;\n   as, the ship grounded on the bar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ground', '', 'imp. & p. p. of Grind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groundage', 'n.', 'A local tax paid by a ship for the ground or space it\n   occupies while in port.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groundedly', 'adv.', 'In a grounded or firmly established manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grounden', '', 'p. p. of Grind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grounding', 'n.', 'The act, method, or process of laying a groundwork or\n   foundation; hence, elementary instruction; the act or process of\n   applying a ground, as of color, to wall paper, cotton cloth, etc.; a\n   basis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groundless', 'a.', 'Without ground or foundation; wanting cause or reason\n   for support; not authorized; false; as, groundless fear; a groundless\n   report or assertion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groundling', 'n.', 'A fish that keeps at the bottom of the water, as the\n   loach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groundling', 'n.', 'A spectator in the pit of a theater, which formerly was\n   on the ground, and without floor or benches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groundly', 'adv.', 'Solidly; deeply; thoroughly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groundnut', 'n.', 'The fruit of the Arachis hypogaea (native country\n   uncertain); the peanut; the earthnut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groundnut', 'n.', 'A leguminous, twining plant (Apios tuberosa), producing\n   clusters of dark purple flowers and having a root tuberous and pleasant\n   to the taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groundnut', 'n.', 'The dwarf ginseng (Aralia trifolia).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groundnut', 'n.', 'A European plant of the genus Bunium (B. flexuosum),\n   having an edible root of a globular shape and sweet, aromatic taste; --\n   called also earthnut, earth chestnut, hawknut, and pignut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groundsel', 'v.', 'An annual composite plant (Senecio vulgaris), one of the\n   most common and widely distributed weeds on the globe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groundsel', 'n.', 'Alt. of Groundsill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groundsill', 'n.', 'See Ground plate (a), under Ground');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groundwork', 'n.', 'That which forms the foundation or support of anything;\n   the basis; the essential or fundamental part; first principle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Group', 'n.', 'A cluster, crowd, or throng; an assemblage, either of\n   persons or things, collected without any regular form or arrangement;\n   as, a group of men or of trees; a group of isles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Group', 'n.', 'An assemblage of objects in a certain order or relation, or\n   having some resemblance or common characteristic; as, groups of strata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Group', 'n.', 'A variously limited assemblage of animals or plants, having\n   some resemblance, or common characteristics in form or structure. The\n   term has different uses, and may be made to include certain species of\n   a genus, or a whole genus, or certain genera, or even several orders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Group', 'n.', 'A number of eighth, sixteenth, etc., notes joined at the\n   stems; -- sometimes rather indefinitely applied to any ornament made up\n   of a few short notes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grouped', 'imp. & p. p.', 'of Group');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grouping', 'p. pr. & vb. n.', 'of Group');
  INSERT INTO words (word, word_type, description) VALUES  (    'Group', 'n.', 'To form a group of; to arrange or combine in a group or in\n   groups, often with reference to mutual relation and the best effect; to\n   form an assemblage of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grouper', 'n.', 'One of several species of valuable food fishes of the\n   genus Epinephelus, of the family Serranidae, as the red grouper, or\n   brown snapper (E. morio), and the black grouper, or warsaw (E.\n   nigritus), both from Florida and the Gulf of Mexico.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grouper', 'n.', 'The tripletail (Lobotes).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grouper', 'n.', 'In California, the name is often applied to the\n   rockfishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grouping', 'n.', 'The disposal or relative arrangement of figures or\n   objects, as in, drawing, painting, and sculpture, or in ornamental\n   design.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grouse', 'n. sing. & pl.', 'Any of the numerous species of gallinaceous\n   birds of the family Tetraonidae, and subfamily Tetraoninae, inhabiting\n   Europe, Asia, and North America. They have plump bodies, strong,\n   well-feathered legs, and usually mottled plumage. The group includes\n   the ptarmigans (Lagopus), having feathered feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grouse', 'v. i.', 'To seek or shoot grouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grouse', 'v. i.', 'To complain or grumble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grouser', 'n.', 'A pointed timber attached to a boat and sliding\n   vertically, to thrust into the ground as a means of anchorage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grout', 'n.', 'Coarse meal; ground malt; pl. groats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grout', 'n.', 'Formerly, a kind of beer or ale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grout', 'n.', 'Lees; dregs; grounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grout', 'n.', 'A thin, coarse mortar, used for pouring into the joints of\n   masonry and brickwork; also, a finer material, used in finishing the\n   best ceilings. Gwilt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grouted', 'imp. & p. p.', 'of Grout');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grouting', 'p. pr. & vb. n.', 'of Grout');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grout', 'v. t.', 'To fill up or finish with grout, as the joints between\n   stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grouthead', 'n.', 'See Growthead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grouting', 'n.', 'The process of filling in or finishing with grout; also,\n   the grout thus filled in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groutnol', 'n.', 'Same as Growthead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grouty', 'a.', 'Cross; sulky; sullen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grove', 'v.', 'A smaller group of trees than a forest, and without\n   underwood, planted, or growing naturally as if arranged by art; a wood\n   of small extent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groveled', 'imp. & p. p.', 'of Grovel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grovelled', '', 'of Grovel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groveling', 'p. pr. & vb. n.', 'of Grovel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grovelling', '', 'of Grovel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grovel', 'adv.', 'To creep on the earth, or with the face to the ground; to\n   lie prone, or move uneasily with the body prostrate on the earth; to\n   lie fiat on ones belly, expressive of abjectness; to crawl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grovel', 'adv.', 'To tend toward, or delight in, what is sensual or base;\n   to be low, abject, or mean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groveler', 'n.', 'One who grovels; an abject wretch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groveling', 'a.', 'Lying prone; low; debased.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grovy', 'a.', 'Pertaining to, or resembling, a grove; situated in, or\n   frequenting, groves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grew', 'imp.', 'of Grow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grown', 'p. p.', 'of Grow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Growing', 'p. pr. & vb. n.', 'of Grow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grow', 'v. i.', 'To increase in size by a natural and organic process; to\n   increase in bulk by the gradual assimilation of new matter into the\n   living organism; -- said of animals and vegetables and their organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grow', 'v. i.', 'To increase in any way; to become larger and stronger; to\n   be augmented; to advance; to extend; to wax; to accrue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grow', 'v. i.', 'To spring up and come to matturity in a natural way; to be\n   produced by vegetation; to thrive; to flourish; as, rice grows in warm\n   countries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grow', 'v. i.', 'To pass from one state to another; to result as an effect\n   from a cause; to become; as, to grow pale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grow', 'v. i.', 'To become attached of fixed; to adhere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grow', 'v. t.', 'To cause to grow; to cultivate; to produce; as, to grow a\n   crop; to grow wheat, hops, or tobacco.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Growable', 'a.', 'Capable of growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Growan', 'n.', 'A decomposed granite, forming a mass of gravel, as in tin\n   lodes in Cornwall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grower', 'n.', 'One who grows or produces; as, a grower of corn; also, that\n   which grows or increases; as, a vine may be a rank or a slow grower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Growled', 'imp. & p. p.', 'of Growl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Growling', 'p. pr. & vb. e.', 'of Growl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Growl', 'v. i.', 'To utter a deep guttural sound, sa an angry dog; to give\n   forth an angry, grumbling sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Growl', 'v. t.', 'To express by growling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Growl', 'n.', 'The deep, threatening sound made by a surly dog; a grumbling\n   sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Growler', 'n.', 'One who growls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Growler', 'n.', 'The large-mouthed black bass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Growler', 'n.', 'A four-wheeled cab.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Growlingly', 'adv.', 'In a growling manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grown', '', 'p. p. of Grow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Growse', 'v. i.', 'To shiver; to have chills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Growth', 'n.', 'The process of growing; the gradual increase of an animal\n   or a vegetable body; the development from a seed, germ, or root, to\n   full size or maturity; increase in size, number, frequency, strength,\n   etc.; augmentation; advancement; production; prevalence or influence;\n   as, the growth of trade; the growth of power; the growth of\n   intemperance. Idle weeds are fast in growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Growth', 'n.', 'That which has grown or is growing; anything produced;\n   product; consequence; effect; result.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Growthead', 'n.', 'A lazy person; a blockhead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Growthful', 'a.', 'Having capacity of growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Groyne', 'n.', 'See Groin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grozing iron', '', 'A tool with a hardened steel point, formerly used\n   instead of a diamond for cutting glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grozing iron', '', 'A tool for smoothing the solder joints of lead pipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grubbed', 'imp. & p. p.', 'of Grub');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grubbing', 'p. pr. & vb. n.', 'of Grub');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grub', 'v. i.', 'To dig in or under the ground, generally for an object\n   that is difficult to reach or extricate; to be occupied in digging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grub', 'v. i.', 'To drudge; to do menial work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grub', 'v. t.', 'To dig; to dig up by the roots; to root out by digging; --\n   followed by up; as, to grub up trees, rushes, or sedge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grub', 'v. t.', 'To supply with food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grub', 'n.', 'The larva of an insect, especially of a beetle; -- called\n   also grubworm. See Illust. of Goldsmith beetle, under Goldsmith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grub', 'n.', 'A short, thick man; a dwarf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grub', 'n.', 'Victuals; food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grubber', 'n.', 'One who, or that which, grubs; especially, a machine or\n   tool of the nature of a grub ax, grub hook, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grubbla', 'v. t. & i.', 'To feel or grope in the dark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grubby', 'a.', 'Dirty; unclean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grubby', 'n.', 'Any species of Cottus; a sculpin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grubworm', 'n.', 'See Grub, n., 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grucche', 'v. i.', 'To murmur; to grumble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grudger', 'imp. & p. p.', 'of Grudge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grudging', 'p. pr. & vb. n.', 'of Grudge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grudge', 'v. t.', 'To look upon with desire to possess or to appropriate;\n   to envy (one) the possession of; to begrudge; to covet; to give with\n   reluctance; to desire to get back again; -- followed by the direct\n   object only, or by both the direct and indirect objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grudge', 'v. t.', 'To hold or harbor with malicioua disposition or purpose;\n   to cherish enviously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grudge', 'v. i.', 'To be covetous or envious; to show discontent; to\n   murmur; to complain; to repine; to be unwilling or reluctant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grudge', 'v. i.', 'To feel compunction or grief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grudge', 'n.', 'Sullen malice or malevolence; cherished malice, enmity, or\n   dislike; ill will; an old cause of hatred or quarrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grudge', 'n.', 'Slight symptom of disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grudgeful', 'a.', 'Full of grudge; envious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grudgeons', 'n. pl.', 'Alt. of Gurgeons');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gurgeons', 'n. pl.', 'Coarse meal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gruddger', 'n.', 'One who grudges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grudgingly', 'adv.', 'In a grudging manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grudgingness', 'n.', 'The state or quality of grudging, or of being full of\n   grudge or unwillingness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gruel', 'n.', 'A light, liquid food, made by boiling meal of maize,\n   oatmeal, or fiour in water or milk; thin porridge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gruelly', 'a.', 'Like gruel; of the consistence of gruel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gruesome', 'a.', 'Same as Grewsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gruf', 'adv.', 'Forwards; with ones face to the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gruff', 'superl.', 'Of a rough or stern manner, voice, or countenance;\n   sour; surly; severe; harsh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grugru palm', '', 'A West Indian name for several kinds of palm. See Macaw\n   tree, under Macaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grugru worm', '', 'The larva or grub of a large South American beetle\n   (Calandra palmarum), which lives in the pith of palm trees and sugar\n   cane. It is eaten by the natives, and esteemed a delicacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grum', 'a.', 'Morose; severe of countenance; sour; surly; glum; grim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grum', 'a.', 'Low; deep in the throat; guttural; rumbling; as,');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grunbled', 'imp. & p. p.', 'of Grumble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grumbling', 'p. pr. & vb. n.', 'of Grumble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grumble', 'v. i.', 'To murmur or mutter with discontent; to make\n   ill-natured complaints in a low voice and a surly manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grumble', 'v. i.', 'To growl; to snarl in deep tones; as, a lion grumbling\n   over his prey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grumble', 'v. i.', 'To rumble; to make a low, harsh, and heavy sound; to\n   mutter; as, the distant thunder grumbles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grumble', 'v. t.', 'To express or utter with grumbling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grumble', 'n.', 'The noise of one that grumbles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grumble', 'n.', 'A grumbling, discontented disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grumbler', 'n.', 'One who grumbles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grumblingly', 'adv.', 'In a grumbling manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grume', 'n.', 'A thick, viscid fluid; a clot, as of blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grumbly', 'adv.', 'In a grum manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grumose', 'a.', 'Clustered in grains at intervals; grumous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grumous', 'a.', 'Resembling or containing grume; thick; concreted; clotted;\n   as, grumous blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grumous', 'a.', 'See Grumose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grumousness', 'n.', 'The state of being grumous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grumpily', 'adv.', 'In a surly manner; sullenly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grumpy', 'a.', 'Surly; dissatisfied; grouty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grundel', 'n.', 'A groundling (fish).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grundsel', 'n.', 'Groundsel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grunted', 'imp. & p. p.', 'of Grunt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grunting', 'p. pr. & vb. n.', 'of Grunt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grunt', 'v. t.', 'To make a deep, short noise, as a hog; to utter a short\n   groan or a deep guttural sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grunt', 'n.', 'A deep, guttural sound, as of a hog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grunt', 'n.', 'Any one of several species of American food fishes, of the\n   genus Haemulon, allied to the snappers, as, the black grunt (A.\n   Plumieri), and the redmouth grunt (H. aurolineatus), of the Southern\n   United States; -- also applied to allied species of the genera\n   Pomadasys, Orthopristis, and Pristopoma. Called also pigfish, squirrel\n   fish, and grunter; -- so called from the noise it makes when taken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grunter', 'n.', 'One who, or that which, grunts; specifically, a hog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grunter', 'n.', 'One of several American marine fishes. See Sea robin, and\n   Grunt, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grunter', 'n.', 'A hook used in lifting a crucible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gruntingly', 'adv.', 'In a grunting manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gruntle', 'v. i.', 'To grunt; to grunt repeatedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gruntling', 'n.', 'A young hog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grutch', 'v.', 'See Grudge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gruyere cheese', '', 'A kind of cheese made at Gruyere, Switzerland. It is\n   a firm cheese containing numerous cells, and is known in the United\n   States as Schweitzerkase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gry', 'n.', 'A measure equal to one tenth of a line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gry', 'n.', 'Anything very small, or of little value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gryde', 'v. i.', 'To gride. See Gride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gryfon', 'n.', 'See Griffin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gryllus', 'n.', 'A genus of insects including the common crickets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grype', 'v. t.', 'To gripe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grype', 'n.', 'A vulture; the griffin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gryphaea', 'n.', 'A genus of cretaceous fossil shells allied to the oyster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gryphite', 'n.', 'A shell of the genus Gryphea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gryphon', 'n.', 'The griffin vulture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Grysbok', 'n.', 'A small South African antelope (Neotragus melanotis). It\n   is speckled with gray and chestnut, above; the under parts are reddish\n   fawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guacharo', 'n.', 'A nocturnal bird of South America and Trinidad\n   (Steatornis Caripensis, or S. steatornis); -- called also oilbird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guachos', 'pl. ', 'of Guacho');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guacho', 'n.', 'One of the mixed-blood (Spanish-Indian) inhabitants of the\n   pampas of South America; a mestizo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guacho', 'n.', 'An Indian who serves as a messenger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guaco', 'n.', 'A plant (Aristolochia anguicida) of Carthagena, used as an\n   antidote to serpent bites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guaco', 'n.', 'The Mikania Guaco, of Brazil, used for the same purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guaiac', 'a.', 'Pertaining to, or resembling, guaiacum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guaiac', 'n.', 'Guaiacum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guaiacum', 'n.', 'A genus of small, crooked trees, growing in tropical\n   America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guaiacum', 'n.', 'The heart wood or the resin of the Guaiacum offinale or\n   lignum-vitae, a large tree of the West Indies and Central America. It\n   is much used in medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guan', 'n.', 'Any one of many species of large gallinaceous birds of\n   Central and South America, belonging to Penelope, Pipile, Ortalis, and\n   allied genera. Several of the species are often domesticated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guana', 'n.', 'See Iguana.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guanacos', 'pl. ', 'of Guanaco');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guanaco', 'n.', 'A South American mammal (Auchenia huanaco), allied to the\n   llama, but of larger size and more graceful form, inhabiting the\n   southern Andes and Patagonia. It is supposed by some to be the llama in\n   a wild state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guanidine', 'n.', 'A strongly alkaline base, CN3H5, formed by the oxidation\n   of guanin, and also obtained combined with methyl in the decomposition\n   of creatin. Boiled with dilute sulphuric acid, it yields urea and\n   ammonia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guaniferous', 'a.', 'Yielding guano.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guanin', 'n.', 'A crystalline substance (C5H5N5O) contained in guano. It is\n   also a constituent of the liver, pancreas, and other glands in mammals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guanos', 'pl. ', 'of Guano');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guano', 'n.', 'A substance found in great abundance on some coasts or\n   islands frequented by sea fowls, and composed chiefly of their\n   excrement. It is rich in phosphates and ammonia, and is used as a\n   powerful fertilizer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guara', 'n.', 'The scarlet ibis. See Ibis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guara', 'n.', 'A large-maned wild dog of South America (Canis jubatus) --\n   named from its cry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guarana', 'n.', 'A preparation from the seeds of Paullinia sorbilis, a\n   woody climber of Brazil, used in making an astringent drink, and also\n   in the cure of headache.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guaranine', 'n.', 'An alkaloid extracted from guarana. Same as Caffeine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guarantees', 'pl. ', 'of Guarantee');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guarantee', 'n.', 'In law and common usage: A promise to answer for the\n   payment of some debt, or the performance of some duty, in case of the\n   failure of another person, who is, in the first instance, liable to\n   such payment or performance; an engagement which secures or insures\n   another against a contingency; a warranty; a security. Same as\n   Guaranty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guarantee', 'n.', 'One who binds himself to see an undertaking of another\n   performed; a guarantor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guarantee', 'n.', 'The person to whom a guaranty is made; -- the\n   correlative of guarantor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'guaranteed', 'imp. & p. p.', 'of Guarantee');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guaranteeing', 'p, pr. & vb. n.', 'of Guarantee');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guarantee', 'n.', 'In law and common usage: to undertake or engage for the\n   payment of (a debt) or the performance of (a duty) by another person;\n   to undertake to secure (a possession, right, claim, etc.) to another\n   against a specified contingency, or at all avents; to give a guarantee\n   concerning; to engage, assure, or secure as a thing that may be\n   depended on; to warrant; as, to guarantee the execution of a treaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guarantor', 'n.', 'One who makes or gives a guaranty; a warrantor; a\n   surety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guarantor', 'n.', 'One who engages to secure another in any right or\n   possession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guaranies', 'pl. ', 'of Guaranty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guaranty', 'n.', 'In law and common usage: An undertaking to answer for the\n   payment of some debt, or the performance of some contract or duty, of\n   another, in case of the failure of such other to pay or perform; a\n   guarantee; a warranty; a security.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guarantied', 'imp. & p. p.', 'of Guaranty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guarantying', 'p. pr. & vb. n.', 'of Guaranty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guaranty', 'n.', 'In law and common usage: To undertake or engage that\n   another person shall perform (what he has stipulated); to undertake to\n   be answerable for (the debt or default of another); to engage to answer\n   for the performance of (some promise or duty by another) in case of a\n   failure by the latter to perform; to undertake to secure (something) to\n   another, as in the case of a contingency. See Guarantee, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guarded', 'imp. & p. p.', 'of Guard');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gurding', 'p. pr. &, vb. n.', 'of Guard');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guard', 'n.', 'To protect from danger; to secure against surprise, attack,\n   or injury; to keep in safety; to defend; to shelter; to shield from\n   surprise or attack; to protect by attendance; to accompany for\n   protection; to care for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guard', 'n.', 'To keep watch over, in order to prevent escape or restrain\n   from acts of violence, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guard', 'n.', 'To protect the edge of, esp. with an ornamental border;\n   hence, to face or ornament with lists, laces, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guard', 'n.', 'To fasten by binding; to gird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guard', 'v. i.', 'To watch by way of caution or defense; to be caution; to\n   be in a state or position of defense or safety; as, careful persons\n   guard against mistakes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guard', 'v. t.', 'One who, or that which, guards from injury, danger,\n   exposure, or attack; defense; protection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guard', 'v. t.', 'A man, or body of men, stationed to protect or control a\n   person or position; a watch; a sentinel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guard', 'v. t.', 'One who has charge of a mail coach or a railway train; a\n   conductor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guard', 'v. t.', 'Any fixture or attachment designed to protect or secure\n   against injury, soiling, or defacement, theft or loss');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guard', 'v. t.', 'That part of a sword hilt which protects the hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guard', 'v. t.', 'Ornamental lace or hem protecting the edge of a garment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guard', 'v. t.', 'A chain or cord for fastening a watch to ones person or\n   dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guard', 'v. t.', 'A fence or rail to prevent falling from the deck of a\n   vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guard', 'v. t.', 'An extension of the deck of a vessel beyond the hull;\n   esp., in side-wheel steam vessels, the framework of strong timbers,\n   which curves out on each side beyond the paddle wheel, and protects it\n   and the shaft against collision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guard', 'v. t.', 'A plate of metal, beneath the stock, or the lock frame,\n   of a gun or pistol, having a loop, called a bow, to protect the\n   trigger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guard', 'v. t.', 'An interleaved strip at the back, as in a scrap book, to\n   guard against its breaking when filled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guard', 'v. t.', 'A posture of defense in fencing, and in bayonet and saber\n   exercise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guard', 'v. t.', 'An expression or admission intended to secure against\n   objections or censure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guard', 'v. t.', 'Watch; heed; care; attention; as, to keep guard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guard', 'v. t.', 'The fibrous sheath which covers the phragmacone of the\n   Belemnites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guardable', 'v. t.', 'Capable of being guarded or protected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guardage', 'v. t.', 'Wardship');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guardant', 'v. t.', 'Acting as guardian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guardant', 'v. t.', 'Same as Gardant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guardant', 'n.', 'A guardian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guarded', 'a.', 'Cautious; wary; circumspect; as, he was guarded in his\n   expressions; framed or uttered with caution; as, his expressions were\n   guarded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guardenage', 'n.', 'Guardianship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guarder', 'n.', 'One who guards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guardfish', 'n.', 'The garfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guardful', 'a.', 'Cautions; wary; watchful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guardhouse', 'n.', 'A building which is occupied by the guard, and in which\n   soldiers are confined for misconduct; hence, a lock-up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guardian', 'v. t.', 'One who guards, preserves, or secures; one to whom any\n   person or thing is committed for protection, security, or preservation\n   from injury; a warden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guardian', 'v. t.', 'One who has, or is entitled to, the custody of the\n   person or property of an infant, a minor without living parents, or a\n   person incapable of managing his own affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guardian', 'a.', 'Performing, or appropriate to, the office of a protector;\n   as, a guardian care.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guardianage', 'n.', 'Guardianship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guardiance', 'n.', 'Guardianship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guardianess', 'n.', 'A female guardian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guardianless', 'a.', 'Without a guardian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guardianship', 'n.', 'The office, duty, or care, of a guardian; protection;\n   care; watch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guardless', 'a.', 'Without a guard or defense; unguarded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guardroom', 'n.', 'The room occupied by the guard during its term of duty;\n   also, a room where prisoners are confined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guards', 'n. pl.', 'A body of picked troops; as, \"The Household Guards.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guardship', 'n.', 'Care; protection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guardsmen', 'pl. ', 'of Guardsman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guardsman', 'n.', 'One who guards; a guard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guardsman', 'n.', 'A member, either officer or private, of any military\n   body called Guards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guarish', 'v. t.', 'To heal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guatemala grass', '', 'See Teosinte.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guava', 'n.', 'A tropical tree, or its fruit, of the genus Psidium. Two\n   varieties are well known, the P. pyriferum, or white guava, and P.\n   pomiferum, or red guava. The fruit or berry is shaped like a\n   pomegranate, but is much smaller. It is somewhat astringent, but makes\n   a delicious jelly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gubernance', 'n.', 'Government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gubernate', 'v. t.', 'To govern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gubernation', 'n.', 'The act of governing; government');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gubernative', 'a.', 'Governing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gubernatorial', 'a.', 'Pertaining to a governor, or to government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gudgeon', 'n.', 'A small European freshwater fish (Gobio fluviatilis),\n   allied to the carp. It is easily caught and often used for food and for\n   bait. In America the killifishes or minnows are often called gudgeons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gudgeon', 'n.', 'What may be got without skill or merit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gudgeon', 'n.', 'A person easily duped or cheated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gudgeon', 'n.', 'The pin of iron fastened in the end of a wooden shaft or\n   axle, on which it turns; formerly, any journal, or pivot, or bearing,\n   as the pintle and eye of a hinge, but esp. the end journal of a\n   horizontal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gudgeon', 'n.', 'A metal eye or socket attached to the sternpost to receive\n   the pintle of the rudder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gudgeon', 'v. t.', 'To deprive fraudulently; to cheat; to dupe; to impose\n   upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gue', 'n.', 'A sharper; a rogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gueber', 'n.', 'Alt. of Guebre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guebre', 'n.', 'Same as Gheber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guelderrose', 'n.', 'A cultivated variety of a species of Viburnum (V.\n   Opulus), bearing large bunches of white flowers; -- called also\n   snowball tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guelph', 'n.', 'Alt. of Guelf');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guelf', 'n.', 'One of a faction in Germany and Italy, in the 12th and 13th\n   centuries, which supported the House of Guelph and the pope, and\n   opposed the Ghibellines, or faction of the German emperors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guelphic', 'a.', 'Alt. of Guelfic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guelfic', 'a.', 'Of or pertaining to the family or the faction of the\n   Guelphs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guenon', 'n.', 'One of several long-tailed Oriental monkeys, of the genus\n   Cercocebus, as the green monkey and grivet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gueparde', 'n.', 'The cheetah.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guerdon', 'n.', 'A reward; requital; recompense; -- used in both a good and\n   a bad sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guerdon', 'n.', 'To give guerdon to; to reward; to be a recompense for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guerdonable', 'a.', 'Worthy of reward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guerdonless', 'a.', 'Without reward or guerdon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guereza', 'n.', 'A beautiful Abyssinian monkey (Colobus guereza), having\n   the body black, with a fringe of long, silky, white hair along the\n   sides, and a tuft of the same at the end of the tail. The frontal band,\n   cheeks, and chin are white.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guerilla', 'a.', 'See Guerrilla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guerite', 'n.', 'A projecting turret for a sentry, as at the salient angles\n   of works, or the acute angles of bastions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guernsey lily', '', 'A South African plant (Nerine Sarniensis) with\n   handsome lilylike flowers, naturalized on the island of Guernsey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guerrilla', 'n.', 'An irregular mode of carrying on war, by the constant\n   attacks of independent bands, adopted in the north of Spain during the\n   Peninsular war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guerrilla', 'n.', 'One who carries on, or assists in carrying on, irregular\n   warfare; especially, a member of an independent band engaged in\n   predatory excursions in war time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guerrilla', 'a.', 'Pertaining to, or engaged in, warfare carried on\n   irregularly and by independent bands; as, a guerrilla party; guerrilla\n   warfare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guessed', 'imp. & p. p.', 'of Guess');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guessing', 'p. pr. & vb. n.', 'of Guess');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guess', 'v. t.', 'To form an opinion concerning, without knowledge or means\n   of knowledge; to judge of at random; to conjecture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guess', 'v. t.', 'To judge or form an opinion of, from reasons that seem\n   preponderating, but are not decisive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guess', 'v. t.', 'To solve by a correct conjecture; to conjecture rightly;\n   as, he who guesses the riddle shall have the ring; he has guessed my\n   designs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guess', 'v. t.', 'To hit upon or reproduce by memory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guess', 'v. t.', 'To think; to suppose; to believe; to imagine; -- followed\n   by an objective clause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guess', 'v. i.', 'To make a guess or random judgment; to conjecture; --\n   with at, about, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guess', 'n.', 'An opinion as to anything, formed without sufficient or\n   decisive evidence or grounds; an attempt to hit upon the truth by a\n   random judgment; a conjecture; a surmise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guessable', 'a.', 'Capable of being guessed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guesser', 'n.', 'One who guesses; one who forms or gives an opinion without\n   means of knowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guessingly', 'adv.', 'By way of conjecture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guessive', 'a.', 'Conjectural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guess rope', '', 'A guess warp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guess warp', '', 'A rope or hawser by which a vessel is towed or warped\n   along; -- so called because it is necessary to guess at the length to\n   be carried in the boat making the attachment to a distant object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guesswork', 'n.', 'Work performed, or results obtained, by guess;\n   conjecture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guest', 'n.', 'A visitor; a person received and entertained in ones house\n   or at ones table; a visitor entertained without pay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guest', 'v. t.', 'To receive or entertain hospitably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guest', 'v. i.', 'To be, or act the part of, a guest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guest rope', '', 'The line by which a boat makes fast to the swinging boom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guestwise', 'adv.', 'In the manner of a guest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guevi', 'n.', 'One of several very small species and varieties of African\n   antelopes, of the genus Cephalophus, as the Cape guevi or kleeneboc\n   (Cephalophus pygmaea); -- called also pygmy antelope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guffaw', 'n.', 'A loud burst of laughter; a horse laugh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guffer', 'n.', 'The eelpout; guffer eel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guggle', 'v. i.', 'See Gurgle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guhr', 'n.', 'A loose, earthy deposit from water, found in the cavities or\n   clefts of rocks, mostly white, but sometimes red or yellow, from a\n   mixture of clay or ocher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guiac', 'n.', 'Same as Guaiac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guiacol', 'n.', 'A colorless liquid, C6H4,OCH3.OH, resembling the phenols,\n   found as a constituent of woodtar creosote, aud produced by the dry\n   distillation of guaiac resin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guiacum', 'n.', 'Same as Guaiacum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guib', 'n.', 'A West African antelope (Tragelaphus scriptus), curiously\n   marked with white stripes and spots on a reddish fawn ground, and hence\n   called harnessed antelope; -- called also guiba.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guicowar', 'n.', '[Mahratta g/ekw/r, prop., a cowherd.] The title of the\n   sovereign of Guzerat, in Western India; -- generally called the\n   Guicowar of Baroda, which is the capital of the country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guidable', 'a.', 'Capable of being guided; willing to be guided or\n   counseled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guidage', 'n.', 'The reward given to a guide for services.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guidage', 'n.', 'Guidance; lead; direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guidance', 'n.', 'The act or result of guiding; the superintendence or\n   assistance of a guide; direction; government; a leading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guided', 'imp. & p. p.', 'of Guide');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guiding', 'p. pr. & vb. n.', 'of Guide');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guide', 'v. t.', 'To lead or direct in a way; to conduct in a course or\n   path; to pilot; as, to guide a traveler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guide', 'v. t.', 'To regulate and manage; to direct; to order; to\n   superintend the training or education of; to instruct and influence\n   intellectually or morally; to train.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guide', 'v. t.', 'A person who leads or directs another in his way or\n   course, as in a strange land; one who exhibits points of interest to\n   strangers; a conductor; also, that which guides; a guidebook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guide', 'v. t.', 'One who, or that which, directs another in his conduct or\n   course of lifo; a director; a regulator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guide', 'v. t.', 'Any contrivance, especially one having a directing edge,\n   surface, or channel, for giving direction to the motion of anything, as\n   water, an instrument, or part of a machine, or for directing the hand\n   or eye, as of an operator');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guide', 'v. t.', 'A blade or channel for directing the flow of water to the\n   wheel buckets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guide', 'v. t.', 'A grooved director for a probe or knife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guide', 'v. t.', 'A strip or device to direct the compositors eye to the\n   line of copy he is setting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guide', 'v. t.', 'A noncommissioned officer or soldier placed on the\n   directiug flank of each subdivision of a column of troops, or at the\n   end of a line, to mark the pivots, formations, marches, and alignments\n   in tactics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guideboard', 'n.', 'A board, as upon a guidepost having upon it directions\n   or information as to the road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guidebook', 'n.', 'A book of directions and information for travelers,\n   tourists, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guideless', 'a.', 'Without a guide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guidepost', 'n.', 'A post at the fork of a road, with a guideboard on it,\n   to direct travelers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guider', 'n.', 'A guide; a director.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guideress', 'n.', 'A female guide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guidguid', 'n.', 'A South American ant bird of the genus Hylactes; --\n   called also barking bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guidon', 'v. t.', 'A small flag or streamer, as that carried by cavalry,\n   which is broad at one end and nearly pointed at the other, or that used\n   to direct the movements of a body of infantry, or to make signals at\n   sea; also, the flag of a guild or fraternity. In the United States\n   service, each company of cavalry has a guidon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guidon', 'v. t.', 'One who carries a flag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guidon', 'v. t.', 'One of a community established at Rome, by Charlemagne,\n   to guide pilgrims to the Holy Land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gulge', 'n.', 'See Gige.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guild', 'v. t.', 'An association of men belonging to the same class, or\n   engaged in kindred pursuits, formed for mutual aid and protection; a\n   business fraternity or corporation; as, the Stationers Guild; the\n   Ironmongers Guild. They were originally licensed by the government,\n   and endowed with special privileges and authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guild', 'v. t.', 'A guildhall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guild', 'v. t.', 'A religious association or society, organized for\n   charitable purposes or for assistance in parish work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guildable', 'a.', 'Liable to a tax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guilder', 'n.', 'A Dutch silver coin worth about forty cents; -- called\n   also florin and gulden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guildhall', 'n.', 'The hall where a guild or corporation usually assembles;\n   a townhall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guile', 'n.', 'Craft; deceitful cunning; artifice; duplicity; wile; deceit;\n   treachery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guile', 'n.', 'To disguise or conceal; to deceive or delude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guileful', 'a.', 'Full of guile; characterized by cunning, deceit, or\n   treachery; guilty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guileless', 'a.', 'Free from guile; artless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guilor', 'n.', 'A deceiver; one who deludes, or uses guile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guillemet', 'n.', 'A quotation mark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guillemot', 'n.', 'One of several northern sea birds, allied to the auks.\n   They have short legs, placed far back, and are expert divers and\n   swimmers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guillevat', 'n.', 'A vat for fermenting liquors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guilloche', 'n.', 'An ornament in the form of two or more bands or strings\n   twisted over each other in a continued series, leaving circular\n   openings which are filled with round ornaments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guilloched', 'a.', 'Waved or engine-turned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guillotine', 'n.', 'A machine for beheading a person by one stroke of a\n   heavy ax or blade, which slides in vertical guides, is raised by a\n   cord, and let fall upon the neck of the victim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guillotine', 'n.', 'Any machine or instrument for cutting or shearing,\n   resembling in its action a guillotine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guillotined', 'imp. & p. p.', 'of Guillotine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guillotining', 'p. pr. & vb. n.', 'of Guillotine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guillotine', 'v. t.', 'To behead with the guillotine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guilt', 'v. t.', 'The criminality and consequent exposure to punishment\n   resulting from willful disobedience of law, or from morally wrong\n   action; the state of one who has broken a moral or political law;\n   crime; criminality; offense against right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guilt', 'v. t.', 'Exposure to any legal penalty or forfeiture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guiltily', 'adv.', 'In a guilty manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guiltiness', 'n.', 'The quality or state of being guilty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guiltless', 'a.', 'Free from guilt; innocent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guiltless', 'a.', 'Without experience or trial; unacquainted (with).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guilt-sick', 'a.', 'Made sick by consciousness of guilt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guilty', 'superl.', 'Having incurred guilt; criminal; morally delinquent;\n   wicked; chargeable with, or responsible for, something censurable;\n   justly exposed to penalty; -- used with of, and usually followed by the\n   crime, sometimes by the punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guilty', 'superl.', 'Evincing or indicating guilt; involving guilt; as, a\n   guilty look; a guilty act; a guilty feeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guilty', 'superl.', 'Conscious; cognizant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guilty', 'superl.', 'Condemned to payment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guiltylike', 'adv.', 'Guiltily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guinea', 'n.', 'A district on the west coast of Africa (formerly noted for\n   its export of gold and slaves) after which the Guinea fowl, Guinea\n   grass, Guinea peach, etc., are named.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guinea', 'n.', 'A gold coin of England current for twenty-one shillings\n   sterling, or about five dollars, but not coined since the issue of\n   sovereigns in 1817.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guipure', 'n.', 'A term used for lace of different kinds; most properly for\n   a lace of large pattern and heavy material which has no ground or mesh,\n   but has the pattern held together by connecting threads called bars or\n   brides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guirland', 'n.', 'See Garland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guise', 'n.', 'Customary way of speaking or acting; custom; fashion;\n   manner; behavior; mien; mode; practice; -- often used formerly in such\n   phrases as: at his own guise; that is, in his own fashion, to suit\n   himself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guise', 'n.', 'External appearance in manner or dress; appropriate\n   indication or expression; garb; shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guise', 'n.', 'Cover; cloak; as, under the guise of patriotism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guiser', 'n.', 'A person in disguise; a masker; a mummer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guitar', 'n.', 'A stringed instrument of music resembling the lute or the\n   violin, but larger, and having six strings, three of silk covered with\n   silver wire, and three of catgut, -- played upon with the fingers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guitguit', 'n.', 'One of several species of small tropical American birds\n   of the family Coerebidae, allied to the creepers; -- called also quit.\n   See Quit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'GulAe', 'pl. ', 'of Gula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gulas', 'pl. ', 'of Gula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gula', 'n.', 'The upper front of the neck, next to the chin; the upper\n   throat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gula', 'n.', 'A plate which in most insects supports the submentum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gula', 'n.', 'A capping molding. Same as Cymatium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gular', 'a.', 'Pertaining to the gula or throat; as, gular plates. See\n   Illust. of Bird, and Bowfin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gulaund', 'n.', 'An arctic sea bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gulch', 'n.', 'Act of gulching or gulping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gulch', 'n.', 'A glutton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gulch', 'n.', 'A ravine, or part of the deep bed of a torrent when dry; a\n   gully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gulch', 'v. t.', 'To swallow greedily; to gulp down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guid', 'n.', 'A flower. See Gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gulden', 'n.', 'See Guilder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gule', 'v. t.', 'To give the color of gules to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gule', 'n.', 'The throat; the gullet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gules', 'n.', 'The tincture red, indicated in seals and engraved figures of\n   escutcheons by parallel vertical lines. Hence, used poetically for a\n   red color or that which is red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gulf', 'n.', 'A hollow place in the earth; an abyss; a deep chasm or basin,');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gulf', 'n.', 'That which swallows; the gullet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gulf', 'n.', 'That which swallows irretrievably; a whirlpool; a sucking\n   eddy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gulf', 'n.', 'A portion of an ocean or sea extending into the land; a\n   partially land-locked sea; as, the Gulf of Mexico.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gulf', 'n.', 'A large deposit of ore in a lode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gulfy', 'a.', 'Full of whirlpools or gulfs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gulgul', 'n.', 'A cement made in India from sea shells, pulverized and\n   mixed with oil, and spread over a ships bottom, to prevent the boring\n   of worms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gulist', 'n.', 'A glutton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gulled', 'imp. & p. p.', 'of Gull');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gulling', 'p. pr. & vb. n.', 'of Gull');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gull', 'v. t.', 'To deceive; to cheat; to mislead; to trick; to defraud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gull', 'n.', 'A cheating or cheat; trick; fraud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gull', 'n.', 'One easily cheated; a dupe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gull', 'n.', 'One of many species of long-winged sea birds of the genus\n   Larus and allied genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gullage', 'n.', 'Act of being gulled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guller', 'n.', 'One who gulls; a deceiver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gullery', 'n.', 'An act, or the practice, of gulling; trickery; fraud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gullet', 'n.', 'The tube by which food and drink are carried from the\n   pharynx to the stomach; the esophagus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gullet', 'n.', 'Something shaped like the food passage, or performing\n   similar functions');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gullet', 'n.', 'A channel for water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gullet', 'n.', 'A preparatory cut or channel in excavations, of sufficient\n   width for the passage of earth wagons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gullet', 'n.', 'A concave cut made in the teeth of some saw blades.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gulleting', 'n.', 'A system of excavating by means of gullets or channels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gullible', 'a.', 'Easily gulled; that may be duped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gullish', 'a.', 'Foolish; stupid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gulles', 'pl. ', 'of Gully');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gully', 'n.', 'A large knife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gullies', 'pl. ', 'of Gully');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gully', 'n.', 'A channel or hollow worn in the earth by a current of water;\n   a short deep portion of a torrents bed when dry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gully', 'n.', 'A grooved iron rail or tram plate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gullied', 'imp. & p. p.', 'of Gully');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gullying', 'p. pr. & vb. n.', 'of Gully');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gully', 'v. t.', 'To wear into a gully or into gullies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gully', 'v. i.', 'To flow noisily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gulosity', 'n.', 'Excessive appetite; greediness; voracity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gulped', 'imp. & p. p.', 'of Gulp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gulping', 'p. pr. & vb. n.', 'of Gulp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gulp', 'v. t.', 'To swallow eagerly, or in large draughts; to swallow up;\n   to take down at one swallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gulp', 'n.', 'The act of taking a large mouthful; a swallow, or as much as\n   is awallowed at once.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gulp', 'n.', 'A disgorging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gulph', 'n.', 'See Gulf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gult', 'n.', 'Guilt. See Guilt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gulty', 'a.', 'Guilty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guly', 'a.', 'Of or pertaining to gules; red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gum', 'n.', 'The dense tissues which invest the teeth, and cover the\n   adjacent parts of the jaws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gum', 'v. t.', 'To deepen and enlarge the spaces between the teeth of (a\n   worn saw). See Gummer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gum', 'n.', 'A vegetable secretion of many trees or plants that hardens\n   when it exudes, but is soluble in water; as, gum arabic; gum\n   tragacanth; the gum of the cherry tree. Also, with less propriety,\n   exudations that are not soluble in water; as, gum copal and gum\n   sandarac, which are really resins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gum', 'n.', 'See Gum tree, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gum', 'n.', 'A hive made of a section of a hollow gum tree; hence, any\n   roughly made hive; also, a vessel or bin made of a hollow log.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gum', 'n.', 'A rubber overshoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gummed', 'imp. &. p.', 'of Gum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gumming', 'p. pr. & vb. n.', 'of Gum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gum', 'v. t.', 'To smear with gum; to close with gum; to unite or stiffen\n   by gum or a gumlike substance; to make sticky with a gumlike substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gum', 'v. i.', 'To exude or from gum; to become gummy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gumbo', 'n.', 'A soup thickened with the mucilaginous pods of the okra;\n   okra soup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gumbo', 'n.', 'The okra plant or its pods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gumboil', 'n.', 'A small suppurting inflamed spot on the gum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gummata', 'pl. ', 'of Gumma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gumma', 'n.', 'A kind of soft tumor, usually of syphilitic origin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gummatous', 'a.', 'Belonging to, or resembling, gumma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gummer', 'n.', 'A punch-cutting tool, or machine for deepening and\n   enlarging the spaces between the teeth of a worn saw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gummiferous', 'a.', 'Producing gum; gum-bearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gumminess', 'n.', 'The state or quality of being gummy; viscousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gummite', 'n.', 'A yellow amorphous mineral, essentially a hydrated oxide\n   of uranium derived from the alteration of uraninite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gummosity', 'n.', 'Gumminess; a viscous or adhesive quality or nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gummous', 'a.', 'Gumlike, or composed of gum; gummy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gummous', 'a.', 'Of or pertaining to a gumma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gummy', 'a.', 'Consisting of gum; viscous; adhesive; producing or\n   containing gum; covered with gum or a substance resembling gum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gump', 'n.', 'A dolt; a dunce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gumption', 'n.', 'Capacity; shrewdness; common sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gumption', 'n.', 'The art of preparing colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gumption', 'n.', 'Megilp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gun', 'n.', 'A weapon which throws or propels a missile to a distance; any\n   firearm or instrument for throwing projectiles by the explosion of\n   gunpowder, consisting of a tube or barrel closed at one end, in which\n   the projectile is placed, with an explosive charge behind, which is\n   ignited by various means. Muskets, rifles, carbines, and fowling pieces\n   are smaller guns, for hand use, and are called small arms. Larger guns\n   are called cannon, ordnance, fieldpieces, carronades, howitzers, etc.\n   See these terms in the Vocabulary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gun', 'n.', 'A piece of heavy ordnance; in a restricted sense, a cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gun', 'n.', 'Violent blasts of wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gun', 'v. i.', 'To practice fowling or hunting small game; -- chiefly in\n   participial form; as, to go gunning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guna', 'n.', 'In Sanskrit grammar, a lengthening of the simple vowels a, i,\n   e, by prefixing an a element. The term is sometimes used to denote the\n   same vowel change in other languages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gunarchy', 'n.', 'See Gynarchy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gunboat', 'n.', 'A vessel of light draught, carrying one or more guns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guncotton', '', 'See under Gun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gundelet', 'n.', 'See Gondola.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gunflint', 'n.', 'A sharpened flint for the lock of a gun, to ignite the\n   charge. It was in common use before the introduction of percussion\n   caps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gunjah', 'n.', 'See Ganja.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gunlock', 'n.', 'The lock of a gun, for producing the discharge. See Lock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gunnage', 'n.', 'The number of guns carried by a ship of war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gunnel', 'n.', 'A gunwale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gunnel', 'n.', 'A small, eel-shaped, marine fish of the genus Muraenoides;\n   esp., M. gunnellus of Europe and America; -- called also gunnel fish,\n   butterfish, rock eel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gunner', 'n.', 'One who works a gun, whether on land or sea; a cannoneer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gunner', 'n.', 'A warrant officer in the navy having charge of the ordnance\n   on a vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gunner', 'n.', 'The great northern diver or loon. See Loon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gunner', 'n.', 'The sea bream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gunnery', 'n.', 'That branch of military science which comprehends the\n   theory of projectiles, and the manner of constructing and using\n   ordnance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gunnie', 'n.', 'Space left by the removal of ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gunning', 'n.', 'The act or practice of hunting or shooting game with a\n   gun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gunny', '', 'Alt. of Gunny cloth');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gunny cloth', '', 'A strong, coarse kind of sacking, made from the fibers\n   (called jute) of two plants of the genus Corchorus (C. olitorius and C.\n   capsularis), of India. The fiber is also used in the manufacture of\n   cordage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gunocracy', 'n.', 'See Gyneocracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gunpowder', 'n.', 'A black, granular, explosive substance, consisting of an\n   intimate mechanical mixture of niter, charcoal, and sulphur. It is used\n   in gunnery and blasting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gunreach', 'n.', 'The reach or distance to which a gun will shoot; gunshot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gunroom', 'n.', 'An apartment on the after end of the lower gun deck of a\n   ship of war, usually occupied as a messroom by the commissioned\n   officers, except the captain; -- called wardroom in the United States\n   navy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gunshot', 'n.', 'Act of firing a gun; a shot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gunshot', 'n.', 'The distance to which shot can be thrown from a gun, so as\n   to be effective; the reach or range of a gun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gunshot', 'a.', 'Made by the shot of a gun: as. a gunshot wound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gunsmith', 'n.', 'One whose occupation is to make or repair small firearms;\n   an armorer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gunsmithery', 'n.', 'Alt. of Gunsmith ing');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gunsmith ing', 'n.', 'The art or business of a gunsmith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gunstick', 'n.', 'A stick to ram down the charge of a musket, etc.; a\n   rammer or ramrod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gunstock', 'n.', 'The stock or wood to which the barrel of a hand gun is\n   fastened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gunstome', 'n.', 'A cannon ball; -- so called because originally made of\n   stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gunter rig', '', 'A topmast arranged with metal bands so that it will\n   readily slide up and down the lower mast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gunters chain', '', 'The chain ordinarily used in measuring land. See\n   Chain, n., 4, and Gunters scale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gunters line', '', 'A logarithmic line on Gunters scale, used for\n   performing the multiplication and division of numbers mechanically by\n   the dividers; -- called also line of lines, and line of numbers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gunters quadrant', '', 'A thin quadrant, made of brass, wood, etc.,\n   showing a stereographic projection on the plane of the equator. By it\n   are found the hour of the day, the suns azimuth, the altitude of\n   objects in degrees, etc. See Gunters scale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gunters scale', '', 'A scale invented by the Rev. Edmund Gunter\n   (1581-1626), a professor of astronomy at Gresham College, London, who\n   invented also Gunters chain, and Gunters quadrant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gunwale', 'n.', 'The upper edge of a vessels or boats side; the uppermost\n   wale of a ship (not including the bulwarks); or that piece of timber\n   which reaches on either side from the quarter-deck to the forecastle,\n   being the uppermost bend, which finishes the upper works of the hull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gurge', 'n.', 'A whirlpool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gurge', 'v. t.', 'To swallow up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gurgeons', 'n. pl.', 'See Grudgeons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gurgled', 'imp. & p. p.', 'of Gurgle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gurgling', 'p. pr. & vb. n.', 'of Gurgle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gurgle', 'v. i.', 'To run or flow in a broken, irregular, noisy current, as\n   water from a bottle, or a small stream among pebbles or stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gurgle', 'n.', 'The act of gurgling; a broken, bubbling noise. \"Tinkling\n   gurgles.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gurglet', 'n.', 'A porous earthen jar for cooling water by evaporation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gurgling-ly', 'adv.', 'In a gurgling manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gurgoyle', 'n.', 'See Gargoyle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gurjun', 'n.', 'A thin balsam or wood oil derived from the Diptcrocarpus\n   laevis, an East Indian tree. It is used in medicine, and as a\n   substitute for linseed oil in the coarser kinds of paint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gurl', 'n.', 'A young person of either sex. [Obs.] See Girl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gurlet', 'n.', 'A pickax with one sharp point and one cutting edge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gurmy', 'n.', 'A level; a working.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gurnard', 'n.', 'Alt. of Gurnet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gurnet', 'n.', 'One ofseveral European marine fishes, of the genus Trigla\n   and allied genera, having a large and spiny head, with mailed cheeks.\n   Some of the species are highly esteemed for food. The name is sometimes\n   applied to the American sea robins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gurniad', 'n.', 'See Gwiniad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gurry', 'n.', 'An alvine evacuation; also, refuse matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gurry', 'n.', 'A small fort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gurt', 'n.', 'A gutter or channel for water, hewn out of the bottom of a\n   working drift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gurts', 'n. pl.', 'Groatts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gushed', 'imp. & p. p.', 'of Gush');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gushing', 'p. pr. & vb. n.', 'of Gush');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gush', 'v. i.', 'To issue with violence and rapidity, as a fluid; to rush\n   forth as a fluid from confinement; to flow copiously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gush', 'v. i.', 'To make a sentimental or untimely exhibition of affection;\n   to display enthusiasm in a silly, demonstrative manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gush', 'v. t.', 'A sudden and violent issue of a fluid from an inclosed\n   plase; an emission of a liquid in a large quantity, and with force; the\n   fluid thus emitted; a rapid outpouring of anything; as, a gush of song\n   from a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gush', 'v. t.', 'A sentimental exhibition of affection or enthusiasm, etc.;\n   effusive display of sentiment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gusher', 'n.', 'One who gushes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gushing', 'a.', 'Rushing forth with violence, as a fluid; flowing\n   copiously; as, gushing waters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gushing', 'a.', 'Emitting copiously, as tears or words; weakly and\n   unreservedly demonstrative in matters of affection; sentimental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gushingly', 'adv.', 'In a gushing manner; copiously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gushingly', 'adv.', 'Weakly; sentimentally; effusively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gusset', 'n.', 'A small piece of cloth inserted in a garment, for the\n   purpose of strengthening some part or giving it a tapering enlargement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gusset', 'n.', 'Anything resembling a gusset in a garment');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gusset', 'n.', 'A small piece of chain mail at the openings of the joints\n   beneath the arms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gusset', 'n.', 'A kind of bracket, or angular piece of iron, fastened in\n   the angles of a structure to give strength or stiffness; esp., the part\n   joining the barrel and the fire box of a locomotive boiler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gusset', 'n.', 'An abatement or mark of dishonor in a coat of arms,\n   resembling a gusset.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gust', 'n.', 'A sudden squall; a violent blast of wind; a sudden and brief\n   rushing or driving of the wind. Snow, and hail, stormy gust and flaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gust', 'n.', 'A sudden violent burst of passion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gust', 'n.', 'The sense or pleasure of tasting; relish; gusto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gust', 'n.', 'Gratification of any kind, particularly that which is\n   exquisitely relished; enjoyment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gust', 'n.', 'Intellectual taste; fancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gust', 'v. t.', 'To taste; to have a relish for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gustable', 'v.', 'Capable of being tasted; tastable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gustable', 'v.', 'Pleasant to the taste; toothsome; savory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gustable', 'n.', 'Anything that can be tasted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gustard', 'n.', 'The great bustard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gustation', 'n.', 'The act of tasting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gustatory', 'a.', 'Pertaining to, or subservient to, the sense of taste;\n   as, the gustatory nerve which supplies the front of the tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gustful', 'a.', 'Tasteful; well-tasted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gustful', 'a.', 'Gusty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gustless', 'a.', 'Tasteless; insipid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gusto', 'n.', 'Nice or keen appreciation or enjoyment; relish; taste;\n   fancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gustoso', 'a. & adv.', 'Tasteful; in a tasteful, agreeable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gusty', 'a.', 'Subject to, or characterized by, gusts or squalls; windy;\n   stormy; tempestuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gut', 'n.', 'A narrow passage of water; as, the Gut of Canso.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gut', 'n.', 'An intenstine; a bowel; the whole alimentary canal; the\n   enteron; (pl.) bowels; entrails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gut', 'n.', 'One of the prepared entrails of an animal, esp. of a sheep,\n   used for various purposes. See Catgut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gut', 'n.', 'The sac of silk taken from a silkworm (when ready to spin its\n   cocoon), for the purpose of drawing it out into a thread. This, when\n   dry, is exceedingly strong, and is used as the snood of a fish line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gutted', 'imp. & p. p.', 'of Gut');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gutting', 'p. pr. & vb. n.', 'of Gut');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gut', 'v. t.', 'To take out the bowels from; to eviscerate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gut', 'v. t.', 'To plunder of contents; to destroy or remove the interior\n   or contents of; as, a mob gutted the bouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'GuttAe', 'pl. ', 'of Gutta');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gutta', 'n.', 'A drop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gutta', 'n.', 'One of a series of ornaments, in the form of a frustum of a\n   cone, attached to the lower part of the triglyphs, and also to the\n   lower faces of the mutules, in the Doric order; -- called also campana,\n   and drop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gutta-percha', 'n.', 'A concrete juice produced by various trees found in\n   the Malayan archipelago, especially by the Isonandra, / Dichopsis,\n   Gutta. It becomes soft, and unpressible at the tamperature of boiling\n   water, and, on cooling, retains its new shape. It dissolves in oils and\n   ethers, but not in water. In many of its properties it resembles\n   caoutchouc, and it is extensively used for many economical purposes.\n   The Mimusops globosa of Guiana also yields this material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guttate', 'a.', 'Spotted, as if discolored by drops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guttated', 'a.', 'Besprinkled with drops, or droplike spots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guttatrap', 'n.', 'The inspissated juice of a tree of the genus Artocarpus\n   (A. incisa, or breadfruit tree), sometimes used in making birdlime, on\n   account of its glutinous quality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gutter', 'n.', 'A channel at the eaves of a roof for conveying away the\n   rain; an eaves channel; an eaves trough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gutter', 'n.', 'A small channel at the roadside or elsewhere, to lead off\n   surface water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gutter', 'n.', 'Any narrow channel or groove; as, a gutter formed by\n   erosion in the vent of a gun from repeated firing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guttered', 'imp. & p. p.', 'of Gutter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guttering', 'p. pr. & vb. n.', 'of Gutter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gutter', 'v. t.', 'To cut or form into small longitudinal hollows; to\n   channel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gutter', 'v. t.', 'To supply with a gutter or gutters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gutter', 'v. i.', 'To become channeled, as a candle when the flame flares\n   in the wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guttifer', 'n.', 'A plant that exudes gum or resin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guttiferous', 'a.', 'Yielding gum or resinous substances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guttiferous', 'a.', 'Pertaining to a natural order of trees and shrubs\n   (Guttiferae) noted for their abounding in a resinous sap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guttiform', 'a.', 'Drop-shaped, as a spot of color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guttle', 'n.', 'To put into the gut; to swallow greedily; to gorge; to\n   gormandize. [Obs.] LEstrange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guttler', 'n.', 'A greedy eater; a glutton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guttulous', 'a.', 'In droplike form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guttural', 'a.', 'Of or pertaining to the throat; formed in the throat;\n   relating to, or characteristic of, a sound formed in the throat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guttural', 'n.', 'A sound formed in the throat; esp., a sound formed by the\n   aid of the back of the tongue, much retracted, and the soft palate;\n   also, a letter representing such a sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gutturalism', 'n.', 'The quality of being guttural; as, the gutturalism of\n   A [in the 16th cent.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gutturality', 'n.', 'The quality of being guttural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gutturalize', 'v. t.', 'To speak gutturally; to give a guttural sound to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gutturally', 'adv.', 'In a guttural manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gutturalness', 'n.', 'The quality of being guttural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gutturine', 'a.', 'Pertaining to the throat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gutturize', 'v. t.', 'To make in the throat; to gutturalize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gutturo-', '', 'A combining form denoting relation to the throat; as,\n   gutturo-nasal, having both a guttural and a nasal character;\n   gutturo-palatal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gutty', 'a.', 'Charged or sprinkled with drops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gutwort', 'n.', 'A plant, Globularia Alypum, a violent purgative, found in\n   Africa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guy', 'n.', 'A rope, chain, or rod attached to anything to steady it; as: a\n   rope to steady or guide an object which is being hoisted or lowered; a\n   rope which holds in place the end of a boom, spar, or yard in a ship; a\n   chain or wire rope connecting a suspension bridge with the land on\n   either side to prevent lateral swaying; a rod or rope attached to the\n   top of a structure, as of a derrick, and extending obliquely to the\n   ground, where it is fastened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guyed', 'imp. & p. p.', 'of Guy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guying', 'p. pr. & vb. n.', 'of Guy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guy', 'v. t.', 'To steady or guide with a guy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guy', 'n.', 'A grotesque effigy, like that of Guy Fawkes, dressed up in\n   England on the fifth of November, the day of the Gunpowder Plot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guy', 'n.', 'A person of queer looks or dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guy', 'v. t.', 'To fool; to baffle; to make (a person) an object of\n   ridicule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guyle', 'v. t.', 'To guile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guze', 'n.', 'A roundlet of tincture sanguine, which is blazoned without\n   mention of the tincture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guzzled', 'imp. & p. p.', 'of Guzzle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guzzling', 'p. pr. & vb. n.', 'of Guzzle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guzzle', 'v. i.', 'To swallow liquor greedily; to drink much or frequently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guzzle', 'v. t.', 'To swallow much or often; to swallow with immoderate\n   gust; to drink greedily or continually; as, one who guzzles beer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guzzle', 'n.', 'An insatiable thing or person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Guzzler', 'n.', 'An immoderate drinker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gwiniad', 'n.', 'A fish (Coregonus ferus) of North Wales and Northern\n   Europe, allied to the lake whitefish; -- called also powan, and\n   schelly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyall', 'n.', 'See Gayal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyb', 'n.', 'Alt. of Gybe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gybe', 'n.', 'See Jib.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gybe', 'n. & v.', 'See Gibe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gybed', 'imp. & p. p.', 'of Gybe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gybing', 'p. pr. & vb. n.', 'of Gybe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gybe', 'v. t. & i.', 'To shift from one side of a vessel to the other; --\n   said of the boom of a fore-and-aft sail when the vessel is steered off\n   the wind until the sail fills on the opposite side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gye', 'v. t.', 'To guide; to govern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyle', 'n.', 'Fermented wort used for making vinegar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnal', 'a. & n.', 'Same as Gimmal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnasiarch', 'n.', 'An Athenian officer who superintended the gymnasia,\n   and provided the oil and other necessaries at his own expense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnasiums', 'pl. ', 'of Gymnasium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnasia', 'pl. ', 'of Gymnasium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnasium', 'n.', 'A place or building where athletic exercises are\n   performed; a school for gymnastics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnasium', 'n.', 'A school for the higher branches of literature and\n   science; a preparatory school for the university; -- used esp. of\n   German schools of this kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnast', 'n.', 'One who teaches or practices gymnastic exercises; the\n   manager of a gymnasium; an athlete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnastic', 'a.', 'Alt. of Gymnastical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnastical', 'a.', 'Pertaining to athletic exercises intended for health,\n   defense, or diversion; -- said of games or exercises, as running,\n   leaping, wrestling, throwing the discus, the javelin, etc.; also,\n   pertaining to disciplinary exercises for the intellect; athletic; as,\n   gymnastic exercises, contests, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnastic', 'n.', 'A gymnast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnastically', 'adv.', 'In a gymnastic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnastics', 'n.', 'Athletic or disciplinary exercises; the art of\n   performing gymnastic exercises; also, disciplinary exercises for the\n   intellect or character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnic', 'a.', 'Alt. of Gymnical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnical', 'a.', 'Athletic; gymnastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnic', 'n.', 'Athletic exercise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnite', 'n.', 'A hydrous silicate of magnesia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnoblastea', 'n. pl.', 'The Athecata; -- so called because the medusoid\n   buds are not inclosed in a capsule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnoblastic', 'a.', 'Of or pertaining to the Gymnoblastea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnocarpous', 'a.', 'Naked-fruited, the fruit either smooth or not\n   adherent to the perianth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnochroa', 'n. pl.', 'A division of Hydroidea including the hydra. See\n   Hydra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnocladus', 'n.', 'A genus of leguminous plants; the Kentucky coffee\n   tree. The leaves are cathartic, and the seeds a substitute for coffee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnocopa', 'n. pl.', 'A group of transparent, free-swimming Annelida,\n   having setae only in the cephalic appendages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnocyte', 'n.', 'A cytode without a proper cell wall, but with a nucleus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnocytode', 'n.', 'A cytode without either a cell wall or a nucleus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnodont', 'n.', 'One of a group of plectognath fishes (Gymnodontes),\n   having the teeth and jaws consolidated into one or two bony plates, on\n   each jaw, as the diodonts and tetradonts. See Bur fish, Globefish,\n   Diodon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnogen', 'n.', 'One of a class of plants, so called by Lindley, because\n   the ovules are fertilized by direct contact of the pollen. Same as\n   Gymnosperm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnoglossa', 'n. pl.', 'A division of gastropods in which the odontophore\n   is without teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnolaema', 'n. pl.', 'Alt. of Gymnolaemata');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnolaemata', 'n. pl.', 'An order of Bryozoa, having no epistome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnonoti', 'n. pl.', 'The order of fishes which includes the Gymnotus or\n   electrical eel. The dorsal fin is wanting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnopaedic', 'a.', 'Having young that are naked when hatched; psilopaedic;\n   -- said of certain birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnophiona', 'n. pl.', 'An order of Amphibia, having a long, annulated,\n   snakelike body. See Ophiomorpha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnophthalmata', 'n. pl.', 'A group of acalephs, including the naked-eyed\n   medusae; the hydromedusae. Most of them are known to be the\n   free-swimming progeny (gonophores) of hydroids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnoplast', 'n.', 'A cell or mass of protoplasm devoid of an envelope, as\n   a white blood corpuscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnorhinal', 'a.', 'Having unfeathered nostrils, as certain birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnosomata', 'n. pl.', 'One of the orders of Pteropoda. They have no\n   shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnosophist', 'n.', 'One of a sect of philosophers, said to have been\n   found in India by Alexander the Great, who went almost naked, denied\n   themselves the use of flesh, renounced bodily pleasures, and employed\n   themselves in the contemplation of nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnosophy', 'n.', 'The doctrines of the Gymnosophists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnosperm', 'n.', 'A plant that bears naked seeds (i. e., seeds not\n   inclosed in an ovary), as the common pine and hemlock. Cf. Angiosperm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnospermous', 'n.', 'Having naked seeds, or seeds not inclosed in a\n   capsule or other vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnospermous', 'n.', 'Belonging to the class of plants consisting of\n   gymnosperms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnotoka', 'n. pl.', 'The Athecata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gymnotus', 'n.', 'A genus of South American fresh-water fishes, including\n   the Gymnotus electricus, or electric eel. It has a greenish, eel-like\n   body, and is possessed of electric power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyn', 'v. i.', 'To begin [Obs.] See Gin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gynaeceum', 'n.', 'Alt. of Gynaecium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gynaecium', 'n.', 'The part of a large house, among the ancients,\n   exclusively appropriated to women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gynaecian', 'a.', 'The same as Gynecian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gynaecophore', 'n.', 'A ventral canal or groove, in which the males of some\n   di/cious trematodes carry the female. See Illust. of Haematozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gynander', 'n.', 'A plant having the stamens inserted in the pistil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gynandria', 'n. pl.', 'A class of plants in the Linnaean system, whose\n   stamens grow out of, or are united with, the pistil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gynandrian', 'a.', 'Alt. of Gynandrous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gynandrous', 'a.', 'Having stamens inserted in the pistil; belonging to the\n   class Gynandria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gynandromorph', 'n.', 'An animal affected with gynandromorphism,');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gynandromorphism', 'n.', 'An abnormal condition of certain animals, in\n   which one side has the external characters of the male, and the other\n   those of the female.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gynandromorphous', 'a.', 'Affected, with gynandromorphism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gynantherous', 'a.', 'Pertaining to an abnormal condition of the flower, in\n   which the stamens are converted into pistils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gynarchy', 'n.', 'Government by a woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyneceum', 'n.', 'See Gynaeceum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gynecian', 'a.', 'Of or relating to women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gynecocracy', 'n.', 'Government by a woman, female power; gyneocracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gynecological', 'a.', 'Of or pertaining to gynecology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gynecology', 'n.', 'The science which treats of the structure and diseases\n   of women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyneocracy', 'n.', 'See Gynecocracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyneolatry', 'n.', 'The adoration or worship of woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gynephobia', 'n.', 'Hatred of women; repugnance to the society of women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gynno', 'v. i.', 'To begin. See Gin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gynobase', 'n.', 'A dilated base or receptacle, supporting a multilocular\n   ovary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gynobasic', 'a.', 'Pertaining to, or having, a gynobase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gynocracy', 'n.', 'Female government; gynecocracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gynodioecious', 'a.', 'Dioecious, but having some hermaphrodite or perfect\n   flowers on an individual plant which bears mostly pistillate flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gynoecium', 'n.', 'The pistils of a flower, taken collectively. See Illust.\n   of Carpophore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gynophore', 'n.', 'The pedicel raising the pistil or ovary above the\n   stamens, as in the passion flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gynophore', 'n.', 'One of the branches bearing the female gonophores, in\n   certain Siphonophora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyp', 'n.', 'A college servant; -- so called in Cambridge, England; at\n   Oxford called a scout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gypse', 'n.', 'See Gypsum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gypseous', 'a.', 'Resembling or containing gypsum; partaking of the\n   qualities of gypsum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gypsey', 'n.', 'A gypsy. See Gypsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gypsiferous', 'a.', 'Containing gypsum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gypsine', 'a.', 'Gypseous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gypsography', 'n.', 'The act or art of engraving on gypsum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gypsoplast', 'n.', 'A cast taken in plaster of Paris, or in white lime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gypsum', 'n.', 'A mineral consisting of the hydrous sulphate of lime\n   (calcium). When calcined, it forms plaster of Paris. Selenite is a\n   transparent, crystalline variety; alabaster, a fine, white, massive\n   variety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gypsies', 'pl. ', 'of Gypsy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gypsy', 'n.', 'One of a vagabond race, whose tribes, coming originally from\n   India, entered Europe in 14th or 15th centry, and are now scattered\n   over Turkey, Russia, Hungary, Spain, England, etc., living by theft,\n   fortune telling, horsejockeying, tinkering, etc. Cf. Bohemian, Romany.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gypsy', 'n.', 'The language used by the gypsies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gypsy', 'n.', 'A dark-complexioned person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gypsy', 'n.', 'A cunning or crafty person');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gypsy', 'a.', 'Pertaining to, or suitable for, gypsies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gypsy', 'v. i.', 'To play the gypsy; to picnic in the woods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gypsyism', 'n.', 'The arts and practices or habits of gypsies; deception;\n   cheating; flattery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gypsyism', 'n.', 'The state of a gypsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gypsywort', 'n.', 'A labiate plant (the Lycopus Europaeus). Gypsies are\n   said to stain their skin with its juice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyracanthus', 'n.', 'A genus of fossil fishes, found in Devonian and\n   carboniferous strata; -- so named from their round, sculptured spines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyral', 'a.', 'Moving in a circular path or way; whirling; gyratory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyral', 'a.', 'Pertaining to a gyrus, or convolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyrant', 'a.', 'Gyrating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyrate', 'a.', 'Winding or coiled round; curved into a circle; taking a\n   circular course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyrated', 'imp. & p. p.', 'of Gyrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyrating', 'p. pr. & vb. n.', 'of Gyrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyrate', 'n.', 'To revolve round a central point; to move spirally about an\n   axis, as a tornado; to revolve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyration', 'n.', 'The act of turning or whirling, as around a fixed center;\n   a circular or spiral motion; motion about an axis; rotation;\n   revolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyration', 'n.', 'One of the whorls of a spiral univalve shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyratory', 'a.', 'Moving in a circle, or spirally; revolving; whirling\n   around.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyre', 'n.', 'A circular motion, or a circle described by a moving body; a\n   turn or revolution; a circuit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyre', 'v. t. & i.', 'To turn round; to gyrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyreful', 'a.', 'Abounding in gyres.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyrencephala', 'n. pl.', 'The higher orders of Mammalia, in which the\n   cerebrum is convoluted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyrfalcon', 'n.', 'One of several species and varieties of large Arctic\n   falcons, esp. Falco rusticolus and the white species F. Islandicus,\n   both of which are circumpolar. The black and the gray are varieties of\n   the former. See Illust. of Accipiter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyri', 'n. pl.', 'See Gyrus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyrland', 'v. t.', 'To garland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyrodus', 'n.', 'A genus of extinct oolitic fishes, having rounded teeth in\n   several rows adapted for crushing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyrogonite', 'n.', 'The petrified fruit of the Chara hispida, a species of\n   stonewort. See Stonewort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyroidal', 'a.', 'Spiral in arrangement or action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyroidal', 'a.', 'Having the planes arranged spirally, so that they incline\n   all to the right (or left) of a vertical line; -- said of certain\n   hemihedral forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyroidal', 'a.', 'Turning the plane of polarization circularly or spirally\n   to the right or left.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyrolepis', 'n.', 'A genus of ganoid fishes, found in strata of the new red\n   sandetone, and the lias bone beds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyroma', 'n.', 'A turning round.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyromancy', 'n.', 'A kind of divination performed by drawing a ring or\n   circle, and walking in or around it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyron', 'n.', 'A subordinary of triangular form having one of its angles at\n   the fess point and the opposite aide at the edge of the escutcheon.\n   When there is only one gyron on the shield it is bounded by two lines\n   drawn from the fess point, one horizontally to the dexter side, and one\n   to the dexter chief corner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyronny', 'a.', 'Covered with gyrons, or divided so as to form several\n   gyrons; -- said of an escutcheon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyropigeon', 'n.', 'A flying object simulating a pigeon in flight, when\n   projected from a spring trap. It is used as a flying target in shooting\n   matches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyroscope', 'n.', 'A rotating wheel, mounted in a ring or rings, for\n   illustrating the dynamics of rotating bodies, the composition of\n   rotations, etc. It was devised by Professor W. R. Johnson, in 1832, by\n   whom it was called the rotascope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyroscope', 'n.', 'A form of the above apparatus, invented by M. Foucault,\n   mounted so delicately as to render visible the rotation of the earth,\n   through the tendency of the rotating wheel to preserve a constant plane\n   of rotation, independently of the earths motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyroscopic', 'a.', 'Pertaining to the gyroscope; resembling the motion of\n   the gyroscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gy-rose', 'a.', 'Turned round like a crook, or bent to and fro.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyrostat', 'n.', 'A modification of the gyroscope, consisting essentially\n   of a fly wheel fixed inside a rigid case to which is attached a thin\n   flange of metal for supporting the instrument. It is used in studying\n   the dynamics of rotating bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyrostatic', 'a.', 'Of or pertaining to the gyrostat or to gyrostatics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyrostatics', 'n.', 'The doctrine or theory of the gyrostat, or of the\n   phenomena of rotating bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyri', 'pl. ', 'of Gyrus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyrus', 'n.', 'A convoluted ridge between grooves; a convolution; as, the\n   gyri of the brain; the gyri of brain coral. See Brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyse', 'n.', 'Guise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyte', 'a.', 'Delirious; senselessly extravagant; as, the man is clean\n   gyte.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyve', 'n.', 'A shackle; especially, one to confine the legs; a fetter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Gyve', 'v. t.', 'To fetter; to shackle; to chain.\n   H () the eighth letter of the English alphabet, is classed among the\n   consonants, and is formed with the mouth organs in the same position as\n   that of the succeeding vowel. It is used with certain consonants to\n   form digraphs representing sounds which are not found in the alphabet,\n   as sh, th, /, as in shall, thing, /ine (for zh see /274); also, to\n   modify the sounds of some other letters, as when placed after c and p,\n   with the former of which it represents a compound sound like that of\n   tsh, as in charm (written also tch as in catch), with the latter, the\n   sound of f, as in phase, phantom. In some words, mostly derived or\n   introduced from foreign languages, h following c and g indicates that\n   those consonants have the hard sound before e, i, and y, as in\n   chemistry, chiromancy, chyle, Ghent, Ghibelline, etc.; in some others,\n   ch has the sound of sh, as in chicane. See Guide to Pronunciation, //\n   153, 179, 181-3, 237-8.');
  INSERT INTO words (word, word_type, description) VALUES  (    'H', '', 'The seventh degree in the diatonic scale, being used by the\n   Germans for B natural. See B.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ha', 'interj.', 'An exclamation denoting surprise, joy, or grief. Both as\n   uttered and as written, it expresses a great variety of emotions,\n   determined by the tone or the context. When repeated, ha, ha, it is an\n   expression of laughter, satisfaction, or triumph, sometimes of derisive\n   laughter; or sometimes it is equivalent to \"Well, it is so.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haaf', 'n.', 'The deepsea fishing for cod, ling, and tusk, off the Shetland\n   Isles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haak', 'n.', 'A sea fish. See Hake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haar', 'n.', 'A fog; esp., a fog or mist with a chill wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habeas corpus', '', 'A writ having for its object to bring a party before a\n   court or judge; especially, one to inquire into the cause of a persons\n   imprisonment or detention by another, with the view to protect the\n   right to personal liberty; also, one to bring a prisoner into court to\n   testify in a pending trial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habendum', 'n.', 'That part of a deed which follows the part called the\n   premises, and determines the extent of the interest or estate granted;\n   -- so called because it begins with the word Habendum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haberdash', 'v. i.', 'To deal in small wares.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haberdasher', 'n.', 'A dealer in small wares, as tapes, pins, needles, and\n   thread; also, a hatter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haberdasher', 'n.', 'A dealer in drapery goods of various descriptions, as\n   laces, silks, trimmings, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haberdashery', 'n.', 'The goods and wares sold by a haberdasher; also\n   (Fig.), trifles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haberdine', 'n.', 'A cod salted and dried.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habergeon', 'n.', 'Properly, a short hauberk, but often used loosely for\n   the hauberk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habilatory', 'a.', 'Of or pertaining to clothing; wearing clothes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habile', 'a.', 'Fit; qualified; also, apt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habiliment', 'n.', 'A garment; an article of clothing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habiliment', 'n.', 'Dress, in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habilimented', 'a.', 'Clothed. Taylor (1630).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habilitate', 'a.', 'Qualified or entitled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habilitate', 'v. t.', 'To fit out; to equip; to qualify; to entitle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habilitation', 'n.', 'Equipment; qualification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hability', 'n.', 'Ability; aptitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habit', 'n.', 'The usual condition or state of a person or thing, either\n   natural or acquired, regarded as something had, possessed, and firmly\n   retained; as, a religious habit; his habit is morose; elms have a\n   spreading habit; esp., physical temperament or constitution; as, a full\n   habit of body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habit', 'n.', 'The general appearance and manner of life of a living\n   organism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habit', 'n.', 'Fixed or established custom; ordinary course of conduct;\n   practice; usage; hence, prominently, the involuntary tendency or\n   aptitude to perform certain actions which is acquired by their frequent\n   repetition; as, habit is second nature; also, peculiar ways of acting;\n   characteristic forms of behavior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habit', 'n.', 'Outward appearance; attire; dress; hence, a garment; esp., a\n   closely fitting garment or dress worn by ladies; as, a riding habit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habited', 'imp. & p. p.', 'of Habit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habiting', 'p. pr. & vb. n.', 'of Habit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habit', 'n.', 'To inhabit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habit', 'n.', 'To dress; to clothe; to array.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habit', 'n.', 'To accustom; to habituate. [Obs.] Chapman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habitability', 'n.', 'Habitableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habitable', 'a.', 'Capable of being inhabited; that may be inhabited or\n   dwelt in; as, the habitable world.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habitakle', 'v.', 'A dwelling place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habitan', 'n.', 'Same as Habitant, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habitance', 'n.', 'Dwelling; abode; residence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habiitancy', 'n.', 'Same as Inhabitancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habitant', 'v. t.', 'An inhabitant; a dweller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habitant', 'v. t.', 'An inhabitant or resident; -- a name applied to and\n   denoting farmers of French descent or origin in Canada, especially in\n   the Province of Quebec; -- usually in plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habitat', 'v. t.', 'The natural abode, locality or region of an animal or\n   plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habitat', 'v. t.', 'Place where anything is commonly found.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habitation', 'n.', 'The act of inhabiting; state of inhabiting or dwelling,\n   or of being inhabited; occupancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habitation', 'n.', 'Place of abode; settled dwelling; residence; house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habitator', 'n.', 'A dweller; an inhabitant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habited', 'p. p. & a.', 'Clothed; arrayed; dressed; as, he was habited like\n   a shepherd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habited', 'p. p. & a.', 'Fixed by habit; accustomed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habited', 'p. p. & a.', 'Inhabited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habitual', 'n.', 'Formed or acquired by habit or use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habitual', 'n.', 'According to habit; established by habit; customary;\n   constant; as, the habiual practice of sin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habituated', 'imp. & p. p.', 'of Habituate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habituating', 'p. pr. & vb. n.', 'of Habituate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habituate', 'v. t.', 'To make accustomed; to accustom; to familiarize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habituate', 'v. t.', 'To settle as an inhabitant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habituate', 'a.', 'Firmly established by custom; formed by habit; habitual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habituation', 'n.', 'The act of habituating, or accustoming; the state of\n   being habituated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habitude', 'n.', 'Habitual attitude; usual or accustomed state with\n   reference to something else; established or usual relations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habitude', 'n.', 'Habitual association, intercourse, or familiarity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habitude', 'n.', 'Habit of body or of action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habitue', 'n.', 'One who habitually frequents a place; as, an habitue of a\n   theater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habiture', 'n.', 'Habitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habitus', 'n.', 'Habitude; mode of life; general appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hable', 'a.', 'See Habile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Habnab', 'adv.', 'By chance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hachure', 'n.', 'A short line used in drawing and engraving, especially in\n   shading and denoting different surfaces, as in map drawing. See\n   Hatching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hacienda', 'n.', 'A large estate where work of any kind is done, as\n   agriculture, manufacturing, mining, or raising of animals; a cultivated\n   farm, with a good house, in distinction from a farming establishment\n   with rude huts for herdsmen, etc.; -- a word used in Spanish-American\n   regions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hack', 'n.', 'A frame or grating of various kinds; as, a frame for drying\n   bricks, fish, or cheese; a rack for feeding cattle; a grating in a mill\n   race, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hack', 'n.', 'Unburned brick or tile, stacked up for drying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hacked', 'imp. & p. p.', 'of Hack');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hacking', 'p. pr. & vb. n.', 'of Hack');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hack', 'v. t.', 'To cut irregulary, without skill or definite purpose; to\n   notch; to mangle by repeated strokes of a cutting instrument; as, to\n   hack a post.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hack', 'v. t.', 'Fig.: To mangle in speaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hack', 'v. i.', 'To cough faintly and frequently, or in a short, broken\n   manner; as, a hacking cough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hack', 'n.', 'A notch; a cut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hack', 'n.', 'An implement for cutting a notch; a large pick used in\n   breaking stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hack', 'n.', 'A hacking; a catch in speaking; a short, broken cough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hack', 'n.', 'A kick on the shins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hack', 'n.', 'A horse, hackneyed or let out for common hire; also, a horse\n   used in all kinds of work, or a saddle horse, as distinguished from\n   hunting and carriage horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hack', 'n.', 'A coach or carriage let for hire; particularly, a a coach\n   with two seats inside facing each other; a hackney coach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hack', 'n.', 'A bookmaker who hires himself out for any sort of literary\n   work; an overworked man; a drudge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hack', 'n.', 'A procuress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hack', 'a.', 'Hackneyed; hired; mercenary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hack', 'v. t.', 'To use as a hack; to let out for hire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hack', 'v. t.', 'To use frequently and indiscriminately, so as to render\n   trite and commonplace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hack', 'v. i.', 'To be exposed or offered or to common use for hire; to\n   turn prostitute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hack', 'v. i.', 'To live the life of a drudge or hack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hackamore', 'n.', 'A halter consisting of a long leather or rope strap and\n   headstall, -- used for leading or tieing a pack animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hackberry', 'n.', 'A genus of trees (Celtis) related to the elm, but\n   bearing drupes with scanty, but often edible, pulp. C. occidentalis is\n   common in the Eastern United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hackbolt', 'n.', 'The greater shearwater or hagdon. See Hagdon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hackbuss', 'n.', 'Same as Hagbut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hackee', 'n.', 'The chipmunk; also, the chickaree or red squirrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hacker', 'n.', 'One who, or that which, hacks. Specifically: A cutting\n   instrument for making notches; esp., one used for notching pine trees\n   in collecting turpentine; a hack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hackery', 'n.', 'A cart with wooden wheels, drawn by bullocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hackle', 'n.', 'A comb for dressing flax, raw silk, etc.; a hatchel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hackle', 'n.', 'Any flimsy substance unspun, as raw silk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hackle', 'n.', 'One of the peculiar, long, narrow feathers on the neck of\n   fowls, most noticeable on the cock, -- often used in making artificial\n   flies; hence, any feather so used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hackle', 'n.', 'An artificial fly for angling, made of feathers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hackled', 'imp. & p. p.', 'of Hackle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hackling', 'p. pr. & vb. n.', 'of Hackle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hackle', 'v. t.', 'To separate, as the coarse part of flax or hemp from the\n   fine, by drawing it through the teeth of a hackle or hatchel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hackle', 'v. t.', 'To tear asunder; to break in pieces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hackly', 'a.', 'Rough or broken, as if hacked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hackly', 'a.', 'Having fine, short, and sharp points on the surface; as,\n   the hackly fracture of metallic iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hackmen', 'pl. ', 'of Hackman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hackman', 'n.', 'The driver of a hack or carriage for public hire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hackmatack', 'n.', 'The American larch (Larix Americana), a coniferous tree\n   with slender deciduous leaves; also, its heavy, close-grained timber.\n   Called also tamarack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hackneys', 'pl. ', 'of Hackney');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hackney', 'n.', 'A horse for riding or driving; a nag; a pony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hackney', 'n.', 'A horse or pony kept for hire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hackney', 'n.', 'A carriage kept for hire; a hack; a hackney coach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hackney', 'n.', 'A hired drudge; a hireling; a prostitute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hackney', 'a.', 'Let out for hire; devoted to common use; hence, much used;\n   trite; mean; as, hackney coaches; hackney authors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hackneyed', 'imp. & p. p.', 'of Hackney');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hackneying', 'p. pr. & vb. n.', 'of Hackney');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hackney', 'v. t.', 'To devote to common or frequent use, as a horse or\n   carriage; to wear out in common service; to make trite or commonplace;\n   as, a hackneyed metaphor or quotation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hackney', 'v. t.', 'To carry in a hackney coach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hackneymen', 'pl. ', 'of Hackneyman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hackneyman', 'n.', 'A man who lets horses and carriages for hire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hackster', 'n.', 'A bully; a bravo; a ruffian; an assassin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hacqueton', 'n.', 'Same as Acton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Had', 'imp. & p. p.', 'See Have.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hadder', 'n.', 'Heather; heath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haddie', 'n.', 'The haddock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haddock', 'n.', 'A marine food fish (Melanogrammus aeglefinus), allied to\n   the cod, inhabiting the northern coasts of Europe and America. It has a\n   dark lateral line and a black spot on each side of the body, just back\n   of the gills. Galled also haddie, and dickie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hade', 'n.', 'The descent of a hill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hade', 'n.', 'The inclination or deviation from the vertical of any mineral\n   vein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hade', 'v. i.', 'To deviate from the vertical; -- said of a vein, fault, or\n   lode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hades', 'n.', 'The nether world (according to classical mythology, the\n   abode of the shades, ruled over by Hades or Pluto); the invisible\n   world; the grave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hadj', 'n.', 'The pilgrimage to Mecca, performed by Mohammedans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hadji', 'n.', 'A Mohammedan pilgrim to Mecca; -- used among Orientals as a\n   respectful salutation or a title of honor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hadji', 'n.', 'A Greek or Armenian who has visited the holy sepulcher at\n   Jerusalem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hadrosaurus', 'n.', 'An American herbivorous dinosaur of great size, allied\n   to the iguanodon. It is found in the Cretaceous formation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haecceity', '', 'Literally, this-ness. A scholastic term to express\n   individuality or singleness; as, this book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haema-', '', 'Alt. of Haemo-');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemato-', '', 'Alt. of Haemo-');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemo-', '', 'Combining forms indicating relation or resemblance to blood,\n   association with blood; as, haemapod, haematogenesis, haemoscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemachrome', 'n.', 'Hematin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemacyanin', 'n.', 'A substance found in the blood of the octopus, which\n   gives to it its blue color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemacytometer', 'n.', 'An apparatus for determining the number of\n   corpuscles in a given quantity of blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemad', 'adv.', 'Toward the haemal side; on the haemal side of; -- opposed\n   to neurad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemadrometer', 'n.', 'Alt. of Haemadremometer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemadremometer', 'n.', 'Same as Hemadrometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemadrometry', 'n.', 'Alt. of Haemadromometry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemadromometry', 'n.', 'Same as Hemadrometry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemadromograph', 'n.', 'An instrument for registering the velocity of the\n   blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemadynameter', '', 'Alt. of Haemadynamometer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemadynamometer', '', 'Same as Hemadynamometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemadynamics', 'n.', 'Same as Hemadynamics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemal', 'a.', 'Pertaining to the blood or blood vessels; also, ventral.\n   See Hemal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemaphaein', 'n.', 'A brownish substance sometimes found in the blood, in\n   cases of jaundice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemapod', 'n.', 'An haemapodous animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemapodous', 'a.', 'Having the limbs on, or directed toward, the ventral\n   or hemal side, as in vertebrates; -- opposed to neuropodous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemapoietic', 'a.', 'Bloodforming; as, the haemapoietic function of the\n   spleen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemapophysis', 'n.', 'Same as Hemapophysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemastatics', 'n.', 'Same as Hemastatics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematachometer', 'n.', 'A form of apparatus (somewhat different from the\n   hemadrometer) for measuring the velocity of the blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematachometry', 'n.', 'The measurement of the velocity of the blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematemesis', 'n.', 'Same as Hematemesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematic', 'a.', 'Of or pertaining to the blood; sanguine; brownish red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematin', 'n.', 'Same as Hematin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematinometer', 'n.', 'Same as Hematinometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematinometric', 'a.', 'Same as Hematinometric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematite', 'n.', 'Same as Hematite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematitic', 'a.', 'Of a blood-red color; crimson; (Bot.) brownish red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemato-', 'prefix.', 'See Haema-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematoblast', 'n.', 'One of the very minute, disk-shaped bodies found in\n   blood with the ordinary red corpuscles and white corpuscles; a third\n   kind of blood corpuscle, supposed by some to be an early stage in the\n   development of the red corpuscles; -- called also blood plaque, and\n   blood plate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematocrya', 'n. pl.', 'The cold-blooded vertebrates. Same as Hematocrya.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematocryal', 'a.', 'Cold-blooded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematocrystallin', 'n.', 'Same as Hematocrystallin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematodynamometer', 'n.', 'Same as Hemadynamometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematogenesis', 'n.', 'The origin and development of blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematogenesis', 'n.', 'The transformation of venous arterial blood by\n   respiration; hematosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematogenic', 'a.', 'Relating to haematogenesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematogenous', 'a.', 'Originating in the blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematoglobulin', 'n.', 'Same as Hematoglobin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematoid', 'a.', 'Same as Hematoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematoidin', 'n.', 'Same as Hematoidin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematoin', 'n.', 'A substance formed from the hematin of blood, by removal\n   of the iron through the action of concentrated sulphuric acid. Two like\n   bodies, called respectively haematoporphyrin and haematolin, are formed\n   in a similar manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematolin', 'n.', 'See Haematoin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematology', 'n.', 'The science which treats of the blood. Same as\n   Hematology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematometer', 'n.', 'Same as Hemadynamometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematometer', 'n.', 'An instrument for determining the number of blood\n   corpuscles in a given quantity of blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematophlina', 'n. pl.', 'A division of Cheiroptera, including the\n   bloodsucking bats. See Vampire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematoplast', 'n.', 'Same as Haematoblast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematoplastic', 'a.', 'Blood formative; -- applied to a substance in early\n   fetal life, which breaks up gradually into blood vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematoporphyrin', 'n.', 'See Haematoin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematosac', 'n.', 'A vascular sac connected, beneath the brain, in many\n   fishes, with the infundibulum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematoscope', 'n.', 'A haemoscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematosin', 'n.', 'Hematin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematosis', 'n.', 'Same as Hematosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematotherma', 'n. pl.', 'Same as Hematotherma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematothermal', 'a.', 'Warm-blooded; homoiothermal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematothorax', 'n.', 'Same as Hemothorax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematexylin', 'n.', 'The coloring principle of logwood. It is obtained as\n   a yellow crystalline substance, C16H14O6, with a sweetish taste.\n   Formerly called also hematin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematoxylon', 'n.', 'A genus of leguminous plants containing but a single\n   species, the H. Campechianum or logwood tree, native in Yucatan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematozoa', 'pl. ', 'of Haematozoon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematozoon', 'n.', 'A parasite inhabiting the blood');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematozoon', 'n.', 'Certain species of nematodes of the genus Filaria,\n   sometimes found in the blood of man, the horse, the dog, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haematozoon', 'n.', 'The trematode, Bilharzia haematobia, which infests the\n   inhabitants of Egypt and other parts of Africa, often causing death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemic', 'a.', 'Pertaining to the blood; hemal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemin', 'n.', 'Same as Hemin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemo-', 'prefix.', 'See Haema-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemochrome', 'n.', 'Same as Haemachrome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemochromogen', 'n.', 'A body obtained from hemoglobin, by the action of\n   reducing agents in the absence of oxygen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemochromometer', 'n.', 'An apparatus for measuring the amount of\n   hemoglobin in a fluid, by comparing it with a solution of known\n   strength and of normal color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemocyanin', 'n.', 'Same as Haemacyanin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemocytolysis', 'n.', 'See Haemocytotrypsis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemocytometer', 'n.', 'See Haemacytometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemocytotrypsis', 'n.', 'A breaking up of the blood corpuscles, as by\n   pressure, in distinction from solution of the corpuscles, or\n   haemcytolysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemodromograph', 'n.', 'Same as Haemadromograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemodynameter', 'n.', 'Same as Hemadynamics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemoglobin', 'n.', 'Same as Hemoglobin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemoglobinometer', 'n.', 'Same as Hemochromometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemolutein', 'n.', 'See Hematoidin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemomanometer', 'n.', 'Same as Hemadynamometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemometer', 'n.', 'Same as Hemadynamometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemony', 'n.', 'A plant described by Milton as \"of sovereign use against\n   all enchantments.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemoplastic', 'a.', 'Same as Haematoplastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemorrhoidal', 'a.', 'Same as Hemorrhoidal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemoscope', 'n.', 'An instrument devised by Hermann, for regulating and\n   measuring the thickness of a layer of blood for spectroscopic\n   examination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemostatic', 'a.', 'Same as Hemostatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemotachometer', 'n.', 'Same as Haematachometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haemotachometry', 'n.', 'Same as Haematachometry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haf', 'imp.', 'Hove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haffle', 'v. i.', 'To stammer; to speak unintelligibly; to prevaricate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haft', 'n.', 'A handle; that part of an instrument or vessel taken into the\n   hand, and by which it is held and used; -- said chiefly of a knife,\n   sword, or dagger; the hilt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haft', 'n.', 'A dwelling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haft', 'v. t.', 'To set in, or furnish with, a haft; as, to haft a dagger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hafter', 'n.', 'A caviler; a wrangler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hag', 'n.', 'A witch, sorceress, or enchantress; also, a wizard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hag', 'n.', 'An ugly old woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hag', 'n.', 'A fury; a she-monster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hag', 'n.', 'An eel-like marine marsipobranch (Myxine glutinosa), allied to\n   the lamprey. It has a suctorial mouth, with labial appendages, and a\n   single pair of gill openings. It is the type of the order Hyperotpeta.\n   Called also hagfish, borer, slime eel, sucker, and sleepmarken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hag', 'n.', 'The hagdon or shearwater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hag', 'n.', 'An appearance of light and fire on a horses mane or a mans\n   hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hagged', 'imp. & p. p.', 'of Hag');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hagging', 'p. pr. & vb. n.', 'of Hag');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hag', 'v. t.', 'To harass; to weary with vexation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hag', 'n.', 'A small wood, or part of a wood or copse, which is marked off\n   or inclosed for felling, or which has been felled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hag', 'n.', 'A quagmire; mossy ground where peat or turf has been cut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hagberry', 'n.', 'A plant of the genus Prunus (P. Padus); the bird cherry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hagborn', 'a.', 'Born of a hag or witch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hagbut', 'n.', 'A harquebus, of which the but was bent down or hooked for\n   convenience in taking aim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hagbutter', 'n.', 'A soldier armed with a hagbut or arquebus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hagdon', 'n.', 'One of several species of sea birds of the genus Puffinus;\n   esp., P. major, the greater shearwarter, and P. Stricklandi, the black\n   hagdon or sooty shearwater; -- called also hagdown, haglin, and hag.\n   See Shearwater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haggadoth', 'pl. ', 'of Haggada');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haggada', 'n.', 'A story, anecdote, or legend in the Talmud, to explain or\n   illustrate the text of the Old Testament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haggard', 'a.', 'Wild or intractable; disposed to break away from duty;\n   untamed; as, a haggard or refractory hawk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haggard', 'a.', 'Having the expression of one wasted by want or suffering;\n   hollow-eyed; having the features distorted or wasted, or anxious in\n   appearance; as, haggard features, eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haggard', 'a.', 'A young or untrained hawk or falcon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haggard',    'a.',    'A fierce, intractable creature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haggard',    'a.',    'A hag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haggard',    'n.',    'A stackyard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haggardly',    'adv.',    'In a haggard manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hagged',    'a.',    'Like a hag; lean; ugly.');
