INSERT INTO words (word, word_type, description)VALUES  (    'Haggis',    'n.',    'A Scotch pudding made of the heart, liver, lights, etc., of\n   asheep or lamb, minced with suet, onions, oatmeal, etc., highly\n   seasoned, and boiled in the stomach of the same animal; minced head and\n  pluck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haggish', 'a.', 'Like a hag; ugly; wrinkled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haggishly', 'adv.', 'In the manner of a hag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haggled', 'imp. & p. p.', 'of Haggle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haggling', 'p. pr. & vb. n.', 'of Haggle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haggle', 'v. t.', 'To cut roughly or hack; to cut into small pieces; to\n   notch or cut in an unskillful manner; to make rough or mangle by\n   cutting; as, a boy haggles a stick of wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haggle', 'v. i.', 'To be difficult in bargaining; to stick at small\n   matters; to chaffer; to higgle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haggle', 'n.', 'The act or process of haggling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haggler', 'n.', 'One who haggles or is difficult in bargaining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haggler', 'n.', 'One who forestalls a market; a middleman between producer\n   and dealer in London vegetable markets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hagiarchy', 'n.', 'A sacred government; by holy orders of men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hagiocracy', 'n.', 'Government by a priesthood; hierarchy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hagiographa', 'n. pl.', 'The last of the three Jewish divisions of the Old\n   Testament, or that portion not contained in the Law and the Prophets.\n   It comprises Psalms, Proverbs, Job, Canticles, Ruth, Lamentations,\n   Ecclesiastes, Esther, Daniel, Ezra, Nehemiah, and Chronicles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hagiographa', 'n. pl.', 'The lives of the saints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hagiographal', '', 'Pertaining to the hagiographa, or to sacred writings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hagiographer', 'n.', 'One of the writers of the hagiographa; a writer of\n   lives of the saints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hagiography', 'n.', 'Same Hagiographa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hagiolatry', 'n.', 'The invocation or worship of saints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hagiologist', 'n.', 'One who treats of the sacred writings; a writer of the\n   lives of the saints; a hagiographer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hagiology', 'n.', 'The history or description of the sacred writings or of\n   sacred persons; a narrative of the lives of the saints; a catalogue of\n   saints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hagioscope', 'n.', 'An opening made in the interior walls of a cruciform\n   church to afford a view of the altar to those in the transepts; --\n   called, in architecture, a squint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hag-ridden', 'a.', 'Ridden by a hag or witch; hence, afflicted with\n   nightmare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hagseed', 'n.', 'The offspring of a hag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hagship', 'n.', 'The state or title of a hag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hag-taper', 'n.', 'The great woolly mullein (Verbascum Thapsus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haguebut', 'n.', 'See Hagbut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hah', 'interj.', 'Same as Ha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ha-ha', 'n.', 'A sunk fence; a fence, wall, or ditch, not visible till one\n   is close upon it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haidingerite', 'n.', 'A mineral consisting of the arseniate of lime; -- so\n   named in honor of W. Haidinger, of Vienna.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haiduck', 'n.', 'Formerly, a mercenary foot soldier in Hungary, now, a\n   halberdier of a Hungarian noble, or an attendant in German or Hungarian\n   courts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haik', 'n.', 'A large piece of woolen or cotton cloth worn by Arabs as an\n   outer garment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haikal', 'n.', 'The central chapel of the three forming the sanctuary of a\n   Coptic church. It contains the high altar, and is usually closed by an\n   embroidered curtain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hail', 'n.', 'Small roundish masses of ice precipitated from the clouds,\n   where they are formed by the congelation of vapor. The separate masses\n   or grains are called hailstones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halled', 'imp. & p. p.', 'of Hail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halting', 'p. pr. & vb. n.', 'of Hail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hail', 'v. i.', 'To pour down particles of ice, or frozen vapors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hail', 'v. t.', 'To pour forcibly down, as hail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hail', 'a.', 'Healthy. See Hale (the preferable spelling).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hail', 'v. t.', 'To call loudly to, or after; to accost; to salute; to\n   address.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hail', 'v. t.', 'To name; to designate; to call.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hail', 'v. i.', 'To declare, by hailing, the port from which a vessel sails\n   or where she is registered; hence, to sail; to come; -- used with from;\n   as, the steamer hails from New York.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hail', 'v. i.', 'To report as ones home or the place from whence one\n   comes; to come; -- with from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hail', 'v. t.', 'An exclamation of respectful or reverent salutation, or,\n   occasionally, of familiar greeting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hail', 'n.', 'A wish of health; a salutation; a loud call.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hail-fellow', 'n.', 'An intimate companion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hailse', 'v. t.', 'To greet; to salute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hailshot', 'n. pl.', 'Small shot which scatter like hailstones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hailstone', 'n.', 'A single particle of ice falling from a cloud; a frozen\n   raindrop; a pellet of hail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hailstorm', 'n.', 'A storm accompanied with hail; a shower of hail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haily', 'a.', 'Of hail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Han', 'v. t.', 'To inclose for mowing; to set aside for grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haint', '', 'A contraction of have not or has not; as, I haint, he\n   haint, we haint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hair', 'n.', 'The collection or mass of filaments growing from the skin of\n   an animal, and forming a covering for a part of the head or for any\n   part or the whole of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hair', 'n.', 'One the above-mentioned filaments, consisting, in\n   invertebrate animals, of a long, tubular part which is free and\n   flexible, and a bulbous root imbedded in the skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hair', 'n.', 'Hair (human or animal) used for various purposes; as, hair\n   for stuffing cushions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hair', 'n.', 'A slender outgrowth from the chitinous cuticle of insects,\n   spiders, crustaceans, and other invertebrates. Such hairs are totally\n   unlike those of vertebrates in structure, composition, and mode of\n   growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hair', 'n.', 'An outgrowth of the epidermis, consisting of one or of\n   several cells, whether pointed, hooked, knobbed, or stellated. Internal\n   hairs occur in the flower stalk of the yellow frog lily (Nuphar).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hair', 'n.', 'A spring device used in a hair-trigger firearm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hair', 'n.', 'A haircloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hair', 'n.', 'Any very small distance, or degree; a hairbreadth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hairbell', 'n.', 'See Harebell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hairbird', 'n.', 'The chipping sparrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hairbrained', 'a.', 'See Harebrained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hairbreadth', '', 'Alt. of Hairsbreadth');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hairsbreadth', '', 'The diameter or breadth of a hair; a very small\n   distance; sometimes, definitely, the forty-eighth part of an inch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hairbreadth', 'a.', 'Having the breadth of a hair; very narrow; as, a\n   hairbreadth escape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hair-brown', 'a.', 'Of a clear tint of brown, resembling brown human hair.\n   It is composed of equal proportions of red and green.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hairbrush', 'n.', 'A brush for cleansing and smoothing the hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haircloth', 'n.', 'Stuff or cloth made wholly or in part of hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hairdresser', 'n.', 'One who dresses or cuts hair; a barber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haired', 'a.', 'Having hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haired', 'a.', 'In composition: Having (such) hair; as, red-haired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hairen', 'a.', 'Hairy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hair grass', '', 'A grass with very slender leaves or branches; as the\n   Agrostis scabra, and several species of Aira or Deschampsia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hairiness', 'n.', 'The state of abounding, or being covered, with hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hairless', 'a.', 'Destitute of hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hairpin', 'n.', 'A pin, usually forked, or of bent wire, for fastening the\n   hair in place, -- used by women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hair-salt', 'n.', 'A variety of native Epsom salt occurring in silky\n   fibers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hairsplitter', 'n.', 'One who makes excessively nice or needless\n   distinctions in reasoning; one who quibbles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hairsplitting', 'a.', 'Making excessively nice or trivial distinctions in\n   reasoning; subtle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hairsplitting', 'n.', 'The act or practice of making trivial distinctions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hairspring', 'n.', 'The slender recoil spring which regulates the motion of\n   the balance in a timepiece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hairstreak', 'n.', 'A butterfly of the genus Thecla; as, the green\n   hairstreak (T. rubi).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hairtail', 'n.', 'Any species of marine fishes of the genus Trichiurus;\n   esp., T. lepterus of Europe and America. They are long and like a band,\n   with a slender, pointed tail. Called also bladefish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hairworm', '', 'A nematoid worm of the genus Gordius, resembling a hair.\n   See Gordius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hairy', 'a.', 'Bearing or covered with hair; made of or resembling hair;\n   rough with hair; rough with hair; rough with hair; hirsute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haitian', 'a. & n.', 'See Haytian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haye', 'n.', 'The Egyptian asp or cobra (Naja haje.) It is related to the\n   cobra of India, and like the latter has the power of inflating its neck\n   into a hood. Its bite is very venomous. It is supposed to be the snake\n   by means of whose bite Cleopatra committed suicide, and hence is\n   sometimes called Cleopatras snake or asp. See Asp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hake', 'n.', 'A drying shed, as for unburned tile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hake', 'n.', 'One of several species of marine gadoid fishes, of the genera\n   Phycis, Merlucius, and allies. The common European hake is M. vulgaris;\n   the American silver hake or whiting is M. bilinearis. Two American\n   species (Phycis chuss and P. tenius) are important food fishes, and are\n   also valued for their oil and sounds. Called also squirrel hake, and\n   codling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hake', 'v. t.', 'To loiter; to sneak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hakes-dame', 'n.', 'See Forkbeard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haketon', 'n.', 'Same as Acton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hakim', 'n.', 'A wise man; a physician, esp. a Mohammedan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hakim', 'n.', 'A Mohammedan title for a ruler; a judge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halachoth', 'pl. ', 'of Halacha');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halacha', 'n.', 'The general term for the Hebrew oral or traditional law;\n   one of two branches of exposition in the Midrash. See Midrash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halation', 'n.', 'An appearance as of a halo of light, surrounding the\n   edges of dark objects in a photographic picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halberd', 'n.', 'An ancient long-handled weapon, of which the head had a\n   point and several long, sharp edges, curved or straight, and sometimes\n   additional points. The heads were sometimes of very elaborate form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halberdier', 'n.', 'One who is armed with a halberd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halberd-shaped', 'a.', 'Hastate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halcyon', 'n.', 'A kingfisher. By modern ornithologists restricted to a\n   genus including a limited number of species having omnivorous habits,\n   as the sacred kingfisher (Halcyon sancta) of Australia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halcyon', 'a.', 'Pertaining to, or resembling, the halcyon, which was\n   anciently said to lay her eggs in nests on or near the sea during the\n   calm weather about the winter solstice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halcyon', 'a.', 'Hence: Calm; quiet; peaceful; undisturbed; happy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halcyonian', 'a.', 'Halcyon; calm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halcyonold', 'a. & n.', 'See Alcyonoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hale', 'a.', 'Sound; entire; healthy; robust; not impaired; as, a hale\n   body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hale', 'n.', 'Welfare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haled', 'imp. & p. p.', 'of Hale');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haling', 'p. pr. & vb. n.', 'of Hale');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hale', 'v. t.', 'To pull; to drag; to haul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halesia', 'n.', 'A genus of American shrubs containing several species,\n   called snowdrop trees, or silver-bell trees. They have showy, white\n   flowers, drooping on slender pedicels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half', 'a.', 'Consisting of a moiety, or half; as, a half bushel; a half\n   hour; a half dollar; a half view.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half', 'a.', 'Consisting of some indefinite portion resembling a half;\n   approximately a half, whether more or less; partial; imperfect; as, a\n   half dream; half knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half', 'adv.', 'In an equal part or degree; in some pa/ appro/mating a\n   half; partially; imperfectly; as, half-colored, half done,\n   half-hearted, half persuaded, half conscious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halves', 'pl. ', 'of Half');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half', 'a.', 'Part; side; behalf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half', 'a.', 'One of two equal parts into which anything may be divided, or\n   considered as divided; -- sometimes followed by of; as, a half of an\n   apple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half', 'v. t.', 'To halve. [Obs.] See Halve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-and-half', 'n.', 'A mixture of two malt liquors, esp. porter and ale,\n   in about equal parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halfbeak', 'n.', 'Any slender, marine fish of the genus Hemirhamphus,\n   having the upper jaw much shorter than the lower; -- called also\n   balahoo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half blood', '', 'The relation between persons born of the same father or\n   of the same mother, but not of both; as, a brother or sister of the\n   half blood. See Blood, n., 2 and 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half blood', 'n.', 'A person so related to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half blood', 'n.', 'A person whose father and mother are of different\n   races; a half-breed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-blooded', 'a.', 'Proceeding from a male and female of different breeds\n   or races; having only one parent of good stock; as, a half-blooded\n   sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-blooded', 'a.', 'Degenerate; mean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-boot', 'n.', 'A boot with a short top covering only the ankle. See\n   Cocker, and Congress boot, under Congress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-bound', 'n.', 'Having only the back and corners in leather, as a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-bred', 'a.', 'Half-blooded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-bred', 'a.', 'Imperfectly acquainted with the rules of good-breeding;\n   not well trained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-breed', 'a.', 'Half-blooded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-breed', 'n.', 'A person who is blooded; the offspring of parents of\n   different races, especially of the American Indian and the white race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-brother', 'n.', 'A brother by one parent, but not by both.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-caste', 'n.', 'One born of a European parent on the one side, and of a\n   Hindoo or Mohammedan on the other. Also adjective; as, half-caste\n   parents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-clammed', 'a.', 'Half-filled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halfcocked', 'imp. & p. p.', 'of Halfcock');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halfcocking', 'p. pr. & vb. n.', 'of Halfcock');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halfcock', 'v. t.', 'To set the cock of (a firearm) at the first notch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-cracked', 'a.', 'Half-demented; half-witted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-deck', 'n.', 'A shell of the genus Crepidula; a boat shell. See Boat\n   shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-deck', 'n.', 'See Half deck, under Deck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-decked', 'a.', 'Partially decked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halfen', 'a.', 'Wanting half its due qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halfendeal', 'adv.', 'Half; by the part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halfendeal', 'n.', 'A half part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halfer', 'n.', 'One who possesses or gives half only; one who shares.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halfer', 'n.', 'A male fallow deer gelded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-faced', 'a.', 'Showing only part of the face; wretched looking;\n   meager.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-fish', 'n.', 'A salmon in its fifth year of growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-hatched', 'a.', 'Imperfectly hatched; as, half-hatched eggs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-heard', 'a.', 'Imperfectly or partly heard to the end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-hearted', 'a.', 'Wanting in heart or spirit; ungenerous; unkind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-hearted', 'a.', 'Lacking zeal or courage; lukewarm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-hourly', 'a.', 'Done or happening at intervals of half an hour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-learned', 'a.', 'Imperfectly learned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-length', 'a.', 'Of half the whole or ordinary length, as a picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-mast', 'n.', 'A point some distance below the top of a mast or staff;\n   as, a flag a half-mast (a token of mourning, etc.).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-moon', 'n.', 'The moon at the quarters, when half its disk appears\n   illuminated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-moon', 'n.', 'The shape of a half-moon; a crescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-moon', 'n.', 'An outwork composed of two faces, forming a salient\n   angle whose gorge resembles a half-moon; -- now called a ravelin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-moon', 'n.', 'A marine, sparoid, food fish of California (Caesiosoma\n   Californiense). The body is ovate, blackish above, blue or gray below.\n   Called also medialuna.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halfness', 'n.', 'The quality of being half; incompleteness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halfpace', 'n.', 'A platform of a staircase where the stair turns back in\n   exactly the reverse direction of the lower flight. See Quarterpace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-pike', 'n.', 'A short pike, sometimes carried by officers of infantry,\n   sometimes used in boarding ships; a spontoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-port', 'n.', 'One half of a shutter made in two parts for closing a\n   porthole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-ray', 'n.', 'A straight line considered as drawn from a center to an\n   indefinite distance in one direction, the complete ray being the whole\n   line drawn to an indefinite distance in both directions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-read', 'a.', 'Informed by insufficient reading; superficial; shallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half seas over', '', 'Half drunk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-sighted', 'a.', 'Seeing imperfectly; having weak discernment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-sister', 'n.', 'A sister by one parent only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-strained', 'a.', 'Half-bred; imperfect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-sword', 'n.', 'Half the length of a sword; close fight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-timbered', 'a.', 'Constructed of a timber frame, having the spaces\n   filled in with masonry; -- said of buildings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-tounue', 'n.', 'A jury, for the trial of a foreigner, composed equally\n   of citizens and aliens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halfway', 'adv.', 'In the middle; at half the distance; imperfectly;\n   partially; as, he halfway yielded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halfway', 'a.', 'Equally distant from the extremes; situated at an\n   intermediate point; midway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-wit', 'n.', 'A foolish; a dolt; a blockhead; a dunce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-witted', 'a.', 'Weak in intellect; silly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Half-yearly', 'a.', 'Two in a year; semiannual. -- adv. Twice in a year;\n   semiannually.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halibut', 'n.', 'A large, northern, marine flatfish (Hippoglossus\n   vulgaris), of the family Pleuronectidae. It often grows very large,\n   weighing more than three hundred pounds. It is an important food fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halichondriae', 'n. pl.', 'An order of sponges, having simple siliceous\n   spicules and keratose fibers; -- called also Keratosilicoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halicore', 'n.', 'Same as Dugong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halidom', 'n.', 'Holiness; sanctity; sacred oath; sacred things; sanctuary;\n   -- used chiefly in oaths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halidom', 'n.', 'Holy doom; the Last Day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halieutics', 'n.', 'A treatise upon fish or the art of fishing;\n   ichthyology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halmas', 'a.', 'The feast of All Saints; Hallowmas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haliographer', 'n.', 'One who writes about or describes the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haliography', 'n.', 'Description of the sea; the science that treats of the\n   sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haliotis', 'n.', 'A genus of marine shells; the ear-shells. See Abalone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haliotoid', 'a.', 'Like or pertaining to the genus Haliotis; ear-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halisauria', 'n. pl.', 'The Enaliosauria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halite', 'n.', 'Native salt; sodium chloride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halituous', 'a.', 'Produced by, or like, breath; vaporous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halk', 'n.', 'A nook; a corner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hall', 'n.', 'A building or room of considerable size and stateliness, used\n   for public purposes; as, Westminster Hall, in London.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hall', 'n.', 'The chief room in a castle or manor house, and in early times\n   the only public room, serving as the place of gathering for the lords\n   family with the retainers and servants, also for cooking and eating. It\n   was often contrasted with the bower, which was the private or sleeping\n   apartment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hall', 'n.', 'A vestibule, entrance room, etc., in the more elaborated\n   buildings of later times.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hall', 'n.', 'Any corridor or passage in a building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hall', 'n.', 'A name given to many manor houses because the magistrates\n   court was held in the hall of his mansion; a chief mansion house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hall', 'n.', 'A college in an English university (at Oxford, an unendowed\n   college).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hall', 'n.', 'The apartment in which English university students dine in\n   common; hence, the dinner itself; as, hall is at six oclock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hall', 'n.', 'Cleared passageway in a crowd; -- formerly an exclamation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hallage', 'n.', 'A fee or toll paid for goods sold in a hall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halleluiah', 'n. & interj.', 'Alt. of Hallelujah');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hallelujah', 'n. & interj.', 'Praise ye Jehovah; praise ye the Lord; -- an\n   exclamation used chiefly in songs of praise or thanksgiving to God, and\n   as an expression of gratitude or adoration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hallelujatic', 'a.', 'Pertaining to, or containing, hallelujahs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halliard', 'n.', 'See Halyard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hallidome', 'n.', 'Same as Halidom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hallier', 'n.', 'A kind of net for catching birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hall-mark', 'n.', 'The official stamp of the Goldsmiths Company and other\n   assay offices, in the United Kingdom, on gold and silver articles,\n   attesting their purity. Also used figuratively; -- as, a word or phrase\n   lacks the hall-mark of the best writers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halloa', '', 'See Halloo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halloo', 'n.', 'A loud exclamation; a call to invite attention or to incite\n   a person or an animal; a shout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hallooed', 'imp. & p. p.', 'of Halloo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halloing', 'p. pr. & vb. n.', 'of Halloo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halloo', 'v. i.', 'To cry out; to exclaim with a loud voice; to call to a\n   person, as by the word halloo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halloo', 'v. t.', 'To encourage with shouts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halloo', 'v. t.', 'To chase with shouts or outcries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halloo', 'v. t.', 'To call or shout to; to hail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halloo', 'n.', 'An exclamation to call attention or to encourage one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hallowed', 'imp. & p. p.', 'of Hallow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hallowing', 'p. pr. & vb. n.', 'of Hallow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hallow', 'v. t.', 'To make holy; to set apart for holy or religious use; to\n   consecrate; to treat or keep as sacred; to reverence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halloween', 'n.', 'The evening preceding Allhallows or All Saints Day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hallowmas', 'n.', 'The feast of All Saints, or Allhallows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halloysite', 'n.', 'A claylike mineral, occurring in soft, smooth,\n   amorphous masses, of a whitish color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hallucal', 'a.', 'Of or pertaining to the hallux.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hallucinate', 'v. i.', 'To wander; to go astray; to err; to blunder; --\n   used of mental processes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hallucination', 'n.', 'The act of hallucinating; a wandering of the mind;\n   error; mistake; a blunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hallucination', 'n.', 'The perception of objects which have no reality, or\n   of sensations which have no corresponding external cause, arising from\n   disorder or the nervous system, as in delirium tremens; delusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hallucinator', 'n.', 'One whose judgment and acts are affected by\n   hallucinations; one who errs on account of his hallucinations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hallucinatory', 'a.', 'Partaking of, or tending to produce, hallucination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hallux', 'n.', 'The first, or preaxial, digit of the hind limb,\n   corresponding to the pollux in the fore limb; the great toe; the hind\n   toe of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halm', 'n.', 'Same as Haulm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halma', 'n.', 'The long jump, with weights in the hands, -- the most\n   important of the exercises of the Pentathlon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halos', 'pl. ', 'of Halo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halo', 'n.', 'A luminous circle, usually prismatically colored, round the\n   sun or moon, and supposed to be caused by the refraction of light\n   through crystals of ice in the atmosphere. Connected with halos there\n   are often white bands, crosses, or arches, resulting from the same\n   atmospheric conditions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halo', 'n.', 'A circle of light; especially, the bright ring represented in\n   painting as surrounding the heads of saints and other holy persons; a\n   glory; a nimbus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halo', 'n.', 'An ideal glory investing, or affecting ones perception of,\n   an object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halo', 'n.', 'A colored circle around a nipple; an areola.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haloed', 'imp. & p. p.', 'of Halo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haloing', 'p. pr. & vb. n.', 'of Halo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halo', 'v. t. & i.', 'To form, or surround with, a halo; to encircle with,\n   or as with, a halo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haloed', 'a.', 'Surrounded with a halo; invested with an ideal glory;\n   glorified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halogen', 'n.', 'An electro-negative element or radical, which, by\n   combination with a metal, forms a haloid salt; especially, chlorine,\n   bromine, and iodine; sometimes, also, fluorine and cyanogen. See\n   Chlorine family, under Chlorine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halogenous', 'a.', 'Of the nature of a halogen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haloid', 'a.', 'Resembling salt; -- said of certain binary compounds\n   consisting of a metal united to a negative element or radical, and now\n   chiefly applied to the chlorides, bromides, iodides, and sometimes also\n   to the fluorides and cyanides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haloid', 'n.', 'A haloid substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halomancy', 'n.', 'See Alomancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halometer', 'n.', 'An instrument for measuring the forms and angles of\n   salts and crystals; a goniometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halones', 'n. pl.', 'Alternating transparent and opaque white rings which\n   are seen outside the blastoderm, on the surface of the developing egg\n   of the hen and other birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halophyte', 'n.', 'A plant found growing in salt marshes, or in the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haloscope', 'n.', 'An instrument for exhibition or illustration of the\n   phenomena of halos, parhelia, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halotrichite', 'n.', 'An iron alum occurring in silky fibrous aggregates of\n   a yellowish white color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haloxyline', 'n.', 'An explosive mixture, consisting of sawdust, charcoal,\n   niter, and ferrocyanide of potassium, used as a substitute for\n   gunpowder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halp', 'imp.', 'Helped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halpace', 'n.', 'See Haut pas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hals', 'n.', 'The neck or throat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halse', 'v. t.', 'To embrace about the neck; to salute; to greet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halse', 'v. t.', 'To adjure; to beseech; to entreat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halsed', 'imp. & p. p.', 'of Halse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halsing', 'p. pr. & vb. n.', 'of Halse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halse', 'v. t.', 'To haul; to hoist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halsening', 'a.', 'Sounding harshly in the throat; inharmonious; rough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halser', 'n.', 'See Hawser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halt', '', '3d pers. sing. pres. of Hold, contraction for holdeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halt', 'n.', 'A stop in marching or walking, or in any action; arrest of\n   progress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halted', 'imp. & p. p.', 'of Halt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halting', 'p. pr. & vb. n.', 'of Halt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halt', 'v. i.', 'To hold ones self from proceeding; to hold up; to cease\n   progress; to stop for a longer or shorter period; to come to a stop; to\n   stand still.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halt', 'v. i.', 'To stand in doubt whether to proceed, or what to do; to\n   hesitate; to be uncertain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halt', 'v. t.', 'To cause to cease marching; to stop; as, the general\n   halted his troops for refreshment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halt', 'a.', 'Halting or stopping in walking; lame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halt', 'n.', 'The act of limping; lameness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halt', 'a.', 'To walk lamely; to limp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halt', 'a.', 'To have an irregular rhythm; to be defective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halter', 'n.', 'One who halts or limps; a cripple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halter', 'n.', 'A strong strap or cord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halter', 'n.', 'A rope or strap, with or without a headstall, for leading\n   or tying a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halter', 'n.', 'A rope for hanging malefactors; a noose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haltered', 'imp. & p. p.', 'of Halter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haltering', 'p. pr. & vb. n.', 'of Halter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halter', 'v. t.', 'To tie by the neck with a rope, strap, or halter; to put\n   a halter on; to subject to a hangmans halter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halteres', 'n. pl.', 'Balancers; the rudimentary hind wings of Diptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halter-sack', 'n.', 'A term of reproach, implying that one is fit to be\n   hanged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haltingly', 'adv.', 'In a halting or limping manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halvans', 'n. pl.', 'Impure ore; dirty ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halve', 'n.', 'A half.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halved', 'imp. & p. p.', 'of Halve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halving', 'p. pr. & vb. n.', 'of Halve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halve', 'v. t.', 'To divide into two equal parts; as, to halve an apple; to\n   be or form half of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halve', 'v. t.', 'To join, as two pieces of timber, by cutting away each\n   for half its thickness at the joining place, and fitting together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halved', 'a.', 'Appearing as if one side, or one half, were cut away;\n   dimidiate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halves', 'n.', 'pl. of Half.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halwe', 'n.', 'A saint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halyard', 'v. t.', 'A rope or tackle for hoisting or lowering yards,\n   sails, flags, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Halysites', 'n.', 'A genus of Silurian fossil corals; the chain corals. See\n   Chain coral, under Chain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ham', 'n.', 'Home.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ham', 'n.', 'The region back of the knee joint; the popliteal space; the\n   hock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ham', 'n.', 'The thigh of any animal; especially, the thigh of a hog cured\n   by salting and smoking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamadryads', 'pl. ', 'of Hamadryad');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamadryades', 'pl. ', 'of Hamadryad');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamadryad', 'n.', 'A tree nymph whose life ended with that of the\n   particular tree, usually an oak, which had been her abode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamadryad', 'n.', 'A large venomous East Indian snake (Orhiophagus\n   bungarus), allied to the cobras.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamadryas', 'n.', 'The sacred baboon of Egypt (Cynocephalus Hamadryas).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamamelis', 'n.', 'A genus of plants which includes the witch-hazel\n   (Hamamelis Virginica), a preparation of which is used medicinally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamate', 'a.', 'Hooked; bent at the end into a hook; hamous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamated', 'a.', 'Hooked, or set with hooks; hamate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamatum', 'n.', 'See Unciform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamble', 'v. t.', 'To hamstring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamburg', 'n.', 'A commercial city of Germany, near the mouth of the Elbe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hame', 'n.', 'Home.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hame', 'n.', 'One of the two curved pieces of wood or metal, in the harness\n   of a draught horse, to which the traces are fastened. They are fitted\n   upon the collar, or have pads fitting the horses neck attached to\n   them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamel', 'v. t.', 'Same as Hamele.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamesecken', 'n.', 'Alt. of Hamesucken');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamesucken', 'n.', 'The felonious seeking and invasion of a person in his\n   dwelling house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamiform', 'n.', 'Hook-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamilton period', '', 'A subdivision of the Devonian system of America; --\n   so named from Hamilton, Madison Co., New York. It includes the\n   Marcellus, Hamilton, and Genesee epochs or groups. See the Chart of\n   Geology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haminura', 'n.', 'A large edible river fish (Erythrinus macrodon) of\n   Guiana.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamite', 'n.', 'A fossil cephalopod of the genus Hamites, related to the\n   ammonites, but having the last whorl bent into a hooklike form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamite', 'n.', 'A descendant of Ham, Noahs second son. See Gen. x. 6-20.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haitic', 'a.', 'Pertaining to Ham or his descendants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamlet', 'n.', 'A small village; a little cluster of houses in the country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamleted', 'p. a.', 'Confined to a hamlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hammer', 'n.', 'An instrument for driving nails, beating metals, and the\n   like, consisting of a head, usually of steel or iron, fixed crosswise\n   to a handle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hammer', 'n.', 'Something which in firm or action resembles the common\n   hammer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hammer', 'n.', 'That part of a clock which strikes upon the bell to\n   indicate the hour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hammer', 'n.', 'The padded mallet of a piano, which strikes the wires, to\n   produce the tones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hammer', 'n.', 'The malleus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hammer', 'n.', 'That part of a gunlock which strikes the percussion cap, or\n   firing pin; the cock; formerly, however, a piece of steel covering the\n   pan of a flintlock musket and struck by the flint of the cock to ignite\n   the priming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hammer', 'n.', 'Also, a person of thing that smites or shatters; as, St.\n   Augustine was the hammer of heresies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hammered', 'imp. & p. p.', 'of Hammer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hammering', 'p. pr. & vb. n.', 'of Hammer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hammer', 'v. t.', 'To beat with a hammer; to beat with heavy blows; as, to\n   hammer iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hammer', 'v. t.', 'To form or forge with a hammer; to shape by beating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hammer', 'v. t.', 'To form in the mind; to shape by hard intellectual\n   labor; -- usually with out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hammer', 'v. i.', 'To be busy forming anything; to labor hard as if shaping\n   something with a hammer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hammer', 'v. i.', 'To strike repeated blows, literally or figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hammerable', 'a.', 'Capable of being formed or shaped by a hammer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hammer-beam', 'n.', 'A member of one description of roof truss, called\n   hammer-beam truss, which is so framed as not to have a tiebeam at the\n   top of the wall. Each principal has two hammer-beams, which occupy the\n   situation, and to some extent serve the purpose, of a tiebeam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hammercloth', 'n.', 'The cloth which covers a coach box.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hammer-dressed', 'a.', 'Having the surface roughly shaped or faced with the\n   stonecutters hammer; -- said of building stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hammerer', 'n.', 'One who works with a hammer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hammer-harden', 'v. t.', 'To harden, as a metal, by hammering it in the\n   cold state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hammerhead', 'n.', 'A shark of the genus Sphyrna or Zygaena, having the\n   eyes set on projections from the sides of the head, which gives it a\n   hammer shape. The Sphyrna zygaena is found in the North Atlantic.\n   Called also hammer fish, and balance fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hammerhead', 'n.', 'A fresh-water fish; the stone-roller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hammerhead', 'n.', 'An African fruit bat (Hypsignathus monstrosus); -- so\n   called from its large blunt nozzle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hammerkop', 'n.', 'A bird of the Heron family; the umber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hammer-less', 'a.', 'Without a visible hammer; -- said of a gun having a\n   cock or striker concealed from sight, and out of the way of an\n   accidental touch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hammermen', 'pl. ', 'of Hammerman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hammerman', 'n.', 'A hammerer; a forgeman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hammochrysos', 'n.', 'A stone with spangles of gold color in it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hammock', 'n.', 'A swinging couch or bed, usually made of netting or canvas\n   about six feet wide, suspended by clews or cords at the ends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hammock', 'n.', 'A piece of land thickly wooded, and usually covered with\n   bushes and vines. Used also adjectively; as, hammock land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamose', '', 'Alt. of Hamous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamous', '', 'Having the end hooked or curved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamper', 'n.', 'A large basket, usually with a cover, used for the packing\n   and carrying of articles; as, a hamper of wine; a clothes hamper; an\n   oyster hamper, which contains two bushels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hampered', 'imp. & p. p.', 'of Hamper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hampering', 'p. pr. & vb. n.', 'of Hamper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamper', 'v. t.', 'To put in a hamper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamper', 'v. t.', 'To put a hamper or fetter on; to shackle; to insnare; to\n   inveigle; hence, to impede in motion or progress; to embarrass; to\n   encumber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamper', 'n.', 'A shackle; a fetter; anything which impedes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamper', 'n.', 'Articles ordinarily indispensable, but in the way at\n   certain times.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamshackle', 'v. t.', 'To fasten (an animal) by a rope binding the head to\n   one of the fore legs; as, to hamshackle a horse or cow; hence, to bind\n   or restrain; to curb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamster', 'n.', 'A small European rodent (Cricetus frumentarius). It is\n   remarkable for having a pouch on each side of the jaw, under the skin,\n   and for its migrations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamstring', 'n.', 'One of the great tendons situated in each side of the\n   ham, or space back of the knee, and connected with the muscles of the\n   back of the thigh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamstrung', 'imp. & p. p.', 'of Hamstring');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamstringing', 'p. pr. & vb. n.', 'of Hamstring');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamstring', 'v. t.', 'To lame or disable by cutting the tendons of the ham\n   or knee; to hough; hence, to cripple; to incapacitate; to disable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamular', 'a.', 'Hooked; hooklike; hamate; as, the hamular process of the\n   sphenoid bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamulate', 'a.', 'Furnished with a small hook; hook-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamule', 'n.', 'A little hook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamulose', 'a.', 'Bearing a small hook at the end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamuli', 'pl. ', 'of Hamulus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamulus', 'n.', 'A hook, or hooklike process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hamulus', 'n.', 'A hooked barbicel of a feather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Han', 'inf. & plural pres.', 'To have; have.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hanap', 'n.', 'A rich goblet, esp. one used on state occasions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hanaper', 'n.', 'A kind of basket, usually of wickerwork, and adapted for\n   the packing and carrying of articles; a hamper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hance', 'v. t.', 'To raise; to elevate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hance', '', 'Alt. of Hanch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hanch', '', 'See Hanse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hanch', '', 'A sudden fall or break, as the fall of the fife rail down to\n   the gangway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hand', 'n.', 'That part of the fore limb below the forearm or wrist in man\n   and monkeys, and the corresponding part in many other animals; manus;\n   paw. See Manus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hand', 'n.', 'That which resembles, or to some extent performs the office\n   of, a human hand');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hand', 'n.', 'A limb of certain animals, as the foot of a hawk, or any one\n   of the four extremities of a monkey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hand', 'n.', 'An index or pointer on a dial; as, the hour or minute hand of\n   a clock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hand', 'n.', 'A measure equal to a hands breadth, -- four inches; a palm.\n   Chiefly used in measuring the height of horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hand', 'n.', 'Side; part; direction, either right or left.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hand', 'n.', 'Power of performance; means of execution; ability; skill;\n   dexterity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hand', 'n.', 'Actual performance; deed; act; workmanship; agency; hence,\n   manner of performance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hand', 'n.', 'An agent; a servant, or laborer; a workman, trained or\n   competent for special service or duty; a performer more or less\n   skillful; as, a deck hand; a farm hand; an old hand at speaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hand', 'n.', 'Handwriting; style of penmanship; as, a good, bad or running\n   hand. Hence, a signature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hand', 'n.', 'Personal possession; ownership; hence, control; direction;\n   management; -- usually in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hand', 'n.', 'Agency in transmission from one person to another; as, to buy\n   at first hand, that is, from the producer, or when new; at second hand,\n   that is, when no longer in the producers hand, or when not new.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hand', 'n.', 'Rate; price.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hand', 'n.', 'That which is, or may be, held in a hand at once');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hand', 'n.', 'The quota of cards received from the dealer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hand', 'n.', 'A bundle of tobacco leaves tied together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hand', 'n.', 'The small part of a gunstock near the lock, which is grasped\n   by the hand in taking aim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hand staves', 'pl. ', 'of Hand');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handed', 'imp. & p. p.', 'of Hand');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handing', 'p. pr. & vb. n.', 'of Hand');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hand', 'v. t.', 'To give, pass, or transmit with the hand; as, he handed\n   them the letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hand', 'v. t.', 'To lead, guide, or assist with the hand; to conduct; as,\n   to hand a lady into a carriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hand', 'v. t.', 'To manage; as, I hand my oar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hand', 'v. t.', 'To seize; to lay hands on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hand', 'v. t.', 'To pledge by the hand; to handfast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hand', 'v. t.', 'To furl; -- said of a sail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hand', 'v. i.', 'To cooperate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handbarrow', 'n.', 'A frame or barrow, without a wheel, carried by hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handbill', 'n.', 'A loose, printed sheet, to be distributed by hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handbill', 'n.', 'A pruning hook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handbook', 'n.', 'A book of reference, to be carried in the hand; a manual;\n   a guidebook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handbreadth', 'n.', 'A space equal to the breadth of the hand; a palm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handcart', 'n.', 'A cart drawn or pushed by hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handcloth', 'n.', 'A handkerchief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handcraft', 'n.', 'Same as Handicraft.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-men', 'pl. ', 'of Handcraftsman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handcraftsman', 'n.', 'A handicraftsman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handcuff', 'n.', 'A fastening, consisting of an iron ring around the wrist,\n   usually connected by a chain with one on the other wrist; a manacle; --\n   usually in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handcuffed', 'imp. & p. p.', 'of Handcuff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handcuffing', 'p. pr. & vb. n.', 'of Handcuff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handcuff', 'v. t.', 'To apply handcuffs to; to manacle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handed', 'a.', 'With hands joined; hand in hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handed', 'a.', 'Having a peculiar or characteristic hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hander', 'n.', 'One who hands over or transmits; a conveyer in succession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handfast', 'n.', 'Hold; grasp; custody; power of confining or keeping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handfast', 'n.', 'Contract; specifically, espousal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handfast', 'a.', 'Fast by contract; betrothed by joining hands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handfasted', 'imp. & p. p.', 'of Handfast');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handfasting', 'p. pr. & vb. n.', 'of Handfast');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handfast', 'v. t.', 'To pledge; to bind; to betroth by joining hands, in\n   order to cohabitation, before the celebration of marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handfast', 'n.', 'Strong; steadfast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handfastly', 'adv.', 'In a handfast or publicly pledged manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handfish', 'n.', 'The frogfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hand flus', 'pl. ', 'of Handful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handful', 'n.', 'As much as the hand will grasp or contain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handful', 'n.', 'A hands breadth; four inches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handful', 'n.', 'A small quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hand-hole', 'n.', 'A small hole in a boiler for the insertion of the hand\n   in cleaning, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handicap', 'n.', 'An allowance of a certain amount of time or distance in\n   starting, granted in a race to the competitor possessing inferior\n   advantages; or an additional weight or other hindrance imposed upon the\n   one possessing superior advantages, in order to equalize, as much as\n   possible, the chances of success; as, the handicap was five seconds, or\n   ten pounds, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handicap', 'n.', 'A race, for horses or men, or any contest of agility,\n   strength, or skill, in which there is an allowance of time, distance,\n   weight, or other advantage, to equalize the chances of the competitors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handicap', 'n.', 'An old game at cards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handicapped', 'imp. & p. p.', 'of Handicap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handicapping', 'p. pr. & vb. n.', 'of Handicap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handicap', 'v. t.', 'To encumber with a handicap in any contest; hence, in\n   general, to place at disadvantage; as, the candidate was heavily\n   handicapped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handicapper', 'n.', 'One who determines the conditions of a handicap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handicraft', 'n.', 'A trade requiring skill of hand; manual occupation;\n   handcraft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handicraft', 'n.', 'A man who earns his living by handicraft; a\n   handicraftsman.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-men', 'pl. ', 'of Handi-craftsman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handi-craftsman', 'n.', 'A man skilled or employed in handcraft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handily', 'adv.', 'In a handy manner; skillfully; conveniently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handiness', 'n.', 'The quality or state of being handy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handiron', 'n.', 'See Andrion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handiwork', 'n.', 'Work done by the hands; hence, any work done personally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handkercher', 'n.', 'A handkerchief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handkerchief', 'n.', 'A piece of cloth, usually square and often fine and\n   elegant, carried for wiping the face or hands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handkerchief', 'n.', 'A piece of cloth shaped like a handkerchief to be\n   worn about the neck; a neckerchief; a neckcloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handled', 'imp. & p. p.', 'of Handle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handling', 'p. pr. & vb. n.', 'of Handle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handle', 'v. t.', 'To touch; to feel with the hand; to use or hold with the\n   hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handle', 'v. t.', 'To manage in using, as a spade or a musket; to wield;\n   often, to manage skillfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handle', 'v. t.', 'To accustom to the hand; to work upon, or take care of,\n   with the hands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handle', 'v. t.', 'To receive and transfer; to have pass through ones\n   hands; hence, to buy and sell; as, a merchant handles a variety of\n   goods, or a large stock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handle', 'v. t.', 'To deal with; to make a business of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handle', 'v. t.', 'To treat; to use, well or ill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handle', 'v. t.', 'To manage; to control; to practice skill upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handle', 'v. t.', 'To use or manage in writing or speaking; to treat, as a\n   theme, an argument, or an objection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handle', 'v. i.', 'To use the hands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handle', 'n.', 'That part of vessels, instruments, etc., which is held in\n   the hand when used or moved, as the haft of a sword, the knob of a\n   door, the bail of a kettle, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handle', 'n.', 'That of which use is made; the instrument for effecting a\n   purpose; a tool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handleable', 'a.', 'Capable of being handled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handless', 'a.', 'Without a hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handling', 'n.', 'A touching, controlling, managing, using, etc., with the\n   hand or hands, or as with the hands. See Handle, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handling', 'v. t.', 'The mode of using the pencil or brush, etc.; style of\n   touch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handmade', 'a.', 'Manufactured by hand; as, handmade shoes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handmaid', 'n.', 'Alt. of Handmaiden');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handmaiden', 'n.', 'A maid that waits at hand; a female servant or\n   attendant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handsaw', 'n.', 'A saw used with one hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handsel', 'n.', 'A sale, gift, or delivery into the hand of another;\n   especially, a sale, gift, delivery, or using which is the first of a\n   series, and regarded as on omen for the rest; a first installment; an\n   earnest; as the first money received for the sale of goods in the\n   morning, the first money taken at a shop newly opened, the first\n   present sent to a young woman on her wedding day, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handsel', 'n.', 'Price; payment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handseled', 'imp. & p. p.', 'of Handsel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handseled', '', 'of Handsel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handseling', 'p. pr. & vb. n.', 'of Handsel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handselling', '', 'of Handsel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handsel', 'n.', 'To give a handsel to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handsel', 'n.', 'To use or do for the first time, esp. so as to make\n   fortunate or unfortunate; to try experimentally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handsome', 'superl.', 'Dexterous; skillful; handy; ready; convenient; --\n   applied to things as persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handsome', 'superl.', 'Agreeable to the eye or to correct taste; having a\n   pleasing appearance or expression; attractive; having symmetry and\n   dignity; comely; -- expressing more than pretty, and less than\n   beautiful; as, a handsome man or woman; a handsome garment, house,\n   tree, horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handsome', 'superl.', 'Suitable or fit in action; marked with propriety and\n   ease; graceful; becoming; appropriate; as, a handsome style, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handsome', 'superl.', 'Evincing a becoming generosity or nobleness of\n   character; liberal; generous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handsome', 'superl.', 'Ample; moderately large.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hadsome', 'v. t.', 'To render handsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handsomely', 'adv.', 'In a handsome manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handsomely', 'adv.', 'Carefully; in shipshape style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handsomeness', 'n.', 'The quality of being handsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handspike', 'n.', 'A bar or lever, generally of wood, used in a windlass or\n   capstan, for heaving anchor, and, in modified forms, for various\n   purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handspring', 'n.', 'A somersault made with the assistance of the hands\n   placed upon the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hand-tight', 'a.', 'As tight as can be made by the hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handwheel', 'n.', 'Any wheel worked by hand; esp., one the rim of which\n   serves as the handle by which a valve, car brake, or other part is\n   adjusted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hand-winged', 'a.', 'Having wings that are like hands in the structure and\n   arrangement of their bones; -- said of bats. See Cheiroptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handwriting', 'n.', 'The cast or form of writing peculiar to each hand or\n   person; chirography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handwriting', 'n.', 'That which is written by hand; manuscript.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handy', 'superl.', 'Performed by the hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handy', 'superl.', 'Skillful in using the hand; dexterous; ready; adroit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handy', 'superl.', 'Ready to the hand; near; also, suited to the use of the\n   hand; convenient; valuable for reference or use; as, my tools are\n   handy; a handy volume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handy', 'superl.', 'Easily managed; obedient to the helm; -- said of a\n   vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handyy-dandy', 'n.', 'A childs play, one child guessing in which closed\n   hand the other holds some small object, winning the object if right and\n   forfeiting an equivalent if wrong; hence, forfeit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handyfight', 'n.', 'A fight with the hands; boxing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handygripe', 'n.', 'Seizure by, or grasp of, the hand; also, close quarters\n   in fighting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Handystroke', 'n.', 'A blow with the hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hand-work', 'n.', 'See Handiwork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hanged', 'imp. & p. p.', 'of Hang');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hung', '', 'of Hang');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hanging', 'p. pr. & vb. n.', 'of Hang');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hang', 'v. i.', 'To suspend; to fasten to some elevated point without\n   support from below; -- often used with up or out; as, to hang a coat on\n   a hook; to hang up a sign; to hang out a banner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hang', 'v. i.', 'To fasten in a manner which will allow of free motion upon\n   the point or points of suspension; -- said of a pendulum, a swing, a\n   door, gate, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hang', 'v. i.', 'To fit properly, as at a proper angle (a part of an\n   implement that is swung in using), as a scythe to its snath, or an ax\n   to its helve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hang', 'v. i.', 'To put to death by suspending by the neck; -- a form of\n   capital punishment; as, to hang a murderer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hang', 'v. i.', 'To cover, decorate, or furnish by hanging pictures\n   trophies, drapery, and the like, or by covering with paper hangings; --\n   said of a wall, a room, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hang', 'v. i.', 'To paste, as paper hangings, on the walls of a room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hang', 'v. i.', 'To hold or bear in a suspended or inclined manner or\n   position instead of erect; to droop; as, he hung his head in shame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hang', 'v. i.', 'To be suspended or fastened to some elevated point without\n   support from below; to dangle; to float; to rest; to remain; to stay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hang', 'v. i.', 'To be fastened in such a manner as to allow of free motion\n   on the point or points of suspension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hang', 'v. i.', 'To die or be put to death by suspension from the neck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hang', 'v. i.', 'To hold for support; to depend; to cling; -- usually with\n   on or upon; as, this question hangs on a single point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hang', 'v. i.', 'To be, or be like, a suspended weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hang', 'v. i.', 'To hover; to impend; to appear threateningly; -- usually\n   with over; as, evils hang over the country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hang', 'v. i.', 'To lean or incline; to incline downward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hang', 'v. i.', 'To slope down; as, hanging grounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hang', 'v. i.', 'To be undetermined or uncertain; to be in suspense; to\n   linger; to be delayed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hang', 'n.', 'The manner in which one part or thing hangs upon, or is\n   connected with, another; as, the hang of a scythe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hang', 'n.', 'Connection; arrangement; plan; as, the hang of a discourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hang', 'n.', 'A sharp or steep declivity or slope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hangbird', 'n.', 'The Baltimore oriole (Icterus galbula); -- so called\n   because its nest is suspended from the limb of a tree. See Baltimore\n   oriole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hang-bies', 'pl. ', 'of Hang-by');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hang-by', 'n.', 'A dependent; a hanger-on; -- so called in contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hangdog', 'n.', 'A base, degraded person; a sneak; a gallows bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hangdog', 'a.', 'Low; sneaking; ashamed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hanger', 'n.', 'One who hangs, or causes to be hanged; a hangman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hanger', 'n.', 'That by which a thing is suspended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hanger', 'n.', 'A strap hung to the girdle, by which a dagger or sword is\n   suspended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hanger', 'n.', 'A part that suspends a journal box in which shafting runs.\n   See Illust. of Countershaft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hanger', 'n.', 'A bridle iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hanger', 'n.', 'That which hangs or is suspended, as a sword worn at the\n   side; especially, in the 18th century, a short, curved sword.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hanger', 'n.', 'A steep, wooded declivity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hangers-on', 'pl. ', 'of Hanger-on');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hanger-on', 'n.', 'One who hangs on, or sticks to, a person, place, or\n   service; a dependent; one who adheres to others society longer than he\n   is wanted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hanging', 'a.', 'Requiring, deserving, or foreboding death by the halter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hanging', 'a.', 'Suspended from above; pendent; as, hanging shelves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hanging', 'a.', 'Adapted for sustaining a hanging object; as, the hanging\n   post of a gate, the post which holds the hinges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hanging', 'n.', 'The act of suspending anything; the state of being\n   suspended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hanging', 'n.', 'Death by suspension; execution by a halter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hanging', 'n.', 'That which is hung as lining or drapery for the walls of a\n   room, as tapestry, paper, etc., or to cover or drape a door or window;\n   -- used chiefly in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hangmen', 'pl. ', 'of Hangman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hangman', 'n.', 'One who hangs another; esp., one who makes a business of\n   hanging; a public executioner; -- sometimes used as a term of reproach,\n   without reference to office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hangmanship', 'n.', 'The office or character of a hangman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hangnail', 'n.', 'A small piece or silver of skin which hangs loose, near\n   the root of finger nail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hangnest', 'n.', 'A nest that hangs like a bag or pocket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hangnest', 'n.', 'A bird which builds such a nest; a hangbird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hank', 'n.', 'A parcel consisting of two or more skeins of yarn or thread\n   tied together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hank', 'n.', 'A rope or withe for fastening a gate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hank', 'n.', 'Hold; influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hank', 'n.', 'A ring or eye of rope, wood, or iron, attached to the edge of\n   a sail and running on a stay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hank', 'v. t.', 'To fasten with a rope, as a gate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hank', 'v. t.', 'To form into hanks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hankered', 'imp. & p. p.', 'of Hanker');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hankering', 'p. pr. & vb. n.', 'of Hanker');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hanker', 'v. i.', 'To long (for) with a keen appetite and uneasiness; to\n   have a vehement desire; -- usually with for or after; as, to hanker\n   after fruit; to hanker after the diversions of the town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hanker', 'v. i.', 'To linger in expectation or with desire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hankeringly', 'adv.', 'In a hankering manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hankey-pankey', 'n.', 'Professional cant; the chatter of conjurers to\n   divert attention from their tricks; hence, jugglery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hanoverian', 'a.', 'Of or pertaining to Hanover or its people, or to the\n   House of Hanover in England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hanoverian', 'n.', 'A native or naturalized inhabitant of Hanover; one of\n   the House of Hanover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Han sa', 'n.', 'See 2d Hanse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hansard', 'n.', 'An official report of proceedings in the British\n   Parliament; -- so called from the name of the publishers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hansard', 'n.', 'A merchant of one of the Hanse towns. See the Note under\n   2d Hanse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hanse', 'n.', 'That part of an elliptical or many-centered arch which has\n   the shorter radius and immediately adjoins the impost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hanse', 'n.', 'An association; a league or confederacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hanseatic', 'a.', 'Pertaining to the Hanse towns, or to their confederacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hansel', 'n. & v.', 'See Handsel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hanselines', 'n.', 'A sort of breeches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hansom', '', 'Alt. of Hansom cab');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hansom cab', '', 'A light, low, two-wheeled covered carriage with the\n   drivers seat elevated behind, the reins being passed over the top.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hant', '', 'A contraction of have not, or has not, used in illiterate\n   speech. In the United States the commoner spelling is haint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hanuman', 'n.', 'See Hoonoomaun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hap', 'v. t.', 'To clothe; to wrap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hap', 'n.', 'A cloak or plaid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hap', 'n.', 'That which happens or comes suddenly or unexpectedly; also,\n   the manner of occurrence or taking place; chance; fortune; accident;\n   casual event; fate; luck; lot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hap', 'v. i.', 'To happen; to befall; to chance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Happenny', 'n.', 'A half-penny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haphazard', 'n.', 'Extra hazard; chance; accident; random.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hapless', 'a.', 'Without hap or luck; luckless; unfortunate; unlucky;\n   unhappy; as, hapless youth; hapless maid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haplessly', 'adv.', 'In a hapless, unlucky manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haplomi', 'n. pl.', 'An order of freshwater fishes, including the true\n   pikes, cyprinodonts, and blindfishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haplostemonous', 'a.', 'Having but one series of stamens, and that equal in\n   number to the proper number of petals; isostemonous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haply', 'adv.', 'By hap, chance, luck, or accident; perhaps; it may be.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Happed', 'p. a.', 'Wrapped; covered; cloaked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Happened', 'imp. & p. p.', 'of Happen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Happening', 'p. pr. & vb. n.', 'of Happen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Happen', 'v. i.', 'To come by chance; to come without previous expectation;\n   to fall out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Happen', 'v. i.', 'To take place; to occur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Happily', 'adv.', 'By chance; peradventure; haply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Happily', 'adv.', 'By good fortune; fortunately; luckily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Happily', 'adv.', 'In a happy manner or state; in happy circumstances; as,\n   he lived happily with his wife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Happily', 'adv.', 'With address or dexterity; gracefully; felicitously; in\n   a manner to success; with success.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Happiness', 'n.', 'Good luck; good fortune; prosperity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Happiness', 'n.', 'An agreeable feeling or condition of the soul arising\n   from good fortune or propitious happening of any kind; the possession\n   of those circumstances or that state of being which is attended\n   enjoyment; the state of being happy; contentment; joyful satisfaction;\n   felicity; blessedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Happiness', 'n.', 'Fortuitous elegance; unstudied grace; -- used especially\n   of language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Happy', 'superl.', 'Favored by hap, luck, or fortune; lucky; fortunate;\n   successful; prosperous; satisfying desire; as, a happy expedient; a\n   happy effort; a happy venture; a happy omen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Happy', 'superl.', 'Experiencing the effect of favorable fortune; having\n   the feeling arising from the consciousness of well-being or of\n   enjoyment; enjoying good of any kind, as peace, tranquillity, comfort;\n   contented; joyous; as, happy hours, happy thoughts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Happy', 'superl.', 'Dexterous; ready; apt; felicitous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hapuku', 'n.', 'A large and valuable food fish (Polyprion prognathus) of\n   New Zealand. It sometimes weighs one hundred pounds or more.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haquebut', 'n.', 'See Hagbut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hara-kiri', 'n.', 'Suicide, by slashing the abdomen, formerly practiced in\n   Japan, and commanded by the government in the cases of disgraced\n   officials; disembowelment; -- also written, but incorrectly, hari-kari.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harangue', 'n.', 'A speech addressed to a large public assembly; a popular\n   oration; a loud address a multitude; in a bad sense, a noisy or pompous\n   speech; declamation; ranting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harangued', 'imp. & p. p.', 'of Harangue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haranguing', 'p. pr. & vb. n.', 'of Harangue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harangue', 'v. i.', 'To make an harangue; to declaim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harangue', 'v. t.', 'To address by an harangue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harangueful', 'a.', 'Full of harangue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haranguer', 'n.', 'One who harangues, or is fond of haranguing; a\n   declaimer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harassed', 'imp. & p. p.', 'of Harass');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harassing', 'p. pr. & vb. n.', 'of Harass');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harass', 'v. t.', 'To fatigue; to tire with repeated and exhausting\n   efforts; esp., to weary by importunity, teasing, or fretting; to cause\n   to endure excessive burdens or anxieties; -- sometimes followed by out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harass', 'n.', 'Devastation; waste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harass', 'n.', 'Worry; harassment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harasser', 'n.', 'One who harasses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harassment', 'n.', 'The act of harassing, or state of being harassed;\n   worry; annoyance; anxiety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harberous', 'a.', 'Harborous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harbinger', 'n.', 'One who provides lodgings; especially, the officer of\n   the English royal household who formerly preceded the court when\n   traveling, to provide and prepare lodgings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harbinger', 'n.', 'A forerunner; a precursor; a messenger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harbingered', 'imp. & p. p.', 'of Harbinger');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harbingering', 'p. pr. & vb. n.', 'of Harbinger');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harbinger', 'v. t.', 'To usher in; to be a harbinger of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harbor', 'n.', 'A station for rest and entertainment; a place of security\n   and comfort; a refuge; a shelter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harbor', 'n.', 'Specif.: A lodging place; an inn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harbor', 'n.', 'The mansion of a heavenly body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harbor', 'n.', 'A portion of a sea, a lake, or other large body of water,\n   either landlocked or artificially protected so as to be a place of\n   safety for vessels in stormy weather; a port or haven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harbor', 'n.', 'A mixing box materials.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harbored', 'imp. & p. p.', 'of Harbor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harboring', 'p. pr. & vb. n.', 'of Harbor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harbor', 'n.', 'To afford lodging to; to enter as guest; to receive; to\n   give a refuge to; indulge or cherish (a thought or feeling, esp. an ill\n   thought).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harbor', 'v. i.', 'To lodge, or abide for a time; to take shelter, as in a\n   harbor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harborage', 'n.', 'Shelter; entertainment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harborer', 'n.', 'One who, or that which, harbors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harborless', 'a.', 'Without a harbor; shelterless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harbor master', '', 'An officer charged with the duty of executing the\n   regulations respecting the use of a harbor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harborough', '', 'Alt. of Harbrough');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harbrough', '', 'A shelter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harborous', 'a.', 'Hospitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hard', 'superl.', 'Not easily penetrated, cut, or separated into parts; not\n   yielding to pressure; firm; solid; compact; -- applied to material\n   bodies, and opposed to soft; as, hard wood; hard flesh; a hard apple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hard', 'superl.', 'Difficult, mentally or judicially; not easily\n   apprehended, decided, or resolved; as a hard problem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hard', 'superl.', 'Difficult to accomplish; full of obstacles; laborious;\n   fatiguing; arduous; as, a hard task; a disease hard to cure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hard', 'superl.', 'Difficult to resist or control; powerful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hard', 'superl.', 'Difficult to bear or endure; not easy to put up with or\n   consent to; hence, severe; rigorous; oppressive; distressing; unjust;\n   grasping; as, a hard lot; hard times; hard fare; a hard winter; hard\n   conditions or terms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hard', 'superl.', 'Difficult to please or influence; stern; unyielding;\n   obdurate; unsympathetic; unfeeling; cruel; as, a hard master; a hard\n   heart; hard words; a hard character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hard', 'superl.', 'Not easy or agreeable to the taste; stiff; rigid;\n   ungraceful; repelling; as, a hard style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hard', 'superl.', 'Rough; acid; sour, as liquors; as, hard cider.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hard', 'superl.', 'Abrupt or explosive in utterance; not aspirated,\n   sibilated, or pronounced with a gradual change of the organs from one\n   position to another; -- said of certain consonants, as c in came, and g\n   in go, as distinguished from the same letters in center, general, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hard', 'superl.', 'Wanting softness or smoothness of utterance; harsh; as,\n   a hard tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hard', 'superl.', 'Rigid in the drawing or distribution of the figures;\n   formal; lacking grace of composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hard', 'superl.', 'Having disagreeable and abrupt contrasts in the coloring\n   or light and shade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hard', 'adv.', 'With pressure; with urgency; hence, diligently; earnestly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hard', 'adv.', 'With difficulty; as, the vehicle moves hard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hard', 'adv.', 'Uneasily; vexatiously; slowly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hard', 'adv.', 'So as to raise difficulties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hard', 'adv.', 'With tension or strain of the powers; violently; with\n   force; tempestuously; vehemently; vigorously; energetically; as, to\n   press, to blow, to rain hard; hence, rapidly; as, to run hard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hard', 'adv.', 'Close or near.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hard', 'v. t.', 'To harden; to make hard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hard', 'n.', 'A ford or passage across a river or swamp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardbake', 'n.', 'A sweetmeat of boiled brown sugar or molasses made with\n   almonds, and flavored with orange or lemon juice, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardbeam', 'n.', 'A tree of the genus Carpinus, of compact, horny texture;\n   hornbeam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardened', 'imp. & p. p.', 'of Harden');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardening', 'p. pr. & vb. n.', 'of Harden');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harden', 'v. t.', 'To make hard or harder; to make firm or compact; to\n   indurate; as, to harden clay or iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harden', 'v. t.', 'To accustom by labor or suffering to endure with\n   constancy; to strengthen; to stiffen; to inure; also, to confirm in\n   wickedness or shame; to make unimpressionable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harden', 'v. i.', 'To become hard or harder; to acquire solidity, or more\n   compactness; as, mortar hardens by drying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harden', 'v. i.', 'To become confirmed or strengthened, in either a good or\n   a bad sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardened', 'a.', 'Made hard, or compact; made unfeeling or callous; made\n   obstinate or obdurate; confirmed in error or vice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardener', 'n.', 'One who, or that which, hardens; specif., one who tempers\n   tools.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardening', 'n.', 'Making hard or harder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardening', 'n.', 'That which hardens, as a material used for converting\n   the surface of iron into steel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harder', 'n.', 'A South African mullet, salted for food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harderian', 'a.', 'A term applied to a lachrymal gland on the inner side of\n   the orbit of many animals which have a third eyelid, or nictitating\n   membrane. See Nictitating membrane, under Nictitate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hard-favored', 'a.', 'Hard-featured; ill-looking; as, Vulcan was\n   hard-favored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardfavoredness', 'n.', 'Coarseness of features.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hard-featured', 'a.', 'Having coarse, unattractive or stern features.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardfern', 'n.', 'A species of fern (Lomaria borealis), growing in Europe\n   and Northwestern America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hard-fisted', 'a.', 'Having hard or strong hands; as, a hard-fisted\n   laborer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hard-fisted', 'a.', 'Close-fisted; covetous; niggardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hard-fought', 'a. Vigorously', 'contested; as, a hard-fought battle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hard grass', '', 'A name given to several different grasses, especially to\n   the Roltbollia incurvata, and to the species of Aegilops, from one of\n   which it is contended that wheat has been derived.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardhack', 'n.', 'A very astringent shrub (Spiraea tomentosa), common in\n   pastures. The Potentilla fruticosa in also called by this name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hard-handed', 'a.', 'Having hard hands, as a manual laborer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardhead', 'n.', 'Clash or collision of heads in contest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardhead', 'n.', 'The menhaden. See Menhaden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardhead', 'n.', 'Blocks gurnard (Trigla gurnardus) of Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardhead', 'n.', 'A California salmon; the steelhead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardhead', 'n.', 'The gray whale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardhead', 'n.', 'A coarse American commercial sponge (Spongia dura).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hard-headed', 'a.', 'Having sound judgment; sagacious; shrewd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hard-hearted', 'a.', 'Unsympathetic; inexorable; cruel; pitiless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harddihead', 'n.', 'Hardihood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harddihood', 'n.', 'Boldness, united with firmness and constancy of mind;\n   bravery; intrepidity; also, audaciousness; impudence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardily', 'adv.', 'Same as Hardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardily', 'adv.', 'Boldly; stoutly; resolutely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardiment', 'n.', 'Hardihood; boldness; courage; energetic action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardiness', 'n.', 'Capability of endurance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardiness', 'n.', 'Hardihood; boldness; firmness; assurance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardiness', 'n.', 'Hardship; fatigue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardish', 'a.', 'Somewhat hard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hard-labored', 'a.', 'Wrought with severe labor; elaborate; studied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardly', 'adv.', 'In a hard or difficult manner; with difficulty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardly', 'adv.', 'Unwillingly; grudgingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardly', 'adv.', 'Scarcely; barely; not guite; not wholly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardly', 'adv.', 'Severely; harshly; roughly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardly', 'adv.', 'Confidently; hardily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardly', 'adv.', 'Certainly; surely; indeed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hard-mouthed', 'a.', 'Not sensible to the bit; not easily governed; as, a\n   hard-mouthed horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardness', 'n.', 'The quality or state of being hard, literally or\n   figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardness', 'n.', 'The cohesion of the particles on the surface of a body,\n   determined by its capacity to scratch another, or be itself\n   scratched;-measured among minerals on a scale of which diamond and talc\n   form the extremes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardness', 'n.', 'The peculiar quality exhibited by water which has mineral\n   salts dissolved in it. Such water forms an insoluble compound with\n   soap, and is hence unfit for washing purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardock', 'n.', 'See Hordock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardpan', 'n.', 'The hard substratum. Same as Hard pan, under Hard, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hards', 'n. pl.', 'The refuse or coarse part of fiax; tow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hard-shell', 'a.', 'Unyielding; insensible to argument; uncompromising;\n   strict.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardship', 'n.', 'That which is hard to hear, as toil, privation, injury,\n   injustice, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardspun', 'a.', 'Firmly twisted in spinning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hard-tack', 'n.', 'A name given by soldiers and sailors to a kind of hard\n   biscuit or sea bread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardtail', 'n.', 'See Jurel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hard-visaged', 'a.', 'Of a harsh or stern countenance; hard-featured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardware', 'n.', 'Ware made of metal, as cutlery, kitchen utensils, and the\n   like; ironmongery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardwaremen', 'pl. ', 'of Hardwareman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardwareman', 'n.', 'One who makes, or deals in, hardware.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardy', 'a.', 'Bold; brave; stout; daring; resolu?e; intrepid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardy', 'a.', 'Confident; full of assurance; in a bad sense, morally\n   hardened; shameless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardy', 'a.', 'Strong; firm; compact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardy', 'a.', 'Inured to fatigue or hardships; strong; capable of\n   endurance; as, a hardy veteran; a hardy mariner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardy', 'a.', 'Able to withstand the cold of winter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hardy', 'n.', 'A blacksmiths fuller or chisel, having a square shank for\n   insertion into a square hole in an anvil, called the hardy hole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hare', 'v. t.', 'To excite; to tease, or worry; to harry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hare', 'n.', 'A rodent of the genus Lepus, having long hind legs, a short\n   tail, and a divided upper lip. It is a timid animal, moves swiftly by\n   leaps, and is remarkable for its fecundity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hare', 'n.', 'A small constellation situated south of and under the foot of\n   Orion; Lepus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harebell', 'n.', 'A small, slender, branching plant (Campanula\n   rotundifolia), having blue bell-shaped flowers; also, Scilla nutans,\n   which has similar flowers; -- called also bluebell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harebrained', 'a.', 'Wild; giddy; volatile; heedless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harefoot', 'n.', 'A long, narrow foot, carried (that is, produced or\n   extending) forward; -- said of dogs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harefoot', 'n.', 'A tree (Ochroma Laqopus) of the West Indies, having the\n   stamens united somewhat in the form of a hares foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hare-hearted', 'a.', 'Timorous; timid; easily frightened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harehound', 'n.', 'See Harrier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hareld', 'n.', 'The long-tailed duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harelip', 'n.', 'A lip, commonly the upper one, having a fissure of\n   perpendicular division like that of a hare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harem', 'n.', 'The apartments or portion of the house allotted to females\n   in Mohammedan families.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harem', 'n.', 'The family of wives and concubines belonging to one man, in\n   Mohammedan countries; a seraglio.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harengiform', 'a.', 'Herring-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hares-ear', 'n.', 'An umbelliferous plant (Bupleurum rotundifolium ); --\n   so named from the shape of its leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hares-foot fern', '', 'A species of fern (Davallia Canariensis) with a\n   soft, gray, hairy rootstock; -- whence the name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hares-tail', 'n.', 'A kind of grass (Eriophorum vaginatum). See Cotton\n   grass, under Cotton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harfang', 'n.', 'The snowy owl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hariali grass', '', 'The East Indian name of the Cynodon Dactylon;\n   dogs-grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haricot', 'n.', 'A ragout or stew of meat with beans and other vegetables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haricot', 'n.', 'The ripe seeds, or the unripe pod, of the common string\n   bean (Phaseolus vulgaris), used as a vegetable. Other species of the\n   same genus furnish different kinds of haricots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harier', 'n.', 'See Harrier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harikari', 'n.', 'See Hara-kiri.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harioiation', 'n.', 'Prognostication; soothsaying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harish', 'a.', 'Like a hare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hark', 'v. i.', 'To listen; to hearken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harken', 'v. t. & i.', 'To hearken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harl', 'n.', 'A filamentous substance; especially, the filaments of flax or\n   hemp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harl', 'n.', 'A barb, or barbs, of a fine large feather, as of a peacock or\n   ostrich, -- used in dressing artificial flies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harle', 'n.', 'The red-breasted merganser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harlech group', '', 'A minor subdivision at the base of the Cambrian system\n   in Wales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harlequin', 'n.', 'A buffoon, dressed in party-colored clothes, who plays\n   tricks, often without speaking, to divert the bystanders or an\n   audience; a merry-andrew; originally, a droll rogue of Italian comedy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harlequin', 'n. i.', 'To play the droll; to make sport by playing ludicrous\n   tricks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harlequin', 'v. t.', 'Toremove or conjure away, as by a harlequins trick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harlequinade', 'n.', 'A play or part of play in which the harlequin is\n   conspicuous; the part of a harlequin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harlock', 'n.', 'Probably a corruption either of charlock or hardock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harlot', 'n.', 'A churl; a common man; a person, male or female, of low\n   birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harlot', 'n.', 'A person given to low conduct; a rogue; a cheat; a rascal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harlot', 'n.', 'A woman who prostitutes her body for hire; a prostitute; a\n   common woman; a strumpet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harlot', 'a.', 'Wanton; lewd; low; base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harlot', 'v. i.', 'To play the harlot; to practice lewdness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harlotize', 'v. i.', 'To harlot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harlotry', 'n.', 'Ribaldry; buffoonery; a ribald story.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harlotry', 'n.', 'The trade or practice of prostitution; habitual or\n   customary lewdness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harlotry', 'n.', 'Anything meretricious; as, harlotry in art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harlotry', 'n.', 'A harlot; a strumpet; a baggage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harm', 'n.', 'Injury; hurt; damage; detriment; misfortune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harm', 'n.', 'That which causes injury, damage, or loss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmed', 'imp. & p. p.', 'of Harm');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harming', 'p. pr. & vb. n.', 'of Harm');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harm', 'n.', 'To hurt; to injure; to damage; to wrong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmaline', 'n.', 'An alkaloid found in the plant Peganum harmala. It forms\n   bitter, yellow salts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmattan', 'n.', 'A dry, hot wind, prevailing on the Atlantic coast of\n   Africa, in December, January, and February, blowing from the interior\n   or Sahara. It is usually accompanied by a haze which obscures the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmel', 'n.', 'A kind of rue (Ruta sylvestris) growing in India. At Lahore\n   the seeds are used medicinally and for fumigation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmful', 'a.', 'Full of harm; injurious; hurtful; mischievous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmine', 'n.', 'An alkaloid accompanying harmaline (in the Peganum\n   harmala), and obtained from it by oxidation. It is a white crystalline\n   substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmless', 'a.', 'Free from harm; unhurt; as, to give bond to save another\n   harmless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmless', 'a.', 'Free from power or disposition to harm; innocent;\n   inoffensive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmonic', 'a.', 'Alt. of Harmonical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmonical', 'a.', 'Concordant; musical; consonant; as, harmonic sounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmonical', 'a.', 'Relating to harmony, -- as melodic relates to melody;\n   harmonious; esp., relating to the accessory sounds or overtones which\n   accompany the predominant and apparent single tone of any string or\n   sonorous body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmonical', 'a.', 'Having relations or properties bearing some resemblance\n   to those of musical consonances; -- said of certain numbers, ratios,\n   proportions, points, lines. motions, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmonic', 'n.', 'A musical note produced by a number of vibrations which\n   is a multiple of the number producing some other; an overtone. See\n   Harmonics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmonica', 'n.', 'A musical instrument, consisting of a series of\n   hemispherical glasses which, by touching the edges with the dampened\n   finger, give forth the tones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmonica', 'n.', 'A toy instrument of strips of glass or metal hung on two\n   tapes, and struck with hammers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Har monically', 'adv.', 'In an harmonical manner; harmoniously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Har monically', 'adv.', 'In respect to harmony, as distinguished from\n   melody; as, a passage harmonically correct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Har monically', 'adv.', 'In harmonical progression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmonicon', 'n.', 'A small, flat, wind instrument of music, in which the\n   notes are produced by the vibration of free metallic reeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmonics', 'n.', 'The doctrine or science of musical sounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmonics', 'n.', 'Secondary and less distinct tones which accompany any\n   principal, and apparently simple, tone, as the octave, the twelfth, the\n   fifteenth, and the seventeenth. The name is also applied to the\n   artificial tones produced by a string or column of air, when the\n   impulse given to it suffices only to make a part of the string or\n   column vibrate; overtones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmonious', 'a.', 'Adapted to each other; having parts proportioned to\n   each other; symmetrical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmonious', 'a.', 'Acting together to a common end; agreeing in action or\n   feeling; living in peace and friendship; as, an harmonious family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmonious', 'a.', 'Vocally or musically concordant; agreeably consonant;\n   symphonious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmoniphon', 'n.', 'An obsolete wind instrument with a keyboard, in which\n   the sound, which resembled the oboe, was produced by the vibration of\n   thin metallic plates, acted upon by blowing through a tube.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmonist', 'n.', 'One who shows the agreement or harmony of corresponding\n   passages of different authors, as of the four evangelists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmonist', 'n.', 'One who understands the principles of harmony or is\n   skillful in applying them in composition; a musical composer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmonist', 'n.', 'Alt. of Harmonite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmonite', 'n.', 'One of a religious sect, founded in Wurtemburg in the\n   last century, composed of followers of George Rapp, a weaver. They had\n   all their property in common. In 1803, a portion of this sect settled\n   in Pennsylvania and called the village thus established, Harmony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmonium', 'n.', 'A musical instrument, resembling a small organ and\n   especially designed for church music, in which the tones are produced\n   by forcing air by means of a bellows so as to cause the vibration of\n   free metallic reeds. It is now made with one or two keyboards, and has\n   pedals and stops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmonization', 'n.', 'The act of harmonizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmonized', 'imp. & p. p.', 'of Harmonize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmonizing', 'p. pr. & vb. n.', 'of Harmonize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmonize', 'v. i.', 'To agree in action, adaptation, or effect on the\n   mind; to agree in sense or purport; as, the parts of a mechanism\n   harmonize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmonize', 'v. i.', 'To be in peace and friendship, as individuals,\n   families, or public organizations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmonize', 'v. i.', 'To agree in vocal or musical effect; to form a\n   concord; as, the tones harmonize perfectly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmonize', 'v. t.', 'To adjust in fit proportions; to cause to agree; to\n   show the agreement of; to reconcile the apparent contradiction of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmonize', 'v. t.', 'To accompany with harmony; to provide with parts, as\n   an air, or melody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmonizer', 'n.', 'One who harmonizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmonometer', 'n.', 'An instrument for measuring the harmonic relations of\n   sounds. It is often a monochord furnished with movable bridges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmonies', 'pl. ', 'of Harmony');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmony', 'n.', 'The just adaptation of parts to each other, in any system\n   or combination of things, or in things, or things intended to form a\n   connected whole; such an agreement between the different parts of a\n   design or composition as to produce unity of effect; as, the harmony of\n   the universe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmony', 'n.', 'Concord or agreement in facts, opinions, manners,\n   interests, etc.; good correspondence; peace and friendship; as, good\n   citizens live in harmony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmony', 'n.', 'A literary work which brings together or arranges\n   systematically parallel passages of historians respecting the same\n   events, and shows their agreement or consistency; as, a harmony of the\n   Gospels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmony', 'n.', 'A succession of chords according to the rules of\n   progression and modulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmony', 'n.', 'The science which treats of their construction and\n   progression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmony', 'n.', 'See Harmonic suture, under Harmonic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmost', 'n.', 'A governor or prefect appointed by the Spartans in the\n   cities subjugated by them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harmotome', 'n.', 'A hydrous silicate of alumina and baryta, occurring\n   usually in white cruciform crystals; cross-stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harness', 'n.', 'Originally, the complete dress, especially in a military\n   sense, of a man or a horse; hence, in general, armor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harness', 'n.', 'The equipment of a draught or carriage horse, for drawing\n   a wagon, coach, chaise, etc.; gear; tackling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harness', 'n.', 'The part of a loom comprising the heddles, with their\n   means of support and motion, by which the threads of the warp are\n   alternately raised and depressed for the passage of the shuttle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harnessed', 'imp. & p. p.', 'of Harness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harnessing', 'p. pr. & vb. n.', 'of Harness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harness', 'v. t.', 'To dress in armor; to equip with armor for war, as a\n   horseman; to array.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harness', 'v. t.', 'Fig.: To equip or furnish for defense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harness', 'v. t.', 'To make ready for draught; to equip with harness, as a\n   horse. Also used figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harness cask', '', 'A tub lashed to a vessels deck and containing salted\n   provisions for daily use; -- called also harness tub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harnesser', 'n.', 'One who harnesses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harns', 'n. pl.', 'The brains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harp', 'n.', 'A musical instrument consisting of a triangular frame\n   furnished with strings and sometimes with pedals, held upright, and\n   played with the fingers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harp', 'n.', 'A constellation; Lyra, or the Lyre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harp', 'n.', 'A grain sieve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harped', 'imp. & p. p.', 'of Harp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harping', 'p. pr. & vb. n.', 'of Harp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harp', 'n.', 'To play on the harp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harp', 'n.', 'To dwell on or recur to a subject tediously or monotonously\n   in speaking or in writing; to refer to something repeatedly or\n   continually; -- usually with on or upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harp', 'v. t.', 'To play on, as a harp; to play (a tune) on the harp; to\n   develop or give expression to by skill and art; to sound forth as from\n   a harp; to hit upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harpa', 'n.', 'A genus of marine univalve shells; the harp shells; -- so\n   called from the form of the shells, and their ornamental ribs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harpagon', 'n.', 'A grappling iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harper', 'n.', 'A player on the harp; a minstrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harper', 'n.', 'A brass coin bearing the emblem of a harp, -- formerly\n   current in Ireland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harping', 'a.', 'Pertaining to the harp; as, harping symphonies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harping iron', '', 'A harpoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harpings', 'n. pl.', 'The fore parts of the wales, which encompass the bow\n   of a vessel, and are fastened to the stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harpist', 'n.', 'A player on the harp; a harper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harpoon', 'n.', 'A spear or javelin used to strike and kill large fish, as\n   whales; a harping iron. It consists of a long shank, with a broad,\n   fiat, triangular head, sharpened at both edges, and is thrown by hand,\n   or discharged from a gun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harpooned', 'imp. & p. p.', 'of Harpoon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harpooning', 'p. pr. & vb. n.', 'of Harpoon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harpoon', 'v. t.', 'To strike, catch, or kill with a harpoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harpooneer', 'n.', 'An harpooner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harpooner', 'n.', 'One who throws the harpoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harpress', 'n.', 'A female harper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harpsichon', 'n.', 'A harpsichord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harpsichord', 'n.', 'A harp-shaped instrument of music set horizontally on\n   legs, like the grand piano, with strings of wire, played by the\n   fingers, by means of keys provided with quills, instead of hammers, for\n   striking the strings. It is now superseded by the piano.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harpies', 'pl. ', 'of Harpy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harpy', 'n.', 'A fabulous winged monster, ravenous and filthy, having the\n   face of a woman and the body of a vulture, with long claws, and the\n   face pale with hunger. Some writers mention two, others three.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harpy', 'n.', 'One who is rapacious or ravenous; an extortioner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harpy', 'n.', 'The European moor buzzard or marsh harrier (Circus\n   aeruginosus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harpy', 'n.', 'A large and powerful, double-crested, short-winged American\n   eagle (Thrasaetus harpyia). It ranges from Texas to Brazil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harquebus', 'n.', 'Alt. of Harquebuse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harquebuse', 'n.', 'A firearm with match holder, trigger, and tumbler, made\n   in the second half of the 15th century. the barrel was about forty\n   inches long. A form of the harquebus was subsequently called arquebus\n   with matchlock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harrage', 'v. t.', 'To harass; to plunder from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harre', 'n.', 'A hinge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harridan', 'n.', 'A worn-out strumpet; a vixenish woman; a hag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harrier', 'n.', 'One of a small breed of hounds, used for hunting hares.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harrier', 'n.', 'One who harries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harrier', 'n.', 'One of several species of hawks or buzzards of the genus\n   Circus which fly low and harry small animals or birds, -- as the\n   European marsh harrier (Circus aerunginosus), and the hen harrier (C.\n   cyaneus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harrow', 'n.', 'An implement of agriculture, usually formed of pieces of\n   timber or metal crossing each other, and set with iron or wooden teeth.\n   It is drawn over plowed land to level it and break the clods, to stir\n   the soil and make it fine, or to cover seed when sown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harrow', 'n.', 'An obstacle formed by turning an ordinary harrow upside\n   down, the frame being buried.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harrowed', 'imp. & p. p.', 'of Harrow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harrowing', 'p. pr. & vb. n.', 'of Harrow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harrow', 'n.', 'To draw a harrow over, as for the purpose of breaking clods\n   and leveling the surface, or for covering seed; as, to harrow land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harrow', 'n.', 'To break or tear, as with a harrow; to wound; to lacerate;\n   to torment or distress; to vex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harrow', 'interj.', 'Help! Halloo! An exclamation of distress; a call for\n   succor;-the ancient Norman hue and cry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harrow', 'v. t.', 'To pillage; to harry; to oppress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harrower', 'n.', 'One who harrows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harrower', 'n.', 'One who harries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harried', 'imp. & p. p.', 'of Harry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harrying', 'p. pr. & vb. n.', 'of Harry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harry', 'v. t.', 'To strip; to lay waste; as, the Northmen came several\n   times and harried the land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harry', 'v. t.', 'To agitate; to worry; to harrow; to harass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harry', 'v. i.', 'To make a predatory incursion; to plunder or lay waste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harsh', 'a.', 'Rough; disagreeable; grating');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harsh', 'a.', 'disagreeable to the touch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harsh', 'a.', 'disagreeable to the taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harsh', 'a.', 'disagreeable to the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harsh', 'a.', 'Unpleasant and repulsive to the sensibilities; austere;\n   crabbed; morose; abusive; abusive; severe; rough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harsh', 'a.', 'Having violent contrasts of color, or of light and shade;\n   lacking in harmony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harshly', 'adv.', 'In a harsh manner; gratingly; roughly; rudely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harshness', 'n.', 'The quality or state of being harsh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harslet', 'n.', 'See Haslet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hart', 'n.', 'A stag; the male of the red deer. See the Note under Buck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hartbeest', 'n.', 'A large South African antelope (Alcelaphus caama),\n   formerly much more abundant than it is now. The face and legs are\n   marked with black, the rump with white.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harten', 'v. t.', 'To hearten; to encourage; to incite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hartford', 'n.', 'The Hartford grape, a variety of grape first raised at\n   Hartford, Connecticut, from the Northern fox grape. Its large\n   dark-colored berries ripen earlier than those of most other kinds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harts clover', '', 'Melilot or sweet clover. See Melilot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harts-ear', 'n.', 'An Asiatic species of Cacalia (C. Kleinia), used\n   medicinally in India.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hartshorn', 'n.', 'The horn or antler of the hart, or male red deer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hartshorn', 'n.', 'Spirits of hartshorn (see below); volatile salts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hart-tongue', 'n.', 'A common British fern (Scolopendrium vulgare), rare in\n   America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hart-tongue', 'n.', 'A West Indian fern, the Polypodium Phyllitidis of\n   Linnaeus. It is also found in Florida.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hartwort', 'n.', 'A coarse umbelliferous plant of Europe (Tordylium\n   maximum).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harum-scarum', 'v. t.', 'Wild; giddy; flighty; rash; thoughtless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haruspication', 'n.', 'See Haruspicy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haruspice', 'n.', 'A diviner of ancient Rome. Same as Aruspice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haruspicy', 'n.', 'The art or practices of haruspices. See Aruspicy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harvest', 'n.', 'The gathering of a crop of any kind; the ingathering of\n   the crops; also, the season of gathering grain and fruits, late summer\n   or early autumn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harvest', 'n.', 'That which is reaped or ready to be reaped or gath//ed; a\n   crop, as of grain (wheat, maize, etc.), or fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harvest', 'n.', 'The product or result of any exertion or labor; gain;\n   reward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harvested', 'imp. & p. p.', 'of Harvest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harvesting', 'p. pr. & vb. n.', 'of Harvest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harvest', 'v. t.', 'To reap or gather, as any crop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harvester', 'n.', 'One who harvests; a machine for cutting and gathering\n   grain; a reaper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harvester', 'n.', 'A harvesting ant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harvest-home', 'n.', 'The gathering and bringing home of the harvest; the\n   time of harvest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harvest-home', 'n.', 'The song sung by reapers at the feast made at the\n   close of the harvest; the feast itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harvest-home', 'n.', 'A service of thanksgiving, at harvest time, in the\n   Church of England and in the Protestant Episcopal Church in the United\n   States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harvest-home', 'n.', 'The opportunity of gathering treasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harvesting', '', 'a. & n., from Harvest, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harvestless', 'a.', 'Without harvest; lacking in crops; barren.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harvestmen', 'pl. ', 'of Harvestman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harvestman', 'n.', 'A man engaged in harvesting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harvestman', 'n.', 'See Daddy longlegs, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Harvestry', 'n.', 'The act of harvesting; also, that which is harvested.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hary', 'v. t.', 'To draw; to drag; to carry off by violence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Has', '', '3d pers. sing. pres. of Have.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hasard', 'n.', 'Hazard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hase', 'v. t.', 'See Haze, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hash', 'n.', 'That which is hashed or chopped up; meat and vegetables,\n   especially such as have been already cooked, chopped into small pieces\n   and mixed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hash', 'n.', 'A new mixture of old matter; a second preparation or\n   exhibition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hashed', 'imp. & p. p.', 'of Hash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hashing', 'p. pr. & vb. n.', 'of Hash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hash', 'n.', 'To /hop into small pieces; to mince and mix; as, to hash\n   meat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hasheesh', 'n.', 'Alt. of Hashish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hashish', 'n.', 'A slightly acrid gum resin produced by the common hemp\n   (Cannabis saltiva), of the variety Indica, when cultivated in a warm\n   climate; also, the tops of the plant, from which the resinous product\n   is obtained. It is narcotic, and has long been used in the East for its\n   intoxicating effect. See Bhang, and Ganja.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hask', 'n.', 'A basket made of rushes or flags, as for carrying fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haslet', 'n.', 'The edible viscera, as the heart, liver, etc., of a beast,\n   esp. of a hog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hasp', 'n.', 'A clasp, especially a metal strap permanently fast at one end\n   to a staple or pin, while the other passes over a staple, and is\n   fastened by a padlock or a pin; also, a metallic hook for fastening a\n   door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hasp', 'n.', 'A spindle to wind yarn, thread, or silk on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hasp', 'n.', 'An instrument for cutting the surface of grass land; a\n   scarifier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hasped', 'imp. & p. p.', 'of Hasp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hasping', 'p. pr. & vb. n.', 'of Hasp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hasp', 'v. t.', 'To shut or fasten with a hasp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hassock', 'n.', 'A rank tuft of bog grass; a tussock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hassock', 'n.', 'A small stuffed cushion or footstool, for kneeling on in\n   church, or for home use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hast', '', '2d pers. sing. pres. of. Have, contr. of havest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hastate', 'n.', 'Alt. of Hastated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hastated', 'n.', 'Shaped like the head of a halberd; triangular, with the\n   basal angles or lobes spreading; as, a hastate leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haste', 'n.', 'Celerity of motion; speed; swiftness; dispatch; expedition;\n   -- applied only to voluntary beings, as men and other animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haste', 'n.', 'The state of being urged or pressed by business; hurry;\n   urgency; sudden excitement of feeling or passion; precipitance;\n   vehemence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hasted', 'imp. & p. p.', 'of Haste');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hasting', 'p. pr. & vb. n.', 'of Haste');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haste', 'n.', 'To hasten; to hurry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hastened', 'imp. & p. p.', 'of Hasten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hastening', 'p. pr. & vb. n.', 'of Hasten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hasten', 'v. t.', 'To press; to drive or urge forward; to push on; to\n   precipitate; to accelerate the movement of; to expedite; to hurry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hasten', 'v. i.', 'To move celerity; to be rapid in motion; to act speedily\n   or quickly; to go quickly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hastener', 'n.', 'One who hastens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hastener', 'n.', 'That which hastens; especially, a stand or reflector used\n   for confining the heat of the fire to meat while roasting before it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hastif', 'a.', 'Hasty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hastile', 'a.', 'Same as Hastate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hastily', 'adv.', 'In haste; with speed or quickness; speedily; nimbly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hastily', 'adv.', 'Without due reflection; precipitately; rashly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hastily', 'adv.', 'Passionately; impatiently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hastiness', 'n.', 'The quality or state of being hasty; haste;\n   precipitation; rashness; quickness of temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hastings', 'v.', 'Early fruit or vegetables; especially, early pease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hastings sands', '', 'The lower group of the Wealden formation; -- so\n   called from its development around Hastings, in Sussex, England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hastive', 'n.', 'Forward; early; -- said of fruits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hasty', 'n.', 'Involving haste; done, made, etc., in haste; as, a hasty\n   sketch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hasty', 'n.', 'Demanding haste or immediate action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hasty', 'n.', 'Moving or acting with haste or in a hurry; hurrying; hence,\n   acting without deliberation; precipitate; rash; easily excited; eager.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hasty', 'n.', 'Made or reached without deliberation or due caution; as, a\n   hasty conjecture, inference, conclusion, etc., a hasty resolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hasty', 'n.', 'Proceeding from, or indicating, a quick temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hasty', 'n.', 'Forward; early; first ripe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hasty pudding', '', 'A thick batter pudding made of Indian meal stirred\n   into boiling water; mush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hasty pudding', '', 'A batter or pudding made of flour or oatmeal, stirred\n   into boiling water or milk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hat', 'a.', 'Hot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hat', '', 'sing. pres. of Hote to be called. Cf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hat', 'n.', 'A covering for the head; esp., one with a crown and brim, made\n   of various materials, and worn by men or women for protecting the head\n   from the sun or weather, or for ornament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatable', 'a.', 'Capable of being, or deserving to be, hated; odious;\n   detestable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatband', 'n.', 'A band round the crown of a hat; sometimes, a band of\n   black cloth, crape, etc., worn as a badge of mourning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatbox', 'n.', 'A box for a hat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatched', 'imp. & p. p.', 'of Hatch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatching', 'p. pr. & vb. n.', 'of Hatch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatch', 'v. t.', 'To cross with lines in a peculiar manner in drawing and\n   engraving. See Hatching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatch', 'v. t.', 'To cross; to spot; to stain; to steep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatch', 'v. t.', 'To produce, as young, from an egg or eggs by incubation,\n   or by artificial heat; to produce young from (eggs); as, the young when\n   hatched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatch', 'v. t.', 'To contrive or plot; to form by meditation, and bring\n   into being; to originate and produce; to concoct; as, to hatch\n   mischief; to hatch heresy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatch', 'v. i.', 'To produce young; -- said of eggs; to come forth from the\n   egg; -- said of the young of birds, fishes, insects, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatch', 'n.', 'The act of hatching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatch', 'n.', 'Development; disclosure; discovery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatch', 'n.', 'The chickens produced at once or by one incubation; a brood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatch', 'n.', 'A door with an opening over it; a half door, sometimes set\n   with spikes on the upper edge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatch', 'n.', 'A frame or weir in a river, for catching fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatch', 'n.', 'A flood gate; a a sluice gate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatch', 'n.', 'A bedstead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatch', 'n.', 'An opening in the deck of a vessel or floor of a warehouse\n   which serves as a passageway or hoistway; a hatchway; also; a cover or\n   door, or one of the covers used in closing such an opening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatch', 'n.', 'An opening into, or in search of, a mine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatch', 'v. t.', 'To close with a hatch or hatches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatch-boat', 'n.', 'A vessel whose deck consists almost wholly of movable\n   hatches; -- used mostly in the fisheries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatchel', 'n.', 'An instrument with long iron teeth set in a board, for\n   cleansing flax or hemp from the tow, hards, or coarse part; a kind of\n   large comb; -- called also hackle and heckle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatcheled', 'imp. & p. p.', 'of Hatchel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatchelled', '', 'of Hatchel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatcheling', 'p. pr. & vb. n.', 'of Hatchel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatchelling', '', 'of Hatchel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatchel', 'n.', 'To draw through the teeth of a hatchel, as flax or hemp,\n   so as to separate the coarse and refuse parts from the fine, fibrous\n   parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatchel', 'n.', 'To tease; to worry; to torment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatcheler', 'n.', 'One who uses a hatchel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatcher', 'n.', 'One who hatches, or that which hatches; a hatching\n   apparatus; an incubator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatcher', 'n.', 'One who contrives or originates; a plotter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatchery', 'n.', 'A house for hatching fish, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatchet', 'n.', 'A small ax with a short handle, to be used with one hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatchet', 'n.', 'Specifically, a tomahawk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatchettine', 'n.', 'Alt. of Hatchettite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatchettite', 'n.', 'Mineral t/ low; a waxy or spermaceti-like substance,\n   commonly of a greenish yellow color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatching', 'n.', 'A mode of execution in engraving, drawing, and miniature\n   painting, in which shading is produced by lines crossing each other at\n   angles more or less acute; -- called also crosshatching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatchment', 'n.', 'A sort of panel, upon which the arms of a deceased\n   person are temporarily displayed, -- usually on the walls of his\n   dwelling. It is lozenge-shaped or square, but is hung cornerwise. It is\n   used in England as a means of giving public notification of the death\n   of the deceased, his or her rank, whether married, widower, widow, etc.\n   Called also achievement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatchment', 'n.', 'A sword or other mark of the profession of arms; in\n   general, a mark of dignity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatchure', 'n.', 'Same as Hachure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatchway', 'n.', 'A square or oblong opening in a deck or floor, affording\n   passage from one deck or story to another; the entrance to a cellar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hated', 'imp. & p. p.', 'of Hate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hating', 'p. pr. & pr. & vb. n', 'of Hate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hate', 'n.', 'To have a great aversion to, with a strong desire that evil\n   should befall the person toward whom the feeling is directed; to\n   dislike intensely; to detest; as, to hate ones enemies; to hate\n   hypocrisy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hate', 'n.', 'To be very unwilling; followed by an infinitive, or a\n   substantive clause with that; as, to hate to get into debt; to hate\n   that anything should be wasted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hate', 'n.', 'To love less, relatively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hate', 'v.', 'Strong aversion coupled with desire that evil should befall\n   the person toward whom the feeling is directed; as exercised toward\n   things, intense dislike; hatred; detestation; -- opposed to love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hateful', 'a.', 'Manifesting hate or hatred; malignant; malevolent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hateful', 'a.', 'Exciting or deserving great dislike, aversion, or disgust;\n   odious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatel', 'a.', 'Hateful; detestable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hater', 'n.', 'One who hates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hath', '3d pers. sing. pres.', 'Has.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatless', 'a.', 'Having no hat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatrack', 'n.', 'A hatstand; hattree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatred', 'n.', 'Strong aversion; intense dislike; hate; an affection of the\n   mind awakened by something regarded as evil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatstand', 'n.', 'A stand of wood or iron, with hooks or pegs upon which to\n   hang hats, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatte', '', 'pres. & imp. sing. & pl. of Hote, to be called. See Hote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatted', 'a.', 'Covered with a hat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatter', 'v. t.', 'To tire or worry; -- out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatter', 'n.', 'One who makes or sells hats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatteria', 'n.', 'A New Zealand lizard, which, in anatomical character,\n   differs widely from all other existing lizards. It is the only living\n   representative of the order Rhynchocephala, of which many Mesozoic\n   fossil species are known; -- called also Sphenodon, and Tuatera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatting', 'n.', 'The business of making hats; also, stuff for hats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatti-sherif', 'n.', 'A irrevocable Turkish decree countersigned by the\n   sultan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hattree', 'n.', 'A hatstand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haubergeon', 'n.', 'See Habergeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hauberk', 'v. t.', 'A coat of mail; especially, the long coat of mail of\n   the European Middle Ages, as contrasted with the habergeon, which is\n   shorter and sometimes sleeveless. By old writers it is often used\n   synonymously with habergeon. See Habergeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hauerite', 'n.', 'Native sulphide of manganese a reddish brown or brownish\n   black mineral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haugh', 'n.', 'A low-lying meadow by the side of a river.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haught', 'a.', 'High; elevated; hence, haughty; proud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haughtily', 'adv.', 'In a haughty manner; arrogantly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haughtiness', 'n.', 'The quality of being haughty; disdain; arrogance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haughty', 'superl.', 'High; lofty; bold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haughty', 'superl.', 'Disdainfully or contemptuously proud; arrogant;\n   overbearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haughty', 'superl.', 'Indicating haughtiness; as, a haughty carriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hauled', 'imp. & p. p.', 'of Haul');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hauling', 'p. pr. & vb. n.', 'of Haul');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haul', 'v. t.', 'To pull or draw with force; to drag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haul', 'v. t.', 'To transport by drawing, as with horses or oxen; as, to\n   haul logs to a sawmill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haul', 'v. i.', 'To change the direction of a ship by hauling the wind. See\n   under Haul, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haul', 'v. t.', 'To pull apart, as oxen sometimes do when yoked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haul', 'n.', 'A pulling with force; a violent pull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haul', 'n.', 'A single draught of a net; as, to catch a hundred fish at a\n   haul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haul', 'n.', 'That which is caught, taken, or gained at once, as by hauling\n   a net.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haul', 'n.', 'Transportation by hauling; the distance through which\n   anything is hauled, as freight in a railroad car; as, a long haul or\n   short haul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haul', 'n.', 'A bundle of about four hundred threads, to be tarred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haulage', 'n.', 'Act of hauling; as, the haulage of cars by an engine;\n   charge for hauling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hauler', 'n.', 'One who hauls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haulm', 'n.', 'The denuded stems or stalks of such crops as buckwheat and\n   the cereal grains, beans, etc.; straw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haulm', 'n.', 'A part of a harness; a hame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hauls', 'n.', 'See Hals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haulse', 'v.', 'See Halse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hault', 'a.', 'Lofty; haughty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haum', 'n.', 'See Haulm, stalk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haunce', 'v. t.', 'To enhance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haunch', 'n.', 'The hip; the projecting region of the lateral parts of the\n   pelvis and the hip joint; the hind part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haunch', 'n.', 'Of meats: The leg and loin taken together; as, a haunch of\n   venison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haunched', 'a.', 'Having haunches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haunted', 'imp. & p. p.', 'of Haunt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haunting', 'p. pr. & vb. n.', 'of Haunt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haunt', 'v. t.', 'To frequent; to resort to frequently; to visit\n   pertinaciously or intrusively; to intrude upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haunt', 'v. t.', 'To inhabit or frequent as a specter; to visit as a ghost\n   or apparition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haunt', 'v. t.', 'To practice; to devote ones self to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haunt', 'v. t.', 'To accustom; to habituate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haunt', 'v. i.', 'To persist in staying or visiting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haunt', 'n.', 'A place to which one frequently resorts; as, drinking\n   saloons are the haunts of tipplers; a den is the haunt of wild beasts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haunt', 'n.', 'The habit of resorting to a place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haunt', 'n.', 'Practice; skill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haunted', 'a.', 'Inhabited by, or subject to the visits of, apparitions;\n   frequented by a ghost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haunter', 'n.', 'One who, or that which, haunts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haurient', 'a.', 'In pale, with the head in chief; -- said of the figure of\n   a fish, as if rising for air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hausen', 'n.', 'A large sturgeon (Acipenser huso) from the region of the\n   Black Sea. It is sometimes twelve feet long.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hausse', 'n.', 'A kind of graduated breech sight for a small arm, or a\n   cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haustellata', 'n. pl.', 'An artificial division of insects, including all\n   those with a sucking proboscis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haustellate', 'a.', 'Provided with a haustellum, or sucking proboscis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haustellate', 'n.', 'One of the Haustellata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haustella', 'pl. ', 'of Haustellum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haustellum', 'n.', 'The sucking proboscis of various insects. See\n   Lepidoptera, and Diptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haustoria', 'pl. ', 'of Haustorium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haustorium', 'n.', 'One of the suckerlike rootlets of such plants as the\n   dodder and ivy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haut', 'a.', 'Haughty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hautboy', 'n.', 'A wind instrument, sounded through a reed, and similar in\n   shape to the clarinet, but with a thinner tone. Now more commonly\n   called oboe. See Illust. of Oboe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hautboy', 'n.', 'A sort of strawberry (Fragaria elatior).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hautboyist', 'n.', 'A player on the hautboy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hautein', 'a.', 'Haughty; proud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hautein', 'a.', 'High; -- said of the voice or flight of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hauteur', 'n.', 'Haughty manner or spirit; haughtiness; pride; arrogance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hautgout', 'n.', 'High relish or flavor; high seasoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hautpas', 'n.', 'A raised part of the floor of a large room; a platform for\n   a raised table or throne. See Dais.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hauynite', 'n.', 'A blue isometric mineral, characteristic of some volcani/\n   rocks. It is a silicate of alumina, lime, and soda, with sulphate of\n   lime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Havana', 'a.', 'Of or pertaining to Havana, the capital of the island of\n   Cuba; as, an Havana cigar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Havana', 'n.', 'An Havana cigar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Havanese', 'a.', 'Of or pertaining to Havana, in Cuba.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Havanese', 'n. sing. & pl.', 'A native or inhabitant, or the people, of\n   Havana.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Had', 'imp. & p. p.', 'of Have');
  INSERT INTO words (word, word_type, description) VALUES  (    'Having', 'p. pr. & vb. n.', 'of Have');
  INSERT INTO words (word, word_type, description) VALUES  (    'have', 'Indic. present', 'of Have');
  INSERT INTO words (word, word_type, description) VALUES  (    'hast', '', 'of Have');
  INSERT INTO words (word, word_type, description) VALUES  (    'has', '', 'of Have');
  INSERT INTO words (word, word_type, description) VALUES  (    'have', '', 'of Have');
  INSERT INTO words (word, word_type, description) VALUES  (    'Have', 'v. t.', 'To hold in possession or control; to own; as, he has a\n   farm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Have', 'v. t.', 'To possess, as something which appertains to, is connected\n   with, or affects, one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Have', 'v. t.', 'To accept possession of; to take or accept.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Have', 'v. t.', 'To get possession of; to obtain; to get.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Have', 'v. t.', 'To cause or procure to be; to effect; to exact; to desire;\n   to require.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Have', 'v. t.', 'To bear, as young; as, she has just had a child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Have', 'v. t.', 'To hold, regard, or esteem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Have', 'v. t.', 'To cause or force to go; to take.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Have', 'v. t.', 'To take or hold (ones self); to proceed promptly; -- used\n   reflexively, often with ellipsis of the pronoun; as, to have after one;\n   to have at one or at a thing, i. e., to aim at one or at a thing; to\n   attack; to have with a companion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Have', 'v. t.', 'To be under necessity or obligation; to be compelled;\n   followed by an infinitive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Have', 'v. t.', 'To understand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Have', 'v. t.', 'To put in an awkward position; to have the advantage of;\n   as, that is where he had him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haveless', 'a.', 'Having little or nothing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Havelock', 'n.', 'A light cloth covering for the head and neck, used by\n   soldiers as a protection from sunstroke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haven', 'n.', 'A bay, recess, or inlet of the sea, or the mouth of a river,\n   which affords anchorage and shelter for shipping; a harbor; a port.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haven', 'n.', 'A place of safety; a shelter; an asylum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haven', 'v. t.', 'To shelter, as in a haven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Havenage', 'n.', 'Harbor dues; port dues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Havened', 'p. a.', 'Sheltered in a haven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Havener', 'n.', 'A harbor master.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haver', 'n.', 'A possessor; a holder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haver', 'n.', 'The oat; oats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haver', 'v. i.', 'To maunder; to talk foolishly; to chatter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haversack', 'n.', 'A bag for oats or oatmeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haversack', 'n.', 'A bag or case, usually of stout cloth, in which a\n   soldier carries his rations when on a march; -- distinguished from\n   knapsack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haversack', 'n.', 'A gunners case or bag used carry cartridges from the\n   ammunition chest to the piece in loading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haversian', 'a.', 'Pertaining to, or discovered by, Clopton Havers, an\n   English physician of the seventeenth century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Havildar', 'n.', 'In the British Indian armies, a noncommissioned officer\n   of native soldiers, corresponding to a sergeant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Having', 'n.', 'Possession; goods; estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Havior', 'n.', 'Behavior; demeanor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Havoc', 'n.', 'Wide and general destruction; devastation; waste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Havoc', 'v. t.', 'To devastate; to destroy; to lay waste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Havoc', 'n.', 'A cry in war as the signal for indiscriminate slaughter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haw', 'n.', 'A hedge; an inclosed garden or yard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haw', 'n.', 'The fruit of the hawthorn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haw', 'n.', 'The third eyelid, or nictitating membrane. See Nictitating\n   membrane, under Nictitate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haw', 'n.', 'An intermission or hesitation of speech, with a sound somewhat\n   like haw! also, the sound so made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haw', 'v. i.', 'To stop, in speaking, with a sound like haw; to speak with\n   interruption and hesitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawed', 'imp. & p. p.', 'of Haw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawing', 'p. pr. & vb. n.', 'of Haw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haw', 'v. i.', 'To turn to the near side, or toward the driver; -- said of\n   cattle or a team: a word used by teamsters in guiding their teams, and\n   most frequently in the imperative. See Gee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haw', 'v. t.', 'To cause to turn, as a team, to the near side, or toward\n   the driver; as, to haw a team of oxen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawaiian', 'a.', 'Belonging to Hawaii or the Sandwich Islands, or to the\n   people of Hawaii.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawaiian', 'n.', 'A native of Hawaii.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawebake', 'n.', 'Probably, the baked berry of the hawthorn tree, that is,\n   coarse fare. See 1st Haw, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawfinch', 'n.', 'The common European grosbeak (Coccothraustes vulgaris);\n   -- called also cherry finch, and coble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haw-haw', 'n.', 'See Ha-ha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawhaw', 'v. i.', 'To laugh boisterously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawk', 'n.', 'One of numerous species and genera of rapacious birds of the\n   family Falconidae. They differ from the true falcons in lacking the\n   prominent tooth and notch of the bill, and in having shorter and less\n   pointed wings. Many are of large size and grade into the eagles. Some,\n   as the goshawk, were formerly trained like falcons. In a more general\n   sense the word is not infrequently applied, also, to true falcons, as\n   the sparrow hawk, pigeon hawk, duck hawk, and prairie hawk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawked', 'imp. & p. p.', 'of Hawk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawking', 'p. pr. & vb. n.', 'of Hawk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawk', 'v. i.', 'To catch, or attempt to catch, birds by means of hawks\n   trained for the purpose, and let loose on the prey; to practice\n   falconry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawk', 'v. i.', 'To make an attack while on the wing; to soar and strike\n   like a hawk; -- generally with at; as, to hawk at flies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawk', 'v. i.', 'To clear the throat with an audible sound by forcing an\n   expiratory current of air through the narrow passage between the\n   depressed soft palate and the root of the tongue, thus aiding in the\n   removal of foreign substances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawk', 'v. t.', 'To raise by hawking, as phlegm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawk', 'n.', 'An effort to force up phlegm from the throat, accompanied\n   with noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawk', 'v. t.', 'To offer for sale by outcry in the street; to carry\n   (merchandise) about from place to place for sale; to peddle; as, to\n   hawk goods or pamphlets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawk', 'n.', 'A small board, with a handle on the under side, to hold\n   mortar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawkbill', 'n.', 'A sea turtle (Eretmochelys imbricata), which yields the\n   best quality of tortoise shell; -- called also caret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawkbit', 'n.', 'The fall dandelion (Leontodon autumnale).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawked', 'a.', 'Curved like a hawks bill; crooked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawker', 'n.', 'One who sells wares by crying them in the street; hence, a\n   peddler or a packman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawker', 'v. i.', 'To sell goods by outcry in the street.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawker', 'n.', 'A falconer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawkey', 'n.', 'See Hockey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawk-eyed', 'a.', 'Having a keen eye; sharpsighted; discerning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawk moth', '', 'Any moth of the family Sphingidae, of which there are\n   numerous genera and species. They are large, handsome moths, which fly\n   mostly at twilight and hover about flowers like a humming bird, sucking\n   the honey by means of a long, slender proboscis. The larvae are large,\n   hairless caterpillars ornamented with green and other bright colors,\n   and often with a caudal spine. See Sphinx, also Tobacco worm, and\n   Tomato worm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawkweed', 'n.', 'A plant of the genus Hieracium; -- so called from the\n   ancient belief that birds of prey used its juice to strengthen their\n   vision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawkweed', 'n.', 'A plant of the genus Senecio (S. hieracifolius).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawm', 'n.', 'See Haulm, straw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawm', 'v. i.', 'To lounge; to loiter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawse', 'n.', 'A hawse hole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawse', 'n.', 'The situation of the cables when a vessel is moored with two\n   anchors, one on the starboard, the other on the port bow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawse', 'n.', 'The distance ahead to which the cables usually extend; as,\n   the ship has a clear or open hawse, or a foul hawse; to anchor in our\n   hawse, or athwart hawse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawse', 'n.', 'That part of a vessels bow in which are the hawse holes for\n   the cables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawser', 'n.', 'A large rope made of three strands each containing many\n   yarns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawser-laid', 'a.', 'Made in the manner of a hawser. Cf. Cable-laid, and\n   see Illust. of Cordage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hawthorn', 'n.', 'A thorny shrub or tree (the Crataegus oxyacantha), having\n   deeply lobed, shining leaves, small, roselike, fragrant flowers, and a\n   fruit called haw. It is much used in Europe for hedges, and for\n   standards in gardens. The American hawthorn is Crataegus cordata, which\n   has the leaves but little lobed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hay', 'n.', 'A hedge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hay', 'n.', 'A net set around the haunt of an animal, especially of a\n   rabbit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hay', 'v. i.', 'To lay snares for rabbits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hay', 'n.', 'Grass cut and cured for fodder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hay', 'v. i.', 'To cut and cure grass for hay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haybird', 'n.', 'The European spotted flycatcher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haybird', 'n.', 'The European blackcap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haybote', 'n.', 'An allowance of wood to a tenant for repairing his hedges\n   or fences; hedgebote. See Bote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haycock', 'n.', 'A conical pile or hear of hay in the field.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hay-cutter', 'n.', 'A machine in which hay is chopped short, as fodder for\n   cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hayfield', 'n.', 'A field where grass for hay has been cut; a meadow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hayfork', 'n.', 'A fork for pitching and tedding hay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hayloft', 'n.', 'A loft or scaffold for hay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haymaker', 'n.', 'One who cuts and cures hay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haymaker', 'n.', 'A machine for curing hay in rainy weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haymaking', 'n.', 'The operation or work of cutting grass and curing it for\n   hay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haymow', 'n.', 'A mow or mass of hay laid up in a barn for preservation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haymow', 'n.', 'The place in a barn where hay is deposited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hayrack', 'n.', 'A frame mounted on the running gear of a wagon, and used\n   in hauling hay, straw, sheaves, etc.; -- called also hay rigging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hayrake', 'n.', 'A rake for collecting hay; especially, a large rake drawn\n   by a horse or horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hayrick', 'n.', 'A heap or pile of hay, usually covered with thatch for\n   preservation in the open air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haystack', 'n.', 'A stack or conical pile of hay in the open air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haystalk', 'n.', 'A stalk of hay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haythorn', 'n.', 'Hawthorn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haytian', 'a.', 'Of pertaining to Hayti.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haytian', 'n.', 'A native of Hayti.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hayward', 'n.', 'An officer who is appointed to guard hedges, and to keep\n   cattle from breaking or cropping them, and whose further duty it is to\n   impound animals found running at large.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hazard', 'n.', 'A game of chance played with dice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hazard', 'n.', 'The uncertain result of throwing a die; hence, a fortuitous\n   event; chance; accident; casualty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hazard', 'n.', 'Risk; danger; peril; as, he encountered the enemy at the\n   hazard of his reputation and life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hazard', 'n.', 'Holing a ball, whether the object ball (winning hazard) or\n   the players ball (losing hazard).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hazard', 'n.', 'Anything that is hazarded or risked, as the stakes in\n   gaming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hazarded', 'imp. & p. p.', 'of Hazard');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hazarding', 'p. pr. & vb. /', 'of Hazard');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hazard', 'n.', 'To expose to the operation of chance; to put in danger of\n   loss or injury; to venture; to risk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hazard', 'n.', 'To venture to incur, or bring on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hazard', 'v. i.', 'To try the chance; to encounter risk or danger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hazardable', 'a.', 'Liable to hazard or chance; uncertain; risky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hazardable', 'a.', 'Such as can be hazarded or risked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hazarder', 'n.', 'A player at the game of hazard; a gamester.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hazarder', 'n.', 'One who hazards or ventures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hazardize', 'n.', 'A hazardous attempt or situation; hazard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hazardous', 'a.', 'Exposed to hazard; dangerous; risky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hazardry', 'n.', 'Playing at hazard; gaming; gambling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hazardry', 'n.', 'Rashness; temerity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haze', 'n.', 'Light vapor or smoke in the air which more or less impedes\n   vision, with little or no dampness; a lack of transparency in the air;\n   hence, figuratively, obscurity; dimness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haze', 'v. i.', 'To be hazy, or tick with haze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hazed', 'imp. & p. p.', 'of Haze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hazing', 'p. pr. & vb. n.', 'of Haze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haze', 'v. t.', 'To harass by exacting unnecessary, disagreeable, or\n   difficult work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haze', 'v. t.', 'To harass or annoy by playing abusive or shameful tricks\n   upon; to humiliate by practical jokes; -- used esp. of college\n   students; as, the sophomores hazed a freshman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hazel', 'n.', 'A shrub or small tree of the genus Corylus, as the C.\n   avellana, bearing a nut containing a kernel of a mild, farinaceous\n   taste; the filbert. The American species are C. Americana, which\n   produces the common hazelnut, and C. rostrata. See Filbert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hazel', 'n.', 'A miners name for freestone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hazel', 'a.', 'Consisting of hazels, or of the wood of the hazel;\n   pertaining to, or derived from, the hazel; as, a hazel wand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hazel', 'a.', 'Of a light brown color, like the hazelnut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hazeless', 'a.', 'Destitute of haze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hazelly', 'a.', 'Of the color of the hazelnut; of a light brown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hazelnut', 'n.', 'The nut of the hazel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hazelwort', 'n.', 'The asarabacca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hazily', 'adv.', 'In a hazy manner; mistily; obscurely; confusedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Haziness', 'n.', 'The quality or state of being hazy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hazle', 'v. t.', 'To make dry; to dry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hazy', 'n.', 'Thick with haze; somewhat obscured with haze; not clear or\n   transparent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hazy', 'n.', 'Obscure; confused; not clear; as, a hazy argument; a hazy\n   intellect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'He', 'obj.', 'The man or male being (or object personified to which the\n   masculine gender is assigned), previously designated; a pronoun of the\n   masculine gender, usually referring to a specified subject already\n   indicated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'He', 'obj.', 'Any one; the man or person; -- used indefinitely, and usually\n   followed by a relative pronoun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'He', 'obj.', 'Man; a male; any male person; -- in this sense used\n   substantively.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-head', 'suffix.', 'A variant of -hood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Head', 'n.', 'The anterior or superior part of an animal, containing the\n   brain, or chief ganglia of the nervous system, the mouth, and in the\n   higher animals, the chief sensory organs; poll; cephalon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Head', 'n.', 'The uppermost, foremost, or most important part of an\n   inanimate object; such a part as may be considered to resemble the head\n   of an animal; often, also, the larger, thicker, or heavier part or\n   extremity, in distinction from the smaller or thinner part, or from the\n   point or edge; as, the head of a cane, a nail, a spear, an ax, a mast,\n   a sail, a ship; that which covers and closes the top or the end of a\n   hollow vessel; as, the head of a cask or a steam boiler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Head', 'n.', 'The place where the head should go; as, the head of a bed, of\n   a grave, etc.; the head of a carriage, that is, the hood which covers\n   the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Head', 'n.', 'The most prominent or important member of any organized body;\n   the chief; the leader; as, the head of a college, a school, a church, a\n   state, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Head', 'n.', 'The place or honor, or of command; the most important or\n   foremost position; the front; as, the head of the table; the head of a\n   column of soldiers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Head', 'n.', 'Each one among many; an individual; -- often used in a plural\n   sense; as, a thousand head of cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Head', 'n.', 'The seat of the intellect; the brain; the understanding; the\n   mental faculties; as, a good head, that is, a good mind; it never\n   entered his head, it did not occur to him; of his own head, of his own\n   thought or will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Head', 'n.', 'The source, fountain, spring, or beginning, as of a stream or\n   river; as, the head of the Nile; hence, the altitude of the source, or\n   the height of the surface, as of water, above a given place, as above\n   an orifice at which it issues, and the pressure resulting from the\n   height or from motion; sometimes also, the quantity in reserve; as, a\n   mill or reservoir has a good head of water, or ten feet head; also,\n   that part of a gulf or bay most remote from the outlet or the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Head', 'n.', 'A headland; a promontory; as, Gay Head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Head', 'n.', 'A separate part, or topic, of a discourse; a theme to be\n   expanded; a subdivision; as, the heads of a sermon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Head', 'n.', 'Culminating point or crisis; hence, strength; force; height.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Head', 'n.', 'Power; armed force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Head', 'n.', 'A headdress; a covering of the head; as, a laced head; a head\n   of hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Head', 'n.', 'An ear of wheat, barley, or of one of the other small\n   cereals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Head', 'n.', 'A dense cluster of flowers, as in clover, daisies, thistles;\n   a capitulum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Head', 'n.', 'A dense, compact mass of leaves, as in a cabbage or a lettuce\n   plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Head', 'n.', 'The antlers of a deer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Head', 'n.', 'A rounded mass of foam which rises on a pot of beer or other\n   effervescing liquor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Head', 'n.', 'Tiles laid at the eaves of a house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Head', 'a.', 'Principal; chief; leading; first; as, the head master of a\n   school; the head man of a tribe; a head chorister; a head cook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headed', 'imp. & p. p.', 'of Head');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heading', 'p. pr. & vb. n.', 'of Head');
  INSERT INTO words (word, word_type, description) VALUES  (    'Head', 'v. t.', 'To be at the head of; to put ones self at the head of; to\n   lead; to direct; to act as leader to; as, to head an army, an\n   expedition, or a riot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Head', 'v. t.', 'To form a head to; to fit or furnish with a head; as, to\n   head a nail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Head', 'v. t.', 'To behead; to decapitate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Head', 'v. t.', 'To cut off the top of; to lop off; as, to head trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Head', 'v. t.', 'To go in front of; to get in the front of, so as to hinder\n   or stop; to oppose; hence, to check or restrain; as, to head a drove of\n   cattle; to head a person; the wind heads a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Head', 'v. t.', 'To set on the head; as, to head a cask.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Head', 'v. i.', 'To originate; to spring; to have its source, as a river.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Head', 'v. i.', 'To go or point in a certain direction; to tend; as, how\n   does the ship head?');
  INSERT INTO words (word, word_type, description) VALUES  (    'Head', 'v. i.', 'To form a head; as, this kind of cabbage heads early.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headache', 'n.', 'Pain in the head; cephalalgia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headachy', 'a.', 'Afflicted with headache.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headband', 'n.', 'A fillet; a band for the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headband', 'n.', 'The band at each end of the back of a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headbeard', 'n.', 'A board or boarding which marks or forms the head of\n   anything; as, the headboard of a bed; the headboard of a grave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headborough', 'n.', 'Alt. of Headborrow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headborrow', 'n.', 'The chief of a frankpledge, tithing, or decennary,\n   consisting of ten families; -- called also borsholder, boroughhead,\n   boroughholder, and sometimes tithingman. See Borsholder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headborrow', 'n.', 'A petty constable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Head-cheese', 'n.', 'A dish made of portions of the head, or head and feet,\n   of swine, cut up fine, seasoned, and pressed into a cheeselike mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headdress', 'n.', 'A covering or ornament for the head; a headtire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headdress', 'n.', 'A manner of dressing the hair or of adorning it, whether\n   with or without a veil, ribbons, combs, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headed', 'a.', 'Furnished with a head (commonly as denoting intellectual\n   faculties); -- used in composition; as, clear-headed, long-headed,\n   thick-headed; a many-headed monster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headed', 'a.', 'Formed into a head; as, a headed cabbage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Header', 'n.', 'One who, or that which, heads nails, rivets, etc., esp. a\n   machine for heading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Header', 'n.', 'One who heads a movement, a party, or a mob; head; chief;\n   leader.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Header', 'n.', 'A brick or stone laid with its shorter face or head in the\n   surface of the wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Header', 'n.', 'In framing, the piece of timber fitted between two\n   trimmers, and supported by them, and carrying the ends of the\n   tailpieces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Header', 'n.', 'A reaper for wheat, that cuts off the heads only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Header', 'n.', 'A fall or plunge headforemost, as while riding a bicycle,\n   or in bathing; as, to take a header.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headfirst', 'adv.', 'Alt. of Headforemost');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headforemost', 'adv.', 'With the head foremost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headfish', 'n.', 'The sunfish (Mola).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Head gear', 'n.', 'Alt. of Headgear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headgear', 'n.', 'Headdress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headgear', 'n.', 'Apparatus above ground at the mouth of a mine or deep\n   well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Head-hunter', 'n.', 'A member of any tribe or race of savages who have the\n   custom of decapitating human beings and preserving their heads as\n   trophies. The Dyaks of Borneo are the most noted head-hunters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headily', 'adv.', 'In a heady or rash manner; hastily; rashly; obstinately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headiness', 'n.', 'The quality of being heady.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heading', 'n.', 'The act or state of one who, or that which, heads;\n   formation of a head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heading', 'n.', 'That which stands at the head; title; as, the heading of a\n   paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heading', 'n.', 'Material for the heads of casks, barrels, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heading', 'n.', 'A gallery, drift, or adit in a mine; also, the end of a\n   drift or gallery; the vein above a drift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heading', 'n.', 'The extension of a line ruffling above the line of stitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heading', 'n.', 'That end of a stone or brick which is presented outward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headland', 'n.', 'A cape; a promontory; a point of land projecting into the\n   sea or other expanse of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headland', 'n.', 'A ridge or strip of unplowed at the ends of furrows, or\n   near a fence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headless', 'a.', 'Having no head; beheaded; as, a headless body, neck, or\n   carcass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headless', 'a.', 'Destitute of a chief or leader.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headless', 'a.', 'Destitute of understanding or prudence; foolish; rash;\n   obstinate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headlight', 'n.', 'A light, with a powerful reflector, placed at the head\n   of a locomotive, or in front of it, to throw light on the track at\n   night, or in going through a dark tunnel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headline', 'n.', 'The line at the head or top of a page.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headline', 'n.', 'See Headrope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headlong', 'a. & adv.', 'With the head foremost; as, to fall headlong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headlong', 'a. & adv.', 'Rashly; precipitately; without deliberation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headlong', 'a. & adv.', 'Hastily; without delay or respite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headlong', 'a.', 'Rash; precipitate; as, headlong folly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headlong', 'a.', 'Steep; precipitous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Head-lugged', 'a.', 'Lugged or dragged by the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headmen', 'pl. ', 'of Headman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headman', 'n.', 'A head or leading man, especially of a village community.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headmold shot', '', 'Alt. of Headmould shot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headmould shot', '', 'An old name for the condition of the skull, in which\n   the bones ride, or are shot, over each other at the sutures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headmost', 'a.', 'Most advanced; most forward; as, the headmost ship in a\n   fleet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headnote', 'n.', 'A note at the head of a page or chapter; in law reports,\n   an abstract of a case, showing the principles involved and the opinion\n   of the court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headpan', 'n.', 'The brainpan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headpiece', 'n.', 'Head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headpiece', 'n.', 'A cap of defense; especially, an open one, as\n   distinguished from the closed helmet of the Middle Ages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headpiece', 'n.', 'Understanding; mental faculty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headpiece', 'n.', 'An engraved ornament at the head of a chapter, or of a\n   page.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headquarters', 'n. sing.', 'The quarters or place of residence of any chief\n   officer, as the general in command of an army, or the head of a police\n   force; the place from which orders or instructions are issued; hence,\n   the center of authority or order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headrace', 'n.', 'See Race, a water course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headroom', 'n.', 'See Headway, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headrope', 'n.', 'That part of a boltrope which is sewed to the upper edge\n   or head of a sail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headsail', 'n.', 'Any sail set forward of the foremast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headshake', 'n.', 'A significant shake of the head, commonly as a signal of\n   denial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headship', 'n.', 'Authority or dignity; chief place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headsmen', 'pl. ', 'of Headsman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headsman', 'n.', 'An executioner who cuts off heads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headspring', 'n.', 'Fountain; source.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headstall', 'n.', 'That part of a bridle or halter which encompasses the\n   head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headstock', 'n.', 'A part (usually separate from the bed or frame) for\n   supporting some of the principal working parts of a machine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headstock', 'n.', 'The part of a lathe that holds the revolving spindle and\n   its attachments; -- also called poppet head, the opposite corresponding\n   part being called a tailstock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headstock', 'n.', 'The part of a planing machine that supports the cutter,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headstone', 'n.', 'The principal stone in a foundation; the chief or corner\n   stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headstone', 'n.', 'The stone at the head of a grave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headstrong', 'a.', 'Not easily restrained; ungovernable; obstinate;\n   stubborn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headstrong', 'a.', 'Directed by ungovernable will, or proceeding from\n   obstinacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headstrongness', 'n.', 'Obstinacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headtire', 'n.', 'A headdress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headtire', 'n.', 'The manner of dressing the head, as at a particular time\n   and place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headway', 'n.', 'The progress made by a ship in motion; hence, progress or\n   success of any kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headway', 'n.', 'Clear space under an arch, girder, and the like,\n   sufficient to allow of easy passing underneath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Headwork', 'n.', 'Mental labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heady', 'a.', 'Willful; rash; precipitate; hurried on by will or passion;\n   ungovernable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heady', 'a.', 'Apt to affect the head; intoxicating; strong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heady', 'a.', 'Violent; impetuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heal', 'v. t.', 'To cover, as a roof, with tiles, slate, lead, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Healed', 'imp. & p. p.', 'of Heal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Healing', 'p. pr. & vb. n.', 'of Heal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heal', 'v. t.', 'To make hale, sound, or whole; to cure of a disease,\n   wound, or other derangement; to restore to soundness or health.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heal', 'v. t.', 'To remove or subdue; to cause to pass away; to cure; --\n   said of a disease or a wound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heal', 'v. t.', 'To restore to original purity or integrity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heal', 'v. t.', 'To reconcile, as a breach or difference; to make whole; to\n   free from guilt; as, to heal dissensions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heal', 'v. i.', 'To grow sound; to return to a sound state; as, the limb\n   heals, or the wound heals; -- sometimes with up or over; as, it will\n   heal up, or over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heal', 'v. t.', 'Health.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Healable', 'a.', 'Capable of being healed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Healall', 'n.', 'A common herb of the Mint family (Brunela vulgaris),\n   destitute of active properties, but anciently thought a panacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heald', 'n.', 'A heddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Healful', 'a.', 'Tending or serving to heal; healing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Healing', 'a.', 'Tending to cure; soothing; mollifying; as, the healing\n   art; a healing salve; healing words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Healingly', 'adv.', 'So as to heal or cure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Health', 'n.', 'The state of being hale, sound, or whole, in body, mind, or\n   soul; especially, the state of being free from physical disease or\n   pain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Health', 'n.', 'A wish of health and happiness, as in pledging a person in\n   a toast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Healthful', 'a.', 'Full of health; free from illness or disease; well;\n   whole; sound; healthy; as, a healthful body or mind; a healthful plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Healthful', 'a.', 'Serving to promote health of body or mind; wholesome;\n   salubrious; salutary; as, a healthful air, diet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Healthful', 'a.', 'Indicating, characterized by, or resulting from, health\n   or soundness; as, a healthful condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Healthful', 'a.', 'Well-disposed; favorable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Healthfully', 'adv.', 'In health; wholesomely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Healthfulness', 'n.', 'The state of being healthful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Healthily', 'adv.', 'In a healthy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Healthiness', 'n.', 'The state of being healthy or healthful; freedom from\n   disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Healthless', 'n.', 'Without health, whether of body or mind; in firm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Healthless', 'n.', 'Not conducive to health; unwholesome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Healthlessness', 'n.', 'The state of being health/ess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Healthsome', 'a.', 'Wholesome; salubrious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Healthward', 'a. & adv.', 'In the direction of health; as, a healthward\n   tendency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Healthy', 'superl.', 'Being in a state of health; enjoying health; hale;\n   sound; free from disease; as, a healthy chid; a healthy plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Healthy', 'superl.', 'Evincing health; as, a healthy pulse; a healthy\n   complexion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Healthy', 'superl.', 'Conducive to health; wholesome; salubrious; salutary;\n   as, a healthy exercise; a healthy climate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heam', 'n.', 'The afterbirth or secundines of a beast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heap', 'n.', 'A crowd; a throng; a multitude or great number of persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heap', 'n.', 'A great number or large quantity of things not placed in a\n   pile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heap', 'n.', 'A pile or mass; a collection of things laid in a body, or\n   thrown together so as to form an elevation; as, a heap of earth or\n   stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heaped', 'imp. & p. p.', 'of Heap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heaping', 'p. pr. & vb. n.', 'of Heap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heap', 'v. t.', 'To collect in great quantity; to amass; to lay up; to\n   accumulate; -- usually with up; as, to heap up treasures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heap', 'v. t.', 'To throw or lay in a heap; to make a heap of; to pile; as,\n   to heap stones; -- often with up; as, to heap up earth; or with on; as,\n   to heap on wood or coal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heap', 'v. t.', 'To form or round into a heap, as in measuring; to fill (a\n   measure) more than even full.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heaper', 'n.', 'One who heaps, piles, or amasses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heapy', 'a.', 'Lying in heaps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heard', 'imp. & p. p.', 'of Hear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearing', 'p. pr. & vb. n.', 'of Hear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hear', 'v. t.', 'To perceive by the ear; to apprehend or take cognizance of\n   by the ear; as, to hear sounds; to hear a voice; to hear one call.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hear', 'v. t.', 'To give audience or attention to; to listen to; to heed;\n   to accept the doctrines or advice of; to obey; to examine; to try in a\n   judicial court; as, to hear a recitation; to hear a class; the case\n   will be heard to-morrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hear', 'v. t.', 'To attend, or be present at, as hearer or worshiper; as,\n   to hear a concert; to hear Mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hear', 'v. t.', 'To give attention to as a teacher or judge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hear', 'v. t.', 'To accede to the demand or wishes of; to listen to and\n   answer favorably; to favor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hear', 'v. i.', 'To have the sense or faculty of perceiving sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hear', 'v. i.', 'To use the power of perceiving sound; to perceive or\n   apprehend by the ear; to attend; to listen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hear', 'v. i.', 'To be informed by oral communication; to be told; to\n   receive information by report or by letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heard', '', 'imp. & p. p. of Hear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearer', 'n.', 'One who hears; an auditor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearing', 'n.', 'The act or power of perceiving sound; perception of sound;\n   the faculty or sense by which sound is perceived; as, my hearing is\n   good.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearing', 'n.', 'Attention to what is delivered; opportunity to be heard;\n   audience; as, I could not obtain a hearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearing', 'n.', 'A listening to facts and evidence, for the sake of\n   adjudication; a session of a court for considering proofs and\n   determining issues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearing', 'n.', 'Extent within which sound may be heard; sound; earshot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearkened', 'imp. & p. p.', 'of Hearken');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearkening', 'p. pr. & vb. n.', 'of Hearken');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearken', 'v. i.', 'To listen; to lend the ear; to attend to what is\n   uttered; to give heed; to hear, in order to obey or comply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearken', 'v. i.', 'To inquire; to seek information.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearken', 'v. t.', 'To hear by listening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearken', 'v. t.', 'To give heed to; to hear attentively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearkener', 'n.', 'One who hearkens; a listener.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearsal', 'n.', 'Rehearsal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearsay', 'n.', 'Report; rumor; fame; common talk; something heard from\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearse', 'n.', 'A hind in the year of its age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearse', 'n.', 'A framework of wood or metal placed over the coffin or tomb\n   of a deceased person, and covered with a pall; also, a temporary canopy\n   bearing wax lights and set up in a church, under which the coffin was\n   placed during the funeral ceremonies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearse', 'n.', 'A grave, coffin, tomb, or sepulchral monument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearse', 'n.', 'A bier or handbarrow for conveying the dead to the grave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearse', 'n.', 'A carriage specially adapted or used for conveying the dead\n   to the grave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearse', 'v. t.', 'To inclose in a hearse; to entomb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearsecloth', 'n.', 'A cloth for covering a coffin when on a bier; a pall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearselike', 'a.', 'Suitable to a funeral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heart', 'n.', 'A hollow, muscular organ, which, by contracting\n   rhythmically, keeps up the circulation of the blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heart', 'n.', 'The seat of the affections or sensibilities, collectively or\n   separately, as love, hate, joy, grief, courage, and the like; rarely,\n   the seat of the understanding or will; -- usually in a good sense, when\n   no epithet is expressed; the better or lovelier part of our nature; the\n   spring of all our actions and purposes; the seat of moral life and\n   character; the moral affections and character itself; the individual\n   disposition and character; as, a good, tender, loving, bad, hard, or\n   selfish heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heart', 'n.', 'The nearest the middle or center; the part most hidden and\n   within; the inmost or most essential part of any body or system; the\n   source of life and motion in any organization; the chief or vital\n   portion; the center of activity, or of energetic or efficient action;\n   as, the heart of a country, of a tree, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heart', 'n.', 'Courage; courageous purpose; spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heart', 'n.', 'Vigorous and efficient activity; power of fertile\n   production; condition of the soil, whether good or bad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heart', 'n.', 'That which resembles a heart in shape; especially, a\n   roundish or oval figure or object having an obtuse point at one end,\n   and at the other a corresponding indentation, -- used as a symbol or\n   representative of the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heart', 'n.', 'One of a series of playing cards, distinguished by the\n   figure or figures of a heart; as, hearts are trumps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heart', 'n.', 'Vital part; secret meaning; real intention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heart', 'n.', 'A term of affectionate or kindly and familiar address.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heart', 'v. t.', 'To give heart to; to hearten; to encourage; to inspirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heart', 'v. i.', 'To form a compact center or heart; as, a hearting\n   cabbage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartache', 'n.', 'Sorrow; anguish of mind; mental pang.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartbreak', 'n.', 'Crushing sorrow or grief; a yielding to such grief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartbreaking', 'a.', 'Causing overpowering sorrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartbroken', 'a.', 'Overcome by crushing sorrow; deeply grieved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartburn', 'n.', 'An uneasy, burning sensation in the stomach, often\n   attended with an inclination to vomit. It is sometimes idiopathic, but\n   is often a symptom of often complaints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartburned', 'a.', 'Having heartburn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartburning', 'a.', 'Causing discontent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartburning', 'n.', 'Same as Heartburn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartburning', 'n.', 'Discontent; secret enmity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartdear', 'a.', 'Sincerely beloved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartdeep', 'a.', 'Rooted in the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heart-eating', 'a.', 'Preying on the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearted', 'a.', 'Having a heart; having (such) a heart (regarded as the\n   seat of the affections, disposition, or character).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearted', 'a.', 'Shaped like a heart; cordate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearted', 'a.', 'Seated or laid up in the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartedness', 'n.', 'Earnestness; sincerity; heartiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearten', 'v. t.', 'To encourage; to animate; to incite or stimulate the\n   courage of; to embolden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearten', 'v. t.', 'To restore fertility or strength to, as to land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartener', 'n.', 'One who, or that which, heartens, animates, or stirs up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartfelt', 'a.', 'Hearty; sincere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartgrief', 'n.', 'Heartache; sorrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearth', 'n.', 'The pavement or floor of brick, stone, or metal in a\n   chimney, on which a fire is made; the floor of a fireplace; also, a\n   corresponding part of a stove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearth', 'n.', 'The house itself, as the abode of comfort to its inmates\n   and of hospitality to strangers; fireside.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearth', 'n.', 'The floor of a furnace, on which the material to be heated\n   lies, or the lowest part of a melting furnace, into which the melted\n   material settles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearthstone', 'n.', 'Stone forming the hearth; hence, the fireside; home.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartily', 'adv.', 'From the heart; with all the heart; with sincerity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartily', 'adv.', 'With zeal; actively; vigorously; willingly; cordially;\n   as, he heartily assisted the prince.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heariness', 'n.', 'The quality of being hearty; as, the heartiness of a\n   greeting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartless', 'a.', 'Without a heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartless', 'a.', 'Destitute of courage; spiritless; despodent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartless', 'a.', 'Destitute of feeling or affection; unsympathetic; cruel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartlet', 'n.', 'A little heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartlings', 'interj.', 'An exclamation used in addressing a familiar\n   acquaintance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartpea', 'n.', 'Same as Heartseed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartquake', 'n.', 'Trembling of the heart; trepidation; fear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartrending', 'a.', 'Causing intense grief; overpowering with anguish;\n   very distressing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heart-robbing', 'a.', 'Depriving of thought; ecstatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heart-robbing', 'a.', 'Stealing the heart or affections; winning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearts-ease', 'n.', 'Ease of heart; peace or tranquillity of mind or\n   feeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearts-ease', 'n.', 'A species of violet (Viola tricolor); -- called also\n   pansy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartseed', 'n.', 'A climbing plant of the genus Cardiospermum, having\n   round seeds which are marked with a spot like a heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartshaped', 'a.', 'Having the shape of a heart; cordate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartsick', 'a.', 'Sick at heart; extremely depressed in spirits; very\n   despondent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartsome', 'a.', 'Merry; cheerful; lively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heart-spoon', 'n.', 'A part of the breastbone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartstricken', 'a.', 'Shocked; dismayed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartstrike', 'v. t.', 'To affect at heart; to shock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartstring', 'n.', 'A nerve or tendon, supposed to brace and sustain the\n   heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartstruck', 'a.', 'Driven to the heart; infixed in the mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartstruck', 'a.', 'Shocked with pain, fear, or remorse; dismayed;\n   heartstricken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartswelling', 'a.', 'Rankling in, or swelling, the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heart-whole', 'a.', 'Having the heart or affections free; not in love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heart-whole', 'a.', 'With unbroken courage; undismayed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heart-whole', 'a.', 'Of a single and sincere heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartwood', 'n.', 'The hard, central part of the trunk of a tree,\n   consisting of the old and matured wood, and usually differing in color\n   from the outer layers. It is technically known as duramen, and\n   distinguished from the softer sapwood or alburnum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heart-wounded', 'a.', 'Wounded to the heart with love or grief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearty', 'superl.', 'Pertaining to, or proceeding from, the heart; warm;\n   cordial; bold; zealous; sincere; willing; also, energetic; active;\n   eager; as, a hearty welcome; hearty in supporting the government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearty', 'superl.', 'Exhibiting strength; sound; healthy; firm; not weak;\n   as, a hearty timber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearty', 'superl.', 'Promoting strength; nourishing; rich; abundant; as,\n   hearty food; a hearty meal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearties', 'pl. ', 'of Hearty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hearty', 'n.', 'Comrade; boon companion; good fellow; -- a term of familiar\n   address and fellowship among sailors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heartyhale', 'a.', 'Good for the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heat', 'n.', 'A force in nature which is recognized in various effects, but\n   especially in the phenomena of fusion and evaporation, and which, as\n   manifested in fire, the suns rays, mechanical action, chemical\n   combination, etc., becomes directly known to us through the sense of\n   feeling. In its nature heat is a mode if motion, being in general a\n   form of molecular disturbance or vibration. It was formerly supposed to\n   be a subtile, imponderable fluid, to which was given the name caloric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heat', 'n.', 'The sensation caused by the force or influence of heat when\n   excessive, or above that which is normal to the human body; the bodily\n   feeling experienced on exposure to fire, the suns rays, etc.; the\n   reverse of cold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heat', 'n.', 'High temperature, as distinguished from low temperature, or\n   cold; as, the heat of summer and the cold of winter; heat of the skin\n   or body in fever, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heat', 'n.', 'Indication of high temperature; appearance, condition, or\n   color of a body, as indicating its temperature; redness; high color;\n   flush; degree of temperature to which something is heated, as indicated\n   by appearance, condition, or otherwise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heat', 'n.', 'A single complete operation of heating, as at a forge or in a\n   furnace; as, to make a horseshoe in a certain number of heats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heat', 'n.', 'A violent action unintermitted; a single effort; a single\n   course in a race that consists of two or more courses; as, he won two\n   heats out of three.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heat', 'n.', 'Utmost violence; rage; vehemence; as, the heat of battle or\n   party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heat', 'n.', 'Agitation of mind; inflammation or excitement; exasperation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heat', 'n.', 'Animation, as in discourse; ardor; fervency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heat', 'n.', 'Sexual excitement in animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heat', 'n.', 'Fermentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heated', 'imp. & p. p.', 'of Heat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heating', 'p. pr. & vb. n.', 'of Heat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heat', 'v. t.', 'To make hot; to communicate heat to, or cause to grow\n   warm; as, to heat an oven or furnace, an iron, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heat', 'v. t.', 'To excite or make hot by action or emotion; to make\n   feverish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heat', 'v. t.', 'To excite ardor in; to rouse to action; to excite to\n   excess; to inflame, as the passions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heat', 'v. i.', 'To grow warm or hot by the action of fire or friction,\n   etc., or the communication of heat; as, the iron or the water heats\n   slowly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heat', 'v. i.', 'To grow warm or hot by fermentation, or the development of\n   heat by chemical action; as, green hay heats in a mow, and manure in\n   the dunghill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heat', 'imp. & p. p.', 'Heated; as, the iron though heat red-hot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heater', 'n.', 'One who, or that which, heats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heater', 'n.', 'Any contrivance or implement, as a furnace, stove, or other\n   heated body or vessel, etc., used to impart heat to something, or to\n   contain something to be heated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heath', 'n.', 'A low shrub (Erica, / Calluna, vulgaris), with minute\n   evergreen leaves, and handsome clusters of pink flowers. It is used in\n   Great Britain for brooms, thatch, beds for the poor, and for heating\n   ovens. It is also called heather, and ling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heath', 'n.', 'Also, any species of the genus Erica, of which several are\n   European, and many more are South African, some of great beauty. See\n   Illust. of Heather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heath', 'n.', 'A place overgrown with heath; any cheerless tract of country\n   overgrown with shrubs or coarse herbage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heathclad', 'a.', 'Clad or crowned with heath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heathens', 'pl. ', 'of Heathen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heathen', 'pl. ', 'of Heathen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heathen', 'n.', 'An individual of the pagan or unbelieving nations, or\n   those which worship idols and do not acknowledge the true God; a pagan;\n   an idolater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heathen', 'n.', 'An irreligious person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heathen', 'a.', 'Gentile; pagan; as, a heathen author.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heathen', 'a.', 'Barbarous; unenlightened; heathenish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heathen', 'a.', 'Irreligious; scoffing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heathendom', 'n.', 'That part of the world where heathenism prevails; the\n   heathen nations, considered collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heathendom', 'n.', 'Heathenism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heathenesse', 'n.', 'Heathendom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heathenish', 'a.', 'Of or pertaining to the heathen; resembling or\n   characteristic of heathens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heathenish', 'a.', 'Rude; uncivilized; savage; cruel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heathenish', 'a.', 'Irreligious; as, a heathenish way of living.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heathenishly', 'adv.', 'In a heathenish manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heathenishness', 'n.', 'The state or quality of being heathenish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heathenism', 'n.', 'The religious system or rites of a heathen nation;\n   idolatry; paganism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heathenism', 'n.', 'The manners or morals usually prevalent in a heathen\n   country; ignorance; rudeness; barbarism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heathenized', 'imp. & p. p.', 'of Heathenize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heathenizing', 'p. pr. & vb. n.', 'of Heathenize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heathenize', 'v. t.', 'To render heathen or heathenish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heathenness', 'n.', 'State of being heathen or like the heathen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heathenry', 'n.', 'The state, quality, or character of the heathen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heathenry', 'n.', 'Heathendom; heathen nations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heather', 'n.', 'Heath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heathery', 'a.', 'Heathy; abounding in heather; of the nature of heath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heathy', 'a.', 'Full of heath; abounding with heath; as, heathy land;\n   heathy hills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heating', 'a.', 'That heats or imparts heat; promoting warmth or heat;\n   exciting action; stimulating; as, heating medicines or applications.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heatingly', 'adv.', 'In a heating manner; so as to make or become hot or\n   heated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heatless', 'a.', 'Destitute of heat; cold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heaved', 'imp.', 'of Heave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hove', '', 'of Heave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heaved', 'p. p.', 'of Heave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hove', '', 'of Heave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoven', '', 'of Heave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heaving', 'p. pr. & vb. n.', 'of Heave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heave', 'v. t.', 'To cause to move upward or onward by a lifting effort; to\n   lift; to raise; to hoist; -- often with up; as, the wave heaved the\n   boat on land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heave', 'v. t.', 'To throw; to cast; -- obsolete, provincial, or\n   colloquial, except in certain nautical phrases; as, to heave the lead;\n   to heave the log.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heave', 'v. t.', 'To force from, or into, any position; to cause to move;\n   also, to throw off; -- mostly used in certain nautical phrases; as, to\n   heave the ship ahead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heave', 'v. t.', 'To raise or force from the breast; to utter with effort;\n   as, to heave a sigh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heave', 'v. t.', 'To cause to swell or rise, as the breast or bosom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heave', 'v. i.', 'To be thrown up or raised; to rise upward, as a tower or\n   mound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heave', 'v. i.', 'To rise and fall with alternate motions, as the lungs in\n   heavy breathing, as waves in a heavy sea, as ships on the billows, as\n   the earth when broken up by frost, etc.; to swell; to dilate; to\n   expand; to distend; hence, to labor; to struggle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heave', 'v. i.', 'To make an effort to raise, throw, or move anything; to\n   strain to do something difficult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heave', 'v. i.', 'To make an effort to vomit; to retch; to vomit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heave', 'n.', 'An effort to raise something, as a weight, or ones self, or\n   to move something heavy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heave', 'n.', 'An upward motion; a rising; a swell or distention, as of the\n   breast in difficult breathing, of the waves, of the earth in an\n   earthquake, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heave', 'n.', 'A horizontal dislocation in a metallic lode, taking place at\n   an intersection with another lode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heaven', 'n.', 'The expanse of space surrounding the earth; esp., that\n   which seems to be over the earth like a great arch or dome; the\n   firmament; the sky; the place where the sun, moon, and stars appear; --\n   often used in the plural in this sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heaven', 'n.', 'The dwelling place of the Deity; the abode of bliss; the\n   place or state of the blessed after death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heaven', 'n.', 'The sovereign of heaven; God; also, the assembly of the\n   blessed, collectively; -- used variously in this sense, as in No. 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heaven', 'n.', 'Any place of supreme happiness or great comfort; perfect\n   felicity; bliss; a sublime or exalted condition; as, a heaven of\n   delight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heavened', 'imp. & p. p.', 'of Heaven');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heavening', 'p. pr. & vb. n.', 'of Heaven');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heaven', 'v. t.', 'To place in happiness or bliss, as if in heaven; to\n   beatify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heavenize', 'v. t.', 'To render like heaven or fit for heaven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heavenliness', 'n.', 'The state or quality of being heavenly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heavenly', 'a.', 'Pertaining to, resembling, or inhabiting heaven;\n   celestial; not earthly; as, heavenly regions; heavenly music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heavenly', 'a.', 'Appropriate to heaven in character or happiness; perfect;\n   pure; supremely blessed; as, a heavenly race; the heavenly, throng.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heavenly', 'adv.', 'In a manner resembling that of heaven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heavenly', 'adv.', 'By the influence or agency of heaven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heavenlyminded', 'a.', 'Having the thoughts and affections placed on, or\n   suitable for, heaven and heavenly objects; devout; godly; pious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heavenward', 'a & adv.', 'Toward heaven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heave offering', '', 'An offering or oblation heaved up or elevated before\n   the altar, as the shoulder of the peace offering. See Wave offering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heaver', 'n.', 'One who, or that which, heaves or lifts; a laborer employed\n   on docks in handling freight; as, a coal heaver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heaver', 'n.', 'A bar used as a lever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heaves', 'n.', 'A disease of horses, characterized by difficult breathing,\n   with heaving of the flank, wheezing, flatulency, and a peculiar cough;\n   broken wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heavily', 'adv.', 'In a heavy manner; with great weight; as, to bear\n   heavily on a thing; to be heavily loaded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heavily', 'adv.', 'As if burdened with a great weight; slowly and\n   laboriously; with difficulty; hence, in a slow, difficult, or suffering\n   manner; sorrowfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heaviness', 'n.', 'The state or quality of being heavy in its various\n   senses; weight; sadness; sluggishness; oppression; thickness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heaving', 'n.', 'A lifting or rising; a swell; a panting or deep sighing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heavisome', 'a.', 'Heavy; dull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heavy', 'a.', 'Having the heaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heavy', 'superl.', 'Heaved or lifted with labor; not light; weighty;\n   ponderous; as, a heavy stone; hence, sometimes, large in extent,\n   quantity, or effects; as, a heavy fall of rain or snow; a heavy\n   failure; heavy business transactions, etc.; often implying strength;\n   as, a heavy barrier; also, difficult to move; as, a heavy draught.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heavy', 'superl.', 'Not easy to bear; burdensome; oppressive; hard to\n   endure or accomplish; hence, grievous, afflictive; as, heavy yokes,\n   expenses, undertakings, trials, news, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heavy', 'superl.', 'Laden with that which is weighty; encumbered; burdened;\n   bowed down, either with an actual burden, or with care, grief, pain,\n   disappointment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heavy', 'superl.', 'Slow; sluggish; inactive; or lifeless, dull, inanimate,\n   stupid; as, a heavy gait, looks, manners, style, and the like; a heavy\n   writer or book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heavy', 'superl.', 'Strong; violent; forcible; as, a heavy sea, storm,\n   cannonade, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heavy', 'superl.', 'Loud; deep; -- said of sound; as, heavy thunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heavy', 'superl.', 'Dark with clouds, or ready to rain; gloomy; -- said of\n   the sky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heavy', 'superl.', 'Impeding motion; cloggy; clayey; -- said of earth; as,\n   a heavy road, soil, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heavy', 'superl.', 'Not raised or made light; as, heavy bread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heavy', 'superl.', 'Not agreeable to, or suitable for, the stomach; not\n   easily digested; -- said of food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heavy', 'superl.', 'Having much body or strength; -- said of wines, or\n   other liquors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heavy', 'superl.', 'With child; pregnant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heavy', 'adv.', 'Heavily; -- sometimes used in composition; as,\n   heavy-laden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heavy', 'v. t.', 'To make heavy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heavy-armed', 'a.', 'Wearing heavy or complete armor; carrying heavy arms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heavy-haded', 'a.', 'Clumsy; awkward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heavy-headed', 'a.', 'Dull; stupid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heavy spar', '', 'Native barium sulphate or barite, -- so called because of\n   its high specific gravity as compared with other non-metallic minerals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebdomad', 'n.', 'A week; a period of seven days.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebdomadal', 'a.', 'Alt. of Hebdomadary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebdomadary', 'a.', 'Consisting of seven days, or occurring at intervals of\n   seven days; weekly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebdomadally', 'adv.', 'In periods of seven days; weekly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebdomadary', 'n.', 'A member of a chapter or convent, whose week it is to\n   officiate in the choir, and perform other services, which, on\n   extraordinary occasions, are performed by the superiors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebdomatical', 'a.', 'Weekly; hebdomadal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebe', 'n.', 'The goddess of youth, daughter of Jupiter and Juno. She was\n   believed to have the power of restoring youth and beauty to those who\n   had lost them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebe', 'n.', 'An African ape; the hamadryas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heben', 'n.', 'Ebony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebenon', 'n.', 'See Henbane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebetated', 'imp. & p. p.', 'of Hebetate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebetating', 'p. pr. & vb. n.', 'of Hebetate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebetate', 'v. t.', 'To render obtuse; to dull; to blunt; to stupefy; as,\n   to hebetate the intellectual faculties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebetate', 'a.', 'Obtuse; dull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebetate', 'a.', 'Having a dull or blunt and soft point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebetation', 'n.', 'The act of making blunt, dull, or stupid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebetation', 'n.', 'The state of being blunted or dulled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebete', 'a.', 'Dull; stupid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebetude', 'n.', 'Dullness; stupidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebraic', 'a.', 'Of or pertaining to the Hebrews, or to the language of the\n   Hebrews.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebraically', 'adv.', 'After the manner of the Hebrews or of the Hebrew\n   language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebraism', 'n.', 'A Hebrew idiom or custom; a peculiar expression or manner\n   of speaking in the Hebrew language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebraism', 'n.', 'The type of character of the Hebrews.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebraist', 'n.', 'One versed in the Hebrew language and learning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebraistic', 'a.', 'Pertaining to, or resembling, the Hebrew language or\n   idiom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebraistically', 'adv.', 'In a Hebraistic sense or form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebraize', 'v. t.', 'To convert into the Hebrew idiom; to make Hebrew or\n   Hebraistic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebraized', 'imp. & p. p.', 'of Hebraize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebraizing', 'p. pr. & vb. n.', 'of Hebraize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebraize', 'v. i.', 'To speak Hebrew, or to conform to the Hebrew idiom, or\n   to Hebrew customs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebrew', 'n.', 'An appellative of Abraham or of one of his descendants,\n   esp. in the line of Jacob; an Israelite; a Jew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebrew', 'n.', 'The language of the Hebrews; -- one of the Semitic family\n   of languages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebrew', 'a.', 'Of or pertaining to the Hebrews; as, the Hebrew language or\n   rites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebrewess', 'n.', 'An Israelitish woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebrician', 'n.', 'A Hebraist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebridean', 'a.', 'Alt. of Hebridian');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebridian', 'a.', 'Of or pertaining to the islands called Hebrides, west of\n   Scotland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hebridian', 'n.', 'A native or inhabitant of the Hebrides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hecatomb', 'n.', 'A sacrifice of a hundred oxen or cattle at the same time;\n   hence, the sacrifice or slaughter of any large number of victims.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hecatompedon', 'n.', 'A name given to the old Parthenon at Athens, because\n   measuring 100 Greek feet, probably in the width across the stylobate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hecdecane', 'n.', 'A white, semisolid, spermaceti-like hydrocarbon, C16H34,\n   of the paraffin series, found dissolved as an important ingredient of\n   kerosene, and so called because each molecule has sixteen atoms of\n   carbon; -- called also hexadecane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heck', 'n.', 'The bolt or latch of a door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heck', 'n.', 'A rack for cattle to feed at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heck', 'n.', 'A door, especially one partly of latticework; -- called also\n   heck door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heck', 'n.', 'A latticework contrivance for catching fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heck', 'n.', 'An apparatus for separating the threads of warps into sets,\n   as they are wound upon the reel from the bobbins, in a warping machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heck', 'n.', 'A bend or winding of a stream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heckimal', 'n.', 'The European blue titmouse (Parus coeruleus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heckle', 'n. & v. t.', 'Same as Hackle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hectare', 'n.', 'A measure of area, or superficies, containing a hundred\n   ares, or 10,000 square meters, and equivalent to 2.471 acres.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hectic', 'a.', 'Habitual; constitutional; pertaining especially to slow\n   waste of animal tissue, as in consumption; as, a hectic type in\n   disease; a hectic flush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hectic', 'a.', 'In a hectic condition; having hectic fever; consumptive;\n   as, a hectic patient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hectic', 'n.', 'Hectic fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hectic', 'n.', 'A hectic flush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hectocotylized', 'a.', 'Changed into a hectocotylus; having a hectocotylis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hectocotyli', 'pl. ', 'of Hectocotylus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hectocotylus', 'n.', 'One of the arms of the male of most kinds of\n   cephalopods, which is specially modified in various ways to effect the\n   fertilization of the eggs. In a special sense, the greatly modified arm\n   of Argonauta and allied genera, which, after receiving the\n   spermatophores, becomes detached from the male, and attaches itself to\n   the female for reproductive purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hectogram', 'n.', 'A measure of weight, containing a hundred grams, or\n   about 3.527 ounces avoirdupois.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hectogramme', 'n.', 'The same as Hectogram.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hectograph', 'n.', 'A contrivance for multiple copying, by means of a\n   surface of gelatin softened with glycerin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hectoliter', 'n.', 'Alt. of Hectolitre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hectolitre', 'n.', 'A measure of liquids, containing a hundred liters;\n   equal to a tenth of a cubic meter, nearly 26/ gallons of wine measure,\n   or 22.0097 imperial gallons. As a dry measure, it contains ten\n   decaliters, or about 2/ Winchester bushels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hectometer', 'n.', 'Alt. of Hectometre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hectometre', 'n.', 'A measure of length, equal to a hundred meters. It is\n   equivalent to 328.09 feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hector', 'n.', 'A bully; a blustering, turbulent, insolent, fellow; one who\n   vexes or provokes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hectored', 'imp. & p. p.', 'of Hector');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hectoring', 'p. pr. & vb. n.', 'of Hector');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hector', 'v. t.', 'To treat with insolence; to threaten; to bully; hence,\n   to torment by words; to tease; to taunt; to worry or irritate by\n   bullying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hector', 'v. i.', 'To play the bully; to bluster; to be turbulent or\n   insolent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hectorism', 'n.', 'The disposition or the practice of a hector; a bullying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hectorly', 'a.', 'Resembling a hector; blustering; insolent; taunting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hectostere', 'n.', 'A measure of solidity, containing one hundred cubic\n   meters, and equivalent to 3531.66 English or 3531.05 United States\n   cubic feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heddles', 'pl. ', 'of Heddle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heddle', 'n.', 'One of the sets of parallel doubled threads which, with\n   mounting, compose the harness employed to guide the warp threads to the\n   lathe or batten in a loom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heddle', 'v. t.', 'To draw (the warp thread) through the heddle-eyes, in\n   weaving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heddle-eye', 'n.', 'The eye or loop formed in each heddle to receive a warp\n   thread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heddling', 'vb. n.', 'The act of drawing the warp threads through the\n   heddle-eyes of a weavers harness; the harness itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hederaceous', 'a.', 'Of, pertaining to, or resembling, ivy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hederal', 'a.', 'Of or pertaining to ivy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hederic', 'a.', 'Pertaining to, or derived from, the ivy (Hedera); as,\n   hederic acid, an acid of the acetylene series.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hederiferous', 'a.', 'Producing ivy; ivy-bearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hederose', 'a.', 'Pertaining to, or of, ivy; full of ivy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hedge', 'n.', 'A thicket of bushes, usually thorn bushes; especially, such\n   a thicket planted as a fence between any two portions of land; and also\n   any sort of shrubbery, as evergreens, planted in a line or as a fence;\n   particularly, such a thicket planted round a field to fence it, or in\n   rows to separate the parts of a garden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hedged', 'imp. & p. p.', 'of Hedge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hedging', 'p. pr. & vb. n.', 'of Hedge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hedge', 'v. t.', 'To inclose or separate with a hedge; to fence with a\n   thickly set line or thicket of shrubs or small trees; as, to hedge a\n   field or garden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hedge', 'v. t.', 'To obstruct, as a road, with a barrier; to hinder from\n   progress or success; -- sometimes with up and out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hedge', 'v. t.', 'To surround for defense; to guard; to protect; to hem\n   (in).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hedge', 'v. t.', 'To surround so as to prevent escape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hedge', 'v. i.', 'To shelter ones self from danger, risk, duty,\n   responsibility, etc., as if by hiding in or behind a hedge; to skulk;\n   to slink; to shirk obligations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hedge', 'v. i.', 'To reduce the risk of a wager by making a bet against the\n   side or chance one has bet on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hedge', 'v. i.', 'To use reservations and qualifications in ones speech so\n   as to avoid committing ones self to anything definite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hedgeborn', 'a.', 'Born under a hedge; of low birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hedgebote', 'n.', 'Same as Haybote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hedgehog', 'n.', 'A small European insectivore (Erinaceus Europaeus), and\n   other allied species of Asia and Africa, having the hair on the upper\n   part of its body mixed with prickles or spines. It is able to roll\n   itself into a ball so as to present the spines outwardly in every\n   direction. It is nocturnal in its habits, feeding chiefly upon insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hedgehog', 'n.', 'The Canadian porcupine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hedgehog', 'n.', 'A species of Medicago (M. intertexta), the pods of which\n   are armed with short spines; -- popularly so called.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hedgehog', 'n.', 'A form of dredging machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hedgeless', 'a.', 'Having no hedge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hedgepig', 'n.', 'A young hedgehog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hedger', 'n.', 'One who makes or mends hedges; also, one who hedges, as, in\n   betting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hedgerow', 'n.', 'A row of shrubs, or trees, planted for inclosure or\n   separation of fields.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hedging bill', '', 'A hedge bill. See under Hedge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hedonic', 'a.', 'Pertaining to pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hedonic', 'a.', 'Of or relating to Hedonism or the Hedonic sect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hedonistic', 'a.', 'Same as Hedonic, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heeded', 'imp. & p. p.', 'of Heed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heeding', 'p. pr. & vb. n.', 'of Heed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heed', 'v. t.', 'To mind; to regard with care; to take notice of; to attend\n   to; to observe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heed', 'v. i.', 'To mind; to consider.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heed', 'n.', 'Attention; notice; observation; regard; -- often with give or\n   take.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heed', 'n.', 'Careful consideration; obedient regard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heed', 'n.', 'A look or expression of heading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heedful', 'a.', 'Full of heed; regarding with care; cautious; circumspect;\n   attentive; vigilant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heedless', 'a.', 'Without heed or care; inattentive; careless; thoughtless;\n   unobservant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heedy', 'a.', 'Heedful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heel', 'v. i.', 'To lean or tip to one side, as a ship; as, the ship heels\n   aport; the boat heeled over when the squall struck it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heel', 'n.', 'The hinder part of the foot; sometimes, the whole foot; -- in\n   man or quadrupeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heel', 'n.', 'The hinder part of any covering for the foot, as of a shoe,\n   sock, etc.; specif., a solid part projecting downward from the hinder\n   part of the sole of a boot or shoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heel', 'n.', 'The latter or remaining part of anything; the closing or\n   concluding part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heel', 'n.', 'Anything regarded as like a human heel in shape; a\n   protuberance; a knob.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heel', 'n.', 'The part of a thing corresponding in position to the human\n   heel; the lower part, or part on which a thing rests');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heel', 'n.', 'The after end of a ships keel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heel', 'n.', 'The lower end of a mast, a boom, the bowsprit, the sternpost,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heel', 'n.', 'In a small arm, the corner of the but which is upwards in the\n   firing position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heel', 'n.', 'The uppermost part of the blade of a sword, next to the hilt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heel', 'n.', 'The part of any tool next the tang or handle; as, the heel of\n   a scythe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heel', 'n.', 'Management by the heel, especially the spurred heel; as, the\n   horse understands the heel well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heel', 'n.', 'The lower end of a timber in a frame, as a post or rafter. In\n   the United States, specif., the obtuse angle of the lower end of a\n   rafter set sloping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heel', 'n.', 'A cyma reversa; -- so called by workmen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heeled', 'imp. & p. p.', 'of Heel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heeling', 'p. pr. & vb. n.', 'of Heel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heel', 'v. t.', 'To perform by the use of the heels, as in dancing,\n   running, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heel', 'v. t.', 'To add a heel to; as, to heel a shoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heel', 'v. t.', 'To arm with a gaff, as a cock for fighting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heelball', 'n.', 'A composition of wax and lampblack, used by shoemakers\n   for polishing, and by antiquaries in copying inscriptions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heeler', 'n.', 'A cock that strikes well with his heels or spurs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heeler', 'n.', 'A dependent and subservient hanger-on of a political\n   patron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heelless', 'a.', 'Without a heel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heelpiece', 'n.', 'A piece of armor to protect the heels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heelpiece', 'n.', 'A piece of leather fixed on the heel of a shoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heelpiece', 'n.', 'The end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heelpost', 'n.', 'The post supporting the outer end of a propeller shaft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heelpost', 'n.', 'The post to which a gate or door is hinged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heelpost', 'n.', 'The quoin post of a lock gate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heelspur', 'n.', 'A slender bony or cartilaginous process developed from\n   the heel bone of bats. It helps to support the wing membranes. See\n   Illust. of Cheiropter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heeltap', 'n.', 'One of the segments of leather in the heel of a shoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heeltap', 'n.', 'A small portion of liquor left in a glass after drinking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heeltapped', 'imp. & p. p.', 'of Heeltap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heeltapping', 'p. pr. & vb. n.', 'of Heeltap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heeltap', 'v. t.', 'To add a piece of leather to the heel of (a shoe, boot,\n   etc.)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heeltool', 'n.', 'A tool used by turners in metal, having a bend forming a\n   heel near the cutting end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heep', 'n.', 'The hip of the dog-rose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heer', 'n.', 'A yarn measure of six hundred yards or / of a spindle. See\n   Spindle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heer', 'n.', 'Hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heft', 'n.', 'Same as Haft, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heft', 'n.', 'The act or effort of heaving/ violent strain or exertion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heft', 'n.', 'Weight; ponderousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heft', 'n.', 'The greater part or bulk of anything; as, the heft of the\n   crop was spoiled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hefted', 'imp. & p. p.', 'of Heft');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heft', '', 'of Heft');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hefting', 'p. pr. & vb. n.', 'of Heft');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heft', 'v. t.', 'To heave up; to raise aloft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heft', 'v. t.', 'To prove or try the weight of by raising.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hefty', 'a.', 'Moderately heavy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hegelian', 'a.', 'Pertaining to Hegelianism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hegelian', 'n.', 'A follower of Hegel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hegelianism', 'n.', 'Alt. of Hegelism');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hegelism', 'n.', 'The system of logic and philosophy set forth by Hegel, a\n   German writer (1770-1831).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hegemonic', 'a.', 'Alt. of Hegemonical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hegemonical', 'a.', 'Leading; controlling; ruling; predominant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hegemony', 'n.', 'Leadership; preponderant influence or authority; --\n   usually applied to the relation of a government or state to its\n   neighbors or confederates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hegge', 'n.', 'A hedge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hegira', 'n.', 'The flight of Mohammed from Mecca, September 13, A. D. 622\n   (subsequently established as the first year of the Moslem era); hence,\n   any flight or exodus regarded as like that of Mohammed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heifer', 'n.', 'A young cow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heigh-ho', 'interj.', 'An exclamation of surprise, joy, dejection,\n   uneasiness, weariness, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Height', 'n.', 'The condition of being high; elevated position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Height', 'n.', 'The distance to which anything rises above its foot, above\n   that on which in stands, above the earth, or above the level of the\n   sea; altitude; the measure upward from a surface, as the floor or the\n   ground, of animal, especially of a man; stature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Height', 'n.', 'Degree of latitude either north or south.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Height', 'n.', 'That which is elevated; an eminence; a hill or mountain;\n   as, Alpine heights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Height', 'n.', 'Elevation in excellence of any kind, as in power, learning,\n   arts; also, an advanced degree of social rank; preeminence or\n   distinction in society; prominence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Height', 'n.', 'Progress toward eminence; grade; degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Height', 'n.', 'Utmost degree in extent; extreme limit of energy or\n   condition; as, the height of a fever, of passion, of madness, of folly;\n   the height of a tempest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heightened', 'imp. & p. p.', 'of Heighten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heightening', 'p. pr. & vb. n.', 'of Heighten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heighten', 'v. t.', 'To make high; to raise higher; to elevate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heighten', 'v. t.', 'To carry forward; to advance; to increase; to augment;\n   to aggravate; to intensify; to render more conspicuous; -- used of\n   things, good or bad; as, to heighten beauty; to heighten a flavor or a\n   tint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heightener', 'n.', 'One who, or that which, heightens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heinous', 'a.', 'Hateful; hatefully bad; flagrant; odious; atrocious;\n   giving great great offense; -- applied to deeds or to character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heir', 'n.', 'One who inherits, or is entitled to succeed to the possession\n   of, any property after the death of its owner; one on whom the law\n   bestows the title or property of another at the death of the latter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heir', 'n.', 'One who receives any endowment from an ancestor or relation;\n   as, the heir of ones reputation or virtues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heir', 'v. t.', 'To inherit; to succeed to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heirdom', 'n.', 'The state of an heir; succession by inheritance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heiress', 'n.', 'A female heir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heirless', 'a.', 'Destitute of an heir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heirloom', 'n.', 'Any furniture, movable, or personal chattel, which by law\n   or special custom descends to the heir along with the inheritance; any\n   piece of personal property that has been in a family for several\n   generations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heirship', 'n.', 'The state, character, or privileges of an heir; right of\n   inheriting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hejira', 'n.', 'See Hegira.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hektare', 'n.', 'Alt. of Hektometer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hektogram', 'n.', 'Alt. of Hektometer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hektoliter', 'n.', 'Alt. of Hektometer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hektometer', 'n.', 'Same as Hectare, Hectogram, Hectoliter, and Hectometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hektograph', 'n.', 'See Hectograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helamys', 'n.', 'See Jumping hare, under Hare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helcoplasty', 'n.', 'The act or process of repairing lesions made by\n   ulcers, especially by a plastic operation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Held', '', 'imp. & p. p. of Hold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hele', 'n.', 'Health; welfare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hele', 'v. t.', 'To hide; to cover; to roof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helena', 'n.', 'See St. Elmos fire, under Saint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helenin', 'n.', 'A neutral organic substance found in the root of the\n   elecampane (Inula helenium), and extracted as a white crystalline or\n   oily material, with a slightly bitter taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliac', 'a.', 'Heliacal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliacal', 'a.', 'Emerging from the light of the sun, or passing into it;\n   rising or setting at the same, or nearly the same, time as the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliacally', 'adv.', 'In a heliacal manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helianthin', 'n.', 'An artificial, orange dyestuff, analogous to tropaolin,\n   and like it used as an indicator in alkalimetry; -- called also methyl\n   orange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helianthoid', 'a.', 'Of or pertaining to the Helianthoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helianthoidea', 'n. pl.', 'An order of Anthozoa; the Actinaria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helical', 'a.', 'Of or pertaining to, or in the form of, a helix; spiral;\n   as, a helical staircase; a helical spring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helichrysum', 'n.', 'A genus of composite plants, with shining, commonly\n   white or yellow, or sometimes reddish, radiated involucres, which are\n   often called \"everlasting flowers.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliciform', 'a.', 'Having the form of a helix; spiral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helicin', 'n.', 'A glucoside obtained as a white crystalline substance by\n   partial oxidation of salicin, from a willow (Salix Helix of Linnaeus.)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helicine', 'a.', 'Curled; spiral; helicoid; -- applied esp. to certain\n   arteries of the penis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helicograph', 'n.', 'An instrument for drawing spiral lines on a plane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helicoid', 'a.', 'Spiral; curved, like the spire of a univalve shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helicoid', 'a.', 'Shaped like a snail shell; pertaining to the Helicidae,\n   or Snail family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helicoid', 'n.', 'A warped surface which may be generated by a straight\n   line moving in such a manner that every point of the line shall have a\n   uniform motion in the direction of another fixed straight line, and at\n   the same time a uniform angular motion about it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helicoidal', 'a.', 'Same as Helicoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helicon', 'n.', 'A mountain in Boeotia, in Greece, supposed by the Greeks\n   to be the residence of Apollo and the Muses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliconia', 'n.', 'One of numerous species of Heliconius, a genus of\n   tropical American butterflies. The wings are usually black, marked with\n   green, crimson, and white.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliconian', 'a.', 'Of or pertaining to Helicon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliconian', 'a.', 'Like or pertaining to the butterflies of the genus\n   Heliconius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helicotrema', 'n.', 'The opening by which the two scalae communicate at the\n   top of the cochlea of the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helio-', '', 'A combining form from Gr. \"h`lios the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliocentric', 'a.', 'Alt. of Heliocentrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliocentrical', 'a.', 'pertaining to the suns center, or appearing to be\n   seen from it; having, or relating to, the sun as a center; -- opposed\n   to geocentrical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliochrome', 'n.', 'A photograph in colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliochromic', 'a.', 'Pertaining to, or produced by, heliochromy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliochromy', 'n.', 'The art of producing photographs in color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliograph', 'n.', 'A picture taken by heliography; a photograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliograph', 'n.', 'An instrument for taking photographs of the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliograph', 'n.', 'An apparatus for telegraphing by means of the suns\n   rays. See Heliotrope, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliographic', 'a.', 'Of or pertaining to heliography or a heliograph; made\n   by heliography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliography', 'n.', 'Photography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliogravure', 'n.', 'The process of photographic engraving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliolater', 'n.', 'A worshiper of the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliolatry', 'n.', 'Sun worship. See Sabianism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliolite', 'n.', 'A fossil coral of the genus Heliolites, having\n   twelve-rayed cells. It is found in the Silurian rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliometer', 'n.', 'An instrument devised originally for measuring the\n   diameter of the sun; now employed for delicate measurements of the\n   distance and relative direction of two stars too far apart to be easily\n   measured in the field of view of an ordinary telescope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliometric', 'a.', 'Alt. of Heliometrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliometrical', 'a.', 'Of or pertaining to the heliometer, or to\n   heliometry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliometry', 'n.', 'The apart or practice of measuring the diameters of\n   heavenly bodies, their relative distances, etc. See Heliometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliopora', 'n.', 'An East Indian stony coral now known to belong to the\n   Alcyonaria; -- called also blue coral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helioscope', 'n.', 'A telescope or instrument for viewing the sun without\n   injury to the eyes, as through colored glasses, or with mirrors which\n   reflect but a small portion of light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliostat', 'n.', 'An instrument consisting of a mirror moved by clockwork,\n   by which a sunbeam is made apparently stationary, by being steadily\n   directed to one spot during the whole of its diurnal period; also, a\n   geodetic heliotrope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliotrope', 'n.', 'An instrument or machine for showing when the sun\n   arrived at the tropics and equinoctial line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliotrope', 'n.', 'A plant of the genus Heliotropium; -- called also\n   turnsole and girasole. H. Peruvianum is the commonly cultivated species\n   with fragrant flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliotrope', 'n.', 'An instrument for making signals to an observer at a\n   distance, by means of the suns rays thrown from a mirror.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliotrope', 'n.', 'See Bloodstone (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliotroper', 'n.', 'The person at a geodetic station who has charge of the\n   heliotrope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliotropic', 'a.', 'Manifesting heliotropism; turning toward the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliotropism', 'n.', 'The phenomenon of turning toward the light, seen in\n   many leaves and flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliotype', 'n.', 'A picture obtained by the process of heliotypy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliotypic', 'a.', 'Relating to, or obtained by, heliotypy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliotypy', 'n.', 'A method of transferring pictures from photographic\n   negatives to hardened gelatin plates from which impressions are\n   produced on paper as by lithography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heliozoa', 'n. pl.', 'An order of fresh-water rhizopods having a more or\n   less globular form, with slender radiating pseudopodia; the sun\n   animalcule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helispheric', 'a.', 'Alt. of Helispherical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helispherical', 'a.', 'Spiral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helium', 'n.', 'A gaseous element found in the atmospheres of the sun and\n   earth and in some rare minerals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helices', 'pl. ', 'of Helix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helixes', 'pl. ', 'of Helix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helix', 'n.', 'A nonplane curve whose tangents are all equally inclined to\n   a given plane. The common helix is the curve formed by the thread of\n   the ordinary screw. It is distinguished from the spiral, all the\n   convolutions of which are in the plane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helix', 'n.', 'A caulicule or little volute under the abacus of the\n   Corinthian capital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helix', 'n.', 'The incurved margin or rim of the external ear. See Illust.\n   of Ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helix', 'n.', 'A genus of land snails, including a large number of species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hell', 'v. t.', 'The place of the dead, or of souls after death; the grave;\n   -- called in Hebrew sheol, and by the Greeks hades.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hell', 'v. t.', 'The place or state of punishment for the wicked after\n   death; the abode of evil spirits. Hence, any mental torment; anguish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hell', 'v. t.', 'A place where outcast persons or things are gathered');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hell', 'v. t.', 'A dungeon or prison; also, in certain running games, a\n   place to which those who are caught are carried for detention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hell', 'v. t.', 'A gambling house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hell', 'v. t.', 'A place into which a tailor throws his shreds, or a\n   printer his broken type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hell', 'v. t.', 'To overwhelm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hellanodic', 'n.', 'A judge or umpire in games or combats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hellbender', 'n.', 'A large North American aquatic salamander (Protonopsis\n   horrida or Menopoma Alleghaniensis). It is very voracious and very\n   tenacious of life. Also called alligator, and water dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hellborn', 'a.', 'Born in or of hell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hellbred', 'a.', 'Produced in hell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hellbrewed', 'a.', 'Prepared in hell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hellbroth', 'n.', 'A composition for infernal purposes; a magical\n   preparation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hell-cat', 'n.', 'A witch; a hag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hell-diver', 'n.', 'The dabchick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helldoomed', 'a.', 'Doomed to hell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hellebore', 'n.', 'A genus of perennial herbs (Helleborus) of the Crowfoot\n   family, mostly having powerfully cathartic and even poisonous\n   qualities. H. niger is the European black hellebore, or Christmas rose,\n   blossoming in winter or earliest spring. H. officinalis was the\n   officinal hellebore of the ancients.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hellebore', 'n.', 'Any plant of several species of the poisonous liliaceous\n   genus Veratrum, especially V. album and V. viride, both called white\n   hellebore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helleborein', 'n.', 'A poisonous glucoside accompanying helleborin in\n   several species of hellebore, and extracted as a white crystalline\n   substance with a bittersweet taste. It has a strong action on the\n   heart, resembling digitalin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helleborin', 'n.', 'A poisonous glucoside found in several species of\n   hellebore, and extracted as a white crystalline substance with a sharp\n   tingling taste. It possesses the essential virtues of the plant; --\n   called also elleborin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helleborism', 'n.', 'The practice or theory of using hellebore as a\n   medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hellene', 'n.', 'A native of either ancient or modern Greece; a Greek.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hellenian', 'a.', 'Of or pertaining to the Hellenes, or Greeks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hellenic', 'a.', 'Of or pertaining to the Hellenes, or inhabitants of\n   Greece; Greek; Grecian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hellenic', 'n.', 'The dialect, formed with slight variations from the\n   Attic, which prevailed among Greek writers after the time of Alexander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hellenism', 'n.', 'A phrase or form of speech in accordance with genius and\n   construction or idioms of the Greek language; a Grecism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hellenism', 'n.', 'The type of character of the ancient Greeks, who aimed\n   at culture, grace, and amenity, as the chief elements in human\n   well-being and perfection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hellenist', 'n.', 'One who affiliates with Greeks, or imitates Greek\n   manners; esp., a person of Jewish extraction who used the Greek\n   language as his mother tongue, as did the Jews of Asia Minor, Greece,\n   Syria, and Egypt; distinguished from the Hebraists, or native Jews\n   (Acts vi. 1).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hellenist', 'n.', 'One skilled in the Greek language and literature; as,\n   the critical Hellenist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hellenistic', 'a.', 'Alt. of Hellenistical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hellenistical', 'a.', 'Pertaining to the Hellenists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hellenistically', 'adv.', 'According to the Hellenistic manner or dialect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hellenize', 'v. i.', 'To use the Greek language; to play the Greek; to\n   Grecize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hellenize', 'v. t.', 'To give a Greek form or character to; to Grecize; as,\n   to Hellenize a word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hellenotype', 'n.', 'See Ivorytype.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hellespont', 'n.', 'A narrow strait between Europe and Asia, now called the\n   Daradanelles. It connects the Aegean Sea and the sea of Marmora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hellespontine', 'a.', 'Of or pertaining to the Hellespont.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hellgamite', 'n.', 'Alt. of Hellgramite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hellgramite', 'n.', 'The aquatic larva of a large American winged insect\n   (Corydalus cornutus), much used a fish bait by anglers; the dobson. It\n   belongs to the Neuroptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hellhag', 'n.', 'A hag of or fit for hell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hell-haunted', 'a.', 'Haunted by devils; hellish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hellhound', 'n.', 'A dog of hell; an agent of hell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hellier', 'v. t.', 'One who heles or covers; hence, a tiler, slater, or\n   thatcher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hellish', 'a.', 'Of or pertaining to hell; like hell; infernal; malignant;\n   wicked; detestable; diabolical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hellkite', 'n.', 'A kite of infernal breed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hello', 'interj. & n.', 'See Halloo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hellward', 'adv.', 'Toward hell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helly', 'a.', 'Hellish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helm', 'n.', 'See Haulm, straw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helm', 'n.', 'The apparatus by which a ship is steered, comprising rudder,\n   tiller, wheel, etc.; -- commonly used of the tiller or wheel alone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helm', 'n.', 'The place or office of direction or administration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helm', 'n.', 'One at the place of direction or control; a steersman; hence,\n   a guide; a director.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helm', 'n.', 'A helve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helmed', 'imp. & p. p.', 'of Helm');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helming', 'p. pr. & vb. n.', 'of Helm');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helm', 'v. t.', 'To steer; to guide; to direct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helm', 'n.', 'A helmet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helm', 'n.', 'A heavy cloud lying on the brow of a mountain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helm', 'v. t.', 'To cover or furnish with a helm or helmet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helmage', 'n.', 'Guidance; direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helmed', 'a.', 'Covered with a helmet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helmet', 'n.', 'A defensive covering for the head. See Casque, Headpiece,\n   Morion, Sallet, and Illust. of Beaver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helmet', 'n.', 'The representation of a helmet over shields or coats of\n   arms, denoting gradations of rank by modifications of form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helmet', 'n.', 'A helmet-shaped hat, made of cork, felt, metal, or other\n   suitable material, worn as part of the uniform of soldiers, firemen,\n   etc., also worn in hot countries as a protection from the heat of the\n   sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helmet', 'n.', 'That which resembles a helmet in form, position, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helmet', 'n.', 'The upper part of a retort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helmet', 'n.', 'The hood-formed upper sepal or petal of some flowers, as of\n   the monkshood or the snapdragon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helmet', 'n.', 'A naked shield or protuberance on the top or fore part of\n   the head of a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helmeted', 'a.', 'Wearing a helmet; furnished with or having a helmet or\n   helmet-shaped part; galeate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helmet-shaped', 'a.', 'Shaped like a helmet; galeate. See Illust. of\n   Galeate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helminth', 'n.', 'An intestinal worm, or wormlike intestinal parasite; one\n   of the Helminthes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helminthagogue', 'n.', 'A vermifuge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helminthes', 'n. pl.', 'One of the grand divisions or branches of the\n   animal kingdom. It is a large group including a vast number of species,\n   most of which are parasitic. Called also Enthelminthes, Enthelmintha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helminthiasis', 'n.', 'A disease in which worms are present in some part of\n   the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helminthic', 'a.', 'Of or relating to worms, or Helminthes; expelling\n   worms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helminthic', 'n.', 'A vermifuge; an anthelmintic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helminthite', 'n.', 'One of the sinuous tracks on the surfaces of many\n   stones, and popularly considered as worm trails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helminthoid', 'a.', 'Wormlike; vermiform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helminthologic', 'a.', 'Alt. of Helminthological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helminthological', 'a.', 'Of or pertaining to helminthology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helminthologist', 'n.', 'One versed in helminthology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helminthology', 'n.', 'The natural history, or study, of worms, esp.\n   parasitic worms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helmless', 'a.', 'Destitute of a helmet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helmless', 'a.', 'Without a helm or rudder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helmsmen', 'pl. ', 'of Helmsman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helmsman', 'n.', 'The man at the helm; a steersman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helmwind', 'n.', 'A wind attending or presaged by the cloud called helm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helot', 'n.', 'A slave in ancient Sparta; a Spartan serf; hence, a slave or\n   serf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helotism', 'n.', 'The condition of the Helots or slaves in Sparta; slavery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helotry', 'n.', 'The Helots, collectively; slaves; bondsmen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helped', 'imp. & p. p.', 'of Help');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holp', 'imp.', 'of Help');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holpen', 'p. p.', 'of Help');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helping', 'p. pr. & vb. n.', 'of Help');
  INSERT INTO words (word, word_type, description) VALUES  (    'Help', 'v. t.', 'To furnish with strength or means for the successful\n   performance of any action or the attainment of any object; to aid; to\n   assist; as, to help a man in his work; to help one to remember; -- the\n   following infinitive is commonly used without to; as, \"Help me scale\n   yon balcony.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Help', 'v. t.', 'To furnish with the means of deliverance from trouble; as,\n   to help one in distress; to help one out of prison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Help', 'v. t.', 'To furnish with relief, as in pain or disease; to be of\n   avail against; -- sometimes with of before a word designating the pain\n   or disease, and sometimes having such a word for the direct object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Help', 'v. t.', 'To change for the better; to remedy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Help', 'v. t.', 'To prevent; to hinder; as, the evil approaches, and who\n   can help it?');
  INSERT INTO words (word, word_type, description) VALUES  (    'Help', 'v. t.', 'To forbear; to avoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Help', 'v. t.', 'To wait upon, as the guests at table, by carving and\n   passing food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Help', 'v. i.', 'To lend aid or assistance; to contribute strength or\n   means; to avail or be of use; to assist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Help', 'v. t.', 'Strength or means furnished toward promoting an object, or\n   deliverance from difficulty or distress; aid; ^; also, the person or\n   thing furnishing the aid; as, he gave me a help of fifty dollars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Help', 'v. t.', 'Remedy; relief; as, there is no help for it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Help', 'v. t.', 'A helper; one hired to help another; also, thew hole force\n   of hired helpers in any business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Help', 'v. t.', 'Specifically, a domestic servant, man or woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helper', 'n.', 'One who, or that which, helps, aids, assists, or relieves;\n   as, a lay helper in a parish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helpful', 'a.', 'Furnishing help; giving aid; assistant; useful; salutary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helpless', 'a.', 'Destitute of help or strength; unable to help or defend\n   ones self; needing help; feeble; weak; as, a helpless infant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helpless', 'a.', 'Beyond help; irremediable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helpless', 'a.', 'Bringing no help; unaiding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helpless', 'a.', 'Unsupplied; destitute; -- with of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helpmate', 'n.', 'A helper; a companion; specifically, a wife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helpmeet', 'n.', 'A wife; a helpmate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helter-skelter', 'adv.', 'In hurry and confusion; without definite purpose;\n   irregularly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helve', 'n.', 'The handle of an ax, hatchet, or adze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helve', 'n.', 'The lever at the end of which is the hammer head, in a forge\n   hammer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helve', 'n.', 'A forge hammer which is lifted by a cam acting on the helve\n   between the fulcrum and the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helved', 'imp. & p. p.', 'of Helve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helving', 'p. pr. & vb. n.', 'of Helve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helve', 'v. t.', 'To furnish with a helve, as an ax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helvetian', 'a.', 'Same as Helvetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helvetian', 'n.', 'A Swiss; a Switzer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helvetic', 'a.', 'Of or pertaining to the Helvetii, the ancient inhabitant\n   of the Alps, now Switzerland, or to the modern states and inhabitant of\n   the Alpine regions; as, the Helvetic confederacy; Helvetic states.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helvine', 'n.', 'Alt. of Helvite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Helvite', 'n.', 'A mineral of a yellowish color, consisting chiefly of\n   silica, glucina, manganese, and iron, with a little sulphur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hem', 'pron.', 'Them');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hem', 'interj.', 'An onomatopoetic word used as an expression of\n   hesitation, doubt, etc. It is often a sort of voluntary half cough,\n   loud or subdued, and would perhaps be better expressed by hm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hem', 'n.', 'An utterance or sound of the voice, hem or hm, often\n   indicative of hesitation or doubt, sometimes used to call attention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hem', 'v. i.', 'To make the sound expressed by the word hem; hence, to\n   hesitate in speaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hem', 'n.', 'The edge or border of a garment or cloth, doubled over and\n   sewed, to strengthen raveling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hem', 'n.', 'Border; edge; margin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hem', 'n.', 'A border made on sheet-metal ware by doubling over the edge of\n   the sheet, to stiffen it and remove the sharp edge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemmed', 'imp. & p. p.', 'of Hem');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemming', 'p. pr. & vb. n.', 'of Hem');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hem', 'v. t.', 'To form a hem or border to; to fold and sew down the edge\n   of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hem', 'v. t.', 'To border; to edge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hema-', '', 'Same as Haema-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemachate', 'n.', 'A species of agate, sprinkled with spots of red jasper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemachrome', 'n.', 'Same as Haemachrome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemacite', 'n.', 'A composition made from blood, mixed with mineral or\n   vegetable substances, used for making buttons, door knobs, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemadrometer', 'n.', 'Alt. of Hemadromometer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemadromometer', 'n.', 'An instrument for measuring the velocity with which\n   the blood moves in the arteries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemadrometry', 'n.', 'Alt. of Hemadromometry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemadromometry', 'n.', 'The act of measuring the velocity with which the\n   blood circulates in the arteries; haemotachometry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemadynamics', 'n.', 'The principles of dynamics in their application to\n   the blood; that part of science which treats of the motion of the\n   blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemadynamometer', 'n.', 'An instrument by which the pressure of the blood\n   in the arteries, or veins, is measured by the height to which it will\n   raise a column of mercury; -- called also a haemomanometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemal', 'a.', 'Relating to the blood or blood vessels; pertaining to,\n   situated in the region of, or on the side with, the heart and great\n   blood vessels; -- opposed to neural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemaphaein', 'n.', 'Same as Haemaphaein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemapophyses', 'pl. ', 'of Hemapophysis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemapophysis', 'n.', 'The second element in each half of a hemal arch,\n   corresponding to the sternal part of a rib.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemastatic', 'a. & n.', 'Alt. of Hemastatical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemastatical', 'a. & n.', 'Same as Hemostatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemastatics', 'n.', 'Laws relating to the equilibrium of the blood in the\n   blood vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hematachometer', 'n.', 'Same as Haematachometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hematein', 'n.', 'A reddish brown or violet crystalline substance,\n   C16H12O6, got from hematoxylin by partial oxidation, and regarded as\n   analogous to the phthaleins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hematemesis', 'n.', 'A vomiting of blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hematherm', 'n.', 'A warm-blooded animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemathermal', 'a.', 'Warm-blooded; hematothermal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hematic', 'a.', 'Same as Haematic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hematic', 'n.', 'A medicine designed to improve the condition of the blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hematin', 'n.', 'Hematoxylin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hematin', 'n.', 'A bluish black, amorphous substance containing iron and\n   obtained from blood. It exists the red blood corpuscles united with\n   globulin, and the form of hemoglobin or oxyhemoglobin gives to the\n   blood its red color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hematinometer', 'n.', 'A form of hemoglobinometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hematinometric', 'a.', 'Relating to the measurement of the amount of\n   hematin or hemoglobin contained in blood, or other fluids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hematinon', 'n.', 'A red consisting of silica, borax, and soda, fused with\n   oxide of copper and iron, and used in enamels, mosaics, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hematite', 'n.', 'An important ore of iron, the sesquioxide, so called\n   because of the red color of the powder. It occurs in splendent\n   rhombohedral crystals, and in massive and earthy forms; -- the last\n   called red ocher. Called also specular iron, oligist iron, rhombohedral\n   iron ore, and bloodstone. See Brown hematite, under Brown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hematitic', 'a.', 'Of or pertaining to hematite, or resembling it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemato', '', 'See Haema-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hematocele', 'n.', 'A tumor filled with blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hematocrya', 'n. pl.', 'The cold-blooded vertebrates, that is, all but the\n   mammals and birds; -- the antithesis to Hematotherma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hematocrystallin', 'n.', 'See Hemoglobin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hematoid', 'a.', 'Resembling blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hematoidin', 'n.', 'A crystalline or amorphous pigment, free from iron,\n   formed from hematin in old blood stains, and in old hemorrhages in the\n   body. It resembles bilirubin. When present in the corpora lutea it is\n   called haemolutein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hematology', 'n.', 'The science which treats of the blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hematoma', 'n.', 'A circumscribed swelling produced by an effusion of blood\n   beneath the skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hematophilia', 'n.', 'A condition characterized by a tendency to profuse\n   and uncontrollable hemorrhage from the slightest wounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hematosin', 'n.', 'The hematin of blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hematosis', 'n.', 'Sanguification; the conversion of chyle into blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hematosis', 'n.', 'The arterialization of the blood in the lungs; the\n   formation of blood in general; haematogenesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hematotherma', 'n. pl.', 'The warm-blooded vertebrates, comprising the\n   mammals and birds; -- the antithesis to hematocrya.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hematothermal', 'a.', 'Warm-blooded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hematoxylin', 'n.', 'Haematoxylin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hematuria', 'n.', 'Passage of urine mingled with blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemautography', 'n.', 'The obtaining of a curve similar to a pulse curve or\n   sphygmogram by allowing the blood from a divided artery to strike\n   against a piece of paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemelytra', 'pl. ', 'of Hemelytrum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemelytron', 'n.', 'Alt. of Hemelytrum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemelytrum', 'n.', 'One of the partially thickened anterior wings of\n   certain insects, as of many Hemiptera, the earwigs, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemeralopia', 'n.', 'A disease of the eyes, in consequence of which a\n   person can see clearly or without pain only by daylight or a strong\n   artificial light; day sight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemerobian', 'n.', 'A neuropterous insect of the genus Hemerobius, and\n   allied genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemerobid', 'a.', 'Of relating to the hemerobians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemerocallis', 'n.', 'A genus of plants, some species of which are\n   cultivated for their beautiful flowers; day lily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemi-', '', 'A prefix signifying half.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemialbumin', 'n.', 'Same as Hemialbumose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemialbumose', 'n.', 'An albuminous substance formed in gastric digestion,\n   and by the action of boiling dilute acids on albumin. It is readily\n   convertible into hemipeptone. Called also hemialbumin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemianaesthesia', 'n.', 'Anaesthesia upon one side of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemibranchi', 'n. pl.', 'An order of fishes having an incomplete or reduced\n   branchial apparatus. It includes the sticklebacks, the flutemouths, and\n   Fistularia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemicardia', 'n.', 'A lateral half of the heart, either the right or left.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemicarp', 'n.', 'One portion of a fruit that spontaneously divides into\n   halves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemicerebrum', 'n.', 'A lateral half of the cerebrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemicollin', 'n.', 'See Semiglutin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemicrania', 'n.', 'A pain that affects only one side of the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemicrany', 'n.', 'Hemicranis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemicycle', 'n.', 'A half circle; a semicircle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemicycle', 'n.', 'A semicircular place, as a semicircular arena, or room,\n   or part of a room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemidactyl', 'n.', 'Any species of Old World geckoes of the genus\n   Hemidactylus. The hemidactyls have dilated toes, with two rows of\n   plates beneath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemi-demi-semiquaver', 'n.', 'A short note, equal to one fourth of a\n   semiquaver, or the sixty-fourth part of a whole note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemiditone', 'n.', 'The lesser third.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemigamous', 'a.', 'Having one of the two florets in the same spikelet\n   neuter, and the other unisexual, whether male or female; -- said of\n   grasses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemiglyph', 'n.', 'The half channel or groove in the edge of the triglyph\n   in the Doric order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemihedral', 'a.', 'Having half of the similar parts of a crystals, instead\n   of all; consisting of half the planes which full symmetry would\n   require, as when a cube has planes only on half of its eight solid\n   angles, or one plane out of a pair on each of its edges; or as in the\n   case of a tetrahedron, which is hemihedral to an octahedron, it being\n   contained under four of the planes of an octahedron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemihedrism', 'n.', 'The property of crystallizing hemihedrally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemihedron', 'n.', 'A solid hemihedrally derived. The tetrahedron is a\n   hemihedron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemiholohedral', 'a.', 'Presenting hemihedral forms, in which half the\n   sectants have the full number of planes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemimellitic', 'a.', 'Having half as many (three) carboxyl radicals as\n   mellitic acid; -- said of an organic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemimetabola', 'n. pl.', 'Those insects which have an incomplete\n   metamorphosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemimetabolic', 'a.', 'Having an incomplete metamorphosis, the larvae\n   differing from the adults chiefly in laking wings, as in the\n   grasshoppers and cockroaches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemimorphic', 'a.', 'Having the two ends modified with unlike planes; --\n   said of a crystal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemin', 'n.', 'A substance, in the form of reddish brown, microscopic,\n   prismatic crystals, formed from dried blood by the action of strong\n   acetic acid and common salt; -- called also Teichmanns crystals.\n   Chemically, it is a hydrochloride of hematin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heminae', 'pl. ', 'of Hemina');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemina', 'n.', 'A measure of half a sextary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemina', 'n.', 'A measure equal to about ten fluid ounces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemionus', 'n.', 'A wild ass found in Thibet; the kiang.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemiopia', 'n.', 'Alt. of Hemiopsia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemiopsia', 'n.', 'A defect of vision in consequence of which a person sees\n   but half of an object looked at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemiorthotype', 'a.', 'Same as Monoclinic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemipeptone', 'n.', 'A product of the gastric and pancreatic digestion of\n   albuminous matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemiplegia', 'n.', 'A palsy that affects one side only of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemiplegy', 'n.', 'Hemiplegia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemipode', 'n.', 'Any bird of the genus Turnix. Various species inhabit\n   Asia, Africa, and Australia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemiprotein', 'n.', 'An insoluble, proteid substance, described by\n   Schutzenberger, formed when albumin is heated for some time with dilute\n   sulphuric acid. It is apparently identical with antialbumid and\n   dyspeptone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemipter', 'n.', 'One of the Hemiptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemiptera', 'n. pl.', 'An order of hexapod insects having a jointed\n   proboscis, including four sharp stylets (mandibles and maxillae), for\n   piercing. In many of the species (Heteroptera) the front wings are\n   partially coriaceous, and different from the others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemipteral', 'a.', 'Alt. of Hemipterous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemipterous', 'a.', 'Of or pertaining to the Hemiptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemipteran', 'n.', 'One of the Hemiptera; an hemipter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemisected', 'imp. & p. p.', 'of Hemisect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemisecting', 'p. pr. & vb. n.', 'of Hemisect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemisect', 'v. t.', 'To divide along the mesial plane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemisection', 'n.', 'A division along the mesial plane; also, one of the\n   parts so divided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemisphere', 'n.', 'A half sphere; one half of a sphere or globe, when\n   divided by a plane passing through its center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemisphere', 'n.', 'Half of the terrestrial globe, or a projection of the\n   same in a map or picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemisphere', 'n.', 'The people who inhabit a hemisphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemispheric', 'a.', 'Alt. of Hemispherical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemispherical', 'a.', 'Containing, or pertaining to, a hemisphere; as, a\n   hemispheric figure or form; a hemispherical body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemispheroid', 'n.', 'A half of a spheroid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemispheroidal', 'a.', 'Resembling, or approximating to, a hemisphere in\n   form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemispherule', 'n.', 'A half spherule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemistich', 'n.', 'Half a poetic verse or line, or a verse or line not\n   completed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemistichal', 'a.', 'Pertaining to, or written in, hemistichs; also, by, or\n   according to, hemistichs; as, a hemistichal division of a verse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemisystole', 'n.', 'Contraction of only one ventricle of the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemitone', 'n.', 'See Semitone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemitropal', 'a.', 'Alt. of Hemitropous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemitropous', 'a.', 'Turned half round; half inverted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemitropous', 'a.', 'Having the raphe terminating about half way between\n   the chalaza and the orifice; amphitropous; -- said of an ovule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemitrope', 'a.', 'Half turned round; half inverted; (Crystallog.) having a\n   twinned structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemitrope', 'n.', 'That which is hemitropal in construction; (Crystallog.)\n   a twin crystal having a hemitropal structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemitropy', 'n.', 'Twin composition in crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemlock', 'n.', 'The name of several poisonous umbelliferous herbs having\n   finely cut leaves and small white flowers, as the Cicuta maculata,\n   bulbifera, and virosa, and the Conium maculatum. See Conium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemlock', 'n.', 'An evergreen tree common in North America (Abies, / Tsuga,\n   Canadensis); hemlock spruce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemlock', 'n.', 'The wood or timber of the hemlock tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemmel', 'n.', 'A shed or hovel for cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemmer', 'n.', 'One who, or that which, hems with a needle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemmer', 'n.', 'An attachment to a sewing machine, for turning under the\n   edge of a piece of fabric, preparatory to stitching it down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemmer', 'n.', 'A tool for turning over the edge of sheet metal to make a\n   hem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemo-', '', 'Same as Haema-, Haemo-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemoglobin', 'n.', 'The normal coloring matter of the red blood corpuscles\n   of vertebrate animals. It is composed of hematin and globulin, and is\n   also called haematoglobulin. In arterial blood, it is always combined\n   with oxygen, and is then called oxyhemoglobin. It crystallizes under\n   different forms from different animals, and when crystallized, is\n   called haematocrystallin. See Blood crystal, under Blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemoglobinometer', 'n.', 'Same as Haemochromometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemophilia', 'n.', 'See Hematophilia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemoptysis', 'n.', 'The expectoration of blood, due usually to hemorrhage\n   from the mucous membrane of the lungs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemorrhage', 'n.', 'Any discharge of blood from the blood vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemorrhagic', 'a.', 'Pertaining or tending to a flux of blood; consisting\n   in, or accompanied by, hemorrhage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemorrhoidal', 'a.', 'Of or pertaining to, or of the nature of,\n   hemorrhoids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemorrhoidal', 'a.', 'Of or pertaining to the rectum; rectal; as, the\n   hemorrhoidal arteries, veins, and nerves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemorrhoids', 'n. pl.', 'Livid and painful swellings formed by the dilation\n   of the blood vessels around the margin of, or within, the anus, from\n   which blood or mucus is occasionally discharged; piles; emerods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemostatic', 'a.', 'Of or relating to stagnation of the blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemostatic', 'a.', 'Serving to arrest hemorrhage; styptic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemostatic', 'n.', 'A medicine or application to arrest hemorrhage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemoothorax', 'n.', 'An effusion of blood into the cavity of the pleura.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemp', 'n.', 'A plant of the genus Cannabis (C. sativa), the fibrous skin\n   or bark of which is used for making cloth and cordage. The name is also\n   applied to various other plants yielding fiber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemp', 'n.', 'The fiber of the skin or rind of the plant, prepared for\n   spinning. The name has also been extended to various fibers resembling\n   the true hemp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hempen', 'a.', 'Made of hemp; as, a hempen cord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hempen', 'a.', 'Like hemp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hempy', 'a.', 'Like hemp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemself', 'pron.', 'Alt. of Hemselven');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemselven', 'pron.', 'Themselves; -- used reflexively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemstitched', 'imp. & p. p.', 'of Hemstitch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemstitching', 'p. pr. & vb. n.', 'of Hemstitch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemstitch', 'v. t.', 'To ornament at the head of a broad hem by drawing out\n   a few parallel threads, and fastening the cross threads in successive\n   small clusters; as, to hemstitch a handkerchief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemstitched', 'a.', 'Having a broad hem separated from the body of the\n   article by a line of open work; as, a hemistitched handkerchief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hemuse', 'n.', 'The roebuck in its third year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hen', 'n.', 'The female of the domestic fowl; also, the female of grouse,\n   pheasants, or any kind of birds; as, the heath hen; the gray hen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Henbane', 'n.', 'A plant of the genus Hyoscyamus (H. niger). All parts of\n   the plant are poisonous, and the leaves are used for the same purposes\n   as belladonna. It is poisonous to domestic fowls; whence the name.\n   Called also, stinking nightshade, from the fetid odor of the plant. See\n   Hyoscyamus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Henbit', 'n.', 'A weed of the genus Lamium (L. amplexicaule) with deeply\n   crenate leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hence', 'adv.', 'From this place; away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hence', 'adv.', 'From this time; in the future; as, a week hence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hence', 'adv.', 'From this reason; as an inference or deduction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hence', 'adv.', 'From this source or origin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hence', 'v. t.', 'To send away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Henceforth', 'adv.', 'From this time forward; henceforward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Henceforward', 'adv.', 'From this time forward; henceforth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Henchboy', 'n.', 'A page; a servant.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-men', 'pl. ', 'of Henchman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Henchman', 'n.', 'An attendant; a servant; a follower. Now chiefly used as\n   a political cant term.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hencoop', 'n.', 'A coop or cage for hens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hende', 'a.', 'Skillful; dexterous; clever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hende', 'a.', 'Friendly; civil; gentle; kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hendecagon', 'n.', 'A plane figure of eleven sides and eleven angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hendecane', 'n.', 'A hydrocarbon, C11H24, of the paraffin series; -- so\n   called because it has eleven atoms of carbon in each molecule. Called\n   also endecane, undecane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hendecasyllabic', 'a.', 'Pertaining to a line of eleven syllables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hendecasyllable', 'n.', 'A metrical line of eleven syllables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hendecatoic', 'a.', 'Undecylic; pertaining to, or derived from, hendecane;\n   as, hendecatoic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hendiadys', 'n.', 'A figure in which the idea is expressed by two nouns\n   connected by and, instead of by a noun and limiting adjective; as, we\n   drink from cups and gold, for golden cups.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hendy', 'a.', 'See Hende.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Henen', 'adv.', 'Hence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Henfish', 'n.', 'A marine fish; the sea bream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Henfish', 'n.', 'A young bib. See Bib, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heng', 'imp.', 'Hung.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hen-hearted', 'a.', 'Cowardly; timid; chicken-hearted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Henhouses', 'pl. ', 'of Henhouse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Henhouse', 'n.', 'A house or shelter for fowls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Henhussy', 'n.', 'A cotquean; a man who intermeddles with womens concerns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heniquen', 'n.', 'See Jeniquen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Henna', 'n.', 'A thorny tree or shrub of the genus Lawsonia (L. alba). The\n   fragrant white blossoms are used by the Buddhists in religious\n   ceremonies. The powdered leaves furnish a red coloring matter used in\n   the East to stain the hails and fingers, the manes of horses, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Henna', 'n.', 'The leaves of the henna plant, or a preparation or dyestuff\n   made from them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hennery', 'n.', 'An inclosed place for keeping hens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hennes', 'adv.', 'Hence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hennotannic', 'a.', 'Pertaining to, or designating, a brown resinous\n   substance resembling tannin, and extracted from the henna plant; as,\n   hennotannic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Henoge ny', 'n.', 'Alt. of Henogenesis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Henogenesis', 'n.', 'Same as Ontogeny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Henotheism', 'n.', 'Primitive religion in which each of several divinities\n   is regarded as independent, and is worshiped without reference to the\n   rest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Henotic', 'a.', 'Harmonizing; irenic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Henpecked', 'imp. & p. p.', 'of Henpeck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Henpecking', 'p. pr. & vb.', 'of Henpeck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Henpeck', 'v. t.', 'To subject to petty authority; -- said of a wife who\n   thus treats her husband. Commonly used in the past participle (often\n   adjectively).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Henroost', 'n.', 'A place where hens roost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Henrys', 'pl. ', 'of Henry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Henry', 'n.', 'The unit of electric induction; the induction in a circuit\n   when the electro-motive force induced in this circuit is one volt,\n   while the inducing current varies at the rate of one ampere a second.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hens-foot', 'n.', 'An umbelliferous plant (Caucalis daucoides).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hente', 'imp.', 'of Hent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hent', 'p. p.', 'of Hent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hent', 'v. t.', 'To seize; to lay hold on; to catch; to get.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Henware', 'n.', 'A coarse, blackish seaweed. See Badderlocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Henxman', 'n.', 'Henchman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hep', 'n.', 'See Hip, the fruit of the dog-rose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hepar', 'n.', 'Liver of sulphur; a substance of a liver-brown color,\n   sometimes used in medicine. It is formed by fusing sulphur with\n   carbonates of the alkalies (esp. potassium), and consists essentially\n   of alkaline sulphides. Called also hepar sulphuris (/).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hepar', 'n.', 'Any substance resembling hepar proper, in appearance;\n   specifically, in homeopathy, calcium sulphide, called also hepar\n   sulphuris calcareum (/).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hepatic', 'a.', 'Of or pertaining to the liver; as, hepatic artery; hepatic\n   diseases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hepatic', 'a.', 'Resembling the liver in color or in form; as, hepatic\n   cinnabar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hepatic', 'a.', 'Pertaining to, or resembling, the plants called Hepaticae,\n   or scale mosses and liverworts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hepaticae', 'pl. ', 'of Hepatica');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hepatica', 'n.', 'A genus of pretty spring flowers closely related to\n   Anemone; squirrel cup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hepatica', 'n.', 'Any plant, usually procumbent and mosslike, of the\n   cryptogamous class Hepaticae; -- called also scale moss and liverwort.\n   See Hepaticae, in the Supplement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hepatical', 'a.', 'Hepatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hepatite', 'n.', 'A variety of barite emitting a fetid odor when rubbed or\n   heated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hepatitis', 'n.', 'Inflammation of the liver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hepatization', 'n.', 'Impregnating with sulphureted hydrogen gas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hepatization', 'n.', 'Conversion into a substance resembling the liver; a\n   state of the lungs when gorged with effused matter, so that they are no\n   longer pervious to the air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hepatized', 'imp. & p. p.', 'of Hepatize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hepatizing', 'p. pr. & vb. n.', 'of Hepatize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hepatize', 'v. t.', 'To impregnate with sulphureted hydrogen gas, formerly\n   called hepatic gas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hepatize', 'v. t.', 'To gorge with effused matter, as the lungs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hepatocele', 'n.', 'Hernia of the liver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hepatocystic', 'a.', 'Of or pertaining to the liver and gall bladder; as,\n   the hepatocystic ducts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hepatogastric', 'a.', 'See Gastrohepatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hepatogenic', 'a.', 'Alt. of Hepatogenous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hepatogenous', 'a.', 'Arising from the liver; due to a condition of the\n   liver; as, hepatogenic jaundice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hepatology', 'n.', 'The science which treats of the liver; a treatise on\n   the liver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hepato-pancreas', 'n.', 'A digestive gland in Crustacea, Mollusca, etc.,\n   usually called the liver, but different from the liver of vertebrates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hepatorenal', 'a.', 'Of or pertaining to the liver and kidneys; as, the\n   hepatorenal ligament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hepatoscopy', 'n.', 'Divination by inspecting the liver of animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heppen', 'a.', 'Neat; fit; comfortable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hepper', 'n.', 'A young salmon; a parr.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hepta', '', 'A combining form from Gr. \"epta`, seven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heptachord', 'n.', 'A system of seven sounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heptachord', 'n.', 'A lyre with seven chords.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heptachord', 'n.', 'A composition sung to the sound of seven chords or\n   tones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heptad', 'n.', 'An atom which has a valence of seven, and which can be\n   theoretically combined with, substituted for, or replaced by, seven\n   monad atoms or radicals; as, iodine is a heptad in iodic acid. Also\n   used as an adjective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heptade', 'n.', 'The sum or number of seven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heptaglot', 'n.', 'A book in seven languages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heptagon', 'n.', 'A plane figure consisting of seven sides and having seven\n   angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heptagonal', 'a.', 'Having seven angles or sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heptagynia', 'n. pl.', 'A Linnaean order of plants having seven pistils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heptagynian', 'a.', 'Alt. of Heptagynous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heptagynous', 'a.', 'Having seven pistils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heptahedron', 'n.', 'A solid figure with seven sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heptamerous', 'a.', 'Consisting of seven parts, or having the parts in sets\n   of sevens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heptandria', 'n. pl.', 'A Linnaean class of plants having seven stamens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heptandrian', 'a.', 'Alt. of Heptandrous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heptandrous', 'a.', 'Having seven stamens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heptane', 'n.', 'Any one of several isometric hydrocarbons, C7H16, of the\n   paraffin series (nine are possible, four are known); -- so called\n   because the molecule has seven carbon atoms. Specifically, a colorless\n   liquid, found as a constituent of petroleum, in the tar oil of cannel\n   coal, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heptangular', 'a.', 'Having seven angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heptaphyllous', 'a.', 'Having seven leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heptarch', 'n.', 'Same as Heptarchist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heptarchic', 'a.', 'Of or pertaining to a heptarchy; constituting or\n   consisting of a heptarchy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heptarchist', 'n.', 'A ruler of one division of a heptarchy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heptarchy', 'n.', 'A government by seven persons; also, a country under\n   seven rulers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heptaspermous', 'a.', 'Having seven seeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heptastich', 'n.', 'A composition consisting of seven lines or verses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heptateuch', 'n.', 'The first seven books of the Testament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heptavalent', 'a.', 'Having seven units of attractive force or affinity; --\n   said of heptad elements or radicals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heptene', 'n.', 'Same as Heptylene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heptine', 'n.', 'Any one of a series of unsaturated metameric hydrocarbons,\n   C7H12, of the acetylene series.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heptoic', 'a.', 'Pertaining to, or derived from, heptane; as, heptoic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heptone', 'n.', 'A liquid hydrocarbon, C7H10, of the valylene series.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hep tree', '', 'The wild dog-rose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heptyl', 'n.', 'A compound radical, C7H15, regarded as the essential\n   radical of heptane and a related series of compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heptylene', 'n.', 'A colorless liquid hydrocarbon, C7H14, of the ethylene\n   series; also, any one of its isomers. Called also heptene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heptylic', 'a.', 'Pertaining to, or derived from, heptyl or heptane; as,\n   heptylic alcohol. Cf. /nanthylic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Her', 'pron. & a.', 'The form of the objective and the possessive case of\n   the personal pronoun she; as, I saw her with her purse out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Her', 'pron. pl.', 'Alt. of Here');
  INSERT INTO words (word, word_type, description) VALUES  (    'Here', 'pron. pl.', 'Of them; their.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heracleonite', 'n.', 'A follower of Heracleon of Alexandria, a Judaizing\n   Gnostic, in the early history of the Christian church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herakline', 'n.', 'A picrate compound, used as an explosive in blasting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herald', 'n.', 'An officer whose business was to denounce or proclaim war,\n   to challenge to battle, to proclaim peace, and to bear messages from\n   the commander of an army. He was invested with a sacred and inviolable\n   character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herald', 'n.', 'In the Middle Ages, the officer charged with the above\n   duties, and also with the care of genealogies, of the rights and\n   privileges of noble families, and especially of armorial bearings. In\n   modern times, some vestiges of this office remain, especially in\n   England. See Heralds College (below), and King-at-Arms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herald', 'n.', 'A proclaimer; one who, or that which, publishes or\n   announces; as, the herald of anothers fame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herald', 'n.', 'A forerunner; a a precursor; a harbinger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herald', 'n.', 'Any messenger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heralded', 'imp. & p. p.', 'of Herald');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heralding', 'p. pr. & vb. n.', 'of Herald');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herald', 'v. t.', 'To introduce, or give tidings of, as by a herald; to\n   proclaim; to announce; to foretell; to usher in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heraldic', 'a.', 'Of or pertaining to heralds or heraldry; as, heraldic\n   blazoning; heraldic language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heraldically', 'adv.', 'In an heraldic manner; according to the rules of\n   heraldry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heraldry', 'n.', 'The art or office of a herald; the art, practice, or\n   science of recording genealogies, and blazoning arms or ensigns\n   armorial; also, of marshaling cavalcades, processions, and public\n   ceremonies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heraldship', 'n.', 'The office of a herald.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herapathite', 'n.', 'The sulphate of iodoquinine, a substance crystallizing\n   in thin plates remarkable for their effects in polarizing light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heraud', 'n.', 'A herald.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herb', 'n.', 'A plant whose stem does not become woody and permanent, but\n   dies, at least down to the ground, after flowering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herb', 'n.', 'Grass; herbage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herbaceous', 'a.', 'Of or pertaining to herbs; having the nature, texture,\n   or characteristics, of an herb; as, herbaceous plants; an herbaceous\n   stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herbage', 'n.', 'Herbs collectively; green food beasts; grass; pasture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herbage', 'n.', 'The liberty or right of pasture in the forest or in the\n   grounds of another man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herbaged', 'a.', 'Covered with grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herbal', 'a.', 'Of or pertaining to herbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herbal', 'n.', 'A book containing the names and descriptions of plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herbal', 'n.', 'A collection of specimens of plants, dried and preserved; a\n   hortus siccus; an herbarium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herbalism', 'n.', 'The knowledge of herbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herbalist', 'n.', 'One skilled in the knowledge of plants; a collector of,\n   or dealer in, herbs, especially medicinal herbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herbar', 'n.', 'An herb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herbarian', 'n.', 'A herbalist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herbarist', 'n.', 'A herbalist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herbariums', 'pl. ', 'of Herbarium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herbaria', 'pl. ', 'of Herbarium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herbarium', 'n.', 'A collection of dried specimens of plants,\n   systematically arranged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herbarium', 'n.', 'A book or case for preserving dried plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herbarize', 'v. t.', 'See Herborize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herbary', 'n.', 'A garden of herbs; a cottage garden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herber', 'n.', 'A garden; a pleasure garden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herbergage', 'n.', 'Harborage; lodging; shelter; harbor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herbergeour', 'n.', 'A harbinger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herbergh', 'n.', 'Alt. of Herberwe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herberwe', 'n.', 'A harbor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herbescent', 'a.', 'Growing into herbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herbid', 'a.', 'Covered with herbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herbiferous', 'a.', 'Bearing herbs or vegetation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herbist', 'n.', 'A herbalist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herbivora', 'n. pl.', 'An extensive division of Mammalia. It formerly\n   included the Proboscidea, Hyracoidea, Perissodactyla, and Artiodactyla,\n   but by later writers it is generally restricted to the two latter\n   groups (Ungulata). They feed almost exclusively upon vegetation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herbivore', 'n.', 'One of the Herbivora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herbivorous', 'a.', 'Eating plants; of or pertaining to the Herbivora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herbless', 'a.', 'Destitute of herbs or of vegetation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herblet', 'n.', 'A small herb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herborist', 'n.', 'A herbalist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herborization', 'n.', 'The act of herborizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herborization', 'n.', 'The figure of plants in minerals or fossils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herborized', 'imp. & p. p.', 'of Herborize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herborizing', 'p. pr. & vb. n.', 'of Herborize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herborize', 'v. i.', 'To search for plants, or new species of plants, with\n   a view to classifying them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herborize', 'v. t.', 'To form the figures of plants in; -- said in\n   reference to minerals. See Arborized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herborough', 'n.', 'A harbor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herbose', 'a.', 'Alt. of Herbous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herbous', 'a.', 'Abounding with herbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herb-women', 'pl. ', 'of Herb-woman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herb-woman', 'n.', 'A woman that sells herbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herby', 'a.', 'Having the nature of, pertaining to, or covered with, herbs\n   or herbage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hercogamous', 'a.', 'Not capable of self-fertilization; -- said of\n   hermaphrodite flowers in which some structural obstacle forbids\n   autogamy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herculean', 'a.', 'Requiring the strength of Hercules; hence, very great,\n   difficult, or dangerous; as, an Herculean task.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herculean', 'a.', 'Having extraordinary strength or size; as, Herculean\n   limbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hercules', 'n.', 'A hero, fabled to have been the son of Jupiter and\n   Alcmena, and celebrated for great strength, esp. for the accomplishment\n   of his twelve great tasks or \"labors.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hercules', 'n.', 'A constellation in the northern hemisphere, near Lyra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hercynian', 'a.', 'Of or pertaining to an extensive forest in Germany, of\n   which there are still portions in Swabia and the Hartz mountains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herd', 'a.', 'Haired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herd', 'n.', 'A number of beasts assembled together; as, a herd of horses,\n   oxen, cattle, camels, elephants, deer, or swine; a particular stock or\n   family of cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herd', 'n.', 'A crowd of low people; a rabble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herd', 'n.', 'One who herds or assembles domestic animals; a herdsman; --\n   much used in composition; as, a shepherd; a goatherd, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herded', 'imp. & p. p.', 'of Herd');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herding', 'p. pr. & vb. n.', 'of Herd');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herd', 'v. i.', 'To unite or associate in a herd; to feed or run together,\n   or in company; as, sheep herd on many hills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herd', 'v. i.', 'To associate; to ally ones self with, or place ones self\n   among, a group or company.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herd', 'v. i.', 'To act as a herdsman or a shepherd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herd', 'v. t.', 'To form or put into a herd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herdbook', 'n.', 'A book containing the list and pedigrees of one or more\n   herds of choice breeds of cattle; -- also called herd record, or herd\n   register.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herder', 'n.', 'A herdsman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herderite', 'n.', 'A rare fluophosphate of glucina, in small white\n   crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herdess', 'n.', 'A shepherdess; a female herder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herdgroom', 'n.', 'A herdsman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herdic', 'n.', 'A kind of low-hung cab.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-men', 'pl. ', 'of Herdsman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herdman', 'n.', 'Alt. of Herdsman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herdsman', 'n.', 'The owner or keeper of a herd or of herds; one employed\n   in tending a herd of cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'women', 'pl. ', 'of Herdswoman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herdswoman', 'n.', 'A woman who tends a herd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Here', 'n.', 'Hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Here', 'pron.', 'See Her, their.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Here', 'pron.', 'Her; hers. See Her.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Here', 'adv.', 'In this place; in the place where the speaker is; --\n   opposed to there.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Here', 'adv.', 'In the present life or state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Here', 'adv.', 'To or into this place; hither. [Colloq.] See Thither.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Here', 'adv.', 'At this point of time, or of an argument; now.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herea-bout', 'adv.', 'Alt. of Hereabouts');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hereabouts', 'adv.', 'About this place; in this vicinity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hereabouts', 'adv.', 'Concerning this.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hereafter', 'adv.', 'In time to come; in some future time or state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hereafter', 'n.', 'A future existence or state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hereafterward', 'adv.', 'Hereafter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Here-at', 'adv.', 'At, or by reason of, this; as, he was offended hereat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hereby', 'adv.', 'By means of this.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hereby', 'adv.', 'Close by; very near.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hereditability', 'n.', 'State of being hereditable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hereditable', 'a.', 'Capable of being inherited. See Inheritable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hereditable', 'a.', 'Qualified to inherit; capable of inheriting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hereditably', 'adv.', 'By inheritance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hereditament', 'n.', 'Any species of property that may be inherited; lands,\n   tenements, anything corporeal or incorporeal, real, personal, or mixed,\n   that may descend to an heir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hereditarily', 'adv.', 'By inheritance; in an hereditary manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hereditary', 'a.', 'Descended, or capable of descending, from an ancestor\n   to an heir at law; received or passing by inheritance, or that must\n   pass by inheritance; as, an hereditary estate or crown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hereditary', 'a.', 'Transmitted, or capable of being transmitted, as a\n   constitutional quality or condition from a parent to a child; as,\n   hereditary pride, bravery, disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heredity', 'n.', 'Hereditary transmission of the physical and psychical\n   qualities of parents to their offspring; the biological law by which\n   living beings tend to repeat their characteristics in their\n   descendants. See Pangenesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hereford', 'n.', 'One of a breed of cattle originating in Herefordshire,\n   England. The Herefords are good working animals, and their\n   beef-producing quality is excellent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herehence', 'adv.', 'From hence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herein', 'adv.', 'In this.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hereinafter', 'adv.', 'In the following part of this (writing, document,\n   speech, and the like).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hereinbefore', 'adv.', 'In the preceding part of this (writing, document,\n   book, etc.).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hereinto', 'adv.', 'Into this.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heremit', 'n.', 'Alt. of Heremite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heremite', 'n.', 'A hermit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heremitical', 'a.', 'Of or pertaining to a hermit; solitary; secluded from\n   society.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heren', 'a.', 'Made of hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hereof', 'adv.', 'Of this; concerning this; from this; hence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hereon', 'adv.', 'On or upon this; hereupon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hereout', 'adv.', 'Out of this.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heresiarch', 'n.', 'A leader in heresy; the chief of a sect of heretics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heresiarchy', 'n.', 'A chief or great heresy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heresiographer', 'n.', 'One who writes on heresies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heresiography', 'n.', 'A treatise on heresy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heresies', 'pl. ', 'of Heresy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heresy', 'n.', 'An opinion held in opposition to the established or\n   commonly received doctrine, and tending to promote a division or party,\n   as in politics, literature, philosophy, etc.; -- usually, but not\n   necessarily, said in reproach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heresy', 'n.', 'Religious opinion opposed to the authorized doctrinal\n   standards of any particular church, especially when tending to promote\n   schism or separation; lack of orthodox or sound belief; rejection of,\n   or erroneous belief in regard to, some fundamental religious doctrine\n   or truth; heterodoxy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heresy', 'n.', 'An offense against Christianity, consisting in a denial of\n   some essential doctrine, which denial is publicly avowed, and\n   obstinately maintained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heretic', 'n.', 'One who holds to a heresy; one who believes some doctrine\n   contrary to the established faith or prevailing religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heretic', 'n.', 'One who having made a profession of Christian belief,\n   deliberately and pertinaciously refuses to believe one or more of the\n   articles of faith \"determined by the authority of the universal\n   church.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heretical', 'a.', 'Containing heresy; of the nature of, or characterized\n   by, heresy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heretically', 'adv.', 'In an heretical manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hereticate', 'v. t.', 'To decide to be heresy or a heretic; to denounce as\n   a heretic or heretical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heretification', 'n.', 'The act of hereticating or pronouncing heretical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hereto', 'adv.', 'To this; hereunto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heretoch', 'n.', 'Alt. of Heretog');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heretog', 'n.', 'The leader or commander of an army; also, a marshal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heretofore', 'adv.', 'Up to this time; hitherto; before; in time past.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hereunto', 'adv.', 'Unto this; up to this time; hereto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hereupon', 'adv.', 'On this; hereon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herewith', 'adv.', 'With this.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herie', 'v. t.', 'To praise; to worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heriot', 'n.', 'Formerly, a payment or tribute of arms or military\n   accouterments, or the best beast, or chattel, due to the lord on the\n   death of a tenant; in modern use, a customary tribute of goods or\n   chattels to the lord of the fee, paid on the decease of a tenant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heriotable', 'a.', 'Subject to the payment of a heriot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herisson', 'n.', 'A beam or bar armed with iron spikes, and turning on a\n   pivot; -- used to block up a passage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heritability', 'n.', 'The state of being heritable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heritable', 'a.', 'Capable of being inherited or of passing by inheritance;\n   inheritable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heritable', 'a.', 'Capable of inheriting or receiving by inheritance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heritage', 'a.', 'That which is inherited, or passes from heir to heir;\n   inheritance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heritage', 'a.', 'A possession; the Israelites, as Gods chosen people;\n   also, a flock under pastoral charge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heritance', 'n.', 'Heritage; inheritance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heritor', 'n.', 'A proprietor or landholder in a parish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herl', 'n.', 'Same as Harl, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herling', 'n.', 'Alt. of Hirling');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hirling', 'n.', 'The young of the sea trout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hermae', 'pl. ', 'of Herma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herma', 'n.', 'See Hermes, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hermaphrodeity', 'n.', 'Hermaphrodism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hermaphrodism', 'n.', 'See Hermaphroditism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hermaphrodite', 'n.', 'An individual which has the attributes of both male\n   and female, or which unites in itself the two sexes; an animal or plant\n   having the parts of generation of both sexes, as when a flower contains\n   both the stamens and pistil within the same calyx, or on the same\n   receptacle. In some cases reproduction may take place without the union\n   of the distinct individuals. In the animal kingdom true hermaphrodites\n   are found only among the invertebrates. See Illust. in Appendix, under\n   Helminths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hermaphrodite', 'a.', 'Including, or being of, both sexes; as, an\n   hermaphrodite animal or flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hermaphroditic', 'a.', 'Alt. of Hermaphroditical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hermaphroditical', 'a.', 'Partaking of the characteristics of both sexes;\n   characterized by hermaphroditism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hermaphroditism', 'n.', 'The union of the two sexes in the same individual,\n   or the combination of some of their characteristics or organs in one\n   individual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hermeneutic', 'a.', 'Alt. of Hermeneutical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hermeneutical', 'a.', 'Unfolding the signification; of or pertaining to\n   interpretation; exegetical; explanatory; as, hermeneutic theology, or\n   the art of expounding the Scriptures; a hermeneutic phrase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hermeneutically', 'adv.', 'According to the principles of interpretation;\n   as, a verse of Scripture was examined hermeneutically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hermeneutics', 'n.', 'The science of interpretation and explanation;\n   exegesis; esp., that branch of theology which defines the laws whereby\n   the meaning of the Scriptures is to be ascertained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hermes', 'n.', 'See Mercury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hermes', 'n.', 'Originally, a boundary stone dedicated to Hermes as the god\n   of boundaries, and therefore bearing in some cases a head, or head and\n   shoulders, placed upon a quadrangular pillar whose height is that of\n   the body belonging to the head, sometimes having feet or other parts of\n   the body sculptured upon it. These figures, though often representing\n   Hermes, were used for other divinities, and even, in later times, for\n   portraits of human beings. Called also herma. See Terminal statue,\n   under Terminal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hermetic', 'a.', 'Alt. of Hermetical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hermetical', 'a.', 'Of, pertaining to, or taught by, Hermes Trismegistus;\n   as, hermetic philosophy. Hence: Alchemical; chemic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hermetical', 'a.', 'Of or pertaining to the system which explains the\n   causes of diseases and the operations of medicine on the principles of\n   the hermetic philosophy, and which made much use, as a remedy, of an\n   alkali and an acid; as, hermetic medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hermetical', 'a.', 'Made perfectly close or air-tight by fusion, so that no\n   gas or spirit can enter or escape; as, an hermetic seal. See Note under\n   Hermetically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hermetically', 'adv.', 'In an hermetical manner; chemically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hermetically', 'adv.', 'By fusion, so as to form an air-tight closure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hermit', 'n.', 'A person who retires from society and lives in solitude; a\n   recluse; an anchoret; especially, one who so lives from religious\n   motives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hermit', 'n.', 'A beadsman; one bound to pray for another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hermitage', 'n.', 'The habitation of a hermit; a secluded residence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hermitage', 'n.', 'A celebrated French wine, both white and red, of the\n   Department of Drome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hermitary', 'n.', 'A cell annexed to an abbey, for the use of a hermit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hermitess', 'n.', 'A female hermit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hermitical', 'a.', 'Pertaining to, or suited for, a hermit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hermodactyl', 'n.', 'A heart-shaped bulbous root, about the size of a\n   finger, brought from Turkey, formerly used as a cathartic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hermogenian', 'n.', 'A disciple of Hermogenes, an heretical teacher who\n   lived in Africa near the close of the second century. He held matter to\n   be the fountain of all evil, and that souls and spirits are formed of\n   corrupt matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hern', 'n.', 'A heron; esp., the common European heron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hernani', 'n.', 'A thin silk or woolen goods, for womens dresses, woven in\n   various styles and colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herne', 'n.', 'A corner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hernias', 'pl. ', 'of Hernia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herniae', 'pl. ', 'of Hernia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hernia', 'n.', 'A protrusion, consisting of an organ or part which has\n   escaped from its natural cavity, and projects through some natural or\n   accidental opening in the walls of the latter; as, hernia of the brain,\n   of the lung, or of the bowels. Hernia of the abdominal viscera in most\n   common. Called also rupture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hernial', 'a.', 'Of, or connected with, hernia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herniotomy', 'n.', 'A cutting for the cure or relief of hernia; celotomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hernshaw', 'n.', 'Heronshaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heroes', 'pl. ', 'of Hero');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hero', 'n.', 'An illustrious man, supposed to be exalted, after death, to a\n   place among the gods; a demigod, as Hercules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hero', 'n.', 'A man of distinguished valor or enterprise in danger, or\n   fortitude in suffering; a prominent or central personage in any\n   remarkable action or event; hence, a great or illustrious person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hero', 'n.', 'The principal personage in a poem, story, and the like, or\n   the person who has the principal share in the transactions related; as\n   Achilles in the Iliad, Ulysses in the Odyssey, and Aeneas in the\n   Aeneid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herodian', 'n.', 'One of a party among the Jews, composed of partisans of\n   Herod of Galilee. They joined with the Pharisees against Christ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herodiones', 'n. pl.', 'A division of wading birds, including the herons,\n   storks, and allied forms. Called also Herodii.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heroess', 'n.', 'A heroine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heroic', 'a.', 'Of or pertaining to, or like, a hero; of the nature of\n   heroes; distinguished by the existence of heroes; as, the heroic age;\n   an heroic people; heroic valor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heroic', 'a.', 'Worthy of a hero; bold; daring; brave; illustrious; as,\n   heroic action; heroic enterprises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heroic', 'a.', 'Larger than life size, but smaller than colossal; -- said\n   of the representation of a human figure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heroical', 'a.', 'Heroic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heroicness', 'n.', 'Heroism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heroicomic', 'a.', 'Alt. of Heroicomical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heroicomical', 'a.', 'Combining the heroic and the ludicrous; denoting high\n   burlesque; as, a heroicomic poem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heroine', 'n.', 'A woman of an heroic spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heroine', 'n.', 'The principal female person who figures in a remarkable\n   action, or as the subject of a poem or story.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heroism', 'n.', 'The qualities characteristic of a hero, as courage,\n   bravery, fortitude, unselfishness, etc.; the display of such qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heron', 'n.', 'Any wading bird of the genus Ardea and allied genera, of the\n   family Ardeidae. The herons have a long, sharp bill, and long legs and\n   toes, with the claw of the middle toe toothed. The common European\n   heron (Ardea cinerea) is remarkable for its directly ascending flight,\n   and was formerly hunted with the larger falcons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heroner', 'n.', 'A hawk used in hunting the heron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heronry', 'n.', 'A place where herons breed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heronsew', 'n.', 'A heronshaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heronshaw', 'n.', 'A heron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heroologist', 'n.', 'One who treats of heroes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heroship', 'n.', 'The character or personality of a hero.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herpes', 'n.', 'An eruption of the skin, taking various names, according to\n   its form, or the part affected; especially, an eruption of vesicles in\n   small distinct clusters, accompanied with itching or tingling,\n   including shingles, ringworm, and the like; -- so called from its\n   tendency to creep or spread from one part of the skin to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herpetic', 'a.', 'Pertaining to, or resembling, the herpes; partaking of\n   the nature of herpes; as, herpetic eruptions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herpetism', 'n.', 'See Dartrous diathesis, under Dartrous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herpetologic', 'a.', 'Alt. of Herpetological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herpetological', 'a.', 'Pertaining to herpetology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herpetologist', 'n.', 'One versed in herpetology, or the natural history of\n   reptiles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herpetology', 'n.', 'The natural history of reptiles; that branch of\n   zoology which relates to reptiles, including their structure,\n   classification, and habits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herpetotomist', 'n.', 'One who dissects, or studies the anatomy of,\n   reptiles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herpetotomy', 'n.', 'The anatomy or dissection of reptiles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herr', 'n.', 'A title of respect given to gentlemen in Germany, equivalent\n   to the English Mister.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herring', 'n.', 'One of various species of fishes of the genus Clupea, and\n   allied genera, esp. the common round or English herring (C. harengus)\n   of the North Atlantic. Herrings move in vast schools, coming in spring\n   to the shores of Europe and America, where they are salted and smoked\n   in great quantities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herringbone', 'a.', 'Pertaining to, or like, the spine of a herring;\n   especially, characterized by an arrangement of work in rows of parallel\n   lines, which in the alternate rows slope in different directions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herrnhuter', 'n.', 'One of the Moravians; -- so called from the settlement\n   of Herrnhut (the Lords watch) made, about 1722, by the Moravians at\n   the invitation of Nicholas Lewis, count of Zinzendorf, upon his estate\n   in the circle of Bautzen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hers', 'pron.', 'See the Note under Her, pron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hersal', 'n.', 'Rehearsal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herschel', 'n.', 'See Uranus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herschelian', 'a.', 'Of or relating to Sir William Herschel; as, the\n   Herschelian telescope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herse', 'n.', 'A kind of gate or portcullis, having iron bars, like a\n   harrow, studded with iron spikes. It is hung above gateways so that it\n   may be quickly lowered, to impede the advance of an enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herse', 'n.', 'See Hearse, a carriage for the dead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herse', 'n.', 'A funeral ceremonial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herse', 'v. t.', 'Same as Hearse, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herself', 'pron.', 'An emphasized form of the third person feminine\n   pronoun; -- used as a subject with she; as, she herself will bear the\n   blame; also used alone in the predicate, either in the nominative or\n   objective case; as, it is herself; she blames herself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herself', 'pron.', 'Her own proper, true, or real character; hence, her\n   right, or sane, mind; as, the woman was deranged, but she is now\n   herself again; she has come to herself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hersillon', 'n.', 'A beam with projecting spikes, used to make a breach\n   impassable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hert', 'n.', 'A hart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Herte', 'n.', 'A heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hertely', 'a. & adv.', 'Hearty; heartily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hery', 'v. t.', 'To worship; to glorify; to praise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hesitancy', 'n.', 'The act of hesitating, or pausing to consider; slowness\n   in deciding; vacillation; also, the manner of one who hesitates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hesitancy', 'n.', 'A stammering; a faltering in speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hesitant', 'a.', 'Not prompt in deciding or acting; hesitating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hesitant', 'a.', 'Unready in speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hesitantly', 'adv.', 'With hesitancy or doubt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hesitated', 'imp. & p. p.', 'of Hesitate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hesitating', 'p. pr. & vb. n.', 'of Hesitate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hesitate', 'v. i.', 'To stop or pause respecting decision or action; to be\n   in suspense or uncertainty as to a determination; as, he hesitated\n   whether to accept the offer or not; men often hesitate in forming a\n   judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hesitate', 'v. i.', 'To stammer; to falter in speaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hesitate', 'v. t.', 'To utter with hesitation or to intimate by a reluctant\n   manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hesitatingly', 'adv.', 'With hesitation or doubt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hesitation', 'n.', 'The act of hesitating; suspension of opinion or action;\n   doubt; vacillation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hesitation', 'n.', 'A faltering in speech; stammering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hesitative', 'a.', 'Showing, or characterized by, hesitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hesitatory', 'a.', 'Hesitating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hesp', 'n.', 'A measure of two hanks of linen thread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hesper', 'n.', 'The evening; Hesperus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hesperetin', 'n.', 'A white, crystalline substance having a sweetish taste,\n   obtained by the decomposition of hesperidin, and regarded as a complex\n   derivative of caffeic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hesperian', 'a.', 'Western; being in the west; occidental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hesperian', 'n.', 'A native or an inhabitant of a western country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hesperian', 'a.', 'Of or pertaining to a family of butterflies called\n   Hesperidae, or skippers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hesperian', 'n.', 'Any one of the numerous species of Hesperidae; a\n   skipper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hesperid', 'a. & n.', 'Same as 3d Hesperian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hesperidene', 'n.', 'An isomeric variety of terpene from orange oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hesperides', 'n. pl.', 'The daughters of Hesperus, or Night (brother of\n   Atlas), and fabled possessors of a garden producing golden apples, in\n   Africa, at the western extremity of the known world. To slay the\n   guarding dragon and get some of these apples was one of the labors of\n   Hercules. Called also Atlantides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hesperides', 'n. pl.', 'The garden producing the golden apples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hesperidin', 'n.', 'A glucoside found in ripe and unripe fruit (as the\n   orange), and extracted as a white crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hesperidium', 'n.', 'A large berry with a thick rind, as a lemon or an\n   orange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hesperornis', 'n.', 'A genus of large, extinct, wingless birds from the\n   Cretaceous deposits of Kansas, belonging to the Odontornithes. They had\n   teeth, and were essentially carnivorous swimming ostriches. Several\n   species are known. See Illust. in Append.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hesperus', 'n.', 'Venus when she is the evening star; Hesper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hesperus', 'n.', 'Evening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hessian', 'a.', 'Of or relating to Hesse, in Germany, or to the Hessians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hessian', 'n.', 'A native or inhabitant of Hesse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hessian', 'n.', 'A mercenary or venal person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hessian', 'n.', 'See Hessian boots and cloth, under Hessian, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hessite', 'n.', 'A lead-gray sectile mineral. It is a telluride of silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hest', 'n.', 'Command; precept; injunction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hestern', 'a.', 'Alt. of Hesternal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hesternal', 'a.', 'Pertaining to yesterday. [Obs.] See Yester, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hesychast', 'n.', 'One of a mystical sect of the Greek Church in the\n   fourteenth century; a quietist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hetairism', 'n.', 'Alt. of Hetarism');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hetarism', 'n.', 'A supposed primitive state of society, in which all the\n   women of a tribe were held in common.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hetchel', 'v. t.', 'Same as Hatchel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hete', 'imp. & p. p.', 'of Hete');
  INSERT INTO words (word, word_type, description) VALUES  (    'Het', '', 'of Hete');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hete', 'v. t. & i.', 'Variant of Hote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteracanth', 'a.', 'Having the spines of the dorsal fin unsymmetrical, or\n   thickened alternately on the right and left sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterarchy', 'n.', 'The government of an alien.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterauxesis', 'n.', 'Unequal growth of a cell, or of a part of a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hetero-', '', 'A combining form signifying other, other than usual,\n   different; as, heteroclite, heterodox, heterogamous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterocarpism', 'n.', 'The power of producing two kinds of reproductive\n   bodies, as in Amphicarpaea, in which besides the usual pods, there are\n   others underground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterocarpous', 'a.', 'Characterized by heterocarpism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hetercephalous', 'a.', 'Bearing two kinds of heads or capitula; -- said of\n   certain composite plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterocera', 'n. pl.', 'A division of Lepidoptera, including the moths, and\n   hawk moths, which have the antennae variable in form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterocercal', 'a.', 'Having the vertebral column evidently continued into\n   the upper lobe of the tail, which is usually longer than the lower one,\n   as in sharks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterocercy', 'n.', 'Unequal development of the tail lobes of fishes; the\n   possession of a heterocercal tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterochromous', 'a.', 'Having the central florets of a flower head of a\n   different color from those of the circumference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterochronism', 'n.', 'Alt. of Heterochrony');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterochrony', 'n.', 'In evolution, a deviation from the typical sequence\n   in the formation of organs or parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteroclite', 'a.', 'Deviating from ordinary forms or rules; irregular;\n   anomalous; abnormal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteroclite', 'n.', 'A word which is irregular or anomalous either in\n   declension or conjugation, or which deviates from ordinary forms of\n   inflection in words of a like kind; especially, a noun which is\n   irregular in declension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteroclite', 'n.', 'Any thing or person deviating from the common rule, or\n   from common forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteroclitic', 'a.', 'Alt. of Heteroclitical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteroclitical', 'a.', 'Deviating from ordinary forms or rules; irregular;\n   anomalous; abnormal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteroclitous', 'a.', 'Heteroclitic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterocyst', 'n.', 'A cell larger than the others, and of different\n   appearance, occurring in certain algae related to nostoc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterodactyl', 'a.', 'Heterodactylous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterodactyl', 'n.', 'One of the Heterodactylae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterodactylae', 'n. pl.', 'A group of birds including the trogons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterodactylous', 'a.', 'Having the first and second toes turned backward,\n   as in the trogons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterodont', 'a.', 'Having the teeth differentiated into incisors, canines,\n   and molars, as in man; -- opposed to homodont.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterodont', 'n.', 'Any animal with heterodont dentition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterodox', 'a.', 'Contrary to, or differing from, some acknowledged\n   standard, as the Bible, the creed of a church, the decree of a council,\n   and the like; not orthodox; heretical; -- said of opinions, doctrines,\n   books, etc., esp. upon theological subjects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterodox', 'a.', 'Holding heterodox opinions, or doctrines not orthodox;\n   heretical; -- said of persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterodox', 'n.', 'An opinion opposed to some accepted standard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterodoxal', 'a.', 'Not orthodox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterodoxy', 'n.', 'An opinion or doctrine, or a system of doctrines,\n   contrary to some established standard of faith, as the Scriptures, the\n   creed or standards of a church, etc.; heresy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterodromous', 'a.', 'Having spirals of changing direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterodromous', 'a.', 'Moving in opposite directions; -- said of a lever,\n   pulley, etc., in which the resistance and the actuating force are on\n   opposite sides of the fulcrum or axis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterogamous', 'a.', 'The condition of having two or more kinds of flowers\n   which differ in regard to stamens and pistils, as in the aster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterogamous', 'a.', 'Characterized by heterogamy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterogamy', 'n.', 'The process of fertilization in plants by an indirect\n   or circuitous method; -- opposed to orthogamy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterogamy', 'n.', 'That form of alternate generation in which two kinds of\n   sexual generation, or a sexual and a parthenogenetic generation,\n   alternate; -- in distinction from metagenesis, where sexual and asexual\n   generations alternate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterogangliate', 'a.', 'Having the ganglia of the nervous system\n   unsymmetrically arranged; -- said of certain invertebrate animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterogene', 'a.', 'Heterogenous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterogeneal', 'a.', 'Heterogeneous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterogeneity', 'n.', 'The state of being heterogeneous; contrariety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterogeneous', 'a.', 'Differing in kind; having unlike qualities;\n   possessed of different characteristics; dissimilar; -- opposed to\n   homogeneous, and said of two or more connected objects, or of a\n   conglomerate mass, considered in respect to the parts of which it is\n   made up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterogenesis', 'n.', 'Spontaneous generation, so called.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterogenesis', 'n.', 'That method of reproduction in which the successive\n   generations differ from each other, the parent organism producing\n   offspring different in habit and structure from itself, the original\n   form, however, reappearing after one or more generations; -- opposed to\n   homogenesis, or gamogenesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterogenetic', 'a.', 'Relating to heterogenesis; as, heterogenetic\n   transformations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterogenist', 'n.', 'One who believes in the theory of spontaneous\n   generation, or heterogenesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterogenous', 'a.', 'Of or pertaining to heterogenesis; heterogenetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterogeny', 'n.', 'Heterogenesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterogonous', 'a.', 'Characterized by heterogony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterogony', 'n.', 'The condition of having two or more kinds of flowers,\n   different as to the length of their stamens and pistils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterographic', 'a.', 'Employing the same letters to represent different\n   sounds in different words or syllables; -- said of methods of spelling;\n   as, the ordinary English orthography is heterographic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterography', 'n.', 'That method of spelling in which the same letters\n   represent different sounds in different words, as in the ordinary\n   English orthography; e. g., g in get and in ginger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterogynous', 'a.', 'Having females very unlike the males in form and\n   structure; -- as certain insects, the males of which are winged, and\n   the females wingless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterologous', 'a.', 'Characterized by heterology; consisting of different\n   elements, or of like elements in different proportions; different; --\n   opposed to homologous; as, heterologous organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterology', 'n.', 'The absence of correspondence, or relation, in type of\n   structure; lack of analogy between parts, owing to their being composed\n   of different elements, or of like elements in different proportions;\n   variation in structure from the normal form; -- opposed to homology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterology', 'n.', 'The connection or relation of bodies which have partial\n   identity of composition, but different characteristics and properties;\n   the relation existing between derivatives of the same substance, or of\n   the analogous members of different series; as, ethane, ethyl alcohol,\n   acetic aldehyde, and acetic acid are in heterology with each other,\n   though each in at the same time a member of a distinct homologous\n   series. Cf. Homology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteromera', 'n. pl.', 'A division of Coleoptera, having heteromerous\n   tarsi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteromerous', 'a.', 'Unrelated in chemical composition, though similar or\n   indentical in certain other respects; as, borax and augite are\n   homoemorphous, but heteromerous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteromerous', 'a.', 'With the parts not corresponding in number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteromerous', 'a.', 'Having the femoral artery developed as the principal\n   artery of the leg; -- said of certain birds, as the cotingas and\n   pipras.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteromerous', 'a.', 'Having five tarsal joints in the anterior and middle\n   legs, but only four in the posterior pair, as the blister beetles and\n   oil beetles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteromorphic', 'a.', 'Deviating from the normal, perfect, or mature form;\n   having different forms at different stages of existence, or in\n   different individuals of the same species; -- applied especially to\n   insects in which there is a wide difference of form between the larva\n   and the adult, and to plants having more than one form of flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteromorphism', 'n.', 'Alt. of Heteromorphy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteromorphy', 'n.', 'The state or quality of being heteromorphic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteromorphous', 'a.', 'Heteromorphic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteromyaria', 'n. pl.', 'A division of bivalve shells, including the\n   marine mussels, in which the two adductor muscles are very unequal. See\n   Dreissena, and Illust. under Byssus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteronereis', 'n.', 'A free-swimming, dimorphic, sexual form of certain\n   species of Nereis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteronomous', 'a.', 'Subject to the law of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteronomy', 'n.', 'Subordination or subjection to the law of another;\n   political subjection of a community or state; -- opposed to autonomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteronomy', 'n.', 'A term applied by Kant to those laws which are imposed\n   on us from without, or the violence done to us by our passions, wants,\n   or desires.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteronym', 'n.', 'That which is heteronymous; a thing having a different\n   name or designation from some other thing; -- opposed to homonym.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteronymous', 'a.', 'Having different names or designations; standing in\n   opposite relations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteroousian', 'a.', 'Having different essential qualities; of a different\n   nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteroousian', 'n.', 'One of those Arians who held that the Son was of a\n   different substance from the Father.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteroousious', 'a.', 'See Heteroousian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteropathic', 'a.', 'Of or pertaining to the method of heteropathy;\n   allopathic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteropathy', 'n.', 'That mode of treating diseases, by which a morbid\n   condition is removed by inducing an opposite morbid condition to\n   supplant it; allopathy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteropelmous', 'a.', 'Having each of the two flexor tendons of the toes\n   bifid, the branches of one going to the first and second toes; those of\n   the other, to the third and fourth toes. See Illust. in Append.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterophagi', 'n. pl.', 'Altrices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterophemist', 'n.', 'One liable to the fault of heterophemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterophemy', 'n.', 'The unconscious saying, in speech or in writing, of\n   that which one does not intend to say; -- frequently the very reverse\n   of the thought which is present to consciousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterophony', 'n.', 'An abnormal state of the voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterophyllous', 'a.', 'Having leaves of more than one shape on the same\n   plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteroplasm', 'n.', 'An abnormal formation foreign to the economy, and\n   composed of elements different from those are found in it in its normal\n   condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteroplastic', 'a.', 'Producing a different type of organism; developing\n   into a different form of tissue, as cartilage which develops into bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteropod', 'n.', 'One of the Heteropoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteropod', 'a.', 'Heteropodous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteropoda', 'n. pl.', 'An order of pelagic Gastropoda, having the foot\n   developed into a median fin. Some of the species are naked; others, as\n   Carinaria and Atlanta, have thin glassy shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteropodous', 'a.', 'Of or pertaining to the Heteropoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteropter', 'n.', 'One of the Heteroptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteroptera', 'n. pl.', 'A suborder of Hemiptera, in which the base of the\n   anterior wings is thickened. See Hemiptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteroptics', 'n.', 'False optics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heteroscian', 'n.', 'One who lives either north or south of the tropics, as\n   contrasted with one who lives on the other side of them; -- so called\n   because at noon the shadows always fall in opposite directions (the one\n   northward, the other southward).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterosis', 'n.', 'A figure of speech by which one form of a noun, verb, or\n   pronoun, and the like, is used for another, as in the sentence: \"What\n   is life to such as me?\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterosomati', 'n. pl.', 'An order of fishes, comprising the flounders,\n   halibut, sole, etc., having the body and head asymmetrical, with both\n   eyes on one side. Called also Heterosomata, Heterosomi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterosporic', 'a.', 'Alt. of Heterosporous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterosporous', 'a.', 'Producing two kinds of spores unlike each other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterostyled', 'a.', 'Having styles of two or more distinct forms or\n   lengths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterostylism', 'n.', 'The condition of being heterostyled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterotactous', 'a.', 'Relating to, or characterized by, heterotaxy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterotaxy', 'n.', 'Variation in arrangement from that existing in a normal\n   form; heterogenous arrangement or structure, as, in botany, the\n   deviation in position of the organs of a plant, from the ordinary or\n   typical arrangement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterotopism', 'n.', 'Alt. of Heterotopy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterotopy', 'n.', 'A deviation from the natural position; -- a term\n   applied in the case of organs or growths which are abnormal in\n   situation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterotopy', 'n.', 'A deviation from the natural position of parts,\n   supposed to be effected in thousands of years, by the gradual\n   displacement of germ cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterotricha', 'n. pl.', 'A division of ciliated Infusoria, having fine\n   cilia all over the body, and a circle of larger ones around the\n   anterior end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterotropal', 'a.', 'Alt. of Heterotropous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heterotropous', 'a.', 'Having the embryo or ovule oblique or transverse to\n   the funiculus; amphitropous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hething', 'n.', 'Contempt; scorn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hetmans', 'pl. ', 'of Hetman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hetman', 'n.', 'A Cossack headman or general. The title of chief hetman is\n   now held by the heir to the throne of Russia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heugh', 'n.', 'A crag; a cliff; a glen with overhanging sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heugh', 'n.', 'A shaft in a coal pit; a hollow in a quarry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heuk', 'n.', 'Variant of Huke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heulandite', 'n.', 'A mineral of the Zeolite family, often occurring in\n   amygdaloid, in foliated masses, and also in monoclinic crystals with\n   pearly luster on the cleavage face. It is a hydrous silicate of alumina\n   and lime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heuristic', 'a.', 'Serving to discover or find out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heved', 'n.', 'The head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hewed', 'imp.', 'of Hew');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hewed', 'p. p.', 'of Hew');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hewn', '', 'of Hew');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hewing', 'p. pr. & vb. n.', 'of Hew');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hew', 'v. t.', 'To cut with an ax; to fell with a sharp instrument; --\n   often with down, or off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hew', 'v. t.', 'To form or shape with a sharp instrument; to cut; hence, to\n   form laboriously; -- often with out; as, to hew out a sepulcher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hew', 'v. t.', 'To cut in pieces; to chop; to hack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hew', 'n.', 'Destruction by cutting down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hew', 'n.', 'Hue; color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hew', 'n.', 'Shape; form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hewe', 'n.', 'A domestic servant; a retainer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hewer', 'n.', 'One who hews.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hewhole', 'n.', 'The European green woodpecker. See Yaffle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hewn', 'a.', 'Felled, cut, or shaped as with an ax; roughly squared; as, a\n   house built of hewn logs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hewn', 'a.', 'Roughly dressed as with a hammer; as, hewn stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hex-', '', 'Alt. of Hexa');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexa', '', 'A prefix or combining form, used to denote six, sixth, etc.;\n   as, hexatomic, hexabasic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexabasic', 'a.', 'Having six hydrogen atoms or six radicals capable of\n   being replaced or saturated by bases; -- said of acids; as, mellitic\n   acid is hexabasic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexacapsular', 'a.', 'Having six capsules or seed vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexachord', 'n.', 'A series of six notes, with a semitone between the third\n   and fourth, the other intervals being whole tones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexacid', 'a.', 'Having six atoms or radicals capable of being replaced by\n   acids; hexatomic; hexavalent; -- said of bases; as, mannite is a\n   hexacid base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexactinellid', 'a.', 'Having six-rayed spicules; belonging to the\n   Hexactinellinae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexactinelline', 'a.', 'Belonging to the Hexactinellinae, a group of\n   sponges, having six-rayed siliceous spicules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexactinia', 'n. pl.', 'The Anthozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexad', 'n.', 'An atom whose valence is six, and which can be theoretically\n   combined with, substituted for, or replaced by, six monad atoms or\n   radicals; as, sulphur is a hexad in sulphuric acid. Also used as an\n   adjective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexadactylous', 'a.', 'Having six fingers or toes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexade', 'n.', 'A series of six numbers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexadecane', 'n.', 'See Hecdecane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexagon', 'n.', 'A plane figure of six angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexagonal', 'a.', 'Having six sides and six angles; six-sided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexagonally', 'adv.', 'In an hexagonal manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexagony', 'n.', 'A hexagon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexagynia', 'n. pl.', 'A Linnaean order of plants having six pistils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexagynian', 'a.', 'Alt. of Hexagynous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexagynous', 'a.', 'Having six pistils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexahedral', 'a.', 'In the form of a hexahedron; having six sides or faces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexahedrons', 'pl. ', 'of Hexahedron');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexahedra', 'pl. ', 'of Hexahedron');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexahedron', 'n.', 'A solid body of six sides or faces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexahemeron', 'n.', 'A term of six days.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexahemeron', 'n.', 'The history of the six days work of creation, as\n   contained in the first chapter of Genesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexamerous', 'a.', 'In six parts; in sixes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexameter', 'n.', 'A verse of six feet, the first four of which may be\n   either dactyls or spondees, the fifth must regularly be a dactyl, and\n   the sixth always a spondee. In this species of verse are composed the\n   Iliad of Homer and the Aeneid of Virgil. In English hexameters accent\n   takes the place of quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexameter', 'a.', 'Having six metrical feet, especially dactyls and\n   spondees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexametric', 'a.', 'Alt. of Hexametrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexametrical', 'a.', 'Consisting of six metrical feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexametrist', 'n.', 'One who writes in hexameters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexandria', 'n. pl.', 'A Linnaean class of plants having six stamens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexandrian', 'a.', 'Alt. of Hex-androus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hex-androus', 'a.', 'Having six stamens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexane', 'n.', 'Any one of five hydrocarbons, C6H14, of the paraffin\n   series. They are colorless, volatile liquids, and are so called because\n   the molecule has six carbon atoms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexangular', 'a.', 'Having six angles or corners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexapetalous', 'a.', 'Having six petals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexaphyllous', 'a.', 'Having six leaves or leaflets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexapla', 'sing.', 'A collection of the Holy Scriptures in six languages or\n   six versions in parallel columns; particularly, the edition of the Old\n   Testament published by Origen, in the 3d century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexapod', 'a.', 'Having six feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexapod', 'n.', 'An animal having six feet; one of the Hexapoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexapoda', 'n. pl.', 'The true, or six-legged, insects; insects other than\n   myriapods and arachnids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexapodous', 'a.', 'Having six feet; belonging to the Hexapoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexapterous', 'a.', 'Having six processes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexastich', 'n.', 'Alt. of Hexastichon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexastichon', 'n.', 'A poem consisting of six verses or lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexastyle', 'a.', 'Having six columns in front; -- said of a portico or\n   temple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexastyle', 'n.', 'A hexastyle portico or temple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexateuch', 'n.', 'The first six books of the Old Testament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexatomic', 'a.', 'Having six atoms in the molecule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexatomic', 'a.', 'Having six replaceable radicals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexavalent', 'p. pr.', 'Having a valence of six; -- said of hexads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexdecyl', 'n.', 'The essential radical, C16H33, of hecdecane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexdecylic', 'a.', 'Pertaining to, or derived from, hexdecyl or hecdecane;\n   as, hexdecylic alcohol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexeikosane', 'n.', 'A hydrocarbon, C26H54, resembling paraffine; -- so\n   called because each molecule has twenty-six atoms of carbon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexene', 'n.', 'Same as Hexylene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexicology', 'n.', 'The science which treats of the complex relations of\n   living creatures to other organisms, and to their surrounding\n   conditions generally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexine', 'n.', 'A hydrocarbon, C6H10, of the acetylene series, obtained\n   artificially as a colorless, volatile, pungent liquid; -- called also\n   hexoylene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexoctahedron', 'n.', 'A solid having forty-eight equal triangular faces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexoic', 'a.', 'Pertaining to, or derived from, hexane; as, hexoic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexone', 'n.', 'A liquid hydrocarbon, C6H8, of the valylene series,\n   obtained from distillation products of certain fats and gums.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexyl', 'n.', 'A compound radical, C6H13, regarded as the essential residue\n   of hexane, and a related series of compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexylene', 'n.', 'A colorless, liquid hydrocarbon, C6H12, of the ethylene\n   series, produced artificially, and found as a natural product of\n   distillation of certain coals; also, any one several isomers of\n   hexylene proper. Called also hexene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hexylic', 'a.', 'Pertaining to, or derived from, hexyl or hexane; as,\n   hexylic alcohol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hey', 'a.', 'High.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hey', 'interj.', 'An exclamation of joy, surprise, or encouragement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hey', 'interj.', 'A cry to set dogs on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heyday', 'interj.', 'An expression of frolic and exultation, and sometimes\n   of wonder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heyday', 'n.', 'The time of triumph and exultation; hence, joy, high\n   spirits, frolicsomeness; wildness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heydeguy', 'n.', 'A kind of country-dance or round.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heyh', 'a.', 'Alt. of Heygh');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heygh', 'a.', 'High.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heyne', 'n.', 'A wretch; a rascal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Heyten', 'adv.', 'Hence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hiation', 'n.', 'Act of gaping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hiatus', 'pl. ', 'of Hiatus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hiatuses', 'pl. ', 'of Hiatus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hiatus', 'n.', 'An opening; an aperture; a gap; a chasm; esp., a defect in\n   a manuscript, where some part is lost or effaced; a space where\n   something is wanting; a break.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hiatus', 'n.', 'The concurrence of two vowels in two successive words or\n   syllables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hibernacle', 'n.', 'That which serves for protection or shelter in winter;\n   winter quarters; as, the hibernacle of an animal or a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hibernaculum', 'n.', 'A winter bud, in which the rudimentary foliage or\n   flower, as of most trees and shrubs in the temperate zone, is protected\n   by closely overlapping scales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hibernaculum', 'n.', 'A little case in which certain insects pass the\n   winter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hibernaculum', 'n.', 'Winter home or abiding place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hibernal', 'a.', 'Belonging or relating to winter; wintry; winterish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hibernated', 'imp. & p. p.', 'of Hibernate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hibernating', 'p. pr. & vb. n.', 'of Hibernate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hibernate', 'v. i.', 'To winter; to pass the season of winter in close\n   quarters, in a torpid or lethargic state, as certain mammals, reptiles,\n   and insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hibernation', 'n.', 'The act or state of hibernating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hibernian', 'a.', 'Of or pertaining to Hibernia, now Ireland; Irish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hibernian', 'n.', 'A native or an inhabitant of Ireland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hibernicism', 'n.', 'Alt. of Hibernianism');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hibernianism', 'n.', 'An idiom or mode of speech peculiar to the Irish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hiberno-Celtic', 'n.', 'The native language of the Irish; that branch of\n   the Celtic languages spoken by the natives of Ireland. Also adj.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hibiscus', 'n.', 'A genus of plants (herbs, shrubs, or trees), some species\n   of which have large, showy flowers. Some species are cultivated in\n   India for their fiber, which is used as a substitute for hemp. See\n   Althea, Hollyhock, and Manoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hiccius doctius', '', 'A juggler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hiccough', 'n.', 'A modified respiratory movement; a spasmodic inspiration,\n   consisting of a sudden contraction of the diaphragm, accompanied with\n   closure of the glottis, so that further entrance of air is prevented,\n   while the impulse of the column of air entering and striking upon the\n   closed glottis produces a sound, or hiccough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hiccoughed', 'imp. & p. p.', 'of Hiccough');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hiccoughing', 'p. pr. & vb. n.', 'of Hiccough');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hiccough', 'v. i.', 'To have a hiccough or hiccoughs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hickory', 'n.', 'An American tree of the genus Carya, of which there are\n   several species. The shagbark is the C. alba, and has a very rough\n   bark; it affords the hickory nut of the markets. The pignut, or brown\n   hickory, is the C. glabra. The swamp hickory is C. amara, having a nut\n   whose shell is very thin and the kernel bitter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hicksite', 'n.', 'A member or follower of the \"liberal\" party, headed by\n   Elias Hicks, which, because of a change of views respecting the\n   divinity of Christ and the Atonement, seceded from the conservative\n   portion of the Society of Friends in the United States, in 1827.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hickup', 'n. & v. i.', 'See Hiccough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hickwall', 'n.', 'Alt. of Hickway');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hickway', 'n.', 'The lesser spotted woodpecker (Dendrocopus minor) of\n   Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hid', '', 'imp. & p. p. of Hide. See Hidden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hidage', 'n.', 'A tax formerly paid to the kings of England for every hide\n   of land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hidalgo', 'n.', 'A title, denoting a Spanish nobleman of the lower class.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hidden', 'p. p. & a.', 'from Hide. Concealed; put out of view; secret; not\n   known; mysterious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hiddenite', 'n.', 'An emerald-green variety of spodumene found in North\n   Carolina; lithia emerald, -- used as a gem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hiddenly', 'adv.', 'In a hidden manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hid', 'imp.', 'of Hide');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hidden', 'p. p.', 'of Hide');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hid', '', 'of Hide');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hiding', 'p. pr. & vb. n.', 'of Hide');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hide', 'v. t.', 'To conceal, or withdraw from sight; to put out of view; to\n   secrete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hide', 'v. t.', 'To withhold from knowledge; to keep secret; to refrain\n   from avowing or confessing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hide', 'v. t.', 'To remove from danger; to shelter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hide', 'v. i.', 'To lie concealed; to keep ones self out of view; to be\n   withdrawn from sight or observation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hide', 'n.', 'An abode or dwelling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hide', 'n.', 'A measure of land, common in Domesday Book and old English\n   charters, the quantity of which is not well ascertained, but has been\n   differently estimated at 80, 100, and 120 acres.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hide', 'n.', 'The skin of an animal, either raw or dressed; -- generally\n   applied to the undressed skins of the larger domestic animals, as oxen,\n   horses, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hide', 'n.', 'The human skin; -- so called in contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hided', 'imp. & p. p.', 'of Hide');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hiding', 'p. pr. & vb. n.', 'of Hide');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hide', 'v. t.', 'To flog; to whip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hidebound', 'a.', 'Having the skin adhering so closely to the ribs and back\n   as not to be easily loosened or raised; -- said of an animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hidebound', 'a.', 'Having the bark so close and constricting that it\n   impedes the growth; -- said of trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hidebound', 'a.', 'Untractable; bigoted; obstinately and blindly or\n   stupidly conservative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hidebound', 'a.', 'Niggardly; penurious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hideous', 'a.', 'Frightful, shocking, or offensive to the eyes; dreadful to\n   behold; as, a hideous monster; hideous looks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hideous', 'a.', 'Distressing or offensive to the ear; exciting terror or\n   dismay; as, a hideous noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hideous', 'a.', 'Hateful; shocking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hider', 'n.', 'One who hides or conceals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hiding', 'n.', 'The act of hiding or concealing, or of withholding from\n   view or knowledge; concealment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hiding', 'n.', 'A flogging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hied', 'imp. & p. p.', 'of Hie');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hying', 'p. pr. & vb. n.', 'of Hie');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hie', 'v. i.', 'To hasten; to go in haste; -- also often with the\n   reciprocal pronoun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hie', 'n.', 'Haste; diligence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hiems', 'n.', 'Winter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hierapicra', 'n.', 'A warming cathartic medicine, made of aloes and canella\n   bark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hierarch', 'n.', 'One who has high and controlling authority in sacred\n   things; the chief of a sacred order; as, princely hierarchs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hierarchal', 'a.', 'Alt. of Hierarchic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hierarchic', 'a.', 'Pertaining to a hierarch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hierarchical', 'a.', 'Pertaining to a hierarchy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hierarchism', 'n.', 'The principles or authority of a hierarchy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hierarchies', 'pl. ', 'of Hierarchy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hierarchy', 'n.', 'Dominion or authority in sacred things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hierarchy', 'n.', 'A body of officials disposed organically in ranks and\n   orders each subordinate to the one above it; a body of ecclesiastical\n   rulers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hierarchy', 'n.', 'A form of government administered in the church by\n   patriarchs, metropolitans, archbishops, bishops, and, in an inferior\n   degree, by priests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hierarchy', 'n.', 'A rank or order of holy beings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hieratic', 'a.', 'Consecrated to sacred uses; sacerdotal; pertaining to\n   priests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hierocracy', 'n.', 'Government by ecclesiastics; a hierarchy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hieroglyph', 'a.', 'Alt. of Hieroglyphic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hieroglyphic', 'a.', 'A sacred character; a character in picture writing,\n   as of the ancient Egyptians, Mexicans, etc. Specifically, in the\n   plural, the picture writing of the ancient Egyptian priests. It is made\n   up of three, or, as some say, four classes of characters: first, the\n   hieroglyphic proper, or figurative, in which the representation of the\n   object conveys the idea of the object itself; second, the ideographic,\n   consisting of symbols representing ideas, not sounds, as an ostrich\n   feather is a symbol of truth; third, the phonetic, consisting of\n   symbols employed as syllables of a word, or as letters of the alphabet,\n   having a certain sound, as a hawk represented the vowel a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hieroglyphic', 'a.', 'Any character or figure which has, or is supposed to\n   have, a hidden or mysterious significance; hence, any unintelligible or\n   illegible character or mark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hieroglyphic', 'a.', 'Alt. of Hieroglyphical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hieroglyphical', 'a.', 'Emblematic; expressive of some meaning by\n   characters, pictures, or figures; as, hieroglyphic writing; a\n   hieroglyphic obelisk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hieroglyphical', 'a.', 'Resembling hieroglyphics; not decipherable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hieroglyphically', 'adv.', 'In hieroglyphics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hieroglyphist', 'n.', 'One versed in hieroglyphics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hierogram', 'n.', 'A form of sacred or hieratic writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hierogrammatic', 'a.', 'Written in, or pertaining to, hierograms;\n   expressive of sacred writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hierogrammatist', 'n.', 'A writer of hierograms; also, one skilled in\n   hieroglyphics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hierographic', 'a.', 'Alt. of Hierographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hierographical', 'a.', 'Of or pertaining to sacred writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hierography', 'n.', 'Sacred writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hierolatry', 'n.', 'The worship of saints or sacred things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hierologic', 'a.', 'Alt. of Hierological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hierological', 'a.', 'Pertaining to hierology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hierologist', 'n.', 'One versed in, or whostudies, hierology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hierology', 'n.', 'A treatise on sacred things; especially, the science\n   which treats of the ancient writings and inscriptions of the Egyptians,\n   or a treatise on that science.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hieromancy', 'n.', 'Divination by observing the objects offered in\n   sacrifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hiermartyr', 'n.', 'A priest who becomes a martyr.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hieromnemon', 'n.', 'The sacred secretary or recorder sent by each state\n   belonging to the Amphictyonic Council, along with the deputy or\n   minister.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hieromnemon', 'n.', 'A magistrate who had charge of religious matters, as\n   at Byzantium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hieron', 'n.', 'A consecrated place; esp., a temple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hieronymite', 'n.', 'See Jeronymite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hierophant', 'n.', 'The presiding priest who initiated candidates at the\n   Eleusinian mysteries; hence, one who teaches the mysteries and duties\n   of religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hierophantic', 'a.', 'Of or relating to hierophants or their teachings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hieroscopy', 'n.', 'Divination by inspection of entrails of victims offered\n   in sacrifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-cae', 'pl. ', 'of Hierotheca');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hierotheca', 'n.', 'A receptacle for sacred objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hierourgy', 'n.', 'A sacred or holy work or worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hifalutin', 'n.', 'See Highfaluting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Higgled', 'imp. & p. p.', 'of Higgle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Higgling', 'p. pr. & vb. n.', 'of Higgle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Higgle', 'v. i.', 'To hawk or peddle provisions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Higgle', 'v. i.', 'To chaffer; to stickle for small advantages in buying\n   and selling; to haggle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Higgledy-piggledy', 'adv.', 'In confusion; topsy-turvy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Higgler', 'n.', 'One who higgles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High', 'v. i.', 'To hie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High', 'superl.', 'Elevated above any starting point of measurement, as a\n   line, or surface; having altitude; lifted up; raised or extended in the\n   direction of the zenith; lofty; tall; as, a high mountain, tower, tree;\n   the sun is high.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High', 'superl.', 'Regarded as raised up or elevated; distinguished;\n   remarkable; conspicuous; superior; -- used indefinitely or relatively,\n   and often in figurative senses, which are understood from the\n   connection');
  INSERT INTO words (word, word_type, description) VALUES  (    'High', 'superl.', 'Elevated in character or quality, whether moral or\n   intellectual; preeminent; honorable; as, high aims, or motives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High', 'superl.', 'Exalted in social standing or general estimation, or in\n   rank, reputation, office, and the like; dignified; as, she was welcomed\n   in the highest circles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High', 'superl.', 'Of noble birth; illustrious; as, of high family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High', 'superl.', 'Of great strength, force, importance, and the like;\n   strong; mighty; powerful; violent; sometimes, triumphant; victorious;\n   majestic, etc.; as, a high wind; high passions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High', 'superl.', 'Very abstract; difficult to comprehend or surmount;\n   grand; noble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High', 'superl.', 'Costly; dear in price; extravagant; as, to hold goods at\n   a high price.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High', 'superl.', 'Arrogant; lofty; boastful; proud; ostentatious; -- used\n   in a bad sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High', 'superl.', 'Possessing a characteristic quality in a supreme or\n   superior degree; as, high (i. e., intense) heat; high (i. e., full or\n   quite) noon; high (i. e., rich or spicy) seasoning; high (i. e.,\n   complete) pleasure; high (i. e., deep or vivid) color; high (i. e.,\n   extensive, thorough) scholarship, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High', 'superl.', 'Strong-scented; slightly tainted; as, epicures do not\n   cook game before it is high.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High', 'superl.', 'Acute or sharp; -- opposed to grave or low; as, a high\n   note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High', 'superl.', 'Made with a high position of some part of the tongue in\n   relation to the palate, as / (/ve), / (f/d). See Guide to\n   Pronunciation, // 10, 11.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High', 'adv.', 'In a high manner; in a high place; to a great altitude; to\n   a great degree; largely; in a superior manner; eminently; powerfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High', 'n.', 'An elevated place; a superior region; a height; the sky;\n   heaven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High', 'n.', 'People of rank or high station; as, high and low.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High', 'n.', 'The highest card dealt or drawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High', 'v. i.', 'To rise; as, the sun higheth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Highbinder', 'n.', 'A ruffian; one who hounds, or spies upon, another; app.\n   esp. to the members of certain alleged societies among the Chinese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-blown', 'a.', 'Inflated, as with conceit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Highborn', 'a.', 'Of noble birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-bred', 'a.', 'Bred in high life; of pure blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-built', 'a.', 'Of lofty structure; tall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-church', 'a.', 'Of or pertaining to, or favoring, the party called the\n   High Church, or their doctrines or policy. See High Church, under High,\n   a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-churchism', 'n.', 'The principles of the high-church party.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-men', 'pl. ', 'of High-churchman');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-churchman', 'n.', 'One who holds high-church principles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-churchman-ship', 'n.', 'The state of being a high-churchman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-colored', 'a.', 'Having a strong, deep, or glaring color; flushed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-colored', 'a.', 'Vivid; strong or forcible in representation; hence,\n   exaggerated; as, high-colored description.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-embowed', 'a.', 'Having lofty arches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Highering', 'a.', 'Rising higher; ascending.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Highfaluting', 'n.', 'High-flown, bombastic language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-fed', 'a.', 'Pampered; fed luxuriously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-finished', 'a.', 'Finished with great care; polished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Highflier', 'n.', 'One who is extravagant in pretensions, opinions, or\n   manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-flown', 'a.', 'Elevated; proud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-flown', 'a.', 'Turgid; extravagant; bombastic; inflated; as,\n   high-flown language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-flushed', 'a.', 'Elated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Highflying', 'a.', 'Extravagant in opinions or ambition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-go', 'n.', 'A spree; a revel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-handed', 'a.', 'Overbearing; oppressive; arbitrary; violent; as, a\n   high-handed act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-hearted', 'a.', 'Full of courage or nobleness; high-souled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-hoe', 'n.', 'The European green woodpecker or yaffle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-holder', 'n.', 'The flicker; -- called also high-hole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Highland', 'n.', 'Elevated or mountainous land; (often in the pl.) an\n   elevated region or country; as, the Highlands of Scotland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Highlander', 'n.', 'An inhabitant of highlands, especially of the Highlands\n   of Scotland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Highlandry', 'n.', 'Highlanders, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-low', 'n.', 'A laced boot, ankle high.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Highly', 'adv.', 'In a high manner, or to a high degree; very much; as,\n   highly esteemed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Highmen', 'n. pl.', 'Loaded dice so contrived as to turn up high numbers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-mettled', 'a.', 'Having abundance of mettle; ardent; full of fire; as,\n   a high-mettled steed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-minded', 'a.', 'Proud; arrogant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-minded', 'a.', 'Having, or characterized by, honorable pride; of or\n   pertaining to elevated principles and feelings; magnanimous; -- opposed\n   to mean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-mindedness', 'n.', 'The quality of being highminded; nobleness;\n   magnanimity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Highmost', 'a.', 'Highest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Highness', 'n.', 'The state of being high; elevation; loftiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Highness', 'n.', 'A title of honor given to kings, princes, or other\n   persons of rank; as, His Royal Highness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-palmed', 'a.', 'Having high antlers; bearing full-grown antlers aloft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-pressure', 'a.', 'Having or involving a pressure greatly exceeding\n   that of the atmosphere; -- said of steam, air, water, etc., and of\n   steam, air, or hydraulic engines, water wheels, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-pressure', 'a.', 'Fig.: Urgent; intense; as, a high-pressure business\n   or social life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High priest', '', 'A chief priest; esp., the head of the Jewish priesthood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-priesthood', 'n.', 'The office, dignity, or position of a high priest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-priestship', 'n.', 'High-priesthood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-principled', 'a.', 'Possessed of noble or honorable principles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-proof', 'a.', 'Highly rectified; very strongly alcoholic; as,\n   high-proof spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-proof', 'a.', 'So as to stand any test.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-raised', 'a.', 'Elevated; raised aloft; upreared.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-raised', 'a.', 'Elated with great ideas or hopes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-reaching', 'a.', 'Reaching high or upward; hence, ambitious; aspiring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-red', 'a.', 'Of a strong red color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Highroad', 'n.', 'A highway; a much traveled or main road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-seasoned', 'a.', 'Enriched with spice and condiments; hence, exciting;\n   piquant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-sighted', 'a.', 'Looking upward; supercilious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-souled', 'a.', 'Having a high or noble spirit; honorable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-sounding', 'a.', 'Pompous; noisy; ostentatious; as, high-sounding\n   words or titles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-spirited', 'a.', 'Full of spirit or natural fire; haughty; courageous;\n   impetuous; not brooking restraint or opposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-stepper', 'n.', 'A horse that moves with a high step or proud gait;\n   hence, a person having a proud bearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-stomached', 'a.', 'Having a lofty spirit; haughty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-strung', 'a.', 'Strung to a high pitch; spirited; sensitive; as, a\n   high-strung horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-swelling', 'a.', 'Inflated; boastful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hight', 'n.', 'A variant of Height.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hight', 'imp.', 'of Hight');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hot', '', 'of Hight');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hight', 'p. p.', 'of Hight');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hote', '', 'of Hight');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoten', '', 'of Hight');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hight', 'v. t. & i.', 'To be called or named.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hight', 'v. t. & i.', 'To command; to direct; to impel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hight', 'v. t. & i.', 'To commit; to intrust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hight', 'v. t. & i.', 'To promise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hightener', 'n.', 'That which heightens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Highth', 'n.', 'Variant of Height.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-toned', 'a.', 'High in tone or sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-toned', 'a.', 'Elevated; high-principled; honorable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-top', 'n.', 'A ships masthead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Highty-tighty', 'a.', 'Hoity-toity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Highway', 'n.', 'A road or way open to the use of the public; a main road\n   or thoroughfare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Highwaymen', 'pl. ', 'of Highwayman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Highwayman', 'n.', 'One who robs on the public road; a highway robber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-wrought', 'a.', 'Wrought with fine art or skill; elaborate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'High-wrought', 'a.', 'Worked up, or swollen, to a high degree; as, a\n   highwrought passion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Higre', 'n.', 'See Eagre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hig-taper', 'n.', 'A plant of the genus Verbascum (V. Thapsus); the common\n   mullein. [Also high-taper and hag-taper.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hijera', 'n.', 'Alt. of Hijra');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hijra', 'n.', 'See Hegira.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hilal', 'a.', 'Of or pertaining to a hilum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hilar', 'a.', 'Belonging to the hilum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hilarious', 'a.', 'Mirthful; noisy; merry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hilarity', 'n.', 'Boisterous mirth; merriment; jollity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hilary term', '', 'Formerly, one of the four terms of the courts of common\n   law in England, beginning on the eleventh of January and ending on the\n   thirty-first of the same month, in each year; -- so called from the\n   festival of St. Hilary, January 13th.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hilding', 'n.', 'A base, menial wretch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hilding', 'a.', 'Base; spiritless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hile', 'v. t.', 'To hide. See Hele.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hile', 'n.', 'Same as Hilum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hill', 'n.', 'A natural elevation of land, or a mass of earth rising above\n   the common level of the surrounding land; an eminence less than a\n   mountain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hill', 'n.', 'The earth raised about the roots of a plant or cluster of\n   plants. [U. S.] See Hill, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hill', 'v. t.', 'A single cluster or group of plants growing close\n   together, and having the earth heaped up about them; as, a hill of corn\n   or potatoes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hilled', 'imp. & p. p.', 'of Hill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hilling', 'p. pr. & vb. n.', 'of Hill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hill', 'v. t.', 'To surround with earth; to heap or draw earth around or\n   upon; as, to hill corn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hilliness', 'n.', 'The state of being hilly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hilling', 'n.', 'The act or process of heaping or drawing earth around\n   plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hillock', 'n.', 'A small hill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hillside', 'n.', 'The side or declivity of a hill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hilltop', 'n.', 'The top of a hill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hilly', 'a.', 'Abounding with hills; uneven in surface; as, a hilly\n   country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hilly', 'a.', 'Lofty; as, hilly empire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hilt', 'n.', 'A handle; especially, the handle of a sword, dagger, or the\n   like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hilted', 'a.', 'Having a hilt; -- used in composition; as, basket-hilted,\n   cross-hilted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hilum', 'n.', 'The eye of a bean or other seed; the mark or scar at the\n   point of attachment of an ovule or seed to its base or support; --\n   called also hile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hilum', 'n.', 'The part of a gland, or similar organ, where the blood\n   vessels and nerves enter; the hilus; as, the hilum of the kidney.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hilus', 'n.', 'Same as Hilum, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Him', 'pron.', 'Them. See Hem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Him', 'pron.', 'The objective case of he. See He.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Himalayan', 'a.', 'Of or pertaining to the Himalayas, the great mountain\n   chain in Hindostan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Himpne', 'n.', 'A hymn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Himself', 'pron.', 'An emphasized form of the third person masculine\n   pronoun; -- used as a subject usually with he; as, he himself will bear\n   the blame; used alone in the predicate, either in the nominative or\n   objective case; as, it is himself who saved himself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Himself', 'pron.', 'Ones true or real character; ones natural temper and\n   disposition; the state of being in ones right or sane mind (after\n   unconsciousness, passion, delirium, or abasement); as, the man has come\n   to himself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Himself', 'pron. pl.', 'Alt. of Himselven');
  INSERT INTO words (word, word_type, description) VALUES  (    'Himselven', 'pron. pl.', 'Themselves. See Hemself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Himselve', 'pron.', 'See 1st Himself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Himyaric', 'a.', 'Alt. of Himyaritic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Himyaritic', 'a.', 'Pertaining to Himyar, an ancient king of Yemen, in\n   Arabia, or to his successors or people; as, the Himjaritic characters,\n   language, etc.; applied esp. to certain ancient inscriptions showing\n   the primitive type of the oldest form of the Arabic, still spoken in\n   Southern Arabia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hin', 'n.', 'A Hebrew measure of liquids, containing three quarts, one\n   pint, one gill, English measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hind', 'n.', 'The female of the red deer, of which the male is the stag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hind', 'n.', 'A spotted food fish of the genus Epinephelus, as E. apua of\n   Bermuda, and E. Drummond-hayi of Florida; -- called also coney, John\n   Paw, spotted hind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hind', 'n.', 'A domestic; a servant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hind', 'n.', 'A peasant; a rustic; a farm servant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hind', 'a.', 'In the rear; -- opposed to front; of or pertaining to the\n   part or end which follows or is behind, in opposition to the part which\n   leads or is before; as, the hind legs or hind feet of a quadruped; the\n   hind man in a procession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hindberry', 'n.', 'The raspberry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hindbrain', 'n.', 'The posterior of the three principal divisions of the\n   brain, including the epencephalon and metencephalon. Sometimes\n   restricted to the epencephalon only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hinder', 'a.', 'Of or belonging to that part or end which is in the rear,\n   or which follows; as, the hinder part of a wagon; the hinder parts of a\n   horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hindered', 'imp. & p. p.', 'of Hinder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hindering', 'p. pr. & vb. n.', 'of Hinder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hinder', 'a.', 'To keep back or behind; to prevent from starting or moving\n   forward; to check; to retard; to obstruct; to bring to a full stop; --\n   often followed by from; as, an accident hindered the coach; drought\n   hinders the growth of plants; to hinder me from going.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hinder', 'a.', 'To prevent or embarrass; to debar; to shut out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hinder', 'v. i.', 'To interpose obstacles or impediments; to be a\n   hindrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hinderance', 'n.', 'Same as Hindrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hinderer', 'n.', 'One who, or that which, hinders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hinderest', 'a.', 'Hindermost; -- superl. of Hind, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hinderling', 'a.', 'A worthless, base, degenerate person or animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hindermost', 'a.', 'Alt. of Hindmost');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hindmost', 'a.', 'Furthest in or toward the rear; last.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hindgut', 'n.', 'The posterior part of the alimentary canal, including the\n   rectum, and sometimes the large intestine also.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hindi', 'n.', 'The name given by Europeans to that form of the Hindustani\n   language which is chiefly spoken by native Hindoos. In employs the\n   Devanagari character, in which Sanskrit is written.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hindleys screw', '', 'A screw cut on a solid whose sides are arcs of the\n   periphery of a wheel into the teeth of which the screw is intended to\n   work. It is named from the person who first used the form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hindoos', 'pl. ', 'of Hindu');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hindus', 'pl. ', 'of Hindu');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hindoo', 'n.', 'Alt. of Hindu');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hindu', 'n.', 'A native inhabitant of Hindostan. As an ethnical term it is\n   confined to the Dravidian and Aryan races; as a religious name it is\n   restricted to followers of the Veda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hindooism', 'n.', 'Alt. of Hinduism');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hinduism', 'n.', 'The religious doctrines and rites of the Hindoos;\n   Brahmanism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hindoostanee', 'a.', 'Alt. of Hindustani');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hindustani', 'a.', 'Of or pertaining to the Hindoos or their language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hindustani', 'n.', 'The language of Hindostan; the name given by Europeans\n   to the most generally spoken of the modern Aryan languages of India. It\n   is Hindi with the addition of Persian and Arabic words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hindrance', 'v. t.', 'The act of hindering, or the state of being hindered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hindrance', 'v. t.', 'That which hinders; an impediment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hindu', 'n.', 'Same as Hindoo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hine', 'n.', 'A servant; a farm laborer; a peasant; a hind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hinge', 'n.', 'The hook with its eye, or the joint, on which a door, gate,\n   lid, etc., turns or swings; a flexible piece, as a strip of leather,\n   which serves as a joint to turn on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hinge', 'n.', 'That on which anything turns or depends; a governing\n   principle; a cardinal point or rule; as, this argument was the hinge on\n   which the question turned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hinge', 'n.', 'One of the four cardinal points, east, west, north, or\n   south.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hinged', 'imp. & p. p.', 'of Hinge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hinging', 'p. pr. & vb. n.', 'of Hinge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hinge', 'v. t.', 'To attach by, or furnish with, hinges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hinge', 'v. t.', 'To bend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hinge', 'v. i.', 'To stand, depend, hang, or turn, as on a hinge; to depend\n   chiefly for a result or decision or for force and validity; -- usually\n   with on or upon; as, the argument hinges on this point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hinged', 'a.', 'Furnished with hinges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hingeless', 'a.', 'Without a hinge or joint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hink', 'n.', 'A reaping hook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hinniate', 'v. i.', 'Alt. of Hinny');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hinny', 'v. i.', 'To neigh; to whinny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hinnies', 'pl. ', 'of Hinny');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hinny', 'n.', 'A hybrid between a stallion and an ass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hinny', 'n.', 'A term of endearment; darling; -- corrupted from honey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hinted', 'imp. & p. p.', 'of Hint');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hinting', 'p. pr. & vb. n.', 'of Hint');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hint', 'v. t.', 'To bring to mind by a slight mention or remote allusion;\n   to suggest in an indirect manner; as, to hint a suspicion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hint', 'v. i.', 'To make an indirect reference, suggestion, or allusion; to\n   allude vaguely to something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hint', 'n.', 'A remote allusion; slight mention; intimation; insinuation; a\n   suggestion or reminder, without a full declaration or explanation;\n   also, an occasion or motive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hintingly', 'adv.', 'In a hinting manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hip', 'n.', 'The projecting region of the lateral parts of one side of the\n   pelvis and the hip joint; the haunch; the huckle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hip', 'n.', 'The external angle formed by the meeting of two sloping sides\n   or skirts of a roof, which have their wall plates running in different\n   directions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hip', 'n.', 'In a bridge truss, the place where an inclined end post meets\n   the top chord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hipped', 'imp. & p. p.', 'of Hip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hipping', 'p. pr. & vb. n.', 'of Hip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hip', 'v. t.', 'To dislocate or sprain the hip of, to fracture or injure\n   the hip bone of (a quadruped) in such a manner as to produce a\n   permanent depression of that side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hip', 'v. t.', 'To throw (ones adversary) over ones hip in wrestling\n   (technically called cross buttock).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hip', 'v. t.', 'To make with a hip or hips, as a roof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hip', 'n.', 'The fruit of a rosebush, especially of the English dog-rose\n   (Rosa canina).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hip', 'interj.', 'Used to excite attention or as a signal; as, hip, hip,\n   hurra!');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hip', 'n.', 'Alt. of Hipps');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hipps', 'n.', 'See Hyp, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hiphalt', 'a.', 'Lame in the hip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hippa', 'n.', 'Alt. of Hippe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hippe', 'n.', 'A genus of marine decapod crustaceans, which burrow rapidly\n   in the sand by pushing themselves backward; -- called also bait bug.\n   See Illust. under Anomura.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hipparion', 'n.', 'An extinct genus of Tertiary mammals allied to the\n   horse, but three-toed, having on each foot a small lateral hoof on each\n   side of the main central one. It is believed to be one of the ancestral\n   genera of the Horse family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hipped', 'a.', 'Alt. of Hippish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hippish', 'a.', 'Somewhat hypochondriac; melancholy. See Hyppish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hippobosca', 'n.', 'A genus of dipterous insects including the horsefly or\n   horse tick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hippocamp', 'n.', 'See Hippocampus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hippocampal', 'a.', 'Of or pertaining to the hippocampus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hippocampus', 'n.', 'A fabulous monster, with the head and fore quarters of\n   a horse joined to the tail of a dolphin or other fish (Hippocampus\n   brevirostris), -- seen in Pompeian paintings, attached to the chariot\n   of Neptune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hippocampus', 'n.', 'A genus of lophobranch fishes of several species in\n   which the head and neck have some resemblance to those of a horse; --\n   called also sea horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hippocampus', 'n.', 'A name applied to either of two ridges of white matter\n   in each lateral ventricle of the brain. The larger is called\n   hippocampus major or simply hippocampus. The smaller, hippocampus\n   minor, is called also ergot and calcar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hippocentaur', 'n.', 'Same as Centaur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hippocras', 'n.', 'A cordial made of spiced wine, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hippocrates', 'n.', 'A famous Greek physician and medical writer, born in\n   Cos, about 460 B. C.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hippocratic', 'a.', 'Of or pertaining to Hippocrates, or to his teachings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hippocratism', 'n.', 'The medical philosophy or system of Hippocrates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hippocrene', 'n.', 'A fountain on Mount Helicon in Boeotia, fabled to have\n   burst forth when the ground was struck by the hoof of Pegasus. Also,\n   its waters, which were supposed to impart poetic inspiration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hippocrepian', 'n.', 'One of an order of fresh-water Bryozoa, in which the\n   tentacles are on a lophophore, shaped like a horseshoe. See\n   Phylactolaema.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hippocrepiform', 'a.', 'Shaped like a horseshoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hippodame', 'n.', 'A fabulous sea monster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hippodrome', 'n.', 'A place set apart for equestrian and chariot races.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hippodrome', 'n.', 'An arena for equestrian performances; a circus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hippogriff', 'n.', 'A fabulous winged animal, half horse and half griffin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hippolith', 'n.', 'A concretion, or kind of bezoar, from the intestines of\n   the horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hippopathology', 'n.', 'The science of veterinary medicine; the pathology\n   of the horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hippophagi', 'n. pl.', 'Eaters of horseflesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hippophagism', 'n.', 'Hippophagy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hippophagist', 'n.', 'One who eats horseflesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hippophagous', 'a.', 'Feeding on horseflesh; -- said of certain nomadic\n   tribes, as the Tartars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hippophagy', 'n.', 'The act or practice of feeding on horseflesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hippophile', 'n.', 'One who loves horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hippopotamuses', 'pl. ', 'of Hippopotamus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hippopotami', 'pl. ', 'of Hippopotamus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hippopotamus', 'n.', 'A large, amphibious, herbivorous mammal (Hippopotamus\n   amphibius), common in the rivers of Africa. It is allied to the hogs,\n   and has a very thick, naked skin, a thick and square head, a very large\n   muzzle, small eyes and ears, thick and heavy body, and short legs. It\n   is supposed to be the behemoth of the Bible. Called also zeekoe, and\n   river horse. A smaller species (H. Liberiencis) inhabits Western\n   Africa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hippotomy', 'n.', 'Anatomy of the horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hippuric', 'a.', 'Obtained from the urine of horses; as, hippuric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hippurite', 'n.', 'A fossil bivalve mollusk of the genus Hippurites, of\n   many species, having a conical, cup-shaped under valve, with a flattish\n   upper valve or lid. Hippurites are found only in the Cretaceous rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hip-roofed', 'a.', 'Having a hip roof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hipshot', 'a.', 'Having the hip dislocated; hence, having one hip lower\n   than the other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hip tree', '', 'The dog-rose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hir', 'pron.', 'See Here, pron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hircic', 'a.', 'Of, pertaining to, or derived from, mutton suet; -- applied\n   by Chevreul to an oily acid which was obtained from mutton suet, and to\n   which he attributed the peculiar taste and smell of that substance. The\n   substance has also been called hircin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hircin', 'n.', 'Hircic acid. See Hircic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hircine', 'a.', 'Alt. of Hircinous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hircinous', 'a.', 'Goatlike; of or pertaining to a goat or the goats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hircinous', 'a.', 'Of a strong goatish smell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hire', 'pron.', 'See Here, pron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hire', 'n.', 'The price, reward, or compensation paid, or contracted to be\n   paid, for the temporary use of a thing or a place, for personal\n   service, or for labor; wages; rent; pay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hire', 'n.', 'A bailment by which the use of a thing, or the services and\n   labor of a person, are contracted for at a certain price or reward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hired', 'imp. & p. p.', 'of Hire');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hiring', 'p. pr. & vb. n.', 'of Hire');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hire', 'n.', 'To procure (any chattel or estate) from another person, for\n   temporary use, for a compensation or equivalent; to purchase the use or\n   enjoyment of for a limited time; as, to hire a farm for a year; to hire\n   money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hire', 'n.', 'To engage or purchase the service, labor, or interest of (any\n   one) for a specific purpose, by payment of wages; as, to hire a\n   servant, an agent, or an advocate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hire', 'n.', 'To grant the temporary use of, for compensation; to engage to\n   give the service of, for a price; to let; to lease; -- now usually with\n   out, and often reflexively; as, he has hired out his horse, or his\n   time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hireless', 'a.', 'Without hire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hireling', 'n.', 'One who is hired, or who serves for wages; esp., one\n   whose motive and interest in serving another are wholly gainful; a\n   mercenary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hireling', 'a.', 'Serving for hire or wages; venal; mercenary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hirer', 'n.', 'One who hires.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hires', 'pron.', 'Alt. of Hirs');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hirs', 'pron.', 'Hers; theirs. See Here, pron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hirsute', 'a.', 'Rough with hair; set with bristles; shaggy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hirsute', 'a.', 'Rough and coarse; boorish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hirsute', 'a.', 'Pubescent with coarse or stiff hairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hirsute', 'a.', 'Covered with hairlike feathers, as the feet of certain\n   birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hirsuteness', 'n.', 'Hairiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hirtellous', 'a.', 'Pubescent with minute and somewhat rigid hairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hirudine', 'a.', 'Of or pertaining to the leeches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hirudinea', 'n. pl.', 'An order of Annelida, including the leeches; --\n   called also Hirudinei.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hirudo', 'n.', 'A genus of leeches, including the common medicinal leech.\n   See Leech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hirundine', 'a.', 'Like or pertaining to the swallows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hirundo', 'n.', 'A genus of birds including the swallows and martins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'His', 'pron.', 'Belonging or pertaining to him; -- used as a pronominal\n   adjective or adjective pronoun; as, tell John his papers are ready;\n   formerly used also for its, but this use is now obsolete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'His', 'pron.', 'The possessive of he; as, the book is his.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hisingerite', 'n.', 'A soft black, iron ore, nearly earthy, a hydrous\n   silicate of iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hispanic', 'a.', 'Of or pertaining to Spain or its language; as, Hispanic\n   words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hispanicism', 'n.', 'A Spanish idiom or mode of speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hispanicize', 'v. t.', 'To give a Spanish form or character to; as, to\n   Hispanicize Latin words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hispid', 'a.', 'Rough with bristles or minute spines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hispid', 'a.', 'Beset with stiff hairs or bristles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hispidulous', 'a.', 'Minutely hispid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hissed', 'imp. & p. p.', 'of Hiss');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hissing', 'p. pr. & vb. n.', 'of Hiss');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hiss', 'v. i.', 'To make with the mouth a prolonged sound like that of the\n   letter s, by driving the breath between the tongue and the teeth; to\n   make with the mouth a sound like that made by a goose or a snake when\n   angered; esp., to make such a sound as an expression of hatred,\n   passion, or disapproval.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hiss', 'v. i.', 'To make a similar noise by any means; to pass with a\n   sibilant sound; as, the arrow hissed as it flew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hiss', 'v. t.', 'To condemn or express contempt for by hissing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hiss', 'v. t.', 'To utter with a hissing sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hiss', 'n.', 'A prolonged sound like that letter s, made by forcing out the\n   breath between the tongue and teeth, esp. as a token of disapprobation\n   or contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hiss', 'n.', 'Any sound resembling that above described');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hiss', 'n.', 'The noise made by a serpent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hiss', 'n.', 'The note of a goose when irritated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hiss', 'n.', 'The noise made by steam escaping through a narrow orifice, or\n   by water falling on a hot stove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hissing', 'n.', 'The act of emitting a hiss or hisses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hissing', 'n.', 'The occasion of contempt; the object of scorn and\n   derision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hissingly', 'adv.', 'With a hissing sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hist', 'interj.', 'Hush; be silent; -- a signal for silence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Histiology', 'n.', 'Same as Histology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Histogenesis', 'n.', 'The formation and development of organic tissues;\n   histogeny; -- the opposite of histolysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Histogenesis', 'n.', 'Germ history of cells, and of the tissues composed of\n   cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Histogenetic', 'a.', 'Tissue-producing; connected with the formation and\n   development of the organic tissues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Histogeny', 'n.', 'Same as Histogenesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Histographer', 'n.', 'One who describes organic tissues; an histologist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Histographical', 'a.', 'Of or pertaining to histography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Histography', 'n.', 'A description of, or treatise on, organic tissues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Histohaematin', 'n.', 'One of a class of respiratory pigments, widely\n   distributed in the animal kingdom, capable of ready oxidation and\n   reduction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Histoid', 'a.', 'Resembling the normal tissues; as, histoid tumors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Histologic', 'a.', 'Alt. of Histological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Histological', 'a.', 'Pertaining to histology, or to the microscopic\n   structure of the tissues of living organisms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Histologist', 'n.', 'One versed in histology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Histology', 'n.', 'That branch of biological science, which treats of the\n   minute (microscopic) structure of animal and vegetable tissues; --\n   called also histiology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Histolysis', 'n.', 'The decay and dissolution of the organic tissues and of\n   the blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Histolytic', 'a.', 'Of or pertaining to histolysis, or the degeneration of\n   tissues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Histonomy', 'n.', 'The science which treats of the laws relating to organic\n   tissues, their formation, development, functions, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Histophyly', 'n.', 'The tribal history of cells, a division of morphophyly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Historial', 'a.', 'Historical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Historian', 'n.', 'A writer of history; a chronicler; an annalist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Historian', 'n.', 'One versed or well informed in history.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Historic', 'a.', 'Alt. of Historical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Historical', 'a.', 'Of or pertaining to history, or the record of past\n   events; as, an historical poem; the historic page.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Historically', 'adv.', 'In the manner of, or in accordance with, history.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Historicize', 'v. t.', 'To record or narrate in the manner of a history; to\n   chronicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Historied', 'a.', 'Related in history.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Historier', 'n.', 'An historian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Historiette', 'n.', 'Historical narration on a small scale; a brief\n   recital; a story.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Histority', 'v. t.', 'To record in or as history.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Historiographer', 'n.', 'An historian; a writer of history; especially, one\n   appointed or designated to write a history; also, a title bestowed by\n   some governments upon historians of distinction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Historiographership', 'n.', 'The office of an historiographer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Historiography', 'n.', 'The art of employment of an historiographer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Historiology', 'n.', 'A discourse on history.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Historionomer', 'n.', 'One versed in the phenomena of history and the laws\n   controlling them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Historize', 'v. t.', 'To relate as history; to chronicle; to historicize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Histories', 'pl. ', 'of History');
  INSERT INTO words (word, word_type, description) VALUES  (    'History', 'n.', 'A learning or knowing by inquiry; the knowledge of facts\n   and events, so obtained; hence, a formal statement of such information;\n   a narrative; a description; a written record; as, the history of a\n   patients case; the history of a legislative bill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'History', 'n.', 'A systematic, written account of events, particularly of\n   those affecting a nation, institution, science, or art, and usually\n   connected with a philosophical explanation of their causes; a true\n   story, as distinguished from a romance; -- distinguished also from\n   annals, which relate simply the facts and events of each year, in\n   strict chronological order; from biography, which is the record of an\n   individuals life; and from memoir, which is history composed from\n   personal experience, observation, and memory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'History', 'v. t.', 'To narrate or record.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Histotomy', 'n.', 'The dissection of organic tissues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Histozyme', 'n.', 'A soluble ferment occurring in the animal body, to the\n   presence of which many normal decompositions and synthetical processes\n   are supposed to be due.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Histrion', 'n.', 'A player.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Histrionic', 'a.', 'Alt. of Histrionical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Histrionical', 'a.', 'Of or relating to the stage or a stageplayer;\n   befitting a theatre; theatrical; -- sometimes in a bad sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Histrionicism', 'n.', 'The histronic art; stageplaying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Histrionism', 'n.', 'Theatrical representation; acting; affectation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Histrionize', 'v. t.', 'To act; to represent on the stage, or theatrically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hit', 'pron.', 'It.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hit', '', '3d pers. sing. pres. of Hide, contracted from hideth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hit', 'imp. & p. p.', 'of Hit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hitting', 'p. pr. & vb. n.', 'of Hit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hit', 'v. t.', 'To reach with a stroke or blow; to strike or touch, usually\n   with force; especially, to reach or touch (an object aimed at).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hit', 'v. t.', 'To reach or attain exactly; to meet according to the\n   occasion; to perform successfully; to attain to; to accord with; to be\n   conformable to; to suit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hit', 'v. t.', 'To guess; to light upon or discover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hit', 'v. t.', 'To take up, or replace by a piece belonging to the opposing\n   player; -- said of a single unprotected piece on a point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hit', 'v. i.', 'To meet or come in contact; to strike; to clash; --\n   followed by against or on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hit', 'v. i.', 'To meet or reach what was aimed at or desired; to succeed,\n   -- often with implied chance, or luck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hit', 'n.', 'A striking against; the collision of one body against another;\n   the stroke that touches anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hit', 'n.', 'A stroke of success in an enterprise, as by a fortunate\n   chance; as, he made a hit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hit', 'n.', 'A peculiarly apt expression or turn of thought; a phrase which\n   hits the mark; as, a happy hit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hit', 'n.', 'A game won at backgammon after the adversary has removed some\n   of his men. It counts less than a gammon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hit', 'n.', 'A striking of the ball; as, a safe hit; a foul hit; --\n   sometimes used specifically for a base hit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hit.', 'adj.', 'having become very popular or acclaimed; -- said of\n   entertainment performances; as, a hit record, a hit movie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hitch', 'v. t.', 'To become entangled or caught; to be linked or yoked; to\n   unite; to cling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hitch', 'v. t.', 'To move interruptedly or with halts, jerks, or steps; --\n   said of something obstructed or impeded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hitch', 'v. t.', 'To hit the legs together in going, as horses; to\n   interfere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hitched', 'imp. & p. p.', 'of Hitch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hitching', 'p. pr. & vb. n.', 'of Hitch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hitch', 'v. t.', 'To hook; to catch or fasten as by a hook or a knot; to\n   make fast, unite, or yoke; as, to hitch a horse, or a halter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hitch', 'v. t.', 'To move with hitches; as, he hitched his chair nearer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hitch', 'n.', 'A catch; anything that holds, as a hook; an impediment; an\n   obstacle; an entanglement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hitch', 'n.', 'The act of catching, as on a hook, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hitch', 'n.', 'A stop or sudden halt; a stoppage; an impediment; a\n   temporary obstruction; an obstacle; as, a hitch in ones progress or\n   utterance; a hitch in the performance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hitch', 'n.', 'A sudden movement or pull; a pull up; as, the sailor gave\n   his trousers a hitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hitch', 'n.', 'A knot or noose in a rope which can be readily undone; --\n   intended for a temporary fastening; as, a half hitch; a clove hitch; a\n   timber hitch, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hitch', 'n.', 'A small dislocation of a bed or vein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hitchel', 'n. & v. t.', 'See Hatchel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hithe', 'n.', 'A port or small haven; -- used in composition; as,\n   Lambhithe, now Lambeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hither', 'adv.', 'To this place; -- used with verbs signifying motion, and\n   implying motion toward the speaker; correlate of hence and thither; as,\n   to come or bring hither.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hither', 'adv.', 'To this point, source, conclusion, design, etc.; -- in a\n   sense not physical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hither', 'a.', 'Being on the side next or toward the person speaking;\n   nearer; -- correlate of thither and farther; as, on the hither side of\n   a hill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hither', 'a.', 'Applied to time: On the hither side of, younger than; of\n   fewer years than.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hithermost', 'a.', 'Nearest on this side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hitherto', 'adv.', 'To this place; to a prescribed limit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hitherto', 'adv.', 'Up to this time; as yet; until now.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hitherward', 'adv.', 'Toward this place; hither.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hitter', 'n.', 'One who hits or strikes; as, a hard hitter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hive', 'n.', 'A box, basket, or other structure, for the reception and\n   habitation of a swarm of honeybees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hive', 'n.', 'The bees of one hive; a swarm of bees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hive', 'n.', 'A place swarming with busy occupants; a crowd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hived', 'imp. & p. p.', 'of Hive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hiving', 'p. pr. & vb. n.', 'of Hive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hive', 'v. t.', 'To collect into a hive; to place in, or cause to enter, a\n   hive; as, to hive a swarm of bees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hive', 'v. t.', 'To store up in a hive, as honey; hence, to gather and\n   accumulate for future need; to lay up in store.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hive', 'v. i.', 'To take shelter or lodgings together; to reside in a\n   collective body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hiveless', 'a.', 'Destitute of a hive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hiver', 'n.', 'One who collects bees into a hive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hives', 'n.', 'The croup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hives', 'n.', 'An eruptive disease (Varicella globularis), allied to the\n   chicken pox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hizz', 'v. i.', 'To hiss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ho', 'pron.', 'Who.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ho', 'interj.', 'Alt. of Hoa');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoa', 'interj.', 'A stop; a halt; a moderation of pace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ho', 'interj.', 'Alt. of Hoa');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoa', 'interj.', 'Halloo! attend! -- a call to excite attention, or to give\n   notice of approach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoa', 'interj.', 'Stop! stand still! hold! -- a word now used by teamsters,\n   but formerly to order the cessation of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoar', 'a.', 'White, or grayish white; as, hoar frost; hoar cliffs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoar', 'a.', 'Gray or white with age; hoary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoar', 'a.', 'Musty; moldy; stale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoar', 'n.', 'Hoariness; antiquity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoar', 'v. t.', 'To become moldy or musty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoard', 'n.', 'See Hoarding, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoard', 'n.', 'A store, stock, or quantity of anything accumulated or laid\n   up; a hidden supply; a treasure; as, a hoard of provisions; a hoard of\n   money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoarded', 'imp. & p. p.', 'of Hoard');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoarding', 'p. pr. & vb. n.', 'of Hoard');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoard', 'v. t.', 'To collect and lay up; to amass and deposit in secret; to\n   store secretly, or for the sake of keeping and accumulating; as, to\n   hoard grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoard', 'v. i.', 'To lay up a store or hoard, as of money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoarder', 'n.', 'One who hoards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoarding', 'n.', 'A screen of boards inclosing a house and materials while\n   builders are at work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoarding', 'n.', 'A fence, barrier, or cover, inclosing, surrounding, or\n   concealing something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoared', 'a.', 'Moldy; musty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoarfrost', 'n.', 'The white particles formed by the congelation of dew;\n   white frost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoarhound', 'n.', 'Same as Horehound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoariness', 'n.', 'The state of being hoary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoarse', 'superl.', 'Having a harsh, rough, grating voice or sound, as when\n   affected with a cold; making a rough, harsh cry or sound; as, the\n   hoarse raven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoarse', 'superl.', 'Harsh; grating; discordant; -- said of any sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoarsely', 'adv.', 'With a harsh, grating sound or voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoarsened', 'imp. & p. p.', 'of Hoarsen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoarsening', 'p. pr. & vb. n.', 'of Hoarsen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoarsen', 'v. t.', 'To make hoarse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoarseness', 'n.', 'Harshness or roughness of voice or sound, due to mucus\n   collected on the vocal cords, or to swelling or looseness of the cords.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoarstone', 'n.', 'A stone designating the /ounds of an estate; a landmark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoary', 'a.', 'White or whitish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoary', 'a.', 'White or gray with age; hoar; as, hoary hairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoary', 'a.', 'remote in time past; as, hoary antiquity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoary', 'a.', 'Moldy; mossy; musty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoary', 'a.', 'Of a pale silvery gray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoary', 'a.', 'Covered with short, dense, grayish white hairs; canescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoatzin', 'n.', 'Same as Hoazin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoax', 'n.', 'A deception for mockery or mischief; a deceptive trick or\n   story; a practical joke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoaxed', 'imp. & p. p.', 'of Hoax');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoaxing', 'p. pr. & vb. n.', 'of Hoax');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoax', 'v. t.', 'To deceive by a story or a trick, for sport or mischief;\n   to impose upon sportively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoaxer', 'n.', 'One who hoaxes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoazin', 'n.', 'A remarkable South American bird (Opisthocomus cristatus);\n   the crested touraco. By some zoologists it is made the type of a\n   distinct order (Opisthocomi).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hob', 'n.', 'The hub of a wheel. See Hub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hob', 'n.', 'The flat projection or iron shelf at the side of a fire grate,\n   where things are put to be kept warm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hob', 'n.', 'A threaded and fluted hardened steel cutter, resembling a tap,\n   used in a lathe for forming the teeth of screw chasers, worm wheels,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hob', 'n.', 'A fairy; a sprite; an elf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hob', 'n.', 'A countryman; a rustic; a clown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobanob', 'v. i.', 'Alt. of Hobandnob');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobandnob', 'v. i.', 'Same as Hobnob.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobbism', 'n.', 'The philosophical system of Thomas Hobbes, an English\n   materialist (1588-1679); esp., his political theory that the most\n   perfect form of civil government is an absolute monarchy with despotic\n   control over everything relating to law, morals, and religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobbist', 'n.', 'One who accepts the doctrines of Thomas Hobbes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobbled', 'imp. & p. p.', 'of Hobble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobbling', 'p. pr. & vb. n.', 'of Hobble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobble', 'n. i.', 'To walk lame, bearing chiefly on one leg; to walk with a\n   hitch or hop, or with crutches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobble', 'n. i.', 'To move roughly or irregularly; -- said of style in\n   writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobble', 'v. t.', 'To fetter by tying the legs; to hopple; to clog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobble', 'v. t.', 'To perplex; to embarrass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobble', 'n.', 'An unequal gait; a limp; a halt; as, he has a hobble in his\n   gait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobble', 'n.', 'Same as Hopple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobble', 'n.', 'Difficulty; perplexity; embarrassment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobblebush', 'n.', 'A low bush (Viburnum lantanoides) having long,\n   straggling branches and handsome flowers. It is found in the Northern\n   United States. Called also shinhopple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobbledehoy', 'n.', 'Alt. of Hobbletehoy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobbletehoy', 'n.', 'A youth between boy and man; an awkward, gawky young\n   fellow .');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobbler', 'n.', 'One who hobbles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobbler', 'n.', 'One who by his tenure was to maintain a horse for military\n   service; a kind of light horseman in the Middle Ages who was mounted on\n   a hobby.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobblingly', 'adv.', 'With a limping step.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobbly', 'a.', 'Rough; uneven; causing one to hobble; as a hobbly road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobbies', 'pl. ', 'of Hobby');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobby', 'n.', 'A small, strong-winged European falcon (Falco subbuteo),\n   formerly trained for hawking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobby', 'n.', 'Alt. of Hobbyhorse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobbyhorse', 'n.', 'A strong, active horse, of a middle size, said to have\n   been originally from Ireland; an ambling nag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobbyhorse', 'n.', 'A stick, often with the head or figure of a horse, on\n   which boys make believe to ride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobbyhorse', 'n.', 'A subject or plan upon which one is constantly setting\n   off; a favorite and ever-recurring theme of discourse, thought, or\n   effort; that which occupies ones attention unduly, or to the weariness\n   of others; a ruling passion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobbyhorsical', 'n.', 'Pertaining to, or having, a hobby or whim;\n   eccentric; whimsical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobgoblin', 'n.', 'A frightful goblin; an imp; a bugaboo; also, a name\n   formerly given to the household spirit, Robin Goodfellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobiler', 'n.', 'A light horseman. See 2d Hobbler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobit', 'n.', 'A small mortar on a gun carriage, in use before the\n   howitzer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobnail', 'n.', 'A short, sharp-pointed, large-headed nail, -- used in\n   shoeing houses and for studding the soles of heavy shoes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobnail', 'n.', 'A clownish person; a rustic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobnail', 'v. t.', 'To tread down roughly, as with hobnailed shoes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobnailed', 'a.', 'See with hobnails, as a shoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobnob', 'adv.', 'Have or have not; -- a familiar invitation to reciprocal\n   drinking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobnob', 'adv.', 'At random; hit or miss. (Obs.)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hornobbed', 'imp. & p. p.', 'of Hobnob');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hornobbing', 'p. pr. & vb. n.', 'of Hobnob');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobnob', 'v. i.', 'To drink familiarly (with another).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobnob', 'v. i.', 'To associate familiarly; to be on intimate terms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobnob', 'n.', 'Familiar, social intercourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobornob', 'adv.', 'See Hobnob.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoboy', 'n.', 'A hautboy or oboe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hobsons choice', '', 'A choice without an alternative; the thing offered\n   or nothing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hocco', 'n.', 'The crested curassow; -- called also royal pheasant. See\n   Curassow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hochepot', 'n.', 'Hotchpot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hock', 'n.', 'A Rhenish wine, of a light yellow color, either sparkling or\n   still. The name is also given indiscriminately to all Rhenish wines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hock', 'n.', 'Alt. of Hough');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hough', 'n.', 'The joint in the hind limb of quadrupeds between the leg and\n   shank, or tibia and tarsus, and corresponding to the ankle in man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hough', 'n.', 'A piece cut by butchers, esp. in pork, from either the front\n   or hind leg, just above the foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hough', 'n.', 'The popliteal space; the ham.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hock', 'v. t.', 'To disable by cutting the tendons of the hock; to\n   hamstring; to hough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hockamore', 'n.', 'A Rhenish wine. [Obs.] See Hock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hockday', 'n.', 'A holiday commemorating the expulsion of the Danes,\n   formerly observed on the second Tuesday after Easter; -- called also\n   hocktide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hockey', 'n.', 'A game in which two parties of players, armed with sticks\n   curved or hooked at the end, attempt to drive any small object (as a\n   ball or a bit of wood) toward opposite goals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hockey', 'n.', 'The stick used by the players.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hockherb', 'n.', 'The mallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hockled', 'imp. & p. p.', 'of Hockle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hockling', 'p. pr. & vb. n.', 'of Hockle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hockle', 'v. t.', 'To hamstring; to hock; to hough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hockle', 'v. t.', 'To mow, as stubble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hocus', 'v. t.', 'To deceive or cheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hocus', 'v. t.', 'To adulterate; to drug; as, liquor is said to be hocused\n   for the purpose of stupefying the drinker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hocus', 'v. t.', 'To stupefy with drugged liquor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hocus', 'n.', 'One who cheats or deceives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hocus', 'n.', 'Drugged liquor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hocuspocus', 'n.', 'A term used by jugglers in pretended incantations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hocuspocus', 'n.', 'A juggler or trickster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hocuspocus', 'n.', 'A jugglers trick; a cheat; nonsense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hocuspocus', 'v. t.', 'To cheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hod', 'n.', 'A kind of wooden tray with a handle, borne on the shoulder,\n   for carrying mortar, brick, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hod', 'n.', 'A utensil for holding coal; a coal scuttle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoddengray', 'a.', 'Applied to coarse cloth made of undyed wool, formerly\n   worn by Scotch peasants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoddy', 'n.', 'See Dun crow, under Dun, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoddydoddy', 'n.', 'An awkward or foolish person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hodgepodge', 'n.', 'A mixed mass; a medley. See Hotchpot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hodgkins disease', '', 'A morbid condition characterized by progressive\n   anaemia and enlargement of the lymphatic glands; -- first described by\n   Dr. Hodgkin, an English physician.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hodiern', 'a.', 'Alt. of Hodiernal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hodiernal', 'a.', 'Of this day; belonging to the present day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hodmen', 'pl. ', 'of Hodman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hodman', 'n.', 'A man who carries a hod; a masons tender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hodmandod', 'n.', 'See Dodman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hodograph', 'n.', 'A curve described by the moving extremity of a line the\n   other end of which is fixed, this line being constantly parallel to the\n   direction of motion of, and having its length constantly proportional\n   to the velocity of, a point moving in any path; -used in investigations\n   respecting central forces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hodometer', 'n.', 'See Odometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoe', 'n.', 'A tool chiefly for digging up weeds, and arranging the earth\n   about plants in fields and gardens. It is made of a flat blade of iron\n   or steel having an eye or tang by which it is attached to a wooden\n   handle at an acute angle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoe', 'n.', 'The horned or piked dogfish. See Dogfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoed', 'imp. & p. p.', 'of Hoe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoeing', 'p. pr. & vb. n.', 'of Hoe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoe', 'v. t.', 'To cut, dig, scrape, turn, arrange, or clean, with a hoe;\n   as, to hoe the earth in a garden; also, to clear from weeds, or to\n   loosen or arrange the earth about, with a hoe; as, to hoe corn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoe', 'v. i.', 'To use a hoe; to labor with a hoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoecake', 'n.', 'A cake of Indian meal, water, and salt, baked before the\n   fire or in the ashes; -- so called because often cooked on a hoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoemother', 'n.', 'The basking or liver shark; -- called also homer. See\n   Liver shark, under Liver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoful', 'a.', 'Careful; wary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hog', 'n.', 'A quadruped of the genus Sus, and allied genera of Suidae;\n   esp., the domesticated varieties of S. scrofa, kept for their fat and\n   meat, called, respectively, lard and pork; swine; porker; specifically,\n   a castrated boar; a barrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hog', 'n.', 'A mean, filthy, or gluttonous fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hog', 'n.', 'A young sheep that has not been shorn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hog', 'n.', 'A rough, flat scrubbing broom for scrubbing a ships bottom\n   under water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hog', 'n.', 'A device for mixing and stirring the pulp of which paper is\n   made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hogged', 'imp. & p. p.', 'of Hog');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hogging', 'p. pr. & vb. n.', 'of Hog');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hog', 'v. t.', 'To cut short like bristles; as, to hog the mane of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hog', 'v. t.', 'To scrub with a hog, or scrubbing broom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hog', 'v. i.', 'To become bent upward in the middle, like a hogs back; --\n   said of a ship broken or strained so as to have this form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hogback', 'n.', 'An upward curve or very obtuse angle in the upper surface\n   of any member, as of a timber laid horizontally; -- the opposite of\n   camber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hogback', 'n.', 'See Hogframe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hogback', 'n.', 'A ridge formed by tilted strata; hence, any ridge with a\n   sharp summit, and steeply sloping sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hogchain', 'n.', 'A chain or tie rod, in a boat or barge, to prevent the\n   vessel from hogging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hogchoker', 'n.', 'An American sole (Achirus lineatus, or A. achirus),\n   related to the European sole, but of no market value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hogcote', 'n.', 'A shed for swine; a sty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hogfish', 'n.', 'A large West Indian and Florida food fish (Lachnolaemus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hogfish', 'n.', 'The pigfish or sailors choice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hogfish', 'n.', 'An American fresh-water fish; the log perch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hogfish', 'n.', 'A large, red, spiny-headed, European marine fish\n   (Scorpaena scrofa).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hogframe', 'n.', 'A trussed frame extending fore and aft, usually above\n   deck, and intended to increase the longitudinal strength and stiffness.\n   Used chiefly in American river and lake steamers. Called also hogging\n   frame, and hogback.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hogged', 'a.', 'Broken or strained so as to have an upward curve between\n   the ends. See Hog, v. i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hogger', 'n.', 'A stocking without a foot, worn by coal miners at work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoggerel', 'n.', 'A sheep of the second year. [Written also hogrel.] Ash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoggerpipe', 'n.', 'The upper terminal pipe of a mining pump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hogger-pump', 'n.', 'The for pump in the pit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoggery', 'n.', 'Hoggish character or manners; selfishness; greed;\n   beastliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hogget', 'n.', 'A young boar of the second year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hogget', 'n.', 'A sheep or colt alter it has passed its first year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hogging', 'n.', 'Drooping at the ends; arching;-in distinction from\n   sagging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoggish', 'a.', 'Swinish; gluttonous; filthy; selfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hogh', 'n.', 'A hill; a cliff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hogherd', 'n.', 'A swineherd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hogmanay', 'n.', 'The old name, in Scotland, for the last day of the year,\n   on which children go about singing, and receive a dole of bread or\n   cakes; also, the entertainment given on that day to a visitor, or the\n   gift given to an applicant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hognosesnake', '', 'A harmless North American snake of the genus Heterodon,\n   esp. H. platyrhynos; -- called also puffing adder, blowing adder, and\n   sand viper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hognut', 'n.', 'The pignut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hognut', 'n.', 'In England, the Bunium flexuosum, a tuberous plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hogo', 'n.', 'High flavor; strong scent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hogpen', 'n.', 'A pen or sty for hogs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hogreeve', 'n.', 'A civil officer charged with the duty of impounding hogs\n   running at large.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hogringer', 'n.', 'One who puts rings into the snouts of hogs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hogs-back', 'n.', 'A hogback.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hogscore', 'n.', 'A distance lime brawn across the rink or course between\n   the middle line and the tee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hogshead', 'n.', 'An English measure of capacity, containing 63 wine\n   gallons, or about 52/ imperial gallons; a half pipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hogshead', 'n.', 'A large cask or barrel, of indefinite contents; esp. one\n   containing from 100 to 140 gallons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hogskin', 'n.', 'Leather tanned from a hogs skin. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hogsties', 'pl. ', 'of Hogsty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hogsty', 'n.', 'A pen, house, or inclosure, for hogs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hogwash', 'n.', 'Swill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hogweed', 'n.', 'A common weed (Ambrosia artemisiaege). See Ambrosia, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hogweed', 'n.', 'In England, the Heracleum Sphondylium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoiden', 'n.', 'A rude, clownish youth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoiden', 'n.', 'A rude, bold girl; a romp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoiden', 'a.', 'Rustic; rude; bold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoiden', 'v. i.', 'To romp rudely or indecently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoidenhood', 'n.', 'State of being a hoiden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoidenish', 'a.', 'Like, or appropriate to, a hoiden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoise', 'v. t.', 'To hoist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoisted', 'imp. & p. p.', 'of Hoist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoisting', 'p. pr. & vb. n.', 'of Hoist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoist', 'v. t.', 'To raise; to lift; to elevate; esp., to raise or lift to\n   a desired elevation, by means of tackle, as a sail, a flag, a heavy\n   package or weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoist', 'n.', 'That by which anything is hoisted; the apparatus for lifting\n   goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoist', 'n.', 'The act of hoisting; a lift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoist', 'n.', 'The perpendicular height of a flag, as opposed to the fly,\n   or horizontal length when flying from a staff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoist', 'n.', 'The height of a fore-and-aft sail next the mast or stay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoist', 'p. p.', 'Hoisted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoistaway', 'n.', 'A mechanical lift. See Elevator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoistway', 'n.', 'An opening for the hoist, or elevator, in the floor of a\n   wareroom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoit', 'v. i.', 'To leap; to caper; to romp noisily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoity-toity', 'a.', 'Thoughtless; giddy; flighty; also, haughty;\n   patronizing; as, to be in hoity-toity spirits, or to assume hoity-toity\n   airs; used also as an exclamation, denoting surprise or disapprobation,\n   with some degree of contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hokeday', 'n.', 'Same as Hockday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoker', 'n.', 'Scorn; derision; abusive talk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hol', 'a.', 'Whole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holaspidean', 'a.', 'Having a single series of large scutes on the\n   posterior side of the tarsus; -- said of certain birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holcad', 'n.', 'A large ship of burden, in ancient Greece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hold', 'n.', 'The whole interior portion of a vessel below the lower deck,\n   in which the cargo is stowed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Held', 'imp. & p. p.', 'of Hold');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holding', 'p. pr. & vb. n.', 'of Hold');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holden', '', 'of Hold');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hold', 'v. t.', 'To cause to remain in a given situation, position, or\n   relation, within certain limits, or the like; to prevent from falling\n   or escaping; to sustain; to restrain; to keep in the grasp; to retain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hold', 'v. t.', 'To retain in ones keeping; to maintain possession of, or\n   authority over; not to give up or relinquish; to keep; to defend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hold', 'v. t.', 'To have; to possess; to be in possession of; to occupy; to\n   derive title to; as, to hold office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hold', 'v. t.', 'To impose restraint upon; to limit in motion or action; to\n   bind legally or morally; to confine; to restrain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hold', 'v. t.', 'To maintain in being or action; to carry on; to prosecute,\n   as a course of conduct or an argument; to continue; to sustain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hold', 'v. t.', 'To prosecute, have, take, or join in, as something which\n   is the result of united action; as to, hold a meeting, a festival, a\n   session, etc.; hence, to direct and bring about officially; to conduct\n   or preside at; as, the general held a council of war; a judge holds a\n   court; a clergyman holds a service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hold', 'v. t.', 'To receive and retain; to contain as a vessel; as, this\n   pail holds milk; hence, to be able to receive and retain; to have\n   capacity or containing power for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hold', 'v. t.', 'To accept, as an opinion; to be the adherent of, openly or\n   privately; to persist in, as a purpose; to maintain; to sustain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hold', 'v. t.', 'To consider; to regard; to esteem; to account; to think;\n   to judge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hold', 'v. t.', 'To bear, carry, or manage; as he holds himself erect; he\n   holds his head high.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hold', 'n. i.', 'In general, to keep ones self in a given position or\n   condition; to remain fixed. Hence:');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hold', 'n. i.', 'Not to more; to halt; to stop;-mostly in the imperative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hold', 'n. i.', 'Not to give way; not to part or become separated; to\n   remain unbroken or unsubdued.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hold', 'n. i.', 'Not to fail or be found wanting; to continue; to last; to\n   endure a test or trial; to abide; to persist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hold', 'n. i.', 'Not to fall away, desert, or prove recreant; to remain\n   attached; to cleave;-often with with, to, or for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hold', 'n. i.', 'To restrain ones self; to refrain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hold', 'n. i.', 'To derive right or title; -- generally with of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hold', 'n.', 'The act of holding, as in or with the hands or arms; the\n   manner of holding, whether firm or loose; seizure; grasp; clasp; gripe;\n   possession; -- often used with the verbs take and lay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hold', 'n.', 'The authority or ground to take or keep; claim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hold', 'n.', 'Binding power and influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hold', 'n.', 'Something that may be grasped; means of support.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hold', 'n.', 'A place of confinement; a prison; confinement; custody;\n   guard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hold', 'n.', 'A place of security; a fortified place; a fort; a castle; --\n   often called a stronghold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hold', 'n.', 'A character [thus /] placed over or under a note or rest, and\n   indicating that it is to be prolonged; -- called also pause, and\n   corona.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holdback', 'n.', 'Check; hindrance; restraint; obstacle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holdback', 'n.', 'The projection or loop on the thill of a vehicle. to\n   which a strap of the harness is attached, to hold back a carriage when\n   going down hill, or in backing; also, the strap or part of the harness\n   so used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holder', 'n.', 'One who is employed in the hold of a vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holder', 'n.', 'One who, or that which, holds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holder', 'n.', 'One who holds land, etc., under another; a tenant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holder', 'n.', 'The payee of a bill of exchange or a promissory note, or\n   the one who owns or holds it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holder-forth', 'n.', 'One who speaks in public; an haranguer; a preacher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holdfast', 'n.', 'Something used to secure and hold in place something\n   else, as a long fiat-headed nail, a catch a hook, a clinch, a clamp,\n   etc.; hence, a support.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holdfast', 'n.', 'A conical or branching body, by which a seaweed is\n   attached to its support, and differing from a root in that it is not\n   specially absorbent of moisture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holding', 'n.', 'The act or state of sustaining, grasping, or retaining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holding', 'n.', 'A tenure; a farm or other estate held of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holding', 'n.', 'That which holds, binds, or influences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holding', 'n.', 'The burden or chorus of a song.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hole', 'a.', 'Whole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hole', 'n.', 'A hollow place or cavity; an excavation; a pit; an opening in\n   or through a solid body, a fabric, etc.; a perforation; a rent; a\n   fissure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hole', 'n.', 'An excavation in the ground, made by an animal to live in, or\n   a natural cavity inhabited by an animal; hence, a low, narrow, or dark\n   lodging or place; a mean habitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hole', 'n.', 'To cut, dig, or bore a hole or holes in; as, to hole a post\n   for the insertion of rails or bars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hole', 'n.', 'To drive into a hole, as an animal, or a billiard ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hole', 'v. i.', 'To go or get into a hole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holethnic', 'a.', 'Of or pertaining to a holethnos or parent race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holethnos', 'n.', 'A parent stock or race of people, not yet divided into\n   separate branches or tribes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holibut', 'n.', 'See Halibut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holidam', 'n.', 'See Halidom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holiday', 'n.', 'A consecrated day; religious anniversary; a day set apart\n   in honor of some person, or in commemoration of some event. See\n   Holyday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holiday', 'n.', 'A day of exemption from labor; a day of amusement and\n   gayety; a festival day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holiday', 'n.', 'A day fixed by law for suspension of business; a legal\n   holiday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holiday', 'a.', 'Of or pertaining to a festival; cheerful; joyous; gay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holiday', 'a.', 'Occurring rarely; adapted for a special occasion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holily', 'adv.', 'Piously; with sanctity; in a holy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holily', 'adv.', 'Sacredly; inviolably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holiness', 'n.', 'The state or quality of being holy; perfect moral\n   integrity or purity; freedom from sin; sanctity; innocence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holiness', 'n.', 'The state of being hallowed, or consecrated to God or to\n   his worship; sacredness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holing', 'n.', 'Undercutting in a bed of coal, in order to bring down the\n   upper mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holla', 'interj.', 'Hollo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hollaed', 'imp. & p. p.', 'of Holla');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hollaing', 'p. pr. & vb. n.', 'of Holla');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holla', 'v. i.', 'See Hollo, v. i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holland', 'n.', 'A kind of linen first manufactured in Holland; a linen\n   fabric used for window shades, childrens garments, etc.; as, brown or\n   unbleached hollands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hollander', 'n.', 'A native or one of the people of Holland; a Dutchman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hollander', 'n.', 'A very hard, semi-glazed, green or dark brown brick,\n   which will not absorb water; -- called also, Dutch clinker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hollandish', 'a.', 'Relating to Holland; Dutch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hollands', 'n.', 'Gin made in Holland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hollands', 'n.', 'See Holland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hollo', 'interj. & n.', 'Ho there; stop; attend; hence, a loud cry or a\n   call to attract attention; a halloo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holloed', 'imp. & p. p.', 'of Hollo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holloing', 'p. pr. & vb. n.', 'of Hollo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hollo', 'interj.', 'To call out or exclaim; to halloo. This form is now\n   mostly replaced by hello.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holloa', 'n. & v. i.', 'Same as Hollo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hollow', 'a.', 'Having an empty space or cavity, natural or artificial,\n   within a solid substance; not solid; excavated in the interior; as, a\n   hollow tree; a hollow sphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hollow', 'a.', 'Depressed; concave; gaunt; sunken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hollow', 'a.', 'Reverberated from a cavity, or resembling such a sound;\n   deep; muffled; as, a hollow roar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hollow', 'a.', 'Not sincere or faithful; false; deceitful; not sound; as, a\n   hollow heart; a hollow friend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hollow', 'n.', 'A cavity, natural or artificial; an unfilled space within\n   anything; a hole, a cavern; an excavation; as the hollow of the hand or\n   of a tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hollow', 'n.', 'A low spot surrounded by elevations; a depressed part of a\n   surface; a concavity; a channel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hollowed', 'imp. & p. p.', 'of Hollow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hollowing', 'p. pr. & vb. n.', 'of Hollow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hollow', 'v. t.', 'To make hollow, as by digging, cutting, or engraving; to\n   excavate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hollow', 'adv.', 'Wholly; completely; utterly; -- chiefly after the verb to\n   beat, and often with all; as, this story beats the other all hollow.\n   See All, adv.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hollow', 'interj.', 'Hollo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hollow', 'v. i.', 'To shout; to hollo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hollow', 'v. t.', 'To urge or call by shouting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hollow-hearted', 'a.', 'Insincere; deceitful; not sound and true; having a\n   cavity or decayed spot within.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hollow-horned', 'a.', 'Having permanent horns with a bony core, as cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hollowly', 'adv.', 'Insincerely; deceitfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hollowness', 'n.', 'State of being hollow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hollowness', 'n.', 'Insincerity; unsoundness; treachery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holly', 'adv.', 'Wholly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holly', 'n.', 'A tree or shrub of the genus Ilex. The European species\n   (Ilex Aguifolium) is best known, having glossy green leaves, with a\n   spiny, waved edge, and bearing berries that turn red or yellow about\n   Michaelmas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holly', 'n.', 'The holm oak. See 1st Holm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hollyhock', 'n.', 'A species of Althaea (A. rosea), bearing flowers of\n   various colors; -- called also rose mallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holm', 'n.', 'A common evergreen oak, of Europe (Quercus Ilex); -- called\n   also ilex, and holly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holm', 'n.', 'An islet in a river.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holm', 'n.', 'Low, flat land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holmia', 'n.', 'An oxide of holmium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holmium', 'n.', 'A rare element said to be contained in gadolinite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holmos', 'n.', 'A name given to a vase having a rounded body');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holmos', 'n.', 'A closed vessel of nearly spherical form on a high stem or\n   pedestal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holmos', 'n.', 'A drinking cup having a foot and stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holo-', '', 'A combining form fr. Gr. \"o`los whole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holoblast', 'n.', 'an ovum composed entirely of germinal matter. See\n   Meroblast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holoblastic', 'a.', 'Undergoing complete segmentation; composed entirely of\n   germinal matter, the whole of the yolk undergoing fission; -- opposed\n   to meroblastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holocaust', 'n.', 'A burnt sacrifice; an offering, the whole of which was\n   consumed by fire, among the Jews and some pagan nations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holocaust', 'n.', 'Sacrifice or loss of many lives, as by the burning of a\n   theater or a ship. [An extended use not authorized by careful writers.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holocephali', 'n. pl.', 'An order of elasmobranch fishes, including, among\n   living species, only the chimaeras; -- called also Holocephala. See\n   Chimaera; also Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holocryptic', 'a.', 'Wholly or completely concealing; incapable of being\n   deciphered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holocrystalline', 'a.', 'Completely crystalline; -- said of a rock like\n   granite, all the constituents of which are crystalline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holograph', 'n.', 'A document, as a letter, deed, or will, wholly in the\n   handwriting of the person from whom it proceeds and whose act it\n   purports to be.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holographic', 'a.', 'Of the nature of a holograph; pertaining to\n   holographs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holohedral', 'a.', 'Having all the planes required by complete symmetry, --\n   in opposition to hemihedral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holohemihedral', 'a.', 'Presenting hemihedral forms, in which all the\n   sectants have halt the whole number of planes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holometabola', 'n. pl.', 'Those insects which have a complete\n   metamorphosis; metabola.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holometabolic', 'a.', 'Having a complete metamorphosis;-said of certain\n   insects, as the butterflies and bees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holometer', 'n.', 'An instrument for making of angular measurements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holophanerous', 'a.', 'Same as Holometabolic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holophotal', 'a.', 'Causing no loss of light; -- applied to reflectors\n   which throw back the rays of light without perceptible loss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holophote', 'n.', 'A lamp with lenses or reflectors to collect the rays of\n   light and throw them in a given direction; -- used in lighthouses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holophrastic', 'a.', 'Expressing a phrase or sentence in a single word, --\n   as is the case in the aboriginal languages of America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holophytic', 'a.', 'Wholly or distinctively vegetable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holorhinal', 'a.', 'Having the nasal bones contiguous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holosiderite', 'n.', 'Meteoric iron; a meteorite consisting of metallic\n   iron without stony matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holostean', 'a.', 'Pertaining to the Holostei.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holostei', 'n. pl.', 'An extensive division of ganoids, including the gar\n   pike, bowfin, etc.; the bony ganoids. See Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holosteric', 'a.', 'Wholly solid; -- said of a barometer constructed of\n   solid materials to show the variations of atmospheric pressure without\n   the use of liquids, as the aneroid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holostomata', 'n. pl.', 'An artificial division of gastropods, including\n   those that have an entire aperture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holostomate', 'a.', 'Same as Holostomatous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holostomatous', 'a.', 'Having an entire aperture; -- said of many univalve\n   shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holostome', 'n.', 'One of the Holostomata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holostraca', 'n. pl.', 'A division of phyllopod Crustacea, including those\n   that are entirely covered by a bivalve shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holothure', 'n.', 'A holothurian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holothurian', 'a.', 'Belonging to the Holothurioidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holothurian', 'n.', 'One of the Holothurioidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holothurioidea', 'n. pl.', 'One of the classes of echinoderms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holotricha', 'n. pl.', 'A group of ciliated Infusoria, having cilia all\n   over the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holour', 'n.', 'A whoremonger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holp', '', 'Alt. of Holpen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holpen', '', 'imp. & p. p. of Help.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holsom', 'a.', 'Wholesome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holster', 'n.', 'A leather case for a pistol, carried by a horseman at the\n   bow of his saddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holstered', 'a.', 'Bearing holsters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holt', '', '3d pers. sing. pres. of Hold, contr. from holdeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holt', 'n.', 'A piece of woodland; especially, a woody hill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holt', 'n.', 'A deep hole in a river where there is protection for fish;\n   also, a cover, a hole, or hiding place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holwe', 'a.', 'Hollow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holy', 'superl.', 'Set apart to the service or worship of God; hallowed;\n   sacred; reserved from profane or common use; holy vessels; a holy\n   priesthood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holy', 'superl.', 'Spiritually whole or sound; of unimpaired innocence and\n   virtue; free from sinful affections; pure in heart; godly; pious;\n   irreproachable; guiltless; acceptable to God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holy cross', '', 'The cross as the symbol of Christs crucifixion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holyday', 'n.', 'A religious festival.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holyday', 'n.', 'A secular festival; a holiday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holystone', 'n.', 'A stone used by seamen for scrubbing the decks of ships.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Holystone', 'v. t.', 'To scrub with a holystone, as the deck of a vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homacanth', 'a.', 'Having the dorsal fin spines symmetrical, and in the\n   same line; -- said of certain fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homage', 'n.', 'A symbolical acknowledgment made by a feudal tenant to, and\n   in the presence of, his lord, on receiving investiture of fee, or\n   coming to it by succession, that he was his man, or vassal; profession\n   of fealty to a sovereign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homage', 'n.', 'Respect or reverential regard; deference; especially,\n   respect paid by external action; obeisance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homage', 'n.', 'Reverence directed to the Supreme Being; reverential\n   worship; devout affection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homaged', 'imp. & p. p.', 'of Homage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homaging', 'p. pr. & vb. n.', 'of Homage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homage', 'v. t.', 'To pay reverence to by external action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homage', 'v. t.', 'To cause to pay homage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homageable', 'a.', 'Subject to homage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homager', 'n.', 'One who does homage, or holds land of another by homage; a\n   vassal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homalographic', 'a.', 'Same as Homolographic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homaloid', 'a.', 'Alt. of Homaloidal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homaloidal', 'a.', 'Flat; even; -- a term applied to surfaces and to\n   spaces, whether real or imagined, in which the definitions, axioms, and\n   postulates of Euclid respecting parallel straight lines are assumed to\n   hold true.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homarus', 'n.', 'A genus of decapod Crustacea, including the common\n   lobsters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homatropine', 'n.', 'An alkaloid, prepared from atropine, and from other\n   sources. It is chemically related to atropine, and is used for the same\n   purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homaxonial', 'a.', 'Relating to that kind of homology or symmetry, the\n   mathematical conception of organic form, in which all axes are equal.\n   See under Promorphology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Home', 'n.', 'See Homelyn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Home', 'n.', 'Ones own dwelling place; the house in which one lives; esp.,\n   the house in which one lives with his family; the habitual abode of\n   ones family; also, ones birthplace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Home', 'n.', 'Ones native land; the place or country in which one dwells;\n   the place where ones ancestors dwell or dwelt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Home', 'n.', 'The abiding place of the affections, especially of the\n   domestic affections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Home', 'n.', 'The locality where a thing is usually found, or was first\n   found, or where it is naturally abundant; habitat; seat; as, the home\n   of the pine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Home', 'n.', 'A place of refuge and rest; an asylum; as, a home for\n   outcasts; a home for the blind; hence, esp., the grave; the final rest;\n   also, the native and eternal dwelling place of the soul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Home', 'n.', 'The home base; he started for home.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Home', 'a.', 'Of or pertaining to ones dwelling or country; domestic; not\n   foreign; as home manufactures; home comforts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Home', 'a.', 'Close; personal; pointed; as, a home thrust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Home', 'adv.', 'To ones home or country; as in the phrases, go home, come\n   home, carry home.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Home', 'adv.', 'Close; closely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Home', 'adv.', 'To the place where it belongs; to the end of a course; to\n   the full length; as, to drive a nail home; to ram a cartridge home.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homeborn', 'a.', 'Native; indigenous; not foreign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homeborn', 'a.', 'Of or pertaining to the home or family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Home-bound', 'a.', 'Kept at home.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Home-bred', 'a.', 'Bred at home; domestic; not foreign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Home-bred', 'a.', 'Not polished; rude; uncultivated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Home-coming', 'n.', 'Return home.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Home-driven', 'a.', 'Driven to the end, as a nail; driven close.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Home-dwelling', 'a.', 'Keeping at home.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Home-felt', 'a.', 'Felt in ones own breast; inward; private.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homefield', 'n.', 'A field adjacent to its owners home.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Home-keeping', 'a.', 'Staying at home; not gadding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Home-keeping', 'n.', 'A staying at home.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homeless', 'a.', 'Destitute of a home.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homelike', 'a.', 'Like a home; comfortable; cheerful; cozy; friendly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homelily', 'adv.', 'Plainly; inelegantly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homeliness', 'n.', 'Domesticity; care of home.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homeliness', 'n.', 'Familiarity; intimacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homeliness', 'n.', 'Plainness; want of elegance or beauty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homeliness', 'n.', 'Coarseness; simplicity; want of refinement; as, the\n   homeliness of manners, or language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homeling', 'n.', 'A person or thing belonging to a home or to a particular\n   country; a native; as, a word which is a homeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homely', 'n.', 'Belonging to, or having the characteristics of, home;\n   domestic; familiar; intimate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homely', 'n.', 'Plain; unpretending; rude in appearance; unpolished; as, a\n   homely garment; a homely house; homely fare; homely manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homely', 'n.', 'Of plain or coarse features; uncomely; -- contrary to\n   handsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homely', 'adv.', 'Plainly; rudely; coarsely; as, homely dressed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homelyn', 'n.', 'The European sand ray (Raia maculata); -- called also\n   home, mirror ray, and rough ray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homemade', 'a.', 'Made at home; of domestic manufacture; made either in a\n   private family or in ones own country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homeopath', 'n.', 'A practitioner of homeopathy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homeopathic', 'a.', 'Of or pertaining to homeopathy; according to the\n   principles of homeopathy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homeopathically', 'adv.', 'According to the practice of homeopathy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homeopathist', 'n.', 'A believer in, or practitioner of, homeopathy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homeopathy', 'n.', 'The art of curing, founded on resemblances; the theory\n   and its practice that disease is cured (tuto, cito, et jucunde) by\n   remedies which produce on a healthy person effects similar to the\n   symptoms of the complaint under which the patient suffers, the remedies\n   being usually administered in minute doses. This system was founded by\n   Dr. Samuel Hahnemann, and is opposed to allopathy, or heteropathy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homer', 'n.', 'A carrier pigeon remarkable for its ability to return home\n   from a distance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homer', 'n.', 'See Hoemother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homer', 'n.', 'A Hebrew measure containing, as a liquid measure, ten baths,\n   equivalent to fifty-five gallons, two quarts, one pint; and, as a dry\n   measure, ten ephahs, equivalent to six bushels, two pecks, four quarts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homeric', 'a.', 'Of or pertaining to Homer, the most famous of Greek poets;\n   resembling the poetry of Homer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homesick', 'a.', 'Pining for home; in a nostalgic condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Home-speaking', 'n.', 'Direct, forcible, and effective speaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homespun', 'a.', 'Spun or wrought at home; of domestic manufacture; coarse;\n   plain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homespun', 'a.', 'Plain in manner or style; not elegant; rude; coarse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homespun', 'n.', 'Cloth made at home; as, he was dressed in homespun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homespun', 'n.', 'An unpolished, rustic person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homestall', 'n.', 'Place of a home; homestead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homestead', 'n.', 'The home place; a home and the inclosure or ground\n   immediately connected with it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homestead', 'n.', 'The home or seat of a family; place of origin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homestead', 'n.', 'The home and appurtenant land and buildings owned by the\n   head of a family, and occupied by him and his family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homesteader', 'n.', 'One who has entered upon a portion of the public land\n   with the purpose of acquiring ownership of it under provisions of the\n   homestead law, so called; one who has acquired a homestead in this\n   manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homeward', 'a.', 'Being in the direction of home; as, the homeward way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homeward', 'adv.', 'Alt. of Homewards');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homewards', 'adv.', 'Toward home; in the direction of ones house, town, or\n   country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homicidal', 'a.', 'Pertaining to homicide; tending to homicide; murderous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homicide', 'v. t.', 'The killing of one human being by another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homicide', 'v. t.', 'One who kills another; a manslayer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homiform', 'a.', 'In human form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homilete', 'n.', 'A homilist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homiletic', 'a.', 'Alt. of Homiletical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homiletical', 'a.', 'Of or pertaining to familiar intercourse; social;\n   affable; conversable; companionable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homiletical', 'a.', 'Of or pertaining to homiletics; hortatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homiletics', 'n.', 'The art of preaching; that branch of theology which\n   treats of homilies or sermons, and the best method of preparing and\n   delivering them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homilist', 'n.', 'One who prepares homilies; one who preaches to a\n   congregation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homilite', 'n.', 'A borosilicate of iron and lime, near datolite in form\n   and composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homilies', 'pl. ', 'of Homily');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homily', 'n.', 'A discourse or sermon read or pronounced to an audience; a\n   serious discourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homily', 'n.', 'A serious or tedious exhortation in private on some moral\n   point, or on the conduct of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homing', 'a.', 'Home-returning; -- used specifically of carrier pigeons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hominy', 'n.', 'Maize hulled and broken, and prepared for food by being\n   boiled in water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homish', 'a.', 'Like a home or a home circle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hommock', 'n.', 'A small eminence of a conical form, of land or of ice; a\n   knoll; a hillock. See Hummock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hommocky', 'a.', 'Filled with hommocks; piled in the form of hommocks; --\n   said of ice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homo-', '', 'A combining form from Gr. \"omo`s, one and the same, common,\n   joint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homocategoric', 'a.', 'Belonging to the same category of individuality; --\n   a morphological term applied to organisms so related.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homocentric', 'a.', 'Having the same center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homocercal', 'a.', 'Having the tail nearly or quite symmetrical, the\n   vertebral column terminating near its base; -- opposed to heterocercal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homocercy', 'n.', 'The possession of a homocercal tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homocerebrin', 'n.', 'A body similar to, or identical with, cerebrin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homochromous', 'a.', 'Having all the florets in the same flower head of the\n   same color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homodemic', 'a.', 'A morphological term signifying development, in the case\n   of multicellular organisms, from the same unit deme or unit of the\n   inferior orders of individuality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homodermic', 'a.', 'Relating to homodermy; originating from the same germ\n   layer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homodermy', 'n.', 'Homology of the germinal layers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homodont', 'a.', 'Having all the teeth similar in front, as in the\n   porpoises; -- opposed to heterodont.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homodromal', 'a.', 'Alt. of Homodromous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homodromous', 'a.', 'Running in the same direction; -- said of stems\n   twining round a support, or of the spiral succession of leaves on stems\n   and their branches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homodromous', 'a.', 'Moving in the same direction; -- said of a lever or\n   pulley in which the resistance and the actuating force are both on the\n   same side of the fulcrum or axis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homodynamic', 'a.', 'Homodynamous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homodynamous', 'a.', 'Pertaining to, or involving, homodynamy; as,\n   successive or homodynamous parts in plants and animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homodynamy', 'n.', 'The homology of metameres. See Metamere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homoeomeria', 'n.', 'The state or quality of being homogeneous in elements\n   or first principles; likeness or identity of parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homoeomeric', 'a.', 'Alt. of Homoeomerical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homoeomerical', 'a.', 'Pertaining to, or characterized by, sameness of\n   parts; receiving or advocating the doctrine of homogeneity of elements\n   or first principles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homoeomerous', 'a.', 'Having the main artery of the leg parallel with the\n   sciatic nerve; -- said of certain birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homoeomery', 'n.', 'Same as Homoeomeria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homoeomorphism', 'n.', 'A near similarity of crystalline forms between\n   unlike chemical compounds. See Isomorphism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homoeomorphous', 'a.', 'Manifesting homoeomorphism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homoeopathic', 'n.', 'Alt. of Homoeopathy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homoeopathist', 'n.', 'Alt. of Homoeopathy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homoeopathy', 'n.', 'Same as Homeopathic, Homeopathist, Homeopathy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homoeothermal', 'a.', 'See Homoiothermal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homoeozoic', 'a.', 'Pertaining to, or including, similar forms or kinds of\n   life; as, homoeozoic belts on the earths surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homogamous', 'a.', 'Having all the flowers alike; -- said of such composite\n   plants as Eupatorium, and the thistels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homogamy', 'n.', 'The condition of being homogamous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homogangliate', 'a.', 'Having the ganglia of the nervous system\n   symmetrically arranged, as in certain invertebrates; -- opposed to\n   heterogangliate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homogene', 'a.', 'Homogeneous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homogeneal', 'a.', 'Homogeneous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homogenealness', 'n.', 'Homogeneousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homogeneity', 'n.', 'Same as Homogeneousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homogeneous', 'a.', 'Of the same kind of nature; consisting of similar\n   parts, or of elements of the like nature; -- opposed to heterogeneous;\n   as, homogeneous particles, elements, or principles; homogeneous bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homogeneous', 'a.', 'Possessing the same number of factors of a given kind;\n   as, a homogeneous polynomial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homogeneousness', 'n.', 'Sameness 9kind or nature; uniformity of structure\n   or material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homogenesis', 'n.', 'That method of reproduction in which the successive\n   generations are alike, the offspring, either animal or plant, running\n   through the same cycle of existence as the parent; gamogenesis; --\n   opposed to heterogenesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homogenetic', 'a.', 'Homogenous; -- applied to that class of homologies\n   which arise from similarity of structure, and which are taken as\n   evidences of common ancestry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homogenous', 'a.', 'Having a resemblance in structure, due to descent from\n   a common progenitor with subsequent modification; homogenetic; --\n   applied both to animals and plants. See Homoplastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homogeny', 'n.', 'Joint nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homogeny', 'n.', 'The correspondence of common descent; -- a term used to\n   supersede homology by Lankester, who also used homoplasy to denote any\n   superinduced correspondence of position and structure in parts\n   embryonically distinct (other writers using the term homoplasmy). Thus,\n   there is homogeny between the fore limb of a mammal and the wing of a\n   bird; but the right and left ventricles of the heart in both are only\n   in homoplasy with each other, these having arisen independently since\n   the divergence of both groups from a univentricular ancestor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homogonous', 'a.', 'Having all the flowers of a plant alike in respect to\n   the stamens and pistils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homogony', 'n.', 'The condition of having homogonous flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homograph', 'n.', 'One of two or more words identical in orthography, but\n   having different derivations and meanings; as, fair, n., a market, and\n   fair, a., beautiful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homographic', 'a.', 'Employing a single and separate character to represent\n   each sound; -- said of certain methods of spelling words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homographic', 'a.', 'Possessing the property of homography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homography', 'n.', 'That method of spelling in which every sound is\n   represented by a single character, which indicates that sound and no\n   other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homography', 'n.', 'A relation between two figures, such that to any point\n   of the one corresponds one and but one point in the other, and vise\n   versa. Thus, a tangent line rolling on a circle cuts two fixed tangents\n   of the circle in two sets of points that are homographic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homoioptoton', 'n.', 'A figure in which the several parts of a sentence end\n   with the same case, or inflection generally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homoiothermal', 'a.', 'Maintaining a uniform temperature; haematothermal;\n   homothermic; -- applied to warm-bodied animals, because they maintain a\n   nearly uniform temperature in spite of the great variations in the\n   surrounding air; in distinct from the cold-blooded (poikilothermal)\n   animals, whose body temperature follows the variations in temperature\n   of the surrounding medium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homoiousian', 'n.', 'One of the semi-Arians of the 4th century, who held\n   that the Son was of like, but not the same, essence or substance with\n   the Father; -- opposed to homoousian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homoiousian', 'a.', 'Of or pertaining to Homoiousians, or their belief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homologated', 'imp. & p. p.', 'of Homologate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homologating', 'p. pr. & vb. n.', 'of Homologate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homologate', 'v. t.', 'To approve; to allow; to confirm; as, the court\n   homologates a proceeding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homologation', 'n.', 'Confirmation or ratification (as of something\n   otherwise null and void), by a court or a grantor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homological', 'a.', 'Pertaining to homology; having a structural affinity\n   proceeding from, or base upon, that kind of relation termed homology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homologinic', 'a.', 'Pertaining to, or characterized by, homology; as,\n   homologinic qualities, or differences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homologize', 'v. t.', 'To determine the homologies or structural relations\n   of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homologon', 'n.', 'See Homologue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homologoumena', 'n. pl.', 'Those books of the New Testament which were\n   acknowledged as canonical by the early church; -- distinguished from\n   antilegomena.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homologous', 'a.', 'Having the same relative position, proportion, value,\n   or structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homologous', 'a.', 'Corresponding in relative position and proportion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homologous', 'a.', 'Having the same relative proportion or value, as the\n   two antecedents or the two consequents of a proportion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homologous', 'a.', 'Characterized by homology; belonging to the same type\n   or series; corresponding in composition and properties. See Homology,\n   3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homologous', 'a.', 'Being of the same typical structure; having like\n   relations to a fundamental type to structure; as, those bones in the\n   hand of man and the fore foot of a horse are homologous that correspond\n   in their structural relations, that is, in their relations to the type\n   structure of the fore limb in vertebrates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homolographic', 'a.', 'Preserving the mutual relations of parts, especially\n   as to size and form; maintaining relative proportion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homologue', 'n.', 'That which is homologous to something else; as, the\n   corresponding sides, etc., of similar polygons are the homologues of\n   each other; the members or terms of an homologous series in chemistry\n   are the homologues of each other; one of the bones in the hand of man\n   is the homologue of that in the paddle of a whale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homology', 'n.', 'The quality of being homologous; correspondence;\n   relation; as, the homologyof similar polygons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homology', 'n.', 'Correspondence or relation in type of structure in\n   contradistinction to similarity of function; as, the relation in\n   structure between the leg and arm of a man; or that between the arm of\n   a man, the fore leg of a horse, the wing of a bird, and the fin of a\n   fish, all these organs being modifications of one type of structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homology', 'n.', 'The correspondence or resemblance of substances belonging\n   to the same type or series; a similarity of composition varying by a\n   small, regular difference, and usually attended by a regular variation\n   in physical properties; as, there is an homology between methane, CH4,\n   ethane, C2H6, propane, C3H8, etc., all members of the paraffin series.\n   In an extended sense, the term is applied to the relation between\n   chemical elements of the same group; as, chlorine, bromine, and iodine\n   are said to be in homology with each other. Cf. Heterology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homomallous', 'a.', 'Uniformly bending or curving to one side; -- said of\n   leaves which grow on several sides of a stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homomorphic', 'a.', 'Alt. of Homomorphous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homomorphous', 'a.', 'Characterized by homomorphism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homomorphism', 'n.', 'Same as Homomorphy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homomorphism', 'n.', 'The possession, in one species of plants, of only one\n   kind of flowers; -- opposed to heteromorphism, dimorphism, and\n   trimorphism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homomorphism', 'n.', 'The possession of but one kind of larvae or young, as\n   in most insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homomorphy', 'n.', 'Similarity of form; resemblance in external characters,\n   while widely different in fundamental structure; resemblance in\n   geometric ground form. See Homophyly, Promorphology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homonomous', 'a.', 'Of or pertaining to homonomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homonomy', 'n.', 'The homology of parts arranged on transverse axes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homonym', 'n.', 'A word having the same sound as another, but differing\n   from it in meaning; as the noun bear and the verb bear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homonymous', 'a.', 'Having the same name or designation; standing in the\n   same relation; -- opposed to heteronymous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homonymous', 'a.', 'Having the same name or designation, but different\n   meaning or relation; hence, equivocal; ambiguous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homonymously', 'adv.', 'In an homonymous manner; so as to have the same\n   name or relation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homonymously', 'adv.', 'Equivocally; ambiguously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homonymy', 'n.', 'Sameness of name or designation; identity in relations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homonymy', 'n.', 'Sameness of name or designation of things or persons\n   which are different; ambiguity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homoorgan', '', 'Same as Homoplast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homoousian', 'n.', 'One of those, in the 4th century, who accepted the\n   Nicene creed, and maintained that the Son had the same essence or\n   substance with the Father; -- opposed to homoiousian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homoousian', 'a.', 'Of or pertaining to the Homoousians, or to the\n   doctrines they held.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homophone', 'n.', 'A letter or character which expresses a like sound with\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homophone', 'n.', 'A word having the same sound as another, but differing\n   from it in meaning and usually in spelling; as, all and awl; bare and\n   bear; rite, write, right, and wright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homophonic', 'a.', 'Alt. of Homophonous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homophonous', 'a.', 'Originally, sounding alike; of the same pitch;\n   unisonous; monodic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homophonous', 'a.', 'Now used for plain harmony, note against note, as\n   opposed to polyphonic harmony, in which the several parts move\n   independently, each with its own melody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homophonous', 'a.', 'Expressing the same sound by a different combination\n   of letters; as, bay and bey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homophony', 'n.', 'Sameness of sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homophony', 'n.', 'Sameness of sound; unison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homophony', 'n.', 'Plain harmony, as opposed to polyphony. See Homophonous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homophylic', 'a.', 'Relating to homophily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homophyly', 'n.', 'That form of homology due to common ancestry\n   (phylogenetic homology), in opposition to homomorphy, to which\n   genealogic basis is wanting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homoplasmy', 'n.', 'Resemblance between different plants or animals, in\n   external shape, in general habit, or in organs, which is not due to\n   descent from a common ancestor, but to similar surrounding\n   circumstances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homoplast', 'n.', 'One of the plastids composing the idorgan of Haeckel; --\n   also called homoorgan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homoplastic', 'a.', 'Of or pertaining to homoplasty; as, homoplasticorgans;\n   homoplastic forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homoplasty', 'n.', 'The formation of homologous tissues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homoplasy', 'n.', 'See Homogeny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homopolic', 'a.', 'In promorphology, pertaining to or exhibiting that kind\n   of organic form, in which the stereometric ground form is a pyramid,\n   with similar poles. See Promorphology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homopter', 'n.', 'One of the Homoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homoptera', 'n. pl.', 'A suborder of Hemiptera, in which both pairs of\n   wings are similar in texture, and do not overlap when folded, as in the\n   cicada. See Hemiptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homopteran', 'n.', 'An homopter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homopterous', 'a.', 'Of or pertaining to the Homoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homostyled', 'a.', 'Having only one form of pistils; -- said of the flowers\n   of some plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homosystemic', 'a.', 'Developing, in the case of multicellular organisms,\n   from the same embryonic systems into which the secondary unit (gastrula\n   or plant enbryo) differentiates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homotaxia', 'n.', 'Same as Homotaxis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homotaxial', 'a.', 'Alt. of Homotaxic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homotaxic', 'a.', 'Relating to homotaxis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homotaxis', 'n.', 'Similarly in arrangement of parts; -- the opposite of\n   heterotaxy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homotaxy', 'n.', 'Same as Homotaxis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homothermic', 'a.', 'Alt. of Homothermous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homothermous', 'a.', 'Warm-blooded; homoiothermal; haematothermal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homotonous', 'a.', 'Of the same tenor or tone; equable; without variation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homotropal', 'a.', 'Alt. of Homotropous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homotropous', 'a.', 'Turned in the same direction with something else.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homotropous', 'a.', 'Having the radicle of the seed directed towards the\n   hilum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homotypal', 'a.', 'Of the same type of structure; pertaining to a homotype;\n   as, homotypal parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homotype', 'n.', 'That which has the same fundamental type of structure\n   with something else; thus, the right arm is the homotype of the right\n   leg; one arm is the homotype of the other, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homotypic', 'a.', 'Alt. of Homotypical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homotypical', 'a.', 'Same as Homotypal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homotypy', 'n.', 'A term suggested by Haeckel to be instead of serial\n   homology. See Homotype.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homunculi', 'pl. ', 'of Homunculus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Homunculus', 'n.', 'A little man; a dwarf; a manikin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hond', 'n.', 'Hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hone', 'v. i.', 'To pine; to lament; to long.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hone', 'n.', 'A kind of swelling in the cheek.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hone', 'n.', 'A stone of a fine grit, or a slab, as of metal, covered with\n   an abrading substance or powder, used for sharpening cutting\n   instruments, and especially for setting razors; an oilstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honed', 'imp. & p. p.', 'of Hone');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honing', 'p]. pr. & vb. n.', 'of Hone');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hone', 'v. t.', 'To sharpen on, or with, a hone; to rub on a hone in order\n   to sharpen; as, to hone a razor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honest', 'a.', 'Decent; honorable; suitable; becoming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honest', 'a.', 'Characterized by integrity or fairness and\n   straight/forwardness in conduct, thought, speech, etc.; upright; just;\n   equitable; trustworthy; truthful; sincere; free from fraud, guile, or\n   duplicity; not false; -- said of persons and acts, and of things to\n   which a moral quality is imputed; as, an honest judge or merchant; an\n   honest statement; an honest bargain; an honest business; an honest\n   book; an honest confession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honest', 'a.', 'Open; frank; as, an honest countenance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honest', 'a.', 'Chaste; faithful; virtuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honest', 'a.', 'To adorn; to grace; to honor; to make becoming,\n   appropriate, or honorable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honestation', 'n.', 'The act of honesting; grace; adornment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honestetee', 'n.', 'Honesty; honorableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honestly', 'adv.', 'Honorably; becomingly; decently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honestly', 'adv.', 'In an honest manner; as, a contract honestly made; to\n   live honestly; to speak honestly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honesty', 'a.', 'Honor; honorableness; dignity; propriety; suitableness;\n   decency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honesty', 'a.', 'The quality or state of being honest; probity; fairness\n   and straightforwardness of conduct, speech, etc.; integrity; sincerity;\n   truthfulness; freedom from fraud or guile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honesty', 'a.', 'Chastity; modesty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honesty', 'a.', 'Satin flower; the name of two cruciferous herbs having\n   large flat pods, the round shining partitions of which are more\n   beautiful than the blossom; -- called also lunary and moonwort. Lunaria\n   biennis is common honesty; L. rediva is perennial honesty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honewort', 'n.', 'An umbelliferous plant of the genus Sison (S. Amomum); --\n   so called because used to cure a swelling called a hone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honey', 'n.', 'A sweet viscid fluid, esp. that collected by bees from\n   flowers of plants, and deposited in the cells of the honeycomb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honey', 'n.', 'That which is sweet or pleasant, like honey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honey', 'n.', 'Sweet one; -- a term of endearment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honeyed', 'imp. & p. p.', 'of Honey');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honeying', 'p. pr. & vb. n.', 'of Honey');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honey', 'v. i.', 'To be gentle, agreeable, or coaxing; to talk fondly; to\n   use endearments; also, to be or become obsequiously courteous or\n   complimentary; to fawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honey', 'v. t.', 'To make agreeable; to cover or sweeten with, or as with,\n   honey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honey-bag', 'n.', 'The receptacle for honey in a honeybee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honeybee', 'n.', 'Any bee of the genus Apis, which lives in communities and\n   collects honey, esp. the common domesticated hive bee (Apis mellifica),\n   the Italian bee (A. ligustica), and the Arabiab bee (A. fasciata). The\n   two latter are by many entomologists considered only varieties of the\n   common hive bee. Each swarm of bees consists of a large number of\n   workers (barren females), with, ordinarily, one queen or fertile\n   female, but in the swarming season several young queens, and a number\n   of males or drones, are produced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honeybird', 'n.', 'The honey guide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honeycomb', 'n.', 'A mass of hexagonal waxen cells, formed by bees, and\n   used by them to hold their honey and their eggs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honeycomb', 'n.', 'Any substance, as a easting of iron, a piece of\n   worm-eaten wood, or of triple, etc., perforated with cells like a\n   honeycomb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honeycombed', 'a.', 'Formed or perforated like a honeycomb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honeydew', 'n.', 'A sweet, saccharine substance, found on the leaves of\n   trees and other plants in small drops, like dew. Two substances have\n   been called by this name; one exuded from the plants, and the other\n   secreted by certain insects, esp. aphids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honeydew', 'n.', 'A kind of tobacco moistened with molasses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honeyed', 'a.', 'Covered with honey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honeyed', 'a.', 'Sweet, as, honeyed words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honeyless', 'a.', 'Destitute of honey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honeymoon', 'n.', 'The first month after marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honey-mouthed', 'a.', 'Soft to sweet in speech; persuasive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honeystone', 'n.', 'See Mellite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honeysucker', 'n.', 'See Honey eater, under Honey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honeysuckle', 'n.', 'One of several species of flowering plants, much\n   admired for their beauty, and some for their fragrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honeysuckled', 'a.', 'Covered with honeysuckles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honey-sweet', 'a.', 'Sweet as honey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honey-tongued', 'a.', 'Sweet speaking; persuasive; seductive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honeyware', 'n.', 'See Badderlocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honeywort', 'n.', 'A European plant of the genus Cerinthe, whose flowers\n   are very attractive to bees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hong', 'n.', 'A mercantile establishment or factory for foreign trade in\n   China, as formerly at Canton; a succession of offices connected by a\n   common passage and used for business or storage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hong', 'v. t. & i.', 'To hang.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honied', 'a.', 'See Honeyed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honiton lace', '', 'A kind of pillow lace, remarkable for the beauty of its\n   figures; -- so called because chiefly made in Honiton, England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honk', 'n.', 'The cry of a wild goose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honor', 'n.', 'Esteem due or paid to worth; high estimation; respect;\n   consideration; reverence; veneration; manifestation of respect or\n   reverence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honor', 'n.', 'That which rightfully attracts esteem, respect, or\n   consideration; self-respect; dignity; courage; fidelity; especially,\n   excellence of character; high moral worth; virtue; nobleness; specif.,\n   in men, integrity; uprightness; trustworthness; in women, purity;\n   chastity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honor', 'n.', 'A nice sense of what is right, just, and true, with course\n   of life correspondent thereto; strict conformity to the duty imposed by\n   conscience, position, or privilege.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honor', 'n.', 'That to which esteem or consideration is paid; distinguished\n   position; high rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honor', 'n.', 'Fame; reputation; credit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honor', 'n.', 'A token of esteem paid to worth; a mark of respect; a\n   ceremonial sign of consideration; as, he wore an honor on his breast;\n   military honors; civil honors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honor', 'n.', 'A cause of respect and fame; a glory; an excellency; an\n   ornament; as, he is an honor to his nation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honor', 'n.', 'A title applied to the holders of certain honorable civil\n   offices, or to persons of rank; as, His Honor the Mayor. See Note under\n   Honorable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honor', 'n.', 'A seigniory or lordship held of the king, on which other\n   lordships and manors depended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honor', 'n.', 'Academic or university prizes or distinctions; as, honors in\n   classics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honor', 'n.', 'The ace, king, queen, and jack of trumps. The ten and nine\n   are sometimes called Dutch honors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honored', 'imp. & p. p.', 'of Honor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honoring', 'p. pr. & vb. n.', 'of Honor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honor', 'n.', 'To regard or treat with honor, esteem, or respect; to\n   revere; to treat with deference and submission; when used of the\n   Supreme Being, to reverence; to adore; to worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honor', 'n.', 'To dignify; to raise to distinction or notice; to bestow\n   honor upon; to elevate in rank or station; to ennoble; to exalt; to\n   glorify; hence, to do something to honor; to treat in a complimentary\n   manner or with civility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honor', 'n.', 'To accept and pay when due; as, to honora bill of exchange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honorable', 'a.', 'Worthy of honor; fit to be esteemed or regarded;\n   estimable; illustrious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honorable', 'a.', 'High-minded; actuated by principles of honor, or a\n   scrupulous regard to probity, rectitude, or reputation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honorable', 'a.', 'Proceeding from an upright and laudable cause, or\n   directed to a just and proper end; not base; irreproachable; fair; as,\n   an honorable motive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honorable', 'a.', 'Conferring honor, or produced by noble deeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honorable', 'a.', 'Worthy of respect; regarded with esteem; to be\n   commended; consistent with honor or rectitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honorable', 'a.', 'Performed or accompanied with marks of honor, or with\n   testimonies of esteem; an honorable burial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honorable', 'a.', 'Of reputable association or use; respectable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honorable', 'a.', 'An epithet of respect or distinction; as, the honorable\n   Senate; the honorable gentleman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honorableness', 'n.', 'The state of being honorable; eminence; distinction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honorableness', 'n.', 'Conformity to the principles of honor, probity, or\n   moral rectitude; fairness; uprightness; reputableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honorably', 'adv.', 'In an honorable manner; in a manner showing, or\n   consistent with, honor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honorably', 'adv.', 'Decently; becomingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honorarium', 'a.', 'Alt. of Honorary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honorary', 'a.', 'A fee offered to professional men for their services; as,\n   an honorarium of one thousand dollars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honorary', 'a.', 'An honorary payment, usually in recognition of services\n   for which it is not usual or not lawful to assign a fixed business\n   price.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honorary', 'a.', 'Done as a sign or evidence of honor; as, honorary\n   services.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honorary', 'a.', 'Conferring honor, or intended merely to confer honor\n   without emolument; as, an honorary degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honorary', 'a.', 'Holding a title or place without rendering service or\n   receiving reward; as, an honorary member of a society.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honorer', 'n.', 'One who honors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honorific', 'a.', 'Conferring honor; tending to honor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Honorless', 'a.', 'Destitute of honor; not honored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hont', 'n. & v.', 'See under Hunt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoo', 'interj.', 'See Ho.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoo', 'interj.', 'Hurrah! -- an exclamation of triumphant joy.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-hood', '', 'A termination denoting state, condition, quality, character,\n   totality, as in manhood, childhood, knighthood, brotherhood. Sometimes\n   it is written, chiefly in obsolete words, in the form -head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hood', 'n.', 'State; condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hood', 'n.', 'A covering or garment for the head or the head and shoulders,\n   often attached to the body garment');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hood', 'n.', 'A soft covering for the head, worn by women, which leaves\n   only the face exposed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hood', 'n.', 'A part of a monks outer garment, with which he covers his\n   head; a cowl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hood', 'n.', 'A like appendage to a cloak or loose overcoat, that may be\n   drawn up over the head at pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hood', 'n.', 'An ornamental fold at the back of an academic gown or\n   ecclesiastical vestment; as, a masters hood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hood', 'n.', 'A covering for a horses head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hood', 'n.', 'A covering for a hawks head and eyes. See Illust. of Falcon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hood', 'n.', 'Anything resembling a hood in form or use');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hood', 'n.', 'The top or head of a carriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hood', 'n.', 'A chimney top, often contrived to secure a constant draught\n   by turning with the wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hood', 'n.', 'A projecting cover above a hearth, forming the upper part of\n   the fireplace, and confining the smoke to the flue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hood', 'n.', 'The top of a pump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hood', 'n.', 'A covering for a mortar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hood', 'n.', 'The hood-shaped upper petal of some flowers, as of monkshood;\n   -- called also helmet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hood', 'n.', 'A covering or porch for a companion hatch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hood', 'n.', 'The endmost plank of a strake which reaches the stem or\n   stern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hooded', 'imp. & p. p.', 'of Hood');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hooding', 'p. pr. & vb. n.', 'of Hood');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hood', 'v. t.', 'To cover with a hood; to furnish with a hood or\n   hood-shaped appendage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hood', 'v. t.', 'To cover; to hide; to blind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoodcap', 'n.', 'See Hooded seal, under Hooded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hooded', 'a.', 'Covered with a hood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hooded', 'a.', 'Furnished with a hood or something like a hood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hooded', 'a.', 'Hood-shaped; esp. (Bot.), rolled up like a cornet of paper;\n   cuculate, as the spethe of the Indian turnip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hooded', 'a.', 'Having the head conspicuously different in color from the\n   rest of the plumage; -- said of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hooded', 'a.', 'Having a hoodlike crest or prominence on the head or neck;\n   as, the hooded seal; a hooded snake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoodless', 'a.', 'Having no hood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoodlum', 'n.', 'A young rowdy; a rough, lawless fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoodman', 'n.', 'The person blindfolded in the game called hoodman-blind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoodman-blind', 'n.', 'An old term for blindmans buff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hood molding', '', 'Alt. of Hood moulding');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hood moulding', '', 'A projecting molding over the head of an arch, forming\n   the outermost member of the archivolt; -- called also hood mold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoodoo', 'n.', 'One who causes bad luck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoodwink', 'v. t.', 'To blind by covering the eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoodwink', 'v. t.', 'To cover; to hide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoodwink', 'v. t.', 'To deceive by false appearance; to impose upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoody', 'n.', 'The hooded crow; also, in Scotland, the hooded gull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoofs', 'pl. ', 'of Hoof');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hooves', 'pl. ', 'of Hoof');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoof', 'n.', 'The horny substance or case that covers or terminates the\n   feet of certain animals, as horses, oxen, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoof', 'n.', 'A hoofed animal; a beast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoof', 'n.', 'See Ungula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoof', 'v. i.', 'To walk as cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoof', 'v. i.', 'To be on a tramp; to foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoofbound', 'a.', 'Having a dry and contracted hoof, which occasions pain\n   and lameness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoofed', 'a.', 'Furnished with hoofs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoofless', 'a.', 'Destitute of hoofs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hook', 'n.', 'A piece of metal, or other hard material, formed or bent into\n   a curve or at an angle, for catching, holding, or sustaining anything;\n   as, a hook for catching fish; a hook for fastening a gate; a boat hook,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hook', 'n.', 'That part of a hinge which is fixed to a post, and on which a\n   door or gate hangs and turns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hook', 'n.', 'An implement for cutting grass or grain; a sickle; an\n   instrument for cutting or lopping; a billhook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hook', 'n.', 'See Eccentric, and V-hook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hook', 'n.', 'A snare; a trap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hook', 'n.', 'A field sown two years in succession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hook', 'n.', 'The projecting points of the thigh bones of cattle; -- called\n   also hook bones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hooked', 'imp. & p. p.', 'of Hook');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hooking', 'p. pr. & vb. n.', 'of Hook');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hook', 'v. t.', 'To catch or fasten with a hook or hooks; to seize,\n   capture, or hold, as with a hook, esp. with a disguised or baited hook;\n   hence, to secure by allurement or artifice; to entrap; to catch; as, to\n   hook a dress; to hook a trout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hook', 'v. t.', 'To seize or pierce with the points of the horns, as cattle\n   in attacking enemies; to gore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hook', 'v. t.', 'To steal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hook', 'v. i.', 'To bend; to curve as a hook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hookah', 'n.', 'A pipe with a long, flexible stem, so arranged that the\n   smoke is cooled by being made to pass through water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hook-billed', 'a.', 'Having a strongly curved bill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hooked', 'a.', 'Having the form of a hook; curvated; as, the hooked bill of\n   a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hooked', 'a.', 'Provided with a hook or hooks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hookedness', 'n.', 'The state of being bent like a hook; incurvation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hooker', 'n.', 'One who, or that which, hooks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hooker', 'n.', 'A Dutch vessel with two masts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hooker', 'n.', 'A fishing boat with one mast, used on the coast of Ireland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hooker', 'n.', 'A sailors contemptuous term for any antiquated craft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hookes gearing', '', 'Spur gearing having teeth slanting across the face\n   of the wheel, sometimes slanting in opposite directions from the\n   middle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hookes joint', '', 'A universal joint. See under Universal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hookey', 'n.', 'See Hockey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hooklet', 'n.', 'A little hook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hook-nosed', 'a.', 'Having a hooked or aquiline nose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hooky', 'a.', 'Full of hooks; pertaining to hooks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hool', 'a.', 'Whole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoolock', 'n.', 'A small black gibbon (Hylobates hoolock), found in the\n   mountains of Assam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoom', 'n.', 'Home.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoonoomaun', 'n.', 'An Indian monkey. See Entellus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoop', 'n.', 'A pliant strip of wood or metal bent in a circular form, and\n   united at the ends, for holding together the staves of casks, tubs,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoop', 'n.', 'A ring; a circular band; anything resembling a hoop, as the\n   cylinder (cheese hoop) in which the curd is pressed in making cheese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoop', 'n.', 'A circle, or combination of circles, of thin whalebone,\n   metal, or other elastic material, used for expanding the skirts of\n   ladies dresses; crinoline; -- used chiefly in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoop', 'n.', 'A quart pot; -- so called because originally bound with\n   hoops, like a barrel. Also, a portion of the contents measured by the\n   distance between the hoops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoop', 'n.', 'An old measure of capacity, variously estimated at from one\n   to four pecks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hooped', 'imp. & p. p.', 'of Hoop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hooping', 'p. pr. & vb. n.', 'of Hoop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoop', 'v. t.', 'To bind or fasten with hoops; as, to hoop a barrel or\n   puncheon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoop', 'v. t.', 'To clasp; to encircle; to surround.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoop', 'v. i.', 'To utter a loud cry, or a sound imitative of the word, by\n   way of call or pursuit; to shout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoop', 'v. i.', 'To whoop, as in whooping cough. See Whoop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoop', 'v. t.', 'To drive or follow with a shout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoop', 'v. t.', 'To call by a shout or peculiar cry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoop', 'n.', 'A shout; a whoop, as in whooping cough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoop', 'n.', 'The hoopoe. See Hoopoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hooper', 'n.', 'One who hoops casks or tubs; a cooper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hooper', 'n.', 'The European whistling, or wild, swan (Olor cygnus); --\n   called also hooper swan, whooping swan, and elk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoopoe', 'n.', 'Alt. of Hoopoo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoopoo', 'n.', 'A European bird of the genus Upupa (U. epops), having a\n   beautiful crest, which it can erect or depress at pleasure. Called also\n   hoop, whoop. The name is also applied to several other species of the\n   same genus and allied genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoosier', 'n.', 'A nickname given to an inhabitant of the State of Indiana.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hooted', 'imp. & p. p.', 'of Hoot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hooting', 'p. pr. & vb. n.', 'of Hoot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoot', 'v. i.', 'To cry out or shout in contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoot', 'v. i.', 'To make the peculiar cry of an owl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoot', 'v. t.', 'To assail with contemptuous cries or shouts; to follow\n   with derisive shouts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoot', 'n.', 'A derisive cry or shout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoot', 'n.', 'The cry of an owl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoove', 'n.', 'A disease in cattle consisting in inflammation of the\n   stomach by gas, ordinarily caused by eating too much green food;\n   tympany; bloating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hooven', 'a.', 'Alt. of Hoven');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoven', 'a.', 'Affected with hoove; as, hooven, or hoven, cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hopped', 'imp. & p. p.', 'of Hop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hopping', 'p. pr. & vb. n.', 'of Hop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hop', 'v. i.', 'To move by successive leaps, as toads do; to spring or jump\n   on one foot; to skip, as birds do.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hop', 'v. i.', 'To walk lame; to limp; to halt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hop', 'v. i.', 'To dance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hop', 'n.', 'A leap on one leg, as of a boy; a leap, as of a toad; a jump;\n   a spring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hop', 'n.', 'A dance; esp., an informal dance of ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hop', 'n.', 'A climbing plant (Humulus Lupulus), having a long, twining,\n   annual stalk. It is cultivated for its fruit (hops).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hop', 'n.', 'The catkin or strobilaceous fruit of the hop, much used in\n   brewing to give a bitter taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hop', 'n.', 'The fruit of the dog-rose. See Hip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hop', 'v. t.', 'To impregnate with hops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hop', 'v. i.', 'To gather hops. [Perhaps only in the form Hopping, vb. n.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hopbine', 'n.', 'Alt. of Hopbind');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hopbind', 'n.', 'The climbing stem of the hop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hope', 'n.', 'A sloping plain between mountain ridges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hope', 'n.', 'A small bay; an inlet; a haven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hope', 'n.', 'A desire of some good, accompanied with an expectation of\n   obtaining it, or a belief that it is obtainable; an expectation of\n   something which is thought to be desirable; confidence; pleasing\n   expectancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hope', 'n.', 'One who, or that which, gives hope, furnishes ground of\n   expectation, or promises desired good.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hope', 'n.', 'That which is hoped for; an object of hope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoped', 'imp. & p. p.', 'of Hope');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoping', 'p. pr. & vb. n.', 'of Hope');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hope', 'v. i.', 'To entertain or indulge hope; to cherish a desire of good,\n   or of something welcome, with expectation of obtaining it or belief\n   that it is obtainable; to expect; -- usually followed by for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hope', 'v. i.', 'To place confidence; to trust with confident expectation\n   of good; -- usually followed by in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hope', 'v. t.', 'To desire with expectation or with belief in the\n   possibility or prospect of obtaining; to look forward to as a thing\n   desirable, with the expectation of obtaining it; to cherish hopes of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hope', 'v. t.', 'To expect; to fear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hopeful', 'a.', 'Full of hope, or agreeable expectation; inclined to hope;\n   expectant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hopeful', 'a.', 'Having qualities which excite hope; affording promise of\n   good or of success; as, a hopeful youth; a hopeful prospect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hopeite', 'n.', 'A hydrous phosphate of zinc in transparent prismatic\n   crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hopeless', 'a.', 'Destitute of hope; having no expectation of good;\n   despairing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hopeless', 'a.', 'Giving no ground of hope; promising nothing desirable;\n   desperate; as, a hopeless cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hopeless', 'a.', 'Unhoped for; despaired of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoper', 'n.', 'One who hopes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hopingly', 'adv.', 'In a hopeful manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoplite', 'n.', 'A heavy-armed infantry soldier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hop-o-my-thumb', 'n.', 'Alt. of Hop-thumb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hop-thumb', 'n.', 'A very diminutive person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hopped', 'p. a.', 'Impregnated with hops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hopper', 'n.', 'One who, or that which, hops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hopper', 'n.', 'A chute, box, or receptacle, usually funnel-shaped with an\n   opening at the lower part, for delivering or feeding any material, as\n   to a machine; as, the wooden box with its trough through which grain\n   passes into a mill by joining or shaking, or a funnel through which\n   fuel passes into a furnace, or coal, etc., into a car.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hopper', 'n.', 'See Grasshopper, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hopper', 'n.', 'A game. See Hopscotch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hopper', 'n.', 'See Grasshopper, and Frog hopper, Grape hopper, Leaf\n   hopper, Tree hopper, under Frog, Grape, Leaf, and Tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hopper', 'n.', 'The larva of a cheese fly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hopper', 'n.', 'A vessel for carrying waste, garbage, etc., out to sea, so\n   constructed as to discharge its load by a mechanical contrivance; --\n   called also dumping scow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hopperings', 'n.', 'Gravel retaining in the hopper of a cradle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoppestere', 'a.', 'An unexplained epithet used by Chaucer in reference to\n   ships. By some it is defined as \"dancing (on the wave)\"; by others as\n   \"opposing,\" \"warlike.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoppet', 'n.', 'A hand basket; also, a dish used by miners for measuring\n   ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoppet', 'n.', 'An infant in arms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hopping', 'n.', 'The act of one who, or that which, hops; a jumping,\n   frisking, or dancing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hopping', 'n.', 'A gathering of hops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoppled', 'imp. & p. p.', 'of Hopple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoppling', 'p. pr. & vb. n.', 'of Hopple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hopple', 'v. t.', 'To impede by a hopple; to tie the feet of (a horse or a\n   cow) loosely together; to hamper; to hobble; as, to hopple an unruly or\n   straying horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hopple', 'v. t.', 'Fig.: To entangle; to hamper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hopple', 'n.', 'A fetter for horses, or cattle, when turned out to graze;\n   -- chiefly used in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hopplebush', 'n.', 'Same as Hobblebush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoppo', 'n.', 'A collector of customs, as at Canton; an overseer of\n   commerce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoppo', 'n.', 'A tribunal or commission having charge of the revenue\n   derived from trade and navigation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hopscotch', 'n.', 'A childs game, in which a player, hopping on one foot,\n   drives a stone from one compartment to another of a figure traced or\n   scotched on the ground; -- called also hoppers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hop-thumb', 'n.', 'See Hop-o-my-thumb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hopyard', 'n.', 'A field where hops are raised.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horal', 'a.', 'Of or pertaining to an hour, or to hours.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horaly', 'adv.', 'Hourly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horary', 'a.', 'Of or pertaining to an hour; noting the hours.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horary', 'a.', 'Occurring once an hour; continuing an hour; hourly;\n   ephemeral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horatian', 'a.', 'Of or pertaining to Horace, the Latin poet, or resembling\n   his style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horde', 'n.', 'A wandering troop or gang; especially, a clan or tribe of a\n   nomadic people migrating from place to place for the sake of pasturage,\n   plunder, etc.; a predatory multitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hordeic', 'a.', 'Pertaining to, or derived from, barley; as, hordeic acid,\n   an acid identical or isomeric with lauric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hordein', 'n.', 'A peculiar starchy matter contained in barley. It is\n   complex mixture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hordeolum', 'n.', 'A small tumor upon the eyelid, resembling a grain of\n   barley; a sty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hordock', 'n.', 'An unidentified plant mentioned by Shakespeare, perhaps\n   equivalent to burdock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hore', 'a.', 'Hoar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horehound', 'n.', 'A plant of the genus Marrubium (M. vulgare), which has a\n   bitter taste, and is a weak tonic, used as a household remedy for\n   colds, coughing, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horizon', 'n.', 'The circle which bounds that part of the earths surface\n   visible to a spectator from a given point; the apparent junction of the\n   earth and sky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horizon', 'n.', 'A plane passing through the eye of the spectator and at\n   right angles to the vertical at a given place; a plane tangent to the\n   earths surface at that place; called distinctively the sensible\n   horizon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horizon', 'n.', 'A plane parallel to the sensible horizon of a place, and\n   passing through the earths center; -- called also rational / celestial\n   horizon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horizon', 'n.', 'The unbroken line separating sky and water, as seen by an\n   eye at a given elevation, no land being visible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horizon', 'n.', 'The epoch or time during which a deposit was made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horizon', 'n.', 'The chief horizontal line in a picture of any sort, which\n   determines in the picture the height of the eye of the spectator; in an\n   extended landscape, the representation of the natural horizon\n   corresponds with this line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horizontal', 'a.', 'Pertaining to, or near, the horizon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horizontal', 'a.', 'Parallel to the horizon; on a level; as, a\n   horizontalline or surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horizontal', 'a.', 'Measured or contained in a plane of the horizon; as,\n   horizontal distance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horizontality', 'n.', 'The state or quality of being horizontal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horizontally', 'adv.', 'In a horizontal direction or position; on a level;\n   as, moving horizontally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hormogonium', 'n.', 'A chain of small cells in certain algae, by which the\n   plant is propogated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horn', 'n.', 'A hard, projecting, and usually pointed organ, growing upon\n   the heads of certain animals, esp. of the ruminants, as cattle, goats,\n   and the like. The hollow horns of the Ox family consist externally of\n   true horn, and are never shed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horn', 'n.', 'The antler of a deer, which is of bone throughout, and\n   annually shed and renewed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horn', 'n.', 'Any natural projection or excrescence from an animal,\n   resembling or thought to resemble a horn in substance or form; esp.:\n   (a) A projection from the beak of a bird, as in the hornbill. (b) A\n   tuft of feathers on the head of a bird, as in the horned owl. (c) A\n   hornlike projection from the head or thorax of an insect, or the head\n   of a reptile, or fish. (d) A sharp spine in front of the fins of a\n   fish, as in the horned pout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horn', 'n.', 'An incurved, tapering and pointed appendage found in the\n   flowers of the milkweed (Asclepias).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horn', 'n.', 'Something made of a horn, or in resemblance of a horn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horn', 'n.', 'A wind instrument of music; originally, one made of a horn\n   (of an ox or a ram); now applied to various elaborately wrought\n   instruments of brass or other metal, resembling a horn in shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horn', 'n.', 'A drinking cup, or beaker, as having been originally made of\n   the horns of cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horn', 'n.', 'The cornucopia, or horn of plenty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horn', 'n.', 'A vessel made of a horn; esp., one designed for containing\n   powder; anciently, a small vessel for carrying liquids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horn', 'n.', 'The pointed beak of an anvil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horn', 'n.', 'The high pommel of a saddle; also, either of the projections\n   on a ladys saddle for supporting the leg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horn', 'n.', 'The Ionic volute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horn', 'n.', 'The outer end of a crosstree; also, one of the projections\n   forming the jaws of a gaff, boom, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horn', 'n.', 'A curved projection on the fore part of a plane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horn', 'n.', 'One of the projections at the four corners of the Jewish\n   altar of burnt offering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horn', 'n.', 'One of the curved ends of a crescent; esp., an extremity or\n   cusp of the moon when crescent-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horn', 'n.', 'The curving extremity of the wing of an army or of a squadron\n   drawn up in a crescentlike form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horn', 'n.', 'The tough, fibrous material of which true horns are composed,\n   being, in the Ox family, chiefly albuminous, with some phosphate of\n   lime; also, any similar substance, as that which forms the hoof crust\n   of horses, sheep, and cattle; as, a spoon of horn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horn', 'n.', 'A symbol of strength, power, glory, exaltation, or pride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horn', 'n.', 'An emblem of a cuckold; -- used chiefly in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horn', 'v. t.', 'To furnish with horns; to give the shape of a horn to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horn', 'v. t.', 'To cause to wear horns; to cuckold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hornbeak', 'n.', 'A fish. See Hornfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hornbeam', 'n.', 'A tree of the genus Carpinus (C. Americana), having a\n   smooth gray bark and a ridged trunk, the wood being white and very\n   hard. It is common along the banks of streams in the United States, and\n   is also called ironwood. The English hornbeam is C. Betulus. The\n   American is called also blue beech and water beech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hornbill', 'n.', 'Any bird of the family Bucerotidae, of which about sixty\n   species are known, belonging to numerous genera. They inhabit the\n   tropical parts of Asia, Africa, and the East Indies, and are remarkable\n   for having a more or less horn-like protuberance, which is usually\n   large and hollow and is situated on the upper side of the beak. The\n   size of the hornbill varies from that of a pigeon to that of a raven,\n   or even larger. They feed chiefly upon fruit, but some species eat dead\n   animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hornblende', 'n.', 'The common black, or dark green or brown, variety of\n   amphibole. (See Amphibole.) It belongs to the aluminous division of the\n   species, and is also characterized by its containing considerable iron.\n   Also used as a general term to include the whole species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hornblendic', 'a.', 'Composed largely of hornblende; resembling or relating\n   to hornblende.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hornblower', 'n.', 'One who, or that which, blows a horn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hornbook', 'n.', 'The first book for children, or that from which in former\n   times they learned their letters and rudiments; -- so called because a\n   sheet of horn covered the small, thin board of oak, or the slip of\n   paper, on which the alphabet, digits, and often the Lords Prayer, were\n   written or printed; a primer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hornbook', 'n.', 'A book containing the rudiments of any science or branch\n   of knowledge; a manual; a handbook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hornbug', 'n.', 'A large nocturnal beetle of the genus Lucanus (as L.\n   capreolus, and L. dama), having long, curved upper jaws, resembling a\n   sickle. The grubs are found in the trunks of old trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horned', 'a.', 'Furnished with a horn or horns; furnished with a hornlike\n   process or appendage; as, horned cattle; having some part shaped like a\n   horn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hornedness', 'n.', 'The condition of being horned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hornel', 'n.', 'The European sand eel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horner', 'n.', 'One who works or deal in horn or horns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horner', 'n.', 'One who winds or blows the horn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horner', 'n.', 'One who horns or cuckolds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horner', 'n.', 'The British sand lance or sand eel (Ammodytes lanceolatus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hornet', 'n.', 'A large, strong wasp. The European species (Vespa crabro)\n   is of a dark brown and yellow color. It is very pugnacious, and its\n   sting is very severe. Its nest is constructed of a paperlike material,\n   and the layers of comb are hung together by columns. The American\n   white-faced hornet (V. maculata) is larger and has similar habits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hornfish', 'n.', 'The garfish or sea needle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hornfoot', 'a.', 'Having hoofs; hoofed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hornify', 'v. t.', 'To horn; to cuckold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horning', 'n.', 'Appearance of the moon when increasing, or in the form of\n   a crescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hornish', 'a.', 'Somewhat like horn; hard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hornito', 'n.', 'A low, oven-shaped mound, common in volcanic regions, and\n   emitting smoke and vapors from its sides and summit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hornless', 'a.', 'Having no horn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horn-mad', 'a.', 'Quite mad; -- raving crazy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hornotine', 'n.', 'A yearling; a bird of the year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hornowl', 'n.', 'See Horned Owl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hornpike', 'n.', 'The garfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hornpipe', 'n.', 'An instrument of music formerly popular in Wales,\n   consisting of a wooden pipe, with holes at intervals. It was so called\n   because the bell at the open end was sometimes made of horn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hornpipe', 'n.', 'A lively tune played on a hornpipe, for dancing; a tune\n   adapted for such playing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hornpout', 'n.', 'See Horned pout, under Horned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hornsnake', 'n.', 'A harmless snake (Farancia abacura), found in the\n   Southern United States. The color is bluish black above, red below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hornstone', 'n.', 'A siliceous stone, a variety of quartz, closely\n   resembling flint, but more brittle; -- called also chert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horntail', 'n.', 'Any one of family (Uroceridae) of large hymenopterous\n   insects, allied to the sawflies. The larvae bore in the wood of trees.\n   So called from the long, stout ovipositors of the females.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hornwork', 'n.', 'An outwork composed of two demibastions joined by a\n   curtain. It is connected with the works in rear by long wings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hornwort', 'n.', 'An aquatic plant (Ceratophyllum), with finely divided\n   leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hornwrack', 'n.', 'A bryozoan of the genus Flustra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horny', 'superl.', 'Having horns or hornlike projections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horny', 'superl.', 'Composed or made of horn, or of a substance resembling\n   horn; of the nature of horn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horny', 'superl.', 'Hard; callous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horny-handed', 'a.', 'Having the hands horny and callous from labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hornyhead', 'n.', 'Any North American river chub of the genus Hybopsis,\n   esp. H. biguttatus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horography', 'n.', 'An account of the hours.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horography', 'n.', 'The art of constructing instruments for making the\n   hours, as clocks, watches, and dials.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horologe', 'n.', 'A servant who called out the hours.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horologe', 'n.', 'An instrument indicating the time of day; a timepiece of\n   any kind; a watch, clock, or dial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horologer', 'n.', 'A maker or vender of clocks and watches; one skilled in\n   horology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horological', 'a.', 'Relating to a horologe, or to horology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horologiographer', 'n.', 'A maker of clocks, watches, or dials.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horologiographic', 'a.', 'Of or pertaining to horologiography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horologiography', 'n.', 'An account of instruments that show the hour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horologiography', 'n.', 'The art of constructing clocks or dials;\n   horography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horologist', 'n.', 'One versed in horology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horology', 'n.', 'The science of measuring time, or the principles and art\n   of constructing instruments for measuring and indicating portions of\n   time, as clocks, watches, dials, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horometer', 'n.', 'An instrument for measuring time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horometrical', 'a.', 'Belonging to horometry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horometry', 'n.', 'The art, practice, or method of measuring time by hours\n   and subordinate divisions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horopter', 'n.', 'The line or surface in which are situated all the points\n   which are seen single while the point of sight, or the adjustment of\n   the eyes, remains unchanged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horopteric', 'a.', 'Of or pertaining to the horopter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horoscope', 'n.', 'The representation made of the aspect of the heavens at\n   the moment of a persons birth, by which the astrologer professed to\n   foretell the events of the persons life; especially, the sign of the\n   zodiac rising above the horizon at such a moment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horoscope', 'n.', 'The diagram or scheme of twelve houses or signs of the\n   zodiac, into which the whole circuit of the heavens was divided for the\n   purposes of such prediction of fortune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horoscope', 'n.', 'The planisphere invented by Jean Paduanus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horoscope', 'n.', 'A table showing the length of the days and nights at all\n   places.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horoscoper', 'n.', 'Alt. of Horoscopist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horoscopist', 'n.', 'One versed in horoscopy; an astrologer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horoscopy', 'n.', 'The art or practice of casting horoscopes, or observing\n   the disposition of the stars, with a view to prediction events.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horoscopy', 'n.', 'Aspect of the stars at the time of a persons birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horrendous', 'a.', 'Fearful; frightful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horrent', 'a.', 'Standing erect, as bristles; covered with bristling\n   points; bristled; bristling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horrible', 'a.', 'Exciting, or tending to excite, horror or fear; dreadful;\n   terrible; shocking; hideous; as, a horrible sight; a horrible story; a\n   horrible murder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horribleness', 'n.', 'The state or quality of being horrible; dreadfulness;\n   hideousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horribly', 'adv.', 'In a manner to excite horror; dreadfully; terribly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horrid', 'a.', 'Rough; rugged; bristling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horrid', 'a.', 'Fitted to excite horror; dreadful; hideous; shocking;\n   hence, very offensive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horridly', 'adv.', 'In a horrid manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horridness', 'n.', 'The quality of being horrid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horrific', 'a.', 'Causing horror; frightful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horrification', 'n.', 'That which causes horror.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horrified', 'imp. & p. p.', 'of Horrify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horrifying', 'p. pr. & vb. n.', 'of Horrify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horrify', 'v. t.', 'To cause to feel horror; to strike or impress with\n   horror; as, the sight horrified the beholders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horripilation', 'n.', 'A real or fancied bristling of the hair of the head\n   or body, resulting from disease, terror, chilliness, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horrisonant', 'a.', 'Horrisonous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horrisonous', 'a.', 'Sounding dreadfully; uttering a terrible sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horror', 'n.', 'A bristling up; a rising into roughness; tumultuous\n   movement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horror', 'n.', 'A shaking, shivering, or shuddering, as in the cold fit\n   which precedes a fever; in old medical writings, a chill of less\n   severity than a rigor, and more marked than an algor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horror', 'n.', 'A painful emotion of fear, dread, and abhorrence; a\n   shuddering with terror and detestation; the feeling inspired by\n   something frightful and shocking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horror', 'n.', 'That which excites horror or dread, or is horrible; gloom;\n   dreariness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horror-sticken', 'a.', 'Struck with horror; horrified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horror-struck', 'a.', 'Horror-stricken; horrified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hors de combat', '', 'Out of the combat; disabled from fighting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horse', 'n.', 'A hoofed quadruped of the genus Equus; especially, the\n   domestic horse (E. caballus), which was domesticated in Egypt and Asia\n   at a very early period. It has six broad molars, on each side of each\n   jaw, with six incisors, and two canine teeth, both above and below. The\n   mares usually have the canine teeth rudimentary or wanting. The horse\n   differs from the true asses, in having a long, flowing mane, and the\n   tail bushy to the base. Unlike the asses it has callosities, or\n   chestnuts, on all its legs. The horse excels in strength, speed,\n   docility, courage, and nobleness of character, and is used for drawing,\n   carrying, bearing a rider, and like purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horse', 'n.', 'The male of the genus horse, in distinction from the female\n   or male; usually, a castrated male.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horse', 'n.', 'Mounted soldiery; cavalry; -- used without the plural\n   termination; as, a regiment of horse; -- distinguished from foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horse', 'n.', 'A frame with legs, used to support something; as, a\n   clotheshorse, a sawhorse, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horse', 'n.', 'A frame of timber, shaped like a horse, on which soldiers\n   were made to ride for punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horse', 'n.', 'Anything, actual or figurative, on which one rides as on a\n   horse; a hobby.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horse', 'n.', 'A mass of earthy matter, or rock of the same character as\n   the wall rock, occurring in the course of a vein, as of coal or ore;\n   hence, to take horse -- said of a vein -- is to divide into branches\n   for a distance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horse', 'n.', 'See Footrope, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horse', 'a.', 'A breastband for a leadsman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horse', 'a.', 'An iron bar for a sheet traveler to slide upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horse', 'a.', 'A jackstay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horsed', 'imp. & p. p.', 'of Horse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horsing', 'p. pr. & vb. n.', 'of Horse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horse', 'v. t.', 'To provide with a horse, or with horses; to mount on, or\n   as on, a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horse', 'v. t.', 'To sit astride of; to bestride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horse', 'v. t.', 'To cover, as a mare; -- said of the male.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horse', 'v. t.', 'To take or carry on the back; as, the keeper, horsing a\n   deer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horse', 'v. t.', 'To place on the back of another, or on a wooden horse,\n   etc., to be flogged; to subject to such punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horse', 'v. i.', 'To get on horseback.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horseback', 'n.', 'The back of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horseback', 'n.', 'An extended ridge of sand, gravel, and bowlders, in a\n   half-stratified condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horse-chestnut', 'n.', 'The large nutlike seed of a species of Aesculus\n   (Ae. Hippocastanum), formerly ground, and fed to horses, whence the\n   name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horse-chestnut', 'n.', 'The tree itself, which was brought from\n   Constantinople in the beginning of the sixteenth century, and is now\n   common in the temperate zones of both hemispheres. The native American\n   species are called buckeyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horse-drench', 'n.', 'A dose of physic for a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horse-drench', 'n.', 'The appliance by which the dose is administred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horsefish', 'n.', 'The moonfish (Selene setipinnis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horsefish', 'n.', 'The sauger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horseflesh', 'n.', 'The flesh of horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horseflesh', 'n.', 'Horses, generally; the qualities of a horse; as, he is\n   a judge of horseflesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horseflies', 'pl. ', 'of Horsefly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horsefly', 'n.', 'Any dipterous fly of the family Tabanidae, that stings\n   horses, and sucks their blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horsefly', 'n.', 'The horse tick or forest fly (Hippobosca).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horsefeet', 'pl. ', 'of Horsefoot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horsefoot', 'n.', 'The coltsfoot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horsefoot', 'n.', 'The Limulus or horseshoe crab.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horse Guards', '', 'A body of cavalry so called; esp., a British regiment,\n   called the Royal Horse Guards, which furnishes guards of state for the\n   sovereign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horsehair', 'n.', 'A hair of a horse, especially one from the mane or tail;\n   the hairs of the mane or tail taken collectively; a fabric or tuft made\n   of such hairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horsehead', 'n.', 'The silver moonfish (Selene vomer).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horsehide', 'n.', 'The hide of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horsehide', 'n.', 'Leather made of the hide of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horse-jockey', 'n.', 'A professional rider and trainer of race horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horse-jockey', 'n.', 'A trainer and dealer in horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horseknop', 'n.', 'Knapweed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horselaugh', 'n.', 'A loud, boisterous laugh; a guffaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horse-leech', 'n.', 'A large blood-sucking leech (Haemopsis vorax), of\n   Europe and Northern Africa. It attacks the lips and mouths of horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horse-leech', 'n.', 'A farrier; a veterinary surgeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horse-leechery', 'n.', 'The business of a farrier; especially, the art of\n   curing the diseases of horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horse-litter', 'n.', 'A carriage hung on poles, and borne by and between\n   two horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horsemen', 'pl. ', 'of Horseman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horseman', 'n.', 'A rider on horseback; one skilled in the management of\n   horses; a mounted man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horseman', 'n.', 'A mounted soldier; a cavalryman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horseman', 'n.', 'A land crab of the genus Ocypoda, living on the coast of\n   Brazil and the West Indies, noted for running very swiftly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horseman', 'n.', 'A West Indian fish of the genus Eques, as the\n   light-horseman (E. lanceolatus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horsemanship', 'n.', 'The act or art of riding, and of training and\n   managing horses; manege.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horsemint', 'n.', 'A coarse American plant of the Mint family (Monarda\n   punctata).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horsemint', 'n.', 'In England, the wild mint (Mentha sylvestris).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horsenail', 'n.', 'A thin, pointed nail, with a heavy flaring head, for\n   securing a horsehoe to the hoof; a horsehoe nail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horseplay', 'n.', 'Rude, boisterous play.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horsepond', 'n.', 'A pond for watering horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horse power', '', 'The power which a horse exerts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horse power', '', 'A unit of power, used in stating the power required to\n   drive machinery, and in estimating the capabilities of animals or steam\n   engines and other prime movers for doing work. It is the power required\n   for the performance of work at the rate of 33,000 English units of work\n   per minute; hence, it is the power that must be exerted in lifting\n   33,000 pounds at the rate of one foot per minute, or 550 pounds at the\n   rate of one foot per second, or 55 pounds at the rate of ten feet per\n   second, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horse power', '', 'A machine worked by a horse, for driving other\n   machinery; a horse motor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horse-radish', 'n.', 'A plant of the genus Nasturtium (N. Armoracia),\n   allied to scurvy grass, having a root of a pungent taste, much used,\n   when grated, as a condiment and in medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horserake', 'n.', 'A rake drawn by a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horseshoe', 'n.', 'A shoe for horses, consisting of a narrow plate of iron\n   in form somewhat like the letter U, nailed to a horses hoof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horseshoe', 'n.', 'Anything shaped like a horsehoe crab.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horseshoe', 'n.', 'The Limulus of horsehoe crab.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horseshoer', 'n.', 'One who shoes horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horseshoeing', 'n.', 'The act or employment of shoeing horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horsetail', 'n.', 'A leafless plant, with hollow and rushlike stems. It is\n   of the genus Equisetum, and is allied to the ferns. See Illust. of\n   Equisetum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horsetail', 'n.', 'A Turkish standard, denoting rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horseweed', 'n.', 'A composite plant (Erigeron Canadensis), which is a\n   common weed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horsewhip', 'n.', 'A whip for horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horsewhip', 'v. t.', 'To flog or chastise with a horsewhip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horsewomen', 'pl. ', 'of Horsewoman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horsewoman', 'n.', 'A woman who rides on horseback.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horsewood', 'n.', 'A West Indian tree (Calliandra latifolia) with showy,\n   crimson blossoms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horseworm', 'n.', 'The larva of a botfly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horsiness', 'n.', 'The condition or quality of being a horse; that which\n   pertains to a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horsiness', 'n.', 'Fondness for, or interest in, horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horsly', 'a.', 'Horselike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horsy', 'a.', 'Pertaining to, or suggestive of, a horse, or of horse\n   racing; as, horsy manners; garments of fantastically horsy fashions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hortation', 'n.', 'The act of exhorting, inciting, or giving advice;\n   exhortation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hortative', 'a.', 'Giving exhortation; advisory; exhortative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hortative', 'n.', 'An exhortation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hortatory', 'a.', 'Giving exhortation or advise; encouraging; exhortatory;\n   inciting; as, a hortatory speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hortensial', 'a.', 'Fit for a garden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horticultor', 'n.', 'One who cultivates a garden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horticultural', 'a.', 'Of or pertaining to horticulture, or the culture of\n   gardens or orchards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horticulture', 'n.', 'The cultivation of a garden or orchard; the art of\n   cultivating gardens or orchards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Horticulturist', 'n.', 'One who practices horticulture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hortulan', 'a.', 'Belonging to a garden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hortus siccus', '', 'A collection of specimens of plants, dried and\n   preserved, and arranged systematically; an herbarium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hortyard', 'n.', 'An orchard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hosannas', 'pl. ', 'of Hosanna');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hosanna', 'n.', 'A Hebrew exclamation of praise to the Lord, or an\n   invocation of blessings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hose', 'pl. ', 'of Hose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hosen', 'pl. ', 'of Hose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hose', 'n.', 'Close-fitting trousers or breeches, as formerly worn,\n   reaching to the knee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hose', 'n.', 'Covering for the feet and lower part of the legs; a stocking\n   or stockings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hose', 'n.', 'A flexible pipe, made of leather, India rubber, or other\n   material, and used for conveying fluids, especially water, from a\n   faucet, hydrant, or fire engine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hosen', 'n. pl.', 'See Hose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hosier', 'n.', 'One who deals in hose or stocking, or in goods knit or\n   woven like hose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hosiery', 'n.', 'The business of a hosier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hosiery', 'n.', 'Stockings, in general; goods knit or woven like hose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hospice', 'n.', 'A convent or monastery which is also a place of refuge or\n   entertainment for travelers on some difficult road or pass, as in the\n   Alps; as, the Hospice of the Great St. Bernard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hospitable', 'a.', 'Receiving and entertaining strangers or guests with\n   kindness and without reward; kind to strangers and guests;\n   characterized by hospitality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hospitable', 'a.', 'Proceeding from or indicating kindness and generosity\n   to guests and strangers; as, hospitable rites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hospitableness', 'n.', 'The quality of being hospitable; hospitality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hospitably', 'adv.', 'In a hospitable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hospitage', 'n.', 'Hospitality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hospital', 'n.', 'A place for shelter or entertainment; an inn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hospital', 'n.', 'A building in which the sick, injured, or infirm are\n   received and treated; a public or private institution founded for\n   reception and cure, or for the refuge, of persons diseased in body or\n   mind, or disabled, infirm, or dependent, and in which they are treated\n   either at their own expense, or more often by charity in whole or in\n   part; a tent, building, or other place where the sick or wounded of an\n   army cared for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hospital', 'a.', 'Hospitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hospitaler', 'n.', 'One residing in a hospital, for the purpose of\n   receiving the poor, the sick, and strangers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hospitaler', 'n.', 'One of an order of knights who built a hospital at\n   Jerusalem for pilgrims, A. D. 1042. They were called Knights of St.\n   John of Jerusalem, and after the removal of the order to Malta, Knights\n   of Malta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hospitalism', 'n.', 'A vitiated condition of the body, due to long\n   confinement in a hospital, or the morbid condition of the atmosphere of\n   a hospital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hospitalities', 'pl. ', 'of Hospitality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hospitality', 'n.', 'The act or practice of one who is hospitable;\n   reception and entertainment of strangers or guests without reward, or\n   with kind and generous liberality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hospitalize', 'v. t.', 'To render (a building) unfit for habitation, by\n   long continued use as a hospital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hospitate', 'v. i.', 'To receive hospitality; to be a guest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hospitate', 'v. t.', 'To receive with hospitality; to lodge as a guest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hospitium', 'n.', 'An inn; a lodging; a hospice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hospitium', 'n.', 'An inn of court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hospodar', 'n.', 'A title borne by the princes or governors of Moldavia and\n   Wallachia before those countries were united as Roumania.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Host', 'n.', 'The consecrated wafer, believed to be the body of Christ,\n   which in the Mass is offered as a sacrifice; also, the bread before\n   consecration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Host', 'n.', 'An army; a number of men gathered for war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Host', 'n.', 'Any great number or multitude; a throng.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Host', 'n.', 'One who receives or entertains another, whether gratuitously\n   or for compensation; one from whom another receives food, lodging, or\n   entertainment; a landlord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Host', 'v. t.', 'To give entertainment to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Host', 'v. i.', 'To lodge at an inn; to take up entertainment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hostage', 'n.', 'A person given as a pledge or security for the performance\n   of the conditions of a treaty or stipulations of any kind, on the\n   performance of which the person is to be released.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hostel', 'n.', 'An inn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hostel', 'n.', 'A small, unendowed college in Oxford or Cambridge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hosteler', 'n.', 'The keeper of a hostel or inn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hosteler', 'n.', 'A student in a hostel, or small unendowed collede in\n   Oxford or Cambridge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hostelry', 'n.', 'An inn; a lodging house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hostess', 'n.', 'A female host; a woman who hospitably entertains guests at\n   her house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hostess', 'n.', 'A woman who entertains guests for compensation; a female\n   innkeeper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hostess-ship', 'n.', 'The character, personality, or office of a hostess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hostie', 'n.', 'The consecrated wafer; the host.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hostile', 'a.', 'Belonging or appropriate to an enemy; showing the\n   disposition of an enemy; showing ill will and malevolence, or a desire\n   to thwart and injure; occupied by an enemy or enemies; inimical;\n   unfriendly; as, a hostile force; hostile intentions; a hostile country;\n   hostile to a sudden change.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hostile', 'n.', 'An enemy; esp., an American Indian in arms against the\n   whites; -- commonly in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hostilely', 'adv.', 'In a hostile manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hostilities', 'pl. ', 'of Hostility');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hostility', 'n.', 'State of being hostile; public or private enemy;\n   unfriendliness; animosity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hostility', 'n.', 'An act of an open enemy; a hostile deed; especially in\n   the plural, acts of warfare; attacks of an enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hostilize', 'v. t.', 'To make hostile; to cause to become an enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hosting', 'n.', 'An encounter; a battle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hosting', 'n.', 'A muster or review.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hostler', 'n.', 'An innkeeper. [Obs.] See Hosteler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hostler', 'n.', 'The person who has the care of horses at an inn or stable;\n   hence, any one who takes care of horses; a groom; -- so called because\n   the innkeeper formerly attended to this duty in person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hostler', 'n.', 'The person who takes charge of a locomotive when it is\n   left by the engineer after a trip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hostless', 'a.', 'Inhospitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hostry', 'n.', 'A hostelry; an inn or lodging house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hostry', 'n.', 'A stable for horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hot', '', 'imp. & p. p. of Hote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hot', 'superl.', 'Having much sensible heat; exciting the feeling of warmth\n   in a great degree; very warm; -- opposed to cold, and exceeding warm in\n   degree; as, a hot stove; hot water or air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hot', 'superl.', 'Characterized by heat, ardor, or animation; easily\n   excited; firely; vehement; passionate; violent; eager.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hot', 'superl.', 'Lustful; lewd; lecherous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hot', 'superl.', 'Acrid; biting; pungent; as, hot as mustard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hotbed', 'n.', 'A bed of earth heated by fermenting manure or other\n   substances, and covered with glass, intended for raising early plants,\n   or for nourishing exotics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hotbed', 'n.', 'A place which favors rapid growth or development; as, a\n   hotbed of sedition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hot blast', '', 'See under Blast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hot-blooded', 'a.', 'Having hot blood; excitable; high-spirited; irritable;\n   ardent; passionate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hot-brained', 'a.', 'Ardent in temper; violent; rash; impetuous; as,\n   hot-brained youth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hotchpot', 'n.', 'Alt. of Hotchpotch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hotchpotch', 'n.', 'A mingled mass; a confused mixture; a stew of various\n   ingredients; a hodgepodge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hotchpotch', 'n.', 'A blending of property for equality of division, as\n   when lands given in frank-marriage to one daughter were, after the\n   death of the ancestor, blended with the lands descending to her and to\n   her sisters from the same ancestor, and then divided in equal portions\n   among all the daughters. In modern usage, a mixing together, or\n   throwing into a common mass or stock, of the estate left by a person\n   deceased and the amounts advanced to any particular child or children,\n   for the purpose of a more equal division, or of equalizing the shares\n   of all the children; the property advanced being accounted for at its\n   value when given.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hotcockles', 'n.', 'A childish play, in which one covers his eyes, and\n   guesses who strikes him or his hand placed behind him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hatte', 'pres. & imp.', 'of Hote');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hot', '', 'of Hote');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hote', 'p. p.', 'of Hote');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoten', '', 'of Hote');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hot', '', 'of Hote');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hote', 'v. t. & i.', 'To command; to enjoin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hote', 'v. t. & i.', 'To promise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hote', 'v. t. & i.', 'To be called; to be named.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hotel', 'n.', 'A house for entertaining strangers or travelers; an inn or\n   public house, of the better class.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hotel', 'n.', 'In France, the mansion or town residence of a person of rank\n   or wealth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hotel-de-ville', 'n.', 'A city hall or townhouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hotel-Dieu', 'n.', 'A hospital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoten', '', 'p. p. of Hote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hotfoot', 'adv.', 'In haste; foothot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hot-head', 'n.', 'A violent, passionate person; a hasty or impetuous\n   person; as, the rant of a hot-head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hot-headed', 'a.', 'Fiery; violent; rash; hasty; impetuous; vehement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hothouse', 'n.', 'A house kept warm to shelter tender plants and shrubs\n   from the cold air; a place in which the plants of warmer climates may\n   be reared, and fruits ripened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hothouse', 'n.', 'A bagnio, or bathing house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hothouse', 'n.', 'A brothel; a bagnio.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hothouse', 'n.', 'A heated room for drying green ware.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hot-livered', 'a.', 'Of an excitable or irritable temperament; irascible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hotly', 'a.', 'In a hot or fiery manner; ardently; vehemently; violently;\n   hastily; as, a hotly pursued.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hotly', 'a.', 'In a lustful manner; lustfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hot-mouthed', 'a.', 'Headstrong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hotness', 'n.', 'The quality or state of being hot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hotness', 'n.', 'Heat or excitement of mind or manner; violence; vehemence;\n   impetuousity; ardor; fury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hotpress', 'v. t.', 'To apply to, in conjunction with mechanical pressure,\n   for the purpose of giving a smooth and glosay surface, or to express\n   oil, etc.; as, to hotpress paper, linen, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hotpressed', 'a.', 'Pressed while heat is applied. See Hotpress, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hot-short', 'a.', 'More or less brittle when heated; as, hot-short iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hot-spirited', 'a.', 'Having a fiery spirit; hot-headed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hotspur', 'n.', 'A rash, hot-headed man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hotspur', 'a.', 'Alt. of Hotspurred');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hotspurred', 'a.', 'Violent; impetuous; headstrong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hottentot', 'n.', 'One of a degraded and savage race of South Africa, with\n   yellowish brown complexion, high cheek bones, and wooly hair growing in\n   tufts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hottentot', 'n.', 'The language of the Hottentots, which is remarkable for\n   its clicking sounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hottentotism', 'n.', 'A term employed to describe one of the varieties of\n   stammering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Houdah', 'n.', 'See Howdah.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hough', 'n.', 'Same as Hock, a joint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Houghed', 'imp. & p. p.', 'of Hough');
  INSERT INTO words (word, word_type, description) VALUES  (    'Houghing', 'p. pr. & vb. n.', 'of Hough');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hough', 'v. t.', 'Same as Hock, to hamstring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hough', 'n.', 'An adz; a hoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hough', 'v. t.', 'To cut with a hoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Houlet', 'n.', 'An owl. See Howlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoult', 'n.', 'A piece of woodland; a small wood. [Obs.] See Holt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hound', 'n.', 'A variety of the domestic dog, usually having large,\n   drooping ears, esp. one which hunts game by scent, as the foxhound,\n   bloodhound, deerhound, but also used for various breeds of fleet\n   hunting dogs, as the greyhound, boarhound, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hound', 'n.', 'A despicable person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hound', 'n.', 'A houndfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hound', 'n.', 'Projections at the masthead, serving as a support for the\n   trestletrees and top to rest on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hound', 'n.', 'A side bar used to strengthen portions of the running gear\n   of a vehicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hounded', 'imp. & p. p.', 'of Hound');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hounding', 'p. pr. & vb. n.', 'of Hound');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hound', 'v. t.', 'To set on the chase; to incite to pursuit; as, to hounda\n   dog at a hare; to hound on pursuers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hound', 'v. t.', 'To hunt or chase with hounds, or as with hounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Houndfish', 'n.', 'Any small shark of the genus Galeus or Mustelus, of\n   which there are several species, as the smooth houndfish (G. canis), of\n   Europe and America; -- called also houndshark, and dogfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hounding', 'n.', 'The act of one who hounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hounding', 'n.', 'The part of a mast below the hounds and above the deck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hounds-tongue', 'n.', 'A biennial weed (Cynoglossum officinale), with soft\n   tongue-shaped leaves, and an offensive odor. It bears nutlets covered\n   with barbed or hooked prickles. Called also dogs-tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Houp', 'n.', 'See Hoopoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hour', 'n.', 'The twenty-fourth part of a day; sixty minutes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hour', 'n.', 'The time of the day, as expressed in hours and minutes, and\n   indicated by a timepiece; as, what is the hour? At what hour shall we\n   meet?');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hour', 'n.', 'Fixed or appointed time; conjuncture; a particular time or\n   occasion; as, the hour of greatest peril; the man for the hour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hour', 'n.', 'Certain prayers to be repeated at stated times of the day, as\n   matins and vespers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hour', 'n.', 'A measure of distance traveled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hourglass', 'n.', 'An instrument for measuring time, especially the\n   interval of an hour. It consists of a glass vessel having two\n   compartments, from the uppermost of which a quantity of sand, water, or\n   mercury occupies an hour in running through a small aperture unto the\n   lower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Houris', 'pl. ', 'of Houri');
  INSERT INTO words (word, word_type, description) VALUES  (    'Houri', 'n.', 'A nymph of paradise; -- so called by the Mohammedans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hourly', 'a.', 'Happening or done every hour; occurring hour by hour;\n   frequent; often repeated; renewed hour by hour; continual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hourly', 'adv.', 'Every hour; frequently; continually.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hours', 'n. pl.', 'Goddess of the seasons, or of the hours of the day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housage', 'n.', 'A fee for keeping goods in a house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Houses', 'pl. ', 'of House');
  INSERT INTO words (word, word_type, description) VALUES  (    'House', 'n.', 'A structure intended or used as a habitation or shelter for\n   animals of any kind; but especially, a building or edifice for the\n   habitation of man; a dwelling place, a mansion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'House', 'n.', 'Household affairs; domestic concerns; particularly in the\n   phrase to keep house. See below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'House', 'n.', 'Those who dwell in the same house; a household.');
  INSERT INTO words (word, word_type, description) VALUES  (    'House', 'n.', 'A family of ancestors, descendants, and kindred; a race of\n   persons from the same stock; a tribe; especially, a noble family or an\n   illustrious race; as, the house of Austria; the house of Hanover; the\n   house of Israel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'House', 'n.', 'One of the estates of a kingdom or other government\n   assembled in parliament or legislature; a body of men united in a\n   legislative capacity; as, the House of Lords; the House of Commons; the\n   House of Representatives; also, a quorum of such a body. See Congress,\n   and Parliament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'House', 'n.', 'A firm, or commercial establishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'House', 'n.', 'A public house; an inn; a hotel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'House', 'n.', 'A twelfth part of the heavens, as divided by six circles\n   intersecting at the north and south points of the horizon, used by\n   astrologers in noting the positions of the heavenly bodies, and casting\n   horoscopes or nativities. The houses were regarded as fixed in respect\n   to the horizon, and numbered from the one at the eastern horizon,\n   called the ascendant, first house, or house of life, downward, or in\n   the direction of the earths revolution, the stars and planets passing\n   through them in the reverse order every twenty-four hours.');
  INSERT INTO words (word, word_type, description) VALUES  (    'House', 'n.', 'A square on a chessboard, regarded as the proper place of a\n   piece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'House', 'n.', 'An audience; an assembly of hearers, as at a lecture, a\n   theater, etc.; as, a thin or a full house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'House', 'n.', 'The body, as the habitation of the soul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'House', 'n.', 'The grave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housed', 'imp. & p. p.', 'of House');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housing', 'p. pr. & vb. n.', 'of House');
  INSERT INTO words (word, word_type, description) VALUES  (    'House', 'v. t.', 'To take or put into a house; to shelter under a roof; to\n   cover from the inclemencies of the weather; to protect by covering; as,\n   to house ones family in a comfortable home; to house farming utensils;\n   to house cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'House', 'v. t.', 'To drive to a shelter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'House', 'v. t.', 'To admit to residence; to harbor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'House', 'v. t.', 'To deposit and cover, as in the grave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'House', 'v. t.', 'To stow in a safe place; to take down and make safe; as,\n   to house the upper spars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'House', 'v. i.', 'To take shelter or lodging; to abide to dwell; to lodge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'House', 'v. i.', 'To have a position in one of the houses. See House, n.,\n   8.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housebote', 'n.', 'Wood allowed to a tenant for repairing the house and for\n   fuel. This latter is often called firebote. See Bote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housebreaker', 'n.', 'One who is guilty of the crime of housebreaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housebreaking', 'n.', 'The act of breaking open and entering, with a\n   felonious purpose, the dwelling house of another, whether done by day\n   or night. See Burglary, and To break a house, under Break.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housebuilder', 'n.', 'One whose business is to build houses; a housewright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housecarl', 'n.', 'A household servant; also, one of the bodyguard of King\n   Canute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Household', 'n.', 'Those who dwell under the same roof and compose a\n   family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Household', 'n.', 'A line of ancestory; a race or house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Household', 'a.', 'Belonging to the house and family; domestic; as,\n   household furniture; household affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Householder', 'n.', 'The master or head of a family; one who occupies a\n   house with his family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housekeeper', 'n.', 'One who occupies a house with his family; a\n   householder; the master or mistress of a family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housekeeper', 'n.', 'One who does, or oversees, the work of keeping house;\n   as, his wife is a good housekeeper; often, a woman hired to superintend\n   the servants of a household and manage the ordinary domestic affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housekeeper', 'n.', 'One who exercises hospitality, or has a plentiful and\n   hospitable household.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housekeeper', 'n.', 'One who keeps or stays much at home.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housekeeper', 'n.', 'A house dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housekeeping', 'n.', 'The state of occupying a dwelling house as a\n   householder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housekeeping', 'n.', 'Care of domestic concerns; management of a house and\n   home affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housekeeping', 'n.', 'Hospitality; a liberal and hospitable table; a supply\n   of provisions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housekeeping', 'a.', 'Domestic; used in a family; as, housekeeping\n   commodities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housel', 'n.', 'The eucharist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housel', 'v. t.', 'To administer the eucharist to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Houseleek', 'n.', 'A succulent plant of the genus Sempervivum (S.\n   tectorum), originally a native of subalpine Europe, but now found very\n   generally on old walls and roofs. It is very tenacious of life under\n   drought and heat; -- called also ayegreen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Houseless', 'a.', 'Destitute of the shelter of a house; shelterless;\n   homeless; as, a houseless wanderer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Houselessness', 'n.', 'The state of being houseless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Houseline', 'n.', 'A small line of three strands used for seizing; --\n   called also housing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Houseling', 'a.', 'Same as Housling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housemaid', 'n.', 'A female servant employed to do housework, esp. to take\n   care of the rooms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housemate', 'n.', 'One who dwells in the same house with another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Houseroom', 'n.', 'Room or place in a house; as, to give any one houseroom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housewarming', 'n.', 'A feast or merry-making made by or for a family or\n   business firm on taking possession of a new house or premises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housewife', 'n.', 'The wife of a householder; the mistress of a family; the\n   female head of a household.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housewife', 'n.', 'A little case or bag for materials used in sewing, and\n   for other articles of female work; -- called also hussy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housewife', 'n.', 'A hussy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housewife', 'v. t.', 'Alt. of Housewive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housewive', 'v. t.', 'To manage with skill and economy, as a housewife or\n   other female manager; to economize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housewifely', 'a.', 'Pertaining or appropriate to a housewife; domestic;\n   economical; prudent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housewifery', 'n.', 'The business of the mistress of a family; female\n   management of domestic concerns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housework', 'n.', 'The work belonging to housekeeping; especially, kitchen\n   work, sweeping, scrubbing, bed making, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housewright', 'n.', 'A builder of houses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housing', 'n.', 'The act of putting or receiving under shelter; the state\n   of dwelling in a habitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housing', 'n.', 'That which shelters or covers; houses, taken collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housing', 'n.', 'The space taken out of one solid, to admit the insertion\n   of part of another, as the end of one timber in the side of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housing', 'n.', 'A niche for a statue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housing', 'n.', 'A frame or support for holding something in place, as\n   journal boxes, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housing', 'n.', 'That portion of a mast or bowsprit which is beneath the\n   deck or within the vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housing', 'n.', 'A covering or protection, as an awning over the deck of a\n   ship when laid up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housing', 'n.', 'A houseline. See Houseline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housing', 'n.', 'A cover or cloth for a horses saddle, as an ornamental or\n   military appendage; a saddlecloth; a horse cloth; in plural, trappings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housing', 'n.', 'An appendage to the hames or collar of a harness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Housling', 'a.', 'Sacramental; as, housling fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Houss', 'n.', 'A saddlecloth; a housing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Houtou', 'n.', 'A beautiful South American motmot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Houve', 'n.', 'A head covering of various kinds; a hood; a coif; a cap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Houyhnhnm', 'n.', 'One of the race of horses described by Swift in his\n   imaginary travels of Lemuel Gulliver. The Houyhnhnms were endowed with\n   reason and noble qualities; subject to them were Yahoos, a race of\n   brutes having the form and all the worst vices of men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hove', '', 'imp. & p. p. of Heave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hove', 'v. i. & t.', 'To rise; to swell; to heave; to cause to swell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hove', 'v. i.', 'To hover around; to loiter; to lurk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hovel', 'n.', 'An open shed for sheltering cattle, or protecting produce,\n   etc., from the weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hovel', 'n.', 'A poor cottage; a small, mean house; a hut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hovel', 'n.', 'A large conical brick structure around which the firing\n   kilns are grouped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoveled', 'imp. & p. p.', 'of Hovel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hovelled', '', 'of Hovel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoveling', 'p. pr. & vb. n.', 'of Hovel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hovelling', '', 'of Hovel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hovel', 'v. t.', 'To put in a hovel; to shelter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoveler', 'n.', 'One who assists in saving life and property from a wreck;\n   a coast boatman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoveling', 'n.', 'A method of securing a good draught in chimneys by\n   covering the top, leaving openings in the sides, or by carrying up two\n   of the sides higher than the other two.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoven', '', 'p. p. of Heave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoven', 'a.', 'Affected with the disease called hoove; as, hoven cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hover', 'n.', 'A cover; a shelter; a protection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hovered', 'imp. & p. p.', 'of Hover');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hovering', 'p. pr. & vb. n.', 'of Hover');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hover', 'v. i.', 'To hang fluttering in the air, or on the wing; to remain\n   in flight or floating about or over a place or object; to be suspended\n   in the air above something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hover', 'v. i.', 'To hang about; to move to and fro near a place,\n   threateningly, watchfully, or irresolutely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoverer', 'n.', 'A device in an incubator for protecting the young chickens\n   and keeping them warm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hover-hawk', 'n.', 'The kestrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoveringly', 'adv.', 'In a hovering manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'How', 'adv.', 'In what manner or way; by what means or process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'How', 'adv.', 'To what degree or extent, number or amount; in what\n   proportion; by what measure or quality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'How', 'adv.', 'For what reason; from what cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'How', 'adv.', 'In what state, condition, or plight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'How', 'adv.', 'By what name, designation, or title.');
  INSERT INTO words (word, word_type, description) VALUES  (    'How', 'adv.', 'At what price; how dear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Howadji', 'n.', 'A traveler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Howadji', 'n.', 'A merchant; -- so called in the East because merchants\n   were formerly the chief travelers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Howbeit', 'conj.', 'Be it as it may; nevertheless; notwithstanding;\n   although; albeit; yet; but; however.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Howdah', 'n.', 'A seat or pavilion, generally covered, fastened on the back\n   of an elephant, for the rider or riders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Howdy', 'n.', 'A midwife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Howel', 'n.', 'A tool used by coopers for smoothing and chamfering rheir\n   work, especially the inside of casks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Howel', 'v. t.', 'To smooth; to plane; as, to howel a cask.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Howell', 'n.', 'The upper stage of a porcelian furnace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'However', 'adv.', 'In whetever manner, way, or degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'However', 'adv.', 'At all events; at least; in any case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'However', 'conj.', 'Nevertheless; notwithstanding; yet; still; though; as,\n   I shall not oppose your design; I can not, however, approve of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Howitz', 'n.', 'A howitzer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Howitzer', 'n.', 'A gun so short that the projectile, which was hollow,\n   could be put in its place by hand; a kind of mortar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Howitzer', 'n.', 'A short, light, largebore cannon, usually having a\n   chamber of smaller diameter than the rest of the bore, and intended to\n   throw large projectiles with comparatively small charges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Howker', 'n.', 'Same as Hooker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Howled', 'imp. & p. p.', 'of Howl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Howling', 'p. pr. & vb. n.', 'of Howl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Howl', 'v. i.', 'To utter a loud, protraced, mournful sound or cry, as dogs\n   and wolves often do.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Howl', 'v. i.', 'To utter a sound expressive of distress; to cry aloud and\n   mournfully; to lament; to wail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Howl', 'v. i.', 'To make a noise resembling the cry of a wild beast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Howl', 'v. t.', 'To utter with outcry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Howl', 'n.', 'The protracted, mournful cry of a dog or a wolf, or other\n   like sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Howl', 'n.', 'A prolonged cry of distress or anguish; a wail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Howler', 'n.', 'One who howls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Howler', 'n.', 'Any South American monkey of the genus Mycetes. Many\n   species are known. They are arboreal in their habits, and are noted for\n   the loud, discordant howling in which they indulge at night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Howlet', 'n.', 'An owl; an owlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Howp', 'v. i.', 'To cry out; to whoop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Howso', 'adv.', 'Howsoever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Howsoever', 'adj. & conj.', 'In what manner soever; to whatever degree or\n   extent; however.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Howsoever', 'adj. & conj.', 'Although; though; however.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Howve', 'n.', 'A hood. See Houve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hox', 'v. t.', 'To hock; to hamstring. See Hock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoy', 'n.', 'A small coaster vessel, usually sloop-rigged, used in\n   conveying passengers and goods from place to place, or as a tender to\n   larger vessels in port.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoy', 'interj.', 'Ho! Halloe! Stop!');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoyden', 'n.', 'Same as Hoiden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoymen', 'pl. ', 'of Hoyman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hoyman', 'n.', 'One who navigates a hoy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huanaco', 'n.', 'See Guanaco.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hub', 'n.', 'The central part, usually cylindrical, of a wheel; the nave.\n   See Illust. of Axle box.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hub', 'n.', 'The hilt of a weapon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hub', 'n.', 'A rough protuberance or projecting obstruction; as, a hub in\n   the road. [U.S.] See Hubby.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hub', 'n.', 'A goal or mark at which quoits, etc., are cast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hub', 'n.', 'A hardened, engraved steel punch for impressing a device upon\n   a die, used in coining, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hub', 'n.', 'A screw hob. See Hob, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hub', 'n.', 'A block for scotching a wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hubble-bubble', 'n.', 'A tobacco pipe, so arranged that the smoke passes\n   through water, making a bubbling noise, whence its name. In India, the\n   bulb containing the water is often a cocoanut shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hubbub', 'v. i.', 'A loud noise of many confused voices; a tumult; uproar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hubby', 'a.', 'Full of hubs or protuberances; as, a road that has been\n   frozen while muddy is hubby.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hubner', 'n.', 'A mineral of brownish black color, occurring in columnar or\n   foliated masses. It is native manganese tungstate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huch', 'n.', 'Alt. of Huchen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huchen', 'n.', 'A large salmon (Salmo, / Salvelinus, hucho) inhabiting the\n   Danube; -- called also huso, and bull trout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huck', 'v. i.', 'To higgle in trading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huckaback', 'n.', 'A kind of linen cloth with raised figures, used for\n   towelings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huckle', 'n.', 'The hip; the haunch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huckle', 'n.', 'A bunch or part projecting like the hip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huckle-backed', 'a.', 'Round-shoulded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huckleberry', 'n.', 'The edible black or dark blue fruit of several species\n   of the American genus Gaylussacia, shrubs nearly related to the\n   blueberries (Vaccinium), and formerly confused with them. The commonest\n   huckelberry comes from G. resinosa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huckleberry', 'n.', 'The shrub that bears the berries. Called also\n   whortleberry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huckster', 'n.', 'A retailer of small articles, of provisions, and the\n   like; a peddler; a hawker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huckster', 'n.', 'A mean, trickish fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huckstered', 'imp. & p. p.', 'of Huckster');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huckstering', 'p. pr. & vb. n.', 'of Huckster');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huckster', 'v. i.', 'To deal in small articles, or in petty bargains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hucksterage', 'n.', 'The business of a huckster; small dealing; peddling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hucksterer', 'n.', 'A huckster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huckstress', 'n.', 'A female huckster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hud', 'n.', 'A huck or hull, as of a nut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huddled', 'imp. & p. p.', 'of Huddle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huddling', 'p. pr. & vb. n.', 'of Huddle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huddle', 'v. i.', 'To press together promiscuously, from confusion,\n   apprehension, or the like; to crowd together confusedly; to press or\n   hurry in disorder; to crowd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huddle', 'v. t.', 'To crowd (things) together to mingle confusedly; to\n   assemble without order or system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huddle', 'v. t.', 'To do, make, or put, in haste or roughly; hence, to do\n   imperfectly; -- usually with a following preposition or adverb; as, to\n   huddle on; to huddle up; to huddle together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huddle', 'n.', 'A crowd; a number of persons or things crowded together in\n   a confused manner; tumult; confusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huddler', 'n.', 'One who huddles things together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hudge', 'n.', 'An iron bucket for hoisting coal or ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hudibrastic', 'a.', 'Similar to, or in the style of, the poem \"Hudibras,\"\n   by Samuel Butler; in the style of doggerel verse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hudsonian', 'a.', 'Of or pertaining to Hudsons Bay or to the Hudson River;\n   as, the Hudsonian curlew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hue', 'n.', 'Color or shade of color; tint; dye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hue', 'n.', 'A predominant shade in a composition of primary colors; a\n   primary color modified by combination with others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hue', 'n.', 'A shouting or vociferation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hued', 'a.', 'Having color; -- usually in composition; as, bright-hued;\n   many-hued.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hueless', 'a.', 'Destitute of color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huer', 'n.', 'One who cries out or gives an alarm; specifically, a balker;\n   a conder. See Balker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huffed', 'imp. & p. p.', 'of Huff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huffing', 'p. pr. & vb. n.', 'of Huff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huff', 'v. t.', 'To swell; to enlarge; to puff up; as, huffed up with air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huff', 'v. t.', 'To treat with insolence and arrogance; to chide or rebuke\n   with insolence; to hector; to bully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huff', 'v. t.', 'To remove from the board (the piece which could have\n   captured an opposing piece). See Huff, v. i., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huff', 'v. i.', 'To enlarge; to swell up; as, bread huffs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huff', 'v. i.', 'To bluster or swell with anger, pride, or arrogance; to\n   storm; to take offense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huff', 'v. i.', 'To remove from the board a man which could have captured a\n   piece but has not done so; -- so called because it was the habit to\n   blow upon the piece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huff', 'n.', 'A swell of sudden anger or arrogance; a fit of disappointment\n   and petulance or anger; a rage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huff', 'n.', 'A boaster; one swelled with a false opinion of his own value\n   or importance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huffcap', 'n.', 'A blusterer; a bully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huffcap', 'a.', 'Blustering; swaggering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huffer', 'n.', 'A bully; a blusterer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huffiness', 'n.', 'The state of being huffish; petulance; bad temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huffingly', 'adv.', 'Blusteringly; arrogantly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huffish', 'a.', 'Disposed to be blustering or arrogant; petulant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huffy', 'a.', 'Puffed up; as, huffy bread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huffy', 'a.', 'Characterized by arrogance or petulance; easily offended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hugged', 'imp. & p. p.', 'of Hug');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hugging', 'p. pr. & vb. n.', 'of Hug');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hug', 'v. i.', 'To cower; to crouch; to curl up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hug', 'v. i.', 'To crowd together; to cuddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hug', 'v. t.', 'To press closely within the arms; to clasp to the bosom; to\n   embrace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hug', 'v. t.', 'To hold fast; to cling to; to cherish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hug', 'v. t.', 'To keep close to; as, to hug the land; to hug the wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hug', 'n.', 'A close embrace or clasping with the arms, as in affection or\n   in wrestling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huge', 'superl.', 'Very large; enormous; immense; excessive; -- used esp.\n   of material bulk, but often of qualities, extent, etc.; as, a huge ox;\n   a huge space; a huge difference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hugger', 'n.', 'One who hugs or embraces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hugger', 'v. t. & i.', 'To conceal; to lurk ambush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hugger-mugger', 'n.', 'Privacy; secrecy. Commonly in the phrase in\n   hugger-mugger, with haste and secrecy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hugger-mugger', 'a.', 'Secret; clandestine; sly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hugger-mugger', 'a.', 'Confused; disorderly; slovenly; mean; as,\n   hugger-mugger doings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huggle', 'v. t.', 'To hug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huguenot', 'n.', 'A French Protestant of the period of the religious wars\n   in France in the 16th century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huguenotism', 'n.', 'The religion of the Huguenots in France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hugy', 'a.', 'Vast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huia bird', '', 'A New Zealand starling (Heteralocha acutirostris),\n   remarkable for the great difference in the form and length of the bill\n   in the two sexes, that of the male being sharp and straight, that of\n   the female much longer and strongly curved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huisher', 'n.', 'See Usher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huisher', 'v. t.', 'To usher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huke', 'n.', 'An outer garment worn in Europe in the Middle Ages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hulan', 'n.', 'See Uhlan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hulch', 'n.', 'A hunch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hulchy', 'a.', 'Swollen; gibbous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hulk', 'n.', 'The body of a ship or decked vessel of any kind; esp., the\n   body of an old vessel laid by as unfit for service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hulk', 'n.', 'A heavy ship of clumsy build.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hulk', 'n.', 'Anything bulky or unwieldly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hulk', 'v. t.', 'To take out the entrails of; to disembowel; as, to hulk a\n   hare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hulking', 'a.', 'Alt. of Hulky');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hulky', 'a.', 'Bulky; unwiedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hull', 'v. t.', 'The outer covering of anything, particularly of a nut or\n   of grain; the outer skin of a kernel; the husk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hull', 'v. t.', 'The frame or body of a vessel, exclusive of her masts,\n   yards, sails, and rigging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hulled', 'imp. & p. p.', 'of Hull');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hulling', 'p. pr. & vb. n.', 'of Hull');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hull', 'v. t.', 'To strip off or separate the hull or hulls of; to free\n   from integument; as, to hull corn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hull', 'v. t.', 'To pierce the hull of, as a ship, with a cannon ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hull', 'v. i.', 'To toss or drive on the water, like the hull of a ship\n   without sails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hullabaloo', 'n.', 'A confused noise; uproar; tumult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hulled', 'a.', 'Deprived of the hulls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huller', 'n.', 'One who, or that which, hulls; especially, an agricultural\n   machine for removing the hulls from grain; a hulling machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hullo', 'interj.', 'See Hollo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hully', 'a.', 'Having or containing hulls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huloist', 'n.', 'See Hyloist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hulotheism', 'n.', 'See Hylotheism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hulver', 'n.', 'Holly, an evergreen shrub or tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hummed', 'imp. & p. p.', 'of Hum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humming', 'p. pr. & vb. n.', 'of Hum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hum', 'v. i.', 'To make a low, prolonged sound, like that of a bee in\n   flight; to drone; to murmur; to buzz; as, a top hums.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hum', 'v. i.', 'To make a nasal sound, like that of the letter m prolonged,\n   without opening the mouth, or articulating; to mumble in monotonous\n   undertone; to drone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hum', 'v. i.', 'To make an inarticulate sound, like hm, through the nose\n   in the process of speaking, from embarrassment or a affectation; to\n   hem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hum', 'v. i.', 'To express satisfaction by a humming noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hum', 'v. i.', 'To have the sensation of a humming noise; as, my head hums,\n   -- a pathological condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hum', 'v. t.', 'To sing with shut mouth; to murmur without articulation; to\n   mumble; as, to hum a tune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hum', 'v. t.', 'To express satisfaction with by humming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hum', 'v. t.', 'To flatter by approving; to cajole; to impose on; to\n   humbug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hum', 'n.', 'A low monotonous noise, as of bees in flight, of a swiftly\n   revolving top, of a wheel, or the like; a drone; a buzz.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hum', 'n.', 'Any inarticulate and buzzing sound');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hum', 'n.', 'The confused noise of a crowd or of machinery, etc., heard at\n   a distance; as, the hum of industry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hum', 'n.', 'A buzz or murmur, as of approbation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hum', 'n.', 'An imposition or hoax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hum', 'interj.', 'An inarticulate nasal sound or murmur, like hm, uttered\n   by a speaker in pause from embarrassment, affectation, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hum', 'interj.', 'A kind of strong drink formerly used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hum', 'interj.', 'Ahem; hem; an inarticulate sound uttered in a pause of\n   speech implying doubt and deliberation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Human', 'a.', 'Belonging to man or mankind; having the qualities or\n   attributes of a man; of or pertaining to man or to the race of man; as,\n   a human voice; human shape; human nature; human sacrifices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Human', 'n.', 'A human being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humanate', 'a.', 'Indued with humanity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humane', 'a.', 'Pertaining to man; human.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humane', 'a.', 'Having the feelings and inclinations creditable to man;\n   having a disposition to treat other human beings or animals with\n   kindness; kind; benevolent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humane', 'a.', 'Humanizing; exalting; tending to refine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humanics', 'n.', 'The study of human nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humanify', 'v. t.', 'To make human; to invest with a human personality; to\n   incarnate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humanism', 'n.', 'Human nature or disposition; humanity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humanism', 'n.', 'The study of the humanities; polite learning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humanist', 'n.', 'One of the scholars who in the field of literature proper\n   represented the movement of the Renaissance, and early in the 16th\n   century adopted the name Humanist as their distinctive title.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humanist', 'n.', 'One who purposes the study of the humanities, or polite\n   literature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humanist', 'n.', 'One versed in knowledge of human nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humanistic', 'a.', 'Of or pertaining to humanity; as, humanistic devotion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humanistic', 'a.', 'Pertaining to polite kiterature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humanitarian', 'a.', 'Pertaining to humanitarians, or to humanitarianism;\n   as, a humanitarian view of Christs nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humanitarian', 'a.', 'Content with right affections and actions toward man;\n   ethical, as distinguished from religious; believing in the\n   perfectibility of mans nature without supernatural aid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humanitarian', 'a.', 'Benevolent; philanthropic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humanitarian', 'n.', 'One who denies the divinity of Christ, and believes\n   him to have been merely human.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humanitarian', 'n.', 'One who limits the sphere of duties to human\n   relations and affections, to the exclusion or disparagement of the\n   religious or spiritual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humanitarian', 'n.', 'One who is actively concerned in promoting the\n   welfare of his kind; a philanthropist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humanitarianism', 'n.', 'The distinctive tenet of the humanitarians in\n   denying the divinity of Christ; also, the whole system of doctrine\n   based upon this view of Christ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humanitarianism', 'n.', 'The doctrine that mans obligations are limited\n   to, and dependent alone upon, man and the human relations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humanitian', 'n.', 'A humanist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humanities', 'pl. ', 'of Humanity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humanity', 'n.', 'The quality of being human; the peculiar nature of man,\n   by which he is distinguished from other beings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humanity', 'n.', 'Mankind collectively; the human race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humanity', 'n.', 'The quality of being humane; the kind feelings,\n   dispositions, and sympathies of man; especially, a disposition to\n   relieve persons or animals in distress, and to treat all creatures with\n   kindness and tenderness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humanity', 'n.', 'Mental cultivation; liberal education; instruction in\n   classical and polite literature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humanity', 'n.', 'The branches of polite or elegant learning; as language,\n   rhetoric, poetry, and the ancient classics; belles-letters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humanization', 'n.', 'The act of humanizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humanized', 'imp. & p. p.', 'of Humanize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humanizing', 'p. pr. & vb. n.', 'of Humanize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humanize', 'v. t.', 'To render human or humane; to soften; to make gentle\n   by overcoming cruel dispositions and rude habits; to refine or\n   civilize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humanize', 'v. t.', 'To give a human character or expression to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humanize', 'v. t.', 'To convert into something human or belonging to man;\n   as, to humanize vaccine lymph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humanize', 'v. i.', 'To become or be made more humane; to become civilized;\n   to be ameliorated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humanizer', 'n.', 'One who renders humane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humankind', 'n.', 'Mankind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humanly', 'adv.', 'In a human manner; after the manner of men; according to\n   the knowledge or wisdom of men; as, the present prospects, humanly\n   speaking, promise a happy issue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humanly', 'adv.', 'Kindly; humanely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humanness', 'n.', 'The quality or state of being human.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humate', 'n.', 'A salt of humic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humation', 'n.', 'Interment; inhumation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humbird', 'n.', 'Humming bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humble', 'superl.', 'Near the ground; not high or lofty; not pretentious or\n   magnificent; unpretending; unassuming; as, a humble cottage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humble', 'superl.', 'Thinking lowly of ones self; claiming little for\n   ones self; not proud, arrogant, or assuming; thinking ones self\n   ill-deserving or unworthy, when judged by the demands of God; lowly;\n   waek; modest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humble', 'a.', 'Hornless. See Hummel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humbled', 'imp. & p. p.', 'of Humble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humbling', 'p. pr. & vb. n.', 'of Humble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humble', 'v. t.', 'To bring low; to reduce the power, independence, or\n   exaltation of; to lower; to abase; to humilate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humble', 'v. t.', 'To make humble or lowly in mind; to abase the pride or\n   arrogance of; to reduce the self-sufficiently of; to make meek and\n   submissive; -- often used rexlexively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humblebee', 'n.', 'The bumblebee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humblehead', 'n.', 'Humble condition or estate; humility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humbleness', 'n.', 'The quality of being humble; humility; meekness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humbler', 'n.', 'One who, or that which, humbles some one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humbles', 'n. pl.', 'Entrails of a deer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humblesse', 'n.', 'Humbleness; abasement; low obeisance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humbly', 'adv.', 'With humility; lowly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humbug', 'n.', 'An imposition under fair pretenses; something contrived in\n   order to deceive and mislead; a trick by cajolery; a hoax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humbug', 'n.', 'A spirit of deception; cajolery; trickishness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humbug', 'n.', 'One who deceives or misleads; a deceitful or trickish\n   fellow; an impostor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humbugged', 'imp. & p. p.', 'of Humbug');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humbugging', 'p. pr. & vb. n.', 'of Humbug');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humbug', 'v. t.', 'To deceive; to impose; to cajole; to hoax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humbugger', 'n.', 'One who humbugs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humbuggery', 'n.', 'The practice of imposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humdrum', 'a.', 'Monotonous; dull; commonplace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humdrum', 'n.', 'A dull fellow; a bore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humdrum', 'n.', 'Monotonous and tedious routine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humdrum', 'n.', 'A low cart with three wheels, drawn by one horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humect', 'v. t.', 'Alt. of Humectate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humectate', 'v. t.', 'To moisten; to wet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humectant', 'a.', 'Diluent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humectant', 'n.', 'A diluent drink or medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humectation', 'n.', 'A moistening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humective', 'a.', 'Tending to moisten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humeral', 'a.', 'Of or pertaining to the humerus, or upper part of the arm;\n   brachial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humeri', 'pl. ', 'of Humerus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humerus', 'n.', 'The bone of the brachium, or upper part of the arm or fore\n   limb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humerus', 'n.', 'The part of the limb containing the humerus; the brachium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humic', 'a.', 'Pertaining to, or derived from, vegetable mold; as, humic\n   acid. See Humin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humicubation', 'n.', 'The act or practice of lying on the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humid', 'a.', 'Containing sensible moisture; damp; moist; as, a humidair or\n   atmosphere; somewhat wet or watery; as, humid earth; consisting of\n   water or vapor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humidity', 'n.', 'Moisture; dampness; a moderate degree of wetness, which\n   is perceptible to the eye or touch; -- used especially of the\n   atmosphere, or of anything which has absorbed moisture from the\n   atmosphere, as clothing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humidness', 'n.', 'Humidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humifuse', 'a.', 'Spread over the surface of the ground; procumbent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humiliant', 'a.', 'Humiliating; humbling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humiliated', 'imp. & p. p.', 'of Humiliate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humiliating', 'p. pr. & vb. n.', 'of Humiliate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humiliate', 'v. t.', 'To reduce to a lower position in ones own eyes, or\n   in the eyes of others; to humble; to mortify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humiliation', 'n.', 'The act of humiliating or humbling; abasement of\n   pride; mortification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humiliation', 'n.', 'The state of being humiliated, humbled, or reduced to\n   lowliness or submission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humilities', 'pl. ', 'of Humility');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humility', 'n.', 'The state or quality of being humble; freedom from pride\n   and arrogance; lowliness of mind; a modest estimate of ones own worth;\n   a sense of ones own unworthiness through imperfection and sinfulness;\n   self-abasement; humbleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humility', 'n.', 'An act of submission or courtesy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humin', 'n.', 'A bitter, brownish yellow, amorphous substance, extracted\n   from vegetable mold, and also produced by the action of acids on\n   certain sugars and carbohydrates; -- called also humic acid, ulmin,\n   gein, ulmic or geic acid, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humiri', 'n.', 'A fragrant balsam obtained from Brazilian trees of the\n   genus Humirium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humite', 'n.', 'A mineral of a transparent vitreous brown color, found in\n   the ejected masses of Vesuvius. It is a silicate of iron and magnesia,\n   containing fluorine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hummel', 'v. t.', 'To separate from the awns; -- said of barley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hummel', 'a.', 'Having no awns or no horns; as, hummelcorn; a hummel cow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hummeler', 'n.', 'One who, or a machine which, hummels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hummer', 'n.', 'One who, or that which, hums; one who applauds by humming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hummer', 'n.', 'A humming bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humming', 'a.', 'Emitting a murmuring sound; droning; murmuring; buzzing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humming', 'n.', 'A sound like that made by bees; a low, murmuring sound; a\n   hum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hummock', 'n.', 'A rounded knoll or hillock; a rise of ground of no great\n   extent, above a level surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hummock', 'n.', 'A ridge or pile of ice on an ice field.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hummock', 'n.', 'Timbered land. See Hammock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hummocking', 'n.', 'The process of forming hummocks in the collision of\n   Arctic ice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hummocky', 'a.', 'Abounding in hummocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hummum', 'n.', 'A sweating bath or place for sweating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humor', 'n.', 'Moisture, especially, the moisture or fluid of animal\n   bodies, as the chyle, lymph, etc.; as, the humors of the eye, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humor', 'n.', 'A vitiated or morbid animal fluid, such as often causes an\n   eruption on the skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humor', 'n.', 'State of mind, whether habitual or temporary (as formerly\n   supposed to depend on the character or combination of the fluids of the\n   body); disposition; temper; mood; as, good humor; ill humor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humor', 'n.', 'Changing and uncertain states of mind; caprices; freaks;\n   vagaries; whims.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humor', 'n.', 'That quality of the imagination which gives to ideas an\n   incongruous or fantastic turn, and tends to excite laughter or mirth by\n   ludicrous images or representations; a playful fancy; facetiousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humored', 'imp. & p. p.', 'of Humor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humoring', 'p. pr. & vb. n.', 'of Humor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humor', 'v. t.', 'To comply with the humor of; to adjust matters so as suit\n   the peculiarities, caprices, or exigencies of; to adapt ones self to;\n   to indulge by skillful adaptation; as, to humor the mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humor', 'v. t.', 'To help on by indulgence or compliant treatment; to\n   soothe; to gratify; to please.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humoral', 'a.', 'Pertaining to, or proceeding from, the humors; as, a\n   humoral fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humoralism', 'n.', 'The state or quality of being humoral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humoralism', 'n.', 'The doctrine that diseases proceed from the humors;\n   humorism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humoralist', 'n.', 'One who favors the humoral pathology or believes in\n   humoralism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humorism', 'n.', 'The theory founded on the influence which the humors were\n   supposed to have in the production of disease; Galenism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humorism', 'n.', 'The manner or disposition of a humorist; humorousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humorist', 'n.', 'One who attributes diseases of the state of the humors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humorist', 'n.', 'One who has some peculiarity or eccentricity of\n   character, which he indulges in odd or whimsical ways.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humorist', 'n.', 'One who displays humor in speaking or writing; one who\n   has a facetious fancy or genius; a wag; a droll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humoristic', 'a.', 'Of, pertaining to, or resembling, a humorist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humorize', 'v. t.', 'To humor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humorless', 'a.', 'Destitute of humor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humorous', 'a.', 'Moist; humid; watery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humorous', 'a.', 'Subject to be governed by humor or caprice; irregular;\n   capricious; whimsical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humorous', 'a.', 'Full of humor; jocular; exciting laughter; playful; as, a\n   humorous story or author; a humorous aspect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humorously', 'adv.', 'Capriciously; whimsically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humorously', 'adv.', 'Facetiously; wittily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humorousness', 'n.', 'Moodiness; capriciousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humorousness', 'n.', 'Facetiousness; jocularity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humorsome', 'a.', 'Moody; whimsical; capricious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humorsome', 'a.', 'Jocose; witty; humorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humorsomely', 'adv.', 'Pleasantly; humorously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humorsomeness', 'n.', 'Quality of being humorsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hump', 'n.', 'A protuberance; especially, the protuberance formed by a\n   crooked back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hump', 'n.', 'A fleshy protuberance on the back of an animal, as a camel or\n   whale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humpback', 'n.', 'A crooked back; a humped back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humpback', 'n.', 'A humpbacked person; a hunchback.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humpback', 'n.', 'Any whale of the genus Megaptera, characterized by a hump\n   or bunch on the back. Several species are known. The most common ones\n   in the North Atlantic are Megaptera longimana of Europe, and M. osphyia\n   of America; that of the California coasts is M. versabilis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humpback', 'n.', 'A small salmon (Oncorhynchus gorbuscha), of the northwest\n   coast of America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humpbacked', 'a.', 'Having a humped back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humped', 'a.', 'Having a hump, as the back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humph', 'interj.', 'An exclamation denoting surprise, or contempt, doubt,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humpless', 'a.', 'Without a hump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hump-shouldered', 'a.', 'Having high, hunched shoulders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humpy', 'a.', 'Full of humps or bunches; covered with protuberances;\n   humped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humstrum', 'n.', 'An instrument out of tune or rudely constructed; music\n   badly played.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humulin', 'n.', 'An extract of hops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Humus', 'n.', 'That portion of the soil formed by the decomposition of\n   animal or vegetable matter. It is a valuable constituent of soils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hun', 'n.', 'One of a warlike nomadic people of Northern Asia who, in the\n   5th century, under Atilla, invaded and conquered a great part of\n   Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunch', 'n.', 'A hump; a protuberance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunch', 'n.', 'A lump; a thick piece; as, a hunch of bread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunch', 'n.', 'A push or thrust, as with the elbow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunched', 'imp. & p. p.', 'of Hunch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunching', 'p. pr. & vb. n.', 'of Hunch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunch', 'v. t.', 'To push or jostle with the elbow; to push or thrust\n   suddenly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunch', 'v. t.', 'To thrust out a hump or protuberance; to crook, as the\n   back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunchback', 'n.', 'A back with a hunch or hump; also, a hunchbacked person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunchbacked', 'a.', 'Having a humped back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hundred', 'n.', 'The product of ten mulitplied by ten, or the number of ten\n   times ten; a collection or sum, consisting of ten times ten units or\n   objects; five score. Also, a symbol representing one hundred units, as\n   100 or C.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hundred', 'n.', 'A division of a country in England, supposed to have\n   originally contained a hundred families, or freemen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hundred', 'a.', 'Ten times ten; five score; as, a hundred dollars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hundreder', 'n.', 'An inhabitant or freeholder of a hundred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hundreder', 'n.', 'A person competent to serve on a jury, in an action for\n   land in the hundred to which he belongs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hundreder', 'n.', 'One who has the jurisdiction of a hundred; and\n   sometimes, a bailiff of a hundred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hundredfold', 'n.', 'A hundred times as much or as many.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hundredth', 'a.', 'Coming last of a hundred successive individuals or\n   units.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hundredth', 'a.', 'Forming one of a hundred equal parts into which anything\n   is divided; the tenth of a tenth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hundredth', 'n.', 'One of a hundred equal parts into which one whole is, or\n   may be, divided; the quotient of a unit divided by a hundred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hundredweight', 'n.', 'A denomination of weight, containing 100, 112, or\n   120 pounds avoirdupois, according to differing laws or customs. By the\n   legal standard of England it is 112 pounds. In most of the United\n   States, both in practice and by law, it is 100 pounds avoirdupois, the\n   corresponding ton of 2,000 pounds, sometimes called the short ton,\n   being the legal ton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hung', '', 'imp. & p. p. of Hang.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hungarian', 'a.', 'Of or pertaining to Hungary or to the people of Hungary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hungarian', 'n.', 'A native or one of the people of Hungary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hungary', 'n.', 'A country in Central Europe, now a part of the\n   Austro-Hungarian Empire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunger', 'n.', 'An uneasy sensation occasioned normally by the want of\n   food; a craving or desire for food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunger', 'n.', 'Any strong eager desire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hungered', 'imp. & p. p.', 'of Hunger');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hungering', 'p. pr. & vb. n.', 'of Hunger');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunger', 'n.', 'To feel the craving or uneasiness occasioned by want of\n   food; to be oppressed by hunger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunger', 'n.', 'To have an eager desire; to long.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunger', 'v. t.', 'To make hungry; to famish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunger-bit', 'a.', 'Alt. of Hunger-bitten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunger-bitten', 'a.', 'Pinched or weakened by hunger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hungered', 'a.', 'Hungry; pinched for food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hungerer', 'n.', 'One who hungers; one who longs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hungerly', 'a.', 'Wanting food; starved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hungerly', 'adv.', 'With keen appetite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunger-starve', 'v. t.', 'To starve with hunger; to famish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hungred', 'a.', 'Hungered; hungry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hungrily', 'adv.', 'In a hungry manner; voraciously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hungry', 'superl.', 'Feeling hunger; having a keen appetite; feeling\n   uneasiness or distress from want of food; hence, having an eager\n   desire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hungry', 'superl.', 'Showing hunger or a craving desire; voracious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hungry', 'superl.', 'Not rich or fertile; poor; barren; starved; as, a\n   hungry soil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunk', 'n.', 'A large lump or piece; a hunch; as, a hunk of bread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunker', 'n.', 'Originally, a nickname for a member of the conservative\n   section of the Democratic party in New York; hence, one opposed to\n   progress in general; a fogy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunkerism', 'n.', 'Excessive conservatism; hostility to progress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunks', 'n.', 'A covetous, sordid man; a miser; a niggard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunted', 'imp. & p. p.', 'of Hunt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunting', 'p. pr. & vb. n.', 'of Hunt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunt', 'v. t.', 'To search for or follow after, as game or wild animals; to\n   chase; to pursue for the purpose of catching or killing; to follow with\n   dogs or guns for sport or exercise; as, to hunt a deer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunt', 'v. t.', 'To search diligently after; to seek; to pursue; to follow;\n   -- often with out or up; as, to hunt up the facts; to hunt out\n   evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunt', 'v. t.', 'To drive; to chase; -- with down, from, away, etc.; as, to\n   hunt down a criminal; he was hunted from the parish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunt', 'v. t.', 'To use or manage in the chase, as hounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunt', 'v. t.', 'To use or traverse in pursuit of game; as, he hunts the\n   woods, or the country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunt', 'v. i.', 'To follow the chase; to go out in pursuit of game; to\n   course with hounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunt', 'v. i.', 'To seek; to pursue; to search; -- with for or after.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunt', 'n.', 'The act or practice of chasing wild animals; chase; pursuit;\n   search.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunt', 'n.', 'The game secured in the hunt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunt', 'n.', 'A pack of hounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunt', 'n.', 'An association of huntsmen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunt', 'n.', 'A district of country hunted over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunt-counter', 'n.', 'A worthless dog that runs back on the scent; a\n   blunderer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunte', 'n.', 'A hunter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunter', 'n.', 'One who hunts wild animals either for sport or for food; a\n   huntsman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunter', 'n.', 'A dog that scents game, or is trained to the chase; a\n   hunting dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunter', 'n.', 'A horse used in the chase; especially, a thoroughbred, bred\n   and trained for hunting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunter', 'n.', 'One who hunts or seeks after anything, as if for game; as,\n   a fortune hunter a place hunter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunter', 'n.', 'A kind of spider. See Hunting spider, under Hunting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunter', 'n.', 'A hunting watch, or one of which the crystal is protected\n   by a metallic cover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunterian', 'a.', 'Discovered or described by John Hunter, an English\n   surgeon; as, the Hunterian chancre. See Chancre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunting', 'n.', 'The pursuit of game or of wild animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huntress', 'n.', 'A woman who hunts or follows the chase; as, the huntress\n   Diana.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huntsmen', 'pl. ', 'of Huntsman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huntsman', 'n.', 'One who hunts, or who practices hunting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huntsman', 'n.', 'The person whose office it is to manage the chase or to\n   look after the hounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huntsmanship', 'n.', 'The art or practice of hunting, or the qualification\n   of a hunter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hunts-up', 'n.', 'A tune played on the horn very early in the morning to\n   call out the hunters; hence, any arousing sound or call.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurden', 'n.', 'A coarse kind of linen; -- called also harden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurdle', 'n.', 'A movable frame of wattled twigs, osiers, or withes and\n   stakes, or sometimes of iron, used for inclosing land, for folding\n   sheep and cattle, for gates, etc.; also, in fortification, used as\n   revetments, and for other purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurdle', 'n.', 'In England, a sled or crate on which criminals were\n   formerly drawn to the place of execution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurdle', 'n.', 'An artificial barrier, variously constructed, over which\n   men or horses leap in a race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurdleed', 'imp. & p. p.', 'of Hurdle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurdleing', 'p. pr. & vb. n.', 'of Hurdle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurdle', 'v. t.', 'To hedge, cover, make, or inclose with hurdles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurdlework', 'n.', 'Work after manner of a hurdle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurds', 'n.', 'The coarse part of flax or hemp; hards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurdy-gurdy', 'n.', 'A stringled instrument, lutelike in shape, in which\n   the sound is produced by the friction of a wheel turned by a crank at\n   the end, instead of by a bow, two of the strings being tuned as drones,\n   while two or more, tuned in unison, are modulated by keys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurdy-gurdy', 'n.', 'In California, a water wheel with radial buckets,\n   driven by the impact of a jet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurkaru', 'n.', 'In India, a running footman; a messenger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurled', 'imp. & p. p.', 'of Hurl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurling', 'p. pr. & vb. n.', 'of Hurl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurl', 'v. t.', 'To send whirling or whizzing through the air; to throw\n   with violence; to drive with great force; as, to hurl a stone or lance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurl', 'v. t.', 'To emit or utter with vehemence or impetuosity; as, to\n   hurl charges or invective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurl', 'v. t.', 'To twist or turn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurl', 'v. i.', 'To hurl ones self; to go quickly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurl', 'v. i.', 'To perform the act of hurling something; to throw\n   something (at another).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurl', 'v. i.', 'To play the game of hurling. See Hurling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurl', 'n.', 'The act of hurling or throwing with violence; a cast; a\n   fling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurl', 'n.', 'Tumult; riot; hurly-burly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurl', 'n.', 'A table on which fiber is stirred and mixed by beating with a\n   bowspring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurlbat', 'n.', 'See Whirlbat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurlbone', 'n.', 'See Whirlbone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurlbone', 'n.', 'A bone near the middle of the buttock of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurler', 'n.', 'One who hurls, or plays at hurling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurling', 'n.', 'The act of throwing with force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurling', 'n.', 'A kind of game at ball, formerly played.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurlwind', 'n.', 'A whirlwind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurly', 'n.', 'Noise; confusion; uproar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurly-burly', 'n.', 'Tumult; bustle; confusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huronian', 'a.', 'Of or pertaining to certain non-fossiliferous rocks on\n   the borders of Lake Huron, which are supposed to correspond in time to\n   the latter part of the Archaean age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huron-Iroquous', 'n.', 'A linguistic group of warlike North American\n   Indians, belonging to the same stock as the Algonquins, and including\n   several tribes, among which were the Five Nations. They formerly\n   occupied the region about Lakes Erie and Ontario, and the larger part\n   of New York.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurons', 'n. pl.', '; sing. Huron. (Ethnol.) A powerful and warlike tribe\n   of North American Indians of the Algonquin stock. They formerly\n   occupied the country between Lakes Huron, Erie, and Ontario, but were\n   nearly exterminated by the Five Nations about 1650.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurr', 'v. i.', 'To make a rolling or burring sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurrah', 'interj.', 'Alt. of Hurra');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurra', 'interj.', 'A word used as a shout of joy, triumph, applause,\n   encouragement, or welcome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurrah', 'n.', 'A cheer; a shout of joy, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurrah', 'v. i.', 'To utter hurrahs; to huzza.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurrah', 'v. t.', 'To salute, or applaud, with hurrahs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurricane', 'n.', 'A violent storm, characterized by extreme fury and\n   sudden changes of the wind, and generally accompanied by rain, thunder,\n   and lightning; -- especially prevalent in the East and West Indies.\n   Also used figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurricanoes', 'pl. ', 'of Hurricano');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurricano', 'n.', 'A waterspout; a hurricane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurried', 'a.', 'Urged on; hastened; going or working at speed; as, a\n   hurried writer; a hurried life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurried', 'a.', 'Done in a hurry; hence, imperfect; careless; as, a hurried\n   job.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurrier', 'n.', 'One who hurries or urges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurries', 'n.', 'A staith or framework from which coal is discharged from\n   cars into vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurried', 'imp. & p. p.', 'of Hurry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurrying', 'p. pr. & vb. n.', 'of Hurry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurry', 'v. t.', 'To hasten; to impel to greater speed; to urge on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurry', 'v. t.', 'To impel to precipitate or thoughtless action; to urge to\n   confused or irregular activity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurry', 'v. t.', 'To cause to be done quickly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurry', 'v. i.', 'To move or act with haste; to proceed with celerity or\n   precipitation; as, let us hurry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurry', 'n.', 'The act of hurrying in motion or business; pressure;\n   urgency; bustle; confusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurryingly', 'adv.', 'In a hurrying manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurry-skurry', 'adv.', 'Confusedly; in a bustle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurst', 'n.', 'A wood or grove; -- a word used in the composition of many\n   names, as in Hazlehurst.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurt', 'n.', 'A band on a trip-hammer helve, bearing the trunnions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurt', 'n.', 'A husk. See Husk, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurt', 'imp. & p. p.', 'of Hurt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurting', 'p. pr. & vb. n.', 'of Hurt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurt', 'v. t.', 'To cause physical pain to; to do bodily harm to; to wound\n   or bruise painfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurt', 'v. t.', 'To impar the value, usefulness, beauty, or pleasure of; to\n   damage; to injure; to harm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurt', 'v. t.', 'To wound the feelings of; to cause mental pain to; to\n   offend in honor or self-respect; to annoy; to grieve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurter', 'n.', 'A bodily injury causing pain; a wound, bruise, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurter', 'n.', 'An injury causing pain of mind or conscience; a slight; a\n   stain; as of sin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurter', 'n.', 'Injury; damage; detriment; harm; mischief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurter', 'n.', 'One who hurts or does harm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurter', 'v. t.', 'A butting piece; a strengthening piece, esp.: (Mil.) A\n   piece of wood at the lower end of a platform, designed to prevent the\n   wheels of gun carriages from injuring the parapet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurtful', 'a.', 'Tending to impair or damage; injurious; mischievous;\n   occasioning loss or injury; as, hurtful words or conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurtled', 'imp. & p. p.', 'of Hurtle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurtling', 'p. pr. & vb. n.', 'of Hurtle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurtle', 'v. t.', 'To meet with violence or shock; to clash; to jostle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurtle', 'v. t.', 'To move rapidly; to wheel or rush suddenly or with\n   violence; to whirl round rapidly; to skirmish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurtle', 'v. t.', 'To make a threatening sound, like the clash of arms; to\n   make a sound as of confused clashing or confusion; to resound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurtle', 'v. t.', 'To move with violence or impetuosity; to whirl; to\n   brandish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurtle', 'v. t.', 'To push; to jostle; to hurl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurtleberry', 'n.', 'See Whortleberry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hurtless', 'a.', 'Doing no injury; harmless; also, unhurt; without injury\n   or harm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Husband', 'n.', 'The male head of a household; one who orders the economy\n   of a family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Husband', 'n.', 'A cultivator; a tiller; a husbandman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Husband', 'n.', 'One who manages or directs with prudence and economy; a\n   frugal person; an economist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Husband', 'n.', 'A married man; a man who has a wife; -- the correlative to\n   wife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Husband', 'n.', 'The male of a pair of animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Husbanded', 'imp. & p. p.', 'of Husband');
  INSERT INTO words (word, word_type, description) VALUES  (    'Husbanding', 'p. pr. & vb. n.', 'of Husband');
  INSERT INTO words (word, word_type, description) VALUES  (    'Husband', 'v. t.', 'To direct and manage with frugality; to use or employ\n   to good purpose and the best advantage; to spend, apply, or use, with\n   economy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Husband', 'v. t.', 'To cultivate, as land; to till.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Husband', 'v. t.', 'To furnish with a husband.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Husbandable', 'a.', 'Capable of being husbanded, or managed with economy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Husbandage', 'n.', 'The commission or compensation allowed to a ships\n   husband.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Husbandless', 'a.', 'Destitute of a husband.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Husbandly', 'a.', 'Frugal; thrifty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Husbandmen', 'pl. ', 'of Husbandman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Husbandman', 'n.', 'The master of a family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Husbandman', 'n.', 'A farmer; a cultivator or tiller of the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Husbandry', 'n.', 'Care of domestic affairs; economy; domestic management;\n   thrift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Husbandry', 'n.', 'The business of a husbandman, comprehending the various\n   branches of agriculture; farming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hushed', 'imp. & p. p.', 'of Hush');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hushing', 'p. pr. & vb. n.', 'of Hush');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hush', 'v. t.', 'To still; to silence; to calm; to make quiet; to repress\n   the noise or clamor of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hush', 'v. t.', 'To appease; to allay; to calm; to soothe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hush', 'v. i.', 'To become or to keep still or quiet; to become silent; --\n   esp. used in the imperative, as an exclamation; be still; be silent or\n   quiet; make no noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hush', 'n.', 'Stillness; silence; quiet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hush', 'a.', 'Silent; quiet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Husher', 'n.', 'An usher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hushing', 'n.', 'The process of washing ore, or of uncovering mineral\n   veins, by a heavy discharge of water from a reservoir; flushing; --\n   also called booming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Husk', 'n.', 'The external covering or envelope of certain fruits or seeds;\n   glume; hull; rind; in the United States, especially applied to the\n   covering of the ears of maize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Husk', 'n.', 'The supporting frame of a run of millstones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Husked', 'imp. & p. p.', 'of Husk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Husking', 'p. pr. & vb. n.', 'of Husk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Husk', 'v. t.', 'To strip off the external covering or envelope of; as, to\n   husk Indian corn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Husked', 'a.', 'Covered with a husk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Husked', 'a.', 'Stripped of husks; deprived of husks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huskily', 'adv.', 'In a husky manner; dryly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huskiness', 'n.', 'The state of being husky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huskiness', 'n.', 'Roughness of sound; harshness; hoarseness; as, huskiness\n   of voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Husking', 'n.', 'The act or process of stripping off husks, as from Indian\n   corn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Husking', 'n.', 'A meeting of neighbors or friends to assist in husking\n   maize; -- called also');
  INSERT INTO words (word, word_type, description) VALUES  (    'Husky', 'n.', 'Abounding with husks; consisting of husks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Husky', 'a.', 'Rough in tone; harsh; hoarse; raucous; as, a husky voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huso', 'n.', 'A large European sturgeon (Acipenser huso), inhabiting the\n   region of the Black and Caspian Seas. It sometimes attains a length of\n   more than twelve feet, and a weight of two thousand pounds. Called also\n   hausen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huso', 'n.', 'The huchen, a large salmon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hussar', 'n.', 'Originally, one of the national cavalry of Hungary and\n   Croatia; now, one of the light cavalry of European armies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hussite', 'n.', 'A follower of John Huss, the Bohemian reformer, who was\n   adjudged a heretic and burnt alive in 1415.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hussy', 'n.', 'A housewife or housekeeper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hussy', 'n.', 'A worthless woman or girl; a forward wench; a jade; -- used\n   as a term of contempt or reproach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hussy', 'n.', 'A pert girl; a frolicsome or sportive young woman; -- used\n   jocosely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hussy', 'n.', 'A case or bag. See Housewife, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hustings', 'n. pl.', 'A court formerly held in several cities of England;\n   specif., a court held in London, before the lord mayor, recorder, and\n   sheriffs, to determine certain classes of suits for the recovery of\n   lands within the city. In the progress of law reform this court has\n   become unimportant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hustings', 'n. pl.', 'Any one of the temporary courts held for the election\n   of members of the British Parliament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hustings', 'n. pl.', 'The platform on which candidates for Parliament\n   formerly stood in addressing the electors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hustled', 'imp. & p. p.', 'of Hustle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hustling', 'p. pr. & vb. n.', 'of Hustle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hustle', 'v. t.', 'To shake together in confusion; to push, jostle, or\n   crowd rudely; to handle roughly; as, to hustle a person out of a room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hustle', 'v. i.', 'To push or crows; to force ones way; to move hustily\n   and with confusion; a hurry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huswife', 'n.', 'A female housekeeper; a woman who manages domestic\n   affairs; a thirfty woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huswife', 'n.', 'A worthless woman; a hussy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huswife', 'n.', 'A case for sewing materials. See Housewife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huswife', 'v. t.', 'To manage with frugality; -- said of a woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huswifely', 'a.', 'Like a huswife; capable; economical; prudent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huswifely', 'adv.', 'In a huswifely manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huswifery', 'n.', 'The business of a housewife; female domestic economy and\n   skill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hut', 'n.', 'A small house, hivel, or cabin; a mean lodge or dwelling; a\n   slightly built or temporary structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hutted', 'imp. & p. p.', 'of Hutch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hutting', 'p. pr. & vb. n.', 'of Hutch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hutch', 'v. t. & i.', 'To place in huts; to live in huts; as, to hut troops\n   in winter quarters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hutch', 'n.', 'A chest, box, coffer, bin, coop, or the like, in which\n   things may be stored, or animals kept; as, a grain hutch; a rabbit\n   hutch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hutch', 'n.', 'A measure of two Winchester bushels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hutch', 'n.', 'The case of a flour bolt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hutch', 'n.', 'A car on low wheels, in which coal is drawn in the mine and\n   hoisted out of the pit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hutch', 'n.', 'A jig for washing ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hutched', 'imp. & p. p.', 'of Hutch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hutching', 'p. pr. & vb. n.', 'of Hutch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hutch', 'v. t.', 'To hoard or lay up, in a chest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hutch', 'v. t.', 'To wash (ore) in a box or jig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hutchunsonian', 'n.', 'A follower of John Hutchinson of Yorkshire, England,\n   who believed that the Hebrew Scriptures contained a complete system of\n   natural science and of theology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huttonian', 'a.', 'Relating to what is now called the Plutonic theory of\n   the earth, first advanced by Dr. James Hutton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huxter', 'n. & v. i.', 'See Huckster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huyghenian', 'a.', 'Pertaining to, or invented by, Christian Huyghens, a\n   Dutch astronomer of the seventeenth century; as, the Huyghenian\n   telescope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huzz', 'v. i.', 'To buzz; to murmur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huzza', 'interj.', 'A word used as a shout of joy, exultation, approbation,\n   or encouragement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huzza', 'n.', 'A shout of huzza; a cheer; a hurrah.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huzzaed', 'imp. & p. p.', 'of Huzza');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huzzaing', 'p. pr. & vb. n.', 'of Huzza');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huzza', 'v. i.', 'To shout huzza; to cheer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Huzza', 'v. t.', 'To receive or attend with huzzas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hy', 'a.', 'High.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyacine', 'n.', 'A hyacinth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyacinth', 'n.', 'A bulbous plant of the genus Hyacinthus, bearing\n   beautiful spikes of fragrant flowers. H. orientalis is a common\n   variety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyacinth', 'n.', 'A plant of the genus Camassia (C. Farseri), called also\n   Eastern camass; wild hyacinth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyacinth', 'n.', 'The name also given to Scilla Peruviana, a Mediterranean\n   plant, one variety of which produces white, and another blue, flowers;\n   -- called also, from a mistake as to its origin, Hyacinth of Peru.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyacinth', 'n.', 'A red variety of zircon, sometimes used as a gem. See\n   Zircon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyacinthian', 'a.', 'Hyacinthine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyacinthine', 'a.', 'Belonging to the hyacinth; resemblingthe hyacinth; in\n   color like the hyacinth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyades', 'n.pl.', 'Alt. of Hyads');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyads', 'n.pl.', 'A cluster of five stars in the face of the constellation\n   Taurus, supposed by the ancients to indicate the coming of rainy\n   weather when they rose with the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyaena', 'n.', 'Same as Hyena.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyalea', 'n.', 'A pteroid of the genus Cavolina. See Pteropoda, and\n   Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyalescence', 'n.', 'The process of becoming, or the state of being,\n   transparent like glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyaline', 'a.', 'Glassy; resembling glass; consisting of glass;\n   transparent, like crystal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyaline', 'n.', 'A poetic term for the sea or the atmosphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyaline', 'n.', 'The pellucid substance, present in cells in process of\n   development, from which, according to some embryologists, the cell\n   nucleous originates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyaline', 'n.', 'The main constituent of the walls of hydatid cysts; a\n   nitrogenous body, which, by decomposition, yields a dextrogyrate sugar,\n   susceptible of alcoholic fermentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyalite', 'n.', 'A pellucid variety of opal in globules looking like\n   colorless gum or resin; -- called also Mullers glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyalograph', 'n.', 'An instrument for tracing designs on glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyalography', 'n.', 'Art of writing or engraving on glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyaloid', 'a.', 'Resembling glass; vitriform; transparent; hyaline; as, the\n   hyaloid membrane, a very delicate membrane inclosing the vitreous humor\n   of the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyalonema', 'n.', 'A genus of hexactinelline sponges, having a long stem\n   composed of very long, slender, transparent, siliceous fibres twisted\n   together like the strands of a color. The stem of the Japanese species\n   (H. Sieboldii), called glass-rope, has long been in use as an ornament.\n   See Glass-rope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyalophane', 'n.', 'A species of the feldspar group containing barium. See\n   Feldspar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyalospongia', 'n. pl.', 'An order of vitreous sponges, having glassy\n   six-rayed, siliceous spicules; -- called also Hexactinellinae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyalotype', 'n.', 'A photographic picture copied from the negative on\n   glass; a photographic transparency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hybernacle', '', 'Alt. of Hybernation');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hybernate', '', 'Alt. of Hybernation');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hybernation', '', 'See Hibernacle, Hibernate, Hibernation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyblaean', 'a.', 'Pertaining to Hybla, an ancient town of Sicily, famous\n   for its bees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hybodont', 'a.', 'Of, pertaining to, or resembling, an extinct genus of\n   sharks (Hybodus), especially in the form of the teeth, which consist of\n   a principal median cone with smaller lateral ones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hybodus', 'n.', 'An extinct genus of sharks having conical, compressed\n   teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hybrid', 'n.', 'The offspring of the union of two distinct species; an\n   animal or plant produced from the mixture of two species. See Mongrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hybrid', 'a.', 'Produced from the mixture of two species; as, plants of\n   hybrid nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hybridism', 'n.', 'The state or quality of being hybrid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hybridist', 'n.', 'One who hybridizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hybridity', 'n.', 'Hybridism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hybridizable', 'a.', 'Capable of forming a hybrid, or of being subjected to\n   a hybridizing process; capable of producing a hybrid by union with\n   another species or stock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hybridization', 'n.', 'The act of hybridizing, or the state of being\n   hybridized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hybridized', 'imp. & p. p.', 'of Hybridize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hybridizing', 'p. pr. & vb. n.', 'of Hybridize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hybridize', 'v. t.', 'To render hybrid; to produce by mixture of stocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hybridizer', 'n.', 'One who hybridizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hybridous', 'a.', 'Same as Hybrid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydage', 'n.', 'A land tax. See Hidage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydantoic', 'a.', 'Pertaining to, or derived from, hydantoin. See\n   Glycoluric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydantoin', 'n.', 'A derivative of urea, C3H4N2O2, obtained from allantion,\n   as a white, crystalline substance, with a sweetish taste; -- called\n   also glycolyl urea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydatid', 'n.', 'A membranous sac or bladder filled with a pellucid fluid,\n   found in various parts of the bodies of animals, but unconnected with\n   the tissues. It is usually formed by parasitic worms, esp. by larval\n   tapeworms, as Echinococcus and Coenurus. See these words in the\n   Vocabulary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydatiform', 'a.', 'Resembling a hydatid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydatoid', 'a.', 'Resembling water; watery; aqueous; hyaloid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydr-', '', 'See under Hydro-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydras', 'pl. ', 'of Hydra');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrae', 'pl. ', 'of Hydra');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydra', 'n.', 'A serpent or monster in the lake or marsh of Lerna, in the\n   Peloponnesus, represented as having many heads, one of which, when cut\n   off, was immediately succeeded by two others, unless the wound was\n   cauterized. It was slain by Hercules. Hence, a terrible monster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydra', 'n.', 'Hence: A multifarious evil, or an evil having many sources;\n   not to be overcome by a single effort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydra', 'n.', 'Any small fresh-water hydroid of the genus Hydra, usually\n   found attached to sticks, stones, etc., by a basal sucker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydra', 'n.', 'A southern constellation of great length lying southerly\n   from Cancer, Leo, and Virgo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrachnid', 'n.', 'An aquatic mite of the genus Hydrachna. The\n   hydrachnids, while young, are parasitic on fresh-water mussels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydracid', 'n.', 'An acid containing hydrogen; -- sometimes applied to\n   distinguish acids like hydrochloric, hydrofluoric, and the like, which\n   contain no oxygen, from the oxygen acids or oxacids. See Acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydracrylic', 'a.', 'Pertaining to, or designating, an isomeric variety of\n   lastic acid that breaks down into acrylic acid and water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydractinian', 'n.', 'Any species or marine hydroids, of the genus\n   Hydractinia and allied genera. These hydroids form, by their\n   rootstalks, a firm, chitinous coating on shells and stones, and esp. on\n   spiral shells occupied by hermit crabs. See Illust. of Athecata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydraemia', 'n.', 'An abnormally watery state of the blood; anaemia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydragogue', 'a.', 'Causing a discharge of water; expelling serum effused\n   into any part of the body, as in dropsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydragogue', 'n.', 'A hydragogue medicine, usually a cathartic or diuretic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydramide', 'n.', 'One of a group of crystalline bodies produced by the\n   action of ammonia on certain aldehydes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydramine', 'n.', 'One of a series of artificial, organic bases, usually\n   produced as thick viscous liquids by the action of ammonia on ethylene\n   oxide. They have the properties both of alcohol and amines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrangea', 'n.', 'A genus of shrubby plants bearing opposite leaves and\n   large heads of showy flowers, white, or of various colors. H.\n   hortensis, the common garden species, is a native of China or Japan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrant', 'n.', 'A discharge pipe with a valve and spout at which water may\n   be drawn from the mains of waterworks; a water plug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydranth', 'n.', 'One of the nutritive zooids of a hydroid colony. Also\n   applied to the proboscis or manubrium of a hydroid medusa. See Illust.\n   of Hydroidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrargochloride', 'n.', 'A compound of the bichloride of mercury with\n   another chloride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrargyrate', 'a.', 'Of or pertaining to mercury; containing, or\n   impregnated with, mercury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrargyrism', 'n.', 'A diseased condition produced by poisoning with\n   hydrargyrum, or mercury; mercurialism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrargyrum', 'n.', 'Quicksilver; mercury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrarthrosis', 'n.', 'An effusion of watery liquid into the cavity of a\n   joint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrastine', 'n.', 'An alkaloid, found in the rootstock of the golden seal\n   (Hydrastis Canadensis), and extracted as a bitter, white, crystalline\n   substance. It is used as a tonic and febrifuge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydra-tainted', 'a.', 'Dipped in the gall of the fabulous hydra; poisonous;\n   deadly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrate', 'n.', 'A compound formed by the union of water with some other\n   substance, generally forming a neutral body, as certain crystallized\n   salts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrate', 'n.', 'A substance which does not contain water as such, but has\n   its constituents (hydrogen, oxygen, hydroxyl) so arranged that water\n   may be eliminated; hence, a derivative of, or compound with, hydroxyl;\n   hydroxide; as, ethyl hydrate, or common alcohol; calcium hydrate, or\n   slaked lime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrated', 'imp. & p. p.', 'of Hydrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrating', 'p. pr. & vb. n.', 'of Hydrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrate', 'v. t.', 'To form into a hydrate; to combine with water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrated', 'a.', 'Formed into a hydrate; combined with water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydration', 'n.', 'The act of becoming, or state of being, a hydrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydraulic', 'a.', 'Of or pertaining to hydraulics, or to fluids in motion;\n   conveying, or acting by, water; as, an hydraulic clock, crane, or dock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydraulical', 'a.', 'Hydraulic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydraulicon', 'n.', 'An ancient musical instrument played by the action of\n   water; a water organ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydraulics', 'n.', 'That branch of science, or of engineering, which treats\n   of fluids in motion, especially of water, its action in rivers and\n   canals, the works and machinery for conducting or raising it, its use\n   as a prime mover, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrazine', 'n.', 'Any one of a series of nitrogenous bases, resembling the\n   amines and produced by the reduction of certain nitroso and diazo\n   compounds; as, methyl hydrazine, phenyl hydrazine, etc. They are\n   derivatives of hydrazine proper, H2N.NH2, which is a doubled amido\n   group, recently (1887) isolated as a stable, colorless gas, with a\n   peculiar, irritating odor. As a base it forms distinct salts. Called\n   also diamide, amidogen, (or more properly diamidogen), etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrencephsloid', 'a.', 'Same as Hydrocephaloid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydria', 'n.', 'A water jar; esp., one with a large rounded body, a small\n   neck, and three handles. Some of the most beautiful Greek vases are of\n   this form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydriad', 'n.', 'A water nymph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydric', 'a.', 'Pertaining to, or containing, hydrogen; as, hydric oxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydride', 'n.', 'A compound of the binary type, in which hydrogen is united\n   with some other element.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydriform', 'a.', 'Having the form or structure of a hydra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrina', 'n. pl.', 'The group of hydroids to which the fresh-water hydras\n   belong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydriodate', 'n.', 'Same as Hydriodide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydriodic', 'a.', 'Pertaining to, or derived from, hydrogen and iodine; --\n   said of an acid produced by the combination of these elements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydriodide', 'n.', 'A compound of hydriodic acid with a base; --\n   distinguished from an iodide, in which only the iodine combines with\n   the base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydro-', '', 'Alt. of Hydr-');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydr-', '', 'A combining form from Gr. /, /, water (see Hydra).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydr-', '', 'A combining form of hydrogen, indicating hydrogen as an\n   ingredient, as hydrochloric; or a reduction product obtained by\n   hydrogen, as hydroquinone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrobarometer', 'n.', 'An instrument for determining the depth of the sea\n   water by its pressure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrobilirubin', 'n.', 'A body formed from bilirubin, identical with\n   urobilin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrobranchiata', 'n. pl.', 'An extensive artificial division of gastropod\n   mollusks, including those that breathe by gills, as contrasted with the\n   Pulmonifera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrobromate', 'n.', 'Same as Hydrobromide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrobromic', 'a.', 'Composed of hydrogen and bromine; as, hydrobromic\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrobromide', 'n.', 'A compound of hydrobromic acid with a base; --\n   distinguished from a bromide, in which only the bromine unites with the\n   base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrocarbon', 'n.', 'A compound containing only hydrogen and carbon, as\n   methane, benzene, etc.; also, by extension, any of their derivatives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrocarbonaceous', 'a.', 'Of the nature, or containing, hydrocarbons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrocarbonate', 'n.', 'A hydrocarbon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrocarbonate', 'n.', 'A hydrous carbonate, as malachite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrocarbostyril', 'n.', 'A white, crystalline, nitrogenous hydrocarbon,\n   C9H9NO, obtained from certain derivatives of cinnamic acid and closely\n   related to quinoline and carbostyril.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrocarburet', 'n.', 'Carbureted hydrogen; also, a hydrocarbon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrocauli', 'pl. ', 'of Hydrocaulus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrocaulus', 'n.', 'The hollow stem of a hydroid, either simple or\n   branched. See Illust. of Gymnoblastea and Hydroidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrocele', 'n.', 'A collection of serous fluid in the areolar texture of\n   the scrotum or in the coverings, especially in the serous sac,\n   investing the testicle or the spermatic cord; dropsy of the testicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrocephalic', 'a.', 'Relating to, or connected with, hydrocephalus, or\n   dropsy of the brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrocephaloid', 'a.', 'Resembling hydrocephalus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrocephalous', 'a.', 'Having hydrocephalus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrocephalus', 'n.', 'An accumulation of liquid within the cavity of the\n   cranium, especially within the ventricles of the brain; dropsy of the\n   brain. It is due usually to tubercular meningitis. When it occurs in\n   infancy, it often enlarges the head enormously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrochlorate', 'n.', 'Same as Hydrochloride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrochloric', 'a.', 'Pertaining to, or compounded of, chlorine and\n   hydrogen gas; as, hydrochloric acid; chlorhydric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrochloride', 'n.', 'A compound of hydrochloric acid with a base; --\n   distinguished from a chloride, where only chlorine unites with the\n   base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrocorallia', 'n. pl.', 'A division of Hydroidea, including those genera\n   that secrete a stony coral, as Millepora and Stylaster. Two forms of\n   zooids in life project from small pores in the coral and resemble those\n   of other hydroids. See Millepora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrocyanate', 'n.', 'See Hydrocyanide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrocyanic', 'a.', 'Pertaining to, or derived from the combination of,\n   hydrogen and cyanogen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrocyanide', 'n.', 'A compound of hydrocyanic acid with a base; --\n   distinguished from a cyanide, in which only the cyanogen so combines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrodynamic', 'a.', 'Alt. of Hydrodynamical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrodynamical', 'a.', 'Pertaining to, or derived from, the dynamical\n   action of water of a liquid; of or pertaining to water power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrodynamics', 'n.', 'That branch of the science of mechanics which\n   relates to fluids, or, as usually limited, which treats of the laws of\n   motion and action of nonelastic fluids, whether as investigated\n   mathematically, or by observation and experiment; the principles of\n   dynamics, as applied to water and other fluids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrodynamometer', 'n.', 'An instrument to measure the velocity of a liquid\n   current by the force of its impact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydro-electric', 'a.', 'Pertaining to, employed in, or produced by, the\n   evolution of electricity by means of a battery in which water or steam\n   is used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydro-extractor', 'n.', 'An apparatus for drying anything, as yarn, cloth,\n   sugar, etc., by centrifugal force; a centrifugal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydroferricyanic', 'n.', 'Pertaining to, or containing, or obtained from,\n   hydrogen, ferric iron, and cyanogen; as, hydroferricyanic acid. See\n   Ferricyanic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydroferrocyanic', 'a.', 'Pertaining to, or containing, or obtained from,\n   hydrogen, ferrous iron, and cyanogen; as, hydroferrocyanic acid. See\n   Ferrocyanic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrofluate', 'n.', 'A supposed compound of hydrofluoris acid and a base; a\n   fluoride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrofluoric', 'a.', 'Pertaining to, or containing, hydrogen and fluorine;\n   fluohydric; as, hydrofluoric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrofluosilicate', 'n.', 'A salt of hydrofluosilic acid; a silicofluoride.\n   See Silicofluoride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrofluosilicic', 'a.', 'Pertaining to, or denoting, a compound consisting\n   of a double fluoride of hydrogen and silicon; silicofluoric. See\n   Silicofluoric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrogalvanic', 'a.', 'Pertaining to, produced by, or consisting of,\n   electricity evolved by the action or use of fluids; as, hydrogalvanic\n   currents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrogen', 'n.', 'A gaseous element, colorless, tasteless, and odorless,\n   the lightest known substance, being fourteen and a half times lighter\n   than air (hence its use in filling balloons), and over eleven thousand\n   times lighter than water. It is very abundant, being an ingredient of\n   water and of many other substances, especially those of animal or\n   vegetable origin. It may by produced in many ways, but is chiefly\n   obtained by the action of acids (as sulphuric) on metals, as zinc,\n   iron, etc. It is very inflammable, and is an ingredient of coal gas and\n   water gas. It is standard of chemical equivalents or combining weights,\n   and also of valence, being the typical monad. Symbol H. Atomic weight\n   1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrogenated', 'imp. & p. p.', 'of Hydrogenate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrogenating', 'p. pr. & vb. n.', 'of Hydrogenate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrogenate', 'v. t.', 'To hydrogenize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrogenation', 'n.', 'The act of combining with hydrogen, or the state of\n   being so combined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrogenide', 'n.', 'A binary compound containing hydrogen; a hydride. [R.]\n   See Hydride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrogenium', 'n.', 'Hydrogen; -- called also in view of its supposed\n   metallic nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrogenized', 'imp. & p. p.', 'of Hydrogenize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrogenizing', 'p. pr. & vb. n.', 'of Hydrogenize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrogenize', 'v. t.', 'To combine with hydrogen; to treat with, or subject\n   to the action of, hydrogen; to reduce; -- contrasted with oxidize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrogenous', 'a.', 'Of or pertaining to hydrogen; containing hydrogen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrognosy', 'n.', 'A treatise upon, or a history and description of, the\n   water of the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrogode', 'n.', 'The negative pole or cathode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrographer', 'n.', 'One skilled in the hydrography; one who surveys, or\n   draws maps or charts of, the sea, lakes, or other waters, with the\n   adjacent shores; one who describes the sea or other waters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrographic', 'a.', 'Alt. of Hydrographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrographical', 'a.', 'Of or relating to hydrography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrography', 'n.', 'The art of measuring and describing the sea, lakes,\n   rivers, and other waters, with their phenomena.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrography', 'n.', 'That branch of surveying which embraces the\n   determination of the contour of the bottom of a harbor or other sheet\n   of water, the depth of soundings, the position of channels and shoals,\n   with the construction of charts exhibiting these particulars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydroguret', 'n.', 'A hydride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydroid', 'a.', 'Related to, or resembling, the hydra; of or pertaining to\n   the Hydroidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydroid', 'n.', 'One of the Hydroideas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydroidea', 'n. pl.', 'An extensive order of Hydrozoa or Acalephae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrokinetic', 'a.', 'Of or pertaining to the motions of fluids, or the\n   forces which produce or affect such motions; -- opposed to hydrostatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrological', 'a.', 'Of or pertaining to hydrology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrologist', 'n.', 'One skilled in hydrology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrology', 'n.', 'The science of water, its properties, phenomena, and\n   distribution over the earths surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrolytic', 'a.', 'Tending to remove or separate water; eliminating water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydromagnesite', 'n.', 'A hydrous carbonate of magnesia occurring in white,\n   early, amorphous masses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydromancy', 'n.', 'Divination by means of water, -- practiced by the\n   ancients.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydromantic', 'a.', 'Of or pertaining to divination by water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydromechanics', 'n.', 'That branch of physics which treats of the\n   mechanics of liquids, or of their laws of equilibrium and of motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydromedusae', 'pl. ', 'of Hydromedusa');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydromedusa', 'n.', 'Any medusa or jellyfish which is produced by budding\n   from a hydroid. They are called also Craspedota, and naked-eyed\n   medusae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydromel', 'n.', 'A liquor consisting of honey diluted in water, and after\n   fermentation called mead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydromellonic', 'a.', 'See Cyamellone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrometallurgical', 'a.', 'Of or pertaining to hydrometallurgy; involving\n   the use of liquid reagents in the treatment or reduction of ores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrometallurgy', 'n.', 'The art or process of assaying or reducing ores by\n   means of liquid reagents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrometeor', 'n.', 'A meteor or atmospheric phenomenon dependent upon the\n   vapor of water; -- in the pl., a general term for the whole aqueous\n   phenomena of the atmosphere, as rain, snow, hail, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrometeorological', 'a.', 'Of or pertaining to hydrometeorology, or to\n   rain, clouds, storms, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrometeorology', 'n.', 'That branch of meteorology which relates to, or\n   treats of, water in the atmosphere, or its phenomena, as rain, clouds,\n   snow, hail, storms, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrometer', 'n.', 'An instrument for determining the specific gravities of\n   liquids, and thence the strength spirituous liquors, saline solutions,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrometer', 'n.', 'An instrument, variously constructed, used for\n   measuring the velocity or discharge of water, as in rivers, from\n   reservoirs, etc., and called by various specific names according to its\n   construction or use, as tachometer, rheometer, hydrometer, pendulum,\n   etc.; a current gauge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrometric', 'a.', 'Alt. of Hydrometrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrometrical', 'a.', 'Of or pertaining to an hydrometer, or to the\n   determination of the specific gravity of fluids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrometrical', 'a.', 'Of or pertaining to measurement of the velocity,\n   discharge, etc., of running water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrometrical', 'a.', 'Made by means of an hydrometer; as, hydrometric\n   observations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrometrograph', 'n.', 'An instrument for determining and recording the\n   quantity of water discharged from a pipe, orifice, etc., in a given\n   time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrometry', 'n.', 'The art of determining the specific gravity of liquids,\n   and thence the strength of spirituous liquors, saline solutions, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrometry', 'n.', 'The art or operation of measuring the velocity or\n   discharge of running water, as in rivers, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydromica', 'n.', 'A variety of potash mica containing water. It is less\n   elastic than ordinary muscovite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydronephrosis', 'n.', 'An accumulation of urine in the pelvis of the\n   kidney, occasioned by obstruction in the urinary passages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydropath', 'n.', 'A hydropathist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydropathic', 'a.', 'Alt. of Hydropathical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydropathical', 'a.', 'Of or pertaining to hydropathy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydropathist', 'n.', 'One who practices hydropathy; a water-cure doctor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydropathy', 'n.', 'The water cure; a mode of treating diseases by the\n   copious and frequent use of pure water, both internally and externally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydroperitoneum', 'n.', 'Same as Ascites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrophane', 'n.', 'A semitranslucent variety of opal that becomes\n   translucent or transparent on immersion in water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrophanous', 'a.', 'Made transparent by immersion in water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrophid', 'n.', 'Any sea snake of the genus Hydrophys and allied genera.\n   These snakes are venomous, live upon fishes, and have a flattened tail\n   for swimming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrophlorone', 'n.', 'A white, crystalline benzene derivative, C8H10O2,\n   obtained by the reduction of phlorone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrophobia', 'n.', 'An abnormal dread of water, said to be a symptom of\n   canine madness; hence:');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrophobia', 'n.', 'The disease caused by a bite form, or inoculation with\n   the saliva of, a rabid creature, of which the chief symptoms are, a\n   sense of dryness and construction in the throat, causing difficulty in\n   deglutition, and a marked heightening of reflex excitability, producing\n   convulsions whenever the patient attempts to swallow, or is disturbed\n   in any way, as by the sight or sound of water; rabies; canine madness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrophobic', 'a.', 'Of or pertaining to hydrophobia; producing or caused\n   by rabies; as, hydrophobic symptoms; the hydrophobic poison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrophoby', 'n.', 'See Hydrophobia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrophora', 'n. pl.', 'The Hydroidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrophore', 'n.', 'An instrument used for the purpose of obtaining\n   specimens of water from any desired depth, as in a river, a lake, or\n   the ocean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrophyllia', 'pl. ', 'of Hydrophyllium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrophylliums', 'pl. ', 'of Hydrophyllium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrophyllium', 'n.', 'One of the flat, leaflike, protective zooids,\n   covering other zooids of certain Siphonophora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrophyte', 'n.', 'An aquatic plant; an alga.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrophytology', 'n.', 'The branch of botany which treats of water plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydropic', 'a.', 'Alt. of Hydropical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydropical', 'a.', 'Dropsical, or resembling dropsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydropically', 'adv.', 'In a hydropical manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydropiper', 'n.', 'A species (Polygonum Hydropiper) of knotweed with acrid\n   foliage; water pepper; smartweed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydropneumatic', 'a.', 'Pertaining to, or depending upon, both liquid and\n   gaseous substances; as, hydropneumatic apparatus for collecting gases\n   over water or other liquids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydropsy', 'n.', 'Same as Dropsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydropult', 'n.', 'A machine for throwing water by hand power, as a garden\n   engine, a fire extinguisher, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydroquinone', 'n.', 'A white crystalline substance, C6H4(OH)2, obtained by\n   the reduction of quinone. It is a diacid phenol, resembling, and\n   metameric with, pyrocatechin and resorcin. Called also dihydroxy\n   benzene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrorhizae', 'pl. ', 'of Hydrorhiza');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrorhizas', 'pl. ', 'of Hydrorhiza');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrorhiza', 'n.', 'The rootstock or decumbent stem by which a hydroid is\n   attached to other objects. See Illust. under Hydroidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrosalt', 'n.', 'A salt supposed to be formed by a hydracid and a base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrosalt', 'n.', 'An acid salt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrosalt', 'n.', 'A hydrous salt; a salt combined with water of hydration\n   or crystallization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydroscope', 'n.', 'An instrument designed to mark the presence of water,\n   especially in air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydroscope', 'n.', 'A kind of water clock, used anciently for measuring\n   time, the water tricking from an orifice at the end of a graduated\n   tube.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrosome', 'n.', 'Alt. of Hydrosoma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrosoma', 'n.', 'All the zooids of a hydroid colony collectively,\n   including the nutritive and reproductive zooids, and often other kinds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrosorbic', 'a.', 'Pertaining to, or designating, an acid obtained from\n   sorbic acid when this takes up hydrogen; as, hydrosorbic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrostat', 'n.', 'A contrivance or apparatus to prevent the explosion of\n   steam boilers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrostatic', 'a.', 'Alt. of Hydrostatical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrostatical', 'a.', 'Of or relating to hydrostatics; pertaining to, or in\n   accordance with, the principles of the equilibrium of fluids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrostatically', 'adv.', 'According to hydrostatics, or to hydrostatic\n   principles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrostatician', 'n.', 'One who is versed or skilled in hydrostatics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrostatics', 'n.', 'The branch of science which relates to the pressure\n   and equilibrium of nonelastic fluids, as water, mercury, etc.; the\n   principles of statics applied to water and other liquids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrosulphate', 'n.', 'Same as Hydrosulphurent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrosulphide', 'n.', 'One of a series of compounds, derived from hydrogen\n   sulphide by the replacement of half its hydrogen by a base or basic\n   radical; as, potassium hydrosulphide, KSH. The hydrosulphides are\n   analogous to the hydrates and include the mercaptans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrosulphite', 'n.', 'A saline compound of hydrosulphurous acid and a\n   base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrosulphuret', 'n.', 'A hydrosulphide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrosulphureted', 'a.', 'Combined with hydrogen sulphide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrosulphuric', 'a.', 'Pertaining to, or derived from, hydrogen and\n   sulphur; as, hydrosulphuric acid, a designation applied to the solution\n   of hydrogen sulphide in water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrosulphurous', 'a.', 'Pertaining to, or designating, an acid obtained by\n   the reduction of sulphurous acid. See Hyposulphurous acid, under\n   Hyposulphurous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrotellurate', 'n.', 'A salt formed by the union of hydrotelluric acid\n   and the base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrotelluric', 'a.', 'Formed by hydrogen and tellurium; as, hydrotelluric\n   acid, or hydrogen telluride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrothecae', 'pl. ', 'of Hydrotheca');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrothecas', 'pl. ', 'of Hydrotheca');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrotheca', 'n.', 'One of the calicles which, in some Hydroidea\n   (Thecaphora), protect the hydrants. See Illust. of Hydroidea, and\n   Campanularian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrotherapy', 'n.', 'See Hydropathy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrothermal', 'a.', 'Of or pertaining to hot water; -- used esp. with\n   reference to the action of heated waters in dissolving, redepositing,\n   and otherwise producing mineral changes within the crust of the globe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrothorax', 'n.', 'An accumulation of serous fluid in the cavity of the\n   chest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrotic', 'a.', 'Causing a discharge of water or phlegm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrotic', 'n.', 'A hydrotic medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrotical', 'a.', 'Hydrotic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrotrope', 'n.', 'A device for raising water by the direct action of\n   steam; a pulsometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrotropic', 'a.', 'Turning or bending towards moisture, as roots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrotropism', 'n.', 'A tendency towards moisture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrous', 'a.', 'Containing water; watery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrous', 'a.', 'Containing water of hydration or crystallization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydroxanthane', 'n.', 'A persulphocyanate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydroxanthic', 'a.', 'Persulphocyanic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydroxide', 'n.', 'A hydrate; a substance containing hydrogen and oxygen,\n   made by combining water with an oxide, and yielding water by\n   elimination. The hydroxides are regarded as compounds of hydroxyl,\n   united usually with basic element or radical; as, calcium hydroxide\n   ethyl hydroxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydroxy-', '', 'A combining form, also used adjectively, indicating\n   hydroxyl as an ingredient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydroxyl', 'n.', 'A compound radical, or unsaturated group, HO, consisting\n   of one atom of hydrogen and one of oxygen. It is a characteristic part\n   of the hydrates, the alcohols, the oxygen acids, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydroxylamine', 'n.', 'A nitrogenous, organic base, NH2.OH, resembling\n   ammonia, and produced by a modified reduction of nitric acid. It is\n   usually obtained as a volatile, unstable solution in water. It acts as\n   a strong reducing agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrozoa', 'n. pl.', 'The Acalephae; one of the classes of coelenterates,\n   including the Hydroidea, Discophora, and Siphonophora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrozoal', 'a.', 'Of or pertaining to the Hydrozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrozoa', 'pl. ', 'of Hydrozoon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrozoons', 'pl. ', 'of Hydrozoon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrozoon', 'n.', 'One of the Hydrozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydruret', 'n.', 'A binary compound of hydrogen; a hydride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hydrus', 'n.', 'A constellation of the southern hemisphere, near the south\n   pole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hye', 'n. & v.', 'See Hie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyemal', 'a.', 'Belonging to winter; done in winter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyemate', 'v. i.', 'To pass the winter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyemation', 'n.', 'The passing of a winter in a particular place; a\n   wintering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyemation', 'n.', 'The act of affording shelter in winter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyen', 'n.', 'A hyena.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyenas', 'pl. ', 'of Hyena');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyena', 'n.', 'Any carnivorous mammal of the family Hyaenidae, of which\n   three living species are known. They are large and strong, but\n   cowardly. They feed chiefly on carrion, and are nocturnal in their\n   habits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyetal', 'a.', 'Of or pertaining to rain; descriptive of the distribution\n   of rain, or of rainy regions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyetograph', 'n.', 'A chart or graphic representation of the average\n   distribution of rain over the surface of the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyetographic', 'a.', 'Of or pertaining to to hyetography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyetography', 'n.', 'The branch of physical science which treats of the\n   geographical distribution of rain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hygeia', 'n.', 'The goddess of health, daughter of Esculapius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hygeian', 'a.', 'Relating to Hygeia, the goddess of health; of or\n   pertaining to health, or its preservation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hygeist', 'n.', 'One skilled in hygiena; a hygienist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hygieist', 'n.', 'A hygienist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hygiene', 'n.', 'That department of sanitary science which treats of the\n   preservation of health, esp. of households and communities; a system of\n   principles or rules designated for the promotion of health.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hygienic', 'a.', 'Of or pertaining to health or hygiene; sanitary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hygienics', 'n.', 'The science of health; hygiene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hygienism', 'n.', 'Hygiene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hygienist', 'n.', 'One versed in hygiene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hygiology', 'n.', 'A treatise on, or the science of, the preservation of\n   health.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hygrine', 'n.', 'An alkaloid associated with cocaine in coca leaves\n   (Erythroxylon coca), and extracted as a thick, yellow oil, having a\n   pungent taste and odor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hygrodeik', 'n.', 'A form of hygrometer having wet and dry bulb\n   thermometers, with an adjustable index showing directly the percentage\n   of moisture in the air, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hygrograph', 'n.', 'An instrument for recording automatically the\n   variations of the humidity of the atmosphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hygrology', 'n.', 'The science which treats of the fluids of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hygrometer', 'n.', 'An instrument for measuring the degree of moisture of\n   the atmosphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hygrometric', 'a.', 'Alt. of Hygrometrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hygrometrical', 'a.', 'Of or pertaining to hygrometry; made with, or\n   according to, the hygrometer; as, hygrometric observations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hygrometrical', 'a.', 'Readily absorbing and retaining moisture; as,\n   hygrometric substances, like potash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hygrometry', 'n.', 'That branch of physics which relates to the\n   determination of the humidity of bodies, particularly of the\n   atmosphere, with the theory and use of the instruments constructed for\n   this purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hygrophanous', 'a.', 'Having such a structure as to be diaphanous when\n   moist, and opaque when dry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hygrophthalmic', 'a.', 'Serving to moisten the eye; -- sometimes applied to\n   the lachrymal ducts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hygroplasm', 'n.', 'The fluid portion of the cell protoplasm, in opposition\n   to stereoplasm, the solid or insoluble portion. The latter is supposed\n   to be partly nutritive and partly composed of idioplasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hygroscope', 'n.', 'An instrument which shows whether there is more or less\n   moisture in the atmosphere, without indicating its amount.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hygroscopic', 'a.', 'Of or pertaining to, or indicated by, the hygroscope;\n   not readily manifest to the senses, but capable of detection by the\n   hygroscope; as, glass is often covered with a film of hygroscopic\n   moisture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hygroscopic', 'a.', 'Having the property of readily inbibing moisture from\n   the atmosphere, or of the becoming coated with a thin film of moisture,\n   as glass, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hygroscopicity', 'n.', 'The property possessed by vegetable tissues of\n   absorbing or discharging moisture according to circumstances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hygrostatics', 'n.', 'The science or art of comparing or measuring degrees\n   of moisture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyke', 'n.', 'See Haik, and Huke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hylaeosaur', 'n.', 'Alt. of Hylaeosaurus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hylaeosaurus', 'n.', 'A large Wealden dinosaur from the Tilgate Forest,\n   England. It was about twenty feet long, protected by bony plates in the\n   skin, and armed with spines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hylarchical', 'a.', 'Presiding over matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyleosaur', 'n.', 'Same as Hylaeosaur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hylic', 'a.', 'Of or pertaining to matter; material; corporeal; as, hylic\n   influences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hylicist', 'n.', 'A philosopher who treats chiefly of matter; one who\n   adopts or teaches hylism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hylism', 'n.', 'A theory which regards matter as the original principle of\n   evil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hylobate', 'n.', 'Any species of the genus Hylobates; a gibbon, or\n   long-armed ape. See Gibbon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hylodes', 'n.', 'The piping frog (Hyla Pickeringii), a small American tree\n   frog, which in early spring, while breeding in swamps and ditches,\n   sings with high, shrill, but musical, notes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyloism', 'n.', 'Same as Hylotheism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyloist', 'n.', 'Same as Hylotheist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hylopathism', 'n.', 'The doctrine that matter is sentient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hylopathist', 'n.', 'One who believes in hylopathism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hylophagous', 'a.', 'Eating green shoots, as certain insects do.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hylotheism', 'n.', 'The doctrine of belief that matter is God, or that\n   there is no God except matter and the universe; pantheism. See\n   Materialism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hylotheist', 'n.', 'One who believes in hylotheism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hylozoic', 'a.', 'Of or pertaining to hylozoism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hylozoism', 'n.', 'The doctrine that matter possesses a species of life and\n   sensation, or that matter and life are inseparable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hylozoist', 'n.', 'A believer in hylozoism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hymar', 'n.', 'The wild ass of Persia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hymen', 'n.', 'A fold of muscous membrane often found at the orifice of the\n   vagina; the vaginal membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hymen', 'n.', 'A fabulous deity; according to some, the son of Apollo and\n   Urania, according to others, of Bacchus and Venus. He was the god of\n   marriage, and presided over nuptial solemnities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hymen', 'n.', 'Marriage; union as if by marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hymeneal', 'n.', 'Alt. of Hymenean');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hymenean', 'n.', 'Of or pertaining to marriage; as, hymeneal rites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hymeneal', 'n.', 'Alt. of Hymenean');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hymenean', 'n.', 'A marriage song.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hymenia', 'pl. ', 'of Hymenium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hymeniums', 'pl. ', 'of Hymenium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hymenium', 'n.', 'The spore-bearing surface of certain fungi, as that on\n   the gills of a mushroom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hymenogeny', 'n.', 'The production of artificial membranes by contact of\n   two fluids, as albumin and fat, by which the globules of the latter are\n   surrounded by a thin film of the former.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hymenomycetes', 'n. pl.', 'One of the great divisions of fungi, containing\n   those species in which the hymenium is completely exposed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hymenophore', 'n.', 'That part of a fungus which is covered with the\n   hymenium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hymenopter', 'n.', 'One of the Hymenoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hymenoptera', 'n. pl.', 'An extensive order of insects, including the bees,\n   ants, ichneumons, sawflies, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hymenopteral', 'a.', 'Alt. of Hymenopterous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hymenopterous', 'a.', 'Like, or characteristic of, the Hymenoptera;\n   pertaining to the Hymenoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hymenopteran', 'n.', 'One of the Hymenoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hymn', 'n.', 'An ode or song of praise or adoration; especially, a\n   religious ode, a sacred lyric; a song of praise or thankgiving intended\n   to be used in religious service; as, the Homeric hymns; Watts hymns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hymned', 'imp. & p. p.', 'of Hymn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hymning', 'p. pr. & vb. n.', 'of Hymn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hymn', 'v. t.', 'To praise in song; to worship or extol by singing hymns;\n   to sing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hymn', 'v. i.', 'To sing in praise or adoration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hymnal', 'n.', 'A collection of hymns; a hymn book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hymnic', 'a.', 'Relating to hymns, or sacred lyrics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hymning', 'a.', 'Praising with hymns; singing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hymning', 'n.', 'The singing of hymns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hymnist', 'n.', 'A writer of hymns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hymnody', 'n.', 'Hymns, considered collectively; hymnology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hymnographer', 'n.', 'One who writes on the subject of hymns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hymnographer', 'n.', 'A writer or composed of hymns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hymnography', 'n.', 'The art or act of composing hymns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hymnologist', 'n.', 'A composer or compiler of hymns; one versed in\n   hymnology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hymnology', 'n.', 'The hymns or sacred lyrics composed by authors of a\n   particular country or period; as, the hymnology of the eighteenth\n   century; also, the collective body of hymns used by any particular\n   church or religious body; as, the Anglican hymnology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hymnology', 'n.', 'A knowledge of hymns; a treatise on hymns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hympne', 'n.', 'A hymn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyndreste', 'a.', 'See Hinderest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyne', 'n.', 'A servant. See Hine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyo-', '', 'A prexif used in anatomy, and generally denoting connection\n   with the hyoid bone or arch; as, hyoglossal, hyomandibular, hyomental,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyoganoidei', 'n. pl.', 'A division of ganoid fishes, including the gar\n   pikes and bowfins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyoglossal', 'a.', 'Pertaining to or connecting the tongue and hyodean\n   arch; as, the hyoglossal membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyoglossal', 'a.', 'Of or pertaining to the hyoglossus muscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyoglossus', 'n.', 'A flat muscle on either side of the tongue, connecting\n   it with the hyoid bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyoid', 'a.', 'Having the form of an arch, or of the Greek letter upsilon\n   [/].');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyoid', 'a.', 'Of or pertaining to the bony or cartilaginous arch which\n   supports the tongue. Sometimes applied to the tongue itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyoid', 'n.', 'The hyoid bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyoideal', 'a.', 'Alt. of Hyoidean');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyoidean', 'a.', 'Same as Hyoid, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyomandibular', 'a.', 'Pertaining both to the hyoidean arch and the\n   mandible or lower jaw; as, the hyomandibular bone or cartilage, a\n   segment of the hyoid arch which connects the lower jaw with the skull\n   in fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyomandibular', 'n.', 'The hyomandibular bone or cartilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyomental', 'a.', 'Between the hyoid bone and the lower jaw, pertaining to\n   them; suprahyoid; submaxillary; as, the hyomental region of the front\n   of the neck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyopastron', 'n.', 'The second lateral plate in the plastron of turtles; --\n   called also hyosternum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyoscine', 'n.', 'An alkaloid found with hyoscyamine (with which it is also\n   isomeric) in henbane, and extracted as a white, amorphous, semisolid\n   substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyoscyamine', 'n.', 'An alkaloid found in henbane (Hyoscyamus niger), and\n   regarded as its active principle. It is also found with other alkaloids\n   in the thorn apple and deadly nightshade. It is extracted as a white\n   crystalline substance, with a sharp, offensive taste. Hyoscyamine is\n   isomeric with atropine, is very poisonous, and is used as a medicine\n   for neuralgia, like belladonna. Called also hyoscyamia, duboisine, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyoscyamus', 'n.', 'A genus of poisonous plants of the Nightshade family;\n   henbane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyoscyamus', 'n.', 'The leaves of the black henbane (Hyoscyamus niger),\n   used in neuralgic and pectorial troubles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyosternal', 'a.', 'Between the hyoid bone and the sternum, or pertaining\n   to them; infrahyoid; as, the hyosternal region of the neck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyosternal', 'a.', 'Pertaining to the hyosternum of turtles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyosternum', 'n.', 'See Hyoplastron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyostylic', 'a.', 'Having the mandible suspended by the hyomandibular, or\n   upper part of the hyoid arch, as in fishes, instead of directly\n   articulated with the skull as in mammals; -- said of the skull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyp', 'n.', 'An abbreviation of hypochonaria; -- usually in plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyp', 'v. t.', 'To make melancholy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypaethral', 'a.', 'Alt. of Hypethral');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypethral', 'a.', 'Exposed to the air; wanting a roof; -- applied to a\n   building or part of a building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypallage', 'n.', 'A figure consisting of a transference of attributes from\n   their proper subjects to other. Thus Virgil says, \"dare classibus\n   austros,\" to give the winds to the fleets, instead of dare classibus\n   austris, to give the fleets to the winds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypanthia', 'pl. ', 'of Hypanthium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypanthiums', 'pl. ', 'of Hypanthium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypanthium', 'n.', 'A fruit consisting in large part of a receptacle,\n   enlarged below the calyx, as in the Calycanthus, the rose hip, and the\n   pear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypapophyles', 'pl. ', 'of Hypapophysis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypapophysis', 'n.', 'A process, or other element, of a vertebra developed\n   from the ventral side of the centrum, as haemal spines, and chevron\n   bones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyparterial', 'a.', 'Situated below an artery; applied esp. to the branches\n   of the bronchi given off below the point where the pulmonary artery\n   crosses the bronchus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypaspist', 'n.', 'A shield-bearer or armor-bearer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypaxial', 'a.', 'Beneath the axis of the skeleton; subvertebral;\n   hyposkeletal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyper-', '', 'A prefix signifying over, above; as, hyperphysical,\n   hyperthyrion; also, above measure, abnormally great, excessive; as,\n   hyperaemia, hyperbola, hypercritical, hypersecretion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyper-', '', 'A prefix equivalent to super- or per-; as hyperoxide, or\n   peroxide. [Obs.] See Per-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperaemia', 'n.', 'A superabundance or congestion of blood in an organ or\n   part of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperaesthesia', 'n.', 'A state of exalted or morbidly increased\n   sensibility of the body, or of a part of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperapophyses', 'pl. ', 'of Hyperapophysis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperapophysis', 'n.', 'A lateral and backward-projecting process on the\n   dorsal side of a vertebra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperaspist', 'n.', 'One who holds a shield over another; hence, a\n   defender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperbatic', 'a.', 'Of or pertaining to an hyperbaton; transposed;\n   inverted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperbaton', 'n.', 'A figurative construction, changing or inverting the\n   natural order of words or clauses; as, \"echoed the hills\" for \"the\n   hills echoed.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperbola', 'n.', 'A curve formed by a section of a cone, when the cutting\n   plane makes a greater angle with the base than the side of the cone\n   makes. It is a plane curve such that the difference of the distances\n   from any point of it to two fixed points, called foci, is equal to a\n   given distance. See Focus. If the cutting plane be produced so as to\n   cut the opposite cone, another curve will be formed, which is also an\n   hyperbola. Both curves are regarded as branches of the same hyperbola.\n   See Illust. of Conic section, and Focus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperbole', 'n.', 'A figure of speech in which the expression is an evident\n   exaggeration of the meaning intended to be conveyed, or by which things\n   are represented as much greater or less, better or worse, than they\n   really are; a statement exaggerated fancifully, through excitement, or\n   for effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperbolic', 'a.', 'Alt. of Hyperbolical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperbolical', 'a.', 'Belonging to the hyperbola; having the nature of the\n   hyperbola.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperbolical', 'a.', 'Relating to, containing, or of the nature of,\n   hyperbole; exaggerating or diminishing beyond the fact; exceeding the\n   truth; as, an hyperbolical expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperbolically', 'adv.', 'In the form of an hyperbola.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperbolically', 'adv.', 'With exaggeration; in a manner to express more or\n   less than the truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperboliform', 'a.', 'Having the form, or nearly the form, of an\n   hyperbola.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperbolism', 'n.', 'The use of hyperbole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperbolist', 'n.', 'One who uses hyperboles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperbolized', 'imp. & p. p.', 'of Hyperbolize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperbolizing', 'p. pr. & vb. n.', 'of Hyperbolize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperbolize', 'v. i.', 'To speak or write with exaggeration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperbolize', 'v. t.', 'To state or represent hyperbolically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperboloid', 'n.', 'A surface of the second order, which is cut by certain\n   planes in hyperbolas; also, the solid, bounded in part by such a\n   surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperboloid', 'a.', 'Having some property that belongs to an hyperboloid or\n   hyperbola.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperborean', 'a.', 'Of or pertaining to the region beyond the North wind,\n   or to its inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperborean', 'a.', 'Northern; belonging to, or inhabiting, a region in\n   very far north; most northern; hence, very cold; fright, as, a\n   hyperborean coast or atmosphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperborean', 'n.', 'One of the people who lived beyond the North wind, in\n   a land of perpetual sunshine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperborean', 'n.', 'An inhabitant of the most northern regions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypercarbureted', 'a.', 'Having an excessive proportion of carbonic acid;\n   -- said of bicarbonates or acid carbonates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypercatalectic', 'a.', 'Having a syllable or two beyond measure; as, a\n   hypercatalectic verse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperchloric', 'a.', 'See Perchloric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperchromatism', 'n.', 'The condition of having an unusual intensity of\n   color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypercritic', 'n.', 'One who is critical beyond measure or reason; a\n   carping critic; a captious censor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypercritic', 'a.', 'Hypercritical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypercritical', 'a.', 'Over critical; unreasonably or unjustly critical;\n   carping; captious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypercritical', 'a.', 'Excessively nice or exact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypercritically', 'adv.', 'In a hypercritical manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypercriticise', 'v. t.', 'To criticise with unjust severity; to criticise\n   captiously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypercriticism', 'n.', 'Excessive criticism, or unjust severity or rigor of\n   criticism; zoilism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperdicrotic', 'a.', 'Excessive dicrotic; as, a hyperdicrotic pulse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperdicrotism', 'n.', 'A hyperdicrotic condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperdicrotous', 'a.', 'Hyperdicrotic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperdulia', 'n.', 'Veneration or worship given to the Virgin Mary as the\n   most exalted of mere creatures; higher veneration than dulia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperduly', 'n.', 'Hyperdulia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperesthesia', 'n.', 'Same as Hyperaesthesia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypericum', 'n.', 'A genus of plants, generally with dotted leaves and\n   yellow flowers; -- called also St. Johns-wort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperinosis', 'n.', 'A condition of the blood, characterized by an\n   abnormally large amount of fibrin, as in many inflammatory diseases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperion', 'n.', 'The god of the sun; in the later mythology identified\n   with Apollo, and distinguished for his beauty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperkinesis', 'n.', 'Abnormally increased muscular movement; spasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperkinetic', 'a.', 'Of or pertaining to hyperkinesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypermetamorphosis', 'n.', 'A kind of metamorphosis, in certain insects, in\n   which the larva itself undergoes remarkable changes of form and\n   structure during its growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypermeter', 'n.', 'A verse which has a redundant syllable or foot; a\n   hypercatalectic verse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypermeter', 'n.', 'Hence, anything exceeding the ordinary standard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypermetrical', 'a.', 'Having a redundant syllable; exceeding the common\n   measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypermetropia', 'n.', 'Alt. of Hypermetropy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypermetropy', 'n.', 'A condition of the eye in which, through shortness of\n   the eyeball or fault of the refractive media, the rays of light come to\n   a focus behind the retina; farsightedness; -- called also hyperopia.\n   Cf. Emmetropia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypermyriorama', 'n.', 'A show or exhibition having a great number of\n   scenes or views.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperoartia', 'n. pl.', 'An order of marsipobranchs including the lampreys.\n   The suckerlike moth contains numerous teeth; the nasal opening is in\n   the middle of the head above, but it does not connect with the mouth.\n   See Cyclostoma, and Lamprey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperopia', 'n.', 'Hypermetropia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperorganic', 'a.', 'Higher than, or beyond the sphere of, the organic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperorthodoxy', 'n.', 'Orthodoxy pushed to excess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperotreta', 'n. pl.', 'An order of marsipobranchs, including the Myxine\n   or hagfish and the genus Bdellostoma. They have barbels around the\n   mouth, one tooth on the plate, and a communication between the nasal\n   aperture and the throat. See Hagfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperoxide', 'n.', 'A compound having a relatively large percentage of\n   oxygen; a peroxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperoxygenated', 'a.', 'Alt. of Hyperoxygenized');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperoxygenized', 'a.', 'Combined with a relatively large amount of oxygen;\n   -- said of higher oxides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperoxymuriate', 'n.', 'A perchlorate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperoxymuriatic', 'a.', 'Perchloric; as, hyperoxymuriatic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperphysical', 'a.', 'Above or transcending physical laws; supernatural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperplasia', 'n.', 'An increase in, or excessive growth of, the normal\n   elements of any part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperplastic', 'a.', 'Of or pertaining to hyperplasia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperplastic', 'a.', 'Tending to excess of formative action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypernoea', 'n.', 'Abnormal breathing, due to slightly deficient\n   arterialization of the blood; -- in distinction from eupnoea. See\n   Eupnoea, and Dispnoea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperpyrexia', 'n.', 'A condition of excessive fever; an elevation of\n   temperature in a disease, in excess of the limit usually observed in\n   that disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypersecretion', 'n.', 'Morbid or excessive secretion, as in catarrh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypersensibility', 'n.', 'See Hyperaesthesia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperspace', 'n.', 'An imagined space having more than three dimensions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypersthene', 'n.', 'An orthorhombic mineral of the pyroxene group, of a\n   grayish or greenish black color, often with a peculiar bronzelike\n   luster (schiller) on the cleavage surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypersthenic', 'a.', 'Composed of, or containing, hypersthene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperthetical', 'a.', 'Exaggerated; excessive; hyperbolical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyperthyrion', 'n.', 'That part of the architrave which is over a door or\n   window.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypertrophic', 'a.', 'Alt. of Hypertrophical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypertrophical', 'a.', 'Of or pertaining to hypertrophy; affected with, or\n   tending to, hypertrophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypertrophied', 'a.', 'Excessively developed; characterized by hypertrophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypertrophy', 'n.', 'A condition of overgrowth or excessive development of\n   an organ or part; -- the opposite of atrophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyphae', 'n. pl.', 'The long, branching filaments of which the mycelium\n   (and the greater part of the plant) of a fungus is formed. They are\n   also found enveloping the gonidia of lichens, making up a large part of\n   their structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyphen', 'n.', 'A mark or short dash, thus [-], placed at the end of a line\n   which terminates with a syllable of a word, the remainder of which is\n   carried to the next line; or between the parts of many a compound word;\n   as in fine-leaved, clear-headed. It is also sometimes used to separate\n   the syllables of words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyphened', 'imp. & p. p.', 'of Hyphen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyphening', 'p. pr. & vb. n.', 'of Hyphen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyphen', 'v. t.', 'To connect with, or separate by, a hyphen, as two words\n   or the parts of a word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyphenated', 'a.', 'United by hyphens; hyphened; as, a hyphenated or\n   hyphened word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyphomycetes', 'n. pl.', 'One of the great division of fungi, containing\n   those species which have naked spores borne on free or only fasciculate\n   threads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypidiomorphic', 'a.', 'Partly idiomorphic; -- said of rock a portion only\n   of whose constituents have a distinct crystalline form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypinosis', 'n.', 'A diminution in the normal amount of fibrin present in\n   the blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypnagogic', 'a.', 'Leading to sleep; -- applied to the illusions of one\n   who is half asleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypnobate', 'n.', 'A somnambulist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypnocyst', 'n.', 'A cyst in which some unicellular organisms temporarily\n   inclose themselves, from which they emerge unchanged, after a period of\n   drought or deficiency of food. In some instances, a process of spore\n   formation seems to occur within such cysts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypnogenic', 'a.', 'Relating to the production of hypnotic sleep; as, the\n   so-called hypnogenic pressure points, pressure upon which is said to\n   cause an attack of hypnotic sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypnologist', 'n.', 'One who is versed in hypnology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypnology', 'n.', 'A treatise on sleep; the doctrine of sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypnosis', 'n.', 'Supervention of sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypnotic', 'a.', 'Having the quality of producing sleep; tending to produce\n   sleep; soporific.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypnotic', 'a.', 'Of or pertaining to hypnotism; in a state of hypnotism;\n   liable to hypnotism; as, a hypnotic condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypnotic', 'n.', 'Any agent that produces, or tends to produce, sleep; an\n   opiate; a soporific; a narcotic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypnotic', 'n.', 'A person who exhibits the phenomena of, or is subject to,\n   hypnotism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypnotism', 'n.', 'A form of sleep or somnambulism brought on by artificial\n   means, in which there is an unusual suspension of some powers, and an\n   unusual activity of others. It is induced by an action upon the nerves,\n   through the medium of the senses, as in persons of very feeble\n   organization, by gazing steadly at a very bright object held before the\n   eyes, or by pressure upon certain points of the surface of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypnotization', 'n.', 'The act or process of producing hypnotism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypnotized', 'imp. & p. p.', 'of Hypnotize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypnotizing', 'p. pr. & vb. n.', 'of Hypnotize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypnotize', 'v. t.', 'To induce hypnotism in; to place in a state of\n   hypnotism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypnotizer', 'n.', 'One who hypnotizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypnum', 'n.', 'The largest genus of true mosses; feather moss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypo-', '', 'A prefix signifying a less quantity, or a low state or degree,\n   of that denoted by the word with which it is joined, or position under\n   or beneath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypo-', '', 'A prefix denoting that the element to the name of which it is\n   prefixed enters with a low valence, or in a low state of oxidization,\n   usually the lowest, into the compounds indicated; as, hyposulphurous\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypo', 'n.', 'Hypochondria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypo', 'n.', 'Sodium hyposulphite, or thiosulphate, a solution of which is\n   used as a bath to wash out the unchanged silver salts in a picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypoarian', 'a.', 'Of or pertaining to a hypoarion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypoaria', 'pl. ', 'of Hypoarion');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypoarion', 'n.', 'An oval lobe beneath each of the optic lobes in many\n   fishes; one of the inferior lobes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypoblast', 'n.', 'The inner or lower layer of the blastoderm; -- called\n   also endoderm, entoderm, and sometimes hypoderm. See Illust. of\n   Blastoderm, Delamination, and Ectoderm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypoblastic', 'a.', 'Relating to, or connected with, the hypoblast; as, the\n   hypoic sac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypobole', 'n.', 'A figure in which several things are mentioned that seem\n   to make against the argument, or in favor of the opposite side, each of\n   them being refuted in order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypobranchial', 'a.', 'Pertaining to the segment between the basibranchial\n   and the ceratobranchial in a branchial arch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypobranchial', 'n.', 'A hypobranchial bone or cartilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypocarp', 'n.', 'Alt. of Hypocarpium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypocarpium', 'n.', 'A fleshy enlargement of the receptacle, or for the\n   stem, below the proper fruit, as in the cashew. See Illust. of Cashew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypocarpogean', 'a.', 'Producing fruit below the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypocaust', 'n.', 'A furnace, esp. one connected with a series of small\n   chambers and flues of tiles or other masonry through which the heat of\n   a fire was distributed to rooms above. This contrivance, first used in\n   bath, was afterwards adopted in private houses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypochlorite', 'n.', 'A salt of hypochlorous acid; as, a calcium\n   hypochloride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypochlorous', 'a.', 'Pertaining to, or derived from, chlorine having a\n   valence lower than in chlorous compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypochondres', 'n. pl.', 'The hypochondriac regions. See Hypochondrium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypochondria', 'n.', 'Hypochondriasis; melancholy; the blues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypochondriac', 'a.', 'Of or pertaining to hypochondria, or the\n   hypochondriac regions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypochondriac', 'a.', 'Affected, characterized, or produced, by\n   hypochondriasis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypochondriac', 'n.', 'A person affected with hypochondriasis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypochondriacal', 'a.', 'Same as Hypochondriac, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypochondriacism', 'n.', 'Hypochondriasis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypochondriasis', 'n.', 'A mental disorder in which melancholy and gloomy\n   views torment the affected person, particularly concerning his own\n   health.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypochondriasm', 'n.', 'Hypochondriasis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypochondria', 'pl. ', 'of Hypochondrium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypochondriums', 'pl. ', 'of Hypochondrium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypochondrium', 'n.', 'Either of the hypochondriac regions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypochondry', 'n.', 'Hypochondriasis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypocist', 'n.', 'An astringent inspissated juice obtained from the fruit\n   of a plant (Cytinus hypocistis), growing from the roots of the Cistus,\n   a small European shrub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypocleida', 'pl. ', 'of Hypocleidium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypocleidiums', 'pl. ', 'of Hypocleidium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypocleidium', 'n.', 'A median process on the furculum, or merrythought, of\n   many birds, where it is connected with the sternum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypocoristic', 'a.', 'Endearing; diminutive; as, the hypocoristic form of a\n   name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypocrateriform', 'a.', 'hypocraterimorphous; salver-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypocraterimorphous', 'a.', 'Salver-shaped; having a slender tube,\n   expanding suddenly above into a bowl-shaped or spreading border, as in\n   the blossom of the phlox and the lilac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypocrisies', 'pl. ', 'of Hypocrisy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypocrisy', 'n.', 'The act or practice of a hypocrite; a feigning to be\n   what one is not, or to feel what one does not feel; a dissimulation, or\n   a concealment of ones real character, disposition, or motives;\n   especially, the assuming of false appearance of virtue or religion; a\n   simulation of goodness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypocrite', 'n.', 'One who plays a part; especially, one who, for the\n   purpose of winning approbation of favor, puts on a fair outside\n   seeming; one who feigns to be other and better than he is; a false\n   pretender to virtue or piety; one who simulates virtue or piety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypocritely', 'adv.', 'Hypocritically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypocritic', 'a.', 'See Hypocritical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypocritical', 'a.', 'Of or pertaining to a hypocrite, or to hypocrisy; as,\n   a hypocriticalperson; a hypocritical look; a hypocritical action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypocrystalline', 'a.', 'Partly crystalline; -- said of rock which consists\n   of crystals imbedded in a glassy ground mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypocycloid', 'n.', 'A curve traced by a point in the circumference of a\n   circle which rolls on the concave side in the fixed circle. Cf.\n   Epicycloid, and Trochoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-tyla', 'pl. ', 'of Hypodactylum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypodactylum', 'n.', 'The under side of the toes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypoderm', 'n.', 'Same as Hypoblast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypoderma', 'n.', 'A layer of tissue beneath the epidermis in plants, and\n   performing the physiological function of strengthening the epidermal\n   tissue. In phanerogamous plants it is developed as collenchyma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypoderma', 'n.', 'An inner cellular layer which lies beneath the chitinous\n   cuticle of arthropods, annelids, and some other invertebrates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypodermatic', 'a.', 'Hypodermic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypodermic', 'a.', 'Of or pertaining to the parts under the skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypodermis', 'n.', 'Same as Hypoblast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypodermis', 'n.', 'Same as Hypoderma, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypodicrotic', 'a.', 'Alt. of Hypodicrotous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypodicrotous', 'a.', 'Exhibiting retarded dicrotism; as, a hypodicrotic\n   pulse curve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypogaeic', 'a.', 'Pertaining to, or obtained from, the peanut, or earthnut\n   (Arachis hypogaea).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypogastric', 'a.', 'Of or pertaining to the hypogastrium or the\n   hypogastric region.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypogastrium', 'n.', 'The lower part of the abdomen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypogean', 'a.', 'Hypogeous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypogene', 'a.', 'Formed or crystallized at depths the earths surface; --\n   said of granite, gneiss, and other rocks, whose crystallization is\n   believed of have taken place beneath a great thickness of overlying\n   rocks. Opposed to epigene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypogeous', 'a.', 'Growing under ground; remaining under ground; ripening\n   its fruit under ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypogea', 'pl. ', 'of Hypogeum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypogeum', 'n.', 'The subterraneous portion of a building, as in\n   amphitheaters, for the service of the games; also, subterranean\n   galleries, as the catacombs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypoglossal', 'a.', 'Under the tongue; -- applied esp., in the higher\n   vertebrates, to the twelfth or last pair of cranial nerves, which are\n   distributed to the base of the tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypoglossal', 'n.', 'One of the hypoglossal nerves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypognatous', 'a.', 'Having the maxilla, or lower jaw, longer than the\n   upper, as in the skimmer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypogyn', 'n.', 'An hypogynous plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypogynous', 'a.', 'Inserted below the pistil or pistils; -- said of\n   sepals, petals, and stamens; having the sepals, petals, and stamens\n   inserted below the pistil; -- said of a flower or a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypohyal', 'a.', 'Pertaining to one or more small elements in the hyoidean\n   arch of fishes, between the caratohyal and urohyal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypohyal', 'n.', 'One of the hypohyal bones or cartilages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyponastic', 'a.', 'Exhibiting a downward convexity caused by unequal\n   growth. Cf. Epinastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyponasty', 'n.', 'Downward convexity, or convexity of the inferior\n   surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyponitrite', 'n.', 'A salt of hyponitrous acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyponitrous', 'a.', 'Containing or derived from nitrogen having a lower\n   valence than in nitrous compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypopharynx', 'n.', 'An appendage or fold on the lower side of the pharynx,\n   in certain insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypophosphate', 'n.', 'A salt of hypophosphoric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypophosphite', 'n.', 'A salt of hypophosphorous acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypophosphoric', 'a.', 'Pertaining to, or derived from, or containing,\n   phosphorus in a lower state of oxidation than in phosphoric compounds;\n   as, hypophosphoric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypophosphorous', 'a.', 'Pertaining to, or containing, phosphorus in a\n   lower state of oxidation than in phosphoric compounds; as,\n   hypophosphorous acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypophyllous', 'a.', 'Being or growing on the under side of a leaf, as the\n   fruit dots of ferns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypophysial', 'a.', 'Of or pertaining to the hypophysis; pituitary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypophysis', 'n.', 'See Pituitary body, under Pituitary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypophysis', 'n.', 'Cataract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypoplastra', 'pl. ', 'of Hypoplastron');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypoplastron', 'n.', 'The third lateral plate in the plastron of turtles;\n   -- called also hyposternum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypoptila', 'pl. ', 'of Hypoptilum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypoptilums', 'pl. ', 'of Hypoptilum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypoptilum', 'n.', 'An accessory plume arising from the posterior side of\n   the stem of the contour feathers of many birds; -- called also\n   aftershaft. See Illust. of Feather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyporadii', 'pl. ', 'of Hyporadius');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyporadius', 'n.', 'One of the barbs of the hypoptilum, or aftershaft of a\n   feather. See Feather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyporhachides', 'pl. ', 'of Hyporhachis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyporhachis', 'n.', 'The stem of an aftershaft or hypoptilum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyposkeletal', 'a.', 'Beneath the endoskeleton; hypaxial; as, the\n   hyposkeletal muscles; -- opposed to episkeletal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypospadias', 'n.', 'A deformity of the penis, in which the urethra opens\n   upon its under surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypostases', 'pl. ', 'of Hypostasis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypostasis', 'n.', 'That which forms the basis of anything; underlying\n   principle; a concept or mental entity conceived or treated as an\n   existing being or thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypostasis', 'n.', 'Substance; subsistence; essence; person; personality;\n   -- used by the early theologians to denote any one of the three\n   subdivisions of the Godhead, the Father, Son, and Holy Spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypostasis', 'n.', 'Principle; an element; -- used by the alchemists in\n   speaking of salt, sulphur, and mercury, which they considered as the\n   three principles of all material bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypostasis', 'n.', 'That which is deposited at the bottom of a fluid;\n   sediment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypostasize', 'v. t.', 'To make into a distinct substance; to conceive or\n   treat as an existing being; to hypostatize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypostatic', 'a.', 'Alt. of Hypostatical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypostatical', 'a.', 'Relating to hypostasis, or substance; hence,\n   constitutive, or elementary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypostatical', 'a.', 'Personal, or distinctly personal; relating to the\n   divine hypostases, or substances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypostatical', 'a.', 'Depending upon, or due to, deposition or setting; as,\n   hypostatic cognestion, cognestion due to setting of blood by\n   gravitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypostatically', 'adv.', 'In a hypostatic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypostatize', 'v. t.', 'To make into, or regarded as, a separate and\n   distinct substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypostatize', 'v. t.', 'To attribute actual or personal existence to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyposterna', 'pl. ', 'of Hyposternum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyposternums', 'pl. ', 'of Hyposternum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyposternum', 'n.', 'See Hypoplastron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypostome', 'n.', 'Alt. of Hypostoma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypostoma', 'n.', 'The lower lip of trilobites, crustaceans, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypostrophe', 'n.', 'The act of a patient turning himself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypostrophe', 'n.', 'A relapse, or return of a disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypostyle', 'a.', 'Resting upon columns; constructed by means of columns;\n   -- especially applied to the great hall at Karnak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyposulphate', 'n.', 'A salt of hyposulphuric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyposulphite', 'n.', 'A salt of what was formerly called hyposulphurous\n   acid; a thiosulphate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyposulphite', 'n.', 'A salt of hyposulphurous acid proper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyposulphuric', 'a.', 'Pertaining to, or containing, sulphur in a lower\n   state of oxidation than in the sulphuric compounds; as, hyposulphuric\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyposulphurous', 'a.', 'Pertaining to, or containing, sulphur, all, or a\n   part, in a low state of oxidation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypotarsi', 'pl. ', 'of Hypotarsus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypotarsus', 'n.', 'A process on the posterior side of the tarsometatarsus\n   of many birds; the calcaneal process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypotenuse', 'n.', 'Alt. of Hypothenuse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypothenuse', 'n.', 'The side of a right-angled triangle that is opposite\n   to the right angle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypothec', 'n.', 'A landlords right, independently of stipulation, over\n   the stocking (cattle, implements, etc.), and crops of his tenant, as\n   security for payment of rent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypotheca', 'n.', 'An obligation by which property of a debtor was made\n   over to his creditor in security of his debt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypothecated', 'imp. & p. p.', 'of Hypothecate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypothecating', 'p. pr. & vb. n.', 'of Hypothecate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypothecate', 'v. t.', 'To subject, as property, to liability for a debt or\n   engagement without delivery of possession or transfer of title; to\n   pledge without delivery of possession; to mortgage, as ships, or other\n   personal property; to make a contract by bottomry. See Hypothecation,\n   Bottomry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypothecation', 'n.', 'The act or contract by which property is\n   hypothecated; a right which a creditor has in or to the property of his\n   debtor, in virtue of which he may cause it to be sold and the price\n   appropriated in payment of his debt. This is a right in the thing, or\n   jus in re.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypothecation', 'n.', 'A contract whereby, in consideration of money\n   advanced for the necessities of the ship, the vessel, freight, or cargo\n   is made liable for its repayment, provided the ship arrives in safety.\n   It is usually effected by a bottomry bond. See Bottomry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypothecator', 'n.', 'One who hypothecates or pledges anything as security\n   for the repayment of money borrowed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypothenal', 'a.', 'Alt. of Hypothenar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypothenar', 'a.', 'Of or pertaining to the prominent part of the palm of\n   the hand above the base of the little finger, or a corresponding part\n   in the forefoot of an animal; as, the hypothenar eminence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypothenar', 'n.', 'The hypothenar eminence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypothenusal', 'a.', 'Of or pertaining to hypothenuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypothenuse', 'n.', 'Same as Hypotenuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypotheses', 'pl. ', 'of Hypothesis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypothesis', 'n.', 'A supposition; a proposition or principle which is\n   supposed or taken for granted, in order to draw a conclusion or\n   inference for proof of the point in question; something not proved, but\n   assumed for the purpose of argument, or to account for a fact or an\n   occurrence; as, the hypothesis that head winds detain an overdue\n   steamer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypothesis', 'n.', 'A tentative theory or supposition provisionally adopted\n   to explain certain facts, and to guide in the investigation of others;\n   hence, frequently called a working hypothesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypothetic', 'a.', 'Alt. of Hypothetical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypothetical', 'a.', 'Characterized by, or of the nature of, an hypothesis;\n   conditional; assumed without proof, for the purpose of reasoning and\n   deducing proof, or of accounting for some fact or phenomenon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypothetist', 'n.', 'One who proposes or supports an hypothesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypotrachelium', 'n.', 'Same as Gorgerin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypotricha', 'n. pl.', 'A division of ciliated Infusoria in which the cilia\n   cover only the under side of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypotrochoid', 'n.', 'A curve, traced by a point in the radius, or radius\n   produced, of a circle which rolls upon the concave side of a fixed\n   circle. See Hypocycloid, Epicycloid, and Trochoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypotyposis', 'n.', 'A vivid, picturesque description of scenes or events.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypoxanthin', 'n.', 'A crystalline, nitrogenous substance, closely related\n   to xanthin and uric acid, widely distributed through the animal body,\n   but especially in muscle tissue; -- called also sarcin, sarkin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypozoic', 'a.', 'Anterior in age to the lowest rocks which contain organic\n   remains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyppish', 'a.', 'Affected with hypochondria; hypped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyppogriff', 'n.', 'See Hyppogriff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypsiloid', 'a.', 'Resembling the Greek letter / in form; hyoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypsometer', 'n.', 'An instrument for measuring heights by observation of\n   barometric pressure; esp., one for determining heights by ascertaining\n   the boiling point of water. It consists of a vessel for water, with a\n   lamp for heating it, and an inclosed thermometer for showing the\n   temperature of ebullition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypsometric', 'a.', 'Alt. of Hypsometrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypsometrical', 'a.', 'Of or pertaining to hypsometry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypsometry', 'n.', 'That branch of the science of geodesy which has to do\n   with the measurement of heights, either absolutely with reference to\n   the sea level, or relatively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hypural', 'a.', 'Under the tail; -- applied to the bones which support the\n   caudal fin rays in most fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyracoid', 'a.', 'Of or pertaining to the Hyracoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyracoid', 'n.', 'One of the Hyracoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyracoidea', 'n. pl.', 'An order of small hoofed mammals, comprising the\n   single living genus Hyrax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyrax', 'n.', 'Any animal of the genus Hyrax, of which about four species\n   are known. They constitute the order Hyracoidea. The best known species\n   are the daman (H. Syriacus) of Palestine, and the klipdas (H. capensis)\n   of South Africa. Other species are H. arboreus and H. Sylvestris, the\n   former from Southern, and the latter from Western, Africa. See Daman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyrcanian', 'a.', 'Alt. of Hyrcan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyrcan', 'a.', 'Of or pertaining to Hyrcania, an ancient country or\n   province of Asia, southeast of the Caspian (which was also called the\n   Hyrcanian) Sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyrse', 'n.', 'Millet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyrst', 'n.', 'A wood. See Hurst.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyson', 'n.', 'A fragrant kind of green tea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hyssop', 'n.', 'A plant (Hyssopus officinalis). The leaves have an aromatic\n   smell, and a warm, pungent taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hysteranthous', 'a.', 'Having the leaves expand after the flowers have\n   opened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hysteresis', 'n.', 'A lagging or retardation of the effect, when the forces\n   acting upon a body are changed, as if from velocity or internal\n   friction; a temporary resistance to change from a condition previously\n   induced, observed in magnetism, thermoelectricity, etc., on reversal of\n   polarity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hysteria', 'n.', 'A nervous affection, occurring almost exclusively in\n   women, in which the emotional and reflex excitability is exaggerated,\n   and the will power correspondingly diminished, so that the patient\n   loses control over the emotions, becomes the victim of imaginary\n   sensations, and often falls into paroxism or fits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hysteric', 'a.', 'Alt. of Hysterical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hysterical', 'a.', 'Of or pertaining to hysteria; affected, or troubled,\n   with hysterics; convulsive, fitful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hysterics', 'n. pl.', 'Hysteria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hysteroepilepsy', 'n.', 'A disease resembling hysteria in its nature, and\n   characterized by the occurrence of epileptiform convulsions, which can\n   often be controlled or excited by pressure on the ovaries, and upon\n   other definite points in the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hysterogenic', 'a.', 'Producing hysteria; as, the hysterogenicpressure\n   points on the surface of the body, pressure upon which is said both to\n   produce and arrest an attack of hysteria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hysterology', 'n.', 'A figure by which the ordinary course of thought is\n   inverted in expression, and the last put first; -- called also hysteron\n   proteron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hysteron proteron', '', 'A figure in which the natural order of sense is\n   reversed; hysterology; as, valet atque vivit, \"he is well and lives.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hysteron proteron', '', 'An inversion of logical order, in which the\n   conclusion is put before the premises, or the thing proved before the\n   evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hysterophyte', 'n.', 'A plant, like the fungus, which lives on dead or\n   living organic matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hysterotomy', 'n.', 'The Caesarean section. See under Caesarean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hystricine', 'a.', 'Like or pertaining to the porcupines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hystricomorphous', 'a.', 'Like, or allied to, the porcupines; -- said of a\n   group (Hystricomorpha) of rodents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hystrix', 'n.', 'A genus of rodents, including the porcupine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Hythe', 'n.', 'A small haven. See Hithe.\n   I () I, the ninth letter of the English alphabet, takes its form from\n   the Phoenician, through the Latin and the Greek. The Phoenician letter\n   was probably of Egyptian origin. Its original value was nearly the same\n   as that of the Italian I, or long e as in mete. Etymologically I is\n   most closely related to e, y, j, g; as in dint, dent, beverage, L.\n   bibere; E. kin, AS. cynn; E. thin, AS. /ynne; E. dominion, donjon,\n   dungeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'I', '', 'In our old authors, I was often used for ay (or aye), yes, which\n   is pronounced nearly like it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'I', '', 'As a numeral, I stands for 1, II for 2, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'I-', 'prefix.', 'See Y-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'We', 'pl. ', 'of I');
  INSERT INTO words (word, word_type, description) VALUES  (    'Our', 'pl. ', 'of I');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ours', 'pl. ', 'of I');
  INSERT INTO words (word, word_type, description) VALUES  (    'Us', 'pl. ', 'of I');
  INSERT INTO words (word, word_type, description) VALUES  (    'I', 'object.', 'The nominative case of the pronoun of the first person; the\n   word with which a speaker or writer denotes himself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iamatology', 'n.', 'Materia Medica; that branch of therapeutics which\n   treats of remedies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iamb', 'n.', 'An iambus or iambic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iambic', 'a.', 'Consisting of a short syllable followed by a long one, or\n   of an unaccented syllable followed by an accented; as, an iambic foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iambic', 'a.', 'Pertaining to, or composed of, iambics; as, an iambic\n   verse; iambic meter. See Lambus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iambic', 'n.', 'An iambic foot; an iambus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iambic', 'n.', 'A verse composed of iambic feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iambic', 'n.', 'A satirical poem (such poems having been anciently written\n   in iambic verse); a satire; a lampoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iambical', 'a.', 'Iambic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iambically', 'adv.', 'In a iambic manner; after the manner of iambics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iambize', 'v. t.', 'To satirize in iambics; to lampoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iambi', 'pl. ', 'of Iambus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iambuses', 'pl. ', 'of Iambus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iambus', 'n.', 'A foot consisting of a short syllable followed by a long\n   one, as in /mans, or of an unaccented syllable followed by an accented\n   one, as invent; an iambic. See the Couplet under Iambic, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ianthinae', 'pl. ', 'of Ianthina');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ianthinas', 'pl. ', 'of Ianthina');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ianthina', 'n.', 'Any gastropod of the genus Ianthina, of which various\n   species are found living in mid ocean; -- called also purple shell, and\n   violet snail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iatraliptic', 'a.', 'Treating diseases by anointing and friction; as, the\n   iatraliptic method.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iatric', 'a.', 'Alt. of Iatrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iatrical', 'a.', 'Of or pertaining to medicine, or to medical men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iatrochemical', 'a.', 'Of or pertaining to iatrochemistry, or to the\n   iatrochemists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iatrochemist', 'n.', 'A physician who explained or treated diseases upon\n   chemical principles; one who practiced iatrochemistry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iatrochemistry', 'n.', 'Chemistry applied to, or used in, medicine; -- used\n   especially with reference to the doctrines in the school of physicians\n   in Flanders, in the 17th century, who held that health depends upon the\n   proper chemical relations of the fluids of the body, and who endeavored\n   to explain the conditions of health or disease by chemical principles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iatromathematical', 'a.', 'Of or pertaining to iatromathematicians or their\n   doctrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iatromathematician', 'n.', 'One of a school of physicians in Italy, about\n   the middle of the 17th century, who tried to apply the laws of\n   mechanics and mathematics to the human body, and hence were eager\n   student of anatomy; -- opposed to the iatrochemists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iberian', 'a.', 'Of or pertaining to Iberia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ibexes', 'pl. ', 'of Ibex');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ibices', 'pl. ', 'of Ibex');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ibex', 'n.', 'One of several species of wild goats having very large,\n   recurved horns, transversely ridged in front; -- called also steinbok.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ibidem', 'adv.', 'In the same place; -- abbreviated ibid. or ib.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ibis', 'n.', 'Any bird of the genus Ibis and several allied genera, of the\n   family Ibidae, inhabiting both the Old World and the New. Numerous\n   species are known. They are large, wading birds, having a long, curved\n   beak, and feed largely on reptiles.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ible', '', 'See -able.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ic', '', 'A suffix signifying, in general, relating to, or characteristic\n   of; as, historic, hygienic, telegraphic, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ic', '', 'A suffix, denoting that the element indicated enters into\n   certain compounds with its highest valence, or with a valence\n   relatively higher than in compounds where the name of the element ends\n   in -ous; as, ferric, sulphuric. It is also used in the general sense of\n   pertaining to; as, hydric, sodic, calcic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Icarian', 'a.', 'Soaring too high for safety, like Icarus; adventurous in\n   flight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ice', 'n.', 'Water or other fluid frozen or reduced to the solid state by\n   cold; frozen water. It is a white or transparent colorless substance,\n   crystalline, brittle, and viscoidal. Its specific gravity (0.92, that\n   of water at 4 C. being 1.0) being less than that of water, ice floats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ice', 'n.', 'Concreted sugar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ice', 'n.', 'Water, cream, custard, etc., sweetened, flavored, and\n   artificially frozen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ice', 'n.', 'Any substance having the appearance of ice; as, camphor ice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iced', 'imp. & p. p.', 'of Ice');
  INSERT INTO words (word, word_type, description) VALUES  (    'Icing', 'p. pr. & vb. n.', 'of Ice');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ice', 'v. t.', 'To cover with ice; to convert into ice, or into something\n   resembling ice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ice', 'v. t.', 'To cover with icing, or frosting made of sugar and milk or\n   white of egg; to frost, as cakes, tarts, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ice', 'v. t.', 'To chill or cool, as with ice; to freeze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iceberg', 'n.', 'A large mass of ice, generally floating in the ocean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Icebird', 'n.', 'An Arctic sea bird, as the Arctic fulmar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Icebound', 'a.', 'Totally surrounded with ice, so as to be incapable of\n   advancing; as, an icebound vessel; also, surrounded by or fringed with\n   ice so as to hinder easy access; as, an icebound coast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ice-built', 'a.', 'Composed of ice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ice-built', 'a.', 'Loaded with ice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iced', 'a.', 'Covered with ice; chilled with ice; as, iced water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iced', 'a.', 'Covered with something resembling ice, as sugar icing;\n   frosted; as, iced cake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Icefall', 'n.', 'A frozen waterfall, or mass of ice resembling a frozen\n   waterfall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Icelander', 'n.', 'A native, or one of the Scandinavian people, of Iceland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Icelandic', 'a.', 'Of or pertaining to Iceland; relating to, or resembling,\n   the Icelanders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Icelandic', 'n.', 'The language of the Icelanders. It is one of the\n   Scandinavian group, and is more nearly allied to the Old Norse than any\n   other language now spoken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iceland moss', '', 'A kind of lichen (Cetraria Icelandica) found from the\n   Arctic regions to the North Temperate zone. It furnishes a nutritious\n   jelly and other forms of food, and is used in pulmonary complaints as a\n   demulcent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iceland spar', '', 'A transparent variety of calcite, the best of which is\n   obtained in Iceland. It is used for the prisms of the polariscope,\n   because of its strong double refraction. Cf. Calcite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Icemen', 'pl. ', 'of Iceman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iceman', 'n.', 'A man who is skilled in traveling upon ice, as among\n   glaciers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iceman', 'n.', 'One who deals in ice; one who retails or delivers ice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ice plant', '', 'A plant (Mesembryanthemum crystallinum), sprinkled with\n   pellucid, watery vesicles, which glisten like ice. It is native along\n   the Mediterranean, in the Canaries, and in South Africa. Its juice is\n   said to be demulcent and diuretic; its ashes are used in Spain in\n   making glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Icequake', 'n.', 'The crash or concussion attending the breaking up of\n   masses of ice, -- often due to contraction from extreme cold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ich', 'pron.', 'I.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichneumon', 'n.', 'Any carnivorous mammal of the genus Herpestes, and\n   family Viverridae. Numerous species are found in Asia and Africa. The\n   Egyptian species(H. ichneumon), which ranges to Spain and Palestine, is\n   noted for destroying the eggs and young of the crocodile as well as\n   various snakes and lizards, and hence was considered sacred by the\n   ancient Egyptians. The common species of India (H. griseus), known as\n   the mongoose, has similar habits and is often domesticated. It is noted\n   for killing the cobra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichneumon', 'n.', 'Any hymenopterous insect of the family Ichneumonidae, of\n   which several thousand species are known, belonging to numerous genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichneumonidan', 'a.', 'Of or pertaining to the Ichneumonidae, or ichneumon\n   flies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichneumonidan', 'n.', 'One of the Ichneumonidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichneumonides', 'n. pl.', 'The ichneumon flies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichnite', 'n.', 'A fossil footprint; as, the ichnites in the Triassic\n   sandstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichnographic', 'a.', 'Alt. of Ichnographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichnographical', 'a.', 'Of or pertaining to ichonography; describing a\n   ground plot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichnography', 'n.', 'A horizontal section of a building or other object,\n   showing its true dimensions according to a geometric scale; a ground\n   plan; a map; also, the art of making such plans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichnolite', 'n.', 'A fossil footprint; an ichnite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichnolithology', 'n.', 'Same as Ichnology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichnological', 'a.', 'Of or pertaining to ichnology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichnology', 'n.', 'The branch of science which treats of fossil footprints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichnoscopy', 'n.', 'The search for the traces of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichor', 'n.', 'An ethereal fluid that supplied the place of blood in the\n   veins of the gods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichor', 'n.', 'A thin, acrid, watery discharge from an ulcer, wound, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichorhaemia', 'n.', 'Infection of the blood with ichorous or putrid\n   substances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichorous', 'a.', 'Of or like ichor; thin; watery; serous; sanious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthidin', 'n.', 'A substance from the egg yolk of osseous fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthin', 'n.', 'A nitrogenous substance resembling vitellin, present in\n   the egg yolk of cartilaginous fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthulin', 'n.', 'A substance from the yolk of salmons egg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthus', 'n.', 'In early Christian and eccesiastical art, an emblematic\n   fish, or the Greek word for fish, which combined the initials of the\n   Greek words /, /, / /, /, Jesus, Christ, Son of God, Savior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthyic', 'a.', 'Like, or pertaining to, fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthyocol', 'n.', 'Alt. of Ichthyocolla');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthyocolla', 'n.', 'Fish glue; isinglass; a glue prepared from the sounds\n   of certain fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthyocoprolite', 'n.', 'Fossil dung of fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthyodorulite', 'n.', 'One of the spiny plates foundon the back and tail\n   of certain skates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthyography', 'n.', 'A treatise on fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthyoid', 'a.', 'Alt. of Ichthyoidal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthyoidal', 'a.', 'Somewhat like a fish; having some of the\n   characteristics of fishes; -- said of some amphibians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthyolatry', 'n.', 'Worship of fishes, or of fish-shaped idols.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthyolite', 'n.', 'A fossil fish, or fragment of a fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthyologic', 'a.', 'Alt. of Ichthyological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthyological', 'a.', 'Of or pertaining to ichthyology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthyologist', 'n.', 'One versed in, or who studies, ichthyology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthyology', 'n.', 'The natural history of fishes; that branch of zoology\n   which relates to fishes, including their structure, classification, and\n   habits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthyomancy', 'n.', 'Divination by the heads or the entrails of fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthyomorpha', 'n. pl.', 'The Urodela.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthyomorphic', 'a.', 'Alt. of Ichthyomorphous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthyomorphous', 'a.', 'Fish-shaped; as, the ichthyomorphic idols of\n   ancient Assyria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthyophagist', 'n.', 'One who eats, or subsists on, fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthyophagous', 'a.', 'Eating, or subsisting on, fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthyohagy', 'n.', 'The practice of eating, or living upon, fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthyophthalmite', 'n.', 'See Apophyllite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthyophthira', 'n. pl.', 'A division of copepod crustaceans, including\n   numerous species parasitic on fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthyopsida', 'n. pl.', 'A grand division of the Vertebrata, including the\n   Amphibia and Fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthyopterygia', 'n. pl.', 'See Ichthyosauria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthyopterygium', 'n.', 'The typical limb, or lateral fin, of fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthyornis', 'n.', 'An extinct genus of toothed birds found in the\n   American Cretaceous formation. It is remarkable for having biconcave\n   vertebrae, and sharp, conical teeth set in sockets. Its wings were well\n   developed. It is the type of the order Odontotormae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthyosaur', 'n.', 'One of the Ichthyosaura.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthyosauria', 'n. pl.', 'An extinct order of marine reptiles, including\n   Ichthyosaurus and allied forms; -- called also Ichthyopterygia. They\n   have not been found later than the Cretaceous period.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthyosaurian', 'a.', 'Of or pertaining to the Ichthyosauria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthyosaurian', 'n.', 'One of the Ichthyosauria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthyosauri', 'pl. ', 'of Ichthyosaurus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthyosaurus', 'n.', 'An extinct genus of marine reptiles; -- so named\n   from their short, biconcave vertebrae, resembling those of fishes.\n   Several species, varying in length from ten to thirty feet, are known\n   from the Liassic, Oolitic, and Cretaceous formations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthyosis', 'n.', 'A disease in which the skin is thick, rough, and scaly;\n   -- called also fishskin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthyotomist', 'n.', 'One skilled in ichthyotomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthyoomy', 'n.', 'The anatomy or dissection of fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ichthys', 'n.', 'Same as Ichthus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Icicle', 'n.', 'A pendent, and usually conical, mass of ice, formed by\n   freezing of dripping water; as, the icicles on the eaves of a house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Icicled', 'a.', 'Having icicles attached.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Icily', 'adv.', 'In an icy manner; coldly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iciness', 'n.', 'The state or quality of being icy or very cold; frigidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Icing', 'n.', 'A coating or covering resembling ice, as of sugar and milk\n   or white of egg; frosting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ickle', 'n.', 'An icicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Icon', 'n.', 'An image or representation; a portrait or pretended portrait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iconical', 'a.', 'Pertaining to, or consisting of, images, pictures, or\n   representations of any kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iconism', 'n.', 'The formation of a figure, representation, or semblance; a\n   delineation or description.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iconize', 'v. t.', 'To form an image or likeness of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iconoclasm', 'n.', 'The doctrine or practice of the iconoclasts; image\n   breaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iconoclast', 'n.', 'A breaker or destroyer of images or idols; a determined\n   enemy of idol worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iconoclast', 'n.', 'One who exposes or destroys impositions or shams; one\n   who attacks cherished beliefs; a radical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iconoclastic', 'a.', 'Of or pertaining to the iconoclasts, or to image\n   breaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iconodule', 'n.', 'Alt. of Iconodulist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iconodulist', 'n.', 'One who serves images; -- opposed to an iconoclast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iconographer', 'n.', 'A maker of images.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iconographic', 'a.', 'Of or pertaining to iconography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iconographic', 'a.', 'Representing by means of pictures or diagrams; as, an\n   icongraphic encyclopaedia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iconography', 'n.', 'The art or representation by pictures or images; the\n   description or study of portraiture or representation, as of persons;\n   as, the iconography of the ancients.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iconography', 'n.', 'The study of representative art in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iconolater', 'n.', 'One who worships images.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iconolatry', 'n.', 'The worship of images as symbols; -- distinguished from\n   idolatry, the worship of images themselves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iconology', 'n.', 'The discussion or description of portraiture or of\n   representative images. Cf. Iconography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iconomachy', 'n.', 'Hostility to images as objects of worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iconomical', 'a.', 'Opposed to pictures or images as objects of worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iconophilist', 'n.', 'A student, or lover of the study, of iconography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Icosahedral', 'a.', 'Having twenty equal sides or faces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Icosahedron', 'n.', 'A solid bounded by twenty sides or faces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Icosandria', 'n. pl.', 'A Linnaean class of plants, having twenty or more\n   stamens inserted in the calyx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Icosandrian', 'a.', 'Alt. of Icosandrous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Icosandrous', 'a.', 'Pertaining to the class Icosandria; having twenty or\n   more stamens inserted in the calyx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Icositetrahedron', 'n.', 'A twenty-four-sided solid; a tetragonal\n   trisoctahedron or trapezohedron.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ics', '', 'A suffix used in forming the names of certain sciences,\n   systems, etc., as acoustics, mathematics, dynamics, statistics,\n   politics, athletics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Icteric', 'n.', 'A remedy for the jaundice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Icteric', 'a.', 'Alt. of Icterical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Icterical', 'a.', 'Pertaining to, or affected with, jaundice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Icterical', 'a.', 'Good against the jaundice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Icteritious', 'a.', 'Alt. of Icteritous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Icteritous', 'a.', 'Yellow; of the color of the skin when it is affected by\n   the jaundice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Icteroid', 'a.', 'Of a tint resembling that produced by jaundice; yellow;\n   as, an icteroid tint or complexion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Icterus', 'a.', 'The jaundice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ictic', 'a.', 'Pertaining to, or caused by, a blow; sudden; abrupt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ictus', 'n.', 'The stress of voice laid upon accented syllable of a word.\n   Cf. Arsis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ictus', 'n.', 'A stroke or blow, as in a sunstroke, the sting of an insect,\n   pulsation of an artery, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Icy', 'superl.', 'Pertaining to, resembling, or abounding in, ice; cold;\n   frosty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Icy', 'superl.', 'Characterized by coldness, as of manner, influence, etc.;\n   chilling; frigid; cold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Icy-pearled', 'a.', 'Spangled with ice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Id', '', 'A contraction from I would or I had.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Id', 'n.', 'A small fresh-water cyprinoid fish (Leuciscus idus or Idus\n   idus) of Europe. A domesticated variety, colored like the goldfish, is\n   called orfe in Germany.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idalian', 'a.', 'Of or pertaining to Idalium, a mountain city in Cyprus, or\n   to Venus, to whom it was sacred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ide', 'n.', 'Same as Id.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ide', '', 'A suffix used to denote: (a) The nonmetallic, or negative,\n   element or radical in a binary compound; as, oxide, sulphide, chloride.\n   (b) A compound which is an anhydride; as, glycolide, phthalide. (c) Any\n   one of a series of derivatives; as, indogenide, glucoside, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ideas', 'pl. ', 'of Idea');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idea', 'n.', 'The transcript, image, or picture of a visible object, that\n   is formed by the mind; also, a similar image of any object whatever,\n   whether sensible or spiritual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idea', 'n.', 'A general notion, or a conception formed by generalization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idea', 'n.', 'Hence: Any object apprehended, conceived, or thought of, by\n   the mind; a notion, conception, or thought; the real object that is\n   conceived or thought of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idea', 'n.', 'A belief, option, or doctrine; a characteristic or\n   controlling principle; as, an essential idea; the idea of development.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idea', 'n.', 'A plan or purpose of action; intention; design.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idea', 'n.', 'A rational conception; the complete conception of an object\n   when thought of in all its essential elements or constituents; the\n   necessary metaphysical or constituent attributes and relations, when\n   conceived in the abstract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idea', 'n.', 'A fiction object or picture created by the imagination; the\n   same when proposed as a pattern to be copied, or a standard to be\n   reached; one of the archetypes or patterns of created things, conceived\n   by the Platonists to have excited objectively from eternity in the mind\n   of the Deity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ideal', 'a.', 'Existing in idea or thought; conceptional; intellectual;\n   mental; as, ideal knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ideal', 'a.', 'Reaching an imaginary standard of excellence; fit for a\n   model; faultless; as, ideal beauty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ideal', 'a.', 'Existing in fancy or imagination only; visionary; unreal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ideal', 'a.', 'Teaching the doctrine of idealism; as, the ideal theory or\n   philosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ideal', 'a.', 'Imaginary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ideal', 'n.', 'A mental conception regarded as a standard of perfection; a\n   model of excellence, beauty, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idealess', 'a.', 'Destitute of an idea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idealism', 'n.', 'The quality or state of being ideal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idealism', 'n.', 'Conception of the ideal; imagery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idealism', 'n.', 'The system or theory that denies the existence of\n   material bodies, and teaches that we have no rational grounds to\n   believe in the reality of anything but ideas and their relations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idealist', 'n.', 'One who idealizes; one who forms picturesque fancies; one\n   given to romantic expectations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idealist', 'n.', 'One who holds the doctrine of idealism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idealistic', 'a.', 'Of or pertaining to idealists or their theories.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idealities', 'pl. ', 'of Ideality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ideality', 'n.', 'The quality or state of being ideal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ideality', 'n.', 'The capacity to form ideals of beauty or perfection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ideality', 'n.', 'The conceptive faculty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idealization', 'n.', 'The act or process of idealizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idealization', 'n.', 'The representation of natural objects, scenes, etc.,\n   in such a way as to show their most important characteristics; the\n   study of the ideal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idealized', 'imp. & p. p.', 'of Idealize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idealizing', 'p. pr. & vb. n.', 'of Idealize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idealize', 'v. t.', 'To make ideal; to give an ideal form or value to; to\n   attribute ideal characteristics and excellences to; as, to idealize\n   real life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idealize', 'v. t.', 'To treat in an ideal manner. See Idealization, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idealize', 'v. i.', 'To form ideals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idealizer', 'n.', 'An idealist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ideally', 'adv.', 'In an ideal manner; by means of ideals; mentally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idealogic', 'a.', 'Of or pertaining to an idealogue, or to idealization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idealogue', 'n.', 'One given to fanciful ideas or theories; a theorist; a\n   spectator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ideat', 'n.', 'Alt. of Ideate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ideate', 'n.', 'The actual existence supposed to correspond with an idea;\n   the correlate in real existence to the idea as a thought or existence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ideate', 'v. t.', 'To form in idea; to fancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ideate', 'v. t.', 'To apprehend in thought so as to fix and hold in the\n   mind; to memorize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ideation', 'n.', 'The faculty or capacity of the mind for forming ideas;\n   the exercise of this capacity; the act of the mind by which objects of\n   sense are apprehended and retained as objects of thought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ideational', 'a.', 'Pertaining to, or characterized by, ideation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idem', 'pron. / adj.', 'The same; the same as above; -- often abbreviated\n   id.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Identic', 'a.', 'Identical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Identical', 'a.', 'The same; the selfsame; the very same; not different;\n   as, the identical person or thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Identical', 'a.', 'Uttering sameness or the same truth; expressing in the\n   predicate what is given, or obviously implied, in the subject;\n   tautological.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Identically', 'adv.', 'In an identical manner; with respect to identity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Identicalness', 'n.', 'The quality or state of being identical; sameness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Identifiable', 'a.', 'Capable of being identified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Identification', 'n.', 'The act of identifying, or proving to be the same;\n   also, the state of being identified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Identified', 'imp. & p. p.', 'of Identify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Identifying', 'p. pr. & vb. n.', 'of Identify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Identify', 'v. t.', 'To make to be the same; to unite or combine in such a\n   manner as to make one; to treat as being one or having the same purpose\n   or effect; to consider as the same in any relation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Identify', 'v. t.', 'To establish the identity of; to prove to be the same\n   with something described, claimed, or asserted; as, to identify stolen\n   property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Identify', 'v. i.', 'To become the same; to coalesce in interest, purpose,\n   use, effect, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Identism', 'n.', 'The doctrine taught by Schelling, that matter and mind,\n   and subject and object, are identical in the Absolute; -- called also\n   the system / doctrine of identity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Identities', 'pl. ', 'of Identity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Identity', 'n.', 'The state or quality of being identical, or the same;\n   sameness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Identity', 'n.', 'The condition of being the same with something described\n   or asserted, or of possessing a character claimed; as, to establish the\n   identity of stolen goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Identity', 'n.', 'An identical equation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ideo-', '', 'A combining form from the Gr. /, an idea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ideogenical', 'a.', 'Of or relating to ideology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ideogeny', 'n.', 'The science which treats of the origin of ideas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ideogram', 'n.', 'An original, pictorial element of writing; a kind of\n   hieroglyph expressing no sound, but only an idea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ideogram', 'n.', 'A symbol used for convenience, or for abbreviation; as,\n   1, 2, 3, +, -, /, $, /, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ideogram', 'n.', 'A phonetic symbol; a letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ideograph', 'n.', 'Same as Ideogram.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ideographic', 'a.', 'Alt. of Ideographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ideographical', 'a.', 'Of or pertaining to an ideogram; representing ideas\n   by symbols, independently of sounds; as, 9 represents not the word\n   \"nine,\" but the idea of the number itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ideographics', 'n.', 'The system of writing in ideographic characters;\n   also, anything so written.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ideography', 'n.', 'The representation of ideas independently of sounds, or\n   in an ideographic manner, as sometimes is done in shorthand writing,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ideological', 'a.', 'Of or pertaining to ideology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ideologist', 'n.', 'One who treats of ideas; one who theorizes or\n   idealizes; one versed in the science of ideas, or who advocates the\n   doctrines of ideology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ideology', 'n.', 'The science of ideas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ideology', 'n.', 'A theory of the origin of ideas which derives them\n   exclusively from sensation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ideo-motion', 'n.', 'An ideo-motor movement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ideo-motor', 'a.', 'Applied to those actions, or muscular movements, which\n   are automatic expressions of dominant ideas, rather than the result of\n   distinct volitional efforts, as the act of expressing the thoughts in\n   speech, or in writing, while the mind is occupied in the composition of\n   the sentence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ides', 'n. pl.', 'The fifteenth day of March, May, July, and October, and\n   the thirteenth day of the other months.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idio-', '', 'A combining form from the Greek /, meaning private, personal,\n   peculiar, distinct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idioblast', 'n.', 'An individual cell, differing greatly from its\n   neighbours in regard to size, structure, or contents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiocrasis', 'n.', 'Idiocracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiocrasies', 'pl. ', 'of Idiocracy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiocracy', 'n.', 'Peculiarity of constitution; that temperament, or state\n   of constitution, which is peculiar to a person; idiosyncrasy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiocratic', 'a.', 'Alt. of Idiocratical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiocratical', 'a.', 'Peculiar in constitution or temperament;\n   idiosyncratic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiocy', 'n.', 'The condition or quality of being an idiot; absence, or\n   marked deficiency, of sense and intelligence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiocyclophanous', 'a.', 'Same as Idiophanous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idioelectric', 'a.', 'Electric by virtue of its own peculiar properties;\n   capable of becoming electrified by friction; -- opposed to anelectric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idioelectric', 'n.', 'An idioelectric substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiograph', 'n.', 'A mark or signature peculiar to an individual; a\n   trade-mark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiographic', 'a.', 'Alt. of Idiographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiographical', 'a.', 'Of or pertaining to an idiograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiolatry', 'n.', 'Self-worship; excessive self-esteem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiom', 'n.', 'The syntactical or structural form peculiar to any language;\n   the genius or cast of a language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiom', 'n.', 'An expression conforming or appropriate to the peculiar\n   structural form of a language; in extend use, an expression sanctioned\n   by usage, having a sense peculiar to itself and not agreeing with the\n   logical sense of its structural form; also, the phrase forms peculiar\n   to a particular author.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiom', 'n.', 'Dialect; a variant form of a language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiomatic', 'a.', 'Alt. of Idiomatical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiomatical', 'a.', 'Of or pertaining to, or conforming to, the mode of\n   expression peculiar to a language; as, an idiomatic meaning; an\n   idiomatic phrase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiomorphic', 'a.', 'Idiomorphous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiomorphous', 'a.', 'Having a form of its own.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiomorphous', 'a.', 'Apperaing in distinct crystals; -- said of the\n   mineral constituents of a rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiomuscular', 'a.', 'Applied to a semipermanent contraction of a muscle,\n   produced by a mechanical irritant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiopathetic', 'a.', 'Idiopathic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiopathic', 'a.', 'Alt. of Idiopathical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiopathical', 'a.', 'Pertaining to idiopathy; characterizing a disease\n   arising primarily, and not in consequence of some other disease or\n   injury; -- opposed to symptomatic, sympathetic, and traumatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiopathies', 'pl. ', 'of Idiopathy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiopathy', 'n.', 'A peculiar, or individual, characteristic or affection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiopathy', 'n.', 'A morbid state or condition not preceded or occasioned\n   by any other disease; a primary disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiophanous', 'a.', 'Exhibiting interference figures without the aid of a\n   polariscope, as certain crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idioplasm', 'n.', 'Same as Idioplasma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idioplasma', 'n.', 'That portion of the cell protoplasm which is the seat\n   of all active changes, and which carries on the function of hereditary\n   transmission; -- distinguished from the other portion, which is termed\n   nutritive plasma. See Hygroplasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiorepulsive', 'a.', 'Repulsive by itself; as, the idiorepulsive power of\n   heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiosyncrasies', 'pl. ', 'of Idiosyncrasy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiosyncrasy', 'n.', 'A peculiarity of physical or mental constitution or\n   temperament; a characteristic belonging to, and distinguishing, an\n   individual; characteristic susceptibility; idiocrasy; eccentricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiosyncratic', 'a.', 'Alt. of Idiosyncratical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiosyncratical', 'a.', 'Of peculiar temper or disposition; belonging to\n   ones peculiar and individual character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiot', 'n.', 'A man in private station, as distinguished from one holding\n   a public office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiot', 'n.', 'An unlearned, ignorant, or simple person, as distinguished\n   from the educated; an ignoramus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiot', 'n.', 'A human being destitute of the ordinary intellectual powers,\n   whether congenital, developmental, or accidental; commonly, a person\n   without understanding from birth; a natural fool; a natural; an\n   innocent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiot', 'n.', 'A fool; a simpleton; -- a term of reproach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiotcy', 'n.', 'Idiocy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idioted', 'a.', 'Rendered idiotic; befooled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiothermic', 'a.', 'Self-heating; warmed, as the body of animal, by\n   process going on within itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiotic', 'a.', 'Alt. of Idiotical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiotical', 'a.', 'Common; simple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiotical', 'a.', 'Pertaining to, or like, an idiot; characterized by\n   idiocy; foolish; fatuous; as, an idiotic person, speech, laugh, or\n   action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiotically', 'adv.', 'In a idiotic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idioticon', 'n.', 'A dictionary of a peculiar dialect, or of the words and\n   phrases peculiar to one part of a country; a glossary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiotish', 'a.', 'Like an idiot; foolish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiotism', 'n.', 'An idiom; a form, mode of expression, or signification,\n   peculiar to a language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiotism', 'n.', 'Lack of knowledge or mental capacity; idiocy;\n   foolishness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiotize', 'v. i.', 'To become stupid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idiotry', 'n.', 'Idiocy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idle', 'superl.', 'Of no account; useless; vain; trifling; unprofitable;\n   thoughtless; silly; barren.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idle', 'superl.', 'Not called into active service; not turned to\n   appropriate use; unemployed; as, idle hours.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idle', 'superl.', 'Not employed; unoccupied with business; inactive; doing\n   nothing; as, idle workmen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idle', 'superl.', 'Given rest and ease; averse to labor or employment;\n   lazy; slothful; as, an idle fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idle', 'superl.', 'Light-headed; foolish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idled', 'imp. & p. p.', 'of Idle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idling', 'p. pr. & vb. n.', 'of Idle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idle', 'v. i.', 'To lose or spend time in inaction, or without being\n   employed in business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idle', 'v. t.', 'To spend in idleness; to waste; to consume; -- often\n   followed by away; as, to idle away an hour a day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idle-headed', 'a.', 'Foolish; stupid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idle-headed', 'a.', 'Delirious; infatuated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idleness', 'n.', 'The condition or quality of being idle (in the various\n   senses of that word); uselessness; fruitlessness; triviality;\n   inactivity; laziness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idle-pated', 'a.', 'Idle-headed; stupid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idler', 'n.', 'One who idles; one who spends his time in inaction; a lazy\n   person; a sluggard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idler', 'n.', 'One who has constant day duties on board ship, and keeps no\n   regular watch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idler', 'n.', 'An idle wheel or pulley. See under Idle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idless', 'n.', 'Alt. of Idlesse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idlesse', 'n.', 'Idleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idly', 'adv.', 'In a idle manner; ineffectually; vainly; lazily;\n   carelessly; (Obs.) foolishly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idocrase', 'n.', 'Same as Vesuvianite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idol', 'n.', 'An image or representation of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idol', 'n.', 'An image of a divinity; a representation or symbol of a deity\n   or any other being or thing, made or used as an object of worship; a\n   similitude of a false god.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idol', 'n.', 'That on which the affections are strongly (often excessively)\n   set; an object of passionate devotion; a person or thing greatly loved\n   or adored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idol', 'n.', 'A false notion or conception; a fallacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idolastre', 'n.', 'An idolater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idolater', 'n.', 'A worshiper of idols; one who pays divine honors to\n   images, statues, or representations of anything made by hands; one who\n   worships as a deity that which is not God; a pagan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idolater', 'n.', 'An adorer; a great admirer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idolatress', 'n.', 'A female worshiper of idols.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idolatrical', 'a.', 'Idolatrous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idolatrized', 'imp. & p. p.', 'of Idolatrize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idolatrizing', 'p. pr. & vb. n.', 'of Idolatrize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idolatrize', 'v. i.', 'To worship idols; to pay idolatrous worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idolatrize', 'v. t.', 'To make in idol of; to idolize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idolatrous', 'a.', 'Of or pertaining to idolatry; partaking of the nature\n   of idolatry; given to idolatry or the worship of false gods; as,\n   idolatrous sacrifices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idolatrous', 'a.', 'Consisting in, or partaking of, an excessive attachment\n   or reverence; as, an idolatrous veneration for antiquity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idolatrously', 'adv.', 'In a idolatrous manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idolatries', 'pl. ', 'of Idolatry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idolatry', 'n.', 'The worship of idols, images, or anything which is not\n   God; the worship of false gods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idolatry', 'n.', 'Excessive attachment or veneration for anything; respect\n   or love which borders on adoration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idolish', 'a.', 'Idolatrous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idolism', 'n.', 'The worship of idols.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idolist', 'n.', 'A worshiper of idols.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idolized', 'imp. & p. p.', 'of Idolize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idolizing', 'p. pr. & vb. n.', 'of Idolize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idolize', 'v. t.', 'To make an idol of; to pay idolatrous worship to; as,\n   to idolize the sacred bull in Egypt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idolize', 'v. t.', 'To love to excess; to love or reverence to adoration;\n   as, to idolize gold, children, a hero.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idolize', 'v. i.', 'To practice idolatry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idolizer', 'n.', 'One who idolizes or loves to the point of reverence; an\n   idolater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idoloclast', 'n.', 'A breaker of idols; an iconoclast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idolographical', 'a.', 'Descriptive of idols.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idolous', 'a.', 'Idolatrous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idoneous', 'a.', 'Appropriate; suitable; proper; fit; adequate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idorgan', 'n.', 'A morphological unit, consisting of two or more plastids,\n   which does not possess the positive character of the person or stock,\n   in distinction from the physiological organ or biorgan. See Morphon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idrialine', 'n.', 'Alt. of Idrialite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idrialite', 'n.', 'A bituminous substance obtained from the mercury mines\n   of Idria, where it occurs mixed with cinnabar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idumean', 'a.', 'Of or pertaining to ancient Idumea, or Edom, in Western\n   Asia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idumean', 'n.', 'An inhabitant of Idumea, an Edomite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idyl', 'n.', 'A short poem; properly, a short pastoral poem; as, the idyls\n   of Theocritus; also, any poem, especially a narrative or descriptive\n   poem, written in an eleveted and highly finished style; also, by\n   extension, any artless and easily flowing description, either in poetry\n   or prose, of simple, rustic life, of pastoral scenes, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Idyllic', 'a.', 'Of or belonging to idyls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'I. e.', '', 'Abbreviation of Latin id est, that is.');
  INSERT INTO words (word, word_type, description) VALUES  (    'If', 'conj.', 'In case that; granting, allowing, or supposing that; --\n   introducing a condition or supposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'If', 'conj.', 'Whether; -- in dependent questions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'I faith', '', 'In faith; indeed; truly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ifere', 'a.', 'Together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Igasuric', 'a.', 'Pertaining to, or obtained from, nux vomica or St.\n   Ignatiuss bean; as, igasuric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Igasurine', 'n.', 'An alkaloid found in nux vomica, and extracted as a\n   white crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Igloo', 'n.', 'An Eskimo snow house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Igloo', 'n.', 'A cavity, or excavation, made in the snow by a seal, over\n   its breathing hole in the ice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignatius bean', '', 'See Saint Ignatiuss bean, under Saint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Igneous', 'a.', 'Pertaining to, having the nature of, fire; containing\n   fire; resembling fire; as, an igneous appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Igneous', 'a.', 'Resulting from, or produced by, the action of fire; as,\n   lavas and basalt are igneous rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignescent', 'a.', 'Emitting sparks of fire when struck with steel;\n   scintillating; as, ignescent stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignicolist', 'n.', 'A worshiper of fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Igniferous', 'a.', 'Producing fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignifluous', 'a.', 'Flowing with fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignified', 'imp. & p. p.', 'of Ignify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignifying', 'p. pr. & vb. n.', 'of Ignify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignify', 'v. t.', 'To form into fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignigenous', 'a.', 'Produced by the action of fire, as lava.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignipotence', 'n.', 'Power over fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignipotent', 'a.', 'Presiding over fire; also, fiery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignes fatui', 'pl. ', 'of Ignis fatuus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignis fatuus', '', 'A phosphorescent light that appears, in the night, over\n   marshy ground, supposed to be occasioned by the decomposition of animal\n   or vegetable substances, or by some inflammable gas; -- popularly\n   called also Will-with-the-wisp, or Will-o-the-wisp, and\n   Jack-with-a-lantern, or Jack-o-lantern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignis fatuus', '', 'Fig.: A misleading influence; a decoy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignited', 'imp. & p. p.', 'of Ignite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Igniting', 'p. pr. & vb. n.', 'of Ignite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignite', 'v. t.', 'To kindle or set on fire; as, to ignite paper or wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignite', 'v. t.', 'To subject to the action of intense heat; to heat\n   strongly; -- often said of incombustible or infusible substances; as,\n   to ignite iron or platinum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignite', 'v. i.', 'To take fire; to begin to burn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignitible', 'a.', 'Capable of being ignited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignition', 'n.', 'The act of igniting, kindling, or setting on fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignition', 'n.', 'The state of being ignited or kindled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignitor', 'n.', 'One who, or that which, produces ignition; especially, a\n   contrivance for igniting the powder in a torpedo or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignivomous', 'a.', 'Vomiting fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignobility', 'n.', 'Ignobleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignoble', 'a.', 'Of low birth or family; not noble; not illustrious;\n   plebeian; common; humble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignoble', 'a.', 'Not honorable, elevated, or generous; base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignoble', 'a.', 'Not a true or noble falcon; -- said of certain hawks, as\n   the goshawk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignoble', 'v. t.', 'To make ignoble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignobleness', 'n.', 'State or quality of being ignoble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignobly', 'adv.', 'In an ignoble manner; basely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignominious', 'a.', 'Marked with ignominy; in curring public disgrace;\n   dishonorable; shameful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignominious', 'a.', 'Deserving ignominy; despicable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignominious', 'a.', 'Humiliating; degrading; as, an ignominious judgment or\n   sentence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignominiously', 'adv.', 'In an ignominious manner; disgracefully;\n   shamefully; ingloriously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignominies', 'pl. ', 'of Ignominy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignominy', 'n.', 'Public disgrace or dishonor; reproach; infamy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignominy', 'n.', 'An act deserving disgrace; an infamous act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignomy', 'n.', 'Ignominy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignoramus', 'n.', 'We are ignorant; we ignore; -- being the word formerly\n   written on a bill of indictment by a grand jury when there was not\n   sufficient evidence to warrant them in finding it a true bill. The\n   phrase now used is, \"No bill,\" \"No true bill,\" or \"Not found,\" though\n   in some jurisdictions \"Ignored\" is still used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignoramuses', 'pl. ', 'of Ignoramus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignoramus', 'n.', 'A stupid, ignorant person; a vain pretender to\n   knowledge; a dunce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignorance', 'n.', 'The condition of being ignorant; the want of knowledge\n   in general, or in relation to a particular subject; the state of being\n   uneducated or uninformed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignorance', 'n.', 'A willful neglect or refusal to acquire knowledge which\n   one may acquire and it is his duty to have.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignorant', 'a.', 'Destitute of knowledge; uninstructed or uninformed;\n   untaught; unenlightened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignorant', 'a.', 'Unacquainted with; unconscious or unaware; -- used with\n   of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignorant', 'a.', 'Unknown; undiscovered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignorant', 'a.', 'Resulting from ignorance; foolish; silly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignorant', 'n.', 'A person untaught or uninformed; one unlettered or\n   unskilled; an ignoramous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignorantism', 'n.', 'The spirit of those who extol the advantage to\n   ignorance; obscuriantism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignorantist', 'n.', 'One opposed to the diffusion of knowledge; an\n   obscuriantist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignorantly', 'adv.', 'In a ignorant manner; without knowledge;\n   inadvertently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignored', 'imp. & p. p.', 'of Ignore');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignoring', 'p. pr. & vb. n.', 'of Ignore');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignore', 'v. t.', 'To be ignorant of or not acquainted with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignore', 'v. t.', 'To throw out or reject as false or ungrounded; -- said\n   of a bill rejected by a grand jury for want of evidence. See Ignoramus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignore', 'v. t.', 'Hence: To refuse to take notice of; to shut the eyes to;\n   not to recognize; to disregard willfully and causelessly; as, to ignore\n   certain facts; to ignore the presence of an objectionable person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignoscible', 'a.', 'Pardonable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignote', 'a.', 'Unknown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ignote', 'n.', 'One who is unknown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iguana', 'n.', 'Any species of the genus Iguana, a genus of large American\n   lizards of the family Iguanidae. They are arboreal in their habits,\n   usually green in color, and feed chiefly upon fruits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iguanian', 'a.', 'Resembling, or pertaining to, the iguana.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iguanid', 'a.', 'Same as Iguanoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iguanodon', 'n.', 'A genus of gigantic herbivorous dinosaurs having a\n   birdlike pelvis and large hind legs with three-toed feet capable of\n   supporting the entire body. Its teeth resemble those of the iguana,\n   whence its name. Several species are known, mostly from the Wealden of\n   England and Europe. See Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iguanodont', 'a.', 'Like or pertaining to the genus Iguanodon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iguanoid', 'a.', 'Pertaining to the Iguanidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ihlang-ihlang', 'n.', 'A rich, powerful, perfume, obtained from the\n   volatile oil of the flowers of Canada odorata, an East Indian tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ihram', 'n.', 'The peculiar dress worn by pilgrims to Mecca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ik', 'pron.', 'I.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Il-', '', 'A form of the prefix in-, not, and in-, among. See In-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ile', 'n.', 'Ear of corn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ile', 'n.', 'An aisle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ile', 'n.', 'An isle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ileac', 'a.', 'Pertaining to the ileum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ileac', 'a.', 'See Iliac, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ileocaecal', 'a.', 'Pertaining to the ileum and caecum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ileocolic', 'a.', 'Pertaining to the ileum and colon; as, the ileocolic, or\n   ileocaecal, valve, a valve where the ileum opens into the large\n   intestine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ileum', 'n.', 'The last, and usually the longest, division of the small\n   intestine; the part between the jejunum and large intestine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ileum', 'n.', 'See Ilium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ileus', 'n.', 'A morbid condition due to intestinal obstruction. It is\n   characterized by complete constipation, with griping pains in the\n   abdomen, which is greatly distended, and in the later stages by\n   vomiting of fecal matter. Called also ileac, / iliac, passion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ilex', 'n.', 'The holm oak (Quercus Ilex).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ilex', 'n.', 'A genus of evergreen trees and shrubs, including the common\n   holly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iliac', 'a.', 'Pertaining to ancient Ilium, or Troy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iliac', 'a.', 'Pertaining to, or in the region of, the ilium, or dorsal\n   bone of the pelvis; as, the iliac artery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iliac', 'a.', 'See Ileac, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iliacal', 'a.', 'Iliac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'liad', 'n.', 'A celebrated Greek epic poem, in twenty-four books, on the\n   destruction of Ilium, the ancient Troy. The Iliad is ascribed to Homer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ilial', 'a.', 'Pertaining to the ilium; iliac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iliche', 'adv.', 'Alike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ilicic', 'a.', 'Pertaining to, or derived from, the holly (Ilex), and\n   allied plants; as, ilicic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ilicin', 'n.', 'The bitter principle of the holly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ilio-', '', 'A combining form used in anatomy to denote connection with, or\n   relation to, the ilium; as, ilio-femoral, ilio-lumbar, ilio-psoas, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iliofemoral', 'a.', 'Pertaining to the ilium and femur; as, iliofemoral\n   ligaments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iliolumbar', 'a.', 'Pertaining to the iliac and lumbar regions; as, the\n   iliolumbar artery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iliopsoas', 'n.', 'The great flexor muscle of the hip joint, divisible into\n   two parts, the iliac and great psoas, -- often regarded as distinct\n   muscles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ilium', 'n.', 'The dorsal one of the three principal bones comprising\n   either lateral half of the pelvis; the dorsal or upper part of the hip\n   bone. See Innominate bone, under Innominate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ilixanthin', 'n.', 'A yellow dye obtained from the leaves of the holly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ilk', 'a.', 'Same; each; every.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ilke', 'a.', 'Same.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ilkon', 'pron.', 'Alt. of Ilkoon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ilkoon', 'pron.', 'Each one; every one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ill', 'a.', 'Contrary to good, in a physical sense; contrary or opposed to\n   advantage, happiness, etc.; bad; evil; unfortunate; disagreeable;\n   unfavorable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ill', 'a.', 'Contrary to good, in a moral sense; evil; wicked; wrong;\n   iniquitious; naughtly; bad; improper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ill', 'a.', 'Sick; indisposed; unwell; diseased; disordered; as, ill of a\n   fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ill', 'a.', 'Not according with rule, fitness, or propriety; incorrect;\n   rude; unpolished; inelegant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ill', 'n.', 'Whatever annoys or impairs happiness, or prevents success;\n   evil of any kind; misfortune; calamity; disease; pain; as, the ills of\n   humanity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ill', 'n.', 'Whatever is contrary to good, in a moral sense; wickedness;\n   depravity; iniquity; wrong; evil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ill', 'adv.', 'In a ill manner; badly; weakly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'I ll', '', 'Contraction for I will or I shall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illabile', 'a.', 'Incapable of falling or erring; infalliable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illacerable', 'a.', 'Not lacerable; incapable of being torn or rent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illacrymable', 'a.', 'Incapable of weeping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illapsable', 'a.', 'Incapable of slipping, or of error.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illapsed', 'imp. & p. p.', 'of Illapse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illapsing', 'p. pr. & vb. n.', 'of Illapse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illapse', 'v. i.', 'To fall or glide; to pass; -- usually followed by into.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illapse', 'v. i.', 'A gliding in; an immisson or entrance of one thing into\n   another; also, a sudden descent or attack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illaqueable', 'a.', 'Capable of being insnared or entrapped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illaqueated', 'imp. & p. p.', 'of Illaqueate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illaqueating', 'p. pr. & vb. n.', 'of Illaqueate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illaqueate', 'v. t.', 'To insnare; to entrap; to entangle; to catch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illaqueation', 'n.', 'The act of catching or insnaring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illaqueation', 'n.', 'A snare; a trap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illation', 'n.', 'The act or process of inferring from premises or reasons;\n   perception of the connection between ideas; that which is inferred;\n   inference; deduction; conclusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illative', 'a.', 'Relating to, dependent on, or denoting, illation;\n   inferential; conclusive; as, an illative consequence or proposition; an\n   illative word, as then, therefore, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illative', 'n.', 'An illative particle, as for, because.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illatively', 'adv.', 'By inference; as an illative; in an illative manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illaudable', 'a.', 'Not laudable; not praise-worthy; worthy of censure or\n   disapprobation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ill-boding', 'a.', 'Boding evil; inauspicious; ill-omened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ill-bred', 'a.', 'Badly educated or brought up; impolite; incivil; rude.\n   See Note under Ill, adv.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illecebration', 'n.', 'Allurement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illecebrous', 'a.', 'Alluring; attractive; enticing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illegal', 'a.', 'Not according to, or authorized by, law; specif., contrary\n   to, or in violation of, human law; unlawful; illicit; hence, immoral;\n   as, an illegal act; illegal trade; illegal love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illegalities', 'pl. ', 'of Illegality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illegality', 'n.', 'The quality or condition of being illegal;\n   unlawfulness; as, the illegality of trespass or of false imprisonment;\n   also, an illegal act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illegalized', 'imp. & p. p.', 'of Illegalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illegalizing', 'p. pr. & vb. n.', 'of Illegalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illegalize', 'v. t.', 'To make or declare illegal or unlawful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illegally', 'adv.', 'In a illegal manner; unlawfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illegalness', 'n.', 'Illegality, unlawfulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illegibility', 'n.', 'The state or quality of being illegible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illegible', 'a.', 'Incapable of being read; not legible; as, illegible\n   handwriting; an illegible inscription.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illegitimacy', 'n.', 'The state of being illegitimate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illegitimate', 'a.', 'Not according to law; not regular or authorized;\n   unlawful; improper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illegitimate', 'a.', 'Unlawfully begotten; born out of wedlock; bastard;\n   as, an illegitimate child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illegitimate', 'a.', 'Not legitimately deduced or inferred; illogical; as,\n   an illegitimate inference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illegitimate', 'a.', 'Not authorized by good usage; not genuine; spurious;\n   as, an illegitimate word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illegitimated', 'imp. & p. p.', 'of Illegitimate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illegitimating', 'p. pr. & vb. n.', 'of Illegitimate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illegitimate', 'v. t.', 'To render illegitimate; to declare or prove to be\n   born out of wedlock; to bastardize; to illegitimatize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illegitimately', 'adv.', 'In a illegitimate manner; unlawfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illegitimation', 'n.', 'The act of illegitimating; bastardizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illegitimation', 'n.', 'The state of being illegitimate; illegitimacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illegitimatize', 'v. t.', 'To render illegitimate; to bastardize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illesive', 'a.', 'Not injurious; harmless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illeviable', 'a.', 'Not leviable; incapable of being imposed, or collected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ill-favored', 'a.', 'Wanting beauty or attractiveness; deformed; ugly;\n   ill-looking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illiberal', 'a.', 'Not liberal; not free or generous; close; niggardly;\n   mean; sordid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illiberal', 'a.', 'Indicating a lack of breeding, culture, and the like;\n   ignoble; rude; narrow-minded; disingenuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illiberal', 'a.', 'Not well authorized or elegant; as, illiberal words in\n   Latin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illiberalism', 'n.', 'Illiberality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illiberality', 'n.', 'The state or quality of being illiberal; narrowness\n   of mind; meanness; niggardliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illiberalized', 'imp. & p. p.', 'of Illiberalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illiberalizing', 'p. pr. & vb. n.', 'of Illiberalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illiberalize', 'v. t.', 'To make illiberal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illiberally', 'adv.', 'In a illiberal manner, ungenerously; uncharitably;\n   parsimoniously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illiberalness', 'n.', 'The state of being illiberal; illiberality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illicit', 'a.', 'Not permitted or allowed; prohibited; unlawful; as,\n   illicit trade; illicit intercourse; illicit pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illicitous', 'a.', 'Illicit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illicium', 'n.', 'A genus of Asiatic and American magnoliaceous trees,\n   having star-shaped fruit; star anise. The fruit of Illicium anisatum is\n   used as a spice in India, and its oil is largely used in Europe for\n   flavoring cordials, being almost identical with true oil of anise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illighten', 'v. t.', 'To enlighten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illimitable', 'a.', 'Incapable of being limited or bounded; immeasurable;\n   limitless; boundless; as, illimitable space.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illimitation', 'n.', 'State of being illimitable; want of, or freedom from,\n   limitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illimited', 'a.', 'Not limited; interminable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illinition', 'n.', 'A smearing or rubbing in or on; also, that which is\n   smeared or rubbed on, as ointment or liniment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illinition', 'n.', 'A thin crust of some extraneous substance formed on\n   minerals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illinois', 'n.sing. & pl.', 'A tribe of North American Indians, which\n   formerly occupied the region between the Wabash and Mississippi rivers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illiquation', 'n.', 'The melting or dissolving of one thing into another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illish', 'a.', 'Somewhat ill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illision', 'n.', 'The act of dashing or striking against.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illiteracies', 'pl. ', 'of Illiteracy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illiteracy', 'n.', 'The state of being illiterate, or uneducated; want of\n   learning, or knowledge; ignorance; specifically, inability to read and\n   write; as, the illiteracy shown by the last census.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illiteracy', 'n.', 'An instance of ignorance; a literary blunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illiteral', 'a.', 'Not literal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illiterate', 'a.', 'Ignorant of letters or books; unlettered; uninstructed;\n   uneducated; as, an illiterate man, or people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illiterature', 'n.', 'Want of learning; illiteracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ill-judged', 'a.', 'Not well judged; unwise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ill-lived', 'a.', 'Leading a wicked life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ill-looking', 'a.', 'Having a bad look; threatening; ugly. See Note under\n   Ill, adv.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ill-mannered', 'a.', 'Impolite; rude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ill-minded', 'a.', 'Ill-disposed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ill-natured', 'a.', 'Of habitual bad temper; peevish; fractious; cross;\n   crabbed; surly; as, an ill-natured person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ill-natured', 'a.', 'Dictated by, or indicating, ill nature; spiteful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ill-natured', 'a.', 'Intractable; not yielding to culture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illness', 'n.', 'The condition of being ill, evil, or bad; badness;\n   unfavorableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illness', 'n.', 'Disease; indisposition; malady; disorder of health;\n   sickness; as, a short or a severe illness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illness', 'n.', 'Wrong moral conduct; wickedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ill-nurtured', 'a.', 'Ill-bred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illocality', 'n.', 'Want of locality or place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illogical', 'a.', 'Ignorant or negligent of the rules of logic or correct\n   reasoning; as, an illogical disputant; contrary of the rules of logic\n   or sound reasoning; as, an illogical inference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ill-omened', 'a.', 'Having unlucky omens; inauspicious. See Note under Ill,\n   adv.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ill-starred', 'a.', 'Fated to be unfortunate; unlucky; as, an ill-starred\n   man or day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ill-tempered', 'a.', 'Of bad temper; morose; crabbed; sour; peevish;\n   fretful; quarrelsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ill-tempered', 'a.', 'Unhealthy; ill-conditioned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ill-timed', 'a.', 'Done, attempted, or said, at an unsuitable or\n   unpropitious time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illtreat', 'v. t.', 'To treat cruelly or improperly; to ill use; to\n   maltreat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuded', 'imp. & p. p.', 'of Illude');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuding', 'p. pr. & vb. n.', 'of Illude');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illude', 'v. t.', 'To play upon by artifice; to deceive; to mock; to excite\n   and disappoint the hopes of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illumed', 'imp. & p. p.', 'of Illume');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuming', 'p. pr. & vb. n.', 'of Illume');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illume', 'v. t.', 'To throw or spread light upon; to make light or bright;\n   to illuminate; to illumine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuminable', 'a.', 'Capable of being illuminated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuminant', 'n.', 'That which illuminates or affords light; as, gas and\n   petroleum are illuminants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuminary', 'a.', 'Illuminative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuminated', 'imp. & p. p.', 'of Illuminate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuminating', 'p. pr. & vb. n.', 'of Illuminate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuminate', 'v. t.', 'To make light; to throw light on; to supply with\n   light, literally or figuratively; to brighten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuminate', 'v. t.', 'To light up; to decorate with artificial lights, as\n   a building or city, in token of rejoicing or respect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuminate', 'v. t.', 'To adorn, as a book or page with borders, initial\n   letters, or miniature pictures in colors and gold, as was done in\n   manuscripts of the Middle Ages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuminate', 'v. t.', 'To make plain or clear; to dispel the obscurity to\n   by knowledge or reason; to explain; to elucidate; as, to illuminate a\n   text, a problem, or a duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuminate', 'v. i.', 'To light up in token or rejoicing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuminate', 'a.', 'Enlightened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuminate', 'n.', 'One who enlightened; esp., a pretender to extraordinary\n   light and knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuminati', 'v. t.', 'Literally, those who are enlightened');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuminati', 'v. t.', 'Persons in the early church who had received\n   baptism; in which ceremony a lighted taper was given them, as a symbol\n   of the spiritual illumination they has received by that sacrament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuminati', 'v. t.', 'Members of a sect which sprung up in Spain about the\n   year 1575. Their principal doctrine was, that, by means of prayer, they\n   had attained to so perfect a state as to have no need of ordinances,\n   sacraments, good works, etc.; -- called also Alumbrados,\n   Perfectibilists, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuminati', 'v. t.', 'Members of certain associations in Modern Europe,\n   who combined to promote social reforms, by which they expected to raise\n   men and society to perfection, esp. of one originated in 1776 by Adam\n   Weishaupt, professor of canon law at Ingolstadt, which spread rapidly\n   for a time, but ceased after a few years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuminati', 'v. t.', 'An obscure sect of French Familists;');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuminati', 'v. t.', 'The Hesychasts, Mystics, and Quietists;');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuminati', 'v. t.', 'The Rosicrucians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuminati', 'v. t.', 'Any persons who profess special spiritual or\n   intellectual enlightenment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuminating', 'a.', 'Giving or producing light; used for illumination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illumination', 'n.', 'The act of illuminating, or supplying with light; the\n   state of being illuminated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illumination', 'n.', 'Festive decoration of houses or buildings with\n   lights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illumination', 'n.', 'Adornment of books and manuscripts with colored\n   illustrations. See Illuminate, v. t., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illumination', 'v. t.', 'That which is illuminated, as a house; also, an\n   ornamented book or manuscript.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illumination', 'v. t.', 'That which illuminates or gives light; brightness;\n   splendor; especially, intellectual light or knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illumination', 'v. t.', 'The special communication of knowledge to the mind\n   by God; inspiration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuminatism', 'n.', 'Illuminism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuminative', 'a.', 'Tending to illuminate or illustrate; throwing light;\n   illustrative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuminator', 'n.', 'One whose occupation is to adorn books, especially\n   manuscripts, with miniatures, borders, etc. See Illuminate, v. t., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuminator', 'v. t.', 'A condenser or reflector of light in optical\n   apparatus; also, an illuminant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illumine', 'v. t.', 'To illuminate; to light up; to adorn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuminee', 'n.', 'One of the Illuminati.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuminer', 'n.', 'One who, or that which, illuminates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuminism', 'n.', 'The principles of the Illuminati.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuministic', 'a.', 'Of or pertaining to illuminism, or the Illuminati.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuminized', 'imp. & p. p.', 'of Illuminize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuminizing', 'p. pr. & vb. n.', 'of Illuminize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuminize', 'v. t.', 'To initiate the doctrines or principles of the\n   Illuminati.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuminous', 'a.', 'Bright; clear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illure', 'v. t.', 'To deceive; to entice; to lure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ill-used', 'a.', 'Misapplied; treated badly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illusion', 'n.', 'An unreal image presented to the bodily or mental vision;\n   a deceptive appearance; a false show; mockery; hallucination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illusion', 'n.', 'Hence: Anything agreeably fascinating and charning;\n   enchantment; witchery; glamour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illusion', 'n.', 'A sensation originated by some external object, but so\n   modified as in any way to lead to an erroneous perception; as when the\n   rolling of a wagon is mistaken for thunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illusion', 'n.', 'A plain, delicate lace, usually of silk, used for veils,\n   scarfs, dresses, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illusionable', 'a.', 'Liable to illusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illusionist', 'n.', 'One given to illusion; a visionary dreamer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illusive', 'a.', 'Deceiving by false show; deceitful; deceptive; false;\n   illusory; unreal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illusively', 'adv.', 'In a illusive manner; falsely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illusiveness', 'n.', 'The quality of being illusive; deceptiveness; false\n   show.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illusory', 'a.', 'Deceiving, or tending of deceive; fallacious; illusive;\n   as, illusory promises or hopes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illustrable', 'a.', 'Capable of illustration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illustrated', 'imp. & p. p.', 'of Illustrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illustrating', 'p. pr. & vb. n.', 'of Illustrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illustrate', 'v. t.', 'To make clear, bright, or luminous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illustrate', 'v. t.', 'To set in a clear light; to exhibit distinctly or\n   conspicuously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illustrate', 'v. t.', 'To make clear, intelligible, or apprehensible; to\n   elucidate, explain, or exemplify, as by means of figures, comparisons,\n   and examples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illustrate', 'v. t.', 'To adorn with pictures, as a book or a subject; to\n   elucidate with pictures, as a history or a romance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illustrate', 'v. t.', 'To give renown or honor to; to make illustrious; to\n   glorify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illustrate', 'a.', 'Illustrated; distinguished; illustrious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illustration', 'n.', 'The act of illustrating; the act of making clear and\n   distinct; education; also, the state of being illustrated, or of being\n   made clear and distinct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illustration', 'n.', 'That which illustrates; a comparison or example\n   intended to make clear or apprehensible, or to remove obscurity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illustration', 'n.', 'A picture designed to decorate a volume or elucidate\n   a literary work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illustrative', 'a.', 'Tending or designed to illustrate, exemplify, or\n   elucidate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illustrative', 'a.', 'Making illustrious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illustratively', 'adv.', 'By way of illustration or elucidation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illustrator', 'n.', 'One who illustrates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illustratory', 'a.', 'Serving to illustrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illustrious', 'a.', 'Possessing luster or brightness; brilliant; luminous;\n   splendid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illustrious', 'a.', 'Characterized by greatness, nobleness, etc.; eminent;\n   conspicuous; distinguished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illustrious', 'a.', 'Conferring luster or honor; renowned; as, illustrious\n   deeds or titles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illustriously', 'adv.', 'In a illustrious manner; conspicuously; eminently;\n   famously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illustriousness', 'n.', 'The state or quality of being eminent; greatness;\n   grandeur; glory; fame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illustrous', 'a.', 'Without luster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illutation', 'n.', 'The act or operation of smearing the body with mud,\n   especially with the sediment from mineral springs; a mud bath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Illuxurious', 'a.', 'Not luxurious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ill-will', '', 'See under Ill, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ill-wisher', 'n.', 'One who wishes ill to another; an enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ilmenite', 'n.', 'Titanic iron. See Menaccanite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ilmenium', 'n.', 'A supposed element claimed to have been discovered by\n   R.Harmann.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ilvaite', 'n.', 'A silicate of iron and lime occurring in black prismatic\n   crystals and columnar masses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Im', '', 'A contraction of I am.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Im-', '', 'A form of the prefix in- not, and in- in. See In-. Im- also\n   occurs in composition with some words not of Latin origin; as, imbank,\n   imbitter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Image', 'n.', 'An imitation, representation, or similitude of any person,\n   thing, or act, sculptured, drawn, painted, or otherwise made\n   perceptible to the sight; a visible presentation; a copy; a likeness;\n   an effigy; a picture; a semblance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Image', 'n.', 'Hence: The likeness of anything to which worship is paid; an\n   idol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Image', 'n.', 'Show; appearance; cast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Image', 'n.', 'A representation of anything to the mind; a picture drawn by\n   the fancy; a conception; an idea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Image', 'n.', 'A picture, example, or illustration, often taken from\n   sensible objects, and used to illustrate a subject; usually, an\n   extended metaphor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Image', 'n.', 'The figure or picture of any object formed at the focus of a\n   lens or mirror, by rays of light from the several points of the object\n   symmetrically refracted or reflected to corresponding points in such\n   focus; this may be received on a screen, a photographic plate, or the\n   retina of the eye, and viewed directly by the eye, or with an eyeglass,\n   as in the telescope and microscope; the likeness of an object formed by\n   reflection; as, to see ones image in a mirror.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imaged', 'imp. & p. p.', 'of Image');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imaging', 'p. pr. & vb. n.', 'of Image');
  INSERT INTO words (word, word_type, description) VALUES  (    'Image', 'v. t.', 'To represent or form an image of; as, the still lake\n   imaged the shore; the mirror imaged her figure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Image', 'v. t.', 'To represent to the mental vision; to form a likeness of\n   by the fancy or recollection; to imagine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imageable', 'a.', 'That may be imaged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imageless', 'a.', 'Having no image.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imager', 'n.', 'One who images or forms likenesses; a sculptor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imagery', 'n.', 'The work of one who makes images or visible representation\n   of objects; imitation work; images in general, or in mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imagery', 'n.', 'Fig.: Unreal show; imitation; appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imagery', 'n.', 'The work of the imagination or fancy; false ideas;\n   imaginary phantasms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imagery', 'n.', 'Rhetorical decoration in writing or speaking; vivid\n   descriptions presenting or suggesting images of sensible objects;\n   figures in discourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imaginability', 'n.', 'Capacity for imagination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imaginable', 'a.', 'Capable of being imagined; conceivable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imaginal', 'a.', 'Characterized by imagination; imaginative; also, given to\n   the use or rhetorical figures or imagins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imaginal', 'a.', 'Of or pertaining to an imago.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imaginant', 'a.', 'Imagining; conceiving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imaginant', 'n.', 'An imaginer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imaginarily', 'a.', 'In a imaginary manner; in imagination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imaginariness', 'n.', 'The state or quality of being imaginary; unreality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imaginary', 'a.', 'Existing only in imagination or fancy; not real;\n   fancied; visionary; ideal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imaginary', 'n.', 'An imaginary expression or quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imaginate', 'a.', 'Imaginative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imagination', 'n.', 'The imagine-making power of the mind; the power to\n   create or reproduce ideally an object of sense previously perceived;\n   the power to call up mental imagines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imagination', 'n.', 'The representative power; the power to reconstruct or\n   recombine the materials furnished by direct apprehension; the complex\n   faculty usually termed the plastic or creative power; the fancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imagination', 'n.', 'The power to recombine the materials furnished by\n   experience or memory, for the accomplishment of an elevated purpose;\n   the power of conceiving and expressing the ideal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imagination', 'n.', 'A mental image formed by the action of the imagination\n   as a faculty; a conception; a notion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imaginational', 'a.', 'Pertaining to, involving, or caused by, imagination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imaginationalism', 'n.', 'Idealism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imaginative', 'a.', 'Proceeding from, and characterized by, the\n   imagination, generally in the highest sense of the word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imaginative', 'a.', 'Given to imagining; full of images, fancies, etc.;\n   having a quick imagination; conceptive; creative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imaginative', 'a.', 'Unreasonably suspicious; jealous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imagined', 'imp. & p. p.', 'of Imagine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imagining', 'p. pr. & vb. n.', 'of Imagine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imagine', 'v. t.', 'To form in the mind a notion or idea of; to form a\n   mental image of; to conceive; to produce by the imagination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imagine', 'v. t.', 'To contrive in purpose; to scheme; to devise; to\n   compass; to purpose. See Compass, v. t., 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imagine', 'v. t.', 'To represent to ones self; to think; to believe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imagine', 'v. i.', 'To form images or conceptions; to conceive; to devise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imagine', 'v. i.', 'To think; to suppose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imaginer', 'n.', 'One who forms ideas or conceptions; one who contrives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imaginous', 'a.', 'Imaginative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imagoes', 'pl. ', 'of Imago');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imago', 'n.', 'An image.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imago', 'n.', 'The final adult, and usually winged, state of an insect. See\n   Illust. of Ant-lion, and Army worm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imam', 'n.', 'Alt. of Imaum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iman', 'n.', 'Alt. of Imaum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imaum', 'n.', 'Among the Mohammedans, a minister or priest who performs the\n   regular service of the mosque.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imaum', 'n.', 'A Mohammedan prince who, as a successor of Mohammed, unites\n   in his person supreme spiritual and temporal power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imaret', 'n.', 'A lodging house for Mohammedan pilgrims.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbalm', 'v. t.', 'See Embalm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imban', 'v. t.', 'To put under a ban.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imband', 'v. t.', 'To form into a band or bands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbanked', 'imp. & p. p.', 'of Imbank');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbanking', 'p. pr. & vb. n.', 'of Imbank');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbank', 'v. t.', 'To inclose or defend with a bank or banks. See Embank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbankment', 'n.', 'The act of surrounding with a bank; a bank or mound\n   raised for defense, a roadway, etc.; an embankment. See Embankment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbannered', 'a.', 'Having banners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbar', 'v. t.', 'To bar in; to secure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbargo', 'n.', 'See Embargo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbark', 'v. i. & t.', 'See Embark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbarn', 'v. t.', 'To store in a barn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbase', 'v. t.', 'See Embase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbase', 'v. i.', 'To diminish in value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbastardize', 'v. t.', 'To bastardize; to debase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbathe', 'v. t.', 'To bathe; to wash freely; to immerce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbay', 'v. t.', 'See Embay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbecile', 'a.', 'Destitute of strength, whether of body or mind; feeble;\n   impotent; esp., mentally wea; feeble-minded; as, hospitals for the\n   imbecile and insane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbecile', 'n.', 'One destitute of strength; esp., one of feeble mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbecile', 'v. t.', 'To weaken; to make imbecile; as, to imbecile mens\n   courage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbecilitate', 'v. t.', 'To weaken, as to the body or the mind; to\n   enfeeble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbecilities', 'pl. ', 'of Imbecility');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbecility', 'n.', 'The quality of being imbecile; weakness; feebleness,\n   esp. of mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbedded', 'imp. & p. p.', 'of Imbed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbedding', 'p. pr. & vb. n.', 'of Imbed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbed', 'v. t.', 'To sink or lay, as in a bed; to deposit in a partly\n   inclosing mass, as of clay or mortar; to cover, as with earth, sand,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbellic', 'a.', 'Not warlike or martial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbenching', 'n.', 'A raised work like a bench.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imber-goose', 'n.', 'The loon. See Ember-goose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbezzle', 'v. t.', 'See Embezzle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbibed', 'imp. & p. p.', 'of Imbibe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbibing', 'p. pr. & vb. n.', 'of Imbibe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbibe', 'v. t.', 'To drink in; to absorb; to suck or take in; to receive\n   as by drinking; as, a person imbibes drink, or a sponge imbibes\n   moisture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbibe', 'v. t.', 'To receive or absorb into the mind and retain; as, to\n   imbibe principles; to imbibe errors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbibe', 'v. t.', 'To saturate; to imbue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbiber', 'n.', 'One who, or that which, imbibes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbibition', 'n.', 'The act or process of imbibing, or absorbing; as, the\n   post-mortem imbibition of poisons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbittered', 'imp. & p. p.', 'of Imbitter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbittering', 'p. pr. & vb. n.', 'of Imbitter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbitter', 'v. t.', 'To make bitter; hence, to make distressing or more\n   distressing; to make sad, morose, sour, or malignant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbitterer', 'n.', 'One who, or that which, imbitters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbitterment', 'n.', 'The act of imbittering; bitter feeling; embitterment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imblaze', 'v. t.', 'See Emblaze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imblazon', 'v. t.', 'See Emblazon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbody', 'v. i.', 'To become corporeal; to assume the qualities of a\n   material body. See Embody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imboil', 'v. t. & i.', 'See Emboil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbolden', 'v. t.', 'See Embolden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbonity', 'n.', 'Want of goodness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbordered', 'imp. & p. p.', 'of Imborder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbordering', 'p. pr. & vb. n.', 'of Imborder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imborder', 'v. t.', 'To furnish or inclose with a border; to form a border\n   of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbosked', 'imp. & p. p.', 'of Imbosk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbosking', 'p. pr. & vb. n.', 'of Imbosk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbosk', 'v. t.', 'To conceal, as in bushes; to hide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbosk', 'v. i.', 'To be concealed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbosomed', 'imp. & p. p.', 'of Imbosom');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbosoming', 'p. pr. & vb. n.', 'of Imbosom');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbosom', 'v. t.', 'To hold in the bosom; to cherish in the heart or\n   affection; to embosom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbosom', 'v. t.', 'To inclose or place in the midst of; to surround or\n   shelter; as, a house imbosomed in a grove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imboss', 'v. t.', 'See Emboss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbosture', 'n.', 'Embossed or raised work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbound', 'v. t.', 'To inclose in limits; to shut in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbow', 'v. t.', 'To make like a bow; to curve; to arch; to vault; to\n   embow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbowel', 'v. t.', 'See Embowel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbower', 'v. t. & i.', 'See Embower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbowment', 'n.', 'act of imbowing; an arch; a vault.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbox', 'v. t.', 'To inclose in a box.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbracery', 'n.', 'Embracery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbraid', 'v. t.', 'See Embraid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbrangle', 'v. t.', 'To entangle as in a cobweb; to mix confusedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbreed', 'v. t.', 'To generate within; to inbreed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbricate', 'a.', 'Alt. of Imbricated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbricated', 'a.', 'Bent and hollowed like a roof or gutter tile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbricated', 'a.', 'Lying over each other in regular order, so as to \"break\n   joints,\" like tiles or shingles on a roof, the scales on the leaf buds\n   of plants and the cups of some acorns, or the scales of fishes;\n   overlapping each other at the margins, as leaves in aestivation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbricated', 'a.', 'In decorative art: Having scales lapping one over the\n   other, or a representation of such scales; as, an imbricated surface;\n   an imbricated pattern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbricate', 'v. t.', 'To lay in order, one lapping over another, so as to\n   form an imbricated surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbrication', 'n.', 'An overlapping of the edges, like that of tiles or\n   shingles; hence, intricacy of structure; also, a pattern or decoration\n   representing such a structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbricative', 'a.', 'Imbricate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbrocadoes', 'pl. ', 'of Imbrocado');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbrocado', 'n.', 'Cloth of silver or of gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbrocata', 'n.', 'Alt. of Imbroccata');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbroccata', 'n.', 'A hit or thrust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbroglios', 'pl. ', 'of Imbroglio');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbroglio', 'n.', 'An intricate, complicated plot, as of a drama or work of\n   fiction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbroglio', 'n.', 'A complicated and embarrassing state of things; a\n   serious misunderstanding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbrown', 'v. t.', 'To make brown; to obscure; to darken; to tan; as,\n   features imbrowned by exposure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbureed', 'imp. & p. p.', 'of Imbrue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbureing', 'p. pr. & vb. n.', 'of Imbrue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbrue', 'v. t.', 'To wet or moisten; to soak; to drench, especially in\n   blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbruement', 'n.', 'The act of imbruing or state of being imbrued.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbruted', 'imp. & p. p.', 'of Imbrute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbruting', 'p. pr. & vb. n.', 'of Imbrute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbrute', 'v. t.', 'To degrade to the state of a brute; to make brutal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbrute', 'v. i.', 'To sink to the state of a brute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbrutement', 'n.', 'The act of imbruting, or the state of being imbruted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbued', 'imp. & p. p.', 'of Imbue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbuing', 'p. pr. & vb. n.', 'of Imbue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbue', 'v. t.', 'To tinge deeply; to dye; to cause to absorb; as, clothes\n   thoroughly imbued with black.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbue', 'v. t.', 'To tincture deply; to cause to become impressed or\n   penetrated; as, to imbue the minds of youth with good principles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbuement', 'n.', 'The act of imbuing; the state of being imbued; hence, a\n   deep tincture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imburse', 'v. t.', 'To supply or stock with money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbursement', 'n.', 'The act of imbursing, or the state of being imbursed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbursement', 'n.', 'Money laid up in stock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imbution', 'n.', 'An imbuing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imesatin', 'n.', 'A dark yellow, crystalline substance, obtained by the\n   action of ammonia on isatin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imide', 'n.', 'A compound with, or derivative of, the imido group; specif.,\n   a compound of one or more acid radicals with the imido group, or with a\n   monamine; hence, also, a derivative of ammonia, in which two atoms of\n   hydrogen have been replaced by divalent basic or acid radicals; --\n   frequently used as a combining form; as, succinimide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imido', 'a.', 'Pertaining to, containing, or combined with, the radical NH,\n   which is called the imido group.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imitability', 'n.', 'The quality of being imitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imitable', 'a.', 'Capble of being imitated or copied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imitable', 'a.', 'Worthy of imitation; as, imitable character or qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imitableness', 'n.', 'The state or quality of being imitable; worthness of\n   imitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imitancy', 'n.', 'Tendency to imitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imitated', 'imp. & p. p.', 'of Imitate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imitating', 'p. pr. & vb. n.', 'of Imitate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imitate', 'v. t.', 'To follow as a pattern, model, or example; to copy or\n   strive to copy, in acts, manners etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imitate', 'v. t.', 'To produce a semblance or likeness of, in form,\n   character, color, qualities, conduct, manners, and the like; to\n   counterfeit; to copy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imitate', 'v. t.', 'To resemble (another species of animal, or a plant, or\n   inanimate object) in form, color, ornamentation, or instinctive habits,\n   so as to derive an advantage thereby; sa, when a harmless snake\n   imitates a venomous one in color and manner, or when an odorless insect\n   imitates, in color, one having secretion offensive to birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imitation', 'n.', 'The act of imitating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imitation', 'n.', 'That which is made or produced as a copy; that which is\n   made to resemble something else, whether for laudable or for fraudulent\n   purposes; likeness; resemblance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imitation', 'n.', 'One of the principal means of securing unity and\n   consistency in polyphonic composition; the repetition of essentially\n   the same melodic theme, phrase, or motive, on different degrees of\n   pitch, by one or more of the other parts of voises. Cf. Canon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imitation', 'n.', 'The act of condition of imitating another species of\n   animal, or a plant, or unanimate object. See Imitate, v. t., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imitational', 'a.', 'Pertaining to, or employed in, imitation; as,\n   imitational propensities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imitative', 'a.', 'Inclined to imitate, copy, or follow; imitating;\n   exhibiting some of the qualities or characteristics of a pattern or\n   model; dependent on example; not original; as, man is an imitative\n   being; painting is an imitative art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imitative', 'a.', 'Formed after a model, pattern, or original.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imitative', 'a.', 'Designed to imitate another species of animal, or a\n   plant, or inanimate object, for some useful purpose, such as protection\n   from enemies; having resamblance to something else; as, imitative\n   colors; imitative habits; dendritic and mammillary forms of minerals\n   are imitative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imitative', 'n.', 'A verb expressive of imitation or resemblance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imitater', 'n.', 'One who imitates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imitatorship', 'n.', 'The state or office of an imitator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imitatress', 'n.', 'A woman who is an imitator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imitatrix', 'n.', 'An imitatress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immaculate', 'a.', 'Without stain or blemish; spotless; undefiled; clear;\n   pure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immailed', 'a.', 'Wearing mail or armor; clad of armor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immalleable', 'a.', 'Not maleable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immanacled', 'imp. & p. p.', 'of Immanacle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immanacling', 'p. pr. & vb. n.', 'of Immanacle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immanacle', 'v. t.', 'To manacle; to fetter; hence; to confine; to restrain\n   from free action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immanation', 'n.', 'A flowing or entering in; -- opposed to emanation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immane', 'a.', 'Very great; huge; vast; also, monstrous in character;\n   inhuman; atrocious; fierce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immanence', 'n.', 'Alt. of Immanency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immanency', 'n.', 'The condition or quality of being immanent; inherence;\n   an indwelling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immanent', 'a.', 'Remaining within; inherent; indwelling; abiding;\n   intrinsic; internal or subjective; hence, limited in activity, agency,\n   or effect, to the subject or associated acts; -- opposed to emanant,\n   transitory, transitive, or objective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immanifest', 'a.', 'Not manifest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immanity', 'n.', 'The state or quality of being immane; barbarity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immantle', 'v. t.', 'See Emmantle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immanuel', 'n.', 'God with us; -- an appellation of the Christ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immarcescible', 'a.', 'Unfading; lasting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immarcescibly', 'adv.', 'Unfadingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immarginate', 'a.', 'Not having a distinctive margin or border.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immartial', 'a.', 'Not martial; unwarlike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immask', 'v. t.', 'To cover, as with a mask; to disguise or conceal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immatchable', 'a.', 'Matchless; peerless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immaterial', 'a.', 'Not consisting of matter; incorporeal; spiritual;\n   disembodied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immaterial', 'a.', 'Of no substantial consequence; without weight or\n   significance; unimportant; as, it is wholly immaterial whether he does\n   so or not.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immaterialism', 'n.', 'The doctrine that immaterial substances or spiritual\n   being exist, or are possible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immaterialism', 'n.', 'The doctrine that external bodies may be reduced to\n   mind and ideas in a mind; any doctrine opposed to materialism or\n   phenomenalism, esp. a system that maintains the immateriality of the\n   soul; idealism; esp., Bishop Berkeleys theory of idealism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immaterialist', 'n.', 'One who believes in or professes, immaterialism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immaterialities', 'pl. ', 'of Immateriality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immateriality', 'n.', 'The state or quality of being immaterial or\n   incorporeal; as, the immateriality of the soul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immaterialize', 'v. t.', 'To render immaterial or incorporeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immaterially', 'adv.', 'In an immaterial manner; without matter or\n   corporeal substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immaterially', 'adv.', 'In an unimportant manner or degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immaterialness', 'n.', 'The state or quality of being immaterial;\n   immateriality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immateriate', 'a.', 'Immaterial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immature', 'a.', 'Not mature; unripe; not arrived at perfection of full\n   development; crude; unfinished; as, immature fruit; immature character;\n   immature plans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immature', 'a.', 'Premature; untimely; too early; as, an immature death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immatured', 'a.', 'Immature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immaturely', 'adv.', 'In an immature manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immatureness', 'n.', 'The state or quality of being immature; immaturity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immaturity', 'n.', 'The state or quality of being immature or not fully\n   developed; unripeness; incompleteness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immeability', 'n.', 'Want of power to pass, or to permit passage;\n   impassableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immeasurability', 'n.', 'The quality of being immeasurable;\n   immensurability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immeasurable', 'a.', 'Incapble of being measured; indefinitely extensive;\n   illimitable; immensurable; vast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immeasurableness', 'n.', 'The state or quality of being immeasurable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immeasurably', 'adv.', 'In an immeasurable manner or degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immeasured', 'a.', 'Immeasurable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immechanical', 'a.', 'Not mechanical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immediacy', 'n.', 'The relation of freedom from the interventionof a\n   medium; immediateness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immediate', 'a.', 'Not separated in respect to place by anything\n   intervening; proximate; close; as, immediate contact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immediate', 'a.', 'Not deferred by an interval of time; present; instant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immediate', 'a.', 'Acting with nothing interposed or between, or without\n   the intervention of another object as a cause, means, or agency;\n   acting, perceived, or produced, directly; as, an immediate cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immediately', 'adv.', 'In an immediate manner; without intervention of any\n   other person or thing; proximately; directly; -- opposed to mediately;\n   as, immediately contiguous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immediately', 'adv.', 'Without interval of time; without delay; promptly;\n   instantly; at once.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immediately', 'adv.', 'As soon as. Cf. Directly, 8, Note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immediateness', 'n.', 'The quality or relations of being immediate in\n   manner, place, or time; exemption from second or interventing causes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immedeatism', 'n.', 'Immediateness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immedicable', 'a.', 'Not to be healed; incurable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immelodious', 'a.', 'Not melodious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immemorable', 'a.', 'Not memorable; not worth remembering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immemorial', 'a.', 'Extending beyond the reach of memory, record, or\n   tradition; indefinitely ancient; as, existing from time immemorial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immemorially', 'adv.', 'Beyond memory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immense', 'a.', 'Immeasurable; unlimited. In commonest use: Very great;\n   vast; huge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immensely', 'adv.', 'In immense manner or degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immenseness', 'n.', 'The state of being immense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immensible', 'a.', 'Immeasurable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immensities', 'pl. ', 'of Immensity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immensity', 'n.', 'The state or quality of being immense; inlimited or\n   immeasurable extension; infinity; vastness in extent or bulk;\n   greatness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immensive', 'a.', 'Huge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immensurability', 'n.', 'The quality of being immensurable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immensurable', 'a.', 'Immeasurable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immensurate', 'a.', 'Unmeasured; unlimited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immerged', 'imp. & p. p.', 'of Immerge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immerging', 'p. pr. & vb. n.', 'of Immerge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immerge', 'v. t.', 'To plungel into, under, or within anything especially a\n   fuid; to dip; to immerse. See Immerse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immerge', 'v. i.', 'To dissapear by entering into any medium, as a star\n   into the light of the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immerit', 'n.', 'Want of worth; demerit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immerited', 'a.', 'Unmerited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immeritous', 'a.', 'Undeserving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immersable', 'a.', 'See Immersible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immerse', 'a.', 'Immersed; buried; hid; sunk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immersed', 'imp. & p. p.', 'of Immerse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immersing', 'p. pr. & vb. n.', 'of Immerse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immerse', 'v. t.', 'To plunge into anything that surrounds or covers,\n   especially into a fluid; to dip; to sink; to bury; to immerge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immerse', 'v. t.', 'To baptize by immersion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immerse', 'v. t.', 'To engage deeply; to engross the attention of; to\n   involve; to overhelm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immersed', 'p. p. & a.', 'Deeply plunged into anything, especially a fluid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immersed', 'p. p. & a.', 'Deeply occupied; engrossed; entangled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immersed', 'p. p. & a.', 'Growing wholly under water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immersible', 'a.', 'Capable of being immersed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immersible', 'a.', 'Not capable of being immersed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immersion', 'n.', 'The act of immersing, or the state of being immersed; a\n   sinking within a fluid; a dipping; as, the immersion of Achilles in the\n   Styx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immersion', 'n.', 'Submersion in water for the purpose of Christian\n   baptism, as, practiced by the Baptists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immersion', 'n.', 'The state of being overhelmed or deeply absorbed; deep\n   engagedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immersion', 'n.', 'The dissapearance of a celestail body, by passing either\n   behind another, as in the occultation of a star, or into its shadow, as\n   in the eclipse of a satellite; -- opposed to emersion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immersionist', 'n.', 'One who holds the doctrine that immersion is\n   essential to Christian baptism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immeshed', 'imp. & p. p.', 'of Immesh');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immeshing', 'p. pr. & vb. n.', 'of Immesh');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immesh', 'v. t.', 'To catch or entangle in, or as in, the meshes of a net.\n   or in a web; to insnare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immethodical', 'a.', 'Not methodical; without method or systematic\n   arrangement; without order or regularity; confused.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immethodically', 'adv.', 'Without method; confusedly; unsystematically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immethodicalness', 'n.', 'Want of method.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immethodize', 'v. t.', 'To render immethodical; to destroy the method of;\n   to confuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immetrical', 'a.', 'Not metrical or rhythmical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immew', 'v. t.', 'See Emmew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immigrant', 'n.', 'One who immigrates; one who comes to a country for the\n   purpose of permanent residence; -- correlative of emigrant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immigrated', 'imp. & p. p.', 'of Immigrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immigrating', 'p. pr. & vb. n.', 'of Immigrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immigrate', 'v. t.', 'To come into a country of which one is not a native,\n   for the purpose of permanent residence. See Emigrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immigration', 'n.', 'The act of immigrating; the passing or coming into a\n   country for the purpose of permanent residence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imminence', 'n.', 'The condition or quality of being imminent; a\n   threatening, as of something about to happen. The imminence of any\n   danger or distress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imminence', 'n.', 'That which is imminent; impending evil or danger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imminent', 'a.', 'Threatening to occur immediately; near at hand;\n   impending; -- said especially of misfortune or peril.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imminent', 'a.', 'Full of danger; threatening; menacing; perilous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imminent', 'a.', '(With upon) Bent upon; attentive to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imminently', 'adv.', 'In an imminent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immingle', 'v. t.', 'To mingle; to mix; to unite; to blend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imminution', 'n.', 'A lessening; diminution; decrease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immiscibility', 'n.', 'Incapability of being mixed, or mingled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immiscible', 'a.', 'Not capable of being mixed or mingled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immission', 'n.', 'The act of immitting, or of sending or thrusting in;\n   injection; -- the correlative of emission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immitted', 'imp. & p. p.', 'of Immit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immiting', 'p. pr. & vb. n.', 'of Immit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immit', 'v. t.', 'To send in; to inject; to infuse; -- the correlative of\n   emit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immitigable', 'a.', 'Not capable of being mitigated, softened, or appeased.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immitigably', 'adv.', 'In an immitigable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immix', 'v. t.', 'To mix; to mingle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immixable', 'a.', 'Not mixable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immixed', 'a.', 'Unmixed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immixture', 'n.', 'Freedom from mixture; purity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immobile', 'a.', 'Incapable of being moved; immovable; fixed; stable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immobility', 'n.', 'The condition or quality of being immobile; fixedness\n   in place or state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immobilize', 'v. t.', 'To make immovable; in surgery, to make immovable (a\n   naturally mobile part, as a joint) by the use of splints, or stiffened\n   bandages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immoble', 'a.', 'See Immobile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immoderacy', 'n.', 'Immoderateness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immoderancy', 'n.', 'Immoderateness; excess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immoderate', 'a.', 'Not moderate; exceeding just or usual and suitable\n   bounds; excessive; extravagant; unreasonable; as, immoderate demands;\n   immoderate grief; immoderate laughter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immoderately', 'adv.', 'In an immoderate manner; excessively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immoderateness', 'n.', 'The quality of being immoderate; excess;\n   extravagance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immoderation', 'n.', 'Want of moderation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immodest', 'a.', 'Not limited to due bounds; immoderate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immodest', 'a.', 'Not modest; wanting in the reserve or restraint which\n   decorum and decency require; indecent; indelicate; obscene; lewd; as,\n   immodest persons, behavior, words, pictures, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immodestly', 'adv.', 'In an immodest manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immodesty', 'n.', 'Want of modesty, delicacy, or decent reserve; indecency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immolated', 'imp. & p. p.', 'of Immolate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immolating', 'p. pr. & vb. n.', 'of Immolate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immolate', 'v. t.', 'To sacrifice; to offer in sacrifice; to kill, as a\n   sacrificial victim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immolation', 'n.', 'The act of immolating, or the state of being immolated,\n   or sacrificed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immolation', 'n.', 'That which is immolated; a sacrifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immolator', 'n.', 'One who offers in sacrifice; specifically, one of a sect\n   of Russian fanatics who practice self-mutilatio and sacrifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immold', 'v. t.', 'Alt. of Immould');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immould', 'v. t.', 'To mold into shape, or form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immoment', 'a.', 'Trifling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immomentous', 'a.', 'Not momentous; unimportant; insignificant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immoral', 'a.', 'Not moral; inconsistent with rectitude, purity, or good\n   morals; contrary to conscience or the divine law; wicked; unjust;\n   dishonest; vicious; licentious; as, an immoral man; an immoral deed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immoralities', 'pl. ', 'of Immorality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immorality', 'n.', 'The state or quality of being immoral; vice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immorality', 'n.', 'An immoral act or practice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immorally', 'adv.', 'In an immoral manner; wickedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immorigerous', 'a.', 'Rude; uncivil; disobedient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immortal', 'a.', 'Not mortal; exempt from liability to die; undying;\n   imperishable; lasting forever; having unlimited, or eternal, existance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immortal', 'a.', 'Connected with, or pertaining to immortability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immortal', 'a.', 'Destined to live in all ages of this world; abiding;\n   exempt from oblivion; imperishable; as, immortal fame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immortal', 'a.', 'Great; excessive; grievous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immortal', 'n.', 'One who will never cease to be; one exempt from death,\n   decay, or annihilation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immortalist', 'n.', 'One who holds the doctrine of the immortality of the\n   soul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immortalities', 'pl. ', 'of Immortality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immortality', 'n.', 'The quality or state of being immortal; exemption from\n   death and annihilation; unending existance; as, the immortality of the\n   soul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immortality', 'n.', 'Exemption from oblivion; perpetuity; as, the\n   immortality of fame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immortalization', 'n.', 'The act of immortalizing, or state of being\n   immortalized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immortalized', 'imp. & p. p.', 'of Immortalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immortalizing', 'p. pr. & vb. n.', 'of Immortalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immortalize', 'v. t.', 'To render immortal; to cause to live or exist\n   forever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immortalize', 'v. t.', 'To exempt from oblivion; to perpetuate in fame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immortalize', 'v. i.', 'To become immortal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immortally', 'adv.', 'In an immortal manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immortelles', 'pl. ', 'of Immortelle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immortelle', 'n.', 'A plant with a conspicuous, dry, unwithering involucre,\n   as the species of Antennaria, Helichrysum, Gomphrena, etc. See\n   Everlasting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immortification', 'n.', 'Failure to mortify the passions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immovability', 'n.', 'The quality or state of being immovable; fixedness;\n   steadfastness; as, immovability of a heavy body; immovability of\n   purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immovable', 'a.', 'Incapable of being moved; firmly fixed; fast; -- used of\n   material things; as, an immovable foundatin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immovable', 'a.', 'Steadfast; fixed; unalterable; unchangeable; -- used of\n   the mind or will; as, an immovable purpose, or a man who remain\n   immovable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immovable', 'a.', 'Not capable of being affected or moved in feeling or by\n   sympathy; unimpressible; impassive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immovable', 'a.', 'Not liable to be removed; permanent in place or tenure;\n   fixed; as, an immovable estate. See Immovable, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immovable', 'n.', 'That which can not be moved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immovable', 'n.', 'Lands and things adherent thereto by nature, as trees;\n   by the hand of man, as buildings and their accessories; by their\n   destination, as seeds, plants, manure, etc.; or by the objects to which\n   they are applied, as servitudes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immovableness', 'n.', 'Quality of being immovable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immovably', 'adv.', 'In an immovable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immund', 'a.', 'Unclean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immundicity', 'n.', 'Uncleanness; filthness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immune', 'a.', 'Exempt; protected by inoculation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immunities', 'pl. ', 'of Immunity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immunity', 'a.', 'Freedom or exemption from any charge, duty, obligation,\n   office, tax, imposition, penalty, or service; a particular privilege;\n   as, the immunities of the free cities of Germany; the immunities of the\n   clergy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immunity', 'a.', 'Freedom; exemption; as, immunity from error.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immured', 'imp. & p. p.', 'of Immure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immuring', 'p. pr. & vb. n.', 'of Immure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immure', 'v. t.', 'To wall around; to surround with walls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immure', 'v. t.', 'To inclose whithin walls, or as within walls; hence, to\n   shut up; to imprison; to incarcerate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immure', 'n.', 'A wall; an inclosure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immurement', 'n.', 'The act iif immuring, or the state of being immured;\n   imprsonment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immusical', 'a.', 'Inharmonious; unmusical; discordant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immutability', 'n.', 'The state or quality of being immutable;\n   immutableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immutable', 'a.', 'Not mutable; not capable or susceptible of change;\n   unchangeable; unalterable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immutate', 'a.', 'Unchanged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immutation', 'n.', 'Change; alteration; mutation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Immute', 'v. t.', 'To change or alter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imp', 'n.', 'A shoot; a scion; a bud; a slip; a graft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imp', 'n.', 'An offspring; progeny; child; scion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imp', 'n.', 'A young or inferior devil; a little, malignant spirit; a puny\n   demon; a contemptible evil worker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imp', 'n.', 'Something added to, or united with, another, to lengthen it\n   out or repair it, -- as, an addition to a beehive; a feather inserted\n   in a broken wing of a bird; a length of twisted hair in a fishing line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imped', 'imp. & p. p.', 'of Imp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imping', 'p. pr. & vb. n.', 'of Imp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imp', 'n.', 'To graft; to insert as a scion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imp', 'n.', 'To graft with new feathers, as a wing; to splice a broken\n   feather. Hence, Fig.: To repair; to extend; to increase; to strengthen\n   to equip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impacable', 'a.', 'Not to be appeased or quieted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impackment', 'n.', 'The state of being closely surrounded, crowded, or\n   pressed, as by ice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impacted', 'imp. & p. p.', 'of Impact');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impacting', 'p. pr. & vb. n.', 'of Impact');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impact', 'v. t.', 'To drive close; to press firmly together: to wedge into\n   a place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impact', 'n.', 'Contact or impression by touch; collision; forcible\n   contact; force communicated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impact', 'n.', 'The single instantaneous stroke of a body in motion against\n   another either in motion or at rest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impacted', 'a.', 'Driven together or close.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impaction', 'n.', 'The driving of one fragment of bone into another so that\n   the fragments are not movable upon each other; as, impaction of the\n   skull or of the hip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impaction', 'n.', 'An immovable packing; (Med.), a lodgment of something in\n   a strait or passage of the body; as, impaction of the fetal head in the\n   strait of the pelvis; impaction of food or feces in the intestines of\n   man or beast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impaint', 'v. t.', 'To paint; to adorn with colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impaired', 'imp. & p. p.', 'of Impair');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impairing', 'p. pr. & vb. n.', 'of Impair');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impair', 'v. t.', 'To make worse; to diminish in quantity, value,\n   excellence, or strength; to deteriorate; as, to impair health,\n   character, the mind, value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impair', 'v. t.', 'To grow worse; to deteriorate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impair', 'a.', 'Not fit or appropriate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impair', 'n.', 'Diminution; injury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impairer', 'n.', 'One who, or that which, impairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impairment', 'n.', 'The state of being impaired; injury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impalatable', 'a.', 'Unpalatable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impaled', 'imp. & p. p.', 'of Impale');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impaling', 'p. pr. & vb. n.', 'of Impale');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impale', 'v. t.', 'To pierce with a pale; to put to death by fixing on a\n   sharp stake. See Empale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impale', 'v. t.', 'To inclose, as with pales or stakes; to surround.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impale', 'v. t.', 'To join, as two coats of arms on one shield, palewise;\n   hence, to join in honorable mention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impalement', 'n.', 'The act of impaling, or the state of being impaled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impalement', 'n.', 'An inclosing by stakes or pales, or the space so\n   inclosed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impalement', 'n.', 'That which hedges in; inclosure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impalement', 'n.', 'The division of a shield palewise, or by a vertical\n   line, esp. for the purpose of putting side by side the arms of husband\n   and wife. See Impale, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impalla', 'n.', 'The pallah deer of South Africa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impallid', 'v. t.', 'To make pallid; to blanch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impalm', 'v. t.', 'To grasp with or hold in the hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impalpability', 'n.', 'The quality of being impalpable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impalpable', 'a.', 'Not palpable; that cannot be felt; extremely fine, so\n   that no grit can be perceived by touch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impalpable', 'a.', 'Not material; intangible; incorporeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impalpable', 'a.', 'Not apprehensible, or readily apprehensible, by the\n   mind; unreal; as, impalpable distinctions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impalpably', 'adv.', 'In an impalpable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impalsy', 'v. t.', 'To palsy; to paralyze; to deaden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impanate', 'a.', 'Embodied in bread, esp. in the bread of the eucharist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impanated', 'imp. & p. p.', 'of Impanate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impanating', 'p. pr. & vb. n.', 'of Impanate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impanate', 'v. t.', 'To embody in bread, esp. in the bread of the\n   eucharist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impanation', 'a.', 'Embodiment in bread; the supposed real presence and\n   union of Christs material body and blood with the substance of the\n   elements of the eucharist without a change in their nature; --\n   distinguished from transubstantiation, which supposes a miraculous\n   change of the substance of the elements. It is akin to\n   consubstantiation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impanator', 'n.', 'One who holds the doctrine of impanation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impaneled', 'imp. & p. p.', 'of Impanel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impanelled', '', 'of Impanel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impaneling', '', 'of Impanel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impanelling', '', 'of Impanel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impanel', 'v. t.', 'To enter in a list, or on a piece of parchment, called\n   a panel; to form or enroll, as a list of jurors in a court of justice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impanelment', 'n.', 'The act or process of impaneling, or the state of\n   being impaneled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imparadised', 'imp. & p. p.', 'of Imparadise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imparadising', 'p. pr. & vb. n.', 'of Imparadise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imparadise', 'v. t.', 'To put in a state like paradise; to make supremely\n   happy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imparalleled', 'a.', 'Unparalleled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impardonable', 'a.', 'Unpardonable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imparidigitate', 'a.', 'Having an odd number of fingers or toes, either\n   one, three, or five, as in the horse, tapir, rhinoceros, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imparipinnate', 'a.', 'Pinnate with a single terminal leaflet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imparisyllabic', 'a.', 'Not consisting of an equal number of syllables; as,\n   an imparisyllabic noun, one which has not the same number of syllables\n   in all the cases; as, lapis, lapidis; mens, mentis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imparity', 'n.', 'Inequality; disparity; disproportion; difference of\n   degree, rank, excellence, number, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imparity', 'n.', 'Lack of comparison, correspondence, or suitableness;\n   incongruity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imparity', 'n.', 'Indivisibility into equal parts; oddness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imparked', 'imp. & p. p.', 'of Impark');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imparking', 'p. pr. & vb. n.', 'of Impark');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impark', 'v. t.', 'To inclose for a park; to sever from a common; hence, to\n   inclose or shut up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imparl', 'v. i.', 'To hold discourse; to parley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imparl', 'v. i.', 'To have time before pleading; to have delay for mutual\n   adjustment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imparlance', 'n.', 'Mutual discourse; conference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imparlance', 'n.', 'Time given to a party to talk or converse with his\n   opponent, originally with the object of effecting, if possible, an\n   amicable adjustment of the suit. The actual object, however, has long\n   been merely to obtain further time to plead, or answer to the\n   allegations of the opposite party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imparlance', 'n.', 'Hence, the delay or continuance of a suit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imparsonee', 'a.', 'Presented, instituted, and inducted into a rectory, and\n   in full possession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imparsonee', 'n.', 'A clergyman so inducted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imparted', 'imp. & p. p.', 'of Impart');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imparting', 'p. pr. & vb. n.', 'of Impart');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impart', 'n.', 'To bestow a share or portion of; to give, grant, or\n   communicate; to allow another to partake in; as, to impart food to the\n   poor; the sun imparts warmth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impart', 'n.', 'To obtain a share of; to partake of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impart', 'n.', 'To communicate the knowledge of; to make known; to show by\n   words or tokens; to tell; to disclose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impart', 'v. i.', 'To give a part or share.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impart', 'v. i.', 'To hold a conference or consultation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impartance', 'n.', 'Impartation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impartation', 'n.', 'The act of imparting, or the thing imparted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imparter', 'n.', 'One who imparts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impartial', 'a.', 'Not partial; not favoring one more than another;\n   treating all alike; unprejudiced; unbiased; disinterested; equitable;\n   fair; just.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impartialist', 'n.', 'One who is impartial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impartiality', 'n.', 'The quality of being impartial; freedom from bias or\n   favoritism; disinterestedness; equitableness; fairness; as,\n   impartiality of judgment, of treatment, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impartially', 'a.', 'In an impartial manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impartialness', 'n.', 'Impartiality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impartibility', 'n.', 'The quality of being impartible; communicability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impartibility', 'n.', 'The quality of being incapable of division into\n   parts; indivisibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impartible', 'a.', 'Capable of being imparted or communicated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impartible', 'a.', 'Not partible; not subject to partition; indivisible;\n   as, an impartible estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impartment', 'n.', 'The act of imparting, or that which is imparted,\n   communicated, or disclosed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impassable', 'a.', 'Incapable of being passed; not admitting a passage; as,\n   an impassable road, mountain, or gulf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impassibility', 'a.', 'The quality or condition of being impassible;\n   insusceptibility of injury from external things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impassible', 'a.', 'Incapable of suffering; inaccessible to harm or pain;\n   not to be touched or moved to passion or sympathy; unfeeling, or not\n   showing feeling; without sensation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impassibleness', 'n.', 'Impassibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impassion', 'v.', 'To move or affect strongly with passion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impassionable', 'a.', 'Excitable; susceptible of strong emotion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impassionate', 'a.', 'Strongly affected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impassionate', 'v. t.', 'To affect powerfully; to arouse the passions of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impassionate', 'a.', 'Without passion or feeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impassioned', 'p. p. & a.', 'Actuated or characterized by passion or zeal;\n   showing warmth of feeling; ardent; animated; excited; as, an\n   impassioned orator or discourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impassive', 'a.', 'Not susceptible of pain or suffering; apathetic;\n   impassible; unmoved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impassivity', 'n.', 'The quality of being insusceptible of feeling, pain,\n   or suffering; impassiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impastation', 'n.', 'The act of making into paste; that which is formed\n   into a paste or mixture; specifically, a combination of different\n   substances by means of cements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impasted', 'imp. & p. p.', 'of Impaste');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impasting', 'p. pr. & vb. n.', 'of Impaste');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impaste', 'v. t.', 'To knead; to make into paste; to concrete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impaste', 'v. t.', 'To lay color on canvas by uniting them skillfully\n   together. [R.] Cf. Impasto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impasting', '', 'The laying on of colors to produce impasto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impasto', 'n.', 'The thickness of the layer or body of pigment applied by\n   the painter to his canvas with especial reference to the juxtaposition\n   of different colors and tints in forming a harmonious whole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impasture', 'v. t.', 'To place in a pasture; to foster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impatible', 'a.', 'Not capable of being borne; impassible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impatience', 'n.', 'The quality of being impatient; want of endurance of\n   pain, suffering, opposition, or delay; eagerness for change, or for\n   something expected; restlessness; chafing of spirit; fretfulness;\n   passion; as, the impatience of a child or an invalid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impatiency', 'n.', 'Impatience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impatiens', 'n.', 'A genus of plants, several species of which have very\n   beautiful flowers; -- so called because the elastic capsules burst when\n   touched, and scatter the seeds with considerable force. Called also\n   touch-me-not, jewelweed, and snapweed. I. Balsamina (sometimes called\n   ladys slipper) is the common garden balsam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impatient', 'a.', 'Not patient; not bearing with composure; intolerant;\n   uneasy; fretful; restless, because of pain, delay, or opposition; eager\n   for change, or for something expected; hasty; passionate; -- often\n   followed by at, for, of, and under.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impatient', 'a.', 'Not to be borne; unendurable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impatient', 'a.', 'Prompted by, or exhibiting, impatience; as, impatient\n   speeches or replies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impatient', 'n.', 'One who is impatient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impatiently', 'adv.', 'In an impatient manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impatronization', 'n.', 'Absolute seignory or possession; the act of\n   investing with such possession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impatronized', 'imp. & p. p.', 'of Impatronize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impatronizing', 'p. pr. & vb. n.', 'of Impatronize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impatronize', 'v. t.', 'To make lord or master; as, to impatronize ones\n   self of a seigniory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impave', 'v. t.', 'To pave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impavid', 'a.', 'Fearless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impawned', 'imp. & p. p.', 'of Impawn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impawning', 'p. pr. & vb. n.', 'of Impawn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impawn', 'v. t.', 'To put in pawn; to pledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impeached', 'imp. & p. p.', 'of Impeach');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impeaching', 'p. pr. & vb. n.', 'of Impeach');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impeach', 'v. t.', 'To hinder; to impede; to prevent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impeach', 'v. t.', 'To charge with a crime or misdemeanor; to accuse;\n   especially to charge (a public officer), before a competent tribunal,\n   with misbehavior in office; to cite before a tribunal for judgement of\n   official misconduct; to arraign; as, to impeach a judge. See\n   Impeachment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impeach', 'v. t.', 'Hence, to charge with impropriety; to dishonor; to\n   bring discredit on; to call in question; as, to impeach ones motives\n   or conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impeach', 'v. t.', 'To challenge or discredit the credibility of, as of a\n   witness, or the validity of, as of commercial paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impeach', 'n.', 'Hindrance; impeachment.');
