  INSERT INTO words (word, word_type, description) VALUES  (    'Impetrated', 'imp. & p. p.', 'of Impetrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impetrating', 'p. pr. & vb. n.', 'of Impetrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impetrate', 'v. t.', 'To obtain by request or entreaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impetration', 'n.', 'The act of impetrating, or obtaining by petition or\n   entreaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impetration', 'n.', 'The obtaining of benefice from Rome by solicitation,\n   which benefice belonged to the disposal of the king or other lay patron\n   of the realm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impetrative', 'a.', 'Of the nature of impetration; getting, or tending to\n   get, by entreaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impetratory', 'a.', 'Containing or expressing entreaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impetuosity', 'n.', 'The condition or quality of being impetuous; fury;\n   violence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impetuosity', 'n.', 'Vehemence, or furiousnes of temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impetuous', 'a.', 'Rushing with force and violence; moving with impetus;\n   furious; forcible; violent; as, an impetuous wind; an impetuous\n   torrent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impetuous', 'a.', 'Vehement in feeling; hasty; passionate; violent; as, a\n   man of impetuous temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impetus', 'n.', 'A property possessed by a moving body in virtue of its\n   weight and its motion; the force with which any body is driven or\n   impelled; momentum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impetus', 'n.', 'Fig.: Impulse; incentive; vigor; force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impetus', 'n.', 'The aititude through which a heavy body must fall to\n   acquire a velocity equal to that with which a ball is discharged from a\n   piece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impeyan pheasant', '', 'An Indian crested pheasant of the genus\n   Lophophorus. Several species are known. Called also monaul, monal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imphee', 'n.', 'The African sugar cane (Holcus saccharatus), -- resembling\n   the sorghum, or Chinese sugar cane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impictured', 'a.', 'Pictured; impressed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impierce', 'v. t.', 'To pierce; to penetrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impierceable', 'a.', 'Not capable of being pierced; impenetrable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impieties', 'pl. ', 'of Impiety');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impiety', 'n.', 'The quality of being impious; want of piety; irreverence\n   toward the Supreme Being; ungodliness; wickedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impiety', 'n.', 'An impious act; an act of wickednes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impignorate', 'v. t.', 'To pledge or pawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impignoration', 'n.', 'The act of pawning or pledging; the state of being\n   pawned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imping', 'n.', 'The act or process of grafting or mending.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imping', 'n.', 'The process of repairing broken feathers or a deficient\n   wing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impinged', 'imp. & p. p.', 'of Impinge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impinging', 'p. pr. & vb. n.', 'of Impinge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impinge', 'v. t.', 'To fall or dash against; to touch upon; to strike; to\n   hit; to ciash with; -- with on or upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impingement', 'n.', 'The act of impinging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impingent', 'a.', 'Striking against or upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impinguate', 'v. t.', 'To fatten; to make fat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impinguation', 'n.', 'The act of making fat, or the state of being fat or\n   fattened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impious', 'a.', 'Not pious; wanting piety; irreligious; irreverent;\n   ungodly; profane; wanting in reverence for the Supreme Being; as, an\n   impious deed; impious language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impire', 'n.', 'See Umpire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impishly', 'a.', 'Having the qualities, or showing the characteristics, of\n   an imp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impishly', 'adv.', 'In the manner of an imp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imppiteous', 'a.', 'Pitiless; cruel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implacability', 'n.', 'The quality or state of being implacable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implacable', 'a.', 'Not placable; not to be appeased; incapable of being\n   pacified; inexorable; as, an implacable prince.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implacable', 'a.', 'Incapable of ebign relieved or assuaged;\n   inextinguishable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implacableness', 'n.', 'The quality of being implacable; implacability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implacably', 'adv.', 'In an implacable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implacental', 'a.', 'Without a placenta, as marsupials and monotremes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implacental', 'n.', 'A mammal having no placenta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implacentalia', 'n. pl.', 'A primary division of the Mammalia, including\n   the monotremes and marsupials, in which no placenta is formed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implanted', 'imp. & p. p.', 'of Implant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implanting', 'p. pr. & vb. n.', 'of Implant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implant', 'v. t.', 'To plant, or infix, for the purpose of growth; to fix\n   deeply; to instill; to inculate; to introduce; as, to implant the seeds\n   of virtue, or the principles of knowledge, in the minds of youth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implantation', 'n.', 'The act or process of implantating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implated', 'imp. & p. p.', 'of Implate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implating', 'p. pr. & vb. n.', 'of Implate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implate', 'v. t.', 'To cover with plates; to sheathe; as, to implate a ship\n   with iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implausibility', 'n.', 'Want of plausibility; the quality of being\n   implausible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implausible', 'a.', 'Not plausible; not wearing the appearance of truth or\n   credibility, and not likely to be believed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impleach', 'v. t.', 'To pleach; to interweave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impleaded', 'imp. & p. p.', 'of Implead');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impleading', 'p. pr. & vb. n.', 'of Implead');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implead', 'v. t.', 'To institute and prosecute a suit against, in court; to\n   sue or prosecute at law; hence, to accuse; to impeach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implead', 'v. i.', 'To sue at law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impleadable', 'a.', 'Not admitting excuse, evasion, or plea; rigorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impleader', 'n.', 'One who prosecutes or sues another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impleasing', 'a.', 'Unpleasing; displeasing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impledge', 'v. t.', 'To pledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implement', 'n.', 'That which fulfills or supplies a want or use; esp., an\n   instrument, toll, or utensil, as supplying a requisite to an end; as,\n   the implements of trade, of husbandry, or of war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implement', 'v. t.', 'To accomplish; to fulfill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implement', 'v. t.', 'To provide with an implement or implements; to cause\n   to be fulfilled, satisfied, or carried out, by means of an implement or\n   implements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implement', 'v. t.', 'To fulfill or perform, as a contract or an\n   engagement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implemental', 'a.', 'Pertaining to, or characterized by, implements or\n   their use; mechanical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impletion', 'n.', 'The act of filling, or the state of being full.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impletion', 'n.', 'That which fills up; filling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implex', 'a.', 'Intricate; entangled; complicated; complex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implexion', 'n.', 'Act of involving, or state of being involved;\n   involution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impliable', 'a.', 'Not pliable; inflexible; inyielding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implicated', 'imp. & p. p.', 'of Implicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implicating', 'p. pr. & vb. n.', 'of Implicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implicate', 'v. t.', 'To infold; to fold together; to interweave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implicate', 'v. t.', 'To bring into connection with; to involve; to\n   connect; -- applied to persons, in an unfavorable sense; as, the\n   evidence implicates many in this conspiracy; to be implicated in a\n   crime, a discreditable transaction, a fault, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implication', 'n.', 'The act of implicating, or the state of being\n   implicated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implication', 'n.', 'An implying, or that which is implied, but not\n   expressed; an inference, or something which may fairly be understood,\n   though not expressed in words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implicative', 'a.', 'Tending to implicate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implicatively', 'adv.', 'By implication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implicit', 'a.', 'Infolded; entangled; complicated; involved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implicit', 'a.', 'Tacitly comprised; fairly to be understood, though not\n   expressed in words; implied; as, an implicit contract or agreement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implicit', 'a.', 'Resting on another; trusting in the word or authority of\n   another, without doubt or reserve; unquestioning; complete; as,\n   implicit confidence; implicit obedience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implicitly', 'adv.', 'In an implicit manner; without reserve; with\n   unreserved confidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implicitly', 'adv.', 'By implication; impliedly; as, to deny the providence\n   of God is implicitly to deny his existence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implicitness', 'n.', 'State or quality of being implicit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implicity', 'n.', 'Implicitness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implied', 'a.', 'Virtually involved or included; involved in substance;\n   inferential; tacitly conceded; -- the correlative of express, or\n   expressed. See Imply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impliedly', 'adv.', 'By implication or inference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imploded', 'a.', 'Formed by implosion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implodent', 'n.', 'An implosive sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imploration', 'n.', 'The act of imploring; earnest supplication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implorator', 'n.', 'One who implores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imploratory', 'a.', 'Supplicatory; entreating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implored', 'imp. & p. p.', 'of Implore');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imploring', 'p. pr. & vb. n.', 'of Implore');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implore', 'v. t.', 'To call upon, or for, in supplication; to beseech; to\n   prey to, or for, earnestly; to petition with urency; to entreat; to\n   beg; -- followed directly by the word expressing the thing sought, or\n   the person from whom it is sought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implore', 'v. i.', 'To entreat; to beg; to prey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implore', 'n.', 'Imploration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implorer', 'n.', 'One who implores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imploring', 'a.', 'That implores; beseeching; entreating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implosion', 'n.', 'A burstion inwards, as of a vessel from which the air\n   has been exhausted; -- contrasted with explosion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implosion', 'n.', 'A sudden compression of the air in the mouth,\n   simultaneously with and affecting the sound made by the closure of the\n   organs in uttering p, t, or k, at the end of a syllable (see Guide to\n   Pronunciation, //159, 189); also, a similar compression made by an\n   upward thrust of the larynx without any accompanying explosive action,\n   as in the peculiar sound of b, d, and g, heard in Southern Germany.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implosive', 'a.', 'Formed by implosion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implosive', 'n.', 'An implosive sound, an implodent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implumed', 'a.', 'Not plumed; without plumes or feathers; featherless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implunge', 'v. t.', 'To plunge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impluvium', 'n.', 'In Roman dwellings, a cistern or tank, set in the atrium\n   or peristyle to recieve the water from the roof, by means of the\n   compluvium; generally made ornamental with flowers and works of art\n   around its birm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implied', 'imp. & p. p.', 'of Imply');
  INSERT INTO words (word, word_type, description) VALUES  (    'Implying', 'p. pr. & vb. n.', 'of Imply');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imply', 'v. t.', 'To infold or involve; to wrap up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imply', 'v. t.', 'To involve in substance or essence, or by fair inference,\n   or by construction of law, when not include virtually; as, war implies\n   fighting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imply', 'v. t.', 'To refer, ascribe, or attribute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impoison', 'v. t.', 'To poison; to imbitter; to impair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impoisoner', 'n.', 'A poisoner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impoisonment', 'n.', 'The act of poisoning or impoisoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impolarily', 'adv.', 'Alt. of Impolarly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impolarly', 'adv.', 'Not according to or in, the direction of the poles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impolicy', 'n.', 'The quality of being impolitic; inexpedience;\n   unsuitableness to the end proposed; bads policy; as, the impolicy of\n   fraud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impolite', 'a.', 'Not polite; not of polished manners; wanting in good\n   manners; discourteous; uncivil; rude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impolitic', 'a.', 'Not politic; contrary to, or wanting in, policy; unwise;\n   imprudent; indiscreet; inexpedient; as, an impolitic ruler, law, or\n   measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impolitical', 'a.', 'Impolitic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impoliticly', 'adv.', 'In an impolitic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impoliticness', 'n.', 'The quality of being impolitic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imponderability', 'n.', 'The quality or state of being imponderable;\n   imponderableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imponderable', 'a.', 'Not ponderable; without sensible or appreciable\n   weight; incapable of being weighed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imponderable', 'n.', 'An imponderable substance or body; specifically, in\n   the plural, a name formerly applied to heat, light, electricity, and\n   magnetism, regarded as subtile fluids destitute of weight but in modern\n   science little used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imponderableness', 'n.', 'The quality or state of being imponderable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imponderous', 'a.', 'Imponderable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impone', 'v. t.', 'To stake; to wager; to pledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impoofo', 'n.', 'The eland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impoon', 'n.', 'The duykerbok.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impoor', 'v. t.', 'To impoverish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imporosity', 'n.', 'The state or quality of being imporous; want of\n   porosity; compactness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imporous', 'a.', 'Destitute of pores; very close or compact in texture;\n   solid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imported', 'imp. & p. p.', 'of Import');
  INSERT INTO words (word, word_type, description) VALUES  (    'Importing', 'p. pr. & vb. n.', 'of Import');
  INSERT INTO words (word, word_type, description) VALUES  (    'Import', 'v. t.', 'To bring in from abroad; to introduce from without;\n   especially, to bring (wares or merchandise) into a place or country\n   from a foreign country, in the transactions of commerce; -- opposed to\n   export. We import teas from China, coffee from Brasil, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Import', 'v. t.', 'To carry or include, as meaning or intention; to imply;\n   to signify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Import', 'v. t.', 'To be of importance or consequence to; to have a bearing\n   on; to concern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Import', 'v. i.', 'To signify; to purport; to be of moment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Import', 'n.', 'Merchandise imported, or brought into a country from\n   without its boundaries; -- generally in the plural, opposed to exports.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Import', 'n.', 'That which a word, phrase, or document contains as its\n   signification or intention or interpretation of a word, action, event,\n   and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Import', 'n.', 'Importance; weight; consequence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Importable', 'a.', 'Capable of being imported.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Importable', 'a.', 'Not to be endured; insupportable; intolerable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Importance', 'n.', 'The quality or state of being important; consequence;\n   weight; moment; significance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Importance', 'n.', 'Subject; matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Importance', 'n.', 'Import; meaning; significance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Importance', 'n.', 'Importunity; solicitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Importancy', 'n.', 'Importance; significance; consequence; that which is\n   important.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Important', 'v. t.', 'Full of, or burdened by, import; charged with great\n   interests; restless; anxious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Important', 'v. t.', 'Carrying or possessing weight or consequence; of\n   valuable content or bearing; significant; weighty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Important', 'v. t.', 'Bearing on; forcible; driving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Important', 'v. t.', 'Importunate; pressing; urgent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Importantly', 'adv.', 'In an important manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Importation', 'v. t.', 'The act of carrying, conveying, or delivering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Importation', 'v. t.', 'The act or practice of importing, or bringing into\n   a country or state; -- opposed to exportation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Importation', 'v. t.', 'That which is imported; commodities or wares\n   introduced into a country from abroad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Importer', 'n.', 'One who imports; the merchant who brings goods into a\n   country or state; -- opposed to exporter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Importing', 'a.', 'Full of meaning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Importless', 'a.', 'Void of meaning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Importunable', 'a.', 'Heavy; insupportable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Importunacy', 'n.', 'The quality of being importunate; importunateness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Importunate', 'a.', 'Troublesomely urgent; unreasonably solicitous;\n   overpressing in request or demand; urgent; teasing; as, an impotunate\n   petitioner, curiosity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Importunate', 'a.', 'Hard to be borne; unendurable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Importunator', 'n.', 'One who importunes; an importuner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Importunee', 'a.', 'Inopportune; unseasonable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Importunee', 'a.', 'Troublesome; vexatious; persistent; urgent; hence,\n   vexatious on account of untimely urgency or perinacious solicitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Importuned', 'imp. & p. p.', 'of Importune');
  INSERT INTO words (word, word_type, description) VALUES  (    'Importuning', 'p. pr. & vb. n.', 'of Importune');
  INSERT INTO words (word, word_type, description) VALUES  (    'Importune', 'a.', 'To request or solicit, with urgency; to press with\n   frequent, unreasonable, or troublesome application or pertinacity;\n   hence, to tease; to irritate; to worry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Importune', 'a.', 'To import; to signify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Importune', 'v. i.', 'To require; to demand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Importunely', 'adv.', 'In an importune manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Importuner', 'n.', 'One who importunes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Importunities', 'pl. ', 'of Importunity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Importunity', 'n.', 'The quality of being importunate; pressing or\n   pertinacious solicitation; urgent request; incessant or frequent\n   application; troublesome pertinacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Importuous', 'a.', 'Without a port or harbor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imposable', 'a.', 'Capable of being imposed or laid on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imposableness', 'n.', 'Quality of being imposable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imposed', 'imp. & p. p.', 'of Impose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imposing', 'p. pr. & vb. n.', 'of Impose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impose', 'v. t.', 'To lay on; to set or place; to put; to deposit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impose', 'v. t.', 'To lay as a charge, burden, tax, duty, obligation,\n   command, penalty, etc.; to enjoin; to levy; to inflict; as, to impose a\n   toll or tribute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impose', 'v. t.', 'To lay on, as the hands, in the religious rites of\n   confirmation and ordination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impose', 'v. t.', 'To arrange in proper order on a table of stone or metal\n   and lock up in a chase for printing; -- said of columns or pages of\n   type, forms, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impose', 'v. i.', 'To practice trick or deception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impose', 'n.', 'A command; injunction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imposement', 'n.', 'Imposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imposer', 'n.', 'One who imposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imposing', 'a.', 'Laying as a duty; enjoining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imposing', 'a.', 'Adapted to impress forcibly; impressive; commanding; as,\n   an imposing air; an imposing spectacle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imposing', 'a.', 'Deceiving; deluding; misleading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imposing', 'n.', 'The act of imposing the columns of a page, or the pages\n   of a sheet. See Impose, v. t., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imposingly', 'adv.', 'In an imposing manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imposingness', 'n.', 'The quality of being imposing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imposition', 'n.', 'The act of imposing, laying on, affixing, enjoining,\n   inflicting, obtruding, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imposition', 'n.', 'That which is imposed, levied, or enjoined; charge;\n   burden; injunction; tax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imposition', 'n.', 'An extra exercise enjoined on students as a punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imposition', 'n.', 'An excessive, arbitrary, or unlawful exaction; hence, a\n   trick or deception put on laid on others; cheating; fraud; delusion;\n   imposture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imposition', 'n.', 'The act of laying on the hands as a religious ceremoy,\n   in ordination, confirmation, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imposition', 'n.', 'The act or process of imosing pages or columns of type.\n   See Impose, v. t., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impossibilities', 'pl. ', 'of Impossibility');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impossibility', 'n.', 'The quality of being impossible; impracticability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impossibility', 'n.', 'An impossible thing; that which can not be thought,\n   done, or endured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impossibility', 'n.', 'Inability; helplessness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impossible', 'a.', 'Not possible; incapable of being done, of existing,\n   etc.; unattainable in the nature of things, or by means at command;\n   insuperably difficult under the circumstances; absurd or impracticable;\n   not feasible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impossible', 'n.', 'An impossibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impossibly', 'adv.', 'Not possibly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impost', 'n.', 'That which is imposed or levied; a tax, tribute, or duty;\n   especially, a duty or tax laid by goverment on goods imported into a\n   country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impost', 'n.', 'The top member of a pillar, pier, wall, etc., upon which\n   the weight of an arch rests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imposthumate', 'v. t.', 'To apostemate; to form an imposthume or abscess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imposthumated', 'imp. & p. p.', 'of Imposthumate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imposthumating', 'p. pr. & vb. n.', 'of Imposthumate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imposthumate', 'v. t.', 'To affect with an imposthume or abscess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imposthumate', 'a.', 'Imposthumated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imposthumation', 'n.', 'The act of forming an abscess; state of being\n   inflamed; suppuration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imposthumation', 'n.', 'An abscess; an imposthume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imposthume', 'n.', 'A collection of pus or purulent matter in any part of\n   an animal body; an abscess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imposthume', 'v. t. & i.', 'Same as Imposthumate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impostor', 'n.', 'One who imposes upon others; a person who assumes a\n   character or title not his own, for the purpose of deception; a\n   pretender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impostorship', 'n.', 'The condition, character, or practice of an impostor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impostress', 'n.', 'Alt. of Impostrix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impostrix', 'n.', 'A woman who imposes upon or deceives others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impostrous', 'n.', 'Characterized by imposture; deceitful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imposturage', 'n.', 'Imposture; cheating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imposture', 'n.', 'The act or conduct of an impostor; deception practiced\n   under a false or assumed character; fraud or imposition; cheating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impostured', 'a.', 'Done by imposture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imposturous', 'a.', 'Impostrous; deceitful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impostury', 'n.', 'Imposture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impotence', 'n.', 'Alt. of Impotency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impotency', 'n.', 'The quality or condition of being impotent; want of\n   strength or power, animal, intellectual, or moral; weakness;\n   feebleness; inability; imbecility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impotency', 'n.', 'Want of self-restraint or self-control.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impotency', 'n.', 'Want of procreative power; inability to copulate, or\n   beget children; also, sometimes, sterility; barrenness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impotent', 'a.', 'Not potent; wanting power, strength. or vigor. whether\n   physical, intellectual, or moral; deficient in capacity; destitute of\n   force; weak; feeble; infirm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impotent', 'a.', 'Wanting the power of self-restraint; incontrolled;\n   ungovernable; violent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impotent', 'a.', 'Wanting the power of procreation; unable to copulate;\n   also, sometimes, sterile; barren.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impotent', 'n.', 'One who is imoitent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impotently', 'adv.', 'In an impotent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impounded', 'imp. & p. p.', 'of Impound');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impounding', 'p. pr. & vb. n.', 'of Impound');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impound', 'v. t.', 'To shut up or place in an inclosure called a pound;\n   hence, to hold in the custody of a court; as, to impound stray cattle;\n   to impound a document for safe keeping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impoundage', 'n.', 'The act of impounding, or the state of being impounded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impoundage', 'n.', 'The fee or fine for impounding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impounder', 'n.', 'One who impounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impoverished', 'imp. & p. p.', 'of Impoverish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impoverishing', 'p. pr. & vb. n.', 'of Impoverish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impoverish', 'v. t.', 'To make poor; to reduce to poverty or indigence; as,\n   misfortune and disease impoverish families.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impoverish', 'v. t.', 'To exhaust the strength, richness, or fertility of;\n   to make sterile; as, to impoverish land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impoverisher', 'n.', 'One who, or that which, impoverishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impoverishment', 'n.', 'The act of impoverishing, or the state of being\n   impoverished; reduction to poverty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impower', 'v. t.', 'See Empower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imp-pole', 'n.', 'A pole for supporting a scaffold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impracticabilities', 'pl. ', 'of Impracticability');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impracticability', 'n.', 'The state or quality of being impracticable;\n   infeasibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impracticability', 'n.', 'An impracticable thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impracticability', 'n.', 'Intractableness; stubbornness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impracticable', 'a.', 'Not practicable; incapable of being performed, or\n   accomplished by the means employed, or at command; impossible; as, an\n   impracticable undertaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impracticable', 'a.', 'Not to be overcome, presuaded, or controlled by any\n   reasonable method; unmanageable; intractable; not capable of being\n   easily dealt with; -- used in a general sense, as applied to a person\n   or thing that is difficult to control or get along with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impracticable', 'a.', 'Incapable of being used or availed of; as, an\n   impracticable road; an impracticable method.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impracticableness', 'n.', 'The state or quality of being impracticable;\n   impracticability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impracticably', 'adv.', 'In an impracticable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impractical', 'a.', 'Not practical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imprecated', 'imp. & p. p.', 'of Imprecate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imprecating', 'p. pr. & vb. n.', 'of Imprecate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imprecate', 'v. t.', 'To call down by prayer, as something hurtful or\n   calamitous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imprecate', 'v. t.', 'To invoke evil upon; to curse; to swear at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imprecation', 'n.', 'The act of imprecating, or invoking evil upon any one;\n   a prayer that a curse or calamity may fall on any one; a curse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imprecatory', 'a.', 'Of the nature of, or containing, imprecation; invoking\n   evil; as, the imprecatory psalms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imprecision', 'n.', 'Want of precision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impregn', 'v. t.', 'To impregnate; to make fruitful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impregnability', 'n.', 'The quality or state of being impregnable;\n   invincibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impregnable', 'a.', 'Not to be stormed, or taken by assault; incapable of\n   being subdued; able to resist attack; unconquerable; as, an impregnable\n   fortress; impregnable virtue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impregnable', 'a.', 'Capable of being impregnated, as the egg of an animal,\n   or the ovule of a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impregnant', 'n.', 'That which impregnates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impregnant', 'a.', 'Not pregnant; unfertilized or infertile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impregnated', 'imp. & p. p.', 'of Impregnate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impregnating', 'p. pr. & vb. n.', 'of Impregnate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impregnate', 'v. t.', 'To make pregnant; to cause to conceive; to render\n   prolific; to get with child or young.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impregnate', 'v. t.', 'To come into contact with (an ovum or egg) so as to\n   cause impregnation; to fertilize; to fecundate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impregnate', 'v. t.', 'To infuse an active principle into; to render\n   fruitful or fertile in any way; to fertilize; to imbue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impregnate', 'v. t.', 'To infuse particles of another substance into; to\n   communicate the quality of another to; to cause to be filled, imbued,\n   mixed, or furnished (with something); as, to impregnate India rubber\n   with sulphur; clothing impregnated with contagion; rock impregnated\n   with ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impregnate', 'v. i.', 'To become pregnant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impregnate', 'a.', 'Impregnated; made prolific.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impregnation', 'n.', 'The act of impregnating or the state of being\n   impregnated; fecundation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impregnation', 'n.', 'The fusion of a female germ cell (ovum) with a male\n   germ cell (in animals, a spermatozoon) to form a single new cell\n   endowed with the power of developing into a new individual;\n   fertilization; fecundation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impregnation', 'n.', 'That with which anything is impregnated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impregnation', 'n.', 'Intimate mixture; influsion; saturation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impregnation', 'n.', 'An ore deposit, with indefinite boundaries,\n   consisting of rock impregnated with ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imprejudicate', 'a.', 'Not prejuged; unprejudiced; impartial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imprenable', 'a.', 'Impregnable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impreparation', 'n.', 'Want of preparation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impresa', 'n.', 'A device on a shield or seal, or used as a bookplate or\n   the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impresarios', 'pl. ', 'of Impresario');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impresario', 'n.', 'The projector, manager, or conductor, of an opera or\n   concert company.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imprescriptibility', 'n.', 'The quality of being imprescriptible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imprescriptible', 'a.', 'Not capable of being lost or impaired by neglect,\n   by disuse, or by the claims of another founded on prescription.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imprescriptible', 'a.', 'Not derived from, or dependent on, external\n   authority; self-evidencing; obvious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imprescriptibly', 'adv.', 'In an imprescriptible manner; obviously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imprese', 'n.', 'A device. See Impresa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impressed', 'imp. & p. p.', 'of Impress');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impressing', 'p. pr. & vb. n.', 'of Impress');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impress', 'v. t.', 'To press, stamp, or print something in or upon; to mark\n   by pressure, or as by pressure; to imprint (that which bears the\n   impression).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impress', 'v. t.', 'To produce by pressure, as a mark, stamp, image, etc.;\n   to imprint (a mark or figure upon something).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impress', 'v. t.', 'Fig.: To fix deeply in the mind; to present forcibly to\n   the attention, etc.; to imprint; to inculcate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impress', 'n.', 'To take by force for public service; as, to impress\n   sailors or money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impress', 'v. i.', 'To be impressed; to rest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impresses', 'pl. ', 'of Impress');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impress', 'n.', 'The act of impressing or making.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impress', 'n.', 'A mark made by pressure; an indentation; imprint; the\n   image or figure of anything, formed by pressure or as if by pressure;\n   result produced by pressure or influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impress', 'n.', 'Characteristic; mark of distinction; stamp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impress', 'n.', 'A device. See Impresa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impress', 'n.', 'The act of impressing, or taking by force for the public\n   service; compulsion to serve; also, that which is impressed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impressibility', 'n.', 'The quality of being impressible; susceptibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impressible', 'a.', 'Capable of being impressed; susceptible; sensitive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impression', 'n.', 'The act of impressing, or the state of being impressed;\n   the communication of a stamp, mold, style, or character, by external\n   force or by influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impression', 'n.', 'That which is impressed; stamp; mark; indentation;\n   sensible result of an influence exerted from without.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impression', 'n.', 'That which impresses, or exercises an effect, action,\n   or agency; appearance; phenomenon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impression', 'n.', 'Influence or effect on the senses or the intellect\n   hence, interest, concern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impression', 'n.', 'An indistinct notion, remembrance, or belief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impression', 'n.', 'Impressiveness; emphasis of delivery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impression', 'n.', 'The pressure of the type on the paper, or the result of\n   such pressure, as regards its appearance; as, a heavy impression; a\n   clear, or a poor, impression; also, a single copy as the result of\n   printing, or the whole edition printed at a given time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impression', 'n.', 'In painting, the first coat of color, as the priming in\n   house painting and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impression', 'n.', 'A print on paper from a wood block, metal plate, or the\n   like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impressionability', 'n.', 'The quality of being impressionable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impresionable', 'a.', 'Liable or subject to impression; capable of being\n   molded; susceptible; impressible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impressionableness', 'n.', 'The quality of being impressionable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impressionism', 'n.', 'The theory or method of suggesting an effect or\n   impression without elaboration of the details; -- a disignation of a\n   recent fashion in painting and etching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impressionist', 'n.', 'One who adheres to the theory or method of\n   impressionism, so called.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impressionistic', 'a.', 'Pertaining to, or characterized by, impressionism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impressionless', 'a.', 'Having the quality of not being impressed or\n   affected; not susceptible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impressive', 'a.', 'Making, or tending to make, an impression; having power\n   to impress; adapted to excite attention and feeling, to touch the\n   sensibilities, or affect the conscience; as, an impressive discourse;\n   an impressive scene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impressive', 'a.', 'Capable of being impressed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impressment', 'n.', 'The act of seizing for public use, or of impressing\n   into public service; compulsion to serve; as, the impressment of\n   provisions or of sailors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impressor', 'n.', 'One who, or that which, impresses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impressure', 'n.', 'Dent; impression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imprested', 'imp. & p. p.', 'of Imprest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impresting', 'p. pr. & vb. n.', 'of Imprest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imprest', 'n.', 'To advance on loan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imprest', 'v. t.', 'A kind of earnest money; loan; -- specifically, money\n   advanced for some public service, as in enlistment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imprevalence', 'n.', 'Alt. of Imprevalency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imprevalency', 'n.', 'Want of prevalence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impreventability', 'n.', 'The state or quality of being impreventable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impreventable', 'a.', 'Not preventable; invitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imprimatur', 'n.', 'A license to print or publish a book, paper, etc.;\n   also, in countries subjected to the censorship of the press, approval\n   of that which is published.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imprimery', 'n.', 'A print; impression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imprimery', 'n.', 'A printing establishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imprimery', 'n.', 'The art of printing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impriming', 'n.', 'A beginning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imprimis', 'adv.', 'In the first place; first in order.');
