  INSERT INTO words (word, word_type, description) VALUES  (    'Imprint', 'v. t.', 'To impress; to mark by pressure; to indent; to stamp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imprint', 'v. t.', 'To stamp or mark, as letters on paper, by means of\n   type, plates, stamps, or the like; to print the mark (figures, letters,\n   etc., upon something).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imprint', 'v. t.', 'To fix indelibly or permanently, as in the mind or\n   memory; to impress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imprint', 'v. t.', 'Whatever is impressed or imprinted; the impress or mark\n   left by something; specifically, the name of the printer or publisher\n   (usually) with the time and place of issue, in the title-page of a\n   book, or on any printed sheet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imprisoned', 'imp. & p. p.', 'of Imprison');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imprisoning', 'p. pr. & vb. n.', 'of Imprison');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imprison', 'v. t.', 'To put in prison or jail; To arrest and detain in\n   custody; to confine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imprison', 'v. t.', 'To limit, restrain, or confine in any way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imprisoner', 'n.', 'One who imprisons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imprison ment', 'n.', 'The act of imprisoning, or the state of being\n   imprisoned; confinement; restraint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improbabilities', 'pl. ', 'of Improbability');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improbability', 'n.', 'The quality or state of being improbable;\n   unlikelihood; also, that which is improbable; an improbable event or\n   result.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improbable', 'a.', 'Not probable; unlikely to be true; not to be expected\n   under the circumstances or in the usual course of events; as, an\n   improbable story or event.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improbate', 'v. t.', 'To disapprove of; to disallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improbation', 'n.', 'The act of disapproving; disapprobation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improbation', 'n.', 'The act by which falsehood and forgery are proved; an\n   action brought for the purpose of having some instrument declared false\n   or forged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improbative', 'a.', 'Alt. of Improbatory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improbatory', 'a.', 'Implying, or tending to, improbation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improbity', 'n.', 'Lack of probity; want of integrity or rectitude;\n   dishonesty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improficience', 'n.', 'Alt. of Improficiency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improficiency', 'n.', 'Want of proficiency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improfitable', 'a.', 'Unprofitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improgressive', 'a.', 'Not progressive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improlific', 'a.', 'Not prolific.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improlificate', 'v. t.', 'To impregnate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imprompt', 'a.', 'Not ready.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impromptu', 'adv. / a.', 'Offhand; without previous study; extemporaneous;\n   extempore; as, an impromptu verse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impromptu', 'n.', 'Something made or done offhand, at the moment, or\n   without previous study; an extemporaneous composition, address, or\n   remark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impromptu', 'n.', 'A piece composed or played at first thought; a\n   composition in the style of an extempore piece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improper', 'a.', 'Not proper; not suitable; not fitted to the\n   circumstances, design, or end; unfit; not becoming; incongruous;\n   inappropriate; indecent; as, an improper medicine; improper thought,\n   behavior, language, dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improper', 'a.', 'Not peculiar or appropriate to individuals; general;\n   common.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improper', 'a.', 'Not according to facts; inaccurate; erroneous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improper', 'v. t.', 'To appropriate; to limit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improperation', 'n.', 'The act of upbraiding or taunting; a reproach; a\n   taunt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improperia', 'n. pl.', 'A series of antiphons and responses, expressing the\n   sorrowful remonstrance of our Lord with his people; -- sung on the\n   morning of the Good Friday in place of the usual daily Mass of the\n   Roman ritual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improperly', 'adv.', 'In an improper manner; not properly; unsuitably;\n   unbecomingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improperty', 'n.', 'Impropriety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impropitious', 'a.', 'Unpropitious; unfavorable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improportionable', 'a.', 'Not proportionable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improportionate', 'a.', 'Not proportionate.');
