  INSERT INTO words (word, word_type, description) VALUES  (    'Impropriating', 'p. pr. & vb. n.', 'of Impropriate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impropriate', 'v. t.', 'To appropriate to ones self; to assume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impropriate', 'v. t.', 'To place the profits of (ecclesiastical property)\n   in the hands of a layman for care and disbursement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impropriate', 'v. i.', 'To become an impropriator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impropriate', 'a.', 'Put into the hands of a layman; impropriated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impropriation', 'n.', 'The act of impropriating; as, the impropriation of\n   property or tithes; also, that which is impropriated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impropriation', 'n.', 'The act of putting an ecclesiastical benefice in the\n   hands of a layman, or lay corporation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impropriation', 'n.', 'A benefice in the hands of a layman, or of a lay\n   corporation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impropriator', 'n.', 'One who impropriates; specifically, a layman in\n   possession of church property.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-trixes', 'pl. ', 'of Impropriatrix');
  INSERT INTO words (word, word_type, description) VALUES  (    '-trices', 'pl. ', 'of Impropriatrix');
