  INSERT INTO words (word, word_type, description) VALUES  (    'Improprieties', 'pl. ', 'of Impropriety');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impropriety', 'n.', 'The quality of being improper; unfitness or\n   unsuitableness to character, time place, or circumstances; as,\n   impropriety of behavior or manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impropriety', 'n.', 'That which is improper; an unsuitable or improper act,\n   or an inaccurate use of language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improsperity', 'n.', 'Want of prosperity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improsperous', 'a.', 'Not prosperous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvability', 'n.', 'The state or quality of being improvable;\n   improvableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvable', 'a.', 'Capable of being improved; susceptible of improvement;\n   admitting of being made better; capable of cultivation, or of being\n   advanced in good qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvable', 'a.', 'Capable of being used to advantage; profitable;\n   serviceable; advantageous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improve', 'v. t.', 'To disprove or make void; to refute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improve', 'v. t.', 'To disapprove; to find fault with; to reprove; to\n   censure; as, to improve negligence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improved', 'imp. & p. p.', 'of Improve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improving', 'p. pr. & vb. n.', 'of Improve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improve', 'v. t.', 'To make better; to increase the value or good qualities\n   of; to ameliorate by care or cultivation; as, to improve land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improve', 'v. t.', 'To use or employ to good purpose; to make productive;\n   to turn to profitable account; to utilize; as, to improve ones time;\n   to improve his means.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improve', 'v. t.', 'To advance or increase by use; to augment or add to; --\n   said with reference to what is bad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improve', 'v. i.', 'To grow better; to advance or make progress in what is\n   desirable; to make or show improvement; as, to improve in health.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improve', 'v. i.', 'To advance or progress in bad qualities; to grow worse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improve', 'v. i.', 'To increase; to be enhanced; to rise in value; as, the\n   price of cotton improves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvement', 'n.', 'The act of improving; advancement or growth; promotion\n   in desirable qualities; progress toward what is better; melioration;\n   as, the improvement of the mind, of land, roads, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvement', 'n.', 'The act of making profitable use or applicaton of\n   anything, or the state of being profitably employed; a turning to good\n   account; practical application, as of a doctrine, principle, or theory,\n   stated in a discourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvement', 'n.', 'The state of being improved; betterment; advance;\n   also, that which is improved; as, the new edition is an improvement on\n   the old.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvement', 'n.', 'Increase; growth; progress; advance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvement', 'n.', 'Valuable additions or betterments, as buildings,\n   clearings, drains, fences, etc., on premises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvement', 'n.', 'A useful addition to, or modification of, a machine,\n   manufacture, or composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improver', 'n.', 'One who, or that which, improves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvided', 'a.', 'Unforeseen; unexpected; not provided against;\n   unprepared.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvidence', 'n.', 'The quality of being improvident; want of foresight\n   or thrift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvident', 'a.', 'Not provident; wanting foresight or forethought; not\n   foreseeing or providing for the future; negligent; thoughtless; as, an\n   improvident man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvidentially', 'adv.', 'Improvidently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvidently', 'adv.', 'In a improvident manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improving', 'a.', 'Tending to improve, beneficial; growing better.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvisate', 'a.', 'Unpremeditated; impromptu; extempore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvisated', 'imp. & p. p.', 'of Improvisate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvisating', 'p. pr. & vb. n.', 'of Improvisate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvisate', 'v. t. & i.', 'To improvise; to extemporize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvisation', 'n.', 'The act or art of composing and rendering music,\n   poetry, and the like, extemporaneously; as, improvisation on the organ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvisation', 'n.', 'That which is improvised; an impromptu.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvisatize', 'v. t. & i.', 'Same as Improvisate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvisator', 'n.', 'An improviser, or improvvisatore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvisatore', 'n.', 'See Improvvisatore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvisatorial', 'a.', 'Alt. of Improvisatory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvisatory', 'a.', 'Of or pertaining to improvisation or extemporaneous\n   composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvisatrice', 'n.', 'See Improvvisatrice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvised', 'imp. & p. p.', 'of Improvise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvising', 'p. pr. & vb. n.', 'of Improvise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvise', 'v. t.', 'To compose, recite, or sing extemporaneously,\n   especially in verse; to extemporize; also, to play upon an instrument,\n   or to act, extemporaneously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvise', 'v. t.', 'To bring about, arrange, or make, on a sudden, or\n   without previous preparation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvise', 'v. t.', 'To invent, or provide, offhand, or on the spur of the\n   moment; as, he improvised a hammer out of a stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvise', 'v. i.', 'To produce or render extemporaneous compositions,\n   especially in verse or in music, without previous preparation; hence,\n   to do anything offhand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improviser', 'n.', 'One who improvises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvision', 'n.', 'Improvidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improviso', 'a.', 'Not prepared or mediated beforehand; extemporaneous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvvisatori', 'pl. ', 'of Improvvisatore');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvvisatore', 'n.', 'One who composes and sings or recites rhymes and\n   short poems extemporaneously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvvisatrici', 'pl. ', 'of Improvvisatrice');
  INSERT INTO words (word, word_type, description) VALUES  (    'Improvvisatrice', 'n.', 'A female improvvisatore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imprudence', 'n.', 'The quality or state of being imprudent; want to\n   caution, circumspection, or a due regard to consequences; indiscretion;\n   inconsideration; reshness; also, an imprudent act; as, he was guilty of\n   an imprudence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imprudent', 'a.', 'Not prudent; wanting in prudence or discretion;\n   indiscreet; injudicious; not attentive to consequence; improper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impuberal', 'a.', 'Not having arrived at puberty; immature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impuberty', 'n.', 'The condition of not having reached puberty, or the age\n   of ability to reproduce ones species; want of age at which the\n   marriage contract can be legally entered into.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impudence', 'n.', 'The quality of being impudent; assurance, accompanied\n   with a disregard of the presence or opinions of others; shamelessness;\n   forwardness; want of modesty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impudency', 'n.', 'Impudence.');
