  INSERT INTO words (word, word_type, description) VALUES  (    'Impudently', 'adv.', 'In an impudent manner; with unbecoming assurance;\n   shamelessly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impudicity', 'n.', 'Immodesty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impugned', 'imp. & p. p.', 'of Impugn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impugning', 'p. pr. & vb. n.', 'of Impugn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impugn', 'v. t.', 'To attack by words or arguments; to contradict; to\n   assail; to call in question; to make insinuations against; to gainsay;\n   to oppose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impugnable', 'a.', 'Capable of being impugned; that may be gainsaid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impugnation', 'n.', 'Act of impugning; opposition; attack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impugner', 'n.', 'One who impugns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impugnment', 'n.', 'The act of impugning, or the state of being impugned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impuissance', 'n.', 'Lack of power; inability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impuissant', 'a.', 'Weak; impotent; feeble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impulse', 'n.', 'The act of impelling, or driving onward with sudden force;\n   impulsion; especially, force so communicated as to produced motion\n   suddenly, or immediately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impulse', 'n.', 'The effect of an impelling force; motion produced by a\n   sudden or momentary force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impulse', 'n.', 'The action of a force during a very small interval of\n   time; the effect of such action; as, the impulse of a sudden blow upon\n   a hard elastic body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impulse', 'n.', 'A mental force which simply and directly urges to action;\n   hasty inclination; sudden motive; momentary or transient influence of\n   appetite or passion; propension; incitement; as, a man of good\n   impulses; passion often gives a violent impulse to the will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impulse', 'v. t.', 'To impel; to incite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impulsion', 'n.', 'The act of impelling or driving onward, or the state of\n   being impelled; the sudden or momentary agency of a body in motion on\n   another body; also, the impelling force, or impulse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impulsion', 'n.', 'Influence acting unexpectedly or temporarily on the\n   mind; sudden motive or influence; impulse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impulsive', 'a.', 'Having the power of driving or impelling; giving an\n   impulse; moving; impellent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impulsive', 'a.', 'Actuated by impulse or by transient feelings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impulsive', 'a.', 'Acting momentarily, or by impulse; not continuous; --\n   said of forces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impulsive', 'n.', 'That which impels or gives an impulse; an impelling\n   agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impulsively', 'adv.', 'In an impulsive manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impulsiveness', 'n.', 'The quality of being impulsive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impulsor', 'n.', 'One who, or that which, impels; an inciter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impunctate', 'a.', 'Not punctuate or dotted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impunctual', 'a.', 'Not punctual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impunctuality', 'n.', 'Neglect of, or failure in, punctuality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impune', 'a.', 'Unpunished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impunibly', 'adv.', 'Without punishment; with impunity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impunity', 'n.', 'Exemption or freedom from punishment, harm, or loss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impuration', 'n.', 'Defilement; obscuration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impure', 'a.', 'Not pure; not clean; dirty; foul; filthy; containing\n   something which is unclean or unwholesome; mixed or impregnated\n   extraneous substances; adulterated; as, impure water or air; impure\n   drugs, food, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impure', 'a.', 'Defiled by sin or guilt; unholy; unhallowed; -- said of\n   persons or things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impure', 'a.', 'Unchaste; lewd; unclean; obscene; as, impure language or\n   ideas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impure', 'a.', 'Not purified according to the ceremonial law of Moses;\n   unclean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impure', 'a.', 'Not accurate; not idiomatic; as, impure Latin; an impure\n   style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impure', 'v. t.', 'To defile; to pollute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impurely', 'adv.', 'In an impure manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impureness', 'n.', 'The quality or condition of being impure; impurity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impurity', 'n.', 'The condition or quality of being impure in any sense;\n   defilement; foulness; adulteration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impurity', 'n.', 'That which is, or which renders anything, impure; foul\n   matter, action, language, etc.; a foreign ingredient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impurity', 'n.', 'Want of ceremonial purity; defilement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impurpled', 'imp. & p. p.', 'of Impurple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impurpling', 'p. pr. & vb. n.', 'of Impurple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impurple', 'v. t.', 'To color or tinge with purple; to make red or reddish;\n   to purple; as, a field impurpled with blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imputability', 'n.', 'The quality of being imputable; imputableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imputable', 'a.', 'That may be imputed; capable of being imputed;\n   chargeable; ascribable; attributable; referable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imputable', 'a.', 'Accusable; culpable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imputableness', 'n.', 'Quality of being imputable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imputably', 'adv.', 'By imputation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imputation', '', 'The act of imputing or charging; attribution; ascription;\n   also, anything imputed or charged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imputation', '', 'Charge or attribution of evil; censure; reproach;\n   insinuation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imputation', '', 'A setting of something to the account of; the attribution\n   of personal guilt or personal righteousness of another; as, the\n   imputation of the sin of Adam, or the righteousness of Christ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imputation', '', 'Opinion; intimation; hint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imputative', 'a.', 'Transferred by imputation; that may be imputed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imputed', 'imp. & p. p.', 'of Impute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imputing', 'p. pr. & vb. n.', 'of Impute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impute', 'v. t.', 'To charge; to ascribe; to attribute; to set to the\n   account of; to charge to one as the author, responsible originator, or\n   possessor; -- generally in a bad sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impute', 'v. t.', 'To adjudge as ones own (the sin or righteousness) of\n   another; as, the righteousness of Christ is imputed to us.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Impute', 'v. t.', 'To take account of; to consider; to regard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imputer', 'n.', 'One who imputes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imputrescible', 'a.', 'Not putrescible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Imrigh', 'n.', 'A peculiar strong soup or broth, made in Scotland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'In-', 'prep.', 'A prefix from Eng. prep. in, also from Lat. prep. in,\n   meaning in, into, on, among; as, inbred, inborn, inroad; incline,\n   inject, intrude. In words from the Latin, in- regularly becomes il-\n   before l, ir- before r, and im- before a labial; as, illusion,\n   irruption, imblue, immigrate, impart. In- is sometimes used with an\n   simple intensive force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'In-', '', 'An inseparable prefix, or particle, meaning not, non-, un- as,\n   inactive, incapable, inapt. In- regularly becomes il- before l, ir-\n   before r, and im- before a labial.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-in', '', 'A suffix. See the Note under -ine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'In', 'prep.', 'The specific signification of in is situation or place with\n   respect to surrounding, environment, encompassment, etc. It is used\n   with verbs signifying being, resting, or moving within limits, or\n   within circumstances or conditions of any kind conceived of as\n   limiting, confining, or investing, either wholly or in part. In its\n   different applications, it approaches some of the meanings of, and\n   sometimes is interchangeable with, within, into, on, at, of, and among.');
  INSERT INTO words (word, word_type, description) VALUES  (    'In', 'prep.', 'With reference to space or place; as, he lives in Boston; he\n   traveled in Italy; castles in the air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'In', 'prep.', 'With reference to circumstances or conditions; as, he is in\n   difficulties; she stood in a blaze of light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'In', 'prep.', 'With reference to a whole which includes or comprises the\n   part spoken of; as, the first in his family; the first regiment in the\n   army.');
  INSERT INTO words (word, word_type, description) VALUES  (    'In', 'prep.', 'With reference to physical surrounding, personal states,\n   etc., abstractly denoted; as, I am in doubt; the room is in darkness;\n   to live in fear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'In', 'prep.', 'With reference to character, reach, scope, or influence\n   considered as establishing a limitation; as, to be in ones favor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'In', 'prep.', 'With reference to movement or tendency toward a certain\n   limit or environment; -- sometimes equivalent to into; as, to put seed\n   in the ground; to fall in love; to end in death; to put our trust in\n   God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'In', 'prep.', 'With reference to a limit of time; as, in an hour; it\n   happened in the last century; in all my life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'In', 'adv.', 'Not out; within; inside. In, the preposition, becomes an\n   adverb by omission of its object, leaving it as the representative of\n   an adverbial phrase, the context indicating what the omitted object is;\n   as, he takes in the situation (i. e., he comprehends it in his mind);\n   the Republicans were in (i. e., in office); in at one ear and out at\n   the other (i. e., in or into the head); his side was in (i. e., in the\n   turn at the bat); he came in (i. e., into the house).');
  INSERT INTO words (word, word_type, description) VALUES  (    'In', 'adv.', 'With privilege or possession; -- used to denote a holding,\n   possession, or seisin; as, in by descent; in by purchase; in of the\n   seisin of her husband.');
  INSERT INTO words (word, word_type, description) VALUES  (    'In', 'n.', 'One who is in office; -- the opposite of out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'In', 'n.', 'A reentrant angle; a nook or corner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'In', 'v. t.', 'To inclose; to take in; to harvest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inability', 'n.', 'The quality or state of being unable; lack of ability;\n   want of sufficient power, strength, resources, or capacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inable', 'v. t.', 'See Enable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inablement', 'n.', 'See Enablement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inabstinence', 'n.', 'Want of abstinence; indulgence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inabstracted', 'a.', 'Not abstracted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inabusively', 'adv.', 'Without abuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inaccessibility', 'n.', 'The quality or state of being inaccessible;\n   inaccessibleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inaccessible', 'a.', 'Not accessible; not to be reached, obtained, or\n   approached; as, an inaccessible rock, fortress, document, prince, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inaccordant', 'a.', 'Not accordant; discordant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inaccuracies', 'pl. ', 'of Inaccuracy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inaccuracy', 'n.', 'The quality of being inaccurate; want of accuracy or\n   exactness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inaccuracy', 'n.', 'That which is inaccurate or incorrect; mistake; fault;\n   defect; error; as, in inaccuracy in speech, copying, calculation, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inaccurate', 'a.', 'Not accurate; not according to truth; inexact;\n   incorrect; erroneous; as, in inaccurate man, narration, copy, judgment,\n   calculation, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inaccurately', 'adv.', 'In an inaccurate manner; incorrectly; inexactly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inacquaintance', 'a.', 'Want of acquaintance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inacquiescent', 'a.', 'Not acquiescent or acquiescing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inaction', 'n.', 'Want of action or activity; forbearance from labor;\n   idleness; rest; inertness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inactive', 'a.', 'Not active; having no power to move; that does not or can\n   not produce results; inert; as, matter is, of itself, inactive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inactive', 'a.', 'Not disposed to action or effort; not diligent or\n   industrious; not busy; idle; as, an inactive officer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inactive', 'a.', 'Not active; inert; esp., not exhibiting any action or\n   activity on polarized light; optically neutral; -- said of isomeric\n   forms of certain substances, in distinction from other forms which are\n   optically active; as, racemic acid is an inactive tartaric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inactively', 'adv.', 'In an inactive manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inactivity', 'n.', 'The state or quality of being inactive; inertness; as,\n   the inactivity of matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inactivity', 'n.', 'Idleness; habitual indisposition to action or exertion;\n   want of energy; sluggishness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inactose', 'n.', 'A variety of sugar, found in certain plants. It is\n   optically inactive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inactuate', 'v. t.', 'To put in action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inactuation', 'n.', 'Operation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inadaptation', 'n.', 'Want of adaptation; unsuitableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inadequacy', 'n.', 'The quality or state of being inadequate or\n   insufficient; defectiveness; insufficiency; inadequateness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inadequate', 'a.', 'Not adequate; unequal to the purpose; insufficient;\n   deficient; as, inadequate resources, power, conceptions,\n   representations, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inadequation', 'n.', 'Want of exact correspondence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inadherent', 'a.', 'Not adhering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inadherent', 'a.', 'Free; not connected with the other organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inadhesion', 'n.', 'Want of adhesion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inadmissibility', 'n.', 'The state or quality of being inadmissible, or not\n   to be received.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inadmissible', 'a.', 'Not admissible; not proper to be admitted, allowed,\n   or received; as, inadmissible testimony; an inadmissible proposition,\n   or explanation.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ces', 'pl. ', 'of Inadvertence');
  INSERT INTO words (word, word_type, description) VALUES  (    '-cies', 'pl. ', 'of Inadvertency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inadvertence', 'n.', 'Alt. of Inadvertency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inadvertency', 'n.', 'The quality of being inadvertent; lack of heedfulness\n   or attentiveness; inattention; negligence; as, many mistakes proceed\n   from inadvertence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inadvertency', 'n.', 'An effect of inattention; a result of carelessness;\n   an oversight, mistake, or fault from negligence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inadvertent', 'a.', 'Not turning the mind to a matter; heedless; careless;\n   negligent; inattentive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inadvisable', 'a.', 'Not advisable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inaffability', 'n.', 'Want of affability or sociability; reticence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inaffable', 'a.', 'Not affable; reserved in social intercourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inaffectation', 'n.', 'Freedom from affectation; naturalness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inaffected', 'a.', 'Unaffected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inaidable', 'a.', 'Incapable of being assisted; helpless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inalienability', 'n.', 'The quality or state of being inalienable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inalienable', 'a.', 'Incapable of being alienated, surrendered, or\n   transferred to another; not alienable; as, in inalienable birthright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inalienableness', 'n.', 'The quality or state of being inalienable;\n   inalienability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inalienably', 'adv.', 'In a manner that forbids alienation; as, rights\n   inalienably vested.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inalimental', 'a.', 'Affording no aliment or nourishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inalterability', 'n.', 'The quality of being unalterable or unchangeable;\n   permanence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inalterable', 'a.', 'Not alterable; incapable of being altered or changed;\n   unalterable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inamiable', 'a.', 'Unamiable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inamissible', 'a.', 'Incapable of being lost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inamorata', 'n.', 'A woman in love; a mistress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inamorate', 'a.', 'Enamored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inamoratos', 'pl. ', 'of Inamorato');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inamorato', 'n.', 'A male lover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inamovable', 'a.', 'Not amovable or removable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'In-and-in', 'n.', 'An old game played with four dice. In signified a\n   doublet, or two dice alike; in-and-in, either two doubles, or the four\n   dice alike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'In and an', 'a. & adv.', 'Applied to breeding from a male and female of the\n   same parentage. See under Breeding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inane', 'a.', 'Without contents; empty; void of sense or intelligence;\n   purposeless; pointless; characterless; useless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inane', 'n.', 'That which is void or empty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inangular', 'a.', 'Not angular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inaniloquent', 'a.', 'Alt. of Inaniloquous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inaniloquous', 'a.', 'Given to talking inanely; loquacious; garrulous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inanimate', 'v. t.', 'To animate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inanimate', 'a.', 'Not animate; destitute of life or spirit; lifeless;\n   dead; inactive; dull; as, stones and earth are inanimate substances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inanimated', 'a.', 'Destitute of life; lacking animation; unanimated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inanimateness', 'n.', 'The quality or state of being inanimate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inanimation', 'n.', 'Want of animation; lifeless; dullness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inanimation', 'n.', 'Infusion of life or vigor; animation; inspiration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inanitiate', 'v. t.', 'To produce inanition in; to exhaust for want of\n   nourishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inanitiation', 'n.', 'Inanition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inanition', 'n.', 'The condition of being inane; emptiness; want of\n   fullness, as in the vessels of the body; hence, specifically,\n   exhaustion from want of food, either from partial or complete\n   starvation, or from a disorder of the digestive apparatus, producing\n   the same result.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inanities', 'pl. ', 'of Inanity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inanity', 'n.', 'Inanition; void space; vacuity; emptiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inanity', 'n.', 'Want of seriousness; aimlessness; frivolity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inanity', 'n.', 'An inane, useless thing or pursuit; a vanity; a silly\n   object; -- chiefly in pl.; as, the inanities of the world.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inantherate', 'a.', 'Not bearing anthers; -- said of sterile stamens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'In antis', '', 'Between antae; -- said of a portico in classical style,\n   where columns are set between two antae, forming the angles of the\n   building. See Anta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inapathy', 'n.', 'Sensibility; feeling; -- opposed to apathy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inappealable', 'a.', 'Not admitting of appeal; not appealable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inappeasable', 'a.', 'Incapable of being appeased or satisfied;\n   unappeasable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inappellability', 'n.', 'The quality of being inappellable; finality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inappellable', 'a.', 'Inappealable; final.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inappetence', 'n.', 'Alt. of Inappetency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inappetency', 'n.', 'Want of appetency; want of desire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inapplicability', 'n.', 'The quality of being inapplicable; unfitness;\n   inapplicableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inapplicable', 'a.', 'Not applicable; incapable of being applied; not\n   adapted; not suitable; as, the argument is inapplicable to the case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inapplication', 'n.', 'Want of application, attention, or diligence;\n   negligence; indolence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inapposite', 'a.', 'Not apposite; not fit or suitable; not pertinent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inappreciable', 'a.', 'Not appreciable; too small to be perceived;\n   incapable of being duly valued or estimated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inappreciation', 'n.', 'Want of appreciation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inapprehensible', 'a.', 'Not apprehensible; unintelligible; inconceivable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inapprehension', 'n.', 'Want of apprehension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inapprehensive', 'a.', 'Not apprehensive; regardless; unconcerned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inapproachable', 'a.', 'Not approachable; unapproachable; inaccessible;\n   unequaled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inappropriate', 'a.', 'Not instrument (to); not appropriate; unbecoming;\n   unsuitable; not specially fitted; -- followed by to or for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inapt', 'a.', 'Unapt; not apt; unsuitable; inept.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inaptitude', 'n.', 'Want of aptitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inaquate', 'a.', 'Embodied in, or changed into, water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inaquation', 'n.', 'The state of being inaquate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inarable', 'a.', 'Not arable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inarched', 'imp. & p. p.', 'of Inarch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inarching', 'p. pr. & vb. n.', 'of Inarch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inarch', 'v. t.', 'To graft by uniting, as a scion, to a stock, without\n   separating either from its root before the union is complete; -- also\n   called to graft by approach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inarching', 'n.', 'A method of ingrafting. See Inarch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inarticulate', 'a.', 'Not uttered with articulation or intelligible\n   distinctness, as speech or words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inarticulate', 'a.', 'Not jointed or articulated; having no distinct body\n   segments; as, an inarticulate worm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inarticulate', 'a.', 'Without a hinge; -- said of an order (Inarticulata or\n   Ecardines) of brachiopods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inarticulate', 'a.', 'Incapable of articulating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inarticulated', 'a.', 'Not articulated; not jointed or connected by a\n   joint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inarticulately', 'adv.', 'In an inarticulate manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inarticulateness', 'n.', 'The state or quality of being inarticulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inarticulation', 'n.', 'Inarticulateness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inartificial', 'a.', 'Not artificial; not made or elaborated by art;\n   natural; simple; artless; as, an inartificial argument; an inartificial\n   character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inasmuch', 'adv.', 'In like degree; in like manner; seeing that;\n   considering that; since; -- followed by as. See In as much as, under\n   In, prep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inattention', 'n.', 'Want of attention, or failure to pay attention;\n   disregard; heedlessness; neglect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inattentive', 'a.', 'Not attentive; not fixing the mind on an object;\n   heedless; careless; negligent; regardless; as, an inattentive spectator\n   or hearer; an inattentive habit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inaudibility', 'n.', 'The quality of being inaudible; inaudibleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inaudible', 'a.', 'Not audible; incapable of being heard; silent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inaugur', 'v. t.', 'To inaugurate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inaugural', 'a.', 'Pertaining to, or performed or pronounced at, an\n   inauguration; as, an inaugural address; the inaugural exercises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inaugural', 'n.', 'An inaugural address.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inaugurate', 'a.', 'Invested with office; inaugurated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inaugurated', 'imp. & p. p.', 'of Inaugurate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inaugurating', 'p. pr. & vb. n.', 'of Inaugurate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inaugurate', 'v. t.', 'To introduce or induct into an office with suitable\n   ceremonies or solemnities; to invest with power or authority in a\n   formal manner; to install; as, to inaugurate a president; to inaugurate\n   a king.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inaugurate', 'v. t.', 'To cause to begin, esp. with formality or solemn\n   ceremony; hence, to set in motion, action, or progress; to initiate; --\n   used especially of something of dignity or worth or public concern; as,\n   to inaugurate a new era of things, new methods, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inaugurate', 'v. t.', 'To celebrate the completion of, or the first public\n   use of; to dedicate, as a statue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inaugurate', 'v. t.', 'To begin with good omens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inauguration', 'n.', 'The act of inuagurating, or inducting into office\n   with solemnity; investiture by appropriate ceremonies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inauguration', 'n.', 'The formal beginning or initiation of any movement,\n   course of action, etc.; as, the inauguration of a new system, a new\n   condition, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inaugurator', 'n.', 'One who inaugurates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inauguratory', 'a.', 'Suitable for, or pertaining to, inauguration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inaurate', 'a.', 'Covered with gold; gilded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inaurate', 'v. t.', 'To cover with gold; to gild.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inauration', 'n.', 'The act or process of gilding or covering with gold.');
