  INSERT INTO words (word, word_type, description) VALUES  (    'Inauspicious', 'a.', 'Not auspicious; ill-omened; unfortunate; unlucky;\n   unfavorable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inauthoritative', 'a.', 'Without authority; not authoritative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inbarge', 'v. t. & i.', 'To embark; to go or put into a barge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inbeaming', 'n.', 'Shining in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inbeing', 'n.', 'Inherence; inherent existence.');
