  INSERT INTO words (word, word_type, description) VALUES  (    'Inboard', 'a. & adv.', 'Inside the line of a vessels bulwarks or hull; the\n   opposite of outboard; as, an inboard cargo; haul the boom inboard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inboard', 'a. & adv.', 'From without inward; toward the inside; as, the\n   inboard stroke of a steam engine piston, the inward or return stroke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inborn', 'a.', 'Born in or with; implanted by nature; innate; as, inborn\n   passions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inbreak', 'n.', 'Alt. of Inbreaking');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inbreaking', 'n.', 'A breaking in; inroad; invasion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inbreathed', 'imp. & p. p.', 'of Inbreathe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inbreathing', 'p. pr. & vb. n.', 'of Inbreathe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inbreathe', 'v. t.', 'To infuse by breathing; to inspire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inbred', 'a.', 'Bred within; innate; as, inbred worth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inbred', 'imp. & p. p.', 'of Inbreed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inbreeding', 'p. pr. & vb. n.', 'of Inbreed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inbreed', 'v. t.', 'To produce or generate within.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inbreed', 'v. t.', 'To breed in and in. See under Breed, v. i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inburning', 'a.', 'Burning within.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inburnt', 'a.', 'Burnt in; ineffaceable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inburst', 'n.', 'A bursting in or into.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inc', 'n.', 'A Japanese measure of length equal to about two and one\n   twelfth yards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inca', 'n.', 'An emperor or monarch of Peru before, or at the time of, the\n   Spanish conquest; any member of this royal dynasty, reputed to have\n   been descendants of the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inca', 'n.', 'The people governed by the Incas, now represented by the\n   Quichua tribe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incaged', 'imp. & p. p.', 'of Incage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incaging', 'p. pr. & vb. n.', 'of Incage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incage', 'v. t.', 'To confine in, or as in, a cage; to coop up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incagement', 'n.', 'Confinement in, or as in, cage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incalculability', 'n.', 'The quality or state of being incalculable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incalculable', 'a.', 'Not capable of being calculated; beyond calculation;\n   very great.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incalescence', 'n.', 'The state of being incalescent, or of growing warm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incalescency', 'n.', 'Incalescence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incalescent', 'a.', 'Growing warm; increasing in heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incameration', 'n.', 'The act or process of uniting lands, rights, or\n   revenues, to the ecclesiastical chamber, i. e., to the popes domain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incan', 'a.', 'Of or pertaining to the Incas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incandescence', 'n.', 'A white heat, or the glowing or luminous whiteness\n   of a body caused by intense heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incandescent', 'a.', 'White, glowing, or luminous, with intense heat; as,\n   incandescent carbon or platinum; hence, clear; shining; brilliant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incanescent', 'a.', 'Becoming hoary or gray; canescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incanous', 'a.', 'Hoary with white pubescence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incantation', 'n.', 'The act or process of using formulas sung or spoken,\n   with occult ceremonies, for the purpose of raising spirits, producing\n   enchantment, or affecting other magical results; enchantment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incantation', 'n.', 'A formula of words used as above.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incantatory', 'a.', 'Dealing by enchantment; magical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incanting', 'a.', 'Enchanting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incanton', 'v. t.', 'To unite to, or form into, a canton or separate\n   community.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incapability', 'n.', 'The quality of being incapable; incapacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incapability', 'n.', 'Want of legal qualifications, or of legal power; as,\n   incapability of holding an office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incapable', 'a.', 'Wanting in ability or qualification for the purpose or\n   end in view; not large enough to contain or hold; deficient in physical\n   strength, mental or moral power, etc.; not capable; as, incapable of\n   holding a certain quantity of liquid; incapable of endurance, of\n   comprehension, of perseverance, of reform, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incapable', 'a.', 'Not capable of being brought to do or perform, because\n   morally strong or well disposed; -- used with reference to some evil;\n   as, incapable of wrong, dishonesty, or falsehood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incapable', 'a.', 'Not in a state to receive; not receptive; not\n   susceptible; not able to admit; as, incapable of pain, or pleasure;\n   incapable of stain or injury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incapable', 'a.', 'Unqualified or disqualified, in a legal sense; as, a man\n   under thirty-five years of age is incapable of holding the office of\n   president of the United States; a person convicted on impeachment is\n   thereby made incapable of holding an office of profit or honor under\n   the government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incapable', 'a.', 'As a term of disgrace, sometimes annexed to a sentence\n   when an officer has been cashiered and rendered incapable of serving\n   his country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incapable', 'n.', 'One who is morally or mentally weak or inefficient; an\n   imbecile; a simpleton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incapableness', 'n.', 'The quality or state of being incapable;\n   incapability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incapably', 'adv.', 'In an incapable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incapacious', 'a.', 'Not capacious; narrow; small; weak or foolish; as, an\n   incapacious soul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incapacitated', 'imp. & p. p.', 'of Incapacitate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incapacitating', 'p. pr. & vb. n.', 'of Incapacitate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incapacitate', 'v. t.', 'To deprive of capacity or natural power; to\n   disable; to render incapable or unfit; to disqualify; as, his age\n   incapacitated him for war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incapacitate', 'v. t.', 'To deprive of legal or constitutional requisites,\n   or of ability or competency for the performance of certain civil acts;\n   to disqualify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incapacitation', 'n.', 'The act of incapacitating or state of being\n   incapacitated; incapacity; disqualification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incapacities', 'pl. ', 'of Incapacity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incapacity', 'n.', 'Want of capacity; lack of physical or intellectual\n   power; inability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incapacity', 'n.', 'Want of legal ability or competency to do, give,\n   transmit, or receive something; inability; disqualification; as, the\n   inacapacity of minors to make binding contracts, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incapsulate', 'v. t.', 'To inclose completely, as in a membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incapsulation', 'n.', 'The process of becoming, or the state or condition\n   of being, incapsulated; as, incapsulation of the ovum in the uterus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incarcerated', 'imp. & p. p.', 'of Incarcerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incarcerating', 'p. pr. & vb. n.', 'of Incarcerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incarcerate', 'v. t.', 'To imprison; to confine in a jail or prison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incarcerate', 'v. t.', 'To confine; to shut up or inclose; to hem in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incarcerate', 'a.', 'Imprisoned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incarceration', 'n.', 'The act of confining, or the state of being\n   confined; imprisonment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incarceration', 'n.', 'Formerly, strangulation, as in hernia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incarceration', 'n.', 'A constriction of the hernial sac, rendering it\n   irreducible, but not great enough to cause strangulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incarcerator', 'n.', 'One who incarcerates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incarn', 'v. t.', 'To cover or invest with flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incarn', 'v. i.', 'To develop flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incarnadine', 'a.', 'Flesh-colored; of a carnation or pale red color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incarnadine', 'v. t.', 'To dye red or crimson.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incarnate', 'a.', 'Not in the flesh; spiritual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incarnate', 'a.', 'Invested with flesh; embodied in a human nature and\n   form; united with, or having, a human body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incarnate', 'a.', 'Flesh-colored; rosy; red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incarnated', 'imp. & p. p.', 'of Incarnate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incarnating', 'p. pr. & vb. n.', 'of Incarnate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incarnate', 'v. t.', 'To clothe with flesh; to embody in flesh; to invest,\n   as spirits, ideals, etc., with a human from or nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incarnate', 'v. i.', 'To form flesh; to granulate, as a wound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incarnation', 'n.', 'The act of clothing with flesh, or the state of being\n   so clothed; the act of taking, or being manifested in, a human body and\n   nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incarnation', 'n.', 'The union of the second person of the Godhead with\n   manhood in Christ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incarnation', 'n.', 'An incarnate form; a personification; a manifestation;\n   a reduction to apparent from; a striking exemplification in person or\n   act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incarnation', 'n.', 'A rosy or red color; flesh color; carnation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incarnation', 'n.', 'The process of healing wounds and filling the part\n   with new flesh; granulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incarnative', 'a.', 'Causing new flesh to grow; healing; regenerative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incarnative', 'n.', 'An incarnative medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incarnification', 'n.', 'The act of assuming, or state of being clothed\n   with, flesh; incarnation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incased', 'imp. & p. p.', 'of Incase');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incasing', 'p. pr. & vb. n.', 'of Incase');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incase', 'v. t.', 'To inclose in a case; to inclose; to cover or surround\n   with something solid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incasement', 'n.', 'The act or process of inclosing with a case, or the\n   state of being incased.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incasement', 'n.', 'That which forms a case, covering, or inclosure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incask', 'v. t.', 'To cover with a casque or as with a casque.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incastellated', 'a.', 'Confined or inclosed in a castle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incastelled', 'a.', 'Hoofbound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incatenation', 'n.', 'The act of linking together; enchaining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incaution', 'n.', 'Want of caution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incautious', 'a.', 'Not cautious; not circumspect; not attending to the\n   circumstances on which safety and interest depend; heedless; careless;\n   as, an incautious step; an incautious remark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incavated', 'a.', 'Made hollow; bent round or in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incavation', 'n.', 'Act of making hollow; also, a hollow; an exvation; a\n   depression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incaved', 'a.', 'Inclosed in a cave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incaverned', 'a.', 'Inclosed or shut up as in a cavern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incedingly', 'adv.', 'Majestically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incelebrity', 'n.', 'Want of celebrity or distinction; obscurity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incend', 'v. t.', 'To inflame; to excite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incendiarism', 'n.', 'The act or practice of maliciously setting fires;\n   arson.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incendiaries', 'pl. ', 'of Incendiary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incendiary', 'n.', 'Any person who maliciously sets fire to a building or\n   other valuable or other valuable property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incendiary', 'n.', 'A person who excites or inflames factions, and promotes\n   quarrels or sedition; an agitator; an exciter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incendiary', 'a.', 'Of or pertaining to incendiarism, or the malicious\n   burning of valuable property; as, incendiary material; as incendiary\n   crime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incendiary', 'a.', 'Tending to excite or inflame factions, sedition, or\n   quarrel; inflammatory; seditious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incendious', 'a.', 'Promoting faction or contention; seditious;\n   inflammatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incensant', 'a.', 'A modern term applied to animals (as a boar) when borne\n   as raging, or with furious aspect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incensation', 'n.', 'The offering of incense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incensed', 'imp. & p. p.', 'of Incense');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incensing', 'p. pr. & vb. n.', 'of Incense');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incense', 'v. t.', 'To set on fire; to inflame; to kindle; to burn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incense', 'v. t.', 'To inflame with anger; to endkindle; to fire; to\n   incite; to provoke; to heat; to madden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incensed', 'imp. & p. p.', 'of Incense');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incensing', 'p. pr. & vb. n.', 'of Incense');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incense', 'n.', 'To offer incense to. See Incense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incense', 'n.', 'To perfume with, or as with, incense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incense', 'n.', 'The perfume or odors exhaled from spices and gums when\n   burned in celebrating religious rites or as an offering to some deity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incense', 'n.', 'The materials used for the purpose of producing a perfume\n   when burned, as fragrant gums, spices, frankincense, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incense', 'n.', 'Also used figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incensebreathing', 'a.', 'Breathing or exhaling incense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incensed', 'a.', 'Angered; enraged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incensed', 'a.', 'Represented as enraged, as any wild creature depicted\n   with fire issuing from mouth and eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incensement', 'n.', 'Fury; rage; heat; exasperation; as, implacable\n   incensement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incenser', 'n.', 'One who instigates or incites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incension', 'n.', 'The act of kindling, or the state of being kindled or on\n   fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incensive', 'a.', 'Tending to excite or provoke; inflammatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incensor', 'n.', 'A kindler of anger or enmity; an inciter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incensories', 'pl. ', 'of Incensory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incensory', 'n.', 'The vessel in which incense is burned and offered; a\n   censer; a thurible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incensurable', 'a.', 'Not censurable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incenter', 'n.', 'The center of the circle inscribed in a triangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incentive', 'a.', 'Inciting; encouraging or moving; rousing to action;\n   stimulative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incentive', 'a.', 'Serving to kindle or set on fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incentive', 'n.', 'That which moves or influences the mind, or operates on\n   the passions; that which incites, or has a tendency to incite, to\n   determination or action; that which prompts to good or ill; motive;\n   spur; as, the love of money, and the desire of promotion, are two\n   powerful incentives to action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incentively', 'adv.', 'Incitingly; encouragingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inception', 'n.', 'Beginning; commencement; initiation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inception', 'n.', 'Reception; a taking in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inceptive', 'a.', 'Beginning; expressing or indicating beginning; as, an\n   inceptive proposition; an inceptive verb, which expresses the beginning\n   of action; -- called also inchoative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inceptive', 'n.', 'An inceptive word, phrase, or clause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inceptor', 'n.', 'A beginner; one in the rudiments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inceptor', 'n.', 'One who is on the point of taking the degree of master of\n   arts at an English university.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inceration', 'n.', 'The act of smearing or covering with wax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incerative', 'a.', 'Cleaving or sticking like wax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incertain', 'n.', 'Uncertain; doubtful; unsteady.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incertainty', 'n.', 'Uncertainty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incertitude', 'n.', 'Uncertainty; doubtfulness; doubt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incertum', 'a.', 'Doubtful; not of definite form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incessable', 'a.', 'Unceasing; continual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incessancy', 'n.', 'The quality of being incessant; unintermitted\n   continuance; unceasingness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incessant', 'a.', 'Continuing or following without interruption; unceasing;\n   unitermitted; uninterrupted; continual; as, incessant clamors;\n   incessant pain, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incessantly', 'adv.', 'Unceasingly; continually.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incession', 'n.', 'Motion on foot; progress in walking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incest', 'n.', 'The crime of cohabitation or sexual commerce between\n   persons related within the degrees wherein marriage is prohibited by\n   law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incesttuous', 'a.', 'Guilty of incest; involving, or pertaining to, the\n   crime of incest; as, an incestuous person or connection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inch', 'n.', 'An island; -- often used in the names of small islands off\n   the coast of Scotland, as in Inchcolm, Inchkeith, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inch', 'n.', 'A measure of length, the twelfth part of a foot, commonly\n   subdivided into halves, quarters, eights, sixteenths, etc., as among\n   mechanics. It was also formerly divided into twelve parts, called\n   lines, and originally into three parts, called barleycorns, its length\n   supposed to have been determined from three grains of barley placed end\n   to end lengthwise. It is also sometimes called a prime (), composed of\n   twelve seconds (), as in the duodecimal system of arithmetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inch', 'n.', 'A small distance or degree, whether of time or space; hence,\n   a critical moment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inched', 'imp. & p. p.', 'of Inch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inching', 'p. pr. & vb. n.', 'of Inch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inch', 'v. t.', 'To drive by inches, or small degrees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inch', 'v. t.', 'To deal out by inches; to give sparingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inch', 'v. i.', 'To advance or retire by inches or small degrees; to move\n   slowly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inch', 'a.', 'Measurement an inch in any dimension, whether length,\n   breadth, or thickness; -- used in composition; as, a two-inch cable; a\n   four-inch plank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inchambered', 'imp. & p. p.', 'of Inchamber');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inchambering', 'p. pr. & vb. n.', 'of Inchamber');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inchamber', 'v. t.', 'To lodge in a chamber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inchangeability', 'n.', 'Unchangeableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inchant', 'v. t.', 'See Enchant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incharitable', 'a.', 'Uncharitable; unfeeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incharity', 'n.', 'Want of charity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inchase', 'v. t.', 'See Enchase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inchastity', 'n.', 'Unchastity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inched', 'a.', 'Having or measuring (so many) inches; as, a four-inched\n   bridge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inchest', 'v. t.', 'To put into a chest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inchipin', 'n.', 'See Inchpin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inchmeal', 'n.', 'A piece an inch long.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inchmeal', 'adv.', 'Little by little; gradually.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inchoate', 'a.', 'Recently, or just, begun; beginning; partially but not\n   fully in existence or operation; existing in its elements; incomplete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inchoate', 'v. t.', 'To begin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inchoation', 'n.', 'Act of beginning; commencement; inception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inchoative', 'a.', 'Expressing or pertaining to a beginning; inceptive; as,\n   an inchoative verb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inchoative', 'n.', 'An inchoative verb. See Inceptive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inchpin', 'n.', 'The sweetbread of a deer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inchworm', 'n.', 'The larva of any geometrid moth. See Geometrid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incicurable', 'a.', 'Untamable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incide', 'v. t.', 'To cut; to separate and remove; to resolve or break up,\n   as by medicines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incidence', 'n.', 'A falling on or upon; an incident; an event.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incidence', 'n.', 'The direction in which a body, or a ray of light or\n   heat, falls on any surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incidency', 'n.', 'Incidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incident', 'a.', 'Falling or striking upon, as a ray of light upon a\n   reflecting surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incident', 'a.', 'Coming or happening accidentally; not in the usual course\n   of things; not in connection with the main design; not according to\n   expectation; casual; fortuitous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incident', 'a.', 'Liable to happen; apt to occur; befalling; hence,\n   naturally happening or appertaining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incident', 'a.', 'Dependent upon, or appertaining to, another thing, called\n   the principal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incident', 'n.', 'That which falls out or takes place; an event; casualty;\n   occurrence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incident', 'n.', 'That which happens aside from the main design; an\n   accidental or subordinate action or event.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incident', 'n.', 'Something appertaining to, passing with, or depending on,\n   another, called the principal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incindental', 'a.', 'Happening, as an occasional event, without regularity;\n   coming without design; casual; accidental; hence, not of prime concern;\n   subordinate; collateral; as, an incidental conversation; an incidental\n   occurrence; incidental expenses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incendental', 'n.', 'An incident; that which is incidental; esp., in the\n   plural, an aggregate of subordinate or incidental items not\n   particularized; as, the expense of tuition and incidentals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incidently', 'adv.', 'Incidentally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incinerable', 'a.', 'Capable of being incinerated or reduced to ashes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incinerate', '', 'Reduced to ashes by burning; thoroughly consumed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incinerated', 'imp. & p. p.', 'of Incinerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incinerating', 'p. pr. & vb. n.', 'of Incinerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incinerate', 'v. t.', 'To burn to ashes; to consume; to burn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incineration', 'n.', 'The act of incinerating, or the state of being\n   incinerated; cremation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incipience', 'n.', 'Alt. of Incipiency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incipiency', 'n.', 'Beginning; commencement; incipient state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incipient', 'a.', 'Beginning to be, or to show itself; commencing; initial;\n   as, the incipient stage of a fever; incipient light of day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incircle', 'v. t.', 'See Encircle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incirclet', 'n.', 'A small circle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incircumscriptible', 'a.', 'Incapable of being circumscribed or limited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incircumscription', 'n.', 'Condition or quality of being incircumscriptible\n   or limitless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incircumspect', 'a.', 'Not circumspect; heedless; careless; reckless;\n   impolitic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incircumspection', 'n.', 'Want of circumspection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incised', 'imp. & p. p.', 'of Incise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incising', 'p. pr. & vb. n.', 'of Incise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incise', 'v. t.', 'To cut in or into with a sharp instrument; to carve; to\n   engrave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incise', 'v. t.', 'To cut, gash, or wound with a sharp instrument; to cut\n   off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incised', 'a.', 'Cut in; carved; engraved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incised', 'a.', 'Having deep and sharp notches, as a leaf or a petal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incisely', 'adv.', 'In an incised manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incision', 'n.', 'The act of incising, or cutting into a substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incision', 'n.', 'That which is produced by incising; the separation of the\n   parts of any substance made by a cutting or pointed instrument; a cut;\n   a gash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incision', 'n.', 'Separation or solution of viscid matter by medicines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incisive', 'a.', 'Having the quality of incising, cutting, or penetrating,\n   as with a sharp instrument; cutting; hence, sharp; acute; sarcastic;\n   biting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incisive', 'a.', 'Of or pertaining to the incisors; incisor; as, the\n   incisive bones, the premaxillaries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incisor', 'n.', 'One of the teeth in front of the canines in either jaw; an\n   incisive tooth. See Tooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incisor', 'a.', 'Adapted for cutting; of or pertaining to the incisors;\n   incisive; as, the incisor nerve; an incisor foramen; an incisor tooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incisory', 'a.', 'Having the quality of cutting; incisor; incisive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incisure', 'n.', 'A cut; an incision; a gash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incitant', 'a.', 'Inciting; stimulating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incitant', 'n.', 'That which incites; an inciting agent or cause; a\n   stimulant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incitation', 'n.', 'The act of inciting or moving to action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incitation', 'n.', 'That which incites to action; that which rouses or\n   prompts; incitement; motive; incentive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incitative', 'n.', 'A provocative; an incitant; a stimulant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incited', 'imp. & p. p.', 'of Incite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inciting', 'p. pr. & vb. n.', 'of Incite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incite', 'v. t.', 'To move to action; to stir up; to rouse; to spur or urge\n   on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incitement', 'n.', 'The act of inciting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incitement', 'n.', 'That which incites the mind, or moves to action;\n   motive; incentive; impulse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inciter', 'n.', 'One who, or that which, incites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incitingly', 'adv.', 'So as to incite or stimulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incito-motor', 'a.', 'Inciting to motion; -- applied to that action which,\n   in the case of muscular motion, commences in the nerve centers, and\n   excites the muscles to contraction. Opposed to excito-motor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incito-motory', 'a.', 'Incitomotor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incivil', 'a.', 'Uncivil; rude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incivilities', 'pl. ', 'of Incivility');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incivility', 'n.', 'The quality or state of being uncivil; want of\n   courtesy; rudeness of manner; impoliteness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incivility', 'n.', 'Any act of rudeness or ill breeding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incivility', 'n.', 'Want of civilization; a state of rudeness or barbarism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incivilization', 'n.', 'The state of being uncivilized; want of\n   civilization; barbarism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incivilly', 'adv.', 'Uncivilly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incivism', 'n.', 'Want of civism; want of patriotism or love to ones\n   country; unfriendliness to ones state or government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclamation', 'n.', 'Exclamation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclasp', 'v. t.', 'To clasp within; to hold fast to; to embrace or\n   encircle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclaudent', 'a.', 'Not closing or shutting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclavated', 'a.', 'Set; fast; fixed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclave', 'a.', 'Resembling a series of dovetails; -- said of a line of\n   division, such as the border of an ordinary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incle', 'n.', 'Same as Inkle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclemencies', 'pl. ', 'of Inclemency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclemency', 'n.', 'The state or quality of being inclement; want of\n   clemency; want of mildness of temper; unmercifulness; severity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclemency', 'n.', 'Physical severity or harshness (commonly in respect to\n   the elements or weather); roughness; storminess; rigor; severe cold,\n   wind, rain, or snow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclement', 'a.', 'Not clement; destitute of a mild and kind temper; void\n   of tenderness; unmerciful; severe; harsh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclement', 'a.', 'Physically severe or harsh (generally restricted to the\n   elements or weather); rough; boisterous; stormy; rigorously cold, etc.;\n   as, inclement weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclemently', 'adv.', 'In an inclement manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclinable', 'a.', 'Leaning; tending.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclinable', 'a.', 'Having a propensity of will or feeling; leaning in\n   disposition; disposed; propense; as, a mind inclinable to truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclinableness', 'n.', 'The state or quality of being inclinable;\n   inclination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclinnation', 'n.', 'The act of inclining, or state of being inclined; a\n   leaning; as, an inclination of the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclinnation', 'n.', 'A direction or tendency from the true vertical or\n   horizontal direction; as, the inclination of a column, or of a road\n   bed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclinnation', 'n.', 'A tendency towards another body or point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclinnation', 'n.', 'The angle made by two lines or planes; as, the\n   inclination of the plane of the earths equator to the plane of the\n   ecliptic is about 23 28; the inclination of two rays of light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclinnation', 'n.', 'A leaning or tendency of the mind, feelings,\n   preferences, or will; propensity; a disposition more favorable to one\n   thing than to another; favor; desire; love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclinnation', 'n.', 'A person or thing loved or admired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclinnation', 'n.', 'Decantation, or tipping for pouring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclinatory', 'a.', 'Having the quality of leaning or inclining; as, the\n   inclinatory needle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclined', 'imp. & p. p.', 'of Incline');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclining', 'p. pr. & vb. n.', 'of Incline');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incline', 'v. i.', 'To deviate from a line, direction, or course, toward an\n   object; to lean; to tend; as, converging lines incline toward each\n   other; a road inclines to the north or south.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incline', 'v. i.', 'Fig.: To lean or tend, in an intellectual or moral\n   sense; to favor an opinion, a course of conduct, or a person; to have a\n   propensity or inclination; to be disposed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incline', 'v. i.', 'To bow; to incline the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incline', 'v. t.', 'To cause to deviate from a line, position, or\n   direction; to give a leaning, bend, or slope to; as, incline the column\n   or post to the east; incline your head to the right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incline', 'v. t.', 'To impart a tendency or propensity to, as to the will\n   or affections; to turn; to dispose; to influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incline', 'v. t.', 'To bend; to cause to stoop or bow; as, to incline the\n   head or the body in acts of reverence or civility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incline', 'n.', 'An inclined plane; an ascent o/ descent; a grade or\n   gradient; a slope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclined', 'p. p. & a.', 'Having a leaning or tendency towards, or away\n   from, a thing; disposed or moved by wish, desire, or judgment; as, a\n   man inclined to virtue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclined', 'p. p. & a.', 'Making an angle with some line or plane; -- said\n   of a line or plane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclined', 'p. p. & a.', 'Bent out of a perpendicular position, or into a\n   curve with the convex side uppermost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incliner', 'n.', 'One who, or that which, inclines; specifically, an\n   inclined dial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclining', 'a.', 'Same as Inclined, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclining', 'n.', 'Inclination; disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclining', 'n.', 'Party or side chosen; a following.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclinnometer', 'n.', 'An apparatus to determine the inclination of the\n   earths magnetic force to the plane of the horizon; -- called also\n   inclination compass, and dip circle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclip', 'v. t.', 'To clasp; to inclose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incloister', 'v. t.', 'To confine as in a cloister; to cloister.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclosed', 'imp. & p. p.', 'of Inclose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclosing', 'p. pr. & vb. n.', 'of Inclose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclose', 'v. t.', 'To surround; to shut in; to confine on all sides; to\n   include; to shut up; to encompass; as, to inclose a fort or an army\n   with troops; to inclose a town with walls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclose', 'v. t.', 'To put within a case, envelope, or the like; to fold (a\n   thing) within another or into the same parcel; as, to inclose a letter\n   or a bank note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclose', 'v. t.', 'To separate from common grounds by a fence; as, to\n   inclose lands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclose', 'v. t.', 'To put into harness; to harness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incloser', 'n.', 'One who, or that which, incloses; one who fences off land\n   from common grounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclosure', 'n.', 'The act of inclosing; the state of being inclosed, shut\n   up, or encompassed; the separation of land from common ground by a\n   fence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclosure', 'n.', 'That which is inclosed or placed within something; a\n   thing contained; a space inclosed or fenced up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclosure', 'n.', 'That which incloses; a barrier or fence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incloud', 'v. t.', 'To envelop as in clouds; to darken; to obscure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Included', 'imp. & p. p.', 'of Include');
  INSERT INTO words (word, word_type, description) VALUES  (    'Including', 'p. pr. & vb. n.', 'of Include');
  INSERT INTO words (word, word_type, description) VALUES  (    'Include', 'v. t.', 'To confine within; to hold; to contain; to shut up; to\n   inclose; as, the shell of a nut includes the kernel; a pearl is\n   included in a shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Include', 'v. t.', 'To comprehend or comprise, as a genus the species, the\n   whole a part, an argument or reason the inference; to contain; to\n   embrace; as, this volume of Shakespeare includes his sonnets; he was\n   included in the invitation to the family; to and including page\n   twenty-five.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Include', 'v. t.', 'To conclude; to end; to terminate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Included', 'a.', 'Inclosed; confined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Includible', 'a.', 'Capable of being included.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclusa', 'n. pl.', 'A tribe of bivalve mollusks, characterized by the\n   closed state of the mantle which envelops the body. The ship borer\n   (Teredo navalis) is an example.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclusion', 'n.', 'The act of including, or the state of being included;\n   limitation; restriction; as, the lines of inclusion of his policy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclusion', 'n.', 'A foreign substance, either liquid or solid, usually of\n   minute size, inclosed in the mass of a mineral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclusive', 'a.', 'Inclosing; encircling; surrounding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclusive', 'a.', 'Comprehending the stated limit or extremes; as, from\n   Monday to Saturday inclusive, that is, taking in both Monday and\n   Saturday; -- opposed to exclusive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inclusively', 'adv.', 'In an inclusive manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incoach', 'v. t.', 'To put a coach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incoact', 'a.', 'Alt. of Incoacted');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incoacted', 'a.', 'Not compelled; unconstrained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incoagulable', 'a.', 'Not coagulable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incoalescence', 'n.', 'The state of not coalescing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incocted', 'a.', 'Raw; indigestible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incoercible', 'a.', 'Not to be coerced; incapable of being compelled or\n   forced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incoercible', 'a.', 'Not capable of being reduced to the form of a liquid\n   by pressure; -- said of any gas above its critical point; -- also\n   particularly of oxygen, hydrogen, nitrogen, and carbon monoxide,\n   formerly regarded as incapable of liquefaction at any temperature or\n   pressure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incoercible', 'a.', 'That can note be confined in, or excluded from,\n   vessels, like ordinary fluids, gases, etc.; -- said of the imponderable\n   fluids, heat, light, electricity, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incoexistence', 'n.', 'The state of not coexisting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incog', 'adv.', 'Incognito.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incogitable', 'a.', 'Not cogitable; inconceivable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incogitance', 'n.', 'Alt. of Incogitancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incogitancy', 'n.', 'Want of thought, or of the power of thinking;\n   thoughtlessness; unreasonableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incogitant', 'a.', 'Toughtless; inconsiderate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incogitantly', 'adv.', 'In an incogitant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incogitative', 'a.', 'Not cogitative; not thinking; wanting the power of\n   thought; as, a vegetable is an incogitative being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incogitativity', 'n.', 'The quality of being incogitative; want of thought\n   or of the power of thinking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incognita', 'n.', 'A woman who is unknown or in disguise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incognita', 'n.', 'The state of being in disguise; -- said of a woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incognitant', 'a.', 'Ignorant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incognito', 'a. / adv.', 'Without being known; in disguise; in an assumed\n   character, or under an assumed title; -- said esp. of great personages\n   who sometimes adopt a disguise or an assumed character in order to\n   avoid notice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incognitos', 'pl. ', 'of Incognito');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incognito', 'a.', 'One unknown or in disguise, or under an assumed\n   character or name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incognito', 'a.', 'The assumption of disguise or of a feigned character;\n   the state of being in disguise or not recognized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incognizable', 'a.', 'Not cognizable; incapable of being recognized, known,\n   or distinguished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incognizance', 'n.', 'Failure to cognize, apprehended, or notice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incognizant', 'a.', 'Not cognizant; failing to apprehended or notice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incognoscible', 'a.', 'Incognizable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incoherence', 'n.', 'Alt. of Incoherency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incoherency', 'n.', 'The quality or state of being incoherent; want of\n   coherence; want of cohesion or adherence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incoherency', 'n.', 'Want of connection; incongruity; inconsistency; want\n   of agreement or dependence of one part on another; as, the incoherence\n   of arguments, facts, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incoherency', 'n.', 'That which is incoherent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incoherent', 'a.', 'Not coherent; wanting cohesion; loose; unconnected;\n   physically disconnected; not fixed to each; -- said of material\n   substances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incoherent', 'a.', 'Wanting coherence or agreement; incongruous;\n   inconsistent; having no dependence of one part on another; logically\n   disconnected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incoherentific', 'a.', 'Causing incoherence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incoherently', 'adv.', 'In an incoherent manner; without due connection of\n   parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incoherentness', 'n.', 'Incoherence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incoincidence', 'n.', 'The quality of being incoincident; want of\n   coincidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incoincident', 'a.', 'Not coincident; not agreeing in time, in place, or\n   principle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incolumity', 'n.', 'Safety; security.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incomber', 'v. t.', 'See Encumber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incombine', 'v. i.', 'To be incapable of combining; to disagree; to differ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incombustibility', 'n.', 'The quality of being incombustible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incombustible', 'a.', 'Not combustible; not capable of being burned,\n   decomposed, or consumed by fire; uninflammable; as, asbestus is an\n   incombustible substance; carbon dioxide is an incombustible gas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Income', 'n.', 'A coming in; entrance; admittance; ingress; infusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Income', 'n.', 'That which is caused to enter; inspiration; influence;\n   hence, courage or zeal imparted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Income', 'n.', 'That gain which proceeds from labor, business, property, or\n   capital of any kind, as the produce of a farm, the rent of houses, the\n   proceeds of professional business, the profits of commerce or of\n   occupation, or the interest of money or stock in funds, etc.; revenue;\n   receipts; salary; especially, the annual receipts of a private person,\n   or a corporation, from property; as, a large income.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Income', 'n.', 'That which is taken into the body as food; the ingesta; --\n   sometimes restricted to the nutritive, or digestible, portion of the\n   food. See Food. Opposed to output.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incomer', 'n.', 'One who comes in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incomer', 'n.', 'One who succeeds another, as a tenant of land, houses,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incoming', 'a.', 'Coming in; accruing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incoming', 'a.', 'Coming in, succeeding, or following, as occupant or\n   possessor; as, in incoming tenant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incoming', 'n.', 'The act of coming in; arrival.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incoming', 'n.', 'Income; gain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incomity', 'n.', 'Want of comity; incivility; rudeness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'In commendam', '', 'See Commendam, and Partnership in Commendam, under\n   Partnership.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incommensurability', 'n.', 'The quality or state of being incommensurable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incommensurable', 'a.', 'Not commensurable; having no common measure or\n   standard of comparison; as, quantities are incommensurable when no\n   third quantity can be found that is an aliquot part of both; the side\n   and diagonal of a square are incommensurable with each other; the\n   diameter and circumference of a circle are incommensurable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incommensurable', 'n.', 'One of two or more quantities which have no common\n   measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incommensurate', 'a.', 'Not commensurate; not admitting of a common\n   measure; incommensurable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incommensurate', 'a.', 'Not of equal of sufficient measure or extent; not\n   adequate; as, our means are incommensurate to our wants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incommiscible', 'a.', 'Not commiscible; not mixable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incommixture', 'n.', 'A state of being unmixed; separateness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incommodated', 'imp. & p. p.', 'of Incommodate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incommodating', 'p. pr. & vb. n.', 'of Incommodate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incommodate', 'v. t.', 'To incommode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incommodation', 'n.', 'The state of being incommoded; inconvenience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incommoded', 'imp. & p. p.', 'of Incommode');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incommoding', 'p. pr. & vb. n.', 'of Incommode');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incommode', 'v. t.', 'To give inconvenience or trouble to; to disturb or\n   molest; to discommode; to worry; to put out; as, we are incommoded by\n   want of room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incommode', 'n.', 'An inconvenience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incommodement', 'n.', 'The act of incommoded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incommodious', 'a.', 'Tending to incommode; not commodious; not affording\n   ease or advantage; unsuitable; giving trouble; inconvenient; annoying;\n   as, an incommodious seat; an incommodious arrangement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incommodities', 'pl. ', 'of Incommodity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incommodity', 'n.', 'Inconvenience; trouble; annoyance; disadvantage;\n   encumbrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incommunicability', 'n.', 'The quality or state of being incommunicable, or\n   incapable of being imparted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incommunicable', 'a.', 'Not communicable; incapable of being communicated,\n   shared, told, or imparted, to others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incommunicated', 'a.', 'Not communicated or imparted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incommunicating', 'a.', 'Having no communion or intercourse with each\n   other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incommunicative', 'a.', 'Not communicative; not free or apt to impart to\n   others in conversation; reserved; silent; as, the messenger was\n   incommunicative; hence, not disposed to hold fellowship or intercourse\n   with others; exclusive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incommutability', 'n.', 'The quality or state of being incommutable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incommutable', 'a.', 'Not commutable; not capable of being exchanged with,\n   or substituted for, another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incompact', 'a.', 'Alt. of Incompacted');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incompacted', 'a.', 'Not compact; not having the parts firmly united; not\n   solid; incoherent; loose; discrete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incomparable', 'a.', 'Not comparable; admitting of no comparison with\n   others; unapproachably eminent; without a peer or equal; matchless;\n   peerless; transcendent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incompared', 'a.', 'Peerless; incomparable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incompass', 'v. t.', 'See Encompass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incompassion', 'n.', 'Want of compassion or pity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incompassionate', 'a.', 'Not compassionate; void of pity or of tenderness;\n   remorseless.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ties', 'pl. ', 'of Incompatibility');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incompatibility', 'n.', 'The quality or state of being incompatible;\n   inconsistency; irreconcilableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incompatible', 'a.', 'Not compatible; so differing as to be incapable of\n   harmonious combination or coexistence; inconsistent in thought or\n   being; irreconcilably disagreeing; as, persons of incompatible tempers;\n   incompatible colors, desires, ambition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incompatible', 'a.', 'Incapable of being together without mutual reaction\n   or decomposition, as certain medicines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incompatible', 'n.', 'An incompatible substance; esp., in pl., things which\n   can not be placed or used together because of a change of chemical\n   composition or of opposing medicinal qualities; as, the incompatibles\n   of iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incompatibleness', 'n.', 'The quality or state of being incompatible;\n   incompatibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incompatibly', 'adv.', 'In an incompatible manner; inconsistently;\n   incongruously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incompetence', 'n.', 'Alt. of Incompetency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incompetency', 'n.', 'The quality or state of being incompetent; want of\n   physical, intellectual, or moral ability; insufficiency; inadequacy;\n   as, the incompetency of a child hard labor, or of an idiot for\n   intellectual efforts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incompetency', 'n.', 'Want of competency or legal fitness; incapacity;\n   disqualification, as of a person to be heard as a witness, or to act as\n   a juror, or of a judge to try a cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incompetent', 'a.', 'Not competent; wanting in adequate strength, power,\n   capacity, means, qualifications, or the like; incapable; unable;\n   inadequate; unfit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incompetent', 'a.', 'Wanting the legal or constitutional qualifications;\n   inadmissible; as, a person professedly wanting in religious belief is\n   an incompetent witness in a court of law or equity; incompetent\n   evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incompetent', 'a.', 'Not lying within ones competency, capacity, or\n   authorized power; not permissible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incompetently', 'adv.', 'In an competent manner; inadequately; unsuitably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incompetibility', 'n.', 'See Incompatibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incompetible', 'a.', 'See Incompatible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incomplete', 'a.', 'Not complete; not filled up; not finished; not having\n   all its parts, or not having them all adjusted; imperfect; defective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incomplete', 'a.', 'Wanting any of the usual floral organs; -- said of a\n   flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incompletely', 'adv.', 'In an incomplete manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incompleteness', 'n.', 'The state of being incomplete; imperfectness;\n   defectiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incompletion', 'n.', 'Want of completion; incompleteness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incomplex', 'a.', 'Not complex; uncompounded; simple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incompliable', 'a.', 'Not compliable; not conformable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incompliance', 'n.', 'The quality or state of being incompliant; unyielding\n   temper; obstinacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incompliance', 'n.', 'Refusal or failure to comply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incompliant', 'a.', 'Not compliant; unyielding to request, solicitation, or\n   command; stubborn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incomposed', 'a.', 'Disordered; disturbed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incomposite', 'a.', 'Not composite; uncompounded; simple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incompossible', 'a.', 'Not capable of joint existence; incompatible;\n   inconsistent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incomprehense', 'a.', 'Incomprehensible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incomprehensibility', 'n.', 'The quality of being incomprehensible, or\n   beyond the reach of human intellect; incomprehensibleness;\n   inconceivability; inexplicability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incomprehensible', 'a.', 'Not capable of being contained within limits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incomprehensible', 'a.', 'Not capable of being comprehended or understood;\n   beyond the reach of the human intellect; inconceivable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incomprehension', 'n.', 'Want of comprehension or understanding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incomprehensive', 'a.', 'Not comprehensive; not capable of including or of\n   understanding; not extensive; limited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incompressibility', 'n.', 'The quality of being incompressible, or\n   incapable of reduction in volume by pressure; -- formerly supposed to\n   be a property of liquids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incompressible', 'a.', 'Not compressible; incapable of being reduced by\n   force or pressure into a smaller compass or volume; resisting\n   compression; as, many liquids and solids appear to be almost\n   incompressible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incomputable', 'a.', 'Not computable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconcealable', 'a.', 'Not concealable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconceivability', 'n.', 'The quality of being inconceivable;\n   inconceivableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconceivable', 'a.', 'Not conceivable; incapable of being conceived by the\n   mind; not explicable by the human intellect, or by any known principles\n   or agencies; incomprehensible; as, it is inconceivable to us how the\n   will acts in producing muscular motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconceptible', 'a.', 'Inconceivable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconcerning', 'a.', 'Unimportant; trifling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconcinne', 'a.', 'Dissimilar; incongruous; unsuitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconcinnity', 'n.', 'Want of concinnity or congruousness; unsuitableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconcinnous', 'a.', 'Not concinnous; unsuitable; discordant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconcludent', 'a.', 'Not inferring a conclusion or consequence; not\n   conclusive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconcluding', 'a.', 'Inferring no consequence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconclusive', 'a.', 'Not conclusive; leading to no conclusion; not closing\n   or settling a point in debate, or a doubtful question; as, evidence is\n   inconclusive when it does not exhibit the truth of a disputed case in\n   such a manner as to satisfy the mind, and put an end to debate or\n   doubt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconcoct', 'a.', 'Inconcocted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconcocted', 'a.', 'Imperfectly digested, matured, or ripened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconcoction', 'n.', 'The state of being undigested; unripeness;\n   immaturity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconcrete', 'a.', 'Not concrete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconcurring', 'a.', 'Not concurring; disagreeing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconcussible', 'a.', 'Not concussible; that cannot be shaken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incondensability', 'n.', 'Alt. of Incondensibility');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incondensibility', 'n.', 'The quality or state of being incondensable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incondensable', 'a.', 'Alt. of Incondensible');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incondensible', 'a.', 'Not condensable; incapable of being made more dense\n   or compact, or reduced to liquid form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incondite', 'a.', 'Badly put together; inartificial; rude; unpolished;\n   irregular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconditional', 'a.', 'Unconditional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconditionate', 'a.', 'Not conditioned; not limited; absolute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconform', 'a.', 'Unconformable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconformable', 'a.', 'Unconformable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconformity', 'n.', 'Want of conformity; nonconformity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconfused', 'a.', 'Not confused; distinct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconfusion', 'n.', 'Freedom from confusion; distinctness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconfutable', 'a.', 'Not confutable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incongealable', 'a.', 'Not congealable; incapable of being congealed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incongenial', 'a.', 'Not congenial; uncongenial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incongruence', 'n.', 'Want of congruence; incongruity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incongruent', 'a.', 'Incongruous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incongruities', 'pl. ', 'of Incongruity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incongruity', 'n.', 'The quality or state of being incongruous; want of\n   congruity; unsuitableness; inconsistency; impropriety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incongruity', 'n.', 'Disagreement of parts; want of symmetry or of harmony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incongruity', 'n.', 'That which is incongruous; want of congruity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incongruous', 'a.', 'Not congruous; reciprocally disagreeing; not capable\n   of harmonizing or readily assimilating; inharmonious; inappropriate;\n   unsuitable; not fitting; inconsistent; improper; as, an incongruous\n   remark; incongruous behavior, action, dress, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconnected', 'a.', 'Not connected; disconnected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconnection', 'n.', 'Disconnection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconnexedly', 'adv.', 'Not connectedly; without connection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconscionable', 'a.', 'Unconscionable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconscious', 'a.', 'Unconscious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconsecutiveness', 'n.', 'The state or quality of not being consecutive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconsequence', 'n.', 'The quality or state of being inconsequent; want of\n   just or logical inference or argument; inconclusiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconsequent', 'a.', 'Not following from the premises; not regularly\n   inferred; invalid; not characterized by logical method; illogical;\n   arbitrary; inconsistent; of no consequence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconsequential', 'a.', 'Not regularly following from the premises; hence,\n   irrelevant; unimportant; of no consequence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconsequentiality', 'n.', 'The state of being inconsequential.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconsequentness', 'n.', 'Inconsequence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconsiderable', 'a.', 'Not considerable; unworthy of consideration or\n   notice; unimportant; small; trivial; as, an inconsiderable distance; an\n   inconsiderable quantity, degree, value, or sum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconsideracy', 'n.', 'Inconsiderateness; thoughtlessness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconsiderate', 'a.', 'Not considerate; not attentive to safety or to\n   propriety; not regarding the rights or feelings of others; hasty;\n   careless; thoughtless; heedless; as, the young are generally\n   inconsiderate; inconsiderate conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconsiderate', 'a.', 'Inconsiderable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconsiderately', 'adv.', 'In an inconsiderate manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconsiderateness', 'n.', 'The quality or state of being inconsiderate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconsideration', 'n.', 'Want of due consideration; inattention to\n   consequences; inconsiderateness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconsistence', 'n.', 'Inconsistency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconsistencies', 'pl. ', 'of Inconsistency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconsistency', 'n.', 'The quality or state of being inconsistent;\n   discordance in respect to sentiment or action; such contrariety between\n   two things that both can not exist or be true together; disagreement;\n   incompatibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconsistency', 'n.', 'Absurdity in argument ore narration; incoherence or\n   irreconcilability in the parts of a statement, argument, or narration;\n   that which is inconsistent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconsistency', 'n.', 'Want of stability or uniformity; unsteadiness;\n   changeableness; variableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconsistent', 'a.', 'Not consistent; showing inconsistency;\n   irreconcilable; discordant; at variance, esp. as regards character,\n   sentiment, or action; incompatible; incongruous; contradictory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconsistent', 'a.', 'Not exhibiting uniformity of sentiment, steadiness to\n   principle, etc.; unequal; fickle; changeable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconsistently', 'adv.', 'In an inconsistent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconsistentness', 'n.', 'Inconsistency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconsisting', 'a.', 'Inconsistent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconsolable', 'a.', 'Not consolable; incapable of being consoled; grieved\n   beyond susceptibility of comfort; disconsolate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconsonance', 'n.', 'Alt. of Inconsonancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconsonancy', 'n.', 'Want of consonance or harmony of sound, action, or\n   thought; disagreement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconsonant', 'a.', 'Not consonant or agreeing; inconsistent; discordant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconspicuous', 'a.', 'Not conspicuous or noticeable; hardly discernible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconstance', 'n.', 'Inconstancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconstancy', 'n.', 'The quality or state of being inconstant; want of\n   constancy; mutability; fickleness; variableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconstant', 'a.', 'Not constant; not stable or uniform; subject to change\n   of character, appearance, opinion, inclination, or purpose, etc.; not\n   firm; unsteady; fickle; changeable; variable; -- said of persons or\n   things; as, inconstant in love or friendship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconstantly', 'adv.', 'In an inconstant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incomsumable', 'a.', 'Not consumable; incapable of being consumed, wasted,\n   or spent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconsummate', 'a.', 'Not consummated; not finished; incomplete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconsumptible', 'a.', 'Inconsumable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incontaminate', 'a.', 'Not contaminated; pure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incontentation', 'n.', 'Discontent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incontestability', 'n.', 'The quality or state of being incontestable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incontestable', 'a.', 'Not contestable; not to be disputed; that cannot be\n   called in question or controverted; incontrovertible; indisputable; as,\n   incontestable evidence, truth, or facts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incontested', 'a.', 'Not contested.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incontiguous', 'a.', 'Not contiguous; not adjoining or in contact;\n   separate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incontinence', 'n.', 'Alt. of Incontinency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incontinency', 'n.', 'Incapacity to hold; hence, incapacity to hold back or\n   restrain; the quality or state of being incontinent; want of\n   continence; failure to restrain the passions or appetites; indulgence\n   of lust; lewdness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incontinency', 'n.', 'The inability of any of the animal organs to restrain\n   the natural evacuations, so that the discharges are involuntary; as,\n   incontinence of urine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incontinent', 'a.', 'Not continent; uncontrolled; not restraining the\n   passions or appetites, particularly the sexual appetite; indulging\n   unlawful lust; unchaste; lewd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incontinent', 'a.', 'Unable to restrain natural evacuations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incontinent', 'n.', 'One who is unchaste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incontinent', 'adv.', 'Incontinently; instantly immediately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incontinently', 'adv.', 'In an incontinent manner; without restraint, or\n   without due restraint; -- used esp. of the passions or appetites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incontinently', 'adv.', 'Immediately; at once; forthwith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incontracted', 'a.', 'Uncontracted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incontrollable', 'a.', 'Not controllable; uncontrollable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incontrovertibility', 'n.', 'The state or condition of being\n   incontrovertible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incontrovertible', 'a.', 'Not controvertible; too clear or certain to admit\n   of dispute; indisputable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconvenience', 'n.', 'The quality or condition of being inconvenient; want\n   of convenience; unfitness; unsuitableness; inexpediency; awkwardness;\n   as, the inconvenience of the arrangement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconvenience', 'n.', 'That which gives trouble, embarrassment, or\n   uneasiness; disadvantage; anything that disturbs quiet, impedes\n   prosperity, or increases the difficulty of action or success; as, one\n   inconvenience of life is poverty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconvenience', 'v. t.', 'To put to inconvenience; to incommode; as, to\n   inconvenience a neighbor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconveniency', 'n.', 'Inconvenience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconvenient', 'a.', 'Not becoming or suitable; unfit; inexpedient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconvenient', 'a.', 'Not convenient; giving trouble, uneasiness, or\n   annoyance; hindering progress or success; uncomfortable;\n   disadvantageous; incommodious; inopportune; as, an inconvenient house,\n   garment, arrangement, or time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconveniently', 'adv.', 'In an inconvenient manner; incommodiously;\n   unsuitably; unseasonably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconversable', 'a.', 'Incommunicative; unsocial; reserved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconversant', 'a.', 'Not conversant; not acquainted; not versed;\n   unfamiliar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconverted', 'a.', 'Not turned or changed about.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconvertibility', 'n.', 'The quality or state of being inconvertible; not\n   capable of being exchanged for, or converted into, something else; as,\n   the inconvertibility of an irredeemable currency, or of lead, into\n   gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconvertible', 'a.', 'Not convertible; not capable of being transmuted,\n   changed into, or exchanged for, something else; as, one metal is\n   inconvertible into another; bank notes are sometimes inconvertible into\n   specie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconvertibleness', 'n.', 'Inconvertibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconvertibly', 'adv.', 'In an inconvertible manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconvincible', 'a.', 'Not convincible; incapable of being convinced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inconvincibly', 'adv.', 'In a manner not admitting of being convinced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incony', 'a.', 'Unlearned; artless; pretty; delicate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incoordinate', 'a.', 'Not coordinate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incoordination', 'n.', 'Want of coordination; lack of harmonious adjustment\n   or action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incoronate', 'a.', 'Crowned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorporal', 'a.', 'Immaterial; incorporeal; spiritual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorporality', 'n.', 'Incorporeality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorporally', 'adv.', 'Incorporeally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorporate', 'a.', 'Not consisting of matter; not having a material body;\n   incorporeal; spiritual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorporate', 'a.', 'Not incorporated; not existing as a corporation; as,\n   an incorporate banking association.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorporate', 'a.', 'Corporate; incorporated; made one body, or united in\n   one body; associated; mixed together; combined; embodied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorporated', 'imp. & p. p.', 'of Incorporate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorporating', 'p. pr. & vb. n.', 'of Incorporate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorporate', 'v. t.', 'To form into a body; to combine, as different\n   ingredients. into one consistent mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorporate', 'v. t.', 'To unite with a material body; to give a material\n   form to; to embody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorporate', 'v. t.', 'To unite with, or introduce into, a mass already\n   formed; as, to incorporate copper with silver; -- used with with and\n   into.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorporate', 'v. t.', 'To unite intimately; to blend; to assimilate; to\n   combine into a structure or organization, whether material or mental;\n   as, to incorporate provinces into the realm; to incorporate anothers\n   ideas into ones work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorporate', 'v. t.', 'To form into a legal body, or body politic; to\n   constitute into a corporation recognized by law, with special\n   functions, rights, duties and liabilities; as, to incorporate a bank, a\n   railroad company, a city or town, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorporate', 'v. i.', 'To unite in one body so as to make a part of it; to\n   be mixed or blended; -- usually followed by with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorporated', 'a.', 'United in one body; formed into a corporation; made a\n   legal entity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorporation', 'n.', 'The act of incorporating, or the state of being\n   incorporated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorporation', 'n.', 'The union of different ingredients in one mass;\n   mixture; combination; synthesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorporation', 'n.', 'The union of something with a body already existing;\n   association; intimate union; assimilation; as, the incorporation of\n   conquered countries into the Roman republic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorporation', 'n.', 'The act of creating a corporation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorporation', 'n.', 'A body incorporated; a corporation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorporative', 'a.', 'Incorporating or tending to incorporate; as, the\n   incorporative languages (as of the Basques, North American Indians,\n   etc. ) which run a whole phrase into one word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorporator', 'n.', 'One of a number of persons who gets a company\n   incorporated; one of the original members of a corporation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorporeal', 'a.', 'Not corporeal; not having a material body or form; not\n   consisting of matter; immaterial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorporeal', 'a.', 'Existing only in contemplation of law; not capable of\n   actual visible seizin or possession; not being an object of sense;\n   intangible; -- opposed to corporeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorporealism', 'n.', 'Existence without a body or material form;\n   immateriality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorporealist', 'n.', 'One who believes in incorporealism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorporeality', 'n.', 'The state or quality of being incorporeal or\n   bodiless; immateriality; incorporealism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorporeally', 'adv.', 'In an incorporeal manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorporeity', 'n.', 'The quality of being incorporeal; immateriality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorpse', 'v. t.', 'To incorporate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorrect', 'a.', 'Not correct; not according to a copy or model, or to\n   established rules; inaccurate; faulty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorrect', 'a.', 'Not in accordance with the truth; inaccurate; not exact;\n   as, an incorrect statement or calculation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorrect', 'a.', 'Not accordant with duty or morality; not duly regulated\n   or subordinated; unbecoming; improper; as, incorrect conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorrection', 'n.', 'Want of correction, restraint, or discipline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorrectly', 'adv.', 'Not correctly; inaccurately; not exactly; as, a\n   writing incorrectly copied; testimony incorrectly stated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorrectness', 'n.', 'The quality of being incorrect; want of conformity\n   to truth or to a standard; inaccuracy; inexactness; as incorrectness\n   may in defect or in redundance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorrespondence', 'n.', 'Alt. of Incorrespondency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorrespondency', 'n.', 'Want of correspondence; disagreement;\n   disproportion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorresponding', 'a.', 'Not corresponding; disagreeing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorrigibility', 'n.', 'The state or quality of being incorrigible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorrigible', 'a.', 'Not corrigible; incapable of being corrected or\n   amended; bad beyond correction; irreclaimable; as, incorrigible error.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorrigible', 'n.', 'One who is corrigible; especially, a hardened\n   criminal; as, the perpetual imprisonment of incorrigibles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorrigibleness', 'n.', 'Incorrigibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorrigibly', 'adv.', 'In an incorrigible manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorrodible', 'a.', 'Incapable of being corroded, consumed, or eaten away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorrupt', 'a.', 'Not affected with corruption or decay; unimpaired; not\n   marred or spoiled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorrupt', 'a.', 'Not defiled or depraved; pure; sound; untainted; above\n   the influence of bribes; upright; honest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorrupted', 'a.', 'Uncorrupted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorruptibility', 'n.', 'The quality of being incorruptible; incapability\n   of corruption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorruptible', 'a.', 'Not corruptible; incapable of corruption, decay, or\n   dissolution; as, gold is incorruptible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorruptible', 'a.', 'Incapable of being bribed or morally corrupted;\n   inflexibly just and upright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorruptible', 'n.', 'One of a religious sect which arose in Alexandria,\n   in the reign of the Emperor Justinian, and which believed that the body\n   of Christ was incorruptible, and that he suffered hunger, thirst, pain,\n   only in appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorruptible', 'n.', 'The quality or state of being incorruptible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorruptibly', 'adv.', 'In an incorruptible manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorruption', 'n.', 'The condition or quality of being incorrupt or\n   incorruptible; absence of, or exemption from, corruption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorruptive', 'a.', 'Incorruptible; not liable to decay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorruptly', 'adv.', 'Without corruption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorruptness', 'n.', 'Freedom or exemption from decay or corruption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incorruptness', 'n.', 'Probity; integrity; honesty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incrassated', 'imp. & p. p.', 'of Incrassate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incrassating', 'p. pr. & vb. n.', 'of Incrassate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incrassate', 'v. t.', 'To make thick or thicker; to thicken; especially, in\n   pharmacy, to thicken (a liquid) by the mixture of another substance, or\n   by evaporating the thinner parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incrassate', 'v. i.', 'To become thick or thicker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incrassate', 'a.', 'Alt. of Incrassated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incrassated', 'a.', 'Made thick or thicker; thickened; inspissated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incrassated', 'a.', 'Thickened; becoming thicker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incrassated', 'a.', 'Swelled out on some particular part, as the antennae\n   of certain insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incrassation', 'n.', 'The act or process of thickening or making thick; the\n   process of becoming thick or thicker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incrassation', 'n.', 'The state of being incrassated or made thick;\n   inspissation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incrassative', 'a.', 'Having the quality of thickening; tending to thicken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incrassative', 'n.', 'A substance which has the power to thicken; formerly,\n   a medicine supposed to thicken the humors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Increasable', 'a.', 'Capable of being increased.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Increased', 'imp. & p. p.', 'of Increase');
  INSERT INTO words (word, word_type, description) VALUES  (    'Increasing', 'p. pr. & vb. n.', 'of Increase');
  INSERT INTO words (word, word_type, description) VALUES  (    'Increase', 'v. i.', 'To become greater or more in size, quantity, number,\n   degree, value, intensity, power, authority, reputation, wealth; to\n   grow; to augment; to advance; -- opposed to decrease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Increase', 'v. i.', 'To multiply by the production of young; to be fertile,\n   fruitful, or prolific.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Increase', 'v. i.', 'To become more nearly full; to show more of the\n   surface; to wax; as, the moon increases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Increase', 'v. t.', 'To augment or make greater in bulk, quantity, extent,\n   value, or amount, etc.; to add to; to extend; to lengthen; to enhance;\n   to aggravate; as, to increase ones possessions, influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Increase', 'v. i.', 'Addition or enlargement in size, extent, quantity,\n   number, intensity, value, substance, etc.; augmentation; growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Increase', 'v. i.', 'That which is added to the original stock by\n   augmentation or growth; produce; profit; interest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Increase', 'v. i.', 'Progeny; issue; offspring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Increase', 'v. i.', 'Generation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Increase', 'v. i.', 'The period of increasing light, or luminous phase; the\n   waxing; -- said of the moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Increaseful', 'a.', 'Full of increase; abundant in produce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Increasement', 'n.', 'Increase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Increaser', 'n.', 'One who, or that, increases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Increasingly', 'adv.', 'More and more.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Increated', 'imp. & p. p.', 'of Increate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Increating', 'p. pr. & vb. n.', 'of Increate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Increate', 'v. t.', 'To create within.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Increate', 'a.', 'Alt. of Increated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Increated', 'a.', 'Uncreated; self-existent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incredibility', 'n.', 'The quality or state of being incredible;\n   incredibleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incredibility', 'n.', 'That which is incredible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incredible', 'a.', 'Not credible; surpassing belief; too extraordinary and\n   improbable to admit of belief; unlikely; marvelous; fabulous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incredibleness', 'n.', 'Incredibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incredibly', 'adv.', 'In an incredible manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incredited', 'a.', 'Uncredited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incredulity', 'n.', 'The state or quality of being i/credulous; a\n   withholding or refusal of belief; skepticism; unbelief; disbelief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incredulous', 'a.', 'Not credulous; indisposed to admit or accept that\n   which is related as true, skeptical; unbelieving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incredulous', 'a.', 'Indicating, or caused by, disbelief or incredulity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incredulous', 'a.', 'Incredible; not easy to be believed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incredulously', 'adv.', 'In an incredulous manner; with incredulity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incredulousness', 'n.', 'Incredulity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incremable', 'a.', 'Incapable of being burnt; incombustibe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incremate', 'v. t.', 'To consume or reduce to ashes by burning, as a dead\n   body; to cremate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incremation', 'n.', 'Burning; esp., the act of burning a dead body;\n   cremation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Increment', 'n.', 'The act or process of increasing; growth in bulk,\n   guantity, number, value, or amount; augmentation; enlargement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Increment', 'n.', 'Matter added; increase; produce; production; -- opposed\n   to decrement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Increment', 'n.', 'The increase of a variable quantity or fraction from its\n   present value to its next ascending value; the finite quantity,\n   generally variable, by which a variable quantity is increased.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Increment', 'n.', 'An amplification without strict climax,');
  INSERT INTO words (word, word_type, description) VALUES  (    'Incremental', 'a.', 'Pertaining to, or resulting from, the process of\n   growth; as, the incremental lines in the dentine of teeth.');
