  INSERT INTO words (word, word_type, description) VALUES  (    'Indefatigation', 'n.', 'Indefatigableness; unweariedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indefeasibility', 'n.', 'The quality of being undefeasible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indefeasible', 'a.', 'Not to be defeated; not defeasible; incapable of\n   being annulled or made void; as, an indefeasible or title.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indefectibility', 'n.', 'The quality of being indefectible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indefectible', 'a.', 'Not defectible; unfailing; not liable to defect,\n   failure, or decay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indefective', 'a.', 'Not defective; perfect; complete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indefeisible', 'a.', 'Indefeasible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indefensibility', 'n.', 'The quality or state of not being defensible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indefensible', '', 'Not defensible; not capable of being defended,\n   maintained, vindicated, or justified; unjustifiable; untenable; as, an\n   indefensible fortress, position, cause, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indefensibly', 'adv.', 'In an indefensible manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indefensive', 'a.', 'Defenseless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indeficiency', 'n.', 'The state or quality of not being deficient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indeficient', 'a.', 'Not deficient; full.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indefinable', 'a.', 'Incapable of being defined or described; inexplicable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indefinably', 'adv.', 'In an indefinable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indefinite', 'a.', 'Not definite; not limited, defined, or specified; not\n   explicit; not determined or fixed upon; not precise; uncertain; vague;\n   confused; obscure; as, an indefinite time, plan, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indefinite', 'a.', 'Having no determined or certain limits; large and\n   unmeasured, though not infinite; unlimited; as indefinite space; the\n   indefinite extension of a straight line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indefinite', 'a.', 'Boundless; infinite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indefinite', 'a.', 'Too numerous or variable to make a particular\n   enumeration important; -- said of the parts of a flower, and the like.\n   Also, indeterminate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indefinitely', 'adv.', 'In an indefinite manner or degree; without any\n   settled limitation; vaguely; not with certainty or exactness; as, to\n   use a word indefinitely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indefiniteness', 'n.', 'The quality of being indefinite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indefinitude', 'n.', 'Indefiniteness; vagueness; also, number or quantity\n   not limited by our understanding, though yet finite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indehiscence', 'n.', 'The property or state of being indehiscent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indehiscent', 'a.', 'Remaining closed at maturity, or not opening along\n   regular lines, as the acorn, or a cocoanut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indelectable', 'a.', 'Not delectable; unpleasant; disagreeable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indeliberate', 'a.', 'Done without deliberation; unpremeditated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indeliberated', 'a.', 'Indeliberate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indelibility', 'n.', 'The quality of being indelible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indelible', 'a.', 'That can not be removed, washed away, blotted out, or\n   effaced; incapable of being canceled, lost, or forgotten; as, indelible\n   characters; an indelible stain; an indelible impression on the memory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indelible', 'a.', 'That can not be annulled; indestructible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indelicacies', 'pl. ', 'of Indelicacy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indelicacy', 'n.', 'The quality of being indelicate; want of delicacy, or\n   of a nice sense of, or regard for, purity, propriety, or refinement in\n   manners, language, etc.; rudeness; coarseness; also, that which is\n   offensive to refined taste or purity of mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indelicate', 'a.', 'Not delicate; wanting delicacy; offensive to good\n   manners, or to purity of mind; coarse; rude; as, an indelicate word or\n   suggestion; indelicate behavior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indemnification', 'n.', 'The act or process of indemnifying, preserving, or\n   securing against loss, damage, or penalty; reimbursement of loss,\n   damage, or penalty; the state of being indemnified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indemnification', 'n.', 'That which indemnifies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indemnified', 'imp. & p. p.', 'of Indemnify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indemnifying', 'p. pr. & vb. n.', 'of Indemnify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indemnify', 'v. t.', 'To save harmless; to secure against loss or damage;\n   to insure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indemnify', 'v. t.', 'To make restitution or compensation for, as for that\n   which is lost; to make whole; to reimburse; to compensate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indemnities', 'pl. ', 'of Indemnity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indemnity', 'n.', 'Security; insurance; exemption from loss or damage, past\n   or to come; immunity from penalty, or the punishment of past offenses;\n   amnesty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indemnity', 'n.', 'Indemnification, compensation, or remuneration for loss,\n   damage, or injury sustained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indemonstrability', 'n.', 'The quality of being indemonstrable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indemonstrable', 'a.', 'Incapable of being demonstrated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indenization', 'n.', 'The act of naturalizing; endenization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indenize', 'v. t.', 'To naturalize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indenizened', 'imp. & p. p.', 'of Indenizen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indenizening', 'p. pr. & vb. n.', 'of Indenizen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indenizen', 'v. t.', 'To invest with the privileges of a denizen; to\n   naturalize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indented', 'imp. & p. p.', 'of Indent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indenting', 'p. pr. & vb. n.', 'of Indent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indent', 'v. t.', 'To notch; to jag; to cut into points like a row of\n   teeth; as, to indent the edge of paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indent', 'v. t.', 'To dent; to stamp or to press in; to impress; as, indent\n   a smooth surface with a hammer; to indent wax with a stamp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indent', 'v. t.', 'To bind out by indenture or contract; to indenture; to\n   apprentice; as, to indent a young man to a shoemaker; to indent a\n   servant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indent', 'v. t.', 'To begin (a line or lines) at a greater or less distance\n   from the margin; as, to indent the first line of a paragraph one em; to\n   indent the second paragraph two ems more than the first. See\n   Indentation, and Indention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indent', 'v. t.', 'To make an order upon; to draw upon, as for military\n   stores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indent', 'v. i.', 'To be cut, notched, or dented.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indent', 'v. i.', 'To crook or turn; to wind in and out; to zigzag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indent', 'v. i.', 'To contract; to bargain or covenant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indent', 'n.', 'A cut or notch in the man gin of anything, or a recess like\n   a notch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indent', 'n.', 'A stamp; an impression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indent', 'n.', 'A certificate, or intended certificate, issued by the\n   government of the United States at the close of the Revolution, for the\n   principal or interest of the public debt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indent', 'n.', 'A requisition or order for supplies, sent to the\n   commissariat of an army.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indentation', 'n.', 'The act of indenting or state of being indented.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indentation', 'n.', 'A notch or recess, in the margin or border of\n   anything; as, the indentations of a leaf, of the coast, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indentation', 'n.', 'A recess or sharp depression in any surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indentation', 'n.', 'The act of beginning a line or series of lines at a\n   little distance within the flush line of the column or page, as in the\n   common way of beginning the first line of a paragraph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indentation', 'n.', 'The measure of the distance; as, an indentation of one\n   em, or of two ems.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indented', 'a.', 'Cut in the edge into points or inequalities, like teeth;\n   jagged; notched; stamped in; dented on the surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indented', 'a.', 'Having an uneven, irregular border; sinuous; undulating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indented', 'a.', 'Notched like the part of a saw consisting of the teeth;\n   serrated; as, an indented border or ordinary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indented', 'a.', 'Bound out by an indenture; apprenticed; indentured; as,\n   an indented servant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indented', 'a.', 'Notched along the margin with a different color, as the\n   feathers of some birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indentedly', 'adv.', 'With indentations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indenting', 'n.', 'Indentation; an impression like that made by a tooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indention', 'n.', 'Same as Indentation, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indentment', 'n.', 'Indenture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indenture', 'n.', 'The act of indenting, or state of being indented.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indenture', 'n.', 'A mutual agreement in writing between two or more\n   parties, whereof each party has usually a counterpart or duplicate;\n   sometimes in the pl., a short form for indentures of apprenticeship,\n   the contract by which a youth is bound apprentice to a master.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indentured', 'imp. & p. p.', 'of Indenture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indenturing', 'p. pr. & vb. n.', 'of Indenture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indenture', 'v. t.', 'To indent; to make hollows, notches, or wrinkles in;\n   to furrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indenture', 'v. t.', 'To bind by indentures or written contract; as, to\n   indenture an apprentice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indenture', 'v. i.', 'To run or wind in and out; to be cut or notched; to\n   indent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Independence', 'n.', 'The state or quality of being independent; freedom\n   from dependence; exemption from reliance on, or control by, others;\n   self-subsistence or maintenance; direction of ones own affairs without\n   interference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Independence', 'n.', 'Sufficient means for a comfortable livelihood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Independency', 'n.', 'Independence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Independency', 'n.', 'Doctrine and polity of the Independents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Independent', 'a.', 'Not dependent; free; not subject to control by others;\n   not relying on others; not subordinate; as, few men are wholly\n   independent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Independent', 'a.', 'Affording a comfortable livelihood; as, an independent\n   property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Independent', 'a.', 'Not subject to bias or influence; not obsequious;\n   self-directing; as, a man of an independent mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Independent', 'a.', 'Expressing or indicating the feeling of independence;\n   free; easy; bold; unconstrained; as, an independent air or manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Independent', 'a.', 'Separate from; exclusive; irrespective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Independent', 'a.', 'Belonging or pertaining to, or holding to the\n   doctrines or methods of, the Independents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Independent', 'a.', 'Not dependent upon another quantity in respect to\n   value or rate of variation; -- said of quantities or functions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Independent', 'a.', 'Not bound by party; exercising a free choice in voting\n   with either or any party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Independent', 'n.', 'One who believes that an organized Christian church is\n   complete in itself, competent to self-government, and independent of\n   all ecclesiastical authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Independent', 'n.', 'One who does not acknowledge an obligation to support\n   a partys candidate under all circumstances; one who exercises liberty\n   in voting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Independentism', 'n.', 'Independency; the church system of Independents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Independently', 'adv.', 'In an independent manner; without control.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indeposable', 'a.', 'Incapable of being deposed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indepravate', 'a.', 'Undepraved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indeprecable', 'a.', 'Incapable or undeserving of being deprecated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indeprehensible', 'a.', 'Incapable of being found out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indeprivable', 'a.', 'Incapable of being deprived, or of being taken away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indescribable', 'a.', 'Incapable of being described.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indescriptive', 'a.', 'Not descriptive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indesert', 'n.', 'Ill desert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indesinent', 'a.', 'Not ceasing; perpetual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indesirable', 'a.', 'Undesirable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indestructibility', 'n.', 'The quality of being indestructible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indestructible', 'a.', 'Not destructible; incapable of decomposition or of\n   being destroyed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indeterminable', 'a.', 'Not determinable; impossible to be determined; not\n   to be definitely known, ascertained, defined, or limited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indeterminable', 'n.', 'An indeterminable thing or quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indeterminate', 'a.', 'Not determinate; not certain or fixed; indefinite;\n   not precise; as, an indeterminate number of years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indetermination', 'n.', 'Want of determination; an unsettled or wavering\n   state, as of the mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indetermination', 'n.', 'Want of fixed or stated direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indetermined', 'a.', 'Undetermined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indevirginate', 'a.', 'Not devirginate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indevote', 'a.', 'Not devoted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indevotion', 'n.', 'Want of devotion; impiety; irreligion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indevout', 'a.', 'Not devout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indew', 'v. t.', 'To indue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indexes', 'pl. ', 'of Index');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indices', 'pl. ', 'of Index');
  INSERT INTO words (word, word_type, description) VALUES  (    'Index', 'n.', 'That which points out; that which shows, indicates,\n   manifests, or discloses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Index', 'n.', 'That which guides, points out, informs, or directs; a\n   pointer or a hand that directs to anything, as the hand of a watch, a\n   movable finger on a gauge, scale, or other graduated instrument. In\n   printing, a sign used to direct particular attention to a note or\n   paragraph; -- called also fist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Index', 'n.', 'A table for facilitating reference to topics, names, and the\n   like, in a book; -- usually alphabetical in arrangement, and printed at\n   the end of the volume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Index', 'n.', 'A prologue indicating what follows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Index', 'n.', 'The second digit, that next pollex, in the manus, or hand;\n   the forefinger; index finger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Index', 'n.', 'The figure or letter which shows the power or root of a\n   quantity; the exponent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'indices', 'pl. ', 'of Index');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indexed', 'imp. & p. p.', 'of Index');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indexing', 'p. pr. & vb. n.', 'of Index');
  INSERT INTO words (word, word_type, description) VALUES  (    'Index', 'v. t.', 'To provide with an index or table of references; to put\n   into an index; as, to index a book, or its contents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indexer', 'n.', 'One who makes an index.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indexical', 'a.', 'Of, pertaining to, or like, an index; having the form of\n   an index.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indexically', 'adv.', 'In the manner of an index.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indexterity', 'n.', 'Want of dexterity or readiness, especially in the use\n   of the hands; clumsiness; awkwardness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'India', 'n.', 'A country in Southern Asia; the two peninsulas of Hither and\n   Farther India; in a restricted sense, Hither India, or Hindostan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indiadem', 'v. t.', 'To place or set in a diadem, as a gem or gems.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indiamen', 'pl. ', 'of Indiaman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indiaman', 'n.', 'A large vessel in the India trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indian', 'a.', 'Of or pertaining to India proper; also to the East Indies,\n   or, sometimes, to the West Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indian', 'a.', 'Of or pertaining to the aborigines, or Indians, of America;\n   as, Indian wars; the Indian tomahawk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indian', 'a.', 'Made of maize or Indian corn; as, Indian corn, Indian meal,\n   Indian bread, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indian', 'n.', 'A native or inhabitant of India.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indian', 'n.', 'One of the aboriginal inhabitants of America; -- so called\n   originally from the supposed identity of America with India.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indianeer', 'n.', 'An Indiaman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'India rubber', '', 'See Caoutchouc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indical', 'a.', 'Indexical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indican', 'n.', 'A glucoside obtained from woad (indigo plant) and other\n   plants, as a yellow or light brown sirup. It has a nauseous bitter\n   taste, a decomposes or drying. By the action of acids, ferments, etc.,\n   it breaks down into sugar and indigo. It is the source of natural\n   indigo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indican', 'n.', 'An indigo-forming substance, found in urine, and other\n   animal fluids, and convertible into red and blue indigo (urrhodin and\n   uroglaucin). Chemically, it is indoxyl sulphate of potash, C8H6NSO4K,\n   and is derived from the indol formed in the alimentary canal. Called\n   also uroxanthin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indicant', 'a.', 'Serving to point out, as a remedy; indicating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indicant', 'n.', 'That which indicates or points out; as, an indicant of\n   the remedy for a disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indicated', 'imp. & p. p.', 'of Indicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indicating', 'p. pr. & vb. n.', 'of Indicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indicate', 'v. t.', 'To point out; to discover; to direct to a knowledge\n   of; to show; to make known.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indicate', 'v. t.', 'To show or manifest by symptoms; to point to as the\n   proper remedies; as, great prostration of strength indicates the use of\n   stimulants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indicate', 'v. t.', 'To investigate the condition or power of, as of steam\n   engine, by means of an indicator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indicated', 'a.', 'Shown; denoted; registered; measured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indication', 'n.', 'Act of pointing out or indicating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indication', 'n.', 'That which serves to indicate or point out; mark;\n   token; sign; symptom; evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indication', 'n.', 'Discovery made; information.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indication', 'n.', 'Explanation; display.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indication', 'n.', 'Any symptom or occurrence in a disease, which serves to\n   direct to suitable remedies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indicative', 'a.', 'Pointing out; bringing to notice; giving intimation or\n   knowledge of something not visible or obvious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indicative', 'a.', 'Suggestive; representing the whole by a part, as a\n   fleet by a ship, a forest by a tree, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indicative', 'n.', 'The indicative mood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indicatively', 'adv.', 'In an indicative manner; in a way to show or\n   signify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indicator', 'n.', 'One who, or that which, shows or points out; as, a fare\n   indicator in a street car.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indicator', 'n.', 'A pressure gauge; a water gauge, as for a steam boiler;\n   an apparatus or instrument for showing the working of a machine or\n   moving part');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indicator', 'n.', 'An instrument which draws a diagram showing the varying\n   pressure in the cylinder of an engine or pump at every point of the\n   stroke. It consists of a small cylinder communicating with the engine\n   cylinder and fitted with a piston which the varying pressure drives\n   upward more or less against the resistance of a spring. A lever imparts\n   motion to a pencil which traces the diagram on a card wrapped around a\n   vertical drum which is turned back and forth by a string connected with\n   the piston rod of the engine. See Indicator card (below).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indicator', 'n.', 'A telltale connected with a hoisting machine, to show,\n   at the surface, the position of the cage in the shaft of a mine, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indicator', 'n.', 'The part of an instrument by which an effect is\n   indicated, as an index or pointer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indicator', 'n.', 'Any bird of the genus Indicator and allied genera. See\n   Honey guide, under Honey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indicator', 'n.', 'That which indicates the condition of acidity,\n   alkalinity, or the deficiency, excess, or sufficiency of a standard\n   reagent, by causing an appearance, disappearance, or change of color,\n   as in titration or volumetric analysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indicatory', 'a.', 'Serving to show or make known; showing; indicative;\n   signifying; implying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indicatrix', 'n.', 'A certain conic section supposed to be drawn in the\n   tangent plane to any surface, and used to determine the accidents of\n   curvature of the surface at the point of contact. The curve is similar\n   to the intersection of the surface with a parallel to the tangent plane\n   and indefinitely near it. It is an ellipse when the curvature is\n   synclastic, and an hyperbola when the curvature is anticlastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indicavit', 'n.', 'A writ of prohibition against proceeding in the\n   spiritual court in certain cases, when the suit belongs to the\n   common-law courts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indice', 'n.', 'Index; indication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indices', 'n. pl.', 'See Index.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indicia', 'n. pl.', 'Discriminating marks; signs; tokens; indications;\n   appearances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indicible', 'a.', 'Unspeakable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indicolite', 'n.', 'A variety of tourmaline of an indigo-blue color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indicted', 'imp. & p. p.', 'of Indict');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indicting', 'p. pr. & vb. n.', 'of Indict');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indict', 'v. t.', 'To write; to compose; to dictate; to indite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indict', 'v. t.', 'To appoint publicly or by authority; to proclaim or\n   announce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indict', 'v. t.', 'To charge with a crime, in due form of law, by the\n   finding or presentment of a grand jury; to find an indictment against;\n   as, to indict a man for arson. It is the peculiar province of a grand\n   jury to indict, as it is of a house of representatives to impeach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indictable', 'a.', 'Capable of being, or liable to be, indicted; subject to\n   indictment; as, an indictable offender or offense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indictee', 'n.', 'A person indicted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indicter', 'n.', 'One who indicts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indiction', 'n.', 'Declaration; proclamation; public notice or appointment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indiction', 'n.', 'A cycle of fifteen years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indictive', 'a.', 'Proclaimed; declared; public.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indictment', 'n.', 'The act of indicting, or the state of being indicted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indictment', 'n.', 'The formal statement of an offense, as framed by the\n   prosecuting authority of the State, and found by the grand jury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indictment', 'n.', 'An accusation in general; a formal accusation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indictor', 'n.', 'One who indicts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indies', 'n. pl.', 'A name designating the East Indies, also the West\n   Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indifference', 'n.', 'The quality or state of being indifferent, or not\n   making a difference; want of sufficient importance to constitute a\n   difference; absence of weight; insignificance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indifference', 'n.', 'Passableness; mediocrity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indifference', 'n.', 'Impartiality; freedom from prejudice, prepossession,\n   or bias.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indifference', 'n.', 'Absence of anxiety or interest in respect to what is\n   presented to the mind; unconcernedness; as, entire indifference to all\n   that occurs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indifferency', 'n.', 'Absence of interest in, or influence from, anything;\n   unconcernedness; equilibrium; indifferentism; indifference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indifferent', 'a.', 'Not mal/ing a difference; having no influence or\n   preponderating weight; involving no preference, concern, or attention;\n   of no account; without significance or importance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indifferent', 'a.', 'Neither particularly good, not very bad; of a middle\n   state or quality; passable; mediocre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indifferent', 'a.', 'Not inclined to one side, party, or choice more than\n   to another; neutral; impartial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indifferent', 'a.', 'Feeling no interest, anxiety, or care, respecting\n   anything; unconcerned; inattentive; apathetic; heedless; as, to be\n   indifferent to the welfare of ones family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indifferent', 'a.', 'Free from bias or prejudice; impartial; unbiased;\n   disinterested.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indifferent', 'adv.', 'To a moderate degree; passably; tolerably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indifferentism', 'n.', 'State of indifference; want of interest or\n   earnestness; especially, a systematic apathy regarding what is true or\n   false in religion or philosophy; agnosticism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indifferentism', 'n.', 'Same as Identism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indifferentism', 'n.', 'A heresy consisting in an unconcern for any\n   particular creed, provided the morals be right and good.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indifferentist', 'n.', 'One governed by indifferentism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indifferently', 'adv.', 'In an indifferent manner; without distinction or\n   preference; impartially; without concern, wish, affection, or aversion;\n   tolerably; passably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indifulvin', 'n.', 'A reddish resinous substance, obtained from indican.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indifuscin', 'n.', 'A brown amorphous powder, obtained from indican.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigeen', 'n.', 'Same as Indigene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigence', 'n.', 'The condition of being indigent; want of estate, or\n   means of comfortable subsistence; penury; poverty; as, helpless,\n   indigence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigency', 'n.', 'Indigence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigene', 'n.', 'One born in a country; an aboriginal animal or plant; an\n   autochthon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigenous', 'a.', 'Native; produced, growing, or living, naturally in a\n   country or climate; not exotic; not imported.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigenous', 'a.', 'Native; inherent; innate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigent', 'a.', 'Wanting; void; free; destitute; -- used with of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigent', 'a.', 'Destitute of property or means of comfortable\n   subsistence; needy; poor; in want; necessitous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigently', 'adv.', 'In an indigent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigest', 'a.', 'Crude; unformed; unorganized; undigested.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigest', 'n.', 'Something indigested.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigested', 'a.', 'Not digested; undigested.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigested', 'a.', 'Not resolved; not regularly disposed and arranged; not\n   methodical; crude; as, an indigested array of facts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigested', 'a.', 'Not in a state suitable for healing; -- said of wounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigested', 'a.', 'Not ripened or suppurated; -- said of an abscess or its\n   contents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigested', 'a.', 'Not softened by heat, hot water, or steam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigestedness', 'n.', 'The state or quality of being undigested;\n   crudeness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigestibility', 'n.', 'The state or quality of being indigestible;\n   indigestibleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigestible', 'a.', 'Not digestible; not readily soluble in the digestive\n   juices; not easily convertible into products fitted for absorption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigestible', 'a.', 'Not digestible in the mind; distressful; intolerable;\n   as, an indigestible simile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigestion', 'n.', 'Lack of proper digestive action; a failure of the\n   normal changes which food should undergo in the alimentary canal;\n   dyspepsia; incomplete or difficult digestion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigitate', 'v. i.', 'To communicative ideas by the fingers; to show or\n   compute by the fingers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigitated', 'imp. & p. p.', 'of Indigitate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigitating', 'p. pr. & vb. n.', 'of Indigitate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigitate', 'v. t.', 'To point out with the finger; to indicate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigitation', 'n.', 'The act of pointing out as with the finger;\n   indication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indiglucin', 'n.', 'The variety of sugar (glucose) obtained from the\n   glucoside indican. It is unfermentable, but reduces Fehlings solution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indign', 'a.', 'Unworthy; undeserving; disgraceful; degrading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indignance', 'n.', 'Alt. of Indignancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indignancy', 'n.', 'Indignation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indignant', 'a.', 'Affected with indignation; wrathful; passionate; irate;\n   feeling wrath, as when a person is exasperated by unworthy or unjust\n   treatment, by a mean action, or by a degrading accusation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indignantly', 'adv.', 'In an indignant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indignation', 'n.', 'The feeling excited by that which is unworthy, base,\n   or disgraceful; anger mingled with contempt, disgust, or abhorrence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indignation', 'n.', 'The effect of anger; punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indignify', 'v. t.', 'To treat disdainfully or with indignity; to contemn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indignities', 'pl. ', 'of Indignity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indignity', 'n.', 'Any action toward another which manifests contempt for\n   him; an offense against personal dignity; unmerited contemptuous\n   treatment; contumely; incivility or injury, accompanied with insult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indignly', 'adv.', 'Unworthily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigoes', 'pl. ', 'of Indigo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigo', 'n.', 'A kind of deep blue, one of the seven prismatic colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigo', 'n.', 'A blue dyestuff obtained from several plants belonging to\n   very different genera and orders; as, the woad, Isatis tinctoria,\n   Indigofera tinctoria, I. Anil, Nereum tinctorium, etc. It is a dark\n   blue earthy substance, tasteless and odorless, with a copper-violet\n   luster when rubbed. Indigo does not exist in the plants as such, but is\n   obtained by decomposition of the glycoside indican.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigo', 'a.', 'Having the color of, pertaining to, or derived from,\n   indigo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigofera', 'n.', 'A genus of leguminous plants having many species,\n   mostly in tropical countries, several of them yielding indigo, esp.\n   Indigofera tinctoria, and I. Anil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigogen', 'n.', 'See Indigo white, under Indigo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigogen', 'n.', 'Same as Indican, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigometer', 'n.', 'An instrument for ascertaining the strength of an\n   indigo solution, as in volumetric analysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigometry', 'n.', 'The art or method of determining the coloring power of\n   indigo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigotic', 'a.', 'Pertaining to, or derived from, indigo; as, indigotic\n   acid, which is also called anilic or nitrosalicylic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigotin', 'n.', 'See Indigo blue, under Indigo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indigrubin', 'n.', 'Same as Urrhodin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indihumin', 'n.', 'A brown amorphous substance resembling humin, and\n   obtained from indican.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indilatory', 'a.', 'Not dilatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indiligence', 'n.', 'Want of diligence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indiligent', 'a.', 'Not diligent; idle; slothful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indiminishable', 'a.', 'Incapable of being diminished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indin', 'n.', 'A dark red crystalline substance, isomeric with and\n   resembling indigo blue, and obtained from isatide and dioxindol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indirect', 'a.', 'Not direct; not straight or rectilinear; deviating from a\n   direct line or course; circuitous; as, an indirect road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indirect', 'a.', 'Not tending to an aim, purpose, or result by the plainest\n   course, or by obvious means, but obliquely or consequentially; by\n   remote means; as, an indirect accusation, attack, answer, or proposal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indirect', 'a.', 'Not straightforward or upright; unfair; dishonest;\n   tending to mislead or deceive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indirect', 'a.', 'Not resulting directly from an act or cause, but more or\n   less remotely connected with or growing out of it; as, indirect\n   results, damages, or claims.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indirect', 'a.', 'Not reaching the end aimed at by the most plain and\n   direct method; as, an indirect proof, demonstration, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indirected', 'a.', 'Not directed; aimless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indirection', 'n.', 'Oblique course or means; dishonest practices;\n   indirectness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indirectly', 'adv.', 'In an direct manner; not in a straight line or\n   course; not in express terms; obliquely; not by direct means; hence,\n   unfairly; wrongly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indirectness', 'n.', 'The quality or state of being indirect; obliquity;\n   deviousness; crookedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indirectness', 'n.', 'Deviation from an upright or straightforward course;\n   unfairness; dishonesty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indiretin', 'n.', 'A dark brown resinous substance obtained from indican.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indirubin', 'n.', 'A substance isomeric with, and resembling, indigo blue,\n   and accompanying it as a side product, in its artificial production.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indiscernible', 'a.', 'Not to be discerned; imperceptible; not discoverable\n   or visible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indiscerpibility', 'n.', 'Alt. of Indiscerptibility');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indiscerptibility', 'n.', 'The state or quality of being indiscerpible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indiscerpible', 'a.', 'Alt. of Indiscerptible');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indiscerptible', 'a.', 'Not discerpible; inseparable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indisciplinable', 'a.', 'Not disciplinable; undisciplinable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indiscipline', 'n.', 'Want of discipline or instruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indiscoverable', 'a.', 'Not discoverable; undiscoverable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indiscovery', 'n.', 'Want of discovery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indiscreet', 'a.', 'Not discreet; wanting in discretion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indiscrete', 'a.', 'Indiscreet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indiscrete', 'a.', 'Not discrete or separated; compact; homogenous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indiscretion', 'n.', 'The quality or state of being indiscreet; want of\n   discretion; imprudence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indiscretion', 'n.', 'An indiscreet act; indiscreet behavior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indiscriminate', 'a.', 'Not discriminate; wanting discrimination;\n   undistinguishing; not making any distinction; confused; promiscuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indiscriminating', 'a.', 'Not discriminating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indiscrimination', 'n.', 'Want of discrimination or distinction;\n   impartiality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indiscriminative', 'a.', 'Making no distinction; not discriminating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indiscussed', 'a.', 'Not discussed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indispensability', 'n.', 'Indispensableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indispensable', 'a.', 'Not dispensable; impossible to be omitted, remitted,\n   or spared; absolutely necessary or requisite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indispensable', 'a.', 'Not admitting dispensation; not subject to release\n   or exemption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indispensable', 'a.', 'Unavoidable; inevitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indispensableness', 'n.', 'The state or quality of being indispensable, or\n   absolutely necessary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indispensably', 'adv.', 'In an indispensable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indispersed', 'a.', 'Not dispersed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indisposed', 'imp. & p. p.', 'of Indispose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indisposing', 'p. pr. & vb. n.', 'of Indispose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indispose', 'v. t.', 'To render unfit or unsuited; to disqualify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indispose', 'v. t.', 'To disorder slightly as regards health; to make\n   somewhat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indispose', 'v. t.', 'To disincline; to render averse or unfavorable; as, a\n   love of pleasure indisposes the mind to severe study; the pride and\n   selfishness of men indispose them to religious duties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indisposedness', 'n.', 'The condition or quality of being indisposed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indisposition', 'n.', 'The state of being indisposed; disinclination; as,\n   the indisposition of two substances to combine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indisposition', 'n.', 'A slight disorder or illness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indisputability', 'n.', 'Indisputableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indisputable', 'a.', 'Not disputable; incontrovertible; too evident to\n   admit of dispute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indisputed', 'a.', 'Undisputed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indissipable', 'a.', 'Incapable o/ being dissipated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indisdolubility', 'n.', 'The quality or state of being indissoluble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indissoluble', 'a.', 'Not dissoluble; not capable of being dissolved,\n   melted, or liquefied; insoluble; as few substances are indissoluble by\n   heat, but many are indissoluble in water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indissoluble', 'a.', 'Incapable of being rightfully broken or dissolved;\n   perpetually binding or obligatory; firm; stable, as, an indissoluble\n   league or covenant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indissolubleness', 'n.', 'Indissolubility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indissolubly', 'adv.', 'In an indissoluble manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indissolvable', 'a.', 'Not dissolvable; incapable of being dissolved or\n   separated; incapable o/ separation; perpetually firm and binding;\n   indissoluble; as, an indissolvable bond of union.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indissolvableness', 'n.', 'Indissolubleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indistancy', 'n.', 'Want of distance o/ separation; nearness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indistinct', 'a.', 'Not distinct or distinguishable; not separate in such a\n   manner as to be perceptible by itself; as, the indistinct parts of a\n   substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indistinct', 'a.', 'Obscure to the mind or senses; not clear; not definite;\n   confused; imperfect; faint; as, indistinct vision; an indistinct sound;\n   an indistinct idea or recollection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indistinctible', 'a.', 'Indistinguishable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indistinction', 'n.', 'Want of distinction or distinguishableness;\n   confusion; uncertainty; indiscrimination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indistinctive', 'a.', 'Having nothing distinctive; common.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indistinctly', 'adv.', 'In an indistinct manner; not clearly; confusedly;\n   dimly; as, certain ideas are indistinctly comprehended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indistinctness', 'n.', 'The quality or condition of being indistinct; want\n   of definiteness; dimness; confusion; as, the indistinctness of a\n   picture, or of comprehension; indistinctness of vision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indistinguishable', 'a.', 'Not distinguishable; not capable of being\n   perceived, known, or discriminated as separate and distinct; hence, not\n   capable of being perceived or known; as, in the distance the flagship\n   was indisguishable; the two copies were indisguishable in form or\n   color; the difference between them was indisguishable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indistinguishably', 'adv.', 'In a indistinguishable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indistinguished', 'a.', 'Indistinct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indistinguishing', 'a.', 'Making no difference; indiscriminative;\n   impartial; as, indistinguishing liberalities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indisturbance', 'n.', 'Freedom from disturbance; calmness; repose; apathy;\n   indifference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inditch', 'v. t.', 'To bury in, or cast into, a ditch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indited', 'imp. & p. p.', 'of Indite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inditing', 'p. pr. & vb. n.', 'of Indite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indite', 'v. t.', 'To compose; to write; to be author of; to dictate; to\n   prompt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indite', 'v. t.', 'To invite or ask.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indite', 'v. t.', 'To indict; to accuse; to censure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indite', 'v. i.', 'To compose; to write, as a poem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inditement', 'n.', 'The act of inditing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inditer', 'n.', 'One who indites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indium', 'n.', 'A rare metallic element, discovered in certain ores of\n   zinc, by means of its characteristic spectrum of two indigo blue lines;\n   hence, its name. In appearance it resembles zinc, being white or lead\n   gray, soft, malleable and easily fusible, but in its chemical relation\n   it resembles aluminium or gallium. Symbol In. Atomic weight, 113.4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indivertible', 'a.', 'Not to be diverted or turned aside.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Individable', 'a.', 'Indivisible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Individed', 'a.', 'Undivided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Individual', 'a.', 'Not divided, or not to be divided; existing as one\n   entity, or distinct being or object; single; one; as, an individual\n   man, animal, or city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Individual', 'a.', 'Of or pertaining to one only; peculiar to, or\n   characteristic of, a single person or thing; distinctive; as,\n   individual traits of character; individual exertions; individual\n   peculiarities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Individual', 'n.', 'A single person, animal, or thing of any kind; a thing\n   or being incapable of separation or division, without losing its\n   identity; especially, a human being; a person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Individual', 'n.', 'An independent, or partially independent, zooid of a\n   compound animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Individual', 'n.', 'The product of a single egg, whether it remains a\n   single animal or becomes compound by budding or fission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Individualism', 'n.', 'The quality of being individual; individuality;\n   personality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Individualism', 'n.', 'An excessive or exclusive regard to ones personal\n   interest; self-interest; selfishness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Individualistic', 'a.', 'Of or pertaining to the individual or\n   individualism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Individualities', 'pl. ', 'of Individuality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Individuality', 'n.', 'The quality or state of being individual or\n   constituting an individual; separate or distinct existence; oneness;\n   unity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Individuality', 'n.', 'The character or property appropriate or peculiar to\n   an individual; that quality which distinguishes one person or thing\n   from another; the sum of characteristic traits; distinctive character;\n   as, he is a person of marked individuality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Individualization', 'n.', 'The act of individualizing; the state of being\n   individualized; individuation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Individualized', 'imp. & p. p.', 'of Individualize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Individualizing', 'p. pr. & vb. n.', 'of Individualize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Individualize', 'v. t.', 'The mark as an individual, or to distinguish from\n   others by peculiar properties; to invest with individuality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Individualizer', 'n.', 'One who individualizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Individually', 'adv.', 'In an individual manner or relation; as\n   individuals; separately; each by itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Individually', 'adv.', 'In an inseparable manner; inseparably;\n   incommunicably; indivisibly; as, individuallyhe same.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Individuate', 'a.', 'Undivided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Individua', 'imp. & p. p.', 'of Individuate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Individuating', 'p. pr. & vb. n.', 'of Individuate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Individuate', 'v. t.', 'To distinguish from others from others of the\n   species; to endow with individuality; to divide into individuals; to\n   discriminate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Individuation', 'n.', 'The act of individuating or state of being\n   individuated; individualization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Individuator', 'n.', 'One who, or that which, individuates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Individuity', 'n.', 'Separate existence; individuality; oneness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indivinity', 'n.', 'Want or absence of divine power or of divinity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indivisibility', 'n.', 'The state or property of being indivisible or\n   inseparable; inseparability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indivisible', 'a.', 'Not divisible; incapable of being divided, separated,\n   or broken; not separable into parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indivisible', 'a.', 'Not capable of exact division, as one quantity by\n   another; incommensurable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indivisible', 'n.', 'That which is indivisible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indivisible', 'n.', 'An infinitely small quantity which is assumed to admit\n   of no further division.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indivisibleness', 'n.', 'The state of being indivisible; indivisibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indivisibly', 'adv.', 'In an indivisible manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indivision', 'n.', 'A state of being not divided; oneness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indo-', '', 'A prefix signifying Indian (i. e., East Indian); of or\n   pertaining of India.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indoaniline', 'n.', 'Any one of a series of artificial blue dyes, in\n   appearance resembling indigo, for which they are often used as\n   substitutes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'IndoBriton', 'n.', 'A person born in India, of mixed Indian and British\n   blood; a half-caste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indo-Chinese', 'a.', 'Of or pertaining to Indo-China (i. e., Farther India,\n   or India beyond the Ganges).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indocibility', 'n.', 'The state of being indocible; indocibleness;\n   indocility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indocible', 'a.', 'Incapable of being taught, or not easily instructed;\n   dull in intellect; intractable; unteachable; indocile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indocile', 'a.', 'Not teachable; indisposed to be taught, trained, or\n   disciplined; not easily instructed or governed; dull; intractable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indocility', 'n.', 'The quality or state of being indocile; dullness of\n   intellect; unteachableness; intractableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indoctrinated', 'imp. & p. p.', 'of Indoctrinate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indoctrinating', 'p. pr. & vb. n.', 'of Indoctrinate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indoctrinate', 'v. t.', 'To instruct in the rudiments or principles of\n   learning, or of a branch of learning; to imbue with learning; to\n   instruct in, or imbue with, principles or doctrines; to teach; -- often\n   followed by in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indoctrination', 'n.', 'The act of indoctrinating, or the condition of\n   being indoctrinated; instruction in the rudiments and principles of any\n   science or system of belief; information.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indo-English', 'a.', 'Of or relating to the English who are born or reside\n   in India; Anglo-Indian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indo-European', 'a.', 'Aryan; -- applied to the languages of India and\n   Europe which are derived from the prehistoric Aryan language; also,\n   pertaining to the people or nations who speak these languages; as, the\n   Indo-European or Aryan family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indogen', 'n.', 'A complex, nitrogenous radical, C8H5NO, regarded as the\n   essential nucleus of indigo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indogenide', 'n.', 'Any one of the derivatives of indogen, which contain\n   that group as a nucleus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indo-Germanic', 'a.', 'Same as Aryan, and Indo-European.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indo-Germanic', 'a.', 'Pertaining to or denoting the Teutonic family of\n   languages as related to the Sanskrit, or derived from the ancient Aryan\n   language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indoin', 'n.', 'A substance resembling indigo blue, obtained artificially\n   from certain isatogen compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indol', 'n.', 'A white, crystalline substance, C8H7N, obtained from blue\n   indigo, and almost all indigo derivatives, by a process of reduction.\n   It is also formed from albuminous matter, together with skatol, by\n   putrefaction, and by fusion with caustic potash, and is present in\n   human excrement, as well as in the intestinal canal of some herbivora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indolence', 'n.', 'Freedom from that which pains, or harasses, as toil,\n   care, grief, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indolence', 'n.', 'The quality or condition of being indolent; inaction, or\n   want of exertion of body or mind, proceeding from love of ease or\n   aversion to toil; habitual idleness; indisposition to labor; laziness;\n   sloth; inactivity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indolency', 'n.', 'Indolence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indolent', 'a.', 'Free from toil, pain, or trouble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indolent', 'a.', 'Indulging in ease; avoiding labor and exertion;\n   habitually idle; lazy; inactive; as, an indolent man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indolent', 'a.', 'Causing little or no pain or annoyance; as, an indolent\n   tumor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indolently', 'adv.', 'In an indolent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indoles', 'n.', 'Natural disposition; natural quality or abilities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indolin', 'n.', 'A dark resinous substance, polymeric with indol, and\n   obtained by the reduction of indigo white.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indomable', 'a.', 'Indomitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indomitable', 'a.', 'Not to be subdued; untamable; invincible; as, an\n   indomitable will, courage, animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indomite', 'a.', 'Not tamed; untamed; savage; wild.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indomptable', 'a.', 'Indomitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indoor', 'a.', 'Done or being within doors; within a house or institution;\n   domestic; as, indoor work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indoors', 'adv.', 'Within the house; -- usually separated, in doors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indophenol', 'n.', 'Any one of a series of artificial blue dyestuffs,\n   resembling indigo in appearance, and obtained by the action of phenol\n   on certain nitrogenous derivatives of quinone. Simple indophenol proper\n   has not yet been isolated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indorsable', 'a.', 'Capable of being indorsed; transferable; convertible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indorsation', 'n.', 'Indorsement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indorsed', 'imp. & p. p.', 'of Indorse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indorsing', 'p. pr. & vb. n.', 'of Indorse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indorse', 'v. t.', 'To cover the back of; to load or burden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indorse', 'v. t.', 'To write upon the back or outside of a paper or letter,\n   as a direction, heading, memorandum, or address.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indorse', 'v. t.', 'To write ones name, alone or with other words, upon\n   the back of (a paper), for the purpose of transferring it, or to secure\n   the payment of a /ote, draft, or the like; to guarantee the payment,\n   fulfillment, performance, or validity of, or to certify something upon\n   the back of (a check, draft, writ, warrant of arrest, etc.).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indorse', 'v. t.', 'To give ones name or support to; to sanction; to aid\n   by approval; to approve; as, to indorse an opinion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indorsed', 'a.', 'See Addorsed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indorsee', 'n.', 'The person to whom a note or bill is indorsed, or\n   assigned by indorsement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indorsement', 'n.', 'The act of writing on the back of a note, bill, or\n   other written instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indorsement', 'n.', 'That which is written on the back of a note, bill, or\n   other paper, as a name, an order for, or a receipt of, payment, or the\n   return of an officer, etc.; a writing, usually upon the back, but\n   sometimes on the face, of a negotiable instrument, by which the\n   property therein is assigned and transferred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indorsement', 'n.', 'Sanction, support, or approval; as, the indorsement of\n   a rumor, an opinion, a course, conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indorser', 'n.', 'Alt. of Indorsor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indorsor', 'n.', 'The person who indorses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indow', 'v. t.', 'See Endow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indowment', 'n.', 'See Endowment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indoxyl', 'n.', 'A nitrogenous substance, C8H7NO, isomeric with oxindol,\n   obtained as an oily liquid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indoxylic', 'a.', 'Of or pertaining to, or producing, indoxyl; as,\n   indoxylic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indraught', 'n.', 'An opening from the sea into the land; an inlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indraught', 'n.', 'A draught of air or flow of water setting inward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indrawn', 'a.', 'Drawn in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indrench', 'v. t.', 'To overwhelm with water; to drench; to drown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indris', 'n.', 'Alt. of Indri');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indri', 'n.', 'Any lemurine animal of the genus Indris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indubious', 'a.', 'Not dubious or doubtful; certain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indubious', 'a.', 'Not doubting; unsuspecting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indubitable', 'a.', 'Not dubitable or doubtful; too evident to admit of\n   doubt; unquestionable; evident; apparently certain; as, an indubitable\n   conclusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indubitable', 'n.', 'That which is indubitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indubitableness', 'n.', 'The state or quality of being indubitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indubitably', 'adv.', 'Undoubtedly; unquestionably; in a manner to remove\n   all doubt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indubitate', 'a.', 'Not questioned or doubtful; evident; certain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indubitate', 'v. t.', 'To bring into doubt; to cause to be doubted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Induced', 'imp. & p. p.', 'of Induce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inducing', 'p. pr. & vb. n.', 'of Induce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Induce', 'v. t.', 'To lead in; to introduce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Induce', 'v. t.', 'To draw on; to overspread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Induce', 'v. t.', 'To lead on; to influence; to prevail on; to incite; to\n   move by persuasion or influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Induce', 'v. t.', 'To bring on; to effect; to cause; as, a fever induced by\n   fatigue or exposure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Induce', 'v. t.', 'To produce, or cause, by proximity without contact or\n   transmission, as a particular electric or magnetic condition in a body,\n   by the approach of another body in an opposite electric or magnetic\n   state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Induce', 'v. t.', 'To generalize or conclude as an inference from all the\n   particulars; -- the opposite of deduce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inducement', 'n.', 'The act of inducing, or the state of being induced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inducement', 'n.', 'That which induces; a motive or consideration that\n   leads one to action or induces one to act; as, reward is an inducement\n   to toil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inducement', 'n.', 'Matter stated by way of explanatory preamble or\n   introduction to the main allegations of a pleading; a leading to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inducer', 'n.', 'One who, or that which, induces or incites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inducible', 'a.', 'Capable of being induced, caused, or made to take place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inducible', 'a.', 'Obtainable by induction; derivable; inferable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inducted', 'imp. & p. p.', 'of Induct');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inducting', 'p. pr. & vb. n.', 'of Induct');
  INSERT INTO words (word, word_type, description) VALUES  (    'Induct', 'v. t.', 'To bring in; to introduce; to usher in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Induct', 'v. t.', 'To introduce, as to a benefice or office; to put in\n   actual possession of the temporal rights of an ecclesiastical living,\n   or of any other office, with the customary forms and ceremonies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inducteous', 'a.', 'Rendered electro-polar by induction, or brought into\n   the opposite electrical state by the influence of inductive bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inductile', 'a.', 'Not ductile; incapable of being drawn into threads, as a\n   metal; inelastic; tough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inductility', 'n.', 'The quality or state of being inductile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Induction', 'n.', 'The act or process of inducting or bringing in;\n   introduction; entrance; beginning; commencement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Induction', 'n.', 'An introduction or introductory scene, as to a play; a\n   preface; a prologue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Induction', 'n.', 'The act or process of reasoning from a part to a whole,\n   from particulars to generals, or from the individual to the universal;\n   also, the result or inference so reached.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Induction', 'n.', 'The introduction of a clergyman into a benefice, or of\n   an official into a office, with appropriate acts or ceremonies; the\n   giving actual possession of an ecclesiastical living or its\n   temporalities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Induction', 'n.', 'A process of demonstration in which a general truth is\n   gathered from an examination of particular cases, one of which is known\n   to be true, the examination being so conducted that each case is made\n   to depend on the preceding one; -- called also successive induction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Induction', 'n.', 'The property by which one body, having electrical or\n   magnetic polarity, causes or induces it in another body without direct\n   contact; an impress of electrical or magnetic force or condition from\n   one body on another without actual contact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inductional', 'a.', 'Pertaining to, or proceeding by, induction; inductive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inductive', 'a.', 'Leading or drawing; persuasive; tempting; -- usually\n   followed by to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inductive', 'a.', 'Tending to induce or cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inductive', 'a.', 'Leading to inferences; proceeding by, derived from, or\n   using, induction; as, inductive reasoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inductive', 'a.', 'Operating by induction; as, an inductive electrical\n   machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inductive', 'a.', 'Facilitating induction; susceptible of being acted upon\n   by induction; as certain substances have a great inductive capacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inductively', 'adv.', 'By induction or inference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inductometer', 'n.', 'An instrument for measuring or ascertaining the\n   degree or rate of electrical induction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inductor', 'n.', 'The person who inducts another into an office or\n   benefice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inductor', 'n.', 'That portion of an electrical apparatus, in which is the\n   inducing charge or current.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inductoriums', 'pl. ', 'of Inductorium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inductoria', 'pl. ', 'of Inductorium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inductorium', 'n.', 'An induction coil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inductric', 'a.', 'Alt. of Inductrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inductrical', 'a.', 'Acting by, or in a state of, induction; relating to\n   electrical induction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indued', 'imp. & p. p.', 'of Indue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Induing', 'p. pr. & vb. n.', 'of Indue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indue', 'v. t.', 'To put on, as clothes; to draw on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indue', 'v. t.', 'To clothe; to invest; hence, to endow; to furnish; to\n   supply with moral or mental qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Induement', 'n.', 'The act of induing, or state of being indued;\n   investment; endowment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indulged', 'imp. & p. p.', 'of Indulge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indulging', 'p. pr. & vb. n.', 'of Indulge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indulge', 'v. t.', 'To be complacent toward; to give way to; not to oppose\n   or restrain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indulge', 'v. t.', 'to give free course to; to give ones self up to; as,\n   to indulge sloth, pride, selfishness, or inclinations;');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indulge', 'v. t.', 'to yield to the desire of; to gratify by compliance; to\n   humor; to withhold restraint from; as, to indulge children in their\n   caprices or willfulness; to indulge ones self with a rest or in\n   pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indulge', 'v. t.', 'To grant as by favor; to bestow in concession, or in\n   compliance with a wish or request.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indulge', 'v. i.', 'To indulge ones self; to gratify ones tastes or\n   desires; esp., to give ones self up (to); to practice a forbidden or\n   questionable act without restraint; -- followed by in, but formerly,\n   also, by to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indulgement', 'n.', 'Indulgence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indulgence', 'n.', 'The act of indulging or humoring; the quality of being\n   indulgent; forbearance of restrain or control.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indulgence', 'n.', 'An indulgent act; favor granted; gratification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indulgence', 'n.', 'Remission of the temporal punishment due to sins, after\n   the guilt of sin has been remitted by sincere repentance; absolution\n   from the censures and public penances of the church. It is a payment of\n   the debt of justice to God by the application of the merits of Christ\n   and his saints to the contrite soul through the church. It is therefore\n   believed to diminish or destroy for sins the punishment of purgatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indulgence', 'v. t.', 'To grant an indulgence to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indulgency', 'n.', 'Indulgence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indulgent', 'a.', 'Prone to indulge; yielding to the wishes, humor, or\n   appetites of those under ones care; compliant; not opposing or\n   restraining; tolerant; mild; favorable; not severe; as, an indulgent\n   parent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indulgential', 'a.', 'Relating to the indulgences of the Roman Catholic\n   Church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indulgently', 'adv.', 'In an indulgent manner; mildly; favorably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indulger', 'n.', 'One who indulges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indulgiate', 'v. t.', 'To indulge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Induline', 'n.', 'Any one of a large series of aniline dyes, colored blue\n   or violet, and represented by aniline violet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Induline', 'n.', 'A dark green amorphous dyestuff, produced by the\n   oxidation of aniline in the presence of copper or vanadium salts; --\n   called also aniline black.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indult', 'n.', 'Alt. of Indulto');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indulto', 'n.', 'A privilege or exemption; an indulgence; a dispensation\n   granted by the pope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indulto', 'n.', 'A duty levied on all importations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indument', 'n.', 'Plumage; feathers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Induplicate', 'a.', 'Having the edges bent abruptly toward the axis; --\n   said of the parts of the calyx or corolla in aestivation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Induplicate', 'a.', 'Having the edges rolled inward and then arranged about\n   the axis without overlapping; -- said of leaves in vernation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Induplicative', 'a.', 'Having induplicate sepals or petals in aestivation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Induplicative', 'a.', 'Having induplicate leaves in vernation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indurance', 'n.', 'See Endurance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indurate', 'a.', 'Hardened; not soft; indurated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indurate', 'a.', 'Without sensibility; unfeeling; obdurate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indurated', 'imp. & p. p.', 'of Indurate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indurating', 'p. pr. & vb. n.', 'of Indurate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indurate', 'v. t.', 'To make hard; as, extreme heat indurates clay; some\n   fossils are indurated by exposure to the air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indurate', 'v. t.', 'To make unfeeling; to deprive of sensibility; to\n   render obdurate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indurate', 'v. i.', 'To grow hard; to harden, or become hard; as, clay\n   indurates by drying, and by heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indurated', 'a.', 'Hardened; as, indurated clay; an indurated heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Induration', 'n.', 'The act of hardening, or the process of growing hard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Induration', 'n.', 'State of being indurated, or of having become hard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Induration', 'n.', 'Hardness of character, manner, sensibility, etc.;\n   obduracy; stiffness; want of pliancy or feeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indusial', 'a.', 'Of, pertaining to, or containing, the petrified cases of\n   the larvae of certain insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indusiate', 'a.', 'Alt. of Indusiated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indusiated', 'a.', 'Furnished with an indusium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indusia', 'pl. ', 'of Indusium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indusium', 'n.', 'A collection of hairs united so as to form a sort of cup,\n   and inclosing the stigma of a flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indusium', 'n.', 'The immediate covering of the fruit dots or sori in many\n   ferns, usually a very thin scale attached by the middle or side to a\n   veinlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indusium', 'n.', 'A peculiar covering found in certain fungi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Industrial', 'a.', 'Consisting in industry; pertaining to industry, or the\n   arts and products of industry; concerning those employed in labor,\n   especially in manual labor, and their wages, duties, and rights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Industrialism', 'n.', 'Devotion to industrial pursuits; labor; industry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Industrialism', 'n.', 'The principles or policy applicable to industrial\n   pursuits or organized labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Industrially', 'adv.', 'With reference to industry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Industrious', 'a.', 'Given to industry; characterized by diligence;\n   constantly, regularly, or habitually occupied; busy; assiduous; not\n   slothful or idle; -- commonly implying devotion to lawful and useful\n   labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Industrious', 'a.', 'Steadily and perseveringly active in a particular\n   pursuit or aim; as, he was negligent in business, but industrious in\n   pleasure; an industrious mischief maker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Industries', 'pl. ', 'of Industry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Industry', 'n.', 'Habitual diligence in any employment or pursuit, either\n   bodily or mental; steady attention to business; assiduity; -- opposed\n   to sloth and idleness; as, industry pays debts, while idleness or\n   despair will increase them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Industry', 'n.', 'Any department or branch of art, occupation, or business;\n   especially, one which employs much labor and capital and is a distinct\n   branch of trade; as, the sugar industry; the iron industry; the cotton\n   industry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Industry', 'n.', 'Human exertion of any kind employed for the creation of\n   value, and regarded by some as a species of capital or wealth; labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indutive', 'a.', 'Covered; -- applied to seeds which have the usual\n   integumentary covering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Induviae', 'n. pl.', 'Persistent portions of a calyx or corolla; also,\n   leaves which do not disarticulate from the stem, and hence remain for a\n   long time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Induviate', 'a.', 'Covered with induviae, as the upper part of the trunk of\n   a palm tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indwelt', 'imp. & p. p.', 'of Indwell');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indwelling', 'p. pr. & vb. n.', 'of Indwell');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indwell', 'v. t. & i.', 'To dwell in; to abide within; to remain in\n   possession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indweller', 'n.', 'An inhabitant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Indwelling', 'n.', 'Residence within, as in the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ine', '', 'A suffix, indicating that those substances of whose names it is\n   a part are basic, and alkaloidal in their nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ine', '', 'A suffix, used to indicate hydrocarbons of the second degree of\n   unsaturation; i. e., members of the acetyline series; as, hexine,\n   heptine, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inearth', 'v. t.', 'To inter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inebriant', 'a.', 'Intoxicating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inebriant', 'n.', 'Anything that intoxicates, as opium, alcohol, etc.; an\n   intoxicant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inebriated', 'imp. & p. p.', 'of Inebriate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inebriating', 'p. pr. & vb. n.', 'of Inebriate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inebriate', 'v. t.', 'To make drunk; to intoxicate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inebriate', 'v. t.', 'Fig.: To disorder the senses of; to exhilarate or\n   elate as if by spirituous drink; to deprive of sense and judgment;\n   also, to stupefy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inebriate', 'v. i.', 'To become drunk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inebriate', 'a.', 'Intoxicated; drunk; habitually given to drink;\n   stupefied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inebriate', 'n.', 'One who is drunk or intoxicated; esp., an habitual\n   drunkard; as, an asylum fro inebriates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inebriation', 'n.', 'The condition of being inebriated; intoxication;\n   figuratively, deprivation of sense and judgment by anything that\n   exhilarates, as success.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inebriety', 'n.', 'Drunkenness; inebriation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inebrious', 'a.', 'Intoxicated, or partially so; intoxicating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inedited', 'a.', 'Not edited; unpublished; as, an inedited manuscript.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inee', 'n.', 'An arrow poison, made from an apocynaceous plant\n   (Strophanthus hispidus) of the Gaboon country; -- called also onaye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ineffability', 'n.', 'The quality or state of being ineffable;\n   ineffableness; unspeakableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ineffable', 'a.', 'Incapable of being expresses in words; unspeakable;\n   unutterable; indescribable; as, the ineffable joys of heaven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ineffableness', 'n.', 'The quality or state of being ineffable or\n   unutterable; unspeakableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ineffably', 'adv.', 'In a manner not to be expressed in words; unspeakably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ineffaceable', 'a.', 'Incapable of being effaced; indelible; ineradicable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ineffaceably', 'adv.', 'So as not to be effaceable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ineffectible', 'a.', 'Ineffectual; impracticable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ineffective', 'a.', 'Not effective; ineffectual; futile; inefficient;\n   useless; as, an ineffective appeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ineffectively', 'adv.', 'In an ineffective manner; without effect;\n   inefficiently; ineffectually.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ineffectiveness', 'n.', 'Quality of being ineffective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ineffectual', 'a.', 'Not producing the proper effect; without effect;\n   inefficient; weak; useless; futile; unavailing; as, an ineffectual\n   attempt; an ineffectual expedient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ineffectuality', 'n.', 'Ineffectualness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ineffectually', 'adv.', 'Without effect; in vain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ineffectualness', 'n.', 'Want of effect, or of power to produce it;\n   inefficacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ineffervescence', 'n.', 'Want of effervescence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ineffervescent', 'a.', 'Not effervescing, or not susceptible of\n   effervescence; quiescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ineffervescibility', 'n.', 'The quality of being ineffervescible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ineffervescible', 'a.', 'Not capable or susceptible of effervescence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inefficacious', 'a.', 'Not efficacious; not having power to produce the\n   effect desired; inadequate; incompetent; inefficient; impotent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inefficaciously', 'adv.', 'without efficacy or effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inefficaciousness', 'n.', 'Want of effect, or of power to produce the\n   effect; inefficacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inefficacy', 'n.', 'Want of power to produce the desired or proper effect;\n   inefficiency; ineffectualness; futility; uselessness; fruitlessness;\n   as, the inefficacy of medicines or means.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inefficiency', 'n.', 'The quality of being inefficient; want of power or\n   energy sufficient; want of power or energy sufficient for the desired\n   effect; inefficacy; incapacity; as, he was discharged from his position\n   for inefficiency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inefficient', 'a.', 'Not efficient; not producing the effect intended or\n   desired; inefficacious; as, inefficient means or measures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inefficient', 'a.', 'Incapable of, or indisposed to, effective action;\n   habitually slack or remiss; effecting little or nothing; as,\n   inefficient workmen; an inefficient administrator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inefficiently', 'adv.', 'In an inefficient manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inelaborate', 'a.', 'Not elaborate; not wrought with care; unpolished;\n   crude; unfinished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inelastic', 'a.', 'Not elastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inelasticity', 'n.', 'Want of elasticity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inelegances', 'pl. ', 'of Inelegancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inelegancies', 'pl. ', 'of Inelegancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inelegance', 'n.', 'Alt. of Inelegancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inelegancy', 'n.', 'The quality of being inelegant; want of elegance or\n   grace; want of refinement, beauty, or polish in language, composition,\n   or manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inelegancy', 'n.', 'Anything inelegant; as, inelegance of style in literary\n   composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inelegant', 'a.', 'Not elegant; deficient in beauty, polish, refinement,\n   grave, or ornament; wanting in anything which correct taste requires.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inelegantly', 'adv.', 'In an inelegant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ineligibility', 'n.', 'The state or quality of being ineligible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ineligible', 'a.', 'Not eligible; not qualified to be chosen for an office;\n   not worthy to be chosen or prefered; not expedient or desirable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inelligibly', 'adv.', 'In an ineligible manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ineloquent', 'a.', 'Not eloquent; not fluent, graceful, or pathetic; not\n   persuasive; as, ineloquent language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ineloquently', 'adv.', 'Without eloquence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ineluctable', 'a.', 'Not to be overcome by struggling; irresistible;\n   inevitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ineludible', 'a.', 'Incapable of being eluded or evaded; unvoidable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inembryonate', 'a.', 'Not embryonate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inernarrable', 'a.', 'Incapable of being narrated; indescribable;\n   ineffable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inept', 'a.', 'Not apt or fit; unfit; unsuitable; improper; unbecoming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inept', 'a.', 'Silly; useless; nonsensical; absurd; foolish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ineptitude', 'n.', 'The quality of being inept; unfitness; inaptitude;\n   unsuitableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ineptitude', 'n.', 'Absurdity; nonsense; foolishness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ineptly', 'adv.', 'Unfitly; unsuitably; awkwardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ineptness', 'n.', 'Unfitness; ineptitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inequable', 'a.', 'Unequable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inequal', 'a.', 'Unequal; uneven; various.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inequalities', 'pl. ', 'of Inequality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inequality', 'n.', 'The quality of being unequal; difference, or want of\n   equality, in any respect; lack of uniformity; disproportion;\n   unevenness; disparity; diversity; as, an inequality in size, stature,\n   numbers, power, distances, motions, rank, property, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inequality', 'n.', 'Unevenness; want of levelness; the alternate rising and\n   falling of a surface; as, the inequalities of the surface of the earth,\n   or of a marble slab, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inequality', 'n.', 'Variableness; changeableness; inconstancy; lack of\n   smoothness or equability; deviation; unsteadiness, as of the weather,\n   feelings, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inequality', 'n.', 'Disproportion to any office or purpose; inadequacy;\n   competency; as, the inequality of terrestrial things to the wants of a\n   rational soul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inequality', 'n.', 'An expression consisting of two unequal quantities,\n   with the sign of inequality (< or >) between them; as, the inequality 2\n   < 3, or 4 > 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inequality', 'n.', 'An irregularity, or a deviation, in the motion of a\n   planet or satellite from its uniform mean motion; the amount of such\n   deviation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inequation', 'n.', 'An inequality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inequidistant', 'a.', 'Not equally distant; not equidistant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inequilateral', 'a.', 'Having unequal sides; unsymmetrical; unequal-sided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inequilateral', 'a.', 'Having the two ends unequal, as in the clam,\n   quahaug, and most lamellibranch shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inequilobate', 'a.', 'Unequally lobed; cut into lobes of different shapes\n   or sizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inequitable', 'a.', 'Not equitable; not just.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inequitate', 'v. t.', 'To ride over or through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inequity', 'n.', 'Want of equity; injustice; wrong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inequivalve', 'a.', 'Alt. of Inequivalvular');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inequivalvular', 'a.', 'Having unequal valves, as the shell of an oyster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ineradicable', 'a.', 'Incapable of being /radicated or rooted out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ineradicably', 'adv.', 'So as not to be eradicable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inergetic', 'a.', 'Alt. of Inergetical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inergetical', 'a.', 'Having no energy; sluggish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inergetically', 'adv.', 'Without energy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inerm', 'a.', 'Alt. of Inermous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inermous', 'a.', 'Same as Inermis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inermis', 'a.', 'Unarmed; destitute of prickles or thorns, as a leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inerrability', 'n.', 'Freedom or exemption from error; infallibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inerrable', 'a.', 'Incapable of erring; infallible; unerring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inerrableness', 'n.', 'Exemption from error; inerrability; infallibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inerrably', 'adv.', 'With security from error; infallibly; unerringly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inerrancy', 'n.', 'Exemption from error.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inerratic', 'a.', 'Not erratic or wandering; fixed; settled; established.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inerringly', 'adv.', 'Without error, mistake, or deviation; unerringly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inert', 'a.', 'Destitute of the power of moving itself, or of active\n   resistance to motion; as, matter is inert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inert', 'a.', 'Indisposed to move or act; very slow to act; sluggish; dull;\n   inactive; indolent; lifeless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inert', 'a.', 'Not having or manifesting active properties; not affecting\n   other substances when brought in contact with them; powerless for an\n   expected or desired effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inertia', 'n.', 'That property of matter by which it tends when at rest to\n   remain so, and when in motion to continue in motion, and in the same\n   straight line or direction, unless acted on by some external force; --\n   sometimes called vis inertiae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inertia', 'n.', 'Inertness; indisposition to motion, exertion, or action;\n   want of energy; sluggishness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inertia', 'n.', 'Want of activity; sluggishness; -- said especially of the\n   uterus, when, in labor, its contractions have nearly or wholly ceased.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inertion', 'n.', 'Want of activity or exertion; inertness; quietude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inertitude', 'n.', 'Inertness; inertia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inertly', 'adv.', 'Without activity; sluggishly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inertness', 'n.', 'Want of activity or exertion; habitual indisposition to\n   action or motion; sluggishness; apathy; insensibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inertness', 'n.', 'Absence of the power of self-motion; inertia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inerudite', 'a.', 'Not erudite; unlearned; ignorant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inescapable', 'a.', 'Not escapable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inescate', 'v. t.', 'To allure; to lay a bait for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inescation', 'n.', 'The act of baiting; allurement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inescutcheon', 'n.', 'A small escutcheon borne within a shield.');
  INSERT INTO words (word, word_type, description) VALUES  (    'In esse', '', 'In being; actually existing; -- distinguished from in posse,\n   or in potentia, which denote that a thing is not, but may be.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inessential', 'a.', 'Having no essence or being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inessential', 'a.', 'Not essential; unessential.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inestimable', 'a.', 'Incapable of being estimated or computed; especially,\n   too valuable or excellent to be measured or fully appreciated; above\n   all price; as, inestimable rights or privileges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inestimably', 'adv.', 'In a manner, or to a degree, above estimation; as,\n   things inestimably excellent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inevasible', 'a.', 'Incapable of being evaded; inevitable; unavoidable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inevidence', 'n.', 'Want of evidence; obscurity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inevident', 'a.', 'Not evident; not clear or obvious; obscure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inevitability', 'n.', 'Impossibility to be avoided or shunned;\n   inevitableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inevitable', 'a.', 'Not evitable; incapable of being shunned; unavoidable;\n   certain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inevitable', 'a.', 'Irresistible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inevitableness', 'n.', 'The state of being unavoidable; certainty to\n   happen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inevitably', 'adv.', 'Without possibility of escape or evasion;\n   unavoidably; certainly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexact', 'a.', 'Not exact; not precisely correct or true; inaccurate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexactitude', 'n.', 'Inexactness; uncertainty; as, geographical\n   inexactitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexactly', 'adv.', 'In a manner not exact or precise; inaccurately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexactness', 'n.', 'Incorrectness; want of exactness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexcitability', 'n.', 'The quality of being inexcitable; insusceptibility\n   to excitement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexcitable', 'a.', 'Not susceptible of excitement; dull; lifeless; torpid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexcusable', 'a.', 'Not excusable; not admitting excuse or justification;\n   as, inexcusable folly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexcusableness', 'n.', 'The quality of being inexcusable; enormity\n   forgiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexcusably', 'adv.', 'With a degree of guilt or folly beyond excuse or\n   justification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexecrable', 'a.', 'That can not be execrated enough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexecutable', 'a.', 'Incapable of being executed or performed;\n   impracticable; infeasible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexecution', 'n.', 'Neglect of execution; nonperformance; as, the\n   inexecution of a treaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexertion', 'n.', 'Want of exertion; want of effort; defect of action;\n   indolence; laziness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexhalable', 'a.', 'Incapable of being exhaled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexhausted', 'a.', 'Not exhausted; not emptied; not spent; not having lost\n   all strength or resources; unexhausted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexhaustedly', 'adv.', 'Without exhaustion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexhaustibility', 'n.', 'The state or quality of being inexhaustible;\n   abundance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexhaustible', 'a.', 'Incapable of being exhausted, emptied, or used up;\n   unfailing; not to be wasted or spent; as, inexhaustible stores of\n   provisions; an inexhaustible stock of elegant words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexhaustive', 'a.', 'Inexhaustible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexist', 'v. i.', 'To exist within; to dwell within.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexistant', 'a.', 'Inexistent; not existing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexistence', 'n.', 'Inherence; subsistence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexistence', 'n.', 'That which exists within; a constituent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexistence', 'n.', 'Want of being or existence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexistent', 'a.', 'Not having being; not existing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexistent', 'a.', 'Inherent; innate; indwelling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexorability', 'n.', 'The quality of being inexorable, or unyielding to\n   entreaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexorable', 'a.', 'Not to be persuaded or moved by entreaty or prayer;\n   firm; determined; unyielding; unchangeable; inflexible; relentless; as,\n   an inexorable prince or tyrant; an inexorable judge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexorableness', 'n.', 'The quality or state of being inexorable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexorably', 'adv.', 'In an inexorable manner; inflexibly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexpansible', 'a.', 'Incapable of expansion, enlargement, or extension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexpectable', 'a.', 'Not to be expected or anticipated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexpectant', 'a.', 'Not expectant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexpectation', 'n.', 'Absence of expectation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexpected', 'a.', 'Unexpected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexpectedly', 'adv.', 'Unexpectedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexpectedness', 'n.', 'Unexpectedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexpedience', 'n.', 'Alt. of Inexpediency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexpediency', 'n.', 'The quality or state of being inexpedient; want of\n   fitness; unsuitableness to the end or object; impropriety; as, the\n   inexpedience of some measures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexpedient', 'a.', 'Not expedient; not tending to promote a purpose; not\n   tending to the end desired; inadvisable; unfit; improper; unsuitable to\n   time and place; as, what is expedient at one time may be inexpedient at\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexpediently', 'adv.', 'Not expediently; unfitly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexpensive', 'a.', 'Not expensive; cheap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexperience', 'n.', 'Absence or want of experience; lack of personal and\n   experimental knowledge; as, the inexperience of youth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexperienced', 'a.', 'Not having experience unskilled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexpert', 'a.', 'Destitute of experience or of much experience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexpert', 'a.', 'Not expert; not skilled; destitute of knowledge or\n   dexterity derived from practice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexpertness', 'n.', 'Want of expertness or skill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexpiable', 'a.', 'Admitting of no expiation, atonement, or satisfaction;\n   as, an inexpiable crime or offense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexpiable', 'a.', 'Incapable of being mollified or appeased; relentless;\n   implacable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexpiableness', 'n.', 'Quality of being inexpiable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexpiably', 'adv.', 'In an inexpiable manner of degree; to a degree that\n   admits of no atonement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexpiate', 'a.', 'Not appeased or placated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexplainable', 'a.', 'Incapable of being explained; inexplicable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexpleably', 'adv.', 'Insatiably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexplicability', 'n.', 'The quality or state of being inexplicable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexplicable', 'a.', 'Not explicable; not explainable; incapable of being\n   explained, interpreted, or accounted for; as, an inexplicable mystery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexplicableness', 'n.', 'A state of being inexplicable; inexplicability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexplicably', 'adv.', 'In an inexplicable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexplicit', 'a.', 'Not explicit; not clearly stated; indefinite; vague.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexplorable', 'a.', 'Incapable of being explored, searched out, or\n   discovered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexplosive', 'a.', 'Not explosive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexposure', 'n.', 'A state of not being exposed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexpressible', 'a.', 'Not capable of expression or utterance in language;\n   ineffable; unspeakable; indescribable; unutterable; as, inexpressible\n   grief or pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexpressibles', 'n. pl.', 'Breeches; trousers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexpressibly', 'adv.', 'In an inexpressible manner or degree; unspeakably;\n   unutterably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexpressive', 'a.', 'Inexpressible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexpressive', 'a.', 'Without expression or meaning; not expressive; dull;\n   unintelligent; as, an inexpressive countenance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexpressiveness', 'n.', 'The state or quality of being inexpressive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexpugnable', 'a.', 'Incapable of being subdued by force; impregnable;\n   unconquerable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexpugnably', 'adv.', 'So as to be inexpugnable; in an inexpugnable\n   manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexsuperable', 'a.', 'Not capable of being passed over; insuperable;\n   insurmountable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inextended', 'a.', 'Not extended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inextensible', 'a.', 'Not capable of being extended; not elastic; as,\n   inextensible fibers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inextension', 'n.', 'Want of extension; unextended state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inexterminable', 'a.', 'Incapable of extermination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inextinct', 'a.', 'Not quenched; not extinct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inextinguible', 'a.', 'Inextinguishable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inextinguishable', 'a.', 'Not capable of being extinguished;\n   extinguishable; unquenchable; as, inextinguishable flame, light,\n   thirst, desire, feuds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inextinguishably', 'adv.', 'So as not to be extinguished; in an\n   inextinguishable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inextirpable', 'a.', 'Not capable of being extirpated or rooted out;\n   ineradicable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inextricable', 'a.', 'Incapable of being extricated, untied, or\n   disentangled; hopelessly intricate, confused, or obscure; as, an\n   inextricable knot or difficulty; inextricable confusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inextricable', 'a.', 'Inevitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inextricableness', 'n.', 'The state of being inextricable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inextricably', 'adv.', 'In an inextricable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ineyed', 'imp. & p. p.', 'of Ineye');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ineyeing', 'p. pr. & vb. n.', 'of Ineye');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ineye', 'v. t.', 'To ingraft, as a tree or plant, by the insertion of a bud\n   or eye; to inoculate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infabricated', 'a.', 'Not fabricated; unwrought; not artificial; natural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infallibilist', 'n.', 'One who accepts or maintains the dogma of papal\n   infallibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infallibility', 'n.', 'The quality or state of being infallible, or exempt\n   from error; inerrability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infallible', 'a.', 'Not fallible; not capable of erring; entirely exempt\n   from liability to mistake; unerring; inerrable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infallible', 'a.', 'Not liable to fail, deceive, or disappoint;\n   indubitable; sure; certain; as, infallible evidence; infallible\n   success; an infallible remedy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infallible', 'a.', 'Incapable of error in defining doctrines touching faith\n   or morals. See Papal infallibility, under Infallibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infallibleness', 'n.', 'The state or quality of being infallible;\n   infallibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infallibly', 'adv.', 'In an infallible manner; certainly; unfailingly;\n   unerringly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infame', 'v. t.', 'To defame; to make infamous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infamized', 'imp. & p. p.', 'of Infamize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infamizing', 'p. pr. & vb. n.', 'of Infamize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infamize', 'v. t.', 'To make infamous; to defame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infamous', 'a.', 'Of very bad report; having a reputation of the worst\n   kind; held in abhorrence; guilty of something that exposes to infamy;\n   base; notoriously vile; detestable; as, an infamous traitor; an\n   infamous perjurer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infamous', 'a.', 'Causing or producing infamy; deserving detestation;\n   scandalous to the last degree; as, an infamous act; infamous vices;\n   infamous corruption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infamous', 'a.', 'Branded with infamy by conviction of a crime; as, at\n   common law, an infamous person can not be a witness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infamous', 'a.', 'Having a bad name as being the place where an odious\n   crime was committed, or as being associated with something detestable;\n   hence, unlucky; perilous; dangerous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infamously', 'adv.', 'In an infamous manner or degree; scandalously;\n   disgracefully; shamefully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infamousness', 'n.', 'The state or quality of being infamous; infamy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infamies', 'pl. ', 'of Infamy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infamy', 'n.', 'Total loss of reputation; public disgrace; dishonor;\n   ignominy; indignity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infamy', 'n.', 'A quality which exposes to disgrace; extreme baseness or\n   vileness; as, the infamy of an action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infamy', 'n.', 'That loss of character, or public disgrace, which a convict\n   incurs, and by which he is at common law rendered incompetent as a\n   witness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infancy', 'n.', 'The state or period of being an infant; the first part of\n   life; early childhood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infancy', 'n.', 'The first age of anything; the beginning or early period\n   of existence; as, the infancy of an art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infancy', 'n.', 'The state or condition of one under age, or under the age\n   of twenty-one years; nonage; minority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infandous', 'a.', 'Too odious to be expressed or mentioned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infangthef', 'n.', 'The privilege granted to lords of certain manors to\n   judge thieves taken within the seigniory of such lords.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infant', 'n.', 'A child in the first period of life, beginning at his\n   birth; a young babe; sometimes, a child several years of age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infant', 'n.', 'A person who is not of full age, or who has not attained\n   the age of legal capacity; a person under the age of twenty-one years;\n   a minor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infant', 'n.', 'Same as Infante.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infant', 'a.', 'Of or pertaining to infancy, or the first period of life;\n   tender; not mature; as, infant strength.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infant', 'a.', 'Intended for young children; as, an infant school.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infant', 'v. t.', 'To bear or bring forth, as a child; hence, to produce,\n   in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infanta', 'n.', 'A title borne by every one of the daughters of the kings\n   of Spain and Portugal, except the eldest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infante', 'n.', 'A title given to every one of sons of the kings of Spain\n   and Portugal, except the eldest or heir apparent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infanthood', 'n.', 'Infancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infanticidal', 'a.', 'Of or pertaining to infanticide; engaged in, or\n   guilty of, child murder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infanticide', 'n.', 'The murder of an infant born alive; the murder or\n   killing of a newly born or young child; child murder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infanticide', 'n.', 'One who commits the crime of infanticide; one who\n   kills an infant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infantile', 'a.', 'Of or pertaining to infancy, or to an infant; similar\n   to, or characteristic of, an infant; childish; as, infantile behavior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infantine', 'a.', 'Infantile; childish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infantlike', 'a.', 'Like an infant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infantly', 'a.', 'Like an infant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infantry', 'n.', 'A body of children.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infantry', 'n.', 'A body of soldiers serving on foot; foot soldiers, in\n   distinction from cavalry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infarce', 'v. t.', 'To stuff; to swell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infarction', 'n.', 'The act of stuffing or filling; an overloading and\n   obstruction of any organ or vessel of the body; constipation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infare', 'n.', 'A house-warming; especially, a reception, party, or\n   entertainment given by a newly married couple, or by the husband upon\n   receiving the wife to his house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infashionable', 'a.', 'Unfashionable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infatigable', 'a.', 'Indefatigable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infatuate', 'a.', 'Infatuated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infatuated', 'imp. & p. p.', 'of Infatuate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infatuating', 'p. pr. & vb. n.', 'of Infatuate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infatuate', 'v. t.', 'To make foolish; to affect with folly; to weaken the\n   intellectual powers of, or to deprive of sound judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infatuate', 'v. t.', 'To inspire with a foolish and extravagant passion;\n   as, to be infatuated with gaming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infatuated', 'a.', 'Overcome by some foolish passion or desire; affected by\n   infatuation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infatuation', 'n.', 'The act of infatuating; the state of being infatuated;\n   folly; that which infatuates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infaust', 'a.', 'Not favorable; unlucky; unpropitious; sinister.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infausting', 'n.', 'The act of making unlucky; misfortune; bad luck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infeasibility', 'n.', 'The state of being infeasible; impracticability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infeasible', 'a.', 'Not capable of being done or accomplished;\n   impracticable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infeasibleness', 'n.', 'The state of quality of being infeasible;\n   infeasibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infect', 'v. t.', 'Infected. Cf. Enfect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infected', 'imp. & p. p.', 'of Infect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infecting', 'p. pr. & vb. n.', 'of Infect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infect', 'v. t.', 'To taint with morbid matter or any pestilential or\n   noxious substance or effluvium by which disease is produced; as, to\n   infect a lancet; to infect an apartment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infect', 'v. t.', 'To affect with infectious disease; to communicate\n   infection to; as, infected with the plague.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infect', 'v. t.', 'To communicate to or affect with, as qualities or\n   emotions, esp. bad qualities; to corrupt; to contaminate; to taint by\n   the communication of anything noxious or pernicious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infect', 'v. t.', 'To contaminate with illegality or to expose to penalty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infecter', 'n.', 'One who, or that which, infects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infectible', 'a.', 'Capable of being infected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infection', 'n.', 'The act or process of infecting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infection', 'n.', 'That which infects, or causes the communicated disease;\n   any effluvium, miasm, or pestilential matter by which an infectious\n   disease is caused.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infection', 'n.', 'The state of being infected; contamination by morbific\n   particles; the result of infecting influence; a prevailing disease;\n   epidemic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infection', 'n.', 'That which taints or corrupts morally; as, the infection\n   of vicious principles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infection', 'n.', 'Contamination by illegality, as in cases of contraband\n   goods; implication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infection', 'n.', 'Sympathetic communication of like qualities or emotions;\n   influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infectious', 'a.', 'Having qualities that may infect; communicable or\n   caused by infection; pestilential; epidemic; as, an infectious fever;\n   infectious clothing; infectious air; infectious vices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infectious', 'a.', 'Corrupting, or tending to corrupt or contaminate;\n   vitiating; demoralizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infectious', 'a.', 'Contaminating with illegality; exposing to seizure and\n   forfeiture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infectious', 'a.', 'Capable of being easily diffused or spread;\n   sympathetic; readily communicated; as, infectious mirth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infectiously', 'adv.', 'In an infectious manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infectiousness', 'n.', 'The quality of being infectious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infective', 'a.', 'Infectious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infecund', 'a.', 'Unfruitful; not producing young; barren; infertile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infecundity', 'n.', 'Want of fecundity or fruitfulness; barrenness;\n   sterility; unproductiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infecundous', 'a.', 'Infertile; barren; unprofitable; unproductive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infeeble', 'v. t.', 'See Enfeeble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infelicitous', 'a.', 'Not felicitous; unhappy; unfortunate; not fortunate\n   or appropriate in application; not well said, expressed, or done; as,\n   an infelicitous condition; an infelicitous remark; an infelicitous\n   description; infelicitous words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infelicities', 'pl. ', 'of Infelicity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infelicity', 'n.', 'The state or quality of being infelicitous;\n   unhappiness; misery; wretchedness; misfortune; want of suitableness or\n   appropriateness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infelicity', 'n.', 'That (as an act, word, expression, etc.) which is\n   infelicitous; as, infelicities of speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infelonious', 'a.', 'Not felonious, malignant, or criminal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infelt', 'a.', 'Felt inwardly; heartfelt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infeodation', 'n.', 'See Infeudation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infeoff', 'v. t.', 'See Enfeoff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infeoffment', 'n.', 'See Enfeoffment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inferred', 'imp. & p. p.', 'of Infer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inferring', 'p. pr. & vb. n.', 'of Infer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infer', 'v. t.', 'To bring on; to induce; to occasion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infer', 'v. t.', 'To offer, as violence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infer', 'v. t.', 'To bring forward, or employ as an argument; to adduce; to\n   allege; to offer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infer', 'v. t.', 'To derive by deduction or by induction; to conclude or\n   surmise from facts or premises; to accept or derive, as a consequence,\n   conclusion, or probability; to imply; as, I inferred his determination\n   from his silence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infer', 'v. t.', 'To show; to manifest; to prove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inferable', 'a.', 'Capable of being inferred or deduced from premises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inference', 'n.', 'The act or process of inferring by deduction or\n   induction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inference', 'n.', 'That which inferred; a truth or proposition drawn from\n   another which is admitted or supposed to be true; a conclusion; a\n   deduction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inferential', 'a.', 'Deduced or deducible by inference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inferentially', 'adv.', 'By way of inference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inferiae', 'n. pl.', 'Sacrifices offered to the souls of deceased heroes or\n   friends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inferior', 'a.', 'Lower in place, rank, excellence, etc.; less important or\n   valuable; subordinate; underneath; beneath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inferior', 'a.', 'Poor or mediocre; as, an inferior quality of goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inferior', 'a.', 'Nearer the sun than the earth is; as, the inferior or\n   interior planets; an inferior conjunction of Mercury or Venus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inferior', 'a.', 'Below the horizon; as, the inferior part of a meridian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inferior', 'a.', 'Situated below some other organ; -- said of a calyx when\n   free from the ovary, and therefore below it, or of an ovary with an\n   adherent and therefore inferior calyx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inferior', 'a.', 'On the side of a flower which is next the bract;\n   anterior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inferior', 'a.', 'Junior or subordinate in rank; as, an inferior officer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inferior', 'n.', 'A person lower in station, rank, intellect, etc., than\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inferiority', '', 'The state of being inferior; a lower state or condition;\n   as, inferiority of rank, of talents, of age, of worth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inferiorly', 'adv.', 'In an inferior manner, or on the inferior part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infernal', 'a.', 'Of or pertaining to or suitable for the lower regions,\n   inhabited, according to the ancients, by the dead; pertaining to\n   Plutos realm of the dead, the Tartarus of the ancients.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infernal', 'a.', 'Of or pertaining to, resembling, or inhabiting, hell;\n   suitable for hell, or to the character of the inhabitants of hell;\n   hellish; diabolical; as, infernal spirits, or conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infernal', 'n.', 'An inhabitant of the infernal regions; also, the place\n   itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infernally', 'adv.', 'In an infernal manner; diabolically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inferobranchian', 'n.', 'One of the Inferobranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inferobranchiata', 'n. pl.', 'A suborder of marine gastropod mollusks, in\n   which the gills are between the foot and the mantle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inferobranchiate', 'a.', 'Having the gills on the sides of the body, under\n   the margin of the mantle; belonging to the Inferobranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inferrible', 'a.', 'Inferable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infertile', 'a.', 'Not fertile; not productive; barren; sterile; as, an\n   infertile soil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infertilely', 'adv.', 'In an infertile manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infertility', 'n.', 'The state or quality of being infertile;\n   unproductiveness; barrenness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infest', 'v. t.', 'Mischievous; hurtful; harassing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infested', 'imp. & p. p.', 'of Infest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infesting', 'p. pr. & vb. n.', 'of Infest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infest', 'v. t.', 'To trouble greatly by numbers or by frequency of\n   presence; to disturb; to annoy; to frequent and molest or harass; as,\n   fleas infest dogs and cats; a sea infested with pirates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infesttation', 'n.', 'The act of infesting or state of being infested;\n   molestation; vexation; annoyance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infester', 'n.', 'One who, or that which, infests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infestive', 'a.', 'Having no mirth; not festive or merry; dull; cheerless;\n   gloomy; forlorn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infestivity', 'n.', 'Want of festivity, cheerfulness, or mirth; dullness;\n   cheerlessness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infestuous', 'a.', 'Mischievous; harmful; dangerous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infeudation', 'n.', 'The act of putting one in possession of an estate in\n   fee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infeudation', 'n.', 'The granting of tithes to laymen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infibulation', 'n.', 'The act of clasping, or fastening, as with a buckle\n   or padlock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infibulation', 'n.', 'The act of attaching a ring, clasp, or frame, to the\n   genital organs in such a manner as to prevent copulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infidel', 'a.', 'Not holding the faith; -- applied esp. to one who does not\n   believe in the inspiration of the Scriptures, and the supernatural\n   origin of Christianity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infidel', 'n.', 'One who does not believe in the prevailing religious\n   faith; especially, one who does not believe in the divine origin and\n   authority of Christianity; a Mohammedan; a heathen; a freethinker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infidelities', 'pl. ', 'of Infidelity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infidelity', 'n.', 'Want of faith or belief in some religious system;\n   especially, a want of faith in, or disbelief of, the inspiration of the\n   Scriptures, of the divine origin of Christianity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infidelity', 'n.', 'Unfaithfulness to the marriage vow or contract;\n   violation of the marriage covenant by adultery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infidelity', 'n.', 'Breach of trust; unfaithfulness to a charge, or to\n   moral obligation; treachery; deceit; as, the infidelity of a servant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infield', 'v. t.', 'To inclose, as a field.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infield', 'n.', 'Arable and manured land kept continually under crop; --\n   distinguished from outfield.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infield', 'n.', 'The diamond; -- opposed to outfield. See Diamond, n., 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infile', 'v. t.', 'To arrange in a file or rank; to place in order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infilm', 'v. t.', 'To cover with a film; to coat thinly; as, to infilm one\n   metal with another in the process of gilding; to infilm the glass of a\n   mirror.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infiltered', 'imp. & p. p.', 'of Infilter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infiltering', 'p. pr. & vb. n.', 'of Infilter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infilter', 'v. t. & i.', 'To filter or sift in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infiltrated', 'imp. & p. p.', 'of Infiltrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infiltrating', 'p. pr. & vb. n.', 'of Infiltrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infiltrate', 'v. i.', 'To enter by penetrating the pores or interstices of\n   a substance; to filter into or through something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infiltrate', 'v. t.', 'To penetrate gradually; -- sometimes used\n   reflexively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infiltration', 'n.', 'The act or process of infiltrating, as if water into\n   a porous substance, or of a fluid into the cells of an organ or part of\n   the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infiltration', 'n.', 'The substance which has entered the pores or cavities\n   of a body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infiltrative', 'a.', 'Of or pertaining to infiltration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infinite', 'a.', 'Unlimited or boundless, in time or space; as, infinite\n   duration or distance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infinite', 'a.', 'Without limit in power, capacity, knowledge, or\n   excellence; boundless; immeasurably or inconceivably great; perfect;\n   as, the infinite wisdom and goodness of God; -- opposed to finite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infinite', 'a.', 'Indefinitely large or extensive; great; vast; immense;\n   gigantic; prodigious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infinite', 'a.', 'Greater than any assignable quantity of the same kind; --\n   said of certain quantities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infinite', 'a.', 'Capable of endless repetition; -- said of certain forms\n   of the canon, called also perpetual fugues, so constructed that their\n   ends lead to their beginnings, and the performance may be incessantly\n   repeated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infinite', 'n.', 'That which is infinite; boundless space or duration;\n   infinity; boundlessness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infinite', 'n.', 'An infinite quantity or magnitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infinite', 'n.', 'An infinity; an incalculable or very great number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infinite', 'n.', 'The Infinite Being; God; the Almighty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infinitely', 'adv.', 'Without bounds or limits; beyond or below assignable\n   limits; as, an infinitely large or infinitely small quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infinitely', 'adv.', 'Very; exceedingly; vastly; highly; extremely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infiniteness', 'n.', 'The state or quality of being infinite; infinity;\n   greatness; immensity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infinitesimal', 'a.', 'Infinitely or indefinitely small; less than any\n   assignable quantity or value; very small.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infinitesimal', 'n.', 'An infinitely small quantity; that which is less\n   than any assignable quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infinitesimally', 'adv.', 'By infinitesimals; in infinitely small\n   quantities; in an infinitesimal degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infinitival', 'a.', 'Pertaining to the infinite mood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infinitive', 'n.', 'Unlimited; not bounded or restricted; undefined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infinitive', 'n.', 'An infinitive form of the verb; a verb in the\n   infinitive mood; the infinitive mood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infinitive', 'adv.', 'In the manner of an infinitive mood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infinito', 'a.', 'Infinite; perpetual, as a canon whose end leads back to\n   the beginning. See Infinite, a., 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infinitude', 'n.', 'The quality or state of being infinite, or without\n   limits; infiniteness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infinitude', 'n.', 'Infinite extent; unlimited space; immensity; infinity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infinitude', 'n.', 'Boundless number; countless multitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infinituple', 'a.', 'Multipied an infinite number of times.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infinities', 'pl. ', 'of Infinity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infinity', 'n.', 'Unlimited extent of time, space, or quantity; eternity;\n   boundlessness; immensity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infinity', 'n.', 'Unlimited capacity, energy, excellence, or knowledge; as,\n   the infinity of God and his perfections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infinity', 'n.', 'Endless or indefinite number; great multitude; as an\n   infinity of beauties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infinity', 'n.', 'A quantity greater than any assignable quantity of the\n   same kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infinity', 'n.', 'That part of a line, or of a plane, or of space, which is\n   infinitely distant. In modern geometry, parallel lines or planes are\n   sometimes treated as lines or planes meeting at infinity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infirm', 'a.', 'Not firm or sound; weak; feeble; as, an infirm body; an\n   infirm constitution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infirm', 'a.', 'Weak of mind or will; irresolute; vacillating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infirm', 'a.', 'Not solid or stable; insecure; precarious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infirm', 'v. t.', 'To weaken; to enfeeble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infirmarian', 'n.', 'A person dwelling in, or having charge of, an\n   infirmary, esp. in a monastic institution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infirmaries', 'pl. ', 'of Infirmary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infirmary', 'n.', 'A hospital, or place where the infirm or sick are lodged\n   and nursed gratuitously, or where out-patients are treated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infirmative', 'a.', 'Weakening; annulling, or tending to make void.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infirmatory', 'n.', 'An infirmary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infirmities', 'pl. ', 'of Infirmity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infirmity', 'a.', 'The state of being infirm; feebleness; an imperfection\n   or weakness; esp., an unsound, unhealthy, or debilitated state; a\n   disease; a malady; as, infirmity of body or mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infirmity', 'a.', 'A personal frailty or failing; foible; eccentricity; a\n   weakness or defect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infirmly', 'adv.', 'In an infirm manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infirmness', 'n.', 'Infirmity; feebleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infixed', 'imp. & p. p.', 'of Infix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infixing', 'p. pr. & vb. n.', 'of Infix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infix', 'v. t.', 'To set; to fasten or fix by piercing or thrusting in; as,\n   to infix a sting, spear, or dart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infix', 'v. t.', 'To implant or fix; to instill; to inculcate, as\n   principles, thoughts, or instructions; as, to infix good principles in\n   the mind, or ideas in the memory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infix', 'n.', 'Something infixed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflamed', 'imp. & p. p.', 'of Inflame');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflaming', 'p. pr. & vb. n.', 'of Inflame');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflame', 'v. t.', 'To set on fire; to kindle; to cause to burn, flame, or\n   glow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflame', 'v. t.', 'Fig.: To kindle or intensify, as passion or appetite;\n   to excite to an excessive or unnatural action or heat; as, to inflame\n   desire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflame', 'v. t.', 'To provoke to anger or rage; to exasperate; to\n   irritate; to incense; to enrage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflame', 'v. t.', 'To put in a state of inflammation; to produce morbid\n   heat, congestion, or swelling, of; as, to inflame the eyes by overwork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflame', 'v. t.', 'To exaggerate; to enlarge upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflame', 'v. i.', 'To grow morbidly hot, congested, or painful; to become\n   angry or incensed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflamed', 'p. a.', 'Set on fire; enkindled; heated; congested; provoked;\n   exasperated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflamed', 'p. a.', 'Represented as burning, or as adorned with tongues of\n   flame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflamer', 'n.', 'The person or thing that inflames.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflammabillty', 'n.', 'Susceptibility of taking fire readily; the state or\n   quality of being inflammable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflammable', 'a.', 'Capable of being easily set fire; easily enkindled;\n   combustible; as, inflammable oils or spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflammable', 'a.', 'Excitable; irritable; irascible; easily provoked; as,\n   an inflammable temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflammableness', 'n.', 'The quality or state of being inflammable;\n   inflammability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflammbly', 'adv.', 'In an inflammable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflammation', 'n.', 'The act of inflaming, kindling, or setting on fire;\n   also, the state of being inflamed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflammation', 'n.', 'A morbid condition of any part of the body,\n   consisting in congestion of the blood vessels, with obstruction of the\n   blood current, and growth of morbid tissue. It is manifested outwardly\n   by redness and swelling, attended with heat and pain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflammation', 'n.', 'Violent excitement; heat; passion; animosity;\n   turbulence; as, an inflammation of the mind, of the body politic, or of\n   parties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflammative', 'a.', 'Inflammatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflammatory', 'a.', 'Tending to inflame, kindle, or irritate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflammatory', 'a.', 'Tending to excite anger, animosity, tumult, or\n   sedition; seditious; as, inflammatory libels, writings, speeches, or\n   publications.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflammatory', 'a.', 'Accompanied with, or tending to cause, preternatural\n   heat and excitement of arterial action; as, an inflammatory disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflatable', 'a.', 'That may be inflated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflate', 'p. a.', 'Blown in; inflated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflated', 'imp. & p. p.', 'of Inflate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflating', 'p. pr. & vb. n.', 'of Inflate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflate', 'v. t.', 'To swell or distend with air or gas; to dilate; to\n   expand; to enlarge; as, to inflate a bladder; to inflate the lungs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflate', 'v. t.', 'Fig.: To swell; to puff up; to elate; as, to inflate\n   one with pride or vanity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflate', 'v. t.', 'To cause to become unduly expanded or increased; as, to\n   inflate the currency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflate', 'v. i.', 'To expand; to fill; to distend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflated', 'a.', 'Filled, as with air or gas; blown up; distended; as, a\n   balloon inflated with gas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflated', 'a.', 'Turgid; swelling; puffed up; bombastic; pompous; as, an\n   inflated style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflated', 'a.', 'Hollow and distended, as a perianth, corolla, nectary, or\n   pericarp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflated', 'a.', 'Distended or enlarged fictitiously; as, inflated prices,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflater', 'n.', 'One who, or that which, inflates; as, the inflaters of\n   the stock exchange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflatingly', 'adv.', 'In a manner tending to inflate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflation', 'n.', 'The act or process of inflating, or the state of being\n   inflated, as with air or gas; distention; expansion; enlargement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflation', 'n.', 'The state of being puffed up, as with pride; conceit;\n   vanity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflation', 'n.', 'Undue expansion or increase, from overissue; -- said of\n   currency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflationist', 'n.', 'One who favors an increased or very large issue of\n   paper money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflatus', 'v. t.', 'A blowing or breathing into; inflation; inspiration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflected', 'imp. & p. p.', 'of Inflect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflecting', 'p. pr. & vb. n.', 'of Inflect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflect', 'v. t.', 'To turn from a direct line or course; to bend; to\n   incline, to deflect; to curve; to bow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflect', 'v. t.', 'To vary, as a noun or a verb in its terminations; to\n   decline, as a noun or adjective, or to conjugate, as a verb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflect', 'v. t.', 'To modulate, as the voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflected', 'a.', 'Bent; turned; deflected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflected', 'a.', 'Having inflections; capable of, or subject to,\n   inflection; inflective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflection', 'n.', 'The act of inflecting, or the state of being inflected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflection', 'n.', 'A bend; a fold; a curve; a turn; a twist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflection', 'n.', 'A slide, modulation, or accent of the voice; as, the\n   rising and the falling inflection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflection', 'n.', 'The variation or change which words undergo to mark\n   case, gender, number, comparison, tense, person, mood, voice, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflection', 'n.', 'Any change or modification in the pitch or tone of the\n   voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflection', 'n.', 'A departure from the monotone, or reciting note, in\n   chanting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflection', 'n.', 'Same as Diffraction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflectional', 'a.', 'Of or pertaining to inflection; having, or\n   characterized by, inflection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflective', 'a.', 'Capable of, or pertaining to, inflection; deflecting;\n   as, the inflective quality of the air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflective', 'a.', 'Inflectional; characterized by variation, or change in\n   form, to mark case, tense, etc.; subject to inflection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflesh', 'v. t.', 'To incarnate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflex', 'v. t.', 'To bend; to cause to become curved; to make crooked; to\n   deflect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflexed', 'a.', 'Turned; bent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflexed', 'a.', 'Bent or turned abruptly inwards, or toward the axis, as\n   the petals of a flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflexibility', 'n.', 'The quality or state of being inflexible, or not\n   capable of being bent or changed; unyielding stiffness; inflexibleness;\n   rigidity; firmness of will or purpose; unbending pertinacity;\n   steadfastness; resoluteness; unchangeableness; obstinacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflexible', 'a.', 'Not capable of being bent; stiff; rigid; firm;\n   unyielding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflexible', 'a.', 'Firm in will or purpose; not to be turned, changed, or\n   altered; resolute; determined; unyieding; inexorable; stubborn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflexible', 'a.', 'Incapable of change; unalterable; immutable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflexibleness', 'n.', 'The quality or state of being inflexible;\n   inflexibility; rigidity; firmness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflexibly', 'adv.', 'In an inflexible manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflexion', 'n.', 'Inflection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflexive', 'a.', 'Inflective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflexive', 'a.', 'Inflexible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflexure', 'n.', 'An inflection; a bend or fold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflicted', 'imp. & p. p.', 'of Inflict');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflicting', 'p. pr. & vb. n.', 'of Inflict');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflict', 'v. t.', 'To give, cause, or produce by striking, or as if by\n   striking; to apply forcibly; to lay or impose; to send; to cause to\n   bear, feel, or suffer; as, to inflict blows; to inflict a wound with a\n   dagger; to inflict severe pain by ingratitude; to inflict punishment on\n   an offender; to inflict the penalty of death on a criminal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflicter', 'n.', 'One who inflicts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infliction', 'n.', 'The act of inflicting or imposing; as, the infliction\n   of torment, or of punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infliction', 'n.', 'That which is inflicted or imposed, as punishment,\n   disgrace, calamity, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflictive', 'a.', 'Causing infliction; acting as an infliction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflorescence', 'n.', 'A flowering; the putting forth and unfolding of\n   blossoms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflorescence', 'n.', 'The mode of flowering, or the general arrangement\n   and disposition of the flowers with reference to the axis, and to each\n   other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflorescence', 'n.', 'An axis on which all the flower buds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflow', 'v. i.', 'To flow in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Influence', 'n.', 'A flowing in or upon; influx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Influence', 'n.', 'Hence, in general, the bringing about of an effect,\n   phusical or moral, by a gradual process; controlling power quietly\n   exerted; agency, force, or tendency of any kind which the sun exerts on\n   animal and vegetable life; the influence of education on the mind; the\n   influence, according to astrologers,of the stars over affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Influence', 'n.', 'Power or authority arising from elevated station,\n   excelence of character or intellect, wealth, etc.; reputation;\n   acknowledged ascendency; as, he is a man of influence in the community.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Influence', 'n.', 'Induction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Influenced', 'imp. & p. p.', 'of Influence');
  INSERT INTO words (word, word_type, description) VALUES  (    'Influencing', 'p. pr. & vb. n.', 'of Influence');
  INSERT INTO words (word, word_type, description) VALUES  (    'Influence', 'v. t.', 'To control or move by power, physical or moral; to\n   affect by gentle action; to exert an influence upon; to modify, bias,\n   or sway; to move; to persuade; to induce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Influencer', 'n.', 'One who, or that which, influences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Influencive', 'a.', 'Tending toinfluence; influential.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Influent', 'a.', 'Flowing in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Influent', 'a.', 'Exerting influence; influential.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inflential', 'a.', 'Exerting or possessing influence or power; potent;\n   efficacious; effective; strong; having authority or ascendency; as, an\n   influential man, station, argument, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Influentially', 'adv.', 'In an influential manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Influenza', 'n.', 'An epidemic affection characterized by acute nasal\n   catarrh, or by inflammation of the throat or the bronchi, and usually\n   accompanied by fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Influx', 'n.', 'The act of flowing in; as, an influx of light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Influx', 'n.', 'A coming in; infusion; intromission; introduction;\n   importation in abundance; also, that which flows or comes in; as, a\n   great influx of goods into a country, or an influx of gold and silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Influx', 'n.', 'Influence; power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Influxion', 'n.', 'A flowing in; infusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Influxious', 'a.', 'Influential.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Influxive', 'a.', 'Having a tendency to flow in; having influence;\n   influential.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Influxively', 'adv.', 'By influxion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infolded', 'imp. & p. p.', 'of Infold');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infolding', 'p. pr. & vb. n.', 'of Infold');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infold', 'v. t.', 'To wrap up or cover with folds; to envelop; to inwrap;\n   to inclose; to involve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infold', 'v. t.', 'To clasp with the arms; to embrace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infoldment', 'n.', 'The act of infolding; the state of being infolded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infoliate', 'v. t.', 'To cover or overspread with, or as with, leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inform', 'a.', 'Without regular form; shapeless; ugly; deformed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Informed', 'imp. & p. p.', 'of Inform');
  INSERT INTO words (word, word_type, description) VALUES  (    'Informing', 'p. pr. & vb. n.', 'of Inform');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inform', 'v. t.', 'To give form or share to; to give vital ororganizing\n   power to; to give life to; to imbue and actuate with vitality; to\n   animate; to mold; to figure; to fashion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inform', 'v. t.', 'To communicate knowledge to; to make known to; to\n   acquaint; to advise; to instruct; to tell; to notify; to enlighten; --\n   usually followed by of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inform', 'v. t.', 'To communicate a knowledge of facts to,by way of\n   accusation; to warn against anybody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inform', 'v. t.', 'To take form; to become visible or manifest; to appear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inform', 'v. t.', 'To give intelligence or information; to tell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Informal', 'a.', 'Not in the regular, usual, or established form; not\n   according to official, conventional, prescribed, or customary forms or\n   rules; irregular; hence, without ceremony; as, an informal writting,\n   proceeding, or visit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Informal', 'a.', 'Deranged in mind; out of ones senses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Informalities', 'pl. ', 'of Informality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Informality', 'n.', 'The state of being informal; want of regular,\n   prescribed, or customary form; as, the informality of legal\n   proceedings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Informality', 'n.', 'An informal, unconventional, or unofficial act or\n   proceeding; something which is not in proper or prescribed form or does\n   not conform to the established rule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Informally', 'adv.', 'In an informal manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Informant', 'v. t.', 'One who, or that which, informs, animates, or\n   vivifies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Informant', 'v. t.', 'One who imparts information or instruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Informant', 'v. t.', 'One who offers an accusation; an informer. See\n   Informer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Information', 'v. t.', 'The act of informing, or communicating knowledge or\n   intelligence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Information', 'v. t.', 'News, advice, or knowledge, communicated by others\n   or obtained by personal study and investigation; intelligence;\n   knowledge derived from reading, observation, or instruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Information', 'v. t.', 'A proceeding in the nature of a prosecution for\n   some offens against the government, instituted and prosecuted, really\n   or nominally, by some authorized public officer on behalt of the\n   government. It differs from an indictment in criminal cases chiefly in\n   not being based on the finding of a grand juri. See Indictment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Informative', 'a.', 'Having power to inform, animate, or vivify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Informatory', 'a.', 'Full of, or conveying, information; instructive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Informed', 'a.', 'Unformed or ill-formed; deformed; shapeless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Informer', 'v.', 'One who informs, animates, or inspires.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Informer', 'v.', 'One who informs, or imparts knowledge or news.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Informer', 'v.', 'One who informs a magistrate of violations of law; one\n   who informs against another for violation of some law or penal statute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Informidable', 'a.', 'Not formidable; not to be feared or dreaded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Informity', 'a.', 'Want of regular form; shapelessness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Informous', 'a.', 'Of irregular form; shapeless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infortunate', 'a.', 'Unlucky; unfortunate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infortune', 'n.', 'Misfortune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infortuned', 'a.', 'Unfortunate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infound', 'v. t.', 'To pour in; to infuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infra', 'adv.', 'Below; beneath; under; after; -- often used as a prefix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infra-axillary', 'a.', 'Situated below the axil, as a bud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infrabranchial', 'a.', 'Below the gills; -- applied to the ventral portion\n   of the pallial chamber in the lamellibranchs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infraclavicular', 'a.', 'Below the clavicle; as, the infraclavicular fossa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infract', 'a.', 'Not broken or fractured; unharmed; whole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infracted', 'imp. & p. p.', 'of Infract');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infracting', 'p. pr. & vb. n.', 'of Infract');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infract', 'v. t.', 'To break; to infringe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infractible', 'a.', 'Capable of being broken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infraction', 'n.', 'The act of infracting or breaking; breach; violation;\n   nonobservance; infringement; as, an infraction of a treaty, compact,\n   rule, or law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infractor', 'n.', 'One who infracts or infringes; a violator; a breaker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infragrant', 'a.', 'Not fragrant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infrahyoid', 'a.', 'Same as Hyosternal (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infralabial', 'a.', 'Below the lower lip; -- said of certain scales of\n   reptiles and fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infralapsarian', 'n.', 'One of that class of Calvinists who consider the\n   decree of election as contemplating the apostasy as past and the elect\n   as being at the time of election in a fallen and guilty state; --\n   opposed to Supralapsarian. The former considered the election of grace\n   as a remedy for an existing evil; the latter regarded the fall as a\n   part of Gods original purpose in regard to men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infralapsarian', 'a.', 'Of or pertaining to the Infralapsarians, or to\n   their doctrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infralapsarianism', 'n.', 'The doctrine, belief, or principles of the\n   Infralapsarians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inframarginal', 'a.', 'Below the margin; submarginal; as, an inframarginal\n   convolution of the brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inframaxillary', 'a.', 'Under the lower jaw; submaxillary; as, the\n   inframaxillary nerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inframaxillary', 'a.', 'Of or pertaining to the lower iaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inframedian', 'a.', 'Of or pertaining to the interval or zone along the sea\n   bottom, at the depth of between fifty and one hundred fathoms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inframundane', 'a.', 'Lying or situated beneath the world.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infranchise', 'v. t.', 'See Enfranchise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infrangibility', 'n.', 'The quality or state of being infrangible;\n   infrangibleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infrangible', 'a.', 'Not capable of being broken or separated into parts;\n   as, infrangible atoms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infrangible', 'a.', 'Not to be infringed or violated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infrangibleness', 'n.', 'The state or quality of being infrangible;\n   infrangibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infraocular', 'a.', 'Situated below the eyes, as the antenna of certain\n   insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infraorbital', 'a.', 'Below the orbit; as, the infraorbital foramen; the\n   infraorbital nerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Infrapose', 'v. t.', 'To place under or beneath.');
