  INSERT INTO words (word, word_type, description) VALUES  (    'Ingrate', 'n.', 'An ungrateful person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingrateful', 'a.', 'Ungrateful; thankless; unappreciative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingrateful', 'a.', 'Unpleasing to the sense; distasteful; offensive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingrately', 'adv.', 'Ungratefully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingratiated', 'imp. & p. p.', 'of Ingratiate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingratiating', 'p. pr. & vb. n.', 'of Ingratiate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingratiate', 'v. t.', 'To introduce or commend to the favor of another; to\n   bring into favor; to insinuate; -- used reflexively, and followed by\n   with before the person whose favor is sought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingratiate', 'v. t.', 'To recommend; to render easy or agreeable; --\n   followed by to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingratiate', 'v. i.', 'To gain favor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingratitude', 'n.', 'Want of gratitude; insensibility to, forgetfulness of,\n   or ill return for, kindness or favors received; unthankfulness;\n   ungratefulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingrave', 'v. t.', 'To engrave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingrave', 'v. t.', 'To bury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingravidate', 'v. t.', 'To impregnate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingravidation', 'n.', 'The state of being pregnant or impregnated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingreat', 'v. t.', 'To make great; to enlarge; to magnify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingredience', 'n.', 'Alt. of Ingrediency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingrediency', 'n.', 'Entrance; ingress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingrediency', 'n.', 'The quality or state of being an ingredient or\n   component part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingredient', 'n.', 'That which enters into a compound, or is a component\n   part of any combination or mixture; an element; a constituent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingredient', 'a.', 'Entering as, or forming, an ingredient or component\n   part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingress', 'n.', 'The act of entering; entrance; as, the ingress of air into\n   the lungs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingress', 'n.', 'Power or liberty of entrance or access; means of entering;\n   as, all ingress was prohibited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingress', 'n.', 'The entrance of the moon into the shadow of the earth in\n   eclipses, the suns entrance into a sign, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingress', 'v. i.', 'To go in; to enter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingression', 'n.', 'Act of entering; entrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingrieve', 'v. t.', 'To render more grievous; to aggravate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingroove', 'v. t.', 'To groove in; to join in or with a groove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingross', 'v. t.', 'See Engross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingrowing', 'a.', 'Growing or appearing to grow into some other substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingrowth', 'n.', 'A growth or development inward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inguen', 'n.', 'The groin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inguilty', 'a.', 'Not guilty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inguinal', 'a.', 'Of or pertaining to, or in the region of, the inguen or\n   groin; as, an inguinal canal or ligament; inguinal hernia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingulfed', 'imp. & p. p.', 'of Ingulf');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingulfing', 'p. pr. & vb. n.', 'of Ingulf');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingulf', 'v. t.', 'To swallow up or overwhelm in, or as in, a gulf; to cast\n   into a gulf. See Engulf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingulfment', 'n.', 'The act of ingulfing, or the state of being ingulfed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingurgitate', 'v. t.', 'To swallow, devour, or drink greedily or in large\n   quantity; to guzzle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingurgitate', 'v. t.', 'To swallow up, as in a gulf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingurgitate', 'v. i.', 'To guzzle; to swill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingurgitation', 'n.', 'The act of swallowing greedily or immoderately; that\n   which is so swallowed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ingustable', 'a.', 'Tasteless; insipid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhabile', 'a.', 'Not apt or fit; unfit; not convenient; inappropriate;\n   unsuitable; as, inhabile matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhabile', 'a.', 'Unskilled; unready; awkward; incompetent; unqualified; --\n   said of person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhability', 'n.', 'Unsuitableness; unaptness; unfitness; inability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhabited', 'imp. & p. p.', 'of Inhabit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhabiting', 'p. pr. & vb. n.', 'of Inhabit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhabit', 'v. t.', 'To live or dwell in; to occupy, as a place of settled\n   residence; as, wild beasts inhabit the forest; men inhabit cities and\n   houses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhabit', 'v. i.', 'To have residence in a place; to dwell; to live; to\n   abide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhabitable', 'a.', 'Capable of being inhabited; habitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhabitable', 'a.', 'Not habitable; not suitable to be inhabited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhabitance', 'n.', 'Alt. of Inhabitancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhabitancy', 'n.', 'The act of inhabiting, or the state of being\n   inhabited; the condition of an inhabitant; residence; occupancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhabitancy', 'n.', 'The state of having legal right to claim the\n   privileges of a recognized inhabitant; especially, the right to support\n   in case of poverty, acquired by residence in a town; habitancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhabitant', 'n.', 'One who dwells or resides permanently in a place, as\n   distinguished from a transient lodger or visitor; as, an inhabitant of\n   a house, a town, a city, county, or state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhabitant', 'n.', 'One who has a legal settlement in a town, city, or\n   parish; a permanent resident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhabitate', 'v. t.', 'To inhabit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhabitation', 'n.', 'The act of inhabiting, or the state of being\n   inhabited; indwelling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhabitation', 'n.', 'Abode; place of dwelling; residence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhabitation', 'n.', 'Population; inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhabitativeness', 'n.', 'A tendency or propensity to permanent residence\n   in a place or abode; love of home and country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhabited', 'a.', 'Uninhabited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhabiter', 'n.', 'An inhabitant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhabitiveness', 'n.', 'See Inhabitativeness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhabitress', 'n.', 'A female inhabitant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhalant', 'a.', 'Inhaling; used for inhaling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhalant', 'n.', 'An apparatus also called an inhaler (which see); that\n   which is to be inhaled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhalation', 'n.', 'The act of inhaling; also, that which is inhaled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhaled', 'imp. & p. p.', 'of Inhale');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhaling', 'p. pr. & vb. n.', 'of Inhale');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhale', 'v. t.', 'To breathe or draw into the lungs; to inspire; as, to\n   inhale air; -- opposed to exhale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhalent', 'a.', 'Used for inhaling; as, the inhalent end of a duct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhaler', 'n.', 'One who inhales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhaler', 'n.', 'An apparatus for inhaling any vapor or volatile substance,\n   as ether or chloroform, for medicinal purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhaler', 'n.', 'A contrivance to filter, as air, in order to protect the\n   lungs from inhaling damp or cold air, noxious gases, dust, etc.; also,\n   the respiratory apparatus for divers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhance', 'v. t.', 'See Enhance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inharmonic', 'a.', 'Alt. of Inharmonical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inharmonical', 'a.', 'Not harmonic; inharmonious; discordant; dissonant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inharmonious', 'a.', 'Not harmonious; unmusical; discordant; dissonant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inharmonious', 'a.', 'Conflicting; jarring; not in harmony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inharmoniously', 'adv.', 'Without harmony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inharmoniousness', 'n.', 'The quality of being inharmonious; want of\n   harmony; discord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inharmony', 'n.', 'Want of harmony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhaul', 'n.', 'Alt. of Inhauler');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhauler', 'n.', 'A rope used to draw in the jib boom, or flying jib boom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhearsed', 'imp. & p. p.', 'of Inhearse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhearsing', 'p. pr. & vb. n.', 'of Inhearse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhearse', 'v. t.', 'To put in, or as in, a hearse or coffin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhered', 'imp. & p. p.', 'of Inhere');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhering', 'p. pr. & vb. n.', 'of Inhere');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhere', 'v. i.', 'To be inherent; to stick (in); to be fixed or\n   permanently incorporated with something; to cleave (to); to belong, as\n   attributes or qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inherence', 'n.', 'Alt. of Inherency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inherency', 'n.', 'The state of inhering; permanent existence in something;\n   innateness; inseparable and essential connection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inherent', 'a.', 'Permanently existing in something; inseparably attached\n   or connected; naturally pertaining to; innate; inalienable; as,\n   polarity is an inherent quality of the magnet; the inherent right of\n   men to life, liberty, and protection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inherently', 'adv.', 'By inherence; inseparably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inherited', 'imp. & p. p.', 'of Inherit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inheriting', 'p. pr. & vb. n.', 'of Inherit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inherit', 'v. t.', 'To take by descent from an ancestor; to take by\n   inheritance; to take as heir on the death of an ancestor or other\n   person to whose estate one succeeds; to receive as a right or title\n   descendible by law from an ancestor at his decease; as, the heir\n   inherits the land or real estate of his father; the eldest son of a\n   nobleman inherits his fathers title; the eldest son of a king inherits\n   the crown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inherit', 'v. t.', 'To receive or take by birth; to have by nature; to\n   derive or acquire from ancestors, as mental or physical qualities; as,\n   he inherits a strong constitution, a tendency to disease, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inherit', 'v. t.', 'To come into possession of; to possess; to own; to\n   enjoy as a possession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inherit', 'v. t.', 'To put in possession of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inherit', 'v. i.', 'To take or hold a possession, property, estate, or\n   rights by inheritance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inheritability', 'n.', 'The quality of being inheritable or descendible to\n   heirs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inheritable', 'a.', 'Capable of being inherited; transmissible or\n   descendible; as, an inheritable estate or title.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inheritable', 'a.', 'Capable of being transmitted from parent to child; as,\n   inheritable qualities or infirmities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inheritable', 'a.', 'Capable of taking by inheritance, or of receiving by\n   descent; capable of succeeding to, as an heir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inheritably', 'adv.', 'By inheritance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inheritance', 'n.', 'The act or state of inheriting; as, the inheritance of\n   an estate; the inheritance of mental or physical qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inheritance', 'n.', 'That which is or may be inherited; that which is\n   derived by an heir from an ancestor or other person; a heritage; a\n   possession which passes by descent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inheritance', 'n.', 'A permanent or valuable possession or blessing, esp.\n   one received by gift or without purchase; a benefaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inheritance', 'n.', 'Possession; ownership; acquisition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inheritance', 'n.', 'Transmission and reception by animal or plant\n   generation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inheritance', 'n.', 'A perpetual or continuing right which a man and his\n   heirs have to an estate; an estate which a man has by descent as heir\n   to another, or which he may transmit to another as his heir; an estate\n   derived from an ancestor to an heir in course of law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inheritor', 'n.', 'One who inherits; an heir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inheritress', 'n.', 'A heiress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inheritrix', 'n.', 'Same as Inheritress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inherse', 'v. t.', 'See Inhearse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhesion', 'n.', 'The state of existing, of being inherent, in something;\n   inherence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhiation', 'n.', 'A gaping after; eager desire; craving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhibited', 'imp. & p. p.', 'of Inhibit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhibiting', 'p. pr. & vb. n.', 'of Inhibit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhibit', 'v. t.', 'To check; to hold back; to restrain; to hinder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhibit', 'v. t.', 'To forbid; to prohibit; to interdict.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhibition', 'n.', 'The act of inhibiting, or the state of being inhibited;\n   restraint; prohibition; embargo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhibition', 'n.', 'A stopping or checking of an already present action; a\n   restraining of the function of an organ, or an agent, as a digestive\n   fluid or ferment, etc.; as, the inhibition of the respiratory center by\n   the pneumogastric nerve; the inhibition of reflexes, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhibition', 'n.', 'A writ from a higher court forbidding an inferior judge\n   from further proceedings in a cause before; esp., a writ issuing from a\n   higher ecclesiastical court to an inferior one, on appeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhibitor', 'n.', 'That which causes inhibitory action; esp., an inhibitory\n   nerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhibitory', 'a.', 'Of or pertaining to, or producing, inhibition;\n   consisting in inhibition; tending or serving to inhibit; as, the\n   inhibitory action of the pneumogastric on the respiratory center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhibitory-motor', 'a.', 'A term applied to certain nerve centers which\n   govern or restrain subsidiary centers, from which motor impressions\n   issue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhive', 'v. t.', 'To place in a hive; to hive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inheld', 'imp. & p. p.', 'of Inhold');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inholding', 'p. pr. & vb. n.', 'of Inhold');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhold', 'v. t.', 'To have inherent; to contain in itself; to possess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inholder', 'n.', 'An inhabitant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhoop', 'v. t.', 'To inclose in a hoop, or as in a hoop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhospitable', 'a.', 'Not hospitable; not disposed to show hospitality to\n   strangers or guests; as, an inhospitable person or people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhospitable', 'a.', 'Affording no shelter or sustenance; barren; desert;\n   bleak; cheerless; wild.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhospitality', 'n.', 'The quality or state of being inhospitable;\n   inhospitableness; lack of hospitality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhuman', 'a.', 'Destitute of the kindness and tenderness that belong to a\n   human being; cruel; barbarous; savage; unfeeling; as, an inhuman person\n   or people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhuman', 'a.', 'Characterized by, or attended with, cruelty; as, an\n   inhuman act or punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhumanities', 'pl. ', 'of Inhumanity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhumanity', 'n.', 'The quality or state of being inhuman; cruelty;\n   barbarity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhumanly', 'adv.', 'In an inhuman manner; cruelly; barbarously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhumate', 'v. t.', 'To inhume; to bury; to inter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhumation', 'n.', 'The act of inhuming or burying; interment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhumation', 'n.', 'The act of burying vessels in warm earth in order to\n   expose their contents to a steady moderate heat; the state of being\n   thus exposed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhumation', 'n.', 'Arenation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhumed', 'imp. & p. p.', 'of Inhume');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhuming', 'p. pr. & vb. n.', 'of Inhume');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhume', 'v. t.', 'To deposit, as a dead body, in the earth; to bury; to\n   inter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inhume', 'v. t.', 'To bury or place in warm earth for chemical or medicinal\n   purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inia', 'n.', 'A South American freshwater dolphin (Inia Boliviensis). It is\n   ten or twelve feet long, and has a hairy snout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inial', 'a.', 'Pertaining to the inion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inimaginable', 'a.', 'Unimaginable; inconceivable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inimical', 'a.', 'Having the disposition or temper of an enemy; unfriendly;\n   unfavorable; -- chiefly applied to private, as hostile is to public,\n   enmity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inimical', 'a.', 'Opposed in tendency, influence, or effects; antagonistic;\n   inconsistent; incompatible; adverse; repugnant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inimicality', 'n.', 'The state or quality of being inimical or hostile;\n   hostility; unfriendliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inimically', 'adv.', 'In an inimical manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inimicitious', 'a.', 'Inimical; unfriendly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inimicous', 'a.', 'Inimical; hurtful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inimitability', 'n.', 'The quality or state of being inimitable;\n   inimitableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inimitable', 'a.', 'Not capable of being imitated, copied, or\n   counterfeited; beyond imitation; surpassingly excellent; matchless;\n   unrivaled; exceptional; unique; as, an inimitable style; inimitable\n   eloquence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inion', 'n.', 'The external occipital protuberance of the skull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iniquitous', 'a.', 'Characterized by iniquity; unjust; wicked; as, an\n   iniquitous bargain; an iniquitous proceeding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iniquitously', 'adv.', 'In an iniquitous manner; unjustly; wickedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iniquities', 'pl. ', 'of Iniquity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iniquity', 'n.', 'Absence of, or deviation from, just dealing; want of\n   rectitude or uprightness; gross injustice; unrighteousness; wickedness;\n   as, the iniquity of bribery; the iniquity of an unjust judge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iniquity', 'n.', 'An iniquitous act or thing; a deed of injustice o/\n   unrighteousness; a sin; a crime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iniquity', 'n.', 'A character or personification in the old English\n   moralities, or moral dramas, having the name sometimes of one vice and\n   sometimes of another. See Vice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iniquous', 'a.', 'Iniquitous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inirritable', 'a.', 'Not irritable; esp. (Physiol.), incapable of being\n   stimulated to action, as a muscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inirritative', 'a.', 'Not accompanied with excitement; as, an inirritative\n   fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inisle', 'v. t.', 'To form into an island; to surround.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Initial', 'a.', 'Of or pertaining to the beginning; marking the\n   commencement; incipient; commencing; as, the initial symptoms of a\n   disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Initial', 'a.', 'Placed at the beginning; standing at the head, as of a\n   list or series; as, the initial letters of a name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Initial', 'n.', 'The first letter of a word or a name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Initialed', 'imp. & p. p.', 'of Initial');
  INSERT INTO words (word, word_type, description) VALUES  (    'Initialing', 'p. pr. & vb. n.', 'of Initial');
  INSERT INTO words (word, word_type, description) VALUES  (    'Initial', 'v. t.', 'To put an initial to; to mark with an initial of\n   initials.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Initially', 'adv.', 'In an initial or incipient manner or degree; at the\n   beginning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Initiated', 'imp. & p. p.', 'of Initiate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Initiating', 'p. pr. & vb. n.', 'of Initiate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Initiate', 'v. t.', 'To introduce by a first act; to make a beginning with;\n   to set afoot; to originate; to commence; to begin or enter upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Initiate', 'v. t.', 'To acquaint with the beginnings; to instruct in the\n   rudiments or principles; to introduce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Initiate', 'v. t.', 'To introduce into a society or organization; to confer\n   membership on; especially, to admit to a secret order with mysterious\n   rites or ceremonies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Initiate', 'v. i.', 'To do the first act; to perform the first rite; to\n   take the initiative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Initiate', 'a.', 'Unpracticed; untried; new.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Initiate', 'a.', 'Begun; commenced; introduced to, or instructed in, the\n   rudiments; newly admitted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Initiate', 'n.', 'One who is, or is to be, initiated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Initiation', 'n.', 'The act of initiating, or the process of being\n   initiated or introduced; as, initiation into a society, into business,\n   literature, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Initiation', 'n.', 'The form or ceremony by which a person is introduced\n   into any society; mode of entrance into an organized body; especially,\n   the rite of admission into a secret society or order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Initiative', 'a.', 'Serving to initiate; inceptive; initiatory;\n   introductory; preliminary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Initiative', 'n.', 'An introductory step or movement; an act which\n   originates or begins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Initiative', 'n.', 'The right or power to introduce a new measure or course\n   of action, as in legislation; as, the initiative in respect to revenue\n   bills is in the House of Representatives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Initiator', 'n.', 'One who initiates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Initiatory', 'a.', 'Suitable for an introduction or beginning;\n   introductory; prefatory; as, an initiatory step.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Initiatory', 'a.', 'Tending or serving to initiate; introducing by\n   instruction, or by the use and application of symbols or ceremonies;\n   elementary; rudimentary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Initiatory', 'n.', 'An introductory act or rite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inition', 'n.', 'Initiation; beginning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injected', 'imp. & p. p.', 'of Inject');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injecting', 'p. pr. & vb. n.', 'of Inject');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inject', 'v. t.', 'To throw in; to dart in; to force in; as, to inject cold\n   water into a condenser; to inject a medicinal liquid into a cavity of\n   the body; to inject morphine with a hypodermic syringe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inject', 'v. t.', 'Fig.: To throw; to offer; to propose; to instill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inject', 'v. t.', 'To cast or throw; -- with on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inject', 'v. t.', 'To fill (a vessel, cavity, or tissue) with a fluid or\n   other substance; as, to inject the blood vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injection', 'n.', 'The act of injecting or throwing in; -- applied\n   particularly to the forcible throwing in of a liquid, or aeriform body,\n   by means of a syringe, pump, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injection', 'n.', 'That which is injected; especially, a liquid medicine\n   thrown into a cavity of the body by a syringe or pipe; a clyster; an\n   enema.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injection', 'n.', 'The act or process of filling vessels, cavities, or\n   tissues with a fluid or other substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injection', 'n.', 'A specimen prepared by injection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injection', 'n.', 'The act of throwing cold water into a condenser to\n   produce a vacuum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injection', 'n.', 'The cold water thrown into a condenser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injector', 'n.', 'One who, or that which, injects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injector', 'n.', 'A contrivance for forcing feed water into a steam boiler\n   by the direct action of the steam upon the water. The water is driven\n   into the boiler by the impulse of a jet of the steam which becomes\n   condensed as soon as it strikes the stream of cold water it impels; --\n   also called Giffards injector, from the inventor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injelly', 'v. t.', 'To place in jelly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injoin', 'v. t.', 'See Enjoin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injoint', 'v. t.', 'To join; to unite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injoint', 'v. t.', 'To disjoint; to separate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injucundity', 'n.', 'Unpleasantness; disagreeableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injudicable', 'a.', 'Not cognizable by a judge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injudicial', 'a.', 'Not according to the forms of law; not judicial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injudicious', 'a.', 'Not judicious; wanting in sound judgment;\n   undiscerning; indiscreet; unwise; as, an injudicious adviser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injudicious', 'a.', 'Not according to sound judgment or discretion; unwise;\n   as, an injudicious measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injudiciously', 'adv.', 'In an injudicious manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injudiciousness', 'n.', 'The quality of being injudicious; want of sound\n   judgment; indiscretion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injunction', 'n.', 'The act of enjoining; the act of directing, commanding,\n   or prohibiting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injunction', 'n.', 'That which is enjoined; an order; a mandate; a decree;\n   a command; a precept; a direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injunction', 'n.', 'A writ or process, granted by a court of equity, and,\n   insome cases, under statutes, by a court of law,whereby a party is\n   required to do or to refrain from doing certain acts, according to the\n   exigency of the writ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injured', 'imp. & p. p.', 'of Injure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injuring', 'p. pr. & vb. n.', 'of Injure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injure', 'v. t.', 'To do harm to; to impair the excellence and value of; to\n   hurt; to damage; -- used in a variety of senses; as: (a) To hurt or\n   wound, as the person; to impair soundness, as of health. (b) To damage\n   or lessen the value of, as goods or estate. (c) To slander, tarnish, or\n   impair, as reputation or character. (d) To impair or diminish, as\n   happiness or virtue. (e) To give pain to, as the sensibilities or the\n   feelings; to grieve; to annoy. (f) To impair, as the intellect or mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injurer', 'n.', 'One who injures or wrongs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injurie', 'pl. ', 'of Injuria');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injuria', 'n.', 'Injury; invasion of anothers rights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injurious', 'a.', 'Not just; wrongful; iniquitous; culpable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injurious', 'a.', 'Causing injury or harm; hurtful; harmful; detrimental;\n   mischievous; as, acts injurious to health, credit, reputation,\n   property, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injuriously', 'adv.', 'In an injurious or hurtful manner; wrongfully;\n   hurtfully; mischievously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injuriousness', 'n.', 'The quality of being injurious or hurtful;\n   harmfulness; injury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injuries', 'pl. ', 'of Injury');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injury', 'a.', 'Any damage or violation of, the person, character,\n   feelings, rights, property, or interests of an individual; that which\n   injures, or occasions wrong, loss, damage, or detriment; harm; hurt;\n   loss; mischief; wrong; evil; as, his health was impaired by a severe\n   injury; slander is an injury to the character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injustice', 'n.', 'Want of justice and equity; violation of the rights of\n   another or others; iniquity; wrong; unfairness; imposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Injustice', 'n.', 'An unjust act or deed; a sin; a crime; a wrong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ink', 'n.', 'The step, or socket, in which the lower end of a millstone\n   spindle runs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ink', 'n.', 'A fluid, or a viscous material or preparation of various kinds\n   (commonly black or colored), used in writing or printing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ink', 'n.', 'A pigment. See India ink, under India.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inked', 'imp. & p. p.', 'of Ink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inking', 'p. pr. & vb. n.', 'of Ink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ink', 'v. t.', 'To put ink upon; to supply with ink; to blacken, color, or\n   daub with ink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inker', 'n.', 'One who, or that which, inks; especially, in printing, the\n   pad or roller which inks the type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inkfish', 'n.', 'A cuttlefish. See Cuttlefish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inkhorn', 'n.', 'A small bottle of horn or other material formerly used for\n   holding ink; an inkstand; a portable case for writing materials.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inkhorn', 'a.', 'Learned; pedantic; affected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inkhornism', 'n.', 'Pedantry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inkiness', 'n.', 'The state or quality of being inky; blackness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inking', 'a.', 'Supplying or covering with ink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inkle', 'n.', 'A kind of tape or braid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inkle', 'v. t.', 'To guess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inkling', 'n.', 'A hint; an intimation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inknee', 'n.', 'Same as Knock-knee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inkneed', 'a.', 'See Knock-kneed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inknot', 'v. t.', 'To fasten or bind, as with a knot; to knot together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inkstand', 'n.', 'A small vessel for holding ink, to dip the pen into;\n   also, a device for holding ink and writing materials.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inkstone', 'n.', 'A kind of stone containing native vitriol or subphate of\n   iron, used in making ink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inky', 'a.', 'Consisting of, or resembling, ink; soiled with ink; black.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inlaced', 'imp. & p. p.', 'of Inlace');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inlacing', 'p. pr. & vb. n.', 'of Inlace');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inlace', 'v. t.', 'To work in, as lace; to embellish with work resembling\n   lace; also, to lace or enlace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inlagation', 'n.', 'The restitution of an outlawed person to the protection\n   of the law; inlawing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inlaid', 'p. p.', 'of Inlay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inland', 'a.', 'Within the land; more or less remote from the ocean or from\n   open water; interior; as, an inland town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inland', 'a.', 'Limited to the land, or to inland routes; within the\n   seashore boundary; not passing on, or over, the sea; as, inland\n   transportation, commerce, navigation, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inland', 'a.', 'Confined to a country or state; domestic; not foreing; as,\n   an inland bill of exchange. See Exchange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inland', 'n.', 'The interior part of a country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inland', 'adv.', 'Into, or towards, the interior, away from the coast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inlander', 'n.', 'One who lives in the interior of a country, or at a\n   distance from the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inlandish', 'a.', 'Inland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inlapidate', 'v. t.', 'To convert into a stony substance; to petrity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inlard', 'v. t.', 'See Inlard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inlaw', 'v. t.', 'To clear of outlawry or attainder; to place under the\n   protection of the law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inlaied', 'imp. & p. p.', 'of Inlay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inlaying', 'p. pr. & vb. n.', 'of Inlay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inlay', 'v. t.', 'To lay within; hence, to insert, as pieces of pearl,\n   iviry, choice woods, or the like, in a groundwork of some other\n   material; to form an ornamental surface; to diversify or adorn with\n   insertions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inlay', 'n.', 'Matter or pieces of wood, ivory, etc., inlaid, or prepared\n   for inlaying; that which is inserted or inlaid for ornament or variety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inlayer', 'n.', 'One who inlays, or whose occupation it is to inlay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inleagued', 'imp. & p. p.', 'of Inleague');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inleaguing', 'p. pr. & vb. n.', 'of Inleague');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inleague', 'v. t.', 'To ally, or form an alliance witgh; to unite; to\n   combine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inleaguer', 'v. t.', 'To beleaguer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inlet', 'n.', 'A passage by which an inclosed place may be entered; a place\n   of ingress; entrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inlet', 'n.', 'A bay or recess,as in the shore of a sea, lake, or large\n   river; a narrow strip of water running into the land or between\n   islands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inlet', 'n.', 'That which is let in or inland; an inserted material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inlighten', 'v. t.', 'See Enlighten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inlist', 'v. t.', 'See Enlist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inlive', 'v. t.', 'To animate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inlock', 'v. t.', 'To lock in, or inclose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'In loco', '', 'In the place; in the proper or natural place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inlumine', 'v. t.', 'See Illumine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inly', 'a.', 'Internal; interior; secret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inly', 'adv.', 'Internally; within; in the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inmacy', 'n.', 'The state of being an inmate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inmate', 'n.', 'One who lives in the same house or apartment with another;\n   a fellow lodger; esp.,one of the occupants of an asylum, hospital, or\n   prison; by extension, one who occupies or lodges in any place or\n   dwelling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inmate', 'a.', 'Admitted as a dweller; resident; internal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inmeats', 'n.pl.', 'The edible viscera of animals, as the heart, liver,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inmeshed', 'imp. & p. p.', 'of Inmesh');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inmeshing', 'p. pr. & vb. n.', 'of Inmesh');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inmesh', 'v. t.', 'To bring within meshes, as of a net; to enmesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inmew', 'v. t.', 'To inclose, as in a mew or cage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inmost', 'a.', 'Deepest within; farthest from the surface or external part;\n   innermost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inn', 'n.', 'A place of shelter; hence, dwelling; habitation; residence;\n   abode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inn', 'n.', 'A house for the lodging and entertainment of travelers or\n   wayfarers; a tavern; a public house; a hotel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inn', 'n.', 'The town residence of a nobleman or distinguished person; as,\n   Leicester Inn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inn', 'n.', 'One of the colleges (societies or buildings) in London, for\n   students of the law barristers; as, the Inns of Court; the Inns of\n   Chancery; Serjeants Inns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inned', 'imp. & p. p.', 'of Inn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inning', 'p. pr. & vb. n.', 'of Inn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inn', 'v. i.', 'To take lodging; to lodge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inn', 'v. t.', 'To house; to lodge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inn', 'v. t.', 'To get in; to in. See In, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Innate', 'a.', 'Inborn; native; natural; as, innate vigor; innate\n   eloquence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Innate', 'a.', 'Originating in, or derived from, the constitution of the\n   intellect, as opposed to acquired from experience; as, innate ideas.\n   See A priori, Intuitive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Innate', 'a.', 'Joined by the base to the very tip of a filament; as, an\n   innate anther.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Innate', 'v. t.', 'To cause to exit; to call into being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Innately', 'adv.', 'Naturally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Innateness', 'n.', 'The quality of being innate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Innative', 'a.', 'Native.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Innavigable', 'a.', 'Incapable of being navigated; impassable by ships or\n   vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inne', 'adv. & prep.', 'In.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inner', 'a.', 'Further in; interior; internal; not outward; as, an spirit\n   or its phenomena.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inner', 'a.', 'Not obvious or easily discovered; obscure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Innerly', 'adv.', 'More within.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Innermost', 'a.', 'Farthest inward; most remote from the outward part;\n   inmost; deepest within.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Innermostly', 'adv.', 'In the innermost place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Innervate', 'v. t.', 'To supply with nerves; as, the heart is innervated by\n   pneumogastric and sympathetic branches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Innervation', 'n.', 'The act of innerving or stimulating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Innervation', 'n.', 'Special activity excited in any part of the nervous\n   system or in any organ of sense or motion; the nervous influence\n   necessary for the maintenance of life,and the functions of the various\n   organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Innervation', 'n.', 'The distribution of nerves in an animal, or to any of\n   its parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Innerved', 'imp. & p. p.', 'of Innerve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Innerving', 'p. pr. & vb. n.', 'of Innerve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Innerve', 'v. t.', 'To give nervous energy or power to; to give increased\n   energy,force,or courage to; to invigorate; to stimulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Innholder', 'n.', 'One who keeps an inn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inning', 'n.', 'Ingathering; harvesting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inning', 'n.', 'The state or turn of being in; specifically, in cricket,\n   baseball, etc.,the turn or time of a player or of a side at the bat; --\n   often in the pl. Hence: The turn or time of a person, or a party, in\n   power; as, the Whigs went out, and the Democrats had their innings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inning', 'n.', 'Lands recovered from the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Innitency', 'n.', 'A leaning; pressure; weight.');
