  INSERT INTO words (word, word_type, description) VALUES  (    'Inrush', 'n.', 'A rush inwards; as, the inrush of the tide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inrush', 'v. i.', 'To rush in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insabbatati', 'n. pl.', 'The Waldenses; -- so called from their peculiary\n   cut or marked sabots, or shoes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insafety', 'n.', 'Insecurity; danger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insalivation', 'n.', 'The mixing of the food with the saliva and other\n   secretions of the mouth in eating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insalubrious', 'a.', 'Not salubrious or healthful; unwholesome; as, an\n   insalubrious air or climate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insalubrity', 'n.', 'Unhealthfulness; unwholesomeness; as, the insalubrity\n   of air, water, or climate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insalutary', 'a.', 'Not salutary or wholesome; unfavorable to health.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insalutary', 'a.', 'Not tending to safety; productive of evil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insanability', 'n.', 'The state of being insanable or incurable;\n   insanableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insanable', 'a.', 'Not capable of being healed; incurable; irremediable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insanableness', 'n.', 'The state of being insanable; insanability;\n   incurableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insanably', 'adv.', 'In an incurable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insane', 'a.', 'Exhibiting unsoundness or disorded of mind; not sane; mad;\n   deranged in mind; delirious; distracted. See Insanity, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insane', 'a.', 'Used by, or appropriated to, insane persons; as, an insane\n   hospital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insane', 'a.', 'Causing insanity or madness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insane', 'a.', 'Characterized by insanity or the utmost folly; chimerical;\n   unpractical; as, an insane plan, attempt, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insanely', 'adv.', 'Without reason; madly; foolishly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insaneness', 'n.', 'Insanity; madness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insaniate', 'v. t.', 'To render unsound; to make mad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insanie', 'n.', 'Insanity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insanitary', 'a.', 'Not sanitary; unhealthy; as, insanitary conditions of\n   drainage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insanitation', 'n.', 'Lack of sanitation; careless or dangerous hygienic\n   conditions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insanity', 'n.', 'The state of being insane; unsoundness or derangement of\n   mind; madness; lunacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insanity', 'n.', 'Such a mental condition, as, either from the existence of\n   delusions, or from incapacity to distinguish between right and wrong,\n   with regard to any matter under action, does away with individual\n   responsibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insapory', 'a.', 'Tasteless; unsavory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insatiability', 'n.', 'The state or quality of being insatiable;\n   insatiableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insatiable', 'a.', 'Not satiable; incapable of being satisfied or appeased;\n   very greedy; as, an insatiable appetite, thirst, or desire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insatiableness', 'n.', 'Greediness of appetite that can not be satisfied or\n   appeased; insatiability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insatiably', 'adv.', 'In an insatiable manner or degree; unappeasably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insatiate', 'a.', 'Insatiable; as, insatiate thirst.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insatiately', 'adv.', 'Insatiably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insatiateness', 'n.', 'The state of being insatiate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insatiety', 'n.', 'Insatiableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insatisfaction', 'n.', 'Insufficiency; emptiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insatisfaction', 'n.', 'Dissatisfaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insaturable', 'a.', 'Not capable of being saturated or satisfied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inscience', 'n.', 'Want of knowledge; ignorance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inscient', 'a.', 'Having little or no knowledge; ignorant; stupid; silly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inscient', 'a.', 'Having knowledge or insight; intelligent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insconce', 'v. t.', 'See Ensconce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inscribable', 'a.', 'Capable of being inscribed, -- used specif. (Math.) of\n   solids or plane figures capable of being inscribed in other solids or\n   figures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inscribableness', 'n.', 'Quality of being inscribable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inscribed', 'imp. & p. p.', 'of Inscribe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inscribing', 'p. pr. & vb. n.', 'of Inscribe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inscribe', 'v. t.', 'To write or engrave; to mark down as something to be\n   read; to imprint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inscribe', 'v. t.', 'To mark with letters, charakters, or words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inscribe', 'v. t.', 'To assign or address to; to commend to by a shot\n   address; to dedicate informally; as, to inscribe an ode to a friend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inscribe', 'v. t.', 'To imprint deeply; to impress; to stamp; as, to\n   inscribe a sentence on the memory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inscribe', 'v. t.', 'To draw within so as to meet yet not cut the\n   boundaries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inscriber', 'n.', 'One who inscribes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inscriptible', 'a.', 'Capable of being inscribed; inscribable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inscription', 'n.', 'The act or process of inscribing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inscription', 'n.', 'That which is inscribed; something written or\n   engraved; especially, a word or words written or engraved on a solid\n   substance for preservation or public inspection; as, inscriptions on\n   monuments, pillars, coins, medals, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inscription', 'n.', 'A line of division or intersection; as, the tendinous\n   inscriptions, or intersections, of a muscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inscription', 'n.', 'An address, consignment, or informal dedication, as of\n   a book to a person, as a mark of respect or an invitation of patronage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inscriptive', 'a.', 'Bearing inscription; of the character or nature of an\n   inscription.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inscrolled', 'imp. & p. p.', 'of Inscroll');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inscrolling', 'p. pr. & vb. n.', 'of Inscroll');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inscroll', 'v. t.', 'To write on a scroll; to record.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inscrutability', 'n.', 'The quality or state of being inscrutable;\n   inscrutableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inscrutable', 'a.', 'Unsearchable; incapable of being searched into and\n   understood by inquiry or study; impossible or difficult to be explained\n   or accounted for satisfactorily; obscure; incomprehensible; as, an\n   inscrutable design or event.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inscrutableness', 'n.', 'The quality or state of being inscrutable;\n   inscrutability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inscrutably', 'adv.', 'In an inscrutable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insculp', 'v. t.', 'To engrave; to carve; to sculpture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insculption', 'n.', 'Inscription.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insculpture', 'n.', 'An engraving, carving, or inscription.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insculptured', 'p. a.', 'Engraved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inseamed', 'imp. & p. p.', 'of Inseam');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inseaming', 'p. pr. & vb. n.', 'of Inseam');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inseam', 'v. t.', 'To impress or mark with a seam or cicatrix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insearch', 'v. t.', 'To make search after; to investigate or examine; to\n   ensearch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insecable', 'a.', 'Incapable of being divided by cutting; indivisible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insect', 'n.', 'One of the Insecta; esp., one of the Hexapoda. See Insecta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insect', 'n.', 'Any air-breathing arthropod, as a spider or scorpion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insect', 'n.', 'Any small crustacean. In a wider sense, the word is often\n   loosely applied to various small invertebrates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insect', 'n.', 'Fig.: Any small, trivial, or contemptible person or thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insect', 'a.', 'Of or pertaining to an insect or insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insect', 'a.', 'Like an insect; small; mean; ephemeral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insecta', 'n. pl.', 'One of the classes of Arthropoda, including those that\n   have one pair of antennae, three pairs of mouth organs, and breathe air\n   by means of tracheae, opening by spiracles along the sides of the body.\n   In this sense it includes the Hexapoda, or six-legged insects and the\n   Myriapoda, with numerous legs. See Insect, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insecta', 'n.', 'In a more restricted sense, the Hexapoda alone. See\n   Hexapoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insecta', 'n.', 'In the most general sense, the Hexapoda, Myriapoda, and\n   Arachnoidea, combined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insectary', 'n.', 'A place for keeping living insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insectation', 'n.', 'The act of pursuing; pursuit; harassment; persecution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insectator', 'n.', 'A pursuer; a persecutor; a censorious critic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insected', 'a.', 'Pertaining to, having the nature of, or resembling, an\n   insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insecticide', 'n.', 'An agent or preparation for destroying insects; an\n   insect powder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insectile', 'a.', 'Pertaining to, or having the nature of, insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insection', 'n.', 'A cutting in; incisure; incision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insectivora', 'n. pl.', 'An order of mammals which feed principally upon\n   insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insectivora', 'n. pl.', 'A division of the Cheiroptera, including the\n   common or insect-eating bats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insectivores', 'pl. ', 'of Insectivore');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insectivore', 'n.', 'One of the Insectivora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insectivorous', 'a.', 'Feeding or subsisting on insects; carnivorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insectivorous', 'a.', 'plants which have some special adaptation for\n   catching and digesting insects, as the sundew, Venuss flytrap,\n   Sarracenia, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insectivorous', 'a.', 'the Insectivora, and to many bats, birds, and\n   reptiles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insectologer', 'n.', 'An entomologist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insectology', 'n.', 'Entomology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insecure', 'a.', 'Not secure; not confident of safety or permanence;\n   distrustful; suspicious; apprehensive of danger or loss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insecure', 'a.', 'Not effectually guarded, protected, or sustained; unsafe;\n   unstable; exposed to danger or loss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insecurely', 'adv.', 'In an insecure manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insecureness', 'n.', 'Insecurity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insecurities', 'pl. ', 'of Insecurity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insecurity', 'n.', 'The condition or quality of being insecure; want of\n   safety; danger; hazard; as, the insecurity of a building liable to\n   fire; insecurity of a debt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insecurity', 'n.', 'The state of feeling insecure; uncertainty; want of\n   confidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insecution', 'n.', 'A following after; close pursuit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inseminate', 'v. t.', 'To sow; to impregnate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insemination', 'n.', 'A sowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insensate', 'a.', 'Wanting sensibility; destitute of sense; stupid;\n   foolish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insense', 'v. t.', 'To make to understand; to instruct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insensibility', 'n.', 'The state or quality of being insensible; want of\n   sensibility; torpor; unconsciousness; as, the insensibility produced by\n   a fall, or by opiates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insensibility', 'n.', 'Want of tenderness or susceptibility of emotion or\n   passion; dullness; stupidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insensible', 'a.', 'Destitute of the power of feeling or perceiving;\n   wanting bodily sensibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insensible', 'a.', 'Not susceptible of emotion or passion; void of feeling;\n   apathetic; unconcerned; indifferent; as, insensible to danger, fear,\n   love, etc.; -- often used with of or to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insensible', 'a.', 'Incapable of being perceived by the senses;\n   imperceptible. Hence: Progressing by imperceptible degrees; slow;\n   gradual; as, insensible motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insensible', 'a.', 'Not sensible or reasonable; meaningless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insensibleness', 'n.', 'Insensibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insensibly', 'adv.', 'In a manner not to be felt or perceived;\n   imperceptibly; gradually.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insensitive', 'a.', 'Not sensitive; wanting sensation, or wanting acute\n   sensibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insensuous', 'a.', 'Not sensuous; not pertaining to, affecting, or\n   addressing, the senses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insentiment', 'a.', 'Not sentient; not having perception, or the power of\n   perception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inseparability', 'n.', 'The quality or state of being inseparable;\n   inseparableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inseparable', 'a.', 'Not separable; incapable of being separated or\n   disjoined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inseparable', 'a.', 'Invariably attached to some word, stem, or root; as,\n   the inseparable particle un-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inseparableness', 'n.', 'The quality or state of being inseparable;\n   inseparability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inseparably', 'adv.', 'In an inseparable manner or condition; so as not to\n   be separable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inseparate', 'a.', 'Not separate; together; united.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inseparately', 'adv.', 'Inseparably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inserted', 'imp. & p. p.', 'of Insert');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inserting', 'p. pr. & vb. n.', 'of Insert');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insert', 'v. t.', 'To set within something; to put or thrust in; to\n   introduce; to cause to enter, or be included, or contained; as, to\n   insert a scion in a stock; to insert a letter, word, or passage in a\n   composition; to insert an advertisement in a newspaper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inserted', 'a.', 'Situated upon, attached to, or growing out of, some part;\n   -- said especially of the parts of the flower; as, the calyx, corolla,\n   and stamens of many flowers are inserted upon the receptacle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inserting', 'n.', 'A setting in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inserting', 'n.', 'Something inserted or set in, as lace, etc., in\n   garments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insertion', 'n.', 'The act of inserting; as, the insertion of scions in\n   stocks; the insertion of words or passages in writings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insertion', 'n.', 'The condition or mode of being inserted or attached; as,\n   the insertion of stamens in a calyx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insertion', 'n.', 'That which is set in or inserted, especially a narrow\n   strip of embroidered lace, muslin, or cambric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insertion', 'n.', 'The point or part by which a muscle or tendon is\n   attached to the part to be moved; -- in contradistinction to its\n   origin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inserve', 'v. i.', 'To be of use to an end; to serve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inservient', 'a.', 'Conducive; instrumental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insession', 'n.', 'The act of sitting, as in a tub or bath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insession', 'n.', 'That in which one sits, as a bathing tub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insessores', 'pl. ', 'of Insessor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insessor', 'n.', 'One of the Insessores. The group includes most of the\n   common singing birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insessores', 'n. pl.', 'An order of birds, formerly established to include\n   the perching birds, but now generally regarded as an artificial group.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insessorial', 'a.', 'Pertaining to, or having the character of, perching\n   birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insessorial', 'a.', 'Belonging or pertaining to the Insessores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inset', 'v. t.', 'To infix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inset', 'n.', 'That which is inserted or set in; an insertion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inset', 'n.', 'One or more separate leaves inserted in a volume before\n   binding; as: (a) A portion of the printed sheet in certain sizes of\n   books which is cut off before folding, and set into the middle of the\n   folded sheet to complete the succession of paging; -- also called\n   offcut. (b) A page or pages of advertisements inserted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inseverable', 'a.', 'Incapable of being severed; indivisible; inseparable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inshaded', 'a.', 'Marked with different shades.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inshave', 'n.', 'A plane for shaving or dressing the concave or inside\n   faces of barrel staves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insheathe', 'v. t.', 'To insert as in a sheath; to sheathe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inshell', 'v. t.', 'To hide in a shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inship', 'v. t.', 'To embark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inshore', 'a.', 'Being near or moving towards the shore; as, inshore\n   fisheries; inshore currents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inshore', 'adv.', 'Towards the shore; as, the boat was headed inshore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inshrine', 'v. t.', 'See Enshrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insiccation', 'n.', 'The act or process of drying in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inside', 'adv.', 'Within the sides of; in the interior; contained within;\n   as, inside a house, book, bottle, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inside', 'a.', 'Being within; included or inclosed in anything; contained;\n   interior; internal; as, the inside passengers of a stagecoach; inside\n   decoration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inside', 'a.', 'Adapted to the interior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inside', 'n.', 'The part within; interior or internal portion; content.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inside', 'n.', 'The inward parts; entrails; bowels; hence, that which is\n   within; private thoughts and feelings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inside', 'n.', 'An inside passenger of a coach or carriage, as\n   distinguished from one upon the outside.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insidiate', 'v. t.', 'To lie in ambush for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insidiator', 'n.', 'One who lies in ambush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insidious', 'a.', 'Lying in wait; watching an opportunity to insnare or\n   entrap; deceitful; sly; treacherous; -- said of persons; as, the\n   insidious foe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insidious', 'a.', 'Intended to entrap; characterized by treachery and\n   deceit; as, insidious arts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insight', 'n.', 'A sight or view of the interior of anything; a deep\n   inspection or view; introspection; -- frequently used with into.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insight', 'n.', 'Power of acute observation and deduction; penetration;\n   discernment; perception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insignia', 'n. pl.', 'Distinguishing marks of authority, office, or honor;\n   badges; tokens; decorations; as, the insignia of royalty or of an\n   order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insignia', 'n. pl.', 'Typical and characteristic marks or signs, by which\n   anything is known or distinguished; as, the insignia of a trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insignificance', 'n.', 'The condition or quality of being insignificant;\n   want of significance, sense, or meaning; as, the insignificance of\n   words or phrases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insignificance', 'n.', 'Want of force or effect; unimportance; pettiness;\n   inefficacy; as, the insignificance of human art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insignificance', 'n.', 'Want of claim to consideration or notice; want of\n   influence or standing; meanness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insignificancy', 'n.', 'Insignificance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insignificant', 'a.', 'Not significant; void of signification, sense, or\n   import; meaningless; as, insignificant words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insignificant', 'a.', 'Having no weight or effect; answering no purpose;\n   unimportant; valueless; futile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insignificant', 'a.', 'Without weight of character or social standing;\n   mean; contemptible; as, an insignificant person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insignificantly', 'adv.', 'without significance, importance, or effect; to\n   no purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insignificative', 'a.', 'Not expressing meaning; not significant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insignment', 'n.', 'A token, mark, or explanation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insimulate', 'v. t.', 'To accuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insincere', 'a.', 'Not being in truth what one appears to be; not sincere;\n   dissembling; hypocritical; disingenuous; deceitful; false; -- said of\n   persons; also of speech, thought; etc.; as, insincere declarations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insincere', 'a.', 'Disappointing; imperfect; unsound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insincerely', 'adv.', 'Without sincerity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insincerity', 'n.', 'The quality of being insincere; want of sincerity, or\n   of being in reality what one appears to be; dissimulation;\n   hypocritical; deceitfulness; hollowness; untrustworthiness; as, the\n   insincerity of a professed friend; the insincerity of professions of\n   regard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insinewed', 'imp. & p. p.', 'of Insinew');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insinewing', 'p. pr. & vb. n.', 'of Insinew');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insinew', 'v. t.', 'To strengthen, as with sinews; to invigorate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insinuant', 'a.', 'Insinuating; insinuative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insinuated', 'imp. & p. p.', 'of Insinuate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insinuating', 'p. pr. & vb. n.', 'of Insinuate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insinuate', 'v. t.', 'To introduce gently or slowly, as by a winding or\n   narrow passage, or a gentle, persistent movement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insinuate', 'v. t.', 'To introduce artfully; to infuse gently; to instill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insinuate', 'v. t.', 'To hint; to suggest by remote allusion; -- often used\n   derogatorily; as, did you mean to insinuate anything?');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insinuate', 'v. t.', 'To push or work (ones self), as into favor; to\n   introduce by slow, gentle, or artful means; to ingratiate; -- used\n   reflexively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insinuate', 'v. i.', 'To creep, wind, or flow in; to enter gently, slowly,\n   or imperceptibly, as into crevices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insinuate', 'v. i.', 'To ingratiate ones self; to obtain access or favor\n   by flattery or cunning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insinuating', 'a.', 'Winding, creeping, or flowing in, quietly or\n   stealthily; suggesting; winning favor and confidence insensibly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insinuatingly', 'adv.', 'By insinuation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insinuation', 'n.', 'The act or process of insinuating; a creeping,\n   winding, or flowing in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insinuation', 'n.', 'The act of gaining favor, affection, or influence, by\n   gentle or artful means; -- formerly used in a good sense, as of\n   friendly influence or interposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insinuation', 'n.', 'The art or power of gaining good will by a\n   prepossessing manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insinuation', 'n.', 'That which is insinuated; a hint; a suggestion or\n   intimation by distant allusion; as, slander may be conveyed by\n   insinuations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insinuative', 'a.', 'Stealing on or into the confidence or affections;\n   having power to gain favor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insinuative', 'a.', 'Using insinuations; giving hints; insinuating; as,\n   insinuative remark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insinuator', 'n.', 'One who, or that which, insinuates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insinuatory', 'a.', 'Insinuative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insipid', 'a.', 'Wanting in the qualities which affect the organs of taste;\n   without taste or savor; vapid; tasteless; as, insipid drink or food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insipid', 'a.', 'Wanting in spirit, life, or animation; uninteresting;\n   weak; vapid; flat; dull; heavy; as, an insipid woman; an insipid\n   composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insipidity', 'n.', 'Alt. of Insipidness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insipidness', 'n.', 'The quality or state of being insipid; vapidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insipidly', 'adv.', 'In an insipid manner; without taste, life, or spirit;\n   flatly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insipience', 'n.', 'Want of intelligence; stupidity; folly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insipient', 'a.', 'Wanting wisdom; stupid; foolish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insipient', 'n.', 'An insipient person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insisted', 'imp. & p. p.', 'of Insist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insisting', 'p. pr. & vb. n.', 'of Insist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insist', 'v. i.', 'To stand or rest; to find support; -- with in, on, or\n   upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insist', 'v. i.', 'To take a stand and refuse to give way; to hold to\n   something firmly or determinedly; to be persistent, urgent, or\n   pressing; to persist in demanding; -- followed by on, upon, or that;\n   as, he insisted on these conditions; he insisted on going at once; he\n   insists that he must have money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insistence', 'n.', 'The quality of insisting, or being urgent or pressing;\n   the act of dwelling upon as of special importance; persistence;\n   urgency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insistent', 'a.', 'Standing or resting on something; as, an insistent wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insistent', 'a.', 'Insisting; persistent; persevering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insistent', 'a.', 'See Incumbent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insistently', 'adv.', 'In an insistent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insisture', 'n.', 'A dwelling or standing on something; fixedness;\n   persistence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insitency', 'n.', 'Freedom from thirst.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insition', 'n.', 'The insertion of a scion in a stock; ingraftment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'In situ', '', 'In its natural position or place; -- said of a rock or\n   fossil, when found in the situation in which it was originally formed\n   or deposited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insnared', 'imp. & p. p.', 'of Insnare');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insnaring', 'p. pr. & vb. n.', 'of Insnare');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insnare', 'v. t.', 'To catch in a snare; to entrap; to take by artificial\n   means.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insnare', 'v. t.', 'To take by wiles, stratagem, or deceit; to involve in\n   difficulties or perplexities; to seduce by artifice; to inveigle; to\n   allure; to entangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insnarer', 'n.', 'One who insnares.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insnarl', 'v. t.', 'To make into a snarl or knot; to entangle; to snarl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insobriety', 'n.', 'Want of sobriety, moderation, or calmness;\n   intemperance; drunkenness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insociability', 'n.', 'The quality of being insociable; want of\n   sociability; unsociability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insociable', 'a.', 'Incapable of being associated, joined, or connected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insociable', 'a.', 'Not sociable or companionable; disinclined to social\n   intercourse or conversation; unsociable; taciturn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insociably', 'adv.', 'Unsociably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insociate', 'a.', 'Not associate; without a companion; single; solitary;\n   recluse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insolated', 'imp. & p. p.', 'of Insolate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insolating', 'p. pr. & vb. n.', 'of Insolate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insolate', 'v. t.', 'To dry in, or to expose to, the suns rays; to ripen\n   or prepare by such exposure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insolation', 'n.', 'The act or process to exposing to the rays of the sun\n   fro the purpose of drying or maturing, as fruits, drugs, etc., or of\n   rendering acid, as vinegar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insolation', 'n.', 'A sunstroke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insolation', 'n.', 'Exposure of a patient to the suns rays; a sun bath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insole', 'n.', 'The inside sole of a boot or shoe; also, a loose, thin\n   strip of leather, felt, etc., placed inside the shoe for warmth or\n   ease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insolence', 'n.', 'The quality of being unusual or novel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insolence', 'n.', 'The quality of being insolent; pride or haughtiness\n   manifested in contemptuous and overbearing treatment of others;\n   arrogant contempt; brutal impudence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insolence', 'n.', 'Insolent conduct or treatment; insult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insolence', 'v. t.', 'To insult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insolency', 'n.', 'Insolence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insolent', 'a.', 'Deviating from that which is customary; novel; strange;\n   unusual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insolent', 'a.', 'Haughty and contemptuous or brutal in behavior or\n   language; overbearing; domineering; grossly rude or disrespectful;\n   saucy; as, an insolent master; an insolent servant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insolent', 'a.', 'Proceeding from or characterized by insolence; insulting;\n   as, insolent words or behavior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insolently', 'adv.', 'In an insolent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insolidity', 'n.', 'Want of solidity; weakness; as, the insolidity of an\n   argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insolubility', 'n.', 'The quality or state of being insoluble or not\n   dissolvable, as in a fluid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insolubility', 'n.', 'The quality of being inexplicable or insolvable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insoluble', 'a.', 'Not soluble; in capable or difficult of being dissolved,\n   as by a liquid; as, chalk is insoluble in water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insoluble', 'a.', 'Not to be solved or explained; insolvable; as, an\n   insoluble doubt, question, or difficulty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insoluble', 'a.', 'Strong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insolubleness', 'n.', 'The quality or state of being insoluble;\n   insolubility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insolvable', 'a.', 'Not solvable; insoluble; admitting no solution or\n   explanation; as, an insolvable problem or difficulty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insolvable', 'a.', 'Incapable of being paid or discharged, as debts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insolvable', 'a.', 'Not capable of being loosed or disentangled;\n   inextricable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insolvencies', 'pl. ', 'of Insolvency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insolvency', 'n.', 'The condition of being insolvent; the state or\n   condition of a person who is insolvent; the condition of one who is\n   unable to pay his debts as they fall due, or in the usual course of\n   trade and business; as, a merchants insolvency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insolvency', 'n.', 'Insufficiency to discharge all debts of the owner; as,\n   the insolvency of an estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insolvent', 'a.', 'Not solvent; not having sufficient estate to pay ones\n   debts; unable to pay ones debts as they fall due, in the ordinary\n   course of trade and business; as, in insolvent debtor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insolvent', 'a.', 'Not sufficient to pay all the debts of the owner; as, an\n   insolvent estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insolvent', 'a.', 'Relating to persons unable to pay their debts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insolvent', 'n.', 'One who is insolvent; as insolvent debtor; -- in\n   England, before 1861, especially applied to persons not traders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insomnia', 'n.', 'Want of sleep; inability to sleep; wakefulness;\n   sleeplessness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insomnious', 'a.', 'Restless; sleepless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insomnolence', 'n.', 'Sleeplessness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insomuch', 'adv.', 'So; to such a degree; in such wise; -- followed by that\n   or as, and formerly sometimes by both. Cf. Inasmuch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insonorous', 'a.', 'Not clear or melodious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insooth', 'adv.', 'In sooth; truly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insouciance', 'n.', 'Carelessness; heedlessness; thoughtlessness;\n   unconcern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insouciant', 'a.', 'Careless; heedless; indifferent; unconcerned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insoul', 'v. t.', 'To set a soul in; reflexively, to fix ones strongest\n   affections on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspan', 'v. t. & i.', 'To yoke or harness, as oxen to a vehicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspected', 'imp. & p. p.', 'of Inspect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspecting', 'p. pr. & vb. n.', 'of Inspect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspect', 'v. t.', 'To look upon; to view closely and critically, esp. in\n   order to ascertain quality or condition, to detect errors, etc., to\n   examine; to scrutinize; to investigate; as, to inspect conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspect', 'v. t.', 'To view and examine officially, as troops, arms, goods\n   offered, work done for the public, etc.; to oversee; to superintend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspect', 'v. t.', 'Inspection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspecttion', 'n.', 'The act or process of inspecting or looking at\n   carefully; a strict or prying examination; close or careful scrutiny;\n   investigation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspecttion', 'n.', 'The act of overseeing; official examination or\n   superintendence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspective', 'a.', 'Engaged in inspection; inspecting; involving\n   inspection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspector', 'n.', 'One who inspects, views, or oversees; one to whom the\n   supervision of any work is committed; one who makes an official view or\n   examination, as a military or civil officer; a superintendent; a\n   supervisor; an overseer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspectorate', 'n.', 'Inspectorship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspectorial', 'a.', 'Of or pertaining to an inspector or to inspection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspectorship', 'n.', 'The office of an inspector.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspectorship', 'n.', 'The district embraced by an inspectors\n   jurisdiction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspectress', 'n.', 'A female inspector.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insperse', 'v. t.', 'To sprinkle; to scatter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspersion', 'n.', 'The act of sprinkling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspeximus', 'n.', 'The first word of ancient charters in England,\n   confirming a grant made by a former king; hence, a royal grant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insphered', 'imp. & p. p.', 'of Insphere');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insphering', 'p. pr. & vb. n.', 'of Insphere');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insphere', 'v. t.', 'To place in, or as in, an orb a sphere. Cf. Ensphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspirable', 'a.', 'Capable of being inspired or drawn into the lungs;\n   inhalable; respirable; admitting inspiration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspiration', 'n.', 'The act of inspiring or breathing in; breath; specif.\n   (Physiol.), the drawing of air into the lungs, accomplished in mammals\n   by elevation of the chest walls and flattening of the diaphragm; -- the\n   opposite of expiration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspiration', 'n.', 'The act or power of exercising an elevating or\n   stimulating influence upon the intellect or emotions; the result of\n   such influence which quickens or stimulates; as, the inspiration of\n   occasion, of art, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspiration', 'n.', 'A supernatural divine influence on the prophets,\n   apostles, or sacred writers, by which they were qualified to\n   communicate moral or religious truth with authority; a supernatural\n   influence which qualifies men to receive and communicate divine truth;\n   also, the truth communicated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspirational', 'a.', 'Pertaining to inspiration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspirationist', 'n.', 'One who holds to inspiration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspirator', 'n.', 'A kind of injector for forcing water by steam. See\n   Injector, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspirtory', 'a.', 'Pertaining to, or aiding, inspiration; as, the\n   inspiratory muscles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspire', 'v. t.', 'To breathe into; to fill with the breath; to animate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspire', 'v. t.', 'To infuse by breathing, or as if by breathing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspire', 'v. t.', 'To draw in by the operation of breathing; to inhale; --\n   opposed to expire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspire', 'v. t.', 'To infuse into the mind; to communicate to the spirit;\n   to convey, as by a divine or supernatural influence; to disclose\n   preternaturally; to produce in, as by inspiration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspire', 'v. t.', 'To infuse into; to affect, as with a superior or\n   supernatural influence; to fill with what animates, enlivens, or\n   exalts; to communicate inspiration to; as, to inspire a child with\n   sentiments of virtue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspired', 'imp. & p. p.', 'of Inspire');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspiring', 'p. pr. & vb. n.', 'of Inspire');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspire', 'v. i.', 'To draw in breath; to inhale air into the lungs; --\n   opposed to expire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspire', 'v. i.', 'To breathe; to blow gently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspired', 'a.', 'Breathed in; inhaled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspired', 'a.', 'Moved or animated by, or as by, a supernatural influence;\n   affected by divine inspiration; as, the inspired prophets; the inspired\n   writers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspired', 'a.', 'Communicated or given as by supernatural or divine\n   inspiration; having divine authority; hence, sacred, holy; -- opposed\n   to uninspired, profane, or secular; as, the inspired writings, that is,\n   the Scriptures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspirer', 'n.', 'One who, or that which, inspirer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspiring', 'a.', 'Animating; cheering; moving; exhilarating; as, an\n   inspiring or scene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspirited', 'imp. & p. p.', 'of Inspirit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspiriting', 'p. pr. & vb. n.', 'of Inspirit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspirit', 'v. t.', 'To infuse new life or spirit into; to animate; to\n   encourage; to invigorate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspissated', 'imp. & p. p.', 'of Inspissate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspissating', 'p. pr. & vb. n.', 'of Inspissate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspissate', 'v. t.', 'To thicken or bring to greater consistence, as\n   fluids by evaporation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspissate', 'a.', 'Thick or thickened; inspissated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inspissation', 'n.', 'The act or the process of inspissating, or thickening\n   a fluid substance, as by evaporation; also, the state of being so\n   thickened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instabilities', 'pl. ', 'of Instability');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instability', 'n.', 'The quality or condition of being unstable; want of\n   stability, firmness, or steadiness; liability to give way or to fail;\n   insecurity; precariousness; as, the instability of a building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instability', 'n.', 'Lack of determination of fixedness; inconstancy;\n   fickleness; mutability; changeableness; as, instability of character,\n   temper, custom, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instable', 'a.', 'Not stable; not standing fast or firm; unstable; prone to\n   change or recede from a purpose; mutable; inconstant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instableness', 'n.', 'Instability; unstableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Installed', 'imp. & p. p.', 'of Install');
  INSERT INTO words (word, word_type, description) VALUES  (    'Installing', 'p. pr. & vb. n.', 'of Install');
  INSERT INTO words (word, word_type, description) VALUES  (    'Install', 'v. t.', 'To set in a seat; to give a place to; establish (one)\n   in a place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Install', 'v. t.', 'To place in an office, rank, or order; to invest with\n   any charge by the usual ceremonies; to instate; to induct; as, to\n   install an ordained minister as pastor of a church; to install a\n   college president.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Installation', 'n.', 'The act of installing or giving possession of an\n   office, rank, or order, with the usual rites or ceremonies; as, the\n   installation of an ordained minister in a parish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Installation', 'n.', 'The whole of a system of machines, apparatus, and\n   accessories, when set up and arranged for practical working, as in\n   electric lighting, transmission of power, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Installment', 'n.', 'The act of installing; installation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Installment', 'n.', 'The seat in which one is placed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Installment', 'n.', 'A portion of a debt, or sum of money, which is divided\n   into portions that are made payable at different times. Payment by\n   installment is payment by parts at different times, the amounts and\n   times being often definitely stipulated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instamp', 'v. t.', 'See Enstamp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instance', 'n.', 'The act or quality of being instant or pressing; urgency;\n   solicitation; application; suggestion; motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instance', 'n.', 'That which is instant or urgent; motive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instance', 'n.', 'Occasion; order of occurrence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instance', 'n.', 'That which offers itself or is offered as an illustrative\n   case; something cited in proof or exemplification; a case occurring; an\n   example.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instance', 'n.', 'A token; a sign; a symptom or indication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instanced', 'imp. & p. p.', 'of Instance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instancing', 'p. pr. & vb. n.', 'of Instance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instance', 'v. t.', 'To mention as a case or example; to refer to; to cite;\n   as, to instance a fact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instance', 'v. i.', 'To give an example.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instancy', 'n.', 'Instance; urgency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instant', 'a.', 'Pressing; urgent; importunate; earnest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instant', 'a.', 'Closely pressing or impending in respect to time; not\n   deferred; immediate; without delay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instant', 'a.', 'Present; current.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instant', 'adv.', 'Instantly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instant', 'a.', 'A point in duration; a moment; a portion of time too short\n   to be estimated; also, any particular moment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instant', 'a.', 'A day of the present or current month; as, the sixth\n   instant; -- an elliptical expression equivalent to the sixth of the\n   month instant, i. e., the current month. See Instant, a., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instantaneity', 'n.', 'Quality of being instantaneous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instantaneous', 'a.', 'Done or occurring in an instant, or without any\n   perceptible duration of time; as, the passage of electricity appears to\n   be instantaneous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instantaneous', 'a.', 'At or during a given instant; as, instantaneous\n   acceleration, velocity, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instanter', 'a.', 'Immediately; instantly; at once; as, he left instanter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instantly', 'adv.', 'Without the least delay or interval; at once;\n   immediately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instantly', 'adv.', 'With urgency or importunity; earnestly; pressingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instar', 'v. t.', 'To stud as with stars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instated', 'imp. & p. p.', 'of Instate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instating', 'p. pr. & vb. n.', 'of Instate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instate', 'v. t.', 'To set, place, or establish, as in a rank, office, or\n   condition; to install; to invest; as, to instate a person in greatness\n   or in favor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instaurated', 'imp. & p. p.', 'of Instaurate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instaurating', 'p. pr. & vb. n.', 'of Instaurate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instaurate', 'v. t.', 'To renew or renovate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instauration', 'n.', 'Restoration after decay, lapse, or dilapidation;\n   renewal; repair; renovation; renaissance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instaurator', 'n.', 'One who renews or restores to a former condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instaure', 'v. t.', 'To renew or renovate; to instaurate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instead', 'adv.', 'In the place or room; -- usually followed by of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instead', 'adv.', 'Equivalent; equal to; -- usually with of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insteeped', 'imp. & p. p.', 'of Insteep');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insteeping', 'p. pr. & vb. n.', 'of Insteep');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insteep', 'v. t.', 'To steep or soak; to drench.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instep', 'n.', 'The arched middle portion of the human foot next in front\n   of the ankle joint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instep', 'n.', 'That part of the hind leg of the horse and allied animals,\n   between the hock, or ham, and the pastern joint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instigated', 'imp. & p. p.', 'of Instigate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instigating', 'p. pr. & vb. n.', 'of Instigate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instigate', 'v. t.', 'To goad or urge forward; to set on; to provoke; to\n   incite; -- used chiefly with reference to evil actions; as to instigate\n   one to a crime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instigatingly', 'adv.', 'Incitingly; temptingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instigation', 'n.', 'The act of instigating, or the state of being\n   instigated; incitement; esp. to evil or wickedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instigator', 'n.', 'One who instigates or incites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instilled', 'imp. & p. p.', 'of Instill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instilling', 'p. pr. & vb. n.', 'of Instill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instill', 'v. t.', 'To drop in; to pour in drop by drop; hence, to impart\n   gradually; to infuse slowly; to cause to be imbibed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instillation', 'n.', 'The of instilling; also, that which is instilled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instilllator', 'n.', 'An instiller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instilllatory', 'a.', 'Belonging to instillation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instiller', 'n.', 'One who instills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instillment', 'n.', 'The act of instilling; also, that which is instilled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instimulate', 'v. t.', 'Not to stimulate; to soothe; to quiet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instimulate', 'v. t.', 'To stimulate; to excite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instimulation', 'n.', 'Stimulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instinct', 'a.', 'Urged or stimulated from within; naturally moved or\n   impelled; imbued; animated; alive; quick; as, birds instinct with life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instinct', 'a.', 'Natural inward impulse; unconscious, involuntary, or\n   unreasoning prompting to any mode of action, whether bodily, or mental,\n   without a distinct apprehension of the end or object to be\n   accomplished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instinct', 'a.', 'Specif., the natural, unreasoning, impulse by which an\n   animal is guided to the performance of any action, without of\n   improvement in the method.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instinct', 'a.', 'A natural aptitude or knack; a predilection; as, an\n   instinct for order; to be modest by instinct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instinct', 'v. t.', 'To impress, as an animating power, or instinct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instinction', 'n.', 'Instinct; incitement; inspiration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instinctive', 'a.', 'Of or pertaining to instinct; derived from, or\n   prompted by, instinct; of the nature of instinct; determined by natural\n   impulse or propensity; acting or produced without reasoning,\n   deliberation, instruction, or experience; spontaneous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instinctively', 'adv.', 'In an instinctive manner; by force of instinct; by\n   natural impulse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instinctivity', 'n.', 'The quality of being instinctive, or prompted by\n   instinct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instipulate', 'a.', 'See Exstipulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Institute', 'p. a.', 'Established; organized; founded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instituted', 'imp. & p. p.', 'of Institute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instituting', 'p. pr. & vb. n.', 'of Institute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Institute', 'v. t.', 'To set up; to establish; to ordain; as, to institute\n   laws, rules, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Institute', 'v. t.', 'To originate and establish; to found; to organize;\n   as, to institute a court, or a society.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Institute', 'v. t.', 'To nominate; to appoint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Institute', 'v. t.', 'To begin; to commence; to set on foot; as, to\n   institute an inquiry; to institute a suit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Institute', 'v. t.', 'To ground or establish in principles and rudiments;\n   to educate; to instruct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Institute', 'v. t.', 'To invest with the spiritual charge of a benefice, or\n   the care of souls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Institute', 'a.', 'The act of instituting; institution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Institute', 'a.', 'That which is instituted, established, or fixed, as a\n   law, habit, or custom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Institute', 'a.', 'Hence: An elementary and necessary principle; a precept,\n   maxim, or rule, recognized as established and authoritative; usually in\n   the plural, a collection of such principles and precepts; esp., a\n   comprehensive summary of legal principles and decisions; as, the\n   Institutes of Justinian; Cokes Institutes of the Laws of England. Cf.\n   Digest, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Institute', 'n.', 'An institution; a society established for the promotion\n   of learning, art, science, etc.; a college; as, the Institute of\n   Technology; also, a building owned or occupied by such an institute;\n   as, the Cooper Institute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Institute', 'n.', 'The person to whom an estate is first given by\n   destination or limitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instituter', 'n.', 'An institutor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Institution', 'n.', 'The act or process of instituting; as: (a)\n   Establishment; foundation; enactment; as, the institution of a school.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Institution', 'n.', 'Instruction; education.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Institution', 'n.', 'The act or ceremony of investing a clergyman with the\n   spiritual part of a benefice, by which the care of souls is committed\n   to his charge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Institution', 'n.', 'That which instituted or established');
  INSERT INTO words (word, word_type, description) VALUES  (    'Institution', 'n.', 'Established order, method, or custom; enactment;\n   ordinance; permanent form of law or polity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Institution', 'n.', 'An established or organized society or corporation; an\n   establishment, especially of a public character, or affecting a\n   community; a foundation; as, a literary institution; a charitable\n   institution; also, a building or the buildings occupied or used by such\n   organization; as, the Smithsonian Institution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Institution', 'n.', 'Anything forming a characteristic and persistent\n   feature in social or national life or habits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Institution', 'n.', 'That which institutes or instructs; a textbook; a\n   system of elements or rules; an institute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Institutional', 'a.', 'Pertaining to, or treating of, institutions; as,\n   institutional legends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Institutional', 'a.', 'Instituted by authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Institutional', 'a.', 'Elementary; rudimental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Institutionary', 'a.', 'Relating to an institution, or institutions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Institutionary', 'a.', 'Containing the first principles or doctrines;\n   elemental; rudimentary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Institutist', 'n.', 'A writer or compiler of, or a commentator on,\n   institutes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Institutive', 'a.', 'Tending or intended to institute; having the power to\n   establish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Institutive', 'a.', 'Established; depending on, or characterized by,\n   institution or order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Institutively', 'adv.', 'In conformity with an institution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Institutor', 'n.', 'One who institutes, founds, ordains, or establishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Institutor', 'n.', 'One who educates; an instructor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Institutor', 'n.', 'A presbyter appointed by the bishop to institute a\n   rector or assistant minister over a parish church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instop', 'v. t.', 'To stop; to close; to make fast; as, to instop the\n   seams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instore', 'v. t.', 'To store up; to inclose; to contain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instratified', 'a.', 'Interstratified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instruct', 'a.', 'Arranged; furnished; provided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instruct', 'a.', 'Instructed; taught; enlightened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instructed', 'imp. & p. p.', 'of Instruct');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instructing', 'p. pr. & vb. n.', 'of Instruct');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instruct', 'v. t.', 'To put in order; to form; to prepare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instruct', 'v. t.', 'To form by communication of knowledge; to inform the\n   mind of; to impart knowledge or information to; to enlighten; to teach;\n   to discipline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instruct', 'v. t.', 'To furnish with directions; to advise; to direct; to\n   command; as, the judge instructs the jury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instructer', 'n.', 'See Instructor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instructible', 'a.', 'Capable of being instructed; teachable; docible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instruction', 'n.', 'The act of instructing, teaching, or furnishing with\n   knowledge; information.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instruction', 'n.', 'That which instructs, or with which one is instructed;\n   the intelligence or information imparted');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instruction', 'n.', 'Precept; information; teachings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instruction', 'n.', 'Direction; order; command.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instructional', 'a.', 'Pertaining to, or promoting, instruction;\n   educational.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instructive', 'a.', 'Conveying knowledge; serving to instruct or inform;\n   as, experience furnishes very instructive lessons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instructor', 'n.', 'One who instructs; one who imparts knowledge to\n   another; a teacher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instructress', 'n.', 'A woman who instructs; a preceptress; a governess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instrument', 'n.', 'That by means of which any work is performed, or result\n   is effected; a tool; a utensil; an implement; as, the instruments of a\n   mechanic; astronomical instruments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instrument', 'n.', 'A contrivance or implement, by which musical sounds are\n   produced; as, a musical instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instrument', 'n.', 'A writing, as the means of giving formal expression to\n   some act; a writing expressive of some act, contract, process, as a\n   deed, contract, writ, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instrument', 'n.', 'One who, or that which, is made a means, or is caused\n   to serve a purpose; a medium, means, or agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instrument', 'v. t.', 'To perform upon an instrument; to prepare for an\n   instrument; as, a sonata instrumented for orchestra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instrumental', 'a.', 'Acting as an instrument; serving as a means;\n   contributing to promote; conductive; helpful; serviceable; as, he was\n   instrumental in conducting the business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instrumental', 'a.', 'Pertaining to, made by, or prepared for, an\n   instrument, esp. a musical instrument; as, instrumental music,\n   distinguished from vocal music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instrumental', 'a.', 'Applied to a case expressing means or agency; as, the\n   instrumental case. This is found in Sanskrit as a separate case, but in\n   Greek it was merged into the dative, and in Latin into the ablative. In\n   Old English it was a separate case, but has disappeared, leaving only a\n   few anomalous forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instrumentalist', 'n.', 'One who plays upon an instrument of music, as\n   distinguished from a vocalist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instrumentalities', 'pl. ', 'of Instrumentality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instrumentality', 'n.', 'The quality or condition of being instrumental;\n   that which is instrumental; anything used as a means; medium; agency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instrumentally', 'adv.', 'By means of an instrument or agency; as means to\n   an end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instrumentally', 'adv.', 'With instruments of music; as, a song\n   instrumentally accompanied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instrumentalness', 'n.', 'Usefulness or agency, as means to an end;\n   instrumentality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instrumentary', 'a.', 'Instrumental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instrumentation', 'n.', 'The act of using or adapting as an instrument; a\n   series or combination of instruments; means; agency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instrumentation', 'n.', 'The arrangement of a musical composition for\n   performance by a number of different instruments; orchestration;\n   instrumental composition; composition for an orchestra or military\n   band.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instrumentation', 'n.', 'The act or manner of playing upon musical\n   instruments; performance; as, his instrumentation is perfect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instrumentist', 'n.', 'A performer on a musical instrument; an\n   instrumentalist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Instyle', 'v. t.', 'To style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insuavity', 'n.', 'Want of suavity; unpleasantness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insubjection', 'n.', 'Want of subjection or obedience; a state of\n   disobedience, as to government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insubmergible', 'a.', 'Not capable of being submerged; buoyant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insubmission', 'n.', 'Want of submission; disobedience; noncompliance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insubordinate', 'a.', 'Not submitting to authority; disobedient;\n   rebellious; mutinous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insubordination', 'n.', 'The quality of being insubordinate; disobedience\n   to lawful authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insubstantial', 'a.', 'Unsubstantial; not real or strong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insubstantiality', 'n.', 'Unsubstantiality; unreality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insuccation', 'n.', 'The act of soaking or moistening; maceration; solution\n   in the juice of herbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insuccess', 'n.', 'Want of success.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insue', 'v. i.', 'See Ensue, v. i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insuetude', 'n.', 'The state or quality of being unaccustomed; absence of\n   use or habit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insufferable', 'a.', 'Incapable of being suffered, borne, or endured;\n   insupportable; unendurable; intolerable; as, insufferable heat, cold,\n   or pain; insufferable wrongs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insufferable', 'a.', 'Offensive beyond endurance; detestable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insufferably', 'adv.', 'In a manner or to a degree beyond endurance;\n   intolerably; as, a blaze insufferably bright; a person insufferably\n   proud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insufficience', 'n.', 'Insufficiency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insufficiency', 'n.', 'The quality or state of being insufficient; want of\n   sufficiency; deficiency; inadequateness; as, the insufficiency of\n   provisions, of an excuse, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insufficiency', 'n.', 'Want of power or skill; inability; incapacity;\n   incompetency; as, the insufficiency of a man for an office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insufficient', 'a.', 'Not sufficient; not enough; inadequate to any need,\n   use, or purpose; as, the provisions are insufficient in quantity, and\n   defective in quality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insufficient', 'a.', 'Wanting in strength, power, ability, capacity, or\n   skill; incompetent; incapable; unfit; as, a person insufficient to\n   discharge the duties of an office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insufficiently', 'adv.', 'In an insufficient manner or degree;\n   unadequately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insufflation', 'n.', 'The act of breathing on or into anything');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insufflation', 'n.', 'The breathing upon a person in the sacrament of\n   baptism to symbolize the inspiration of a new spiritual life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insufflation', 'n.', 'The act of blowing (a gas, powder, or vapor) into any\n   cavity of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insuitable', 'a.', 'Unsuitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insular', 'a.', 'Of or pertaining to an island; of the nature, or\n   possessing the characteristics, of an island; as, an insular climate,\n   fauna, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insular', 'a.', 'Of or pertaining to the people of an island; narrow;\n   circumscribed; illiberal; contracted; as, insular habits, opinions, or\n   prejudices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insular', 'n.', 'An islander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insularity', 'n.', 'The state or quality of being an island or consisting\n   of islands; insulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insularity', 'n.', 'Narrowness or illiberality of opinion; prejudice;\n   exclusiveness; as, the insularity of the Chinese or of the aristocracy.');
