INSERT INTO words (word, word_type, description)VALUES  (    'Insulated',    'imp. & p. p.',    'of Insulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insulating', 'p. pr. & vb. n.', 'of Insulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insulate', 'v. t.', 'To make an island of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insulate', 'v. t.', 'To place in a detached situation, or in a state having\n   no communication with surrounding objects; to isolate; to separate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insulate', 'v. t.', 'To prevent the transfer o/ electricity or heat to or\n   from (bodies) by the interposition of nonconductors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insulated', 'p. a.', 'Standing by itself; not being contiguous to other\n   bodies; separated; unconnected; isolated; as, an insulated house or\n   column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insulated', 'p. a.', 'Separated from other bodies by means of nonconductors\n   of heat or electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insulated', 'p. a.', 'Situated at so great a distance as to be beyond the\n   effect of gravitation; -- said of stars supposed to be so far apart\n   that the affect of their mutual attraction is insensible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insulation', 'n.', 'The act of insulating, or the state of being insulated;\n   detachment from other objects; isolation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insulation', 'n.', 'The act of separating a body from others by\n   nonconductors, so as to prevent the transfer of electricity or of heat;\n   also, the state of a body so separated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insulator', 'n.', 'One who, or that which, insulates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insulator', 'n.', 'The substance or body that insulates; a nonconductor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insulite', 'n.', 'An insulating material, usually some variety of\n   compressed cellulose, made of sawdust, paper pulp, cotton waste, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insulous', 'a.', 'Abounding in islands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insulse', 'a.', 'Insipid; dull; stupid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insulsity', 'n.', 'Insipidity; stupidity; dullness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insult', 'v. t.', 'The act of leaping on; onset; attack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insult', 'v. t.', 'Gross abuse offered to another, either by word or act;\n   an act or speech of insolence or contempt; an affront; an indignity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insulted', 'imp. & p. p.', 'of Insult');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insulting', 'p. pr. & vb. n.', 'of Insult');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insult', 'v. t.', 'To leap or trample upon; to make a sudden onset upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insult', 'v. t.', 'To treat with abuse, insolence, indignity, or contempt,\n   by word or action; to abuse; as, to call a man a coward or a liar, or\n   to sneer at him, is to insult him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insult', 'v. i.', 'To leap or jump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insult', 'v. i.', 'To behave with insolence; to exult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insultable', 'a.', 'Capable of being insulted or affronted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insultation', 'n.', 'The act of insulting; abusive or insolent treatment;\n   insult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insultation', 'n.', 'Exultation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insulter', 'n.', 'One who insults.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insulting', 'a.', 'Containing, or characterized by, insult or abuse;\n   tending to insult or affront; as, insulting language, treatment, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insultment', 'n.', 'Insolent treatment; insult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insume', 'v. t.', 'To take in; to absorb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insuperability', 'n.', 'The quality or state of being insuperable;\n   insuperableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insuperable', 'a.', 'Incapable of being passed over or surmounted;\n   insurmountable; as, insuperable difficulties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insupportable', 'a.', 'Incapable of being supported or borne; unendurable;\n   insufferable; intolerable; as, insupportable burdens; insupportable\n   pain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insupposable', 'a.', 'Incapable of being supposed; not supposable;\n   inconceivable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insuppressible', 'a.', 'That can not be suppressed or concealed;\n   irrepressible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insuppressive', 'a.', 'Insuppressible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insurable', 'a.', 'Capable of being insured against loss, damage, death,\n   etc.; proper to be insured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insurance', 'n.', 'The act of insuring, or assuring, against loss or damage\n   by a contingent event; a contract whereby, for a stipulated\n   consideration, called premium, one party undertakes to indemnify or\n   guarantee another against loss by certain specified risks. Cf.\n   Assurance, n., 6.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insurance', 'n.', 'The premium paid for insuring property or life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insurance', 'n.', 'The sum for which life or property is insured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insurance', 'n.', 'A guaranty, security, or pledge; assurance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insurancer', 'n.', 'One who effects insurance; an insurer; an underwriter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insurant', 'n.', 'The person insured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insured', 'imp. & p. p.', 'of Insure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insuring', 'p. pr. & vb. n.', 'of Insure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insure', 'v. t.', 'To make sure or secure; as, to insure safety to any one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insure', 'v. t.', 'Specifically, to secure against a loss by a contingent\n   event, on certain stipulated conditions, or at a given rate or premium;\n   to give or to take an insurance on or for; as, a merchant insures his\n   ship or its cargo, or both, against the dangers of the sea; goods and\n   buildings are insured against fire or water; persons are insured\n   against sickness, accident, or death; and sometimes hazardous debts are\n   insured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insure', 'v. i.', 'To underwrite; to make insurance; as, a company insures\n   at three per cent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insurer', 'n.', 'One who, or that which, insures; the person or company\n   that contracts to indemnify losses for a premium; an underwriter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insurgence', 'n.', 'Alt. of Insurgency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insurgency', 'n.', 'A state of insurrection; an uprising; an insurrection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insurgent', 'a.', 'Rising in opposition to civil or political authority, or\n   against an established government; insubordinate; rebellious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insurgent', 'n.', 'A person who rises in revolt against civil authority or\n   an established government; one who openly and actively resists the\n   execution of laws; a rebel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insurmountability', 'n.', 'The state or quality of being insurmountable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insurmountable', 'a.', 'Incapable of being passed over, surmounted, or\n   overcome; insuperable; as, insurmountable difficulty or obstacle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insurmountableness', 'n.', 'The state or quality of being insurmountable;\n   insurmountability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insurmountably', 'adv.', 'In a manner or to a degree not to be overcome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insurrection', 'n.', 'A rising against civil or political authority, or the\n   established government; open and active opposition to the execution of\n   law in a city or state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insurrection', 'n.', 'A rising in mass to oppose an enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insurrectional', 'a.', 'Pertaining to insurrection; consisting in\n   insurrection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insurrectionary', 'a.', 'Pertaining to, or characterized by, insurrection;\n   rebellious; seditious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insurrectionist', 'n.', 'One who favors, or takes part in, insurrection; an\n   insurgent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insusceptibility', 'n.', 'Want of susceptibility, or of capacity to feel or\n   perceive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insusceptible', 'a.', 'Not susceptible; not capable of being moved,\n   affected, or impressed; that can not feel, receive, or admit; as, a\n   limb insusceptible of pain; a heart insusceptible of pity; a mind\n   insusceptible to flattery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insusceptive', 'a.', 'Not susceptive or susceptible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Insusurration', 'n.', 'The act of whispering into something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inswathed', 'imp. & p. p.', 'of Inswathe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inswating', 'p. pr. & vb. n.', 'of Inswathe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inswathe', 'v. t.', 'To wrap up; to infold; to swathe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intact', 'a.', 'Untouched, especially by anything that harms, defiles, or\n   the like; uninjured; undefiled; left complete or entire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intactible', 'a.', 'Alt. of Intactable');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intactable', 'a.', 'Not perceptible to the touch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intagliated', 'a.', 'Engraved in intaglio; as, an intagliated stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intaglius', 'pl. ', 'of Intaglio');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intagli', 'pl. ', 'of Intaglio');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intaglio', 'n.', 'A cutting or engraving; a figure cut into something, as a\n   gem, so as to make a design depressed below the surface of the\n   material; hence, anything so carved or impressed, as a gem, matrix,\n   etc.; -- opposed to cameo. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intail', 'v. t.', 'See Entail, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intake', 'n.', 'The place where water or air is taken into a pipe or\n   conduit; -- opposed to outlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intake', 'n.', 'the beginning of a contraction or narrowing in a tube or\n   cylinder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intake', 'n.', 'The quantity taken in; as, the intake of air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intaminated', 'a.', 'Uncontaminated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intangibilities', 'pl. ', 'of Intangibility');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intangibility', 'n.', 'The quality or state of being intangible;\n   intangibleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intangible', 'a.', 'Not tangible; incapable of being touched; not\n   perceptible to the touch; impalpable; imperceptible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intangle', 'v. t.', 'See Entangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intastable', 'a.', 'Incapable of being tasted; tasteless; unsavory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Integer', 'n.', 'A complete entity; a whole number, in contradistinction to\n   a fraction or a mixed number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Integrability', 'n.', 'The quality of being integrable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Integrable', 'a.', 'Capable of being integrated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Integral', 'a.', 'Lacking nothing of completeness; complete; perfect;\n   uninjured; whole; entire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Integral', 'a.', 'Essential to completeness; constituent, as a part;\n   pertaining to, or serving to form, an integer; integrant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Integral', 'a.', 'Of, pertaining to, or being, a whole number or undivided\n   quantity; not fractional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Integral', 'a.', 'Pertaining to, or proceeding by, integration; as, the\n   integral calculus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Integral', 'n.', 'A whole; an entire thing; a whole number; an individual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Integral', 'n.', 'An expression which, being differentiated, will produce a\n   given differential. See differential Differential, and Integration. Cf.\n   Fluent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Integrality', 'n.', 'Entireness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Integrally', 'adv.', 'In an integral manner; wholly; completely; also, by\n   integration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Integrant', 'a.', 'Making part of a whole; necessary to constitute an\n   entire thing; integral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Integrated', 'imp. & p. p.', 'of Integrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Integrating', 'p. pr. & vb. n.', 'of Integrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Integrate', 'v. t.', 'To form into one whole; to make entire; to complete;\n   to renew; to restore; to perfect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Integrate', 'v. t.', 'To indicate the whole of; to give the sum or total\n   of; as, an integrating anemometer, one that indicates or registers the\n   entire action of the wind in a given time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Integrate', 'v. t.', 'To subject to the operation of integration; to find\n   the integral of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Integration', 'n.', 'The act or process of making whole or entire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Integration', 'n.', 'The operation of finding the primitive function which\n   has a given function for its differential coefficient. See Integral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Integration', 'n.', 'In the theory of evolution: The process by which the\n   manifold is compacted into the relatively simple and permanent. It is\n   supposed to alternate with differentiation as an agent in development.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Integrator', 'n.', 'That which integrates; esp., an instrument by means of\n   which the area of a figure can be measured directly, or its moment of\n   inertia, or statical moment, etc., be determined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Integrity', 'n.', 'The state or quality of being entire or complete;\n   wholeness; entireness; unbroken state; as, the integrity of an empire\n   or territory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Integrity', 'n.', 'Moral soundness; honesty; freedom from corrupting\n   influence or motive; -- used especially with reference to the\n   fulfillment of contracts, the discharge of agencies, trusts, and the\n   like; uprightness; rectitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Integrity', 'n.', 'Unimpaired, unadulterated, or genuine state; entire\n   correspondence with an original condition; purity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Integropallial', 'a.', 'Having the pallial line entire, or without a sinus,\n   as certain bivalve shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Integumation', 'n.', 'That part of physiology which treats of the\n   integuments of animals and plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Integument', 'n.', 'That which naturally invests or covers another thing,\n   as the testa or the tegmen of a seed; specifically (Anat.), a covering\n   which invests the body, as the skin, or a membrane that invests a\n   particular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Integumentary', 'n.', 'Belonging to, or composed of, integuments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Integumentation', 'n.', 'The act or process of covering with integuments;\n   the state or manner of being thus covered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intellect', 'n.', 'The part or faculty of the human soul by which it knows,\n   as distinguished from the power to feel and to will; sometimes, the\n   capacity for higher forms of knowledge, as distinguished from the power\n   to perceive objects in their relations; the power to judge and\n   comprehend; the thinking faculty; the understanding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intellected', 'a.', 'Endowed with intellect; having intellectual powers or\n   capacities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intellection', 'n.', 'A mental act or process; especially: (a) The act of\n   understanding; simple apprehension of ideas; intuition. Bentley. (b) A\n   creation of the mind itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intellective', 'a.', 'Pertaining to, or produced by, the intellect or\n   understanding; intellectual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intellective', 'a.', 'Having power to understand, know, or comprehend;\n   intelligent; rational.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intellective', 'a.', 'Capable of being perceived by the understanding only,\n   not by the senses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intellectively', 'adv.', 'In an intellective manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intellectual', 'a.', 'Belonging to, or performed by, the intellect; mental;\n   as, intellectual powers, activities, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intellectual', 'a.', 'Endowed with intellect; having the power of\n   understanding; having capacity for the higher forms of knowledge or\n   thought; characterized by intelligence or mental capacity; as, an\n   intellectual person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intellectual', 'a.', 'Suitable for exercising the intellect; formed by, and\n   existing for, the intellect alone; perceived by the intellect; as,\n   intellectual employments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intellectual', 'a.', 'Relating to the understanding; treating of the mind;\n   as, intellectual philosophy, sometimes called \"mental\" philosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intellectual', 'n.', 'The intellect or understanding; mental powers or\n   faculties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intellectualism', 'n.', 'Intellectual power; intellectuality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intellectualism', 'n.', 'The doctrine that knowledge is derived from pure\n   reason.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intellectualist', 'n.', 'One who overrates the importance of the\n   understanding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intellectualist', 'n.', 'One who accepts the doctrine of intellectualism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intellectuality', 'n.', 'Intellectual powers; possession of intellect;\n   quality of being intellectual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intellectualize', 'v. t.', 'To treat in an intellectual manner; to discuss\n   intellectually; to reduce to intellectual form; to express\n   intellectually; to idealize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intellectualize', 'v. t.', 'To endow with intellect; to bestow intellectual\n   qualities upon; to cause to become intellectual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intellectually', 'adv.', 'In an intellectual manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intelligence', 'n.', 'The act or state of knowing; the exercise of the\n   understanding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intelligence', 'n.', 'The capacity to know or understand; readiness of\n   comprehension; the intellect, as a gift or an endowment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intelligence', 'n.', 'Information communicated; news; notice; advice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intelligence', 'n.', 'Acquaintance; intercourse; familiarity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intelligence', 'n.', 'Knowledge imparted or acquired, whether by study,\n   research, or experience; general information.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intelligence', 'n.', 'An intelligent being or spirit; -- generally applied\n   to pure spirits; as, a created intelligence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intelligencer', 'n.', 'One who, or that which, sends or conveys\n   intelligence or news; a messenger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intelligencing', 'a.', 'Informing; giving information; talebearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intelligency', 'n.', 'Intelligence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intelligent', 'a.', 'Endowed with the faculty of understanding or reason;\n   as, man is an intelligent being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intelligent', 'a.', 'Possessed of intelligence, education, or judgment;\n   knowing; sensible; skilled; marked by intelligence; as, an intelligent\n   young man; an intelligent architect; an intelligent answer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intelligent', 'a.', 'Gognizant; aware; communicate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intelligential', 'a.', 'Of or pertaining to the intelligence; exercising or\n   implying understanding; intellectual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intelligential', 'a.', 'Consisting of unembodied mind; incorporeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intelligentiary', 'n.', 'One who gives information; an intelligencer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intelligently', 'adv.', 'In an intelligent manner; with intelligence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intelligibility', '', 'The quality or state of being intelligible;\n   clearness; perspicuity; definiteness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intelligible', '', 'Capable of being understood or comprehended; as, an\n   intelligible account or description; intelligible pronunciation,\n   writing, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intelligibleness', 'n.', 'The quality or state of being intelligible;\n   intelligibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intelligibly', 'adv.', 'In an intelligible manner; so as to be understood;\n   clearly; plainly; as, to write or speak intelligibly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intemerate', 'a.', 'Alt. of Intemerated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intemerated', 'a.', 'Pure; undefiled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intemerateness', 'n.', 'The state of being unpolluted; purity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intemerament', 'n.', 'A bad state; as, the intemperament of an ulcerated\n   part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intemperance', 'n.', 'The act of becoming, or state of being, intemperate;\n   excess in any kind of action or indulgence; any immoderate indulgence\n   of the appetites or passions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intemperance', 'n.', 'Specifically: Habitual or excessive indulgence in\n   alcoholic liquors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intemperancy', 'n.', 'Intemperance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intemperant', 'a.', 'Intemperate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intemperate', 'a.', 'Indulging any appetite or passion to excess;\n   immoderate to enjoyments or exertion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intemperate', 'a.', 'Specifically, addicted to an excessive or habitual use\n   of alcoholic liquors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intemperate', 'a.', 'Excessive; ungovernable; inordinate; violent;\n   immoderate; as, intemperate language, zeal, etc.; intemperate weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intemperate', 'v. t.', 'To disorder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intemperately', 'adv.', 'In an intemperate manner; immoderately;\n   excessively; without restraint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intemperateness', 'n.', 'The state of being intemperate; excessive\n   indulgence of any appetite or passion; as, intemperateness in eating or\n   drinking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intemperateness', 'n.', 'Severity of weather; inclemency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intemperature', 'n.', 'Intemperateness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intempestive', 'a.', 'Out of season; untimely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intempestively', 'adv.', 'Unseasonably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intempestivity', 'n.', 'Unseasonableness; untimeliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intenable', 'a.', 'Incapable of being held; untenable; not defensible; as,\n   an intenable opinion; an intenable fortress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intended', 'imp. & p. p.', 'of Intend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intending', 'p. pr. & vb. n.', 'of Intend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intend', 'v. t.', 'To stretch to extend; to distend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intend', 'v. t.', 'To strain; to make tense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intend', 'v. t.', 'To intensify; to strengthen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intend', 'v. t.', 'To apply with energy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intend', 'v. t.', 'To bend or turn; to direct, as ones course or journey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intend', 'v. t.', 'To fix the mind on; to attend to; to take care of; to\n   superintend; to regard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intend', 'v. t.', 'To fix the mind upon (something to be accomplished); to\n   be intent upon; to mean; to design; to plan; to purpose; -- often\n   followed by an infinitely with to, or a dependent clause with that; as,\n   he intends to go; he intends that she shall remain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intend', 'v. t.', 'To design mechanically or artistically; to fashion; to\n   mold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intend', 'v. t.', 'To pretend; to counterfeit; to simulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intendancies', 'pl. ', 'of Intendancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intendancy', 'n.', 'The office or employment of an intendant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intendancy', 'n.', 'A territorial district committed to the charge of an\n   intendant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intendant', 'n.', 'One who has the charge, direction, or management of some\n   public business; a superintendent; as, an intendant of marine; an\n   intendant of finance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intendant', 'a.', 'Attentive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intended', 'a.', 'Made tense; stretched out; extended; forcible; violent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intended', 'a.', 'Purposed; designed; as, intended harm or help.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intended', 'a.', 'Betrothed; affianced; as, an intended husband.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intended', 'n.', 'One with whom marriage is designed; one who is betrothed;\n   an affianced lover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intendedly', 'adv.', 'Intentionally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intendent', 'n.', 'See Intendant, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intender', 'n.', 'One who intends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intendiment', 'n.', 'Attention; consideration; knowledge; understanding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intendment', 'n.', 'Charge; oversight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intendment', 'n.', 'Intention; design; purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intendment', 'n.', 'The true meaning, understanding, or intention of a law,\n   or of any legal instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intenerated', 'imp. & p. p.', 'of Intenerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intenerating', 'p. pr. & vb. n.', 'of Intenerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intenerate', 'a.', 'To make tender or sensitive; to soften.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intenerate', 'a.', 'Made tender or soft; softened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inteneration', 'n.', 'The act or process of intenerating, or the state of\n   being intenerated; softening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intenible', 'a.', 'Incapable of holding or containing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intensated', 'imp. & p. p.', 'of Intensate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intensating', 'p. pr. & vb. n.', 'of Intensate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intensate', 'v. t.', 'To intensify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intensation', 'n.', 'The act or process of intensifying; intensification;\n   climax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intensative', 'a.', 'Adding intensity; intensifying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intense', 'a.', 'Strained; tightly drawn; kept on the stretch; strict; very\n   close or earnest; as, intense study or application; intense thought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intense', 'a.', 'Extreme in degree; excessive; immoderate; as: (a) Ardent;\n   fervent; as, intense heat. (b) Keen; biting; as, intense cold. (c)\n   Vehement; earnest; exceedingly strong; as, intense passion or hate. (d)\n   Very severe; violent; as, intense pain or anguish. (e) Deep; strong;\n   brilliant; as, intense color or light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intensely', 'adv.', 'Intently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intensely', 'adv.', 'To an extreme degree; as, weather intensely cold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intenseness', 'n.', 'The state or quality of being intense; intensity; as,\n   the intenseness of heat or cold; the intenseness of study or thought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intensification', 'n.', 'The act or process of intensifying, or of making\n   more intense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intensifier', 'n.', 'One who or that which intensifies or strengthens; in\n   photography, an agent used to intensify the lights or shadows of a\n   picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intensified', 'imp. & p. p.', 'of Intensify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intensifying', 'p. pr. & vb. n.', 'of Intensify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intensify', 'v. t.', 'To render more intense; as, to intensify heat or\n   cold; to intensify colors; to intensify a photographic negative; to\n   intensify animosity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intensify', 'v. i.', 'To become intense, or more intense; to act with\n   increasing power or energy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intension', 'n.', 'A straining, stretching, or bending; the state of being\n   strained; as, the intension of a musical string.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intension', 'n.', 'Increase of power or energy of any quality or thing;\n   intenseness; fervency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intension', 'n.', 'The collective attributes, qualities, or marks that make\n   up a complex general notion; the comprehension, content, or\n   connotation; -- opposed to extension, extent, or sphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intensitive', 'a.', 'Increasing the force or intensity of; intensive; as,\n   the intensitive words of a sentence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intensity', 'n.', 'The state or quality of being intense; intenseness;\n   extreme degree; as, intensity of heat, cold, mental application,\n   passion, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intensity', 'n.', 'The amount or degree of energy with which a force\n   operates or a cause acts; effectiveness, as estimated by results\n   produced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intensity', 'n.', 'The magnitude of a distributed force, as pressure,\n   stress, weight, etc., per unit of surface, or of volume, as the case\n   may be; as, the measure of the intensity of a total stress of forty\n   pounds which is distributed uniformly over a surface of four square\n   inches area is ten pounds per square inch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intensity', 'n.', 'The degree or depth of shade in a picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intensive', 'a.', 'Stretched; admitting of intension, or increase of\n   degree; that can be intensified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intensive', 'a.', 'Characterized by persistence; intent; unremitted;\n   assiduous; intense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intensive', 'a.', 'Serving to give force or emphasis; as, an intensive verb\n   or preposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intensive', 'n.', 'That which intensifies or emphasizes; an intensive verb\n   or word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intensively', 'adv.', 'In an intensive manner; by increase of degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intensiveness', 'n.', 'The quality or state of being intensive; intensity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intent', 'a.', 'Closely directed; strictly attentive; bent; -- said of the\n   mind, thoughts, etc.; as, a mind intent on self-improvement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intent', 'a.', 'Having the mind closely directed to or bent on an object;\n   sedulous; eager in pursuit of an object; -- formerly with to, but now\n   with on; as, intent on business or pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intent', 'n.', 'The act of turning the mind toward an object; hence, a\n   design; a purpose; intention; meaning; drift; aim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intentation', 'n.', 'Intention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intention', 'n.', 'A stretching or bending of the mind toward of the mind\n   toward an object; closeness of application; fixedness of attention;\n   earnestness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intention', 'n.', 'A determination to act in a certain way or to do a\n   certain thing; purpose; design; as, an intention to go to New York.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intention', 'n.', 'The object toward which the thoughts are directed; end;\n   aim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intention', 'n.', 'The state of being strained. See Intension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intention', 'n.', 'Any mental apprehension of an object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intentional', 'a.', 'Done by intention or design; intended; designed; as,\n   the act was intentional, not accidental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intentionality', 'n.', 'The quality or state of being intentional; purpose;\n   design.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intentionally', 'adv.', 'In an intentional manner; with intention; by\n   design; of purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intentioned', 'a.', 'Having designs; -- chiefly used in composition; as,\n   well-intentioned, having good designs; ill-intentioned, having ill\n   designs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intentive', 'n.', 'Attentive; intent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intentively', 'adv.', 'Attentively; closely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intentiveness', 'n.', 'Closeness of attention or application of mind;\n   attentiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intently', 'adv.', 'In an intent manner; as, the eyes intently fixed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intentness', 'n.', 'The state or quality of being intent; close\n   application; attention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inter-', '', 'A prefix signifying among, between, amid; as, interact,\n   interarticular, intermit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interred', 'imp. & p. p.', 'of Inter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interring', 'p. pr. & vb. n.', 'of Inter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inter', 'v. t.', 'To deposit and cover in the earth; to bury; to inhume;\n   as, to inter a dead body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interact', 'n.', 'A short act or piece between others, as in a play; an\n   interlude; hence, intermediate employment or time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interact', 'v. i.', 'To act upon each other; as, two agents mutually\n   interact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interaction', 'n.', 'Intermediate action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interaction', 'n.', 'Mutual or reciprocal action or influence; as, the\n   interaction of the heart and lungs on each other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interadditive', 'a.', 'Added or placed between the parts of another thing,\n   as a clause inserted parenthetically in a sentence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interagency', 'n.', 'Intermediate agency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interagent', 'n.', 'An intermediate agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interall', 'n.', 'Entrail or inside.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interalveolar', 'a.', 'Between alveoli; as, the interalveolar septa between\n   adjacent air cells in the lungs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interambulacral', 'a.', 'Of or pertaining to the interambulacra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interambulacra', 'pl. ', 'of Interambulacrum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interambulacrums', 'pl. ', 'of Interambulacrum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interambulacrum', 'n.', 'In echinoderms, one of the areas or zones\n   intervening between two ambulacra. See Illust. of Ambulacrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interamnian', 'a.', 'Situated between rivers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interanimate', 'v. t.', 'To animate or inspire mutually.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interarboration', 'n.', 'The interweaving of branches of trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interarticular', 'a.', 'Situated between joints or articulations; as,\n   interarticular cartilages and ligaments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interatomic', 'a.', 'Between atoms; situated, or acting, between the atoms\n   of bodies; as, interatomic forces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interaulic', 'a.', 'Existing between royal courts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interauricular', 'a.', 'Between the auricles; as, the interauricular\n   partition of the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interaxal', 'a.', 'Situated in an interaxis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interaxillary', 'a.', 'Situated within or between the axils of leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interaxes', 'pl. ', 'of Interaxis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interaxis', 'n.', 'The space between two axes. See Axis, 6.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interbastation', 'n.', 'Patchwork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interbrachial', 'a.', 'Between the arms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interbrain', 'n.', 'See Thalamencephalon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interbranchial', 'a.', 'Between the branchiae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interbreed', 'v. t. & i.', 'To breed by crossing different stocks of\n   animals or plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercalar', 'a.', 'Intercalary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercalary', 'a.', 'Inserted or introduced among others in the calendar;\n   as, an intercalary month, day, etc.; -- now applied particularly to the\n   odd day (Feb. 29) inserted in the calendar of leap year. See\n   Bissextile, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercalary', 'n.', 'Introduced or inserted among others; additional;\n   supernumerary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercalated', 'imp. & p. p.', 'of Intercalate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercalating', 'p. pr. & vb. n.', 'of Intercalate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercalate', 'v. t.', 'To insert, as a day or other portion of time, in a\n   calendar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercalate', 'v. t.', 'To insert among others, as a verse in a stanza;\n   specif. (Geol.), to introduce as a bed or stratum, between the layers\n   of a regular series of rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercalation', 'n.', 'The insertion of a day, or other portion of time, in\n   a calendar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercalation', 'n.', 'The insertion or introduction of anything among\n   others, as the insertion of a phrase, line, or verse in a metrical\n   composition; specif. (Geol.), the intrusion of a bed or layer between\n   other layers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercarotid', 'a.', 'Situated between the external and internal carotid\n   arteries; as, an intercarotid ganglion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercarpal', 'a.', 'Between the carpal bone; as, intercarpal\n   articulations, ligaments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercartilaginous', 'a.', 'Within cartilage; endochondral; as,\n   intercartilaginous ossification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercavernous', 'a.', 'Between the cavernous sinuses; as, the\n   intercavernous sinuses connecting the cavernous sinuses at the base of\n   the brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interceded', 'imp. & p. p.', 'of Intercede');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interceding', 'p. pr. & vb. n.', 'of Intercede');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercede', 'v. i.', 'To pass between; to intervene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercede', 'v. i.', 'To act between parties with a view to reconcile\n   differences; to make intercession; to beg or plead in behalf of\n   another; to mediate; -- usually followed by with and for; as, I will\n   intercede with him for you.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercede', 'v. t.', 'To be, to come, or to pass, between; to separate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercedence', 'n.', 'The act of interceding; intercession; intervention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercedent', 'a.', 'Passing between; mediating; pleading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interceder', 'n.', 'One who intercedes; an intercessor; a mediator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercellular', 'a.', 'Lying between cells or cellules; as, intercellular\n   substance, space, or fluids; intercellular blood channels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercentral', 'a.', 'Between centers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercentra', 'pl. ', 'of Intercentrum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercentrum', 'n.', 'The median of the three elements composing the centra\n   of the vertebrae in some fossil batrachians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercepted', 'imp. & p. p.', 'of Intercept');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercepting', 'p. pr. & vb. n.', 'of Intercept');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercept', 'v. t.', 'To take or seize by the way, or before arrival at the\n   destined place; to cause to stop on the passage; as, to intercept a\n   letter; a telegram will intercept him at Paris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercept', 'v. t.', 'To obstruct or interrupt the progress of; to stop; to\n   hinder or oppose; as, to intercept the current of a river.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercept', 'v. t.', 'To interrupt communication with, or progress toward;\n   to cut off, as the destination; to blockade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercept', 'v. t.', 'To include between; as, that part of the line which\n   is intercepted between the points A and B.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercept', 'n.', 'A part cut off or intercepted, as a portion of a line\n   included between two points, or cut off two straight lines or curves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercepter', 'n.', 'One who, or that which, intercepts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interception', 'n.', 'The act of intercepting; as, interception of a\n   letter; interception of the enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interceptive', 'a.', 'Intercepting or tending to intercept.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercession', 'n.', 'The act of interceding; mediation; interposition\n   between parties at variance, with a view to reconcilation; prayer,\n   petition, or entreaty in favor of, or (less often) against, another or\n   others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercessional', 'a.', 'Pertaining to, of the nature of, or characterized\n   by, intercession or entreaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercessionate', 'v. t.', 'To entreat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercessor', 'n.', 'One who goes between, or intercedes; a mediator. (a)\n   One who interposes between parties at variance, with a view to\n   reconcile them. (b) One who pleads in behalf of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercessor', 'n.', 'A bishop, who, during a vacancy of the see,\n   administers the bishopric till a successor is installed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercessorial', 'a.', 'Intercessory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercessory', 'a.', 'Pertaining to, of the nature of, or characterized by,\n   intercession; interceding; as, intercessory prayer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interchained', 'imp. & p. p.', 'of Interchain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interchaining', 'p. pr. & vb. n.', 'of Interchain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interchain', 'v. t.', 'To link together; to unite closely or firmly, as by\n   a chain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interchanged', 'imp. & p. p.', 'of Interchange');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interchanging', 'p. pr. & vb. n.', 'of Interchange');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interchange', 'v. t.', 'To put each in the place of the other; to give and\n   take mutually; to exchange; to reciprocate; as, to interchange places;\n   they interchanged friendly offices and services.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interchange', 'v. t.', 'To cause to follow alternately; to intermingle; to\n   vary; as, to interchange cares with pleasures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interchange', 'v. i.', 'To make an interchange; to alternate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interchange', 'n.', 'The act of mutually changing; the act of mutually\n   giving and receiving; exchange; as, the interchange of civilities\n   between two persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interchange', 'n.', 'The mutual exchange of commodities between two persons\n   or countries; barter; commerce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interchange', 'n.', 'Alternate succession; alternation; a mingling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interchangeability', 'n.', 'The state or quality of being interchangeable;\n   interchangeableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interchangeable', 'a.', 'Admitting of exchange or mutual substitution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interchangeable', 'a.', 'Following each other in alternate succession; as,\n   the four interchangeable seasons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interchangement', 'n.', 'Mutual transfer; exchange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interchapter', 'n.', 'An intervening or inserted chapter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercidence', 'n.', 'The act or state of coming or falling between;\n   occurrence; incident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercident', 'a.', 'Falling or coming between; happening accidentally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercipient', 'a.', 'Intercepting; stopping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercipient', 'n.', 'One who, or that which, intercepts or stops anything\n   on the passage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inrecision', 'n.', 'A cutting off, through, or asunder; interruption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercitizenship', 'n.', 'The mutual right to civic privileges, in the\n   different States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interclavicle', 'n.', 'See Episternum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interclavicular', 'a.', 'Between the clavicles; as, the interclavicular\n   notch of the sternum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interclavicular', 'a.', 'Of or pertaining to the interclavicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interclose', 'v. t.', 'To shut in; to inclose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercloud', 'v. t.', 'To cloud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercluded', 'imp. & p. p.', 'of Interclude');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercluding', 'p. pr. & vb. n.', 'of Interclude');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interclude', 'v. t.', 'To shut off or out from a place or course, by\n   something intervening; to intercept; to cut off; to interrupt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interclusion', 'n.', 'Interception; a stopping / obstruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercollegiate', 'a.', 'Existing or carried on between colleges or\n   universities; as, intercollegiate relations, rivalry, games, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercolline', 'a.', 'Situated between hills; -- applied especially to\n   valleys lying between volcanic cones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercolonial', 'a.', 'Between or among colonies; pertaining to the\n   intercourse or mutual relations of colonies; as, intercolonial trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercolumnar', 'a.', 'Between columns or pillars; as, the intercolumnar\n   fibers of Pouparts ligament; an intercolumnar statue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercolumniation', 'n.', 'The clear space between two columns, measured at\n   the bottom of their shafts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercombat', 'n.', 'Combat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercoming', 'n.', 'The act of coming between; intervention; interference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercommoned', 'imp. & p. p.', 'of Intercommon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercommoning', 'p. pr. & vb. n.', 'of Intercommon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercommon', 'v. t.', 'To share with others; to participate; especially,\n   to eat at the same table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercommon', 'v. t.', 'To graze cattle promiscuously in the commons of\n   each other, as the inhabitants of adjoining townships, manors, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercommonage', 'n.', 'The right or privilege of intercommoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercommuned', 'imp. & p. p.', 'of Intercommune');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercommuning', 'p. pr. & vb. n.', 'of Intercommune');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercommune', 'v. i.', 'To intercommunicate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercommune', 'v. i.', 'To have mutual communication or intercourse by\n   conservation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercommunicable', 'a.', 'Capable of being mutually communicated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercommunicate', 'v. i.', 'To communicate mutually; to hold mutual\n   communication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercommunicate', 'v. t.', 'To communicate mutually; to interchange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercommunication', 'n.', 'Mutual communication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercommunion', 'n.', 'Mutual communion; as, an intercommunion of deities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercommunity', 'n.', 'Intercommunication; community of possessions,\n   religion, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercomparison', 'n.', 'Mutual comparison of corresponding parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercondylar', 'a.', 'Alt. of Intercondyloid');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercondyloid', 'a.', 'Between condyles; as, the intercondylar fossa or\n   notch of the femur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interconnect', 'v. t.', 'To join together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interconnection', 'n.', 'Connection between; mutual connection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercontinental', 'a.', 'Between or among continents; subsisting or\n   carried on between continents; as, intercontinental relations or\n   commerce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interconvertible', 'a.', 'Convertible the one into the other; as, coin and\n   bank notes are interconvertible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercostal', 'a.', 'Between the ribs; pertaining to, or produced by, the\n   parts between the ribs; as, intercostal respiration, in which the chest\n   is alternately enlarged and contracted by the intercostal muscles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercourse', 'n.', 'A commingling; intimate connection or dealings between\n   persons or nations, as in common affairs and civilities, in\n   correspondence or trade; communication; commerce; especially,\n   interchange of thought and feeling; association; communion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercrossed', 'imp. & p. p.', 'of Intercross');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercrossing', 'p. pr. & vb. n.', 'of Intercross');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercross', 'v. t. & i.', 'To cross each other, as lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercross', 'v. t. & i.', 'To fertilize by the impregnation of one species\n   or variety by another; to impregnate by a different species or variety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercross', 'n.', 'The process or result of cross fertilization between\n   different kinds of animals, or different varieties of plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercrural', 'a.', 'Between crura; -- applied especially to the\n   interneural plates in the vertebral column of many cartilaginous\n   fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercur', 'v. i.', 'To intervene; to come or occur in the meantime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercurrence', 'n.', 'A passing or running between; occurrence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercurrent', 'a.', 'Running between or among; intervening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercurrent', 'a.', 'Not belonging to any particular season.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercurrent', 'a.', 'Said of diseases occurring in the course of another\n   disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercurrent', 'n.', 'Something intervening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intercutaneous', 'a.', 'Subcutaneous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interdashed', 'imp. & p. p.', 'of Interdash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interdashing', 'p. pr. & vb. n.', 'of Interdash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interdash', 'v. t.', 'To dash between or among; to intersperse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interdeal', 'v. i.', 'To intrigue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interdental', 'a.', 'Situated between teeth; as, an interdental space, the\n   space between two teeth in a gear wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interdental', 'a.', 'Formed between the upper and lower teeth; as,\n   interdental consonants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interdentil', 'n.', 'The space between two dentils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interdependence', 'n.', 'Mutual dependence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interdependency', 'n.', 'Mutual dependence; as, interdependency of\n   interests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interdependent', 'a.', 'Mutually dependent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interdicted', 'imp. & p. p.', 'of Interdict');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interdicting', 'p. pr. & vb. n.', 'of Interdict');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interdict', 'n.', 'To forbid; to prohibit or debar; as, to interdict\n   intercourse with foreign nations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interdict', 'n.', 'To lay under an interdict; to cut off from the enjoyment\n   of religious privileges, as a city, a church, an individual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interdict', 'n.', 'A prohibitory order or decree; a prohibition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interdict', 'n.', 'A prohibition of the pope, by which the clergy or laymen\n   are restrained from performing, or from attending, divine service, or\n   from administering the offices or enjoying the privileges of the\n   church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interdict', 'n.', 'An order of the court of session, having the like\n   purpose and effect with a writ of injunction out of chancery in England\n   and America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interdiction', 'n.', 'The act of interdicting; prohibition; prohibiting\n   decree; curse; interdict.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interdictive', 'a.', 'Having the power to prohibit; as, an interdictive\n   sentence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interdictory', 'a.', 'Belonging to an interdiction; prohibitory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interdigital', 'a.', 'Between the fingers or toes; as, interdigital space.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interdigitate', 'v. t.', 'To interweave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interdigitate', 'v. i.', 'To interlock, as the fingers of two hands that\n   are joined; to be interwoven; to commingle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interdigitation', 'n.', 'The state of interdigitating; interdigital space.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interdome', 'n.', 'The open space between the inner and outer shells of a\n   dome or cupola of masonry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interduce', 'n.', 'An intertie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interepimeral', 'a.', 'Between the epimeral plates of insects and\n   crustaceans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interequinoctial', 'a.', 'Coming between the equinoxes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interess', 'v. t.', 'To interest or affect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interesse', 'n.', 'Interest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interested', 'imp. & p. p.', 'of Interest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interesting', 'p. pr. & vb. n.', 'of Interest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interest', 'n.', 'To engage the attention of; to awaken interest in; to\n   excite emotion or passion in, in behalf of a person or thing; as, the\n   subject did not interest him; to interest one in charitable work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interest', 'n.', 'To be concerned with or engaged in; to affect; to\n   concern; to excite; -- often used impersonally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interest', 'n.', 'To cause or permit to share.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interest', 'n.', 'Excitement of feeling, whether pleasant or painful,\n   accompanying special attention to some object; concern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interest', 'n.', 'Participation in advantage, profit, and responsibility;\n   share; portion; part; as, an interest in a brewery; he has parted with\n   his interest in the stocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interest', 'n.', 'Advantage, personal or general; good, regarded as a\n   selfish benefit; profit; benefit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interest', 'n.', 'Premium paid for the use of money, -- usually reckoned as\n   a percentage; as, interest at five per cent per annum on ten thousand\n   dollars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interest', 'n.', 'Any excess of advantage over and above an exact\n   equivalent for what is given or rendered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interest', 'n.', 'The persons interested in any particular business or\n   measure, taken collectively; as, the iron interest; the cotton\n   interest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interested', 'v. t.', 'Having the attention engaged; having emotion or\n   passion excited; as, an interested listener.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interested', 'v. t.', 'Having an interest; concerned in a cause or in\n   consequences; liable to be affected or prejudiced; as, an interested\n   witness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interestedness', 'n.', 'The state or quality of being interested;\n   selfishness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interesting', 'a.', 'Engaging the attention; exciting, or adapted to\n   excite, interest, curiosity, or emotion; as, an interesting story;\n   interesting news.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interestingly', 'adv.', 'In an interesting manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interestingness', 'n.', 'The condition or quality of being interesting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interfacial', 'a.', 'Included between two plane surfaces or faces; as, an\n   interfacial angle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interfascicular', 'a.', 'Between fascicles or bundles; as, the\n   interfascicular spaces of connective tissue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interferant', 'n.', 'One of the contestants in interference before the\n   Patent Office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interfered', 'imp. & p. p.', 'of Interfere');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interfering', 'p. pr. & vb. n.', 'of Interfere');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interfere', 'v. i.', 'To come in collision; to be in opposition; to clash;\n   as, interfering claims, or commands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interfere', 'v. i.', 'To enter into, or take a part in, the concerns of\n   others; to intermeddle; to interpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interfere', 'v. i.', 'To strike one foot against the opposite foot or ankle\n   in using the legs; -- sometimes said of a human being, but usually of a\n   horse; as, the horse interferes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interfere', 'v. i.', 'To act reciprocally, so as to augment, diminish, or\n   otherwise affect one another; -- said of waves, rays of light, heat,\n   etc. See Interference, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interfere', 'v. i.', 'To cover the same ground; to claim the same\n   invention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interference', 'n.', 'The act or state of interfering; as, the stoppage of\n   a machine by the interference of some of its parts; a meddlesome\n   interference in the business of others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interference', 'n.', 'The mutual influence, under certain conditions, of\n   two streams of light, or series of pulsations of sound, or, generally,\n   two waves or vibrations of any kind, producing certain characteristic\n   phenomena, as colored fringes, dark bands, or darkness, in the case of\n   light, silence or increased intensity in sounds; neutralization or\n   superposition of waves generally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interference', 'n.', 'The act or state of interfering, or of claiming a\n   right to the same invention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interferer', 'n.', 'One who interferes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interferingly', 'adv.', 'By or with interference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interflow', 'v. i.', 'To flow in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interfluent', 'a.', 'Alt. of Interfluous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interfluous', 'a.', 'Flowing between or among; intervening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interfolded', 'p. a.', 'Intertwined; interlocked; clasped together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interfoliaceous', 'a.', 'At the same node with opposite or whorled leaves,\n   but occupying a position between their places of attachment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interfoliate', 'v. t.', 'To interleave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interfollicular', 'a.', 'Between follicles; as, the interfollicular septa\n   in a lymphatic gland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interfretted', 'a.', 'Interlaced; linked together; -- said of charges or\n   bearings. See Fretted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interfulgent', 'a.', 'Shining between.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interfuse', 'v. t.', 'To pour or spread between or among; to diffuse; to\n   scatter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interfuse', 'v. t.', 'To spread through; to permeate; to pervade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interfuse', 'v. t.', 'To mix up together; to associate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interfusion', 'n.', 'The act of interfusing, or the state of being\n   interfused.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interganglionic', 'a.', 'Between and uniting the nervous ganglions; as,\n   interganglionic cords.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interglobular', 'a.', 'Between globules; -- applied esp. to certain small\n   spaces, surrounded by minute globules, in dentine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intergraved', 'imp.', 'of Intergrave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intergraved', 'p. p.', 'of Intergrave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intergraven', '', 'of Intergrave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intergraving', 'p. pr. & vb. n.', 'of Intergrave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intergrave', 'v. t.', 'To grave or carve between; to engrave in the\n   alternate sections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interhemal', 'a.', 'Alt. of Interhaemal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interhaemal', 'a.', 'Between the hemal arches or hemal spines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interhaemal', 'n.', 'An interhemal spine or cartilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interhyal', 'a.', 'Of or pertaining to a segment sometimes present at the\n   proximal end of the hyoidean arch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interhyal', 'n.', 'An interhyal ligament or cartilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interim', 'n.', 'The meantime; time intervening; interval between events,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interim', 'n.', 'A name given to each of three compromises made by the\n   emperor Charles V. of Germany for the sake of harmonizing the\n   connecting opinions of Protestants and Catholics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interior', 'a.', 'Being within any limits, inclosure, or substance; inside;\n   internal; inner; -- opposed to exterior, or superficial; as, the\n   interior apartments of a house; the interior surface of a hollow ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interior', 'a.', 'Remote from the limits, frontier, or shore; inland; as,\n   the interior parts of a region or country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interior', 'n.', 'That which is within; the internal or inner part of a\n   thing; the inside.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interior', 'n.', 'The inland part of a country, state, or kingdom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interiority', 'n.', 'State of being interior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interiorly', 'adv.', 'Internally; inwardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interjacence', 'n.', 'Alt. of Interjacency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interjacency', 'n.', 'The state of being between; a coming or lying between\n   or among; intervention; also, that which lies between.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interjacent', 'a.', 'Lying or being between or among; intervening; as,\n   interjacent isles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interjaculate', 'v. t.', 'To ejaculate parenthetically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interjangle', 'v. i.', 'To make a dissonant, discordant noise one with\n   another; to talk or chatter noisily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interjected', 'imp. & p. p.', 'of Interject');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interjecting', 'p. pr. & vb. n.', 'of Interject');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interject', 'v. t.', 'To throw in between; to insert; to interpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interject', 'v. i.', 'To throw ones self between or among; to come\n   between; to interpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interjection', 'n.', 'The act of interjecting or throwing between; also,\n   that which is interjected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interjection', 'n.', 'A word or form of speech thrown in to express emotion\n   or feeling, as O! Alas! Ha ha! Begone! etc. Compare Exclamation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interjectional', 'a.', 'Thrown in between other words or phrases;\n   parenthetical; ejaculatory; as, an interjectional remark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interjectional', 'a.', 'Pertaining to, or having the nature of, an\n   interjection; consisting of natural and spontaneous exclamations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interjectionalize', 'v. t.', 'To convert into, or to use as, an\n   interjection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interjectionally', 'adv.', 'In an interjectional manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interjectionary', 'a.', 'Interjectional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interjoined', 'imp. & p. p.', 'of Interjoin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interjoining', 'p. pr. & vb. n.', 'of Interjoin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interjoin', 'v. t.', 'To join mutually; to unite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interjoist', 'n.', 'The space or interval between two joists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interjoist', 'n.', 'A middle joist or crossbeam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interjunction', 'n.', 'A mutual joining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interknit', 'v. t.', 'To knit together; to unite closely; to intertwine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interknow', 'v. t.', 'To know mutually.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interknowledge', 'n.', 'Mutual knowledge or acquaintance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlaced', 'imp. & p. p.', 'of Interlace');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlacing', 'p. pr. & vb. n.', 'of Interlace');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlace', 'v. t. & i.', 'To unite, as by lacing together; to insert or\n   interpose one thing within another; to intertwine; to interweave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlacement', 'n.', 'The act of interlacing, or the state of being\n   interlaced; also, that which is interlaced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlamellar', 'a.', 'Alt. of Interlaminar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlaminar', 'a.', 'Between lammellae or laminae; as, interlamellar\n   spaces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlaminated', 'a.', 'Placed between, or containing, laminae or plates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlamination', 'n.', 'The state of being interlaminated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlapse', 'n.', 'The lapse or interval of time between two events.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlarded', 'imp. & p. p.', 'of Interlard');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlarding', 'p. pr. & vb. n.', 'of Interlard');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlard', 'v. t.', 'To place lard or bacon amongst; to mix, as fat meat\n   with lean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlard', 'v. t.', 'Hence: To insert between; to mix or mingle;\n   especially, to introduce that which is foreign or irrelevant; as, to\n   interlard a conservation with oaths or allusions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlaid', 'imp. & p. p.', 'of Interlay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlaying', 'p. pr. & vb. n.', 'of Interlay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlay', 'v. t.', 'To lay or place among or between.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interleaves', 'pl. ', 'of Interleaf');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interleaf', 'n.', 'A leaf inserted between other leaves; a blank leaf\n   inserted, as in a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interleaved', 'imp. & p. p.', 'of Interleave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interleaving', 'p. pr. & vb. n.', 'of Interleave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interleave', 'v. t.', 'To insert a leaf or leaves in; to bind with blank\n   leaves inserted between the others; as, to interleave a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlibel', 'v. t.', 'To libel mutually.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlined', 'imp. & p. p.', 'of Interline');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlining', 'p. pr. & vb. n.', 'of Interline');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interline', 'v. t.', 'To write or insert between lines already written or\n   printed, as for correction or addition; to write or print something\n   between the lines of; as, to interline a page or a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interline', 'v. t.', 'To arrange in alternate lines; as, to interline Latin\n   and English.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interline', 'v. t.', 'To mark or imprint with lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlineal', 'a.', 'Alt. of Interlinear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlinear', 'a.', 'Contained between lines; written or inserted between\n   lines already written or printed; containing interlineations; as, an\n   interlinear manuscript, translation, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlineary', 'a.', 'Interlinear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlineary', 'n.', 'A book containing interlineations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlineation', 'n.', 'The act of interlining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlineation', 'n.', 'That which is interlined; a passage, word, or line\n   inserted between lines already written or printed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlining', 'n.', 'Correction or alteration by writing between the lines;\n   interlineation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlink', 'v. t.', 'To link together; to join, as one chain to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlink', 'n.', 'An intermediate or connecting link.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlobar', 'a.', 'Between lobes; as, the interlobar notch of the liver;\n   the interlobar ducts of a gland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlobular', 'a.', 'Between lobules; as, the interlobular branches of the\n   portal vein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlocation', 'n.', 'A placing or coming between; interposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlock', 'v. i.', 'To unite, embrace, communicate with, or flow into,\n   one another; to be connected in one system; to lock into one another;\n   to interlace firmly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlock', 'v. t.', 'To unite by locking or linking together; to secure in\n   place by mutual fastening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlocution', 'n.', 'Interchange of speech; dialogue; conversation;\n   conference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlocution', 'n.', 'An intermediate act or decree before final decision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlocution', 'n.', 'Hence, intermediate argument or discussion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlocutor', 'n.', 'One who takes part in dialogue or conversation; a\n   talker, interpreter, or questioner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlocutor', 'n.', 'An interlocutory judgment or sentence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlocutory', 'a.', 'Consisting of, or having the nature of, dialogue;\n   conversational.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlocutory', 'a.', 'Intermediate; not final or definitive; made or done\n   during the progress of an action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlocutory', 'n.', 'Interpolated discussion or dialogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlocutrice', 'n.', 'A female interlocutor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interloped', 'imp. & p. p.', 'of Interlope');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interloping', 'p. pr. & vb. n.', 'of Interlope');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlope', 'v. i.', 'To run between parties and intercept without right\n   the advantage that one should gain from the other; to traffic without a\n   proper license; to intrude; to forestall others; to intermeddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interloper', 'n.', 'One who interlopes; one who interlopes; one who\n   unlawfully intrudes upon a property, a station, or an office; one who\n   interferes wrongfully or officiously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlucate', 'v. t.', 'To let in light upon, as by cutting away branches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlucation', 'n.', 'Act of thinning a wood to let in light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlucent', 'a.', 'Shining between.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlude', 'n.', 'A short entertainment exhibited on the stage between the\n   acts of a play, or between the play and the afterpiece, to relieve the\n   tedium of waiting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlude', 'n.', 'A form of English drama or play, usually short, merry,\n   and farcical, which succeeded the Moralities or Moral Plays in the\n   transition to the romantic or Elizabethan drama.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlude', 'n.', 'A short piece of instrumental music played between the\n   parts of a song or cantata, or the acts of a drama; especially, in\n   church music, a short passage played by the organist between the\n   stanzas of a hymn, or in German chorals after each line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interluded', 'a.', 'Inserted in the manner of an interlude; having or\n   containing interludes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interluder', 'n.', 'An actor who performs in an interlude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interluency', 'n.', 'A flowing between; intervening water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlunar', 'a.', 'Alt. of Interlunary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interlunary', 'a.', 'Belonging or pertaining to the time when the moon, at\n   or near its conjunction with the sun, is invisible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermandibular', 'a.', 'Between the mandibles; interramal; as, the\n   intermandibular space.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermarriage', 'n.', 'Connection by marriage; reciprocal marriage; giving\n   and taking in marriage, as between two families, tribes, castes, or\n   nations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermarry', 'v. i.', 'To become connected by marriage between their\n   members; to give and take mutually in marriage; -- said of families,\n   ranks, castes, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermaxillae', 'pl. ', 'of Intermaxilla');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermaxilla', 'n.', 'See Premaxilla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermaxillary', 'a.', 'Between the maxillary bones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermaxillary', 'a.', 'Of or pertaining to the intermaxillae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermaxillary', 'n.', 'An intermaxilla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermean', 'n.', 'Something done in the meantime; interlude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermeation', 'n.', 'A flowing between.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermeddle', 'v. i.', 'To meddle with the affairs of others; to meddle\n   officiously; to interpose or interfere improperly; to mix or meddle\n   with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermeddle', 'v. t.', 'To intermix; to mingle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermeddler', 'n.', 'One who meddles with, or intrudes into, the affairs\n   of others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermeddlesome', 'a.', 'Inclined or disposed to intermeddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermeddling', 'n.', 'The act of improperly interfering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermede', 'n.', 'A short musical dramatic piece, of a light and pleasing,\n   sometimes a burlesque, character; an interlude introduced between the\n   acts of a play or an opera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermediacy', 'n.', 'Interposition; intervention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermediae', 'n. pl.', 'The middle pair of tail feathers, or middle\n   rectrices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermedial', 'a.', 'Lying between; intervening; intermediate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermedian', 'a.', 'Intermediate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermediary', 'a.', 'Lying, coming, or done, between; intermediate; as, an\n   intermediary project.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermediaries', 'pl. ', 'of Intermediary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermediary', 'n.', 'One who, or that which, is intermediate; an\n   interagent; a go-between.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermediate', 'a.', 'Lying or being in the middle place or degree, or\n   between two extremes; coming or done between; intervening; interposed;\n   interjacent; as, an intermediate space or time; intermediate colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermediate', 'v. i.', 'To come between; to intervene; to interpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermediately', 'adv.', 'In an intermediate manner; by way of\n   intervention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermediation', 'n.', 'The act of coming between; intervention;\n   interposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermediator', 'n.', 'A mediator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermedious', 'a.', 'Intermediate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermediums', 'pl. ', 'of Intermedium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermedia', 'pl. ', 'of Intermedium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermedium', 'n.', 'Intermediate space.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermedium', 'n.', 'An intervening agent or instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermedium', 'n.', 'The bone or cartilage between the radiale and ulnare\n   in the carpus, and between the tibiale and fibulare in the tarsus. It\n   corresponds to the lunar in the carpus, and to a part of the astragalus\n   in the tarsus of man and most mammals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermell', 'v. i. & t.', 'To intermeddle; to intermix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermembral', 'a.', 'Between members or limbs; as, intermembral homology,\n   the correspondence of the limbs with each other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermembranous', 'a.', 'Within or beneath a membrane; as, intermembranous\n   ossification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interment', 'v. t.', 'The act or ceremony of depositing a dead body in the\n   earth; burial; sepulture; inhumation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermention', 'v. t.', 'To mention among other things, or casually or\n   incidentally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermesenteric', 'a.', 'Within the mesentery; as, the intermesenteric, or\n   aortic, plexus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermetacarpal', 'a.', 'Between the metacarpal bones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermetatarsal', 'a.', 'Between the metatarsal bones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermezzo', 'n.', 'An interlude; an intermede. See Intermede.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermicate', 'v. i.', 'To flash or shine between or among.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermication', 'n.', 'A shining between or among.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermigration', 'n.', 'Reciprocal migration; interchange of dwelling place\n   by migration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interminable', 'a.', 'Without termination; admitting no limit; boundless;\n   endless; wearisomely protracted; as, interminable space or duration;\n   interminable sufferings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interminableness', 'n.', 'The state of being endless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interminably', 'adv.', 'Without end or limit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interminate', 'a.', 'Endless; as, interminate sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interminate', 'v. t.', 'To menace; to threaten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interminated', 'a.', 'Interminable; interminate; endless; unending.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermination', 'n.', 'A menace or threat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermine', 'v. t.', 'To intersect or penetrate with mines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermingle', 'v. t.', 'To mingle or mix together; to intermix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermingle', 'v. i.', 'To be mixed or incorporated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermise', 'n.', 'Interference; interposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermission', 'n.', 'The act or the state of intermitting; the state of\n   being neglected or disused; disuse; discontinuance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermission', 'n.', 'Cessation for a time; an intervening period of time;\n   an interval; a temporary pause; as, to labor without intermission; an\n   intermission of ten minutes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermission', 'n.', 'The temporary cessation or subsidence of a fever; the\n   space of time between the paroxysms of a disease. Intermission is an\n   entire cessation, as distinguished from remission, or abatement of\n   fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermission', 'n.', 'Intervention; interposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermissive', 'a.', 'Having temporary cessations; not continual;\n   intermittent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermitted', 'imp. & p. p.', 'of Intermit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermitting', 'p. pr. & vb. n.', 'of Intermit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermit', 'v. t.', 'To cause to cease for a time, or at intervals; to\n   interrupt; to suspend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermit', 'v. i.', 'To cease for a time or at intervals; to moderate; to\n   be intermittent, as a fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermittence', 'n.', 'Act or state of intermitting; intermission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermittent', 'a.', 'Coming and going at intervals; alternating;\n   recurrent; periodic; as, an intermittent fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermittent', 'n.', 'An intermittent fever or disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermittently', 'adv.', 'With intermissions; in an intermittent manner;\n   intermittingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermittingly', 'adv.', 'With intermissions; at intervals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermix', 'v. t.', 'To mix together; to intermingle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermix', 'v. i.', 'To be mixed together; to be intermingled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermixedly', 'adv.', 'In a mixed manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermixture', 'n.', 'A mass formed by mixture; a mass of ingredients\n   mixed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermixture', 'n.', 'Admixture; an additional ingredient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermobility', 'n.', 'Capacity of things to move among each other; as, the\n   intermobility of fluid particles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermodillion', 'n.', 'The space between two modillions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermontane', 'a.', 'Between mountains; as, intermontane soil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermundane', 'a.', 'Being, between worlds or orbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermundian', 'a.', 'Intermundane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermural', 'a.', 'Lying between walls; inclosed by walls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermured', 'imp. & p. p.', 'of Intermure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermuring', 'p. pr. & vb. n.', 'of Intermure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermure', 'v. t.', 'To wall in; to inclose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermuscular', 'a.', 'Between muscles; as, intermuscular septa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermutation', 'n.', 'Interchange; mutual or reciprocal change.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intermutual', 'a.', 'Mutual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intern', 'a.', 'Internal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intern', 'a.', 'To put for safe keeping in the interior of a place or\n   country; to confine to one locality; as, to intern troops which have\n   fled for refuge to a neutral country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Internal', 'a.', 'Inward; interior; being within any limit or surface;\n   inclosed; -- opposed to external; as, the internal parts of a body, or\n   of the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Internal', 'a.', 'Derived from, or dependent on, the thing itself;\n   inherent; as, the internal evidence of the divine origin of the\n   Scriptures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Internal', 'a.', 'Pertaining to its own affairs or interests; especially,\n   (said of a country) domestic, as opposed to foreign; as, internal\n   trade; internal troubles or war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Internal', 'a.', 'Pertaining to the inner being or the heart; spiritual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Internal', 'a.', 'Intrinsic; inherent; real.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Internal', 'a.', 'Lying toward the mesial plane; mesial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Internality', 'n.', 'The state of being internal or within; interiority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Internally', 'adv.', 'Inwardly; within the enveloping surface, or the\n   boundary of a thing; within the body; beneath the surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Internally', 'adv.', 'Hence: Mentally; spiritually.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Internasal', 'a.', 'Between the nasal cavities; as, the internasal\n   cartilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'International', 'a.', 'Between or among nations; pertaining to the\n   intercourse of nations; participated in by two or more nations; common\n   to, or affecting, two or more nations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'International', 'a.', 'Of or concerning the association called the\n   International.');
  INSERT INTO words (word, word_type, description) VALUES  (    'International', 'n.', 'The International; an abbreviated from of the title\n   of the International Workingmens Association, the name of an\n   association, formed in London in 1864, which has for object the\n   promotion of the interests of the industrial classes of all nations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'International', 'n.', 'A member of the International Association.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Internationalism', 'n.', 'The state or principles of international\n   interests and intercourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Internationalism', 'n.', 'The doctrines or organization of the\n   International.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Internationalist', 'n.', 'One who is versed in the principles of\n   international law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Internationalist', 'n.', 'A member of the International; one who believes\n   in, or advocates the doctrines of, the International.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Internationalize', 'v. t.', 'To make international; to cause to affect the\n   mutual relations of two or more nations; as, to internationalize a\n   principle of law, or a philanthropic enterprise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Internationally', 'adv.', 'In an international manner; from an\n   international point of view.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interne', 'a.', 'That which is within; the interior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interneciary', 'a.', 'Alt. of Internecinal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Internecinal', 'a.', 'Internecine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Internecine', 'a.', 'Involving, or accompanied by, mutual slaughter;\n   mutually destructive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Internecion', 'n.', 'Mutual slaughter or destruction; massacre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Internecive', 'a.', 'Internecine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Internection', 'n.', 'Intimate connection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interneural', 'a.', 'Between the neural arches or neural spines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interneural', 'n.', 'An interneural spine or cartilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Internity', 'n.', 'State of being within; interiority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Internment', 'n.', 'Confinement within narrow limits, -- as of foreign\n   troops, to the interior of a country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Internodal', 'a.', 'Of or pertaining to internodes; intervening between\n   nodes or joints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Internode', 'n.', 'The space between two nodes or points of the stem from\n   which the leaves properly arise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Internode', 'n.', 'A part between two joints; a segment; specifically, one\n   of the phalanges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Internodial', 'a.', 'Internodal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Internuncial', 'a.', 'Of or pertaining to an internuncio.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Internuncial', 'a.', 'Communicating or transmitting impressions between\n   different parts of the body; -- said of the nervous system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Internunciess', 'n.', 'A female messenger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Internuncios', 'pl. ', 'of Internuncio');
  INSERT INTO words (word, word_type, description) VALUES  (    'Internuncio', 'n.', 'A messenger between two parties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Internuncio', 'n.', 'A representative, or charge daffaires, of the pope at\n   a foreign court or seat of government, ranking next below a nuncio.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Internuncioship', 'n.', 'The office or function of an internuncio.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Internuncius', 'n.', 'Internuncio.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interoceanic', 'a.', 'Between oceans; connecting oceans; as, interoceanic\n   communication; an interoceanic canal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interocular', 'a.', 'Between, or within, the eyes; as, the interocular\n   distance; situated between the eyes, as the antennae of some insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpercular', 'a.', 'Of or pertaining to the interoperculum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpercular', 'n.', 'The interopercular bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-la', 'pl. ', 'of Interoperculum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interoperculum', 'n.', 'The postero-inferior opercular bone, in fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interorbital', 'a.', 'Between the orbits; as, the interorbital septum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interosculant', 'a.', 'Mutually touching or intersecting; as, interosculant\n   circles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interosculant', 'a.', 'Uniting two groups; -- said of certain genera which\n   connect family groups, or of species that connect genera. See Osculant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interosculate', 'v. i. & t.', 'To kiss together to touch. See Osculate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interosculate', 'v. i. & t.', 'To have the character of, or to lie between,\n   two distinct groups.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interosseal', 'a.', 'Alt. of Interosseous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interosseous', 'a.', 'Situated between bones; as, an interosseous ligament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpale', 'v. t.', 'To place pales between or among; to separate by\n   pales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpale', 'v. t.', 'To interweave or interlace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interparietal', 'a.', 'Between the parietal bones or cartilages; as, the\n   interparietal suture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interparietal', 'n.', 'The interparietal bone or cartilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpause', 'n.', 'An intermission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpeal', 'v. t.', 'To interpel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpedencular', 'a.', 'Between peduncles; esp., between the peduncles, or\n   crura, of the cerebrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpel', 'v. t.', 'To interrupt, break in upon, or intercede with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpellant', 'a.', 'Interpelling; interrupting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpellant', 'n.', 'One who, or that which, interpels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpellate', 'v. t.', 'To question imperatively, as a minister, or other\n   executive officer, in explanation of his conduct; -- generally on the\n   part of a legislative body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpellation', 'n.', 'The act of interpelling or interrupting;\n   interruption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpellation', 'n.', 'The act of interposing or interceding;\n   intercession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpellation', 'n.', 'An act of interpellating, or of demanding of an\n   officer an explanation of his action; imperative or peremptory\n   questioning; a point raised in a debate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpellation', 'n.', 'A official summons or citation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpenetrate', 'v. t.', 'To penetrate between or within; to penetrate\n   mutually.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpenetrate', 'v. i.', 'To penetrate each the other; to penetrate\n   between bodies or their parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpenetration', 'n.', 'The act of penetrating between or within other\n   substances; mutual penetration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpenetrative', 'a.', 'Penetrating among or between other substances;\n   penetrating each the other; mutually penetrative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpetalary', 'a.', 'Between the petals of a flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpetiolar', 'a.', 'Being between petioles. Cf. Intrapetiolar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interphalangeal', 'a.', 'Between phalanges; as, interphalangeal\n   articulations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpilaster', 'n.', 'The interval or space between two pilasters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interplace', 'v. t.', 'To place between or among; as, to interplace a name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interplanetary', 'a.', 'Between planets; as, interplanetary spaces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interplay', 'n.', 'Mutual action or influence; interaction; as, the\n   interplay of affection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interplead', 'v. i.', 'To plead against each other, or go to trial between\n   themselves, as the claimants in an in an interpleader. See\n   Interpleader.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpleader', 'n.', 'One who interpleads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpleader', 'n.', 'A proceeding devised to enable a person, of whom the\n   same debt, duty, or thing is claimed adversely by two or more parties,\n   to compel them to litigate the right or title between themselves, and\n   thereby to relieve himself from the suits which they might otherwise\n   bring against him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpledge', 'v. t.', 'To pledge mutually.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpoint', 'v. t.', 'To point; to mark with stops or pauses; to\n   punctuate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpolable', 'a.', 'That may be interpolated; suitable to be\n   interpolated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpolated', 'imp. & p. p.', 'of Interpolate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpolating', 'p. pr. & vb. n.', 'of Interpolate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpolate', 'v. t.', 'To renew; to carry on with intermission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpolate', 'v. t.', 'To alter or corrupt by the insertion of new or\n   foreign matter; especially, to change, as a book or text, by the\n   insertion of matter that is new, or foreign to the purpose of the\n   author.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpolate', 'v. t.', 'To fill up intermediate terms of, as of a series,\n   according to the law of the series; to introduce, as a number or\n   quantity, in a partial series, according to the law of that part of the\n   series.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpolated', 'a.', 'Inserted in, or added to, the original; introduced;\n   foisted in; changed by the insertion of new or spurious matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpolated', 'a.', 'Provided with necessary interpolations; as, an\n   interpolated table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpolated', 'a.', 'Introduced or determined by interpolation; as,\n   interpolated quantities or numbers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpolation', 'n.', 'The act of introducing or inserting anything,\n   especially that which is spurious or foreign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpolation', 'n.', 'That which is introduced or inserted, especially\n   something foreign or spurious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpolation', 'n.', 'The method or operation of finding from a few given\n   terms of a series, as of numbers or observations, other intermediate\n   terms in conformity with the law of the series.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpolator', 'n.', 'One who interpolates; esp., one who inserts foreign\n   or spurious matter in genuine writings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpone', 'v. t.', 'To interpose; to insert or place between.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interponent', 'n.', 'One who, or that which, interposes; an interloper, an\n   opponent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interposal', 'n.', 'The act of interposing; interposition; intervention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interposed', 'imp. & p. p.', 'of Interpose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interposing', 'p. pr. & vb. n.', 'of Interpose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpose', 'v. t.', 'To place between; as, to interpose a screen between\n   the eye and the light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpose', 'v. t.', 'To thrust; to intrude; to between, either for aid or\n   for troubling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpose', 'v. t.', 'To introduce or inject between the parts of a\n   conversation or argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpose', 'v. i.', 'To be or come between.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpose', 'v. i.', 'To step in between parties at variance; to mediate;\n   as, the prince interposed and made peace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpose', 'v. i.', 'To utter a sentiment by way of interruption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpose', 'n.', 'Interposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interposer', 'n.', 'One who, or that which, interposes or intervenes; an\n   obstacle or interruption; a mediator or agent between parties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interposit', 'n.', 'An intermediate depot or station between one commercial\n   city or country and another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interposition', 'n.', 'The act of interposing, or the state of being\n   interposed; a being, placing, or coming between; mediation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interposition', 'n.', 'The thing interposed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interposure', 'n.', 'Interposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpreted', 'imp. & p. p.', 'of Interpret');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpreting', 'p. pr. & vb. n.', 'of Interpret');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpret', 'v. t.', 'To explain or tell the meaning of; to expound; to\n   translate orally into intelligible or familiar language or terms; to\n   decipher; to define; -- applied esp. to language, but also to dreams,\n   signs, conduct, mysteries, etc.; as, to interpret the Hebrew language\n   to an Englishman; to interpret an Indian speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpret', 'v. t.', 'To apprehend and represent by means of art; to show\n   by illustrative representation; as, an actor interprets the character\n   of Hamlet; a musician interprets a sonata; an artist interprets a\n   landscape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpret', 'v. i.', 'To act as an interpreter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpretable', 'a.', 'Admitting of interpretation; capable of being\n   interpreted or explained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpretament', 'n.', 'Interpretation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpretation', 'n.', 'The act of interpreting; explanation of what is\n   obscure; translation; version; construction; as, the interpretation of\n   a foreign language, of a dream, or of an enigma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpretation', 'n.', 'The sense given by an interpreter; exposition or\n   explanation given; meaning; as, commentators give various\n   interpretations of the same passage of Scripture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpretation', 'n.', 'The power or explaining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpretation', 'n.', 'An artists way of expressing his thought or\n   embodying his conception of nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpretation', 'n.', 'The act or process of applying general principles\n   or formulae to the explanation of the results obtained in special\n   cases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpretative', 'a.', 'Designed or fitted to interpret; explanatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpretative', 'a.', 'According to interpretation; constructive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpretatively', 'adv.', 'By interpretation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpreter', 'n.', 'One who or that which interprets, explains, or\n   expounds; a translator; especially, a person who translates orally\n   between two parties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpretive', 'a.', 'Interpretative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpubic', 'a.', 'Between the pubic bones or cartilages; as, the\n   interpubic disk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interpunction', 'n.', 'The insertion of points between word or sentences;\n   punctuation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interradial', 'a.', 'Between the radii, or rays; -- in zoology, said of\n   certain parts of radiate animals; as, the interradial plates of a\n   starfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interramal', 'a.', 'Between rami or branches; esp., between the mandibles,\n   or rami of the lower jaw; intermandibular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interreceive', 'v. t.', 'To receive between or within.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interregency', 'n.', 'An interregnum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interregent', 'n.', 'A person who discharges the royal functions during an\n   interregnum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interregnums', 'pl. ', 'of Interregnum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interregnum', 'n.', 'The time during which a throne is vacant between the\n   death or abdication of a sovereign and the accession of his successor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interregnum', 'n.', 'Any period during which, for any cause, the executive\n   branch of a government is suspended or interrupted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interreign', 'n.', 'An interregnum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interrelated', 'a.', 'Having a mutual or reciprocal relation or\n   parallelism; correlative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interrelation', 'n.', 'Mutual or reciprocal relation; correlation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interrenal', 'a.', 'Between the kidneys; as, the interrenal body, an organ\n   found in many fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interrenal', 'n.', 'The interrenal body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interrepellent', 'a.', 'Mutually repellent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interrer', 'n.', 'One who inters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interrexes', 'pl. ', 'of Interrex');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interreges', 'pl. ', 'of Interrex');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interrex', 'n.', 'An interregent, or a regent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interrogating', 'imp. & p. p.', 'of Interrogate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interrogate', 'v. t.', 'To question formally; to question; to examine by\n   asking questions; as, to interrogate a witness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interrogate', 'v. i.', 'To ask questions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interrogate', 'n.', 'An interrogation; a question.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interrogatee', 'n.', 'One who is interrogated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interrogation', 'n.', 'The act of interrogating or questioning; examination\n   by questions; inquiry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interrogation', 'n.', 'A question put; an inquiry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interrogation', 'n.', 'A point, mark, or sign, thus [?], indicating that\n   the sentence with which it is connected is a question. It is used to\n   express doubt, or to mark a query. Called also interrogation point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interrogative', 'a.', 'Denoting a question; expressed in the form of a\n   question; as, an interrogative sentence; an interrogative pronoun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interrogative', 'n.', 'A word used in asking questions; as, who? which?\n   why?');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interrogatively', 'adv.', 'In the form of, or by means of, a question; in\n   an interrogative manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interrogator', 'n.', 'One who asks questions; a questioner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interrogatories', 'pl. ', 'of Interrogatory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interrogatory', 'n.', 'A formal question or inquiry; esp. (Law), a question\n   asked in writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interrogatory', 'a.', 'Containing, expressing, or implying a question; as,\n   an interrogatory sentence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interrupted', 'imp. & p. p.', 'of Interrupt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interrupting', 'p. pr. & vb. n.', 'of Interrupt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interrupt', 'v. t.', 'To break into, or between; to stop, or hinder by\n   breaking in upon the course or progress of; to interfere with the\n   current or motion of; to cause a temporary cessation of; as, to\n   interrupt the remarks speaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interrupt', 'v. t.', 'To divide; to separate; to break the monotony of; as,\n   the evenness of the road was not interrupted by a single hill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interrupt', 'p. a.', 'Broken; interrupted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interrupted', 'a.', 'Broken; intermitted; suddenly stopped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interrupted', 'a.', 'Irregular; -- said of any arrangement whose symmetry\n   is destroyed by local causes, as when leaflets are interposed among the\n   leaves in a pinnate leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interruptedly', 'adv.', 'With breaks or interruptions; discontinuously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interrupter', 'n.', 'One who, or that which, interrupts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interrupter', 'n.', 'A device for opening and closing an electrical\n   circuit; a vibrating spring or tuning fork, arranged to make and break\n   a circuit at rapidly recurring intervals, by the action of the current\n   itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interruption', 'n.', 'The act of interrupting, or breaking in upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interruption', 'n.', 'The state of being interrupted; a breach or break,\n   caused by the abrupt intervention of something foreign; intervention;\n   interposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interruption', 'n.', 'Obstruction caused by breaking in upon course,\n   current, progress, or motion; stop; hindrance; as, the author has met\n   with many interruptions in the execution of his work; the speaker or\n   the argument proceeds without interruption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interruption', 'n.', 'Temporary cessation; intermission; suspension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interruptive', 'a.', 'Tending to interrupt; interrupting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interscapular', 'a.', 'Between the scapulae or shoulder blades.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interscapular', 'a.', 'Pertaining to the upper back, or the part between\n   the shoulders; as, the interscapular feathers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interscapulars', 'n. pl.', 'The interscapular feathers of a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interscendent', 'a.', 'Having exponents which are radical quantities; --\n   said of certain powers; as, x2, or xa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interscinded', 'imp. & p. p.', 'of Interscind');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interscinding', 'p. pr. & vb. n.', 'of Interscind');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interscind', 'v. t.', 'To cut off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interscribed', 'imp. & p. p.', 'of Interscribe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interscribing', 'p. pr. & vb. n.', 'of Interscribe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interscribe', 'v. t.', 'To write between.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intersecant', 'a.', 'Dividing into parts; crossing; intersecting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intersected', 'imp. & p. p.', 'of Intersect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intersecting', 'p. pr. & vb. n.', 'of Intersect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intersect', 'v. t.', 'To cut into or between; to cut or cross mutually; to\n   divide into parts; as, any two diameters of a circle intersect each\n   other at the center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intersect', 'v. i.', 'To cut into one another; to meet and cross each\n   other; as, the point where two lines intersect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intersection', 'n.', 'The act, state, or place of intersecting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intersection', 'n.', 'The point or line in which one line or surface cuts\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intersectional', 'a.', 'Pertaining to, or formed by, intersections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interseminate', 'v. t.', 'To sow between or among.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interseptal', 'a.', 'Between septa; as, the interseptal spaces or zones,\n   between the transparent, or septal, zones in striated muscle; the\n   interseptal chambers of a shell, or of a seed vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interserted', 'imp. & p. p.', 'of Intersert');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interserting', 'p. pr. & vb. n.', 'of Intersert');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intersert', 'v. t.', 'To put in between other things; to insert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interserttion', 'n.', 'The act of interserting, or that which is\n   interserted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intersesamoid', 'a.', 'Between sesamoid bones; as, intersesamoid ligaments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interset', 'v. t.', 'To set between or among.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intershock', 'v. t.', 'To shock mutually.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intersidereal', 'a.', 'Between or among constellations or stars;\n   interstellar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intersocial', 'a.', 'Pertaining to the mutual intercourse or relations of\n   persons in society; social.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intersomnious', 'a.', 'Between the times of sleeping; in an interval of\n   wakefulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interspace', 'n.', 'Intervening space.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interspeech', 'n.', 'A speech interposed between others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interspersed', 'imp. & p. p.', 'of Intersperse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interspersing', 'p. pr. & vb. n.', 'of Intersperse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intersperse', 'v. t.', 'To scatter or set here and there among other\n   things; to insert at intervals; as, to intersperse pictures in a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intersperse', 'v. t.', 'To diversify or adorn with things set or scattered\n   at intervals; to place something at intervals in or among; as, to\n   intersperse a book with pictures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interspersion', 'n.', 'The act of interspersing, or the state of being\n   interspersed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interspinal', 'a.', 'Alt. of Interspinous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interspinous', 'a.', 'Between spines; esp., between the spinous processes\n   of the vertebral column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interspiration', 'n.', 'Spiritual inspiration at separate times, or at\n   intervals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interstapedial', 'a.', 'Pertaining to a part of the columella of the ear,\n   between the stapes and the mediostapedial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interstapedial', 'n.', 'The interstapedial part of the columella.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interstate', 'a.', 'Pertaining to the mutual relations of States; existing\n   between, or including, different States; as, interstate commerce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interstellar', 'a.', 'Between or among the stars; as, interstellar space.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interstellary', 'a.', 'Interstellar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intersternal', 'a.', 'Between the sternal; -- said of certain membranes or\n   parts of insects and crustaceans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interstices', 'pl. ', 'of Interstice');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interstice', 'n.', 'That which intervenes between one thing and another;\n   especially, a space between things closely set, or between the parts\n   which compose a body; a narrow chink; a crack; a crevice; a hole; an\n   interval; as, the interstices of a wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interstice', 'n.', 'An interval of time; specifically (R. C. Ch.), in the\n   plural, the intervals which the canon law requires between the\n   reception of the various degrees of orders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intersticed', 'a.', 'Provided with interstices; having interstices between;\n   situated at intervals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interstinctive', 'a.', 'Distinguishing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interstitial', 'a.', 'Of or pertaining to interstices; intermediate; within\n   the tissues; as, interstitial cavities or spaces in the tissues of\n   animals or plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interstition', 'n.', 'An intervening period of time; interval.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interstratification', 'n.', 'Stratification among or between other layers\n   or strata; also, that which is interstratified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interstratified', 'a.', 'Stratified among or between other bodies; as,\n   interstratified rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interstratify', 'v. t.', 'To put or insert between other strata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intertalk', 'v. i.', 'To converse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intertangle', 'v. t.', 'To entangle; to intertwine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intertarsal', 'a.', 'Between the tarsal bones; as, the intertarsal\n   articulations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intertex', 'v. t.', 'To intertwine; to weave or bind together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intertexture', 'n.', 'The act of interweaving, or the state of being\n   interwoven; that which is interwoven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interthoracic', 'a.', 'In the thorax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intertie', 'n.', 'In any framed work, a horizontal tie other than sill and\n   plate or other principal ties, securing uprights to one another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intertissued', 'a.', 'Interwoven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intertraffic', 'n.', 'Mutual trade of traffic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intertranspicuous', 'a.', 'Transpicuous within or between.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intertransverse', 'a.', 'Between the transverse processes of the vertebrae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intertrigo', 'n.', 'A rubbing or chafing of the skin; especially, an\n   abrasion or excoriation of the skin between folds, as in fat or\n   neglected children.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intertrochanteric', 'a.', 'Between the trochanters of the femur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intertropical', 'a.', 'Situated between or within the tropics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intertubular', 'a.', 'Between tubes or tubules; as, intertubular cells;\n   intertubular substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intertwine', 'v. t.', 'To unite by twining one with another; to entangle;\n   to interlace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intertwine', 'v. i.', 'To be twined or twisted together; to become mutually\n   involved or enfolded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intertwine', 'n.', 'The act intertwining, or the state of being\n   intertwined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intertwiningly', 'adv.', 'By intertwining or being intertwined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intertwist', 'v. t.', 'To twist together one with another; to intertwine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intertwistingly', 'adv.', 'By intertwisting, or being intertwisted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interungular', 'a.', 'Alt. of Interungulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interungulate', 'a.', 'Between ungulae; as, interungular glands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interval', 'n.', 'A space between things; a void space intervening between\n   any two objects; as, an interval between two houses or hills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interval', 'n.', 'Space of time between any two points or events; as, the\n   interval between the death of Charles I. of England, and the accession\n   of Charles II.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interval', 'n.', 'A brief space of time between the recurrence of similar\n   conditions or states; as, the interval between paroxysms of pain;\n   intervals of sanity or delirium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interval', 'n.', 'Difference in pitch between any two tones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interval', 'n.', 'Alt. of Intervale');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intervale', 'n.', 'A tract of low ground between hills, or along the banks\n   of a stream, usually alluvial land, enriched by the overflowings of the\n   river, or by fertilizing deposits of earth from the adjacent hills. Cf.\n   Bottom, n., 7.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intervallums', 'pl. ', 'of Intervallum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intervalla', 'pl. ', 'of Intervallum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intervallum', 'n.', 'An interval.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intervary', 'v. i.', 'To alter or vary between; to change.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interveined', 'a.', 'Intersected, as with veins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intervened', 'imp. & p. p.', 'of Intervene');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intervening', 'p. pr. & vb. n.', 'of Intervene');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intervene', 'v. i.', 'To come between, or to be between, persons or things;\n   -- followed by between; as, the Mediterranean intervenes between Europe\n   and Africa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intervene', 'v. i.', 'To occur, fall, or come between, points of time, or\n   events; as, an instant intervened between the flash and the report;\n   nothing intervened ( i. e., between the intention and the execution) to\n   prevent the undertaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intervene', 'v. i.', 'To interpose; as, to intervene to settle a quarrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intervene', 'v. i.', 'In a suit to which one has not been made a party, to\n   put forward a defense of ones interest in the subject matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intervene', 'v. t.', 'To come between.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intervene', 'n.', 'A coming between; intervention; meeting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intervener', 'n.', 'One who intervenes; especially (Law), a person who\n   assumes a part in a suit between others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intervenience', 'n.', 'Alt. of Interveniency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interveniency', 'n.', 'Intervention; interposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intervenient', 'a.', 'Being or coming between; intercedent; interposed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intervent', 'v. t.', 'To thwart; to obstruct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intervention', 'n.', 'The act of intervening; interposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intervention', 'n.', 'Any interference that may affect the interests of\n   others; especially, of one or more states with the affairs of another;\n   mediation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intervention', 'n.', 'The act by which a third person, to protect his own\n   interest, interposes and becomes a party to a suit pending between\n   other parties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interventor', 'n.', 'One who intervenes; a mediator; especially (Eccles.\n   Hist.), a person designated by a church to reconcile parties, and unite\n   them in the choice of officers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interventricular', 'a.', 'Between the ventricles; as, the interventricular\n   partition of the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intervenue', 'n.', 'Interposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intervert', 'v. t.', 'To turn to another course or use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intervertebral', 'a.', 'Between vertebrae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interview', 'n.', 'A mutual sight or view; a meeting face to face; usually,\n   a formal or official meeting for consultation; a conference; as, the\n   secretary had an interview with the President.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interview', 'n.', 'A conservation, or questioning, for the purpose of\n   eliciting information for publication; the published statement so\n   elicited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interview', 'v. t.', 'To have an interview with; to question or converse\n   with, especially for the purpose of obtaining information for\n   publication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interviewer', 'n.', 'One who interviews; especially, one who obtains an\n   interview with another for the purpose of eliciting his opinions or\n   obtaining information for publication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interviewing', 'n.', 'The act or custom of holding an interview or\n   interviews.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intervisible', 'a.', 'Mutually visible, or in sight, the one from the\n   other, as stations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intervisit', 'v. i.', 'To exchange visits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intervital', 'a.', 'Between two lives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intervolution', 'n.', 'The state of being intervolved or coiled up; a\n   convolution; as, the intervolutions of a snake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intervolved', 'imp. & p. p.', 'of Intervolve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intervolving', 'p. pr. & vb. n.', 'of Intervolve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intervolve', 'v. t.', 'To involve one within another; to twist or coil\n   together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interwove', 'imp. & obs. p. p.', 'of Interweave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interwoven', 'p. p.', 'of Interweave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interweaving', 'p. pr. & vb. n.', 'of Interweave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interweave', 'v. t.', 'To weave together; to intermix or unite in texture\n   or construction; to intertwine; as, threads of silk and cotton\n   interwoven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interweave', 'v. t.', 'To intermingle; to unite intimately; to connect\n   closely; as, to interweave truth with falsehood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interwish', 'v. t.', 'To wish mutually in regarded to each other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interworking', 'n.', 'The act of working in together; interweaving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interworld', 'n.', 'A world between other worlds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interwove', '', 'Alt. of Interwoven');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interwoven', '', 'imp. & p. p. of Interweave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Interwreathe', 'v. t.', 'To weave into a wreath; to intertwine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intestable', 'a.', 'Not capable of making a will; not legally qualified or\n   competent to make a testament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intestacy', 'n.', 'The state of being intestate, or of dying without having\n   made a valid will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intestate', 'a.', 'Without having made a valid will; without a will; as, to\n   die intestate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intestate', 'a.', 'Not devised or bequeathed; not disposed of by will; as,\n   an intestate estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intestate', 'n.', 'A person who dies without making a valid will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intestinal', 'a.', 'Of or pertaining to the intestines of an animal; as,\n   the intestinal tube; intestinal digestion; intestinal ferments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intestine', 'a.', 'Internal; inward; -- opposed to external.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intestine', 'a.', 'Internal with regard to a state or country; domestic;\n   not foreign; -- applied usually to that which is evil; as, intestine\n   disorders, calamities, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intestine', 'a.', 'Depending upon the internal constitution of a body or\n   entity; subjective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intestine', 'a.', 'Shut up; inclosed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intestines', 'pl. ', 'of Intestine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intestine', 'a.', 'That part of the alimentary canal between the stomach\n   and the anus. See Illust. of Digestive apparatus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intestine', 'a.', 'The bowels; entrails; viscera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intext', 'n.', 'The text of a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intextine', 'n.', 'A thin membrane existing in the pollen grains of some\n   plants, and situated between the extine and the intine, as in /nothera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intextured', 'a.', 'Inwrought; woven in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inthirst', 'v. t.', 'To make thirsty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inthralled', 'imp. & p. p.', 'of Inthrall');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inthralling', 'p. pr. & vb. n.', 'of Inthrall');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inthrall', 'v. t.', 'To reduce to bondage or servitude; to make a thrall,\n   slave, vassal, or captive of; to enslave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inthrallment', 'n.', 'Act of inthralling, or state of being inthralled;\n   servitude; bondage; vassalage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inthrone', 'v. t.', 'Same as Enthrone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inthrong', 'v. i.', 'To throng or collect together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inthronization', 'n.', 'Enthronement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inthronize', 'v. t.', 'To enthrone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intice', 'v. t.', 'See Entice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intimacies', 'pl. ', 'of Intimacy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intimacy', 'n.', 'The state of being intimate; close familiarity or\n   association; nearness in friendship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intimate', 'a.', 'Innermost; inward; internal; deep-seated; hearty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intimate', 'a.', 'Near; close; direct; thorough; complete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intimate', 'a.', 'Close in friendship or acquaintance; familiar;\n   confidential; as, an intimate friend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intimate', 'n.', 'An intimate friend or associate; a confidant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intimated', 'imp. & p. p.', 'of Intimate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intimating', 'p. pr. & vb. n.', 'of Intimate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intimate', 'a.', 'To announce; to declare; to publish; to communicate; to\n   make known.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intimate', 'a.', 'To suggest obscurely or indirectly; to refer to remotely;\n   to give slight notice of; to hint; as, he intimated his intention of\n   resigning his office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intimately', 'adv.', 'In an intimate manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intimation', 'n.', 'The act of intimating; also, the thing intimated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intimation', 'n.', 'Announcement; declaration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intimation', 'n.', 'A hint; an obscure or indirect suggestion or notice; a\n   remote or ambiguous reference; as, he had given only intimations of his\n   design.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intime', 'a.', 'Inward; internal; intimate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intimidated', 'imp. & p. p.', 'of Intimidate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intimidating', 'p. pr. & vb. n.', 'of Intimidate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intimidate', 'v. t.', 'To make timid or fearful; to inspire of affect with\n   fear; to deter, as by threats; to dishearten; to abash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intimidation', 'n.', 'The act of making timid or fearful or of deterring by\n   threats; the state of being intimidated; as, the voters were kept from\n   the polls by intimidation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intimidatory', 'a.', 'Tending or serving to intimidate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intinction', 'n.', 'The act of tingeing or dyeing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intinction', 'n.', 'A method or practice of the administration of the\n   sacrament by dipping the bread or wafer in the wine and administering\n   both together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intinctivity', 'n.', 'The want of the quality of coloring or tingeing other\n   bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intine', 'n.', 'A transparent, extensible membrane of extreme tenuity,\n   which forms the innermost coating of grains of pollen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intire', 'adv.', 'Alt. of Intirely');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intirely', 'adv.', 'See Entire, a., Entirely, adv.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intitle', 'v. t.', 'See Entitle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intituled', 'imp. & p. p.', 'of Intitule');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intituling', 'p. pr. & vb. n.', 'of Intitule');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intitule', 'v. t.', 'To entitle; to give a title to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Into', 'prep.', 'To the inside of; within. It is used in a variety of\n   applications.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Into', 'prep.', 'Expressing entrance, or a passing from the outside of a\n   thing to its interior parts; -- following verbs expressing motion; as,\n   come into the house; go into the church; one stream falls or runs into\n   another; water enters into the fine vessels of plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Into', 'prep.', 'Expressing penetration beyond the outside or surface, or\n   access to the inside, or contents; as, to look into a letter or book;\n   to look into an apartment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Into', 'prep.', 'Indicating insertion; as, to infuse more spirit or\n   animation into a composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Into', 'prep.', 'Denoting inclusion; as, put these ideas into other words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Into', 'prep.', 'Indicating the passing of a thing from one form,\n   condition, or state to another; as, compound substances may be resolved\n   into others which are more simple; ice is convertible into water, and\n   water into vapor; men are more easily drawn than forced into\n   compliance; we may reduce many distinct substances into one mass; men\n   are led by evidence into belief of truth, and are often enticed into\n   the commission of crimesinto; she burst into tears; children are\n   sometimes frightened into fits; all persons are liable to be seduced\n   into error and folly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intolerability', 'n.', 'The quality of being intolerable; intolerableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intolerable', 'a.', 'Not tolerable; not capable of being borne or endured;\n   not proper or right to be allowed; insufferable; insupportable;\n   unbearable; as, intolerable pain; intolerable heat or cold; an\n   intolerable burden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intolerable', 'a.', 'Enormous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intolerance', 'n.', 'Want of capacity to endure; as, intolerance of light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intolerance', 'n.', 'The quality of being intolerant; refusal to allow to\n   others the enjoyment of their opinions, chosen modes of worship, and\n   the like; want of patience and forbearance; illiberality; bigotry; as,\n   intolerance shown toward a religious sect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intolerancy', 'n.', 'Intolerance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intolerant', 'a.', 'Not enduring; not able to endure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intolerant', 'a.', 'Not tolerating difference of opinion or sentiment,\n   especially in religious matters; refusing to allow others the enjoyment\n   of their opinions, rights, or worship; unjustly impatient of the\n   opinion of those disagree with us; not tolerant; unforbearing; bigoted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intolerant', 'n.', 'An intolerant person; a bigot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intolerantly', 'adv.', 'In an intolerant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intolerated', 'a.', 'Not tolerated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intolerating', 'a.', 'Intolerant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intoleration', 'n.', 'Intolerance; want of toleration; refusal to tolerate\n   a difference of opinion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intombed', 'imp. & p. p.', 'of Intomb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intombing', 'p. pr. & vb. n.', 'of Intomb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intomb', 'v. t.', 'To place in a tomb; to bury; to entomb. See Entomb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intombment', 'n.', 'See Entombment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intonate', 'v. i.', 'To thunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intonated', 'imp. & p. p.', 'of Intonate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intonating', 'p. pr. & vb. n.', 'of Intonate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intonate', 'v. i.', 'To sound the tones of the musical scale; to practice\n   the sol-fa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intonate', 'v. i.', 'To modulate the voice in a musical, sonorous, and\n   measured manner, as in reading the liturgy; to intone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intonate', 'v. t.', 'To utter in a musical or sonorous manner; to chant;\n   as, to intonate the liturgy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intonation', 'n.', 'A thundering; thunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intonation', 'n.', 'The act of sounding the tones of the musical scale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intonation', 'n.', 'Singing or playing in good tune or otherwise; as, her\n   intonation was false.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intonation', 'n.', 'Reciting in a musical prolonged tone; intonating, or\n   singing of the opening phrase of a plain-chant, psalm, or canticle by a\n   single voice, as of a priest. See Intone, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intoned', 'imp. & p. p.', 'of Intone');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intoning', 'p. pr. & vb. n.', 'of Intone');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intone', 'v. t.', 'To utter with a musical or prolonged note or tone; to\n   chant; as, to intone the church service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intone', 'v. i.', 'To utter a prolonged tone or a deep, protracted sound;\n   to speak or recite in a measured, sonorous manner; to intonate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intorsion', 'n.', 'A winding, bending, or twisting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intorsion', 'n.', 'The bending or twining of any part of a plant toward one\n   side or the other, or in any direction from the vertical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intorted', 'imp. & p. p.', 'of Intort');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intorting', 'p. pr. & vb. n.', 'of Intort');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intort', 'v. t.', 'To twist in and out; to twine; to wreathe; to wind; to\n   wring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intortion', 'n.', 'See Intorsion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intoxicant', 'n.', 'That which intoxicates; an intoxicating agent; as,\n   alcohol, opium, and laughing gas are intoxicants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intoxicate', 'a.', 'Intoxicated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intoxicate', 'a.', 'Overexcited, as with joy or grief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intoxicated', 'imp. & p. p.', 'of Intoxicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intoxicating', 'p. pr. & vb. n.', 'of Intoxicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intoxicate', 'v. t.', 'To poison; to drug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intoxicate', 'v. t.', 'To make drunk; to inebriate; to excite or to stupefy\n   by strong drink or by a narcotic substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intoxicate', 'v. t.', 'To excite to a transport of enthusiasm, frenzy, or\n   madness; to elate unduly or excessively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intoxicatedness', 'n.', 'The state of being intoxicated; intoxication;\n   drunkenness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intoxicating', 'a.', 'Producing intoxication; fitted to intoxicate; as,\n   intoxicating liquors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intoxication', 'n.', 'A poisoning, as by a spirituous or a narcotic\n   substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intoxication', 'n.', 'The state of being intoxicated or drunk; inebriation;\n   ebriety; drunkenness; the act of intoxicating or making drunk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intoxication', 'n.', 'A high excitement of mind; an elation which rises to\n   enthusiasm, frenzy, or madness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intra-', '', 'A prefix signifying in, within, interior; as, intraocular,\n   within the eyeball; intramarginal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intraaxillary', 'a.', 'Situated below the point where a leaf joins the\n   stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intracellular', 'a.', 'Within a cell; as, the intracellular movements seen\n   in the pigment cells, the salivary cells, and in the protoplasm of some\n   vegetable cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intracolic', 'a.', 'Within the colon; as, the intracolic valve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intracranial', 'a.', 'Within the cranium or skull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intractability', 'n.', 'The quality of being intractable; intractableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intractable', 'a.', 'Not tractable; not easily governed, managed, or\n   directed; indisposed to be taught, disciplined, or tamed; violent;\n   stubborn; obstinate; refractory; as, an intractable child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intractile', 'a.', 'Not tractile; incapable of being drawn out or extended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrados', 'n.', 'The interior curve of an arch; esp., the inner or lower\n   curved face of the whole body of voussoirs taken together. See\n   Extrados.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrafoliaceous', 'a.', 'Growing immediately above, or in front of, a leaf;\n   as, intrafoliaceous stipules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrafusion', 'n.', 'The act of pouring into a vessel; specif. (Med.), the\n   operation of introducing a substance into a blood vessel; as,\n   intrafusion of blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intralobular', 'a.', 'Within lobules; as, the intralobular branches of the\n   hepatic veins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intramarginal', 'a.', 'Situated within the margin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intramercurial', 'a.', 'Between the planet Mercury and the sun; -- as, the\n   hypothetical Vulcan is intramercurial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intramolecular', 'a.', 'Between molecules; situated, or acting, between the\n   molecules of bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intramundane', 'a.', 'Being within the material world; -- opposed to\n   extramundane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intramural', 'a.', 'Being within the walls, as of a city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intramural', 'a.', 'Being within the substance of the walls of an organ;\n   as, intramural pregnancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intranquillity', 'n.', 'Unquietness; restlessness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intranscalent', 'a.', 'Impervious to heat; adiathermic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intransgressible', 'a.', 'Incapable of being transgressed; not to be passes\n   over or crossed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intranssient', 'a.', 'Not transient; remaining; permanent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intransigent', 'a.', 'Refusing compromise; uncompromising; irreconcilable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intransigentes', 'n. pl.', 'The extreme radicals; the party of the\n   irreconcilables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intransitive', 'a.', 'Not passing farther; kept; detained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intransitive', 'a.', 'Not transitive; not passing over to an object;\n   expressing an action or state that is limited to the agent or subject,\n   or, in other words, an action which does not require an object to\n   complete the sense; as, an intransitive verb, e. g., the bird flies;\n   the dog runs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intransitively', 'adv.', 'Without an object following; in the manner of an\n   intransitive verb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'In transitu', '', 'In transit; during passage; as, goods in transitu.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intransmissible', 'a.', 'Not capable of being transmitted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intransmutability', 'n.', 'The quality of being intransmutable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intransmutable', 'a.', 'Not capable of being transmuted or changed into\n   another substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrant', 'a.', 'Entering; penetrating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrant', 'n.', 'One who enters; especially, a person entering upon some\n   office or station.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intranuclear', 'a.', 'Within the nucleus of a cell; as. the intranuclear\n   network of fibrils, seen in the first stages of karyokinesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrap', 'v. t.', 'See Entrap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intraparietal', 'a.', 'Situated or occurring within an inclosure; shut off\n   from public sight; private; secluded; retired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrapetiolar', 'a.', 'Situated between the petiole and the stem; -- said\n   of the pair of stipules at the base of a petiole when united by those\n   margins next the petiole, thus seeming to form a single stipule between\n   the petiole and the stem or branch; -- often confounded with\n   interpetiolar, from which it differs essentially in meaning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intraterritorial', 'a.', 'Within the territory or a territory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrathoracic', 'a.', 'Within the thora/ or chest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intratropical', 'a.', 'Within the tropics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrauterine', 'a.', 'Within the uterus or womb; as, intrauterine\n   hemorrhage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intravalvular', 'a.', 'Between valves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intravenous', 'a.', 'Within the veins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intraventricular', 'a.', 'Within or between ventricles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intreasure', 'v. t.', 'To lay up, as in a treasury; to hoard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intreat', 'v. t.', 'See Entreat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intreatable', 'a.', 'Not to be entreated; inexorable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intreatance', 'n.', 'Entreaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intreatful', 'a.', 'Full of entreaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrenched', 'imp. & p. p.', 'of Intrench');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrenching', 'p. pr. & vb. n.', 'of Intrench');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrench', 'v. t.', 'To cut in; to furrow; to make trenches in or upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrench', 'v. t.', 'To surround with a trench or with intrenchments, as in\n   fortification; to fortify with a ditch and parapet; as, the army\n   intrenched their camp, or intrenched itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrench', 'v. i.', 'To invade; to encroach; to infringe or trespass; to\n   enter on, and take possession of, that which belongs to another; --\n   usually followed by on or upon; as, the king was charged with\n   intrenching on the rights of the nobles, and the nobles were accused of\n   intrenching on the prerogative of the crown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrenchant', 'a.', 'Not to be gashed or marked with furrows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrenchment', 'n.', 'The act of intrenching or the state of being\n   intrenched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrenchment', 'n.', 'Any defensive work consisting of at least a trench or\n   ditch and a parapet made from the earth thrown up in making such a\n   ditch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrenchment', 'n.', 'Any defense or protection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrenchment', 'n.', 'An encroachment or infringement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrepid', 'a.', 'Not trembling or shaking with fear; fearless; bold;\n   brave; undaunted; courageous; as, an intrepid soldier; intrepid spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrepidity', 'n.', 'The quality or state of being intrepid; fearless\n   bravery; courage; resoluteness; valor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrepidly', 'adv.', 'In an intrepid manner; courageously; resolutely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intricable', 'a.', 'Entangling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intricacies', 'pl. ', 'of Intricacy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intricacy', 'n.', 'The state or quality of being intricate or entangled;\n   perplexity; involution; complication; complexity; that which is\n   intricate or involved; as, the intricacy of a knot; the intricacy of\n   accounts; the intricacy of a cause in controversy; the intricacy of a\n   plot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intricate', 'a.', 'Entangled; involved; perplexed; complicated; difficult\n   to understand, follow, arrange, or adjust; as, intricate machinery,\n   labyrinths, accounts, plots, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intricate', 'v. t.', 'To entangle; to involve; to make perplexing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intricately', 'adv.', 'In an intricate manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intricateness', 'n.', 'The state or quality of being intricate; intricacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrication', 'n.', 'Entanglement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrigante', 'n.', 'A female intriguer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrigued', 'imp. & p. p.', 'of Intrigue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intriguing', 'p. pr. & vb. n.', 'of Intrigue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrigue', 'v. i.', 'To form a plot or scheme; to contrive to accomplish a\n   purpose by secret artifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrigue', 'v. i.', 'To carry on a secret and illicit love or amour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrigue', 'v. t.', 'To fill with artifice and duplicity; to complicate; to\n   embarrass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrigue', 'v. i.', 'Intricacy; complication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrigue', 'v. i.', 'A complicated plot or scheme intended to effect some\n   purpose by secret artifice; conspiracy; stratagem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrigue', 'v. i.', 'The plot or romance; a complicated scheme of designs,\n   actions, and events.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrigue', 'v. i.', 'A secret and illicit love affair between two persons\n   of different sexes; an amour; a liaison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intriguer', 'n.', 'One who intrigues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intriguery', 'n.', 'Arts or practice of intrigue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intriguingly', 'adv.', 'By means of, or in the manner of, intrigue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrinse', 'a.', 'Tightly drawn; or (perhaps) intricate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrinsic', 'a.', 'Inward; internal; hence, true; genuine; real; essential;\n   inherent; not merely apparent or accidental; -- opposed to extrinsic;\n   as, the intrinsic value of gold or silver; the intrinsic merit of an\n   action; the intrinsic worth or goodness of a person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrinsic', 'a.', 'Included wholly within an organ or limb, as certain\n   groups of muscles; -- opposed to extrinsic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrinsic', 'n.', 'A genuine quality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrinsical', 'a.', 'Intrinsic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrinsical', 'a.', 'Intimate; closely familiar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrinsicality', 'n.', 'The quality of being intrinsic; essentialness;\n   genuineness; reality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrinsically', 'adv.', 'Internally; in its nature; essentially; really;\n   truly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrinsicalness', 'n.', 'The quality of being intrinsical; intrinsicality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrinsicate', 'a.', 'Intricate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intro-', '', 'A prefix signifying within, into, in, inward; as, introduce,\n   introreception, introthoracic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introcession', 'n.', 'A depression, or inward sinking of parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introduced', 'imp. & p. p.', 'of Introduce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introducing', 'p. pr. & vb. n.', 'of Introduce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introduce', 'v. t.', 'To lead or bring in; to conduct or usher in; as, to\n   introduce a person into a drawing-room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introduce', 'v. t.', 'To put (something into a place); to insert; as, to\n   introduce the finger, or a probe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introduce', 'v. t.', 'To lead to and make known by formal announcement or\n   recommendation; hence, to cause to be acquainted; as, to introduce\n   strangers; to introduce one person to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introduce', 'v. t.', 'To bring into notice, practice, cultivation, or use;\n   as, to introduce a new fashion, method, or plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introduce', 'v. t.', 'To produce; to cause to exist; to induce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introduce', 'v. t.', 'To open to notice; to begin; to present; as, he\n   introduced the subject with a long preface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introducement', 'n.', 'Introduction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introducer', 'n.', 'One who, or that which, introduces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introduct', 'v. t.', 'To introduce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introduction', 'n.', 'The act of introducing, or bringing to notice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introduction', 'n.', 'The act of formally making persons known to each\n   other; a presentation or making known of one person to another by name;\n   as, the introduction of one stranger to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introduction', 'n.', 'That part of a book or discourse which introduces or\n   leads the way to the main subject, or part; preliminary; matter;\n   preface; proem; exordium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introduction', 'n.', 'A formal and elaborate preliminary treatise;\n   specifically, a treatise introductory to other treatises, or to a\n   course of study; a guide; as, an introduction to English literature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introductive', 'a.', 'Serving to introduce; introductory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introductor', 'n.', 'An introducer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introductorily', 'adv.', 'By way of introduction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introductory', 'a.', 'Serving to introduce something else; leading to the\n   main subject or business; preliminary; prefatory; as, introductory\n   proceedings; an introductory discourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introductress', 'n.', 'A female introducer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introflexed', 'a.', 'Flexed or bent inward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introgression', 'n.', 'The act of going in; entrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introit', 'n.', 'A going in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introit', 'n.', 'A psalm sung or chanted immediately before the collect,\n   epistle, and gospel, and while the priest is entering within the rails\n   of the altar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introit', 'n.', 'A part of a psalm or other portion of Scripture read by\n   the priest at Mass immediately after ascending to the altar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introit', 'n.', 'An anthem or psalm sung before the Communion service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introit', 'n.', 'Any composition of vocal music appropriate to the opening\n   of church services.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intromission', 'n.', 'The act of sending in or of putting in; insertion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intromission', 'n.', 'The act of letting go in; admission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intromission', 'n.', 'An intermeddling with the affairs of another, either\n   on legal grounds or without authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intromitted', 'imp. & p. p.', 'of Intromit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intromitting', 'p. pr. & vb. n.', 'of Intromit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intromit', 'v. t.', 'To send in or put in; to insert or introduce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intromit', 'v. t.', 'To allow to pass in; to admit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intromit', 'v. i.', 'To intermeddle with the effects or goods of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intromittent', 'a.', 'Throwing, or allowing to pass, into or within.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intromittent', 'a.', 'Used in copulation; -- said of the external\n   reproductive organs of the males of many animals, and sometimes of\n   those of the females.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intromitter', 'n.', 'One who intromits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intropression', 'n.', 'Pressure acting within.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introreception', 'n.', 'The act of admitting into or within.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introrse', 'a.', 'Turning or facing inward, or toward the axis of the part\n   to which it belongs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introspect', 'v. t.', 'To look into or within; to view the inside of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introspection', 'n.', 'A view of the inside or interior; a looking inward;\n   specifically, the act or process of self-examination, or inspection of\n   ones own thoughts and feelings; the cognition which the mind has of\n   its own acts and states; self-consciousness; reflection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introspectionist', 'n.', 'One given to the introspective method of\n   examining the phenomena of the soul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introspective', 'a.', 'Inspecting within; seeing inwardly; capable of, or\n   exercising, inspection; self-conscious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introspective', 'a.', 'Involving the act or results of conscious knowledge\n   of physical phenomena; -- contrasted with associational.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introsume', 'v. t.', 'To draw in; to swallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introsusception', 'n.', 'The act or process of receiving within.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introsusception', 'n.', 'Same as Intussusception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introvenient', 'a.', 'Coming in together; entering; commingling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introversion', 'n.', 'The act of introverting, or the state of being\n   introverted; the act of turning the mind inward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introverted', 'imp. & p. p.', 'of Introvert');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introverting', 'p. pr. & vb. n.', 'of Introvert');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introvert', 'v. t.', 'To turn or bend inward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Introvert', 'v. t.', 'To look within; to introspect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrude', 'v. i.', 'To thrust ones self in; to come or go in without\n   invitation, permission, or welcome; to encroach; to trespass; as, to\n   intrude on families at unseasonable hours; to intrude on the lands of\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intruded', 'imp. & p. p.', 'of Intrude');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intruding', 'p. pr. & vb. n.', 'of Intrude');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrude', 'v. t.', 'To thrust or force (something) in or upon; especially,\n   to force (ones self) in without leave or welcome; as, to intrude ones\n   presence into a conference; to intrude ones opinions upon another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrude', 'v. t.', 'To enter by force; to invade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrude', 'v. t.', 'The cause to enter or force a way, as into the crevices\n   of rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intruded', 'p. a.', 'Same as Intrusive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intruder', 'n.', 'One who intrudes; one who thrusts himself in, or enters\n   without right, or without leave or welcome; a trespasser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrudress', 'n.', 'A female intruder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrunk', 'v. t.', 'To inclose as in a trunk; to incase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrusion', 'n.', 'The act of intruding, or of forcing in; especially, the\n   forcing (ones self) into a place without right or welcome;\n   encroachment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrusion', 'n.', 'The penetrating of one rock, while in a plastic or metal\n   state, into the cavities of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrusion', 'n.', 'The entry of a stranger, after a particular estate or\n   freehold is determined, before the person who holds in remainder or\n   reversion has taken possession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrusion', 'n.', 'The settlement of a minister over 3 congregation without\n   their consent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrusional', 'a.', 'Of or pertaining to intrusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrusionist', 'n.', 'One who intrudes; especially, one who favors the\n   appointment of a clergyman to a parish, by a patron, against the wishes\n   of the parishioners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrusive', 'a.', 'Apt to intrude; characterized by intrusion; entering\n   without right or welcome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrusted', 'imp. & p. p.', 'of Intrust');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrusting', 'p. pr. & vb. n.', 'of Intrust');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intrust', 'v. t.', 'To deliver (something) to another in trust; to deliver\n   to (another) something in trust; to commit or surrender (something) to\n   another with a certain confidence regarding his care, use, or disposal\n   of it; as, to intrust a servant with ones money or intrust money or\n   goods to a servant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intubation', 'n.', 'The introduction of a tube into an organ to keep it\n   open, as into the larynx in croup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intuition', 'n.', 'A looking after; a regard to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intuition', 'n.', 'Direct apprehension or cognition; immediate knowledge,\n   as in perception or consciousness; -- distinguished from \"mediate\"\n   knowledge, as in reasoning; as, the mind knows by intuition that black\n   is not white, that a circle is not a square, that three are more than\n   two, etc.; quick or ready insight or apprehension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intuition', 'n.', 'Any object or truth discerned by direct cognition;\n   especially, a first or primary truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intuitional', 'a.', 'Pertaining to, or derived from, intuition;\n   characterized by intuition; perceived by intuition; intuitive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intuitionalism', 'n.', 'The doctrine that the perception or recognition of\n   primary truth is intuitive, or direct and immediate; -- opposed to\n   sensationalism, and experientialism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intuitionalist', 'n.', 'One who holds the doctrine of intuitionalism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intuitionism', 'n.', 'Same as Intuitionalism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intuitionist', 'n.', 'Same as Intuitionalist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intuitive', 'a.', 'Seeing clearly; as, an intuitive view; intuitive vision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intuitive', 'a.', 'Knowing, or perceiving, by intuition; capable of knowing\n   without deduction or reasoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intuitive', 'a.', 'Received. reached, obtained, or perceived, by intuition;\n   as, intuitive judgment or knowledge; -- opposed to deductive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intuitively', 'adv.', 'In an intuitive manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intuitivism', 'n.', 'The doctrine that the ideas of right and wrong are\n   intuitive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intumesced', 'imp. & p. p.', 'of Intumesce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intumescing', 'p. pr. & vb. n.', 'of Intumesce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intumesce', 'v. i.', 'To enlarge or expand with heat; to swell;\n   specifically, to swell up or bubble up under the action of heat, as\n   before the blowpipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intumescence', 'n.', 'The act or process of swelling or enlarging; also,\n   the state of being swollen; expansion; tumidity; especially, the\n   swelling up of bodies under the action of heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intumescence', 'n.', 'Anything swollen or enlarged, as a tumor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intumescent', 'a.', 'Swelling up; expanding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intumulated', 'a.', 'Unburied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intune', 'v. t.', 'To intone. Cf. Entune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inturbidated', 'imp. & p. p.', 'of Inturbidate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inturbidating', 'p. pr. & vb. n.', 'of Inturbidate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inturbidate', 'v. t.', 'To render turbid; to darken; to confuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inturgescence', 'n.', 'A swelling; the act of swelling, or state of being\n   swelled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intuse', 'n.', 'A bruise; a contusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intussuscepted', 'a.', 'Received into some other thing or part, as a sword\n   into a sheath; invaginated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intussusception', 'n.', 'The reception of one part within another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intussusception', 'n.', 'The abnormal reception or slipping of a part of a\n   tube, by inversion and descent, within a contiguous part of it;\n   specifically, the reception or slipping of the upper part of the small\n   intestine into the lower; introsusception; invagination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intussusception', 'n.', 'The interposition of new particles of formative\n   material among those already existing, as in a cell wall, or in a\n   starch grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intussusception', 'n.', 'The act of taking foreign matter, as food, into a\n   living body; the process of nutrition, by which dead matter is absorbed\n   by the living organism, and ultimately converted into the organized\n   substance of its various tissues and organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intwine', 'v. t.', 'To twine or twist into, or together; to wreathe; as, a\n   wreath of flowers intwined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intwine', 'v. i.', 'To be or to become intwined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intwinement', 'n.', 'The act of twinning, or the state of being intwined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Intwist', 'v. t.', 'To twist into or together; to interweave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inuendo', 'n.', 'See Innuendo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inulin', 'n.', 'A substance of very wide occurrence. It is found dissolved\n   in the sap of the roots and rhizomes of many composite and other\n   plants, as Inula, Helianthus, Campanula, etc., and is extracted by\n   solution as a tasteless, white, semicrystalline substance, resembling\n   starch, with which it is isomeric. It is intermediate in nature between\n   starch and sugar. Called also dahlin, helenin, alantin, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inuloid', 'n.', 'A substance resembling inulin, found in the unripe bulbs\n   of the dahila.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inumbrate', 'v. t.', 'To shade; to darken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inuncted', 'a.', 'Anointed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inunction', 'n.', 'The act of anointing, or the state of being anointed;\n   unction; specifically (Med.), the rubbing of ointments into the pores\n   of the skin, by which medicinal agents contained in them, such as\n   mercury, iodide of potash, etc., are absorbed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inunctuosity', 'n.', 'The want of unctuosity; freedom from greasiness or\n   oiliness; as, the inunctuosity of porcelain clay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inundant', 'a.', 'Overflowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inundated', 'imp. & p. p.', 'of Inundate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inundating', 'p. pr. & vb. n.', 'of Inundate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inundate', 'v. t.', 'To cover with a flood; to overflow; to deluge; to\n   flood; as, the river inundated the town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inundate', 'v. t.', 'To fill with an overflowing abundance or superfluity;\n   as, the country was inundated with bills of credit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inundation', 'n.', 'The act of inundating, or the state of being inundated;\n   an overflow; a flood; a rising and spreading of water over grounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inundation', 'n.', 'An overspreading of any kind; overflowing or\n   superfluous abundance; a flood; a great influx; as, an inundation of\n   tourists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inunderstanding', 'a.', 'Void of understanding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inurbane', 'a.', 'Uncivil; unpolished; rude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inurbanity', 'n.', 'Want of urbanity or courtesy; unpolished manners or\n   deportment; inurbaneness; rudeness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inured', 'imp. & p. p.', 'of Inure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inuring', 'p. pr. & vb. n.', 'of Inure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inure', 'v. t.', 'To apply in use; to train; to discipline; to use or\n   accustom till use gives little or no pain or inconvenience; to harden;\n   to habituate; to practice habitually.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inure', 'v. i.', 'To pass into use; to take or have effect; to be applied;\n   to serve to the use or benefit of; as, a gift of lands inures to the\n   heirs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inurement', 'n.', 'Use; practice; discipline; habit; custom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inurned', 'imp. & p. p.', 'of Inurn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inurning', 'p. pr. & vb. n.', 'of Inurn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inurn', 'v. t.', 'To put in an urn, as the ashes of the dead; hence, to\n   bury; to intomb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inusitate', 'a.', 'Unusual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inusitation', 'n.', 'Want of use; disuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inust', 'a.', 'Burnt in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inustion', 'n.', 'The act of burning or branding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inutile', 'a.', 'Useless; unprofitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inutility', 'n.', 'Uselessness; the quality of being unprofitable;\n   unprofitableness; as, the inutility of vain speculations and visionary\n   projects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inutterable', 'a.', 'Unutterable; inexpressible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'In vacuo', '', 'In a vacuum; in empty space; as, experiments in vacuo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invaded', 'imp. & p. p.', 'of Invade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invading', 'p. pr. & vb. n.', 'of Invade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invade', 'v. t.', 'To go into or upon; to pass within the confines of; to\n   enter; -- used of forcible or rude ingress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invade', 'v. t.', 'To enter with hostile intentions; to enter with a view\n   to conquest or plunder; to make an irruption into; to attack; as, the\n   Romans invaded Great Britain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invade', 'v. t.', 'To attack; to infringe; to encroach on; to violate; as,\n   the king invaded the rights of the people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invade', 'v. t.', 'To grow or spread over; to affect injuriously and\n   progressively; as, gangrene invades healthy tissue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invade', 'v. i.', 'To make an invasion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invader', 'n.', 'One who invades; an assailant; an encroacher; an intruder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invaginate', 'v. t.', 'To insert as in a sheath; to produce intussusception\n   in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invaginate', 'a.', 'Alt. of Invaginated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invaginated', 'a.', 'Sheathed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invaginated', 'a.', 'Having one portion of a hollow organ drawn back within\n   another portion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invagination', 'n.', 'The condition of an invaginated organ or part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invagination', 'n.', 'One of the methods by which the various germinal\n   layers of the ovum are differentiated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invalescence', 'n.', 'Strength; health.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invaletudinary', 'a.', 'Wanting health; valetudinary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invalid', 'a.', 'Of no force, weight, or cogency; not valid; weak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invalid', 'a.', 'Having no force, effect, or efficacy; void; null; as, an\n   invalid contract or agreement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invalid', 'a.', 'A person who is weak and infirm; one who is disabled for\n   active service; especially, one in chronic ill health.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invalid', 'n.', 'Not well; feeble; infirm; sickly; as, he had an invalid\n   daughter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invalid', 'v. t.', 'To make or render invalid or infirm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invalid', 'v. t.', 'To classify or enroll as an invalid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invalidated', 'imp. & p. p.', 'of Invalidate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invalidating', 'p. pr. & vb. n.', 'of Invalidate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invalidate', 'v. t.', 'To render invalid; to weaken or lessen the force of;\n   to destroy the authority of; to render of no force or effect; to\n   overthrow; as, to invalidate an agreement or argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invalidation', 'n.', 'The act of inavlidating, or the state of being\n   invalidated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invalide', 'n.', 'See Invalid, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invalidism', 'n.', 'The condition of an invalid; sickness; infirmity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invalidity', 'n.', 'Want of validity or cogency; want of legal force or\n   efficacy; invalidness; as, the invalidity of an agreement or of a will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invalidity', 'n.', 'Want of health; infirmity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invalidness', 'n.', 'Invalidity; as, the invalidness of reasoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invalorous', 'a.', 'Not valorous; cowardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invaluable', 'a.', 'Valuable beyond estimation; inestimable; priceless;\n   precious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invaluably', 'adv.', 'Inestimably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invalued', 'a.', 'Inestimable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invariability', 'n.', 'The quality of being invariable; invariableness;\n   constancy; uniformity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invariable', 'a.', 'Not given to variation or change; unalterable;\n   unchangeable; always uniform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invariable', 'n.', 'An invariable quantity; a constant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invariance', 'n.', 'The property of remaining invariable under prescribed\n   or implied conditions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invariant', 'n.', 'An invariable quantity; specifically, a function of the\n   coefficients of one or more forms, which remains unaltered, when these\n   undergo suitable linear transformations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invasion', 'n.', 'The act of invading; the act of encroaching upon the\n   rights or possessions of another; encroachment; trespass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invasion', 'n.', 'A warlike or hostile entrance into the possessions or\n   domains of another; the incursion of an army for conquest or plunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invasion', 'n.', 'The incoming or first attack of anything hurtful or\n   pernicious; as, the invasion of a disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invasive', 'a.', 'Tending to invade; characterized by invasion; aggressive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invect', 'v. i.', 'To inveigh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invected', 'a.', 'Having a border or outline composed of semicircles with\n   the convexity outward; -- the opposite of engrailed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invection', 'n.', 'An inveighing against; invective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invective', 'a.', 'Characterized by invection; critical; denunciatory;\n   satirical; abusive; railing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invective', 'n.', 'An expression which inveighs or rails against a person;\n   a severe or violent censure or reproach; something uttered or written,\n   intended to cast opprobrium, censure, or reproach on another; a harsh\n   or reproachful accusation; -- followed by against, having reference to\n   the person or thing affected; as an invective against tyranny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invectively', 'adv.', 'In an invective manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inveighed', 'imp. & p. p.', 'of Inveigh');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inveighing', 'p. pr. & vb. n.', 'of Inveigh');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inveigh', 'v. i.', 'To declaim or rail (against some person or thing); to\n   utter censorious and bitter language; to attack with harsh criticism or\n   reproach, either spoken or written; to use invectives; -- with against;\n   as, to inveigh against character, conduct, manners, customs, morals, a\n   law, an abuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inveigher', 'n.', 'One who inveighs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inveigled', 'imp. & p. p.', 'of Inveigle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inveigling', 'p. pr. & vb. n.', 'of Inveigle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inveigle', 'v. t.', 'To lead astray as if blind; to persuade to something\n   evil by deceptive arts or flattery; to entice; to insnare; to seduce;\n   to wheedle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inveiglement', 'n.', 'The act of inveigling, or the state of being\n   inveigled; that which inveigles; enticement; seduction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inveigler', 'n.', 'One who inveigles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inveil', 'v. t.', 'To cover, as with a vail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invendibility', 'n.', 'The quality of being invendible; invendibleness;\n   unsalableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invendible', 'a.', 'Not vendible or salable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invenom', 'v. t.', 'See Envenom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invented', 'imp. & p. p.', 'of Invent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inventing', 'p. pr. & vb. n.', 'of Invent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invent', 'v. t.', 'To come or light upon; to meet; to find.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invent', 'v. t.', 'To discover, as by study or inquiry; to find out; to\n   devise; to contrive or produce for the first time; -- applied commonly\n   to the discovery of some serviceable mode, instrument, or machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invent', 'v. t.', 'To frame by the imagination; to fabricate mentally; to\n   forge; -- in a good or a bad sense; as, to invent the machinery of a\n   poem; to invent a falsehood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inventer', 'n.', 'One who invents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inventful', 'a.', 'Full of invention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inventible', 'a.', 'Capable of being invented.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inventibleness', 'n.', 'Quality of being inventible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invention', 'n.', 'The act of finding out or inventing; contrivance or\n   construction of that which has not before existed; as, the invention of\n   logarithms; the invention of the art of printing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invention', 'n.', 'That which is invented; an original contrivance or\n   construction; a device; as, this fable was the invention of Esop; that\n   falsehood was her own invention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invention', 'n.', 'Thought; idea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invention', 'n.', 'A fabrication to deceive; a fiction; a forgery; a\n   falsehood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invention', 'n.', 'The faculty of inventing; imaginative faculty; skill or\n   ingenuity in contriving anything new; as, a man of invention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invention', 'n.', 'The exercise of the imagination in selecting and\n   treating a theme, or more commonly in contriving the arrangement of a\n   piece, or the method of presenting its parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inventious', 'a.', 'Inventive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inventive', 'a.', 'Able and apt to invent; quick at contrivance; ready at\n   expedients; as, an inventive head or genius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inventor', 'n.', 'One who invents or finds out something new; a contriver;\n   especially, one who invents mechanical devices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inventorial', 'a.', 'Of or pertaining to an inventory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inventories', 'pl. ', 'of Inventory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inventory', 'n.', 'An account, catalogue, or schedule, made by an executor\n   or administrator, of all the goods and chattels, and sometimes of the\n   real estate, of a deceased person; a list of the property of which a\n   person or estate is found to be possessed; hence, an itemized list of\n   goods or valuables, with their estimated worth; specifically, the\n   annual account of stock taken in any business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inventoried', 'imp. & p. p.', 'of Inventory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inventorying', 'p. pr. & vb. n.', 'of Inventory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inventory', 'v. t.', 'To make an inventory of; to make a list, catalogue,\n   or schedule of; to insert or register in an account of goods; as, a\n   merchant inventories his stock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inventress', 'n.', 'A woman who invents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inveracity', 'n.', 'Want of veracity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inverisimilitude', 'n.', 'Want of verisimilitude or likelihood;\n   improbability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inverse', 'a.', 'Opposite in order, relation, or effect; reversed;\n   inverted; reciprocal; -- opposed to direct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inverse', 'a.', 'Inverted; having a position or mode of attachment the\n   reverse of that which is usual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inverse', 'a.', 'Opposite in nature and effect; -- said with reference to\n   any two operations, which, when both are performed in succession upon\n   any quantity, reproduce that quantity; as, multiplication is the\n   inverse operation to division. The symbol of an inverse operation is\n   the symbol of the direct operation with -1 as an index. Thus sin-1 x\n   means the arc whose sine is x.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inverse', 'n.', 'That which is inverse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inversely', 'adv.', 'In an inverse order or manner; by inversion; --\n   opposed to directly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inversion', 'n.', 'The act of inverting, or turning over or backward, or\n   the state of being inverted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inversion', 'n.', 'A change by inverted order; a reversed position or\n   arrangement of things; transposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inversion', 'n.', 'A movement in tactics by which the order of companies in\n   line is inverted, the right being on the left, the left on the right,\n   and so on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inversion', 'n.', 'A change in the order of the terms of a proportion, so\n   that the second takes the place of the first, and the fourth of the\n   third.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inversion', 'n.', 'A peculiar method of transformation, in which a figure\n   is replaced by its inverse figure. Propositions that are true for the\n   original figure thus furnish new propositions that are true in the\n   inverse figure. See Inverse figures, under Inverse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inversion', 'n.', 'A change of the usual order of words or phrases; as, \"of\n   all vices, impurity is one of the most detestable,\" instead of,\n   \"impurity is one of the most detestable of all vices.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inversion', 'n.', 'A method of reasoning in which the orator shows that\n   arguments advanced by his adversary in opposition to him are really\n   favorable to his cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inversion', 'n.', 'Said of intervals, when the lower tone is placed an\n   octave higher, so that fifths become fourths, thirds sixths, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inversion', 'n.', 'Said of a chord, when one of its notes, other than its\n   root, is made the bass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inversion', 'n.', 'Said of a subject, or phrase, when the intervals of\n   which it consists are repeated in the contrary direction, rising\n   instead of falling, or vice versa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inversion', 'n.', 'Said of double counterpoint, when an upper and a lower\n   part change places.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inversion', 'n.', 'The folding back of strata upon themselves, as by\n   upheaval, in such a manner that the order of succession appears to be\n   reversed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inversion', 'n.', 'The act or process by which cane sugar (sucrose), under\n   the action of heat and acids or ferments (as diastase), is broken or\n   split up into grape sugar (dextrose), and fruit sugar (levulose); also,\n   less properly, the process by which starch is converted into grape\n   sugar (dextrose).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inverted', 'imp. & p. p.', 'of Invert');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inverting', 'p. pr. & vb. n.', 'of Invert');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invert', 'v. t.', 'To turn over; to put upside down; to upset; to place in\n   a contrary order or direction; to reverse; as, to invert a cup, the\n   order of words, rules of justice, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invert', 'v. t.', 'To change the position of; -- said of tones which form a\n   chord, or parts which compose harmony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invert', 'v. t.', 'To divert; to convert to a wrong use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invert', 'v. t.', 'To convert; to reverse; to decompose by, or subject to,\n   inversion. See Inversion, n., 10.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invert', 'v. i.', 'To undergo inversion, as sugar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invert', 'a.', 'Subjected to the process of inversion; inverted; converted;\n   as, invert sugar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invert', 'n.', 'An inverted arch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invertebral', 'a.', 'Same as Invertebrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invertebrata', 'n. pl.', 'A comprehensive division of the animal kingdom,\n   including all except the Vertebrata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invertebrate', 'a.', 'Destitute of a backbone; having no vertebrae; of or\n   pertaining to the Invertebrata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invertebrate', 'n.', 'One of the Invertebrata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invertebrated', 'a.', 'Having no backbone; invertebrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inverted', 'a.', 'Changed to a contrary or counterchanged order; reversed;\n   characterized by inversion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inverted', 'a.', 'Situated apparently in reverse order, as strata when\n   folded back upon themselves by upheaval.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invertedly', 'adv.', 'In an inverted order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invertible', 'a.', 'Capable of being inverted or turned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invertible', 'a.', 'Capable of being changed or converted; as, invertible\n   sugar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invertible', 'a.', 'Incapable of being turned or changed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invertin', 'n.', 'An unorganized ferment which causes cane sugar to take up\n   a molecule of water and be converted into invert sugar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invested', 'imp. & p. p.', 'of Invest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Investing', 'p. pr. & vb. n.', 'of Invest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invest', 'v. t.', 'To put garments on; to clothe; to dress; to array; --\n   opposed to divest. Usually followed by with, sometimes by in; as, to\n   invest one with a robe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invest', 'v. t.', 'To put on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invest', 'v. t.', 'To clothe, as with office or authority; to place in\n   possession of rank, dignity, or estate; to endow; to adorn; to grace;\n   to bedeck; as, to invest with honor or glory; to invest with an estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invest', 'v. t.', 'To surround, accompany, or attend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invest', 'v. t.', 'To confer; to give.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invest', 'v. t.', 'To inclose; to surround of hem in with troops, so as to\n   intercept succors of men and provisions and prevent escape; to lay\n   siege to; as, to invest a town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invest', 'v. t.', 'To lay out (money or capital) in business with the /iew\n   of obtaining an income or profit; as, to invest money in bank stock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invest', 'v. i.', 'To make an investment; as, to invest in stocks; --\n   usually followed by in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Investient', 'a.', 'Covering; clothing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Investigable', 'a.', 'Capable or susceptible of being investigated;\n   admitting research.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Investigable', 'a.', 'Unsearchable; inscrutable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Investigated', 'imp. & p. p.', 'of Investigate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Investigating', 'p. pr. & vb. n.', 'of Investigate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Investigate', 'v. t.', 'To follow up step by step by patient inquiry or\n   observation; to trace or track mentally; to search into; to inquire and\n   examine into with care and accuracy; to find out by careful\n   inquisition; as, to investigate the causes of natural phenomena.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Investigate', 'v. i.', 'To pursue a course of investigation and study; to\n   make investigation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Investigation', 'n.', 'The act of investigating; the process of inquiring\n   into or following up; research; study; inquiry, esp. patient or\n   thorough inquiry or examination; as, the investigations of the\n   philosopher and the mathematician; the investigations of the judge, the\n   moralist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Investigative', 'a.', 'Given to investigation; inquisitive; curious;\n   searching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Investigator', 'n.', 'One who searches diligently into a subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Investiture', 'n.', 'The act or ceremony of investing, or the of being\n   invested, as with an office; a giving possession; also, the right of so\n   investing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Investiture', 'n.', 'Livery of seizin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Investiture', 'n.', 'That with which anyone is invested or clothed;\n   investment; clothing; covering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Investive', 'a.', 'Investing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Investment', 'n.', 'The act of investing, or the state of being invested.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Investment', 'n.', 'That with which anyone is invested; a vestment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Investment', 'n.', 'The act of surrounding, blocking up, or besieging by an\n   armed force, or the state of being so surrounded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Investment', 'n.', 'The laying out of money in the purchase of some species\n   of property; the amount of money invested, or that in which money is\n   invested.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Investor', 'n.', 'One who invests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Investure', 'n.', 'Investiture; investment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Investure', 'v. t.', 'To clothe; to invest; to install.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inveteracy', 'n.', 'Firm establishment by long continuance; firmness or\n   deep-rooted obstinacy of any quality or state acquired by time; as, the\n   inveteracy of custom, habit, or disease; -- usually in a bad sense; as,\n   the inveteracy of prejudice or of error.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inveteracy', 'n.', 'Malignity; spitefulness; virulency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inveterate', 'a.', 'Old; long-established.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inveterate', 'a.', 'Firmly established by long continuance; obstinate;\n   deep-rooted; of long standing; as, an inveterate disease; an inveterate\n   abuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inveterate', 'a.', 'Having habits fixed by long continuance; confirmed;\n   habitual; as, an inveterate idler or smoker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inveterate', 'a.', 'Malignant; virulent; spiteful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inveterate', 'v. t.', 'To fix and settle by long continuance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inveterately', 'adv.', 'In an inveterate manner or degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inveterateness', 'n.', 'Inveteracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inveteration', 'n.', 'The act of making inveterate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invict', 'a.', 'Invincible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invidious', 'a.', 'Envious; malignant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invidious', 'a.', 'Worthy of envy; desirable; enviable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invidious', 'a.', 'Likely to incur or produce ill will, or to provoke envy;\n   hateful; as, invidious distinctions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invigilance', 'n.', 'Alt. of Invigilancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invigilancy', 'n.', 'Want of vigilance; neglect of watching; carelessness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invigor', 'v. t.', 'To invigorate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invigorated', 'imp. & p. p.', 'of Invigorate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invigorating', 'p. pr. & vb. n.', 'of Invigorate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invigorate', 'v. t.', 'To give vigor to; to strengthen; to animate; to give\n   life and energy to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invigoration', 'n.', 'The act of invigorating, or the state of being\n   invigorated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invile', 'v. t.', 'To render vile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invillaged', 'p. a.', 'Turned into, or reduced to, a village.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invincibility', 'n.', 'The quality or state of being invincible;\n   invincibleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invincible', 'a.', 'Incapable of being conquered, overcome, or subdued;\n   unconquerable; insuperable; as, an invincible army, or obstacle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inviolability', 'n.', 'The quality or state of being inviolable;\n   inviolableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inviolable', 'a.', 'Not violable; not susceptible of hurt, wound, or harm\n   (used with respect to either physical or moral damage); not susceptible\n   of being profaned or corrupted; sacred; holy; as, inviolable honor or\n   chastity; an inviolable shrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inviolable', 'a.', 'Unviolated; uninjured; undefiled; uncorrupted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inviolable', 'a.', 'Not capable of being broken or violated; as, an\n   inviolable covenant, agreement, promise, or vow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inviolableness', 'n.', 'The quality or state of being inviolable; as, the\n   inviolableness of divine justice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inviolably', 'adv.', 'Without violation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inviolacy', 'n.', 'The state or quality of being inviolate; as, the\n   inviolacy of an oath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inviolate', 'a.', 'Alt. of Inviolated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inviolated', 'a.', 'Not violated; uninjured; unhurt; unbroken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inviolated', 'a.', 'Not corrupted, defiled, or profaned; chaste; pure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inviolately', 'adv.', 'In an inviolate manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inviolaness', 'n.', 'The state of being inviolate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invious', 'a.', 'Untrodden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invirile', 'a.', 'Deficient in manhood; unmanly; effeminate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invirility', 'n.', 'Absence of virility or manhood; effeminacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inviscated', 'imp. & p. p.', 'of Inviscate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inviscating', 'p. pr. & vb. n.', 'of Inviscate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inviscate', 'v. t.', 'To daub or catch with glue or birdlime; to entangle\n   with glutinous matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inviscerated', 'imp. & p. p.', 'of Inviscerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inviscerating', 'p. pr. & vb. n.', 'of Inviscerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inviscerate', 'v. t.', 'To breed; to nourish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inviscerate', 'a.', 'Deep-seated; internal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invisibilities', 'pl. ', 'of Invisibility');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invisibility', 'n.', 'The state or quality of being invisible; also, that\n   which is invisible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invisible', 'a.', 'Incapable of being seen; not perceptible by vision; not\n   visible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invisible', 'n.', 'An invisible person or thing; specifically, God, the\n   Supreme Being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invisible', 'n.', 'A Rosicrucian; -- so called because avoiding declaration\n   of his craft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invisible', 'n.', 'One of those (as in the 16th century) who denied the\n   visibility of the church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invisibleness', 'n.', 'The quality or state of being invisible;\n   invisibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invisibly', 'adv.', 'In an invisible manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invision', 'n.', 'Want of vision or of the power of seeing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invitation', 'n.', 'The act of inviting; solicitation; the requesting of a\n   persons company; as, an invitation to a party, to a dinner, or to\n   visit a friend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invitation', 'n.', 'A document written or printed, or spoken words,\n   /onveying the message by which one is invited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invitation', 'n.', 'Allurement; enticement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invitatory', 'a.', 'Using or containing invitations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invitatories', 'pl. ', 'of Invitatory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invitatory', 'n.', 'That which invites; specifically, the invitatory psalm,\n   or a part of it used in worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invited', 'imp. & p. p.', 'of Invite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inviting', 'p. pr. & vb. n.', 'of Invite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invite', 'v. t.', 'To ask; to request; to bid; to summon; to ask to do some\n   act, or go to some place; esp., to ask to an entertainment or visit; to\n   request the company of; as, to invite to dinner, or a wedding, or an\n   excursion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invite', 'v. t.', 'To allure; to draw to; to tempt to come; to induce by\n   pleasure or hope; to attract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invite', 'v. t.', 'To give occasion for; as, to invite criticism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invite', 'v. i.', 'To give invitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invitement', 'n.', 'Invitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inviter', 'n.', 'One who, or that which, invites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invitiate', 'a.', 'Not vitiated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inviting', 'a.', 'Alluring; tempting; as, an inviting amusement or\n   prospect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invitrifiable', 'a.', 'Not admitting of being vitrified, or converted into\n   glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invocated', 'imp. & p. p.', 'of Invocate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invocating', 'p. pr. & vb. n.', 'of Invocate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invocate', 'v. t.', 'To invoke; to call on, or for, in supplication; to\n   implore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invocation', 'n.', 'The act or form of calling for the assistance or\n   presence of some superior being; earnest and solemn entreaty; esp.,\n   prayer offered to a divine being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invocation', 'n.', 'A call or summons; especially, a judicial call, demand,\n   or order; as, the invocation of papers or evidence into court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invocatory', 'a.', 'Making or containing invocation; invoking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invoice', 'n.', 'A written account of the particulars of merchandise\n   shipped or sent to a purchaser, consignee, factor, etc., with the value\n   or prices and charges annexed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invoice', 'n.', 'The lot or set of goods as shipped or received; as, the\n   merchant receives a large invoice of goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invoiced', 'imp. & p. p.', 'of Invoice');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invoicing', 'p. pr. & vb. n.', 'of Invoice');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invoice', 'v. t.', 'To make a written list or account of, as goods to be\n   sent to a consignee; to insert in a priced list; to write or enter in\n   an invoice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invoked', 'imp. & p. p.', 'of Invoke');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invoking', 'p. pr. & vb. n.', 'of Invoke');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invoke', 'v. t.', 'To call on for aid or protection; to invite earnestly or\n   solemnly; to summon; to address in prayer; to solicit or demand by\n   invocation; to implore; as, to invoke the Supreme Being, or to invoke\n   His and blessing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involucel', 'n.', 'A partial, secondary, or small involucre. See Illust. of\n   Involucre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involucellate', 'a.', 'Furnished with involucels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involucella', 'pl. ', 'of Involucellum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involucellum', 'n.', 'See Involucel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involucral', 'a.', 'Pertaining to, possessing, or like, an involucrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involucrate', 'a.', 'Alt. of Involucrated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involucrated', 'a.', 'Having an involucre; involucred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involucre', 'n.', 'A whorl or set of bracts around a flower, umbel, or\n   head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involucre', 'n.', 'A continuous marginal covering of sporangia, in certain\n   ferns, as in the common brake, or the cup-shaped processes of the filmy\n   ferns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involucre', 'n.', 'The peridium or volva of certain fungi. Called also\n   involucrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involucred', 'a.', 'Having an involucre, as umbels, heads, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involucret', 'n.', 'An involucel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involucra', 'pl. ', 'of Involucrum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involucrums', 'pl. ', 'of Involucrum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involucrum', 'n.', 'See Involucre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involucrum', 'n.', 'A sheath which surrounds the base of the lasso cells in\n   the Siphonophora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involuntarily', 'adv.', 'In an involuntary manner; not voluntarily; not\n   intentionally or willingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involuntariness', 'n.', 'The quality or state of being involuntary;\n   unwillingness; automatism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involuntary', 'a.', 'Not having will or the power of choice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involuntary', 'a.', 'Not under the influence or control of the will; not\n   voluntary; as, the involuntary movements of the body; involuntary\n   muscle fibers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involuntary', 'a.', 'Not proceeding from choice; done unwillingly;\n   reluctant; compulsory; as, involuntary submission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involute', 'a.', 'Alt. of Involuted');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involuted', 'a.', 'Rolled inward from the edges; -- said of leaves in\n   vernation, or of the petals of flowers in aestivation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involuted', 'a.', 'Turned inward at the margin, as the exterior lip of the\n   Cyprea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involuted', 'a.', 'Rolled inward spirally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involute', 'n.', 'A curve traced by the end of a string wound upon another\n   curve, or unwound from it; -- called also evolvent. See Evolute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involution', 'n.', 'The act of involving or infolding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involution', 'n.', 'The state of being entangled or involved; complication;\n   entanglement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involution', 'n.', 'That in which anything is involved, folded, or wrapped;\n   envelope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involution', 'n.', 'The insertion of one or more clauses between the\n   subject and the verb, in a way that involves or complicates the\n   construction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involution', 'n.', 'The act or process of raising a quantity to any power\n   assigned; the multiplication of a quantity into itself a given number\n   of times; -- the reverse of evolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involution', 'n.', 'The relation which exists between three or more sets of\n   points, a.a, b.b, c.c, so related to a point O on the line, that the\n   product Oa.Oa = Ob.Ob = Oc.Oc is constant. Sets of lines or surfaces\n   possessing corresponding properties may be in involution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involution', 'n.', 'The return of an enlarged part or organ to its normal\n   size, as of the uterus after pregnancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involved', 'imp. & p. p.', 'of Involve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involving', 'p. pr. & vb. n.', 'of Involve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involve', 'v. t.', 'To roll or fold up; to wind round; to entwine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involve', 'v. t.', 'To envelop completely; to surround; to cover; to hide;\n   to involve in darkness or obscurity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involve', 'v. t.', 'To complicate or make intricate, as in grammatical\n   structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involve', 'v. t.', 'To connect with something as a natural or logical\n   consequence or effect; to include necessarily; to imply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involve', 'v. t.', 'To take in; to gather in; to mingle confusedly; to\n   blend or merge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involve', 'v. t.', 'To envelop, infold, entangle, or embarrass; as, to\n   involve a person in debt or misery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involve', 'v. t.', 'To engage thoroughly; to occupy, employ, or absorb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involve', 'v. t.', 'To raise to any assigned power; to multiply, as a\n   quantity, into itself a given number of times; as, a quantity involved\n   to the third or fourth power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involved', 'a.', 'Same as Involute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involvedness', 'n.', 'The state of being involved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Involvement', 'n.', 'The act of involving, or the state of being involved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invulgar', 'v. t.', 'To cause to become or appear vulgar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invulgar', 'a.', 'Not vulgar; refined; elegant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invulnerability', 'n.', 'Quality or state of being invulnerable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invulnerable', 'a.', 'Incapable of being wounded, or of receiving injury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invulnerable', 'a.', 'Unanswerable; irrefutable; that can not be refuted or\n   convinced; as, an invulnerable argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invulnerableness', 'n.', 'Invulnerability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Invulnerate', 'a.', 'Invulnerable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inwalled', 'imp. & p. p.', 'of Inwall');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inwalling', 'p. pr. & vb. n.', 'of Inwall');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inwall', 'v. t.', 'To inclose or fortify as with a wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inwall', 'n.', 'An inner wall; specifically (Metal.), the inner wall, or\n   lining, of a blast furnace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inward', 'a.', 'Being or placed within; inner; interior; -- opposed to\n   outward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inward', 'a.', 'Seated in the mind, heart, spirit, or soul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inward', 'a.', 'Intimate; domestic; private.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inward', 'n.', 'That which is inward or within; especially, in the plural,\n   the inner parts or organs of the body; the viscera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inward', 'n.', 'The mental faculties; -- usually pl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inward', 'n.', 'An intimate or familiar friend or acquaintance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inward', 'a.', 'Alt. of Inwards');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inwards', 'a.', 'Toward the inside; toward the center or interior; as, to\n   bend a thing inward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inwards', 'a.', 'Into, or toward, the mind or thoughts; inwardly; as, to\n   turn the attention inward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inwardly', 'adv.', 'In the inner parts; internally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inwardly', 'adv.', 'Toward the center; inward; as, to curve inwardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inwardly', 'adv.', 'In the heart or mind; mentally; privately; secret/y;\n   as, he inwardly repines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inwardly', 'adv.', 'Intimately; thoroughly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inwardness', 'n.', 'Internal or true state; essential nature; as, the\n   inwardness of conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inwardness', 'n.', 'Intimacy; familiarity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inwardness', 'n.', 'Heartiness; earnestness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inwards', 'adv.', 'See Inward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inweave', 'v. t.', 'To weave in or together; to intermix or intertwine by\n   weaving; to interlace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inwheel', 'v. t.', 'To encircle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inwit', 'n.', 'Inward sense; mind; understanding; conscience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inwith', 'prep.', 'Within.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inwork', 'v. t. & i.', 'To work in or within.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Itworn', 'p. a.', 'Worn, wrought, or stamped in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inwrap', 'v. t.', 'To cover by wrapping; to involve; to infold; as, to\n   inwrap in a cloak, in smoke, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inwrap', 'v. t.', 'To involve, as in difficulty or perplexity; to perplex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inwreathe', 'v. t.', 'To surround or encompass as with a wreath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Inwrought', 'p. p. / a.', 'Wrought or worked in or among other things;\n   worked into any fabric so as to from a part of its texture; wrought or\n   adorned, as with figures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ios', 'pl. ', 'of Io');
  INSERT INTO words (word, word_type, description) VALUES  (    'Io', 'n.', 'An exclamation of joy or triumph; -- often interjectional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iod-', '', 'See Iodo-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iodal', 'n.', 'An oily liquid, Cl3.CHO, analogous to chloral and bromal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iodate', 'n.', 'A salt of iodic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iodhydrin', 'n.', 'One of a series of compounds containing iodine, and\n   analogous to the chlorhydrins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iodic', 'a.', 'to, or containing, iodine; specif., denoting those compounds\n   in which it has a relatively high valence; as, iodic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iodide', 'n.', 'A binary compound of iodine, or one which may be regarded\n   as binary; as, potassium iodide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iodine', 'n.', 'A nonmetallic element, of the halogen group, occurring\n   always in combination, as in the iodides. When isolated it is in the\n   form of dark gray metallic scales, resembling plumbago, soft but\n   brittle, and emitting a chlorinelike odor. Symbol I. Atomic weight\n   126.5. If heated, iodine volatilizes in beautiful violet vapors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iodism', 'n.', 'A morbid state produced by the use of iodine and its\n   compounds, and characterized by palpitation, depression, and general\n   emaciation, with a pustular eruption upon the skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iodized', 'imp. & p. p.', 'of Iodize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iodizing', 'p. pr. & vb. n.', 'of Iodize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iodize', 'v. t.', 'To treat or impregnate with iodine or its compounds; as,\n   to iodize a plate for photography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iodizer', 'n.', 'One who, or that which, iodizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iodo-', '', 'Alt. of Iod-');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iod-', '', 'A prefix, or combining from, indicating iodine as an\n   ingredient; as, iodoform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iodoform', 'n.', 'A yellow, crystalline, volatile substance, CI3H, having\n   an offensive odor and sweetish taste, and analogous to chloroform. It\n   is used in medicine as a healing and antiseptic dressing for wounds and\n   sores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iodoquinine', 'n.', 'A iodide of quinine obtained as a brown substance,. It\n   is the base of herapathite. See Herapathite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iodous', 'a.', 'Pertaining to, or containing, iodine. See -ous (chemical\n   suffix).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ioduret', 'n.', 'Iodide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iodyrite', 'n.', 'Silver iodide, a mineral of a yellowish color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iolite', 'n.', 'A silicate of alumina, iron, and magnesia, having a bright\n   blue color and vitreous luster; cordierite. It is remarkable for its\n   dichroism, and is also called dichroite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Io moth', '', 'A large and handsome American moth (Hyperchiria Io), having\n   a large, bright-colored spot on each hind wing, resembling the spots on\n   the tail of a peacock. The larva is covered with prickly hairs, which\n   sting like nettles.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ion', '', 'A noun suffix denoting act, process, result of an act or a\n   process, thing acted upon, state, or condition; as, revolution, the act\n   or process of revolving; construction, the act or process of\n   constructing; a thing constructed; dominion, territory ruled over;\n   subjection, state of being subject; dejection; abstraction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ion', 'n.', 'One of the elements which appear at the respective poles when\n   a body is subjected to electro-chemical decomposition. Cf. Anion,\n   Cation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ionian', 'a.', 'Of or pertaining to Ionia or the Ionians; Ionic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ionian', 'n.', 'A native or citizen of Ionia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ionic', 'a.', 'Of or pertaining to Ionia or the Ionians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ionic', 'a.', 'Pertaining to the Ionic order of architecture, one of the\n   three orders invented by the Greeks, and one of the five recognized by\n   the Italian writers of the sixteenth century. Its distinguishing\n   feature is a capital with spiral volutes. See Illust. of Capital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ionic', 'a.', 'Of or pertaining to an ion; composed of ions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ionic', 'n.', 'A foot consisting of four syllables: either two long and two\n   short, -- that is, a spondee and a pyrrhic, in which case it is called\n   the greater Ionic; or two short and two long, -- that is, a pyrrhic and\n   a spondee, in which case it is called the smaller Ionic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ionic', 'n.', 'A verse or meter composed or consisting of Ionic feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ionic', 'n.', 'The Ionic dialect; as, the Homeric Ionic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ionic', 'n.', 'Ionic type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ionidium', 'n.', 'A genus of violaceous plants, chiefly found in tropical\n   America, some species of which are used as substitutes for ipecacuanha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ioqua shell', '', 'The shell of a large Dentalium (D. pretiosum), formerly\n   used as shell money, and for ornaments, by the Indians of the west\n   coast of North America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iota', 'n.', 'The ninth letter of the Greek alphabet (/) corresponding with\n   the English i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iota', 'n.', 'A very small quantity or degree; a jot; a particle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iotacism', 'n.', 'The frequent use of the sound of iota (that of English e\n   in be), as among the modern Greeks; also, confusion from sounding /, /,\n   /, /, //, etc., like /.');
  INSERT INTO words (word, word_type, description) VALUES  (    'I O U', '', 'A paper having on it these letters, with a sum named, and duly\n   signed; -- in use in England as an acknowledgment of a debt, and taken\n   as evidence thereof, but not amounting to a promissory note; a due\n   bill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iowas', 'n. pl.', 'A tribe of Indians which formerly occupied the region\n   now included in the State of Iowa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ipecac', 'n.', 'An abbreviation of Ipecacuanha, and in more frequent use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ipecacuanha', 'n.', 'The root of a Brazilian rubiaceous herb (Cephaelis\n   Ipecacuanha), largely employed as an emetic; also, the plant itself;\n   also, a medicinal extract of the root. Many other plants are used as a\n   substitutes; among them are the black or Peruvian ipecac (Psychotria\n   emetica), the white ipecac (Ionidium Ipecacuanha), the bastard or wild\n   ipecac (Asclepias Curassavica), and the undulated ipecac (Richardsonia\n   scabra).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ipocras', 'n.', 'Hippocras.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ipomoea', 'n.', 'A genus of twining plants with showy monopetalous flowers,\n   including the morning-glory, the sweet potato, and the cypress vine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ipomoeic', 'a.', 'Pertaining to, or designating, an acid obtained by the\n   oxidation of convolvulin (obtained from jalap, the tubers of Ipomoea\n   purga), and identical in most of its properties with sebacic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ir-', '', 'A form of the prefix in-. See In-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iracund', 'a.', 'Irascible; choleric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irade', 'n.', 'A decree of the Sultan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iran', 'n.', 'The native name of Persia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iranian', 'a.', 'Of or pertaining to Iran.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iranian', 'n.', 'A native of Iran; also, the Iranian or Persian language, a\n   division of the Aryan family of languages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iranic', 'a.', 'Iranian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irascibility', 'n.', 'The quality or state of being irascible; irritability\n   of temper; irascibleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irascible', 'a.', 'Prone to anger; easily provoked or inflamed to anger;\n   choleric; irritable; as, an irascible man; an irascible temper or mood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irate', 'a.', 'Angry; incensed; enraged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ire', 'n.', 'Anger; wrath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ireful', 'a.', 'Full of ire; angry; wroth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irefulness', 'n.', 'Wrathfulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irenarch', 'n.', 'An officer in the Greek empire having functions\n   corresponding to those of a justice of the peace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irenic', 'a.', 'Alt. of Irenical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irenical', 'a.', 'Fitted or designed to promote peace; pacific;\n   conciliatory; peaceful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irenicon', 'n.', 'A proposition or device for securing peace, especially in\n   the church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irenics', 'n.', 'That branch of Christian science which treats of the\n   methods of securing unity among Christians or harmony and union among\n   the churches; -- called also Irenical theology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irestone', 'n.', 'Any very hard rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irian', 'a.', 'Of or pertaining to the iris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iricism', 'n.', 'Irishism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iridaceous', 'a.', 'Alt. of Irideous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irideous', 'a.', 'Pertaining to, or resembling, a large natural order of\n   endogenous plants (Iridaceae), which includes the genera Iris, Ixia,\n   Crocus, Gladiolus, and many others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iridal', 'a.', 'Of or pertaining to the iris or rainbow; prismatic; as, the\n   iridal colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iridectomy', 'n.', 'The act or process of cutting out a portion of the iris\n   in order to form an artificial pupil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iridescence', 'n.', 'Exhibition of colors like those of the rainbow; the\n   quality or state of being iridescent; a prismatic play of color; as,\n   the iridescence of mother-of-pearl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iridescent', 'a.', 'Having colors like the rainbow; exhibiting a play of\n   changeable colors; nacreous; prismatic; as, iridescent glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iridian', 'a.', 'Of or pertaining to the iris or rainbow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iridiated', 'a.', 'Iridescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iridic', 'a.', 'Of or pertaining to the iris of the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iridic', 'a.', 'Of or pertaining to iridium; -- said specifically of those\n   compounds in which iridium has a relatively high valence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iridioscope', 'n.', 'A kind of ophthalmoscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iridious', 'a.', 'Of or pertaining to iridium; -- applied specifically to\n   compounds in which iridium has a low valence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iridium', 'n.', 'A rare metallic element, of the same group as platinum,\n   which it much resembles, being silver-white, but harder, and brittle,\n   and indifferent to most corrosive agents. With the exception of osmium,\n   it is the heaviest substance known, its specific gravity being 22.4.\n   Symbol Ir. Atomic weight 192.5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iridized', 'imp. & p. p.', 'of Iridize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iridizing', 'p. pr. & vb. n.', 'of Iridize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iridize', 'v. t.', 'To point or tip with iridium, as a gold pen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iridize', 'v. t.', 'To make iridescent; as, to iridize glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iridoline', 'n.', 'A nitrogenous base C10H9N, extracted from coal-tar\n   naphtha, as an oily liquid. It is a member of the quinoline series, and\n   is probably identical with lepidine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iridosmine', 'n.', 'Alt. of Iridosmium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iridosmium', 'n.', 'The native compound of iridium and osmium. It is found\n   in flattened metallic grains of extreme hardness, and is often used for\n   pointing gold pens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irises', 'pl. ', 'of Iris');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irides', 'pl. ', 'of Iris');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iris', 'n.', 'The goddess of the rainbow, and swift-footed messenger of the\n   gods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iris', 'n.', 'The rainbow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iris', 'n.', 'An appearance resembling the rainbow; a prismatic play of\n   colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iris', 'n.', 'The contractile membrane perforated by the pupil, and forming\n   the colored portion of the eye. See Eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iris', 'n.', 'A genus of plants having showy flowers and bulbous or\n   tuberous roots, of which the flower-de-luce (fleur-de-lis), orris, and\n   other species of flag are examples. See Illust. of Flower-de-luce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iris', 'n.', 'See Fleur-de-lis, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irisated', 'a.', 'Exhibiting the prismatic colors; irised; iridescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iriscope', 'n.', 'A philosophical toy for exhibiting the prismatic tints by\n   means of thin films.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irised', 'a.', 'Having colors like those of the rainbow; iridescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irish', 'a.', 'Of or pertaining to Ireland or to its inhabitants; produced\n   in Ireland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irish', 'n. sing. & pl.', 'The natives or inhabitants of Ireland, esp. the\n   Celtic natives or their descendants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irish', 'n. sing. & pl.', 'The language of the Irish; the Hiberno-Celtic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irish', 'n. sing. & pl.', 'An old game resembling backgammon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irishism', 'n.', 'A mode of speaking peculiar to the Irish; an Hibernicism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irishmen', 'pl. ', 'of Irishman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irishman', 'n.', 'A man born in Ireland or of the Irish race; an Hibernian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irishry', 'n.', 'The Celtic people of Ireland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iritis', 'n.', 'An inflammation of the iris of the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irk', 'v. t.', 'To weary; to give pain; to annoy; -- used only impersonally\n   at present.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irksome', 'a.', 'Wearisome; tedious; disagreeable or troublesome by reason\n   of long continuance or repetition; as, irksome hours; irksome tasks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irksome', 'a.', 'Weary; vexed; uneasy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iron', 'n.', 'The most common and most useful metallic element, being of\n   almost universal occurrence, usually in the form of an oxide (as\n   hematite, magnetite, etc.), or a hydrous oxide (as limonite, turgite,\n   etc.). It is reduced on an enormous scale in three principal forms;\n   viz., cast iron, steel, and wrought iron. Iron usually appears dark\n   brown, from oxidation or impurity, but when pure, or on a fresh\n   surface, is a gray or white metal. It is easily oxidized (rusted) by\n   moisture, and is attacked by many corrosive agents. Symbol Fe (Latin\n   Ferrum). Atomic weight 55.9. Specific gravity, pure iron, 7.86; cast\n   iron, 7.1. In magnetic properties, it is superior to all other\n   substances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iron', 'n.', 'An instrument or utensil made of iron; -- chiefly in\n   composition; as, a flatiron, a smoothing iron, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iron', 'n.', 'Fetters; chains; handcuffs; manacles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iron', 'n.', 'Strength; power; firmness; inflexibility; as, to rule with a\n   rod of iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iron', 'n.', 'Of, or made of iron; consisting of iron; as, an iron bar,\n   dust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iron', 'n.', 'Resembling iron in color; as, iron blackness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iron', 'n.', 'Like iron in hardness, strength, impenetrability, power of\n   endurance, insensibility, etc.;');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iron', 'n.', 'Rude; hard; harsh; severe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iron', 'n.', 'Firm; robust; enduring; as, an iron constitution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iron', 'n.', 'Inflexible; unrelenting; as, an iron will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iron', 'n.', 'Not to be broken; holding or binding fast; tenacious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ironed', 'imp. & p. p.', 'of Iron');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ironing', 'p. pr. & vb. n.', 'of Iron');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iron', 'v. t.', 'To smooth with an instrument of iron; especially, to\n   smooth, as cloth, with a heated flatiron; -- sometimes used with out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iron', 'v. t.', 'To shackle with irons; to fetter or handcuff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iron', 'v. t.', 'To furnish or arm with iron; as, to iron a wagon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ironbark tree', '', 'The Australian Eucalyptus Sideroxylon, used largely by\n   carpenters and shipbuilders; -- called also ironwood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ironbound', 'a.', 'Bound as with iron; rugged; as, an ironbound coast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ironbound', 'a.', 'Rigid; unyielding; as, ironbound traditions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iron-cased', 'a.', 'Cased or covered with iron, as a vessel; ironclad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ironclad', 'a.', 'Clad in iron; protected or covered with iron, as a vessel\n   for naval warfare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ironclad', 'a.', 'Rigorous; severe; exacting; as, an ironclad oath or\n   pledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ironclad', 'n.', 'A naval vessel having the parts above water covered and\n   protected by iron or steel usually in large plates closely joined and\n   made sufficiently thick and strong to resist heavy shot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ironer', 'n.', 'One who, or that which, irons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iron-fisted', 'a.', 'Closefisted; stingy; mean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iron-gray', 'a.', 'Of a gray color, somewhat resembling that of iron\n   freshly broken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iron-gray', 'n.', 'An iron-gray color; also, a horse of this color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ironheads', 'n.', 'A European composite herb (Centaurea nigra); -- so\n   called from the resemblance of its knobbed head to an iron ball fixed\n   on a long handle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iron-hearted', 'a.', 'Hard-hearted; unfeeling; cruel; as, an iron-hearted\n   master.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ironic', 'a.', 'Ironical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ironical', 'a.', 'Pertaining to irony; containing, expressing, or\n   characterized by, irony; as, an ironical remark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ironical', 'a.', 'Addicted to the use of irony; given to irony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ironing', 'n.', 'The act or process of smoothing, as clothes, with hot\n   flatirons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ironing', 'n.', 'The clothes ironed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ironish', 'a.', 'Resembling iron, as in taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ironist', 'n.', 'One who uses irony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ironmaster', 'n.', 'A manufacturer of iron, or large dealer therein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ironmonger', 'n.', 'A dealer in iron or hardware.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ironmongery', 'n.', 'Hardware; a general name for all articles made of\n   iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iron-sick', 'a.', 'Having the ironwork loose or corroded; -- said of a ship\n   when her bolts and nails are so eaten with rust that she has become\n   leaky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iron-sided', 'a.', 'Having iron sides, or very firm sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ironsides', 'n. /', 'A cuirassier or cuirassiers; also, hardy veteran\n   soldiers; -- applied specifically to Cromwells cavalry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ironsmith', 'n.', 'A worker in iron; one who makes and repairs utensils of\n   iron; a blacksmith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ironsmith', 'n.', 'An East Indian barbet (Megalaima faber), inhabiting the\n   Island of Hainan. The name alludes to its note, which resembles the\n   sounds made by a smith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ironstone', 'n.', 'A hard, earthy ore of iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ironware', 'n.', 'Articles made of iron, as household utensils, tools, and\n   the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ironweed', 'n.', 'A tall weed with purplish flowers (Vernonia\n   Noveboracensis). The name is also applied to other plants of the same\n   genus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ironwood', 'n.', 'A tree unusually hard, strong, or heavy wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ironwork', 'n.', 'Anything made of iron; -- a general name of such parts or\n   pieces of a building, vessel, carriage, etc., as consist of iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iron works', '', 'See under Iron, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ironwort', 'n.', 'An herb of the Mint family (Sideritis), supposed to heal\n   sword cuts; also, a species of Galeopsis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irony', 'a.', 'Made or consisting of iron; partaking of iron; iron; as,\n   irony chains; irony particles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irony', 'a.', 'Resembling iron taste, hardness, or other physical property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irony', 'n.', 'Dissimulation; ignorance feigned for the purpose of\n   confounding or provoking an antagonist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irony', 'n.', 'A sort of humor, ridicule, or light sarcasm, which adopts a\n   mode of speech the meaning of which is contrary to the literal sense of\n   the words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iroquois', 'n. sing. & pl.', 'A powerful and warlike confederacy of Indian\n   tribes, formerly inhabiting Central New York and constituting most of\n   the Five Nations. Also, any Indian of the Iroquois tribes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irous', 'a.', 'Irascible; passionate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irp', 'n.', 'Alt. of Irpe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irpe', 'n.', 'A fantastic grimace or contortion of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irp', 'a.', 'Making irps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irradiance', 'n.', 'Alt. of Irradiancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irradiancy', 'n.', 'The act of irradiating; emission of rays of light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irradiancy', 'n.', 'That which irradiates or is irradiated; luster;\n   splendor; irradiation; brilliancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irradiant', 'a.', 'Irradiating or illuminating; as, the irradiant moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irradiated', 'imp. & p. p.', 'of Irradiate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irradiating', 'p. pr. & vb. n.', 'of Irradiate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irradiate', 'v. t.', 'To throw rays of light upon; to illuminate; to\n   brighten; to adorn with luster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irradiate', 'v. t.', 'To enlighten intellectually; to illuminate; as, to\n   irradiate the mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irradiate', 'v. t.', 'To animate by heat or light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irradiate', 'v. t.', 'To radiate, shed, or diffuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irradiate', 'v. i.', 'To emit rays; to shine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irradiate', 'a.', 'Illuminated; irradiated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irradiation', 'n.', 'Act of irradiating, or state of being irradiated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irradiation', 'n.', 'Illumination; irradiance; brilliancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irradiation', 'n.', 'Fig.: Mental light or illumination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irradiation', 'n.', 'The apparent enlargement of a bright object seen upon\n   a dark ground, due to the fact that the portions of the retina around\n   the image are stimulated by the intense light; as when a dark spot on a\n   white ground appears smaller, or a white spot on a dark ground larger,\n   than it really is, esp. when a little out of focus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irradicate', 'v. t.', 'To root deeply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrational', 'a.', 'Not rational; void of reason or understanding; as,\n   brutes are irrational animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrational', 'a.', 'Not according to reason; absurd; foolish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrational', 'a.', 'Not capable of being exactly expressed by an integral\n   number, or by a vulgar fraction; surd; -- said especially of roots. See\n   Surd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrationality', 'n.', 'The quality or state of being irrational.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrationally', 'adv.', 'In an irrational manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrationalness', 'n.', 'Irrationality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrebuttable', 'a.', 'Incapable of being rebutted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreceptive', 'a.', 'Not receiving; incapable of receiving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreclaimable', 'a.', 'Incapable of being reclaimed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrecognition', 'n.', 'A failure to recognize; absence of recognition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrecognizable', 'a.', 'Not recognizable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreconcilability', 'n.', 'The quality or state of being irreconcilable;\n   irreconcilableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreconcilable', 'a.', 'Not reconcilable; implacable; incompatible;\n   inconsistent; disagreeing; as, irreconcilable enemies, statements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreconcile', 'v. t.', 'To prevent from being reconciled; to alienate or\n   disaffect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreconcilement', 'n.', 'The state or quality of being unreconciled;\n   disagreement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreconciliation', 'n.', 'Want of reconciliation; disagreement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrecordable', 'a.', 'Not fit or possible to be recorded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrecoverable', 'a.', 'Not capable of being recovered, regained, or\n   remedied; irreparable; as, an irrecoverable loss, debt, or injury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrecuperable', 'a.', 'Irrecoverable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrecured', 'a.', 'Incurable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrecusable', 'a.', 'Not liable to exception or rejection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irredeemability', 'n.', 'The state or quality of being irredeemable;\n   irredeemableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irredeemable', 'a.', 'Not redeemable; that can not be redeemed; not payable\n   in gold or silver, as a bond; -- used especially of such government\n   notes, issued as currency, as are not convertible into coin at the\n   pleasure of the holder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreducibility', 'n.', 'The state or quality of being irreducible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreducible', 'a.', 'Incapable of being reduced, or brought into a\n   different state; incapable of restoration to its proper or normal\n   condition; as, an irreducible hernia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreducible', 'a.', 'Incapable of being reduced to a simpler form of\n   expression; as, an irreducible formula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreflection', 'n.', 'Want of reflection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreflective', 'a.', 'Not reflective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrefromable', 'a.', 'Incapable of being reformed; incorrigible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrefragability', 'n.', 'The quality or state of being irrefragable;\n   incapability of being refuted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrefragable', 'a.', 'Not refragable; not to be gainsaid or denied; not to\n   be refuted or overthrown; unanswerable; incontestable; undeniable; as,\n   an irrefragable argument; irrefragable evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrefrangibility', 'n.', 'The quality or state of being irrefrangible;\n   irrefrangibleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrefrangible', 'a.', 'Not refrangible; that can not be refracted in\n   passing from one medium to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrefutable', 'a.', 'Incapable of being refuted or disproved; indisputable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irregeneracy', 'n.', 'Unregeneracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irregeneration', 'n.', 'An unregenerate state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irregular', 'a.', 'Not regular; not conforming to a law, method, or usage\n   recognized as the general rule; not according to common form; not\n   conformable to nature, to the rules of moral rectitude, or to\n   established principles; not normal; unnatural; immethodical;\n   unsymmetrical; erratic; no straight; not uniform; as, an irregular\n   line; an irregular figure; an irregular verse; an irregular physician;\n   an irregular proceeding; irregular motion; irregular conduct, etc. Cf.\n   Regular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irregular', 'n.', 'One who is not regular; especially, a soldier not in\n   regular service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irregularist', 'n.', 'One who is irregular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irregularities', 'pl. ', 'of Irregularity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irregularity', 'n.', 'The state or quality of being irregular; that which\n   is irregular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irregularly', 'adv.', 'In an irregular manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irregulate', 'v. t.', 'To make irregular; to disorder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irregulous', 'a.', 'Lawless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrejectable', 'a.', 'That can not be rejected; irresistible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrelapsable', 'a.', 'Not liable to relapse; secure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrelate', 'a.', 'Irrelative; unconnected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrelation', 'n.', 'The quality or state of being irrelative; want of\n   connection or relation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrelative', 'a.', 'Not relative; without mutual relations; unconnected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrelavance', 'n.', 'Irrelevancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrelavancy', 'n.', 'The quality or state of being irrelevant; as, the\n   irrelevancy of an argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrelavant', 'a.', 'Not relevant; not applicable or pertinent; not bearing\n   upon or serving to support; foreign; extraneous; as, testimony or\n   arguments irrelevant to a case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrelievable', 'a.', 'Not admitting relief; incurable; hopeless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreligion', 'n.', 'The state of being irreligious; want of religion;\n   impiety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreligionist', 'n.', 'One who is irreligious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreligious', 'a.', 'Destitute of religion; not controlled by religious\n   motives or principles; ungodly. Cf. Impious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreligious', 'a.', 'Indicating a want of religion; profane; wicked; as,\n   irreligious speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreligiously', 'adv.', 'In an irreligious manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreligiousness', 'n.', 'The state or quality of being irreligious;\n   ungodliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irremeable', 'a.', 'Admitting no return; as, an irremeable way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irremediable', 'a.', 'Not to be remedied, corrected, or redressed;\n   incurable; as, an irremediable disease or evil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irremediableness', 'n.', 'The state or quality of being irremediable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irremediably', 'adv.', 'In a manner, or to a degree, that precludes remedy,\n   cure, or correction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irremissible', 'a.', 'Not remissible; unpardonable; as, irremissible\n   crimes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irremission', 'n.', 'Refusal of pardon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irremissive', 'a.', 'Not remitting; unforgiving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irremittable', 'a.', 'Not capable of being remitted; irremissible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irremobability', 'n.', 'The quality or state of being irremovable;\n   immovableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irremovable', 'a.', 'Not removable; immovable; inflexible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irremoval', 'n.', 'Absence of removal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irremunerable', 'a.', 'Not remunerable; not capable of remuneration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrenowned', 'a.', 'Not renowned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreparability', 'n.', 'The quality or state of being irreparable;\n   irreparableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreparable', 'a.', 'Not reparable; not capable of being repaired,\n   recovered, regained, or remedied; irretrievable; irremediable; as, an\n   irreparable breach; an irreparable loss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreparableness', 'n.', 'Quality of being irreparable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreparably', 'adv.', 'In an irreparable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrepealability', 'n.', 'The quality or state of being irrepealable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrepealable', 'a.', 'Not repealable; not capable of being repealed or\n   revoked, as a law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrepentance', 'n.', 'Want of repentance; impenitence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrepleviable', 'a.', 'Alt. of Irreplevisable');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreplevisable', 'a.', 'Not capable of being replevied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreprehensible', 'a.', 'Not reprehensible; blameless; innocent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrepresentable', 'a.', 'Not capable of being represented or portrayed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrepressible', 'a.', 'Not capable of being repressed, restrained, or\n   controlled; as, irrepressible joy; an irrepressible conflict.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrepressibly', 'adv.', 'In a manner or to a degree that can not be\n   repressed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreproachable', 'a.', 'Not reproachable; above reproach; not deserving\n   reproach; blameless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreproachableness', 'n.', 'The quality or state of being irreproachable;\n   integrity; innocence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreproachably', 'adv.', 'In an irreproachable manner; blamelessly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreprovable', 'a.', 'Incapable of being justly reproved; irreproachable;\n   blameless; upright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreptitious', 'a.', 'Surreptitious; spurious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreputable', 'a.', 'Disreputable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irresilient', 'a.', 'Not resilient; not recoiling or rebounding; inelastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irresistance', 'n.', 'Nonresistance; passive submission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irresistibility', 'n.', 'The quality or state of being irrestible,\n   irresistibleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irresistible', 'a.', 'That can not be successfully resisted or opposed;\n   superior to opposition; resistless; overpowering; as, an irresistible\n   attraction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irresistibleness', 'n.', 'Quality of being irrestible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irresistibly', 'adv.', 'In an irrestible manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irresistless', 'a.', 'Irresistible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irresoluble', 'a.', 'Incapable of being dissolved or resolved into parts;\n   insoluble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irresoluble', 'a.', 'Incapable of being relieved or assisted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irresolubleness', 'n.', 'The state or quality of being irresoluble;\n   insolubility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irresolute', 'a.', 'Not resolute; not decided or determined; wavering;\n   given to doubt or irresolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irresolution', 'n.', 'Want of resolution; want of decision in purpose; a\n   fluctuation of mind, as in doubt, or between hope and fear;\n   irresoluteness; indecision; vacillation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irresolvability', 'n.', 'The quality of being irresolvable;\n   irresolvableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irresolvable', 'a.', 'Incapable of being resolved; not separable into\n   component parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irresolvableness', 'n.', 'The quality or state of being irresolvable;\n   irresolvability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irresolvedly', 'adv.', 'Without settled determination; in a hesitating\n   manner; doubtfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrespective', 'a.', 'Without regard for conditions, circumstances, or\n   consequences; unbiased; independent; impartial; as, an irrespective\n   judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrespective', 'a.', 'Disrespectful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrespectively', 'adv.', 'Without regard to conditions; not making\n   circumstances into consideration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrespirable', 'a.', 'Unfit for respiration; not having the qualities\n   necessary to support animal life; as, irrespirable air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irresponsibility', 'n.', 'Want of, or freedom from, responsibility or\n   accountability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irresponsible', 'a.', 'Nor responsible; not liable or able to answer fro\n   consequences; innocent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irresponsible', 'a.', 'Not to be trusted; unreliable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irresponsibly', 'adv.', 'So as not to be responsible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irresponsive', 'a.', 'Not responsive; not able, ready, or inclined to\n   respond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irresuscitable', 'a.', 'Incapable of being resuscitated or revived.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irretention', 'n.', 'Want of retaining power; forgetfulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irretentive', 'a.', 'Not retentive; as, an irretentive memory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irretraceable', 'a.', 'Incapable of being retraced; not retraceable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irretractile', 'a.', 'Not retractile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irretractile', 'a.', 'Not tractile or ductile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irretrievable', 'a.', 'Not retrievable; irrecoverable; irreparable; as, an\n   irretrievable loss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irretrievableness', 'n.', 'The state or quality of being irretrievable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irretrievably', 'adv.', 'In an irretrievable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreturnable', 'a.', 'Not to be returned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrevealable', 'a.', 'Incapable of being revealed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreverence', 'n.', 'The state or quality of being irreverent; want of\n   proper reverence; disregard of the authority and character of a\n   superior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreverend', 'a.', 'Irreverent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreverent', 'a.', 'Not reverent; showing a want of reverence; expressive\n   of a want of veneration; as, an irreverent babbler; an irreverent jest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreverently', 'adv.', 'In an irreverent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreversibility', 'n.', 'The state or quality of being irreversible;\n   irreversibleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreversible', 'a.', 'Incapable of being reversed or turned about or back;\n   incapable of being made to run backward; as, an irreversible engine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreversible', 'a.', 'Incapable of being reversed, recalled, repealed, or\n   annulled; as, an irreversible sentence or decree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreversibleness', 'n.', 'The state or quality of being irreversible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irreversibly', 'adv.', 'In an irreversible manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrevocability', 'n.', 'The state or quality of being irrevocable;\n   irrevocableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrevocable', 'a.', 'Incapable of being recalled or revoked; unchangeable;\n   irreversible; unalterable; as, an irrevocable promise or decree;\n   irrevocable fate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrevokable', 'a.', 'Irrevocable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrevoluble', 'a.', 'That has no finite period of revolution; not\n   revolving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrhetorical', 'a.', 'Not rethorical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrigated', 'imp. & p. p.', 'of Irrigate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrigating', 'p. pr. & vb. n.', 'of Irrigate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrigate', 'v. t.', 'To water; to wet; to moisten with running or dropping\n   water; to bedew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrigate', 'v. t.', 'To water, as land, by causing a stream to flow upon,\n   over, or through it, as in artificial channels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrigation', 'n.', 'The act or process of irrigating, or the state of being\n   irrigated; especially, the operation of causing water to flow over\n   lands, for nourishing plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irriguous', 'a.', 'Watered; watery; moist; dewy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irriguous', 'a.', 'Gently penetrating or pervading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrisible', 'a.', 'Not risible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrision', 'n.', 'The act of laughing at another; derision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irritability', 'n.', 'The state or quality of being irritable; quick\n   excitability; petulance; fretfulness; as, irritability of temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irritability', 'n.', 'A natural susceptibility, characteristic of all\n   living organisms, tissues, and cells, to the influence of certain\n   stimuli, response being manifested in a variety of ways, -- as that\n   quality in plants by which they exhibit motion under suitable\n   stimulation; esp., the property which living muscle processes, of\n   responding either to a direct stimulus of its substance, or to the\n   stimulating influence of its nerve fibers, the response being indicated\n   by a change of form, or contraction; contractility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irritability', 'n.', 'A condition of morbid excitability of an organ or\n   part of the body; undue susceptibility to the influence of stimuli. See\n   Irritation, n., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irritable', 'a.', 'Capable of being irriated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irritable', 'a.', 'Very susceptible of anger or passion; easily inflamed or\n   exasperated; as, an irritable temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irritable', 'a.', 'Endowed with irritability; susceptible of irritation;\n   capable of being excited to action by the application of certain\n   stimuli.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irritable', 'a.', 'Susceptible of irritation; unduly sensitive to irritants\n   or stimuli. See Irritation, n., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irritableness', 'n.', 'Irritability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irritably', 'adv.', 'In an irritable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irritancy', 'n.', 'The state or quality of being null and void; invalidity;\n   forfeiture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irritancy', 'n.', 'The state o quality of being irritant or irritating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irritant', 'a.', 'Rendering null and void; conditionally invalidating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irritant', 'a.', 'Irritating; producing irritation or inflammation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irritant', 'n.', 'That which irritates or excites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irritant', 'n.', 'Any agent by which irritation is produced; as, a chemical\n   irritant; a mechanical or electrical irritant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irritant', 'n.', 'A poison that produces inflammation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irritate', 'v. t.', 'To render null and void.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irritated', 'imp. & p. p.', 'of Irritate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irritating', 'p. pr. & vb. n.', 'of Irritate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irritate', 'v. t.', 'To increase the action or violence of; to heighten\n   excitement in; to intensify; to stimulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irritate', 'v. t.', 'To excite anger or displeasure in; to provoke; to\n   tease; to exasperate; to annoy; to vex; as, the insolence of a tyrant\n   irritates his subjects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irritate', 'v. t.', 'To produce irritation in; to stimulate; to cause to\n   contract. See Irritation, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irritate', 'n.', 'To make morbidly excitable, or oversensitive; to fret;\n   as, the skin is irritated by friction; to irritate a wound by a coarse\n   bandage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irritate', 'a.', 'Excited; heightened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irritation', 'n.', 'The act of irritating, or exciting, or the state of\n   being irritated; excitement; stimulation, usually of an undue and\n   uncomfortable kind; especially, excitement of anger or passion;\n   provocation; annoyance; anger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irritation', 'n.', 'The act of exciting, or the condition of being excited\n   to action, by stimulation; -- as, the condition of an organ of sense,\n   when its nerve is affected by some external body; esp., the act of\n   exciting muscle fibers to contraction, by artificial stimulation; as,\n   the irritation of a motor nerve by electricity; also, the condition of\n   a muscle and nerve, under such stimulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irritation', 'n.', 'A condition of morbid excitability or oversensitiveness\n   of an organ or part of the body; a state in which the application of\n   ordinary stimuli produces pain or excessive or vitiated action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irritative', 'a.', 'Serving to excite or irritate; irritating; as, an\n   irritative agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irritative', 'a.', 'Accompanied with, or produced by, increased action or\n   irritation; as, an irritative fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irritatory', 'a.', 'Exciting; producing irritation; irritating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrorated', 'imp. & p. p.', 'of Irrorate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrorating', 'p. pr. & vb. n.', 'of Irrorate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrorate', 'v. t.', 'To sprinkle or moisten with dew; to bedew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrorate', 'a.', 'Covered with minute grains, appearing like fine sand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irroration', 'n.', 'The act of bedewing; the state of being moistened with\n   dew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrotational', 'a.', 'Not rotatory; passing from one point to another by a\n   movement other than rotation; -- said of the movement of parts of a\n   liquid or yielding mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrubrical', 'a.', 'Contrary to the rubric; not rubrical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrugate', 'v. t.', 'To wrinkle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irrupted', 'a.', 'Broken with violence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irruption', 'n.', 'A bursting in; a sudden, violent rushing into a place;\n   as, irruptions of the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irruption', 'n.', 'A sudden and violent inroad, or entrance of invaders;\n   as, the irruptions of the Goths into Italy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irruptive', 'a.', 'Rushing in or upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Irvingite', 'n.', 'The common designation of one a sect founded by the Rev.\n   Edward Irving (about 1830), who call themselves the Catholic Apostolic\n   Church. They are highly ritualistic in worship, have an elaborate\n   hierarchy of apostles, prophets, etc., and look for the speedy coming\n   of Christ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Is-', '', 'See Iso-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Is', 'v. i.', 'The third person singular of the substantive verb be, in the\n   indicative mood, present tense; as, he is; he is a man. See Be.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isabel', '', 'Alt. of Isabel color');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isabel color', '', 'See Isabella.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isabella', '', 'Alt. of Isabella color');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isabella color', '', 'A brownish yellow color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isabella grape', '', 'A favorite sweet American grape of a purple color.\n   See Fox grape, under Fox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isabella moth', '', 'A common American moth (Pyrrharctia isabella), of an\n   isabella color. The larva, called woolly bear and hedgehog caterpillar,\n   is densely covered with hairs, which are black at each end of the body,\n   and red in the middle part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isabelline', 'a.', 'Of an isabel or isabella color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isagelous', 'a.', 'Containing the same information; as, isagelous\n   sentences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isagel', 'n.', 'One of two or more objects containing the same information.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isagoge', 'n.', 'An introduction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isagogic', 'a.', 'Alt. of Isagogical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isagogical', 'a.', 'Introductory; especially, introductory to the study of\n   theology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isagogics', 'n.', 'That part of theological science directly preliminary to\n   actual exegesis, or interpretation of the Scriptures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isagon', 'a.', 'A figure or polygon whose angles are equal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isapostolic', 'a.', 'Having equal, or almost equal, authority with the\n   apostles of their teachings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isatic', 'a.', 'Alt. of Isatinic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isatinic', 'a.', 'Pertaining to, or derived from, isatin; as, isatic acid,\n   which is also called trioxindol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isatide', 'n.', 'A white crystalline substance obtained by the partial\n   reduction of isatin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isatin', 'n.', 'An orange-red crystalline substance, C8H5NO2, obtained by\n   the oxidation of indigo blue. It is also produced from certain\n   derivatives of benzoic acid, and is one important source of artificial\n   indigo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isatis', 'n.', 'A genus of herbs, some species of which, especially the\n   Isatis tinctoria, yield a blue dye similar to indigo; woad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isatogen', 'n.', 'A complex nitrogenous radical, C8H4NO2, regarded as the\n   essential residue of a series of compounds, related to isatin, which\n   easily pass by reduction to indigo blue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isatropic', 'a.', 'Pertaining to, or designating, an acid obtained from\n   atropine, and isomeric with cinnamic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ischias', 'a.', 'See Ischial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ischiadic', 'a.', 'Ischial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ischial', 'a.', 'Of or pertaining to the ischium or hip; ischiac;\n   ischiadic; ischiatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ischiatic', 'a.', 'Same as Ishial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ischiocapsular', 'a.', 'Of or pertaining to the ischium and the capsule of\n   the hip joint; as, the ischiocapsular ligament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ischiocerite', 'n.', 'The third joint or the antennae of the Crustacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ischion', 'n.', 'Alt. of Ischium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ischium', 'n.', 'The ventral and posterior of the three principal bones\n   composing either half of the pelvis; seat bone; the huckle bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ischium', 'n.', 'One of the pleurae of insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ischiopodite', 'n.', 'The third joint of the typical appendages of\n   Crustacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ischiorectal', 'a.', 'Of or pertaining to the region between the rectum and\n   ishial tuberosity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ischuretic', 'a.', 'Having the quality of relieving ischury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ischuretic', 'n.', 'An ischuretic medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ischury', 'n.', 'A retention or suppression of urine.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ise', '', 'See -ize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isentropic', 'a.', 'Having equal entropy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isethionic', 'a.', 'Pertaining to, derived from, or designating, an acid,\n   HO.C2H4.SO3H, obtained as an oily or crystalline substance, by the\n   action of sulphur trioxide on alcohol or ether. It is derivative of\n   sulphuric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ish', '', 'A suffix used to from adjectives from nouns and from\n   adjectives. It denotes relation, resemblance, similarity, and sometimes\n   has a diminutive force; as, selfish, boyish, brutish; whitish, somewhat\n   white.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ish', '', 'A verb ending, originally appearing in certain verbs of French\n   origin; as, abolish, cherish, finish, furnish, garnish, impoverish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ishmaelite', 'n.', 'A descendant of Ishmael (the son of Abraham and Hagar),\n   of whom it was said, \"His hand will be against every man, and every\n   mans hand against him.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ishmaelite', 'n.', 'One at enmity with society; a wanderer; a vagabond; an\n   outcast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ishmaelite', 'n.', 'See Ismaelian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ishmaelitish', 'a.', 'Of, pertaining to, or resembling, an Ishmaelite or\n   the Ishmaelites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isiac', 'a.', 'Pertaining to the goddess Isis; as, Isiac mysteries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isicle', 'n.', 'A icicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isidorian', 'a.', 'Pertaining, or ascribed, to Isidore; as, the Isidorian\n   decretals, a spurious collection of decretals published in the ninth\n   century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isinglass', 'n.', 'A semitransparent, whitish, and very pure from of\n   gelatin, chiefly prepared from the sounds or air bladders of various\n   species of sturgeons (as the Acipenser huso) found in the of Western\n   Russia. It used for making jellies, as a clarifier, etc. Cheaper forms\n   of gelatin are not unfrequently so called. Called also fish glue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isinglass', 'n.', 'A popular name for mica, especially when in thin sheets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isis', 'n.', 'The principal goddess worshiped by the Egyptians. She was\n   regarded as the mother of Horus, and the sister and wife of Osiris. The\n   Egyptians adored her as the goddess of fecundity, and as the great\n   benefactress of their country, who instructed their ancestors in the\n   art of agriculture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isis', 'n.', 'Any coral of the genus Isis, or family Isidae, composed of\n   joints of white, stony coral, alternating with flexible, horny joints.\n   See Gorgoniacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isis', 'n.', 'One of the asteroids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Islam', 'n.', 'The religion of the Mohammedans; Mohammedanism; Islamism.\n   Their formula of faith is: There is no God but Allah, and Mohammed is\n   his prophet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Islam', 'n.', 'The whole body of Mohammedans, or the countries which they\n   occupy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Islamism', 'n.', 'The faith, doctrines, or religious system of the\n   Mohammedans; Mohammedanism; Islam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Islamite', 'n.', 'A Mohammedan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Islamitic', 'a.', 'Of or pertaining to Islam; Mohammedan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Islamized', 'imp. & p. p.', 'of Islamize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Islamizing', 'p. pr. & vb. n.', 'of Islamize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Islamize', 'v. i. & t.', 'To conform, or cause to conform, to the religion\n   of Islam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Island', 'n.', 'A tract of land surrounded by water, and smaller than a\n   continent. Cf. Continent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Island', 'n.', 'Anything regarded as resembling an island; as, an island of\n   ice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Island', 'n.', 'See Isle, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Island', 'v. t.', 'To cause to become or to resemble an island; to make an\n   island or islands of; to isle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Island', 'v. t.', 'To furnish with an island or with islands; as, to island\n   the deep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Islander', 'n.', 'An inhabitant of an island.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Islandy', 'a.', 'Of or pertaining to islands; full of islands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isle', 'n.', 'See Aisle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isle', 'n.', 'An island.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isle', 'n.', 'A spot within another of a different color, as upon the wings\n   of some insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isle', 'v. t.', 'To cause to become an island, or like an island; to\n   surround or encompass; to island.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Islet', 'n.', 'A little island.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ism', '', 'A suffix indicating an act, a process, the result of an act or\n   a process, a state; also, a characteristic (as a theory, doctrine,\n   idiom, etc.); as, baptism, galvanism, organism, hypnotism, socialism,\n   sensualism, Anglicism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ism', 'n.', 'A doctrine or theory; especially, a wild or visionary theory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ismaelian', 'n.', 'Alt. of Ismaelite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ismaelite', 'n.', 'One of a sect of Mohammedans who favored the pretensions\n   of the family of Mohammed ben Ismael, of the house Ali.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iso-', '', 'Alt. of Is-');
  INSERT INTO words (word, word_type, description) VALUES  (    'Is-', '', 'A prefix or combining form, indicating identity, or equality;\n   the same numerical value; as in isopod, isomorphous, isochromatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Is-', '', 'Applied to certain compounds having the same composition but\n   different properties; as in isocyanic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Is-', '', 'Applied to compounds of certain isomeric series in whose\n   structure one carbon atom, at least, is connected with three other\n   carbon atoms; -- contrasted with neo- and normal; as in isoparaffine;\n   isopentane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isobar', 'n.', 'A line connecting or marking places upon the surface of the\n   earth where height of the barometer reduced to sea level is the same\n   either at a given time, or for a certain period (mean height), as for a\n   year; an isopiestic line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isobaric', 'a.', 'Denoting equal pressure; as, an isobaric line;\n   specifically, of or pertaining to isobars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isobar', 'n.', 'The quality or state of being equal in weight, especially\n   in atmospheric pressure. Also, the theory, method, or application of\n   isobaric science.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isobarometric', 'a.', 'Indicating equal barometric pressure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isobathytherm', 'n.', 'A line connecting the points on the surface of the\n   earth where a certain temperature is found at the same depth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isobathythermic', 'a.', 'Of or pertaining to an isobathytherm; possessing\n   or indicating the same temperature at the same depth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isocephalism', 'n.', 'A peculiarity in the design of bas-relief by which\n   the heads of human figures are kept at the same height from the ground,\n   whether the personages are seated, standing, or mounted on horseback;\n   -- called also isokephaleia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isochasm', 'n.', 'A line connecting places on the earths surface at which\n   there is the same mean frequency of auroras.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isochasmic', 'a.', 'Indicating equal auroral display; as, an isochasmic\n   line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isocheim', 'n.', 'A line connecting places on the earth having the same\n   mean winter temperature. Cf. Isothere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isocheimal', 'a.', 'Alt. of Isochimal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isochimal', 'a.', 'Pertaining to, having the nature of, or making,\n   isocheims; as, an isocheimal line; an isocheimal chart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isocheimenal', 'a.', 'Alt. of Isochimenal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isochimenal', 'a.', 'The same as Isocheimal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isocheimic', 'a.', 'The same as Isocheimal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isochimene', 'n.', 'The same as Isocheim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isochromatic', 'a.', 'Having the same color; connecting parts having the\n   same color, as lines drawn through certain points in experiments on the\n   chromatic effects of polarized light in crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isochronal', 'a.', 'Uniform in time; of equal time; performed in equal\n   times; recurring at regular intervals; isochronal vibrations or\n   oscillations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isochronic', 'a.', 'Isochronal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isochronism', 'n.', 'The state or quality of being isochronous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isochronon', 'n.', 'A clock that is designed to keep very accurate time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isochronous', 'a.', 'Same as Isochronal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isochroous', 'a.', 'Having the same tint or color throughout; uniformly or\n   evenly colored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isoclinal', 'a.', 'Alt. of Isoclinic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isoclinic', 'a.', 'Of or pertaining to, or indicating, equality of\n   inclination or dip; having equal inclination or dip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isocrymal', 'a.', 'Pertaining to, having the nature of, or illustrating, an\n   isocryme; as, an isocrymal line; an isocrymal chart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isocryme', 'n.', 'A line connecting points on the earths surface having\n   the same mean temperature in the coldest month of the year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isocrymic', 'a.', 'Isocrymal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isocyanic', 'a.', 'Designating an acid isomeric with cyanic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isocyanuric', 'a.', 'Designating, or pertaining to, an acid isomeric with\n   cyanuric acid, and called also fulminuric acid. See under Fulminuric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isodiabatic', 'a.', 'Pertaining to the reception or the giving out of equal\n   quantities of heat by a substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isodiametric', 'a.', 'Developed alike in the directions of the several\n   lateral axes; -- said of crystals of both the tetragonal and hexagonal\n   systems.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isodiametric', 'a.', 'Having the several diameters nearly equal; -- said of\n   the cells of ordinary parenchyma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isodimorphic', 'a.', 'Isodimorphous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isodimorphism', 'n.', 'Isomorphism between the two forms severally of two\n   dimorphous substances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isodimorphous', 'a.', 'Having the quality of isodimorphism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isodulcite', 'n.', 'A white, crystalline, sugarlike substance, obtained by\n   the decomposition of certain glucosides, and intermediate in nature\n   between the hexacid alcohols (ductile, mannite, etc.) and the glucoses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isodynamic', 'a.', 'Of, pertaining to, having, or denoting, equality of\n   force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isodynamous', 'a.', 'Of equal force or size.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isogeotherm', 'n.', 'A line or curved surface passing beneath the earths\n   surface through points having the same mean temperature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isogeothermal', 'a.', 'Alt. of Isogeothermic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isogeothermic', 'a.', 'Pertaining to, having the nature of, or marking,\n   isogeotherms; as, an isogeothermal line or surface; as isogeothermal\n   chart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isogeothermic', 'n.', 'An isogeotherm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isogonic', 'a.', 'Pertaining to, or noting, equal angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isogonic', 'a.', 'Characterized by isogonism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isogonism', 'n.', 'The quality of having similar sexual zooids or\n   gonophores and dissimilar hydrants; -- said of certain hydroids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isographic', 'a.', 'Of or pertaining to isography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isography', 'n.', 'Imitation of anothers handwriting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isohyetose', 'a.', 'Of or pertaining to lines connecting places on the\n   earths surface which have a mean annual rainfall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isohyetose', 'n.', 'An isohyetose line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isolable', 'a.', 'Capable of being isolated, or of being obtained in a pure\n   state; as, gold is isolable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isolated', 'imp. & p. p.', 'of Isolate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isolating', 'p. pr. & vb. n.', 'of Isolate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isolate', 'v. t.', 'To place in a detached situation; to place by itself or\n   alone; to insulate; to separate from others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isolate', 'v. t.', 'To insulate. See Insulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isolate', 'v. t.', 'To separate from all foreign substances; to make pure;\n   to obtain in a free state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isolated', 'a.', 'Placed or standing alone; detached; separated from\n   others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isolatedly', 'adv.', 'In an isolated manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isolation', 'n.', 'The act of isolating, or the state of being isolated;\n   insulation; separation; loneliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isolator', 'n.', 'One who, or that which, isolates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isologous', 'a.', 'Having similar proportions, similar relations, or\n   similar differences of composition; -- said specifically of groups or\n   series which differ by a constant difference; as, ethane, ethylene, and\n   acetylene, or their analogous compounds, form an isologous series.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isomer', 'n.', 'A body or compound which is isomeric with another body or\n   compound; a member of an isomeric series.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isomeric', 'a.', 'Having the same percentage composition; -- said of two or\n   more different substances which contain the same ingredients in the\n   same proportions by weight, often used with with. Specif.: (a)\n   Polymeric; i. e., having the same elements united in the same\n   proportion by weight, but with different molecular weights; as,\n   acetylene and benzine are isomeric (polymeric) with each other in this\n   sense. See Polymeric. (b) Metameric; i. e., having the same elements\n   united in the same proportions by weight, and with the same molecular\n   weight, but which a different structure or arrangement of the ultimate\n   parts; as, ethyl alcohol and methyl ether are isomeric (metameric) with\n   each other in this sense. See Metameric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isomeride', 'n.', 'An isomer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isomerism', 'n.', 'The state, quality, or relation, of two or more isomeric\n   substances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isomeromorphism', 'n.', 'Isomorphism between substances that are isomeric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isometric', 'a.', 'Alt. of Isometrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isometrical', 'a.', 'Pertaining to, or characterized by, equality of\n   measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isometrical', 'a.', 'Noting, or conforming to, that system of\n   crystallization in which the three axes are of equal length and at\n   right angles to each other; monometric; regular; cubic. Cf.\n   Crystallization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isomorph', 'n.', 'A substance which is similar to another in crystalline\n   form and composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isomorphic', 'a.', 'Isomorphous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isomorphism', 'n.', 'A similarity of crystalline form between substances of\n   similar composition, as between the sulphates of barium (BaSO4) and\n   strontium (SrSO4). It is sometimes extended to include similarity of\n   form between substances of unlike composition, which is more properly\n   called homoeomorphism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isomorphous', 'a.', 'Having the quality of isomorphism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isonandra', 'n.', 'A genus of sapotaceous trees of India. Isonandra Gutta\n   is the principal source of gutta-percha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isonephelic', 'a.', 'Having, or indicating, an equal amount of cloudiness\n   for a given period; as, isonephelic regions; an isonephelic line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isonicotine', 'n.', 'A crystalline, nitrogenous base, C10H14N2, isomeric\n   with nicotine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isonicotinic', 'a.', 'Pertaining to, or derived from, isonicotine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isonicotinic', 'a.', 'Pertaining to, or designating, an acid isomeric with\n   nicotinic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isonitroso-', '', 'A combining from (also used adjectively), signifying:\n   Pertaining to, or designating, the characteristic, nitrogenous radical,\n   NOH, called the isonitroso group.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isonomic', 'a.', 'The same, or equal, in law or right; one in kind or\n   origin; analogous; similar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isonomy', 'n.', 'Equal law or right; equal distribution of rights and\n   privileges; similarity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isopathy', 'n.', 'The system which undertakes to cure a disease by means of\n   the virus of the same disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isopathy', 'n.', 'The theory of curing a diseased organ by eating the\n   analogous organ of a healthy animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isopathy', 'n.', 'The doctrine that the power of therapeutics is equal to\n   that of the causes of disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isopepsin', 'n.', 'Pepsin modified by exposure to a temperature of from 40\n   to 60 C.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isoperimetrical', 'a.', 'Having equal perimeters of circumferences; as,\n   isoperimetrical figures or bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isoperimetry', 'n.', 'The science of figures having equal perimeters or\n   boundaries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isopiestic', 'a.', 'Having equal pressure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isopleura', 'n. pl.', 'A subclass of Gastropoda, in which the body is\n   symmetrical, the right and left sides being equal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isopod', 'a.', 'Having the legs similar in structure; belonging to the\n   Isopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isopod', 'n.', 'One of the Isopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isopoda', 'n. pl.', 'An order of sessile-eyed Crustacea, usually having\n   seven pairs of legs, which are all similar in structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isopodiform', 'a.', 'Having the shape of an isopod; -- said of the larvae\n   of certain insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isopodous', 'a.', 'Same as Isopod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isopogonous', 'a.', 'Having the two webs equal in breath; -- said of\n   feathers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isoprene', 'n.', 'An oily, volatile hydrocarbon, obtained by the\n   distillation of caoutchouc or guttaipercha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isopycnic', 'a.', 'Having equal density, as different regions of a medium;\n   passing through points at which the density is equal; as, an isopycnic\n   line or surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isopycnic', 'n.', 'A line or surface passing through those points in a\n   medium, at which the density is the same.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isorcin', 'n.', 'A crystalline hydrocarbon derivative, metameric with\n   orcin, but produced artificially; -- called also cresorcin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isorropic', 'a.', 'Of equal value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isosceles', 'a.', 'Having two legs or sides that are equal; -- said of a\n   triangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isospondyli', 'n. pl.', 'An extensive order of fishes, including the\n   salmons, herrings, and many allied forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isospondylous', 'a.', 'Of or pertaining to the Isospondyli; having the\n   anterior vertebrae separate and normal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isosporic', 'a.', 'Producing but one kind of spore, as the ferns and\n   Equiseta. Cf. Heterosporic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isostemonous', 'a.', 'Having exactly as many stamens as petals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isostemony', 'n.', 'The quality or state of being isostemonous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isosulphocyanate', 'n.', 'A salt of isosulphocyanic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isosulphocyanic', 'a.', 'Pertaining to, or designating, an acid, HNCS,\n   isomeric with sulphocyanic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isotheral', 'a.', 'Having the nature of an isothere; indicating the\n   distribution of temperature by means of an isothere; as, an isotheral\n   chart or line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isothere', 'n.', 'A line connecting points on the earths surface having\n   the same mean summer temperature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isotherm', 'n.', 'A line connecting or marking points on the earths\n   surface having the same temperature. This may be the temperature for a\n   given time of observation, or the mean temperature for a year or other\n   period. Also, a similar line based on the distribution of temperature\n   in the ocean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isothermal', 'a.', 'Relating to equality of temperature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isothermal', 'a.', 'Having reference to the geographical distribution of\n   temperature, as exhibited by means of isotherms; as, an isothermal\n   line; an isothermal chart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isothermobath', 'n.', 'A line drawn through points of equal temperature in\n   a vertical section of the ocean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isothermobathic', 'a.', 'Of or pertaining to an isothermobath; possessing\n   or indicating equal temperatures in a vertical section, as of the\n   ocean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isotherombrose', 'n.', 'A line connecting or marking points on the earths\n   surface, which have the same mean summer rainfall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isotonic', 'a.', 'Having or indicating, equal tones, or tension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isotrimorphic', 'a.', 'Isotrimorphous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isotrimorphism', 'n.', 'Isomorphism between the three forms, severally, of\n   two trimorphous substances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isotrimorphous', 'a.', 'Having the quality of isotrimorphism;\n   isotrimorphic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isotropic', 'a.', 'Having the same properties in all directions;\n   specifically, equally elastic in all directions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isotropism', 'n.', 'Isotropy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isotropous', 'a.', 'Isotropic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isotropy', 'n.', 'Uniformity of physical properties in all directions in a\n   body; absence of all kinds of polarity; specifically, equal elasticity\n   in all directions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isouric', 'a.', 'Pertaining to, or designating, a complex nitrogenous acid,\n   isomeric with uric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Israelite', 'n.', 'A descendant of Israel, or Jacob; a Hebrew; a Jew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Israelitic', 'a.', 'Alt. of Israelitish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Israelitish', 'a.', 'Of or pertaining to Israel, or to the Israelites;\n   Jewish; Hebrew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Issuable', 'a.', 'Leading to, producing, or relating to, an issue; capable\n   of being made an issue at law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Issuable', 'a.', 'Lawful or suitable to be issued; as, a writ issuable on\n   these grounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Issuably', 'adv.', 'In an issuable manner; by way of issue; as, to plead\n   issuably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Issuance', 'n.', 'The act of issuing, or giving out; as, the issuance of an\n   order; the issuance of rations, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Issuant', 'a.', 'Issuing or coming up; -- a term used to express a charge\n   or bearing rising or coming out of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Issue', 'n.', 'The act of passing or flowing out; a moving out from any\n   inclosed place; egress; as, the issue of water from a pipe, of blood\n   from a wound, of air from a bellows, of people from a house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Issue', 'n.', 'The act of sending out, or causing to go forth; delivery;\n   issuance; as, the issue of an order from a commanding officer; the\n   issue of money from a treasury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Issue', 'n.', 'That which passes, flows, or is sent out; the whole quantity\n   sent forth or emitted at one time; as, an issue of bank notes; the\n   daily issue of a newspaper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Issue', 'n.', 'Progeny; a child or children; offspring. In law, sometimes,\n   in a general sense, all persons descended from a common ancestor; all\n   lineal descendants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Issue', 'n.', 'Produce of the earth, or profits of land, tenements, or\n   other property; as, A conveyed to B all his right for a term of years,\n   with all the issues, rents, and profits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Issue', 'n.', 'A discharge of flux, as of blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Issue', 'n.', 'An artificial ulcer, usually made in the fleshy part of the\n   arm or leg, to produce the secretion and discharge of pus for the\n   relief of some affected part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Issue', 'n.', 'The final outcome or result; upshot; conclusion; event;\n   hence, contest; test; trial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Issue', 'n.', 'A point in debate or controversy on which the parties take\n   affirmative and negative positions; a presentation of alternatives\n   between which to choose or decide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Issue', 'n.', 'In pleading, a single material point of law or fact\n   depending in the suit, which, being affirmed on the one side and denied\n   on the other, is presented for determination. See General issue, under\n   General, and Feigned issue, under Feigned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Issued', 'imp. & p. p.', 'of Issue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Issuing', 'p. pr. & vb. n.', 'of Issue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Issue', 'v. i.', 'To pass or flow out; to run out, as from any inclosed\n   place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Issue', 'v. i.', 'To go out; to rush out; to sally forth; as, troops issued\n   from the town, and attacked the besiegers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Issue', 'v. i.', 'To proceed, as from a source; as, water issues from\n   springs; light issues from the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Issue', 'v. i.', 'To proceed, as progeny; to be derived; to be descended;\n   to spring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Issue', 'v. i.', 'To extend; to pass or open; as, the path issues into the\n   highway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Issue', 'v. i.', 'To be produced as an effect or result; to grow or accrue;\n   to arise; to proceed; as, rents and profits issuing from land,\n   tenements, or a capital stock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Issue', 'v. i.', 'To close; to end; to terminate; to turn out; as, we know\n   not how the cause will issue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Issue', 'v. i.', 'In pleading, to come to a point in fact or law, on which\n   the parties join issue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Issue', 'v. t.', 'To send out; to put into circulation; as, to issue notes\n   from a bank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Issue', 'v. t.', 'To deliver for use; as, to issue provisions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Issue', 'v. t.', 'To send out officially; to deliver by authority; as, to\n   issue an order; to issue a writ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Issueless', 'a.', 'Having no issue or progeny; childless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Issuer', 'n.', 'One who issues, emits, or publishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ist', '', 'A noun suffix denoting an agent, or doer, one who practices, a\n   believer in; as, theorist, one who theorizes; socialist, one who holds\n   to socialism; sensualist, one given to sensuality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ist', '', 'A contraction of is it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isthmian', 'a.', 'Of or pertaining to an isthmus, especially to the Isthmus\n   of Corinth, in Greece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isthmuses', 'pl. ', 'of Isthmus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isthmus', 'n.', 'A neck or narrow slip of land by which two continents are\n   connected, or by which a peninsula is united to the mainland; as, the\n   Isthmus of Panama; the Isthmus of Suez, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Istle', 'n.', 'Same as Ixtle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Isuret', 'n.', 'An artificial nitrogenous base, isomeric with urea, and\n   forming a white crystalline substance; -- called also isuretine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'It', 'pron.', 'The neuter pronoun of the third person, corresponding to the\n   masculine pronoun he and the feminine she, and having the same plural\n   (they, their or theirs, them).');
  INSERT INTO words (word, word_type, description) VALUES  (    'It', 'pron.', 'As a substance for any noun of the neuter gender; as, here\n   is the book, take it home.');
  INSERT INTO words (word, word_type, description) VALUES  (    'It', 'pron.', 'As a demonstrative, especially at the beginning of a\n   sentence, pointing to that which is about to be stated, named, or\n   mentioned, or referring to that which apparent or well known; as, I saw\n   it was John.');
  INSERT INTO words (word, word_type, description) VALUES  (    'It', 'pron.', 'As an indefinite nominative for a impersonal verb; as, it\n   snows; it rains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'It', 'pron.', 'As a substitute for such general terms as, the state of\n   affairs, the condition of things, and the like; as, how is it with the\n   sick man?');
  INSERT INTO words (word, word_type, description) VALUES  (    'It', 'pron.', 'As an indefinite object after some intransitive verbs, or\n   after a substantive used humorously as a verb; as, to foot it (i. e.,\n   to walk).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Itacism', 'n.', 'Pronunciation of / (eta) as the modern Greeks pronounce\n   it, that is, like e in the English word be. This was the pronunciation\n   advocated by Reu/hlin and his followers, in opposition to the etacism\n   of Erasmus. See Etacism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Itacist', 'n.', 'One who is in favor of itacism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Itacolumite', 'n.', 'A laminated, granular, siliceous rocks, often\n   occurring in regions where the diamond is found.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Itaconic', 'a.', 'Pertaining to, or designating, an acid, C5H6O4, which is\n   obtained as a white crystalline substance by decomposing aconitic and\n   other organic acids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Itala', 'n.', 'An early Latin version of the Scriptures (the Old Testament\n   was translated from the Septuagint, and was also called the Italic\n   version).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Italian', 'a.', 'Of or pertaining to Italy, or to its people or language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Italian', 'n.', 'A native or inhabitant of Italy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Italian', 'n.', 'The language used in Italy, or by the Italians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Italianate', 'v. t.', 'To render Italian, or conformable to Italian\n   customs; to Italianize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Italianate', 'a.', 'Italianized; Italianated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Italianism', 'n.', 'A word, phrase, or idiom, peculiar to the Italians; an\n   Italicism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Italianism', 'n.', 'Attachment to, or sympathy for, Italy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Italianized', 'imp. & p. p.', 'of Italianize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Italianizing', 'p. pr. & vb. n.', 'of Italianize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Italianize', 'v. i.', 'To play the Italian; to speak Italian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Italianize', 'v. i.', 'To render Italian in any respect; to Italianate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Italic', 'a.', 'Relating to Italy or to its people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Italic', 'a.', 'Applied especially to a kind of type in which the letters\n   do not stand upright, but slope toward the right; -- so called because\n   dedicated to the States of Italy by the inventor, Aldus Manutius, about\n   the year 1500.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Italics', 'pl. ', 'of Italic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Italic', 'n.', 'An Italic letter, character, or type (see Italic, a., 2.);\n   -- often in the plural; as, the Italics are the authors. Italic\n   letters are used to distinguish words for emphasis, importance,\n   antithesis, etc. Also, collectively, Italic letters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Italicism', 'n.', 'A phrase or idiom peculiar to the Italian language; to\n   Italianism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Italicism', 'n.', 'The use of Italics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Italicized', 'imp. & p. p.', 'of Italicize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Italicizing', 'p. pr. & vb. n.', 'of Italicize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Italicize', 'v. t. & i.', 'To print in Italic characters; to underline\n   written letters or words with a single line; as, to Italicize a word;\n   Italicizes too much.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ita palm', '', 'A magnificent species of palm (Mauritia flexuosa), growing\n   near the Orinoco. The natives eat its fruit and buds, drink its sap,\n   and make thread and cord from its fiber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Itched', 'imp. & p. p.', 'of Itch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Itching', 'p. pr. & vb. n.', 'of Itch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Itch', 'v. i.', 'To have an uneasy sensation in the skin, which inclines\n   the person to scratch the part affected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Itch', 'v. i.', 'To have a constant desire or teasing uneasiness; to long\n   for; as, itching ears.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Itch', 'n.', 'An eruption of small, isolated, acuminated vesicles, produced\n   by the entrance of a parasitic mite (the Sarcoptes scabei), and\n   attended with itching. It is transmissible by contact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Itch', 'n.', 'Any itching eruption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Itch', 'n.', 'A sensation in the skin occasioned (or resembling that\n   occasioned) by the itch eruption; -- called also scabies, psora, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Itch', 'n.', 'A constant irritating desire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Itchiness', 'n.', 'The state of being itchy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Itchless', 'a.', 'Free from itching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Itchy', 'a.', 'Infected with the itch, or with an itching sensation.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ite', '', 'A suffix denoting one of a party, a sympathizer with or\n   adherent of, and the like, and frequently used in ridicule; as, a\n   Millerite; a Benthamite.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ite', '', 'A suffix used in naming minerals; as, chlorite, from its\n   characteristic green color; barite, from its heaviness; graphite, from\n   its use in writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ite', '', 'A suffix used to denote the salts formed from those acids whose\n   names end in -ous; as, sulphite, from sulphurous; nitrite, from nitrous\n   acid, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Item', 'adv.', 'Also; as an additional article.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Item', 'n.', 'An article; a separate particular in an account; as, the\n   items in a bill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Item', 'n.', 'A hint; an innuendo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Item', 'n.', 'A short article in a newspaper; a paragraph; as, an item\n   concerning the weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Itemed', 'imp. & p. p.', 'of Item');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iteming', 'p. pr. & vb. n.', 'of Item');
  INSERT INTO words (word, word_type, description) VALUES  (    'Item', 'v. t.', 'To make a note or memorandum of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Itemize', 'v. t.', 'To state in items, or by particulars; as, to itemize\n   the cost of a railroad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iter', 'n.', 'A passage; esp., the passage between the third and fourth\n   ventricles in the brain; the aqueduct of Sylvius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iterable', 'a.', 'Capable of being iterated or repeated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iterance', 'n.', 'Iteration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iterant', 'a.', 'Repeating; iterating; as, an iterant echo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iterate', 'a.', 'Uttered or done again; repeated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iterated', 'imp. & p. p.', 'of Iterate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iterating', 'p. pr. & vb. n.', 'of Iterate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iterate', 'v. t.', 'To utter or do a second time or many times; to repeat;\n   as, to iterate advice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iterate', 'adv.', 'By way of iteration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iteration', 'n.', 'Recital or performance a second time; repetition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iterative', 'a.', 'Repeating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ithyphallic', 'a.', 'Lustful; lewd; salacious; indecent; obscene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Itineracy', 'n.', 'The act or practice of itinerating; itinerancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Itinerancy', 'n.', 'A passing from place to place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Itinerancy', 'n.', 'A discharge of official duty involving frequent change\n   of residence; the custom or practice of discharging official duty in\n   this way; also, a body of persons who thus discharge official duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Itinerant', 'a.', 'Passing or traveling about a country; going or preaching\n   on a circuit; wandering; not settled; as, an itinerant preacher; an\n   itinerant peddler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Itinerant', 'a.', 'One who travels from place to place, particularly a\n   preacher; one who is unsettled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Itinerantly', 'adv.', 'In an itinerant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Itinerary', 'a.', 'Itinerant; traveling; passing from place to place; done\n   on a journey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Itineraries', 'pl. ', 'of Itinerary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Itinerary', 'a.', 'An account of travels, or a register of places and\n   distances as a guide to travelers; as, the Itinerary of Antoninus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Itinerated', 'imp. & p. p.', 'of Itinerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Itinerating', 'p. pr. & vb. n.', 'of Itinerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Itinerate', 'v. i.', 'To wander without a settled habitation; to travel\n   from place or on a circuit, particularly for the purpose of preaching,\n   lecturing, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-itis', '', 'A suffix used in medical terms to denote an inflammatory\n   disease of; as, arthritis; bronchitis, phrenitis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Its', '', 'Possessive form of the pronoun it. See It.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Itself', 'pron.', 'The neuter reciprocal pronoun of It; as, the thing is\n   good in itself; it stands by itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ittria', 'n.', 'See Yttria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ittrium', 'n.', 'See Yttrium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Itzibu', 'n.', 'A silver coin of Japan, worth about thirty-four cents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iulidan', 'n.', 'One of the Iulidae, a family of myriapods, of which the\n   genus Iulus is the type. See Iulus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iulus', 'n.', 'A genus of chilognathous myriapods. The body is long and\n   round, consisting of numerous smooth, equal segments, each of which\n   bears two pairs of short legs. It includes the galleyworms. See\n   Chilognatha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ivan Ivanovitch', '', 'An ideal personification of the typical Russian or\n   of the Russian people; -- used as \"John Bull\" is used for the typical\n   Englishman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ive', '', 'Colloquial contraction of I have.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ive', '', 'An adjective suffix signifying relating or belonging to, of the\n   nature of, tending to; as affirmative, active, conclusive, corrective,\n   diminutive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ivied', 'a.', 'Overgrown with ivy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ivoride', 'n.', 'A composition resembling ivory in appearance and used as a\n   substitute for it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ivories', 'pl. ', 'of Ivory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ivory', 'n.', 'The hard, white, opaque, fine-grained substance constituting\n   the tusks of the elephant. It is a variety of dentine, characterized by\n   the minuteness and close arrangement of the tubes, as also by their\n   double flexure. It is used in manufacturing articles of ornament or\n   utility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ivory', 'n.', 'The tusks themselves of the elephant, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ivory', 'n.', 'Any carving executed in ivory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ivory', 'n.', 'Teeth; as, to show ones ivories.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ivory-bill', 'n.', 'A large, handsome, North American woodpecker\n   (Campephilus principalis), having a large, sharp, ivory-colored beak.\n   Its general color is glossy black, with white secondaries, and a white\n   dorsal stripe. The male has a large, scarlet crest. It is now rare, and\n   found only in the Gulf States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ivorytype', 'n.', 'A picture produced by superposing a very light print,\n   rendered translucent by varnish, and tinted upon the back, upon a\n   stronger print, so as to give the effect of a photograph in natural\n   colors; -- called also hellenotype.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ivies', 'pl. ', 'of Ivy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ivy', 'n.', 'A plant of the genus Hedera (H. helix), common in Europe. Its\n   leaves are evergreen, dark, smooth, shining, and mostly five-pointed;\n   the flowers yellowish and small; the berries black or yellow. The stem\n   clings to walls and trees by rootlike fibers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ivy-mantled', 'a.', 'Covered with ivy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Iwis', 'adv.', 'Indeed; truly. See Ywis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ixia', 'n.', 'A South African bulbous plant of the Iris family, remarkable\n   for the brilliancy of its flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ixodes', 'n.', 'A genus of parasitic Acarina, which includes various\n   species of ticks. See Tick, the insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ixodian', 'n.', 'A tick of the genus Ixodes, or the family Ixodidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ixtle', 'n.', 'Alt. of Ixtli');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ixtli', 'n.', 'A Mexican name for a variety of Agave rigida, which\n   furnishes a strong coarse fiber; also, the fiber itself, which is\n   called also pita, and Tampico fiber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Izard', 'n.', 'A variety of the chamois found in the Pyrenees.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ize', '', 'A verb suffix signifying to make, to do, to practice; as\n   apologize, baptize, theorize, tyrannize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Izedi', 'n.', 'One of an Oriental religious sect which worships Satan or\n   the Devil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Izedism', 'n.', 'The religion of the Izedis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Izzard', 'n.', 'See Izard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Izzard', 'n.', 'The letter z; -- formerly so called.\n   J () J is the tenth letter of the English alphabet. It is a later\n   variant form of the Roman letter I, used to express a consonantal\n   sound, that is, originally, the sound of English y in yet. The forms J\n   and I have, until a recent time, been classed together, and they have\n   been used interchangeably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaal goat', '', 'A species of wild goat (Capra Nubiana) found in the\n   mountains of Abyssinia, Upper Egypt, and Arabia; -- called also beden,\n   and jaela.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jab', 'v. t.', 'To thrust; to stab; to punch. See Job, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jab', 'n.', 'A thrust or stab.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jabbered', 'imp. & p. p.', 'of Jabber');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jabbering', 'p. pr. & vb. n.', 'of Jabber');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jabber', 'v. i.', 'To talk rapidly, indistinctly, or unintelligibly; to\n   utter gibberish or nonsense; to chatter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jabber', 'v. t.', 'To utter rapidly or indistinctly; to gabble; as, to\n   jabber French.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jabber', 'n.', 'Rapid or incoherent talk, with indistinct utterance;\n   gibberish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jabber', 'n.', 'One who jabbers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jabberingly', 'adv.', 'In a jabbering manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jabberment', 'n.', 'Jabber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jabbernowl', 'n.', 'Same as Jobbernowl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jabiru', 'n.', 'One of several large wading birds of the genera Mycteria\n   and Xenorhynchus, allied to the storks in form and habits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaborandi', 'n.', 'The native name of a South American rutaceous shrub\n   (Pilocarpus pennatifolius). The leaves are used in medicine as an\n   diaphoretic and sialogogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaborine', 'n.', 'An alkaloid found in jaborandi leaves, from which it is\n   extracted as a white amorphous substance. In its action it resembles\n   atropine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jabot', 'n.', 'Originally, a kind of ruffle worn by men on the bosom of the\n   shirt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jabot', 'n.', 'An arrangement of lace or tulle, looped ornamentally, and\n   worn by women on the front of the dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacamar', 'n.', 'Any one of numerous species of tropical American birds of\n   the genus Galbula and allied genera. They are allied to the\n   kingfishers, but climb on tree trunks like nuthatches, and feed upon\n   insects. Their colors are often brilliant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacana', 'n.', 'Any of several wading birds belonging to the genus Jacana\n   and several allied genera, all of which have spurs on the wings. They\n   are able to run about over floating water weeds by means of their very\n   long, spreading toes. Called also surgeon bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacaranda', 'n.', 'The native Brazilian name for certain leguminous trees,\n   which produce the beautiful woods called king wood, tiger wood, and\n   violet wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacaranda', 'n.', 'A genus of bignoniaceous Brazilian trees with showy\n   trumpet-shaped flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacare', 'n.', 'A cayman. See Yacare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacchus', 'n.', 'The common marmoset (Hapale vulgaris). Formerly, the name\n   was also applied to other species of the same genus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacconet', 'n.', 'See Jaconet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacent', 'a.', 'Lying at length; as, the jacent posture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacinth', 'n.', 'See Hyacinth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack', 'n.', 'A large tree, the Artocarpus integrifolia, common in the East\n   Indies, closely allied to the breadfruit, from which it differs in\n   having its leaves entire. The fruit is of great size, weighing from\n   thirty to forty pounds, and through its soft fibrous matter are\n   scattered the seeds, which are roasted and eaten. The wood is of a\n   yellow color, fine grain, and rather heavy, and is much used in\n   cabinetwork. It is also used for dyeing a brilliant yellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack', 'n.', 'A familiar nickname of, or substitute for, John.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack', 'n.', 'An impertinent or silly fellow; a simpleton; a boor; a clown;\n   also, a servant; a rustic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack', 'n.', 'A popular colloquial name for a sailor; -- called also Jack\n   tar, and Jack afloat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack', 'n.', 'A mechanical contrivance, an auxiliary machine, or a\n   subordinate part of a machine, rendering convenient service, and often\n   supplying the place of a boy or attendant who was commonly called Jack');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack', 'n.', 'A device to pull off boots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack', 'n.', 'A sawhorse or sawbuck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack', 'n.', 'A machine or contrivance for turning a spit; a smoke jack, or\n   kitchen jack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack', 'n.', 'A wooden wedge for separating rocks rent by blasting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack', 'n.', 'A lever for depressing the sinkers which push the loops down\n   on the needles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack', 'n.', 'A grating to separate and guide the threads; a heck box.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack', 'n.', 'A machine for twisting the sliver as it leaves the carding\n   machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack', 'n.', 'A compact, portable machine for planing metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack', 'n.', 'A machine for slicking or pebbling leather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack', 'n.', 'A system of gearing driven by a horse power, for multiplying\n   speed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack', 'n.', 'A hood or other device placed over a chimney or vent pipe, to\n   prevent a back draught.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack', 'n.', 'In the harpsichord, an intermediate piece communicating the\n   action of the key to the quill; -- called also hopper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack', 'n.', 'In hunting, the pan or frame holding the fuel of the torch\n   used to attract game at night; also, the light itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack', 'n.', 'A portable machine variously constructed, for exerting great\n   pressure, or lifting or moving a heavy body through a small distance.\n   It consists of a lever, screw, rack and pinion, hydraulic press, or any\n   simple combination of mechanical powers, working in a compact pedestal\n   or support and operated by a lever, crank, capstan bar, etc. The name\n   is often given to a jackscrew, which is a kind of jack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack', 'n.', 'The small bowl used as a mark in the game of bowls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack', 'n.', 'The male of certain animals, as of the ass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack', 'n.', 'A young pike; a pickerel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack', 'n.', 'The jurel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack', 'n.', 'A large, California rock fish (Sebastodes paucispinus); --\n   called also boccaccio, and merou.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack', 'n.', 'The wall-eyed pike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack', 'n.', 'A drinking measure holding half a pint; also, one holding a\n   quarter of a pint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack', 'n.', 'A flag, containing only the union, without the fly, usually\n   hoisted on a jack staff at the bowsprit cap; -- called also union jack.\n   The American jack is a small blue flag, with a star for each State.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack', 'n.', 'A bar of iron athwart ships at a topgallant masthead, to\n   support a royal mast, and give spread to the royal shrouds; -- called\n   also jack crosstree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack', 'n.', 'The knave of a suit of playing cards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack', 'n.', 'A coarse and cheap mediaeval coat of defense, esp. one made\n   of leather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack', 'n.', 'A pitcher or can of waxed leather; -- called also black jack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack', 'v. i.', 'To hunt game at night by means of a jack. See 2d Jack, n.,\n   4, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack', 'v. t.', 'To move or lift, as a house, by means of a jack or jacks.\n   See 2d Jack, n., 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack-a-dandy', 'n.', 'A little dandy; a little, foppish, impertinent\n   fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jackal', 'n.', 'Any one of several species of carnivorous animals\n   inhabiting Africa and Asia, related to the dog and wolf. They are\n   cowardly, nocturnal, and gregarious. They feed largely on carrion, and\n   are noted for their piercing and dismal howling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jackal', 'n.', 'One who does mean work for anothers advantage, as jackals\n   were once thought to kill game which lions appropriated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack-a-lent', 'n.', 'A small stuffed puppet to be pelted in Lent; hence, a\n   simple fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jackanapes', 'n.', 'A monkey; an ape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jackanapes', 'n.', 'A coxcomb; an impertinent or conceited fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jackass', 'n.', 'The male ass; a donkey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jackass', 'n.', 'A conceited dolt; a perverse blockhead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jackdaw', 'n.', 'See Daw, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jackeen', 'n.', 'A drunken, dissolute fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacket', 'n.', 'A short upper garment, extending downward to the hips; a\n   short coat without skirts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacket', 'n.', 'An outer covering for anything, esp. a covering of some\n   nonconducting material such as wood or felt, used to prevent radiation\n   of heat, as from a steam boiler, cylinder, pipe, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacket', 'n.', 'In ordnance, a strengthening band surrounding and\n   reenforcing the tube in which the charge is fired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacket', 'n.', 'A garment resembling a waistcoat lined with cork, to serve\n   as a life preserver; -- called also cork jacket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacket', 'v. t.', 'To put a jacket on; to furnish, as a boiler, with a\n   jacket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacket', 'v. t.', 'To thrash; to beat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacketed', 'a.', 'Wearing, or furnished with, a jacket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacketing', 'n.', 'The material of a jacket; as, nonconducting jacketing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack Ketch', '', 'A public executioner, or hangman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jackknife', 'n.', 'A large, strong clasp knife for the pocket; a pocket\n   knife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jackmen', 'pl. ', 'of Jackman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jackman', 'n.', 'One wearing a jack; a horse soldier; a retainer. See 3d\n   Jack, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jackman', 'n.', 'A cream cheese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jack-o-lantern', 'n.', 'See Jack-with-a-lantern, under 2d Jack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jackpudding', 'n.', 'A merry-andrew; a buffoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacksaw', 'n.', 'The merganser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jackscrew', 'n.', 'A jack in which a screw is used for lifting, or exerting\n   pressure. See Illust. of 2d Jack, n., 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jackslave', 'n.', 'A low servant; a mean fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacksmith', 'n.', 'A smith who makes jacks. See 2d Jack, 4, c.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacksnipe', 'n.', 'A small European snipe (Limnocryptes gallinula); --\n   called also judcock, jedcock, juddock, jed, and half snipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacksnipe', 'n.', 'A small American sandpiper (Tringa maculata); -- called\n   also pectoral sandpiper, and grass snipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jackstay', 'n.', 'A rail of wood or iron stretching along a yard of a\n   vessel, to which the sails are fastened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jackstone', 'n.', 'One of the pebbles or pieces used in the game of\n   jackstones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jackstone', 'n.', 'A game played with five small stones or pieces of metal.\n   See 6th Chuck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jackstraw', 'n.', 'An effigy stuffed with straw; a scarecrow; hence, a man\n   without property or influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jackstraw', 'n.', 'One of a set of straws of strips of ivory, bone, wood,\n   etc., for playing a childs game, the jackstraws being thrown\n   confusedly together on a table, to be gathered up singly by a hooked\n   instrument, without touching or disturbing the rest of the pile. See\n   Spilikin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jackwood', 'n.', 'Wood of the jack (Artocarpus integrifolia), used in\n   cabinetwork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacob', 'n.', 'A Hebrew patriarch (son of Isaac, and ancestor of the Jews),\n   who in a vision saw a ladder reaching up to heaven (Gen. xxviii. 12);\n   -- also called Israel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacobaean lily', '', 'A bulbous plant (Amaryllis, / Sprekelia,\n   formosissima) from Mexico. It bears a single, large, deep, red,\n   lilylike flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacobean', 'a.', 'Alt. of Jacobian');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacobian', 'a.', 'Of or pertaining to a style of architecture and\n   decoration in the time of James the First, of England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacobin', 'n.', 'A Dominican friar; -- so named because, before the French\n   Revolution, that order had a convent in the Rue St. Jacques, Paris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacobin', 'n.', 'One of a society of violent agitators in France, during\n   the revolution of 1789, who held secret meetings in the Jacobin convent\n   in the Rue St. Jacques, Paris, and concerted measures to control the\n   proceedings of the National Assembly. Hence: A plotter against an\n   existing government; a turbulent demagogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacobin', 'n.', 'A fancy pigeon, in which the feathers of the neck form a\n   hood, -- whence the name. The wings and tail are long, and the beak\n   moderately short.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacobin', 'a.', 'Same as Jacobinic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacobine', 'n.', 'A Jacobin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacobinic', 'a.', 'Alt. of Jacobinical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacobinical', 'a.', 'Of or pertaining to the Jacobins of France;\n   revolutionary; of the nature of, or characterized by, Jacobinism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacobinism', 'n.', 'The principles of the Jacobins; violent and factious\n   opposition to legitimate government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacobinized', 'imp. & p. p.', 'of Jacobinize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacobinizing', 'p. pr. & vb. n.', 'of Jacobinize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacobinize', 'v. t.', 'To taint with, or convert to, Jacobinism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacobite', 'n.', 'A partisan or adherent of James the Second, after his\n   abdication, or of his descendants, an opposer of the revolution in 1688\n   in favor of William and Mary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacobite', 'n.', 'One of the sect of Syrian Monophysites. The sect is named\n   after Jacob Baradaeus, its leader in the sixth century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacobite', 'a.', 'Of or pertaining to the Jacobites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacobitic', 'a.', 'Alt. of Jacobitical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacobitical', 'a.', 'Of or pertaining to the Jacobites; characterized by\n   Jacobitism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacobitism', 'n.', 'The principles of the Jacobites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacobuses', 'pl. ', 'of Jacobus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacobus', 'n.', 'An English gold coin, of the value of twenty-five\n   shillings sterling, struck in the reign of James I.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaconet', 'n.', 'A thin cotton fabric, between and muslin, used for\n   dresses, neckcloths, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacquard', 'a.', 'Pertaining to, or invented by, Jacquard, a French\n   mechanician, who died in 1834.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacqueminot', 'n.', 'A half-hardy, deep crimson rose of the remontant\n   class; -- so named after General Jacqueminot, of France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jacquerie', 'n.', 'The name given to a revolt of French peasants against\n   the nobles in 1358, the leader assuming the contemptuous title, Jacques\n   Bonhomme, given by the nobles to the peasantry. Hence, any revolt of\n   peasants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jactancy', 'n.', 'A boasting; a bragging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jactation', 'n.', 'A throwing or tossing of the body; a shaking or\n   agitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jactitation', 'n.', 'Vain boasting or assertions repeated to the prejudice\n   of anothers right; false claim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jactitation', 'n.', 'A frequent tossing or moving of the body;\n   restlessness, as in delirium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaculable', 'a.', 'Fit for throwing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaculated', 'imp. & p. p.', 'of Jaculate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaculating', 'p. pr. & vb. n.', 'of Jaculate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaculate', 'v. t.', 'To throw or cast, as a dart; to throw out; to emit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaculation', 'n.', 'The act of tossing, throwing, or hurling, as spears.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaculator', '', 'One who throws or casts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaculator', '', 'The archer fish (Toxotes jaculator).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaculatory', 'a.', 'Darting or throwing out suddenly; also, suddenly thrown\n   out; uttered in short sentences; ejaculatory; as, jaculatory prayers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jadding', 'n.', 'See Holing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jade', 'n.', 'A stone, commonly of a pale to dark green color but sometimes\n   whitish. It is very hard and compact, capable of fine polish, and is\n   used for ornamental purposes and for implements, esp. in Eastern\n   countries and among many early peoples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jade', 'n.', 'A mean or tired horse; a worthless nag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jade', 'n.', 'A disreputable or vicious woman; a wench; a quean; also,\n   sometimes, a worthless man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jade', 'n.', 'A young woman; -- generally so called in irony or slight\n   contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaded', 'imp. & p. p.', 'of Jade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jading', 'p. pr. & vb. n.', 'of Jade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jade', 'v. t.', 'To treat like a jade; to spurn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jade', 'v. t.', 'To make ridiculous and contemptible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jade', 'v. t.', 'To exhaust by overdriving or long-continued labor of any\n   kind; to tire or wear out by severe or tedious tasks; to harass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jade', 'v. i.', 'To become weary; to lose spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jadeite', 'n.', 'See Jade, the stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jadery', 'n.', 'The tricks of a jade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jadish', 'a.', 'Vicious; ill-tempered; resembling a jade; -- applied to a\n   horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jadish', 'a.', 'Unchaste; -- applied to a woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaeger', 'n.', 'See Jager.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jag', 'n.', 'A notch; a cleft; a barb; a ragged or sharp protuberance; a\n   denticulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jag', 'n.', 'A part broken off; a fragment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jag', 'n.', 'A cleft or division.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jagged', 'imp. & p. p.', 'of Jag');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jagging', 'p. pr. & vb. n.', 'of Jag');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jag', 'v. t.', 'To cut into notches or teeth like those of a saw; to notch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jag', 'n.', 'A small load, as of hay or grain in the straw, or of ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jag', 'v. t.', 'To carry, as a load; as, to jag hay, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaganatha', 'n.', 'Alt. of Jaganatha');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaganatha', 'n.', 'See Juggernaut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jager', 'n.', 'A sharpshooter. See Yager.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jager', 'n.', 'Any species of gull of the genus Stercorarius. Three species\n   occur on the Atlantic coast. The jagers pursue other species of gulls\n   and force them to disgorge their prey. The two middle tail feathers are\n   usually decidedly longer than the rest. Called also boatswain, and\n   marline-spike bird. The name is also applied to the skua, or Arctic\n   gull (Megalestris skua).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jagg', 'v. t. & n.', 'See Jag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jagged', 'a.', 'Having jags; having rough, sharp notches, protuberances, or\n   teeth; cleft; laciniate; divided; as, jagged rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jagger', 'n.', 'One who carries about a small load; a peddler. See 2d Jag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jagger', 'n.', 'One who, or that which, jags; specifically: (a) jagging\n   iron used for crimping pies, cakes, etc. (b) A toothed chisel. See Jag,\n   v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaggery', 'n.', 'Raw palm sugar, made in the East Indies by evaporating the\n   fresh juice of several kinds of palm trees, but specifically that of\n   the palmyra (Borassus flabelliformis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaggy', 'a.', 'Having jags; set with teeth; notched; uneven; as, jaggy\n   teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaghir', 'n.', 'A village or district the government and revenues of which\n   are assigned to some person, usually in consideration of some service\n   to be rendered, esp. the maintenance of troops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaghirdar', 'n.', 'The holder of a jaghir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jagua palm', '', 'A great Brazilian palm (Maximiliana regia), having\n   immense spathes which are used for baskets and tubs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaguar', 'n.', 'A large and powerful feline animal (Felis onca), ranging\n   from Texas and Mexico to Patagonia. It is usually brownish yellow, with\n   large, dark, somewhat angular rings, each generally inclosing one or\n   two dark spots. It is chiefly arboreal in its habits. Called also the\n   American tiger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaguarondi', 'n.', 'A South American wild cat (Felis jaguarondi), having a\n   long, slim body and very short legs. Its color is grayish brown, varied\n   with a blackish hue. It is arboreal in its habits and feeds mostly on\n   birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jah', 'n.', 'Jehovah.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jail', 'n.', 'A kind of prison; a building for the confinement of persons\n   held in lawful custody, especially for minor offenses or with reference\n   to some future judicial proceeding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jail', 'v. t.', 'To imprison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jailer', 'n.', 'The keeper of a jail or prison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jain', 'n.', 'Alt. of Jaina');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaina', 'n.', 'One of a numerous sect in British India, holding the tenets\n   of Jainism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jainism', 'n.', 'The heterodox Hindoo religion, of which the most striking\n   features are the exaltation of saints or holy mortals, called jins,\n   above the ordinary Hindoo gods, and the denial of the divine origin and\n   infallibility of the Vedas. It is intermediate between Brahmanism and\n   Buddhism, having some things in common with each.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jairou', 'n.', 'The ahu or Asiatic gazelle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jak', 'n.', 'see Ils Jack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jakes', 'n.', 'A privy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jakie', 'n.', 'A South American striped frog (Pseudis paradoxa), remarkable\n   for having a tadpole larger than the adult, and hence called also\n   paradoxical frog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jako', 'n.', 'An African parrot (Psittacus erithacus), very commonly kept\n   as a cage bird; -- called also gray parrot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jakwood', 'n.', 'See Jackwood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jalap', 'n.', 'The tubers of the Mexican plant Ipomoea purga (or Exogonium\n   purga), a climber much like the morning-glory. The abstract, extract,\n   and powder, prepared from the tubers, are well known purgative\n   medicines. Other species of Ipomoea yield several inferior kinds of\n   jalap, as the I. Orizabensis, and I. tuberosa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jalapic', 'a.', 'Of or pertaining to jalap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jalapin', 'n.', 'A glucoside found in the stems of the jalap plant and\n   scammony. It is a strong purgative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jalons', 'n. pl.', 'Long poles, topped with wisps of straw, used as\n   landmarks and signals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jalousie', 'n.', 'A Venetian or slatted inside window blind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jalousied', 'a.', 'Furnished with jalousies; as, jalousied porches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jam', 'n.', 'A kind of frock for children.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jam', 'n.', 'See Jamb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jammed', 'imp. & p. p.', 'of Jam');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jamming', 'p. pr. & vb. n.', 'of Jam');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jam', 'v. t.', 'To press into a close or tight position; to crowd; to\n   squeeze; to wedge in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jam', 'v. t.', 'To crush or bruise; as, to jam a finger in the crack of a\n   door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jam', 'v. t.', 'To bring (a vessel) so close to the wind that half her\n   upper sails are laid aback.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jam', 'n.', 'A mass of people or objects crowded together; also, the\n   pressure from a crowd; a crush; as, a jam in a street; a jam of logs in\n   a river.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jam', 'n.', 'An injury caused by jamming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jam', 'n.', 'A preserve of fruit boiled with sugar and water; as, raspberry\n   jam; currant jam; grape jam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jamacina', 'n.', 'Jamaicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jamadar', 'n.', 'Same as Jemidar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jamaica', 'n.', 'One of the West India is islands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jamaican', 'a.', 'Of or pertaining to Jamaica.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jamaican', 'n.', 'A native or inhabitant of Jamaica.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jamaicine', 'n.', 'An alkaloid said to be contained in the bark of\n   Geoffroya inermis, a leguminous tree growing in Jamaica and Surinam; --\n   called also jamacina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jamb', 'n.', 'The vertical side of any opening, as a door or fireplace;\n   hence, less properly, any narrow vertical surface of wall, as the of a\n   chimney-breast or of a pier, as distinguished from its face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jamb', 'n.', 'Any thick mass of rock which prevents miners from following\n   the lode or vein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jamb', 'v. t.', 'See Jam, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jambee', 'n.', 'A fashionable cane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jambes', 'n.', 'Alt. of Jambeux');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jambeux', 'n.', 'In the Middle Ages, armor for the legs below the knees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jambolana', 'n.', 'A myrtaceous tree of the West Indies and tropical\n   America (Calyptranthes Jambolana), with astringent bark, used for\n   dyeing. It bears an edible fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jamdani', 'n.', 'A silk fabric, with a woven pattern of sprigs of flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jamesonite', 'n.', 'A steel-gray mineral, of metallic luster, commonly\n   fibrous massive. It is a sulphide of antimony and lead, with a little\n   iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jamess powder', '', 'Antimonial powder, first prepared by Dr. James, ar\n   English physician; -- called also fever powder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jamestown weed', '', 'The poisonous thorn apple or stramonium (Datura\n   stramonium), a rank weed early noticed at Jamestown, Virginia. See\n   Datura.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jan', 'n.', 'One of intermediate order between angels and men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jane', 'n.', 'A coin of Genoa; any small coin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jane', 'n.', 'A kind of twilled cotton cloth. See Jean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jane-of-apes', 'n.', 'A silly, pert girl; -- corresponding to jackanapes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jangled', 'imp. & p. p.', 'of Jangle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jangling', 'p. pr. & vb. n.', 'of Jangle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jangle', 'v. i.', 'To sound harshly or discordantly, as bells out of tune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jangle', 'v. i.', 'To talk idly; to prate; to babble; to chatter; to\n   gossip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jangle', 'v. i.', 'To quarrel in words; to altercate; to wrangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jangle', 'v. t.', 'To cause to sound harshly or inharmoniously; to produce\n   discordant sounds with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jangle', 'n.', 'Idle talk; prate; chatter; babble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jangle', 'n.', 'Discordant sound; wrangling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jangler', 'n.', 'An idle talker; a babbler; a prater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jangler', 'n.', 'A wrangling, noisy fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jangleress', 'n.', 'A female prater or babbler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Janglery', 'n.', 'Jangling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jangling', 'a.', 'Producing discordant sounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jangling', 'n.', 'Idle babbling; vain disputation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jangling', 'n.', 'Wrangling; altercation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Janissary', 'n.', 'See Janizary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Janitor', 'n.', 'A door-keeper; a porter; one who has the care of a public\n   building, or a building occupied for offices, suites of rooms, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Janitress', 'n.', 'Alt. of Janitrix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Janitrix', 'n.', 'A female janitor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Janizar', 'n.', 'A janizary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Janizarian', 'a.', 'Of or pertaining to the janizaries, or their\n   government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Janizaries', 'pl. ', 'of Janizary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Janizary', 'n.', 'A soldier of a privileged military class, which formed\n   the nucleus of the Turkish infantry, but was suppressed in 1826.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Janker', 'n.', 'A long pole on two wheels, used in hauling logs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jansenism', 'n.', 'The doctrine of Jansen regarding free will and divine\n   grace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jansenist', 'n.', 'A follower of Cornelius Jansen, a Roman Catholic bishop\n   of Ypres, in Flanders, in the 17th century, who taught certain\n   doctrines denying free will and the possibility of resisting divine\n   grace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jant', 'v. i.', 'See Jaunt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Janthina', 'n.', 'See Ianthina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jantily', 'adv.', 'See Jauntily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jantiness', 'n.', 'See Jauntiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jantu', 'n.', 'A machine of great antiquity, used in Bengal for raising\n   water to irrigate land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Janty', 'a.', 'See Jaunty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'January', 'n.', 'The first month of the year, containing thirty-one days.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Janus', 'n.', 'A Latin deity represented with two faces looking in opposite\n   directions. Numa is said to have dedicated to Janus the covered passage\n   at Rome, near the Forum, which is usually called the Temple of Janus.\n   This passage was open in war and closed in peace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Janus-faced', 'a.', 'Double-faced; deceitful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Janus-headed', 'a.', 'Double-headed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Japan', 'n.', 'Work varnished and figured in the Japanese manner; also, the\n   varnish or lacquer used in japanning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Japan', 'a.', 'Of or pertaining to Japan, or to the lacquered work of that\n   country; as, Japan ware.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Japanned', 'imp. & p. p.', 'of Japan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Japanning', 'p. pr. & vb. n.', 'of Japan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Japan', 'v. t.', 'To cover with a coat of hard, brilliant varnish, in the\n   manner of the Japanese; to lacquer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Japan', 'v. t.', 'To give a glossy black to, as shoes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Japanese', 'a.', 'Of or pertaining to Japan, or its inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Japanese', 'n. sing. & pl.', 'A native or inhabitant of Japan;\n   collectively, the people of Japan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Japanese', 'n. sing. & pl.', 'The language of the people of Japan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Japanned', 'a.', 'Treated, or coated, with varnish in the Japanese manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Japanner', 'n.', 'One who varnishes in the manner of the Japanese, or one\n   skilled in the art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Japanner', 'n.', 'A bootblack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Japanning', 'n.', 'The art or act of varnishing in the Japanese manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Japannish', 'a.', 'After the manner of the Japanese; resembling japanned\n   articles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jape', 'v. i.', 'To jest; to play tricks; to jeer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jape', 'v. t.', 'To mock; to trick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Japer', 'n.', 'A jester; a buffoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Japery', 'n.', 'Jesting; buffoonery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Japhethite', 'n.', 'A Japhetite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Japhetic', 'a.', 'Pertaining to, or derived from, Japheth, one of the sons\n   of Noah; as, Japhetic nations, the nations of Europe and Northern Asia;\n   Japhetic languages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Japhetite', 'n.', 'A descendant of Japheth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Japonica', 'n.', 'A species of Camellia (Camellia Japonica), a native of\n   Japan, bearing beautiful red or white flowers. Many other genera have\n   species of the same name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jar', 'n.', 'A turn. [Only in phrase.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jar', 'n.', 'A deep, broad-mouthed vessel of earthenware or glass, for\n   holding fruit, preserves, etc., or for ornamental purposes; as, a jar\n   of honey; a rose jar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jar', 'n.', 'The measure of what is contained in a jar; as, a jar of oil; a\n   jar of preserves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jarred', 'imp. & p. p.', 'of Jar');
  INSERT INTO words (word, word_type, description) VALUES  (    'jarring', 'p. pr. & vb. n.', 'of Jar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jar', 'v. i.', 'To give forth a rudely quivering or tremulous sound; to\n   sound harshly or discordantly; as, the notes jarred on my ears.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jar', 'v. i.', 'To act in opposition or disagreement; to clash; to\n   interfere; to quarrel; to dispute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jar', 'v. t.', 'To cause a short, tremulous motion of, to cause to tremble,\n   as by a sudden shock or blow; to shake; to shock; as, to jar the earth;\n   to jar ones faith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jar', 'v. t.', 'To tick; to beat; to mark or tell off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jar', 'n.', 'A rattling, tremulous vibration or shock; a shake; a harsh\n   sound; a discord; as, the jar of a train; the jar of harsh sounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jar', 'n.', 'Clash of interest or opinions; collision; discord; debate;\n   slight disagreement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jar', 'n.', 'A regular vibration, as of a pendulum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jar', 'n.', 'In deep well boring, a device resembling two long chain links,\n   for connecting a percussion drill to the rod or rope which works it, so\n   that the drill is driven down by impact and is jerked loose when\n   jammed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jararaca', 'n.', 'A poisonous serpent of Brazil (Bothrops jararaca), about\n   eighteen inches long, and of a dusky, brownish color, variegated with\n   red and black spots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jarble', 'v. t.', 'To wet; to bemire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jardiniere', 'n.', 'An ornamental stand or receptacle for plants, flowers,\n   etc., used as a piece of decorative furniture in room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jards', 'n.', 'A callous tumor on the leg of a horse, below the hock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jargle', 'v. i.', 'To emit a harsh or discordant sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jargon', 'n.', 'Confused, unintelligible language; gibberish; hence, an\n   artificial idiom or dialect; cant language; slang.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jargoned', 'imp. & p. p.', 'of Jargon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jargoning', 'p. pr. & vb. n.', 'of Jargon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jargon', 'v. i.', 'To utter jargon; to emit confused or unintelligible\n   sounds; to talk unintelligibly, or in a harsh and noisy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jargon', 'n.', 'A variety of zircon. See Zircon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jargonelle', 'n.', 'A variety of pear which ripens early.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jargonic', 'a.', 'Of or pertaining to the mineral jargon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jargonist', 'n.', 'One addicted to jargon; one who uses cant or slang.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jarl', 'n.', 'A chief; an earl; in English history, one of the leaders in\n   the Danish and Norse invasions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jarnut', 'n.', 'An earthnut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jarosite', 'n.', 'An ocher-yellow mineral occurring on minute rhombohedral\n   crystals. It is a hydrous sulphate of iron and potash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jar-owl', 'n.', 'The goatsucker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jarrah', 'n.', 'The mahoganylike wood of the Australian Eucalyptus\n   marginata. See Eucalyptus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jarring', 'a.', 'Shaking; disturbing; discordant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jarring', 'n.', 'A shaking; a tremulous motion; as, the jarring of a\n   steamship, caused by its engines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jarring', 'n.', 'Discord; a clashing of interests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jarringly', 'adv.', 'In a jarring or discordant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jarvey', 'n.', 'Alt. of Jarvy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jarvy', 'n.', 'The driver of a hackney coach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jarvy', 'n.', 'A hackney coach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jasey', 'n.', 'A wig; -- so called, perhaps, from being made of, or\n   resembling, Jersey yarn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jashawk', 'n.', 'A young hawk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jasmine', 'n.', 'A shrubby plant of the genus Jasminum, bearing flowers of\n   a peculiarly fragrant odor. The J. officinale, common in the south of\n   Europe, bears white flowers. The Arabian jasmine is J. Sambac, and,\n   with J. angustifolia, comes from the East Indies. The yellow false\n   jasmine in the Gelseminum sempervirens (see Gelsemium). Several other\n   plants are called jasmine in the West Indies, as species of Calotropis\n   and Faramea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jasp', 'n.', 'Jasper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaspachate', 'n.', 'Agate jasper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jasper', 'n.', 'An opaque, impure variety of quartz, of red, yellow, and\n   other dull colors, breaking with a smooth surface. It admits of a high\n   polish, and is used for vases, seals, snuff boxes, etc. When the colors\n   are in stripes or bands, it is called striped / banded jasper. The\n   Egyptian pebble is a brownish yellow jasper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jasperated', 'a.', 'mixed with jasper; containing particles of jasper; as,\n   jasperated agate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jasperize', 'v. t.', 'To convert into, or make to resemble, jasper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaspery', 'a.', 'Of the nature of jasper; mixed with jasper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaspidean', 'a.', 'Alt. of Jaspideous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaspideous', 'a.', 'Consisting of jasper, or containing jasper; jaspery;\n   jasperlike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaspilite', 'n.', 'A compact siliceous rock resembling jasper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaspoid', 'a.', 'Resembling jasper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jasponyx', 'n.', 'An onyx, part or all of whose layers consist of jasper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jatrophic', 'a.', 'Of or pertaining to physic nuts, the seeds of plants of\n   the genus Jatropha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaunce', 'v. i.', 'To ride hard; to jounce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaundice', 'n.', 'A morbid condition, characterized by yellowness of the\n   eyes, skin, and urine, whiteness of the faeces, constipation,\n   uneasiness in the region of the stomach, loss of appetite, and general\n   languor and lassitude. It is caused usually by obstruction of the\n   biliary passages and consequent damming up, in the liver, of the bile,\n   which is then absorbed into the blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaundice', 'v. t.', 'To affect with jaundice; to color by prejudice or\n   envy; to prejudice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaundiced', 'a.', 'Affected with jaundice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaundiced', 'a.', 'Prejudiced; envious; as, a jaundiced judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaunted', 'imp. & p. p.', 'of Jaunt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaunting', 'p. pr. & vb. n.', 'of Jaunt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaunt', 'v. i.', 'To ramble here and there; to stroll; to make an\n   excursion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaunt', 'v. i.', 'To ride on a jaunting car.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaunt', 'v. t.', 'To jolt; to jounce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaunt', 'n.', 'A wearisome journey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaunt', 'n.', 'A short excursion for pleasure or refreshment; a ramble; a\n   short journey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jauntily', 'adv.', 'In a jaunty manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jauntiness', 'n.', 'The quality of being jaunty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaunty', 'superl.', 'Airy; showy; finical; hence, characterized by an\n   affected or fantastical manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Java', 'n.', 'One of the islands of the Malay Archipelago belonging to the\n   Netherlands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Java', 'n.', 'Java coffee, a kind of coffee brought from Java.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Javanese', 'a.', 'Of or pertaining to Java, or to the people of Java.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Javanese', 'n. sing. & pl.', 'A native or natives of Java.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Javel', 'n.', 'A vagabond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Javelin', 'n.', 'A sort of light spear, to be thrown or cast by thew hand;\n   anciently, a weapon of war used by horsemen and foot soldiers; now used\n   chiefly in hunting the wild boar and other fierce game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Javelin', 'v. t.', 'To pierce with a javelin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Javelinier', 'n.', 'A soldier armed with a javelin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaw', 'n.', 'One of the bones, usually bearing teeth, which form the\n   framework of the mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaw', 'n.', 'Hence, also, the bone itself with the teeth and covering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaw', 'n.', 'In the plural, the mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaw', 'n.', 'Fig.: Anything resembling the jaw of an animal in form or\n   action; esp., pl., the mouth or way of entrance; as, the jaws of a\n   pass; the jaws of darkness; the jaws of death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaw', 'n.', 'A notch or opening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaw', 'n.', 'A notched or forked part, adapted for holding an object in\n   place; as, the jaw of a railway-car pedestal. See Axle guard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaw', 'n.', 'One of a pair of opposing parts which are movable towards or\n   from each other, for grasping or crushing anything between them, as,\n   the jaws of a vise, or the jaws of a stone-crushing machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaw', 'n.', 'The inner end of a boom or gaff, hollowed in a half circle so\n   as to move freely on a mast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaw', 'n.', 'Impudent or abusive talk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jawed', 'imp. & p. p.', 'of Jaw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jawing', 'p. pr. & vb. n.', 'of Jaw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaw', 'v. i.', 'To scold; to clamor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaw', 'v. t.', 'To assail or abuse by scolding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jawbone', 'n.', 'The bone of either jaw; a maxilla or a mandible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jawed', 'a.', 'Having jaws; -- chiefly in composition; as, lantern-jawed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Law-fall', 'n.', 'Depression of the jaw; hence, depression of spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jaw-fallen', 'a.', 'Dejected; chopfallen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jawfoot', 'n.', 'See Maxilliped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jawing', 'n.', 'Scolding; clamorous or abusive talk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jawn', 'v. i.', 'See Yawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jawy', 'a.', 'Relating to the jaws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jay', 'n.', 'Any one of the numerous species of birds belonging to\n   Garrulus, Cyanocitta, and allied genera. They are allied to the crows,\n   but are smaller, more graceful in form, often handsomely colored, and\n   usually have a crest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jayet', 'n.', 'See Jet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jayhawker', 'n.', 'A name given to a free-booting, unenlisted, armed man or\n   guerrilla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jazel', 'n.', 'A gem of an azure color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jazerant', 'n.', 'A coat of defense made of small plates of metal sewed\n   upon linen or the like; also, this kind of armor taken generally; as, a\n   coat of jazerant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jealous', 'a.', 'Zealous; solicitous; vigilant; anxiously watchful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jealous', 'a.', 'Apprehensive; anxious; suspiciously watchful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jealous', 'a.', 'Exacting exclusive devotion; intolerant of rivalry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jealous', 'a.', 'Disposed to suspect rivalry in matters of interest and\n   affection; apprehensive regarding the motives of possible rivals, or\n   the fidelity of friends; distrustful; having morbid fear of rivalry in\n   love or preference given to another; painfully suspicious of the\n   faithfulness of husband, wife, or lover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jealoushood', 'n.', 'Jealousy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jealously', 'adv.', 'In a jealous manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jealousness', 'n.', 'State or quality of being jealous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jealousies', 'pl. ', 'of Jealousy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jealousy', 'n.', 'The quality of being jealous; earnest concern or\n   solicitude; painful apprehension of rivalship in cases nearly affecting\n   ones happiness; painful suspicion of the faithfulness of husband,\n   wife, or lover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeames', 'n.', 'A footman; a flunky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jean', 'n.', 'A twilled cotton cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jears', 'n. pl.', 'See 1st Jeer (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeat', 'n.', 'See Jet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jedding ax', 'n.', 'A stone masons tool, having a flat face and a pointed\n   part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jee', 'v. t. & i.', 'See Gee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeel', 'n.', 'A morass; a shallow lake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeer', 'n.', 'A gear; a tackle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeer', 'n.', 'An assemblage or combination of tackles, for hoisting or\n   lowering the lower yards of a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeered', 'imp. & p. p.', 'of Jeer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeering', 'p. pr. & vb. n.', 'of Jeer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeer', 'v.', 'To utter sarcastic or scoffing reflections; to speak with\n   mockery or derision; to use taunting language; to scoff; as, to jeer at\n   a speaker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeer', 'v. t.', 'To treat with scoffs or derision; to address with jeers;\n   to taunt; to flout; to mock at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeer', 'n.', 'A railing remark or reflection; a scoff; a taunt; a biting\n   jest; a flout; a jibe; mockery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeerer', 'n.', 'A scoffer; a railer; a mocker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeering', 'a.', 'Mocking; scoffing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeering', 'n.', 'A mocking utterance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeers', 'n. pl.', 'See 1st Jeer (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeffersonia', 'n.', 'An American herb with a pretty, white, solitary\n   blossom, and deeply two-cleft leaves (Jeffersonia diphylla); twinleaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeffersonian', 'a.', 'Pertaining to, or characteristic of, Thomas Jefferson\n   or his policy or political doctrines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeffersonite', 'n.', 'A variety of pyroxene of olive-green color passing\n   into brown. It contains zinc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeg', 'n.', 'See Jig, 6.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jehovah', 'n.', 'A Scripture name of the Supreme Being, by which he was\n   revealed to the Jews as their covenant God or Sovereign of the\n   theocracy; the \"ineffable name\" of the Supreme Being, which was not\n   pronounced by the Jews.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jehovist', 'n.', 'One who maintains that the vowel points of the word\n   Jehovah, in Hebrew, are the proper vowels of that word; -- opposed to\n   adonist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jehovist', 'n.', 'The writer of the passages of the Old Testament,\n   especially those of the Pentateuch, in which the Supreme Being is\n   styled Jehovah. See Elohist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jehovistic', 'a.', 'Relating to, or containing, Jehovah, as a name of God;\n   -- said of certain parts of the Old Testament, especially of the\n   Pentateuch, in which Jehovah appears as the name of the Deity. See\n   Elohistic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jehu', 'n.', 'A coachman; a driver; especially, one who drives furiously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jejunal', 'a.', 'Pertaining to the jejunum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jejune', 'a.', 'Lacking matter; empty; void of substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jejune', 'a.', 'Void of interest; barren; meager; dry; as, a jejune\n   narrative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jejunity', 'n.', 'The quality of being jejune; jejuneness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jejunum', 'n.', 'The middle division of the small intestine, between the\n   duodenum and ileum; -- so called because usually found empty after\n   death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jelerang', 'n.', 'A large, handsome squirrel (Sciurus Javensis), native of\n   Java and Southern Asia; -- called also Java squirrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jell', 'v. i.', 'To jelly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jellied', 'a.', 'Brought to the state or consistence of jelly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jellies', 'pl. ', 'of Jelly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jelly', 'n.', 'Anything brought to a gelatinous condition; a viscous,\n   translucent substance in a condition between liquid and solid; a\n   stiffened solution of gelatin, gum, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jelly', 'n.', 'The juice of fruits or meats boiled with sugar to an elastic\n   consistence; as, currant jelly; calfs-foot jelly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jellied', 'imp. & p. p.', 'of Jelly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jellying', 'p. pr. & vb. n.', 'of Jelly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jelly', 'v. i.', 'To become jelly; to come to the state or consistency of\n   jelly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jellyfish', 'n.', 'Any one of the acalephs, esp. one of the larger species,\n   having a jellylike appearance. See Medusa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jemidar', 'n.', 'The chief or leader of a hand or body of persons; esp., in\n   the native army of India, an officer of a rank corresponding to that of\n   lieutenant in the English army.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jemlah goat', '', 'The jharal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jemminess', 'n.', 'Spruceness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jemmy', 'a.', 'Spruce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jemmy', 'n.', 'A short crowbar. See Jimmy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jemmy', 'n.', 'A baked sheeps head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeniquen', 'n.', 'A Mexican name for the Sisal hemp (Agave rigida, var.\n   Sisalana); also, its fiber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jenite', 'n.', 'See Yenite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jenkins', 'n.', 'name of contempt for a flatterer of persons high in social\n   or official life; as, the Jenkins employed by a newspaper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jennet', 'n.', 'A small Spanish horse; a genet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jenneting', 'n.', 'A variety of early apple. See Juneating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jennies', 'pl. ', 'of Jenny');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jenny', 'n.', 'A familiar or pet form of the proper name Jane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jenny', 'n.', 'A familiar name of the European wren.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jenny', 'n.', 'A machine for spinning a number of threads at once, -- used\n   in factories.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jentling', 'n.', 'A fish of the genus Leuciscus; the blue chub of the\n   Danube.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeofail', 'n.', 'An oversight in pleading, or the acknowledgment of a\n   mistake or oversight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeoparded', 'imp. & p. p.', 'of Jeopard');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeoparding', 'p. pr. & vb. n.', 'of Jeopard');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeopard', 'v. t.', 'To put in jeopardy; to expose to loss or injury; to\n   imperil; to hazard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeoparder', 'n.', 'One who puts in jeopardy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeopardized', 'imp. & p. p.', 'of Jeopardize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeopardizing', 'p. pr. & vb. n.', 'of Jeopardize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeopardize', 'v. t.', 'To expose to loss or injury; to risk; to jeopard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeopardous', 'a.', 'Perilous; hazardous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeopardy', 'n.', 'Exposure to death, loss, or injury; hazard; danger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeopardy', 'v. t.', 'To jeopardize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jerboa', 'n.', 'Any small jumping rodent of the genus Dipus, esp. D.\n   Aegyptius, which is common in Egypt and the adjacent countries. The\n   jerboas have very long hind legs and a long tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jereed', 'n.', 'A blunt javelin used by the people of the Levant,\n   especially in mock fights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeremiad', 'n.', 'Alt. of Jeremiade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeremiade', 'n.', 'A tale of sorrow, disappointment, or complaint; a\n   doleful story; a dolorous tirade; -- generally used satirically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jerfalcon', 'n.', 'The gyrfalcon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jerguer', 'n.', 'See Jerquer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jerid', 'n.', 'Same as Jereed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jerk', 'v. t.', 'To cut into long slices or strips and dry in the sun; as,\n   jerk beef. See Charqui.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jerked', 'imp. & p. p.', 'of Jerk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jerking', 'p. pr. & vb. n.', 'of Jerk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jerk', 'v. t.', 'To beat; to strike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jerk', 'v. t.', 'To give a quick and suddenly arrested thrust, push, pull,\n   or twist, to; to yerk; as, to jerk one with the elbow; to jerk a coat\n   off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jerk', 'v. t.', 'To throw with a quick and suddenly arrested motion of the\n   hand; as, to jerk a stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jerk', 'v. i.', 'To make a sudden motion; to move with a start, or by\n   starts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jerk', 'v. i.', 'To flout with contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jerk', 'n.', 'A short, sudden pull, thrust, push, twitch, jolt, shake, or\n   similar motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jerk', 'n.', 'A sudden start or spring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jerker', 'n.', 'A beater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jerker', 'n.', 'One who jerks or moves with a jerk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jerker', 'n.', 'A North American river chub (Hybopsis biguttatus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jerkin', 'n.', 'A jacket or short coat; a close waistcoat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jerkin', 'n.', 'A male gyrfalcon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jerking', 'n.', 'The act of pulling, pushing, or throwing, with a jerk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jerkinhead', 'n.', 'The hipped part of a roof which is hipped only for a\n   part of its height, leaving a truncated gable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jerky', 'a.', 'Moving by jerks and starts; characterized by abrupt\n   transitions; as, a jerky vehicle; a jerky style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jermoonal', 'n.', 'The Himalayan now partridge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeronymite', 'n.', 'One belonging of the mediaeval religious orders called\n   Hermits of St. Jerome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeropigia', 'n.', 'See Geropigia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jerquer', 'n.', 'A customhouse officer who searches ships for unentered\n   goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jerquing', 'n.', 'The searching of a ship for unentered goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jerquing', 'n.', 'The searching of a ship for unentered goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jerry-built', 'a.', 'Built hastily and of bad materials; as, jerry-built\n   houses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jerseys', 'pl. ', 'of Jersey');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jersey', 'n.', 'The finest of wool separated from the rest; combed wool;\n   also, fine yarn of wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jersey', 'n.', 'A kind of knitted jacket; hence, in general, a closefitting\n   jacket or upper garment made of an elastic fabric (as stockinet).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jersey', 'n.', 'One of a breed of cattle in the Island of Jersey. Jerseys\n   are noted for the richness of their milk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jerusalem', 'n.', 'The chief city of Palestine, intimately associated with\n   the glory of the Jewish nation, and the life and death of Jesus Christ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jervine', 'n.', 'A poisonous alkaloid resembling veratrine, and found with\n   it in white hellebore (Veratrum album); -- called also jervina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jesses', 'pl. ', 'of Jess');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jess', 'n.', 'A short strap of leather or silk secured round the leg of a\n   hawk, to which the leash or line, wrapped round the falconers hand,\n   was attached when used. See Illust. of Falcon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jessamine', 'n.', 'Same as Jasmine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jessant', 'a.', 'Springing up or emerging; -- said of a plant or animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jesse', 'n.', 'Any representation or suggestion of the genealogy of Christ,\n   in decorative art');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jesse', 'n.', 'A genealogical tree represented in stained glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jesse', 'n.', 'A candlestick with many branches, each of which bears the\n   name of some one of the descendants of Jesse; -- called also tree of\n   Jesse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jessed', 'a.', 'Having jesses on, as a hawk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jest', 'n.', 'A deed; an action; a gest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jest', 'n.', 'A mask; a pageant; an interlude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jest', 'n.', 'Something done or said in order to amuse; a joke; a\n   witticism; a jocose or sportive remark or phrase. See Synonyms under\n   Jest, v. i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jest', 'v. i.', 'The object of laughter or sport; a laughingstock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jested', 'imp. & p. p.', 'of Jest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jesting', 'p. pr. & vb. n.', 'of Jest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jest', 'v. i.', 'To take part in a merrymaking; -- especially, to act in a\n   mask or interlude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jest', 'v. i.', 'To make merriment by words or actions; to joke; to make\n   light of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jester', 'n.', 'A buffoon; a merry-andrew; a court fool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jester', 'n.', 'A person addicted to jesting, or to indulgence in light and\n   amusing talk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jestful', 'a.', 'Given to jesting; full of jokes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jesting', 'a.', 'Sportive; not serious; fit for jests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jesting', 'n.', 'The act or practice of making jests; joking; pleasantry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jestingly', 'adv.', 'In a jesting manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jesuit', 'n.', 'One of a religious order founded by Ignatius Loyola, and\n   approved in 1540, under the title of The Society of Jesus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jesuit', 'n.', 'Fig.: A crafty person; an intriguer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jesuited', 'a.', 'Conforming to the principles of the Jesuits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jesuitess', 'n.', 'One of an order of nuns established on the principles of\n   the Jesuits, but suppressed by Pope Urban in 1633.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jesuitic', 'a.', 'Alt. of Jesuitical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jesuitical', 'a.', 'Of or pertaining to the Jesuits, or to their principles\n   and methods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jesuitical', 'a.', 'Designing; cunning; deceitful; crafty; -- an\n   opprobrious use of the word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jesuitically', 'adv.', 'In a jesuitical manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jesuitism', 'n.', 'The principles and practices of the Jesuits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jesuitism', 'n.', 'Cunning; deceit; deceptive practices to effect a\n   purpose; subtle argument; -- an opprobrious use of the word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jesuitocracy', 'n.', 'Government by Jesuits; also, the whole body of\n   Jesuits in a country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jesuitry', 'n.', 'Jesuitism; subtle argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jesus', 'n.', 'The Savior; the name of the Son of God as announced by the\n   angel to his parents; the personal name of Our Lord, in distinction\n   from Christ, his official appellation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jet', 'n.', 'Same as 2d Get.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jet', 'n.', 'A variety of lignite, of a very compact texture and velvet\n   black color, susceptible of a good polish, and often wrought into\n   mourning jewelry, toys, buttons, etc. Formerly called also black amber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jet', 'n.', 'A shooting forth; a spouting; a spurt; a sudden rush or gush,\n   as of water from a pipe, or of flame from an orifice; also, that which\n   issues in a jet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jet', 'n.', 'Drift; scope; range, as of an argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jet', 'n.', 'The sprue of a type, which is broken from it when the type is\n   cold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jetted', 'imp. & p. p.', 'of Jet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jetting', 'p. pr. & vb. n.', 'of Jet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jet', 'v. i.', 'To strut; to walk with a lofty or haughty gait; to be\n   insolent; to obtrude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jet', 'v. i.', 'To jerk; to jolt; to be shaken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jet', 'v. i.', 'To shoot forward or out; to project; to jut out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jet', 'v. t.', 'To spout; to emit in a stream or jet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jet-black', 'a.', 'Black as jet; deep black.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jets deau', 'pl. ', 'of Jet deau');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jet deau', '', 'A stream of water spouting from a fountain or pipe\n   (especially from one arranged to throw water upward), in a public place\n   or in a garden, for ornament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeterus', 'n.', 'A yellowness of the parts of plants which are normally\n   green; yellows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jetsam', 'n.', 'Alt. of Jetson');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jetson', 'n.', 'Goods which sink when cast into the sea, and remain under\n   water; -- distinguished from flotsam, goods which float, and ligan,\n   goods which are sunk attached to a buoy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jetson', 'n.', 'Jettison. See Jettison, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jetteau', 'n.', 'See Jet deau.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jettee', 'n.', 'See Jetty, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jetter', 'n.', 'One who struts; one who bears himself jauntily; a fop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jettiness', 'n.', 'The state of being jetty; blackness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jettison', 'n.', 'The throwing overboard of goods from necessity, in order\n   to lighten a vessel in danger of wreck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jettison', 'n.', 'See Jetsam, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jetton', 'n.', 'A metal counter used in playing cards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jetty', 'a.', 'Made of jet, or like jet in color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jetties', 'pl. ', 'of Jetty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jetty', 'n.', 'A part of a building that jets or projects beyond the rest,\n   and overhangs the wall below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jetty', 'n.', 'A wharf or pier extending from the shore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jetty', 'n.', 'A structure of wood or stone extended into the sea to\n   influence the current or tide, or to protect a harbor; a mole; as, the\n   Eads system of jetties at the mouth of the Mississippi River.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jetty', 'v. i.', 'To jut out; to project.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeu desprit', '', 'A witticism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jew', 'n.', 'Originally, one belonging to the tribe or kingdom of Judah;\n   after the return from the Babylonish captivity, any member of the new\n   state; a Hebrew; an Israelite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jewbush', 'n.', 'A euphorbiaceous shrub of the genus Pedilanthus (P.\n   tithymaloides), found in the West Indies, and possessing powerful\n   emetic and drastic qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jewel', 'n.', 'An ornament of dress usually made of a precious metal, and\n   having enamel or precious stones as a part of its design.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jewel', 'n.', 'A precious stone; a gem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jewel', 'n.', 'An object regarded with special affection; a precious thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jewel', 'n.', 'A bearing for a pivot a pivot in a watch, formed of a\n   crystal or precious stone, as a ruby.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeweled', 'imp. & p. p.', 'of Jewel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jewelled', '', 'of Jewel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeweling', 'p. pr. & vb. n.', 'of Jewel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jewelling', '', 'of Jewel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jewel', 'v. t.', 'To dress, adorn, deck, or supply with jewels, as a dress,\n   a sword hilt, or a watch; to bespangle, as with jewels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jeweler', 'n.', 'One who makes, or deals in, jewels, precious stones, and\n   similar ornaments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jewellery', 'n.', 'See Jewelry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jewelry', 'n.', 'The art or trade of a jeweler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jewelry', 'n.', 'Jewels, collectively; as, a brides jewelry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jewelweed', 'n.', 'See Impatiens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jewess', 'fem.', 'A Hebrew woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jewfish', 'n.', 'A very large serranoid fish (Promicrops itaiara) of\n   Florida and the Gulf of Mexico. It often reaches the weight of five\n   hundred pounds. Its color is olivaceous or yellowish, with numerous\n   brown spots. Called also guasa, and warsaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jewfish', 'n.', 'A similar gigantic fish (Stereolepis gigas) of Southern\n   California, valued as a food fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jewfish', 'n.', 'The black grouper of Florida and Texas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jewfish', 'n.', 'A large herringlike fish; the tarpum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jewise', 'n.', 'Same as Juise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jewish', 'a.', 'Of or pertaining to the Jews or Hebrews; characteristic of\n   or resembling the Jews or their customs; Israelitish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jewry', 'n.', 'Judea; also, a district inhabited by Jews; a Jews quarter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jews-ear', 'n.', 'A species of fungus (Hirneola Auricula-Judae, /\n   Auricula), bearing some resemblance to the human ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jews-harp', 'n.', 'An instrument of music, which, when placed between the\n   teeth, gives, by means of a bent metal tongue struck by the finger, a\n   sound which is modulated by the breath; -- called also Jews-trump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jews-harp', 'n.', 'The shackle for joining a chain cable to an anchor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jews-stone', 'n.', 'Alt. of Jewstone');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jewstone', 'n.', 'A large clavate spine of a fossil sea urchin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jezebel', 'n.', 'A bold, vicious woman; a termagant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jharal', 'n.', 'A wild goat (Capra Jemlaica) which inhabits the loftiest\n   mountains of India. It has long, coarse hair, forming a thick mane on\n   its head and neck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jib', 'v. i.', 'A triangular sail set upon a stay or halyard extending from\n   the foremast or fore-topmast to the bowsprit or the jib boom. Large\n   vessels often carry several jibe; as, inner jib; outer jib; flying jib;\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jib', 'v. i.', 'The projecting arm of a crane, from which the load is\n   suspended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jib', 'v. i.', 'To move restively backward or sidewise, -- said of a horse;\n   to balk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jibber', 'n.', 'A horse that jibs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jibed', 'imp. & p. p.', 'of Jibe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jibing', 'p. pr. & vb. n.', 'of Jibe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jibe', 'v. i.', 'To shift, as the boom of a fore-and-aft sail, from one\n   side of a vessel to the other when the wind is aft or on the quarter.\n   See Gybe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jibe', 'v. i.', 'To change a ships course so as to cause a shifting of the\n   boom. See Jibe, v. t., and Gybe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jibe', 'v. t.', 'To agree; to harmonize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jiffy', 'n.', 'A moment; an instant; as, I will be ready in a jiffy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jig', 'n.', 'A light, brisk musical movement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jig', 'n.', 'A light, humorous piece of writing, esp. in rhyme; a farce in\n   verse; a ballad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jig', 'n.', 'A piece of sport; a trick; a prank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jig', 'n.', 'A trolling bait, consisting of a bright spoon and a hook\n   attached.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jig', 'n.', 'A small machine or handy tool');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jig', 'n.', 'A contrivance fastened to or inclosing a piece of work, and\n   having hard steel surfaces to guide a tool, as a drill, or to form a\n   shield or templet to work to, as in filing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jig', 'n.', 'An apparatus or a machine for jigging ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jigged', 'imp. & p. p.', 'of Jig');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jigging', 'p. pr. & vb. n.', 'of Jig');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jig', 'v. t.', 'To sing to the tune of a jig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jig', 'v. t.', 'To trick or cheat; to cajole; to delude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jig', 'v. t.', 'To sort or separate, as ore in a jigger or sieve. See\n   Jigging, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jig', 'n.', 'To cut or form, as a piece of metal, in a jigging machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jig', 'v. i.', 'To dance a jig; to skip about.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jigger', 'n.', 'A species of flea (Sarcopsylla, / Pulex, penetrans), which\n   burrows beneath the skin. See Chigoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jigger', 'n. & v.', 'One who, or that which, jigs; specifically, a miner\n   who sorts or cleans ore by the process of jigging; also, the sieve used\n   in jigging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jigger', 'n. & v.', 'A horizontal table carrying a revolving mold, on which\n   earthen vessels are shaped by rapid motion; a potters wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jigger', 'n. & v.', 'A templet or tool by which vessels are shaped on a\n   potters wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jigger', 'n. & v.', 'A light tackle, consisting of a double and single\n   block and the fall, used for various purposes, as to increase the\n   purchase on a topsail sheet in hauling it home; the watch tackle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jigger', 'n. & v.', 'A small fishing vessel, rigged like a yawl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jigger', 'n. & v.', 'A supplementary sail. See Dandy, n., 2 (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jigger', 'n.', 'A pendulum rolling machine for slicking or graining\n   leather; same as Jack, 4 (i).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jigging', 'n.', 'The act or using a jig; the act of separating ore with a\n   jigger, or wire-bottomed sieve, which is moved up and down in water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jiggish', 'a.', 'Resembling, or suitable for, a jig, or lively movement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jiggish', 'a.', 'Playful; frisky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jiggle', 'v. i.', 'To wriggle or frisk about; to move awkwardly; to shake\n   up and down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jigjog', 'n.', 'A jolting motion; a jogging pace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jigjog', 'a.', 'Having a jolting motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jill', 'n.', 'A young woman; a sweetheart. See Gill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jill-flirt', 'n.', 'A light, giddy, or wanton girl or woman. See\n   Gill-flirt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jilt', 'n.', 'A woman who capriciously deceives her lover; a coquette; a\n   flirt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jilted', 'imp. & p. p.', 'of Jilt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jilting', 'p. pr. & vb. n.', 'of Jilt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jilt', 'v. t.', 'To cast off capriciously or unfeeling, as a lover; to\n   deceive in love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jilt', 'v. i.', 'To play the jilt; to practice deception in love; to\n   discard lovers capriciously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jimcrack', 'n.', 'See Gimcrack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jim-crow', 'n.', 'A machine for bending or straightening rails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jim-crow', 'n.', 'A planing machine with a reversing tool, to plane both\n   ways.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jimmies', 'pl. ', 'of Jimmy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jimmy', 'n.', 'A short crowbar used by burglars in breaking open doors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jimp', 'a.', 'Neat; handsome; elegant. See Gimp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jimson weed', '', 'See Jamestown weed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jin', 'n.', 'Alt. of Jinn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jinn', 'n.', 'See Jinnee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jingal', 'n.', 'A small portable piece of ordnance, mounted on a swivel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jingle', 'v. i.', 'To sound with a fine, sharp, rattling, clinking, or\n   tinkling sound; as, sleigh bells jingle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jingle', 'v. i.', 'To rhyme or sound with a jingling effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jingled', 'imp. & p. p.', 'of Jingle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jingling', 'p. pr. & vb. n.', 'of Jingle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jingle', 'v. t.', 'To cause to give a sharp metallic sound as a little\n   bell, or as coins shaken together; to tinkle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jingle', 'n.', 'A rattling, clinking, or tinkling sound, as of little bells\n   or pieces of metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jingle', 'n.', 'That which makes a jingling sound, as a rattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jingle', 'n.', 'A correspondence of sound in rhymes, especially when the\n   verse has little merit; hence, the verse itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jingler', 'n.', 'One who, or that which, jingles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jingling', 'n.', 'The act or process of producing a jingle; also, the sound\n   itself; a chink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jinglingly', 'adv.', 'So as to jingle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jingoes', 'pl. ', 'of Jingo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jingo', 'n.', 'A word used as a jocular oath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jingo', 'n.', 'A statesman who pursues, or who favors, aggressive,\n   domineering policy in foreign affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jingoism', 'n.', 'The policy of the Jingoes, so called. See Jingo, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jinn', 'pl. ', 'of Jinnee');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jinnee', 'n.', 'A genius or demon; one of the fabled genii, good and evil\n   spirits, supposed to be the children of fire, and to have the power of\n   assuming various forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jinny road', '', 'An inclined road in a coal mine, on which loaded cars\n   descend by gravity, drawing up empty ones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jinrikisha', 'n.', 'A small, two-wheeled, hooded vehicle drawn by one more\n   men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jippo', 'n.', 'A waistcoat or kind of stays for women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joes', 'pl. ', 'of Jo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jo', 'n.', 'A sweetheart; a darling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Job', 'n.', 'A sudden thrust or stab; a jab.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Job', 'n.', 'A piece of chance or occasional work; any definite work\n   undertaken in gross for a fixed price; as, he did the job for a\n   thousand dollars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Job', 'n.', 'A public transaction done for private profit; something\n   performed ostensibly as a part of official duty, but really for private\n   gain; a corrupt official business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Job', 'n.', 'Any affair or event which affects one, whether fortunately or\n   unfortunately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Job', 'n.', 'A situation or opportunity of work; as, he lost his job.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jobbed', 'imp. & p. p.', 'of Job');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jobbing', 'p. pr. & vb. n.', 'of Job');
  INSERT INTO words (word, word_type, description) VALUES  (    'Job', 'v. t.', 'To strike or stab with a pointed instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Job', 'v. t.', 'To thrust in, as a pointed instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Job', 'v. t.', 'To do or cause to be done by separate portions or lots; to\n   sublet (work); as, to job a contract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Job', 'v. t.', 'To buy and sell, as a broker; to purchase of importers or\n   manufacturers for the purpose of selling to retailers; as, to job\n   goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Job', 'v. t.', 'To hire or let by the job or for a period of service; as,\n   to job a carriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Job', 'v. i.', 'To do chance work for hire; to work by the piece; to do\n   petty work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Job', 'v. i.', 'To seek private gain under pretense of public service; to\n   turn public matters to private advantage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Job', 'v. i.', 'To carry on the business of a jobber in merchandise or\n   stocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Job', 'n.', 'The hero of the book of that name in the Old Testament; the\n   typical patient man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jobation', 'n.', 'A scolding; a hand, tedious reproof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jobber', 'n.', 'One who works by the job.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jobber', 'n.', 'A dealer in the public stocks or funds; a stockjobber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jobber', 'n.', 'One who buys goods from importers, wholesalers, or\n   manufacturers, and sells to retailers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jobber', 'n.', 'One who turns official or public business to private\n   advantage; hence, one who performs low or mercenary work in office,\n   politics, or intrigue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jobbernowl', 'n.', 'A blockhead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jobbery', 'n.', 'The act or practice of jobbing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jobbery', 'n.', 'Underhand management; official corruption; as, municipal\n   jobbery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jobbing', 'a.', 'Doing chance work or add jobs; as, a jobbing carpenter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jobbing', 'a.', 'Using opportunities of public service for private gain;\n   as, a jobbing politician.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jocantry', 'n.', 'The act or practice of jesting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jockeys', 'pl. ', 'of Jockey');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jockey', 'n.', 'A professional rider of horses in races.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jockey', 'n.', 'A dealer in horses; a horse trader.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jockey', 'n.', 'A cheat; one given to sharp practice in trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jockeyed', 'imp. & p. p.', 'of Jockey');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jockeying', 'p. pr. & vb. n.', 'of Jockey');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jockey', 'v. t.', '\" To jostle by riding against one.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jockey', 'v. t.', 'To play the jockey toward; to cheat; to trick; to impose\n   upon in trade; as, to jockey a customer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jockey', 'v. i.', 'To play or act the jockey; to cheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jockeying', 'n.', 'The act or management of one who jockeys; trickery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jockeyism', 'n.', 'The practice of jockeys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jockeyship', 'n.', 'The art, character, or position, of a jockey; the\n   personality of a jockey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jocose', 'a.', 'Given to jokes and jesting; containing a joke, or abounding\n   in jokes; merry; sportive; humorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jocoserious', 'a.', 'Mingling mirth and seriousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jocosity', 'n.', 'A jocose act or saying; jocoseness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jocular', 'a.', 'Given to jesting; jocose; as, a jocular person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jocular', 'a.', 'Sportive; merry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jocularity', 'n.', 'Jesting; merriment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jocularly', 'adv.', 'In jest; for sport or mirth; jocosely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joculary', 'a.', 'Jocular; jocose; sportive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joculator', 'n.', 'A jester; a joker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joculatory', 'a.', 'Droll; sportive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jocund', '', 'Merry; cheerful; gay; airy; lively; sportive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jocund', 'adv.', 'Merrily; cheerfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jocundity', 'n.', 'The state or quality of being jocund; gayety;\n   sportiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joe', 'n.', 'See Johannes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joe Miller', '', 'A jest book; a stale jest; a worn-out joke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joe-Pye weed', '', 'A tall composite plant of the genus Eupatorium (E.\n   purpureum), with purplish flowers, and whorled leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jogged', 'imp. & p. p.', 'of Jog');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jogging', 'p. pr. & vb. n.', 'of Jog');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jog', 'v. t.', 'To push or shake with the elbow or hand; to jostle; esp.,\n   to push or touch, in order to give notice, to excite ones attention,\n   or to warn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jog', 'v. t.', 'To suggest to; to notify; to remind; to call the attention\n   of; as, to jog the memory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jog', 'v. t.', 'To cause to jog; to drive at a jog, as a horse. See Jog, v.\n   i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jog', 'v. i.', 'To move by jogs or small shocks, like those of a slow trot;\n   to move slowly, leisurely, or monotonously; -- usually with on,\n   sometimes with over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jog', 'n.', 'A slight shake; a shake or push intended to give notice or\n   awaken attention; a push; a jolt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jog', 'n.', 'A rub; a slight stop; an obstruction; hence, an irregularity\n   in motion of from; a hitch; a break in the direction of a line or the\n   surface of a plane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jogger', 'n.', 'One who jogs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jogging', 'n.', 'The act of giving a jog or jogs; traveling at a jog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joggled', 'imp. & p. p.', 'of Joggle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joggling', 'p. pr. & vb. n.', 'of Joggle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joggle', 'v. t.', 'To shake slightly; to push suddenly but slightly, so as\n   to cause to shake or totter; to jostle; to jog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joggle', 'v. t.', 'To join by means of joggles, so as to prevent sliding\n   apart; sometimes, loosely, to dowel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joggle', 'v. i.', 'To shake or totter; to slip out of place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joggle', 'n.', 'A notch or tooth in the joining surface of any piece of\n   building material to prevent slipping; sometimes, but incorrectly,\n   applied to a separate piece fitted into two adjacent stones, or the\n   like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Johannean', 'a.', 'Of or pertaining to John, esp. to the Apostle John or\n   his writings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Johannes', 'n.', 'A Portuguese gold coin of the value of eight dollars,\n   named from the figure of King John which it bears; -- often contracted\n   into joe; as, a joe, or a half joe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Johannisberger', 'n.', 'A fine white wine produced on the estate of Schloss\n   (or Castle) Johannisberg, on the Rhine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'John', 'n.', 'A proper name of a man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Johnadreams', 'n.', 'A dreamy, idle fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Johnnies', 'pl. ', 'of Johnny');
  INSERT INTO words (word, word_type, description) VALUES  (    'Johnny', 'n.', 'A familiar diminutive of John.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Johnny', 'n.', 'A sculpin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Johnnycake', 'n.', 'A kind of bread made of the meal of maize (Indian\n   corn), mixed with water or milk, etc., and baked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Johnsonese', 'n.', 'The literary style of Dr. Samuel Johnson, or one formed\n   in imitation of it; an inflated, stilted, or pompous style, affecting\n   classical words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Johnson grass', '', 'A tall perennial grass (Sorghum Halepense), valuable\n   in the Southern and Western States for pasture and hay. The rootstocks\n   are large and juicy and are eagerly sought by swine. Called also Cuba\n   grass, Means grass, Evergreen millet, and Arabian millet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Johnsonian', 'a.', 'Pertaining to or resembling Dr. Johnson or his style;\n   pompous; inflated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Johnsonianism', 'n.', 'A manner of acting or of writing peculiar to, or\n   characteristic of, Dr. Johnson.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Johns-wort', 'n.', 'See St. Johns-wort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joined', 'imp. & p. p.', 'of Join');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joining', 'p. pr. & vb. n.', 'of Join');
  INSERT INTO words (word, word_type, description) VALUES  (    'Join', 'v. t.', 'To bring together, literally or figuratively; to place in\n   contact; to connect; to couple; to unite; to combine; to associate; to\n   add; to append.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Join', 'v. t.', 'To associate ones self to; to be or become connected\n   with; to league ones self with; to unite with; as, to join a party; to\n   join the church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Join', 'v. t.', 'To unite in marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Join', 'v. t.', 'To enjoin upon; to command.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Join', 'v. t.', 'To accept, or engage in, as a contest; as, to join\n   encounter, battle, issue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Join', 'v. i.', 'To be contiguous, close, or in contact; to come together;\n   to unite; to mingle; to form a union; as, the hones of the skull join;\n   two rivers join.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Join', 'n.', 'The line joining two points; the point common to two\n   intersecting lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joinant', 'a.', 'Adjoining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joinder', 'v. t.', 'The act of joining; a putting together; conjunction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joinder', 'v. t.', 'A joining of parties as plaintiffs or defendants in a\n   suit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joinder', 'v. t.', 'Acceptance of an issue tendered in law or fact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joinder', 'v. t.', 'A joining of causes of action or defense in civil suits\n   or criminal prosecutions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joiner', 'n.', 'One who, or that which, joins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joiner', 'n.', 'One whose occupation is to construct articles by joining\n   pieces of wood; a mechanic who does the woodwork (as doors, stairs,\n   etc.) necessary for the finishing of buildings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joiner', 'n.', 'A wood-working machine, for sawing, plaining, mortising,\n   tenoning, grooving, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joinery', 'n.', 'The art, or trade, of a joiner; the work of a joiner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joinhand', 'n.', 'Writing in which letters are joined in words; --\n   distinguished from writing in single letters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joint', 'n.', 'The place or part where two things or parts are joined or\n   united; the union of two or more smooth or even surfaces admitting of a\n   close-fitting or junction; junction as, a joint between two pieces of\n   timber; a joint in a pipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joint', 'n.', 'A joining of two things or parts so as to admit of motion;\n   an articulation, whether movable or not; a hinge; as, the knee joint; a\n   node or joint of a stem; a ball and socket joint. See Articulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joint', 'n.', 'The part or space included between two joints, knots, nodes,\n   or articulations; as, a joint of cane or of a grass stem; a joint of\n   the leg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joint', 'n.', 'Any one of the large pieces of meat, as cut into portions by\n   the butcher for roasting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joint', 'n.', 'A plane of fracture, or divisional plane, of a rock\n   transverse to the stratification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joint', 'n.', 'The space between the adjacent surfaces of two bodies joined\n   and held together, as by means of cement, mortar, etc.; as, a thin\n   joint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joint', 'n.', 'The means whereby the meeting surfaces of pieces in a\n   structure are secured together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joint', 'a.', 'Joined; united; combined; concerted; as joint action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joint', 'a.', 'Involving the united activity of two or more; done or\n   produced by two or more working together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joint', 'a.', 'United, joined, or sharing with another or with others; not\n   solitary in interest or action; holding in common with an associate, or\n   with associates; acting together; as, joint heir; joint creditor; joint\n   debtor, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joint', 'a.', 'Shared by, or affecting two or more; held in common; as,\n   joint property; a joint bond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jointed', 'imp. & p. p.', 'of Joint');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jointing', 'p. pr. & vb. n.', 'of Joint');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joint', 'v. t.', 'To unite by a joint or joints; to fit together; to\n   prepare so as to fit together; as, to joint boards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joint', 'v. t.', 'To join; to connect; to unite; to combine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joint', 'v. t.', 'To provide with a joint or joints; to articulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joint', 'v. t.', 'To separate the joints; of; to divide at the joint or\n   joints; to disjoint; to cut up into joints, as meat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joint', 'v. i.', 'To fit as if by joints; to coalesce as joints do; as, the\n   stones joint, neatly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jointed', 'a.', 'Having joints; articulated; full of nodes; knotty; as, a\n   jointed doll; jointed structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jointer', 'n.', 'One who, or that which, joints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jointer', 'n.', 'A plane for smoothing the surfaces of pieces which are to\n   be accurately joined');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jointer', 'n.', 'The longest plane used by a joiner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jointer', 'n.', 'A long stationary plane, for plaining the edges of barrel\n   staves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jointer', 'n.', 'A bent piece of iron inserted to strengthen the joints of\n   a wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jointer', 'n.', 'A tool for pointing the joints in brickwork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joint-fir', 'n.', 'A genus (Ephedra) of leafless shrubs, with the stems\n   conspicuously jointed; -- called also shrubby horsetail. There are\n   about thirty species, of which two or three are found from Texas to\n   California.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jointing', 'n.', 'The act or process of making a joint; also, the joints\n   thus produced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jointless', 'a.', 'Without a joint; rigid; stiff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jointly', 'adv.', 'In a joint manner; together; unitedly; in concert; not\n   separately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jointress', 'n.', 'A woman who has a jointure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jointure', 'n.', 'A joining; a joint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jointure', 'n.', 'An estate settled on a wife, which she is to enjoy after\n   husbands decease, for her own life at least, in satisfaction of dower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jointured', 'imp. & p. p.', 'of Jointure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jointuring', 'p. pr. & vb. n.', 'of Jointure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jointure', 'v. t.', 'To settle a jointure upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jointureless', 'a.', 'Having no jointure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jointuress', 'n.', 'See Jointress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jointweed', 'n.', 'A slender, nearly leafless, American herb (Polygonum\n   articulatum), with jointed spikes of small flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jointworm', 'n.', 'The larva of a small, hymenopterous fly (Eurytoma\n   hordei), which is found in gall-like swellings on the stalks of wheat,\n   usually at or just above the first joint. In some parts of America it\n   does great damage to the crop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joist', 'n.', 'A piece of timber laid horizontally, or nearly so, to which\n   the planks of the floor, or the laths or furring strips of a ceiling,\n   are nailed; -- called, according to its position or use, binding joist,\n   bridging joist, ceiling joist, trimming joist, etc. See Illust. of\n   Double-framed floor, under Double, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joisted', 'imp. & p. p.', 'of Joist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joisting', 'p. pr. & vb. n.', 'of Joist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joist', 'v. t.', 'To fit or furnish with joists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joke', 'n.', 'Something said for the sake of exciting a laugh; something\n   witty or sportive (commonly indicating more of hilarity or humor than\n   jest); a jest; a witticism; as, to crack good-natured jokes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joke', 'n.', 'Something not said seriously, or not actually meant;\n   something done in sport.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joked', 'imp. & p. p.', 'of Joke');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joking', 'p. pr. & vb. n.', 'of Joke');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joke', 'v. t.', 'To make merry with; to make jokes upon; to rally; to\n   banter; as, to joke a comrade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joke', 'v. i.', 'To do something for sport, or as a joke; to be merry in\n   words or actions; to jest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joker', 'n.', 'One who makes jokes or jests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joker', 'n.', 'See Rest bower, under 2d Bower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jokingly', 'adv.', 'In a joking way; sportively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jole', 'v. t. & n.', 'Alt. of Joll');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joll', 'v. t. & n.', 'Same as Jowl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jolif', 'a.', 'Joyful; merry; pleasant; jolly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jollification', 'n.', 'A merrymaking; noisy festivity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jollily', 'adv.', 'In a jolly manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jolloment', 'n.', 'Jollity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jolliness', 'n.', 'Jollity; noisy mirth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jollity', 'n.', 'Noisy mirth; gayety; merriment; festivity; boisterous\n   enjoyment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jolly', 'superl.', 'Full of life and mirth; jovial; joyous; merry;\n   mirthful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jolly', 'superl.', 'Expressing mirth, or inspiring it; exciting mirth and\n   gayety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jolly', 'superl.', 'Of fine appearance; handsome; excellent; lively;\n   agreeable; pleasant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jolly-boat', 'n.', 'A boat of medium size belonging to a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jollyhead', 'n.', 'Jollity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jolted', 'imp. & p. p.', 'of Jolt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jolt', 'v. i.', 'To shake with short, abrupt risings and fallings, as a\n   carriage moving on rough ground; as, the coach jolts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jolt', 'v. t.', 'To cause to shake with a sudden up and down motion, as in\n   a carriage going over rough ground, or on a high-trotting horse; as,\n   the horse jolts the rider; fast driving jolts the carriage and the\n   passengers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jolt', 'n.', 'A sudden shock or jerk; a jolting motion, as in a carriage\n   moving over rough ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jolter', 'n.', 'One who, or that which, jolts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jolterhead', 'n.', 'Alt. of Jolthead');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jolthead', 'n.', 'A dunce; a blockhead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joltingly', 'adv.', 'In a jolting manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jolty', 'a.', 'That jolts; as, a jolty coach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jonah', 'n.', 'The Hebrew prophet, who was cast overboard as one who\n   endangered the ship; hence, any person whose presence is unpropitious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jonesian', 'a.', 'Of or pertaining to Jones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jongleur', 'n.', 'Alt. of Jongler');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jongler', 'n.', 'In the Middle Ages, a court attendant or other person who,\n   for hire, recited or sang verses, usually of his own composition. See\n   Troubadour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jongler', 'n.', 'A juggler; a conjuror. See Juggler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jonquil', 'n.', 'Alt. of Jonquille');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jonquille', 'n.', 'A bulbous plant of the genus Narcissus (N. Jonquilla),\n   allied to the daffodil. It has long, rushlike leaves, and yellow or\n   white fragrant flowers. The root has emetic properties. It is sometimes\n   called the rush-leaved daffodil. See Illust. of Corona.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joram', 'n.', 'See Jorum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jordan', 'n.', 'Alt. of Jorden');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jorden', 'n.', 'A pot or vessel with a large neck, formerly used by\n   physicians and alchemists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jorden', 'n.', 'A chamber pot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jorum', 'n.', 'A large drinking vessel; also, its contents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joseph', 'n.', 'An outer garment worn in the 18th century; esp., a womans\n   riding habit, buttoned down the front.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Josephs flower', '', 'A composite herb (Tragopogon pratensis), of the same\n   genus as the salsify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joso', 'n.', 'A small gudgeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joss', 'n.', 'A Chinese household divinity; a Chinese idol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jossa', 'interj.', 'A command to a horse, probably meaning \"stand still.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jostled', 'imp. & p. p.', 'of Jostle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jostling', 'p. pr. & vb. n.', 'of Jostle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jostle', 'v. t.', 'To run against and shake; to push out of the way; to\n   elbow; to hustle; to disturb by crowding; to crowd against.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jostle', 'v. i.', 'To push; to crowd; to hustle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jostle', 'n.', 'A conflict by collisions; a crowding or bumping together;\n   interference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jostlement', 'n.', 'Crowding; hustling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jot', 'n.', 'An iota; a point; a tittle; the smallest particle. Cf. Bit, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jotted', 'imp. & p. p.', 'of Jot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jotting', 'p. pr. & vb. n.', 'of Jot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jot', 'v. t.', 'To set down; to make a brief note of; -- usually followed\n   by down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jotter', 'n.', 'One who jots down memoranda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jotter', 'n.', 'A memorandum book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jougs', 'n.', 'An iron collar fastened to a wall or post, formerly used in\n   Scotland as a kind of pillory. [Written also juggs.] See Juke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jouissance', 'n.', 'Jollity; merriment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jouk', 'v. i.', 'See Juke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joul', 'v. t.', 'See Jowl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joule', 'n.', 'A unit of work which is equal to 107 units of work in the C.\n   G. S. system of units (ergs), and is practically equivalent to the\n   energy expended in one second by an electric current of one ampere in a\n   resistance of one ohm. One joule is approximately equal to 0.738 foot\n   pounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jounced', 'imp. & p. p.', 'of Jounce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jouncing', 'p. pr. & vb. n.', 'of Jounce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jounce', 'v. t. & i.', 'To jolt; to shake, especially by rough riding or by\n   driving over obstructions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jounce', 'n.', 'A jolt; a shake; a hard trot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Journal', 'a.', 'Daily; diurnal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Journal', 'a.', 'A diary; an account of daily transactions and events.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Journal', 'a.', 'A book of accounts, in which is entered a condensed and\n   grouped statement of the daily transactions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Journal', 'a.', 'A daily register of the ships course and distance, the\n   winds, weather, incidents of the voyage, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Journal', 'a.', 'The record of daily proceedings, kept by the clerk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Journal', 'a.', 'A newspaper published daily; by extension, a weekly\n   newspaper or any periodical publication, giving an account of passing\n   events, the proceedings and memoirs of societies, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Journal', 'a.', 'That which has occurred in a day; a days work or travel;\n   a days journey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Journal', 'a.', 'That portion of a rotating piece, as a shaft, axle,\n   spindle, etc., which turns in a bearing or box. See Illust. of Axle\n   box.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Journalism', 'n.', 'The keeping of a journal or diary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Journalism', 'n.', 'The periodical collection and publication of current\n   news; the business of managing, editing, or writing for, journals or\n   newspapers; as, political journalism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Journalist', 'n.', 'One who keeps a journal or diary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Journalist', 'n.', 'The conductor of a public journal, or one whose\n   business it to write for a public journal; an editorial or other\n   professional writer for a periodical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Journalistic', 'a.', 'Pertaining to journals or to journalists; contained\n   in, or characteristic of, the public journals; as journalistic\n   literature or enterprise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Journalized', 'imp. & p. p.', 'of Journalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Journalizing', 'p. pr. & vb. n.', 'of Journalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Journalize', 'v. t.', 'To enter or record in a journal or diary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Journalize', 'v. i.', 'to conduct or contribute to a public journal; to\n   follow the profession of a journalist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Journeys', 'pl. ', 'of Journey');
  INSERT INTO words (word, word_type, description) VALUES  (    'Journey', 'n.', 'The travel or work of a day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Journey', 'n.', 'Travel or passage from one place to another; hence,\n   figuratively, a passage through life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Journeyed', 'imp. & p. p.', 'of Journey');
  INSERT INTO words (word, word_type, description) VALUES  (    'Journeying', 'p. pr. & vb. n.', 'of Journey');
  INSERT INTO words (word, word_type, description) VALUES  (    'Journey', 'v. i.', 'To travel from place to place; to go from home to a\n   distance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Journey', 'v. t.', 'To traverse; to travel over or through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Journey-bated', 'a.', 'Worn out with journeying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Journeyer', 'n.', 'One who journeys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Journeymen', 'pl. ', 'of Journeyman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Journeyman', 'n.', 'Formerly, a man hired to work by the day; now,\n   commonly, one who has mastered a handicraft or trade; -- distinguished\n   from apprentice and from master workman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Journeywork', 'n.', 'Originally, work done by the day; work done by a\n   journeyman at his trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joust', 'v. i.', 'To engage in mock combat on horseback, as two knights in\n   the lists; to tilt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joust', 'v. i.', 'A tilting match; a mock combat on horseback between two\n   knights in the lists or inclosed field.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jouster', 'n.', 'One who jousts or tilts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jove', 'n.', 'The chief divinity of the ancient Romans; Jupiter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jove', 'n.', 'The planet Jupiter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jove', 'n.', 'The metal tin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jovial', 'a.', 'Of or pertaining to the god, or the planet, Jupiter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jovial', 'a.', 'Sunny; serene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jovial', 'a.', 'Gay; merry; joyous; jolly; mirth-inspiring; hilarious;\n   characterized by mirth or jollity; as, a jovial youth; a jovial\n   company; a jovial poem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jovialist', 'n.', 'One who lives a jovial life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joviality', 'n.', 'The quality or state of being jovial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jovially', 'adv.', 'In a jovial manner; merrily; gayly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jovialness', 'n.', 'Noisy mirth; joviality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jovialty', 'n.', 'Joviality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jovian', 'a.', 'Of or pertaining to Jove, or Jupiter (either the deity or\n   the planet).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jovicentric', 'a.', 'Revolving around the planet Jupiter; appearing as\n   viewed from Jupiter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jovinianist', 'n.', 'An adherent to the doctrines of Jovinian, a monk of\n   the fourth century, who denied the virginity of Mary, and opposed the\n   asceticism of his time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jowl', 'n.', 'The cheek; the jaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jowl', 'v. t.', 'To throw, dash, or knock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jowler', 'n.', 'A dog with large jowls, as the beagle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jowter', 'n.', 'A mounted peddler of fish; -- called also jouster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joy', 'n.', 'The passion or emotion excited by the acquisition or\n   expectation of good; pleasurable feelings or emotions caused by\n   success, good fortune, and the like, or by a rational prospect of\n   possessing what we love or desire; gladness; exhilaration of spirits;\n   delight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joy', 'n.', 'That which causes joy or happiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joy', 'n.', 'The sign or exhibition of joy; gayety; mirth; merriment;\n   festivity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joyed', 'imp. & p. p.', 'of Joy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joying', 'p. pr. & vb. n.', 'of Joy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joy', 'n.', 'To rejoice; to be glad; to delight; to exult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joy', 'v. t.', 'To give joy to; to congratulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joy', 'v. t.', 'To gladden; to make joyful; to exhilarate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joy', 'v. t.', 'To enjoy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joyace', 'n.', 'Enjoyment; gayety; festivity; joyfulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joyancy', 'n.', 'Joyance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joyful', 'a.', 'Full of joy; having or causing joy; very glad; as, a joyful\n   heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joyless', 'a.', 'Not having joy; not causing joy; unenjoyable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joyous', 'a.', 'Glad; gay; merry; joyful; also, affording or inspiring joy;\n   with of before the word or words expressing the cause of joy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Joysome', 'a.', 'Causing joyfulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jub', 'n.', 'A vessel for holding ale or wine; a jug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jubae', 'pl. ', 'of Juba');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juba', 'n.', 'The mane of an animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juba', 'n.', 'A loose panicle, the axis of which falls to pieces, as in\n   certain grasses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jubate', 'a.', 'Fringed with long, pendent hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jube', 'n.', 'chancel screen or rood screen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jube', 'n.', 'gallery above such a screen, from which certain parts of the\n   service were formerly read.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jubilant', 'a.', 'Uttering songs of triumph; shouting with joy; triumphant;\n   exulting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jubilantly', 'adv.', 'In a jubilant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jubilar', 'a.', 'Pertaining to, or having the character of, a jubilee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jubilate', 'n.', 'The third Sunday after Easter; -- so called because the\n   introit is the 66th Psalm, which, in the Latin version, begins with the\n   words, \"Jubilate Deo.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jubilate', 'n.', 'A name of the 100th Psalm; -- so called from its opening\n   word in the Latin version.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jubilate', 'v. i.', 'To exult; to rejoice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jubilation', 'n.', 'A triumphant shouting; rejoicing; exultation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jubilee', 'n.', 'Every fiftieth year, being the year following the\n   completion of each seventh sabbath of years, at which time all the\n   slaves of Hebrew blood were liberated, and all lands which had been\n   alienated during the whole period reverted to their former owners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jubilee', 'n.', 'The joyful commemoration held on the fiftieth anniversary\n   of any event; as, the jubilee of Queen Victorias reign; the jubilee of\n   the American Board of Missions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jubilee', 'n.', 'A church solemnity or ceremony celebrated at Rome, at\n   stated intervals, originally of one hundred years, but latterly of\n   twenty-five; a plenary and extraordinary indulgence grated by the\n   sovereign pontiff to the universal church. One invariable condition of\n   granting this indulgence is the confession of sins and receiving of the\n   eucharist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jubilee', 'n.', 'A season of general joy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jubilee', 'n.', 'A state of joy or exultation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jucundity', 'n.', 'Pleasantness; agreeableness. See Jocundity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judahite', 'n.', 'One of the tribe of Judah; a member of the kingdom of\n   Judah; a Jew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judaic', 'a.', 'Alt. of Judaical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judaical', 'a.', 'Of or pertaining to the Jews.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judaically', 'adv.', 'After the Jewish manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judaism', 'n.', 'The religious doctrines and rites of the Jews as enjoined\n   in the laws of Moses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judaism', 'n.', 'Conformity to the Jewish rites and ceremonies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judaist', 'n.', 'One who believes and practices Judaism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judaistic', 'a.', 'Of or pertaining to Judaism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judaization', 'n.', 'The act of Judaizing; a conforming to the Jewish\n   religion or ritual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judaized', 'imp. & p. p.', 'of Judaize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judaizing', 'p. pr. & vb. n.', 'of Judaize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judaize', 'v. i.', 'To conform to the doctrines, observances, or methods of\n   the Jews; to inculcate or impose Judaism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judaize', 'v. t.', 'To impose Jewish observances or rites upon; to convert\n   to Judaism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judaizer', 'n.', 'One who conforms to or inculcates Judaism; specifically,\n   pl. (Ch. Hist.), those Jews who accepted Christianity but still adhered\n   to the law of Moses and worshiped in the temple at Jerusalem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judas', 'n.', 'The disciple who betrayed Christ. Hence: A treacherous\n   person; one who betrays under the semblance of friendship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judas', 'a.', 'Treacherous; betraying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judas-colored', 'a.', 'Red; -- from a tradition that Judas Iscariot had red\n   hair and beard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juddock', 'n.', 'See Jacksnipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judean', 'a.', 'Of or pertaining to Judea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judean', 'n.', 'A native of Judea; a Jew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judge', 'v. i.', 'A public officer who is invested with authority to hear\n   and determine litigated causes, and to administer justice between\n   parties in courts held for that purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judge', 'v. i.', 'One who has skill, knowledge, or experience, sufficient\n   to decide on the merits of a question, or on the quality or value of\n   anything; one who discerns properties or relations with skill and\n   readiness; a connoisseur; an expert; a critic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judge', 'v. i.', 'A person appointed to decide in a/trial of skill, speed,\n   etc., between two or more parties; an umpire; as, a judge in a horse\n   race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judge', 'v. i.', 'One of supreme magistrates, with both civil and military\n   powers, who governed Israel for more than four hundred years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judge', 'v. i.', 'The title of the seventh book of the Old Testament; the\n   Book of Judges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judged', 'imp. & p. p.', 'of Judge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judging', 'p. pr. & vb. n.', 'of Judge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judge', 'a.', 'To hear and determine, as in causes on trial; to decide as a\n   judge; to give judgment; to pass sentence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judge', 'a.', 'To assume the right to pass judgment on another; to sit in\n   judgment or commendation; to criticise or pass adverse judgment upon\n   others. See Judge, v. t., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judge', 'v. t.', 'To compare facts or ideas, and perceive their relations\n   and attributes, and thus distinguish truth from falsehood; to\n   determine; to discern; to distinguish; to form an opinion about.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judge', 'v. t.', 'To hear and determine by authority, as a case before a\n   court, or a controversy between two parties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judge', 'v. t.', 'To examine and pass sentence on; to try; to doom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judge', 'v. t.', 'To arrogate judicial authority over; to sit in judgment\n   upon; to be censorious toward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judge', 'v. t.', 'To determine upon or deliberation; to esteem; to think;\n   to reckon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judge', 'v. t.', 'To exercise the functions of a magistrate over; to\n   govern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judger', 'n.', 'One who judges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judgeship', 'n.', 'The office of a judge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judgment', 'v. i.', 'The act of judging; the operation of the mind,\n   involving comparison and discrimination, by which a knowledge of the\n   values and relations of thins, whether of moral qualities, intellectual\n   concepts, logical propositions, or material facts, is obtained; as, by\n   careful judgment he avoided the peril; by a series of wrong judgments\n   he forfeited confidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judgment', 'v. i.', 'The power or faculty of performing such operations\n   (see 1); esp., when unqualified, the faculty of judging or deciding\n   rightly, justly, or wisely; good sense; as, a man of judgment; a\n   politician without judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judgment', 'v. i.', 'The conclusion or result of judging; an opinion; a\n   decision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judgment', 'v. i.', 'The act of determining, as in courts of law, what is\n   conformable to law and justice; also, the determination, decision, or\n   sentence of a court, or of a judge; the mandate or sentence of God as\n   the judge of all.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judgment', 'v. i.', 'That act of the mind by which two notions or ideas\n   which are apprehended as distinct are compared for the purpose of\n   ascertaining their agreement or disagreement. See 1. The comparison may\n   be threefold: (1) Of individual objects forming a concept. (2) Of\n   concepts giving what is technically called a judgment. (3) Of two\n   judgments giving an inference. Judgments have been further classed as\n   analytic, synthetic, and identical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judgment', 'v. i.', 'That power or faculty by which knowledge dependent\n   upon comparison and discrimination is acquired. See 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judgment', 'v. i.', 'A calamity regarded as sent by God, by way of\n   recompense for wrong committed; a providential punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judgment', 'v. i.', 'The final award; the last sentence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judicable', 'v. i.', 'Capable of being judged; capable of being tried or\n   decided upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judicative', 'a.', 'Having power to judge; judicial; as, the judicative\n   faculty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judicatory', 'a.', 'Pertaining to the administration of justice; dispensing\n   justice; judicial; as, judicatory tribunals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judicatory', 'n.', 'A court of justice; a tribunal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judicatory', 'n.', 'Administration of justice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judicature', 'n.', 'The state or profession of those employed in the\n   administration of justice; also, the dispensing or administration of\n   justice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judicature', 'n.', 'A court of justice; a judicatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judicature', 'n.', 'The right of judicial action; jurisdiction; extent\n   jurisdiction of a judge or court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judicial', 'a.', 'Pertaining or appropriate to courts of justice, or to a\n   judge; practiced or conformed to in the administration of justice;\n   sanctioned or ordered by a court; as, judicial power; judicial\n   proceedings; a judicial sale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judicial', 'a.', 'Fitted or apt for judging or deciding; as, a judicial\n   mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judicial', 'a.', 'Belonging to the judiciary, as distinguished from\n   legislative, administrative, or executive. See Executive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judicial', 'a.', 'Judicious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judicially', 'adv.', 'In a judicial capacity or judicial manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judiciary', 'a.', 'Of or pertaining to courts of judicature, or legal\n   tribunals; judicial; as, a judiciary proceeding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judiciary', 'n.', 'That branch of government in which judicial power is\n   vested; the system of courts of justice in a country; the judges, taken\n   collectively; as, an independent judiciary; the senate committee on the\n   judiciary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judicious', 'a.', 'Of or relating to a court; judicial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judicious', 'a.', 'Directed or governed by sound judgment; having sound\n   judgment; wise; prudent; sagacious; discreet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judiciously', 'adv.', 'In a judicious manner; with good judgment; wisely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Judiciousness', 'n.', 'The quality or state of being judicious; sagacity;\n   sound judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jug', 'n.', 'A vessel, usually of coarse earthenware, with a swelling belly\n   and narrow mouth, and having a handle on one side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jug', 'n.', 'A pitcher; a ewer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jug', 'n.', 'A prison; a jail; a lockup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jugged', 'imp. & p. p.', 'of Jug');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jugging', 'p. pr. & vb. n.', 'of Jug');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jug', 'v. t.', 'To seethe or stew, as in a jug or jar placed in boiling\n   water; as, to jug a hare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jug', 'v. t.', 'To commit to jail; to imprison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jug', 'v. i.', 'To utter a sound resembling this word, as certain birds do,\n   especially the nightingale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jug', 'v. i.', 'To nestle or collect together in a covey; -- said of quails\n   and partridges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jugal', 'a.', 'Relating to a yoke, or to marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jugal', 'a.', 'Pertaining to, or in the region of, the malar, or cheek\n   bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jugata', 'n. pl.', 'The figures of two heads on a medal or coin, either\n   side by side or joined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jugated', 'a.', 'Coupled together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juge', 'n.', 'A judge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jugement', 'n.', 'Judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juger', 'n.', 'A Roman measure of land, measuring 28,800 square feet, or\n   240 feet in length by 120 in breadth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jugger', 'n.', 'An East Indian falcon. See Lugger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juggernaut', 'n.', 'One of the names under which Vishnu, in his incarnation\n   as Krishna, is worshiped by the Hindoos.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juggled', 'imp. & p. p.', 'of Juggle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juggling', 'p. pr. & vb. n.', 'of Juggle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juggle', 'v. i.', 'To play tricks by sleight of hand; to cause amusement\n   and sport by tricks of skill; to conjure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juggle', 'v. i.', 'To practice artifice or imposture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juggle', 'v. t.', 'To deceive by trick or artifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juggle', 'n.', 'A trick by sleight of hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juggle', 'n.', 'An imposture; a deception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juggle', 'n.', 'A block of timber cut to a length, either in the round or\n   split.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juggler', 'n.', 'One who practices or exhibits tricks by sleight of hand;\n   one skilled in legerdemain; a conjurer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juggler', 'n.', 'A deceiver; a cheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juggleress', 'n.', 'A female juggler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jugglery', 'n.', 'The art or act of a juggler; sleight of hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jugglery', 'n.', 'Trickery; imposture; as, political jugglery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juggling', 'a.', 'Cheating; tricky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juggling', 'n.', 'Jugglery; underhand practice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juggs', 'n. pl.', 'See Jougs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juglandin', 'n.', 'An extractive matter contained in the juice of the green\n   shucks of the walnut (Juglans regia). It is used medicinally as an\n   alterative, and also as a black hair dye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juglandine', 'n.', 'An alkaloid found in the leaves of the walnut (Juglans\n   regia).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juglans', 'n.', 'A genus of valuable trees, including the true walnut of\n   Europe, and the America black walnut, and butternut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juglone', 'n.', 'A yellow crystalline substance resembling quinone,\n   extracted from green shucks of the walnut (Juglans regia); -- called\n   also nucin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jugular', 'a.', 'Of or pertaining to the throat or neck; as, the jugular\n   vein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jugular', 'a.', 'Of or pertaining to the jugular vein; as, the jugular\n   foramen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jugular', 'a.', 'Having the ventral fins beneath the throat; -- said of\n   certain fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jugular', 'a.', 'One of the large veins which return the blood from the\n   head to the heart through two chief trunks, an external and an\n   internal, on each side of the neck; -- called also the jugular vein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jugular', 'a.', 'Any fish which has the ventral fins situated forward of\n   the pectoral fins, or beneath the throat; one of a division of fishes\n   (Jugulares).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jugulated', 'imp. & p. p.', 'of Jugulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jugulating', 'p. pr. & vb. n.', 'of Jugulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jugulate', 'v. t.', 'To cut the throat of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jugula', 'pl. ', 'of Jugulum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jugulum', 'n.', 'The lower throat, or that part of the neck just above the\n   breast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juga', 'pl. ', 'of Jugum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jugums', 'pl. ', 'of Jugum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jugum', 'n.', 'One of the ridges commonly found on the fruit of\n   umbelliferous plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jugum', 'n.', 'A pair of the opposite leaflets of a pinnate plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juice', 'n.', 'The characteristic fluid of any vegetable or animal\n   substance; the sap or part which can be expressed from fruit, etc.; the\n   fluid part which separates from meat in cooking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juice', 'v. t.', 'To moisten; to wet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juiceless', 'a.', 'Lacking juice; dry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juiciness', 'n.', 'The state or quality of being juicy; succulence plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juicy', 'superl.', 'A bounding with juice; succulent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juise', 'n.', 'Judgment; justice; sentence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jujube', 'n.', 'The sweet and edible drupes (fruits) of several\n   Mediterranean and African species of small trees, of the genus\n   Zizyphus, especially the Z. jujuba, Z. vulgaris, Z. mucronata, and Z.\n   Lotus. The last named is thought to have furnished the lotus of the\n   ancient Libyan Lotophagi, or lotus eaters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juke', 'v. i.', 'To bend the neck; to bow or duck the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juke', 'n.', 'The neck of a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juke', 'v. i.', 'To perch on anything, as birds do.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Julaceous', 'a.', 'Like an ament, or bearing aments; amentaceous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Julep', 'n.', 'A refreshing drink flavored with aromatic herbs');
  INSERT INTO words (word, word_type, description) VALUES  (    'Julep', 'n.', 'a sweet, demulcent, acidulous, or mucilaginous mixture, used\n   as a vehicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Julep', 'n.', 'A beverage composed of brandy, whisky, or some other\n   spirituous liquor, with sugar, pounded ice, and sprigs of mint; --\n   called also mint julep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Julian', 'a.', 'Relating to, or derived from, Julius Caesar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Julienne', 'n.', 'A kind of soup containing thin slices or shreds of\n   carrots, onions, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juliform', 'a.', 'Having the shape or appearance of a julus or catkin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juli', 'pl. ', 'of Julus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Julus', 'n.', 'A catkin or ament. See Ament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Julies', 'pl. ', 'of July');
  INSERT INTO words (word, word_type, description) VALUES  (    'July', 'n.', 'The seventh month of the year, containing thirty-one days.');
  INSERT INTO words (word, word_type, description) VALUES  (    'July-flower', 'n.', 'See Gillyflower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jumart', 'n.', 'The fabled offspring of a bull and a mare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jumbled', 'imp. & p. p.', 'of Jumble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jumbling', 'p. pr. & vb. n.', 'of Jumble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jumble', 'v. t.', 'To mix in a confused mass; to put or throw together\n   without order; -- often followed by together or up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jumble', 'v. i.', 'To meet or unite in a confused way; to mix confusedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jumble', 'n.', 'A confused mixture; a mass or collection without order; as,\n   a jumble of words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jumble', 'n.', 'A small, thin, sugared cake, usually ring-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jumblement', 'n.', 'Confused mixture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jumbler', 'n.', 'One who confuses things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jumblingly', 'adv.', 'In a confused manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jument', 'n.', 'A beast; especially, a beast of burden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jump', 'n.', 'A kind of loose jacket for men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jump', 'n.', 'A bodice worn instead of stays by women in the 18th century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jumped', 'imp. & p. p.', 'of Jump');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jumping', 'p. pr. & vb. n.', 'of Jump');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jump', 'v. i.', 'To spring free from the ground by the muscular action of\n   the feet and legs; to project ones self through the air; to spring; to\n   bound; to leap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jump', 'v. i.', 'To move as if by jumping; to bounce; to jolt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jump', 'v. i.', 'To coincide; to agree; to accord; to tally; -- followed by\n   with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jump', 'v. t.', 'To pass by a spring or leap; to overleap; as, to jump a\n   stream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jump', 'v. t.', 'To cause to jump; as, he jumped his horse across the\n   ditch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jump', 'v. t.', 'To expose to danger; to risk; to hazard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jump', 'v. t.', 'To join by a butt weld.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jump', 'v. t.', 'To thicken or enlarge by endwise blows; to upset.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jump', 'v. t.', 'To bore with a jumper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jump', 'n.', 'The act of jumping; a leap; a spring; a bound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jump', 'n.', 'An effort; an attempt; a venture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jump', 'n.', 'The space traversed by a leap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jump', 'n.', 'A dislocation in a stratum; a fault.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jump', 'n.', 'An abrupt interruption of level in a piece of brickwork or\n   masonry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jump', 'a.', 'Nice; exact; matched; fitting; precise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jump', 'adv.', 'Exactly; pat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jumper', 'n.', 'One who, or that which, jumps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jumper', 'n.', 'A long drilling tool used by masons and quarrymen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jumper', 'n.', 'A rude kind of sleigh; -- usually, a simple box on runners\n   which are in one piece with the poles that form the thills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jumper', 'n.', 'The larva of the cheese fly. See Cheese fly, under Cheese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jumper', 'n.', 'A name applied in the 18th century to certain Calvinistic\n   Methodists in Wales whose worship was characterized by violent\n   convulsions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jumper', 'n.', 'spring to impel the star wheel, also a pawl to lock fast a\n   wheel, in a repeating timepiece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jumper', 'n.', 'A loose upper garment');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jumper', 'n.', 'A sort of blouse worn by workmen over their ordinary dress\n   to protect it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jumper', 'n.', 'A fur garment worn in Arctic journeys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jumping', 'p. a. & vb. n.', 'of Jump, to leap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jumpweld', 'v. t.', 'See Buttweld, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juncaceous', 'a.', 'Of. pertaining to, or resembling, a natural order of\n   plants (Juncaceae), of which the common rush (Juncus) is the type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juncate', 'n.', 'See Junket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juncite', 'n.', 'A fossil rush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Junco', 'n.', 'Any bird of the genus Junco, which includes several species\n   of North American finches; -- called also snowbird, or blue snowbird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juncous', 'a.', 'Full of rushes: resembling rushes; juncaceous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Junction', 'n.', 'The act of joining, or the state of being joined; union;\n   combination; coalition; as, the junction of two armies or detachments;\n   the junction of paths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Junction', 'n.', 'The place or point of union, meeting, or junction;\n   specifically, the place where two or more lines of railway meet or\n   cross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juncture', 'n.', 'A joining; a union; an alliance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juncture', 'n.', 'The line or point at which two bodies are joined; a\n   joint; an articulation; a seam; as, the junctures of a vessel or of the\n   bones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juncture', 'n.', 'A point of time; esp., one made critical or important by\n   a concurrence of circumstances; hence, a crisis; an exigency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'June', 'n.', 'The sixth month of the year, containing thirty days.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juneating', 'n.', 'A kind of early apple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juneberry', 'n.', 'The small applelike berry of American trees of genus\n   Amelanchier; -- also called service berry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juneberry', 'n.', 'The shrub or tree which bears this fruit; -- also called\n   shad bush, and had tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jungermanniae', 'pl. ', 'of Jungermannia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jungermannia', 'n.', 'A genus of hepatic mosses, now much circumscribed,\n   but formerly comprising most plants of the order, which is sometimes\n   therefore called Jungermanniaceae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jungle', 'n.', 'A dense growth of brushwood, grasses, reeds, vines, etc.;\n   an almost impenetrable thicket of trees, canes, and reedy vegetation,\n   as in India, Africa, Australia, and Brazil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jungly', 'a.', 'Consisting of jungles; abounding with jungles; of the\n   nature of a jungle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Junior', 'a.', 'Less advanced in age than another; younger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Junior', 'a.', 'Lower in standing or in rank; later in office; as, a junior\n   partner; junior counsel; junior captain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Junior', 'a.', 'Composed of juniors, whether younger or a lower standing;\n   as, the junior class; of or pertaining to juniors or to a junior class.\n   See Junior, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Junior', 'n.', 'Belonging to a younger person, or an earlier time of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Junior', 'n.', 'A younger person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Junior', 'n.', 'Hence: One of a lower or later standing; specifically, in\n   American colleges, one in the third year of his course, one in the\n   fourth or final year being designated a senior; in some seminaries, one\n   in the first year, in others, one in the second year, of a three years\n   course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juniority', 'n.', 'The state or quality of being junior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juniper', 'n.', 'Any evergreen shrub or tree, of the genus Juniperus and\n   order Coniferae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juniperin', 'n.', 'A yellow amorphous substance extracted from juniper\n   berries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juniperite', 'n.', 'One of the fossil Coniferae, evidently allied to the\n   juniper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Junk', 'n.', 'A fragment of any solid substance; a thick piece. See Chunk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Junk', 'n.', 'Pieces of old cable or old cordage, used for making gaskets,\n   mats, swabs, etc., and when picked to pieces, forming oakum for filling\n   the seams of ships.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Junk', 'n.', 'Old iron, or other metal, glass, paper, etc., bought and sold\n   by junk dealers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Junk', 'n.', 'Hard salted beef supplied to ships.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Junk', 'n.', 'A large vessel, without keel or prominent stem, and with huge\n   masts in one piece, used by the Chinese, Japanese, Siamese, Malays,\n   etc., in navigating their waters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Junker', 'n.', 'A young German noble or squire; esp., a member of the\n   aristocratic party in Prussia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Junkerism', 'n.', 'The principles of the aristocratic party in Prussia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Junket', 'n.', 'A cheese cake; a sweetmeat; any delicate food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Junket', 'n.', 'A feast; an entertainment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Junket', 'v. i.', 'To feast; to banquet; to make an entertainment; --\n   sometimes applied opprobriously to feasting by public officers at the\n   public cost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Junketed', 'imp. & p. p.', 'of Junket');
  INSERT INTO words (word, word_type, description) VALUES  (    'Junketing', 'p. pr. & vb. n.', 'of Junket');
  INSERT INTO words (word, word_type, description) VALUES  (    'Junket', 'v. t.', 'To give entertainment to; to feast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Junketing', 'n.', 'A feast or entertainment; a revel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Junketries', 'n. pl.', 'Sweetmeats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Junos', 'pl. ', 'of June');
  INSERT INTO words (word, word_type, description) VALUES  (    'June', 'n.', 'The sister and wife of Jupiter, the queen of heaven, and the\n   goddess who presided over marriage. She corresponds to the Greek Hera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'June', 'n.', 'One of the early discovered asteroids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juntas', 'pl. ', 'of Junta');
  INSERT INTO words (word, word_type, description) VALUES  (    'Junta', 'n.', 'A council; a convention; a tribunal; an assembly; esp., the\n   grand council of state in Spain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juntos', 'pl. ', 'of Junto');
  INSERT INTO words (word, word_type, description) VALUES  (    'Junto', 'n.', 'A secret council to deliberate on affairs of government or\n   politics; a number of men combined for party intrigue; a faction; a\n   cabal; as, a junto of ministers; a junto of politicians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Junartie', 'n.', 'Jeopardy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jupati palm', '', 'A great Brazilian palm tree (Raphia taedigera), used by\n   the natives for many purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jupe', 'n.', 'Same as Jupon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jupiter', 'n.', 'The supreme deity, king of gods and men, and reputed to be\n   the son of Saturn and Rhea; Jove. He corresponds to the Greek Zeus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jupiter', 'n.', 'One of the planets, being the brightest except Venus, and\n   the largest of them all, its mean diameter being about 85,000 miles. It\n   revolves about the sun in 4,332.6 days, at a mean distance of 5.2028\n   from the sun, the earths mean distance being taken as unity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jupon', 'n.', 'Alt. of Juppon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juppon', 'n.', 'A sleeveless jacket worn over the armor in the 14th\n   century. It fitted closely, and descended below the hips.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juppon', 'n.', 'A petticoat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jura', 'n.', '1. A range of mountains between France and Switzerland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jura', 'n.', 'The Jurassic period. See Jurassic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jural', 'a.', 'Pertaining to natural or positive right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jural', 'a.', 'Of or pertaining to jurisprudence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juramenta', 'pl. ', 'of Juramentum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juramentum', 'n.', 'An oath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jurassic', 'a.', 'Of the age of the middle Mesozoic, including, as divided\n   in England and Europe, the Lias, Oolite, and Wealden; -- named from\n   certain rocks of the Jura mountains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jurassic', 'n.', 'The Jurassic period or formation; -- called also the\n   Jura.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jurat', 'n.', 'A person under oath; specifically, an officer of the nature\n   of an alderman, in certain municipal corporations in England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jurat', 'n.', 'The memorandum or certificate at the end of an asffidavit,\n   or a bill or answer in chancery, showing when, before whom, and (in\n   English practice), where, it was sworn or affirmed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juratory', 'a.', 'Relating to or comprising an oath; as, juratory caution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jura-trias', 'n.', 'A term applied to many American Mesozoic strata, in\n   which the characteristics of the Jurassic and Triassic periods appear\n   to be blended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jurdiccion', 'n.', 'Jurisdiction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jurdon', 'n.', 'Jordan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jurel', 'n.', 'A yellow carangoid fish of the Atlantic and Gulf coasts\n   (Caranx chrysos), most abundant southward, where it is valued as a food\n   fish; -- called also hardtail, horse crevalle, jack, buffalo jack,\n   skipjack, yellow mackerel, and sometimes, improperly, horse mackerel.\n   Other species of Caranx (as C. fallax) are also sometimes called jurel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juridic', 'a.', 'Alt. of Juridical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juridical', 'a.', 'Pertaining to a judge or to jurisprudence; acting in the\n   distribution of justice; used in courts of law; according to law;\n   legal; as, juridical law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juridically', 'adv.', 'In a juridical manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jurisconsult', 'n.', 'A man learned in the civil law; an expert in\n   juridical science; a professor of jurisprudence; a jurist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jurisdiction', 'a.', 'The legal power, right, or authority of a particular\n   court to hear and determine causes, to try criminals, or to execute\n   justice; judicial authority over a cause or class of causes; as,\n   certain suits or actions, or the cognizance of certain crimes, are\n   within the jurisdiction of a particular court, that is, within the\n   limits of its authority or commission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jurisdiction', 'a.', 'The authority of a sovereign power to govern or\n   legislate; the right of making or enforcing laws; the power or right of\n   exercising authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jurisdiction', 'a.', 'Sphere of authority; the limits within which any\n   particular power may be exercised, or within which a government or a\n   court has authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jurisdictional', 'a.', 'Of or pertaining to jurisdiction; as jurisdictional\n   rights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jurisdictive', 'a.', 'Having jurisdiction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jurisprudence', 'a.', 'The science of juridical law; the knowledge of the\n   laws, customs, and rights of men in a state or community, necessary for\n   the due administration of justice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jurisprudent', 'a.', 'Understanding law; skilled in jurisprudence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jurisprudent', 'n.', 'One skilled in law or jurisprudence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jurisprudential', 'a.', 'Of or pertaining to jurisprudence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jurist', 'a.', 'One who professes the science of law; one versed in the\n   law, especially in the civil law; a writer on civil and international\n   law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juristic', 'a.', 'Alt. of Juristical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juristical', 'a.', 'Of or pertaining to a jurist, to the legal profession,\n   or to jurisprudence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juror', 'n.', 'A member of a jury; a juryman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juror', 'n.', 'A member of any jury for awarding prizes, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jury', 'a.', 'For temporary use; -- applied to a temporary contrivance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juries', 'pl. ', 'of Jury');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jury', 'a.', 'A body of men, usually twelve, selected according to law,\n   impaneled and sworn to inquire into and try any matter of fact, and to\n   render their true verdict according to the evidence legally adduced.\n   See Grand jury under Grand, and Inquest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jury', 'a.', 'A committee for determining relative merit or awarding prizes\n   at an exhibition or competition; as, the art jury gave him the first\n   prize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jurymen', 'pl. ', 'of Juryman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juryman', 'n.', 'One who is impaneled on a jury, or who serves as a juror.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jury-rigged', 'a.', 'Rigged for temporary service. See Jury, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jussi', 'n.', 'A delicate fiber, produced in the Philippine Islands from an\n   unidentified plant, of which dresses, etc., are made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Just', 'a.', 'Conforming or conformable to rectitude or justice; not doing\n   wrong to any; violating no right or obligation; upright; righteous;\n   honest; true; -- said both of persons and things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Just', 'a.', 'Not transgressing the requirement of truth and propriety;\n   conformed to the truth of things, to reason, or to a proper standard;\n   exact; normal; reasonable; regular; due; as, a just statement; a just\n   inference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Just', 'a.', 'Rendering or disposed to render to each one his due;\n   equitable; fair; impartial; as, just judge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Just', 'adv.', 'Precisely; exactly; -- in place, time, or degree; neither\n   more nor less than is stated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Just', 'adv.', 'Closely; nearly; almost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Just', 'adv.', 'Barely; merely; scarcely; only; by a very small space or\n   time; as, he just missed the train; just too late.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Just', 'v. i.', 'To joust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Just', 'n.', 'A joust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justice', 'a.', 'The quality of being just; conformity to the principles of\n   righteousness and rectitude in all things; strict performance of moral\n   obligations; practical conformity to human or divine law; integrity in\n   the dealings of men with each other; rectitude; equity; uprightness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justice', 'a.', 'Conformity to truth and reality in expressing opinions and\n   in conduct; fair representation of facts respecting merit or demerit;\n   honesty; fidelity; impartiality; as, the justice of a description or of\n   a judgment; historical justice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justice', 'a.', 'The rendering to every one his due or right; just\n   treatment; requital of desert; merited reward or punishment; that which\n   is due to ones conduct or motives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justice', 'a.', 'Agreeableness to right; equity; justness; as, the justice\n   of a claim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justice', 'a.', 'A person duly commissioned to hold courts, or to try and\n   decide controversies and administer justice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justice', 'v. t.', 'To administer justice to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justiceable', 'a.', 'Liable to trial in a court of justice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justicehood', 'n.', 'Justiceship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justicement', 'n.', 'Administration of justice; procedure in courts of\n   justice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justicer', 'n.', 'One who administers justice; a judge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justiceship', 'n.', 'The office or dignity of a justice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justiciable', 'a.', 'Proper to be examined in a court of justice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justiciar', 'n.', 'Same as Justiciary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justiciary', 'n.', 'An old name for the judges of the higher English\n   courts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justico', 'n.', 'Alt. of Justicoat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justicoat', 'n.', 'Formerly, a close coat or waistcoat with sleeves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justifiable', 'a.', 'Capable of being justified, or shown to be just.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justification', 'n.', 'The act of justifying or the state of being\n   justified; a showing or proving to be just or conformable to law,\n   justice, right, or duty; defense; vindication; support; as, arguments\n   in justification of the prisoners conduct; his disobedience admits\n   justification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justification', 'n.', 'The showing in court of a sufficient lawful reason\n   why a party charged or accused did that for which he is called to\n   answer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justification', 'n.', 'The act of justifying, or the state of being\n   justified, in respect to Gods requirements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justification', 'n.', 'Adjustment of type by spacing it so as to make it\n   exactly fill a line, or of a cut so as to hold it in the right place;\n   also, the leads, quads, etc., used for making such adjustment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justificative', 'a.', 'Having power to justify; justificatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justificator', 'n.', 'One who justifies or vindicates; a justifier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justificatory', 'a.', 'Vindicatory; defensory; justificative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justifier', 'n.', 'One who justifies; one who vindicates, supports,\n   defends, or absolves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justified', 'imp. & p. p.', 'of Justify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justifying', 'p. pr. & vb. n.', 'of Justify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justify', 'a.', 'To prove or show to be just; to vindicate; to maintain or\n   defend as conformable to law, right, justice, propriety, or duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justify', 'a.', 'To pronounce free from guilt or blame; to declare or prove\n   to have done that which is just, right, proper, etc.; to absolve; to\n   exonerate; to clear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justify', 'a.', 'To treat as if righteous and just; to pardon; to\n   exculpate; to absolve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justify', 'a.', 'To prove; to ratify; to confirm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justify', 'a.', 'To make even or true, as lines of type, by proper spacing;\n   to adjust, as type. See Justification, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justify', 'v. i.', 'To form an even surface or true line with something\n   else; to fit exactly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justify', 'v. i.', 'To take oath to the ownership of property sufficient to\n   qualify ones self as bail or surety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justinian', 'a.', 'Of or pertaining to the Institutes or laws of the Roman\n   Justinian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justle', 'v. i.', 'To run or strike against each other; to encounter; to\n   clash; to jostle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justled', 'imp. & p. p.', 'of Justle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justling', 'p. pr. & vb. n.', 'of Justle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justle', 'v. t.', 'To push; to drive; to force by running against; to\n   jostle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justle', 'n.', 'An encounter or shock; a jostle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justly', 'a.', 'In a just manner; in conformity to law, justice, or\n   propriety; by right; honestly; fairly; accurately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Justness', 'n.', 'The quality of being just; conformity to truth,\n   propriety, accuracy, exactness, and the like; justice; reasonableness;\n   fairness; equity; as, justness of proportions; the justness of a\n   description or representation; the justness of a cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jutted', 'imp. & p. p.', 'of Jut');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jutting', 'p. pr. & vb. n.', 'of Jut');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jut', 'v. i.', 'To shoot out or forward; to project beyond the main body;\n   as, the jutting part of a building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jut', 'v. i.', 'To butt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jut', 'n.', 'That which projects or juts; a projection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jut', 'n.', 'A shove; a push.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jute', 'n.', 'The coarse, strong fiber of the East Indian Corchorus\n   olitorius, and C. capsularis; also, the plant itself. The fiber is much\n   used for making mats, gunny cloth, cordage, hangings, paper, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jutes', 'n. pl.', 'Jutlanders; one of the Low German tribes, a portion of\n   which settled in Kent, England, in the 5th century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jutlander', 'n.', 'A native or inhabitant of Jutland in Denmark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jutlandish', 'a.', 'Of or pertaining to Jutland, or to the people of\n   Jutland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jutting', 'a.', 'Projecting, as corbels, cornices, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jutty', 'n.', 'A projection in a building; also, a pier or mole; a jetty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Jutty', 'v. t. & i.', 'To project beyond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juvenal', 'n.', 'A youth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juvenescence', 'n.', 'A growing young.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juvenescent', 'a.', 'Growing or becoming young.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juvenile', 'a.', 'Young; youthful; as, a juvenile appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juvenile', 'a.', 'Of or pertaining to youth; as, juvenile sports.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juvenile', 'n.', 'A young person or youth; -- used sportively or\n   familiarly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juvenileness', 'n.', 'The state or quality of being juvenile; juvenility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juvenilities', 'pl. ', 'of Juvenility');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juvenility', 'n.', 'Youthfulness; adolescence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juvenility', 'n.', 'The manners or character of youth; immaturity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juvia', 'n.', 'A Brazilian name for the lofty myrtaceous tree (Bertholetia\n   excelsa) which produces the large seeds known as Brazil nuts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juwansa', 'n.', 'The camels thorn. See under Camel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juwise', 'n.', 'Same as Juise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juxtapose', 'v. t.', 'To place in juxtaposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juxtaposited', 'imp. & p. p.', 'of Juxtaposit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juxtapositing', 'p. pr. & vb. n.', 'of Juxtaposit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juxtaposit', 'v. t.', 'To place in close connection or contiguity; to\n   juxtapose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Juxtaposition', 'v. i.', 'A placing or being placed in nearness or\n   contiguity, or side by side; as, a juxtaposition of words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Junold', 'a.', 'See Gimmal.\n   K () the eleventh letter of the English alphabet, is nonvocal\n   consonant. The form and sound of the letter K are from the Latin, which\n   used the letter but little except in the early period of the language.\n   It came into the Latin from the Greek, which received it from a\n   Phoenician source, the ultimate origin probably being Egyptian.\n   Etymologically K is most nearly related to c, g, h (which see).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kaama', 'n.', 'The hartbeest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kabala', 'n.', 'See Cabala.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kabassou', 'n.', 'See Cabassou.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kabob', 'n. & v. t.', 'See Cabob, n. & v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kabook', 'n.', 'A clay ironstone found in Ceylon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kabyle', 'n.', 'A Berber, as in Algiers or Tunis. See Berber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kadder', 'n.', 'The jackdaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kadi', 'n.', 'Alt. of Kadiaster');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kadiaster', 'n.', 'A Turkish judge. See Cadi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kafal', 'n.', 'The Arabian name of two trees of the genus Balsamodendron,\n   which yield a gum resin and a red aromatic wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kaffir', 'n.', 'Alt. of Kafir');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kafir', 'n.', 'One of a race which, with the Hottentots and Bushmen,\n   inhabit South Africa. They inhabit the country north of Cape Colony,\n   the name being now specifically applied to the tribes living between\n   Cape Colony and Natal; but the Zulus of Natal are true Kaffirs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kafir', 'n.', 'One of a race inhabiting Kafiristan in Central Asia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kaffle', 'n.', 'See Coffle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kafilah', 'n.', 'See Cafila.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kaftan', 'n & v.', 'See Caftan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kage', 'n.', 'A chantry chapel inclosed with lattice or screen work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kagu', 'n.', 'A singular, crested, grallatorial bird (Rhinochetos jubatus),\n   native of New Caledonia. It is gray above, paler beneath, and the\n   feathers of the wings and tail are handsomely barred with brown, black,\n   and gray. It is allied to the sun bittern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kaguan', 'n.', 'The colugo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kahani', 'n.', 'A kind of notary public, or attorney, in the Levant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kahau', 'n.', 'A long-nosed monkey (Semnopithecus nasalis), native of\n   Borneo. The general color of the body is bright chestnut, with the\n   under parts, shoulders, and sides of the head, golden yellow, and the\n   top of the head and upper part of the back brown. Called also proboscis\n   monkey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kail', 'n.', 'A kind of headless cabbage. Same as Kale, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kail', 'n.', 'Any cabbage, greens, or vegetables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kail', 'n.', 'A broth made with kail or other vegetables; hence, any broth;\n   also, a dinner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kaimacam', 'n.', 'Same as Caimacam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kain', 'n.', 'Poultry, etc., required by the lease to be paid in kind by a\n   tenant to his landlord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kainit', 'n.', 'Salts of potassium used in the manufacture of fertilizers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kainite', 'n.', 'A compound salt consisting chiefly of potassium chloride\n   and magnesium sulphate, occurring at the Stassfurt salt mines in\n   Prussian Saxony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kainozoic', 'a.', 'See Cenozoic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kaique', 'n.', 'See Caique.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kairine', 'n.', 'A pale buff or white crystalline alkaloid derived from\n   quinoline, and used as an antipyretic in medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kairoline', 'n.', 'An organic base obtained from quinoline. It is used as a\n   febrifuge, and resembles kairine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kaiser', 'n.', 'The ancient title of emperors of Germany assumed by King\n   William of Prussia when crowned sovereign of the new German empire in\n   1871.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kaka', 'n.', 'A New Zealand parrot of the genus Nestor, especially the\n   brown parrot (Nestor meridionalis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kakapo', 'n.', 'A singular nocturnal parrot (Strigops habroptilus), native\n   of New Zealand. It lives in holes during the day, but is active at\n   night. It resembles an owl in its colors and general appearance. It has\n   large wings, but can fly only a short distance. Called also owl parrot,\n   night parrot, and night kaka.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kakaralli', 'n.', 'A kind of wood common in Demerara, durable in salt\n   water, because not subject to the depredations of the sea worm and\n   barnacle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kakistocracy', 'n.', 'Government by the worst men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kakoxene', 'n.', 'See Cacoxene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kalan', 'n.', 'The sea otter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kalasie', 'n.', 'A long-tailed monkey of Borneo (Semnopithecus rubicundus).\n   It has a tuft of long hair on the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kale', 'n.', 'A variety of cabbage in which the leaves do not form a head,\n   being nearly the original or wild form of the species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kale', 'n.', 'See Kail, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kaleege', 'n.', 'One of several species of large, crested, Asiatic\n   pheasants, belonging to the genus Euplocamus, and allied to the\n   firebacks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kaleidophon', '', 'Alt. of Kaleidophone');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kaleidophone', '', 'An instrument invented by Professor Wheatstone,\n   consisting of a reflecting knob at the end of a vibrating rod or thin\n   plate, for making visible, in the motion of a point of light reflected\n   from the knob, the paths or curves corresponding with the musical notes\n   produced by the vibrations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kaleidoscope', 'n.', 'An instrument invented by Sir David Brewster, which\n   contains loose fragments of colored glass, etc., and reflecting\n   surfaces so arranged that changes of position exhibit its contents in\n   an endless variety of beautiful colors and symmetrical forms. It has\n   been much employed in arts of design.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kaleidoscopic', 'a.', 'Alt. of Kaleidoscopical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kaleidoscopical', 'a.', 'Of, pertaining to, or formed by, a kaleidoscope;\n   variegated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kalendar', 'n.', 'See Calendar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kalendarial', 'a.', 'See Calendarial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kalender', 'n.', 'See 3d Calender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kalends', 'n.', 'Same as Calends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kali', 'n.', 'The last and worst of the four ages of the world; --\n   considered to have begun B. C. 3102, and to last 432,000 years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kali', 'n.', 'The black, destroying goddess; -- called also Doorga, Anna\n   Purna.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kali', 'n.', 'The glasswort (Salsola Kali).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kalif', 'n.', 'See Caliph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kaliform', 'a.', 'Formed like kali, or glasswort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kaligenous', 'a.', 'Forming alkalies with oxygen, as some metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kalium', 'n.', 'Potassium; -- so called by the German chemists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kalki', 'n.', 'The name of Vishnu in his tenth and last avatar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kalmia', 'n.', 'A genus of North American shrubs with poisonous evergreen\n   foliage and corymbs of showy flowers. Called also mountain laurel, ivy\n   bush, lamb kill, calico bush, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kalmuck', 'n.', 'See Calmucks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kalmuck', 'n.', 'A kind of shaggy cloth, resembling bearskin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kalmuck', 'n.', 'A coarse, dyed, cotton cloth, made in Prussia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kalong', 'n.', 'A fruit bat, esp. the Indian edible fruit bat (Pteropus\n   edulis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kaloyer', 'n.', 'See Caloyer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kalpa', 'n.', 'One of the Brahmanic eons, a period of 4,320,000,000 years.\n   At the end of each Kalpa the world is annihilated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kalsomine', 'n. & v. t.', 'Same as Calcimine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kam', 'n.', 'Crooked; awry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kama', 'n.', 'The Hindoo Cupid. He is represented as a beautiful youth,\n   with a bow of sugar cane or flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kamala', 'n.', 'The red dusty hairs of the capsules of an East Indian tree\n   (Mallotus Philippinensis) used for dyeing silk. It is violently emetic,\n   and is used in the treatment of tapeworm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kame', 'n.', 'A low ridge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kami', 'n. pl.', 'A title given to the celestial gods of the first mythical\n   dynasty of Japan and extended to the demigods of the second dynasty,\n   and then to the long line of spiritual princes still represented by the\n   mikado.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kamichi', 'n.', 'A curious South American bird (Anhima, / Palamedea,\n   cornuta), often domesticated by the natives and kept with poultry,\n   which it defends against birds of prey. It has a long, slender,\n   hornlike ornament on its head, and two sharp spurs on each wing.\n   Although its beak, feet, and legs resemble those of gallinaceous birds,\n   it is related in anatomical characters to the ducks and geese\n   (Anseres). Called also horned screamer. The name is sometimes applied\n   also to the chaja. See Chaja, and Screamer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kamptulicon', 'n.', 'A kind of elastic floor cloth, made of India rubber,\n   gutta-percha, linseed oil, and powdered cork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kampylite', 'n.', 'A variety of mimetite or arseniate of lead in hexagonal\n   prisms of a fine orange yellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kamsin', 'n.', 'Alt. of Khamsin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Khamsin', 'n.', 'A hot southwesterly wind in Egypt, coming from the Sahara.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kamtschadales', 'n. pl.', 'An aboriginal tribe inhabiting the southern part\n   of Kamtschatka.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kan', 'v. t.', 'To know; to ken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kan', 'n.', 'See Khan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kanacka', 'n.', 'Alt. of Kanaka');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kanaka', 'n.', 'A native of the Sandwich Islands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kanchil', 'n.', 'A small chevrotain of the genus Tragulus, esp. T.\n   pygmaeus, or T. kanchil, inhabiting Java, Sumatra, and adjacent\n   islands; a deerlet. It is noted for its agility and cunning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kand', 'n.', 'Fluor spar; -- so called by Cornish miners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kangaroo', 'n.', 'Any one of numerous species of jumping marsupials of the\n   family Macropodidae. They inhabit Australia, New Guinea, and adjacent\n   islands, They have long and strong hind legs and a large tail, while\n   the fore legs are comparatively short and feeble. The giant kangaroo\n   (Macropus major) is the largest species, sometimes becoming twelve or\n   fourteen feet in total length. The tree kangaroos, belonging to the\n   genus Dendrolagus, live in trees; the rock kangaroos, of the genus\n   Petrogale, inhabit rocky situations; and the brush kangaroos, of the\n   genus Halmaturus, inhabit wooded districts. See Wallaby.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kansas', 'n. pl.', 'A tribe of Indians allied to the Winnebagoes and\n   Osages. They formerly inhabited the region which is now the State of\n   Kansas, but were removed to the Indian Territory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kantian', 'a.', 'Of or pertaining to Immanuel Kant, the German philosopher;\n   conformed or relating to any or all of the philosophical doctrines of\n   Immanuel Kant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kantian', 'n.', 'A follower of Kant; a Kantist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kantianism', 'n.', 'Alt. of Kantism');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kantism', 'n.', 'The doctrine or theory of Kant; the Kantian philosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kantist', 'n.', 'A disciple or follower of Kant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kanttry', 'n.', 'Same as Cantred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kaolin', 'n.', 'Alt. of Kaoline');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kaoline', 'n.', 'A very pure white clay, ordinarily in the form of an\n   impalpable powder, and used to form the paste of porcelain; China clay;\n   porcelain clay. It is chiefly derived from the decomposition of common\n   feldspar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kaolinization', 'n.', 'The process by which feldspar is changed into\n   kaolin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kaolinize', 'v. t.', 'To convert into kaolin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kapelle', 'n.', 'A chapel; hence, the choir or orchestra of a princes\n   chapel; now, a musical establishment, usually orchestral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kapellmeister', 'n.', 'See Capellmeister.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kapia', 'n.', 'The fossil resin of the kauri tree of New Zealand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kapnomar', 'n. Chem.', ') See Capnomor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Karagane', 'n.', 'A species of gray fox found in Russia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Karaism', 'n.', 'Doctrines of the Karaites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Karaite', 'n.', 'A sect of Jews who adhere closely to the letter of the\n   Scriptures, rejecting the oral law, and allowing the Talmud no binding\n   authority; -- opposed to the Rabbinists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Karatas', 'n.', 'A West Indian plant of the Pineapple family (Nidularium\n   Karatas).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Karma', 'n.', 'Ones acts considered as fixing ones lot in the future\n   existence. (Theos.) The doctrine of fate as the inflexible result of\n   cause and effect; the theory of inevitable consequence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Karmathian', 'n.', 'One of a Mohammedan sect founded in the ninth century\n   by Karmat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Karn', 'n.', 'A pile of rocks; sometimes, the solid rock. See Cairn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Karob', 'n.', 'The twenty-fourth part of a grain; -- a weight used by\n   goldsmiths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Karpholite', 'n.', 'A fibrous mineral occurring in tufts of a straw-yellow\n   color. It is a hydrous silicate of alumina and manganese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Karroos', 'pl. ', 'of Karreo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Karreo', 'n.', 'One of the dry table-lands of South Africa, which often\n   rise terracelike to considerable elevations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Karstenite', 'n.', 'Same as Anhydrite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Karvel', 'n.', 'See Carvel, and Caravel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Karyokinesis', 'n.', 'The indirect division of cells in which, prior to\n   division of the cell protoplasm, complicated changes take place in the\n   nucleus, attended with movement of the nuclear fibrils; -- opposed to\n   karyostenosis. The nucleus becomes enlarged and convoluted, and finally\n   the threads are separated into two groups which ultimately become\n   disconnected and constitute the daughter nuclei. Called also mitosis.\n   See Cell development, under Cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Karyokinetic', 'a.', 'Of or pertaining to karyokinesis; as, karyokinetic\n   changes of cell division.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Karyomiton', 'n.', 'The reticular network of fine fibers, of which the\n   nucleus of a cell is in part composed; -- in opposition to kytomiton,\n   or the network in the body of the cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Karyoplasma', 'n.', 'The protoplasmic substance of the nucleus of a cell:\n   nucleoplasm; -- in opposition to kytoplasma, the protoplasm of the\n   cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Karyostenosis', 'n.', 'Direct cell division (in which there is first a\n   simple division of the nucleus, without any changes in its structure,\n   followed by division of the protoplasm of the karyostenotic mode of\n   nuclear division.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kasack', 'n.', 'Same as Cossack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kat', 'n.', 'An Arabian shrub Catha edulis) the leaves of which are used as\n   tea by the Arabs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Katabolic', 'a.', 'Of or pertaining to katabolism; as, katabolic processes,\n   which give rise to substances (katastates) of decreasing complexity and\n   increasing stability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Katabolism', 'n.', 'Destructive or downward metabolism; regressive\n   metamorphism; -- opposed to anabolism. See Disassimilation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Katastate', 'n.', '(Physiol.) A substance formed by a katabolic process; --\n   opposed to anastate. See Katabolic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kate', 'n.', 'The brambling finch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kathetal', 'a.', 'Making a right angle; perpendicular, as two lines or two\n   sides of a triangle, which include a right angle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kathetometer', 'n.', 'Same as Cathetometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kattinumdoo', 'n.', 'A caoutchouc like substance obtained from the milky\n   juice of the East Indian Euphorbia Kattimundoo. It is used as a cement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Katydid', 'n.', 'A large, green, arboreal, orthopterous insect\n   (Cyrtophyllus concavus) of the family Locustidae, common in the United\n   States. The males have stridulating organs at the bases of the front\n   wings. During the summer and autumn, in the evening, the males make a\n   peculiar, loud, shrill sound, resembling the combination Katy-did,\n   whence the name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kauri', 'n.', 'A lofty coniferous tree of New Zealand Agathis, / Dammara,\n   australis), furnishing valuable timber and yielding one kind of dammar\n   resin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kava', 'n.', 'A species of Macropiper (M. methysticum), the long pepper,\n   from the root of which an intoxicating beverage is made by the\n   Polynesians, by a process of mastication; also, the beverage itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kavasses', 'pl. ', 'of Kavass');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kavass', 'n.', 'An armed constable; also, a government servant or courier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kaw', 'v. i. & n.', 'See Caw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kawaka', 'n.', 'a New Zealand tree, the Cypress cedar (Libocedrus Doniana),\n   having a valuable, fine-grained, reddish wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kawn', 'n.', 'An inn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kayak', 'n.', 'A light canoe, made of skins stretched over a frame, and\n   usually capable of carrying but one person, who sits amidships and uses\n   a double-bladed paddle. It is peculiar to the Eskimos and other Arctic\n   tribes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kayaker', 'n.', 'One who uses a kayak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kayko', 'n.', 'The dog salmon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kayles', 'n. pl.', 'A game; ninepins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kaynard', 'n.', 'A lazy or cowardly person; a rascal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kecked', 'imp. & p. p.', 'of Keck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kecking', 'p. pr. & vb. n.', 'of Keck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keck', 'v. i.', 'To heave or to retch, as in an effort to vomit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keck', 'n.', 'An effort to vomit; queasiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keckle', 'v. i. & n.', 'See Keck, v. i. & n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keckled', 'imp. & p. p.', 'of Keckle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keckling', 'p. pr. & vb. n.', 'of Keckle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keckle', 'v. t.', 'To wind old rope around, as a cable, to preserve its\n   surface from being fretted, or to wind iron chains around, to defend\n   from the friction of a rocky bottom, or from the ice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keckling', 'n.', 'Old rope or iron chains wound around a cable. See Keckle,\n   v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kecklish', 'a.', 'Inclined to vomit; squeamish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kecksies', 'pl. ', 'of Kecksy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kecksy', 'n.', 'The hollow stalk of an umbelliferous plant, such as the cow\n   parsnip or the hemlock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kecky', 'a.', 'Resembling a kecksy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kedged', 'imp. & p. p.', 'of Kedge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kedging', 'p. pr. & vb. n.', 'of Kedge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kedge', 'n.', 'To move (a vessel) by carrying out a kedge in a boat,\n   dropping it overboard, and hauling the vessel up to it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kedge', 'v. t.', 'A small anchor used whenever a large one can be dispensed\n   witch. See Kedge, v. t., and Anchor, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kedger', 'n.', 'A small anchor; a kedge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kedlook', 'n.', 'See Charlock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kee', 'n. pl.', 'See Kie, Ky, and Kine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keech', 'n.', 'A mass or lump of fat rolled up by the butcher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keel', 'v. t. & i.', 'To cool; to skim or stir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keel', 'n.', 'A brewers cooling vat; a keelfat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keel', 'n.', 'A longitudinal timber, or series of timbers scarfed together,\n   extending from stem to stern along the bottom of a vessel. It is the\n   principal timber of the vessel, and, by means of the ribs attached on\n   each side, supports the vessels frame. In an iron vessel, a\n   combination of plates supplies the place of the keel of a wooden ship.\n   See Illust. of Keelson.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keel', 'n.', 'Fig.: The whole ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keel', 'n.', 'A barge or lighter, used on the Type for carrying coal from\n   Newcastle; also, a barge load of coal, twenty-one tons, four cwt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keel', 'n.', 'The two lowest petals of the corolla of a papilionaceous\n   flower, united and inclosing the stamens and pistil; a carina. See\n   Carina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keel', 'n.', 'A projecting ridge along the middle of a flat or curved\n   surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keeled', 'imp. & p. p.', 'of Keel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keeling', 'p. pr. & vb. n.', 'of Keel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keel', 'v. i.', 'To traverse with a keel; to navigate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keel', 'v. i.', 'To turn up the keel; to show the bottom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keelage', 'n.', 'The right of demanding a duty or toll for a ship entering\n   a port; also, the duty or toll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keeled', 'a.', 'Keel-shaped; having a longitudinal prominence on the back;\n   as, a keeled leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keeled', 'a.', 'Having a median ridge; carinate; as, a keeled scale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keeler', 'n.', 'One employed in managing a Newcastle keel; -- called also\n   keelman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keeler', 'n.', 'A small or shallow tub; esp., one used for holding\n   materials for calking ships, or one used for washing dishes, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keelfat', 'n.', 'A cooler; a vat for cooling wort, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keelhauled', 'imp. & p. p.', 'of Keelhaul');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keelhauling', 'p. pr. & vb. n.', 'of Keelhaul');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keelhaul', 'v. i.', 'To haul under the keel of a ship, by ropes attached to\n   the yardarms on each side. It was formerly practiced as a punishment in\n   the Dutch and English navies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keeling', 'n.', 'A cod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keelivine', 'n.', 'A pencil of black or red lead; -- called also keelyvine\n   pen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'men', 'pl. ', 'of Keelman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keelman', 'n.', 'See Keeler, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keelrake', 'v. t.', 'Same as Keelhaul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keels', 'n. pl.', 'Ninepins. See Kayles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keelson', 'n.', 'A piece of timber in a ship laid on the middle of the\n   floor timbers over the keel, and binding the floor timbers to the keel;\n   in iron vessels, a structure of plates, situated like the keelson of a\n   timber ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keelvat', 'n.', 'See Keelfat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keen', 'superl.', 'Sharp; having a fine edge or point; as, a keen razor, or\n   a razor with a keen edge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keen', 'superl.', 'Acute of mind; sharp; penetrating; having or expressing\n   mental acuteness; as, a man of keen understanding; a keen look; keen\n   features.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keen', 'superl.', 'Bitter; piercing; acrimonious; cutting; stinging;\n   severe; as, keen satire or sarcasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keen', 'superl.', 'Piercing; penetrating; cutting; sharp; -- applied to\n   cold, wind, etc, ; as, a keen wind; the cold is very keen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keen', 'superl.', 'Eager; vehement; fierce; as, a keen appetite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keen', 'v. t.', 'To sharpen; to make cold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keen', 'n.', 'A prolonged wail for a deceased person. Cf. Coranach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keen', 'v. i.', 'To wail as a keener does.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keener', 'n.', 'A professional mourner who wails at a funeral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keenly', 'adv.', 'In a keen manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keenness', 'n.', 'The quality or state of being keen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kept', 'imp. & p. p.', 'of Keep');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keeping', 'p. pr. & vb. n.', 'of Keep');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keep', 'v. t.', 'To care; to desire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keep', 'v. t.', 'To hold; to restrain from departure or removal; not to let\n   go of; to retain in ones power or possession; not to lose; to retain;\n   to detain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keep', 'v. t.', 'To cause to remain in a given situation or condition; to\n   maintain unchanged; to hold or preserve in any state or tenor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keep', 'v. t.', 'To have in custody; to have in some place for\n   preservation; to take charge of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keep', 'v. t.', 'To preserve from danger, harm, or loss; to guard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keep', 'v. t.', 'To preserve from discovery or publicity; not to\n   communicate, reveal, or betray, as a secret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keep', 'v. t.', 'To attend upon; to have the care of; to tend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keep', 'v. t.', 'To record transactions, accounts, or events in; as, to\n   keep books, a journal, etc. ; also, to enter (as accounts, records,\n   etc. ) in a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keep', 'v. t.', 'To maintain, as an establishment, institution, or the\n   like; to conduct; to manage; as, to keep store.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keep', 'v. t.', 'To supply with necessaries of life; to entertain; as, to\n   keep boarders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keep', 'v. t.', 'To have in ones service; to have and maintain, as an\n   assistant, a servant, a mistress, a horse, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keep', 'v. t.', 'To have habitually in stock for sale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keep', 'v. t.', 'To continue in, as a course or mode of action; not to\n   intermit or fall from; to hold to; to maintain; as, to keep silence; to\n   keep ones word; to keep possession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keep', 'v. t.', 'To observe; to adhere to; to fulfill; not to swerve from\n   or violate; to practice or perform, as duty; not to neglect; to be\n   faithful to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keep', 'v. t.', 'To confine ones self to; not to quit; to remain in; as,\n   to keep ones house, room, bed, etc. ; hence, to haunt; to frequent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keep', 'v. t.', 'To observe duty, as a festival, etc. ; to celebrate; to\n   solemnize; as, to keep a feast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keep', 'v. i.', 'To remain in any position or state; to continue; to abide;\n   to stay; as, to keep at a distance; to keep aloft; to keep near; to\n   keep in the house; to keep before or behind; to keep in favor; to keep\n   out of company, or out reach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keep', 'v. i.', 'To last; to endure; to remain unimpaired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keep', 'v. i.', 'To reside for a time; to lodge; to dwell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keep', 'v. i.', 'To take care; to be solicitous; to watch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keep', 'v. i.', 'To be in session; as, school keeps to-day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keep', 'n.', 'The act or office of keeping; custody; guard; care; heed;\n   charge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keep', 'n.', 'The state of being kept; hence, the resulting condition;\n   case; as, to be in good keep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keep', 'n.', 'The means or provisions by which one is kept; maintenance;\n   support; as, the keep of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keep', 'n.', 'That which keeps or protects; a stronghold; a fortress; a\n   castle; specifically, the strongest and securest part of a castle,\n   often used as a place of residence by the lord of the castle,\n   especially during a siege; the donjon. See Illust. of Castle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keep', 'n.', 'That which is kept in charge; a charge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keep', 'n.', 'A cap for retaining anything, as a journal box, in place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keeper', 'n.', 'One who, or that which, keeps; one who, or that which,\n   holds or has possession of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keeper', 'n.', 'One who retains in custody; one who has the care of a\n   prison and the charge of prisoners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keeper', 'n.', 'One who has the care, custody, or superintendence of\n   anything; as, the keeper of a park, a pound, of sheep, of a gate, etc.\n   ; the keeper of attached property; hence, one who saves from harm; a\n   defender; a preserver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keeper', 'n.', 'One who remains or keeps in a place or position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keeper', 'n.', 'A ring, strap, clamp, or any device for holding an object\n   in place; as: (a) The box on a door jamb into which the bolt of a lock\n   protrudes, when shot. (b) A ring serving to keep another ring on the\n   finger. (c) A loop near the buckle of a strap to receive the end of the\n   strap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keeper', 'n.', 'A fruit that keeps well; as, the Roxbury Russet is a good\n   keeper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keepership', 'n.', 'The office or position of a keeper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keeping', 'n.', 'A holding; restraint; custody; guard; charge; care;\n   preservation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keeping', 'n.', 'Maintenance; support; provision; feed; as, the cattle have\n   good keeping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keeping', 'n.', 'Conformity; congruity; harmony; consistency; as, these\n   subjects are in keeping with each other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keeping', 'n.', 'Harmony or correspondence between the different parts of a\n   work of art; as, the foreground of this painting is not in keeping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keepsake', 'n.', 'Anything kept, or given to be kept, for the sake of the\n   giver; a token of friendship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keesh', 'n.', 'See Kish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keeve', 'n.', 'A vat or tub in which the mash is made; a mash tub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keeve', 'n.', 'A bleaching vat; a kier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keeve', 'n.', 'A large vat used in dressing ores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keeved', 'imp. & p. p.', 'of Keeve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keeving', 'p. pr. & vb. n.', 'of Keeve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keeve', 'v. t.', 'To set in a keeve, or tub, for fermentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keeve', 'v. t.', 'To heave; to tilt, as a cart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keever', 'n.', 'See Keeve, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keffe-kil', 'n.', 'See Kiefekil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keg', 'n.', 'A small cask or barrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keilhau-ite', 'n.', 'A mineral of a brownish black color, related to\n   titanite in form. It consists chiefly of silica, titanium dioxide,\n   lime, and yttria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keir', 'n.', 'See Kier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keitloa', 'n.', 'A black, two-horned, African rhinoceros (Atelodus\n   keitloa). It has the posterior horn about as long as the anterior one,\n   or even longer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keld', 'a.', 'Having a kell or covering; webbed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kele', 'v. t.', 'To cool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kell', 'n.', 'A kiln.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kell', 'n.', 'A sort of pottage; kale. See Kale, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kell', 'n.', 'The caul; that which covers or envelops as a caul; a net; a\n   fold; a film.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kell', 'n.', 'The cocoon or chrysalis of an insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keloid', 'a.', 'Applied to a variety of tumor forming hard, flat, irregular\n   excrescences upon the skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keloid', 'n.', 'A keloid tumor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kelotomy', 'n.', 'See Celotomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kelp', 'n.', 'The calcined ashes of seaweed, -- formerly much used in the\n   manufacture of glass, now used in the manufacture of iodine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kelp', 'n.', 'Any large blackish seaweed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kelpfish', 'n.', 'A small California food fish (Heterostichus rostratus),\n   living among kelp. The name is also applied to species of the genus\n   Platyglossus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kelpies', 'pl. ', 'of Kelpy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kelpie', 'n.', 'Alt. of Kelpy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kelpy', 'n.', 'An imaginary spirit of the waters, horselike in form,\n   vulgarly believed to warn, by preternatural noises and lights, those\n   who are to be drowned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kelpware', 'n.', 'Same as Kelp, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kelson', 'n.', 'See Keelson.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kelt', 'n.', 'See Kilt, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kelt', 'n.', 'Cloth with the nap, generally of native black wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kelt', 'n.', 'A salmon after spawning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kelt', 'n.', 'Same as Celt, one of Celtic race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kelter', 'n.', 'Regular order or proper condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keltic', 'a. & n.', 'Same as Celtic, a. & n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kembed', 'imp. & p. p.', 'of Kemb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kempt', '', 'of Kemb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kembing', 'p. pr. & vb. n.', 'of Kemb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kemb', 'v. t.', 'To comb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kemelin', 'n.', 'A tub; a brewers vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kemp', 'n.', 'Alt. of Kempty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kempty', 'n.', 'Coarse, rough hair wool or fur, injuring its quality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kempe', 'a.', 'Rough; shaggy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kemps', 'n. pl.', 'The long flower stems of the ribwort plantain (Plantago\n   Lanceolata).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kempt', '', 'p. p. of Kemb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ken', 'n.', 'A house; esp., one which is a resort for thieves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kenned', 'imp. & p. p.', 'of Ken');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kenning', 'p. pr. & vb. n.', 'of Ken');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ken', 'n. t.', 'To know; to understand; to take cognizance of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ken', 'n. t.', 'To recognize; to descry; to discern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ken', 'v. i.', 'To look around.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ken', 'n.', 'Cognizance; view; especially, reach of sight or knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kendal green', '', 'Alt. of Kendal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kendal', '', 'A cloth colored green by dye obtained from the woad-waxen,\n   formerly used by Flemish weavers at Kendal, in Westmoreland, England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kennel', 'n.', 'The water course of a street; a little canal or channel; a\n   gutter; also, a puddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kennel', 'n.', 'A house for a dog or for dogs, or for a pack of hounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kennel', 'n.', 'A pack of hounds, or a collection of dogs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kennel', 'n.', 'The hole of a fox or other beast; a haunt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kenneled', 'imp. & p. p.', 'of Kennel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kennelled', '', 'of Kennel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kennelling', 'p. pr. & vb. n.', 'of Kennel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kennel', 'v. i.', 'To lie or lodge; to dwell, as a dog or a fox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kennel', 'v. t.', 'To put or keep in a kennel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kennel coal', '', 'See Cannel coal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kenning', 'v. t.', 'Range of sight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kenning', 'v. t.', 'The limit of vision at sea, being a distance of about\n   twenty miles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keno', 'n.', 'A gambling game, a variety of the game of lotto, played with\n   balls or knobs, numbered, and cards also numbered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kenogenesis', 'n.', 'Modified evolution, in which nonprimitive characters\n   make their appearance in consequence of a secondary adaptation of the\n   embryo to the peculiar conditions of its environment; -- distinguished\n   from palingenesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kenogenetic', 'a.', 'Of or pertaining to kenogenesis; as, kenogenetic\n   processes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kenspeckle', 'a.', 'Having so marked an appearance as easily to be\n   recognized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kent bugle', '', 'A curved bugle, having six finger keys or stops, by means\n   of which the performer can play upon every key in the musical scale; --\n   called also keyed bugle, and key bugle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kentle', 'n.', 'A hundred weight; a quintal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kentledge', 'n.', 'Pigs of iron used for ballast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kentucky', 'n.', 'One of the United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kephalin', 'n.', 'One of a group of nitrogenous phosphorized principles,\n   supposed by Thudichum to exist in brain tissue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kept', 'imp. & p. p.', 'of Keep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keramic', 'a.', 'Same as Ceramic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keramics', 'n.', 'Same as Ceramics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keramographic', 'a.', 'Suitable to be written upon; capable of being\n   written upon, as a slate; -- said especially of a certain kind of\n   globe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kerana', 'n.', 'A kind of long trumpet, used among the Persians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kerargyrite', 'n.', 'See Cerargyrite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kerasin', 'n.', 'A nitrogenous substance free from phosphorus, supposed to\n   be present in the brain; a body closely related to cerebrin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kerasine', 'a.', 'Resembling horn; horny; corneous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keratin', 'n.', 'A nitrogenous substance, or mixture of substances,\n   containing sulphur in a loose state of combination, and forming the\n   chemical basis of epidermal tissues, such as horn, hair, feathers, and\n   the like. It is an insoluble substance, and, unlike elastin, is not\n   dissolved even by gastric or pancreatic juice. By decomposition with\n   sulphuric acid it yields leucin and tyrosin, as does albumin. Called\n   also epidermose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keratitis', 'n.', 'Inflammation of the cornea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keratode', 'n.', 'See Keratose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keratogenous', 'a.', 'Producing horn; as, the keratogenous membrane within\n   the horny hoof of the horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keratoidea', 'n. pl.', 'Same as Keratosa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keratome', 'n.', 'An instrument for dividing the cornea in operations for\n   cataract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keratonyxis', 'n.', 'The operation of removing a cataract by thrusting a\n   needle through the cornea of the eye, and breaking up the opaque mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keratophyte', 'n.', 'A gorgonian coral having a horny axis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keratosa', 'n. pl.', 'An order of sponges having a skeleton composed of\n   hornlike fibers. It includes the commercial sponges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keratose', 'n.', 'A tough, horny animal substance entering into the\n   composition of the skeleton of sponges, and other invertebrates; --\n   called also keratode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keratose', 'a.', 'Containing hornlike fibers or fibers of keratose;\n   belonging to the Keratosa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keraunograph', 'n.', 'A figure or picture impressed by lightning upon the\n   human body or elsewhere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kerb', 'n.', 'See Curb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kerbstone', 'n.', 'See Curbstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kercher', 'n.', 'A kerchief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kerchered', 'a.', 'Covered, or bound round, with a kercher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kerchiefs', 'pl. ', 'of Kerchief');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kerchief', 'n.', 'A square of fine linen worn by women as a covering for\n   the head; hence, anything similar in form or material, worn for\n   ornament on other parts of the person; -- mostly used in compounds; as,\n   neckerchief; breastkerchief; and later, handkerchief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kerchief', 'n.', 'A lady who wears a kerchief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kerchiefed', 'a.', 'Alt. of Kerchieft');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kerchieft', 'a.', 'Dressed; hooded; covered; wearing a kerchief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kerf', 'n.', 'A notch, channel, or slit made in any material by cutting or\n   sawing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kerite', 'n.', 'A compound in which tar or asphaltum combined with animal\n   or vegetable oils is vulcanized by sulphur, the product closely\n   resembling rubber; -- used principally as an insulating material in\n   telegraphy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kerl', 'n.', 'See Carl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kermes', 'n.', 'The dried bodies of the females of a scale insect (Coccus\n   ilicis), allied to the cochineal insect, and found on several species\n   of oak near the Mediterranean. They are round, about the size of a pea,\n   contain coloring matter analogous to carmine, and are used in dyeing.\n   They were anciently thought to be of a vegetable nature, and were used\n   in medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kermes', 'n.', 'A small European evergreen oak (Quercus coccifera) on which\n   the kermes insect (Coccus ilicis) feeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kermesse', 'n.', 'See Kirmess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kern', 'n.', 'A light-armed foot soldier of the ancient militia of Ireland\n   and Scotland; -- distinguished from gallowglass, and often used as a\n   term of contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kern', 'n.', 'Any kind of boor or low-lived person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kern', 'n.', 'An idler; a vagabond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kern', 'n.', 'A part of the face of a type which projects beyond the body,\n   or shank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kerned', 'imp. & p. p.', 'of Kern');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kerning', 'p. pr. & vb. n.', 'of Kern');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kern', 'v. t.', 'To form with a kern. See 2d Kern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kern', 'n.', 'A churn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kern', 'n.', 'A hand mill. See Quern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kern', 'v. i.', 'To harden, as corn in ripening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kern', 'v. i.', 'To take the form of kernels; to granulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kerned', 'a.', 'Having part of the face projecting beyond the body or\n   shank; -- said of type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kernel', 'n.', 'The essential part of a seed; all that is within the seed\n   walls; the edible substance contained in the shell of a nut; hence,\n   anything included in a shell, husk, or integument; as, the kernel of a\n   nut. See Illust. of Endocarp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kernel', 'n.', 'A single seed or grain; as, a kernel of corn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kernel', 'n.', 'A small mass around which other matter is concreted; a\n   nucleus; a concretion or hard lump in the flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kernel', 'n.', 'The central, substantial or essential part of anything; the\n   gist; the core; as, the kernel of an argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kerneled', 'imp. & p. p.', 'of Kernel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kernelled', '', 'of Kernel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kerneling', 'p. pr. & vb. n.', 'of Kernel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kernelling', '', 'of Kernel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kernel', 'v. i.', 'To harden or ripen into kernels; to produce kernels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kerneled', 'a.', 'Alt. of Kernelled');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kernelled', 'a.', 'Having a kernel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kernelly', 'a.', 'Full of kernels; resembling kernels; of the nature of\n   kernels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kerish', 'a.', 'Clownish; boorish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kerolite', 'n.', 'Same as Cerolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kerosene', 'n.', 'An oil used for illuminating purposes, formerly obtained\n   from the distillation of mineral wax, bituminous shale, etc., and hence\n   called also coal oil. It is now produced in immense quantities, chiefly\n   by the distillation and purification of petroleum. It consists chiefly\n   of several hydrocarbons of the methane series.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kers', 'n.', 'Alt. of Kerse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kerse', 'n.', 'A cress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kerseys', 'pl. ', 'of Kersey');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kersey', 'n.', 'A kind of coarse, woolen cloth, usually ribbed, woven from\n   wool of long staple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kerseymere', 'n.', 'See Cassimere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kerseynette', 'n.', 'See Cassinette.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kerve', 'v. t.', 'To carve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kerver', 'n.', 'A carver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kesar', 'n.', 'See Kaiser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keslop', 'n.', 'The stomach of a calf, prepared for rennet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kess', 'v. t.', 'To kiss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kest', 'imp.', 'of Cast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kestrel', 'n.', 'A small, slender European hawk (Falco alaudarius), allied\n   to the sparrow hawk. Its color is reddish fawn, streaked and spotted\n   with white and black. Also called windhover and stannel. The name is\n   also applied to other allied species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ket', 'n.', 'Carrion; any filth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ketch', 'n.', 'An almost obsolete form of vessel, with a mainmast and a\n   mizzenmast, -- usually from one hundred to two hundred and fifty tons\n   burden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ketch', 'n.', 'A hangman. See Jack Ketch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ketch', 'v. t.', 'To catch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ketchup', 'n.', 'A sauce. See Catchup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ketine', 'n.', 'One of a series of organic bases obtained by the reduction\n   of certain isonitroso compounds of the ketones. In general they are\n   unstable oily substances having a pungent aromatic odor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ketmie', 'n.', 'The name of certain African species of Hibiscus, cultivated\n   for the acid of their mucilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ketol', 'n.', 'One of a series of series of complex nitrogenous substances,\n   represented by methyl ketol and related to indol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ketone', 'n.', 'One of a large class of organic substances resembling the\n   aldehydes, obtained by the distillation of certain salts of organic\n   acids and consisting of carbonyl (CO) united with two hydrocarbon\n   radicals. In general the ketones are colorless volatile liquids having\n   a pungent ethereal odor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ketonic', 'a.', 'Pertaining to, or derived from, a ketone; as, a ketonic\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kettle', 'n.', 'A metallic vessel, with a wide mouth, often without a\n   cover, used for heating and boiling water or other liguids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kettledrum', 'n.', 'A drum made of thin copper in the form of a\n   hemispherical kettle, with parchment stretched over the mouth of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kettledrum', 'n.', 'An informal social party at which a light collation is\n   offered, held in the afternoon or early evening. Cf. Drum, n., 4 and 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kettledrummer', 'n.', 'One who plays on a kettledrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keuper', 'n.', 'The upper division of the European Triassic. See Chart of\n   Geology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kevel', 'n.', 'A strong cleat to which large ropes are belayed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kevel', 'n.', 'A stone masons hammer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kevel', 'n.', 'Alt. of Kevin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kevin', 'n.', 'The gazelle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kever', 'v. t. &', 'i. To cover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keverchief', 'n.', 'A kerchief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kex', 'n.', 'A weed; a kecksy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kex', 'n.', 'A dry husk or covering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Key', 'n.', 'An instrument by means of which the bolt of a lock is shot or\n   drawn; usually, a removable metal instrument fitted to the mechanism of\n   a particular lock and operated by turning in its place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Key', 'n.', 'An instrument which is turned like a key in fastening or\n   adjusting any mechanism; as, a watch key; a bed key, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Key', 'n.', 'That part of an instrument or machine which serves as the\n   means of operating it; as, a telegraph key; the keys of a pianoforte,\n   or of a typewriter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Key', 'n.', 'A position or condition which affords entrance, control, pr\n   possession, etc.; as, the key of a line of defense; the key of a\n   country; the key of a political situation. Hence, that which serves to\n   unlock, open, discover, or solve something unknown or difficult; as,\n   the key to a riddle; the key to a problem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Key', 'n.', 'That part of a mechanism which serves to lock up, make fast,\n   or adjust to position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Key', 'n.', 'A piece of wood used as a wedge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Key', 'n.', 'The last board of a floor when laid down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Key', 'n.', 'A keystone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Key', 'n.', 'That part of the plastering which is forced through between\n   the laths and holds the rest in place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Key', 'n.', 'A wedge to unite two or more pieces, or adjust their relative\n   position; a cotter; a forelock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Key', 'n.', 'A bar, pin or wedge, to secure a crank, pulley, coupling,\n   etc., upon a shaft, and prevent relative turning; sometimes holding by\n   friction alone, but more frequently by its resistance to shearing,\n   being usually embedded partly in the shaft and partly in the crank,\n   pulley, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Key', 'n.', 'An indehiscent, one-seeded fruit furnished with a wing, as the\n   fruit of the ash and maple; a samara; -- called also key fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Key', 'n.', 'A family of tones whose regular members are called diatonic\n   tones, and named key tone (or tonic) or one (or eight), mediant or\n   three, dominant or five, subdominant or four, submediant or six,\n   supertonic or two, and subtonic or seven. Chromatic tones are temporary\n   members of a key, under such names as \" sharp four,\" \"flat seven,\" etc.\n   Scales and tunes of every variety are made from the tones of a key.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Key', 'n.', 'The fundamental tone of a movement to which its modulations\n   are referred, and with which it generally begins and ends; keynote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Key', 'n.', 'Fig: The general pitch or tone of a sentence or utterance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keved', 'imp. & p. p.', 'of Key');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keying', 'p. pr. & vb. n.', 'of Key');
  INSERT INTO words (word, word_type, description) VALUES  (    'Key', 'v. t.', 'To fasten or secure firmly; to fasten or tighten with keys\n   or wedges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keyage', 'n.', 'Wharfage; quayage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keyboard', 'n.', 'The whole arrangement, or one range, of the keys of an\n   organ, typewriter, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Key-cold', 'a.', 'Cold as a metallic key; lifeless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keyed', 'a.', 'Furnished with keys; as, a keyed instrument; also, set to a\n   key, as a tune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Keyhole', 'n.', 'A hole or apertupe in a door or lock, for receiving a key.');
