  INSERT INTO words (word, word_type, description) VALUES  (    'Keynote', 'n.', 'The tonic or first tone of the scale in which a piece or\n   passage is written; the fundamental tone of the chord, to which all the\n   modulations of the piece are referred; -- called also key tone.');
