  INSERT INTO words (word, word_type, description) VALUES  (    'Keystone', 'n.', 'The central or topmost stone of an arch. This in some\n   styles is made different in size from the other voussoirs, or projects,\n   or is decorated with carving. See Illust. of Arch.');
