  INSERT INTO words (word, word_type, description) VALUES  (    'Khaya', 'n.', 'A lofty West African tree (Khaya Senegalensis), related to\n   the mahogany, which it resembles in the quality of the wood. The bark\n   is used as a febrifuge.');
