  INSERT INTO words (word, word_type, description) VALUES  (    'Kicred', 'imp. & p. p.', 'of Kick');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kicking', 'p. pr. & vb. n.', 'of Kick');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kick', 'v. t.', 'To strike, thrust, or hit violently with the foot; as, a\n   horse kicks a groom; a man kicks a dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kick', 'v. i.', 'To thrust out the foot or feet with violence; to strike\n   out with the foot or feet, as in defense or in bad temper; esp., to\n   strike backward, as a horse does, or to have a habit of doing so.\n   Hence, figuratively: To show ugly resistance, opposition, or hostility;\n   to spurn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kick', 'v. i.', 'To recoil; -- said of a musket, cannon, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kick', 'n.', 'A blow with the foot or feet; a striking or thrust with the\n   foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kick', 'n.', 'The projection on the tang of the blade of a pocket knife,\n   which prevents the edge of the blade from striking the spring. See\n   Illust. of Pocketknife.');
